  [7001] = {
    id = 7001,
    Desc = "1.冒险者可以在活动时间内激活「基金」功能，若活动期间未激活，则活动结束后功能将关闭\n2.激活「基金」后，登录达到指定天数，均可领取不同数量的B格猫金币，直至全部奖励领完，功能关闭\n3.「基金」为帐号限定类功能",
    Title = "基金"
  },
  [35060] = {
