  [2502000] = "{id=2502000,Desc='达纳托斯每5秒会积攒20点狂乱值，每点提升%s%%的人形种族减伤，最多积攒100点。每次使用剑式，提升自身%s%%的物理穿刺，持续8秒，可叠加3次',}",
  [2502001] = "{id=2502001,Desc='达纳托斯每5秒会积攒20点狂乱值，每点提升%s%%的人形种族减伤，最多积攒100点。每次使用剑式，提升自身%s%%的物理穿刺，持续8秒，可叠加3次。狂乱值上限提高至150点',}",
