  [2503000] = "{id=2503000,Desc='消耗5点狂乱值，朝指定方向发出剑气，对前方的敌人造成（物理攻击＋魔法攻击）*[8df936]%s%%[-]的物理伤害，同时附加一层深渊烙印，深渊烙印持续12秒。该技能可打断后摇',}",
  [2504000] = "{id=2504000,Desc='使用【灾厄】后，在5秒内将招式变为【天罚】。消耗25点狂乱值，朝指定区域飞跃，落地时对半径3米内的敌人造成（物理攻击＋魔法攻击）*[8df936]%s%%[-]*2^深渊烙印层数的物理伤害，使敌人移动速度降低80%%，持续1秒，同时清除区域内的范围效果，附加一层深渊烙印。该技能可打断后摇',}",
  [2505000] = "{id=2505000,Desc='使用【天罚】后，在5秒内将招式变为【魔舞】。消耗30点狂乱值，原地消失1秒，1秒后对周围4米的敌人进行斩击，造成（物理攻击＋魔法攻击）*[8df936]%s%%[-]*2^深渊烙印层数的物理伤害，同时使他们陷入1秒的【噩梦】，附加一层深渊烙印。该技能可打断后摇',}",
  [2506000] = "{id=2506000,Desc='使用【魔舞】后，在5秒内将招式变为【终结】。瞬移至一名敌人身后，对路径上的所有敌人造成（物理攻击＋魔法攻击）*[8df936]%s%%[-]*2^深渊烙印层数的物理伤害，清除受到伤害的敌人的深渊烙印，每一层回复自身10点狂乱值。该技能可打断后摇',}",
  [2507000] = "{id=2507000,Desc='回复自身30点狂乱值，并使周围10米内敌人的深渊烙印持续时间重置',}",
  [2508000] = "{id=2508000,Desc='达纳托斯释放魔剑中的力量，令自身周围4米内的敌人被梦魇缠身，强制陷入【噩梦】之中，持续4秒。【噩梦】状态下无法移动和攻击，受到2次攻击后解除，状态结束后清除敌人所有的增益效果，冷却时间固定不可减少',}",
  [2509000] = "{id=2509000,Desc='达纳托斯接受魔剑的力量，化身为强大的魔王。提高自身[8df936]%s%%[-]的生命上限和[8df936]%s%%[-]的物理穿刺，将自身种族变为恶魔，同时自身处于霸体状态，持续30秒',}",
  [2510000] = "{id=2510000,Desc='达纳托斯在人类时期就学会的剑士系技能。使自己的伤害减免提高[8df936]%s%%[-]，异常状态抵抗提高[8df936]%s%%[-]，持续5秒，消耗35点狂乱值。使用后会将剑式重置为【灾厄】',}",
  [2511000] = "{id=2511000,Desc='向指定的敌人进行重劈，撕裂虚空，对路径上2*8米范围的敌人造成物理攻击*[8df936]%s%%[-]的物理伤害。该技能可打断后摇',}",
  [2512000] = "{id=2512000,Desc='达纳托斯精通剑术，使用长剑时有[8df936]%s%%[-]的概率完全格挡敌人的伤害，每点智力额外提高[8df936]%s[-]点魔法攻击',}",
  [2513000] = "{id=2513000,Desc='即使背负的力量为世人所厌弃，也要用它驱除邪恶的孤胆英雄，勇气与信念赋予他更强大的力量。提升达纳托斯[8df936]%s%%[-]的物伤加成，非骑乘状态下移动速度提高[8df936]%s%%[-]',}",
  [2514000] = "{id=2514000,Desc='身负罪孽之人，会给接触他的人带来诅咒与不幸。受到攻击时有[8df936]%s%%[-]的概率使对方陷入【中毒】和【恐惧】',}",
  [2515000] = "{id=2515000,Desc='朝指定区域飞跃，落地时对半径3米内的敌人造成物理伤害，使敌人移动速度降低80%%，持续1秒，同时清除区域内的范围效果，附加一层深渊烙印',}",
  [2516000] = "{id=2516000,Desc='原地消失1秒，1秒后对周围4米的敌人进行斩击造成物理伤害，同时使他们陷入【噩梦】，附加一层深渊烙印',}",
  [2517000] = "{id=2517000,Desc='瞬移至一名敌人身后，对路径上的所有敌人造成物理伤害，清除受到伤害的敌人的深渊烙印，每一层回复自身10点狂乱值',}",
  [2531000] = "{id=2531000,Desc='使尼德霍格和【龙之族民】受到的伤害降低%s%%',}",
  [2531001] = "{id=2531001,Desc='使尼德霍格和【龙之族民】受到的伤害降低%s%%。尼德霍格攻击造成的伤害以20%%的比例转换为自身和【龙之族民】的护盾，持续5秒',}",
  [2531002] = "{id=2531002,Desc='使尼德霍格和【龙之族民】受到的伤害降低%s%%[HeroLvSeparator]尼德霍格攻击造成的伤害以20%%的比例转换为自身和【龙之族民】的护盾，持续5秒[HeroLvSeparator]尼德霍格造成技能伤害或【龙之族民】攻击造成伤害时，尼德霍格对目标额外造成一次【堕夜流光】的伤害，每秒最多触发3次',}",
  [2532000] = "{id=2532000,Desc='尼德霍格攻击一名敌人，使目标及其3米范围内的敌人受到魔法攻击*[8df936]%s%%[-]的暗属性魔法伤害，同时有20%%概率使所有敌人降低[8df936]%s%%[-]的最终恢复效果，持续5秒',}",
  [2533000] = "{id=2533000,Desc='释放自然之力，治疗自身和【龙之族民】，恢复（自身魔法攻击*[8df936]%s[-]＋自身智力*[8df936]%s[-]）点生命，并在接下来的5秒内恢复同等治疗量（PVP或GVG时，效果为4倍）',}",
  [2534000] = "{id=2534000,Desc='将一名队友视为【龙之族民】，用血脉共鸣之力提高自身与【龙之族民】[8df936]%s[-]点全能力，持续300秒。如果【龙之族民】和自身距离超过15米时，效果会暂时失效。无法将其他的尼德霍格视为【龙之族民】',}",
  [2535000] = "{id=2535000,Desc='尼德霍格释放天地树庞大的生命能量，使自身和【龙之族民】免于死亡，同时复活周围5米范围内死亡的队友，恢复其所有的生命和魔法，效果持续[8df936]%s[-]秒。冷却时间固定不可减少',}",
  [2536000] = "{id=2536000,Desc='借助天地树的力量，扭曲自身和【龙之族民】周围2米的空间，进入其中的自己和队友可以减少[8df936]%s%%[-]来自外界敌人的伤害，结界持续8秒。此技能可在【地元素领域】、【火雨】内施放',}",
  [2537000] = "{id=2537000,Desc='将自身周围15米的队友均短暂视为【龙之族民】，同时将自身种族变为龙，持续6秒，冷却时间固定不可减少。无法将其他的尼德霍格视为【龙之族民】',}",
  [2538000] = "{id=2538000,Desc='提高【堕夜流光】的伤害，伤害增幅为尼德霍格当前Sp/最大Sp*[8df936]%s%%[-]',}",
  [2539000] = "{id=2539000,Desc='【堕夜流光】会降低敌人[8df936]%s%%[-]的物理攻击、魔法攻击和魔法防御，持续5秒，可叠加5次，效果不可驱散',}",
  [2540000] = "{id=2540000,Desc='尼德霍格攻击时有[8df936]%s%%[-]概率净化自身和【龙之族民】，清除一个负面效果',}",
  [2541000] = "{id=2541000,Desc='尼德霍格在死亡时会爆发出生命能量，恢复全体队友[8df936]%s%%[-]的生命和魔法，同时复活5米范围内死亡的队友，恢复其所有的生命和魔法。此效果每8秒最多生效一次',}",
  [2560000] = "{id=2560000,Desc='对敌方单体造成物理攻击*600%%的物理伤害，自身的攻击速度恒定为100%%，且无法触发暴击。每10点敏捷会转换为1%%的移动速度。【通常攻击】不会造成击退',}",
  [2561002] = "{id=2561002,Desc='对敌人造成伤害时，会将敌人击退3米，对方每有100点力量，额外被击退1米，同时附加双方力量和*物理攻击*%s%%的额外伤害（击退效果对魔物无效，额外击退的距离无视霸体，额外伤害受元素/体型/种族影响）[HeroLvSeparator]击退路径上的所有敌人会被强制降低80%%的移动速度，持续3秒（对魔物会直接造成减速效果）[HeroLvSeparator]攻击【专注对决】的目标时，伤害无视闪避及格挡；击杀【专注对决】的目标时，恢复所有生命',}",
  [2562000] = "{id=2562000,Desc='与一名敌人进行对决，持续[8df936]%s[-]秒，期间只能对该目标造成伤害，并且只会受到来自目标的伤害，当距离超过15米时，对决效果会消失。冷却时间固定，对决期间如果击杀对方，可以减少5秒冷却时间',}",
  [2563000] = "{id=2563000,Desc='对一名敌人造成物理攻击*（[8df936]%s%%[-]＋自身力量/5）的物理伤害，并将其击退，后方3*8米范围内的敌人受到20%%的溅射伤害，并因为气流旋转1秒。【最强力量】的击退距离和额外伤害在本次攻击中效果翻倍。此技能无视闪避和物理防御',}",
  [2564000] = "{id=2564000,Desc='提高自身[8df936]%s%%[-]的移动速度和[8df936]%s%%[-]的物伤加成，持续10秒，效果无法驱散，冷却时间固定不能减少',}",
  [2565000] = "{id=2565000,Desc='锁定一名敌人，对其进行快速地攻击，每0.2秒对其造成一次【通常攻击】*[8df936]%s%%[-]的物理伤害，持续2秒，造成最后一次伤害时才会将敌人击退，被锁定的敌人无法移动，无法隐匿，无法使用位移技能',}",
  [2566000] = "{id=2566000,Desc='经过3秒的俯卧撑，提高[8df936]%s[-]点体质和力量，[8df936]%s%%[-]的物伤和魔伤减免，持续120秒。锻炼身体效果可以叠加3次，但攻击后会取消叠加效果，回到单次【锻炼身体】状态',}",
  [2567000] = "{id=2567000,Desc='以肉眼不可见的速度跳跃到最远[8df936]%s[-]米的位置',}",
  [2568000] = "{id=2568000,Desc='提高[8df936]%s%%[-]的黑暗抵抗和恐惧抵抗，并使受到异常状态的时间减少[8df936]%s%%[-]。使自身受MVP、Mini魔物的伤害减少[8df936]%s%%[-]',}",
  [2569000] = "{id=2569000,Desc='当受到的伤害低于（自身力量＋自身体质）*[8df936]%s[-]时，无视此次伤害。【超速移动】状态下，效果提高[8df936]%s[-]倍',}",
  [2570000] = "{id=2570000,Desc='移动速度每提高1%%，就会额外增加自身[8df936]%s[-]点物理攻击，并提高[8df936]%s%%[-]【正义执行】的最终伤害',}",
  [2571000] = "{id=2571000,Desc='通常攻击没有击杀对方时，5秒内下一次通常攻击有[8df936]%s%%[-]概率触发【正义执行】，概率会持续叠加直至触发后重置。每次通常攻击或连续攻击，提高自身[8df936]%s%%[-]的伤害减免，持续3秒',}",
  [2591000] = "{id=2591000,Desc='濒死时不会死亡，会将生命锁定进入【改造】，如果在7秒内受到的攻击次数低于%s次（魔物每次攻击消耗3次），7秒后会恢复所有生命和魔法。【改造】时，无法攻击和使用技能，也无法加速[HeroLvSeparator]【改造】时，每2秒对周围3米的敌人施放静电干扰，使敌人无法进行攻击，持续2秒[HeroLvSeparator]【改造】时不再禁止攻击和技能',}",
  [2592000] = "{id=2592000,Desc='通过0.2秒的准备，朝前方目标发射出能量炮，对3*10米范围内的敌人造成（魔法攻击＋自身力量*自身体质/5）*[8df936]%s%%[-]的火属性魔法伤害，并有[8df936]%s%%[-]概率使敌人陷入【灼烧】。对魔物额外造成[8df936]%s%%[-]的伤害',}",
  [2593000] = "{id=2593000,Desc='持续扫描前方扇形区域，使区域内的敌人降低[8df936]%s%%[-]的魔伤减免，同时使隐匿的敌人解除隐匿状态，扫描持续10秒。此技能可以在【充能焚烧炮】引导期间使用',}",
  [2594000] = "{id=2594000,Desc='提高[8df936]%s%%[-]的魔伤加成，[8df936]%s%%[-]的风属性攻击和火属性攻击，但每秒会消耗1%%最大Sp，Sp不足或再次使用技能会取消状态。此技能可以在【充能焚烧炮】引导期间使用',}",
  [2595000] = "{id=2595000,Desc='向指定目的地快速移动，最多移动[8df936]%s[-]米，使用后刷新【机械吊拳】的冷却时间',}",
  [2596000] = "{id=2596000,Desc='消耗[8df936]%s%%[-]当前生命，补充[8df936]%s%%[-]最大魔法，同时使自己受到的伤害降低[8df936]%s%%[-]，持续3秒。此技能可以在【充能焚烧炮】引导期间使用',}",
  [2597000] = "{id=2597000,Desc='向远处的敌人发出机械拳头，对其和周围5米的敌人造成（魔法攻击＋自身力量*自身体质/5）*[8df936]%s%%[-]的无属性魔法伤害，并将他们聚集在一起，同时使其无法移动，持续2秒',}",
  [2598000] = "{id=2598000,Desc='朝前方引导螺旋形能量炮，每0.3秒造成一次【焚烧炮】*[8df936]%s%%[-]的火属性魔法伤害，同时消耗3%%当前魔法，当魔法不足20%%时会结束引导，再次使用也可以终止技能。【充能焚烧炮】有[8df936]%s%%[-]概率使敌人陷入【灼烧】，并且逐渐降低敌人移动速度。引导时可以改变方向',}",
  [2599000] = "{id=2599000,Desc='【改造】成功后，会提高[8df936]%s%%[-]的魔法攻击和[8df936]%s%%[-]的生命上限，持续600秒。死亡或离开地图会移除效果',}",
  [2600000] = "{id=2600000,Desc='【电光之印】开启时，【焚烧炮】额外附带[8df936]%s%%[-]的风属性魔法伤害',}",
  [2601000] = "{id=2601000,Desc='提高[8df936]%s[-]点闪避和[8df936]%s[-]点暴击防护，在闪避攻击时刷新【迅捷动力】的冷却时间',}",
  [2602000] = "{id=2602000,Desc='机器躯壳能承受更多的击打，每10点力量可以提高自身[8df936]%s%%[-]的物伤减免和近战物理伤害减免',}",
  [2611000] = "{id=2611000,Desc='每次对玩家进行攻击时，使目标数值最高的素质点减少%s点，持续30秒\n每次对魔物进行攻击时，使自己的智力、灵巧、幸运提高%s点，持续30秒\n（以上效果3秒内最多生效一次）[HeroLvSeparator]对玩家攻击减少的素质点以30%%的比例转为自身的属性[HeroLvSeparator]海拉可以通过冒险技能自由选择干涉的属性，并将效果的持续时间翻倍',}",
  [2612000] = "{id=2612000,Desc='从虚空中召唤地狱犬加尔姆，跟随海拉，每次攻击会造成海拉魔法攻击*[8df936]%s%%[-]的无属性魔法伤害，地狱犬不会消失。当自身受到致死伤害时，减少一只地狱犬使自己免受伤害，持续1秒，并恢复自身30%%的生命',}",
  [2613000] = "{id=2613000,Desc='命令地狱犬发出怒吼，使范围5米内的敌人每秒全能力降低[8df936]%s[-]点，并且该区域内死亡的敌人无法原地复活，降低效果和吼叫时间持续10秒',}",
  [2614000] = "{id=2614000,Desc='对远距离的敌人投掷一个沮丧的幽灵瓶，对目标造成魔法攻击*[8df936]%s%%[-]的毒属性魔法伤害，并使其【沮丧】，在后续的15秒内每秒造成10%%的伤害，【沮丧】效果可以最多叠加15层。对魔物造成的【沮丧】不可被驱散',}",
  [2615000] = "{id=2615000,Desc='海拉锁定一名敌人使其坠入甜蜜的噩梦，噩梦散播对其和周围5米的敌人造成魔法攻击*[8df936]%s%%[-]的毒属性魔法伤害。对方被干涉每减少10点素质点，额外增加4%%的伤害。如果对方剩余的生命低于10%%，则直接被斩杀（每一层【沮丧】提高3%%的斩杀上限，至多提高15%%，斩杀对魔物无效）',}",
  [2616000] = "{id=2616000,Desc='海拉拥有亡者血脉，能将自己受到的生命流失效果按[8df936]%s%%[-]的比例转为治疗，持续[8df936]%s[-]秒，效果无法驱散',}",
  [2617000] = "{id=2617000,Desc='诞生于尼夫海姆的流行病，被遗忘者将真正消亡，海拉将该病传播给敌人，目标使用技能后会在4秒内遗忘掉该技能，疫病持续10秒。冷却时间固定不可减少',}",
  [2618000] = "{id=2618000,Desc='喝下致命的毒药，使自己受到毒属性以外的元素伤害减少[8df936]%s%%[-]，毒属性攻击提高[8df936]%s%%[-]，持续30秒，如果在【亡者血脉】状态下，还会使自己陷入【中毒】',}",
  [2619000] = "{id=2619000,Desc='当自身转换的属性超过80点时，可以解锁使用。海拉宣布派对开始，使所有的主动技能获得强化效果：\n★【加尔姆】额外召唤一只加尔姆的幻影\n★地狱犬每秒对【恶犬勿近】范围内的敌人进行一次攻击\n★【瓶装幽灵】每次叠加2层【沮丧】\n★【致命美梦】必定会对敌人造成斩杀上限的伤害\n★【亡者血脉】效果覆盖为周围的队友，队友持续时间减半\n★【遗忘蜜酒】的遗忘时间＋4秒\n★【死亡毒药】的中毒和毒属性攻击效果，叠加3层\n\n派对持续40秒，冷却时间固定不可减少',}",
  [2620000] = "{id=2620000,Desc='使地狱犬的伤害提高[8df936]%s%%[-]，并将[8df936]%s%%[-]的伤害转为海拉的生命',}",
  [2621000] = "{id=2621000,Desc='当自身处于异常状态时，造成伤害或受到伤害时有[8df936]%s%%[-]概率将自身所有的异常状态传染给敌人',}",
  [2622000] = "{id=2622000,Desc='当目标存在素质点为0时，海拉每存在5点幸运，便会对目标额外造成[8df936]%s%%[-]的技能伤害',}",
  [2623000] = "{id=2623000,Desc='海拉使用技能为自己或队友添加增益效果时，会使其【隐匿】，并提高[8df936]%s%%[-]的技能伤害减免，持续[8df936]%s[-]秒',}",
  [2624000] = "{id=2624000,Desc='使用后将自己的灵魂干涉指定为力量，只有在PVP/GVG环境中生效',}",
