  [2629000] = "{id=2629000,Desc='使用后将自己的灵魂干涉指定为幸运，只有在PVP/GVG环境中生效',}",
  [2638000] = "{id=2638000,Desc='加尔姆的吼叫',}",
  [2651000] = "{id=2651000,Desc='忽克连每次【挥舞铁锤】，都有%s%%概率随机使自身的一个装备部位的精炼等级＋1，持续30秒，可以突破精炼等级上限[HeroLvSeparator]如果自身的所有防具精炼等级均达到12，则使自身的物理防御和魔法防御不会被忽视[HeroLvSeparator]如果自身的武器和饰品精炼等级均达到17，则使精炼物攻受到物理攻击%%的加成',}",
  [2652000] = "{id=2652000,Desc='忽克连【挥舞铁锤】，对周围4米的敌人造成物理攻击*[8df936]%s%%[-]的物理伤害，同时有20%%的概率将敌人的所有装备和头饰的精炼等级降低1级，持续15秒，幸运每比敌人高10点，概率提升1%%。对魔物造成伤害时降低目标20%%的精炼物免，持续10秒',}",
  [2653000] = "{id=2653000,Desc='忽克连将自己的铁砧向指定方向飞掷，炙热的铁砧对前方的敌人造成物理攻击*（1＋副手精炼等级/10）*[8df936]%s%%[-]的物理伤害。同时有30%%的概率将敌人的武器的精炼等级强制降为＋3，持续10秒，幸运每比敌人高10点，概率提升1%%。对魔物造成伤害时降低目标30%%的最终伤害，持续10秒',}",
  [2654000] = "{id=2654000,Desc='忽克连进入专注模式，每0.6秒进行一次【挥舞铁锤】，对周围4米的敌人造成【破坏之锤】*[8df936]%s%%[-]的物理伤害，同时附加【破坏之锤】的负面效果，共敲击7次，专注模式的命中补正提高[8df936]%s%%[-]。使用时如果不在【极限锤炼】状态，移动速度上限限制为150%%',}",
  [2655000] = "{id=2655000,Desc='忽克连3次【挥舞铁锤】，提高自身周围15米范围内队友的防具及头饰的精炼等级，每50点幸运提高一级，不超过精炼＋18，持续[8df936]%s[-]秒，如果目标的所有防具及头饰均满足精炼＋15，则额外获得10%%的物伤减免和魔伤减免。冷却时间固定不可减少',}",
  [2656000] = "{id=2656000,Desc='大幅度提高熔炉温度，有[8df936]%s%%[-]概率使周围3米的敌人陷入【灼烧】，反弹15%%自身受到的火属性伤害，同时提高自身[8df936]%s%%[-]的火属性攻击，持续30秒',}",
  [2657000] = "{id=2657000,Desc='忽克连【挥舞铁锤】，将自己和一名队友钢铁化，提高[8df936]%s%%[-]的受到近距离和远距离物理伤害减免，同时所有装备和头饰不会被破坏和脱卸，移动速度上限限制为150%%，持续10秒。效果不可驱散',}",
  [2658000] = "{id=2658000,Desc='在危急关头极限锤炼自己的身体，使自身肌肉膨胀，武器或饰品的精炼等级每突破1级上限，提高自身[8df936]%s%%[-]的最终伤害加成，防具的精炼等级每突破1级上限，提高自身[8df936]%s%%[-]的最终伤害减免。每次为敌人添加精炼等级降低效果时，强制对方对忽克连进行普通攻击，持续1秒。极限锤炼持续10秒，冷却时间固定不可减少',}",
  [2659000] = "{id=2659000,Desc='忽克连学会制作各种辅助战斗的特殊合金，技能等级的提升以及自身的幸运和灵巧会影响制作成功率。忽克连自身使用特殊合金的持续时间提高[8df936]%s%%[-]',}",
  [2660000] = "{id=2660000,Desc='在装备手推车时，提高自身[8df936]%s[-]点幸运，自身每5点幸运提高[8df936]%s[-]点物理防御，每10点幸运提高[8df936]%s[-]点魔法防御',}",
  [2661000] = "{id=2661000,Desc='积累的财富是忽克连立足的底气，在进行技能攻击时，自身每拥有一百万的Zeny，即可提高1%%的伤害，至多提高[8df936]%s%%[-]，对魔物攻击至多可提高至[8df936]%s%%[-]',}",
  [2662000] = "{id=2662000,Desc='忽克连对于精炼的一腔热血迸发，每一次添加精炼提升效果时，提高自身[8df936]%s[-]点精炼物攻，持续30秒；忽克连每一次给敌人添加精炼降低效果时或对Mvp/Mini魔物进行攻击时，恢复自身[8df936]%s%%[-]的最大生命',}",
  [2663000] = "{id=2663000,Desc='对敌人造成技能伤害时，有[8df936]%s%%[-]概率使敌人陷入【眩晕】，持续3秒。如果目标抵抗【眩晕】，则强制降低其[8df936]%s%%[-]的移动速度，持续5秒，可叠加3次',}",
  [2671000] = "{id=2671000,Desc='拉斯格瑞丝每移动20米，可以获得一支圣雪羽环绕自身，上限为4支羽毛。每消耗一支圣雪羽时，提高自身%s%%的移动速度和物伤加成，持续18秒，可叠加4次[HeroLvSeparator]每次消耗圣雪羽时，使自身在2秒内免疫物理和魔法伤害，并清除一个负面效果[HeroLvSeparator]每次造成伤害时，使【瓦尔基里】的冷却时间减少1秒',}",
  [2672000] = "{id=2672000,Desc='拉斯格瑞丝对前方3*8米范围的敌人进行羽刃攻击，每0.5秒造成（物理攻击＋魔法攻击）*[8df936]%s%%[-]的物理伤害，持续4秒，每次攻击降低目标[8df936]%s%%[-]的物理防御，持续4秒，至多叠加5层',}",
  [2673000] = "{id=2673000,Desc='拉斯格瑞丝消耗一支圣雪羽，腾空而起，1秒后冲至指定地点使用武器横扫战场，对周围4米范围内的敌人造成（物理攻击＋魔法攻击）*[8df936]%s%%[-]的物理伤害，击退同时使他们无法进行攻击，持续1秒',}",
  [2674000] = "{id=2674000,Desc='将自身和一名队友的攻击属性转为圣属性，并提高[8df936]%s%%[-]对不死和恶魔种族的伤害，自身额外获得[8df936]%s%%[-]的物理攻击和魔法攻击，持续30秒。可以选择是否改变队友的攻击属性',}",
  [2675000] = "{id=2675000,Desc='使用后开启【圣翼盔甲】，当自身受到超过生命上限20%%的伤害时，可以失去一支圣雪羽，免去溢出的伤害。再次使用【圣翼盔甲】可以取消状态',}",
  [2676000] = "{id=2676000,Desc='燃烧消耗一支圣雪羽，附着在一名敌人身上，使该敌人每次受到拉斯格瑞丝的攻击时，并会灼烧3%%的最大生命，因灼烧而损失的生命在持续期间内无法恢复，效果持续[8df936]%s[-]秒，效果无法驱散',}",
  [2677000] = "{id=2677000,Desc='使自身周边的全体队员和自身进入冲锋状态，不受硬直，免疫定身，移动速度提高[8df936]%s%%[-]，且不会被降低，持续[8df936]%s[-]秒',}",
  [2678000] = "{id=2678000,Desc='拉斯格瑞丝将自身种族变为天使，提高自身[8df936]%s%%[-]的技能伤害加成和技能伤害减免，使自身处于【霸体】状态，造成技能伤害时将[8df936]%s%%[-]转换为自身恢复，持续30秒，效果不可驱散',}",
  [2679000] = "{id=2679000,Desc='拉斯格瑞丝消耗自身所有的圣雪羽，召唤同等数量的天使，每个天使每隔3秒会自动对周围的敌人进行冲锋，对目标及其周围3米的敌人造成【圣羽裁决】*150%%的伤害，并附带【圣羽裁决】的负面效果，天使持续15秒。冷却时间固定不可减少',}",
  [2680000] = "{id=2680000,Desc='【秩序羽刃】对敌人造成伤害时，有[8df936]%s%%[-]概率使其陷入3秒的【定身】状态。如果【秩序羽刃】被清除了，会使清除者受到一次【秩序羽刃】*[8df936]%s%%[-]的伤害，并触发【定身】',}",
  [2681000] = "{id=2681000,Desc='提高拉斯格瑞丝[8df936]%s%%[-]的生命上限。在攻击敌人时，会降低目标[8df936]%s%%[-]的物理穿刺和魔法穿刺，持续8秒',}",
  [2682000] = "{id=2682000,Desc='【瓦尔基里】状态下，将[8df936]%s%%[-]的恢复效果扩散至周围5米的队友。每次通过【瓦尔基里】恢复生命时，提高自身[8df936]%s%%[-]的圣属性攻击和异常状态抵抗，持续10秒，可叠加5次',}",
  [2683000] = "{id=2683000,Desc='拉斯格瑞丝持有盾牌时，受到伤害时有[8df936]%s%%[-]概率免疫伤害。使用【指挥冲锋】后，使其他队友也有[8df936]%s%%[-]概率可以免疫伤害，免疫效果不可被驱散',}",
  [2701000] = "{id=2701000,Desc='耶梦加得可以设置2个攻击或增减益技能进入【叠加态】，进行普通攻击时有%s%%概率触发【叠加态】下的伤害技能，有%s%%概率触发【叠加态】下的增减益技能[HeroLvSeparator]每设置一个【叠加态】，攻击和施法距离增加1米[HeroLvSeparator]【叠加态】数量＋1；【尘世巨蟒】状态下，触发率＋10%%',}",
  [2702000] = "{id=2702000,Desc='耶梦加得创造出一个4米范围的时空屏障，使在其中的队友和自己最终伤害减免提高[8df936]%s%%[-]，屏障持续[8df936]%s[-]秒',}",
  [2703000] = "{id=2703000,Desc='对一名敌人使用，使其在[8df936]%s[-]秒内添加的可驱散增益效果转移到自己身上，并偷取其[8df936]%s%%[-]的魔法穿刺，偷取的穿刺效果不可叠加',}",
  [2704000] = "{id=2704000,Desc='耶梦加得带来无限的灾祸，使自己的技能伤害可以暴击，造成[8df936]%s%%[-]的暴击伤害，持续8秒或进行10次技能攻击，效果持续期间每秒恢复耶梦加得[8df936]%s%%[-]的最大生命',}",
  [2705000] = "{id=2705000,Desc='向指定方向发出一个快速移动的沙尘暴，对前方3*10米范围内的敌人造成魔法攻击*[8df936]%s%%[-]地属性魔法伤害。对一级地属性铠甲进行攻击时不受元素克制影响',}",
  [2706000] = "{id=2706000,Desc='将一定范围内的岩石变成坚硬的枪，从对象脚下的地面刺出，对3米范围内所有敌人造成魔法攻击*[8df936]%s%%[-]地属性魔法伤害，破除敌人的隐身状态并有[8df936]%s%%[-]几率使其陷入流血状态。对一级地属性铠甲进行攻击时不受元素克制影响',}",
  [2707000] = "{id=2707000,Desc='耶梦加得将一名敌人所处的时空冻结，使其冷却时间提高20%%，攻击速度降低20%%，持续[8df936]%s[-]秒，可以至多叠加3次。当叠加到第3层时，使目标无法使用技能和普通攻击，第3层效果对Mini/Mvp无效',}",
  [2708000] = "{id=2708000,Desc='耶梦加得可以通过虫洞，传送至指定位置，最远可以传送[8df936]%s[-]米，并有[8df936]%s%%[-]概率使周围3米的敌人陷入【黑洞】，持续1秒',}",
  [2709000] = "{id=2709000,Desc='●在指定区域创造出一片巨蟒领域，耶梦加得在范围内会提高自身[8df936]%s%%[-]的所有异常状态攻击，范围内的敌人会降低[8df936]%s%%[-]所有异常状态抵抗，巨蟒领域持续30秒，最多存在一个。\n●巨蟒领域会对敌方玩家有生命的召唤物使用【毒液蚀刻】\n●在范围内，每触发3次【叠加态】，并可以获得1个【蛇之回响】：最终伤害提高10%%。【蛇之回响】持续10秒，最多获得3个',}",
  [2710000] = "{id=2710000,Desc='●耶梦加得变为大蛇，恢复所有的生命，提高自身100%%的生命上限和50%%的人形种族减免，并使周围的敌人陷入不可免疫和抵抗的【恐惧】状态，持续1秒。\n●大蛇状态下解锁【赤焰吐息】和【毒液蚀刻】\n●大蛇经过的区域，会将敌人的范围效果转变为持续4秒的【巨蟒领域】\n●大蛇效果持续15秒，冷却时间固定不可减少',}",
  [2711000] = "{id=2711000,Desc='普通攻击变为喷射熔岩，对其及周围2米的敌人造成魔法攻击*1000%%的火属性魔法伤害。每多1个【蛇之回响】，可以额外喷射一名敌人，如果仅有1名敌人，每个【蛇之回响】提高50%%的伤害。【赤焰吐息】享受【灾祸狂潮】和【蛇瘴】',}",
  [2712000] = "{id=2712000,Desc='大蛇吞咬近距离的一名敌人，使其陷入不可免疫的【石化】状态，受到攻击时不会解除，持续5秒。效果不可驱散，对Mini/Mvp无效',}",
  [2713000] = "{id=2713000,Desc='耶梦加得造成技能伤害时，会额外附加自身魔法攻击*[8df936]%s%%[-]的毒属性魔法伤害，并减少目标[8df936]%s%%[-]的移动速度和[8df936]%s%%[-]的移动速度上限，持续5秒，可以叠加3次',}",
  [2714000] = "{id=2714000,Desc='每个【蛇之回响】提高耶梦加得[8df936]%s%%[-]的地属性攻击、火属性攻击和忽视魔法防御，【蛇之回响】的持续时间提高[8df936]%s[-]秒',}",
  [2715000] = "{id=2715000,Desc='耶梦加得每次受到伤害，有[8df936]%s%%[-]概率回到过去，恢复本次受到的伤害*[8df936]%s%%[-]的生命',}",
  [2730000] = "{id=2730000,Desc='海因里希不能进行普通攻击，在攻击敌人时会依照攻击速度，顺序循环地使用【白星·风】、【白星·花】、【白星·雪】、【白星·月】技能。海因里希可以在副手部位持有武器，每装备一把长剑或短剑，他的技能便有%s%%概率暴击，暴击时造成自身的暴击伤害，同时无视物理防御[HeroLvSeparator]每一层【蓄势】提高自身3%%的技能暴击概率和10%%的暴击伤害[HeroLvSeparator]海因里希所有技能享受普攻伤害加成，且不受普攻伤害减免影响',}",
  [2731000] = "{id=2731000,Desc='从远处快速靠近敌人，对其进行攻击造成物理攻击*[8df936]%s%%[-]的物理伤害，并偷取其30%%的移动速度，持续5秒',}",
  [2732000] = "{id=2732000,Desc='对指定目标及其周围3米的敌人进行攻击造成物理攻击*[8df936]%s%%[-]的物理伤害，并恢复自身10%%已损失生命',}",
  [2733000] = "{id=2733000,Desc='对指定敌人及其周围5米的敌人进行攻击造成物理攻击*[8df936]%s%%[-]的物理伤害，为自己添加一层【蓄势】：提高3%%的技能伤害，持续20秒，至多叠加10层',}",
  [2734000] = "{id=2734000,Desc='对指定敌人进行攻击，对其造成（物理攻击＋目标最大Sp）*[8df936]%s%%[-]的物理伤害，并扣除其10%%当前Sp',}",
  [2735000] = "{id=2735000,Desc='对一名敌人使用，使其所有的反射、格挡、闪避效果及免于死亡失效，持续[8df936]%s[-]秒\n\n如果目标被【劫火之击】击杀时，会对目标4米范围的友方造成一次（目标死亡前剩余生命*[8df936]%s%%[-]）的爆炸伤害\n\n当效果被驱散时，会刷新该技能的冷却时间',}",
  [2736000] = "{id=2736000,Desc='向后空翻5米，并使自身免疫地面魔法效果和远程物理伤害，持续[8df936]%s[-]秒',}",
  [2737000] = "{id=2737000,Desc='激发自身高贵的血脉，使自身受到魔物的伤害和生命流失减少[8df936]%s%%[-]，当海因里希的生命保持在60%%以上时，使自身造成的伤害提高[8df936]%s%%[-]，持续30秒',}",
  [2738000] = "{id=2738000,Desc='在敌人身上施加明镜，所有的队友可以在PVP/GVG中识别该敌人的铠甲属性，并使该敌人的暴伤减免和所有元素减免降低[8df936]%s%%[-]，效果持续30秒',}",
