  [23401001] = "{id=23401001,Desc='在随机2个地点召唤从天而降的陨石，陨石落地时会对周围3米范围内的玩家造成大量伤害，并在原地留下陨石残骸',}",
  [23402001] = "{id=23402001,Desc='熔石巨像蓄力后，击退自身周围一定范围内的冒险者并造成大量伤害',}",
