Table_Dialog_Left_39 = {
  [420804] = "{id=420804,Text='这里交给我们，你继续前进吧，冒险者。',Speaker=801966,}",
  [420805] = "{id=420805,Text='誓死守护荒境！',Speaker=801979,Option='我来帮忙',}",
  [420806] = "{id=420806,Text='去更需要你的地方吧，冒险者。',Speaker=801979,}",
  [420807] = "{id=420807,Text='我还能...战...斗....',Speaker=801979,}",
  [420808] = "{id=420808,Text='魔力...不够了...不赶紧使用传送魔法...大家会...',Speaker=801989,Option='我来帮忙',}",
  [502994] = "{id=502994,Text='交给你了，梭罗队长。',Speaker=813146,}",
  [502995] = "{id=502995,Text='这批货物将交给国家宪兵的指挥官。',Speaker=813146,}",
  [502996] = "{id=502996,Text='动作快一点，别被他们发现！',}",
  [502997] = "{id=502997,Text='嘘！小点声！',}",
  [502998] = "{id=502998,Text='说一些恶毒的话挑衅警员！',Speaker=813059,Option='{恶棍,3},{坏人,4}',}",
  [503001] = "{id=503001,Text='私はプリン。1人前の冒険猫となるため、ラザーニャからここに来たにゃ。',Speaker=813148,}",
  [503002] = "{id=503002,Text='冒険は「旅は道連れ世は情け」が大事と聞いたにゃん。',Speaker=813148,}",
  [503003] = "{id=503003,Text='ここで色々な人たちに助けてもらったから、お礼をすることにしたにゃん。',Speaker=813148,}",
  [503004] = "{id=503004,Text='ドラム族専用の猫じゃらしとも交換できる、セイント武器交換チケットIIIをプレゼントするけど、欲しいにゃ？',Speaker=813148,Option='{はい,2},{いいえ,3}',}",
  [503005] = "{id=503005,Text='おしゃれな猫じゃらしと交換するかは君次第にゃ。職業に合わせて交換するものを選ぶといいにゃ。',Speaker=813148,}",
  [503006] = "{id=503006,Text='ねえ、君ー。',Speaker=813149,}",
  [503007] = "{id=503007,Text='最近、運動もしないで長い時間をのんびりしていたら、なぜかすごい能力を手に入れてしまったにゃ～。',Speaker=813149,}",
  [503008] = "{id=503008,Text='気になるのにゃ！君がしりたいのにゃらその能力を教えるにゃけどどうするにゃ？',Speaker=813149,Option='{はい,2},{いいえ,3}',}",
  [503009] = "{id=503009,Text='そうだよにゃー。やっぱり、知りたいよにゃ！',Speaker=813149,}",
  [503010] = "{id=503010,Text='マラン島上空からプロンテラ南に行けることは知っているかにゃ？',Speaker=813149,}",
  [503011] = "{id=503011,Text='それと同じことが私にも出来るようになったんだにゃ。',Speaker=813149,}",
  [503012] = "{id=503012,Text='君を今からプロンテラ南まで行かせてみようにゃ？',Speaker=813149,Option='{はい,4},{いいえ,5}',}",
  [503013] = "{id=503013,Text='よし！それじゃ出発するにゃ～。',Speaker=813149,}",
  [503014] = "{id=503014,Text='にゃ・にゃ・にゃ・にゃ～出発～',Speaker=813149,}",
  [503015] = "{id=503015,Text='いつでもいから私の能力に頼りたい時は、気軽く話かけてにゃ。',Speaker=813149,}",
  [503016] = "{id=503016,Text='遠慮なんかしなくていいからにゃー。',Speaker=813149,}",
  [503017] = "{id=503017,Text='待っているにゃん！',Speaker=813149,}",
  [503018] = "{id=503018,Text='知りたくないならしょうがないにゃ。',Speaker=813149,}",
  [503019] = "{id=503019,Text='他にも私の能力を知りたかっている者たちはいくらでもいるにゃから。',Speaker=813149,}",
  [503020] = "{id=503020,Text='気が変わったらいつ来てもいいにゃから。',Speaker=813149,}",
  [503024] = "{id=503024,Text='嗯？不得了、不得了——竟然注意到我了吗，年轻的冒险者？',Speaker=813150,Emoji=8,}",
  [503025] = "{id=503025,Text='这证明你有足以成为“悟灵士”的天赋，想要尝试这条完全不同的道路吗？',Speaker=813150,Option='{我愿意,2},{再想想,3}',}",
  [503026] = "{id=503026,Text='好好考虑一下吧，像你这样有天赋的年轻人……倒也不常见！',Speaker=813150,}",
  [503027] = "{id=503027,Text='嗯……有志向是好的，不过现在的你看起来还不够强大，恐怕没法承受这份力量。',Speaker=813150,}",
  [503028] = "{id=503028,Text='继续锤炼自己，Base达到10级再来找我吧，我会一直在这里等你的！',Speaker=813150,}",
  [503029] = "{id=503029,Text='真是明智的决定——',Speaker=813150,Emoji=5,}",
  [503037] = "{id=503037,Text='就是这里了……',Speaker=813150,}",
  [503038] = "{id=503038,Text='远古英雄们的长眠之地……',Speaker=813150,}",
  [503039] = "{id=503039,Text='悟灵士需要借助灵魂的力量战斗——但人死后，灵魂或是进入英灵殿成为英灵，或是前往尼夫海姆进入轮回……',Speaker=813150,}",
  [503040] = "{id=503040,Text='只有极少部分的灵魂会因为某些原因，弥留人间……',Speaker=813150,}",
  [503041] = "{id=503041,Text='你需要做的就是获得他们的力量——',Speaker=813150,Option='我该怎么做',}",
  [503042] = "{id=503042,Text='只需要走到这个房间的终点就可以了，不过在这之前，我想先给你介绍几位朋友——他们会在这场试炼中帮助你的！',Speaker=813150,}",
  [503043] = "{id=503043,Text='莱恩，这就是你给我们找到的新同伴吗？看起来挺有精神的吗！',Speaker=813151,}",
  [503044] = "{id=503044,Text='那么以后就请——多多关照了，伙伴！',Speaker=813151,}",
  [503045] = "{id=503045,Text='在接下来的试炼里，好好合作吧！一定、要相信、自己的伙伴哦！',Speaker=813150,}",
  [503046] = "{id=503046,Text='这么多恶灵——可恶，是被这里的灵魂能量吸引过来的吗？',Speaker=813151,}",
  [503047] = "{id=503047,Text='不能让他们玷污这里，快把他们消灭！',Speaker=813151,}",
  [503048] = "{id=503048,Text='这些恶灵想要使用魔法恢复力量！',Speaker=813151,}",
  [503049] = "{id=503049,Text='快用我的力量，将它们一鼓作气消灭吧！',Speaker=813152,}",
  [503050] = "{id=503050,Text='不要疑惑，[c][ffff00][PlayerName][-][/c]！快将我的力量向恶灵的方向释放吧！',Speaker=813152,}",
  [503051] = "{id=503051,Text='成功了！',Speaker=813151,Option='克里斯蒂安呢？',}",
  [503052] = "{id=503052,Text='现在先不要在意这些了，继续前进吧！',Speaker=813151,}",
  [503053] = "{id=503053,Text='小心！那是可以点燃灵魂的火焰！是为了保护这座墓穴设下的机关！',Speaker=813151,}",
  [503054] = "{id=503054,Text='守护同伴是我最擅长的事！我可以赋予伙伴“钢铁之心”之心的效果……',Speaker=813151,}",
  [503055] = "{id=503055,Text='借助我的力量，一口气冲过去吧！',Speaker=813151,}",
  [503056] = "{id=503056,Text='哈哈，我们合为一体了！趁现在，冲过去吧！',Speaker=813151,}",
  [503057] = "{id=503057,Text='恭喜你，通过了试炼！',Speaker=813150,}",
  [503058] = "{id=503058,Text='你果然成功了，不愧是我、看、中、的、人！',Speaker=813161,Option='{怎么回事？,0},{（疑惑）,0}',}",
  [503059] = "{id=503059,Text='这里是古代英雄的墓地，被改造成为了“灵魂圣所”，长眠于此的灵魂并不会被召往英灵殿或是尼夫海姆……',Speaker=813161,}",
  [503060] = "{id=503060,Text='他们永眠于此，只为了有朝一日可以再次、为了守护这个世界献上自己的力量……',Speaker=813161,}",
  [503061] = "{id=503061,Text='你已经通过了我、我的同伴们的考验……',Speaker=813161,}",
  [503062] = "{id=503062,Text='现在……接受我们的力量……成为悟灵士吧！',Speaker=813161,}",
  [503063] = "{id=503063,SubViewId=2,Speaker=813161,Option='了解完毕',}",
  [503064] = "{id=503064,SubViewId=2,Speaker=813162,Option='就职成为悟灵士',}",
  [503070] = "{id=503070,Text='我们到了！',Speaker=813165,Option='这里是？',}",
  [503071] = "{id=503071,Text='这是几百年前我修建的一座密室，这里——',Speaker=813165,}",
  [503072] = "{id=503072,Text='就在前面那个魂龛中，封印着一个灵魂……',Speaker=813165,Option='{难道是,0},{传说中的魔王？,0}',}",
  [503073] = "{id=503073,Text='想什么呢！哪来的那么多魔王……这个魂龛是为了保护那个灵魂才存在的……',Speaker=813165,}",
  [503074] = "{id=503074,Text='因为某种原因，那个灵魂很不稳定，随时可能会消散，所以我只能用这种方法将她保护起来。',Speaker=813165,}",
  [503075] = "{id=503075,Text='每隔一段时间，我需要到这里来加固魂龛的力量，不过现在——我有帮手了！',Speaker=813165,}",
  [503076] = "{id=503076,Text='试着将自己的力量注入到魂龛中吧！',Speaker=813165,}",
  [503077] = "{id=503077,Text='嗯？好陌生的力量……',Speaker=813165,}",
  [503078] = "{id=503078,Text='不过，刚刚好……',Speaker=813165,}",
  [503079] = "{id=503079,Text='姐……姐姐？！你怎么跑出来了！',Speaker=813165,}",
  [503080] = "{id=503080,Text='你竟然敢出现在我面前！！！咱们可要好好算一下这笔账了！',Speaker=813166,}",
  [503081] = "{id=503081,Text='[c][ffff00][PlayerName][-][/c]，不要伤到姐姐！',Speaker=813165,}",
  [503082] = "{id=503082,Text='哼，自以为是的家伙，咱们走着瞧！',Speaker=813166,}",
  [503083] = "{id=503083,Text='姐姐……',Speaker=813165,}",
  [503084] = "{id=503084,Text='怎么会这样…… ',Speaker=813165,Option='{怎么回事？,0},{姐姐？,0}',}",
  [503085] = "{id=503085,Text='就像你看到的那样，魂龛中的灵魂正是我的亲生姐姐般若……',Speaker=813165,}",
  [503086] = "{id=503086,Text='她跟我一样，也是悟灵士。当年我们进行一项危险的魔法实验时，我们的灵魂被卷入了一片危险的[c][ffff00]心象世界[-][/c]……',Speaker=813165,}",
  [503087] = "{id=503087,Text='我不想让姐姐也困在其中，就用尽全力把她推了出去……但是在穿越现实与心象世界的裂隙时，姐姐的灵魂被重创……',Speaker=813165,}",
  [503088] = "{id=503088,Text='我花了几个月时间才从那个心象世界中脱困，当我回到现实，只看到姐姐的尸体……',Speaker=813165,}",
  [503089] = "{id=503089,Text='我用秘法将姐姐残余的灵魂保存在了魂龛中，只希望有朝一日能够找到让她复活的方法……',Speaker=813165,}",
  [503090] = "{id=503090,Text='不管怎么说——我们必须尽快找到姐姐！她的灵魂状态很不稳定，随时都可能会崩溃！',Speaker=813165,}",
  [503091] = "{id=503091,Text='姐姐，不要！！',Speaker=813165,}",
  [503092] = "{id=503092,Text='莱恩，你这个自以为是的家伙！准备好接受教训了吗！',Speaker=813166,}",
  [503093] = "{id=503093,Text='那是姐姐特有的能力“灵魂锁链”——它能够链接人的灵魂，甚至吸收他人的灵魂力量为自己所用……',Speaker=813165,}",
  [503094] = "{id=503094,Text='嘻嘻，没错……在种种灵魂力量中，人的“执念”，是最为强大的一种……',Speaker=813166,}",
  [503095] = "{id=503095,Text='此刻的我已经链接了这片猎场所有人、吸收了他们的“执念”……',Speaker=813166,}",
  [503096] = "{id=503096,Text='莱恩，我愚蠢的弟弟……你过去不是常常跟我争论谁是更强的悟灵士吗？不如——就让我们再来比试一下吧！',Speaker=813166,}",
  [503097] = "{id=503097,Text='姐姐吸收了太多“执念”力量，凭我一个人根本不可能战胜——帮助我吧，[c][ffff00][PlayerName][-][/c]！',Speaker=813165,}",
  [503098] = "{id=503098,Text='呃啊啊啊啊！！',Speaker=813166,}",
  [503099] = "{id=503099,Text='不好！姐姐——似乎压制不住吸收的力量了！',Speaker=813165,}",
  [503100] = "{id=503100,Text='糟了，“执念”蕴含的能量非常庞大，更何况姐姐吸收了猎场中所有人的执念……',Speaker=813165,}",
  [503101] = "{id=503101,Text='如果让这股力量直接爆发的话——恐怕普隆德拉也会受到冲击！',Speaker=813165,}",
  [503102] = "{id=503102,Text='不行、我不能让这种事发生，而且——我要保护好姐姐！',Speaker=813165,}",
  [503103] = "{id=503103,Text='真是自以为是……',Speaker=813166,}",
  [503104] = "{id=503104,Text='真是愚蠢的弟弟、愚蠢至极！我怎么会有这么愚蠢的弟弟！',Speaker=813166,}",
  [503105] = "{id=503105,Text='姐姐……',Speaker=813165,}",
  [503106] = "{id=503106,Text='不由分说的把我关在那个破魂龛里——整整三百年！你知道我一个人在那样无边无际的虚空中有多孤独、多煎熬吗！',Speaker=813166,}",
  [503107] = "{id=503107,Text='你口口声声的说要保护我——但是把我置于那样永无止境的孤寂中，还不如让我消散呢！',Speaker=813166,}",
  [503108] = "{id=503108,Text='……对不起，姐姐…………',Speaker=813165,}",
  [503109] = "{id=503109,Text='算了算了，谁让我是姐姐呢，就原谅你吧——至于眼前的问题……',Speaker=813166,}",
  [503110] = "{id=503110,Text='既然是我自己惹出来的麻烦，就交给我来解决吧！',Speaker=813166,}",
  [503111] = "{id=503111,Text='姐姐，你要做什么？！',Speaker=813165,}",
  [503112] = "{id=503112,Text='这是一片以猎场为基础构建的心灵世界……',Speaker=813165,}",
  [503113] = "{id=503113,Text='那座剑形石碑——我从那上面感觉到了姐姐的气息！',Speaker=813165,}",
  [503114] = "{id=503114,Text='…………',Speaker=813175,}",
  [503115] = "{id=503115,Text='……………………',Speaker=813175,}",
  [503116] = "{id=503116,Text='嘻嘻~~',Speaker=813175,}",
  [503117] = "{id=503117,Text='姐姐？！！',Speaker=813165,}",
  [503118] = "{id=503118,Text='我可真是个天才！',Speaker=813175,}",
  [503119] = "{id=503119,Text='我用自己的灵魂力量创造了这个空间，那些“执念”都会被暂时约束在这个空间内，并且会具象化……',Speaker=813175,}",
  [503120] = "{id=503120,Text='这样，就有足够的时间留给你们消除这些“执念”了……',Speaker=813175,}",
  [503121] = "{id=503121,Text='可是，这样你就……',Speaker=813165,}",
  [503122] = "{id=503122,Text='愚蠢的弟弟！我在三百年前就已经死了……而现在，我要为自己做下的错事赎罪……',Speaker=813175,}",
  [503123] = "{id=503123,Text='姐姐……',Speaker=813165,}",
  [503124] = "{id=503124,Text='对了，[c][ffff00][PlayerName][-][/c]，我给你留了一份礼物！',Speaker=813175,}",
  [503125] = "{id=503125,Text='我为你留下了一份力量，那是我在魂龛中感悟的、超越悟灵士的力量！',Speaker=813175,}",
  [503126] = "{id=503126,Text='我跟我的傻弟弟生前总喜欢争个高下——现在我将这份力量传承给你！你要代替我——',Speaker=813175,}",
  [503127] = "{id=503127,Text='证明我比莱恩更强哦！',Speaker=813175,}",
  [503130] = "{id=503130,Text='我保管着过往英雄的回忆，并以诗与歌的形式传唱。冒险者，这其中，有没有你的故事呢？',Speaker=813499,Option='{光与清风,2},{光与拾期,3},{光与暖阳,4},{光与守护,5},{光与窗,6},{光与花嫁,7},{肥猫幼稚园,8},{稍后再看,10}',}",
  [503136] = "{id=503136,Text='奇怪，怎么有种不好的预感……',Speaker=813177,Emoji=19,}",
  [503137] = "{id=503137,Text='这里是——',Speaker=813177,Option='心灵猎场！',}",
  [503138] = "{id=503138,Text='就是在这里，可以获得让你变强的力量吗？',Speaker=813177,}",
  [503139] = "{id=503139,Text='…………',Speaker=813177,}",
  [503140] = "{id=503140,Text='既然来到这里了，不如陪我去祭拜一下姐姐吧！',Speaker=813177,}",
  [503141] = "{id=503141,Text='等等！有点不对劲！',Speaker=813177,}",
  [503142] = "{id=503142,Text='怎么回事？心灵猎场出现了裂痕？！',Speaker=813177,}",
  [503143] = "{id=503143,Text='执念……美味……吞噬………吞噬……',Speaker=813184,}",
  [503144] = "{id=503144,Text='吞噬执念力量的恶灵？快把它们消灭吧！',Speaker=813177,}",
  [503145] = "{id=503145,Text='总算修复了……',Speaker=813177,}",
  [503146] = "{id=503146,Text='心灵猎场与现实世界的界限竟然出现了裂隙……为什么？',Speaker=813177,}",
  [503147] = "{id=503147,Text='难不成是姐姐遗留下来的力量不够了？而且……',Speaker=813177,}",
  [503148] = "{id=503148,Text='我从裂隙中——感受到了不祥又熟悉的气息……',Speaker=813177,}",
  [503149] = "{id=503149,Text='不行，要赶快去剑碑调查一下！',Speaker=813177,}",
  [503150] = "{id=503150,Text='[c][ffff00][PlayerName][-][/c]……',Speaker=813175,}",
  [503151] = "{id=503151,Text='你怎么了，[c][ffff00][PlayerName][-][/c]？',Speaker=813177,Option='{你听到了吗？,0},{有人在叫我,0}',}",
  [503152] = "{id=503152,Text='有人叫你？我怎么没听到？',Speaker=813177,}",
  [503153] = "{id=503153,Text='[c][ffff00][PlayerName][-][/c]……',Speaker=813175,}",
  [503154] = "{id=503154,Text='（声音似乎是从剑碑的方向传来的……）',}",
  [503155] = "{id=503155,Text='这是——发生了什么？！',Speaker=813177,}",
  [503156] = "{id=503156,Text='（你感觉灵魂被抽离出身体，真是奇妙的感觉……）',}",
  [503157] = "{id=503157,Text='（然后感到自己似乎坠入了无尽的深渊……）',}",
  [503158] = "{id=503158,Text='（不知过了多久……）',}",
  [503159] = "{id=503159,Text='唉，你醒了？',Speaker=813177,}",
  [503160] = "{id=503160,Text='你总算是醒了……',Speaker=813177,Option='{这是哪里,0},{发生了什么,0}',}",
  [503161] = "{id=503161,Text='这里是剑碑内部的空间，是由姐姐的力量创造的空间……',Speaker=813177,}",
  [503162] = "{id=503162,Text='不知道为什么，我们的灵魂被拉扯到了这个空间内……心灵猎场里突然发生的一切，真是太奇怪了！',Speaker=813177,}",
  [503163] = "{id=503163,Text='我们必须尽快搞清楚发生了什么！',Speaker=813177,}",
  [503164] = "{id=503164,Text='嗯？那是什么？',Speaker=813177,Emoji=8,}",
  [503165] = "{id=503165,Text='嗯——我感觉到……这是姐姐的气息？！！',Speaker=813177,}",
  [503166] = "{id=503166,Text='不好！',Speaker=813177,Option='怎么了？',}",
  [503167] = "{id=503167,Text='这里竟然也出现了裂隙——难不成它们的目标是剑碑……或者说是姐姐？！',Speaker=813177,}",
  [503168] = "{id=503168,Text='可恶，究竟是谁……',Speaker=813177,}",
  [503169] = "{id=503169,Text='灵魂……新鲜……给我！',Speaker=813184,}",
  [503170] = "{id=503170,Text='当心，[c][ffff00][PlayerName][-][/c]！没有肉体的保护，一旦被击败会被这些恶灵吞噬的！',Speaker=813177,}",
  [503171] = "{id=503171,Text='啊这——怎么感觉那个光团变大了一些……',Speaker=813177,}",
  [503172] = "{id=503172,Text='怎么会这样，侵入的恶灵越来越多了！坚持住，[c][ffff00][PlayerName][-][/c]！',Speaker=813177,}",
  [503173] = "{id=503173,Text='灵魂……本源……吞噬！',Speaker=813185,}",
  [503174] = "{id=503174,Text='这光团、真是太诡异了……',Speaker=813177,}",
  [503175] = "{id=503175,Text='好熟悉的气息……我有种不祥的预感……',Speaker=813177,}",
  [503176] = "{id=503176,Text='小心！！！！！',Speaker=813177,}",
  [503177] = "{id=503177,Text='看来是个难缠的家伙！冒险者，稳定心神！不要让心象世界在冲击中崩溃！',Speaker=813177,}",
  [503178] = "{id=503178,Text='呃呜呜呜…………',Speaker=813180,}",
  [503179] = "{id=503179,Text='好可怕的怨气、好熟悉的感觉……',Speaker=813177,}",
  [503180] = "{id=503180,Text='呃呜呜呜……复仇……',Speaker=813180,}",
  [503181] = "{id=503181,Text='我想起来了——原来是他！',Speaker=813177,}",
  [503182] = "{id=503182,Text='这家伙叫怨念聚合体，是三百多年前就存在的邪恶亡灵……它被击败逃脱，不知所踪……而击败它的人，正是姐姐……',Speaker=813177,Option='般若？',}",
  [503183] = "{id=503183,Text='唔唔唔啊啊……般若……杀……复仇……',Speaker=813180,}",
  [503184] = "{id=503184,Text='这家伙，这三百年来看来是吞噬了不少怨念，比当年更强了！',Speaker=813177,}",
  [503185] = "{id=503185,Text='看来它是感觉到了心灵猎场里姐姐的力量，前来寻仇了……',Speaker=813177,}",
  [503186] = "{id=503186,Text='般若……般若……杀……',Speaker=813180,}",
  [503187] = "{id=503187,Text='嗷嗷嗷嗷嗷！！',Speaker=813180,}",
  [503188] = "{id=503188,Text='这个光团……应该与姐姐有关，不能让这个怪物靠近她！！',Speaker=813177,}",
  [503189] = "{id=503189,Text='[c][ffff00][PlayerName][-][/c]！！',Speaker=813177,}",
  [503190] = "{id=503190,Text='可恶啊……这家伙，怎么变得这么强……难道，就要终结于此了吗？',Speaker=813177,}",
  [503191] = "{id=503191,Text='这是？？',Speaker=813177,}",
  [503192] = "{id=503192,Text='姐姐！',Speaker=813177,}",
  [503193] = "{id=503193,Text='不对……虽然感觉很相似……',Speaker=813177,}",
  [503194] = "{id=503194,Text='一个以姐姐的力量为基石的……崭新的灵魂？',Speaker=813177,}",
  [503195] = "{id=503195,Text='这或许是我们扭转败局的机会！',Speaker=813177,}",
  [503196] = "{id=503196,Text='（力量恢复了？！）',}",
  [503197] = "{id=503197,Text='[c][ffff00][PlayerName][-][/c]！！你的体内有姐姐留下的力量……所以你与这个灵体的力量本就是同根同源的……',Speaker=813177,}",
  [503198] = "{id=503198,Text='现在，[c][ffff00][PlayerName][-][/c]，融合她的力量，与她合为一体吧！这是我们在此消灭怨念聚合体唯一的机会！',Speaker=813177,}",
  [503199] = "{id=503199,Text='使用这份合二为一的力量，战胜那个家伙吧！',Speaker=813177,}",
  [503200] = "{id=503200,Text='那家伙想要自爆！[c][ffff00][PlayerName][-][/c]，想想你新获得的能力！跟般若一样的能力！',Speaker=813177,Option='灵魂链接？！',}",
  [503201] = "{id=503201,Text='姐姐……',Speaker=813177,}",
  [503202] = "{id=503202,Text='[c][ffff00][PlayerName][-][/c]，看起来你还有些疑问？',Speaker=813177,}",
  [503203] = "{id=503203,Text='本源魔灵——超脱了这个世界生死轮回的规律，由纯粹的灵魂力量孕育出的灵体……',Speaker=813177,}",
  [503204] = "{id=503204,Text='它本来是传闻中才存在的东西，也许是心灵猎场这样特殊的空间才给了它诞生的可能……',Speaker=813177,}",
  [503205] = "{id=503205,Text='它诞生的基石姐姐留下的力量，而你体内“猎灵士”的力量也源自姐姐，这就是你能够与本源魔灵融为一体的原因……',Speaker=813177,}",
  [503206] = "{id=503206,Text='就把它当作是姐姐最后的馈赠吧——',Speaker=813177,}",
  [503207] = "{id=503207,Text='用这份力量，守护这个世界——留下姐姐，最强猎灵士般若存在过的证明吧！',Speaker=813177,}",
  [503208] = "{id=503208,Text='伟大的主人……',}",
  [503209] = "{id=503209,Text='我愿献上一切，',}",
  [503210] = "{id=503210,Text='换取您赐予力量。',}",
  [503211] = "{id=503211,Text='糖姐姐：不好，它在举行“降临仪式”，快阻止他！',}",
  [503212] = "{id=503212,Text='糖姐姐：啊！！！！',}",
  [503213] = "{id=503213,Text='千年之后的勇士……',}",
  [503214] = "{id=503214,Text='我终于，等到你们了。',}",
  [503215] = "{id=503215,Text='但是，这力量过于纯粹，根本不像是一个“生者”的灵魂……',Speaker=813177,}",
  [503216] = "{id=503216,Text='[c][ffff00]裂隙4引导[-][/c]',}",
  [503217] = "{id=503217,Text='[c][ffff00]姜饼城冒险任务引导[-][/c]',}",
  [503218] = "{id=503218,Text='[c][ffff00]科技树8引导[-][/c]',}",
  [503219] = "{id=503219,Text='[c][ffff00]蛋糕保卫战引导[-][/c]',}",
  [503220] = "{id=503220,Text='[c][ffff00]精炼+6引导[-][/c]',}",
  [503221] = "{id=503221,Text='[c][ffff00]强化引导[-][/c]',}",
  [503222] = "{id=503222,Text='[c][ffff00]科技树11引导[-][/c]',}",
  [503223] = "{id=503223,Text='[c][ffff00]天津町冒险任务又引导[-][/c]',}",
  [503224] = "{id=503224,Text='[c][ffff00]家园引导[-][/c]',}",
  [503225] = "{id=503225,Text='[c][ffff00]精炼+6引导[-][/c]',}",
  [503226] = "{id=503226,Text='[c][ffff00]无限回廊引导[-][/c]',}",
  [503227] = "{id=503227,Text='[c][ffff00]科技树15引导[-][/c]',}",
  [503228] = "{id=503228,Text='[c][ffff00]企业之都冒险任务引导[-][/c]',}",
  [503229] = "{id=503229,Text='[c][ffff00]企业之都蛋糕引导[-][/c]',}",
  [503230] = "{id=503230,Text='[c][ffff00]裂隙6引导[-][/c]',}",
  [503231] = "{id=503231,Text='[c][ffff00]科技树18引导[-][/c]',}",
  [503232] = "{id=503232,Text='[c][ffff00]企业冒险任务2引导[-][/c]',}",
  [503233] = "{id=503233,Voice='Npc_Quest/503233',Text='无论好坏、疾病还是健康，特里斯坦·乔治，你是否会始终尊重、爱护你的妻子。',Speaker=813225,}",
  [503234] = "{id=503234,Voice='Npc_Quest/503234',Text='哼，愚蠢的誓言。妻子只是丈夫的附庸，她才该向我献上全部的忠贞与臣服。',Speaker=813221,}",
  [503235] = "{id=503235,Voice='Npc_Quest/503235',Text='（悲伤）特里斯坦，你——！',Speaker=813222,}",
  [503236] = "{id=503236,Voice='Npc_Quest/503236',Text='（训斥）凯瑟琳！现在是海涅与乔治家族缔约的神圣时刻！',Speaker=813224,}",
  [503237] = "{id=503237,Voice='Npc_Quest/503237',Text='（训斥）别忘了你的身份，别忘了你的责任！',Speaker=813224,}",
  [503238] = "{id=503238,Voice='Npc_Quest/503238',Text='（悲伤）呜……抱歉，舅舅。我会记住的。',Speaker=813222,}",
  [503239] = "{id=503239,Voice='Npc_Quest/503239',Text='我……凯瑟琳·海涅，愿意为我的丈夫献上一切。',Speaker=813222,}",
  [503240] = "{id=503240,Voice='Npc_Quest/503240',Text='好，那么誓约成立，让我们为这对新人献上祝福！',Speaker=813225,}",
  [503241] = "{id=503241,Voice='Npc_Quest/503241',Text='什么，他竟然想陷害舅舅和派特？！我……我不能坐视不管。',Speaker=813222,}",
  [503242] = "{id=503242,Voice='Npc_Quest/503242',Text='盗取情报的人果然是你。凯瑟琳，你好大的胆子！',Speaker=813221,}",
  [503243] = "{id=503243,Voice='Npc_Quest/503243',Text='不然呢？难道要我坐视不理吗，放任你为了权力，伤害无辜的人吗？',Speaker=813222,}",
  [503244] = "{id=503244,Voice='Npc_Quest/503244',Text='呵，看来是我对你太放任，竟然让你觉得，棋子也可以教我做事。',Speaker=813221,}",
  [503245] = "{id=503245,Voice='Npc_Quest/503245',Text='来人，给我把王后关到地牢里去。让她明白，顺从，是她唯一要做和能做的事情。',Speaker=813221,}",
  [503246] = "{id=503246,Voice='Npc_Quest/503246',Text='放我出去，我是你们的王后陛下，我命令你们放我出去！',Speaker=813222,}",
  [503247] = "{id=503247,Voice='Npc_Quest/503247',Text='我要见国王陛下，我要见我的丈夫，求求你们，特里斯坦，你怎么可以这么对待你的妻子……',Speaker=813222,}",
  [503248] = "{id=503248,Voice='Npc_Quest/503248',Text='我会顺从，国王陛下。我永远不会忤逆您，永远不会……',Speaker=813222,}",
  [503249] = "{id=503249,Voice='Npc_Quest/503249',Text='这种小事都做不好，还想请求原谅？再有下次，就不是挨打这么简单了！',Speaker=813221,}",
  [503250] = "{id=503250,Voice='Npc_Quest/503250',Text='母后，您怎么哭了，是父王又凶你了吗？',Speaker=813229,}",
  [503251] = "{id=503251,Voice='Npc_Quest/503251',Text='（紧张）艾伦，你怎么来了！ 母后没事，只是摔了一跤。',Speaker=813222,}",
  [503252] = "{id=503252,Voice='Npc_Quest/503252',Text='艾伦不用担心。只要有艾伦，母后就不会再哭了……为了艾伦，什么样的折磨和痛苦我都承受得住。',Speaker=813222,}",
  [503253] = "{id=503253,Voice='Npc_Quest/503253',Text='（懵懂）嗯！那我就永远陪着母后，这样母后就能一直快乐，不用再哭了！',Speaker=813229,}",
  [503254] = "{id=503254,Voice='Npc_Quest/503254',Text='母后也会好好照顾艾伦的，母后会一直爱护你、保护你……',Speaker=813222,}",
  [503255] = "{id=503255,Voice='Npc_Quest/503255',Text='不让任何人伤害你……我发誓……我发誓！',Speaker=813222,}",
  [503256] = "{id=503256,Voice='Npc_Quest/503256',Text='国王陛下，阿伦到底是怎么死的？他真的只是被人误杀吗？',Speaker=813230,}",
  [503257] = "{id=503257,Voice='Npc_Quest/503257',Text='艾伦斯特，谁给你的胆子可以这么跟我说话？',Speaker=813221,}",
  [503258] = "{id=503258,Voice='Npc_Quest/503258',Text='现在正是乔治家族争夺王位的关键时期，少了一个劲敌，你应该高兴才是。 ',Speaker=813221,}",
  [503259] = "{id=503259,Voice='Npc_Quest/503259',Text='您说什么？难道阿伦的死真得和您有关？！！',Speaker=813230,}",
  [503260] = "{id=503260,Voice='Npc_Quest/503260',Text='哼，那是他们巴尔特家自找的。伊安那个奸猾的家伙，以为刺杀了你就能推阿伦登上王位。',Speaker=813221,}",
  [503261] = "{id=503261,Voice='Npc_Quest/503261',Text='可他的一举一动都在我的监视下，于是我二次买通了那名杀手……',Speaker=813221,}",
  [503262] = "{id=503262,Voice='Npc_Quest/503262',Text='听说阿伦死时，伊安那老小子都晕过去了。做父亲的亲手杀了自己的儿子，这出戏真是太棒了！哈哈哈哈！',Speaker=813221,}",
  [503263] = "{id=503263,Voice='Npc_Quest/503263',Text='你！你这个冷血的魔鬼！人命在你心里，就是可以随意践踏的东西吗？把我的朋友还给我！',Speaker=813230,}",
  [503264] = "{id=503264,Voice='Npc_Quest/503264',Text='你竟然敢打我！那就为你的愚蠢付出代价吧！',Speaker=813221,}",
  [503265] = "{id=503265,Voice='Npc_Quest/503265',Text='不、不！求求您陛下，快住手！',Speaker=813222,}",
  [503266] = "{id=503266,Voice='Npc_Quest/503266',Text='母后！',Speaker=813230,}",
  [503267] = "{id=503267,Voice='Npc_Quest/503267',Text='呃……',Speaker=813222,}",
  [503268] = "{id=503268,Voice='Npc_Quest/503268',Text='凯瑟琳，你这碍事的女人，你竟敢来妨碍我！该死、该死！',Speaker=813221,}",
  [503269] = "{id=503269,Voice='Npc_Quest/503269',Text='够了，不许再伤害她！',Speaker=813230,}",
  [503270] = "{id=503270,Voice='Npc_Quest/503270',Text='（不可思议的语气）艾……艾伦，你……',Speaker=813222,}",
  [503271] = "{id=503271,Voice='Npc_Quest/503271',Text='父、父王从栏杆上掉下去了……是我、我杀了他……',Speaker=813230,}",
  [503272] = "{id=503272,Voice='Npc_Quest/503272',Text='呵，弑杀国王是重罪……我，也会死吧？',Speaker=813230,}",
  [503273] = "{id=503273,Voice='Npc_Quest/503273',Text='不，我可怜的艾伦。你没有故意杀他，你只是想保护我……',Speaker=813222,}",
  [503274] = "{id=503274,Voice='Npc_Quest/503274',Text='安心吧，艾伦……母亲，绝不会让你出事的。无论用什么手段，母亲绝对会守护你的——绝对！',Speaker=813222,}",
  [503275] = "{id=503275,Voice='Npc_Quest/503275',Text='这就是凯瑟琳的心象世界，她想要隐藏的的记忆，会变成四块心象之镜的碎片散落在这里。',Speaker=825141,}",
  [503276] = "{id=503276,Voice='Npc_Quest/503276',Text='不过，凯瑟琳守护秘密的决心，比之前我们遇到的所有人都要大。',Speaker=825141,}",
  [503277] = "{id=503277,Text='因此，她的心象世界也更加危险。',Speaker=825141,}",
  [503278] = "{id=503278,Voice='Npc_Quest/503277',Text='你需要加倍小心，用周围的环境来躲避守护者，集齐四块心象之镜。',Speaker=825141,}",
  [503279] = "{id=503279,Speaker=825141,}",
  [503280] = "{id=503280,Voice='Npc_Quest/503278',Text='最后，将获得的心象之境镶嵌在心之晷上，我们就能看到王后陛下想要隐瞒的全部记忆。',Speaker=825141,}",
  [503281] = "{id=503281,Text='这是凯瑟琳的心象之镜，回忆从镜片折射的光芒中显现了出来······',Speaker=825141,}",
  [503282] = "{id=503282,Text='回忆的片段戛然而止\n成功收集到了一块心象之镜',}",
  [503283] = "{id=503283,Voice='Npc_Quest/503283',Text='王后陛下的心象世界里，居然摆放着这么大一幅画？',Speaker=825141,}",
  [503284] = "{id=503284,Voice='Npc_Quest/503284',Text='我也觉得有些奇怪，冒险者，用[c][ffff00]洞察[-][/c]好好调查一下这幅画和周围环境吧。',Speaker=825154,}",
  [503285] = "{id=503285,Voice='Npc_Quest/503285',Text='对了，别忘记高级军官能[c][ffff00]察觉[-][/c]出附身。',Speaker=825154,}",
  [503286] = "{id=503286,Voice='Npc_Quest/503286',Text='那有机会的话，还是直接附身到高级军官的身上吧。',Speaker=825141,}",
  [503287] = "{id=503287,Voice='Npc_Quest/503287',Text='这里有块地砖看起来怪怪的，可以仔细研究下。',Speaker=825141,}",
  [503288] = "{id=503288,Voice='Npc_Quest/503288',Text='画的后面居然隐藏了通道，冒险者我们快过去看看。',Speaker=825141,}",
  [503289] = "{id=503289,Voice='Npc_Quest/503289',Text='要小心这些地刺，心象世界的身体无法承受地刺带来的伤害，被碰到可就麻烦了。',Speaker=825154,}",
  [503290] = "{id=503290,Voice='Npc_Quest/503290',Text='通往心之晷的桥梁不见了，冒险者，我们要想办法修复旋转桥，才能寻找到所有的秘密。',Speaker=825154,}",
  [503291] = "{id=503291,Voice='Npc_Quest/503291',Text='冒险者，那边放着守卫一直在看守的物品，我们去把它取下来吧。',Speaker=825141,}",
  [503292] = "{id=503292,Voice='Npc_Quest/503292',Text='这应该就是通往记忆碎片的道路了，冒险者我们过去看看吧。',Speaker=825141,}",
  [503293] = "{id=503293,Voice='Npc_Quest/503293',Text='心象之纹使用后，还可以从台子上取下来，放在其它可以放置心象之纹的台子上。',Speaker=825154,}",
  [503294] = "{id=503294,Voice='Npc_Quest/503294',Text='那冒险者探索完一块区域后，尽量把能带走的心象之纹都带在身上吧。',Speaker=825141,}",
  [503295] = "{id=503295,Voice='Npc_Quest/503295',Text='又发现了和之前一样的画，冒险者快用洞察找找看附近有没有机关吧！',Speaker=825141,}",
  [503296] = "{id=503296,Voice='Npc_Quest/503296',Text='先在附近找找心象之纹吧，记得小心守卫。',Speaker=825141,}",
  [503297] = "{id=503297,Voice='Npc_Quest/503297',Text='画后面的通道出现了！冒险者我们快过去看看。',Speaker=825141,}",
  [503298] = "{id=503298,Voice='Npc_Quest/503298',Text='冒险者，从这里能看到对面那块地砖有些异样，走过去的时候可以留意一下。',Speaker=825154,}",
  [503299] = "{id=503299,Voice='Npc_Quest/503299',Text='这个人怎么一直盯着这幅画，我们要不要洞察一下？',Speaker=825141,}",
  [503300] = "{id=503300,Voice='Npc_Quest/503300',Text='这边也有地砖，冒险者我们去踩踩看。',Speaker=825141,}",
  [503301] = "{id=503301,Voice='Npc_Quest/503301',Text='快看！日之晷那边出现了一个门！',Speaker=825141,}",
  [503302] = "{id=503302,Voice='Npc_Quest/503302',Text='但我们还要修复另一半的旋转桥，才能走到那扇门前往日之晷，冒险者，我们继续加油吧！',Speaker=825154,}",
  [503303] = "{id=503303,Voice='Npc_Quest/503303',Text='隐藏的密室出现了，里面是心象之纹！',Speaker=825141,}",
  [503304] = "{id=503304,Voice='Npc_Quest/503304',Text='最后一块旋转桥也修复成功了！',Speaker=825141,}",
  [503305] = "{id=503305,Voice='Npc_Quest/503305',Text='干得漂亮冒险者！接下来就一鼓作气前往日之晷吧！',Speaker=825154,}",
  [503306] = "{id=503306,Voice='Npc_Quest/503306',Text='这个台子太高了，我们看看旁边有什么地方可走上去吧。',Speaker=825141,}",
  [503307] = "{id=503307,Voice='Npc_Quest/503307',Text='面前的地刺把路堵住了，看来只能从旁边小心绕过去了。',Speaker=825141,}",
  [503308] = "{id=503308,Voice='Npc_Quest/503308',Text='地刺消失了，我们可以不用走来时那条危险的路了！',Speaker=825141,}",
  [503309] = "{id=503309,Voice='Npc_Quest/503309',Text='旁边的小路也可以通行了，冒险者我们过去看看吧。',Speaker=825154,}",
  [503310] = "{id=503310,Text='……',}",
  [503311] = "{id=503311,Text='……',}",
  [503312] = "{id=503312,Text='我是“武器搜集神”赫萝克，想要了解“瓦尔哈拉之心”的秘密吗？',Speaker=813498,}",
  [503315] = "{id=503315,Text='[c][ffff00][PlayerName][-][/c]，你终于来啦！',Speaker=1050,}",
  [503316] = "{id=503316,Text='第2句',Speaker=1050,}",
  [503317] = "{id=503317,Text='第3句',Speaker=1050,}",
  [503318] = "{id=503318,Text='对了[c][ffff00][PlayerName][-][/c]，不知道你有没有点开过你的背包，那里面有个叫“[c][ffff00]任务手册[-][/c]”的好东西。',Speaker=1024,Option='{任务手册？,0},{那是什么,0}',}",
  [503319] = "{id=503319,Text='任务手册，是一本被灌注了米德加尔特神奇魔法的故事书。在这里，你可以清晰看到自己旅行的印记，相信我，你会非常需要它，赶快打开看看吧。',Speaker=1024,}",
  [503320] = "{id=503320,Text='是不是很神奇？来我这儿的冒险者都发誓，一定会把自己的任务手册填满，充满冒险的美好回忆。',Speaker=1024,}",
  [503321] = "{id=503321,Text='哦不过说到这个，你先去普隆德拉看看吧！',Speaker=1024,Option='没问题',}",
  [503322] = "{id=503322,Text='冒险者当心！',Speaker=813233,}",
  [503323] = "{id=503323,Text='[c][ffff00][PlayerName][-][/c]，巨大波利很危险，如果觉得对抗起来有难度，可以使用[c][ffff00]重击[-][/c]消除危险！',Speaker=813233,Option='重击？',}",
  [503324] = "{id=503324,Text='没错，这是非常有用的技能。那么就让本冒险女王来教教你吧！',Speaker=813233,}",
  [503325] = "{id=503325,Text='是不是很厉害！以后遇到类似的情况，都可以用重击消除危险！',Speaker=813233,}",
  [503326] = "{id=503326,Text='[c][ffff00]血迹树枝[-][/c]，我记得是很危险的树枝。等我们变得更强了再去找来玩吧。',Speaker=813233,}",
  [503327] = "{id=503327,Text='冒险者，救命啊！',Speaker=813233,}",
  [503328] = "{id=503328,Text='[c][ffff00][PlayerName][-][/c]，你看到小路上那个人了吗？，他就直挺挺得躺着，像是没有呼吸了！',Speaker=813233,}",
  [503329] = "{id=503329,Text='[c][ffff00][PlayerName][-][/c]，我们过去看看呢？如果他还有一线生机，我们还能救救他！',Speaker=813233,}",
  [503330] = "{id=503330,Text='对了，搜集材料有时候需要打很多魔物。觉得累的话，可以使用[c][ffff00]自动战斗[-][/c]……',Speaker=1054,Option='自动战斗？',}",
  [503331] = "{id=503331,Text='没错，这样可以让你轻松一点获得你所需要的掉落物。',Speaker=1054,}",
  [503332] = "{id=503332,Text='既然这么有缘分，我们就把这一刻用照相机拍下来做个纪念吧！',Speaker=813233,}",
  [503333] = "{id=503333,Text='嘿嘿，相机是共和国的雷根贝勒公司最伟大的发明之一！',Speaker=813233,}",
  [503334] = "{id=503334,Text='只要轻轻按一下，就能记录下你喜欢的东西！',Speaker=813233,}",
  [503335] = "{id=503335,Text='既然你这么喜欢，就把这台试验用的魔导照相机送你好了！',Speaker=813233,}",
  [503336] = "{id=503336,Text='哎，相机到处有，奇妙的邂逅可不是天天有的啊！',Speaker=813233,}",
  [503337] = "{id=503337,Text='别急着把相机收起来，你能帮忙做一件事么？',Speaker=813233,}",
  [503338] = "{id=503338,Text='我们一起在跨海大桥上拍一张照片吧，记录下这次神奇的旅行！',Speaker=813233,}",
  [503339] = "{id=503339,Text='来来来~你们两个站中间吧！记得把这美丽的跨海大桥拍全哦！',Speaker=813233,}",
  [503340] = "{id=503340,Text='都准备好了吗？',Speaker=813233,}",
  [503341] = "{id=503341,Text='对了[c][ffff00][PlayerName][-][/c]，我看到你的小伙伴在八音盒旁边等你。',Speaker=1054,Option='赛尼亚？',}",
  [503342] = "{id=503342,Text='没错，身边好像还有几位朋友，快过去看看吧。',Speaker=1054,}",
  [503343] = "{id=503343,Text='另外这是进城的[c][ffff00]通行证[-][/c]，第一次去普隆德拉需要将交给守卫登记。',Speaker=1054,Option='好的！',}",
  [503344] = "{id=503344,Text='这里这里！',Speaker=813233,}",
  [503345] = "{id=503345,Text='[c][ffff00][PlayerName][-][/c]，刚就看到你一直在忙，辛苦了。要不要休息下？',Speaker=813233,Option='{想休息,2},{没关系,3}',}",
  [503346] = "{id=503346,Text='嗯，一名优秀的冒险者懂得劳逸结合是好事。',Speaker=813234,Option='你是？',}",
  [503347] = "{id=503347,Text='冒险者果然和赛尼亚说得一样，充满活力和热情！但还是要学会适当休息。',Speaker=813234,Option='你是？',}",
  [503348] = "{id=503348,Text='这是我的好朋友赛依连，旁边是他可爱的女朋友玛嘉雷特！',Speaker=813233,}",
  [503349] = "{id=503349,Text='咳……咳咳！',Speaker=813234,}",
  [503350] = "{id=503350,Text='咳嗽什么，我有说错吗？',Speaker=813233,}",
  [503351] = "{id=503351,Text='[c][ffff00][PlayerName][-][/c]，我和赛依连都很高兴能认识你。最近的米德加尔特大陆有些不安的因素，但我相信你会是那束[c][ffff00]守护大家[-][/c]的光芒。',Speaker=813235,}",
  [503352] = "{id=503352,Voice='Npc/utopiafolks_female_1',Text='玛嘉雷特可是有预知梦能力的哦，她还说我一定会成为冒险女王！咩哈哈！',Speaker=813233,}",
  [503353] = "{id=503353,Text='[c][ffff00][PlayerName][-][/c]，不管是不是身兼重任，都要学会劳逸结合。看到旁边的八音盒了吗？',Speaker=813234,}",
  [503354] = "{id=503354,Text='不知道你有没有在意过一个叫[c][ffff00]战斗时长[-][/c]的东西。',Speaker=813234,Option='战斗时长？',}",
  [503355] = "{id=503355,Text='就是这里，如果每天的战斗超过了这个时长，你能获得的经验和掉落就会开始减少。这时候可以去八音盒那边听歌恢复体力，不过，这个体力回复也有上限，要注意。',Speaker=813234,}",
  [503356] = "{id=503356,Text='赛依连还专门给你和赛尼亚准备了唱片，[c][ffff00][PlayerName][-][/c]，可以去试试看哦！以后想要听歌也可以直接去商店购买。',Speaker=813235,Option='明白了',}",
  [503357] = "{id=503357,Text='我也有礼物！好耶！',Speaker=813233,}",
  [503358] = "{id=503358,Text='对了，刚维琪',Speaker=813233,}",
  [503359] = "{id=503359,Text='您好，首次前往普隆德拉需要您提交通行证！',Speaker=1008,Option='递交 ',}",
  [503360] = "{id=503360,Text='尊敬的[c][ffff00][PlayerName][-][/c]，欢迎你来到普隆德拉！愿你在普隆德拉收获永恒的快乐与爱！',Speaker=1008,Option='谢谢',}",
  [503361] = "{id=503361,Text='既然这么有缘分，我们就把这一刻用照相机拍下来做个纪念吧！',Speaker=813233,Option='相机？',}",
  [503362] = "{id=503362,Text='对了，刚维琪是不是给你了去普隆德拉的通行证，那就赶快去看看吧。普隆德拉是我最喜欢的城市没有之一。',Speaker=813233,}",
  [503363] = "{id=503363,Text='我还有点事情要去做，嘻嘻，那我们就在普隆德拉再见吧！',Speaker=813233,}",
  [503367] = "{id=503367,Text='对了，之前在南门的时候我说我有事情要忙，其实……嘿嘿，也是为了给你准备礼物！',Speaker=1050,Option='礼物？！',}",
  [503368] = "{id=503368,Voice='Npc/neula_8',Text='对啊！陪伴了你这么久的冒险女王，在送礼物上居然被赛依连他们抢了先，好不甘心！哼！',Speaker=1050,}",
  [503369] = "{id=503369,Text='所以！铛铛铛铛！我准备了一些[c][ffff00]扭蛋券[-][/c]送给你！',Speaker=1050,Option='扭蛋券？',}",
  [503370] = "{id=503370,Voice='Npc/neula_5',Text='是幻想马戏团和卡普拉公司联合推出的幻想创造器！可以获得很多有爱又有用的饰品！让我们漂漂亮亮去冒险吧！',Speaker=1050,}",
  [503372] = "{id=503372,Text='哦对了，说到冒险，为了能让你今后的旅行更加的便利，请允许我隆重介绍我们的[c][ffff00]卡普拉[-][/c]小姐姐！',Speaker=1050,}",
  [503373] = "{id=503373,Text='卡普拉是我们米德加尔特大陆非常重要的运输和存储公司。几乎所有的城市和野外都有他们的服务人员，可以帮助我们快速到达你已经去过地方。非常的方便和安全！还有存储功能，事不宜迟，赶紧去看看呢？',Speaker=1050,}",
  [503385] = "{id=503385,Text='嘿嘿是！我本来想和你一起去，但我的朋友跟我说，金字塔那边也出了些事情要我过去帮忙。',Speaker=1050,}",
  [503386] = "{id=503386,Text='哎呀，别愁眉苦脸的，我会给你写信报平安啦~[c][ffff00][PlayerName][-][/c]，接下来的冒险也要一切顺利哦！',Speaker=1050,}",
  [503387] = "{id=503387,Text='团伙作案还真厉害，他们抢完竟然分散逃走了！人员有限，我让部下去追赶西边的那群了，可是这边还有一大群怎么办啊？愁死我了！',Speaker=1090,Option='交给我吧',}",
  [503388] = "{id=503388,Text='真的吗？那太好了！不过那些蝗虫挺凶猛的，你要小心！',Speaker=1090,}",
  [503398] = "{id=503398,Text='冒险者，达纳托斯为了消灭魔王，守护世界，曾经冲进时空的裂隙中，与邪恶的力量战斗。',Speaker=1413,}",
  [503399] = "{id=503399,Text='既然你也拥有不同寻常无法解释的力量，那相信你也一定可以冲进裂缝中，切断危机的源头。',Speaker=1413,}",
  [503402] = "{id=503402,Text='冒险者，没想到你会获得这么了不起的东西！',Speaker=1413,Option='它的作用',}",
  [503403] = "{id=503403,Text='恕我不才，没有办法告诉你它的作用。但是我相信史芬斯老师一定知道答案，快去把这一切都告诉他吧！',Speaker=1413,}",
  [503406] = "{id=503406,Text='[c][ffff00][PlayerName][-][/c]，传说中霜巨人伊米尔的心脏因为邪恶之人的贪婪，碎成万片散落到了世界各地。它成为了整个世界力量的源泉。可以为善所用、也可以为恶助力。',Speaker=1067,}",
  [503407] = "{id=503407,Text='如此以来，伊米尔之心的碎片或许能以某种印记的形式出现在你的身边。[c][ffff00][PlayerName][-][/c]，尝试着与这片伊米尔之心建立羁绊吧！',Speaker=1067,Option='好的',}",
  [503408] = "{id=503408,Text='太神奇了！冒险者，你果然是非同一般的人。我想，你身上的这些神奇的羁绊，应该和魔王的封印松动有关。',Speaker=1067,}",
  [503419] = "{id=503419,Text='……',Speaker=813233,}",
  [503420] = "{id=503420,Text='依斯鲁得是附属于普隆德拉的卫星城市，在哪里你能了解到关于公会的更多信息，城防队的雷德会带你了解它。',Speaker=1118,}",
  [503426] = "{id=503426,Text='是公会派来的冒险者吗？沉船目前已经被控制了，成方队整忙着调查情况。',Speaker=1140,Option='艾里克让我来',}",
  [503429] = "{id=503429,Text='好友引导。好友引导结束后',Speaker=1140,}",
  [503432] = "{id=503432,Text='替我告诉母亲，我准备一下就出发了。',Speaker=1111,}",
  [503435] = "{id=503435,Text='具体的你可以去问问赛伊连和玛嘉雷特，他们很擅长和小动物沟通，赛依连还饲养了很多坐骑。他们一定会很热心帮你……只要你也能保护好你的宠物。',Speaker=1111,}",
  [503439] = "{id=503439,Text='……',}",
  [503440] = "{id=503440,Text='……',}",
  [503442] = "{id=503442,Text='……',}",
  [503451] = "{id=503451,Text='具体状况我也不是很清楚……………………………………………………………………………………',Speaker=1148,}",
  [503456] = "{id=503456,Text='这是蝴蝶翅膀……………………………………………………………………………………………………',Speaker=1156,Option='好哒',}",
  [503465] = "{id=503465,Text='嗯……既然是你推荐的……好吧，但我这里的检测工作一结束就要立即回吉芬的公会总部，没太多时间等他。让那小子马上过来吧，现在和我一起回吉芬。',Speaker=1110,}",
  [503466] = "{id=503466,Text='啊呀，暗恋，总有点害羞的，你懂的嘛~其实我偷偷挺喜欢[c][ffff00]飞空艇管理员 爱丽丝[-][/c]好久了，这次一走不知道什么时候回来，你能帮我把花送给她吗？',Speaker=1111,}",
  [503467] = "{id=503467,Text='等我成为伟大的魔法师后，马上就回来娶她。',Speaker=1111,}",
  [503477] = "{id=503477,Text='对了[c][ffff00][PlayerName][-][/c]，还记得南门[c][ffff00]八音盒[-][/c]的吗？[c][ffff00]普隆德拉[-][/c]也有个一模一样的哦，就在[c][ffff00]城市的正西方[-][/c]。如果你在城里想听歌恢复体力，完全不需要再跑去南门啦~',Speaker=1050,}",
  [503478] = "{id=503478,Text='真不愧是赛尼亚最喜欢的城市，普隆德拉真是太舒适便利了！住得越久越能发现它的好！',}",
  [503479] = "{id=503479,Text='你获得的这片[c][ffff00]勇气之心[-][/c]很不一般，尤其是在获得了达纳托斯赠与你的[c][ffff00]心之始源[-][/c]之后。',Speaker=1067,}",
  [503480] = "{id=503480,Text='这是！',}",
  [503483] = "{id=503483,Text='[c][ffff00][PlayerName][-][/c]，掉进时间空隙的你还好吗？',Speaker=1424,Option='达纳托斯？！',}",
  [503484] = "{id=503484,Voice='Npc/Thanatos_think_01',Text='确切的说，是我一片残存的魂魄，留在了被梦罗克力量影响的时空裂隙。也正因为如此，我才能有机会以这种方式和你见面。',Speaker=1424,}",
  [503485] = "{id=503485,Voice='Npc/Thanatos_pain_01',Text='但这里不是什么愉快的会面地点，你需要赶快找到引发裂隙被侵蚀的[c][ffff00]强大魔物[-][/c]，消灭它们并且离开这里。不然会有肉体消散的可能。',Speaker=1424,}",
  [503486] = "{id=503486,Text='但好在这些魔物都有不同的属性，可以通过[c][ffff00]克制属性[-][/c]造成超出预期的强大伤害！',Speaker=1424,}",
  [503487] = "{id=503487,Text='要尽快躲避不停下落的陨石，这是足以侵蚀这个空间的力量。',Speaker=1424,}",
  [503488] = "{id=503488,Text='就趁现在，一鼓作气制服虎王吧！',Speaker=1424,}",
  [503489] = "{id=503489,Voice='Npc/vox_emo_male_old_agree_02',Text='吼……谢谢，谢谢你们。我被虚空但可怕的力量禁锢于此，终于……终于可以解脱了……',Speaker=813239,}",
  [503490] = "{id=503490,Text='想要拯救时间与空间的旅行者啊！有些令人毛骨悚然的回应会再次出现在世间，它来自于你难以想象的黑暗深渊。',Speaker=813239,}",
  [503491] = "{id=503491,Voice='Npc/vox_emo_male_old_agree_02',Text='那强烈的，梦魇般的声音会吸引更多像我一样的灵魂，成为时间裂隙里吞噬一切的存在。旅行者，愿你能战胜未知之力，拯救这些灵魂，让他们也和我一样获得解脱。',Speaker=813239,}",
  [503492] = "{id=503492,Voice='Npc/Thanatos_think_01',Text='虎王说得没错，米德加尔特大陆上最近所发生的一切，似乎都在预示着曾经的危难将再度袭来。',Speaker=1424,}",
  [503493] = "{id=503493,Text='而你[c][ffff00][PlayerName][-][/c]，应该就是那个能拯救世人度过危难的命运般的存在。我终于找到了我一直存在于此的原因。',Speaker=1424,}",
  [503494] = "{id=503494,Text='这是[c][ffff00]心之始源[-][/c]，在米德加尔特大陆的土地上，会有几片伊米尔之心的碎片，能与它产生共鸣，赐予你霜巨人的祝福，获得对抗邪祟的力量。',Speaker=1424,}",
  [503495] = "{id=503495,Text='只可惜当时的我并没有时间与耐心去收集碎片，而是选择了另一条对抗邪恶的道路。但你和我不同，我想，或许你能用这件宝物，开启不一样的征程！',Speaker=1424,}",
  [503496] = "{id=503496,Voice='Npc/Thanatos_agree_01',Text='[c][ffff00][PlayerName][-][/c]赶快离开这里吧，相信以你的能力，一定能获得最终的胜利。',Speaker=1424,}",
  [503497] = "{id=503497,Text='不必担心，我不过是一片裂隙里残存的魂魄，裂隙平复后，我也有我想要前往的地方。',Speaker=1424,}",
  [503498] = "{id=503498,Text='但我也确实有种预感，有一天，我们会以另一种方式再相见！',Speaker=1424,}",
  [503500] = "{id=503500,Text='这……这里就是时空的裂隙？',Speaker=1156,Option='讲述情况',}",
  [503501] = "{id=503501,Text='我明白了，米德加尔特大陆的魔物我都很熟悉，我会尽我所能帮助你。',Speaker=1156,}",
  [503502] = "{id=503502,Text='呃啊……为了大海而努力的你们，感谢你们。但伸向海底的爪牙不会就此罢休！它会每时每刻在你的四周漂浮，让一切都沾染上毁灭世界的恶臭。',Speaker=813240,}",
  [503503] = "{id=503503,Text='决不能让这股力量得逞，来吧！让我用我最后的力量送你们出去，冲出海水浸泡的裂隙，去拯救大家吧。',Speaker=813240,}",
  [503541] = "{id=503541,Voice='Npc/vox_emo_female_puppy_joy_02',Text='[c][ffff00][PlayerName][-][/c]，你终于来啦！',Speaker=1050,}",
  [503542] = "{id=503542,Text='说好一起来报名乐园团的，结果让我先排队，自己却不见了……',Speaker=1050,Option='乐园团？',}",
  [503543] = "{id=503543,Voice='Npc/vox_emo_female_puppy_agree_02',Text='对啊！在普隆德拉冒险梦想的第一步，就是通过[c][ffff00]乐园团[-][/c]的考试啊！',Speaker=1050,}",
  [503544] = "{id=503544,Voice='Npc/vox_emo_female_puppy_agree_03',Text='毕竟没有任何战斗技巧和武器防具的[c][ffff00][PlayerName][-][/c]，到处乱跑还是非常危险的。',Speaker=1050,}",
  [503545] = "{id=503545,Voice='Npc/vox_emo_female_puppy_joy_01',Text='咱们得赶快成长成为一名伟大的冒险者，保护身边的伙伴！',Speaker=1050,Option='好的',}",
  [503546] = "{id=503546,Text='那就好……在你来之前我已经帮你登记好了，咱们就直接去找教官维琪进行考试审核吧！',Speaker=1050,Option='现在就考试',}",
  [503549] = "{id=503549,Text='旅行者，你已经下定了决心要通过乐园团的选拔，成为真正意义上冒险者了吗？',Speaker=1054,}",
  [503576] = "{id=503576,Voice='Npc/vox_emo_male_young_joy_01',Text='做得很好[c][ffff00][PlayerName][-][/c]！即使是短暂变回人类，我也非常感激！',Speaker=813244,}",
  [503579] = "{id=503579,Text='这是！！！',Speaker=813244,}",
  [503620] = "{id=503620,Text='啊！怎么回事？',Speaker=813250,}",
  [503623] = "{id=503623,Voice='Npc/vox_emo_male_gentle_suspect_02',Text='没有……[c][ffff00][PlayerName][-][/c]、赛尼亚，我想我要先离开去寻找拉比丝，不能让她伤害到无辜的人。',Speaker=1060,}",
  [503624] = "{id=503624,Text='接下来请前往商队所在的位置，那里是最后考试的场所。',Speaker=1060,}",
  [503626] = "{id=503626,Text='好的，你们也要注意安全，最近南门有些异样，我担心有污秽和扭曲的力量渗透到了这片土地，这种力量让[c][ffff00]时空裂隙[-][/c]产生，魔物开始变得不受控制。',Speaker=1060,Option='{时空裂隙？,0},{听起来好危险,0}',}",
  [503627] = "{id=503627,Text='没错，那是由一种虚空但可怕的力量产生的，时间与空间的黑暗深渊，已经很久没有出现过了。',Speaker=1060,}",
  [503647] = "{id=503647,Text='……',}",
  [503649] = "{id=503649,Text='……',}",
  [503652] = "{id=503652,Text='咳嗽什么，我有说错吗？对了，我刚才在跟[c][ffff00][PlayerName][-][/c]介绍你们身后的八音盒。',Speaker=813251,}",
  [503657] = "{id=503657,Text='那也要能通过最后的考试：[c][ffff00]护送宠物[-][/c]。',Speaker=813253,Option='护送宠物？',}",
  [503658] = "{id=503658,Voice='Npc/vox_emo_male_gentle_agree_01',Text='是的！现在全国上下各个组织和公会都在研究魔物异变的原因，寻找让他们恢复正常的办法。',Speaker=813253,}",
  [503659] = "{id=503659,Text='你和赛尼亚要利用学会的技巧，各自护送一只小巴风特宠物安全到达宠物商人卡博尔哪里，帮助他进行更深入的研究。',Speaker=813253,}",
  [503660] = "{id=503660,Text='这也太简单了吧~！',Speaker=813251,}",
  [503661] = "{id=503661,Text='别小瞧这个任务，护送的宠物身上会散发着一种奇特的粉尘，时不时会吸引到其他魔物！',Speaker=813253,}",
  [503662] = "{id=503662,Text='啊！说到这个……如果在护送过程中遇到很多魔物的话，可以使用[c][ffff00]自动战斗[-][/c]来解决它们哦。',Speaker=813256,Option='自动战斗？',}",
  [503663] = "{id=503663,Text='没错，在打斗或者获取掉落物的时候，自动战斗都是很好用的技巧哦。',Speaker=813256,Option='开始考试吧！',}",
  [503664] = "{id=503664,Text='哼，本想以后再告诉你们的，不过现在知道也不是坏事，快出发吧！我等你们的好消息。',Speaker=813253,}",
  [503669] = "{id=503669,Text='虽然比冒险女王赛尼亚晚了一点点，但确实还是很棒的！嘻嘻！',Speaker=813251,}",
  [503684] = "{id=503684,Voice='Npc/vox_emo_male_young_fear_03',Text='这是怎么回事！！！？',Speaker=1043,}",
  [503685] = "{id=503685,Voice='Npc/neula_8',Text='我的耳朵好痛……这个小巴风特，在发出奇怪的叫声。',Speaker=813252,}",
  [503686] = "{id=503686,Voice='Npc/neula_6',Text='这是什么可怕的魔物！？',Speaker=813252,}",
  [503687] = "{id=503687,Text='大家小心！',Speaker=813259,}",
  [503688] = "{id=503688,Text='维琪教官！',Speaker=813252,}",
  [503689] = "{id=503689,Voice='Npc/Nidhogg_angry_01',Text='糟了，要赶快阻拦皮里恩，不能让它伤害到王后，更不能让它就这样进入城内！这是真正的战斗，但我相信你可以做到！',Speaker=813259,Option='开始并肩作战！',}",
  [503690] = "{id=503690,Text='我去喊玛嘉雷特和赛伊连他们，来支援我们！',Speaker=813259,}",
  [503691] = "{id=503691,Text='使用投掷武器，从皮里恩的背后攻击它。',Speaker=813259,}",
  [503692] = "{id=503692,Text='这光亮？难道冒险者你……',Speaker=813259,}",
  [503693] = "{id=503693,Voice='Npc/sfx_cutscene_10143_01',Text='吼……要复活……吼……大人……',Speaker=63552,}",
  [503694] = "{id=503694,Text='阻挡……吼……吼……要消灭……',Speaker=63552,}",
  [503695] = "{id=503695,Voice='Npc/Nidhogg_angry_01',Text='结界把我们隔绝开了！[c][ffff00][PlayerName][-][/c]，先把皮里恩的手下消灭掉吧！',Speaker=813259,}",
  [503696] = "{id=503696,Text='远离……吼……要消灭……吼',Speaker=63552,}",
  [503697] = "{id=503697,Voice='Npc/vox_emo_male_young_fear_02',Text='这就是异变的皮里恩吗！',Speaker=813255,}",
  [503698] = "{id=503698,Text='哈沃德、玛嘉雷特、赛依连，你们终于赶到了！',Speaker=813259,}",
  [503699] = "{id=503699,Voice='Npc/vox_emo_male_young_agree_03',Text='看来，得用机器人试着将结界轰开个口子，把力量传递给冒险者！',Speaker=813255,}",
  [503725] = "{id=503725,Text='[c][ffff00][PlayerName][-][/c]辛苦了，我们都没想到你能这么顺利解除危险。',Speaker=813253,Option='最后的考试成绩？',}",
  [503732] = "{id=503732,Text='就别取笑我了，喏……坐骑就在边上，这只大嘴鸟叫利力。',Speaker=813253,}",
  [503733] = "{id=503733,Voice='Npc/Nidhogg_laugh_01',Text='旁边这个书包波利叫萌萌……是不是都很可爱？',Speaker=813256,}",
  [503739] = "{id=503739,Voice='Npc/Lasgrith_laugh_02',Text='[c][ffff00][PlayerName][-][/c]，今天辛苦了。不知道赛尼亚有没有告诉你一个恢复体力的好地方。',Speaker=1054,}",
  [503740] = "{id=503740,Voice='Npc/vox_emo_female_puppy_fear_01',Text='啊，我忘记说了！[c][ffff00][PlayerName][-][/c]可能连[c][ffff00]战斗时长[-][/c]还没意识到……',Speaker=813251,Option='{战斗时长？,0},{恢复体力？,0}',}",
  [503741] = "{id=503741,Voice='Npc/Lasgrith_think_01',Text='如果每天的战斗超过了这个时长，你能获得的经验和掉落就会开始减少。这时候可以来[c][ffff00]八音盒[-][/c]这边听歌恢复体力。',Speaker=1054,}",
  [503742] = "{id=503742,Text='乐园团的小伙伴给你和赛尼亚准备了用来恢复体力的唱片，可以去试试看，以后想要听歌也可以直接去商店购买，不过，这个体力恢复也有上限，要注意。',Speaker=1054,Option='明白了',}",
  [503743] = "{id=503743,Text='是不是很棒？冒险女王赛尼亚除了冒险，第二喜欢的就是音乐了！',Speaker=813251,}",
  [503753] = "{id=503753,Text='回南门记得来找我和拉比丝聊天啊！',Speaker=1060,}",
  [503763] = "{id=503763,Text='你们来了呀，这是我特地为你们准备唱片，放到边上的八音盒里就可以播放啦！音乐可是最好的治愈手段呢~',Speaker=2647,Option='谢谢',}",
  [503764] = "{id=503764,Text='小巴风特的力量好像被削弱了！[c][ffff00][PlayerName][-][/c]，就趁现在！！！',Speaker=813252,}",
  [503765] = "{id=503765,Text='好耶！！！[c][ffff00][PlayerName][-][/c]真得太棒了！',Speaker=813252,}",
  [503767] = "{id=503767,Text='……',Speaker=1054,}",
  [503771] = "{id=503771,Voice='Npc/neula_5',Text='[c][ffff00][PlayerName][-][/c]我一会带你去看看八音盒吧，正好就在附近， 你会更清楚一些。',Speaker=813251,Option='好的',}",
  [503772] = "{id=503772,Text='[c][ffff00][PlayerName][-][/c]我们一起过去吧。',Speaker=813251,}",
  [503773] = "{id=503773,Voice='Npc/Nidhogg_laugh_01',Text='啊~八音盒可是赛尼亚非常喜欢的地方呢！正好我这里有唱片，就送给你和赛尼亚吧！',Speaker=813256,Option='唱片的用处是？',}",
  [503774] = "{id=503774,Voice='Npc/utopiafolks_female_1',Text='是不是很棒？冒险女王赛尼亚除了冒险，第二喜欢的就是音乐了！',Speaker=813251,}",
  [503775] = "{id=503775,Text='这是用来[c][ffff00]恢复体力[-][/c]的唱片，放到八音盒那边就可以播放出音乐恢复体力啦~，以后想要听歌也可以直接去商店购买。',Speaker=813256,}",
  [503776] = "{id=503776,Voice='Npc/Nidhogg_agree_01',Text='嗯嗯！不过即便没有唱片也可以来休息。任何一个人播放了唱片，都会帮[c][ffff00]所有在附近的人[-][/c]恢复体力。',Speaker=813256,Option='真是太体贴了！',}",
  [503777] = "{id=503777,Text='没错呢！！不过，这个体力恢复[c][ffff00]也有上限[-][/c]，要注意哦~',Speaker=813256,}",
  [503779] = "{id=503779,Text='嘎！嘎~嘎嘎~！',Speaker=813266,}",
  [503780] = "{id=503780,Text='波利！波利！',Speaker=813267,}",
  [503787] = "{id=503787,Text='怎么会这样……呜……这种力量……',Speaker=1347,Option='觉醒仪式？',}",
  [503794] = "{id=503794,Text='冒险者，要坚持住啊！',Speaker=813255,}",
  [503795] = "{id=503795,Text='皮里恩有防护罩时是不会受伤的，先消灭它的手下吧！',Speaker=813259,}",
  [503796] = "{id=503796,Text='我的治疗术可以穿透结界来帮助你了！',Speaker=813256,}",
  [503797] = "{id=503797,Text='收好猎人引以为傲的风之步吧！战斗时不跑快点怎么行！',Speaker=813258,}",
  [503798] = "{id=503798,Text='就这样快速消灭小魔物吧！',Speaker=813258,}",
  [503799] = "{id=503799,Text='雷霆裁决法阵已开启，把皮里恩引诱进来解除无敌状态！',Speaker=813257,}",
  [503800] = "{id=503800,Text='最后，拿上剑士的力量，一举攻破皮里恩吧！',Speaker=813253,}",
  [503801] = "{id=503801,Text='大人……吼……预言之人……吼……出现了嘛？呜吼……',Speaker=63552,}",
  [503802] = "{id=503802,Voice='Npc/neula_9',Text='[c][ffff00][PlayerName][-][/c]，小心身后！',Speaker=813252,}",
  [503803] = "{id=503803,Voice='Npc/vox_emo_male_gentle_agree_03',Text='不到最后一刻，都不能放松警惕，不然，要么受伤，要么，就会把胜利拱手让给别人！',Speaker=813254,}",
  [503804] = "{id=503804,Text='记住了吗？[c][ffff00][PlayerName][-][/c]',Speaker=813254,}",
  [503805] = "{id=503805,Voice='Npc/vox_emo_male_deep_agree_03',Text='恭喜你[c][ffff00][PlayerName][-][/c]，我们成功了！',Speaker=813253,}",
  [503816] = "{id=503816,Text='那我们就赶快来合影吧！',Speaker=813252,}",
  [503822] = "{id=503822,Text='不过你们放心，离开前我会把顺利通过的成绩单交给二位。',Speaker=1060,}",
  [503824] = "{id=503824,Text='我这个可是红遍米德加尔特大陆的[c][ffff00]紫碟发带[-][/c]哦，不管过了多少年，都是最受大家喜爱的头饰，没有之一！',Speaker=813246,}",
  [503829] = "{id=503829,Text='这样吧，我再教你一个“[c][ffff00]紧急治疗[-][/c]”的技能，受伤时可以使用。去吧……',Speaker=813253,}",
  [503831] = "{id=503831,Text='小心！皮里恩突破了禁锢，又召唤了一批小怪……',Speaker=813258,}",
  [503833] = "{id=503833,Text='...',}",
  [503834] = "{id=503834,Text='...',}",
  [503835] = "{id=503835,Text='...',}",
  [503836] = "{id=503836,Text='...',}",
  [503837] = "{id=503837,Text='...',}",
  [503838] = "{id=503838,Text='...',}",
  [503839] = "{id=503839,Text='...',}",
  [503840] = "{id=503840,Text='...',}",
  [503849] = "{id=503849,Text='吾乃世界树饲育者，堕夜之救赎者，原初之龙尼德霍格。',Speaker=813275,}",
  [503850] = "{id=503850,Voice='Npc/Nidhogg_doubt_01',Text='凡人，你何故召唤吾？此地又是何处？',Speaker=813275,Option='{实现愿望！,2},{无限书库！,3}',}",
  [503851] = "{id=503851,Text='实现愿望……？这是……伊米尔之书和命运宝石……',Speaker=813275,}",
  [503852] = "{id=503852,Text='无限书库……？这是……伊米尔之书和命运宝石……',Speaker=813275,}",
  [503853] = "{id=503853,Voice='Npc/Nidhogg_agree_01',Text='原来如此，吾只是书中之影，而你是来实现吾之愿望的吧。',Speaker=813275,Option='{你怎么知道？,0},{你猜的没错,0}',}",
  [503854] = "{id=503854,Voice='Npc/Nidhogg_laugh_01',Text='不必感觉惊讶，吾是这个世界上最了解伊米尔之书和命运宝石的人之一。',Speaker=813275,}",
  [503855] = "{id=503855,Text='只是……只是……吾想知道……吾……还活着吗？',Speaker=813275,Option='{虽然活着,0},{但在沉睡,0}',}",
  [503856] = "{id=503856,Voice='Npc/Nidhogg_agree_01',Text='呼……那就好……吾的族人还在等着吾……',Speaker=813275,}",
  [503857] = "{id=503857,Voice='Npc/Nidhogg_agree_02',Text='那么，你可知[c][ffff00]永恒之花[-][/c]？',Speaker=813275,Option='{永恒之花？,0},{要做什么？,0}',}",
  [503858] = "{id=503858,Voice='Npc/Nidhogg_doubt_02',Text='你不是想要为吾实现愿望吗？吾的愿望便是找到永恒之花。',Speaker=813275,}",
  [503859] = "{id=503859,Voice='Npc/Nidhogg_think_02',Text='你会替吾找到的吧？会吧？会吧？这可是吾，最迫切的心愿。',Speaker=813275,Option='{会的会的,0},{我会尽力,0}',}",
  [503860] = "{id=503860,Voice='Npc/Nidhogg_think_01',Text='那么！哼哼～从现在开始！你就是[c][ffff00]吾的挚友[-][/c]！',Speaker=813275,Option='{好的！挚友！,0},{好吧……挚友……,0}',}",
  [503861] = "{id=503861,Text='冒险者，正在进行命运回响吗？',Speaker=813307,}",
  [503862] = "{id=503862,Text='我正好来无限书库查一些资料，没想到正好碰上你。这就是英雄之影啊……',Speaker=813307,}",
  [503863] = "{id=503863,Voice='Npc/Nidhogg_agree_01',Text='凡人，吾乃众生之长、天地之灵的……',Speaker=813275,Option='{龙族女王,0},{尼德霍格,0}',}",
  [503864] = "{id=503864,Text='没错！记住吾的名字了吗？凡人。',Speaker=813275,}",
  [503865] = "{id=503865,Text='原来是龙族女王……感谢您为保护伊米尔之书做出的贡献。',Speaker=813307,}",
  [503866] = "{id=503866,Voice='Npc/Nidhogg_doubt_01',Text='发生过那样的事吗？吾只是书中之影，没有你所说的记忆。',Speaker=813275,}",
  [503867] = "{id=503867,Voice='Npc/Nidhogg_agree_01',Text='不过，如果是保护伊米尔之书，这是吾的责任，不必言谢～',Speaker=813275,}",
  [503868] = "{id=503868,Text='不愧为命运宝石的守护者，那我就祝您的愿望能够实现。',Speaker=813307,Option='{说到愿望……,0},{永恒之花……,0}',}",
  [503869] = "{id=503869,Text='永恒之花？……我确实没有听说过。',Speaker=813307,}",
  [503870] = "{id=503870,Text='这是吾寻找多年未能找到的，[c][ffff00]永远不会凋零的花[-][/c]，你有所不知也是当然～',Speaker=813275,}",
  [503871] = "{id=503871,Text='不过，这里是无限书库，我可以试着帮你们查找一下是否有相关记载书籍。',Speaker=813307,}",
  [503872] = "{id=503872,Text='诶？真的吗？真的吗？如此大的书库！你可以吗？',Speaker=813275,Option='{这可是西芙大人,0},{他是贤者学院院长,0}',}",
  [503873] = "{id=503873,Text='是……是什么很厉害的人吗？好吧……很厉害的凡人，那就拜托你了。',Speaker=813275,}",
  [503874] = "{id=503874,Text='不用担心，这也是我工作的职责之一。不过，我需要你们二位的帮助，为两边的魔法宝石注入力量。',Speaker=813307,}",
  [503875] = "{id=503875,Voice='Npc/Nidhogg_agree_02',Text='这种小事，就交给吾和挚友吧！',Speaker=813275,}",
  [503876] = "{id=503876,Text='吾去左边，右边的宝石就交给你了。',Speaker=813275,}",
  [503877] = "{id=503877,Text='永恒之花………………………………找到了！',Speaker=813307,}",
  [503878] = "{id=503878,Text='找到了，记载着永恒之花的是一本名为[c][ffff00]《浮空的晨曦之旅》[-][/c]的诗歌集中。',Speaker=813307,Option='{能借给我们吗？,0},{我们能阅读吗？,0}',}",
  [503879] = "{id=503879,Text='这本书目前在吉芬图书馆，状态是……可借阅。',Speaker=813307,Option='{吉芬图书馆？,0},{不在这里吗？,0}',}",
  [503880] = "{id=503880,Text='无限书库的藏书虽然很多，但还不至于能被称为“无限”。与其说是书库，不如说是信息库。',Speaker=813307,}",
  [503881] = "{id=503881,Text='是大贤者巴特蒙德将米德加尔特大陆的所有藏书信息以魔力流动的形式汇总在此处。',Speaker=813307,}",
  [503882] = "{id=503882,Voice='Npc/Nidhogg_doubt_02',Text='这个巴特蒙德又是谁？居然能做到这样的事！是人类吗？这么厉害？',Speaker=813275,}",
  [503883] = "{id=503883,Text='是个……老怪物。好了，你们快前往吉芬图书馆吧，这本书在第九借阅室的第一书架上。',Speaker=813307,}",
  [503884] = "{id=503884,Text='第九借阅室，第一书架上！吾记住了！那，吾等就出发吧！',Speaker=813275,}",
  [503892] = "{id=503892,Voice='Npc/Nidhogg_doubt_01',Text='这里是书的作者创造的空间吗？',Speaker=813275,}",
  [503893] = "{id=503893,Text='不必惊慌，吾能感受到此处魔力流动十分平稳轻盈，不会有危险。',Speaker=813275,}",
  [503894] = "{id=503894,Text='这是一本幻象之书，眼见的一切即为书中所记录的。况且，有吾在，挚友无需担心。',Speaker=813275,}",
  [503895] = "{id=503895,Text='呀！是小妖精～跟上她看看！',Speaker=813275,}",
  [503896] = "{id=503896,Text='她似乎想要带吾等去什么地方……',Speaker=813275,}",
  [503897] = "{id=503897,Text='她是在祈祷什么吗？……',Speaker=813275,}",
  [503898] = "{id=503898,Text='难道永恒之花与信仰之力有关？',Speaker=813275,}",
  [503899] = "{id=503899,Text='唔……确实，吾从未想过依靠神的力量来达成愿望。',Speaker=813275,}",
  [503900] = "{id=503900,Text='这倒是个新思路，或许真能找到永恒之花。',Speaker=813275,}",
  [503901] = "{id=503901,Text='永恒之花，纯洁的永恒之花，',Speaker=813276,}",
  [503902] = "{id=503902,Text='[c][ffff00]晨曦[-][/c]的气息滋养着你，[c][ffff00]辉煌[-][/c]地绽放。',Speaker=813276,}",
  [503903] = "{id=503903,Text='你从古老的土壤中而来，去到崭新纪年，在[c][ffff00]伊始之地[-][/c]开花。',Speaker=813276,}",
  [503904] = "{id=503904,Text='[c][ffff00]奥丝塔拉[-][/c]，请收下永恒之花……',Speaker=813276,}",
  [503905] = "{id=503905,Text='吾等先离开这里吧……',Speaker=813275,}",
  [503916] = "{id=503916,Text='不可思议……实在是太不可思议了！[c][ffff00][PlayerName][-][/c]，你就是我们一直在寻找的那个人。',Speaker=1067,}",
  [503917] = "{id=503917,Text='有件名叫”心之始源“的宝物，在等待着与你见面。',Speaker=1067,Option='心之始源？',}",
  [503918] = "{id=503918,Text='传说中霜巨人[c][ffff00]伊米尔的心脏[-][/c]因为邪恶之人的贪婪，碎成万片散落到了世界各地。它成为了整个世界力量的源泉。可以为善所用、也可以为恶助力。',Speaker=1067,}",
  [503919] = "{id=503919,Text='你手中的[c][ffff00]“心之始源”[-][/c]就是其中一块。但它又有些不同。',Speaker=1067,}",
  [503920] = "{id=503920,Text='它会让你感受到所在区域名为[c][ffff00]“心之刹那”[-][/c]的能量，将他们搜集并且灌注到心之始源中，你会获得来自伊米尔的馈赠。',Speaker=1067,Option='大概明白了',}",
  [503921] = "{id=503921,Text='如此一来，伊米尔之心的碎片或许能以某种印记的形式出现在你的身边。[c][ffff00][PlayerName][-][/c]，尝试着与心之始源建立羁绊吧！',Speaker=1067,}",
  [503922] = "{id=503922,Text='去搜集心之刹那吧，守护世界者理应获得世界的祝福。当[c][ffff00]“勇气之心”[-][/c]灌注满后，记得来找我。',Speaker=1067,Option='没问题',}",
  [503923] = "{id=503923,Text='孩子，看来你的“勇气之心”还没有灌注满，再去努力寻找吧。',Speaker=1067,}",
  [503924] = "{id=503924,Text='[c][ffff00][PlayerName][-][/c]，点亮的勇气之心是不是带给你了祝福。接下来只需要按照我说的，再次与心之始源建立羁绊，就能开启“试炼之心”。',Speaker=1067,}",
  [503925] = "{id=503925,Text='去搜集心之刹那吧，守护世界者理应获得世界的祝福。当[c][ffff00]“试炼之心”[-][/c]灌注满后，记得再来找我。',Speaker=1067,Option='没问题',}",
  [503926] = "{id=503926,Text='孩子，看来你的“试炼之心”还没有灌注满，再去努力寻找吧。',Speaker=1067,}",
  [503927] = "{id=503927,Text='[c][ffff00][PlayerName][-][/c]，点亮的试炼之心是不是带给你了祝福。接下来只需要按照我说的，再次与心之始源建立羁绊，就能开启“纯洁之心”。',Speaker=1067,}",
  [503928] = "{id=503928,Text='去搜集心之刹那吧，守护世界者理应获得世界的祝福。当[c][ffff00]“纯洁之心”[-][/c]灌注满后，记得再来找我。',Speaker=1067,Option='没问题',}",
  [503929] = "{id=503929,Text='孩子，看来你的“纯洁之心”还没有灌注满，再去努力寻找吧。',Speaker=1067,}",
  [503930] = "{id=503930,Text='[c][ffff00][PlayerName][-][/c]，点亮的纯洁之心是不是带给你了祝福。接下来只需要按照我说的，再次与心之始源建立羁绊，就能开启“忠贞之心”。',Speaker=1067,}",
  [503931] = "{id=503931,Text='去搜集心之刹那吧，守护世界者理应获得世界的祝福。当[c][ffff00]“忠贞之心”[-][/c]灌注满后，记得再来找我。',Speaker=1067,Option='没问题',}",
  [503932] = "{id=503932,Text='孩子，看来你的“忠贞之心”还没有灌注满，再去努力寻找吧。',Speaker=1067,}",
  [503933] = "{id=503933,Text='[c][ffff00][PlayerName][-][/c]，点亮的忠贞之心是不是带给你了祝福。接下来只需要按照我说的，再次与心之始源建立羁绊，就能开启最后的“守护之心”。',Speaker=1067,}",
  [503934] = "{id=503934,Text='去搜集心之刹那吧，守护世界者理应获得世界的祝福。当[c][ffff00]“守护之心”[-][/c]灌注满后，记得再来找我。',Speaker=1067,Option='没问题',}",
  [503935] = "{id=503935,Text='孩子，看来你的“守护之心”还没有灌注满，再去努力寻找吧。',Speaker=1067,}",
  [503936] = "{id=503936,Text='真不愧是[c][ffff00][PlayerName][-][/c]，全部点亮的心之始源可真是光彩夺目。仅仅只是看一眼，都能感受到温暖的力量。',Speaker=1067,}",
  [503937] = "{id=503937,Text='古书上说“心之始源照亮混沌无序，命运之光得以绽放与长存”。我想，是时候获得心之始源最终的祝福了。',Speaker=1067,Option='该怎么做',}",
  [503938] = "{id=503938,Text='作为唯一能和心之始源建立羁绊的[c][ffff00][PlayerName][-][/c]，就顺从内心，和心之始源共鸣就好。',Speaker=1067,}",
  [503939] = "{id=503939,Text='孩子，带着心之始源的力量继续旅行吧，是你的话，一定可以守护好我们深爱的世界，我一直这么坚信着。',Speaker=1067,}",
  [503940] = "{id=503940,Text='奥斯塔拉……',}",
  [503941] = "{id=503941,Voice='Npc/Nidhogg_agree_02',Text='吾听见了你的召唤，挚友……',Speaker=813275,}",
  [503942] = "{id=503942,Text='今日，你该与吾一同前往艾卡拉奇，寻找永恒之花。',Speaker=813275,}",
  [503943] = "{id=503943,Voice='Npc/Nidhogg_think_02',Text='吾……有些担心，如果这次还是没能找到的话……',Speaker=813275,Option='为什么要寻找永恒之花',}",
  [503944] = "{id=503944,Text='……其实这也不是什么不能说的，吾有一个妹妹，名为维德佛尔尼尔。',Speaker=813275,Option='{我知道！,0},{我见过！,0}',}",
  [503945] = "{id=503945,Voice='Npc/Nidhogg_agree_01',Text='嗯？看来，吾与挚友曾经的关系也是相当不错的嘛～那你知道她的来历吗？',Speaker=813275,Option='{不太清楚,0},{不是妹妹？,0}',}",
  [503946] = "{id=503946,Text='准确说，她是从吾体内分裂出来，带着原本属于吾的[c][ffff00]凋零之力[-][/c]诞生于这个世上的。',Speaker=813275,}",
  [503947] = "{id=503947,Voice='Npc/Nidhogg_think_01',Text='她是吾的分身，亦流淌着吾的血液，但是，与吾的生命之力不同……',Speaker=813275,}",
  [503948] = "{id=503948,Text='凋零之力只会带来毁灭与死亡，其他生灵忌惮她厌恶她。',Speaker=813275,}",
  [503949] = "{id=503949,Voice='Npc/Nidhogg_think_02',Text='甚至她无法碰触她最喜欢的东西——花朵，她经常远远的看着，很是伤心。',Speaker=813275,}",
  [503950] = "{id=503950,Text='吾想找到一朵不受凋零之力的影响，永远盛开的花，让维德不再难过。',Speaker=813275,Option='{一定会找到,0},{希望可以找到,0}',}",
  [503951] = "{id=503951,Voice='Npc/Nidhogg_agree_01',Text='嗯！那么，挚友，你准备好了吗？吾等要出发了！',Speaker=813275,Option='{立刻出发,2},{再等一下,3}',}",
  [503976] = "{id=503976,Text='这里四周都是瀑布，水分充足，很适合花朵生长。',Speaker=813275,}",
  [503977] = "{id=503977,Text='另外这里的空气中有着非常蓬勃的生命之力。',Speaker=813275,}",
  [503978] = "{id=503978,Text='呀！那个神神叨叨的小家伙在前面！',Speaker=813275,}",
  [503979] = "{id=503979,Text='我不知道这些花是你们的！星辰在上，我都没碰过它！',Speaker=813277,}",
  [503980] = "{id=503980,Text='我也不知道它怎么就枯萎了！这样冤枉我！星辰会惩罚你们的！',Speaker=813277,}",
  [503981] = "{id=503981,Text='异乡人！快来帮助我赶走它们！',Speaker=813277,}",
  [503983] = "{id=503983,Text='都怪我出门的时候太着急，没看星象，看来今天不宜出门。',Speaker=813277,Option='{发生了什么,0},{星空之眼呢,0}',}",
  [503984] = "{id=503984,Text='哎，我好不容易在这发现一朵星空之眼，刚想靠近，发现这朵花耷拉着脑袋。',Speaker=813277,}",
  [503985] = "{id=503985,Text='然后这群米米怪就突然出现了，好像是在责问我为什么要弄坏它们的花！',Speaker=813277,}",
  [503986] = "{id=503986,Text='嗯……米米怪是靠吸食花中的生命之力存活的物种。',Speaker=813275,}",
  [503987] = "{id=503987,Voice='Npc/Nidhogg_agree_02',Text='而这朵星空之眼中的生命之力它们被过度吸食所以才会这样。',Speaker=813275,}",
  [503988] = "{id=503988,Voice='npc/Nidhogg_sigh_01',Text='能被米米怪吸食生命之力而枯萎的花，看来这并不是吾要找的永恒之花。',Speaker=813275,}",
  [503989] = "{id=503989,Text='挚友，吾等该去寻找下一朵花了。',Speaker=813275,}",
  [503990] = "{id=503990,Text='吾曾有过一片花园，吾亲力亲为照料着里面的花。',Speaker=813275,}",
  [503991] = "{id=503991,Text='那时唯一陪伴吾的生命，除了天地树就是它们了。',Speaker=813275,}",
  [503992] = "{id=503992,Text='直到梦罗克这个坏家伙的出现……然后再是维德……',Speaker=813275,}",
  [503993] = "{id=503993,Text='啊！前面的是不是那个崇拜苍夜公主的小家伙？',Speaker=813275,}",
  [503994] = "{id=503994,Text='是你们！你们看，这苍夜花洁白无暇，从岩石中破出，洁白而又坚韧！',Speaker=813284,}",
  [503995] = "{id=503995,Text='这世上没有任何的语言可以表达我现在的心情！',Speaker=813284,}",
  [503996] = "{id=503996,Text='能帮我采摘这些苍夜花吗？我实在太激动了，手有些抖，怕把花弄伤了！',Speaker=813284,}",
  [503997] = "{id=503997,Text='这是最古老的花种之一，也是吾培育出来的……',Speaker=813275,}",
  [503998] = "{id=503998,Text='虽然改了名字，但苍夜花也不是吾要找的永恒之花……',Speaker=813275,}",
  [503999] = "{id=503999,Text='苍夜花……这也太美了！这就是永恒之花啊！',Speaker=813284,}",
  [504000] = "{id=504000,Text='苍夜公主……哦……我的苍夜公主……',Speaker=813284,}",
  [504001] = "{id=504001,Text='没想到，这种花中竟然有一朵孕育出了一个浮空精灵。',Speaker=813275,}",
  [504002] = "{id=504002,Voice='Npc/Nidhogg_laugh_01',Text='知道这样的事，吾还是挺高兴的～哼哼哼～吾也算是创造了一个生命～～',Speaker=813275,}",
  [504003] = "{id=504003,Voice='Npc/Nidhogg_think_02',Text='嗯哼哼～那么最后一朵奥斯塔拉花会不会是吾寻找的永恒之花呢！',Speaker=813275,}",
  [504004] = "{id=504004,Text='奥斯塔拉，晨曦女神……在诸神黄昏后就没了她的消息……',Speaker=813275,}",
  [504005] = "{id=504005,Text='如果吾没有记错，她是浮空精灵一族最初的信仰……',Speaker=813275,}",
  [504006] = "{id=504006,Text='诶，那边尽头的树干上好像站着一个精灵！',Speaker=813275,}",
  [504007] = "{id=504007,Text='啊,你们也跟过来了，如你们所见，这就是奥斯塔拉花。',Speaker=813278,}",
  [504008] = "{id=504008,Text='但是……我忘记带浮光石过来了，这样的话我没办法把它带走。',Speaker=813278,Option='为什么？',}",
  [504009] = "{id=504009,Text='奥斯塔拉花如果离开了光线，就会枯萎，必须用浮光石让它保持绽放。',Speaker=813278,}",
  [504010] = "{id=504010,Voice='Npc/Nidhogg_think_01',Text='原来如此，将光之力转化为生命之力，看来这也不是永恒之花。',Speaker=813275,}",
  [504011] = "{id=504011,Text='奥斯塔拉花就是永恒之花！光永远不会消失，那奥斯塔拉花就不会凋谢！',Speaker=813278,}",
  [504012] = "{id=504012,Text='不和你们说了！我回去取浮光石！',Speaker=813278,}",
  [504013] = "{id=504013,Text='……依靠外力而生存的事物，是永远无法触及永恒的。',Speaker=813275,}",
  [504014] = "{id=504014,Text='抱歉，打扰了，冒险者，我听到你们在讨论永恒之花……',Speaker=813279,}",
  [504015] = "{id=504015,Text='你偷听吾等说话！说来听听，你对于永恒之花有何见解？',Speaker=813275,}",
  [504016] = "{id=504016,Text='对……对不起……见解说不上……但是，我知道永恒之花是什么！',Speaker=813279,}",
  [504017] = "{id=504017,Text='不过……说来话长……布兰迪斯大人急着要我把东西给他送过去……',Speaker=813279,}",
  [504018] = "{id=504018,Text='嗯……那……你们之后有空了的话，来[c][ffff00]晨星殿堂[-][/c]找我！一定要来找我啊！',Speaker=813279,}",
  [504019] = "{id=504019,Voice='Npc/Nidhogg_doubt_01',Text='是吾沉睡太久了？……以前的浮空精灵们似乎没有这么古怪啊……',Speaker=813275,}",
  [504020] = "{id=504020,Text='吾等也先离开这里回到城内，再做打算吧。',Speaker=813275,}",
  [504024] = "{id=504024,Text='唔……要怎么把它带走呢……',Speaker=813278,}",
  [504025] = "{id=504025,Voice='Npc/Nidhogg_laugh_01',Text='挚友，又见面了。看来你已经做好了再次与吾同行的准备。',Speaker=813275,}",
  [504026] = "{id=504026,Text='如果吾没有记错，吾等需去寻找一个自称知道永恒之花的浮空精灵。',Speaker=813275,Option='{是的,0},{她叫莱拉,0}',}",
  [504027] = "{id=504027,Text='那天的三个精灵所说的都不是真正的永恒之花，不知她是否能为吾找到答案……',Speaker=813275,}",
  [504061] = "{id=504061,Text='挚友，将它小心摘下，吾的愿望终于要实现了。',Speaker=813275,}",
  [504062] = "{id=504062,Text='龙丫头……命运之风小崽子……过来……',Speaker=813281,}",
  [504063] = "{id=504063,Voice='Npc/Nidhogg_doubt_02',Text='原本时间花园的时间流逝就异常的缓慢，现在却是完全停滞了。究竟发生了什么……',Speaker=813275,Option='{你有没有听到,0},{一个熟悉的声音,0}',}",
  [504064] = "{id=504064,Text='吾听到了……龙丫头……敢这样称呼吾的……不会吧？……',Speaker=813275,Option='{你知道是谁？,2},{我知道是谁,3}',}",
  [504065] = "{id=504065,Text='智慧巨人[c][ffff00]密米尔[-][/c]……一个难缠的臭老头。',Speaker=813275,}",
  [504066] = "{id=504066,Voice='Npc/Nidhogg_doubt_01',Text='你也认识这个[c][ffff00]密米尔[-][/c]这个臭老头吗？',Speaker=813275,}",
  [504067] = "{id=504067,Text='可是，那个臭老头还留在这里吗？走吧，现在只能看看他要做什么了。',Speaker=813275,}",
  [504068] = "{id=504068,Text='你们两个小家伙！瞧瞧，在我的花园里都干了什么！？',Speaker=813281,}",
  [504069] = "{id=504069,Voice='Npc/Nidhogg_angry_02',Text='谁是小家伙！吾可是……吾可是……',Speaker=813275,}",
  [504070] = "{id=504070,Text='知道啦知道啦！可是我第一次见你的时候，你还是那么小的一只小紫龙，和个小泥鳅似的。',Speaker=813281,}",
  [504071] = "{id=504071,Text='吾！吾乃原初之龙！什么小泥鳅！臭老头！！你这被埋在土里样子，肯定是因为你这张嘴！',Speaker=813275,}",
  [504072] = "{id=504072,Text='…………………………咳咳咳……这么久没见，一来就戳老头子我的伤心事。',Speaker=813281,}",
  [504073] = "{id=504073,Text='行了行了，和[c][ffff00]一纸书页[-][/c]也没什么好计较的，等你丫头真的睡醒了我再来好好和你说说。',Speaker=813281,}",
  [504074] = "{id=504074,Text='[c][ffff00][PlayerName][-][/c]小崽子，不愧是命运之风啊，伊米尔之书的书页听说都被你吹没了？',Speaker=813281,Option='{……对不起,0},{……不是我！,0}',}",
  [504075] = "{id=504075,Text='哎……遥想当年啊……老头我啊……早就和我那三个不争气的女儿说……这书啊……脆弱的很……',Speaker=813281,}",
  [504076] = "{id=504076,Text='停！别回忆过去了。吾还有事要做，快放吾等出去。',Speaker=813275,}",
  [504077] = "{id=504077,Text='我可什么都没做，还不是你们偷偷摸摸地在时间花园里摘花，导致时间流逝的异常。',Speaker=813281,}",
  [504078] = "{id=504078,Text='等着吧，幸好你们破坏的不多，过一会就好了。',Speaker=813281,}",
  [504079] = "{id=504079,Text='等等……摘花……龙丫头！你不会还在寻找永恒之花吧！可小维德她……',Speaker=813281,}",
  [504080] = "{id=504080,Voice='Npc/Nidhogg_think_01',Text='维德还在等吾将永恒之花带回去，吾答应过她的！',Speaker=813275,}",
  [504081] = "{id=504081,Text='……好吧……可时间花园里的花，并不是你所寻找的永恒之花哦！',Speaker=813281,}",
  [504082] = "{id=504082,Text='……从这里的花上我确实感受到了时间的停滞。',Speaker=813275,}",
  [504083] = "{id=504083,Text='没错，因为这里的花是时间凝结而成的，并不是真正的花。',Speaker=813281,}",
  [504084] = "{id=504084,Text='它们是维系神与浮空精灵的约定——长久的寿命与容颜的魔法。',Speaker=813281,}",
  [504085] = "{id=504085,Text='至于为什么是花的模样……大约是神族的那群讲究的家伙觉得比较好看？',Speaker=813281,}",
  [504086] = "{id=504086,Voice='Npc/Nidhogg_doubt_02',Text='那……浮空精灵在晨曦祭典上献上的永恒之花是什么？',Speaker=813275,}",
  [504087] = "{id=504087,Text='哦吼吼……你们说的那个呀……不如，你们亲自问问……',Speaker=813281,Option='{问谁？,0},{怎么问？,0}',}",
  [504088] = "{id=504088,Text='吼吼吼~一会你们就知道了~这附近应该有散落的音符，把它们收集起来吧。',Speaker=813281,}",
  [504089] = "{id=504089,Text='嗯嗯……那么，接下来……',Speaker=813281,}",
  [504090] = "{id=504090,Text='吾对她有印象……是……《浮空的晨曦之旅》里的浮空精灵！可……她的鳞翅呢……？',Speaker=813275,}",
  [504091] = "{id=504091,Text='她是浮空精灵的公主，苍夜。不过这个身份对她来说是很久已经的事情了。',Speaker=813281,}",
  [504092] = "{id=504092,Text='怎会如此！浮空精灵不是拥有无尽的生命与不会老去的容颜吗？',Speaker=813275,}",
  [504093] = "{id=504093,Text='[c][ffff00]没有任何生命会是永恒的[-][/c]，小家伙们。',Speaker=813281,}",
  [504094] = "{id=504094,Text='好了，苍夜，不……灵魂奏者，献给晨曦的永恒之花是什么？',Speaker=813281,}",
  [504095] = "{id=504095,Text='浮空精灵从来到这天地树的枝灌，女神将晨曦之光赐予了这片黑暗的领域。',Speaker=813282,}",
  [504096] = "{id=504096,Text='微风从[c][ffff00]古老的花园[-][/c]中吹来了种子，巨人让花朵绽放点缀了这片荒芜的土地。',Speaker=813282,}",
  [504097] = "{id=504097,Text='这里被称作辉煌领域，这片土地就是精灵们献给晨曦的永恒之花。',Speaker=813282,}",
  [504098] = "{id=504098,Text='（演奏）永恒之花，纯洁的永恒之花，晨曦的气息滋养着你，辉煌地绽放。',Speaker=813282,}",
  [504099] = "{id=504099,Text='（演奏）你从古老的土壤中而来，去到崭新纪年，在伊始之地开花。',Speaker=813282,}",
  [504100] = "{id=504100,Text='龙丫头，你懂了吗？晨曦祭典上，浮空精灵们要献上的是这片[c][ffff00]土地的生机[-][/c]。',Speaker=813281,}",
  [504101] = "{id=504101,Text='感谢神给予的土地，祈祷故乡生生不息，这是苍夜举办晨曦祭典的初衷。',Speaker=813281,}",
  [504102] = "{id=504102,Voice='Npc/Nidhogg_think_01',Text='所以，这里也不存在永恒之花是吗？……',Speaker=813275,}",
  [504103] = "{id=504103,Text='[c][ffff00]没有任何生命会是永恒的[-][/c]。好了，时间的异常也差不多要结束了……我累了……要睡了……',Speaker=813281,}",
  [504104] = "{id=504104,Text='你们两个小家伙别再折腾老头子我的花园了……小妖精们可是要生气了……Zzzz……',Speaker=813281,}",
  [504105] = "{id=504105,Text='Zzzzzzzz……',Speaker=813281,}",
  [504110] = "{id=504110,Text='[c][ffff00][PlayerName][-][/c]，快过来~我有一样东西要给你。\n刚才冒险者协会的小伙子交给我一沓手册，说是什么……《执事课题》。',Speaker=1694,}",
  [504111] = "{id=504111,Text='这里头记录了许多任务，完成后不仅可以获得神秘奖励，还能帮你快速适应冒险生活。不过其他手册我都分发完了，你只能拿到我手中的最后一份。',Speaker=1694,}",
  [504112] = "{id=504112,Text='确认现在领取的话，以后同一账号的其他角色都无法再领取手册~你想现在就获得《执事课题》吗？',Speaker=1694,Option='{现在领取,3},{再考虑一下,2}',}",
  [504113] = "{id=504113,Text='好。那在你考虑清楚之前，我会替你保管这本手册的。',Speaker=1694,}",
  [504114] = "{id=504114,Text='既然决定收下手册，就务必要好好完成哦~完成所有课题后的最终奖励，不会让你失望的！',Speaker=1694,}",
  [504115] = "{id=504115,Text='课题已经全部完成了！？[c][ffff00][PlayerName][-][/c]果然是真正的冒险家，太厉害了！',Speaker=1694,}",
  [504116] = "{id=504116,Text='还有说好的神秘奖励呢！我这就拿给你~',Speaker=1694,Option='谢谢',}",
  [504117] = "{id=504117,Text='罪人已经得到审判，他们永远无法从虚空领域逃脱！',Speaker=826620,}",
  [504125] = "{id=504125,Text='祝你好运冒险家！这是一点微薄奖励，愿你在虚空领域所向披靡！',Speaker=826620,}",
  [504126] = "{id=504126,Text='初入卢恩王国的冒险者与同为初心者的赛尼亚在普隆德拉南门相遇，身为“命运之风”的冒险者，即将在辽阔而神秘的米德加尔特大陆开启一段不同寻常的传奇冒险。\n为了获得正式冒险的资格，冒险者与赛尼亚一起在南门接受了乐园团的考试。\n乐园团的考试共有三项，在一次次的考验中，冒险者真切感受到了自己的成长。严格的考试通过后，冒险者与赛尼亚成功获得了认可，并结伴来到冒险的圣地：普隆德拉。\n在普隆德拉，冒险者得以与各式各样的人进行交流，对这个世界有了更深入的了解，也清楚的意识到这个世界正在逐渐陷入危机，阴暗的势力正在卷土重来。作为“命运之风”的冒险者，或许正是阻止危机出现最为重要的一环。',}",
  [504127] = "{id=504127,Text='在赛尼亚的建议下，冒险者来到了西门，并从西门学者史芬斯口中了解到了大陆中最为强大的神秘力量——伊米尔之心，也听说了曾经拯救这个世界的勇士——达纳托斯的英雄事迹。\n但当冒险者获得这些信息之后，天地突然震动，邪恶的力量出现在时空的裂隙中。冒险者冲进裂隙，在达纳托斯灵魂碎片的帮助下，消灭了危机。',}",
  [504128] = "{id=504128,Text='随后，冒险者获得了达纳托斯赠与他的心之始源，它能与伊米尔之心的碎片产生共鸣，赐予冒险者霜巨人伊米尔的祝福，以获得对抗邪祟的力量。\n西门的危机解除后，冒险者便与史芬斯告别，接受了前往普隆德拉的卫星城市、港口城市依斯鲁得的委托。\n在那里，冒险家认识了梦想成为魔法师的少年庞德，帮助他完成了加入魔法学院的梦想。',}",
  [504129] = "{id=504129,Text='与庞德告别后，冒险者深入神秘的海底洞穴，继续未完成的委托。\n在完成委托的过程中，冒险者得知了魔物暴动的原因。在众人和小人鱼卢西娅的帮助下，冒险者修复了海底封印，阻止了裂隙的吞噬，挽救了大海。',}",
  [504130] = "{id=504130,Text='但与此同时，邪恶的巴风特彻底复苏了，并且它显然在计划着巨大的阴谋……\n为了阻止巴风特的复苏，离开依斯鲁得的冒险者受庞德的邀请来到了伟大的魔法都市吉芬。在与魔法学院院长雷藏的交流中，冒险者得知了在吉芬混乱期间，巴风特已经悄悄潜入了梦罗克金字塔，即将破坏某个危险的封印。\n为了对抗潜伏在梦罗克的伪天使巴风特，雷藏拜托冒险者前往妙勒尼山脉，与法师摩根一起去获得真正的彩虹项链布里辛嘉曼！\n在妙勒尼山脉生活的哥布灵的帮助下，冒险者集齐了能让彩虹项链觉醒的元素，但在仪式上，时空裂隙再次出现，裂隙内的力量在阻挠布里辛嘉曼觉醒！最终，在冒险者的努力下，时空裂隙的危机得以再次消除，仪式得以顺利完成。\n但危机并没有就此结束，平和的表象下隐藏着的躁动愈加明显，一切都将何去何从。',}",
  [504131] = "{id=504131,Text='“想和我们一起冒险吗？首先要学会喵喵叫哦！”小猫咪们眨着眼睛说。\n远星大陆的绵绵云岛上，飘着一道跨越天际的彩虹。多兰族猫猫们意外发现，彩虹其实是连接人类大陆的桥梁，并给它起名为糖果滑滑梯。为了加深多兰族对异世界的了解，蝴蝶巡守队正积极招募志愿前往人类大陆的冒险者猫猫。\n在松饼岗亭，冒险者作为预备役参加了巡守队的测试，结识了也想去异世界历险的小猫咪艾比。测试中，艾比意外发现了黑魔法的踪迹。他与冒险者一起追踪到了糖果滑滑梯，却遭遇了神秘黑影的袭击。千钧一发之际，一只神秘喵挺身而出，在黑影手中救下了他们。\n冒险者与神秘喵交谈后发现，他就是多兰族赫赫有名的大冒险家——多拉贡。多拉贡与两只小猫咪相约第二天再见。但在次日，冒险者发现时间回到了昨天。罗扎纳的所有小猫咪中，艾比和冒险者的意志最为坚定，只有他们感应到了时间回溯。',}",
  [504132] = "{id=504132,Text='为了调查原因，两只猫咪前去咨询了多拉贡。在大冒险家的帮助下，冒险者得知：远星大陆的时间，是由记忆的不断累积而推动流逝的。存储所有猫咪记忆的泪之湖，就在罗比奥森林的深处。而这与艾比追踪的黑魔法踪迹，指向了同一个方向……\n在森林里，冒险者和艾比遇到了猫咪女巫嘉莉，嘉莉告诉他们泪之湖已经被污染。每过一天，就会有未知的黑影来吞噬湖中新生的碎片。因此，所有猫咪都不断在失去昨天的记忆，所以远星的时间在昨天不停地重置。黑影还在湖水中扩散，到最后所有猫咪的记忆都会被吞噬。如果放之任之，远星的时间会永远停止，而整个大陆将会崩溃。\n嘉莉带领冒险者和艾比来到了泪之湖的底部。这里存储着所有猫咪的记忆碎片，同时还住着远星大陆的守护者——塔鲁圆皮龙。这只小龙是依靠记忆的力量存活的，可由于黑影吞噬记忆，小龙变得十分虚弱。\n此时，黑影趁虚而入攻击了冒险者一行，掳走了艾比，而小龙为了保护冒险者也陷入昏迷。为了救回艾比唤醒小龙，冒险者和嘉莉紧追黑影，一路来到了森林附近的冰激凌洞穴。',}",
  [504133] = "{id=504133,Text='在冰激凌洞穴，冒险者和嘉莉找到了黑影，艾比也被其束缚。救下艾比后，冒险者夺回了被吞噬的记忆碎片，黑影趁势逃走。冒险者想要追查，却无疾而终。\n在嘉莉的引导下，冒险者通过修复记忆碎片唤醒了小龙。之后，三只猫咪举行净化仪式，试图彻底清除湖底残存的黑影。但仪式即将完成的时候，艾比却攻击了冒险者和嘉莉，差点毁了仪式。\n千钧一发之际，小龙再次救了冒险者。艾比被击败，在转身逃走时，在他身后出现了熟悉的黑影。原来，早在艾比被冒险者救下的时候，他就已经是黑影的傀儡了……\n发现艾比被黑影控制，冒险者和嘉莉万分焦急。此时，小龙已通过净化仪式恢复了力量。它帮助冒险者追踪到了黑影的行迹，竟然在多拉贡海岛上。',}",
  [504134] = "{id=504134,Text='冒险者和嘉莉连忙前往海岛，发现了被控制的艾比。在他们的努力下，艾比恢复了自我意识。而黑影失去了凭依，也渐渐露出了原本的模样——竟然是异世界魔王梦罗克的影子。\n原来，梦罗克的本体被禁锢在人类大陆，而糖果滑滑梯的开启与它的魔力产生了共鸣。这让梦罗克之影伺机潜入了远星，妄图吸取到足以让本体挣脱封印的力量。\n见计划败露，梦罗克之影迅速通过糖果滑滑梯离开了远星大陆。随后，嘉莉将所有被吞噬的记忆碎片恢复了原样。远星上的时间继续流动了，冒险者也决心阻止魔王继续作恶，便通过糖果滑滑梯来到了米德加尔特大陆。\n在普隆德拉南门，冒险者和赛尼亚相遇了。此时，梦罗克在人类大陆的封印已经松动，而破坏封印想复活魔王的是恶魔巴风特。至此，消灭巴风特变得至关重要，而赛尼亚正是为了此事在调查。明白二人拥有共同的目标——阻止梦罗克的复活，冒险者决定帮助人类，消灭恶魔巴风特。于是，冒险者便跟随赛尼亚一同前往柏伊亚兰，在那里有着巴风特的最新线索……',}",
  [504135] = "{id=504135,Text='四处松动的结界，古老的羊角怪物，不寻常的异变指引着初来乍到的冒险者来到了卢恩王国。同伴会成为你的力量，信仰会成为你的依靠，而你想守护什么？\n与摩根告别后，冒险者出发前往梦罗克。驻守在梦罗克的巴尔洛将军告诉了冒险者现在糟糕的情况——巴风特要破坏的封印是六百年前魔王梦罗克的封印，一旦魔王梦罗克复苏，整个世界都会毁灭。情况紧急，冒险者在驱赶了梦罗克周边暴乱的魔物后，找到了神工匠哈沃德，请求他为自己打造强大的附魔容器。\n一切准备就绪，冒险者在格丽塔的引导下前往金字塔，与海陆曼见面。',}",
  [504136] = "{id=504136,Text='幽暗的金字塔呢，海陆曼告诉冒险者：需要尽快前往封印之间，才有可能阻止巴风特。\n就这样，冒险者开始了觉醒祭坛的工作，而陪伴冒险者完成这一切的，正是许久未见的好友赛尼亚。在艰难消灭了兽人英雄与蛇妖莫莉娅后，两人终于能将封印之间的祭坛激活。即使是这样，冒险者一行人而还是慢了一步，梦罗克城此时已经彻底被巴风特率领的魔物攻陷，四处都是断壁残垣。\n为了最终一战，冒险者前往普隆德拉呼叫了大量援军，并决定在大家的掩护下闯入了封印之间，独自正面对抗邪恶的伪天使巴风特。',}",
  [504137] = "{id=504137,Text='但令冒险者感到惊讶的是，自己一路走来的伙伴们都陪伴他来到了最终的战场。危机虽然降临，但没有任何一位伙伴退缩，他们会陪伴冒险者，直到战争的最后一刻。战斗中，冒险者多次附魔武器，但依然无法撼动强大的巴风特。\n最终，冒险者为了守护这个世界，决定使用自己身上的伊米尔之心碎片，付出自己的生命击败并封印了邪恶的巴风特，令魔王梦罗克的复苏化为泡影。勇敢的冒险者击败了恶魔，他为了荣誉而战，为了心中的爱而战。他的英勇行径令公会导师们大为震惊，他们也再次确认，冒险者就是预言中拯救世界于危难中的“命运之风”。\n怀着敬佩与希望，导师们用女神之泪的力量唤醒了牺牲的冒险者。\n苏醒的冒险者展露了笑颜，他无惧于未来的黑暗，带着自己的勇气回到了冒险开始的地方……',}",
  [504138] = "{id=504138,Text='梦罗克一战不久后，冒险者受到卢恩王国内务官的派遣，前往了东方的斐扬村。据派遣队队长透露，斐扬一带近期出现了神秘的黑衣人和魔物袭击事件，而斐扬自古以来对村外人十分排斥，斐扬统帅对派遣队的态度非常拒绝。\n为了获得统帅的认可，冒险者决定换上一身斐扬当地的服装，在此过程中，冒险者从统帅旧部霍克姆口中得知了统帅曾经使用了“禁忌的力量”，因此才会性格大变。\n再次来到统帅府，这里却遭到了黑衣人的袭击，一番打斗后，统帅终于信任了冒险者，并透露了这些事的起因是黑衣人想夺取斐扬的秘宝。',}",
  [504139] = "{id=504139,Text='为了追击黑衣人，冒险者前往了斐扬森林与艾勒梅斯、迪文、大运、金胜展开了调查，终于发现了斐扬秘宝正是传说中的伊米尔之心碎片，而黑衣人的身份是女神之手的十二神使之一，自称希默梅思。\n希默梅思将金胜打伤，再次前往统帅府想夺取秘宝，虽然冒险者及时阻止了希默梅思，但伙伴金胜不幸死去，统帅在复杂的情绪下说出了真相，决定将伊米尔之心交给大运保管。\n斐扬事件后，王国内务官将冒险者派遣往兽人村进行了调查。驻扎在当地的骑士团遗失了许多武器，在冒险者的调查下，他们发现这些武器竟然是被兽人偷走了，而原本思维单纯的兽人似乎正在谋划一些诡计。为了调查事情的真相，冒险者与星耀三兄弟展开了深入探索，过程中解救了许多被兽人囚禁的人类村民，发现兽人背后的主使其实是某个神秘的人类。击败兽人村长老奥巴尼后，奥巴尼透露了原来幕后主使正是女神之手组织，而这次罪魁祸首是一个名为霍格的邪恶魔法师，他的目的竟是煽动兽人进攻人类村落。在一番苦战后，冒险者终于击败了被蛊惑的兽人首领和邪恶魔法师霍格，暂时逼退了女神之手，令他们的阴谋没有进一步达成。',}",
  [504140] = "{id=504140,Text='离开兽人村后，冒险者被派遣前往了不远的克特森林，调查圣歌教会在克特森林遭到犬妖袭击的怪异事件。从魔法学院院长雷藏口中，冒险者得知克特森林似乎混入了某种“不该存在”的东西…\n于是冒险者深入森林，与魔法师伙伴凯特莉娜和庞德会合。根据魔法学院收到的神秘信件，冒险者顺着线索找到了寄信者是犬妖一族先知的后代，善良的提尔·剑齿。在提尔的解释下，冒险者明白原来这里的事件也是女神之手在作祟，他们假意要为犬妖们消除梦罗克的诅咒，实质上怂恿犬妖们复活已经死去的先知，从而夺得犬妖一族的至宝“亚苏哈卡”，来达成某些不可告人的目的。\n在解救了三名正义的犬妖伙伴后，冒险者在召唤祭坛见到了这次幕后主使洛斯·拉比尔。在洛斯的挑拨下，犬妖首领复活了已经失去人性的初代犬妖先知。\n虽然在魔法学院及时的救场下，先知和犬妖首领得到了控制，但没想到，“亚苏哈卡”竟然就是一片伊米尔之心碎片！而在先知的影响下，洛斯·拉比尔顺利夺走了被激活的伊米尔之心碎片，并在希默梅思的掩护下逃离了现场。事后，魔法学院帮助犬妖们解除了诅咒，并察觉到，女神之手的下一个目标，竟然是危险的古城·克雷斯特汉姆……！？\n希默梅思与女神之手盘踞在古城，集结着他们的不死军团。海陆曼决定集结各方力量组成联军阻止女神之手的阴谋。吉芬的魔法师与斐扬的猎人公会决定加入联盟军，大运也为了向希默梅思报仇加入其中。克雷斯特同盟军就此成立，海陆曼作为军团长统领这整个军团。',}",
  [504141] = "{id=504141,Text='联盟军在古城郊外受到了不明袭击。根据士兵们的口供，袭击他们的人身穿黑色盔甲，使用着强大的圣光力量。\n这些人是瓦尔基里强大英灵组成的漆黑部队，他们夺走同盟军的物资是为了阻止人类同盟军进入古城，他们的使命是不让任何人进入古城，也不让古城里的魔物出来。\n就在此时，希默梅思抓走庞德，并声称要将庞德作为祭品召唤沉睡已久的不死恶魔——黑暗之王！漆黑部队对希默梅思进行了追击，冒险者也与联盟军一起寻找庞德。\n然而，希默梅思此时已经利用庞德富含魔法潜质的身体与龙区的龙族一起作为贡品，组成了邪恶的召唤魔法阵。同盟军、漆黑部队和龙族这三个本是水火不容的势力在此时共同对敌，成功救出了庞德。\n但是一切已晚，庞德的灵魂已被抽离，召唤仪式也已经完成，黑暗之王再次现身于大陆。希默梅思趁乱逃走后，漆黑部队前往追击，同盟军则退回了古城郊区边缘，等待普隆德拉派来更多的支援进军古城。',}",
  [504142] = "{id=504142,Text='在克雷斯特同盟军成功驻扎后，冒险者自愿前往古城对同盟军进行支援。冒险者刚来到古城，就得知魔法师学徒庞德的灵魂被黑暗之王带走了，至今毫无消息！\n冒险者在调查的过程中得知，黑暗之王和他的属下迷幻之王以灵魂为食，他们平日便在古城封印了无数残缺的灵魂。',}",
  [504143] = "{id=504143,Text='庞德的灵魂比普通人纯净数倍，正是黑暗之王与迷幻之王口中的美食盛宴。\n庞德的灵魂被他们分为了三份，如果不能及时救出这三份灵魂，庞德就会有生命危险！\n另一方面，莉莎正在寻找充满下水道污秽气息的物品，她想用它来感应下水道的具体方位，从而建立传送通道。',}",
  [504144] = "{id=504144,Text='冒险者意外获得了莉莎需要的物品后，帮助她建立了与下水道的连接，传送通道就此被打开。\n然而在冒险者为传送通道的安全问题奔走的过程中，却被迷幻之王盯上了自己纯净的灵魂，若不是莉莎及时将冒险者传送出来，他可能已然成为第二个庞德。\n冒险者不惧危险，继续帮助莉莎开通古城别的地区的传送通道。\n可是渐渐的，莉莎发现自己传递出去的魔力波动在某片区域中断了联系，仿佛被吞噬了一样。',}",
  [504145] = "{id=504145,Text='冒险者来到魔力波动中断的地方，发现邪恶的祭司与神官聚在一起进行着某种邪恶仪式，而这个仪式正是为迷幻之王举行的！\n冒险者和众人一起成功破坏了迷幻之王的这场仪式，成功为莉莎开启了古城墓地的传送通道。\n迷幻之王愤怒的想要夺取冒险者的灵魂！千钧一发之际，冒险者在雷藏和莉莎的帮助下成功逃离……\n在充分的调查与准备后，冒险者在法师摩根的帮助下，进入了以庞德记忆为蓝本创造出来的邪恶梦境中。\n在冒险者奋不顾身的努力下，终于唤醒了庞德沉睡的灵魂。在诸位勇士的帮助下，同盟军成功救出了庞德并击败了黑暗之王及它的手下。\n虽然古城的危机远远没有解除，同盟军们仍需在古城驻守以保卫王国的安全，但这一次的战役无疑是一场巨大的胜利，给英勇的士兵们带来莫大的鼓舞，他们心中的正义永不熄灭……',}",
  [504146] = "{id=504146,Text='拥有伊米尔碎片之力的黄金时钟，百年来默默维持着艾尔帕兰的时空秩序。\n然而这份平和被一股神秘势力所打破。唯一能开启黄金时钟的秘法典籍被盗走，这引起了时钟守护者首领默斯曼·岁月的高度警觉，他派遣了两位优秀的时钟守卫者诺曼、瑞德兄弟与冒险者共同追查此事。\n冒险者发现秘法典籍的守护人死于一种古老的蛇毒。经过一系列的调查，事情的真相渐渐浮出水面……\n一切因果的幕后黑手，竟然是传说中的世界大蛇耶梦加得！他执着于得到黄金时钟，并用它的强大力量解救出被关在月亮上的兄长——魔狼芬里尔。\n耶梦加得长年蛰伏在时钟守卫者中，他用阴谋手段燃起了时钟守卫者内战的导火索。',}",
  [504147] = "{id=504147,Text='日常',}",
  [504148] = "{id=504148,Text='寻找瑞德下落的诺曼与冒险者一同潜入叛军的据点——钟楼底层。激战之后，诺曼以付出重伤的代价换得了瑞德的自由。\n然而之后情况发生了进一步的恶化，耶梦加得妄图夺得威克斯发明的秘宝——奇点球，并用其制造一个巨大虫洞以掀起更大的混乱。更令人震惊的，冒险者经过对瑞德的追查，难以置信的发现瑞德竟然就是世界大蛇的本体——耶梦加得。\n成功骗得奇点球的耶梦加得，打破了过去的时空壁垒并召唤出了可怕的古代巴风特。但令他自己都没料到的是，古代巴风特完全不听指挥，反而向作为召唤者的他发起攻击！一直将耶梦加得视作亲弟弟的诺曼并不在乎他对自己的欺瞒，舍身为他挡下了攻击。\n这个举动深深动摇了耶梦加得的内心，他与冒险者联手击败古代巴风特，并与奄奄一息的诺曼许下了守护黄金时钟的诺言。本以为一切就此结束，但艾尔帕兰的噩梦却丝毫没有苏醒的迹象……\n诺曼之死使耶梦加得放下了对黄金时钟的执念，但时空扭曲的乱象却愈发严重。失控的奇点球开启了异世界的虫洞，耶梦加得就这样和另一个世界线的自己相遇了。\n另一世界线的耶梦加得成功夺取了黄金时钟。他沉溺于力量带来的满足，迷失了拯救兄长的初心。最终，他舍弃了耶梦加得之名，称自己为轮回梦魇！并带着对力量的无尽渴求，侵入了人类的世界…\n耶梦加得为了遵守和诺曼的约定，毅然拒绝了轮回梦魇发出的联手邀约，摒弃前嫌和时钟守护者、炼金术士公会组成同盟。众人穿越到轮回梦魇所在的世界线，赶在钟楼崩塌前找到了轮回梦魇，大战一触即发！',}",
  [504149] = "{id=504149,Text='日常',}",
  [504150] = "{id=504150,Text='在艾尔帕兰联合战线处于下风的关键时刻，诺曼的怀表发出了夺目的光芒，这给了耶梦加得与冒险者击退轮回梦魇的勇气。众人用时空碎片的力量将黑洞永久封存。悠扬的钟声再次回响在大街小巷，艾尔帕兰恢复了往日的平静。\n尘埃落定后，时钟守卫者与耶梦加得的同盟关系也到此为止。默斯曼并没有阻止他带走奇点球，耶梦加得露出捉摸不透的轻笑，消失在运河之畔的人群中。“我觉得他，也不是那么的坏。”面对质疑的声音，默斯曼沉吟道。\n可又有谁能真正猜透他呢？合上手中的怀表，耶梦加得抬头望了眼天上的孤月，亦正亦邪的神明继续踏上了拯救兄长的旅途。',}",
  [504151] = "{id=504151,Text='冒险者怀着满心的期待来到了幻想之地——姜饼城，但此时的姜饼城正陷于危机之中。城中的孩子一个个的失踪了……\n为了追查事件的真相，冒险者陪同小女孩珍妮去马戏团观看表演并结识了马戏团的红鼻子小丑加尔，他们携手击退了突然魔化的表演者。\n冒险者和圣诞老公公经过线索分析后察觉到，马戏团的这些表演者正是姜饼城失踪的孩子！而如今发生的这一切，背后都有女神之手的傀儡巫师——毕尤的身影。\n六十年前的冬天，年幼的加尔被毕尤带到了姜饼城，从那时候开始，毕尤就计划着利用加尔抢夺在姜饼城的伊米尔之心碎片。',}",
  [504152] = "{id=504152,Text='圣诞老人将事情的真相告诉了姜饼城的居民，并将大家集结在一起，共同对抗加尔和毕尤。这时，冒险者发现珍妮也失踪了！\n冒险者连忙向珍妮的家人打听情况，却意外的从珍妮的奶奶贝蒂口中知道了她小时候曾与加尔是非常要好的玩伴。\n借着贝蒂对加尔的了解，冒险者在马戏团帐篷内找到了加尔，但小女孩珍妮依旧不见踪影。在千钧一发之际，加尔竟然在毕尤的帮助下逃走了。',}",
  [504153] = "{id=504153,Text='经过一番调整与准备，冒险者决定再次前往马戏团营救孩子们。这一次的行动成功了，但珍妮却在冒险者抵达之前就被加尔带走了。\n获救的孩子告诉冒险者，其实加尔并没有大家想的那么坏，他从来没有伤害过孩子们，而他所做的一切都是在毕尤的唆使下为了引起贝蒂的注意。\n圣诞老公公猜测，加尔带走珍妮是因为将她误认成了贝蒂，因为那是他记忆中最不可磨灭的美好回忆。\n此时，毕尤意识到贝蒂是唯一可以阻止加尔并破坏她计划的人，她连忙派出傀儡去攻击贝蒂，想把贝蒂赶出姜饼城。',}",
  [504154] = "{id=504154,Text='然而贝蒂在遭受攻击的过程中，找回了自己那段丢失的记忆，她坚持与冒险者一起前往马戏团，并凭借自己的记忆找到了加尔。\n在贝蒂一声声的呼唤下，加尔终于认出了她。贝蒂为没能遵守当年的约定向加尔道歉，并告诉加尔当时毕尤杀害了她的父母，使她无法回到姜饼城。\n加尔这才知道这一切都是毕尤的诡计，终于清醒了过来。毕尤恼羞成怒发起了攻击，冒险者帮助他们击退了毕尤，守护了姜饼城。\n一切都结束了，危机成功解除了，孩子们回到了自己的家里。\n而加尔和贝蒂一起离开了，没有人知道他们去了哪里，但可以确定的是，他们会和小时候约定的一样，手牵着手，相互陪伴，永不分开。',}",
  [504155] = "{id=504155,Text='柏伊亚岚岛的船长新开了一条前往天水之国的航线，冒险者憧憬着美丽的樱花之都，乘船来到了天津町。\n刚来到天津町，冒险者就遇到了一个老熟人——法师摩根。此时的摩根令冒险家目瞪口呆，他喝醉了酒，身着女式和服，混在樱花小姐选拔大赛中跳着舞。\n就在冒险者与摩根说话的时候，一个名叫伊达秀明的少年出现了，他出声制止了摩根的喧闹，而摩根却被突然出现的“女鬼”吓的晕了过去。秀明知道冒险者和摩根不是普通人，便想请二人帮忙。',}",
  [504156] = "{id=504156,Text='就在冒险者与摩根说话的时候，一个名叫伊达秀明的少年出现了，他出声制止了摩根的喧闹，而摩根却被突然出现的“女鬼”吓的晕了过去。秀明知道冒险者和摩根不是普通人，便想请二人帮忙。\n然而这本卷轴由神社的巫女，也就是秀明的姐姐伊达千代子掌管，她唯独不愿意将卷轴借给秀明。\n摩根假扮成巫女继承人去借阅咒法卷轴，却被千代子当场识破。秀明只能令作他法，他利用冒险者分散了千代子的注意力，并趁机偷走了卷轴。冒险者这才意识到秀明一直在欺骗自己，而千代子想要极力阻止的命运之轮终究还是开始转动……\n秀明带着卷轴逃走后，樱却现身了。她告诉冒险者，她的灵力越来越弱已经快要消失了，秀明做的一切其实都是为了保护她。\n秀明想要利用卷轴中记载的一个大规模聚灵法术，把整个天水之国的灵聚集起来，将它们的灵力补充到樱的身上。\n善良的樱不愿意牺牲这些灵来延续自己的生命，她希望千代子和冒险者能阻止秀明的聚灵之法。',}",
  [504157] = "{id=504157,Text='秀明此时正在为庞大的聚灵仪式做准备工作，冒险者在千代子的帮助下破坏了秀明的法阵。但是这个法阵居然只是个障眼法，而秀明在结界中施展了真正的咒法！\n千代子和冒险者终究还是晚了一步，仪式已经开始了！然而樱根本承受不住这么多灵力，她的灵体因为集结了过多的灵，已然变成一股恐怖的力量并失去了自己的意识，千代子和冒险者已经无力阻止。\n被聚集的灵们在冒险者的帮助下回到了地下，原本枯萎的樱花树们重新争相盛开，天水之国恢复了往日的美景。\n一切真相都浮出了水面，原来秀明从小体弱多病，樱一直用自己的灵力为他治疗，这才导致灵力变弱。秀明直到此刻才知道自己的天真，做着自以为对的事，害得樱成了如今这副模样。\n摩根手上有个魔法珠，可以为二人实现一个愿望。樱希望用她最后一点灵力为秀明治疗，然后让他忘记关于自己的一切，去拥有一个美好的未来。而秀明则希望将身上的灵力还给樱，哪怕自己只能再活五年，这也会是他人生中最幸福的时光。\n他们都将殷切的目光投向拥有决定权的冒险家……是让有情人不惧已知的未来享受当下短暂的幸福，还是让他们忘记一切等待着不知是否会到来的重逢……你的选择会是什么呢？',}",
  [504158] = "{id=504158,Text='从贤者西芙手中接过护送伊米尔之书的任务后，赛尼亚与冒险者踏上了前往朱诺的旅途。但一只名为索斯的变异魔物突然出现，拥有指挥魔物力量的他，妄图掳走冒险者！\n正当大家陷入与索斯的苦战时，朱诺的特殊护卫队——曙光队及时赶到，曙光队队长塞壬拔刀相助击退了索斯的侵袭，此时塞壬惊讶的发现，父亲留给他的项链竟然出现了发光反应，还没等塞壬想明白，索斯就在一阵痛苦的挣扎后慌忙地逃离了国境检查站。\n在成功护送伊米尔之书到达朱诺后，朱诺大总统卡尔出于对索斯可怕能力的担忧，指示塞壬立刻开启位于广场中央的海姆达水晶——水晶的神力可以保护朱诺免受魔物侵袭。\n赛尼亚与冒险者在雷根贝勒公司成功得到开启水晶的钥匙。但出乎意料的是，海姆达水晶竟然无法启动！福无双至，祸不单行。在众人对水晶的故障束手无策时，城外传来了大群魔物袭来的警报，魔物的指挥者正是可怕的索斯！临危受命的冒险者，击退了入侵朱诺的魔物，塞壬则与索斯展开了决斗，奇怪的是，索斯没对塞壬进行任何反击，便带着魔物慌忙撤出了朱诺。\n索斯怪异的行为引起了塞壬的怀疑，为了解开心中的疑惑，赛尼亚与冒险者带着索斯遗落的毛发来到魔物研究所。通过雷根贝勒公司的技术，他们窥视了索斯的记忆并惊讶的发现，原来索斯曾经也只是个普通人类！那么它又是如何变成今天这副模样的？更大的谜团浮现在大家的心中，是谁创造了索斯这个魔物？这个幕后黑手到底又有着什么不可告人的阴谋呢？',}",
  [504159] = "{id=504159,Text='日常',}",
  [504160] = "{id=504160,Text='就在对索斯的调查略有进展之时，卡尔大总统却向全城发布了戒严令。聪明的赛尼亚通过计谋突破了封锁的重重关卡，顺利进入熔岩洞穴调查失踪的冒险者。他们意外的发现被囚禁的人质不仅没有受到伤害，还被给予了充足的食物。\n在朱诺城中按兵不动的卡尔大总统偷偷交给塞壬一张写着暗号的字条，原来碍于雷根贝勒在朱诺的强大势力，他只得通过暗号来召集隐藏力量，营救人质。这股力量就是秘密之翼，一个由卢恩王国与修发兹共和国的精英冒险者们组成的地下反抗组织。秘密之翼麾下的援军——赛依连与他的小队在冒险者的召集下集合完毕，前往熔岩洞窟解救人质的秘密行动已万事俱备！\n虽然人质营救计划十分顺利，但关键的敌人索斯却消失的无影无踪。塞壬认为他失踪的父亲就在被索斯掳走的人质中，心中重新燃起了与父亲相见的希望。经过六人小队的暗中调查，所有线索都指向雷根贝勒的研究员——宝伏。他的秘密研究与索斯存在着千丝万缕的联系。索斯绑架无辜的人质，很可能是受到了宝伏的命令。愤怒的塞壬前往雷根贝勒研究所向宝伏兴师问罪，但宝伏却给了他一个难以相信的结果：塞壬的父亲早已被索斯杀害。塞壬无法抑制为父报仇的冲动，独自前往索斯的巢穴。\n当大家到达索斯的巢穴时，在他们面前的却是一场单方面碾压的战斗。无论塞壬如何攻击，强大的索斯没有一丝还击的意愿。但此时的塞壬已被愤怒与仇恨所支配，他拔出父亲留给自己的匕首，毫不迟疑地给了索斯最后一击。体无完肤的索斯随着一声悲鸣，沉重的倒在地上，一根熟悉的项链从索斯身上掉落。那根与他脖间一摸一样的项链，让塞壬眼里充满了疑惑。在生命的最后一刻，索斯说出了震惊所有人的话语“塞壬…我的儿子”。塞壬如同发狂一般击打不会再醒来的索斯，不断喃喃自语，他无法接受摆在自己面前的事实——索斯其实就是他失踪多年的父亲！',}",
  [504161] = "{id=504161,Text='日常',}",
  [504162] = "{id=504162,Text='塞壬来到宝伏的办公室，却发现这里早已人去楼空，唯一的收获只有一本空白笔记本。在贤者学院院长西芙的帮助下，笔记本上显现出了隐藏的文字。上面的线索指出，宝伏利用塞壬的父亲绑架了大量冒险者用作人体实验。但索斯在见到塞壬之后觉醒了人类的意识，擅自释放了被关押的冒险者。 恶毒的宝伏为了除掉失去利用价值的索斯，利用塞壬对父亲的思念挑拨离间，策划了塞壬弑父的悲剧。\n就在此时，机敏的六人小队发现了宝伏地下研究所的隐蔽入口。为了弄清威胁朱诺的邪恶阴谋，众人一同进入了宝伏的地下研究所。但狡诈的宝伏早已做好了迎击的准备，大量的变异魔物被释放出来。塞壬与六人小队集中攻击，从魔物的包围圈中撕扯开一个突破口，利用这个间隙给了宝伏致命的一击。宝伏在临死前狂妄地叫嚣着，他不过是无数幻像中的一个罢了。\n所有人都以为只要宝伏被击败，群龙无首的魔物们便会就此退散，却没想到魔物们竟然开始重新集结，他们忠诚地执行着宝伏下达的最后一个命令——破坏海姆达水晶！无数魔物在朱诺的街道横行无阻，将天空之城渲染成了恐怖的地狱。\n寡不敌众的人们眼睁睁地看着魔物大军涌向水晶，难道朱诺最后的希望也将随着水晶一同分崩离析吗？在魔爪挥落的一刹那，冒险者终于彻底激活了水晶！海姆达水晶所散发出的耀眼圣光，形成了一个覆盖朱诺的纯白结界，沐浴其中的变异魔物纷纷倒地，恢复成了人类的样子，朱诺的魔物围城危机终于被成功化解！\n朱诺的危机解除之后，大总统的猜想仍得不到证实，躲在黑色帘幕后的操纵者是否就隐藏在雷根贝勒公司中？赛尼亚为了查出最深层的黑暗真相，与六人小队一同踏上了前往雷根贝勒研究所的道路。\n在塞壬与冒险者道别之际，早已离开的六人小队成员——凯特莉娜却突然出现在塞壬面前。她低声地向塞壬耳语了几句，便匆匆离开。塞壬神色骤变，反复告诉冒险者“我要去救出父亲。”眨眼间，塞壬竟从朱诺的万丈高空一跃而下…朱诺的事件虽告一段落，但这些处于风眼中心的人们不曾料到，一场更大的命运风暴正在前方等待着他们。',}",
  [504163] = "{id=504163,Text='卢恩王国的大法师们发现，死者之都尼夫海姆的灵魂不知什么原因，被困在那里无法转生，这严重影响了世界的平衡。冒险者被委托去调查事情的起因。他来到了汶巴拉，在雨檀族族长的帮助下，获得了天地树的生命之力。他义无反顾地跳下树洞，前往一个未知的世界……\n冒险者在尼夫海姆醒来，却意外与失踪的塞壬重逢，并结识了酒吧老板华莱士和魔女薇格。塞壬来这里是为了解救自己父亲的灵魂，由于目标一致，都是为了解放被禁锢的灵魂，二人决定再次联手。\n而魔女薇格不知出于什么原因，紧紧跟着他们一起行动，在华莱士的帮助下，他们一起来到雾之森林寻找海拉的仆从格拉提。薇格与格拉提是旧识，格拉提答应帮助他们，并让他们回去等他的消息。\n很快格拉提便传来了消息，三人见到了死者之都的统治者——死亡女神海拉。海拉告诉他们自己对这一切也无能为力，唯有得到无心之人的眼泪，她才能帮助他们解放所有的灵魂。他们与海拉做了约定，帮助海拉修复祭坛，海拉便将无心之人身份的线索告诉他们。薇格的聒噪让塞壬很是心烦，他一心只想尽快救出父亲，没时间搭理薇格。薇格却不在意塞壬的无视，坚持不懈的在他身边。三人很快发现祭坛无法被修复，都是因为怨恨之灵的存在。\n这个怨恨之灵悲伤的过去让三人无意间发现了死者之都的一个秘密抵抗组织。这个组织认为灵魂被禁锢都是海拉所为，所以他们竭力阻挠海拉祭坛的修复，这让塞壬头痛不已。另一方面，海拉的仆从格拉提十分喜欢与人类相处，并与薇格是好朋友。他暗中帮助他们，找到了居住在雾之森林的神器研究员伊诺克。\n伊诺克煽动抵抗组织并妄图利用他们夺取海拉手中的神器——九界权威。而怨恨之灵的悲剧正是由他一手造成。塞壬与薇格决定，无论是为了帮助怨恨之灵还是完成祭坛的修复，都要揭露伊诺克的真面目。在雾之森林的深处，三人找到了抵抗组织的据点，向抵抗组织的首领揭露伊诺克的真面目，并表明修复祭坛是为了帮助灵魂们解放禁锢，成功说服了他们不再阻挠祭坛的修复。阻挠祭坛修复的灵魂们都离开了，祭坛的怨恨之灵也消散了。祭坛的修复工作成功完成，塞壬、薇格与冒险者携手来到了海拉的宫殿，却没能见到海拉，只有格拉提在那里等着他们……',}",
  [504164] = "{id=504164,Text='日常',}",
  [504165] = "{id=504165,Text='海拉如约将无心之人的线索通过格拉提交给了塞壬，但是三人都无法理解线索所表达的含义。而格拉提则偷偷告诉三人一个秘密。在斯凯领顿有一个名叫斯利德的宝石，它的力量能召唤出持有者想见的一切人与事物。\n然而当众人赶到时，在斯凯领顿等候众人的，却是海拉的另一个仆人——怠惰的格洛特，她不分青红皂白的向三人发起了猛烈的攻击。\n在成功逃离怠惰之兽尖牙利爪的追击后，大家决定向斯凯领顿的当地居民——南瓜公爵询问关于斯利德宝石的消息，原来，藏着斯利德宝石的海拉宝库就在斯凯领顿的某座禁忌的墓室中。\n通过顶级裁缝卡莉小姐的巧手，三人小队得到了可以涉足这一禁地的海拉之靴，并伪装成白幽灵的样貌骗过了守卫，成功的潜入了墓室。\n塞壬、薇格、冒险者一路披荆斩棘，顺利通过了墓室中的种种考验，终于，蕴藏死神力量的斯利德宝石呈现在三人的面前。正当大家准备向宝石许愿从而召唤出无心之人时，薇格却擅自说出了自己想见到美食的愿望！伴随着绿色的光芒，实现了愿望的斯利德宝石当即碎裂，此前众人所付出的努力皆化为泡影。勃然大怒的塞壬将所有消极情绪发泄向了薇格，大声斥责薇格滚开，受塞壬言行伤害的薇格黯然离开了小队，所有人的心情都随着不可挽回的局面沉入了前所未有的绝望深渊。此时，怠惰的格洛特突然出现，她称塞壬与冒险者在斯凯领顿完美地通过了海拉的全部考验，只要进一步修复通向海拉宫殿的往生祭坛，她就会说出关于无心之人的第二条线索。这一消息是打破绝望僵局的黎明曙光还是暗流涌动的致命陷阱？分崩离析的尼夫海姆三人小队究竟会走向怎样的未来。',}",
  [504166] = "{id=504166,Text='日常',}",
  [504167] = "{id=504167,Text='通过华莱士的帮助，冒险者与塞壬明白了海拉给予的第二条线索竟然指引向尼夫海姆66号——薇格的住所。在冒险者的一番开导下，塞壬暂时放下了对薇格的怒气，决定还是去找薇格谈谈，既是道歉，也是为了破解第二条线索的谜底。为了弥补之前不友好的态度，塞壬和冒险者决定用甜食哄薇格开心，此时恰逢海拉节庆典，塞壬与冒险者从尼夫海姆幽灵的手中换取了糖果，带着糖果敲响了薇格66号小屋的门。\n然而意想不到的是，薇格竟然不在小屋，还留下了一张充满愧疚的纸条，向塞壬表达了歉意，并告知塞壬自己将去海拉的墓室重新寻找宝石。预感到薇格有危险的塞壬立刻与冒险者前往了斯凯领顿，终于找到了正遭遇毒藤怪的薇格，一番战斗过后，虽然塞壬与薇格关系缓和，但塞壬却意外中了藤毒，幸好薇格将塞壬带回66号小屋，在冒险者的帮助下调制了解毒剂。\n为了破解第二条线索，一行人将目光放在了66号小屋内的管风琴上，在尝试弹奏后，薇格的好友高登突然显身。高登表示愿意带塞壬前往一个特殊的地方，但条件是必须吃下死者之都的食物，成为假死的状态。塞壬和冒险者找到了华莱士，忍耐着吃掉了口味奇特的亡灵套餐，然而却不料高登欺骗了他们，他受海拉的指示，要让塞壬和冒险者永远陷入假死。在薇格的帮助下，塞壬和冒险者喝下了恢复假死的解药，总算暂时解除了危机。\n一行人再次找到高登对峙，却在战斗中意外进入了海拉的梦境。虽然从梦境中得知了海拉的身世，并得知了海拉的爱犬加尔姆，但由于薇格的状态突然变差，线索再次陷入瓶颈。无奈的众人遇到了海拉的仆从芮娃，芮娃表示海拉大人为塞壬一行人准备了考验，并让他们去找格拉提和格洛特。\n在格拉提和格洛特的接引下，一行人进入了埃琉德尼尔宫殿，击败了贪婪，月影幻影，用芮娃给予的骨头解决了血斑巨犬加尔姆，终于见到了海拉的真身，并与她展开了最后的决战。一番打斗过后，被击败的海拉终于说出真相——原来薇格就是海拉的心，而无心之人正是死亡女神海拉。一路的冒险，塞壬与薇格已经有了微妙的感情，而面对着薇格沉重的提问，塞壬终究作出了艰难的抉择……',}",
  [504168] = "{id=504168,Text='前往里希塔乐镇的六人小队全员失联，不久后，雷根贝勒公司还发生了重大火灾。这让海陆曼团长忧心万分，在他的委托下，冒险者肩负起了调查的任务。而在里希塔乐附近的原野，一位少女的灵魂出现了。她提醒前路凶险万分，希望来人能远离此地。但是，这并没能阻止冒险者的脚步。不久后，他与前来支援的卫斯理正式相见。带着卫斯理的嘱托和期望，冒险者继续踏上了追寻真相之路……\n冒险者抵达了里希塔乐镇后，意外和忽克连相遇。原来，他是来寻找师父哈沃德的。在二人毫无头绪时，一只大嘴鸟的出现带来了希望——它正是赛依连的坐骑利力！跟着利力的引导，冒险者和忽克连找到了昏迷的玛嘉雷特。在唤醒她后，一行人整理线索，得到了一个惊人的目的地：雷根贝勒公司……\n带着满腹疑惑，众人突破了看守的警戒，到达了雷根贝勒的地下研究所。在这里，他们终于见到了赛依连。来不及体会重逢的喜悦，赛依连就告诉了他们残酷的现实：雷根贝勒在此处进行人体实验，而随他一起前往的伙伴们，很可能已经成了实验品！忽克连难以接受这样的真相，独自去寻找哈沃德。冒险者一行紧跟其后，但等发现哈沃德时，他已经失去了理智，甚至还打晕了忽克连……这让所有人都震惊不已。为了明白是什么导致哈沃德变成了这样，一行人开始探查这个古怪的研究所。\n随着探查的深入，无数变异的生物包围了冒险者一行，还出现了许多人类变异者。赛依连注意到，这些变异的人们和之前哈沃德的情况十分相似。众人艰难地寻找线索，终于发现了实验体的真相：他们会随着时间的流逝彻底失去自我意识，分裂出复制品，最终变成思念体，永恒徘徊在这黑暗的地下。而引起他们变异的核心，竟然是人造伊米尔之心的碎片！\n面对研究所的黑暗行径，想到伙伴们可能已遭遇毒手，赛依连愤怒无比。他下定决心，要拯救所有变异的人们。在调查途中，冒险者一行遇到了消防员的亡灵。之前研究所突发的大火，与雷根贝勒的恶行不无干系。为了寻找更多的线索，众人决定分头行动，继续调查这个神秘诡异的地方……',}",
  [504169] = "{id=504169,Text='昏迷中的玛嘉雷特迟迟没有苏醒的迹象，赛依连的治疗也于事无补，幸好他没有放弃，在医疗室遇到了研究所职员杜克医生，在和杜克达成共识后，杜克为玛嘉雷特检查了身体状况，令冒险者和赛依连没有想到的是，玛嘉雷特竟是陷入了梦魇之中。为了唤回玛嘉雷特，冒险者毅然进入到了玛嘉雷特的梦中。\n结果没有让人失望，玛嘉雷特安全的醒来，告别了医生杜克，众人继续向地下研究所所二层深入调查，其中遇到了被关在监狱里做人体实验的腹语师裴诺和他的玩偶熊伙伴亨丝，裴诺的情况不容乐观，好在巴吉尔夫妇出现告诉了一行人可以暂时抑制变化的方法。\n令众人惊喜的事，玩偶熊亨丝竟然知道迪文几人的下落，为了拯救伙伴，赛依连和冒险者决定先将裴诺和亨丝从监狱中拯救出来，在寻找监狱钥匙的途中，玛嘉雷特再次陷入梦魇之中，这次赛依连决定和冒险者一同前往她的梦境之中查看原因，待到玛嘉雷特醒来，众人惊恐的发现玛嘉雷特梦中的事竟然在现实中发生了。\n毒气的出现使巴吉尔夫妇身体异变情况变得越来越剧烈，亨丝无意中唱出了巴吉尔夫妇的相爱之歌让两人恢复了理智，一波未平一波又起，裴诺竟然直接开始攻击起同伴来，为了让裴诺解脱，玛嘉雷特施展了治愈术，裴诺彻底消失了，更糟糕的事是巴吉尔夫妇的最后一场演出谢幕之后，也消失在了众人的眼前。\n一群人伤心的同时却更加坚定了拯救同伴的信念，在利力的提示，冒险者在录像带中发现了伙伴失踪的真相，还没来得及高兴发现了线索，冒险者一众就被那幕后黑手宝勒世伏派人抓到了他的大本营。\n在那里，他们见到了失踪的赛尼亚一行人，赛尼亚一众已经被宝勒世伏控制失去了理智开始攻击赛依连和冒险者，千钧一发之际秘密之翼徽章唤回了他们的理智，六人小队再次集结，但这喜悦只是暂时的，宝勒世伏不见踪影，危机还远远没有解除。',}",
  [504170] = "{id=504170,Text='再次重逢的六人小队决定齐心协力揭穿雷根贝勒公司的阴谋，他们向着实验室深处进发，却发现实验体是无法恢复的。迪文等人做了一个重要决定，他们愿意牺牲自己让赛依连和玛嘉雷特带着资料逃出研究所。赛依连果断拒绝了迪文的提议，表示六人小队要共同进退。此时赛尼亚出现并攻击了凯特莉娜，艾勒梅斯迅速挡在了凯特莉娜的身前，几人这时才看清，赛尼亚的双眼早已变的血红，她已经沦为了宝勒世伏的傀儡……\n艾勒梅斯受到了赛尼亚的攻击后永远闭上了双眼，剩余五人忍住伤痛发誓要将雷根贝勒的阴谋公诸于世。但此时玛嘉雷特却再次陷入了梦境。冒险者和迪文，哈沃德三人前往雷根贝勒研究室获取能够治疗玛嘉雷特的月见草。没想到宝勒世伏早已设下埋伏，哈沃德为了保护月见草牺牲了自己，迪文含泪将月见草带回。令人没想到的是在玛嘉雷特的梦境中，却看到了她和宝勒世伏的交易——牺牲四个伙伴，换取赛依连与她的自由……\n知道了真相的赛依连决定和玛嘉雷特分开，仅剩的三人继续向研究所深处调查。在实验室中找到的录像带让三人知道了制造器失控的原因——凯特莉娜的狂化使得实验室产生了大爆炸，人造伊米尔之心制造器因此升到了空中。冒险者为了前往实验室，提议搭乘飞空艇……\n几人得到飞空艇钥匙后，却发现飞空艇的燃料竟然是人造伊米尔之心。为了取得燃料，众人前往能源仓，没想到疯狂的雪依为了阻止他们竟然自爆摧毁了能源仓。危急关头，迪文牺牲了自己，用含有人造伊米尔之心力量的血液驱动飞空艇。宝勒世伏早就料到几人会来到空中实验室，他派出艾勒梅斯等人的思念体，让六人小队自相残杀。不仅如此，宝勒世伏竟让赛尼亚成为了人造伊米尔之心的守护者，阻拦冒险者。就在众人绝望之际，塞壬的出现唤醒了赛尼亚的理智，她选择和宝勒世伏同归于尽。然而人造伊米尔之心制造器仍然存在，玛嘉雷特为了赎罪决定留下来毁灭制造器。此时，赛依连握住了玛嘉雷特的手愿意同她一起面对。冒险者肩负着揭露一切真相的使命离开实验室，将雷根贝勒公司的阴谋彻底曝光在光明之下……',}",
  [504171] = "{id=504171,Text='卢恩王国迎来一群不速之客——来自阿卢纳贝兹教国的使臣。使臣以教皇的加冕仪式为由，邀请卢恩王国优秀的冒险者来参加以此缓解两国之间的关系，冒险者不辱使命前往阿卢纳贝兹的首都——拉赫。\n冒险者到达拉赫，遇到了拉赫的王储——双胞胎兄妹路克斯和莎缇拉，两个人对来自卢恩的冒险者很是欢迎，主动带着冒险者在拉赫四处游玩。\n然而在加冕当天，冒险者在前往与瑟西娅圣母会面时，目睹了瑟西娅被她的亲儿子路克斯所杀害了，并且卢克斯扬言要将卢恩也纳入自己的麾下。\n为了卢恩王国，也为了帮助莎缇拉，冒险者独自跟踪调查路克斯的阴谋，看到路克斯与反叛的信徒勾结后，冒险者拍下了证据。',}",
  [504172] = "{id=504172,Text='莎缇拉找回了闭关中的拉赫大祭司杰德，然后与冒险者一起将路克斯的所为告知他，为了拉赫的未来，杰德决定将实情告知拉赫的所有信徒。\n当着所有信徒的面前，莎缇拉请求信徒们的原谅自己的哥哥，甚至愿意牺牲自己的生命，莎缇拉的举动震撼了所有的信徒，被信徒们推举为拉赫的教皇。\n就在冒险者以为事情要结束的时候，莎缇拉竟然以女神的名义要将冒险者除掉，千钧一发之际，一直呆在瑟西娅身边的侍女尼乐妍救走了冒险者。\n为了调查莎缇拉的真正目的，冒险者与尼乐妍再次前往圣域神殿，没想到竟中了莎缇拉的圈套，原来冒险者看到杀害瑟西娅的人是莎缇拉假扮的，真正的路克斯早就被莎缇拉囚禁在了冰洞之中。\n冒险者不敌继承权杖中伊米尔之心之力的莎缇拉，也被囚禁到了冰洞里。冰洞里，冒险者克服丛丛阻碍，救出了被囚禁的路克斯。\n冒险者发现了隐藏在冰洞之中的秘密，原来瑟西娅一直只疼爱路克斯，忽视莎缇拉，甚至还用莎缇拉的血液治愈路克斯的身体，路克斯知道自己妹妹的遭遇时内心自责不已，下定决心重回拉赫，将一切做个了断。\n由于双生子的羁绊，莎缇拉知晓了路克斯被救出的事情，早就在拉赫城中做好了准备，同时也告知了路克斯，他们的母亲所做的一切都是因为一条神谕。\n多年的囚禁与折磨，让莎缇拉变得疯狂，她决心将路克斯献祭给女神的石像，要毁掉所有的一切，冒险者与路克斯的心灵相通，共同阻止了莎缇拉。\n此时幕后一直帮助莎缇拉的女神之手出现，他想要杀掉失败的莎缇拉，路克斯毫不犹豫的挡在了自己妹妹的面前，这一次，他希望留下的是自己的妹妹，这是他亏欠莎缇拉的……\n看着自己的双生哥哥倒在血泊之中，莎缇拉做出了一个决定，她借由伊米尔之心的力量，牺牲自己复活了路克斯。',}",
  [504173] = "{id=504173,Text='醒来的路克斯知道莎缇拉为自己牺牲后，内心也在慢慢的变化，他拿起权杖带起皇冠抱起了莎缇拉的尸体离开，他不再是那个是仁慈善良的王子，黑暗的种子在他心里的逐渐发芽……',}",
  [504174] = "{id=504174,Text='洛阳城外出现异动，冒险者受法海委托，前往荒境支援。然而刚抵达荒境，冒险者便被卷入一场战斗：堕落的枫叶精灵伊斯玛，正率领潮水般的魔物，袭击衰退回种子状态的神树。\n冒险者与罗马尼等荒境巡守队成员共同击败了伊斯玛，但偏执的枫叶精灵不肯撤退，反而打算用最后的力量与神树同归于尽——危急时刻，一阵笛声响起。听到笛声的伊斯玛恢复了些许神智，念叨着“哥哥……”离开；神树似乎也对笛声有感应，逸散出微弱的光芒。\n事后，冒险者从罗马尼口中得知，神树维系着洛阳的结界。要彻底解除魔物对洛阳的威胁，只有举行唤灵仪式，让衰弱的神树复苏。于是，冒险者帮助罗马尼收集仪式所需的材料，并答应出席唤灵仪式。',}",
  [504175] = "{id=504175,Text='祭典的准备工作已经完成，祭司长艾露开启了唤灵祭典，就在众人以为要成功之时，神树种子的光芒暗淡了下去，祭典失败了。\n艾露表示，她感受到了神树的意志不想醒来。冒险者则在调查神树的过程中，又听到了昨天的笛音。冒险者将笛音的事情告诉罗马尼，后者并未听到笛声，但认同笛音或许可以唤醒神树的想法。\n于是，冒险者开始独自寻找笛声的来源，最终找到了失明的枫。经过一番交谈，枫答应帮助冒险者唤醒神树，但他也表示目前的自己十分虚弱，需要休养一段时间。两人约定第二天再前往神树。\n冒险者带领枫来到神树前，枫发现自己能感受到光明了。他吹奏笛子后，神树种子果然发生变化。罗马尼道谢，冒险者却发现他看不见枫。\n这件事让枫怀疑起自己的身份，于是冒险者带枫拜访艾露寻求答案。枫经受不住探灵的痛苦，陷入昏迷，艾露则忧伤地告诉冒险者：枫是即将消散的灵魂体。\n为了挽救枫，冒险者开始寻找系魂草。在这个过程里，冒险者被一阵阴森、嘶哑的声音威胁，对方对神树和枫都带有极大的恶意。\n冒险者带回的系魂草，最终治愈了枫。他平静地接受了自己已死的事实，却希望找回失去的记忆。考虑到枫与神树的联系，冒险者决定帮他找寻回忆。',}",
  [504176] = "{id=504176,Text='冒险者在荒境寻找有关枫记忆的线索，却意外遭遇了恶灵的袭击。他知道关于枫的事情，并表达了对洛阳的厌恶。\n经过一番激战，恶灵先行撤退。冒险者感应恶灵的魔力一路尾随，发现恶灵与一个神秘女人暗中密谋：他们想要破坏洛阳的结界，魔物变异与袭击神树也是他们做的。监听过程中，冒险者不慎被发现。神秘女人——也就是毕尤，与恶灵共同出手；幸而枫及时出现，与冒险者并肩作战。\n恶灵见到枫情绪失控，不战而走；毕尤却趁冒险者分神时偷袭了枫，随后才离开。冒险者担心枫的伤势，可枫表示并无大碍。他告知冒险者自己只需要休息，而神树那边似乎有些变化，让冒险者前去查看。\n冒险者回到神树前，发现神树果然长大了一些。冒险者又去拜访了枫，枫表示自己的视力有所恢复；同时在他见到恶灵后，还想起了什么。枫拜托冒险者寻找一个神秘的地方，经过一番调查，冒险者得知枫要找的地方是月光岩洞。\n冒险者进入月光岩洞展开调查，最终在水晶树中发现了回忆之匣。打开回忆之匣后，冒险者获得了一张画卷和一只知音鸟。知音鸟告诉冒险者，画卷是属于古恩的，并描述过往的事情。\n就在此时，岩洞中冒出火光——恶灵猛然现身，想对冒险者发起突袭。危急时刻，画卷迸发出一股力量，既保护了冒险者，还驱散了恶灵的伪装。冒险者惊讶地发现，这个恶灵的真身与枫一模一样！\n暴露真面目的恶灵气急败坏地离开了。冒险者顺利从火海中逃脱，他的心中有无数疑问，正需要找人解答。\n冒险者在神树附近找到了枫，并将月光岩洞中的经历告诉了他。拿到画卷的枫似乎想起了一切，在他的笛音中，神树又一次萌发生长，变成了一颗参天巨木。随后，枫与冒险者告别，不知所踪。\n神树已经长成，冒险者便找到法海，将最近的情况告知于他。拿到画像后，法海向冒险者解释了枫的身世，以及洛阳百年前发生的旧事：原来在当年战争中，毒牙族的首领乌洛牺牲，导致结界无法张开；而法纳族的首领古恩只能将自己化作巨龙，以身为界，保护住洛阳。\n法海还提到了与乌洛面目一致的恶灵，他担忧恶灵及其背后的势力，在荒境掀起更大风浪，于是拜托冒险者返回荒境。同时，他还给予冒险者隐身卷轴，以应付将来的危险。',}",
  [504177] = "{id=504177,Text='冒险者再次来到荒境调查异常，却发现枫被恶灵和毕尤困住。冒险者准备营救枫，却没想到这只是毕尤和恶灵设下的圈套。两人联手将冒险者困在幻境之中，似乎还有别的阴谋。\n当冒险者从幻境离开后，荒境已经陷入混乱，魔物到处肆虐。冒险者救下罗马尼等荒境巡守队成员，从他们口中得到了恶灵下落，便向月光岩洞追击而去。\n然而，岩洞入口被结界封锁，冒险者无法进入。荒境祭祀艾露及时出现，她表示自己可以破解结界，请冒险者耐心等待。\n艾露解开了岩洞的封印，冒险者进入其中，发现恶灵布下阵法扭曲回忆之匣，并从中吸取邪恶的力量。\n冒险者破坏法阵后，恶灵惊醒并狂怒，他斥责古恩对自己的背叛，发誓要破坏洛阳。毕尤也派出傀儡干扰冒险者，她让恶灵带着回忆之匣先走，务必破坏神树。\n击败毕尤的傀儡后，冒险者对恶灵进行追击，终于在神树前击败恶灵，可恶灵不甘心消失。就在这时，枫——或者说乌洛再次出现，他净化了恶灵身上的怨恨，将这具遗失的半身吸收。\n荒境的危机得到了解决，可冒险者也不得不与乌洛道别：由于动用神树的力量，乌洛将与神树融合、陷入长眠。乌洛宽慰着失落的冒险者，并拜托这位新朋友，替他继续欣赏荒境的红枫……',}",
  [504178] = "{id=504178,Text='乌洛的灵魂沉睡后，荒境巡守队一直在搜寻毕尤的下落。他们在神树下找到一个由魔力生成的入口，重伤的毕尤似乎逃入其中。\n冒险者受托进入入口查看，意外救下了被青蛇围攻的少女伊斯玛。这个入口连接的世界与荒境十分相似，但又有一些不同。通过与伊斯玛交谈，冒险者得知枫还活着，而神树仍然是一颗种子。冒险者想继续收集线索，确认这个世界的情况，却听到了一个冰冷声音的警告。\n冒险者与伊斯玛一起前往神树处查看，与乌洛重逢，但对方不认识自己。通过与乌洛对话，冒险者了解到这个世界是千年前的荒境——枫叶彼岸。\n乌洛正为神树无法成长而烦恼，冒险者根据经验建议他吹奏笛子。神树在笛声中成长，乌洛正要感谢冒险者，枫叶彼岸突然魔气弥漫，魔物们纷纷发狂。乌洛将解决魔物的重任交于冒险者，自己匆匆找古恩商量对策。',}",
  [504179] = "{id=504179,Text='冒险者得知魔气的出现是因为毕尤施加了诅咒。在追击毕尤的过程中，冒险者被毕尤的诡计所骗，遭到了埋伏，身受重伤。危难之际，一个法纳族青年出现并救下了冒险者。这个法纳族青年劝诫冒险者快点离开枫叶彼岸，随后便离开了。\n冒险者十分困惑，在伊斯玛的带领下，冒险者再次见到了乌洛和刚才救下自己的法纳族青年。原来这个法纳族的青年就是巨龙古恩。\n古恩在看到冒险者后，再次转身离开。乌洛替古恩向冒险者道歉，冒险者将古恩救下自己的事告知了乌洛，乌洛交给冒险者一个可以寻找古恩踪迹的罗盘。\n冒险者从古恩的言行中察觉到古恩似乎知道自己的来历，决定使用罗盘找到古恩，一问究竟。在寻找的过程中，冒险者意外发现伊斯玛与毕尤见面。\n毕尤利用伊斯玛对乌洛的爱，想蛊惑她听从自己，冒险者立刻出手制止。毕尤召唤出魔物拖延冒险者，自己逃离了现场。古恩也再次现身，他告知冒险者使用罗盘就能找到真相。\n冒险者通过一番努力在冰霜领域中找到了古恩的笔记，了解到枫叶彼岸并不是真正的、千年前的荒境，而只是古恩的一个梦境。古恩将当年的事情告知了冒险者。此时整个梦境发生了剧烈的震动，古恩将冒险者送离了枫叶彼岸。',}",
  [504180] = "{id=504180,Text='冒险者醒来，与法海就枫叶彼岸的经过进行交流，了解到毕尤的目的是在梦境中击溃古恩的意志，好让洛阳的结界崩塌。\n法海表示古恩一直将乌洛的死归结于自己的过失，十分愧疚，或许只有找到当年的真相才能将古恩从梦境中解放出来。于是，冒险者再次进入枫叶彼岸，此时这里已经燃起战火。\n冒险者找到了伊斯玛，通过交谈得知，她受到了毕尤的蛊惑，给乌洛服下了一种毒药。毕尤也在此时现身，她告诉伊斯玛：乌洛本可以不死，但他为了保护伊斯玛，将神树的力量用在了她身上，这才失去生命。\n伊斯玛无法承受这残酷的真相，在濒临发狂堕落的最后时刻，她选择自己走向消亡。而同时，毕尤也让古恩亲眼见证了乌洛的死亡，导致古恩精神崩溃，枫叶彼岸开始崩塌。\n为了阻止毕尤的阴谋得逞，冒险者一路来到神树前，在神树力量的加持下，与毕尤展开决战。\n在艰难的战斗后，冒险者击溃了毕尤，也将乌洛之死的真相告知古恩。与此同时，本该死去的乌洛，竟然又一次出现了——原来他不是古恩梦境中的乌洛，而是冒险者在荒境中遇到的那个真正的乌洛。\n灵魂体的乌洛表示，他的意识已经和神树融合，将会在古恩的梦境中守护古恩、守护洛阳。',}",
  [504181] = "{id=504181,Text='普隆德拉发光的喷泉吸引了众多市民围聚在一起，见多识广的博学者认出金色的光芒属于命运女神的使者。远在神域的女神带来关于未来的预言，年轻的救世主将吹起命运之风，拯救这个岌岌可危的世界。命运的使者指引着懵懂的冒险者，穿过时空来到天地之树尤克特拉希尔的巨枝上。树梢之顶，远空的浮云中，闪烁着苍蓝色一双的眼睛…\n远离阿斯加德的神域边缘，繁星之光后背的黯淡之处，有一个古老的城池。天边之水从高空顺流而下，笼聚连片的枫树，和树荫下若隐若现的洁白房屋。水流汇入城中的湖泊，扰动平静的湖水，激起湖面成群的花灯起伏不定，也带动水面红枫随波而流。这座被枫树和流水环绕的城市，被世人称为龙之城。它还有个更浪漫的名字，叫做洛阳。\n年轻的许仙是洛阳的城主，他生性浪漫多情，拥有一颗善良慈悲的心。与法海一起，许仙一直恪尽职守地管理洛阳，履行自己身为一城之主的责任。他原以为他这一生都会像现在这样平静的度过，如同洛阳清澈见底的流水。直到在花街灯会，在漫天的花火和车水游龙里，遇见一个令他毕生难忘的毒牙族—白素贞。  \n许仙爱上这个满身清冷又满脸幽婉的女孩儿。许仙放下自己城主的身份，伪装成一个普通的洛阳居民平安，一步步巧妙地帮助白素贞成为洛阳花魁！ \n只是，白素贞似乎并不在乎成为花魁，她做的一切另有目的，她利用许仙帮助自己一步步完成花魁的试炼。从银鱼湖取得纯净之水，用纯净之水净化落日浅滩的魔物，一切都在按照白素贞的设想发展。可她没有想到，自己也在与许仙的相处过程中，慢慢对许仙化名的平安产生了感情。最终在许仙的帮助下，白素贞成功当选花魁。',}",
  [504182] = "{id=504182,Text='洛阳灯会，花魁跳舞，白素贞在华丽的天灯上翩翩起舞，许仙在洛阳皇城遥遥相望。当白素贞看见，法海出现在许仙的身边，亲切地与许仙交谈时，她才知道原来一直以来帮助自己的法纳族男人，就是自己的仇人之一。悲愤交加之下，白素贞将起舞的花灯沉入银鱼湖中，在一片汪洋里消失了踪影。\n千年以前，洛阳还不像如今和平安稳，梦罗克带领魔族大军在整个世界掀起惊涛骇浪，诸神的黄昏也由此开始。洛阳也未能幸免于难，无数魔族通过彩虹之桥踏入洛阳，法纳族和毒牙族陷入漫长的保卫家园之战。那时，法海还只是个年幼的孩子，他的养父古恩才是时代的英雄。与法纳族古恩领袖地位相当的，还有一位毒牙族领袖。他叫做乌洛，也是诞生于伊米尔之手的远古神祇之一。乌洛有着温柔又体贴的性格，他收养了一个失去双亲的女孩，这个可怜又幸运的女孩就是白素贞。战争是无情的，命运是残酷的，它们总是将美好的事物从世界上带走。一场艰难的战事之后，白素贞又一次失去自己的亲人…乌洛在洛阳的枫叶里牺牲了…同时间，古恩化身为巨龙带领洛阳冲破黑暗，他成为所有洛阳居民心中永恒的神。白素贞不能理解，为什么自己的父亲惨死沙场，而古恩却能活在众生的崇拜之中…她产生了一个疯狂的想法…黑暗的爪牙在暗中窥视这个悲情的女孩，引诱她走向万劫不复的深渊…原本没有资格进入银鱼湖的白素贞通过成为花魁，得以进入湖底。她当着许仙和法海的面开启湖底时空裂隙，将大量游离在洛阳结界之外的魔物放入洛阳，消失在众人眼前。湖底的裂隙让越来越多的魔族进入洛阳，洛阳城危在旦夕。为了修复这条巨大的裂隙，法海带着冒险者闯入时空之外的领域，收集能够修复时空的秘源之力。在这里他们找到了昏迷中的白素贞。\n虽然获得秘源之力，可仍需要通天的神力修复裂隙，原本就非常虚弱的巨龙古恩贡献自己所有的力量，陷入无尽的沉睡中。\n法海非常懊恼，自己敬重的父亲再一次为了洛阳牺牲自己。\n得知古恩牺牲的白素贞，开始怀疑自己的复仇是否是正确的，可她背后的黑手并没有给她忏悔的机会，她们逼迫白素贞完成自己的任务并与反抗的白素贞发生矛盾。同时，洛阳城民自发组织前往流之云边，看望再也无法醒来的巨龙古恩的…纪念仪式上，白素贞站了出来，声称自己有一枚伊米尔之心的碎片，可以唤醒沉睡中的古恩。\n尽管法海感觉到事情不是那么简单，但希望唤醒父亲的感情太强烈，他选择相信了白素贞。约定之时已到，白素贞带着碎片走上流之云边，在森严防备的锦衣卫眼前使用碎片的力量唤醒古恩。仪式失败了，原本举行仪式的白素贞向法海和锦衣卫出手，希默梅思的手下毕尤帮助白素贞再次撕裂洛阳的时空结界。两人打伤保护古恩的许仙，乘法海不备逃出洛阳。',}",
  [504183] = "{id=504183,Text='法海仔细分析事情的前因后果，发现了花楼的异样，冒险者与法海一起回到花楼调查。花楼表面上如同往常一样繁华热闹，法海对“小青”的古怪之处心生怀疑，他用法术打破舞女们表面的伪装，让魔物显现出真实的模样。花楼早已被魔物入侵，真正的小青和舞女不知所踪。冒险者和法海找遍整个花楼，才在偏远的房间里找到小青和白素贞。原来，连流之云边的白素贞都是希默梅思假冒的。\n解除白素贞的束缚后，她去湖底看望受伤的许仙，当她得知希默梅思假冒她的模样刺伤许仙时，她的心中充满了悔恨。白素贞向冒险者透露千年前，希默梅思引诱自己复仇的往事，她终于知道希默梅思一直在利用自己。\n幡然醒悟的白素贞，连同锦衣卫的指挥使法海，和年轻的冒险者一起进入落日浅滩的海底，找希默梅思拿去解救许仙的药。出乎大家意料的是，希默梅思干脆的把药交了出来。',}",
  [504184] = "{id=504184,Text='彩虹桥碎片的坠落危机让米德加尔特大陆上的人类感到了不安。没有人知道，在那人类无法触及的神域之中，究竟发生了什么。但世界大蛇耶梦加得的突然出现，将一切的起因指向了身为命运之风的冒险者。\n冒险者跟随着耶梦加得的指示来到了发乐斯灯塔。在那里的海岸边，月光照射着大海，浪花拍打着海岸边的月之阵。冒险者受到了法阵的影响，不由自主地触摸了它，一段记忆涌入了冒险者的脑海之中，那段属于他自己的，丢失的记忆……\n耶梦加得用伊米尔之心的力量凝结成的星之花，阿玛丝塔西雅，在发乐斯灯塔撕开了前往月球的通道，形成了月之阵。命运女神诗寇蒂遵从其父亲智慧巨人密米尔的指示，化成命运使者，指引冒险者从月之阵来到了孤独而又冰冷的月亮之上，帮助耶梦加得解救他的哥哥芬里尔。\n然而，月狼芬里尔的声音响起，它让耶梦加得不要多管闲事，离开这里。耶梦加得不顾芬里尔的警告，闯入神庙之中。\n耶梦加得终于在神殿的主殿中见到了满身是锁链的芬里尔。然而虚弱的芬里尔仍然拒绝了耶梦加得的帮助，并表示这是自己的命运。耶梦加得很快察觉到，与芬里尔在一起的，竟然还有在诸神黄昏后失踪已久的神王奥丁！而芬里尔正通过束缚自己的锁链将力量输送给昏迷的奥丁。耶梦加得无法理解芬里尔守护奥丁的行为，那可是导致他们兄妹三人遭遇不幸的罪魁祸首。愤怒之下的耶梦加得强行破坏了芬里尔身上的锁链，并出手攻击了奥丁。\n芬里尔及时出手替奥丁挡下了耶梦加得的攻击。耶梦加得带着愤怒与芬里尔对峙。芬里尔惊讶于弟弟的成长，但仍用自己的实力压制了耶梦加得。就在此时，月球竟然开始剧烈的摇晃。智慧巨人密米尔现出自己庞大的身躯，一拳击碎了连接英灵殿的通道——彩虹桥。这巨大的冲击力使得月球也发生了震荡，令耶梦加得与芬里尔失去平衡，落石纷纷从神殿的顶端砸落下来。芬里尔用身体将包裹住耶梦加得，保护他免收落石的伤害。\n天地树剧烈摇晃，通往英灵殿的彩虹桥被破坏，沉睡的奥丁被这场大变动唤醒。冒险者也是在此时被落石砸中，昏迷后失去了这段记忆。\n在触摸月之阵后，冒险者重新找回了这段关于彩虹桥碎裂的记忆，他再一次通过月之阵前往月亮，想要查看耶梦加得等人的情况。\n芬里尔与耶梦加得仍然守在了奥丁神殿的中，奥丁虽已苏醒，但因为力量还没有完全恢复，还需要一些时间调养。芬里尔将他所得知的一些信息告知了耶梦加得与冒险者，并对诸神黄昏时期，奥丁是否真的对他们兄妹三人做出残忍行为提出了质疑，他认为，有人在背后操控了这一切。耶梦加得虽然对芬里尔的说法抱有疑问，但他答应芬里尔在一切真相浮出水面之前，不会轻举妄动。\n命运的齿轮已然开始转动，冒险者究竟是顺从命运还是摆脱命运，一切都会在他未来的选择里给出答案……',}",
  [504185] = "{id=504185,Text='在普隆德拉，一名古怪的老人拦住了冒险者的去路。冒险者同他交谈后，发现此人竟是摩根。原来，他一直在寻找盗走伊米尔之心碎片的不速之客——神秘的女法师莎拉·艾琳。此次摩根前来，正是请求冒险者帮忙追踪她。两人一路追查到天津町，终于在御神樱前与莎拉·艾琳相遇。然而，莎拉并没有将二人放在眼里。她在触摸御神樱的枝叶后，消失于神秘的光芒中。在冒险者和摩根面面相觑时，一只雪白的鸟儿出现了。冒险者乘着它飞往樱花中的通道，并与莎拉·艾琳进行了正面交锋。面对冒险者的穷追不舍，莎拉·艾琳显得越发急躁。为了摆脱追踪，她竟然直接引爆了通道。爆炸引发了巨大的气浪，将冒险者彻底震晕了过去……\n冒险者醒来后，发现自己竟然来到了浮空精灵的国度——辉煌领域。在雪翼鸟的指引下，他邂逅了纯真美丽的精灵少女赫蒂，和一名神秘冷漠的精灵少年。为了追踪莎拉·艾琳的下落，冒险者来到了辉煌领域的中心，精灵城艾卡拉奇。随后他了解到：象征着美丽与慈悲的女神芙蕾雅，正在此处养伤。而莎拉·艾琳，正是芙蕾雅的神使！为了追回伊米尔之心碎片，冒险者决定找莎拉问个明白。大贤者克莱尔告诉他，想要见到神使，必须要有晨曦圣女的帮助。然而，冒险者意外发现，浮空精灵的圣女竟然就是赫蒂。赫蒂在明白冒险者的来意后，欣然带着他前去拜访芙蕾雅。芙蕾雅听闻神使们犯下的罪孽，得知自己遭遇了下属的背叛，非常痛苦。当着冒险者的面，她审判了入侵人类大陆的阿卡娜，同时命令莎拉·艾琳归还伊米尔之心的碎片。在审判的过程中冒险者得知，神使们作出这一切，都是为了修复被摧毁的彩虹桥。收回伊米尔之心碎片后，一切似乎尘埃落定，冒险者感念于女神的公允。在临别时，芙蕾雅请求冒险者，帮助浮空精灵们修复彩虹桥。随后，赫蒂也向他提出了同样的请求，并希望入夜后能在祈愿广场相见。然而没过多久，先前偶遇的神秘少年忽然出现了。他告诫冒险者：绝不可修复彩虹桥，并发出了傍晚在时间花园会面的邀请。不知不觉间，夜晚即将降临。究竟是前往祈愿广场，还是时间花园？冒险者陷入了沉思……',}",
  [504186] = "{id=504186,Text='夜幕降临，冒险者想起与赫蒂的约定，只身前往祈愿广场赴约。得知冒险者愿意帮助修复彩虹桥，赫蒂十分高兴。她告诉玩家，需要前往云海的法阵，才可以明悉彩虹桥的现状。\n为了潜入蔚云之海，赫蒂与冒险者一起制作了变身药水。然而，药水配方出现了问题，赫蒂服用后竟然变成了米米怪！此时，一名自称菲利的精灵男子拦住了两人。他似乎是极光之翼的统领，试图阻止眼前的可疑人员前往云海。在冒险者陷入窘境时，大祭司贞奴比亚出现了。\n贞奴比亚轻松破解了赫蒂的伪装，并告诉两人，修复法阵目前魔力匮乏，需要雪翼鸟的翎羽才可让其稳定。冒险者便与赫蒂一同前往浮光星岩寻找翎羽。期间，冒险者再次与菲利发生冲突，才得知菲利竟然是赫蒂的哥哥。眼看两人之间误会越来越深，好在赫蒂赶来，及时化解了矛盾。随后，冒险者也顺利拿到了翎羽，便与赫蒂一同返回云海。\n蔚云之海上，贞奴比亚赞叹两人的合作与默契。可片刻之后，修复法阵发生了剧烈的震颤。伴随着一阵爆炸声与白烟，一名神秘精灵率领着小妖精们入侵了云海！冒险者意外发现，他正是先前见过的银发少年布兰迪斯。云海中瞬间陷入了混乱，浮空精灵与小妖精们剑拔弩张，而赫蒂二人与布兰迪斯也陷入对峙。\n随着贞奴比亚一声令下，浮空精灵正式对小妖精开战。在双方胶着时，一股诡异的紫色魔法忽然弥漫开来，整个云海顷刻便被浓郁的紫雾笼罩。下一秒，意外发生了——原本与小妖精交战的菲利晕倒在地，而浮空精灵们在紫雾中渐渐都失去了意识！一时间，所有人都陷入震惊与慌乱中。贞奴比亚怀疑是布兰迪斯所为，后者匆忙辩解未果后，带着剩下的小妖精匆匆离去。\n面对哥哥菲利与族人的昏迷不醒，赫蒂悲伤不已。她决心肩负起圣女的责任，查清这背后的真相。而此刻，面对浮空精灵与彩虹桥的阴谋，才刚刚显露出冰山一角……\n突如其来的昏睡怪病肆虐了整个艾卡拉奇。赫蒂为了拯救族人，消耗了过多的魔力，生命危在旦夕。眼看赫蒂再次晕厥，冒险者焦急万分。关键时刻，贞奴比亚出现救下了赫蒂，同时告诉两人：要想遏制蔓延的昏睡病，需要去时间花园摧毁一枚玛那结晶。然而，当两人顺利毁去结晶后，一股神秘的力量将他们拉入了花园的最深处——在那里，一个庞然大物出现了。他竟然是失踪已久的智慧巨人密米尔！\n赫蒂见到密米尔后，记忆似乎出现了紊乱，再次昏迷了过去。而密米尔则意味深长地告诉了冒险者一则预言：“无垢的晨曦与夜空之星，将于风中寻回失落的秘宝，重构彩虹的辉光”。赫蒂正是预言中所指的晨曦，而一切都与彩虹桥息息相关。说完这些后，一阵紫色的迷雾渐渐笼罩了时间花园，密米尔随之陷入了沉睡。\n等回过神来，冒险者与赫蒂已经回到了艾卡拉奇。赫蒂已经忘记了见过密米尔的事，而此时，他们也等到了一个好消息：在玛那结晶被摧毁后，昏睡病的患者们已经有了苏醒的迹象！  然而，冒险者和赫蒂并不知道，这一切都只是悲剧的序幕……\n冒险者再次见到赫蒂时，却被她告知：浮空精灵的昏睡病没有任何缓解。他们虽然苏醒了，可却失去了自己的意识，变得浑浑噩噩，仿佛一具行尸走肉。赫蒂的哥哥菲利也是如此，这让原本就疲惫的赫蒂更加焦虑不安。而冒险者思考现状后发现，是在他们摧毁玛那结晶之后，昏睡病才会发生变异。而指使他们做这件事的，正是大祭司贞奴比亚。\n贞奴比亚暂时失去了联系，于是赫蒂与冒险者打算前往时间花园，向密米尔询问一切的真相。然而，他们还没来得及问出什么，就被突如其来的紫色蝴蝶带离了花园。等两人回过神来，发现自身已经来到了神降之地，而芙蕾雅女神就在眼前。\n在与女神交谈后，冒险者与赫蒂才明白：摧毁玛那结晶，让昏睡病恶化，这一切都是芙蕾雅女神安排的。她放任浮空精灵的病情，眼睁睁让他们走向死亡。面对愤怒的两人，女神却显得异常愉悦。她当着赫蒂的面杀死了菲利，这让赫蒂的内心堕入了黑暗。\n冒险者在大贤者克莱尔的帮助下，唤醒了赫蒂原本的意识。克莱尔告诉两人，女神是一切的幕后黑手，她正在酝酿着巨大的阴谋。只有找到预言中的秘宝，才有可能阻止悲剧继续。随后，克莱尔便因女神的诅咒而死。\n兄长和老师的死，给了赫蒂巨大的打击。此时的赫蒂，已不再是过去那个天真的小圣女。她背负着仇恨与责任，决心背弃过去的信仰，与曾经最崇敬的女神为敌……\n冒险者在寻找赫蒂的路上，遭遇了神秘黑衣人的袭击，险些丧命。在浮光星岩，他找到了藏身于结界中的赫蒂。原来，这段时间中她一直在躲避女神的监视。冒险者确信，袭击自已的黑衣人也正是女神派来的。两人商议片刻，决定立即动身，赶在女神之前找到秘宝。\n赫蒂身上的太阳魔法，与秘宝似乎产生了奇异的共鸣。他们一路寻找，终于在辰星殿堂发现了秘宝——那是一枚拥有太阳刻印的真理指环。两人触碰指环后，来到了过去的辰星殿堂。在那里，两人看到了令人震惊的一幕：芙蕾雅对着大贤者施加灵魂诅咒，并让贞奴比亚从浮空精灵的身上提取修复彩虹桥的力量。赫蒂终于明白，之前的昏睡病正是精灵生命力被汲取的结果，而这一切都是女神所为。而女神做的这一切，都是为了修复彩虹桥，回到神域。在那里，她准备实施某个更加危险的计划……\n从指环中的回忆离开后，冒险者与赫蒂遇到了埋伏许久的贞奴比亚。她正是为了秘宝而来。两人不敌全盛状态的贞奴比亚，在危急时刻，赫蒂展开了结界。她将真理指环托付给冒险者，请求他务必找到另一件秘宝，阻止芙蕾雅的阴谋。随后，赫蒂将冒险者送到了安全的地方，只身一人迎接贞奴比亚的攻击……',}",
  [504187] = "{id=504187,Text='夜幕降临后，冒险者决定前往时间花园赴约。在花园的入口处，一朵奇怪的星降花出现了。它自称就是银发少年本人，但为冒险者必须通过考验后，它才会以原身与之相见。无奈之下，冒险者接受了星降花的要求，并在考验中结识了小妖精一族的萨满维维塔。\n在顺利通过考验后，银发少年如约出现。他自称布兰迪斯。布兰迪斯告诉冒险者，浮空精灵一直被束缚于彩虹桥的使命下，彻底失去了前往外界的自由。同时，也是正是因为彩虹桥，他的兄长死亡，老师失去踪迹。因此，布兰迪斯对彩虹桥心怀愤懑，立誓要彻底破坏它。他的决心打动了冒险者，两人正式成为同伴。\n随后，布兰迪斯便透露了他下一步的计划：使用隐身药剂潜入蔚云之海，炸毁修复彩虹桥所用的法阵。然而，片刻后便发生了意外——本该是布兰迪斯使用的隐身药，全部洒在了冒险者的身上！情急之下，冒险者接受了前去炸毁法阵的重任……\n在蔚云之海，冒险者完成准备工作后，爆炸如意料之中的发生了。烟雾弥漫中，布兰迪斯率领着小妖精们出现了。云海中瞬间陷入了混乱，浮空精灵与小妖精们剑拔弩张，而布兰迪斯与赫蒂二人也陷入对峙。\n随着大祭司贞奴比亚一声令下，浮空精灵正式对小妖精开战。在双方胶着时，一股诡异的紫色魔法忽然弥漫开来，整个云海顷刻便被浓郁的紫雾笼罩。下一秒，意外发生了——原本与小妖精交战的菲利晕倒在地，而浮空精灵们在紫雾中渐渐都失去了意识！一时间，所有人都陷入震惊与慌乱中。贞奴比亚怀疑是布兰迪斯所为，后者匆忙辩解未果后，带着剩下的小妖精匆匆离去。\n布兰迪斯原本的计划彻底偏移了。面对突如其来的诡异紫雾，他陷入了沉思。而此刻，面对浮空精灵与彩虹桥的阴谋，才刚刚显露出冰山一角……\n布兰迪斯为了调查已故兄长的线索，和冒险者一起找到了卡德曾经的未婚妻，“彗光的魔女”塞西莉亚。然而，塞西莉亚的冷漠让两人困惑不已。临别时，她给布兰迪斯留下了一枚水晶石手链，那是正是卡德的遗物。\n冒险者与布兰迪斯调查手链后，发现了一句奇怪的谜语。他们跟随谜语的提示来到了时间花园，却意外在浓雾中看见了卡德的身影……见到兄长的布兰迪斯情绪激动，而此时，一股神秘的力量将两人拉入了花园的深处。在那里，他们见到了布兰迪斯的老师，智慧巨人密米尔。\n密米尔告诉了两人一则预言：“无垢的晨曦与夜空之星，将于风中寻回失落的秘宝，重构彩虹的辉光”。布兰迪斯正是预言中所指的星星，而所有的一切都与彩虹桥息息相关。说完这些后，一阵紫色的迷雾渐渐笼罩了时间花园，密米尔随之陷入了沉睡。新的谜团出现了，此时的冒险者和布兰迪斯并不知道，这一切都只是悲剧的序幕……\n布兰迪斯与冒险者再次遇到了塞西莉亚。这一次，她见到了两人之间的默契与羁绊，态度不再像之前那样针锋相对。塞西莉亚走后，她留下的手链再次发出了奇异的光芒。而冒险者与布兰迪斯顺着光芒寻找，意外发现了一根琴弦。那是月桂琴的一部分，它也是布兰迪斯与他哥哥卡德的回忆之物。\n两人顺着手链中留下的谜语，将原本残缺的月桂琴修复完整。布兰迪斯弹奏了回忆中的旋律，两人也因此进入到了琴中隐藏的记忆中。在那里，布兰迪斯见到了过去的自己与卡德。卡德最后的自白，解开了布兰迪斯一直以来对哥哥的心结。而此时，塞西莉亚出现了。原来，她一直与密米尔暗中合作，就为了将两人引到这处回忆之地。\n塞西莉亚告诉他们，无论是卡德的死，还是浮空精灵正在遭遇的悲剧，这一切都是芙蕾雅女神所为。要想阻止女神，必须找到密米尔预言中的秘宝。等说完这些后，塞西莉亚便在女神的诅咒中死去了。兄长与塞西莉亚的死，让布兰迪斯更加坚定了与女神为敌的决心。彩虹桥带来的风暴，也即将真正降临……\n冒险者在寻找布兰迪斯的途中，遭遇了神秘黑衣人的袭击，险些丧命。布兰迪斯及时赶到，救下了冒险者。两人察觉到，芙蕾雅女神正在监视自己，黑衣人也是她派来的。商议片刻后，冒险者与布兰迪斯决定立刻出发寻找秘宝。\n布兰迪斯身上的星辰魔法，与秘宝似乎产生了不可思议的共鸣。两人一路寻找，终于在花开伊始之地发现了秘宝——那是一枚拥有星辰刻印的戒指，名为虚妄之戒。两人触碰戒指后，来到了过去的辰星殿堂。在那里，两人看到了令人震惊的真相：卡德被芙蕾雅与贞奴比亚杀害，而他灵魂的力量则被女神的利用，成为了禁锢浮空精灵一族的魔咒。精灵们从此失去了关于密米尔与卡德的记忆，将女神奉为唯一的信仰。而女神做的这一切，都是为了修复彩虹桥，回到神域。在那里，她准备实施某个更加危险的计划……\n从虚妄之戒中的回忆离开后，冒险者与布兰迪斯遇到了诈死的阿卡娜。她正是为了秘宝而来。两人不敌全盛状态的阿卡娜，在危急时刻，布兰迪斯展开了结界。他将虚妄之戒托付给冒险者，请求他务必找到另一件秘宝，阻止芙蕾雅的阴谋。随后，布兰迪斯将冒险者送到了安全的地方，只身一人迎接阿卡娜的攻击……',}",
  [504188] = "{id=504188,Text='彩虹桥即将被修复，芙蕾雅女神就要离开，留给冒险者的时间所剩无几。他在寻找另一件秘宝的途中，遭遇了女神设下的埋伏。关键时刻，密米尔救下了冒险者。他使用伊米尔之心碎片的力量，开启了一个时空与因果交叠的空间。也只有在那里，两件原本永不会有所交集的秘宝，才有了被冒险者寻回的可能。\n冒险者进入了因果纠缠之地。这里的守护者名为拉普拉斯妖，它告诉冒险者，只有强烈的羁绊能够突破时空的法则。冒险者必须领悟自己那段羁绊的力量，才能得到另一半秘宝，见到想见之人。在即将离开空间的最后，冒险者在时空尽头见到了与自己一模一样的身影。等回过神来，他已经身处于蔚云之海，而缺失的另一半秘宝，也已经出现在自己的手中。\n还没等冒险者思考其中的缘由，云海之上发生了强烈的震动。浮空精灵与小妖精们都倒地不醒，而法阵旁，布兰迪斯与赫蒂也已身受重伤。此时，贞奴比亚出现了。她看穿了冒险者要破坏彩虹桥的意图，对其展开致命一击。关键时刻，一个熟悉的身影挡在了冒险者的身前。在冒险者陷入震惊与慌乱时，贞奴比亚趁机汲取了最后的魔力，彻底将彩虹桥修复完成。\n在彩虹的辉光中，芙蕾雅女神缓缓出现在了云海之上。此刻，她依然对秘宝念念不忘。贞奴比亚逼问冒险者秘宝的下落，两人展开最终的决战。千钧一发之际，冒险者身上羁绊的力量，让他战胜了原本近乎无敌的神使。而女神见到夺取秘宝无望，便准备抛下贞奴比亚，独自从彩虹桥离开。\n最后的时刻，布兰迪斯与赫蒂并没有放弃。他们的决心，激发了秘宝的力量。两枚指环在空中融为一体，化作一道巨大的屏障，将整个辉煌领域阻隔其中。芙蕾雅被这股强烈的魔力所伤，重新跌落云岛。为了还未实现的计划，女神献祭了贞奴比亚的生命与灵魂，最终打破屏障，脱离了辉煌领域，消失在彩虹桥的尽头。\n随着芙蕾雅的离去，精灵们将逐渐回到原本正常的生活，布兰迪斯与赫蒂也找到了自己未来的道路。此时，冒险者在辉煌领域的旅途也告一段落。望着云端的彼岸，他心中明了：自己终有一日将越过彩虹桥，与那位美丽但邪恶的神祇再次对决。',}",
  [504192] = "{id=504192,Text='幻想之书本是克魔岛的圣物，近来却由于不明原因，不断泄露出黑暗的邪恶气息。前来度假的冒险者只能临危受命，应村长阿罗哈的请求，出手净化幻想之书。\n出人意料的是，冒险者在净化过程中与幻想之书产生了共鸣——一阵耀眼的光芒后，他彻底失去了意识……\n冒险者从昏迷中醒来，发现自己来到了书中世界：幻想乡。贤者赛纳斯向冒险者介绍了这里的基本情况，又安排他参与蔷薇话剧社的排演。通过剧本，冒险者了解到黑色恶魔的存在——正是因为这种魔物破坏幻想乡，幻想之书才出现异常。\n就在冒险者与赛纳斯交谈时，黑色恶魔突袭城镇。冒险者帮忙解救遇袭的居民，却误将一个黑发红瞳的女孩，当做变身后的恶魔。尽管误会解除后，两人有了并肩作战的经历，可女孩十分记仇，与冒险者不欢而散。随后，冒险者才从赛纳斯口中得知：女孩叫做涅拉，同样来自克魔岛。\n冒险者尝试和涅拉修好，并在此过程中，加深了对她的认识——因为异于常人的外貌，涅拉被岛民视为“不祥之子”，受到欺侮和排挤。所以，她十分讨厌把自己当做坏蛋的人，渴望得到认可和喜爱。\n经过不懈的努力，冒险者最终消除了涅拉的怨气，两人结为伙伴。偶然间，他们还找到了恶魔的据点，发现一名遭到囚禁的白衣女性。涅拉指认，这位女性是幻想乡重要的“守护者”。经过一番苦战，两人击败看守的恶魔，成功救出了她。\n守护者自称密斯特，还提到了“魔女”、“封印”等字眼。冒险者又召来赛纳斯讨论，终于理清来龙去脉——原来，一切动乱的元凶，是名为灾厄魔女的强大邪灵。她想要挣脱日渐衰落的封印，才指使手下四处作恶。\n为了守护幻想乡的安宁，冒险者等人向密斯特请教对策。密斯特表示，唯有“预言中的女孩”有能力修复封印、阻止魔女。涅拉惊喜地发现，她与“预言中的女孩”有很多相似之处。随后，她又通过了密斯特的考验，坐实了救世主的身份。\n于是，在密斯特的带领下，一行人深入禁忌之地，想将涅拉送到封印之前。然而途中，他们却被控诉为“邪恶之徒”，并受到各种生灵的抵抗。种种蛛丝马迹，以及密斯特越发诡异的态度，令这段救世之旅蒙上疑云……可涅拉已经被虚荣冲昏头脑，她急切地渴望成为救世主，得到所有人的爱戴和赞美。\n终于，一行人来到了封印之前。涅拉献出了她的力量，由密斯特注入封印之中。一声剧烈的轰响过后，封印不仅没有被修复，反而彻彻底底地崩溃。突破束缚的魔女之力涌入了密斯特的身体——原来，她根本不是守护者，而是灾厄魔女逃出封印的一缕分身。魔女尽情嘲笑遭到利用的众人，并称涅拉为自己的祭品，想要吞噬掉她。冒险者拼死抵抗，赛纳斯抓住机会，将三人传送回了童话王国。\n涅拉羞愧又自责，哭着逃回了书外世界。赛纳斯也不得不带领幻想乡，应对复苏的灾厄。冒险者则困惑于灾厄魔女与涅拉之间的联系。而这答案，或许还要从克魔岛找起……',}",
  [504193] = "{id=504193,Text='暂时告别克魔岛的冒险者，又一次收到关于涅拉的消息：停留在现实中的她，再度被灾厄魔女的侵扰，灵魂迷失在未知的空间。通过岛上祭祀的帮助，冒险者进入了涅拉所在的混沌裂隙。他跨越无数幻象，了解到涅拉不幸的过往，也粉碎了魔女想强行带走她的阴谋。\n获救的涅拉与冒险者一同回到梦幻城，她本以为自己会因为放出魔女，而遭受怨恨和厌恶。可梦幻城的居民，不仅没有责怪她，反而报以关怀和谅解。涅拉被居民的善意打动，她不再逃避，决定留在梦幻城与大家并肩作战。赛纳斯也向冒险者分享了最新的发现：已逝的梦幻女王，似乎在幻光森林留下了针对魔女的武器。\n不过，眼下的幻光森林正被魔女控制，她在那里建立的要塞，成为了通向希望的阻碍。为了组建强大的团队，冒险者和涅拉向梦幻城中的“能人异士”发送召集令。经过一些小小的波折，两人完成了自己的任务。罗娜、克里夫等人相继成为冒险者的新伙伴，一只团结又勇敢的队伍，向着幻光森林进发。\n在伙伴们的助战下，冒险者与涅拉突破敌人的重重阻拦，朝着森林深处的光阴神木前进。离奇的大雾，使得两人与大部队失散；梦幻女王的残影现身于神木之下，并向两人授予存放“晨星”的宝盒。可如何利用“晨星”击败魔女，仍旧是个迷。\n冒险者等人，只能带着宝盒返回梦幻城。随着宝盒缓缓开启，星光四溢开来，构起了一个奇妙的领域。伙伴们各自陷入梦幻，涅拉则似乎在和什么人争辩着，发出抗拒的呼喊……最终，闪亮的晨星没入涅拉体内。可她脸上没有当上英雄的喜悦。涅拉解释说，她没有觉醒特殊的力量，也不知道打败魔女的方法。随后，她就情绪失控地跑出了梦幻城。\n冒险者赶快追上去，想了解涅拉身上发生了什么，然而还没来得及问出关键，梦幻城的方向传来巨响。一群慌乱的居民跑向冒险者和涅拉，告诉他们：魔女为消灭“晨星”这个隐患，正在强攻梦幻城。闻言，涅拉彻底崩溃，她怀着某种目的，决然地跑向梦幻城。而冒险者也紧随其后，试图救援还困在城里的居民。\n昔日宁静美丽的城市，染上了火光与死亡。尽管冒险者竭力抢救，还是永远失去了一些伙伴。同时，赛纳斯也在拼命阻拦魔女，他宁可牺牲自己，也不愿“晨星”的光辉被邪恶熄灭……然而，超乎所有人意料的一幕发生了——在混战中，涅拉竟然从背后偷袭了赛纳斯，选择向魔女投诚。\n伴随着张狂的狞笑，灾厄魔女带着涅拉飘然离去。尽管她暂时放过了梦幻城的幸存者，可每个人的心头，都蒙上深重的阴影：魔女将利用涅拉做什么？书中世界的生灵，是否还有可能战胜灾厄？幻想乡的未来，又该何去何从……',}",
  [504194] = "{id=504194,Text='经历涅拉的背叛，又失去女王留下的“晨星”，深感绝望的梦幻城居民决定放弃家园，出逃躲避魔女的迫害。尽管非常不舍，冒险者还是帮助大家收拾行装，并在这一过程中发现了被丢弃的神秘宝盒。宝盒中残存的光辉将冒险者传送到了时空裂隙中，在那里，他再次与奥菲莉娅女王的残影相逢。女王的残影告诉冒险者：魔女并非不可战胜，她存在致命的弱点。同时，她也暗示冒险者，“重聚的友谊和信任，将指引胜利”。重返现实后，冒险者将这则消息带给了居民，梦幻城的居民就是否还要对抗魔女发生争执。不过最终，贤者赛纳斯及时复苏，他说服大家按照女王的预言，集结全部的力量和魔女决战。\n由于敌众我寡，冒险者一行组建了先遣队，潜入魔女的大本营——灾厄领域，打算先削弱其中的防御力量。在探查的过程中，冒险者听到了重要的情报：魔女带走涅拉，是为了恢复实力；魔女正在准备所谓的“融合仪式”，一旦仪式完成，她便会吞噬涅拉。冒险者等人当然不能坐视魔女的计划得逞。伴随着神秘星光的指引，先遣队快速消灭了灾厄领域的守军，又建立传送阵，让更多伙伴加入助战。而在继续深入的过程中，冒险者又一次见到了涅拉。昔日战友变成如今的敌人，冒险者一行尝试截击涅拉，阻止她前往魔女身边，无奈还是迟了一步——魔女终究完成了仪式，并将涅拉召唤到了她的身边。冒险者拼命追赶，也只来得及见证魔女完全吞噬涅拉的一幕。冒险者带着悲愤的情绪和魔女展开最终决战，然而恢复实力的魔女果然非常可怕，她酝酿的招式将所有人击倒在地。就在魔女将要处决众人时，她的体内竟然迸发出刺眼的光亮：那是涅拉化作的晨星。涅拉告诉大家：“我来牵制魔女，你们攻击星光所在，那便是魔女的弱点。”\n集结所有人的力量，冒险者最终击破了魔女的弱点，后者哀嚎着倒下。同时，大家也看到涅拉的记忆：原来她并没有背叛，而是牺牲自己，创造击败魔女的条件。随着回忆消失，所有人都看见一个小小的、纯净的，却濒临破碎的灵魂。伙伴们流下悲伤的眼泪，涅拉却显得释然。她表示梦幻城的经历，是她最美好的回忆；而守护大家，是她最幸福的选择。接着，她露出灿烂的笑容，化作光点消失。而在她的脚下、被魔女腐蚀的大地上，留下了一丛新生的、洁白的小花……',}",
  [504195] = "{id=504195,Text='卢恩·米德加尔特王国的现任国王特里斯坦，已经失踪了半年。偌大的王国不能没有国王，于是王国最为尊贵的七王室决定举办选王仪式，七大家族各推举一位候选人，参与下一任国王的竞选。冒险者也收到了邀请，去见证这一历史性的时刻。\n然而在王宫的门口，冒险者在围观七王室入城的盛大队列时，无意间与一位王室少女的目光交汇了，冒险者的脑海中出现了奇怪的画面：尸体遍布的皇宫，跪坐在地上哭泣的女人，鲜血滴落在了红色地毯上。这些画面似乎是那位王室少女的梦境，但一切又似乎那么的真实。冒险者感到了这个梦境的不同寻常，他立即进入了王宫。\n王宫内，七王室的成员们似乎都聚集在正厅里，三三两两的交谈着。冒险者一眼就看到了站在角落，独自一人的王室少女。这个少女名叫爱葛妮斯，是雷根弗莱格家的小姐。她从小就拥有预知梦的能力，这次她会前来普隆德拉参与王选仪式也是为了她的梦境。而暗恋者爱葛妮斯的海涅家少爷尼希尔也担忧着爱葛妮斯的情况。\n此时，现任国王特里斯坦的儿子，奈特王子出现在众人的面前，他强势地宣布自己将在明日继承王位，并让女仆爱丽丝准备好代表王权的权杖。这一决定让在场的王室成员们非常的不满，但奈特利用王宫侍卫的指挥权，逼迫王室们明天参加自己的继位仪式。\n第二天，王室们陆续聚集到了王室之间，奈特王子与众人一一握手后，走上了通往王座台阶，并在王座前接受主教的祝福。此时爱丽丝手捧着装着权杖的银盒来到奈特王子的身边，王子得意地伸手拿起了权杖，但是没想到就在他准备宣布自己正式继位之时，他的嘴角流出了黑色的血，随后他就倒在地上，一动不动。\n现场一瞬间陷入了混乱之中，王后立即调集侍卫对在场的所有人进行搜查。医生卡尔在对王子的尸体检查后告知大家，奈特王子的手心有接触后不久就会死亡的剧毒，而且权杖上面也沾满着毒药。而女仆爱丽丝在侍卫的搜查中被发现身上藏有这种毒药，并且她是最后一个接触过权杖的人。王后立即让侍卫将爱丽丝押送进了大牢。\n案件看似告了一个段落，但爱葛妮斯和尼希尔却提出了疑问。首先，会有凶手在犯案后仍然将如此重要的证据藏在自己身上吗？其次，爱丽丝生性胆小，怎么看她也不会是这个案件的凶手。冒险者与爱葛妮斯和尼希尔一起找到王后，并请求让她授予在王宫中自由调查的权利。\n就在冒险者一行人的调查有所进展之时，爱丽丝明天将接受审判的消息传来，三人决定用调查所获得的证据在明天的庭审上为爱丽丝证明清白。\n庭审如期举办，冒险者一行三人在众人面前用证据揭露出真相，原来，爱丽丝十分胆小，皇宫中又谣传着闹鬼的事情，她不敢自己去密室拿取权杖，于是拜托了王宫侍卫长马丁在深夜无人之时，帮她从密室中拿出权杖，并放进了主教的银盒之中。整个过程爱丽丝都没有接触过权杖，也就没有时间能够下毒。\n虽然庭审中证实了爱丽丝的清白，但是仍然留下了很多疑点。由于毒药是在爱丽丝身上被发现的，尼希尔便向她询问案发当天是否有人靠近过她的身边。爱丽丝回忆起来，在案发的当天她曾经被珀·力哈尔特撞倒过。这让调查有了新的方向，冒险者一行人围绕着珀展开了一系列的调查，确认了案件是珀所为。\n尼希尔将一切禀明了王后，并召集大家再次到王座之间召开庭审。冒险者当庭指认了珀为杀害王子的真凶，并用证据为众人还原了珀的作案手法。原来毒药并不是被涂抹在权杖上的，而是案发当天珀涂抹在自己的手套上。王子入场与他握手时，接触到了毒药从而导致了死亡。珀在众人的视线被王子吸引之时，将有毒的手套戴在了雕塑之上，躲避了侍卫的搜身。\n至此，这个案件已经明朗，王后命令侍卫将珀关押了起来。但此时爱葛妮斯的突然晕倒让众人陷入了混乱之中。而冒险者的脑海中再次出现了爱葛妮斯的预知梦，这一次的画面与上一次的不同，王宫之中将会掀起新的风波……',}",
  [504196] = "{id=504196,Text='奈特王子的不幸离世让整个米德加尔特王国都沉浸在悲痛之中。\n冒险者和爱葛妮斯等人受邀参加王子的追悼会。在现场，一个似曾相识的背影和熟悉的转身动作让爱葛妮斯突然意识到，梦里那个哭泣的女人应该就是王后凯瑟琳。那么凯瑟琳或许能帮她解决心中的疑惑：梦里那个会在未来死去的人，到底是谁？\n就这样，冒险者一行人在花园找到了王后，以及与她愈加形影不离的乐师艾伦斯特。两人暧昧的言语让“艾伦斯特表面上是宫廷乐师，实际上是王后情人”的传言显得更加可信。记者弗兰克想趁机拍下两人亲密的照片，却不小心将他和冒险者一行人暴露在众人的视线中。\n未等愤怒的王后兴师问罪，七王室中从事海上贸易的王室家族族长，老国王的御前首相伊安·巴尔特大人带着他的儿子阿伦与阿诺出现在了花园。伊安之前就看不得王后和乐师的亲昵，此时此景让他更加愤怒。借着酒劲伊安和乐师起了争执。在冒险者的帮助下，众人才勉强把伊安从王后和乐师身边拉开。王后非常生气离开了花园，冒险者一行人想要跟王后讨论预知梦的计划扑了个空。\n就在众人沮丧时，阿伦·巴尔特告诉艾伦斯特和冒险者一行人，他的父亲只是过度感伤王子陛下的离世，借酒消愁饮酒过量才会如此冒犯。伊安·巴尔特本是想邀请王后陛下等人参加自己的生日宴，希望能借此宽慰王后，愿她能早日从丧子的痛苦里走出来。为此伊安还下令将家中珍宝金弦琴拿出来送给艾伦，让他能用金弦琴为王后陛下弹奏。\n阿伦真诚的道歉消解了之前的诸多不快。做为艾伦斯特在王宫里唯一的朋友，艾伦斯特表示自己不会拒绝阿伦的邀约。至于冒险者一行人，大家觉得生日宴是能再次得见王后的机会，便也接受了邀请。\n伊安·巴尔特的生日宴会如期举行，王室成员悉数到场，欢声笑语热闹非凡，完全不像是刚经历过一场生死离别的样子。宴会上伊安告诉众人，乐师艾伦斯特会带着巴尔特家的珍宝”金弦琴“进行演出。话音刚落，休息室内便传出艾伦斯特的尖叫。众人闻讯赶来，看到休息室内血流满地：伊安·巴尔特最心爱的儿子，谦逊且温柔的阿伦被人用匕首刺中胸口，倒在血泊中。他的旁边还躺着一位陌生的死者。\n在生日当天痛失爱子的伊安不允许任何人以任何理由离开，王后陛下便命冒险者一行人配合法医卡尔调查案发现场，希望能尽快查明事情的真相。\n通过目击者艾伦斯特的证词，众人得知那位陌生的死者就是杀害阿伦的凶手，他在杀害阿伦后吞药自杀。但没有人知道这个杀手是谁，他和阿伦之间会有什么仇怨。\n在调查现场的过程中，冒险者偶然发现凶手杀害阿伦的凶器，居然是爱葛尼斯的哥哥海尔穆特的私人物品。海尔穆特一口咬定自己的匕首丢失，却拒绝说出丢失的具体信息。加上侍卫长马丁的证词，海尔穆特成为杀害阿伦最大的嫌疑人，王后只得下令将海尔穆特关进地牢。\n但海尔穆特和阿伦之间素来没有仇怨，爱葛尼斯觉得哥哥不会是幕后指使者，她拜托冒险者和尼希尔陪她前往关押哥哥的地牢，她想要查出到底是谁想要陷害海尔穆特，陷害雷根弗莱格家族。 \n天一亮，冒险者一行人就迫不及待前往地牢和海尔穆特见面。在问及匕首的事情时，海尔穆特明显说了谎。冒险者用搜集来的证据攻破了海尔穆特的谎言，了解到海尔穆特不愿说出匕首丢失的真正原因：海尔穆特奉命护送的重要药材曼德拉草失窃，海尔穆特本想在众人神不知鬼不觉的情况下弥补这个失误，却不想被黑市商人摆了一道，不仅没有拿到草药，还丢失了自己贴身的匕首。\n虽说得知了事情真相，但依旧没有任何证据可以证明海尔穆特是无辜的，除非能抓到盗窃曼德拉草的士兵安德鲁，让他出庭作证。海尔穆特告诉冒险者，自己的好伙伴——草药商人多娜或许能帮上忙。\n冒险者在多娜的住所居然真得找到了能证明海尔穆特在案发前就遗失匕首的证据。除此之外，他们还了解到安德鲁偷窃曼德拉草是为了拯救自己病重的孩子。\n已经获得充足证据的冒险者一行人正准备离开，突然发现安德鲁好像知道巴尔特家的很多事情。狐疑中的冒险者将案发现场的一个证物拿给安德鲁的太太，被告知居然是安德鲁的物品。难道冒险者一行人偶然间调查出了杀手的身份？但如果一切都是真的，那这个凶杀案的幕后指使者到底是谁？\n王座之间的庭审如期举行，海尔穆特与阿诺吵得不可开交。冒险者一行人拿出搜集到的证据，证实了海尔穆特的清白。众人也在庭审中得知杀害阿伦的杀手名叫安德鲁，是盗取海尔穆特护送的曼德拉草的元凶。但这样的一个人为何会成为谋害阿伦的凶手还是个谜。\n但有一点可以确认，安德鲁的身上有很多能证明他与伊安·巴尔特有关的证据，冒险者一行人大胆猜测，安德鲁为了能让妻女好好生活，接受了伊安的命令：杀掉阿伦·巴尔特，并且在完成任务后自杀。\n这个推论令整个巴尔特家族暴怒，众王室成员也没有人相信伊安会杀掉自己最心爱的儿子。就在双方争执不下时，巴尔特家族的管家帕维尔站出来承认是他杀害了阿伦，主动要求王后陛下将他关入大牢。\n这个结果显然无法令人满意，但王后陛下好像并不在意事实真相，她表示有些事情有了结论就好。联想到帕维尔被关进地牢前对王后说的话以及王后奇怪的反应，冒险者一行人决定继续探查，找出事实的真相，不让任何人蒙冤，也不要让阿伦的死就此成为悬案。\n冒险者一行人再次梳理现有的证据，意识到巴尔特原本要谋害的极有可能是乐师艾伦斯特而非阿伦：巴尔特让安德鲁以金弦琴作为目标，在宴会中杀掉拿着金弦琴的人。但没想到这把琴偶然落回到阿伦的身上，导致阿伦被误杀。\n如果推论属实，那乐师做为一个深藏宫中的伶人，有什么理由让巴尔特冒巨大的风险刺杀？除非……乐师艾伦斯特还有另一重身份。\n带着这个推论，冒险者一行人找到了艾伦斯特想要了解更多的情况，却发现艾伦斯特一改往日温和软懦的性格，拒绝与众人讨论此事。调查陷入僵局的冒险者在法医卡尔的提醒下从储藏室里找到了些证据，证明乐师手臂上有陈旧的伤病需要长期服用止痛药，碰巧的是，王宫里，已经去世的王子奈特身上同样位置也有一模一样的伤痕。\n随后冒险者在王后寝宫珍藏的日记本里发现了疑似艾伦斯特写下的话，几乎可以艾伦斯特的真实身份。爱葛妮斯和尼希尔觉得，是时候去和艾伦斯特好好聊聊了。\n但众人不知道的是，艾伦斯特内心深处也在期待着他们的出现，期待冒险者帮他做出人生中非常重要的决定。\n就在冒险者一行人准备和艾伦斯特对峙的时候，卡尔带来了另一个重要的证据，他冒着巨大风险打开奈特王子的棺木，发现死去王子的胳膊上并没有任何伤痕。\n就这样，在众人的帮助下，冒险者艰难攻破了艾伦斯特的所有心锁，了解到他才是特里斯坦·乔治国王和凯瑟琳王后的亲生儿子。特里斯坦为了毁坏七王室家族契约，完成自己家族稳坐王位的计划，强行命艾伦斯特搬出永恒之宫，远离自己的生母，让他作为“宫廷乐师”，更作为自己计划的一枚棋子生活。\n艾伦原本以为自己已经接受了这个命运，却没想到自己会因此葬送了好友的性命，外表柔弱但内心坚毅的艾伦表示自己再也不想这样生活下去，便拜托冒险者召集众人，将自己是真王子的事情昭告天下，为了缅怀自己的好友，也更是为了做回自己，保护母亲不再受到父亲的压迫。\n但就在此时，巴尔特父子先后来到了现场，要艾伦斯特为阿伦偿命。冒险者制止了伊安·巴尔特的暴乱，却意外从伊安那里获得了一些永远不可能被查证但比真相更加真实的事情：\n大家都以为伊安是因为金弦琴意外易手，导致杀手认错了要谋杀的目标。但实际上伊安明确告诉了安德鲁要杀的人长什么样子，金弦琴只是其中一个参照。但安德鲁依旧杀错了人，只能说明有人得知了伊安的计划，同时利用了这个计划，杀掉了阿伦少爷，这个最有竞争力的王位候选人之一。\n这个人会躲在暗地里，把王室家族一个一个推向毁灭。\n留下这个信息后，伊安就离开了。同时爱葛妮斯也预知到了更大的“灾难”……阴谋还在继续……一切都还未结束。',}",
  [504197] = "{id=504197,Text='凯瑟琳王后召开紧急会议，宣布次日进行王选仪式。这个仓猝的决定，引起了贵族们的担忧；爱葛妮斯也通过预知梦，看到不详的预兆。为防止意外发生，冒险者三人设法劝说王后推迟王选仪式。\n可凯瑟琳王后十分固执。哪怕冒险者呈上了艾瑞希·奈利乌斯收到的恐吓信，说明有危险势力在干预王选，王后还是不肯改变计划。无奈之下，冒险者只能服从她的安排。在前往客房休息的途中，冒险者瞥见两名骑士将一口储物箱搬出永恒之宫。\n午夜时分，悲剧果然降临：艾伦斯特王子被人烧死在留宿的房间里，通体焦黑、极为惨烈。冒险者等人立刻展开调查，然而还未理清案发现场的证据，凶手似乎就已经浮出水面——海尔穆特自称目击到派特纵火，后者的自辩漏洞百出，被作为嫌疑人收押。\n凭借对派特的了解，尼希尔相信他没有杀害艾伦斯特。可想要证明这点，冒险者等人还需要找出更多证据。爱葛妮斯拿出带有宴会厅徽记的铜钥匙，提出前往餐厅和厨房进一步调查的想法。三人相约次日清晨再见。\n冒险者前往宴会厅调查，宫廷总管费尔南多却不肯配合，冒险者只能自己先展开洞察、经过一番搜索，冒险者等人发现王子房间内，竟然有许多来自厨房的易燃物品。再次逼问费尔南多后，他告知冒险者：厨房曾在艾伦斯特遇害的前一天遭遇盗窃。\n冒险者梳理手中证据，还原出凶手纵火的手法，证明艾伦斯特并非被派特的魔法火焰烧死，但这个结论仍不足以洗脱派特的嫌疑——因为派特拥有感知火焰的能力，本可以阻止王子被焚。为了搞清派特是否存在间接谋害王子的可能，冒险者又对派特展开调查。\n在派特留宿的房间里，冒险者发现了一本被施加魔咒的书册，并从中获得一封神秘信笺：原来案发当晚，派特根本不在房间，而是在远离火灾现场的角落，与一个神秘人私会。尼希尔打算查明寄信人的身份，只有这样，才能确认派特是否真的无辜。\n为查清寄信人的身份，冒险者根据信笺上的线索，寻找宫廷骑士伊柏问话。然而伊柏心中有鬼，一见到冒险者便转身逃跑。在追捕伊柏的过程中，冒险者遭遇王后禁卫长的阻拦，尽管冒险者击退了对方，但也丢失了伊柏的踪迹。\n幸好，信笺上残留的香气同样可以指引破案的方向。冒险者在爱葛妮斯的建议下，前往玫瑰歌剧院，向精通香水的宫廷贵妇们打听线索。经过一番调查，冒险者三人得出结论——那位神秘的寄信人，竟是凯瑟琳王后。\n凯瑟琳是艾伦斯特的母亲，不可能伤害对方。于是尼希尔认为证据已经齐全，与冒险者、爱葛妮斯商议次日召开庭审，为派特洗脱谋杀的罪名。\n冒险者与海尔穆特在庭审上辩论，通过还原起火经过，证明派特并未纵火杀死王子。冒险者还出示了书中的信笺，解释派特未能救下王子的真正原因。众人因派特与凯瑟琳私会议论纷纷，但凯瑟琳出面平息了骚动，并请派特讲述当夜真实的情况。\n听完派特的叙述后，众人接受了他无罪的结果，但庭审还没有结束。尼希尔猜测王子在被焚之前便被昏迷或者死亡，请求进一步尸检，寻找有关真凶的线索，可凯瑟琳不忍儿子的尸体再受破坏。一番争执后，她强制结束了庭审，并收走王子的尸体，准备在五天后将其下葬。\n冒险者等人十分无奈，只能从其他方面寻找凶手的线索。就在这时，巴尔特派手下与冒险者秘密接头：他指控凯瑟琳王后谋杀王子，并提供相关证据。作为交换，巴尔特要求冒险者在调查王后的同时，协助他调查杀死阿伦的真凶。为了真相，冒险者暂时与巴尔特合作。\n冒险者前去试探凯瑟琳王后，但她为了将王子的尸体运送到教堂，并不在宫中。另外，王后还吩咐禁卫们阻拦冒险者进入永恒之宫。对此，尼希尔提议先向侍人们打听情报。\n从侍人阿弗莱尔口中，冒险者得知一桩怪事——艾伦斯特遇害前夕，凯瑟琳和他曾爆发争吵；同时，房间内疑似还有第三人。为探究真相，冒险者等人击晕了守门禁卫，强行进入王后的寝殿。\n寝殿内的证据，说明这里确实发生过争执甚至打斗。更惊人的是，王后房间里竟然还有个密室，其中有着陌生男性生活过的痕迹。冒险者三人正要讨论时，凯瑟琳王后自教堂归来。她恼怒地召唤禁卫，想将入侵者逐出宫廷。\n冒险者击败凯瑟琳的禁卫，迫使她坐下谈判。经过逼问，凯瑟琳坦白道：自己的确在寝殿内藏匿情夫，艾伦斯特撞破了这件事，于是母子二人爆发冲突。但她否认自己因此杀害艾伦斯特。\n凯瑟琳的供词看似通顺，但仍有疑点。冒险者用手头的证据，再次对凯瑟琳进行追问。出示一沓无字信纸时，凯瑟琳的身上果然浮现心锁，可她以谎言巧妙地掩饰了过去。为查明凯瑟琳的隐秘，冒险者决定再度拜访巴尔特，寻求他的帮助。\n冒险者与巴尔特分享永恒之宫的发现，后者看到无字信纸时蓦然变色。他指点冒险者将清水喷洒到信纸，纸张上果然显出字迹：那是一连串针对王室候选人的阴谋，甚至连马丁侍卫长都牵涉其中——是他买通了安德鲁，刺杀阿伦。\n巴尔特万分悲愤，他联合冒险者，将马丁诱骗到地下监狱进行质问。马丁扛不住压力，暴露心锁：原来，王后口中的“情夫”，就是国王本人。他根本没有失踪，而是为了铲除其他候选人，才隐藏于幕后操纵局势。\n同时，马丁的供词还透露一个更离奇的细节：本该在永恒之宫的密室里运筹帷幄的国王，竟然也下落不明了，且王后刻意隐瞒了这点。\n一切案件的疑点，最终还是汇集到了凯瑟琳王后身上。可王后极度抗拒调查，冒险者一时也找不到突破口。不过，巴尔特信心满满地表示，他可以动用宫廷中的眼线打开困局。于是冒险者等人先回到客房修整，准备次日继续调查。\n巴尔特找来了小报记者弗兰克，对方表示自己曾在午夜的晨曦花园中听到异响。冒险者随即来到花园，经过一番搜证与推论，众人得到一个可怕的结论：国王极有可能被王子或王后从永恒之宫的阳台上推落，重伤不治而亡。\n然而，国王的尸体仍旧下落不明。正当大家苦思冥想时，冒险者突然忆起了一个细节——在纵火案发生前，王后曾将一口巨大的储物箱送进王子的客房中；当晚，王子客房内便发生大火，只留下一具面目不清的焦尸。\n国王的尸体是否在箱中？死者又真的是艾伦斯特王子吗？疑问盘旋在众人心头，只有证据才能解惑。于是冒险者等人突破了王后的封锁，击败顽抗的禁卫长，来到“王子”的棺木前。法医再次尸检后确认，死者并非艾伦斯特，而是国王特里斯坦·乔治。\n听闻国王的死讯，巴尔特癫狂大笑。他似乎了却心愿，准备离开普隆德拉。而冒险者却还肩负着王室侦探的责任：他们必须澄清死者的身份，让杀人者接受应有的惩罚。\n怀着复杂的心情，冒险者等人离开墓地。在他们身后，被击败的王后禁卫长去而复返。他对王后的守护之心，似乎并不是纯粹出于忠诚……\n冒险者一行人进入王座之间，打断了凯瑟琳王后举办的悼念仪式。尼希尔揭露死者是国王的真相，王后仍旧不肯认罪，抵死狡辩。但面对越来越的证据，她最终哑口无言，暴露出心底的隐秘。\n为了解凯瑟琳谋害特里斯坦国王的动机，冒险者进入了她的心象世界。在那里，冒险者看见了王后的悲惨过往：她与国王的婚姻纯粹出于政治，而国王从未给予过她爱意与尊重。在晦暗的生活中，唯有艾伦斯特支撑着王后濒临崩溃的心智，王后所期待的只是母子两人相依为命，哪怕艾伦被迫成为乐师，无法唤他一声母亲。然而，阿伦之死引起了王子与国王的决裂。争斗的过程中，王子失手将国王推落阳台。为让儿子免受弑君的死罪，凯瑟琳自编自导了这起纵火案。\n明白大势已去，凯瑟琳王后决定牺牲自己。她恳请冒险者隐瞒部分事实，让她作为弑君者接受审判，以避免已经逃走的艾伦斯特再受追捕。然而，如此卑微的心愿最终也没有实现——巴尔特带领着一大群王室贵族突然现身，他们竟然在暗处，听完了这一场特殊的“庭审”。\n刻板而冷酷的王室贵族们，不顾案件的隐情，打算以艾伦斯特与凯瑟琳的死，捍卫王室的权威与尊严。王后发出绝望的悲泣，却被突然出现的禁卫长救下——原来，他就是艾伦斯特。忧心母亲的他从未按计划逃离卢恩，而始终在教堂等待着。而巴尔特也亮出了自己的獠牙——他派出所有私兵，打算将王后母子当场斩杀，以祭典爱子阿伦。\n出于对正义的维护，和对王后母子的同情，冒险者主动阻击巴尔特与他的手下。艾伦斯特也拼死战斗，带着凯瑟琳往宫门外撤离。突然，一只暗箭袭向艾伦斯特的后背，凯瑟琳下意识张开手臂掩护儿子，利箭刺入了她的心口。\n被囚困一生的母亲，到死也没能逃离这座宫廷。而被独自留在人间的孩子，也失去了生存的意义。崩溃的艾伦斯特抱起凯瑟琳的尸体，顶着箭雨一步一步向着宫门走去，但最终，他们谁也没能走进触手可及的自由之中……',}",
  [504198] = "{id=504198,Text='饱含英雄之力的神圣之作伊米尔之书从无限书库中失踪，同时，被封印的魔剑上的残魂也逃离了达纳托斯塔不知去向。女武神拉丝格瑞斯感知到无限书库中残留的魔剑气息，意识到伊米尔之书已被魔剑盗走。拉丝格瑞斯委托冒险者去守护一块本应镶嵌在伊米尔之书扉页的宝石。这块宝石是唯一能开启并且使用书中英雄之力的钥匙，魔剑若是想要使用伊米尔之书的力量，必须得到命运宝石。而宝石一直由沉睡在天地树树根处泉水里的少女保管着。冒险者临危受命，踏上了寻找宝石与伊米尔之书的旅程。\n冒险者来到了赫瓦格密尔寻找命运宝石，尼德霍格对冒险者的到来十分警惕。就在双方对峙之时，达纳托斯的出现阻止了双方的冲突。奇怪的是，尼德霍格称呼达纳托斯为自己的妹妹。尼德霍格告诉冒险者，不久前，达纳托斯的游魂出现在了她的梦里。尼德霍格从达纳托斯的气息中感知到了自己的妹妹，维德佛尔尼尔的力量，她可以肯定，达纳托斯就是她妹妹的转世。虽然她并不知道为什么达纳托斯的游魂会在梦境中出现，但思念妹妹至深的尼德霍格不顾一切用自己的力量，将达纳托斯带出了梦境。但作为游魂的达纳托斯没有曾经的力量与记忆，甚至头脑也不是很清楚，只能靠尼德霍格的力量勉强存活。当尼德霍格知道冒险者的来意后，表示并不介意将宝石交给冒险者。但她希望冒险者能帮助她一起护送达纳托斯的游魂去往尼夫海姆。处在生死间隙中的游魂，只有死亡女神海拉才知道该如何救治，刚刚苏醒又耗费巨大能力的她需要冒险者的陪伴才能前往那片死灵之地。',}",
  [504199] = "{id=504199,Text='三人一起来到了死者之都尼夫海姆，这里的死亡气息让尼德霍格十分不适，她身上的生命之力在这里会被排斥，甚至有可能引发混乱。在酒馆中的一番调查后，三人得到了隐藏气息的死灵之酒以及如何见到海拉的方法。尼德霍格为了尽快培育出海拉最喜欢的南瓜，不惜伤害自己取下龙鳞来加速南瓜的成熟。',}",
  [504200] = "{id=504200,Text='等待南瓜成熟后，三人喝下死灵之酒，假扮成死灵成功进入到海拉的宫殿里。海拉对达纳托斯的游魂十分感兴趣，她已经很久没有见到处于这种状态边缘的人类了。她想留下达纳托斯作为自己的玩物，但是在她真正碰触到了游魂之时，她流下了眼泪，在达纳托斯的游魂残留的记忆中看到了塞壬。海拉帮助达纳托斯修补了灵魂，达纳托斯的眼睛恢复了清明，虽然大多数的事情他还是不记得，但是他记得有一个名为希尔斯的姑娘，他必须回到那个姑娘的身边。海拉表示记忆是构成灵魂的关键，没有记忆，灵魂迟早会在时光中迷失自我，再度变成游魂，而游魂只能存活在梦境中，是无法在世间存活也无法转世。而作为当之无愧的人类第一英雄的达纳托斯，或许能在伊米尔之书中找回他的记忆。',}",
  [504201] = "{id=504201,Text='无论是为了世界安稳还是达纳托斯，找回伊米尔之书都迫在眉睫，尼德霍格告诉冒险者，命运宝石是伊米尔用她的龙鳞与自己的血液交融在一起制造出来的，所以，命运宝石可以感应到同样拥有着伊米尔血液的伊米尔之书的所在。三人跟随着命运宝石的指引发现魔剑带着伊米尔之书居然躲藏在只有龙族才知道的龙族秘境之中。而秘境入口的封印，也只有尼德霍格和妹妹维德佛尔尼尔才知道如何解开。太多的巧合，过于顺利的寻找以及龙族秘境周围的一些古怪都让达纳托斯产生了警惕。',}",
  [504202] = "{id=504202,Text='尼德霍格却对这一切毫无察觉，她提出在进入龙族秘境后，用命运宝石作为诱饵将偷书贼引入陷阱后一举抓获。她的计策成功引来了诡异黑影，想要抢夺命运宝石，败在冒险者与达纳托斯手下后，黑影露出了自己的真实的面貌——是一个与尼德霍格长得一模一样的少女！此时的尼德霍格才意识到达纳托斯真的不是自己的妹妹，眼前的这个少女才是真正的维德佛尔尼尔。维德祈求姐姐的原谅和拯救，达纳托斯却从维德佛尔尼尔的话中听出了谎言，但是尼德霍格虽然对于妹妹还活着的事实很震惊，但是她仍然选择帮助自己的妹妹。尼德霍格不顾冒险者与达纳托斯的反对，用命运宝石为妹妹激活了伊米尔之书来恢复力量。\n尼德霍格全部的力量都用在维持命运宝石之上，切断了所有与外界的感知，而维德佛尔尼尔阻挡着冒险者与达纳托斯靠近尼德霍格，随着她的力量逐渐恢复，维德佛尔尼尔露出了自己的真面目。原来从达纳托斯的游魂进入尼德霍格的梦中开始，一切都是她利用姐姐对自己的感情所策划的阴谋，目的就是为了夺取伊米尔之心的力量。然而，就在她得意洋洋要将二人斩杀之时，尼德霍格原本紧闭的双眼突然睁开，将伊米尔之书合上了，伊米尔之书的光芒消失了。维德弗尔尼尔错愕的转头看向自己的姐姐。尼德霍格伸手将一束金光打在了达纳托斯的身上，达纳托斯从金光中冲出，拿起武器冲向了维德弗尔尼尔。维德弗尔尼尔威胁达纳托斯，告诉他自己死了靠魔剑气息才活下来的游魂也会灰飞烟灭。达纳托斯的剑却依然毫不留情地刺穿了维德弗尔尼尔。维德弗尔尼尔痛苦地尖叫着质问自己姐姐为什么要这么做，尼德霍格表示，虽然她将维德佛尔尼尔视为与自己生命等重之人，但世间众生也是同样的。维德佛尔尼尔被击杀，她吸收的英雄之力恢复成了书页，书页凌乱地飞舞在空中，随着冲击散落到了世界各地。一切都恢复了平静，尼德霍格跌坐在地上，几近消失的达纳托斯向她伸出了手拉她站起来，却没想到最后在尼德霍格的手心化作伊米尔之书中的一页。\n尼德霍格失声痛哭，她在伊米尔之书中看到了达纳托斯的记忆。原来当年维德佛尔尼尔在与梦罗克联手背叛了姐姐尼德霍格，在被尼德霍格发现并击败后神魂失去依凭，来到了深渊之湖寄宿在了魔剑赫格尼之上。而后维德佛尔尼尔并不如自己所说的那样陷入沉睡，而是作为魔剑蚕食着达纳托斯。所以她关上了伊米尔之书，将记忆传给了达纳托斯，但是没想到，恢复记忆的达纳托斯最终仍然为了这个世界选择再次牺牲自己。尼德霍格认为自己的私心差点酿成大祸，她不配继续做命运宝石的守护者，她将命运宝石交给了冒险者，将寻回所有书页的重任托付。尼德霍格再次陷入了沉睡，或许在梦里，她能与达纳托斯重逢。',}",
  [504208] = "{id=504208,Text='普隆德拉第一铁匠在此',Speaker=827513,}",
  [504258] = "{id=504258,Text='这就是火焰力较多的霜炎水晶，我们搜集一部分用以唤醒火焰龟！',Speaker=827907,}",
  [504259] = "{id=504259,Text='敲击水晶时可能会有危险，[c][ffff00][PlayerName][-][/c]要当心。',Speaker=827907,}",
  [504260] = "{id=504260,Text='冒险者当心！',Speaker=827907,}",
  [504261] = "{id=504261,Text='接下来就是带着水晶，去古人留下的雕塑旁唤醒沉睡的火焰龟，跟我来。',Speaker=827907,}",
  [504264] = "{id=504264,Text='就是这里，把带来的水晶放一些在雕塑前吧！',Speaker=827907,}",
  [504265] = "{id=504265,Text='这就是火焰龟，战胜它们吧！',Speaker=827907,}",
  [504266] = "{id=504266,Text='这就是制作熔焰护甲的火之灵，最后，我们还需要火焰背甲的碎片。',Speaker=827907,}",
  [504267] = "{id=504267,Text='传说中，拥有火之灵后，会吸引来巨大的火焰龟……',Speaker=827907,}",
  [504268] = "{id=504268,Text='就是它，一鼓作气战胜这个巨大的火焰龟吧！',Speaker=827907,}",
  [504269] = "{id=504269,Text='我来看看：水晶、火之灵、 铠甲碎片。这些就够了，我们去找[c][ffff00]忽克连[-][/c]帮忙打造熔焰护甲吧！',Speaker=827907,Option='忽克连？',}",
  [504270] = "{id=504270,Text='当然，你不会以为我会制作龟甲吧？',Speaker=827907,Option='那先回艾尔贝塔吧',}",
  [504283] = "{id=504283,Text='冒险者当心！',Speaker=827907,}",
  [504284] = "{id=504284,Text='接下来就是带着水晶，去古人留下的雕塑那儿唤醒沉睡的水灵龟，跟我来。',Speaker=827907,}",
  [504285] = "{id=504285,Text='乌龟岛上散落着不同的元素之力。',Speaker=827907,}",
  [504286] = "{id=504286,Text='但暂时还不清楚它们出现的原因。',Speaker=827907,}",
  [504287] = "{id=504287,Text='就是这里，把带来的水晶放一些在雕塑前吧！',Speaker=827907,}",
  [504288] = "{id=504288,Text='就是它们，把它们消灭了就可以。',Speaker=827907,}",
  [504289] = "{id=504289,Text='这就是制作幽海护甲的水之灵，最后，我们还需要水灵铠甲的碎片。',Speaker=827907,}",
  [504290] = "{id=504290,Text='传说中，拥有水之灵后，会吸引来巨大的水灵龟……',Speaker=827907,}",
  [504291] = "{id=504291,Text='就是它，一鼓作气战胜这个巨大的水灵龟吧！',Speaker=827907,}",
  [504292] = "{id=504292,Text='我来看看：水晶、水之灵、 铠甲碎片。这些就够了，我们去找[c][ffff00]忽克连[-][/c]帮忙打造幽海护甲吧！',Speaker=827907,Option='忽克连？',}",
  [504293] = "{id=504293,Text='当然，我是个教授，不会打造这些。',Speaker=827907,Option='那先回艾尔贝塔吧',}",
  [504304] = "{id=504304,Text='这就是巨岩灵沙，我们搜集一部分灵沙碎片用以唤醒岩石龟！',Speaker=827907,}",
  [504305] = "{id=504305,Text='敲击时可能会有危险，[c][ffff00][PlayerName][-][/c]要当心。',Speaker=827907,}",
  [504306] = "{id=504306,Text='冒险者当心！',Speaker=827907,}",
  [504307] = "{id=504307,Text='接下来就是带着灵沙碎片，去古人留下的雕塑那儿唤醒沉睡的岩石龟，跟我来。',Speaker=827907,}",
  [504310] = "{id=504310,Text='就是这里，把带来的灵沙碎片放一些在雕塑前吧！',Speaker=827907,}",
  [504311] = "{id=504311,Text='就是它们，把它们消灭了就可以。',Speaker=827907,}",
  [504312] = "{id=504312,Text='这就是制作凝岩护甲的[c][ffff00]岩之灵[-][/c]，最后，我们还需要岩石背甲的碎片。',Speaker=827907,}",
  [504313] = "{id=504313,Text='拥有岩之灵后，就会吸引来巨大的岩石龟……',Speaker=827907,}",
  [504314] = "{id=504314,Text='就是它，一鼓作气战胜这个巨大的岩石龟吧！',Speaker=827907,}",
  [504315] = "{id=504315,Text='我来看看：灵沙碎片、岩之灵、 铠甲碎片。这些就够了，我们去找[c][ffff00]忽克连[-][/c]帮忙打造凝岩护甲吧！',Speaker=827907,Option='忽克连？',}",
  [504316] = "{id=504316,Text='当然，我是个教授，不会打造这些。',Speaker=827907,Option='那先回艾尔贝塔吧',}",
  [504327] = "{id=504327,Text='就是这里，听说念动咒语，就会有守护岛屿的风之力出现。',Speaker=827907,}",
  [504328] = "{id=504328,Text='韦伯念出一段奇怪的咒语。',}",
  [504329] = "{id=504329,Text='这就是咏咒御风，消灭它可以获得一部分风灵碎片用以唤醒风灵龟！',Speaker=827907,}",
  [504330] = "{id=504330,Text='消灭时可能会有危险，[c][ffff00][PlayerName][-][/c]要当心。',Speaker=827907,}",
  [504331] = "{id=504331,Text='冒险者当心！',Speaker=827907,}",
  [504332] = "{id=504332,Text='接下来就是带着风灵碎片，去古人留下的雕塑那儿唤醒沉睡的风灵龟，跟我来。',Speaker=827907,}",
  [504335] = "{id=504335,Text='就是这里，把带来的风灵碎片放一些在雕塑前吧！',Speaker=827907,}",
  [504336] = "{id=504336,Text='就是它们，把它们消灭了就可以。',Speaker=827907,}",
  [504337] = "{id=504337,Text='这就是制作凛风护甲的[c][ffff00]风之灵[-][/c]，最后，我们还需要风灵背甲的碎片。',Speaker=827907,}",
  [504338] = "{id=504338,Text='拥有风之灵后，就会吸引来巨大的风灵龟……',Speaker=827907,}",
  [504339] = "{id=504339,Text='就是它，一鼓作气战胜这个巨大的风灵龟吧！',Speaker=827907,}",
  [504340] = "{id=504340,Text='我来看看：风灵碎片、风之灵、 铠甲碎片。这些就够了，我们去找[c][ffff00]忽克连[-][/c]帮忙打造凛风护甲吧！',Speaker=827907,Option='忽克连？',}",
  [504341] = "{id=504341,Text='当然，我是个教授，不会打造这些。',Speaker=827907,Option='那先回艾尔贝塔吧',}",
  [504355] = "{id=504355,Text='以后你就可以给小龟龟替换[c][ffff00]凛风护甲[-][/c]了。等你再多了解些乌龟岛和乌龟坐骑后， 我会告诉你获得其它龟甲的方法。',Speaker=827907,}",
  [504358] = "{id=504358,Text='只有巴尔特家族的人和其友人才能翻阅我。',Speaker=813372,}",
  [504380] = "{id=504380,Voice='Npc_Quest/504380',Text='大海是巴尔特家族的战场，我们绝不会在这里后退。',Speaker=813333,}",
  [504381] = "{id=504381,Voice='Npc_Quest/504381',Text='这一次我带来了充足的弹药，足以将乌龟岛击沉。',Speaker=813333,}",
  [504382] = "{id=504382,Voice='Npc_Quest/504382',Text='把……把岛击沉？',Speaker=813332,Option='这么可怕？',}",
  [504383] = "{id=504383,Voice='Npc_Quest/504383',Text='老虎即便在沉睡中也不会允许危险靠近。乌龟岛离艾尔贝塔的距离太近，是个很大的威胁。',Speaker=813333,}",
  [504384] = "{id=504384,Voice='Npc_Quest/504384',Text='比起一次次驱逐老鼠，我更乐意轰平老鼠窝。',Speaker=813333,}",
  [504385] = "{id=504385,Voice='Npc_Quest/504385',Text='可……可岛上还有很多矿石……',Speaker=813332,}",
  [504386] = "{id=504386,Voice='Npc_Quest/504386',Text='不值钱的玩意儿罢了，还不够巴尔特家族出航的费用。',Speaker=813333,}",
  [504387] = "{id=504387,Voice='Npc_Quest/504387',Text='好吧……',Speaker=813332,}",
  [504388] = "{id=504388,Voice='Npc_Quest/504388',Text='冒险者，过会很有可能会发生战斗，还需要你帮忙。',Speaker=813333,Option='{我要做什么？,0},{你有什么吩咐？,0}',}",
  [504389] = "{id=504389,Voice='Npc_Quest/504389',Text='很简单，只要帮助船员整理舱内的武器箱和炮台。',Speaker=813333,}",
  [504390] = "{id=504390,Voice='Npc_Quest/504390',Text='对你而言并不是难事，只是一点点热身。',Speaker=813333,}",
  [504391] = "{id=504391,Voice='Npc_Quest/504391',Text='忽克连，你也跟着一起去吧？',Speaker=813333,Option='{好吧……,0},{（小声）你是懂压榨的,0}',}",
  [504392] = "{id=504392,Voice='Npc_Quest/504392',Text='幸亏你不是巴尔特家族的人，我去安排作战计划，你们也去做自己的事吧。',Speaker=813333,}",
  [504393] = "{id=504393,Text='真沉啊……总算整理好了。',}",
  [504394] = "{id=504394,Voice='Npc_Quest/504394',Text='糟糕！进水了！船体进水了！救命！海里的魔物被冲上来了！',Speaker=813338,}",
  [504395] = "{id=504395,Voice='Npc_Quest/504395',Text='什么？',Speaker=813339,}",
  [504396] = "{id=504396,Voice='Npc_Quest/504396',Text='还有？',Speaker=813340,}",
  [504397] = "{id=504397,Voice='Npc_Quest/504397',Text='得救了……这到底……或许只有见多识广的伊安老爷才知道发生了什么。',Speaker=813341,Option='我去找他',}",
  [504398] = "{id=504398,Voice='Npc_Quest/504398',Text='我在做梦吗？',Speaker=813332,}",
  [504399] = "{id=504399,Voice='Npc_Quest/504399',Text='到底是礁石，还是……',Speaker=813333,Option='{发生什么了？,0},{有敌人吗？,0}',}",
  [504400] = "{id=504400,Voice='Npc_Quest/504400',Text='老兄，你也听到了刚才的声音吧！这果然不是梦。',Speaker=813332,}",
  [504401] = "{id=504401,Voice='Npc_Quest/504401',Text='我已经让瞭望手观察过了，大海上并[c][ffff00]没有敌舰出没[-][/c]。',Speaker=813333,}",
  [504402] = "{id=504402,Voice='Npc_Quest/504402',Text='或许是暗礁，也可能是海底的[c][ffff00]某些生物[-][/c]撞到了船体，导致破损。',Speaker=813333,}",
  [504403] = "{id=504403,Voice='Npc_Quest/504403',Text='不管怎么说，现在当务之急是修复船舱。',Speaker=813333,}",
  [504404] = "{id=504404,Voice='Npc_Quest/504404',Text='没问题，这正是我拿手的，[c][ffff00][PlayerName][-][/c]肯定也很乐于帮忙的，对吧？',Speaker=813332,Option='{你都这样说了……,0},{好吧……,0}',}",
  [504405] = "{id=504405,Voice='Npc_Quest/504405',Text='既然你们主动要求帮忙……',Speaker=813333,}",
  [504406] = "{id=504406,Voice='Npc_Quest/504406',Text='上面的船舱恐怕破损更为严重，就交给你们了。',Speaker=813333,}",
  [504407] = "{id=504407,Voice='Npc_Quest/504407',Text='没问题！就交给我吧！',Speaker=813332,}",
  [504408] = "{id=504408,Voice='Npc_Quest/504408',Text='老兄，我好像看到了那个神秘的漂亮姐姐，我是不是眼花了？',Speaker=813332,Option='{我也看到了,0},{应该不是错觉,0}',}",
  [504409] = "{id=504409,Voice='Npc_Quest/504409',Text='她怎么会出现在逐日远航号上？太奇怪了……',Speaker=813332,}",
  [504410] = "{id=504410,Voice='Npc_Quest/504410',Text='快来人，这里严重人手不足！',Speaker=813338,}",
  [504411] = "{id=504411,Voice='Npc_Quest/504411',Text='没时间追究漂亮姐姐的事了，我们快去帮忙！',Speaker=813332,Option='{好的,0},{走快点,0}',}",
  [504412] = "{id=504412,Voice='Npc_Quest/504412',Text='救命！',Speaker=813338,}",
  [504413] = "{id=504413,Voice='Npc_Quest/504413',Text='怎么回事？',Speaker=813339,}",
  [504414] = "{id=504414,Voice='Npc_Quest/504414',Text='有战斗？',Speaker=813340,}",
  [504415] = "{id=504415,Voice='Npc_Quest/504415',Text='敌袭？',Speaker=813341,}",
  [504416] = "{id=504416,Voice='Npc_Quest/504416',Text='两位尊敬的客人，你们怎么来了？',Speaker=813338,}",
  [504417] = "{id=504417,Voice='Npc_Quest/504417',Text='伊安老爷让我们前来帮忙。',Speaker=813332,}",
  [504418] = "{id=504418,Voice='Npc_Quest/504418',Text='谢天谢地，那真的太好了，我们正愁忙不过来呢！',Speaker=813338,Option='需要我们做什么？',}",
  [504419] = "{id=504419,Voice='Npc_Quest/504419',Text='船舱外的破损太严重，许多地方都要重新订上木板。',Speaker=813338,}",
  [504420] = "{id=504420,Voice='Npc_Quest/504420',Text='这一带明明没有暗礁，天呐，难道真的是海里的魔兽？那它得多么庞大啊……',Speaker=813338,}",
  [504421] = "{id=504421,Voice='Npc_Quest/504421',Text='又、又又又又来？',Speaker=813332,}",
  [504422] = "{id=504422,Voice='Npc_Quest/504422',Text='什么声音，我的耳朵，啊……救命！',Speaker=813338,}",
  [504423] = "{id=504423,Voice='Npc_Quest/504423',Text='醒醒！老兄！快醒醒！',Speaker=813332,}",
  [504424] = "{id=504424,Voice='Npc_Quest/504424',Text='谢天谢地！你终于醒了！这里也太热了吧，简直就像在[c][ffff00]熔炉[-][/c]里一样。',Speaker=813332,}",
  [504425] = "{id=504425,Voice='Npc_Quest/504425',Text='对了，这是之前制作的降温药剂，你喝了以后应该能好一点。',Speaker=813332,}",
  [504429] = "{id=504429,Voice='Npc_Quest/504429',Text='不管怎么样，我们必须得寻找失散的人群！[c][ffff00][-][/c]祈祷能尽快找到[-][/c]。',Speaker=813332,}",
  [504438] = "{id=504438,Voice='Npc_Quest/504438',Text='哦~神明啊~[c][ffff00]拜托拜托~给我们一点圣灵粉吧~[-][/c]！',Speaker=813332,Action={actionid=819,num=1},}",
  [504440] = "{id=504440,Voice='Npc_Quest/504440',Text='既然有了圣灵粉，我们可以去寻找大家了！唉……',Speaker=813332,Option='为什么叹气？',}",
  [504441] = "{id=504441,Voice='Npc_Quest/504441',Text='没想到这次出海会发生这些乱七八糟的事。',Speaker=813332,}",
  [504442] = "{id=504442,Voice='Npc_Quest/504442',Text='我本来只是想登上乌龟岛，收集一些霜炎水晶啊，可现在去哪里找呢？',Speaker=813332,}",
  [504443] = "{id=504443,Voice='Npc_Quest/504443',Text='哎哟喂，好痛，什么东西砸到我了。',Speaker=813332,}",
  [504444] = "{id=504444,Voice='Npc_Quest/504444',Text='这是……霜炎水晶？！',Speaker=813332,Option='{什么？,0},{这么巧？,0}',}",
  [504445] = "{id=504445,Voice='Npc_Quest/504445',Text='天呐，真的是霜炎水晶！我今天的运气也太好了吧？',Speaker=813332,Option='不可思议',}",
  [504446] = "{id=504446,Voice='Npc_Quest/504446',Text='嘿嘿，这下我的[c][ffff00]心愿满足[-][/c]了！走吧，咱们快去救人！',Speaker=813332,Option='你总算还记得……',}",
  [504455] = "{id=504455,Voice='Npc_Quest/504455',Text='糟糕，他们都受伤了。',Speaker=813332,}",
  [504456] = "{id=504456,Voice='Npc_Quest/504456',Text='我们必须找到能治疗他们的草药！',Speaker=813332,Option='试试许个愿？',}",
  [504457] = "{id=504457,Voice='Npc_Quest/504457',Text='哎，怎么可能，就算我[c][ffff00]许愿[-][/c]了，也……',Speaker=813332,}",
  [504458] = "{id=504458,Voice='Npc_Quest/504458',Text='竟然真的有草药，快，老兄！赶紧的！',Speaker=813332,}",
  [504462] = "{id=504462,Text='糟糕，该怎么办？',Speaker=813332,Option='{给予降温剂,2},{草药包扎,3 }',}",
  [504467] = "{id=504467,Text='糟糕，该怎么办？',Option='{给予降温剂,2},{草药包扎,3 }',}",
  [504472] = "{id=504472,Text='糟糕，该怎么办？',Option='{给予降温剂,2},{草药包扎,3 }',}",
  [504477] = "{id=504477,Text='糟糕，该怎么办？',Option='{给予降温剂,2},{草药包扎,3 }',}",
  [504513] = "{id=504513,Text='出了这么大的事情，我难辞其咎。',Speaker=813338,}",
  [504514] = "{id=504514,Text='但所幸的是，我清点了一下人数，全员到齐。',Speaker=813338,Option='是个好消息',}",
  [504515] = "{id=504515,Text='这也是不幸中的万幸了吧。',Speaker=813338,}",
  [504516] = "{id=504516,Text='我之前一直在甲板上，抬头突然看见巨大的浪涌来，船被掀翻了！',Speaker=813339,}",
  [504517] = "{id=504517,Text='等我再次睁开眼就到了这里……我也不知道后面究竟发生了什么。',Speaker=813339,}",
  [504518] = "{id=504518,Text='但当时！一定是那个海浪掀翻了船只！然后我们就顺着水流被冲到了这里……',Speaker=813339,}",
  [504519] = "{id=504519,Text='昏迷之前，好像看到了一个黑影。',Speaker=813340,}",
  [504520] = "{id=504520,Text='请注意，我没有结巴，但真的是好大好大好大好大的一个影子啊……',Speaker=813340,Option='……',}",
  [504521] = "{id=504521,Text='好吧，其实我也不能确定自己看到的是不是幻觉。',Speaker=813340,}",
  [504522] = "{id=504522,Text='不远处似乎有一些雕塑，难道你不觉得它们在守护什么吗？',Speaker=813341,}",
  [504523] = "{id=504523,Text='一定是不得了的宝藏吧，嘿嘿嘿，它们的目光交汇处，应该会有惊喜呢。',Speaker=813341,}",
  [504524] = "{id=504524,Text='这些雕像互相背对着，双眼黯淡，仿佛失去了什么重要的宝物。',Option='{转动,2},{无视,3}',}",
  [504525] = "{id=504525,Text='里面似乎存放着什么东西，却因为缺少守护者的注视，无法打开。',Speaker=813359,}",
  [504526] = "{id=504526,Text='里面似乎存放着什么东西，是否要打开看看？',Speaker=813359,Option='{打开,2},{无视,3}',}",
  [504527] = "{id=504527,Text='一阵金光后，箱子里出现了大量的龟甲，上面刻着不知名的文字以及各种图案，有日月星辰，也有高山大海。',}",
  [504528] = "{id=504528,Text='看起来是记录文献的甲骨。不认识的古老文字，大量的甲骨文献，这里曾经有过古老的文明。',}",
  [504529] = "{id=504529,Text='这个守护雕像孤零零的在一旁，上面还有黏糊糊的液体。',}",
  [504530] = "{id=504530,Text='看形状是祭祀用的器皿，上面刻着乌龟形状的的图腾，还有一些看不懂的文字。',Speaker=813365,}",
  [504531] = "{id=504531,Text='这里的文字……',Speaker=813344,}",
  [504532] = "{id=504532,Text='是的，我能看懂，但不要问我原因。',Speaker=813344,}",
  [504533] = "{id=504533,Text='如果你有收集到足够的线索，可以拿来给我，除此之外不要打扰我的思绪。',Speaker=813344,}",
  [504534] = "{id=504534,Text='呼呼~唧~~',Speaker=813349,}",
  [504535] = "{id=504535,Text='和乌龟岛的乌龟不同，它的眼中似乎闪烁着某种光辉，甚至和人类很像……',}",
  [504536] = "{id=504536,Text='靠近时能感觉到温暖的力量，它能听懂人类的语言吗？',}",
  [504537] = "{id=504537,Text='呼呼……',Speaker=813349,}",
  [504538] = "{id=504538,Text='必须尽快查清这到底是哪里，以及我们如何才能离开。',Speaker=813333,Option='{正在查,0},{知道了,0}',}",
  [504539] = "{id=504539,Text='我担心教国的走狗，还会继续对艾尔贝塔不利。',Speaker=813333,}",
  [504540] = "{id=504540,Text='可恨！巴尔特家族竟在大海上翻了船。',Speaker=813333,}",
  [504541] = "{id=504541,Voice='Npc_Quest/504541',Text='这里虽然又热又闷，但很让人心安……大概因为我今天很[c][ffff00]幸运[-][/c]吧！调查的怎么样了？',Speaker=813332,Option='{继续调查,2},{调查完成,3}',}",
  [504542] = "{id=504542,Voice='Npc_Quest/504542',Text='怎么样，对我幸运之地的调查如何了？',Speaker=813332,}",
  [504543] = "{id=504543,Voice='Npc_Quest/658783',Text='再多看看，胆大心细，这是以前老师常说的话。',Speaker=813332,}",
  [504544] = "{id=504544,Voice='Npc_Quest/658784',Text='我怎么觉得差不多了？这可是未来的大陆第一铁匠的直觉。',Speaker=813332,}",
  [504545] = "{id=504545,Voice='Npc_Quest/658785',Text='接下来，就是好好整理一下线索了！',Speaker=813332,}",
  [504546] = "{id=504546,Voice='Npc_Quest/504546',Text='不过……这些线索……我！看！不！懂！啊！',Speaker=813332,}",
  [504547] = "{id=504547,Voice='Npc_Quest/504547',Text='对了~！神秘的漂亮姐姐好像可以看懂这里的文字，我们快去找她问问！',Speaker=813332,}",
  [504548] = "{id=504548,Voice='Npc_Quest/504548',Text='你们已经找到了线索？给我看看。',Speaker=813344,}",
  [504549] = "{id=504549,Voice='Npc_Quest/504549',Text='这里到处都是房屋，但却没有人居住。或许是有和我们一样流落至此的人搭建的，也不知他们是如何离开的。',Speaker=813344,}",
  [504550] = "{id=504550,Text='不，这里曾经有过一个文明，属于一个历史悠久的族群，哪个证据可以证明这一点。',}",
  [504551] = "{id=504551,Text='我不喜欢和愚蠢的人多费口舌。',Speaker=813344,}",
  [504552] = "{id=504552,Voice='Npc_Quest/504552',Text='嗯……这些文字……甲骨上写的是文献，有艺术创作，有历史记录，还有知识技术。',Speaker=813344,Option='{你认识这种文字？,0}',}",
  [504553] = "{id=504553,Voice='Npc_Quest/504553',Text='不，我也是第一次见到。与其说认识……不如说，它进入了我的脑子里……',Speaker=813344,}",
  [504554] = "{id=504554,Voice='Npc_Quest/504554',Text='曾经生活在这里的族群叫[c][ffff00]旧良族[-][/c]，甲骨文献上记载着，旧良族人信奉神明海姆达尔。',Speaker=813344,}",
  [504555] = "{id=504555,Voice='Npc_Quest/504555',Text='海姆达尔代表守护，所以守护也是旧良族人贯彻一生的主题。',Speaker=813344,}",
  [504556] = "{id=504556,Voice='Npc_Quest/504556',Text='还有其它线索上有这种特殊文字留下的信息吗？',Speaker=813344,}",
  [504557] = "{id=504557,Voice='Npc_Quest/504557',Text='祭祀用的器皿……上面刻着的文字意味忠贞的信仰，而这乌龟的图案应该就是他们崇拜的图腾。',Speaker=813344,}",
  [504558] = "{id=504558,Voice='Npc_Quest/504558',Text='信仰乌龟，难道此地仍然在乌龟岛上？难道……这里也是[c][ffff00]乌龟岛的地下[-][/c]？',Speaker=813344,}",
  [504559] = "{id=504559,Voice='Npc_Quest/504559',Text='……回答我，你们是如何抵达这里的？',Speaker=813344,}",
  [504560] = "{id=504560,Text='虽然当时在船舱内对外面的情况一无所知，但是水手们有一些猜测——',}",
  [504561] = "{id=504561,Text='我不喜欢和愚蠢的人多费口舌。',Speaker=813344,}",
  [504562] = "{id=504562,Voice='Npc_Quest/504562',Text='所以你们认为，船只被海浪掀翻后，水流将我们冲到了这里。',Speaker=813344,}",
  [504563] = "{id=504563,Voice='Npc_Quest/504563',Text='你们清点过这里的人数吗？是否和出航时一致？',Speaker=813344,}",
  [504564] = "{id=504564,Text='我不喜欢和愚蠢的人多费口舌。',Speaker=813344,}",
  [504565] = "{id=504565,Voice='Npc_Quest/504565',Text='果然如此。呵，可笑。什么样的水流能把我们，[c][ffff00]一个不少，全部，整齐[-][/c]的冲到[c][ffff00]同一个地点[-][/c]？',Speaker=813344,}",
  [504566] = "{id=504566,Voice='Npc_Quest/504566',Text='并且，在这个地点找不到任何一个入水口。',Speaker=813344,Option='{啊！确实……,0},{你说的有道理,0}',}",
  [504567] = "{id=504567,Voice='Npc_Quest/504567',Text='矛盾，会引领我们触及真相。还有其它的发现吗？',Speaker=813344,}",
  [504568] = "{id=504568,Voice='Npc_Quest/504568',Text='（嗅）……咳咳咳！',Speaker=813344,}",
  [504569] = "{id=504569,Voice='Npc_Quest/504569',Text='一股腐蚀的味道，这简直像[c][ffff00]生物体内的消化液[-][/c]。',Speaker=813344,}",
  [504570] = "{id=504570,Voice='Npc_Quest/504570',Text='根据现有的线索，我们眼前的房屋曾经是[c][ffff00]人类的村庄[-][/c]，这里生活的人们名为旧良族。',Speaker=813344,}",
  [504571] = "{id=504571,Text='……',Speaker=813344,}",
  [504572] = "{id=504572,Voice='Npc_Quest/504572',Text='而我们一船人，被海浪掀翻了船只后，一个不少地出现在了这里。',Speaker=813344,}",
  [504573] = "{id=504573,Voice='Npc_Quest/504573',Text='一个疑似在乌龟岛地下，到处都是黏液的古怪之地。',Speaker=813344,}",
  [504574] = "{id=504574,Voice='Npc_Quest/504574',Text='救命！墙、墙壁动了！它……它在蠕动——[c][ffff00]是活的[-][/c]——！',Speaker=813341,}",
  [504575] = "{id=504575,Voice='Npc_Quest/504575',Text='你疯了吗？墙怎么可能是活的？',Speaker=813338,}",
  [504576] = "{id=504576,Voice='Npc_Quest/504576',Text='我没有说谎！刚才我想看看这里还有没有宝藏，可触摸墙壁的时候发现它在蠕动！',Speaker=813341,}",
  [504577] = "{id=504577,Voice='Npc_Quest/504577',Text='什么？我去看看！怎么可能！',Speaker=813332,}",
  [504578] = "{id=504578,Voice='Npc_Quest/504578',Text='我相信他没有说谎，其实昏迷过去前，我也看到了一个巨大的黑影，难道……',Speaker=813340,}",
  [504579] = "{id=504579,Voice='Npc_Quest/504579',Text='结合刚才的证据，我有一个猜想……',Speaker=813344,Option='{我们不会是……,0},{什么猜想……？,0}',}",
  [504580] = "{id=504580,Voice='Npc_Quest/504580',Text='这个猜想会让所有的矛盾都可以被解释。',Speaker=813344,}",
  [504581] = "{id=504581,Voice='Npc_Quest/504581',Text='随处可见的黏液，蠕动的墙壁，没有入水口但我们却出现在这里。',Speaker=813344,}",
  [504582] = "{id=504582,Voice='Npc_Quest/504582',Text='这里处于乌龟岛地下，而乌龟岛的位置又经常发生变化——',Speaker=813344,Option='{所以乌龟岛……,0},{是个活物！！！,0}',}",
  [504583] = "{id=504583,Voice='Npc_Quest/504583',Text='嗯。或许旧良族人当初正是被一个可怕的魔物吞入腹中。',Speaker=813344,}",
  [504584] = "{id=504584,Voice='Npc_Quest/504584',Text='难怪乌龟岛的位置一直在发生变化，原来它是活的！！！',Speaker=813333,}",
  [504585] = "{id=504585,Voice='Npc_Quest/504585',Text='活、活活活的？伊安老爷，难道我们正在那个魔物的肚子里？',Speaker=813338,}",
  [504586] = "{id=504586,Voice='Npc_Quest/504586',Text='蠢货，怕什么！我们带来了足够的火炮，足以从内部轰平这个魔物！',Speaker=813333,}",
  [504587] = "{id=504587,Voice='Npc_Quest/504587',Text='……',Speaker=813333,}",
  [504588] = "{id=504588,Voice='Npc_Quest/504588',Text='天呐！！我找到了，我找到了！！！',Speaker=813332,}",
  [504589] = "{id=504589,Voice='Npc_Quest/504589',Text='你们肯定不敢相信我发现了什么！',Speaker=813332,Option='{快点说,0},{别绕圈子,0}',}",
  [504590] = "{id=504590,Voice='Npc_Quest/504590',Text='我找到了传说中的熔炉，你们快跟我过来！',Speaker=813332,}",
  [504591] = "{id=504591,Voice='Npc_Quest/504591',Text='嘿嘿，有了这座熔炉，影子装备就不是梦啦！',Speaker=813332,}",
  [504592] = "{id=504592,Voice='Npc_Quest/504592',Text='我马上就要成为大陆第一铁匠啦！',Speaker=813332,}",
  [504593] = "{id=504593,Text='好热',Speaker=813339,}",
  [504594] = "{id=504594,Text='好温暖',Speaker=813340,}",
  [504595] = "{id=504595,Text='奇怪的感觉',Speaker=813341,}",
  [504596] = "{id=504596,Voice='Npc_Quest/504596',Text='我的伤完全好了！',Speaker=813340,}",
  [504597] = "{id=504597,Voice='Npc_Quest/504597',Text='胳膊也完全不疼了，太神奇了！',Speaker=813341,}",
  [504598] = "{id=504598,Voice='Npc_Quest/504598',Text='多么壮观的熔炉啊，老师如果能看见……',Speaker=813332,}",
  [504599] = "{id=504599,Voice='Npc_Quest/504599',Text='老兄，你帮我从不同的角度拍几张照片吧，我要挂在我的铁匠铺里！',Speaker=813332,Option='{好吧,0},{拿你没办法,0}',}",
  [504600] = "{id=504600,Voice='Npc_Quest/504600',Text='多么漂亮的熔炉啊，[c][ffff00]就像心脏[-][/c]，呃，简直太像了……换个位置吧。',Speaker=813332,}",
  [504601] = "{id=504601,Voice='Npc_Quest/504601',Text='[c][ffff00]虽然炙热，却很温暖[-][/c]，我喜欢这种感觉。',Speaker=813332,}",
  [504602] = "{id=504602,Voice='Npc_Quest/504602',Text='说起来刚才水手身上的伤，好像突然就好了？',Speaker=813332,}",
  [504603] = "{id=504603,Text='那边两个人，你们在干什么？别拍了！',Speaker=813333,}",
  [504604] = "{id=504604,Voice='Npc_Quest/504604',Text='都什么时候了，竟然还有心情玩闹！',Speaker=813333,}",
  [504605] = "{id=504605,Voice='Npc_Quest/504605',Text='别生气嘛，您看这个熔炉多漂亮，我老兄的拍照技术真不错。',Speaker=813332,}",
  [504606] = "{id=504606,Voice='Npc_Quest/504606',Text='这不是熔炉，这是那个魔物的心脏！',Speaker=813333,}",
  [504607] = "{id=504607,Voice='Npc_Quest/504607',Text='魔物？什么魔物？',Speaker=813332,}",
  [504608] = "{id=504608,Voice='Npc_Quest/504608',Text='刚才那个蒙面女人分析，我们所有人现在都在魔物的肚子里！',Speaker=813333,}",
  [504609] = "{id=504609,Voice='Npc_Quest/504609',Text='等等！！住手啊！',Speaker=813332,}",
  [504610] = "{id=504610,Voice='Npc/Aileen_Coldsmile_01',Text='哼。',Speaker=813345,Option='{你是莎拉·艾琳,0},{芙蕾雅的神使,0}',}",
  [504611] = "{id=504611,Voice='Npc_Quest/504611',Text='什……什么！芙蕾雅？那个女神芙蕾雅？',Speaker=813332,}",
  [504612] = "{id=504612,Voice='Npc_Quest/504612',Text='你们无法阻止我，我来这里本就是为了毁掉熔炉。',Speaker=813345,Option='{为什么？,0},{有阴谋！,0}',}",
  [504613] = "{id=504613,Voice='Npc_Quest/504613',Text='你们已经知道了，乌龟岛其实是一个巨大的魔物，它会吞噬人类的村庄。',Speaker=813345,}",
  [504614] = "{id=504614,Voice='Npc_Quest/504614',Text='它的下一个目标正是艾尔贝塔，只有毁坏它的心脏，才能杀死这个魔物。',Speaker=813345,}",
  [504615] = "{id=504615,Voice='Npc_Quest/504615',Text='什么！？毁掉它！必须毁掉它！',Speaker=813333,}",
  [504616] = "{id=504616,Voice='Npc_Quest/504616',Text='为了艾尔贝塔，就算你不出手，我也要毁了这个恶心的心脏熔炉！',Speaker=813333,}",
  [504617] = "{id=504617,Voice='Npc_Quest/504617',Text='不！不要！老兄，只有你能帮我了……',Speaker=813332,}",
  [504618] = "{id=504618,Voice='Npc_Quest/504618',Text='冒险者，你也感觉到了吧？',Speaker=813332,Option='{什么？,0},{你的意思是……,0}',}",
  [504619] = "{id=504619,Voice='Npc_Quest/504619',Text='这个熔炉的价值！它不但能帮助我锻造出影子装备，还能治愈大家。',Speaker=813332,}",
  [504620] = "{id=504620,Voice='Npc_Quest/504620',Text='它是如此温暖，它的主人又怎么会伤害人类？',Speaker=813332,Option='这个嘛……',}",
  [504621] = "{id=504621,Voice='Npc_Quest/504621',Text='哼，你只是为了打造装备，可你考虑过艾尔贝塔吗？',Speaker=813333,}",
  [504622] = "{id=504622,Voice='Npc_Quest/504622',Text='我、我的确想打造影子装备，可熔炉刚才也让水手们的伤口恢复了！它怎么能代表邪恶呢？',Speaker=813332,}",
  [504623] = "{id=504623,Voice='Npc_Quest/504623',Text='这正是魔物设下的陷阱！我们不能上当。',Speaker=813333,}",
  [504624] = "{id=504624,Voice='Npc_Quest/504624',Text='说不定！艾尔贝塔现在的状况也和这个魔物脱不开关系！',Speaker=813333,Option='{艾尔贝塔是寒潮,0},{这里却十分炎热,0}',}",
  [504625] = "{id=504625,Voice='Npc_Quest/504625',Text='对！没错！一冷一热，怎么可能会有关系？',Speaker=813332,}",
  [504626] = "{id=504626,Voice='Npc_Quest/504626',Text='老兄，难道你也认为该毁了熔炉吗？',Speaker=813332,Option='{还得再观察,0},{不能草率行动,0}',}",
  [504627] = "{id=504627,Voice='Npc_Quest/504627',Text='观察？呵……',Speaker=813345,}",
  [504628] = "{id=504628,Voice='Npc_Quest/504628',Text='等等！神使姐姐！！快住手！',Speaker=813332,}",
  [504629] = "{id=504629,Voice='Npc_Quest/504629',Text='你们打算阻拦我？',Speaker=813345,}",
  [504630] = "{id=504630,Voice='Npc_Quest/504630',Text='漂亮姐姐，不如再观察一会吧？',Speaker=813332,Option='{你的真正目的是什么？,0},{芙蕾雅有什么阴谋？,0}',}",
  [504631] = "{id=504631,Voice='Npc_Quest/504631',Text='可笑，我要做什么，还不需要你们来同意！',Speaker=813345,}",
  [504632] = "{id=504632,Voice='Npc_Quest/504632',Text='忽克连，我看你是昏了头。赛尔！拦住忽克连和冒险者！',Speaker=813333,}",
  [504633] = "{id=504633,Voice='Npc_Quest/504633',Text='没有人能阻止我！熔炉不能留！',Speaker=813345,}",
  [504634] = "{id=504634,Voice='Npc_Quest/504634',Text='又是乌龟，哼！废物，都站起来！把这些乌龟通通杀了！',Speaker=813333,}",
  [504635] = "{id=504635,Voice='Npc_Quest/504635',Text='住手！你们快住手！它们很温顺的，是乌龟带我找到了你们！',Speaker=813332,}",
  [504636] = "{id=504636,Voice='Npc_Quest/504636',Text='让开！——不然别怪我不客气了。',Speaker=813345,}",
  [504637] = "{id=504637,Voice='Npc_Quest/504637',Text='不要，大家听我说句话吧，求求你们别破坏熔炉，也别伤害小乌龟。',Speaker=813332,}",
  [504638] = "{id=504638,Voice='Npc_Quest/504638',Text='滚出去！擅入者死！',Speaker=813366,}",
  [504639] = "{id=504639,Voice='Npc_Quest/504639',Text='这威猛的人形乌龟是什么东西？',Speaker=813332,}",
  [504640] = "{id=504640,Voice='Npc_Quest/504640',Text='看来是守护熔炉的魔物，有点本事。',Speaker=813345,}",
  [504641] = "{id=504641,Voice='Npc_Quest/504641',Text='你……你怎么在一旁看戏啊！姐姐！',Speaker=813332,}",
  [504642] = "{id=504642,Voice='Npc_Quest/504642',Text='该死，什么声音？',Speaker=813333,}",
  [504652] = "{id=504652,Voice='Npc_Quest/504652',Text='我接到消息，有人在港口看到了你们。',Speaker=813367,}",
  [504653] = "{id=504653,Voice='Npc_Quest/504653',Text='父亲怎么了？水手们怎么也都受伤了？',Speaker=813367,Option='{我们也不知道,0},{情况很复杂,0}',}",
  [504654] = "{id=504654,Voice='Npc_Quest/504654',Text='我需要知道究竟发生了什么！',Speaker=813367,}",
  [504682] = "{id=504682,Voice='Npc_Quest/504682',Text='哎，这乌龟岛到底还隐藏着多少秘密？',Speaker=828115,}",
  [504687] = "{id=504687,Text='……我来试试？[c][ffff00]拜托拜托~来点草药吧[-][/c]！',Speaker=813332,}",
  [504695] = "{id=504695,Text='调整炮台方向',Speaker=813336,Option='{左转90°,2},{右转90°,3}',}",
  [504696] = "{id=504696,Text='调整炮台方向',Speaker=813336,Option='{左转90°,4},{右转90°,5}',}",
  [504697] = "{id=504697,Text='调整炮台方向',Speaker=813336,Option='{左转90°,6},{右转90°,7}',}",
  [504698] = "{id=504698,Text='调整炮台方向',Speaker=813336,Option='{左转90°,8},{右转90°,9}',}",
  [504699] = "{id=504699,Text='调整炮台方向',Speaker=813337,Option='{左转90°,2},{右转90°,3}',}",
  [504700] = "{id=504700,Text='调整炮台方向',Speaker=813337,Option='{左转90°,4},{右转90°,5}',}",
  [504701] = "{id=504701,Text='调整炮台方向',Speaker=813337,Option='{左转90°,6},{右转90°,7}',}",
  [504702] = "{id=504702,Text='调整炮台方向',Speaker=813337,Option='{左转90°,8},{右转90°,9}',}",
  [504703] = "{id=504703,Text='这些雕像互相背对着，双眼黯淡，仿佛失去了什么重要的宝物。',Option='{转动,5},{无视,6}',}",
  [504704] = "{id=504704,Text='这些雕像互相背对着，双眼黯淡，仿佛失去了什么重要的宝物。',Option='{转动,8},{无视,9}',}",
  [504705] = "{id=504705,Text='这些雕像互相背对着，双眼黯淡，仿佛失去了什么重要的宝物。',Option='{转动,11},{无视,12}',}",
  [504710] = "{id=504710,Voice='Npc_Quest/504710',Text='登记之前，我需要问你们几个问题。',Speaker=813333,Option='{好吧……,0},{（小声嘀咕）真麻烦,0}',}",
  [504711] = "{id=504711,Voice='Npc_Quest/504711',Text='哼，那就别妄想我能让你上船。',Speaker=813333,}",
  [504712] = "{id=504712,Voice='Npc_Quest/504712',Text='很好，记住你说的话！快去登记，别耽误出航。',Speaker=813333,}",
  [504718] = "{id=504718,Text='唔——有了！我们可以从熔岩洞窟的火焰精灵身上采集点火种。',Speaker=828115,}",
  [504719] = "{id=504719,Text='我从火种里提炼出火焰之心，这样能让你暖和点。',Speaker=828115,Option='谢……（打颤）谢谢。',}",
  [504720] = "{id=504720,Text='喂喂，你还能撑得住吗？别被冻得神志不清了！',Speaker=828115,}",
  [504721] = "{id=504721,Text='我问你，我们现在要去哪里？做什么？',Speaker=828115,Option='{熔岩洞窟,0},{火焰精灵,0},{采集火种,0}',}",
  [504722] = "{id=504722,Text='看来还没糊涂。没错，我们要去熔岩洞窟从火焰精灵身上采集火种！',Speaker=828115,}",
  [504723] = "{id=504723,Text='看你冻成这样，我们赶紧出发吧！',Speaker=828115,Option='{立刻出发,4},{再等一下,5}',}",
  [504724] = "{id=504724,Text='嘶——赶紧啊，老兄！看你的样子，坚持不了太久了。',Speaker=828115,Option='好的！',}",
  [504725] = "{id=504725,Text='走走走，赶紧出发！',Speaker=828115,}",
  [504726] = "{id=504726,Text='这里的温度和湿度都很适合锻造！',Speaker=813332,}",
  [504727] = "{id=504727,Text='下次我要来这里修炼！老兄，你也一起啊！',Speaker=813332,}",
  [504728] = "{id=504728,Text='数量足够了，老兄。好了，用我珍藏的蝴蝶翅膀回铁匠铺吧！',Speaker=813332,}",
  [504729] = "{id=504729,Text='接下去，就交给我吧！再忍耐一下，马上就好！',Speaker=813332,}",
  [504730] = "{id=504730,Text='怎么样？感觉好点了吗？',Speaker=813332,Option='{好多了,0},{谢谢你,0}',}",
  [504731] = "{id=504731,Text='和我客气什么？说起火焰……昨晚我想了很久，咱们还是得保住铁炉。',Speaker=813332,}",
  [504745] = "{id=504745,Voice='Npc_Quest/504745',Text='就是这里了，真让人怀念……',Speaker=813367,}",
  [504746] = "{id=504746,Voice='Npc_Quest/504746',Text='藏书阁是哥哥最喜欢的地方，他总说我们家的藏书有许多都是王室也不曾拥有的孤本。',Speaker=813367,}",
  [504747] = "{id=504747,Voice='Npc_Quest/504747',Text='小时候，他总会把书中有趣的故事说给我听。',Speaker=813367,}",
  [504748] = "{id=504748,Voice='Npc_Quest/504748',Text='如果他还活着，他应该能劝阻父亲的决定……',Speaker=813367,Option='{请节哀,0},{很抱歉……,0}',}",
  [504749] = "{id=504749,Voice='Npc_Quest/504749',Text='唉，我最讨厌看书，每次哥哥讲故事时，我总喜欢在书的扉页写写画画。',Speaker=813367,}",
  [504750] = "{id=504750,Voice='Npc_Quest/504750',Text='啊？大家不是都会这样吗？老师每次看到都会敲我的脑袋。',Speaker=813332,Option='{只有你们,0},{我也是……,0}',}",
  [504751] = "{id=504751,Voice='Npc_Quest/504751',Text='看来我们都不是爱书之人啊，所以我只依稀记得听过乌龟岛的传说。',Speaker=813367,}",
  [504752] = "{id=504752,Voice='Npc_Quest/504752',Text='至于究竟是哪本书，只能靠你们自己找了，我至今还是不擅长与书有关的一切。',Speaker=813367,}",
  [504753] = "{id=504753,Voice='Npc_Quest/504753',Text='我看到书也很头疼……老兄，这件事我没办法帮你了。',Speaker=813332,}",
  [504754] = "{id=504754,Voice='Npc_Quest/504754',Text='祝你顺利，我也会再努力回忆一下当时听到的故事。',Speaker=813367,}",
  [504755] = "{id=504755,Text='这里记载着人类未知的故事，只有命定之人才能知晓我守护的秘密。',Speaker=813369,}",
  [504756] = "{id=504756,Text='将你的心神沉浸其中，呼唤那段历史，准备好了吗？',Speaker=813369,Option='{准备好了,2},{再等等,3}',}",
  [504757] = "{id=504757,Text='历史无法容忍虚伪，它本应是生命的老师，而我听见了命运之风的呼唤。',Speaker=813369,}",
  [504758] = "{id=504758,Text='你对真实的渴望我感受到了，由我来为你揭开眼前的薄纱。',Speaker=813369,}",
  [504759] = "{id=504759,Text='A.W.元年—A.W.100：诸神黄昏令神族们沉寂，属于人类的纪元正式到来，全新文明围绕着人类而建。',Speaker=813369,}",
  [504760] = "{id=504760,Text='然后，天神们的争斗并未结束。[c][ffff00]霜巨人安德尔伯达[-][/c]的神器[c][ffff00]冰霜之杖[-][/c]坠落，使得人类大陆面临前所未有的[c][ffff00]寒潮[-][/c]，沿海地带的人们遭受重创，寒潮逐渐向内陆延伸。',Speaker=813369,}",
  [504761] = "{id=504761,Text='直到……呼……呼呼……Zzzz……',Speaker=813369,Option='{醒醒——！,0},{喂喂——！,0}',}",
  [504762] = "{id=504762,Text='Zzzz……Zzzz……',Speaker=813369,}",
  [504763] = "{id=504763,Text='看来是叫不醒了……先放入背包吧。',}",
  [504764] = "{id=504764,Text='时间的车轮向前滚动，历史从不因谁驻足，去吧，我将在困倦中等待命定之人。',Speaker=813369,}",
  [504765] = "{id=504765,Text='住手，不要捣乱了！你们这群混蛋！',Speaker=813368,Option='{发生了什么？,0},{需要帮助吗？,0}',}",
  [504766] = "{id=504766,Text='这些书中幽灵总喜欢来捣乱，只有阿诺少爷的气枪能让它们回到书中。',Speaker=813368,}",
  [504767] = "{id=504767,Text='客人，你的枪法怎么样？',Speaker=813368,Option='交给我吧',}",
  [504768] = "{id=504768,Text='哈哈哈哈，总算安静了，这些可恨的家伙们不知给我增加了多少工作量。',Speaker=813368,}",
  [504769] = "{id=504769,Text='总算清闲下来了，对了，客人，有什么需要帮助的吗？',Speaker=813368,Option='关于乌龟岛……',}",
  [504770] = "{id=504770,Text='关于乌龟岛的文献并不多，它太神秘了！只有零星的一些记录。请稍等——',Speaker=813368,}",
  [504771] = "{id=504771,Text='找到了，这个地质学家们用多年时间，在灯塔用瞭望镜观察乌龟岛，留下来的日志。',Speaker=813368,}",
  [504772] = "{id=504772,Text='还有一份，是阿伦少爷留下的关于乌龟岛的调查报告。',Speaker=813368,}",
  [504773] = "{id=504773,Text='希望它们对你有所帮助。',Speaker=813368,}",
  [504774] = "{id=504774,Text='前面所有的页数都日复一日地记录着每天对乌龟岛的观测情况。',Speaker=813405,}",
  [504775] = "{id=504775,Text='通过日志可知，在一年极少数的天数中能在迷雾的空隙中观测到乌龟岛的位置。',Speaker=813405,}",
  [504776] = "{id=504776,Text='在日志的最后，地质学家们通过一复一日的观察得出了一个准确的结论。',Speaker=813405,}",
  [504777] = "{id=504777,Text='乌龟岛的移动存在一定的规律，它的移动路线永远[c][ffff00]围绕着冰霜之杖[-][/c]。',Speaker=813405,}",
  [504778] = "{id=504778,Text='这是一份阿伦撰写的调查报告，他曾率领船队试图调查乌龟岛。',Speaker=813377,}",
  [504779] = "{id=504779,Text='但这场调查行动随着阿伦的去世中止了，只留下了一条关于乌龟岛的准确线索。',Speaker=813377,}",
  [504780] = "{id=504780,Text='船队对乌龟岛周围的雾气进行了取样分析，确认雾气是由于岛屿内部的高温导致[c][ffff00]冰块的融化[-][/c]而造成的。',Speaker=813377,}",
  [504781] = "{id=504781,Text='月亮的光辉照着你的脸庞，它也觉得自惭形秽。而我更喜欢太阳，它的出现意味着我不用忍受漫漫长夜，能与你在路上相会，我多希望无论日夜，都能……',Speaker=813371,}",
  [504782] = "{id=504782,Text='喂！别念了！快住口！',Speaker=813367,}",
  [504783] = "{id=504783,Text='这才不是什么情诗！这什么也不是，只是一张废纸!',Speaker=813367,Option='{是吗？,0},{我怎么觉得……,0}',}",
  [504784] = "{id=504784,Text='你快去找乌龟岛的资料吧，还有……这件事不！准！说！出！去！',Speaker=813367,}",
  [504785] = "{id=504785,Text='上面放着一张古老的唱片，要听听看吗？',Speaker=813373,Option='{听一听,2},{稍后再听,3}',}",
  [504786] = "{id=504786,Text='约顿海姆的边陲小岛，三个孩子在海边远眺，心中满是烦恼。',Speaker=813373,}",
  [504787] = "{id=504787,Text='孤独的孩子如浪花飘荡，妈妈该去何处寻找？',Speaker=813373,}",
  [504788] = "{id=504788,Text='霜巨人伫立在彩虹桥，风中的呼喊被她听到，可怜的雏鸟无法归巢，母亲的心多么煎熬……',Speaker=813373,}",
  [504789] = "{id=504789,Text='一个积灰的箱子，钥匙还插在锁孔中。',Speaker=813403,Option='{打开,2},{放弃,3}',}",
  [504790] = "{id=504790,Text='箱子内部有一个船模，两本册子与一张纸条，纸条上写着——',Speaker=813403,}",
  [504791] = "{id=504791,Text='“著名的远航者阿卡蕾娅留下的两份记录，皆提及了寒潮以及艾尔贝塔海域中曾出现巨大的乌龟。',Speaker=813406,}",
  [504792] = "{id=504792,Text='虽没有直接证据表明[c][ffff00]巨型乌龟、寒潮以及乌龟岛之间的关联[-][/c]，但我相信这并不是巧合。',Speaker=813406,}",
  [504793] = "{id=504793,Text='这些海上的谜团令我着迷，这或许是我的姓氏以及血液中流淌的本能。',Speaker=813406,}",
  [504794] = "{id=504794,Text='等王选结束后，我将再度远航，希望这两本尘封已久的记录能引领我们找到乌龟岛的真相。——阿伦·巴尔特”',Speaker=813406,}",
  [504795] = "{id=504795,Text='日记中记录着寒潮来临的当日，阿卡蕾娅所在的船只被冻结在了海面上。',Speaker=813370,}",
  [504796] = "{id=504796,Text='而她也因此见证了在空无一物的海面上，突然[c][ffff00]凭空出现[-][/c]了一只巨型乌龟。',Speaker=813370,}",
  [504797] = "{id=504797,Text='这是阿卡蕾娅在临终前出版的一本回忆录。',Speaker=813406,}",
  [504798] = "{id=504798,Text='回忆录中，有关巨型乌龟的描述中，她补充了一点。',Speaker=813406,}",
  [504799] = "{id=504799,Text='巨型乌龟出现后，就开始在海面上[c][ffff00]缓慢游动[-][/c]，并且在那之后[c][ffff00]从未停止[-][/c]。',Speaker=813406,}",
  [504800] = "{id=504800,Text='一艘巴尔特家船只的模型，看底部还刻着一行字：“人类的文明建立在探索上“。',Speaker=813404,}",
  [504801] = "{id=504801,Text='看署名，这艘船模似乎是阿伦与阿诺一起共同制作的。',Speaker=813404,}",
  [504802] = "{id=504802,Text='所有的书籍似乎都和巴尔特家族的航行史有关，真让人赞叹。',Speaker=813378,}",
  [504803] = "{id=504803,Text='嘿，老兄，调查的怎么样了？',Speaker=813332,Option='{继续调查,2},{调查完成,3}',}",
  [504804] = "{id=504804,Voice='Npc_Quest/658783',Text='再多看看，胆大心细，这是以前老师常说的话。',Speaker=813332,}",
  [504805] = "{id=504805,Voice='Npc_Quest/658784',Text='我怎么觉得差不多了？这可是未来的大陆第一铁匠的直觉。',Speaker=813332,}",
  [504806] = "{id=504806,Voice='Npc_Quest/658785',Text='接下来，就是好好整理一下线索了！',Speaker=813332,}",
  [504807] = "{id=504807,Voice='Npc_Quest/504807',Text='阿诺那边似乎也想起了一些什么，不如我们和他分享一下线索。',Speaker=813332,}",
  [504808] = "{id=504808,Voice='Npc_Quest/504808',Text='咳咳，我用我仅存的记忆！拼凑出了那段故事！一定不会有错的！',Speaker=813367,}",
  [504809] = "{id=504809,Voice='Npc_Quest/504809',Text='很久很久以前，艾尔贝塔与人类就曾一起经历过一次前所未有的寒潮。',Speaker=813367,}",
  [504810] = "{id=504810,Voice='Npc_Quest/504810',Text='极端的天气让许多人类死去，连大海都冰冻住了。',Speaker=813367,}",
  [504811] = "{id=504811,Voice='Npc_Quest/504811',Text='这是一场天地树给予的惩罚，惩罚人类当时的极度扩张以及对环境的破坏。',Speaker=813367,}",
  [504812] = "{id=504812,Voice='Npc_Quest/504812',Text='哎哟我的老铁匠啊，怎么会这样！？',Speaker=813332,}",
  [504813] = "{id=504813,Text='不，这场寒潮并不是天地树的惩罚，有一个证据可以证明这一点。',}",
  [504814] = "{id=504814,Voice='Npc_Quest/504814',Text='嗯？这个是什么？和我的故事有什么关系？',Speaker=813440,}",
  [504815] = "{id=504815,Voice='Npc_Quest/504815',Text='是我记错了吗，让我重新述说一下这个故事。',Speaker=813440,}",
  [504816] = "{id=504816,Voice='Npc_Quest/504816',Text='巨人安格尔伯达的[c][ffff00]冰霜之杖意外坠落[-][/c]，人类因此受到的波及，寒潮席卷了大陆！',Speaker=813440,}",
  [504817] = "{id=504817,Voice='Npc_Quest/504817',Text='而冰杖坠落时的冲击让一只海面上漂浮的巨大乌龟陷入了千百年的昏迷！这才形成了乌龟岛！',Speaker=813440,}",
  [504818] = "{id=504818,Voice='Npc_Quest/504818',Text='哦——这只可怜的乌龟，受了无妄之灾。',Speaker=813332,}",
  [504819] = "{id=504819,Text='不，乌龟岛的出现并非如此，有目击者看到了这一切。',}",
  [504820] = "{id=504820,Text='嗯？这个是什么？和我的故事有什么关系？',Speaker=813440,}",
  [504821] = "{id=504821,Text='是我记错了吗，让我重新述说一下这个故事。',Speaker=813440,}",
  [504822] = "{id=504822,Voice='Npc_Quest/504822',Text='冰杖坠落时的冲击让一只在海底的巨大乌龟陷入了昏迷，急速浮上了水面！',Speaker=813440,}",
  [504823] = "{id=504823,Voice='Npc_Quest/504823',Text='随着时间的流逝，迟迟没有苏醒的乌龟身上，长出了植物，这才[c][ffff00]形成了乌龟岛[-][/c]。',Speaker=813440,}",
  [504824] = "{id=504824,Voice='Npc_Quest/504824',Text='这样就合理了，解释了为什么乌龟岛是突然出现在海面上的。',Speaker=813332,}",
  [504825] = "{id=504825,Text='不，乌龟迟迟没有苏醒这一点是不可能的，有一份资料可以证明这一点。',}",
  [504826] = "{id=504826,Text='嗯？这个是什么？和我的故事有什么关系？',Speaker=813440,}",
  [504827] = "{id=504827,Text='是我记错了吗，让我重新述说一下这个故事。',Speaker=813440,}",
  [504828] = "{id=504828,Voice='Npc_Quest/504828',Text='海水冰封，使得原本生活在海底的巨大乌龟浮上了尚未结冰的海面！',Speaker=813440,}",
  [504829] = "{id=504829,Voice='Npc_Quest/504829',Text='它不停地游动不停地游动，想要找到回家的路。',Speaker=813440,}",
  [504830] = "{id=504830,Voice='Npc_Quest/504830',Text='这……这……这可怜的大乌龟！能有什么坏心思？！',Speaker=813332,}",
  [504831] = "{id=504831,Text='不，这只巨型乌龟的游动路径[c][ffff00]不可能是在寻找回家的路[-][/c]，这个证据可以证明这一点！',}",
  [504832] = "{id=504832,Text='嗯？这个是什么？和我的故事有什么关系？',Speaker=813440,}",
  [504833] = "{id=504833,Text='是我记错了吗，让我重新述说一下这个故事。',Speaker=813440,}",
  [504834] = "{id=504834,Voice='Npc_Quest/504834',Text='巨大的乌龟被迫浮上了海面，它想要回家，但很可惜它做不到。',Speaker=813440,}",
  [504835] = "{id=504835,Voice='Npc_Quest/504835',Text='厚重的寒雾围绕着它，让它无法看清前路，只能不停[c][ffff00]原地打转[-][/c]。',Speaker=813440,}",
  [504836] = "{id=504836,Voice='Npc_Quest/504836',Text='原来是这样！哦可怜的乌龟，它的方向感一定很差吧……',Speaker=813332,}",
  [504837] = "{id=504837,Text='不，围绕着乌龟岛的雾气并不是寒雾，有份资料可以说明这一点。',}",
  [504838] = "{id=504838,Voice='Npc_Quest/504838',Text='啊……这只乌龟如果能够融化寒冰的话，那它完全可以回到深海中去，甚至不必浮上水面。',Speaker=813332,}",
  [504839] = "{id=504839,Voice='Npc_Quest/504839',Text='那……那……那它为什么要不停地绕着冰霜之杖游动啊！',Speaker=813332,}",
  [504840] = "{id=504840,Voice='Npc_Quest/504840',Text='我知道了！它！想觊觎冰霜之杖的力量，要得到它！',Speaker=813367,}",
  [504841] = "{id=504841,Voice='Npc_Quest/504841',Text='你们想啊，它曾经吞噬过一整个人类村庄，它甚至还想吞噬艾尔贝塔！',Speaker=813367,Option='这还只是猜测……',}",
  [504842] = "{id=504842,Voice='Npc_Quest/504842',Text='但是艾尔贝塔的规模比一个村庄要大的多！它需要更强大的力量！这不，冰霜之杖从天而降！',Speaker=813367,}",
  [504843] = "{id=504843,Voice='Npc_Quest/504843',Text='[c][ffff00]它不停地融化冰霜之杖周围的寒冰，但强大的冰霜之力将它的努力再次冻结！[-][/c]',Speaker=813367,}",
  [504844] = "{id=504844,Voice='Npc_Quest/504844',Text='什么！这居然不是可怜的巨型乌龟！是只可恶的巨型乌龟！',Speaker=813332,}",
  [504845] = "{id=504845,Voice='Npc_Quest/504845',Text='故事说完了，问题解决了！',Speaker=813367,Option='{这故事……,0},{完全是现编的吧……,0}',}",
  [504846] = "{id=504846,Voice='Npc_Quest/504846',Text='嗯？这不重要。关键是，它现在很合理。',Speaker=813367,Option='{听上去确实合理,0},{但想想又很古怪,0}',}",
  [504847] = "{id=504847,Voice='Npc_Quest/504847',Text='不管怎么说，至少我们知道了，这只巨型乌龟的出现与[c][ffff00]冰霜之杖[-][/c]的坠落脱不开关系。',Speaker=813332,}",
  [504864] = "{id=504864,Voice='Npc_Quest/504864',Text='谁允许你擅自前来，立刻给我消失！',Speaker=813379,}",
  [504865] = "{id=504865,Voice='Npc_Quest/504865',Text='哇……好凶……',Speaker=813332,}",
  [504866] = "{id=504866,Voice='Npc_Quest/504866',Text='每次遇见你都没什么好事，这次还带来了另一个蠢货。',Speaker=813379,Option='……',}",
  [504867] = "{id=504867,Voice='Npc_Quest/504867',Text='喂，就算你是神族也应该懂礼貌吧，这么说话太过分了！',Speaker=813332,}",
  [504868] = "{id=504868,Voice='Npc_Quest/504868',Text='礼貌？呵，礼貌是给活人的，如果你们是死人……',Speaker=813379,}",
  [504869] = "{id=504869,Voice='Npc_Quest/504869',Text='耶梦加得，住手！',Speaker=813380,}",
  [504870] = "{id=504870,Voice='Npc_Quest/504870',Text='抱歉，耶梦加得他……心情不太好……',Speaker=813380,Option='{吓我一跳,0},{你来的真及时,0}',}",
  [504871] = "{id=504871,Voice='Npc_Quest/504871',Text='很高兴再次见到你，命运之风，你和你的友人为何前来？',Speaker=813380,}",
  [504872] = "{id=504872,Voice='Npc_Quest/504872',Text='霜巨人……冰霜之杖……你是说我们的母亲，安格尔伯达？',Speaker=813380,}",
  [504873] = "{id=504873,Voice='Npc_Quest/504873',Text='抛弃自己的孩子，也配称为母亲？',Speaker=813379,}",
  [504874] = "{id=504874,Voice='Npc_Quest/504874',Text='无论人还是神，亦或是巨人，都有自己的无奈，或许她只是……',Speaker=813380,}",
  [504875] = "{id=504875,Voice='Npc_Quest/504875',Text='够了，你总是如此宽容甚至软弱，所以才会落得如此下场！',Speaker=813379,}",
  [504876] = "{id=504876,Voice='Npc_Quest/504876',Text='那个……我们还是说回冰霜之杖？',Speaker=813332,Option='{艾尔贝塔很危险,0},{乌龟岛的来历很诡异,0}',}",
  [504877] = "{id=504877,Voice='Npc_Quest/504877',Text='关于冰霜之杖坠落到米德加尔特这件事，我有所耳闻。',Speaker=813380,}",
  [504878] = "{id=504878,Voice='Npc_Quest/504878',Text='冰霜之杖的力量确实会吸引巨兽，但当时我已被囚禁在这里，具体情况不得而知。',Speaker=813380,}",
  [504879] = "{id=504879,Voice='Npc_Quest/504879',Text='就连神族也不知道吗？',Speaker=813332,}",
  [504880] = "{id=504880,Voice='Npc_Quest/504880',Text='神族并不意味着全知全能，但我们可以前往冰霜之杖的所在地一探究竟。',Speaker=813380,}",
  [504881] = "{id=504881,Voice='Npc_Quest/504881',Text='哼，我同你们一起。',Speaker=813379,}",
  [504882] = "{id=504882,Voice='Npc_Quest/504882',Text='（超小声）好吓人，他难道想抢神器？不会对我们动手吧。',Speaker=813332,}",
  [504883] = "{id=504883,Voice='Npc_Quest/504883',Text='你说什么？！鬼鬼祟祟的人类！',Speaker=813379,}",
  [504884] = "{id=504884,Voice='Npc_Quest/504884',Text='呃，我、我自言自语，什么也没说。',Speaker=813332,}",
  [504885] = "{id=504885,Voice='Npc_Quest/504885',Text='毕竟霜巨人是生下我们的人，耶梦加得或许想找到一些她留下的痕迹吧。',Speaker=813380,}",
  [504886] = "{id=504886,Voice='Npc_Quest/504886',Text='可笑，那个蠢货说得对，我只是想看看能不能将这所谓的神器收为己用罢了。',Speaker=813379,}",
  [504887] = "{id=504887,Voice='Npc_Quest/504887',Text='冰霜之杖由远古冰河的幻影之力所打造，所散发出的寒冷是人类身体难以承受的。',Speaker=813380,Option='那怎么办？',}",
  [504888] = "{id=504888,Voice='Npc_Quest/504888',Text='月之国的月亮是奥丁用火之国中一朵朵蓝色火焰制成，寒冷时会凝结成水晶。',Speaker=813380,}",
  [504889] = "{id=504889,Voice='Npc_Quest/504889',Text='将这些蓝焰水晶佩戴在身上，它所散发的能量可以帮助你们抵御严寒。',Speaker=813380,}",
  [504890] = "{id=504890,Voice='Npc_Quest/504890',Text='太好了，我们现在就去收集水晶！',Speaker=813332,}",
  [504891] = "{id=504891,Voice='Npc_Quest/504891',Text='蠢货，小心点，别弄坏了这里的东西。',Speaker=813379,}",
  [504892] = "{id=504892,Voice='Npc_Quest/504892',Text='好漂亮的水晶，不知道能不能用来锻造？',Speaker=813332,}",
  [504893] = "{id=504893,Voice='Npc_Quest/504893',Text='虽然和我想象中不太一样，但神族真的好帅哦。',Speaker=813332,}",
  [504894] = "{id=504894,Voice='Npc_Quest/504894',Text='不过，他们真的是两兄弟吗？性格差异好大。',Speaker=813332,}",
  [504895] = "{id=504895,Voice='Npc_Quest/504895',Text='很好，这样一来你们可以安然无恙地接近冰霜之杖了。',Speaker=813380,}",
  [504896] = "{id=504896,Voice='Npc_Quest/504896',Text='如此脆弱的身体，人类真是可悲的种族。',Speaker=813379,}",
  [504897] = "{id=504897,Voice='Npc_Quest/504897',Text='（超小声）可恶——我竟然无力反驳！',Speaker=813332,}",
  [504898] = "{id=504898,Voice='Npc_Quest/504898',Text='除此之外，我还需要准备一些浮光魔粉。',Speaker=813380,Option='{有什么作用？,0},{哪里能获得？,0}',}",
  [504899] = "{id=504899,Voice='Npc_Quest/504899',Text='我并不清楚冰霜之杖掉落的确切位置，而浮光魔粉可以帮助我找到它。',Speaker=813380,}",
  [504900] = "{id=504900,Voice='Npc_Quest/504900',Text='距离神之领地最近的地方，精灵的国度艾卡拉奇的周边，生活着一种名为米米怪的神奇生物。',Speaker=813380,}",
  [504901] = "{id=504901,Voice='Npc_Quest/504901',Text='传闻中它是亡者的化身，它身上的浮光魔粉为迷途之人指引到心之所往。',Speaker=813380,}",
  [504902] = "{id=504902,Voice='Npc_Quest/504902',Text='命运之风，我已撕开空间之门，前往浮光星岩吧。',Speaker=813380,}",
  [504912] = "{id=504912,Text='空间之门的尽头连接着冰杖坠落之处，是否进入？',Speaker=813408,Option='{是,10},{否,11}',}",
  [504913] = "{id=504913,Voice='Npc_Quest/504913',Text='冷、冷冷，好冷啊，我感受不到我的身体了。',Speaker=813332,Option='{我要冻成冰了,0},{我的手脚发麻,0}',}",
  [504914] = "{id=504914,Voice='Npc_Quest/504914',Text='去感受蓝焰水晶的力量，你们需要尽快适应这里的环境。',Speaker=813380,}",
  [504915] = "{id=504915,Voice='Npc_Quest/504915',Text='真麻烦，说来说去，还是你们太过弱小。',Speaker=813379,}",
  [504916] = "{id=504916,Voice='Npc_Quest/504916',Text='（超小声）……你就非得跟我们一起吗？',Speaker=813332,}",
  [504917] = "{id=504917,Voice='Npc_Quest/504917',Text='哼，冰霜之杖的气息就在[c][ffff00]深处[-][/c]，我没工夫陪你们闲逛。',Speaker=813379,}",
  [504918] = "{id=504918,Text='咯——咯——咯——咯——',Speaker=63815,}",
  [504919] = "{id=504919,Voice='Npc_Quest/504919',Text='什么声音？',Speaker=813380,}",
  [504920] = "{id=504920,Voice='Npc_Quest/504920',Text='小心！',Speaker=813380,}",
  [504921] = "{id=504921,Voice='Npc_Quest/504921',Text='冷、冷死了，武器都差点拿不住。这些怪东西是什么？',Speaker=813332,}",
  [504922] = "{id=504922,Voice='Npc_Quest/504922',Text='远古神器都有自我保护的意志，这些应该是它为了自保创造出的守护者。',Speaker=813380,}",
  [504923] = "{id=504923,Voice='Npc_Quest/504923',Text='前路并不平静，命运之风，要小心，神器会对其周围产生不可估量的改变。',Speaker=813380,}",
  [504924] = "{id=504924,Voice='Npc_Quest/504924',Text='啊，寒风吹进我每一处的骨头缝……',Speaker=813332,}",
  [504925] = "{id=504925,Voice='Npc_Quest/504925',Text='神器在阻止我们的靠近，小心别被砸中了。',Speaker=813380,}",
  [504926] = "{id=504926,Text='“这边！”',Speaker=813380,}",
  [504927] = "{id=504927,Text='“当心！”',Speaker=813380,}",
  [504928] = "{id=504928,Text='“哎呀”',Speaker=813332,}",
  [504929] = "{id=504929,Text='“差点滑倒……”',Speaker=813332,}",
  [504930] = "{id=504930,Voice='Npc_Quest/504930',Text='小心，这也是冰霜之杖创造出的守护者。',Speaker=813380,}",
  [504931] = "{id=504931,Voice='Npc_Quest/504931',Text='难怪神器始终能完好的留在此地，它的自我保护意识超出我的想象，仿佛要拼死守护什么一般。',Speaker=813380,}",
  [504932] = "{id=504932,Voice='Npc_Quest/504932',Text='真别说，打起架来，身体稍微暖和了点。趁现在，我们继续前进吧！',Speaker=813332,}",
  [504933] = "{id=504933,Voice='Npc_Quest/504933',Text='这里的雾好浓啊，怎么什么都看不见了？',Speaker=813332,}",
  [504934] = "{id=504934,Voice='Npc_Quest/504934',Text='这里的浓雾是冰霜之杖隐藏自己的封印，若强行进入，将会永远迷失直至被死亡带走。',Speaker=813380,}",
  [504935] = "{id=504935,Voice='Npc_Quest/504935',Text='这……这可怎么办！？我们都到了这里，只差最后一步了……',Speaker=813332,}",
  [504936] = "{id=504936,Voice='Npc_Quest/504936',Text='或许只有用霜巨人的[c][ffff00]血之力[-][/c]才能驱散迷雾，解开封印。',Speaker=813380,}",
  [504937] = "{id=504937,Voice='Npc_Quest/504937',Text='什么？！那我们岂不是永远也解不开了？',Speaker=813332,Option='{笨蛋,0},{仔细想想,0}',}",
  [504938] = "{id=504938,Voice='Npc_Quest/504938',Text='你的眼前不正有两个流淌着霜巨人血脉的兄弟吗？',Speaker=813380,}",
  [504939] = "{id=504939,Voice='Npc_Quest/504939',Text='我和耶梦加得会将血之力通过地面传输到封印内部，与冰霜之杖产生链接，安抚它的躁动。',Speaker=813380,}",
  [504940] = "{id=504940,Voice='Npc_Quest/504940',Text='但与此同时，我们的血液会吸引周遭的魔物。命运之风，拜托你和你的友人将它们驱赶。',Speaker=813380,}",
  [504941] = "{id=504941,Voice='Npc_Quest/504941',Text='没问题，我和老兄一定会好好守护你们的！',Speaker=813332,}",
  [504942] = "{id=504942,Voice='Npc_Quest/504942',Text='呵，你们？守护我们？你倒是很会说笑话。',Speaker=813379,}",
  [504943] = "{id=504943,Voice='Npc_Quest/504943',Text='喂喂，别小瞧人啊，总有些我能做的事吧！',Speaker=813332,}",
  [504944] = "{id=504944,Voice='Npc_Quest/504944',Text='像你这样的蠢货，离我远点就是最大的用处。',Speaker=813379,}",
  [504945] = "{id=504945,Voice='Npc_Quest/504945',Text='耶梦加得，和你说过多少次了，别说这种话！',Speaker=813380,}",
  [504946] = "{id=504946,Voice='Npc_Quest/504946',Text='友人先生，你也可以贡献出自己的力量，帮助我们更快地安抚冰霜之杖。',Speaker=813380,}",
  [504947] = "{id=504947,Voice='Npc_Quest/504947',Text='没问题！看来我的力量很强大嘛！得到了神族的认可！',Speaker=813332,}",
  [504948] = "{id=504948,Voice='Npc_Quest/504948',Text='我们还需要做一些准备，麻烦二位先清除平台上的冰晶。',Speaker=813380,Option='{当然,0},{交给我吧,0}',}",
  [504949] = "{id=504949,Voice='Npc_Quest/504949',Text='（小声）老兄，我有重大发现！',Speaker=813332,Option='{什么发现？,0},{说清楚点！,0}',}",
  [504950] = "{id=504950,Voice='Npc_Quest/504950',Text='这里的冰晶都是非常稀有的[c][ffff00]幻影冰晶[-][/c]，它们能够始终保持绝对的低温。',Speaker=813332,}",
  [504951] = "{id=504951,Voice='Npc_Quest/504951',Text='这可是打造铁砧最好的材料！能快速冷却武器，让武器拥有非一般的硬度！',Speaker=813332,}",
  [504952] = "{id=504952,Voice='Npc_Quest/504952',Text='老兄，打个商量怎么样？将收集到的冰晶都交给我，如何？',Speaker=813332,Option='{可以,0},{反正我拿着也没用,0}',}",
  [504953] = "{id=504953,Voice='Npc_Quest/504953',Text='太让人感动了老兄，你果然是我最好的朋友！',Speaker=813332,}",
  [504954] = "{id=504954,Voice='Npc_Quest/504954',Text='跟在你身边，我一点都不冷了，知道为什么吗？',Speaker=813332,}",
  [504955] = "{id=504955,Voice='Npc_Quest/504955',Text='你就像那一把火，温暖了我的心窝~~',Speaker=813332,}",
  [504956] = "{id=504956,Voice='Npc_Quest/504956',Text='嘿嘿嘿，我已经可以想象到我的新铁砧了……',Speaker=813332,Option='{擦擦口水,0},{先做正事,0}',}",
  [504957] = "{id=504957,Voice='Npc_Quest/504957',Text='对对对，让我把它们收起来，可不能磕坏了。',Speaker=813332,}",
  [504958] = "{id=504958,Voice='Npc_Quest/504958',Text='我们已经做好准备了。',Speaker=813380,}",
  [504959] = "{id=504959,Voice='Npc_Quest/504959',Text='一群蝼蚁，竟敢觊觎神的力量！',Speaker=813379,}",
  [504960] = "{id=504960,Voice='Npc_Quest/504960',Text='呼……刚才注入的力量太多，我已经脱力了……',Speaker=813332,}",
  [504961] = "{id=504961,Voice='Npc_Quest/504961',Text='呵！你的力量就像大海中的一滴水，根本不值一提。',Speaker=813379,}",
  [504962] = "{id=504962,Voice='Npc_Quest/504962',Text='别发呆，还有呢，怎么没完没了。',Speaker=813332,}",
  [504963] = "{id=504963,Voice='Npc_Quest/504963',Text='老兄，小心身后！',Speaker=813332,}",
  [504964] = "{id=504964,Voice='Npc_Quest/504964',Text='不要放松警惕！我感受到一股强大的力量正在靠近！',Speaker=813380,}",
  [504965] = "{id=504965,Voice='Npc_Quest/504965',Text='命运之风，我们还需要一点时间！相信命运会站在我们的身边。',Speaker=813380,}",
  [504966] = "{id=504966,Voice='Npc_Quest/504966',Text='这就是她的冰霜之杖吗？',Speaker=813380,}",
  [504967] = "{id=504967,Voice='Npc_Quest/504967',Text='这东西，果然和那个女人一样冷酷。',Speaker=813379,}",
  [504968] = "{id=504968,Voice='Npc_Quest/504968',Text='命运之风，伸出你的手去读取蕴藏在它力量中的[c][ffff00]记忆[-][/c]吧。',Speaker=813380,}",
  [504969] = "{id=504969,Voice='Npc_Quest/504969',Text='嘶，这冰杖看着好冷啊，手摸到不会被冻掉吧？',Speaker=813332,}",
  [504970] = "{id=504970,Text='“怎么会……”',Speaker=813380,}",
  [504971] = "{id=504971,Text='“……”',Speaker=813379,}",
  [504972] = "{id=504972,Voice='Npc_Quest/504972',Text='原来冰霜之杖的坠落，是因为她想要救回我们……',Speaker=813380,}",
  [504973] = "{id=504973,Voice='Npc_Quest/504973',Text='……所以就应该原谅吗？',Speaker=813379,}",
  [504974] = "{id=504974,Voice='Npc_Quest/504974',Text='被驱赶，被排斥，那些无处可去，寄人篱下的日子你都忘了吗？',Speaker=813379,}",
  [504975] = "{id=504975,Voice='Npc_Quest/504975',Text='她在哪里？她现在，到底在哪里？！',Speaker=813379,}",
  [504976] = "{id=504976,Voice='Npc_Quest/504976',Text='耶梦加得！你也看到了！她为了我们，与神族发生了争斗……',Speaker=813380,}",
  [504977] = "{id=504977,Voice='Npc_Quest/504977',Text='我要听她说，听她亲口告诉我！',Speaker=813379,}",
  [504978] = "{id=504978,Voice='Npc_Quest/504978',Text='（超小声）冰霜之杖坠落的原因我们算是知道了，可关于乌龟岛的事情，我怎么还是那么糊涂？',Speaker=813332,}",
  [504979] = "{id=504979,Voice='Npc_Quest/504979',Text='只看到[c][ffff00]一群身着怪异服饰的人[-][/c]……还有突然出现的乌龟岛……这两者之间有什么关联吗？',Speaker=813332,}",
  [504980] = "{id=504980,Voice='Npc_Quest/504980',Text='终于见面了……',Speaker=813392,}",
  [504981] = "{id=504981,Voice='Npc_Quest/504981',Text='芬里尔、耶梦加得，我的孩子。',Speaker=813392,}",
  [504982] = "{id=504982,Voice='Npc_Quest/504982',Text='母亲？！',Speaker=813380,}",
  [504983] = "{id=504983,Text='？？？！！！',Speaker=813379,}",
  [504984] = "{id=504984,Voice='Npc_Quest/504984',Text='我并非真正的安格尔伯达，但我又是她。',Speaker=813393,}",
  [504985] = "{id=504985,Voice='Npc_Quest/504985',Text='我是她残留在冰霜之杖中的情感，因你们的血之力化作灵体。',Speaker=813393,}",
  [504986] = "{id=504986,Voice='Npc_Quest/504986',Text='她在何处？',Speaker=813379,}",
  [504987] = "{id=504987,Voice='Npc_Quest/504987',Text='……耶梦加得，我的孩子，你长大了，我还记得你儿时的模样。',Speaker=813393,}",
  [504988] = "{id=504988,Voice='Npc_Quest/504988',Text='住、住口！',Speaker=813379,}",
  [504989] = "{id=504989,Voice='Npc_Quest/504989',Text='芬里尔，我很高兴你一如我想象中般优秀，海拉呢？她还好吗？',Speaker=813393,}",
  [504990] = "{id=504990,Voice='Npc_Quest/504990',Text='母亲……不用担心，海拉在尼夫海姆，她现在是那里的主人。',Speaker=813380,}",
  [504991] = "{id=504991,Voice='Npc_Quest/504991',Text='那就好……我的心中溢满喜悦，我所牵挂的终于来到了我的面前。',Speaker=813393,}",
  [504992] = "{id=504992,Voice='Npc_Quest/504992',Text='看到你们安然无恙的长大，我也就放心了。',Speaker=813393,}",
  [504993] = "{id=504993,Voice='Npc_Quest/504993',Text='另外两个小家伙，你们到这里来，是有想追寻的真相吧？',Speaker=813393,Option='{乌龟岛,0},{巨型乌龟,0}',}",
  [504994] = "{id=504994,Voice='Npc_Quest/504994',Text='抱歉，你提到的我并不知晓。',Speaker=813393,}",
  [504995] = "{id=504995,Voice='Npc_Quest/504995',Text='咦？那个巨型乌龟可是一直围绕着冰霜之杖游动，想要试图抢夺冰霜之杖！',Speaker=813332,}",
  [504996] = "{id=504996,Voice='Npc_Quest/504996',Text='不，我从未感知到有别的个体觊觎着冰霜之杖。',Speaker=813393,}",
  [504997] = "{id=504997,Voice='Npc_Quest/504997',Text='怎么可能？！我们非常确定，它的出现与冰霜之杖息息相关！',Speaker=813332,}",
  [504998] = "{id=504998,Voice='Npc_Quest/504998',Text='友人先生，稍安勿躁。母亲，在冰霜之杖掉落之后，有发生过什么特别的事情吗？',Speaker=813380,}",
  [504999] = "{id=504999,Voice='Npc_Quest/504999',Text='如果真要说有什么特别之事的话——当时，[c][ffff00]旧良族[-][/c]的人曾来这里查看过情况。',Speaker=813393,}",
  [505000] = "{id=505000,Voice='Npc_Quest/505000',Text='旧良族？怎么名字这么耳熟……',Speaker=813332,Option='被乌龟吞噬的村庄',}",
  [505001] = "{id=505001,Voice='Npc_Quest/505001',Text='啊！原来是他们……可怜的家伙们……',Speaker=813332,}",
  [505002] = "{id=505002,Voice='Npc_Quest/505002',Text='那个信仰海姆达尔的人类种族？',Speaker=813379,}",
  [505003] = "{id=505003,Voice='Npc_Quest/505003',Text='没错。事实上，旧良族是守护神海姆达尔与人类的后代。',Speaker=813393,Option='他们来做什么？',}",
  [505004] = "{id=505004,Voice='Npc_Quest/505004',Text='他们什么也没做，只是静静地跪拜在冰霜之杖的面前。',Speaker=813393,}",
  [505005] = "{id=505005,Voice='Npc_Quest/505005',Text='不过当时，我的本体受了伤，冰霜之杖立刻开启了自我保护的机制。',Speaker=813393,}",
  [505006] = "{id=505006,Voice='Npc_Quest/505006',Text='冰霜之杖即将陷入沉睡之际，那位族长似乎说了一句话。',Speaker=813393,Option='他说了什么？',}",
  [505007] = "{id=505007,Voice='Npc_Quest/505007',Text='[c][ffff00]“千年后的风与种子终会相遇。”[-][/c]',Speaker=813393,}",
  [505008] = "{id=505008,Voice='Npc_Quest/505008',Text='[c][ffff00]风与种子？[-][/c]什么意思？',Speaker=813332,}",
  [505009] = "{id=505009,Voice='Npc_Quest/505009',Text='或许是命运选择了他们，他们也选择了命运。',Speaker=813393,}",
  [505010] = "{id=505010,Voice='Npc_Quest/505010',Text='什么意思……老兄，你听懂了吗？',Speaker=813332,Option='{隐隐约约吧,0},{完全不明白,0}',}",
  [505011] = "{id=505011,Voice='Npc_Quest/505011',Text='无需彷徨，只要走下去，你们总会明白其中真意。',Speaker=813393,}",
  [505012] = "{id=505012,Voice='Npc_Quest/505012',Text='你要去哪？又要抛弃我们吗？！',Speaker=813379,}",
  [505013] = "{id=505013,Voice='Npc_Quest/505013',Text='抱歉，我的力量已经耗尽，你们要平安，一如我所祈祷的。',Speaker=813393,}",
  [505014] = "{id=505014,Voice='Npc_Quest/505014',Text='母亲，你现在到底在何处？',Speaker=813380,}",
  [505015] = "{id=505015,Voice='Npc_Quest/505015',Text='时光会指引我们相会，孩子们，好好活着……',Speaker=813393,}",
  [505016] = "{id=505016,Voice='Npc_Quest/505016',Text='母亲！！！',Speaker=813380,}",
  [505017] = "{id=505017,Text='……',Speaker=813379,}",
  [505018] = "{id=505018,Text='……',Speaker=813379,}",
  [505019] = "{id=505019,Voice='Npc_Quest/505019',Text='别难过啦，说不定你们很快就能真正的相遇了！',Speaker=813332,}",
  [505020] = "{id=505020,Voice='Npc_Quest/505020',Text='哼，我还不需要一个愚蠢的人类来安慰我。',Speaker=813379,}",
  [505021] = "{id=505021,Voice='Npc_Quest/505021',Text='谁要安慰你，我只是还想问问旧良族的故事，怎么突然就没了？',Speaker=813332,}",
  [505022] = "{id=505022,Voice='Npc_Quest/505022',Text='据我所知，旧良族在某天[c][ffff00]突然消失[-][/c]，毫无音讯。',Speaker=813379,Option='{大概是因为……,0},{被乌龟吞噬了,0}……,0}',}",
  [505023] = "{id=505023,Voice='Npc_Quest/505023',Text='根据你们提供的线索，这种可能性很大。',Speaker=813379,}",
  [505024] = "{id=505024,Voice='Npc_Quest/505024',Text='（小声嘀咕）没想到你竟然会回答……',Speaker=813332,}",
  [505025] = "{id=505025,Voice='Npc_Quest/505025',Text='耶梦加得游历大陆，见多识广，并且此刻他的心情还不错。',Speaker=813380,}",
  [505026] = "{id=505026,Voice='Npc_Quest/505026',Text='少胡乱揣测了，我只是受不了他的愚蠢和聒噪罢了。',Speaker=813379,Option='还有其他信息吗？',}",
  [505027] = "{id=505027,Voice='Npc_Quest/505027',Text='旧良族消失后，一直有传言，艾尔贝塔的海域中时常能见到一些[c][ffff00]发光的奇异乌龟[-][/c]。',Speaker=813379,}",
  [505028] = "{id=505028,Voice='Npc_Quest/505028',Text='对！我们也见过，它们散发着温暖的光芒。',Speaker=813332,}",
  [505029] = "{id=505029,Voice='Npc_Quest/505029',Text='哼，倒很像对旧良族人的描述，他们的身上总是笼罩着类似的光芒。',Speaker=813379,Option='{难道……,0},{我有一个猜测……,0}',}",
  [505030] = "{id=505030,Voice='Npc_Quest/505030',Text='难不成！？这些小乌龟都是旧良族的人变得！？这是什么可怕的[c][ffff00]诅咒[-][/c]吗？',Speaker=813332,}",
  [505031] = "{id=505031,Voice='Npc_Quest/505031',Text='乌龟岛是大乌龟，它吃了旧良族，旧良族的人们又变成了小乌龟……',Speaker=813332,}",
  [505032] = "{id=505032,Voice='Npc_Quest/505032',Text='啊啊啊啊啊啊！根本搞不明白嘛！',Speaker=813332,}",
  [505033] = "{id=505033,Voice='Npc_Quest/505033',Text='就你这样比鸟还要小的大脑，是不可能想明白的。',Speaker=813379,}",
  [505034] = "{id=505034,Voice='Npc_Quest/505034',Text='……（小声）果然还是那个他。',Speaker=813332,}",
  [505035] = "{id=505035,Voice='Npc_Quest/505035',Text='仅靠猜测是无法找到真相的。我这里有一块海拉制作的淬魂石，可以与生物的灵魂共鸣。',Speaker=813380,}",
  [505036] = "{id=505036,Voice='Npc_Quest/505036',Text='你们带去被乌龟吞噬的旧良族遗址，或许能召唤出他们的灵魂，看到他们真实的模样。',Speaker=813380,Option='{太好了！,0},{是个好办法！,0}',}",
  [505037] = "{id=505037,Voice='Npc_Quest/505037',Text='或许这一切都是命运的安排，但你们需要追赶时间。',Speaker=813380,}",
  [505038] = "{id=505038,Voice='Npc_Quest/505038',Text='我能感受到冰霜之杖正在[c][ffff00]逐渐苏醒[-][/c]，它的力量愈加强盛，这样下去人类将难以生存。',Speaker=813380,}",
  [505039] = "{id=505039,Voice='Npc_Quest/505039',Text='你们两个可是神族诶！难道不能想办法把神器收起来？',Speaker=813332,}",
  [505040] = "{id=505040,Voice='Npc_Quest/505040',Text='哼，这是人类的命运，和我们又有什么关系？',Speaker=813379,}",
  [505041] = "{id=505041,Voice='Npc_Quest/505041',Text='抱歉，我们只有一半的巨人血脉，无法控制它。',Speaker=813380,}",
  [505042] = "{id=505042,Voice='Npc_Quest/505042',Text='[c][ffff00]或许人类的灾难终究需要人类去解决。[-][/c]',Speaker=813380,}",
  [505043] = "{id=505043,Voice='Npc_Quest/505043',Text='命运之风，我与弟弟将守在此地尽量延缓冰霜之杖的苏醒，你们务必要尽快想出办法。',Speaker=813380,Option='{我们会的,0},{希望一切顺利,0}',}",
  [505044] = "{id=505044,Voice='Npc_Quest/505044',Text='去吧，前往一切的源头之所。',Speaker=813380,}",
  [505067] = "{id=505067,Voice='Npc_Quest/505067',Text='[c][ffff00]守护神的后代[-][/c]啊，也不知道长什么样。',Speaker=828115,}",
  [505068] = "{id=505068,Text='哼！',Speaker=813379,}",
  [505070] = "{id=505070,Voice='Npc_Quest/505070',Text='好了，居然线索已经找到了，我也差不多该去给父亲请安了。',Speaker=813367,}",
  [505071] = "{id=505071,Voice='Npc_Quest/505071',Text='放心去吧，小少爷，接下来就交给我和老兄吧！',Speaker=813332,}",
  [505077] = "{id=505077,Text='太重了，不能同时搬两个箱子。',}",
  [505085] = "{id=505085,Text='将冰霜之杖及对旧良族的猜测告知了阿诺',}",
  [505086] = "{id=505086,Text='奇异的魔力笼罩在众人身上，一阵眩晕过后……',}",
  [505087] = "{id=505087,Text='很快耳边回荡起轻柔的海浪声，仿佛儿时的摇篮曲',}",
  [505088] = "{id=505088,Voice='Npc_Quest/505088',Text='我一上船就想吐……对不起……我先休息会。',Speaker=813332,}",
  [505090] = "{id=505090,Text='嗯？它怎么突然出现在这里的？刚刚这个位置有乌龟吗？',Speaker=813332,}",
  [505092] = "{id=505092,Text='它和小龟龟好像啊……',Speaker=813332,}",
  [505093] = "{id=505093,Text='它似乎在示意我们跟上。',Speaker=813332,}",
  [505094] = "{id=505094,Text='“好痛”',Speaker=813338,}",
  [505095] = "{id=505095,Text='“好热”',Speaker=813339,}",
  [505096] = "{id=505096,Text='“我要死了吗？”',Speaker=813340,}",
  [505097] = "{id=505097,Text='“救命啊”',Speaker=813341,}",
  [505098] = "{id=505098,Text='糟糕，他们都受伤了。',Speaker=813332,}",
  [505099] = "{id=505099,Text='我们必须找到能治疗他们的草药！',Speaker=813332,}",
  [505100] = "{id=505100,Text='……我来试试？拜托拜托~来点草药吧！',Speaker=813332,}",
  [505101] = "{id=505101,Text='哎，怎么可能，就算我许愿了，也……',Speaker=813332,}",
  [505102] = "{id=505102,Text='竟然真的有草药，快，老兄！赶紧的！',Speaker=813332,}",
  [505103] = "{id=505103,Text='刚才这里明明什么都没有……',Speaker=813332,}",
  [505104] = "{id=505104,Text='总不能是从石头缝里新长出来的吧……',Speaker=813332,}",
  [505105] = "{id=505105,Text='他们中似乎还有人中暑了，在这种温度下，如果没有降温药剂，他们很有可能脱水死去。',Speaker=813332,}",
  [505106] = "{id=505106,Text='持续的高温会让人惊厥，我们得在药剂中加入圣灵粉，才能解决这个问题。',Speaker=813332,}",
  [505107] = "{id=505107,Text='太好了！那边有许多圣灵龟，我们能从它们身上获得圣灵粉！',Speaker=813332,}",
  [505108] = "{id=505108,Text='奇怪？怎么一丁点圣灵粉都没有？',Speaker=813332,}",
  [505109] = "{id=505109,Text='怎么回事？这些圣灵龟是变异了吗？',Speaker=813332,}",
  [505110] = "{id=505110,Text='太奇怪了！不可能啊！',Speaker=813332,}",
  [505111] = "{id=505111,Text='这些乌龟是什么玩意？怎么能一点圣灵粉都不掉，那船员们怎么办？',Speaker=813332,}",
  [505112] = "{id=505112,Text='希望能快点弄到圣灵粉，不要耽误时间啊。',Speaker=813332,}",
  [505113] = "{id=505113,Text='哦~神明啊~拜托拜托~给我们一点圣灵粉吧~！',Speaker=813332,}",
  [505114] = "{id=505114,Text='有了！哈哈哈，没想到这次竟然会这么顺利！',Speaker=813332,}",
  [505115] = "{id=505115,Voice='Npc_Quest/505115',Text='既然有了圣灵粉，我们可以制造降温药剂，治疗大家了！',Speaker=813332,}",
  [505116] = "{id=505116,Text='没想到这次出海会发生这些乱七八糟的事。',Speaker=813332,}",
  [505117] = "{id=505117,Text='我本来只是想登上乌龟岛，收集一些霜炎水晶啊，可现在去哪里找呢？',Speaker=813332,}",
  [505118] = "{id=505118,Text='啊~来点霜炎水晶吧！拜托了！',Speaker=813332,}",
  [505119] = "{id=505119,Text='哎哟喂，好痛，什么东西砸到我了。',Speaker=813332,}",
  [505120] = "{id=505120,Text='这是……霜炎水晶？！',Speaker=813332,}",
  [505121] = "{id=505121,Text='嘿嘿，这下我的心愿满足了！走吧，咱们快去救人！',Speaker=813332,}",
  [505122] = "{id=505122,Text='那边两个人，你们在干什么？别拍了！',Speaker=813333,}",
  [505123] = "{id=505123,Text='喂，你们以为自己是来旅游的？',Speaker=813333,}",
  [505124] = "{id=505124,Voice='Npc_Quest/505124',Text='喂，那边两个人，你们以为自己是来旅游的？',Speaker=813333,}",
  [505125] = "{id=505125,Voice='Npc_Quest/505125',Text='糟糕，他们都受伤了，可我没有带治疗药剂。',Speaker=813332,}",
  [505126] = "{id=505126,Voice='Npc_Quest/505126',Text='如果能找到草药就好了……',Speaker=813332,}",
  [505127] = "{id=505127,Voice='Npc_Quest/505127',Text='哎，是呀，怎么可能，就算我[c][ffff00]许愿[-][/c]了，也……',Speaker=813332,}",
  [505128] = "{id=505128,Voice='Npc_Quest/505128',Text='哎？！出现了！竟然真的有草药，快，老兄！赶紧的！',Speaker=813332,}",
  [505129] = "{id=505129,Voice='Npc_Quest/505129',Text='啊~来点霜炎水晶吧！拜托了！',Speaker=813332,}",
  [505130] = "{id=505130,Voice='Npc_Quest/505130',Text='那边两个人，你们在干什么？别',Speaker=813333,}",
  [505131] = "{id=505131,Voice='Npc_Quest/505131',Text='刚才那个莎拉·艾琳分析，我们所有人现在都在[c][ffff00]魔物[-][/c]的肚子里！',Speaker=813333,}",
  [505132] = "{id=505132,Voice='Npc_Quest/505132',Text='只要毁了这颗心脏，这个吃人的东西自然也就死了吧！',Speaker=813345,}",
  [505133] = "{id=505133,Text='好了，我知道你的忠心了。从刚才开始你已经说了八百遍。',Speaker=813333,}",
  [505134] = "{id=505134,Voice='Npc_Quest/505134',Text='我的霜炎水晶呢？！还好……还在……',Speaker=813332,}",
  [505135] = "{id=505135,Voice='Npc_Quest/505135',Text='其他人怎么样了？我们快去看看他们的情况。',Speaker=813332,}",
  [505137] = "{id=505137,Voice='Npc_Quest/505137',Text='离开这里！命运的种子，还未到破土之时……',Speaker=813439,}",
  [505138] = "{id=505138,Text='随着船体遭受了未知的撞击，所有人在轰鸣声中陷入昏迷……',}",
  [505139] = "{id=505139,Text='冒险者与忽克连登上了巴尔特家族的舰队，却在海上遭遇未知的撞击，巨大的轰鸣声后，所有人都陷入昏迷。\n再次醒来时，冒险者与忽克连发现他们进入了一处炎热的神秘之所，两人在乌龟的引导下找到了受伤的水手，并为他们进行治疗。在这里忽克连的祈祷总能奏效，这让他坚信此处正是自己的幸运之地。\n随后众人继续跟着乌龟，找到了伊安·巴尔特，他的身边却站着忽克连与冒险者曾在乌龟岛上见过的神秘女子，她似乎是为了寻找某样物品，和巴尔特舰队一起漂流到了这里。\n一番调查后，冒险者发现此地曾是人类的村庄，生活着名为旧良族的种族，却被某个巨大的魔物吞入腹中，而他们此时正在魔物的肚子里！难怪水手们难以找到乌龟岛的具体位置，因为这座岛屿竟是活物，在不停地游动着。\n就在这时忽克连发现了一座具有神奇力量的巨大熔炉，这让他欣喜若狂，打算用这座熔炉打造影子装备，然而神秘女子却突然对熔炉发起攻击，却被忽克连扑倒在地，面纱掉落，原来神秘女子正是芙蕾雅的神使——莎拉·艾琳。\n莎拉·艾琳声称这个熔炉是魔物的心脏，只有毁灭心脏，才能杀死魔物，避免给大陆带来更大的灾难。伊安紧随其后，表示一定要毁了熔炉，保护艾尔贝塔的安全。\n忽克连却认为熔炉能治愈众人的伤口，并非大家想象中那样邪恶，双方争执不下时，一只如同将军的乌龟突然出现，要将所有人驱逐离开，就在众人打败乌龟将军，打算给它最后一击时，滚石落下，旧良宫发生地震，所有人再一次陷入昏迷。\n醒来后，众人竟回到了艾尔贝塔的港口，伊安等人身受重伤，被阿诺带回家中休养。阿诺从众人的描述中回忆起一些相关信息，让冒险者与忽克连明日来家中找他。',}",
  [505140] = "{id=505140,Text='艾尔贝塔寒潮加剧，冒险者与忽克连前往巴尔特府邸，随后阿诺带领他们进入家中藏书阁，找到了关于乌龟岛的线索。\n据悉，乌龟岛的出现与曾经的寒潮有关，而这场寒潮是由霜巨人的神器——冰霜之杖引起的。为寻求帮助，忽克连和冒险者前往月之湖找到冰霜巨人的孩子，芬里尔与耶梦加得。\n得知两人来意后，芬里尔答应帮忙，使用空间之门带着二人来到冰杖坠落之处，一路上遇见了许多冰雪魔物，而这些都是由冰霜之杖的自我意识幻化而成，仿佛在拼死守护着什么。\n芬里尔与耶梦加得以血脉之力解开冰杖封印并与之共鸣，得知冰霜之杖是霜巨人安格尔伯达为寻回芬里尔三兄妹，与神族对峙时不慎掉落的，却给人类带来了巨大灾难。但不知为何，寒潮却并未蔓延开来，米德加尔特大陆安然无恙，而这一切或许与旧良族有关。\n为了进一步揭开真相，芬里尔将淬魂石交给忽克连与冒险者，让他们在旧良族遗迹使用，或许能通过淬魂石召唤出旧良族人的灵魂，从而知道当年的故事。\n回到艾尔贝塔后，忧心忡忡的阿诺早已等候在忽克连的铁匠铺前，他下定决心，打算带领手下护送忽克连与冒险者重登乌龟岛，势必要找出真相，守护艾尔贝塔！',}",
  [505141] = "{id=505141,Text='哇！好期待！！',}",
  [505142] = "{id=505142,Text='这是要去哪里呀？',}",
  [505143] = "{id=505143,Text='前面好像有什么东西……',}",
  [505144] = "{id=505144,Text='快跟上！快跟上！',}",
  [505192] = "{id=505192,Text='呜哈……',Speaker=813464,}",
  [505195] = "{id=505195,Voice='Npc_Quest/505195',Text='我代表克雷斯特汉姆感谢你的帮助，冒险者。',Speaker=815052,Option='{不用道谢,0},{我是骑士团的一员,0}',}",
  [505196] = "{id=505196,Voice='Npc_Quest/505196',Text='是我说错话了，我为有你这样的骑士感到骄傲。',Speaker=815052,}",
  [505197] = "{id=505197,Voice='Npc_Quest/505197',Text='等一切平息后，我将授予你荣誉骑士的勋章，你会是克雷斯特汉姆永远的座上宾。',Speaker=815052,}",
  [505198] = "{id=505198,Voice='Npc_Quest/505198',Text='准了！朋友，遇见你是这场灾难中唯一的幸运。',Speaker=815051,Option='{哈哈哈,0},{过奖了,0}',}",
  [505199] = "{id=505199,Voice='Npc_Quest/505199',Text='接下来我们需要找到另外三个骑士团团长。',Speaker=815052,Option='{他们在哪？,0},{怎么找到他们？,0}',}",
  [505200] = "{id=505200,Voice='Npc_Quest/505200',Text='根据防御布置，距离这里最近的是[c][ffff00]守卫团团长立典拉德[-][/c]，他正在授勋圣所指挥骑士团及卫兵团。',Speaker=815052,Option='立典拉德？',}",
  [505201] = "{id=505201,Voice='Npc_Quest/505201',Text='他可是骑士中的典范，自律严谨，就是话有点少。',Speaker=815051,}",
  [505202] = "{id=505202,Voice='Npc_Quest/505202',Text='走，我们快去授勋圣所，你见到他就知道了。',Speaker=815051,}",
  [505203] = "{id=505203,Voice='Npc_Quest/505203',Text='我同你们一起，免得国王不知道什么时候又失踪了。',Speaker=815052,}",
  [505204] = "{id=505204,Voice='Npc_Quest/505204',Text='哎呀，怎么会呢，我觉得你应该相信我。',Speaker=815051,}",
  [505205] = "{id=505205,Voice='Npc_Quest/505205',Text='前提是你值得被信任。',Speaker=815052,Option='{你们在吵架吗？,0},{快走吧,0}',}",
  [505206] = "{id=505206,Text='……',Speaker=815051,}",
  [505207] = "{id=505207,Text='坚持住！',Speaker=815255,}",
  [505208] = "{id=505208,Text='该死！',Speaker=815059,}",
  [505209] = "{id=505209,Text='完全……',Speaker=815059,}",
  [505210] = "{id=505210,Text='动不了……',Speaker=815059,}",
  [505211] = "{id=505211,Voice='Npc_Quest/505211',Text='城里怎么会出现诅咒光球？',Speaker=815052,}",
  [505212] = "{id=505212,Voice='Npc_Quest/505212',Text='糟糕，诅咒光球在吸收大家的生命力，必须尽快处理！',Speaker=815051,Option='我来试试',}",
  [505213] = "{id=505213,Voice='Npc_Quest/505213',Text='不，这太危险了，还是我来！',Speaker=815051,}",
  [505214] = "{id=505214,Voice='Npc_Quest/505214',Text='施密特！别忘了，诅咒光球只对荣誉骑士毫无作用！',Speaker=815052,Option='{没错,0},{我有“时空差”！,0}',}",
  [505215] = "{id=505215,Voice='Npc_Quest/505215',Text='差点忘了！那就——拜托了，朋友！',Speaker=815051,}",
  [505216] = "{id=505216,Voice='Npc_Quest/505216',Text='如果没有你，诅咒光球会带来无数的麻烦。',Speaker=815052,}",
  [505217] = "{id=505217,Voice='Npc_Quest/505217',Text='施密特能将你带来，恐怕是他少有做的正确的事了。',Speaker=815052,}",
  [505218] = "{id=505218,Voice='Npc_Quest/505218',Text='喂喂……你还是应该给你的君王留点面子……',Speaker=815051,}",
  [505219] = "{id=505219,Voice='Npc_Quest/505219',Text='请您见谅，实话总会显得格外刺耳。',Speaker=815052,}",
  [505220] = "{id=505220,Voice='Npc_Quest/505220',Text='立典拉德大人！你怎么了！？',Speaker=815054,}",
  [505221] = "{id=505221,Voice='Npc_Quest/505221',Text='糟糕，死亡之力没办法清除，那几个人要成为感染者了！',Speaker=815051,}",
  [505222] = "{id=505222,Voice='Npc_Quest/505222',Text='立刻阻止他们！',Speaker=815052,}",
  [505223] = "{id=505223,Voice='Npc_Quest/505223',Text='不好，再这样下去，立典拉德和骑士们也会从感染者变成腐败者。',Speaker=815051,}",
  [505224] = "{id=505224,Voice='Npc_Quest/505224',Text='如果……我们……',Speaker=815255,}",
  [505225] = "{id=505225,Voice='Npc_Quest/505225',Text='陛下……请您……结束……我们的痛苦……',Speaker=815255,}",
  [505226] = "{id=505226,Voice='Npc_Quest/505226',Text='在失去理智……之前……不能让更多人……',Speaker=815255,}",
  [505227] = "{id=505227,Voice='Npc_Quest/505227',Text='不，事情还没有到最糟糕的一步，我想想，肯定还有别的办法。',Speaker=815051,}",
  [505228] = "{id=505228,Voice='Npc_Quest/505228',Text='施密特，爱德伦的生命之水。',Speaker=815052,}",
  [505229] = "{id=505229,Voice='Npc_Quest/505229',Text='我怎么能忘了生命之水！',Speaker=815051,}",
  [505230] = "{id=505230,Voice='Npc_Quest/505230',Text='我感觉好多了……陛下，医生们已经研制出解药了吗？',Speaker=815053,}",
  [505231] = "{id=505231,Voice='Npc_Quest/505231',Text='这是爱德伦医生研制出的生命之水，虽然没办法完全治愈，但可以暂时缓解。',Speaker=815051,}",
  [505232] = "{id=505232,Voice='Npc_Quest/505232',Text='诅咒光球让守卫团的许多骑士都出现了感染前兆，陛下，求您救治大家。',Speaker=815053,}",
  [505233] = "{id=505233,Voice='Npc_Quest/505233',Text='放心交给我吧！多亏最后击败了那条巨龙，现在我们拥有足够的生命之水。',Speaker=815051,}",
  [505234] = "{id=505234,Voice='Npc_Quest/505234',Text='你是黑暗军团的主要目标，诅咒光球既然能在王城出现，就有可能对你造成伤害。',Speaker=815052,}",
  [505235] = "{id=505235,Voice='Npc_Quest/505235',Text='请你留在这里，接受骑士们的保护。',Speaker=815052,}",
  [505236] = "{id=505236,Voice='Npc_Quest/505236',Text='我不需要保护，有冒险者跟在我身边，肯定没问题的，你说对吧？',Speaker=815051,Option='{呃……,0},{应该吧……,0}',}",
  [505237] = "{id=505237,Voice='Npc_Quest/505237',Text='君王有君王的责任，请您留在我的身边。骑士们！',Speaker=815052,}",
  [505238] = "{id=505238,Voice='Npc_Quest/505238',Text='（小声）看来真的不行了，朋友，只能麻烦你拿着生命之水救治大家了。',Speaker=815051,Option='{交给我吧,0},{等着我,0}',}",
  [505239] = "{id=505239,Text='总算还有点神智，去用生命之水净化他们吧。',}",
  [505240] = "{id=505240,Text='诅咒光球实在太可怕了……快用生命之水净化他们吧。',}",
  [505241] = "{id=505241,Text='好像恢复了点理智，趁现在快用生命之水净化他们吧。',}",
  [505242] = "{id=505242,Voice='Npc_Quest/505242',Text='诅咒光球。',Speaker=815053,}",
  [505243] = "{id=505243,Voice='Npc_Quest/505243',Text='原来如此。',Speaker=815051,}",
  [505244] = "{id=505244,Text='……',Speaker=815052,}",
  [505245] = "{id=505245,Voice='Npc_Quest/505245',Text='感谢您施以援手。',Speaker=815053,Option='{太客气了,0},{这是我应该做的,0}',}",
  [505246] = "{id=505246,Voice='Npc_Quest/505246',Text='我们正在讨论诅咒光球是怎么进入王城的，立典拉德，你再说说当时的情况。',Speaker=815051,}",
  [505247] = "{id=505247,Voice='Npc_Quest/505247',Text='是，我原本正在巡逻，突然听到授勋圣所传来异常声响，等我赶来时，诅咒光球已经在这里了。',Speaker=815053,Option='这么突然？',}",
  [505248] = "{id=505248,Voice='Npc_Quest/505248',Text='等发现时已经太晚了，很多人都产生了感染，我也只能苦苦支撑，好在终于等到了你们的救援。',Speaker=815053,}",
  [505249] = "{id=505249,Voice='Npc_Quest/505249',Text='……除此以外，你还有什么能提供的线索？',Speaker=815052,}",
  [505250] = "{id=505250,Voice='Npc_Quest/505250',Text='很抱歉，或许是感染的影响，我的记忆很混乱……',Speaker=815053,}",
  [505251] = "{id=505251,Voice='Npc_Quest/505251',Text='这次全然是我的疏忽，请陛下和总司令官责罚。',Speaker=815053,}",
  [505252] = "{id=505252,Voice='Npc_Quest/505252',Text='黑暗军团无孔不入，这不能怪你，也该怪我突然消失，致使王国动荡。',Speaker=815051,}",
  [505253] = "{id=505253,Voice='Npc_Quest/505253',Text='够了，现在最重要的是查明真相，其余的先放在一旁。',Speaker=815052,Option='{有道理,0},{该怎么办？,0}',}",
  [505254] = "{id=505254,Voice='Npc_Quest/505254',Text='看来必须调取“[c][ffff00]王国之眼[-][/c]”的记录了。',Speaker=815052,Option='王国之眼？',}",
  [505255] = "{id=505255,Voice='Npc_Quest/505255',Text='和跃迁塔一样，王国之眼也是老师从遗迹中复原的科技成果，能记录下一段时间内发生的景象。',Speaker=815051,}",
  [505256] = "{id=505256,Voice='Npc_Quest/505256',Text='大贤者是划时代的天才，我们都十分尊敬他。',Speaker=815051,}",
  [505257] = "{id=505257,Voice='Npc_Quest/505257',Text='……我还是第一次听说王国之眼。',Speaker=815053,}",
  [505258] = "{id=505258,Voice='Npc_Quest/505258',Text='哈哈，从制作到放置都是海因里希秘密安排下去的，连我都不是很清楚。',Speaker=815051,}",
  [505259] = "{id=505259,Voice='Npc_Quest/505259',Text='王国之眼藏得很隐蔽，我会告知你方位，麻烦你将授勋圣所附近的王国之眼回收。',Speaker=815052,Option='没问题！',}",
  [505260] = "{id=505260,Text='收集完毕，可以回去交给海因里希了。',}",
  [505261] = "{id=505261,Voice='Npc_Quest/505261',Text='看来我们很快就能知道真相了。',Speaker=815052,}",
  [505262] = "{id=505262,Voice='Npc_Quest/505262',Text='该死，快阻止他们！',Speaker=815051,}",
  [505263] = "{id=505263,Voice='Npc_Quest/505263',Text='是！',Speaker=815053,}",
  [505264] = "{id=505264,Voice='Npc_Quest/505264',Text='他们的目标是王国之眼！',Speaker=815051,}",
  [505265] = "{id=505265,Voice='Npc_Quest/505265',Text='如你们所见，他们的目标正是王国之眼，即便在你们战斗时，我竭尽全力保护王国之眼，但——',Speaker=815052,Option='{怎么了？,0},{难道还是……,0}',}",
  [505266] = "{id=505266,Voice='Npc_Quest/505266',Text='还是受了一些影响，很有可能无法读取，但也可能我们会有好运。',Speaker=815052,}",
  [505267] = "{id=505267,Voice='Npc_Quest/505267',Text='试试就知道了！',Speaker=815051,}",
  [505268] = "{id=505268,Voice='Npc_Quest/505268',Text='立典拉德，请你立刻召集守卫团骑士警戒并守卫这里，不要再出现类似的情况了。',Speaker=815052,}",
  [505269] = "{id=505269,Voice='Npc_Quest/505269',Text='集合！警戒！',Speaker=815053,}",
  [505270] = "{id=505270,Voice='Npc_Quest/505270',Text='不出意外的话，这次应该不会有意外了。',Speaker=815051,}",
  [505271] = "{id=505271,Voice='Npc_Quest/505271',Text='快看看到底是怎么回事，我已经等不及了！',Speaker=815051,Option='是啊',}",
  [505272] = "{id=505272,Voice='Npc_Quest/505272',Text='（远处的声音）希默梅思：啊哈哈哈哈哈……做得很好，暗答莱尸。',Speaker=815058,}",
  [505273] = "{id=505273,Voice='Npc_Quest/505273',Text='没想到，真的能看到当时的情况！还能记录声音！不过可惜有些短暂……',Speaker=815051,}",
  [505274] = "{id=505274,Voice='Npc_Quest/505274',Text='大贤者之前说过，现在的王国之眼还有许多需要改进的。',Speaker=815052,}",
  [505275] = "{id=505275,Voice='Npc_Quest/505275',Text='一个就是记录的时长，还有就是他希望以后可以实时监控，不再需要回收。',Speaker=815052,}",
  [505276] = "{id=505276,Voice='Npc_Quest/505276',Text='只不过，计划还没有展开，他就因为一些要事离开了都城。',Speaker=815052,}",
  [505277] = "{id=505277,Voice='Npc_Quest/505277',Text='不过，这段影像……（思考）……',Speaker=815052,}",
  [505278] = "{id=505278,Text='（持续沉思）……',Speaker=815052,Option='{海因里希？,0},{白星？,0}',}",
  [505279] = "{id=505279,Voice='Npc_Quest/505279',Text='抱歉，我还在整理线索，现在有让我很在意的问题。',Speaker=815052,Option='{什么问题？,0},{说来听听!,0}',}",
  [505280] = "{id=505280,Voice='Npc_Quest/505280',Text='克雷斯特汉姆的防御还未被黑暗军团突破，暗答莱尸是如何进入城内的？',Speaker=815052,}",
  [505281] = "{id=505281,Voice='Npc_Quest/505281',Text='根据我们现有的结论，感染者没有理智，可刚才的感染者却选择破坏王国之眼，这说明……',Speaker=815052,Option='{有人操纵它们,0},{操纵者就在附近,0}',}",
  [505282] = "{id=505282,Voice='Npc_Quest/505282',Text='没错，我们甚至不知道感染者是如何出现的，这说明授勋圣所或许已经不安全了，另外——',Speaker=815052,}",
  [505283] = "{id=505283,Voice='Npc_Quest/505283',Text='我很在意施密特失踪一事，他是在阅兵大典上突然消失不见的，而授勋圣所正是阅兵仪式举办的地方。',Speaker=815052,Option='这里？',}",
  [505284] = "{id=505284,Voice='Npc_Quest/505284',Text='你知道吗，有一种鸟名为嗅魔鸟，它以吞噬魔法之力为食……',Speaker=815052,}",
  [505285] = "{id=505285,Voice='Npc_Quest/505285',Text='这些嗅魔鸟有些碍事，我们去把它们赶走！',Speaker=815051,}",
  [505286] = "{id=505286,Voice='Npc_Quest/505286',Text='还记得当时阅兵仪式上的场景吗？当时，准点的钟声响起，施密特端起酒杯，随后他就消失了……',Speaker=815052,}",
  [505287] = "{id=505287,Voice='Npc_Quest/505287',Text='我想想……嗯……当时就在这个位置……',Speaker=815051,}",
  [505288] = "{id=505288,Voice='Npc_Quest/505288',Text='我听你的嘱咐，在准点的钟声响起时，就拿起了旁边的酒杯……',Speaker=815051,}",
  [505289] = "{id=505289,Voice='Npc_Quest/505289',Text='然后，我眼前就一阵白光闪过，再醒来的时候就在亚时空空间中了。',Speaker=815051,}",
  [505290] = "{id=505290,Voice='Npc_Quest/505290',Text='嗯，你就在这个位置，在众目睽睽之下消失了。然后，城外就传来了黑暗军团进攻的声音，',Speaker=815052,}",
  [505291] = "{id=505291,Voice='Npc_Quest/505291',Text='这样的“巧合”让民众认为是你使用了禁术召唤出了黑暗军团。',Speaker=815052,}",
  [505292] = "{id=505292,Voice='Npc_Quest/505292',Text='可这真的是巧合吗？嗅魔鸟聚集在这个位置，说明这里[c][ffff00]残存着魔法之力[-][/c]！',Speaker=815052,}",
  [505293] = "{id=505293,Voice='Npc_Quest/505293',Text='说起来……那天我的确也感应到了空气中魔力的波动……',Speaker=815051,}",
  [505294] = "{id=505294,Voice='Npc_Quest/505294',Text='老师曾教过我如何追寻魔法的残留痕迹，让我试试……',Speaker=815051,}",
  [505295] = "{id=505295,Voice='Npc_Quest/505295',Text='出现了，让我们顺着这些这些痕迹，看看还能找到点什么线索！',Speaker=815052,}",
  [505296] = "{id=505296,Voice='Npc_Quest/505296',Text='似乎是通往钟楼的方向……',Speaker=815051,}",
  [505297] = "{id=505297,Text='保护大家！',Speaker=815054,}",
  [505298] = "{id=505298,Text='清醒点啊！',Speaker=815054,}",
  [505299] = "{id=505299,Text='怎么会这样……',Speaker=815054,}",
  [505300] = "{id=505300,Voice='Npc_Quest/505300',Text='可恶，这里竟然也沦陷了……',Speaker=815051,}",
  [505301] = "{id=505301,Voice='Npc_Quest/505301',Text='冒险者！',Speaker=815052,Option='{明白了！,0},{我去清理！,0}',}",
  [505302] = "{id=505302,Voice='Npc_Quest/505302',Text='看来现在王城内也并不安全了。',Speaker=815051,}",
  [505303] = "{id=505303,Voice='Npc_Quest/505303',Text='但这里并没有黑暗军团入侵的痕迹……',Speaker=815052,}",
  [505304] = "{id=505304,Voice='Npc_Quest/505304',Text='啊啊啊啊，救命！！！',Speaker=815060,}",
  [505305] = "{id=505305,Voice='Npc_Quest/505305',Text='糟糕，晚了一步！',Speaker=815051,}",
  [505306] = "{id=505306,Voice='Npc_Quest/505306',Text='那边交给你们！我去指挥疏散民众！',Speaker=815052,}",
  [505307] = "{id=505307,Voice='Npc_Quest/505307',Text='守卫团骑士听令，立刻带着所有民众前往圣泉礼拜堂避难！',Speaker=815052,}",
  [505308] = "{id=505308,Voice='Npc_Quest/505308',Text='老人孩子在前，骑士断后，遵守秩序。',Speaker=815052,}",
  [505309] = "{id=505309,Text='我的孩子……',Speaker=815054,}",
  [505310] = "{id=505310,Voice='Npc_Quest/505310',Text='没想到……最后竟然死在了……同伴的剑下……',Speaker=815054,}",
  [505311] = "{id=505311,Voice='Npc_Quest/505311',Text='陛下……求……求您，保护好我的孩子……',Speaker=815054,}",
  [505312] = "{id=505312,Voice='Npc_Quest/505312',Text='我答应你……',Speaker=815051,}",
  [505313] = "{id=505313,Voice='Npc_Quest/505313',Text='一定送他走……[c][ffff00]离开这个可怕的地方[-][/c]……让他……活下去。',Speaker=815054,}",
  [505314] = "{id=505314,Voice='Npc_Quest/505314',Text='你是一位英勇的骑士，你的孩子理应得到照顾。',Speaker=815051,}",
  [505315] = "{id=505315,Voice='Npc_Quest/505315',Text='谢谢……您……',Speaker=815054,}",
  [505316] = "{id=505316,Voice='Npc_Quest/505316',Text='希默梅斯，我绝不会放过你！这些骑士不应该死在自己人手中，该死的诅咒光球！',Speaker=815051,Option='{请节哀……,0},{唉……,0}',}",
  [505317] = "{id=505317,Voice='Npc_Quest/505317',Text='我们一定要开启王座之门，只有这样，才不会让更多的人失去生命。',Speaker=815051,Option='{你说得对,0},{或许是唯一的办法,0}',}",
  [505318] = "{id=505318,Voice='Npc_Quest/505318',Text='还需要三位骑士，不，两位，我们已经找到了立典拉德！',Speaker=815051,}",
  [505319] = "{id=505319,Voice='Npc_Quest/505319',Text='陛下，请您指示。',Speaker=815053,}",
  [505320] = "{id=505320,Voice='Npc_Quest/505320',Text='关于消灭黑暗军团……',Speaker=815051,}",
  [505321] = "{id=505321,Voice='Npc_Quest/505321',Text='坐上王座的骑士需要骑士团长们提供生命力，这很危险，但我希望你能答应。',Speaker=815051,}",
  [505322] = "{id=505322,Voice='Npc_Quest/505322',Text='我不会以君王的身份强压你，一切都由你来决定。',Speaker=815051,}",
  [505323] = "{id=505323,Voice='Npc_Quest/505323',Text='为了民众，为了克雷斯特汉姆，这是我应该做的。',Speaker=815053,}",
  [505324] = "{id=505324,Voice='Npc_Quest/505324',Text='太好了，立典拉德，你是一位令人尊敬的骑士。',Speaker=815051,}",
  [505325] = "{id=505325,Voice='Npc_Quest/505325',Text='谢谢您的夸赞，可我……',Speaker=815053,}",
  [505326] = "{id=505326,Voice='Npc_Quest/505326',Text='没关系，有什么顾虑可以直接告诉我。',Speaker=815051,}",
  [505327] = "{id=505327,Voice='Npc_Quest/505327',Text='卢恩钟楼对我，不，应该说对我的家族而言意义重大，我希望能修好它。',Speaker=815053,}",
  [505328] = "{id=505328,Voice='Npc_Quest/505328',Text='当[c][ffff00]听到钟声再次响起[-][/c]后，我想我就可以无憾地离开这里，前往王座之间。',Speaker=815053,Option='原来如此',}",
  [505329] = "{id=505329,Voice='Npc_Quest/505329',Text='要是消灭魔物还简单点，修复钟楼啊……这可让人苦恼了。',Speaker=815051,Option='{你不是……,0},{巴特蒙德的弟子？,0}',}",
  [505330] = "{id=505330,Voice='Npc_Quest/505330',Text='咳咳，虽然这么说，但老师觉得我这方面没什么天赋，我对这些简直是一窍不通。',Speaker=815051,Option='那怎么办？',}",
  [505331] = "{id=505331,Voice='Npc_Quest/505331',Text='倒是海因里希跟随老师学习过一段时间，他很聪明，学什么都很快，说不定能有办法！',Speaker=815051,}",
  [505332] = "{id=505332,Voice='Npc_Quest/505332',Text='民众都已避难，你们那边看起来也稳住了局面。',Speaker=815052,Option='{告知立典拉德的请求,0}',}",
  [505333] = "{id=505333,Voice='Npc_Quest/505333',Text='事情就是这样，老师夸赞过你有科研和机械的天赋，你应该能修复钟楼吧？',Speaker=815051,}",
  [505334] = "{id=505334,Voice='Npc_Quest/505334',Text='……作为一个总是捅娄子的国王，您知道接下来我还有多少事要安排吗？',Speaker=815052,}",
  [505335] = "{id=505335,Voice='Npc_Quest/505335',Text='我是总司令官，不是维修人员。',Speaker=815052,}",
  [505336] = "{id=505336,Voice='Npc_Quest/505336',Text='哎呀，别这么生气嘛……实在不行我去找找老师有没有留下什么笔记，我来试着修复吧。',Speaker=815051,Option='{你能看得懂？,0},{还不如我来,0}',}",
  [505337] = "{id=505337,Voice='Npc_Quest/505337',Text='二位，请不要异想天开。算了，我先去看看钟楼的损坏情况。',Speaker=815052,}",
  [505338] = "{id=505338,Voice='Npc_Quest/505338',Text='立典拉德的姓氏原本代表着一个显赫的贵族。',Speaker=815052,}",
  [505339] = "{id=505339,Voice='Npc_Quest/505339',Text='但因一些往事，整个家族已然落魄，现只剩他一人。',Speaker=815052,}",
  [505340] = "{id=505340,Voice='Npc_Quest/505340',Text='他成为一名高尚的骑士，一心只为恢复家族荣耀。',Speaker=815052,}",
  [505341] = "{id=505341,Voice='Npc_Quest/505341',Text='这座钟楼的钟芯正是由立典拉德家族所造，听闻上面还刻有家族的箴言——荣耀不灭。',Speaker=815052,}",
  [505342] = "{id=505342,Voice='Npc_Quest/505342',Text='怎么样？对你而言容易吗？是你的话一定没问题的吧，白星。',Speaker=815051,}",
  [505343] = "{id=505343,Voice='Npc_Quest/505343',Text='安静一点，我还在评估……',Speaker=815052,}",
  [505344] = "{id=505344,Voice='Npc_Quest/505344',Text='或许可以修复，但我需要一些工具。',Speaker=815052,Option='{什么工具？,0},{我能帮上忙吗？,0}',}",
  [505345] = "{id=505345,Voice='Npc_Quest/505345',Text='我需要钟楼的建造图纸、工具箱以及发条零件。',Speaker=815052,}",
  [505346] = "{id=505346,Voice='Npc_Quest/505346',Text='我以前去到钟楼里看过，知道图纸和工具箱的位置，不如……',Speaker=815051,}",
  [505347] = "{id=505347,Voice='Npc_Quest/505347',Text='请您安静地待在我的身旁，我相信以荣誉骑士的能力，这些不过是小事，对吗？',Speaker=815052,Option='{对……对吧,0},{我一个人也可以！,0}',}",
  [505348] = "{id=505348,Voice='Npc_Quest/505348',Text='好吧，这附近日常巡查的皇家侍卫会为钟楼上发条，你可以从他们身上找到发条，图纸和工具箱的位置我也一并告诉你。',Speaker=815051,}",
  [505349] = "{id=505349,Voice='Npc_Quest/505349',Text='朋友，没有我在旁边，你自己可得小心点。',Speaker=815051,Option='{知道了,0},{不用担心,0}',}",
  [505350] = "{id=505350,Text='好多感染者……看来必须先击败他们。',}",
  [505351] = "{id=505351,Text='快拿给海因里希吧。',}",
  [505352] = "{id=505352,Voice='Npc/vox_emo_male_gentle_suspect_02',Text='我看看……嗯，图纸标注的很清楚，至少我们知道了该如何修理。',Speaker=815052,}",
  [505353] = "{id=505353,Text='东西都掉出来了，得快点整理好。',}",
  [505354] = "{id=505354,Text='沉甸甸的，快去交给海因里希吧。',}",
  [505355] = "{id=505355,Voice='Npc/vox_emo_male_gentle_agree_02',Text='（检查一番）可以，里面的工具也都很齐全。',Speaker=815052,}",
  [505356] = "{id=505356,Text='数量足够了，把发条带给海因里希吧。',}",
  [505357] = "{id=505357,Voice='Npc/vox_emo_male_gentle_agree_02',Text='（数了数）很好，数量足够了。',Speaker=815052,}",
  [505358] = "{id=505358,Voice='Npc_Quest/505358',Text='东西都准备齐全了，可以开始修复了。',Speaker=815052,Option='{太好了！,0},{终于……,0}',}",
  [505359] = "{id=505359,Voice='Npc_Quest/505359',Text='请稍等片刻。',Speaker=815052,}",
  [505360] = "{id=505360,Voice='Npc_Quest/505360',Text='久违的钟声，上一次听到还是在阅兵大典。',Speaker=815051,}",
  [505361] = "{id=505361,Voice='Npc_Quest/505361',Text='看来有些不速之客在听到钟声后前来做客了。',Speaker=815051,}",
  [505362] = "{id=505362,Voice='Npc_Quest/505362',Text='……让这钟声为他们送行吧，愿他们得到安息。',Speaker=815052,}",
  [505363] = "{id=505363,Voice='Npc_Quest/505363',Text='大人……大人……',Speaker=64550,}",
  [505364] = "{id=505364,Voice='Npc_Quest/505364',Text='他似乎还有一丝清醒，快捉住他，或许能获得一些情报。',Speaker=815052,}",
  [505365] = "{id=505365,Voice='Npc_Quest/505365',Text='又一次！这里简直被渗透成了筛子，守卫团的骑士们到底在做什么！',Speaker=815052,}",
  [505366] = "{id=505366,Voice='Npc_Quest/505366',Text='唉，他们损失惨重，大部分骑士都被诅咒光球所感染，现在人手严重不足，也不能怪他们。',Speaker=815051,}",
  [505367] = "{id=505367,Voice='Npc_Quest/505367',Text='你总是……算了，我不想与你争吵，我们换个话题，看这个。',Speaker=815052,}",
  [505368] = "{id=505368,Voice='Npc_Quest/505368',Text='这是什么？等等……为什么有种莫名熟悉的感觉？',Speaker=815051,Option='熟悉？',}",
  [505369] = "{id=505369,Voice='Npc_Quest/505369',Text='这是刚才修复钟楼时在钟芯上找到的，当钟表走到特定时间后，齿轮将触发咒符。',Speaker=815052,Option='{特定时间？,0},{难道是阅兵仪式……,0}',}",
  [505370] = "{id=505370,Voice='Npc_Quest/505370',Text='如果我没有弄错的话，正是这张咒符上的魔法，在阅兵大典的钟声响起后，将施密特带去了亚空间。',Speaker=815052,}",
  [505371] = "{id=505371,Voice='Npc_Quest/505371',Text='这是时空魔法？！除了老师外，竟然还有人拥有这样掌控时空的力量？',Speaker=815051,Option='是谁？',}",
  [505372] = "{id=505372,Voice='Npc_Quest/505372',Text='不，其实在阅兵仪式的前一天，[c][ffff00]巴特蒙德的房间曾被人潜入[-][/c]。',Speaker=815052,}",
  [505373] = "{id=505373,Voice='Npc_Quest/505373',Text='那个小偷并不谨慎，根本没有清扫痕迹，在检查后我们发现，那人偷走了许多魔法咒符。',Speaker=815052,}",
  [505374] = "{id=505374,Voice='Npc_Quest/505374',Text='到底是什么人？我们可以调取王国之眼查看吗？',Speaker=815051,}",
  [505375] = "{id=505375,Voice='Npc_Quest/505375',Text='那附近的王国之眼全部都被破坏了，很明显，王城内部早就出了问题。',Speaker=815052,}",
  [505376] = "{id=505376,Voice='Npc_Quest/505376',Text='该死，那个小偷到底是谁？他的目的是什么？为什么要把我传送到亚空间？……',Speaker=815051,Option='{太多问题了 ……,0},{内忧外患啊,0}',}",
  [505377] = "{id=505377,Voice='Npc_Quest/505377',Text='现在去深究那些已经太晚了，更重要的是往前走，我们不能再受制于人了。',Speaker=815052,}",
  [505378] = "{id=505378,Voice='Npc_Quest/505378',Text='呼……你说的没错，我必须冷静下来，至少我们已经修复了钟楼，实现了立典拉德的心愿。',Speaker=815051,}",
  [505379] = "{id=505379,Voice='Npc_Quest/505379',Text='嗯，先让他坐上王座，我们就只需要再寻找剩余的两名骑士团长。',Speaker=815052,}",
  [505380] = "{id=505380,Voice='Npc_Quest/505380',Text='我恳求您允许我加入！',Speaker=815061,}",
  [505381] = "{id=505381,Voice='Npc_Quest/505381',Text='孩子，我理解你的心情，可你的父亲希望你能平安，远离战争……',Speaker=815053,}",
  [505382] = "{id=505382,Voice='Npc_Quest/505382',Text='立典拉德叔叔，求您了，黑暗军团夺走了我的父亲，我要让他们血债血偿。',Speaker=815061,}",
  [505383] = "{id=505383,Voice='Npc_Quest/505383',Text='不要冲动！你的父亲只有你这一个儿子，若是你出了意外，你的家族……',Speaker=815053,}",
  [505384] = "{id=505384,Voice='Npc_Quest/505384',Text='不，您说的不对，如果我真的苟且偷生，那才是为家族蒙羞，我请求您，以家族的荣耀，以我的生命请求您——',Speaker=815061,}",
  [505385] = "{id=505385,Voice='Npc_Quest/505385',Text='允许我成为守护团骑士的一员，继承父亲的意志！！！',Speaker=815061,}",
  [505386] = "{id=505386,Voice='Npc_Quest/505386',Text='陛下，总司令官，我已听到了钟声，只是眼前这位……',Speaker=815053,}",
  [505387] = "{id=505387,Voice='Npc_Quest/505387',Text='这位少年正是此前为了救人而死的那位英勇骑士的儿子，也是他留下的唯一的血脉。',Speaker=815053,}",
  [505388] = "{id=505388,Voice='Npc_Quest/505388',Text='还记得他父亲的嘱托吗？',Speaker=815053,Option='{送孩子离开……,0},{好好活着,0}',}",
  [505389] = "{id=505389,Voice='Npc_Quest/505389',Text='唉，是啊，可他却一心想加入骑士团，延续父辈的荣光。',Speaker=815053,}",
  [505390] = "{id=505390,Voice='Npc_Quest/505390',Text='我要为父亲报仇！这是我的心愿…… 陛下……大人……求求你们了。',Speaker=815061,}",
  [505391] = "{id=505391,Voice='Npc_Quest/505391',Text='……我理解你的悲痛，但也无法辜负你父亲的嘱托……',Speaker=815051,}",
  [505392] = "{id=505392,Text='冒险者，你的到来是命运的安排，请由你来做出决断吧……',Speaker=815051,}",
  [505393] = "{id=505393,Text='您说的对，这位新晋骑士显然是一个勇敢而聪敏的人，他肯定能做出合适的选择。',Speaker=815053,}",
  [505394] = "{id=505394,Voice='Npc_Quest/505394',Text='看来你又要做出抉择了，要不要听听未来的故事呀？',Speaker=815062,Option='{要！,0},{会发生什么？,0}',}",
  [505395] = "{id=505395,Voice='Npc_Quest/505395',Text='嗯……我看到了命运之河的不同流向……',Speaker=815062,}",
  [505396] = "{id=505396,Voice='Npc_Quest/505396',Text='如果他没有加入骑士团，将会离开这里，成为一个寂寂无名的普通人。',Speaker=815062,}",
  [505397] = "{id=505397,Voice='Npc_Quest/505397',Text='就像他父亲希望的那样，远离战争，娶妻生子，幸福的过完一生。',Speaker=815062,Option='如果留下呢？',}",
  [505398] = "{id=505398,Voice='Npc_Quest/505398',Text='但是如果留下的话……没有太多战争经验的骑士将死在战场上。',Speaker=815062,}",
  [505399] = "{id=505399,Voice='Npc_Quest/505399',Text='没有人会记得他……但似乎他战死时并不后悔，甚至有些开心终于能去见父亲了。',Speaker=815062,}",
  [505400] = "{id=505400,Voice='Npc_Quest/505400',Text='唔……人类的情感可真是复杂啊，真是难以理解。',Speaker=815062,}",
  [505401] = "{id=505401,Voice='Npc_Quest/505401',Text='算了，反正我只是提醒你未来的走向，做出决定的人是你咯。',Speaker=815062,}",
  [505402] = "{id=505402,Text='来吧，做出你的选择吧。',Speaker=815062,Option='{答应少年,4},{拒绝少年,5},{返回,3}',}",
  [505403] = "{id=505403,Voice='Npc_Quest/860420',Text='是吗……？这就是你的选择吗？这个时空，会依循你的意志。',Speaker=815062,}",
  [505404] = "{id=505404,Voice='Npc_Quest/505404',Text='谢谢你，谢谢！总司令官，求您亲自为我授勋。',Speaker=815061,}",
  [505405] = "{id=505405,Voice='Npc_Quest/505405',Text='我答应你的请求。',Speaker=815052,}",
  [505406] = "{id=505406,Voice='Npc_Quest/505406',Text='愿你恪守荣耀、谦卑勇敢、公正怜悯，一如你的父辈。',Speaker=815052,}",
  [505407] = "{id=505407,Voice='Npc_Quest/505407',Text='我将铭记于心，如父亲般战斗，直到流尽最后一滴血。',Speaker=815063,}",
  [505408] = "{id=505408,Voice='Npc_Quest/505408',Text='立典拉德团长，守护团新晋骑士——请求归队。',Speaker=815063,}",
  [505409] = "{id=505409,Voice='Npc_Quest/505409',Text='欢迎归队，前去集合吧。',Speaker=815053,}",
  [505410] = "{id=505410,Voice='Npc_Quest/505410',Text='是！！！',Speaker=815063,}",
  [505411] = "{id=505411,Voice='Npc_Quest/505411',Text='为什么……是我还不够优秀吗？是我不配成为骑士吗？',Speaker=815061,Option='{想想你的父亲,0},{想想你的家族,0}',}",
  [505412] = "{id=505412,Voice='Npc_Quest/505412',Text='战争是残酷的，你还太年轻，你的人生才刚刚开始，不该被葬送在这里。',Speaker=815053,}",
  [505413] = "{id=505413,Voice='Npc_Quest/505413',Text='牺牲只需要一次冲锋，可长久地活下去才是更勇敢的决定，请相信我，不要浪费你父亲的牺牲。',Speaker=815053,}",
  [505414] = "{id=505414,Voice='Npc_Quest/505414',Text='如果连你都在战场上死去，那么还有谁会记得他的名字？活下去吧，把他的事迹告诉你的孩子，你孩子的孩子……',Speaker=815053,}",
  [505415] = "{id=505415,Voice='Npc_Quest/505415',Text='立典拉德叔叔……对不起，是我太任性了。',Speaker=815061,}",
  [505416] = "{id=505416,Voice='Npc_Quest/505416',Text='您说的对，我不能辜负父亲的期望，我要努力活着。',Speaker=815061,}",
  [505417] = "{id=505417,Voice='Npc_Quest/505417',Text='好孩子，愿你有灿烂的人生。',Speaker=815051,}",
  [505418] = "{id=505418,Voice='Npc_Quest/505418',Text='陛下，谢谢您的祝福，我想……我该离开了，愿幸运和胜利长伴白骑士团。',Speaker=815061,}",
  [505419] = "{id=505419,Voice='Npc_Quest/505419',Text='我并不是一个善于言辞的人，但我不得不说，你是我见过最优秀的新人，听说你在修复钟楼时也出了许多力。',Speaker=815053,Option='{多谢夸奖,0},{我应当做的,0}',}",
  [505420] = "{id=505420,Voice='Npc_Quest/505420',Text='这座钟楼是支撑我走到现在的精神支柱，每当我停滞不前，就会来到这里，看着转圈的钟表。',Speaker=815053,}",
  [505421] = "{id=505421,Voice='Npc_Quest/505421',Text='这会提醒我，我背负着家族的荣誉，永远也不能休息……',Speaker=815053,Option='{这太累了,0},{真不容易,0}',}",
  [505422] = "{id=505422,Voice='Npc_Quest/505422',Text='陛下，我的身体渐渐从感染中恢复了，我想我可以完成我的诺言了。',Speaker=815053,}",
  [505423] = "{id=505423,Voice='Npc_Quest/505423',Text='现在就出发吧，留给我们的时间不多了。',Speaker=815052,}",
  [505424] = "{id=505424,Voice='Npc_Quest/505424',Text='还差两位骑士团长，可谁知道这段时间会发生什么……',Speaker=815051,}",
  [505425] = "{id=505425,Voice='Npc_Quest/505425',Text='万一黑暗军团再次入侵，万一希默梅斯已经拿到了爱丽丝，万一……',Speaker=815051,}",
  [505426] = "{id=505426,Voice='Npc_Quest/505426',Text='冷静一点，焦虑并不能改变现状！只有一步步完成计划，才能带领大家走向胜利。',Speaker=815052,}",
  [505427] = "{id=505427,Voice='Npc_Quest/505427',Text='你总是对的，我只是太心急了。',Speaker=815051,}",
  [505428] = "{id=505428,Voice='Npc_Quest/505428',Text='刚才立典拉德告诉我，他安排斯特汀前往圣泉礼拜堂协助教会建立避难所，收容民众。',Speaker=815052,}",
  [505429] = "{id=505429,Voice='Npc_Quest/505429',Text='真没想到那个家伙竟然会答应做这些琐事……真没想到。',Speaker=815051,}",
  [505430] = "{id=505430,Voice='Npc_Quest/505430',Text='毕竟立典拉德同样是贵族之后，如果换成费沙或者沙利，斯特汀恐怕会立刻拒绝。',Speaker=815052,Option='为什么？',}",
  [505431] = "{id=505431,Voice='Npc_Quest/505431',Text='无非就是那些贵族和平民之类的言论……哼，他这个人……',Speaker=815051,}",
  [505432] = "{id=505432,Voice='Npc_Quest/505432',Text='斯特汀出身于老牌贵族，实际上骑士团的大部分人都是贵族之后，也只有近些年施密特改革后才有平民加入。',Speaker=815052,}",
  [505433] = "{id=505433,Voice='Npc_Quest/505433',Text='所以施密特，请慎言，毕竟斯特汀在贵族中地位不低，如果你想让他帮助你开启王座之门，恐怕需要费一番功夫。',Speaker=815052,Option='{原来如此,0},{听起来不好惹,0}',}",
  [505434] = "{id=505434,Voice='Npc_Quest/505434',Text='知道了，唉，又是那套，所以我讨厌当国王，当初父王真应该让你来坐这个位置！',Speaker=815051,}",
  [505435] = "{id=505435,Voice='Npc_Quest/505435',Text='……别抱怨了，国！王！陛！下！先去圣泉礼拜堂找斯特汀吧。',Speaker=815052,}",
  [505436] = "{id=505436,Text='都快点，没吃饭吗？！',Speaker=815064,}",
  [505437] = "{id=505437,Text='这边，加固防御！',Speaker=815125,}",
  [505438] = "{id=505438,Text='这就来……',Speaker=815126,}",
  [505439] = "{id=505439,Text='神啊，保佑我们。',Speaker=815115,}",
  [505440] = "{id=505440,Text='太可怕了。',Speaker=815116,}",
  [505441] = "{id=505441,Text='安全了吗？',Speaker=815117,}",
  [505442] = "{id=505442,Voice='Npc_Quest/505442',Text='看看是谁来了？原来是尊贵的施密特王。',Speaker=815064,}",
  [505443] = "{id=505443,Voice='Npc_Quest/505443',Text='陛下终于舍得露面了，度假结束了吗？您总是如此富有冒险精神，真让人佩服。',Speaker=815064,}",
  [505444] = "{id=505444,Voice='Npc_Quest/505444',Text='只可惜您回来的太早，作为主角没能好好招待黑暗军团，只能由我们这些不入流的小人物代劳。',Speaker=815064,Option='{这是讽刺吗？,0},{绝对是讽刺吧……,0}',}",
  [505445] = "{id=505445,Voice='Npc_Quest/505445',Text='注意你的言辞！斯特汀，不要将王的宽容当成软弱。',Speaker=815052,}",
  [505446] = "{id=505446,Voice='Npc_Quest/505446',Text='真是抱歉，我怎么忘了旁边还有忠心耿耿的总司令官大人呢，真不该让我的抱怨脏了您和陛下的耳朵。',Speaker=815064,}",
  [505447] = "{id=505447,Voice='Npc_Quest/505447',Text='以下犯上，你的贵族礼仪都学到何处了？',Speaker=815052,}",
  [505448] = "{id=505448,Voice='Npc_Quest/505448',Text='我们贵族的命恐怕在他的眼中还不如卑贱的平民！看看吧，死了多少骑士，他那时人又在何处？',Speaker=815064,}",
  [505449] = "{id=505449,Voice='Npc_Quest/505449',Text='够了，都请冷静点！我为我的言行向你道歉，斯特汀。',Speaker=815051,Option='{不用向他道歉！,0},{不是你的错！,0}',}",
  [505450] = "{id=505450,Voice='Npc_Quest/505450',Text='这位在我们交谈时随意插话的骑士又是谁？看他的模样，恐怕又是被王提拔的平民吧。',Speaker=815064,}",
  [505451] = "{id=505451,Voice='Npc_Quest/505451',Text='巴特蒙德大人喜欢挖掘遗迹，而他的弟子却热爱从垃圾堆里淘金，真是一脉相承。',Speaker=815064,Option='{这……,0},{又在讽刺？,0}',}",
  [505452] = "{id=505452,Voice='Npc_Quest/505452',Text='我理解你的不满，也可以容忍你对我的无礼，但他是我的客人，也是克雷斯特汉姆的贵客，请你放尊重点。',Speaker=815051,}",
  [505453] = "{id=505453,Voice='Npc_Quest/505453',Text='谨遵施密特王的命令，毕竟我也不想被白星的剑架到我的脖子上。',Speaker=815064,}",
  [505454] = "{id=505454,Voice='Npc_Quest/505454',Text='那么这位……怎么称呼？',Speaker=815064,Option='{冒险者,0},{荣誉骑士,0}',}",
  [505455] = "{id=505455,Voice='Npc_Quest/505455',Text='我的名字是[c][ffff00]斯特汀·力哈尔特[-][/c]，称呼我时不要忘记我的姓氏，那是我的先祖为这个卢恩流过血的证明。',Speaker=815064,}",
  [505456] = "{id=505456,Voice='Npc_Quest/505456',Text='也请尊贵的陛下能记住，是这些被你所轻慢的贵族祖辈们的牺牲，换来了王国的建立！',Speaker=815064,}",
  [505457] = "{id=505457,Voice='Npc_Quest/505457',Text='我时刻铭记着。但也请你记住，民众也是帝国的基础，人生来无法选择自己的出身。',Speaker=815051,}",
  [505458] = "{id=505458,Voice='Npc_Quest/505458',Text='斯特汀，无谓的口舌之快就到此为止。我理解你的心情，我也是为解决克雷斯特汉姆的现状而来。',Speaker=815051,}",
  [505459] = "{id=505459,Voice='Npc_Quest/505459',Text='哼，听起来高贵的陛下似乎需要我的帮助，但很可惜，我现在没有时间，我需要先让骑士们建立避难区。',Speaker=815064,}",
  [505460] = "{id=505460,Voice='Npc_Quest/505460',Text='圣泉礼拜堂的人越来越多，根本无法安置这么多人，这可是为了保护陛下所重视的民众，您不会不同意吧。',Speaker=815064,}",
  [505461] = "{id=505461,Voice='Npc_Quest/505461',Text='……当然，我们也会一起协助的。',Speaker=815051,}",
  [505462] = "{id=505462,Voice='Npc_Quest/505462',Text='那就请便，想了解具体情况就去找我手下的两个队长吧。',Speaker=815064,}",
  [505463] = "{id=505463,Voice='Npc_Quest/505463',Text='祝你们一切顺利，陛下、总司令官以及这位……抱歉，你的名字太普通，我不记得了。',Speaker=815064,Option='……',}",
  [505464] = "{id=505464,Voice='Npc/vox_emo_male_deep_angry_02',Text='陛下、总司令官，我们将民众护送前来后不久，发现周围有许多堕落之眼。',Speaker=815118,}",
  [505465] = "{id=505465,Voice='Npc/vox_emo_male_deep_admire_01',Text='真不知道它们到底是怎么出现的，我们正在清理，可数量太多了……',Speaker=815118,Option='{交给我吧！,0},{放着我来！,0}',}",
  [505466] = "{id=505466,Voice='Npc/vox_emo_male_gentle_agree_01',Text='嗯，我们分开行动，加快速度。',Speaker=815052,}",
  [505467] = "{id=505467,Voice='Npc/vox_emo_male_deep_sad_01',Text='那真是太感谢了！唉，现在人手严重不足，真让人头疼。',Speaker=815118,}",
  [505468] = "{id=505468,Voice='Npc/vox_emo_shimite_admire_01',Text='斯特汀对我的怨气可真重啊……',Speaker=815051,}",
  [505469] = "{id=505469,Voice='Npc/vox_emo_male_gentle_agree_01',Text='他只是贵族的代表，不必在意，一切都是为了利益罢了。',Speaker=815052,}",
  [505470] = "{id=505470,Voice='Npc/vox_emo_male_deep_admire_01',Text='总算清理了那些恶心的眼睛，真让人奇怪，那玩意儿到底是什么？',Speaker=815118,Option='{不知道,0},{有点恶心,0}',}",
  [505471] = "{id=505471,Voice='Npc/vox_emo_male_deep_angry_02',Text='当它盯着你的时候，就感觉被什么人盯着似的，这让人毛骨悚然……我们打算在周围布置防御结界装置。',Speaker=815118,}",
  [505472] = "{id=505472,Voice='Npc/vox_emo_shimite_suspect_01',Text='还有什么需要帮忙的吗？',Speaker=815051,}",
  [505473] = "{id=505473,Voice='Npc/vox_emo_male_deep_sad_01',Text='有的有的，为了这片区域的安全，我们还需要在周围布置防御结界装置。',Speaker=815118,}",
  [505474] = "{id=505474,Voice='Npc/vox_emo_male_deep_sad_01',Text='具体的点位斯特汀大人在地图上已经标注，各位大人，拜托了！',Speaker=815118,}",
  [505475] = "{id=505475,Voice='Npc/vox_emo_male_deep_deny_02',Text='看起来还不错，那么现在就开启防御！',Speaker=815118,}",
  [505476] = "{id=505476,Voice='Npc/vox_emo_shimite_admire_01',Text='这样就可以了？',Speaker=815051,}",
  [505477] = "{id=505477,Voice='Npc/vox_emo_male_deep_agree_01',Text='我想是的，非常感谢，这样一来我们可以安心点了。',Speaker=815118,}",
  [505478] = "{id=505478,Text='怎么办？',Speaker=815125,}",
  [505479] = "{id=505479,Text='人太多了……',Speaker=815126,}",
  [505480] = "{id=505480,Text='求求您了。',Speaker=815099,}",
  [505481] = "{id=505481,Text='救救我们吧。',Speaker=815099,}",
  [505482] = "{id=505482,Text='唉！',Speaker=815120,}",
  [505483] = "{id=505483,Voice='Npc/vox_emo_male_deep_agree_01',Text='很抱歉让国王陛下看到如此混乱的情况，可前来教堂的民众实在太多了，远远超过这里能容纳的范围。',Speaker=815120,}",
  [505484] = "{id=505484,Voice='Npc/vox_emo_shimite_deny_02',Text='还能再找一个为止搭建新的临时避难所吗？',Speaker=815051,}",
  [505485] = "{id=505485,Voice='Npc/vox_emo_male_deep_agree_01',Text='我们想过这个方案，可在短时间内很难收集到足够的建筑材料，而且这方面的人才也稀缺。',Speaker=815120,}",
  [505486] = "{id=505486,Text='……我记得教堂的地底在修建之初有地下避难所，或许可以考虑将这些房间打开。',Speaker=815052,}",
  [505487] = "{id=505487,Voice='Npc/vox_emo_male_deep_agree_02',Text='是的，斯特汀团长也想到了，可那些门都是特制的，只有使用神官们的钥匙才能打开……',Speaker=815120,}",
  [505488] = "{id=505488,Voice='Npc/vox_emo_shimite_admire_01',Text='那还在等什么？！让神官们用钥匙把门打开啊！',Speaker=815051,}",
  [505489] = "{id=505489,Voice='Npc/vox_emo_male_deep_agree_03',Text='那些神官因为之前的诅咒光球，大部分都感染了……',Speaker=815120,}",
  [505490] = "{id=505490,Voice='Npc/vox_emo_male_deep_angry_02',Text='我们若是贸然接近很容易引发更多伤亡，现在活着的骑士太珍贵了。',Speaker=815120,}",
  [505491] = "{id=505491,Voice='Npc/vox_emo_shimite_agree_02',Text='我知道了，交给我们吧。',Speaker=815051,}",
  [505492] = "{id=505492,Voice='Npc/vox_emo_male_deep_fear_03',Text='不可以！陛下！若是您出了事，又由谁来带领我们呢？',Speaker=815120,}",
  [505493] = "{id=505493,Voice='Npc/vox_emo_male_gentle_agree_02',Text='他说得对，君王的安全是至关重要的，你绝不能出意外。',Speaker=815052,Option='还是我来吧！',}",
  [505494] = "{id=505494,Voice='Npc/vox_emo_shimite_relief_02',Text='可我如何能看着民众受苦，或许斯特汀说得对，这一切都是因为我……',Speaker=815051,}",
  [505495] = "{id=505495,Voice='Npc/vox_emo_male_gentle_angry_01',Text='如果你真的这么想，那藏在背后的阴谋家恐怕要得逞了，如果你实在放心不下，就由我和冒险者一起吧。',Speaker=815052,}",
  [505496] = "{id=505496,Text='留在这里，安抚民众，让他们安心是比腾出空间更重要的事。',Speaker=815052,}",
  [505497] = "{id=505497,Voice='Npc/vox_emo_shimite_relief_01',Text='……好吧，那就拜托你们了！',Speaker=815051,}",
  [505498] = "{id=505498,Voice='Npc/vox_emo_male_deep_sad_01',Text='施密特的心肠太软，这对于国王而言并非好事。',Speaker=815052,}",
  [505499] = "{id=505499,Voice='Npc/vox_emo_male_gentle_deny_01',Text='但正是这份特质，让施密特比任何人都明白民众的苦楚。',Speaker=815052,}",
  [505500] = "{id=505500,Text='数量差不多了，去找扎纳特吧。',Speaker=815052,}",
  [505501] = "{id=505501,Voice='Npc/vox_emo_male_deep_joy_02',Text='太好了，这下我们可以打开那些房间，容纳更多民众了。',Speaker=815120,}",
  [505502] = "{id=505502,Voice='Npc/vox_emo_male_deep_agree_02',Text='不过地下避难所房间所对应的钥匙只有主神官最清楚，还得麻烦你们将钥匙交给他。',Speaker=815120,Option='{没问题,0},{现在就去！,0}',}",
  [505503] = "{id=505503,Voice='Npc/vox_emo_shimite_relief_02',Text='民众的情绪很低落，早一点打开地下避难所的门，或许他们也能早一点安心。',Speaker=815051,}",
  [505504] = "{id=505504,Text='救、救命……',Speaker=815129,}",
  [505505] = "{id=505505,Text='糟糕，怎么这里也有诅咒光球！快去保护主神官！',Speaker=815051,}",
  [505506] = "{id=505506,Voice='Npc/vox_emo_male_deep_admire_01',Text='感谢神……',Speaker=815129,}",
  [505507] = "{id=505507,Voice='Npc/vox_emo_male_deep_admire_01',Text='哦，是国王陛下和总司令官，还有这位骑士，幸亏你们及时赶到，感谢神明！',Speaker=815129,Option='发生了什么？',}",
  [505508] = "{id=505508,Voice='Npc/vox_emo_male_deep_sad_03',Text='这些侍卫是斯特汀团长派来保护我的，可没想到会发生这种事，他们……他们差点害死我。',Speaker=815129,}",
  [505509] = "{id=505509,Voice='Npc/vox_emo_male_deep_fear_03',Text='神明在上，这一切太可怕了，是因为世人不够虔诚吗？为何要降下如此灾难。',Speaker=815129,}",
  [505510] = "{id=505510,Voice='Npc/vox_emo_male_gentle_deny_01',Text='战争总是残酷的，你的脸色不好看，似乎还在发抖，你还好吗？',Speaker=815052,}",
  [505511] = "{id=505511,Voice='Npc/vox_emo_male_deep_agree_01',Text='我……我很好，非常好……绝对没有问题。',Speaker=815129,}",
  [505512] = "{id=505512,Voice='Npc/vox_emo_shimite_relief_01',Text='那就好，我们还得麻烦您用钥匙打开地下避难所的门。',Speaker=815051,}",
  [505513] = "{id=505513,Voice='Npc/vox_emo_male_gentle_admire_03',Text='钥匙钥匙钥匙！我知道了，现在就把钥匙给我吧，我……我还要去神像前祷告。',Speaker=815129,}",
  [505514] = "{id=505514,Voice='Npc/vox_emo_male_young_suspect_02',Text='等一等，需要我们护送你前去吗？',Speaker=815052,}",
  [505515] = "{id=505515,Voice='Npc/vox_emo_male_deep_deny_02',Text='不不不，我一个人就好……再、再见，愿神保佑你们。',Speaker=815129,}",
  [505516] = "{id=505516,Voice='Npc/vox_emo_shimite_suspect_01',Text='……从未发现他还能跑得这么快，看来这次是真的受到了惊吓，我们快去告诉扎纳特吧。',Speaker=815051,}",
  [505517] = "{id=505517,Voice='Npc/vox_emo_male_young_agree_01',Text='主神官总是这样，遇到危险跑得比谁都快，你们不用在意。',Speaker=815120,}",
  [505518] = "{id=505518,Text='唉，只可惜那些侍卫……',Speaker=815120,Option='{唉……,0},{为他们默哀,0}',}",
  [505519] = "{id=505519,Voice='Npc/vox_emo_male_young_sad_01',Text='不管怎么说，好在避难所的问题解决了！',Speaker=815120,}",
  [505520] = "{id=505520,Voice='Npc_Quest/505520',Text='（小声）这下斯特汀总能空出时间了吧，再去找他聊聊。',Speaker=815051,}",
  [505521] = "{id=505521,Text='烦死了……',Speaker=815064,}",
  [505522] = "{id=505522,Voice='Npc_Quest/505522',Text='哦，你们来了？',Speaker=815064,Option='避难所建好了！',}",
  [505523] = "{id=505523,Voice='Npc_Quest/505523',Text='收起你邀功的表情吧，我已经知道了情况。',Speaker=815064,}",
  [505524] = "{id=505524,Voice='Npc_Quest/505524',Text='你做的比我想象中要快一点，但也就那样，算是平民中还可以的。',Speaker=815064,Option='{……,0},{哦,0}',}",
  [505525] = "{id=505525,Voice='Npc_Quest/505525',Text='现在，能听一下我们的请求了吗？',Speaker=815051,}",
  [505526] = "{id=505526,Voice='Npc_Quest/505526',Text='当然，尊敬的施密特王，请问您有什么吩咐？',Speaker=815064,}",
  [505527] = "{id=505527,Voice='Npc_Quest/505527',Text='……其实我们来找你，是希望你前往王座之间，打开那扇门。',Speaker=815051,}",
  [505528] = "{id=505528,Voice='Npc_Quest/505528',Text='那扇门？哦，我想起来了，是那个只为了保护那个什么遗迹的离谱机关？',Speaker=815064,}",
  [505529] = "{id=505529,Voice='Npc_Quest/505529',Text='难怪今天陛下和总司令官看起来脾气格外好，原来是有求于我。',Speaker=815064,}",
  [505530] = "{id=505530,Voice='Npc_Quest/505530',Text='斯特汀，嘲讽毫无意义，这一切都是为了克雷斯特汉姆！贵族和平民都会在战争中死去，没有任何区别。',Speaker=815052,}",
  [505531] = "{id=505531,Text='暴君！你是暴君！！！',Speaker=815135,}",
  [505532] = "{id=505532,Voice='Npc_Quest/505532',Text='施密特！阅兵大典上正是你招来了黑暗军团，你现在还不知悔改吗？！',Speaker=815135,}",
  [505533] = "{id=505533,Voice='Npc_Quest/505533',Text='我能感受到那扇门后藏着可怕的禁忌的力量，你和巴特蒙德到底在偷偷摸摸研究什么？',Speaker=815135,}",
  [505534] = "{id=505534,Voice='Npc_Quest/505534',Text='不，我们所做的都是为了克雷斯特汉姆，是为了保护大家！',Speaker=815051,}",
  [505535] = "{id=505535,Voice='Npc_Quest/505535',Text='哼，保护我们？国王陛下，你真的要保护我们，就应该去和黑暗军团战斗，而不是打开什么大门！',Speaker=815135,}",
  [505536] = "{id=505536,Voice='Npc_Quest/505536',Text='你现在是想害死所有人，害死我们这些可怜的民众吗？',Speaker=815135,}",
  [505537] = "{id=505537,Voice='Npc_Quest/505537',Text='对！这一切都是因为国王，是国王的错！',Speaker=815135,}",
  [505538] = "{id=505538,Voice='Npc_Quest/505538',Text='施密特，你这个暴君！',Speaker=815100,}",
  [505539] = "{id=505539,Text='……',Speaker=815051,}",
  [505540] = "{id=505540,Voice='Npc_Quest/505540',Text='神明在上，陛下，听听大家的声音吧，那扇门不可打开啊。',Speaker=815129,}",
  [505541] = "{id=505541,Text='……',Speaker=815051,}",
  [505542] = "{id=505542,Voice='Npc_Quest/505542',Text='那你们打算如何？',Speaker=815052,}",
  [505543] = "{id=505543,Voice='Npc_Quest/505543',Text='只要陛下能跪在神像前真心忏悔，一定可以获得主神的原谅。',Speaker=815129,}",
  [505544] = "{id=505544,Voice='Npc_Quest/505544',Text='……你们是打算逼迫君主吗？',Speaker=815052,}",
  [505545] = "{id=505545,Voice='Npc_Quest/505545',Text='绝无此意啊，一切都是为了民众，这也是您所期望的不是吗？',Speaker=815129,}",
  [505546] = "{id=505546,Voice='Npc_Quest/505546',Text='我……',Speaker=815051,}",
  [505547] = "{id=505547,Voice='Npc_Quest/505547',Text='你背后的人是谁，是那些反叛的贵族？亦或者是拉赫的那位教皇？',Speaker=815052,}",
  [505548] = "{id=505548,Voice='Npc_Quest/505548',Text='你们在说什么！我……我……',Speaker=815129,}",
  [505549] = "{id=505549,Voice='Npc_Quest/505549',Text='我说怎么闻到一股臭味，这个家伙原来也感染了……',Speaker=815064,}",
  [505550] = "{id=505550,Voice='Npc_Quest/505550',Text='阻止他！不要让他伤害到其他人。',Speaker=815051,}",
  [505551] = "{id=505551,Voice='Npc_Quest/505551',Text='暴君想要杀害主神官！',Speaker=815135,}",
  [505552] = "{id=505552,Voice='Npc_Quest/505552',Text='遗迹中到底有什么！',Speaker=815131,}",
  [505553] = "{id=505553,Voice='Npc_Quest/505553',Text='神一定会责罚我们……',Speaker=815099,}",
  [505554] = "{id=505554,Voice='Npc_Quest/505554',Text='国王陛下处决了神官大人！他在杀人灭口！暴君！你会给这个国家带来毁灭！',Speaker=815135,}",
  [505555] = "{id=505555,Voice='Npc_Quest/505555',Text='暴君！暴君！',Speaker=815099,}",
  [505556] = "{id=505556,Voice='Npc_Quest/505556',Text='吵死了！一群叫嚷的垃圾！',Speaker=815064,}",
  [505557] = "{id=505557,Voice='Npc_Quest/505557',Text='我倒真希望施密特是个暴君，这样你们就能安安静静闭上你们的嘴，少说这些让人作呕的话。',Speaker=815064,}",
  [505558] = "{id=505558,Voice='Npc_Quest/505558',Text='你们看不到有多少骑士为你们而死吗？看不到国王都在东奔西跑为你们寻找避难所吗？',Speaker=815064,}",
  [505559] = "{id=505559,Voice='Npc_Quest/505559',Text='你们这些可悲的，只会喊救命的蠢货！不过三两句话，就能让你们叫嚷不休，像一群无头苍蝇。',Speaker=815064,}",
  [505560] = "{id=505560,Text='好、好可怕。',Speaker=815099,}",
  [505561] = "{id=505561,Text='嘘……',Speaker=815099,}",
  [505562] = "{id=505562,Voice='Npc_Quest/505562',Text='陛下，你太过仁慈了。民众愚昧又无知，弱小又没脑子，他们就该乖乖听从命令，等待骑士的保护。',Speaker=815064,}",
  [505563] = "{id=505563,Voice='Npc_Quest/505563',Text='在这点上我们倒是难得一致，他们中的大部分缺乏良好的教育，很难学会独立思考，若无君王领导，只会走入末路。',Speaker=815052,}",
  [505564] = "{id=505564,Voice='Npc_Quest/505564',Text='你们……',Speaker=815051,}",
  [505565] = "{id=505565,Voice='Npc_Quest/505565',Text='不好了！',Speaker=815160,}",
  [505566] = "{id=505566,Voice='Npc_Quest/505566',Text='报！原本四散的黑暗军团突然集结在王都正门，似乎打算攻城。',Speaker=815160,}",
  [505567] = "{id=505567,Voice='Npc_Quest/505567',Text='是希默梅斯做的吗？看来遗迹那边出现了变故。',Speaker=815052,}",
  [505568] = "{id=505568,Voice='Npc_Quest/505568',Text='无论如何，我们必须集结一切力量抵御黑暗军团，决不能让他们攻破城门！',Speaker=815051,}",
  [505569] = "{id=505569,Voice='Npc_Quest/505569',Text='斯特汀，你立刻率领骑兵团防御城墙，准备迎战。',Speaker=815051,}",
  [505570] = "{id=505570,Voice='Npc_Quest/505570',Text='我可不是费沙那个酒鬼，连守护祭坛都能失守。',Speaker=815064,}",
  [505571] = "{id=505571,Voice='Npc_Quest/505571',Text='黑暗军团来势汹汹，我们也去城门口一起防御。',Speaker=815051,}",
  [505572] = "{id=505572,Voice='Npc_Quest/505572',Text='追随王的意志。',Speaker=815052,}",
  [505573] = "{id=505573,Voice='Npc_Quest/505573',Text='你们问我情况如何？我的建议是直接打开城门，这样大家死的可以痛快点。',Speaker=815064,Option='{别开玩笑……,0},{绝不可能！,0}',}",
  [505574] = "{id=505574,Voice='Npc_Quest/505574',Text='火枪团那边不知道在干什么，弹药严重不足，民众还在到处乱跑，在这种情况下能做的也有限吧。',Speaker=815064,}",
  [505575] = "{id=505575,Voice='Npc_Quest/505575',Text='我会去协调调配火枪团骑士，你继续负责指挥这边的防守。',Speaker=815052,}",
  [505576] = "{id=505576,Voice='Npc_Quest/505576',Text='有白星的参与，我们的末日能来的晚一点。就这样做吧！',Speaker=815064,Option='{我能做什么？,0},{还有我呢,0}',}",
  [505577] = "{id=505577,Voice='Npc_Quest/505577',Text='你？倒是挺积极嘛，的确，现在不能浪费一切有生力量，你去找我手下的两个队长吧。',Speaker=815064,}",
  [505578] = "{id=505578,Voice='Npc_Quest/505578',Text='相信罗拉穆尔和扎纳特能给你找点活干。',Speaker=815064,Option='{知道了,0},{没问题,0}',}",
  [505579] = "{id=505579,Voice='Npc_Quest/505579',Text='那就拜托了，我也留在这里，和大家一起部署城墙，迎战黑暗军团。',Speaker=815051,}",
  [505580] = "{id=505580,Voice='Npc/vox_emo_male_deep_suspect_01',Text='你知道守城最重要的是什么吗？',Speaker=815118,Option='{坚固的城门？,0},{英勇的守卫？,0},{出色的战术？,0}',}",
  [505581] = "{id=505581,Voice='Npc/vox_emo_male_deep_agree_02',Text='是火力！火力！轰炸一切的火力！',Speaker=815118,}",
  [505582] = "{id=505582,Voice='Npc/vox_emo_male_deep_sad_01',Text='可现在弹药库前都是感染的宫廷侍卫，唉……',Speaker=815118,Option='交给我吧！',}",
  [505583] = "{id=505583,Text='你真是一位出色而勇敢的骑士！那就等你的好消息了。',Speaker=815118,}",
  [505584] = "{id=505584,Text='这下弹药库周围安全了。',}",
  [505585] = "{id=505585,Voice='Npc/vox_emo_male_deep_joy_01',Text='太好了！嘿嘿，不过克雷斯特汉姆有句老话——“既然你要做南瓜饼，就请放到客人的盘子里”。',Speaker=815118,Option='{什么意思？,0},{得寸进尺啊……,0}',}",
  [505586] = "{id=505586,Voice='Npc/vox_emo_male_deep_agree_03',Text='这句话的意思是既然要做一件事，就请做到最后一步，所以请你帮忙将弹药搬运来吧。',Speaker=815118,Option='{哦,0},{知道了,0}',}",
  [505587] = "{id=505587,Text='差不多了，可以去找罗拉穆尔了。',}",
  [505588] = "{id=505588,Voice='Npc/vox_emo_male_deep_admire_01',Text='这么快？真是出乎意料啊，嘿嘿，其实克雷斯特汉姆还有一句老话……',Speaker=815118,Option='{够了！,0},{有话直说！,0}',}",
  [505589] = "{id=505589,Voice='Npc/vox_emo_male_deep_deny_03',Text='其实我们还需要骑士去帮忙整理炮台，人手不足嘛，理解一下。',Speaker=815118,Option='{……,0},{知道了,0}',}",
  [505590] = "{id=505590,Text='伤还没好，刚摆好一个炮台就不行了，剩下的麻烦你了，记住炮口要朝外，不然会出事的。',Speaker=815144,}",
  [505591] = "{id=505591,Text='这下好了，谢谢你！罗拉穆尔队长正等着你呢。',Speaker=815144,}",
  [505592] = "{id=505592,Voice='Npc/vox_emo_male_deep_admire_01',Text='你真是我见过最有行动力的骑士了，就像克雷斯特汉姆那句老话——',Speaker=815118,Option='{又来了,0},{这次又干嘛？,0}',}",
  [505593] = "{id=505593,Voice='Npc/vox_emo_male_deep_admire_03',Text='“伟大的骑士如星辰，为后来者照亮前路”，这就是我想说的，你的优秀就像星星那样璀璨。',Speaker=815118,Option='{嘿嘿,0},{还好啦~,0}',}",
  [505594] = "{id=505594,Voice='Npc/vox_emo_male_deep_joy_02',Text='哈哈哈哈，你已经给了我们很多帮助，去吧，去照亮更多人。',Speaker=815118,}",
  [505595] = "{id=505595,Voice='Npc/vox_emo_male_deep_suspect_01',Text='你是斯特汀·力哈尔特团长派来支援的骑士吗？',Speaker=815120,Option='{嗯……,0},{是的,0}',}",
  [505596] = "{id=505596,Text='我的职责是照管好城内的民众，可刚才接到消息，教堂那边避难的民众中，有几个孩子突然失踪了。',Speaker=815120,Option='失踪？',}",
  [505597] = "{id=505597,Voice='Npc/vox_emo_male_deep_angry_01',Text='是的，根据调查，他们在失踪前似乎商量要玩什么捉迷藏的游戏，唉，这都什么时候了。',Speaker=815120,}",
  [505598] = "{id=505598,Voice='Npc/vox_emo_male_deep_angry_01',Text='我找了一圈也没找到，能麻烦你再去找找吗？',Speaker=815120,Option='{我试试看,0},{没问题！,0}',}",
  [505599] = "{id=505599,Voice='Npc/vox_emo_male_puppy_agree_01',Text='哇，其实我正在找大家呢，这次我抽到了捉人的角色。',Speaker=815145,}",
  [505600] = "{id=505600,Voice='Npc/vox_emo_male_puppy_agree_03',Text='呃……你是说我们让大家担心了，对不起嘛，我现在就和你回去。',Speaker=815145,}",
  [505601] = "{id=505601,Voice='Npc/vox_emo_male_puppy_suspect_01',Text='哎呀，被发现了，我就知道菲利克斯找人最厉害，他是我们中间最聪明的！',Speaker=815146,}",
  [505602] = "{id=505602,Voice='Npc/vox_emo_male_puppy_angry_01',Text='什么？游戏结束啦，可我还没玩够呢，哼！大人真讨厌。',Speaker=815147,}",
  [505603] = "{id=505603,Voice='Npc/vox_emo_male_puppy_deny_01',Text='啊哦，被捉到了，要再来一局吗？不可以啊，那就算了。',Speaker=815148,}",
  [505604] = "{id=505604,Voice='Npc/vox_emo_female_puppy_joy_01',Text='哇，好厉害呀，被找到了，那你能帮我一起找妈妈吗？',Speaker=815149,}",
  [505605] = "{id=505605,Text='应该没有其他孩子了，去向扎纳特汇报吧。',}",
  [505606] = "{id=505606,Voice='Npc/vox_emo_male_deep_agree_01',Text='谢天谢地，总算回来了，我数数看，1、2……5，等等，怎么只有五个？',Speaker=815120,Option='{只找到五个,0},{还有其他孩子吗？,0}',}",
  [505607] = "{id=505607,Voice='Npc/vox_emo_male_deep_suspect_03',Text='可明明[c][ffff00]失踪了六个孩子[-][/c]啊。',Speaker=815120,}",
  [505608] = "{id=505608,Voice='Npc/vox_emo_male_puppy_deny_02',Text='是有个[c][ffff00]妹妹[-][/c]原本和我们一起，但她不想玩捉迷藏。',Speaker=815145,}",
  [505609] = "{id=505609,Voice='Npc/vox_emo_female_puppy_agree_02',Text='是的呢，她说很无聊，然后就回去啦。',Speaker=815149,}",
  [505610] = "{id=505610,Voice='Npc/vox_emo_male_puppy_deny_03',Text='她是不想和我们玩，哼，我也不喜欢和她玩。',Speaker=815147,}",
  [505611] = "{id=505611,Voice='Npc/vox_emo_male_deep_agree_03',Text='好了，安静点！现在是特殊时间，你们不可以随便乱跑，你们的家人会担心的！我们也会担心！',Speaker=815120,}",
  [505612] = "{id=505612,Text='对不起……',}",
  [505613] = "{id=505613,Voice='Npc/vox_emo_male_deep_sad_01',Text='唉，这些孩子啊……现在城门口很危险，我会派骑士将他们送回教堂。',Speaker=815120,}",
  [505614] = "{id=505614,Text='再说一遍！不要！再乱跑了！！！听到没有！！！',Speaker=815120,}",
  [505615] = "{id=505615,Text='对不起！！！',}",
  [505616] = "{id=505616,Voice='Npc_Quest/505616',Text='看来战前准备都做好了？',Speaker=815052,Option='将情况告知海因里希',}",
  [505617] = "{id=505617,Voice='Npc_Quest/505617',Text='安置民众，准备火器，你做的很好，也到了该动手的时候。',Speaker=815052,}",
  [505618] = "{id=505618,Voice='Npc_Quest/505618',Text='动手？我们先出击？',Speaker=815064,}",
  [505619] = "{id=505619,Voice='Npc_Quest/505619',Text='海因里希说的没错，进攻是最好的防守，我们不能被动挨打，也该由我们来吹响战斗的号角。',Speaker=815051,}",
  [505620] = "{id=505620,Voice='Npc_Quest/505620',Text='既然国王陛下都这么说了，那就来吧，我的骑兵团可不怕他们。',Speaker=815064,}",
  [505621] = "{id=505621,Voice='Npc_Quest/505621',Text='将荣誉凝于剑锋，无畏前行——冲锋！',Speaker=815051,}",
  [505622] = "{id=505622,Voice='Npc_Quest/505622',Text='去吧，我英勇的朋友！用炮台给他们点颜色看看！',Speaker=815051,}",
  [505623] = "{id=505623,Text='一鼓作气消灭它们！',Speaker=815051,}",
  [505624] = "{id=505624,Text='仔细瞄准，别着急！',Speaker=815051,}",
  [505625] = "{id=505625,Voice='Npc_Quest/505625',Text='胜利了，但我总有一种不好的预感，你发现了吗？暗答莱尸并未出现。',Speaker=815052,Option='{对喔……,0},{为什么呢？,0}',}",
  [505626] = "{id=505626,Voice='Npc_Quest/505626',Text='总司令官大人，城里突然出现了黑暗军团！民众陷入恐慌中，正在四散奔逃，后方骑士们正请求支援。',Speaker=815152,}",
  [505627] = "{id=505627,Voice='Npc_Quest/505627',Text='它们是怎么潜入王城的，这怎么可能……',Speaker=815051,}",
  [505628] = "{id=505628,Voice='Npc_Quest/505628',Text='别忘了之前也出现过诅咒光球，恐怕内部已经有了蛀虫。',Speaker=815052,Option='{太可怕了！,0},{蛀虫？,0}',}",
  [505629] = "{id=505629,Voice='Npc_Quest/505629',Text='先处理眼前的事吧，斯特汀，你立刻带领骑士团前去支援！',Speaker=815052,}",
  [505630] = "{id=505630,Voice='Npc_Quest/505630',Text='当然！喂，菜鸟骑士，你也和我一起吧，我看你还挺有用的。',Speaker=815064,Option='{……,0},{为了民众……,0}',}",
  [505631] = "{id=505631,Voice='Npc_Quest/505631',Text='我也与你们一起！',Speaker=815051,}",
  [505632] = "{id=505632,Voice='Npc_Quest/505632',Text='不，施密特，作为国王，你必须留在正面战场，让所有人看到你的身影！',Speaker=815052,}",
  [505633] = "{id=505633,Voice='Npc_Quest/505633',Text='这不仅仅为了士气，也为了减少城里骑士的压力，你知道你才是黑暗军团的目标。',Speaker=815052,}",
  [505634] = "{id=505634,Voice='Npc_Quest/505634',Text='……你说得对！我会在这里等你们的，一定要平安归来啊。',Speaker=815051,Option='{放心~,0},{不会有事！,0}',}",
  [505635] = "{id=505635,Voice='Npc_Quest/505635',Text='黑暗军团到底是怎么突破防御的？',Speaker=815064,}",
  [505636] = "{id=505636,Voice='Npc_Quest/505636',Text='不会是费沙那个蠢货又喝多了乱来吧……',Speaker=815064,}",
  [505637] = "{id=505637,Voice='Npc_Quest/505637',Text='那个孩子！……哼……用我们的剑教教它们规矩吧！',Speaker=815064,}",
  [505638] = "{id=505638,Text='终于，解脱了',Speaker=815097,}",
  [505639] = "{id=505639,Text='他是……',Speaker=815113,}",
  [505640] = "{id=505640,Text='神啊，原谅我吧。',Speaker=815113,}",
  [505641] = "{id=505641,Voice='Npc_Quest/505641',Text='你感觉怎么样？',Speaker=815064,}",
  [505642] = "{id=505642,Voice='Npc_Quest/505642',Text='我……我的手，好痛啊，我是不是快要死了？救救我……',Speaker=815154,}",
  [505643] = "{id=505643,Voice='Npc_Quest/505643',Text='[c][ffff00]你的手已经被死亡之力浸染了[-][/c]，恐怕很难恢复。',Speaker=815064,}",
  [505644] = "{id=505644,Voice='Npc_Quest/505644',Text='呜呜呜，怎么会这样，我也会变成怪物吗？',Speaker=815154,}",
  [505645] = "{id=505645,Voice='Npc_Quest/505645',Text='……别哭了，眼泪不能解决问题。',Speaker=815064,}",
  [505646] = "{id=505646,Voice='Npc_Quest/505646',Text='该死，到这里我才明白，难怪城里会出现黑暗军团。',Speaker=815064,Option='{为什么？,0},{到底怎么回事？,0}',}",
  [505647] = "{id=505647,Voice='Npc_Quest/505647',Text='难道你没发现通往护城河的闸门被打开了吗？原本驻守在这里的骑士也全部被调离。',Speaker=815064,}",
  [505648] = "{id=505648,Voice='Npc_Quest/505648',Text='你们几个，留在这里关闭闸门，有什么情况随时汇报。',Speaker=815064,}",
  [505649] = "{id=505649,Text='是！',Speaker=815125,}",
  [505650] = "{id=505650,Voice='Npc_Quest/505650',Text='我们走吧，这件事需要告诉海因里希。',Speaker=815126,Option='这个孩子怎么办？',}",
  [505651] = "{id=505651,Voice='Npc_Quest/505651',Text='带着她一起吧，希望她能保住这条手臂……至少保住这条命。',Speaker=815064,}",
  [505652] = "{id=505652,Voice='Npc_Quest/505652',Text='呜呜……不……',Speaker=815154,}",
  [505653] = "{id=505653,Text='太好了！！！',Speaker=815125,}",
  [505654] = "{id=505654,Text='打退他们了……',Speaker=815126,}",
  [505655] = "{id=505655,Text='总算能休息会了。',Speaker=815136,}",
  [505656] = "{id=505656,Voice='Npc_Quest/505656',Text='我们重伤了黑暗军团，它们暂时停止了攻城！',Speaker=815051,}",
  [505657] = "{id=505657,Voice='Npc_Quest/505657',Text='至于你们那边，调查清楚了吗？黑暗军团是怎么出现在城里的？',Speaker=815052,Option='将一切告知',}",
  [505658] = "{id=505658,Voice='Npc_Quest/505658',Text='好在我已经让人关闭了闸门，短时间内那些丑陋的家伙应该不会出现了。',Speaker=815064,}",
  [505659] = "{id=505659,Voice='Npc_Quest/505659',Text='对了，我们还带回来了这个可怜的小东西……来人，带她去治疗。',Speaker=815064,}",
  [505660] = "{id=505660,Voice='Npc_Quest/505660',Text='……施密特的失踪，暗答莱尸的出现，黑暗军团的入侵，这下我们可以确定反叛者是骑士团内部的人了。',Speaker=815052,Option='{为什么？,0},{怎么判断的？,0}',}",
  [505661] = "{id=505661,Voice='Npc_Quest/505661',Text='只有骑士团里的人，才能知道调动的口令，能让驻守的骑士暂时离开。',Speaker=815052,}",
  [505662] = "{id=505662,Voice='Npc_Quest/505662',Text='真是该死，这是叛国！',Speaker=815051,}",
  [505663] = "{id=505663,Voice='Npc_Quest/505663',Text='只是不知道那人究竟是为什么背叛了我们。',Speaker=815052,}",
  [505664] = "{id=505664,Voice='Npc_Quest/505664',Text='王，兹事体大，请容许我前去骑士团驻地进行调查。',Speaker=815052,}",
  [505665] = "{id=505665,Voice='Npc_Quest/505665',Text='好，一切就交给你了，白星。',Speaker=815051,}",
  [505666] = "{id=505666,Voice='Npc_Quest/505666',Text='你和荣誉骑士一起行动吧，至少不用担心遇到诅咒光球。',Speaker=815052,Option='交给我吧！',}",
  [505667] = "{id=505667,Voice='Npc_Quest/505667',Text='放心，我会注意安全的，毕竟我还有重要的使命未能完成。',Speaker=815051,}",
  [505668] = "{id=505668,Voice='Npc_Quest/505668',Text='斯特汀……你还记得我之前说的话吗？',Speaker=815051,}",
  [505669] = "{id=505669,Voice='Npc_Quest/505669',Text='虽然黑暗军团暂时被击退，但它们一定会卷土重来，我必须打开王座之间的大门，彻底消灭它们。',Speaker=815051,}",
  [505670] = "{id=505670,Voice='Npc_Quest/505670',Text='……知道了，我可没忘。',Speaker=815064,Option='所以你的答案是？',}",
  [505671] = "{id=505671,Voice='Npc_Quest/505671',Text='我也没说不答应吧，不过在这之前，我想先去看看那个女孩的情况。',Speaker=815064,}",
  [505672] = "{id=505672,Voice='Npc_Quest/505672',Text='她怎么样了？',Speaker=815064,}",
  [505673] = "{id=505673,Voice='Npc_Quest/505673',Text='情况不容乐观，尤其是她的这条胳膊……我们能做的只有用生命之水为她疗伤，延缓感染的扩散。',Speaker=815157,}",
  [505674] = "{id=505674,Voice='Npc_Quest/505674',Text='我还活着吗？是你们救了我吗？（看向斯特汀）骑士叔叔，谢谢你……你真好。',Speaker=815154,Option='{还活着,0},{不要担心,0}',}",
  [505675] = "{id=505675,Voice='Npc_Quest/505675',Text='你很幸运小姑娘，至少比起那些已经失去了生命的人而言，你叫什么名字？',Speaker=815064,}",
  [505676] = "{id=505676,Voice='Npc_Quest/505676',Text='我一点也不幸运，我想要我的胳膊！还有……我、我叫毕尤。',Speaker=815154,Option='{毕尤！,0},{竟然是你？,0}',}",
  [505677] = "{id=505677,Voice='Npc_Quest/505677',Text='你认识她？',Speaker=815051,}",
  [505678] = "{id=505678,Text='毕尤……毕尤……',}",
  [505679] = "{id=505679,Voice='Npc_Quest/505679',Text='难道，她未来是个很了不起的人？',Speaker=815051,Option='{刚好相反……,0},{她作恶多端,0}',}",
  [505680] = "{id=505680,Voice='Npc_Quest/505680',Text='小声点……我们到旁边说吧。',Speaker=815064,}",
  [505681] = "{id=505681,Voice='Npc_Quest/505681',Text='你刚才说的究竟是怎么回事？她还只是一个孩子，未来能做出什么事？',Speaker=815064,}",
  [505682] = "{id=505682,Text='因她而死的人不计其数，她的手上染满鲜血，她的名字代表死亡的丧钟……',Picture='new_npc_pic_37',BlackMask=1,}",
  [505683] = "{id=505683,Voice='Npc_Quest/505683',Text='真没想到，现在完全看不出来她以后的罪行，她多么依赖斯特汀这位救命恩人啊。',Speaker=815051,}",
  [505684] = "{id=505684,Voice='Npc_Quest/505684',Text='哼，果然我这种人不适合好心，不过施密特你曾经说过……',Speaker=815064,}",
  [505685] = "{id=505685,Voice='Npc_Quest/505685',Text='平民若得到教诲，他们的品格不会输给贵族，或许我们能改变她的未来呢？',Speaker=815064,}",
  [505686] = "{id=505686,Voice='Npc_Quest/505686',Text='这简直不像你说的话，你竟然也会觉得不忍心吗？',Speaker=815051,}",
  [505687] = "{id=505687,Voice='Npc_Quest/505687',Text='哼，我可没有对幼童下手的习惯，至少现在，她还未铸下大错，我们不该以未来的罪行审判现在的人。',Speaker=815064,}",
  [505688] = "{id=505688,Voice='Npc_Quest/505688',Text='有点难办啊……',Speaker=815051,}",
  [505689] = "{id=505689,Voice='Npc_Quest/505689',Text='嘀嘀嘀，我又嗅到了命运的分支，看来你又要做一个重要的决定了啊。',Speaker=815062,Option='未来会发生什么？',}",
  [505690] = "{id=505690,Voice='Npc_Quest/505690',Text='危险应在萌芽前及时掐断，很多无辜的人也能活下来，听起来很不错吧？',Speaker=815062,}",
  [505691] = "{id=505691,Voice='Npc_Quest/505691',Text='但是，现在的她还是个懵懵懂懂的孩子，清白干净得就像一张纸。',Speaker=815062,Option='{我们可以教导她！,0},{想办法改变未来,0}',}",
  [505692] = "{id=505692,Voice='Npc_Quest/505692',Text='不，[c][ffff00]杀戮是毕尤既定的命运[-][/c]，她依旧会成为希默梅斯的爪牙，无数的人将因她而死。',Speaker=815062,}",
  [505693] = "{id=505693,Voice='Npc_Quest/505693',Text='若你选择让毕尤消失，成百上千的人会因此活下去，虽然此刻的毕尤并未犯下任何罪行。',Speaker=815062,}",
  [505694] = "{id=505694,Voice='Npc_Quest/505694',Text='是熄灭罪恶的种子，还是让罪恶在多年后燃烧。',Speaker=815062,}",
  [505695] = "{id=505695,Text='来吧，做出你的选择吧。',Speaker=815062,Option='{熄灭罪恶的种子,4},{放过毕尤,5},{返回,3}',}",
  [505696] = "{id=505696,Voice='Npc_Quest/860420',Text='是吗……？这就是你的选择吗？这个时空，会依循你的意志。',Speaker=815062,}",
  [505697] = "{id=505697,Voice='Npc_Quest/505697',Text='未来的罪行，应当被现在的我们审判吗？',Speaker=815064,}",
  [505698] = "{id=505698,Voice='Npc_Quest/505698',Text='如果海因里希在这里，他应该也会这样做吧……',Speaker=815051,}",
  [505699] = "{id=505699,Voice='Npc_Quest/505699',Text='……如果我不救她，刚才她已经成了一具尸体，或许这只是纠正之前的错误。',Speaker=815064,}",
  [505700] = "{id=505700,Voice='Npc_Quest/505700',Text='……交给我吧。来人！将她带去……处刑……',Speaker=815064,}",
  [505701] = "{id=505701,Voice='Npc_Quest/505701',Text='你、你们要干什么？为什么，我做错了什么？',Speaker=815155,}",
  [505702] = "{id=505702,Voice='Npc_Quest/505702',Text='斯特汀叔叔，救救我！是因为我被感染了吗？不……不要……我可以不要我的胳膊，饶了我吧！',Speaker=815155,}",
  [505703] = "{id=505703,Voice='Npc_Quest/505703',Text='海因里希说过，黑暗会从阴影中滋生，当你放过一只老鼠时，就要做好遇见一窝老鼠的准备。',Speaker=815051,}",
  [505704] = "{id=505704,Voice='Npc_Quest/505704',Text='但是我仍旧希望我们做的是正确的。',Speaker=815051,}",
  [505705] = "{id=505705,Voice='Npc_Quest/505705',Text='陛下，您也曾经说过，不应当以出身评价他人，那我们是否也不该以未发生的罪行去惩罚她。',Speaker=815064,}",
  [505706] = "{id=505706,Voice='Npc_Quest/505706',Text='律法是为正义而生，如何能审判一个无辜的孩子？否则执政官又该以什么作为准绳，虚无缥缈的未来吗？',Speaker=815064,}",
  [505707] = "{id=505707,Voice='Npc_Quest/505707',Text='你是对的，从公平的角度而言，我们当以罪行来量罪，就这样吧，希望她能长成一个好孩子。',Speaker=815051,}",
  [505708] = "{id=505708,Voice='Npc_Quest/505708',Text='叔叔，你们刚才在说什么呀？',Speaker=815155,}",
  [505709] = "{id=505709,Voice='Npc_Quest/505709',Text='没什么，你好好养伤，我救了你，之后也会好好教导你。',Speaker=815064,}",
  [505710] = "{id=505710,Voice='Npc_Quest/505710',Text='叔叔……谢谢你！',Speaker=815155,}",
  [505711] = "{id=505711,Voice='Npc_Quest/505711',Text='你，还记得该怎么称呼我吗？',Speaker=815065,Option='{斯特汀·力哈尔特,0},{不会忘了你的姓氏,0}',}",
  [505712] = "{id=505712,Voice='Npc_Quest/505712',Text='啧，你这个家伙还挺记仇，喂，以后就叫我斯特汀吧。',Speaker=815065,Option='为什么？',}",
  [505713] = "{id=505713,Voice='Npc_Quest/505713',Text='你应该感到荣幸，你可是我的第一个平民朋友。',Speaker=815065,Option='{……,0},{好吧……,0}',}",
  [505714] = "{id=505714,Voice='Npc_Quest/505714',Text='莫大的荣幸啊，朋友，斯特汀可是连我这个君王都不放在眼里的人。',Speaker=815051,}",
  [505715] = "{id=505715,Voice='Npc_Quest/505715',Text='陛下，你闭嘴的时候最值得人尊敬。',Speaker=815065,}",
  [505716] = "{id=505716,Voice='Npc_Quest/505716',Text='走吧，是时候去王座之间了，这一切也该快点结束了。',Speaker=815065,}",
  [505717] = "{id=505717,Voice='Npc_Quest/505717',Text='虽然对施密特这个家伙的很多方面都看不惯……',Speaker=815065,}",
  [505718] = "{id=505718,Voice='Npc_Quest/505718',Text='但他至少是个强者，也勉强算是个合格的君主。',Speaker=815065,}",
  [505719] = "{id=505719,Voice='Npc_Quest/505719',Text='强者就该带着弱者前行，这是规矩，懂吗？',Speaker=815065,}",
  [505720] = "{id=505720,Voice='Npc_Quest/505720',Text='陛下，我们不应当把所有的希望都寄托在“爱丽丝”的身上，那毕竟只是一个传说。',Speaker=815065,}",
  [505721] = "{id=505721,Voice='Npc_Quest/505721',Text='你有什么建议？',Speaker=815051,}",
  [505722] = "{id=505722,Voice='Npc_Quest/505722',Text='进攻才是最好的防守，骑士们不能被动地龟缩在城内，等待黑暗军团的进攻。',Speaker=815065,}",
  [505723] = "{id=505723,Voice='Npc_Quest/505723',Text='至少我们应当先想办法夺回南门区域，恢复瞭望台和驿站的运作，让我们有能力掌握主动权。',Speaker=815065,}",
  [505724] = "{id=505724,Voice='Npc_Quest/505724',Text='其实之前海因里希已做了相应部署，沙利正在为此奔走。',Speaker=815051,Option='{沙利？0},{他是谁？,0}',}",
  [505725] = "{id=505725,Voice='Npc_Quest/505725',Text='哈哈，那个家伙可是[c][ffff00]施密特和海因里希的孩子[-][/c]。',Speaker=815065,Option='{！！！,0},{谁的孩子？,0}',}",
  [505726] = "{id=505726,Voice='Npc_Quest/505726',Text='……请不要说这种让人误会的话！朋友，我们先去找沙利，他应该在凯旋门附近。',Speaker=815051,}",
  [505727] = "{id=505727,Voice='Npc_Quest/505727',Text='啧啧。菜鸟骑士，你可得多多“管束”施密特，不然出了什么事，海因里希会心疼的。',Speaker=815065,}",
  [505728] = "{id=505728,Voice='Npc_Quest/505728',Text='斯特汀——闭嘴吧！',Speaker=815051,}",
  [505729] = "{id=505729,Voice='Npc_Quest/505729',Text='你别听斯特汀瞎说，沙利原本是个孤儿……',Speaker=815051,}",
  [505730] = "{id=505730,Voice='Npc_Quest/505730',Text='我们捡到了年幼的他，他就像我们的弟弟。',Speaker=815051,}",
  [505731] = "{id=505731,Text='嘿~我在这呢！',Speaker=815159,}",
  [505732] = "{id=505732,Voice='Npc_Quest/505732',Text='太好了，施密特哥哥！我就知道你会平安无事！你之前突然消失真是吓死我了。',Speaker=815159,}",
  [505733] = "{id=505733,Voice='Npc_Quest/505733',Text='这位是谁？你新交的朋友吗？我喜欢他的打扮~',Speaker=815159,Option='{你好！,0},{你也不错~,0}',}",
  [505734] = "{id=505734,Voice='Npc_Quest/505734',Text='这位是海因里希亲封的荣誉骑士！也是我的救命恩人！',Speaker=815051,}",
  [505735] = "{id=505735,Voice='Npc_Quest/505735',Text='来来来，我和你仔细说说！还有王座之门和爱丽丝的事。',Speaker=815051,}",
  [505736] = "{id=505736,Text='施密特将一切告知了沙利。',}",
  [505737] = "{id=505737,Voice='Npc_Quest/505737',Text='哇，没想到这里面发生了这么多的事！',Speaker=815159,Option='{这倒不用……,0},{冷静点！,0}',}",
  [505738] = "{id=505738,Voice='Npc_Quest/505738',Text='冒险者，谢谢你为我的家人所做的一切，太感动了……',Speaker=815159,}",
  [505739] = "{id=505739,Voice='Npc_Quest/505739',Text='……快擦擦眼泪吧，你已经长大了，怎么还是个小哭包。',Speaker=815051,}",
  [505740] = "{id=505740,Voice='Npc_Quest/505740',Text='呜呜，对不起，我实在忍不住嘛……',Speaker=815159,}",
  [505741] = "{id=505741,Voice='Npc_Quest/505741',Text='我愿意前往王座之间！不过在这之前，我想先把我的“[c][ffff00]家[-][/c]”夺回来。',Speaker=815159,Option='家？',}",
  [505742] = "{id=505742,Voice='Npc_Quest/505742',Text='沙利说的应该是[c][ffff00]风之瞭望台[-][/c]吧，他从小是被侦查团的骑士们带大的，他在那里长大。',Speaker=815051,}",
  [505743] = "{id=505743,Voice='Npc_Quest/505743',Text='不仅如此，施密特哥哥，我以白骑士侦查团团长的身份向您谏言！',Speaker=815159,}",
  [505744] = "{id=505744,Voice='Npc_Quest/505744',Text='在夺回城郊外的这些区域后，请准许侦查团骑士携带密令向普隆德拉求援。',Speaker=815159,}",
  [505745] = "{id=505745,Voice='Npc_Quest/505745',Text='我知道普隆德拉那边的贵族各有算计，但为了民众，我们应该试一试！',Speaker=815159,}",
  [505746] = "{id=505746,Voice='Npc_Quest/505746',Text='沙利……你长大了，就按你说的做吧。',Speaker=815051,Option='我也会帮忙的',}",
  [505747] = "{id=505747,Voice='Npc_Quest/505747',Text='啊，你们真好呜呜，对不起，我又要哭了……真是让你见笑了……',Speaker=815159,}",
  [505748] = "{id=505748,Voice='Npc_Quest/505748',Text='……等会再哭！沙利团长，接下去要怎么做？',Speaker=815051,}",
  [505749] = "{id=505749,Voice='Npc_Quest/505749',Text='是！那么就麻烦施密特哥哥和这位新朋友带领骑士清理门口感染的卫兵们。',Speaker=815159,}",
  [505750] = "{id=505750,Voice='Npc_Quest/505750',Text='而我将在这里做好一切准备工作，等待凯旋门再次开启。',Speaker=815159,}",
  [505751] = "{id=505751,Voice='Npc_Quest/505751',Text='别受伤了，不然海因里希哥哥不会放过我的！',Speaker=815159,}",
  [505752] = "{id=505752,Voice='Npc_Quest/505752',Text='……我知道，你别大惊小怪。',Speaker=815051,}",
  [505753] = "{id=505753,Voice='Npc_Quest/505753',Text='哇，真厉害~！我这边的准备工作也已经就绪！',Speaker=815159,}",
  [505754] = "{id=505754,Voice='Npc_Quest/505754',Text='在打开城门后，我们需要尽快激活凯旋大桥上的防御魔法装置，以防黑暗军团入侵。',Speaker=815159,}",
  [505755] = "{id=505755,Voice='Npc_Quest/505755',Text='你指的是克雷斯特汉姆建造之初就安装的那个装置？',Speaker=815051,}",
  [505756] = "{id=505756,Voice='Npc_Quest/505756',Text='没错！之前也不知道什么原因，装置突然就停止运作了，不然黑暗军团想攻城可没这么容易。',Speaker=815159,}",
  [505757] = "{id=505757,Voice='Npc_Quest/505757',Text='我明白了。所有骑士听令！打开凯旋门，击退残留的黑暗军团！',Speaker=815051,}",
  [505758] = "{id=505758,Voice='Npc_Quest/505758',Text='是！谨遵陛下的命令！侦查团骑士——开城门！',Speaker=815159,}",
  [505759] = "{id=505759,Voice='Npc_Quest/505759',Text='沙利，去看看那些装置。',Speaker=815051,}",
  [505760] = "{id=505760,Voice='Npc_Quest/505760',Text='遵命！',Speaker=815159,}",
  [505761] = "{id=505761,Text='奇怪……',Speaker=815159,}",
  [505762] = "{id=505762,Voice='Npc_Quest/505762',Text='我原本以为这些装置是被黑暗军团破坏了，可检查后才发现，它们竟然是被人为关闭的！',Speaker=815159,}",
  [505763] = "{id=505763,Voice='Npc_Quest/505763',Text='……又是这样，我知道了。',Speaker=815051,}",
  [505764] = "{id=505764,Voice='Npc_Quest/505764',Text='不过幸运的是，因为没有被破坏，所以这些装置都能重新启动！',Speaker=815159,Option='{太好了,0},{交给我吧,0}',}",
  [505765] = "{id=505765,Voice='Npc_Quest/505765',Text='天呐，你可真是乐于助人的热心肠！其实很简单，只需要一个小小的仪式，准备好了吗？',Speaker=815159,Option='{准备好了,7},{再等等,8}',}",
  [505766] = "{id=505766,Voice='Npc_Quest/505766',Text='我们需要尽快开启防御装置！',Speaker=815159,}",
  [505767] = "{id=505767,Voice='Npc_Quest/505767',Text='来，跟我学，很简单的！',Speaker=815159,}",
  [505768] = "{id=505768,Voice='Npc_Quest/505768',Text='哇，我可真羡慕你的脑袋，当初我可是学了好久呢~',Speaker=815159,Option='{嘿嘿,0},{还行吧,0}',}",
  [505769] = "{id=505769,Voice='Npc_Quest/505769',Text='那么另外三个装置的开启就交给你啦！',Speaker=815159,}",
  [505770] = "{id=505770,Voice='Npc_Quest/505770',Text='还有我，我也一起去。',Speaker=815051,}",
  [505771] = "{id=505771,Voice='Npc_Quest/505771',Text='防御魔法装置的关闭不是偶然，我需要去寻找线索。',Speaker=815051,}",
  [505772] = "{id=505772,Voice='Npc_Quest/505772',Text='一切要小心啊，荣誉骑士，拜托你帮我照顾施密特哥哥！',Speaker=815159,Option='{我知道,0},{我会的,0}',}",
  [505773] = "{id=505773,Text='这又是什么……？有点恶心……',Speaker=815051,}",
  [505774] = "{id=505774,Text='施密特王……',Speaker=815173,}",
  [505775] = "{id=505775,Voice='Npc/vox_emo_shimite_deny_01',Text='这些东西到底是什么？算了……先开启装置吧！',Speaker=815051,}",
  [505776] = "{id=505776,Voice='Npc/vox_emo_shimite_deny_02',Text='别着急，再试试。',Speaker=815051,}",
  [505777] = "{id=505777,Text='这到底……什么时候才能休息，我……我可能不行了。',Speaker=815170,}",
  [505778] = "{id=505778,Text='为了我们的孩子，我们必须坚持，国王陛下不是说了吗？一切都是为了未来！',Speaker=815171,}",
  [505779] = "{id=505779,Text='放屁！怎么能相信他的话！他根本不会把我们当成活生生的人！',Speaker=815172,}",
  [505780] = "{id=505780,Text='嘘……不要这么说，会被听到的。',Speaker=815171,}",
  [505781] = "{id=505781,Text='那又怎么样，我说错了吗？王宫里的一条狗恐怕都比我们吃的好睡的好！',Speaker=815172,}",
  [505782] = "{id=505782,Text='狗屁的迁都，什么为了后代，根本是为了满足国王的集权吧！',Speaker=815172,}",
  [505783] = "{id=505783,Text='呜……可我们有什么选择？啊……肯恩，肯恩你怎么了？',Speaker=815171,}",
  [505784] = "{id=505784,Text='肯恩死了，又一个……',Speaker=815171,}",
  [505785] = "{id=505785,Text='哼，我们都会死的，就像畜生般被扔到山丘，变成一滩烂泥！',Speaker=815172,}",
  [505786] = "{id=505786,Text='神啊，让那个混蛋国王得到报应吧！',Speaker=815172,}",
  [505787] = "{id=505787,Text='……',Speaker=815051,}",
  [505788] = "{id=505788,Text='这些东西，到底为什么会出现在这里？',Speaker=815051,}",
  [505789] = "{id=505789,Text='去死……去死！',Speaker=815173,}",
  [505790] = "{id=505790,Voice='Npc/vox_emo_shimite_deny_02',Text='别着急，再试试。',Speaker=815051,}",
  [505791] = "{id=505791,Text='求求您了，大人，我的孩子还饿着肚子……',Speaker=815174,}",
  [505792] = "{id=505792,Text='我们已经等了两个月了，真的没有办法才来找您。',Speaker=815175,}",
  [505793] = "{id=505793,Text='你们这两个贱民竟然还想要钱？能为国王陛下工作是你们的荣幸！',Speaker=815176,}",
  [505794] = "{id=505794,Text='可我们总要活下去啊……陛下一开始的公告里允诺了薪酬。',Speaker=815174,}",
  [505795] = "{id=505795,Text='那你去找国王陛下要啊！我告诉你们，现在回去乖乖工作也就罢了，不然……我要你们的命！',Speaker=815176,}",
  [505796] = "{id=505796,Text='大人……大人，求您等等，我……',Speaker=815175,}",
  [505797] = "{id=505797,Text='这就是下场，你看到了？',Speaker=815176,}",
  [505798] = "{id=505798,Text='……',Speaker=815051,}",
  [505799] = "{id=505799,Text='这些……到底是什么？',Speaker=815051,}",
  [505800] = "{id=505800,Text='可恨！！！',Speaker=815177,}",
  [505801] = "{id=505801,Voice='Npc/vox_emo_shimite_deny_02',Text='别着急，再试试。',Speaker=815051,}",
  [505802] = "{id=505802,Text='这些贱民，简直就像虫子一样脆弱，这么容易就死了。',Speaker=815178,}",
  [505803] = "{id=505803,Text='不过也多亏了他们，国王拨给他们的酬金都进了我们口袋。',Speaker=815178,}",
  [505804] = "{id=505804,Text='哼，过会让底下的人把他们扔去坟堆里，别脏了我们的地方。',Speaker=815179,}",
  [505805] = "{id=505805,Text='只是这些事还得做得隐蔽点，不然被施密特知道……恐怕会找我们的麻烦。',Speaker=815179,}",
  [505806] = "{id=505806,Text='哼，他正因为迁都和推行新政策的事和那几大贵族家族内斗呢，哪里能管得了这些？',Speaker=815178,}",
  [505807] = "{id=505807,Text='一个空有武力却缺乏政治素养的国王，竟还妄图提高平民的地位，可平民会领情吗？',Speaker=815179,}",
  [505808] = "{id=505808,Text='那些愚蠢的家伙，恐怕恨死施密特了，哈哈哈哈。',Speaker=815178,}",
  [505809] = "{id=505809,Text='哈哈哈哈，没脑子的国王，果然统领着没脑子的民众！',Speaker=815179,}",
  [505810] = "{id=505810,Text='哈哈哈哈！',Speaker=815178,}",
  [505811] = "{id=505811,Text='哈哈哈！',Speaker=815179,}",
  [505812] = "{id=505812,Text='……',Speaker=815051,}",
  [505813] = "{id=505813,Text='毁掉！统统毁掉！',Speaker=815187,}",
  [505814] = "{id=505814,Voice='Npc_Quest/505814',Text='不好……必须阻止它！',Speaker=815051,}",
  [505815] = "{id=505815,Voice='Npc_Quest/505815',Text='冒险者……你怎么了？',Speaker=815051,}",
  [505816] = "{id=505816,Voice='Npc_Quest/505816',Text='回来，别追了！',Speaker=815051,}",
  [505817] = "{id=505817,Voice='Npc_Quest/505817',Text='让它……让那个东西走吧，现在最重要的是从黑暗军团手中夺回风之瞭望台。',Speaker=815051,Option='{刚才……,0},{那个是？,0}',}",
  [505818] = "{id=505818,Voice='Npc_Quest/505818',Text='过会再聊吧。趁现在推进战线，收复失地！',Speaker=815051,}",
  [505819] = "{id=505819,Voice='Npc_Quest/505819',Text='施密特哥哥说的没错，这样我们才能更好掌握都城郊外的情报，应对黑暗军团。',Speaker=815159,}",
  [505820] = "{id=505820,Voice='Npc_Quest/505820',Text='骑士们！追随陛下的脚步，冲啊！',Speaker=815159,}",
  [505821] = "{id=505821,Voice='Npc_Quest/505821',Text='没想到这里变成了这样……',Speaker=815051,}",
  [505822] = "{id=505822,Voice='Npc_Quest/505822',Text='建设需要很久，但破坏只需要一瞬间，或许王国也是如此。',Speaker=815051,Option='唉……',}",
  [505823] = "{id=505823,Voice='Npc_Quest/505823',Text='朋友，你似乎一直很沉默，你有什么想问我的吗？',Speaker=815051,Option='{堕落之根,0},{那些画面,0}',}",
  [505824] = "{id=505824,Voice='Npc_Quest/505824',Text='这……其实……施密特哥哥也不想的。',Speaker=815159,}",
  [505825] = "{id=505825,Voice='Npc_Quest/505825',Text='沙利，还是我来说吧……这一切都是因为我执意要迁都！',Speaker=815051,}",
  [505826] = "{id=505826,Voice='Npc_Quest/505826',Text='就像那些贵族和民众说的那样，克雷斯特汉姆是建立在血肉和牺牲上的，这一切都是我的错。',Speaker=815051,}"
}
