  [655529] = "{id=655529,Text='（维维塔看起来不太对劲！）',}",
  [655530] = "{id=655530,Text='……[c][ffff00][PlayerName][-][/c]……[c][ffff00][PlayerName][-][/c]……[c][ffff00][PlayerName][-][/c]……',Speaker=812024,Option='你怎么了',}",
