Table_Dialog_Quest_11 = {
  [145829] = "{id=145829,Text='年轻的冒险者~你确定接取我的委托吗？\n[c][ffff00]【该任务在同账号下只能完成一次，请确认账号角色后谨慎接取！】[-][/c]',Speaker=890143,Option='{确认,0},{我再想想,2}',}",
  [146424] = "{id=146424,Text='咦，我看看是谁来了……',Speaker=890143,}",
  [146425] = "{id=146425,Text='哎呀！这不是[c][ffff00][PlayerName][-][/c]吗？',Speaker=890143,}",
  [146426] = "{id=146426,Text='你来得正好，我们马上要举行庆典，有没有空参加呀~',Speaker=890143,Option='什么庆典',}",
  [146427] = "{id=146427,Text='当然是哥布灵王国最盛大最隆重最有意思的春日庆典啦！！',Speaker=890143,}",
  [146428] = "{id=146428,Text='这是一年才有一次的活动哦！有兴趣去玩吗？',Speaker=890143,Option='想去',}",
  [146429] = "{id=146429,Text='呀吼~我就知道你不会错过的~',Speaker=890143,}",
  [146430] = "{id=146430,Text='这样吧，你去吉芬找找向导斐基。他会给你游玩的建议！',Speaker=890143,Emoji=2,}",
  [146431] = "{id=146431,Text='阿——阿嚏——！',Speaker=890144,}",
  [146432] = "{id=146432,Text='咳咳，抱歉最近有点感冒。请问找我有事吗？',Speaker=890144,Option='说明来意',}",
  [146433] = "{id=146433,Text='原来如此，是参加春日庆典的冒险者呀！欢迎欢迎！',Speaker=890144,}",
  [146434] = "{id=146434,Text='我先跟你说一下规则吧~',Speaker=890144,}",
  [146435] = "{id=146435,Text='我们今年的春日主题是——寻宝的冒险！',Speaker=890144,}",
  [146436] = "{id=146436,Text='参加庆典的客人们需要两两一组，根据提示寻找藏匿在哥布灵森林里的宝贝~',Speaker=890144,}",
  [146437] = "{id=146437,Text='虽然有提示，但是寻宝的过程还是会有点难度的~',Speaker=890144,}",
  [146438] = "{id=146438,Text='最后找到宝贝的那一组，会获得神秘的超级大奖哦！',Speaker=890144,}",
  [146439] = "{id=146439,Text='哦呀，感觉怎么样~',Speaker=890144,Option='挺好',}",
  [146440] = "{id=146440,Text='不过……嗯……现在有一个小问题……',Speaker=890144,}",
  [146441] = "{id=146441,Text='现在参加的客人已经都组好队啦……',Speaker=890144,}",
  [146442] = "{id=146442,Text='你需要找一个同伴，两个人一起跟我报名才行哦！',Speaker=890144,Option='好吧',}",
  [146443] = "{id=146443,Text='那你先去哥布灵森林找同伴吧~遇到困难的话可以回来找我！',Speaker=890144,Emoji=20,}",
  [146444] = "{id=146444,Text='加油哦……阿、阿嚏！',Speaker=890144,}",
  [146445] = "{id=146445,Text='前面的草丛里好像有什么东西……',}",
  [146447] = "{id=146447,Text='完蛋了完蛋了完蛋了！！',Speaker=890145,Emoji=1,}",
  [146448] = "{id=146448,Text='哇啊啊啊啊吓我一跳！',Speaker=890145,}",
  [146449] = "{id=146449,Text='你谁啊你？干嘛找我，我忙着呢！',Speaker=890145,Option='怎么了',}",
  [146450] = "{id=146450,Text='我的笔记本丢了！！',Speaker=890145,}",
  [146451] = "{id=146451,Text='你不知道，那是我的魔法笔记！那是为春日庆典准备的！！',Speaker=890145,}",
  [146452] = "{id=146452,Text='结果……现在丢了……得不到庆典第一，我怎么回去见爸妈啊！！',Speaker=890145,Option='嗯……',}",
  [146453] = "{id=146453,Text='你怎么一脸欲言又止的表情？',Speaker=890145,}",
  [146454] = "{id=146454,Text='你是不是知道什么线索？？',Speaker=890145,}",
  [146455] = "{id=146455,Text='喂喂，别用这种眼神看着我，有线索赶紧告诉我啊！！',Speaker=890145,}",
  [146456] = "{id=146456,Text='我的笔记本——！',Speaker=890145,Emoji=1,}",
  [146457] = "{id=146457,Text='我，我的笔记本！',Speaker=890145,}",
  [146458] = "{id=146458,Text='我以为我这辈子都找不回来了……',Speaker=890145,}",
  [146459] = "{id=146459,Text='呜呜呜呜谢谢！！刚刚居然凶你，是我不好！！',Speaker=890145,Option='没关系',}",
  [146460] = "{id=146460,Text='完蛋！昨天下了一场雨，笔记本被打湿根本不能用了……这可怎么办啊……',Speaker=890145,}",
  [146461] = "{id=146461,Text='唉，如果有治愈灵石就好了……它蕴含的修复之力可以让笔记本恢复原状……',Speaker=890145,Option='我来帮你吧',}",
  [146462] = "{id=146462,Text='真、真的吗？你愿意帮我找治愈灵石？',Speaker=890145,}",
  [146463] = "{id=146463,Text='我记得在哥布灵森林的南边住着金甲虫，它们身上就会有。',Speaker=890145,}",
  [146464] = "{id=146464,Text='拜托你帮我去找啦！',Speaker=890145,}",
  [146465] = "{id=146465,Text='哔叽哔叽哔叽哔叽。',Speaker=890147,}",
  [146466] = "{id=146466,Text='哔叽？哔叽哔叽！！',Speaker=890147,}",
  [146467] = "{id=146467,Text='那就是金甲虫，好像被它发现了！',}",
  [146468] = "{id=146468,Text='拿到灵石了，回去找库拉拉吧。',}",
  [146469] = "{id=146469,Text='我的天哪，是治愈灵石！',Speaker=890145,}",
  [146470] = "{id=146470,Text='真是太谢谢你了！！',Speaker=890145,}",
  [146471] = "{id=146471,Text='这样我的笔记就可以恢复原状了！',Speaker=890145,Emoji=17,}",
  [146472] = "{id=146472,Text='说起来，以前我好像没有见过你耶！',Speaker=890145,}",
  [146473] = "{id=146473,Text='你是冒险者吗？来这里做什么呀？',Speaker=890145,Option='春日庆典',}",
  [146474] = "{id=146474,Text='哦呼！！你也是来参加庆典的吗？',Speaker=890145,}",
  [146475] = "{id=146475,Text='有没有人跟你一起组队？',Speaker=890145,Option='还没有',}",
  [146476] = "{id=146476,Text='嘿嘿，既然你还没有搭档，不如我跟你一起吧？',Speaker=890145,}",
  [146477] = "{id=146477,Text='为了这次的庆典我准备好久了，一定可以顺利找到宝藏！',Speaker=890145,}",
  [146478] = "{id=146478,Text='放心，有我带着你，胜利近在眼前！！',Speaker=890145,Option='唔…',}",
  [146479] = "{id=146479,Text='……请你考虑考虑我啦！！',Speaker=890145,Option='那好吧',}",
  [146480] = "{id=146480,Text='哦呼！那我们明天在斐基那里碰头！',Speaker=890145,}",
  [146481] = "{id=146481,Text='就这么说定了，不许一个人溜掉哦！！',Speaker=890145,Emoji=9,}",
  [146482] = "{id=146482,Text='呀吼~[c][ffff00][PlayerName][-][/c]，今天看起来精神不错嘛！',Speaker=890144,}",
  [146483] = "{id=146483,Text='有没有找到一起去庆典的同伴呀！',Speaker=890144,Option='找到了',}",
  [146484] = "{id=146484,Text='很好~你带着他过来，我帮你们报名！',Speaker=890144,}",
  [146485] = "{id=146485,Text='[c][ffff00][PlayerName][-][/c]，久等啦！',Speaker=890145,}",
  [146486] = "{id=146486,Text='我，库拉拉，全森林最英俊的哥布灵，闪亮登场~！',Speaker=890145,}",
  [146487] = "{id=146487,Text='……',Speaker=890144,Emoji=14,}",
  [146488] = "{id=146488,Text='阿嚏、阿嚏……咳咳，冒险者，你是认真的吗？',Speaker=890144,}",
  [146489] = "{id=146489,Text='（小声）你真的要跟库拉拉搭档？',Speaker=890144,}",
  [146490] = "{id=146490,Text='他可是我见过的最古怪的哥布灵了……',Speaker=890144,Option='没关系',}",
  [146491] = "{id=146491,Text='咳咳，好吧。这是魔法提示板，会根据你们寻宝的进度给予提示。',Speaker=890144,}",
  [146492] = "{id=146492,Text='交给你了，要收好哦！',Speaker=890144,}",
  [146493] = "{id=146493,Text='提示板上出现了一行奇怪的字，完全看不懂是什么意思。',Emoji=8,}",
  [146494] = "{id=146494,Text='去问问库拉拉吧。',}",
  [146495] = "{id=146495,Text='哦呼~搭档！斐基给你寻宝的线索了吗？',Speaker=890145,Option='讲述经过',}",
  [146496] = "{id=146496,Text='我看看……哦！',Speaker=890145,Emoji=7,}",
  [146497] = "{id=146497,Text='这是经过特殊加密的符文！一般人是看不懂的！',Speaker=890145,}",
  [146498] = "{id=146498,Text='一上来就这么刺激吗，我喜欢~',Speaker=890145,Option='能破解吗',}",
  [146499] = "{id=146499,Text='你当我库拉拉是什么人？我可是百年一遇的发明小天才啊！',Speaker=890145,}",
  [146500] = "{id=146500,Text='看，这是我自己研究的魔力转换器！它可以破解提示！',Speaker=890145,}",
  [146501] = "{id=146501,Text='唔，不过现在缺乏一些作为能源的荧光草……',Speaker=890145,}",
  [146502] = "{id=146502,Text='[c][ffff00][PlayerName][-][/c]，你帮我在哥布灵森林找一点过来吧！',Speaker=890145,}",
  [146503] = "{id=146503,Text='哦呼！你回来啦！',Speaker=890145,}",
  [146504] = "{id=146504,Text='对对，就是这些荧光草！它们可以启动我的转换器~',Speaker=890145,}",
  [146505] = "{id=146505,Text='好啦，你现在把转换器放在提示板上试试~',Speaker=890145,}",
  [146506] = "{id=146506,Text='嘿嘿，你瞧起作用了！',Speaker=890145,}",
  [146507] = "{id=146507,Text='提示板上的字迹改变了。',}",
  [146508] = "{id=146508,Text='“博学之人，请明天到正北方寻找”',}",
  [146509] = "{id=146509,Text='唔……看来我们破解了第一关的线索！',Speaker=890145,}",
  [146510] = "{id=146510,Text='到正北方寻找……真是个奇怪的提示，也不说要找什么东西。',Speaker=890145,}",
  [146511] = "{id=146511,Text='没办法啦，我们就明天继续寻宝吧！',Speaker=890145,}",
  [146512] = "{id=146512,Text='记得先去斐基那里呀，可别迟到啦！',Speaker=890145,}",
  [146517] = "{id=146517,Text='阿、阿嚏！这感冒真是折磨人……',Speaker=890144,}",
  [146518] = "{id=146518,Text='[c][ffff00][PlayerName][-][/c]，你到啦？库拉拉刚刚来过了，帮你做了登记。',Speaker=890144,}",
  [146519] = "{id=146519,Text='看来我的担心是多余的，你们目前是最顺利的一组……',Speaker=890144,}",
  [146520] = "{id=146520,Text='库拉拉以前就喜欢搞些稀奇古怪的发明，我还以为他是个坏孩子呢。',Speaker=890144,}",
  [146521] = "{id=146521,Text='看来是我误会了。',Speaker=890144,}",
  [146522] = "{id=146522,Text='庆典还在继续，你们要加油哦！后面还有很多考验呢。',Speaker=890144,Option='一定加油',}",
  [146523] = "{id=146523,Text='那我就好好期待一下你们的表现吧！',Speaker=890144,Option='库拉拉在哪里',}",
  [146524] = "{id=146524,Text='他让我转告你，说先到哥布灵森林北边调查了。',Speaker=890144,}",
  [146525] = "{id=146525,Text='地上有一些荧光草的粉末，应该是库拉拉掉的。',}",
  [146526] = "{id=146526,Text='库拉拉不在此处，再找找看吧。',}",
  [146527] = "{id=146527,Text='别过来，有陷阱！！',Speaker=890145,Emoji=1,}",
  [146528] = "{id=146528,Text='呼——呼——呼——',Speaker=890148,}",
  [146529] = "{id=146529,Text='呼呼！守卫宝藏！守卫宝藏！守卫宝藏！',Speaker=890148,}",
  [146530] = "{id=146530,Text='呼呼！发现目标！发现目标！发现目标！',Speaker=890148,}",
  [146531] = "{id=146531,Text='开——始——考——核——',Speaker=890148,}",
  [146532] = "{id=146532,Text='哇啊啊啊啊！！',Speaker=890145,}",
  [146533] = "{id=146533,Text='看来只能去打倒黑影了……',}",
  [146534] = "{id=146534,Text='呼呼！通过考核！通过考核！通过考核！',Speaker=890148,}",
  [146535] = "{id=146535,Text='哦呼！！太吓人了，我都快不能呼吸了……',Speaker=890145,}",
  [146536] = "{id=146536,Text='[c][ffff00][PlayerName][-][/c]，你真厉害！多谢你及时赶到，不然我可能就死啦！',Speaker=890145,Option='不客气',}",
  [146537] = "{id=146537,Text='我找搭档的眼光果然没错~有你在，我们绝对会赢得胜利的！',Speaker=890145,Option='一起加油',}",
  [146538] = "{id=146538,Text='那是当然~',Speaker=890145,Emoji=2,}",
  [146539] = "{id=146539,Text='刚才的黑影应该是宝藏的守护者吧！看来我们刚刚又过了一关！',Speaker=890145,}",
  [146540] = "{id=146540,Text='现在看看提示板有什么变化没有……',Speaker=890145,}",
  [146541] = "{id=146541,Text='提示板上的字迹变了。',}",
  [146542] = "{id=146542,Text='“机敏之人，请明天到南部寻找”',}",
  [146543] = "{id=146543,Text='哦呼，看来又得到明天才知道下一个谜题了。',Speaker=890145,}",
  [146544] = "{id=146544,Text='那我就先走啦，明天记得去斐基那里碰头哦~',Speaker=890145,}",
  [146553] = "{id=146553,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=890144,}",
  [146554] = "{id=146554,Text='是来找库拉拉的吧？他说先往哥布灵森林的南边走了。',Speaker=890144,Option='又不等我',}",
  [146555] = "{id=146555,Text='他很着急的样子，想必有什么事吧。',Speaker=890144,}",
  [146556] = "{id=146556,Text='而且克劳斯兄弟俩也跟过去了，我有点担心……',Speaker=890144,Option='那是谁',}",
  [146557] = "{id=146557,Text='是一对哥布灵兄弟，也参加了这次的庆典。',Speaker=890144,}",
  [146558] = "{id=146558,Text='他们有些心机，平时就经常欺负别的小哥布灵。',Speaker=890144,}",
  [146559] = "{id=146559,Text='根据我的预测，你们现在离胜利只有一步之遥了。克劳斯兄弟多半也是冲着冠军来的，说不好会盯上库拉拉……',Speaker=890144,}",
  [146560] = "{id=146560,Text='你快去看看吧，这时候可别出什么事。',Speaker=890144,Emoji=15,}",
  [146561] = "{id=146561,Text='千万小心克劳斯兄弟……阿嚏！',Speaker=890144,}",
  [146562] = "{id=146562,Text='你们要干什么！',Speaker=890145,}",
  [146563] = "{id=146563,Text='啧啧啧，这不是我们的大发明家吗~',Speaker=890150,}",
  [146564] = "{id=146564,Text='啧啧啧，听说你在庆典里得了不少好东西呀~',Speaker=890151,}",
  [146565] = "{id=146565,Text='啧啧啧，有发财的机会，但不跟我们俩说，这不好吧！',Speaker=890150,Emoji=5,}",
  [146566] = "{id=146566,Text='啧啧啧，把提示板的线索告诉我们，就放过你！',Speaker=890151,Emoji=5,}",
  [146567] = "{id=146567,Text='休想！这是我和搭档一起努力找到的，绝不给你们！',Speaker=890145,Emoji=12,}",
  [146568] = "{id=146568,Text='啧啧啧，看来你是欠教训了！',Speaker=890150,}",
  [146569] = "{id=146569,Text='啧啧啧，那就怪不得我们了！',Speaker=890151,}",
  [146570] = "{id=146570,Text='哇啊！！又要打架了！！',Speaker=890145,}",
  [146571] = "{id=146571,Text='得赶紧上去帮库拉拉！',}",
  [146572] = "{id=146572,Text='啧啧啧，居然找了帮手！',Speaker=890150,}",
  [146573] = "{id=146573,Text='啧啧啧，一切还没结束！',Speaker=890151,}",
  [146574] = "{id=146574,Text='搭档！！让我来帮你！！',Speaker=890145,}",
  [146575] = "{id=146575,Text='吃我昏昏粉——',Speaker=890145,}",
  [146576] = "{id=146576,Text='啊————',Speaker=890150,}",
  [146577] = "{id=146577,Text='啊————',Speaker=890151,}",
  [146578] = "{id=146578,Text='哦呼……真是吓死我了……',Speaker=890145,}",
  [146579] = "{id=146579,Text='现在不用担心，他们已经被打跑了！',Speaker=890145,}",
  [146580] = "{id=146580,Text='搭档，我还挺厉害吧？没、没有给你拖后腿吧？',Speaker=890145,Option='没有的',Emoji=2,}",
  [146581] = "{id=146581,Text='哦呼~太好了……',Speaker=890145,}",
  [146582] = "{id=146582,Text='不过搭档……',Speaker=890145,}",
  [146583] = "{id=146583,Text='我在找了很久，也没有找到下一个提示了……',Speaker=890145,}",
  [146584] = "{id=146584,Text='会不会是那块板子出问题了呢？',Speaker=890145,}",
  [146585] = "{id=146585,Text='我们再看一下吧！',Speaker=890145,}",
  [146586] = "{id=146586,Text='提示板上的字迹不知什么时候已经变了。',}",
  [146587] = "{id=146587,Text='“勇敢之人，请明天到中心处寻找”',}",
  [146588] = "{id=146588,Text='哦呼！原来这一关是对勇气的考核吗？',Speaker=890145,}",
  [146589] = "{id=146589,Text='刚刚我们一起阻止了克劳斯兄弟，就算通过考验了吧！',Speaker=890145,Option='没错',}",
  [146590] = "{id=146590,Text='太好了！搭档，多亏有你！',Speaker=890145,Emoji=2,}",
  [146591] = "{id=146591,Text='马上就是最后一关了，我还真有点激动……得回去好好准备才行……',Speaker=890145,}",
  [146592] = "{id=146592,Text='那搭档，明天再见啦！',Speaker=890145,}",
  [146593] = "{id=146593,Text='恭喜你，[c][ffff00][PlayerName][-][/c]！',Speaker=890144,}",
  [146594] = "{id=146594,Text='咳咳，你和库拉拉现在是唯一一组走到最后关卡的！',Speaker=890144,Emoji=2,}",
  [146595] = "{id=146595,Text='希望你们可以得到冠军哦！',Speaker=890144,Option='库拉拉呢',}",
  [146596] = "{id=146596,Text='他已经提前去哥布灵森林的中心啦，你快去找他汇合吧！',Speaker=890144,}",
  [146597] = "{id=146597,Text='哦呼~搭档啊！！',Speaker=890145,}",
  [146598] = "{id=146598,Text='终于到了最后一天了！！',Speaker=890145,}",
  [146599] = "{id=146599,Text='你看地上这里！！摆着奇怪的蛋糕鲜花和苹果！！',Speaker=890145,Option='是谜题吗',}",
  [146600] = "{id=146600,Text='多半是的……而且我想碰它们的时候，总会被神秘的力量弹开！',Speaker=890145,}",
  [146601] = "{id=146601,Text='这一定是最后的考验了！',Speaker=890145,}",
  [146602] = "{id=146602,Text='快看看，提示板上有新消息吗？',Speaker=890145,}",
  [146603] = "{id=146603,Text='线索变化了，出现了一则谜语。',}",
  [146604] = "{id=146604,Text='“圆圆脸，脆又甜”',}",
  [146605] = "{id=146605,Text='啊，是个谜语！',Speaker=890145,}",
  [146606] = "{id=146606,Text='搭档，你觉得谜底是什么啊？',Speaker=890145,Option='{蛋糕,3},{鲜花,3},{苹果,4}',}",
  [146607] = "{id=146607,Text='你好笨哦，怎么可能是这个！',Speaker=890145,}",
  [146608] = "{id=146608,Text='对，谜底肯定是苹果啊！',Speaker=890145,Emoji=17,}",
  [146609] = "{id=146609,Text='可是已经猜出了谜底，为什么我还是碰不到地上的苹果呢……',Speaker=890145,Option='烂苹果',}",
  [146610] = "{id=146610,Text='啊……地上这个是烂苹果，谜语里说的是脆又甜，那一定是不可能是烂的！',Speaker=890145,}",
  [146611] = "{id=146611,Text='我想想、我想想！一定有把苹果变新鲜的办法……',Speaker=890145,}",
  [146612] = "{id=146612,Text='有了！我前几天做的生长魔药刚好能派上用场！！',Speaker=890145,}",
  [146613] = "{id=146613,Text='把它泼到烂苹果上试试吧，拜托你了搭档！',Speaker=890145,}",
  [146614] = "{id=146614,Text='成功了！！',Speaker=890145,}",
  [146615] = "{id=146615,Text='哦呼，是庆典大使来了！',Speaker=890145,}",
  [146616] = "{id=146616,Text='呀吼~又见面啦！我是春日庆典的大使托拉基~',Speaker=890143,}",
  [146617] = "{id=146617,Text='库拉拉，和来自异乡的冒险者啊！',Speaker=890143,}",
  [146618] = "{id=146618,Text='恭喜你们，获得了最后的胜利！！',Speaker=890143,}",
  [146619] = "{id=146619,Text='这一次的寻宝活动，分别对参与者的智慧、勇气和应变力进行了考察。',Speaker=890143,}",
  [146620] = "{id=146620,Text='而最后象征希望的金苹果，就是你们亲手制造的宝藏！',Speaker=890143,}",
  [146621] = "{id=146621,Text='祝贺你们完美通过了全部考验~',Speaker=890143,}",
  [146622] = "{id=146622,Text='现在我宣布：库拉拉、[c][ffff00][PlayerName][-][/c]，你们就是本次春日庆典的冠军！',Speaker=890143,}",
  [146623] = "{id=146623,Text='呀吼~终于到了颁奖的时候了！',Speaker=890143,}",
  [146624] = "{id=146624,Text='这是月光石，我们哥布灵一族的珍宝。每当夜晚降临，就会发出不可思议的美丽光芒！',Speaker=890143,}",
  [146625] = "{id=146625,Text='谢谢！我们会好好珍惜的！',Speaker=890145,}",
  [146626] = "{id=146626,Text='呀吼~有这句话我就放心啦！',Speaker=890143,}",
  [146627] = "{id=146627,Text='希望这次庆典会成为你们美好的回忆~',Speaker=890143,Emoji=20,}",
  [146628] = "{id=146628,Text='我还有事情，就先走啦！你们好好休息！',Speaker=890143,}",
  [146629] = "{id=146629,Text='啊……大使走掉了。',Speaker=890145,}",
  [146630] = "{id=146630,Text='其实……我……',Speaker=890145,Emoji=18,}",
  [146631] = "{id=146631,Text='我……那个……搭档，谢谢你！',Speaker=890145,}",
  [146632] = "{id=146632,Text='这次能拿到冠军，我一个人是根本做不到的！！！',Speaker=890145,}",
  [146633] = "{id=146633,Text='真的很感谢你一直帮助我！！',Speaker=890145,Option='不客气啦',}",
  [146634] = "{id=146634,Text='搭档，你、你是不是等下也要走了啊？',Speaker=890145,Option='要继续旅行',}",
  [146635] = "{id=146635,Text='好嘛……就知道是这样。',Speaker=890145,}",
  [146636] = "{id=146636,Text='我以后也想和你一样，去大陆各地冒险！',Speaker=890145,Option='会再见的',}",
  [146637] = "{id=146637,Text='嗯！一定会再见的！我们可是最佳搭档！',Speaker=890145,Emoji=2,}",
  [146638] = "{id=146638,Text='那拜拜啦，[c][ffff00][PlayerName][-][/c]。',Speaker=890145,}",
  [146639] = "{id=146639,Text='一个人去冒险要注意安全！还有……绝对不要忘了我哦！',Speaker=890145,}",
  [147569] = "{id=147569,Text='刻印着古老花纹的宝箱，里面到底藏着什么东西呢？',Speaker=890236,Option='{打开看看,14},{害怕离开,13}',}",
  [147570] = "{id=147570,Text='一个优秀的冒险者可不能胆小呦！',Speaker=890236,}",
  [147571] = "{id=147571,Text='奖励要给予你这种真正的勇士！',Speaker=890236,}",
  [147572] = "{id=147572,Text='是哪个讨厌的人类打扰我休息！',Speaker=890248,}",
  [147573] = "{id=147573,Text='岸边好像有什么东西？',}",
  [147574] = "{id=147574,Text='人类！看我！低头！',Speaker=890239,Option='你是？',Emoji=7,}",
  [147575] = "{id=147575,Text='我是咸鱼王卢克，被一群愚蠢的捕鱼人意外抓到了陆地上。',Speaker=890239,}",
  [147576] = "{id=147576,Text='该死的！在陆地上我没有任何魔法，快让我回到水里，没有水我会死的！',Speaker=890239,Option='{帮助它,34},{转身离开,33}',}",
  [147577] = "{id=147577,Text='算你有眼光救了我。',Speaker=890239,}",
  [147578] = "{id=147578,Text='我咸鱼王不想欠你人情！哼，这个给你！我走了！',Speaker=890239,}",
  [147579] = "{id=147579,Text='果然人类都是冷漠无情的！',Speaker=890239,Emoji=12,}",
  [147580] = "{id=147580,Text='呜呜呜，是谁戳到了宝宝的屁股！',Speaker=890248,}",
  [147582] = "{id=147582,Text='大陆上罕见的魔法书，要打开它么？',Speaker=890237,Option='{打开,33},{转身离开,32}',}",
  [147583] = "{id=147583,Text='呦吼~人类，是你打开了这本书？',Speaker=890238,Option='是的',Emoji=17,}",
  [147584] = "{id=147584,Text='我已经被关在这本书里好久了，是你放了我出来！',Speaker=890238,}",
  [147585] = "{id=147585,Text='我可以实现你一个愿望！',Speaker=890238,Option='{无尽的财富,34},{永恒的生命,34},{强大的力量,34}',Emoji=20,}",
  [147586] = "{id=147586,Text='抱歉，我做不到！',Speaker=890238,Emoji=15,}",
  [147587] = "{id=147587,Text='我要是有那种力量，还至于被困在书里这么多年么！',Speaker=890238,Option='……',}",
  [147588] = "{id=147588,Text='嘿嘿，别担心嘛，那些做不到，小礼物还是可以给你的！',Speaker=890238,}",
  [147589] = "{id=147589,Text='这个你收好了，后会有期！',Speaker=890238,Option='卑鄙……',Emoji=5,}",
  [147590] = "{id=147590,Text='闯到我的长眠之地，你以为你还能走的了？',Speaker=890248,}",
  [147591] = "{id=147591,Text='一个奇怪的蛋，要打碎它么？',Speaker=890240,Option='{打碎,33},{离开,32}',}",
  [147592] = "{id=147592,Text='啾啾，你是我的妈妈么？',Speaker=890241,Option='不是呢',}",
  [147593] = "{id=147593,Text='呜呜呜，我的妈妈呢！',Speaker=890241,Option='别哭了',Emoji=24,}",
  [147594] = "{id=147594,Text='呼呼~谢谢你安慰我！',Speaker=890241,}",
  [147595] = "{id=147595,Text='叽仔要坚强！叽仔自己去寻找麻麻了！',Speaker=890241,}",
  [147596] = "{id=147596,Text='哎？年轻人，你能看到我？',Speaker=890243,Option='是的',}",
  [147597] = "{id=147597,Text='啧，我游历这么多年，你还是第一个！',Speaker=890243,Option='什么？',Emoji=7,}",
  [147598] = "{id=147598,Text='没什么，看在你我这么有缘的份儿上，我这肩膀有些酸痛，你帮我捏捏吧！',Speaker=890243,Option='……',Emoji=20,}",
  [147599] = "{id=147599,Text='怎么？你不愿意么？',Speaker=890243,Option='{好吧,34},{无视离开,33}',}",
  [147600] = "{id=147600,Text='嗯嗯……力道不错，就是那里！',Speaker=890243,Option='……',}",
  [147601] = "{id=147601,Text='年轻人要懂得尊老爱幼，老头子我很满意！',Speaker=890243,Emoji=17,}",
  [147602] = "{id=147602,Text='这个给你，老头子我走了！',Speaker=890243,}",
  [147603] = "{id=147603,Text='哎……现在的年轻人还是太浮躁了。',Speaker=890243,}",
  [147604] = "{id=147604,Text='嗡嗡嗡……嗡嗡嗡……',Speaker=890248,}",
  [147605] = "{id=147605,Text='喂喂！那是我做考察用的红酒！',Speaker=890245,Option='额……',}",
  [147606] = "{id=147606,Text='而且这个红酒人喝了可能会死。',Speaker=890245,Option='怎么办？！',}",
  [147607] = "{id=147607,Text='谁让你偷喝我红酒！切!',Speaker=890245,Option='对不起',}",
  [147608] = "{id=147608,Text='这样吧，我问你一个问题，你答对了我就给你解药！',Speaker=890245,Option='你说吧',}",
  [147609] = "{id=147609,Text='我帅么？',Speaker=890245,Option='……',Emoji=5,}",
  [147610] = "{id=147610,Text='问你话呢？我帅么？',Speaker=890245,Option='{帅死了,34},{丑,33}',}",
  [147611] = "{id=147611,Text='嘿嘿，我果然很帅！',Speaker=890245,}",
  [147612] = "{id=147612,Text='解药给你！',Speaker=890245,}",
  [147613] = "{id=147613,Text='……你等死吧！！',Speaker=890245,Emoji=12,}",
  [147614] = "{id=147614,Text='是谁闯入了狐族的地盘！',Speaker=890248,}",
  [147615] = "{id=147615,Text='兽人部落不允许人类踏足！！',Speaker=890248,}",
  [147616] = "{id=147616,Text='唔嘛！我的爱人是你么！',Speaker=890247,Option='？！',Emoji=1,}",
  [147617] = "{id=147617,Text='捡了我的爱情之花，你就是我的人了！',Speaker=890247,}",
  [147618] = "{id=147618,Text='我要嫁给你！',Speaker=890247,Option='……',Emoji=2,}",
  [147619] = "{id=147619,Text='知道这个消息，开心么！',Speaker=890247,Option='{欣然接受,32},{打扰了,33}',}",
  [147620] = "{id=147620,Text='果然，我这种女神一般的美貌没人能抵得住诱惑！',Speaker=890247,}",
  [147621] = "{id=147621,Text='这个送给你，我的爱人！',Speaker=890247,Emoji=1,}",
  [147622] = "{id=147622,Text='我要回去和父亲大人说准备我们的婚礼!',Speaker=890247,Emoji=20,}",
  [147623] = "{id=147623,Text='竟然敢拒绝我罗拉？！',Speaker=890247,}",
  [147624] = "{id=147624,Text='你死定了！',Speaker=890247,}",
  [147626] = "{id=147626,Text='好浓郁的红酒，快尝尝看吧！',}",
  [148316] = "{id=148316,Text='是料理协会！你闻着料理的香味，仿佛参透了人生的真谛……\n[c][ffff00]*获得随机皇家料理×1[-][/c]',Speaker=890290,}",
  [148317] = "{id=148317,Text='烫烫烫！踩到了一块滚烫的石头……\n[c][ffff00]*获得「火灵原石」×1[-][/c]\n[c][ffff00]*被高温灼伤[-][/c]',Speaker=890290,}",
  [148318] = "{id=148318,Text='执事们为你准备了一点礼物哦~\n[c][ffff00]*获得「锁链雷锭」×1[-][/c]',Speaker=890290,}",
  [148319] = "{id=148319,Text='这里是神圣美丽的教堂。要不要来围观婚礼呢~\n[c][ffff00]*获得「糖果」×10[-][/c]',Speaker=890290,}",
  [148320] = "{id=148320,Text='这……这是什么地方？？\n[c][ffff00]*获得「潇洒的B格猫随变箱」×1[-][/c]',Speaker=890290,}",
  [148321] = "{id=148321,Text='一见倾心，飞鸽传书，圣地约会，婚礼发糖，二人世界，一气呵成~\n[c][ffff00]*获得「我中意你」×1、「恋人絮语」×1、「幻想之证·银」×1、「蜜恋糖果」×1、「绝对领域」×1[-][/c]',Speaker=890290,}",
  [148322] = "{id=148322,Text='据说用变傻激光枪照射哥布灵，有可能产生意想不到的效果哦~\n[c][ffff00]*获得「变傻激光枪」×3[-][/c]',Speaker=890290,}",
  [148323] = "{id=148323,Text='裂隙内部并没有很烫，反倒意外地暖心。\n[c][ffff00]*获得「暖心料理」×1[-][/c]',Speaker=890290,}",
  [148324] = "{id=148324,Text='从裂隙里摸出来一些Zeny~这种日常生活中的小惊喜，就像是在旧衣服里突然翻到了钱一样~\n[c][ffff00]*获得Zeny×12345[-][/c]',Speaker=890290,}",
  [148325] = "{id=148325,Text='你突然感到一阵剧痛，仿佛生命都被抽干了。但是从裂隙里掉出不少Zeny！正所谓富贵险中求！\n[c][ffff00]*身受重伤[-][/c]',Speaker=890290,}",
  [151433] = "{id=151433,Text='惨了！跟七七姐姐走散了，找不到她了！',Speaker=890752,}",
  [151434] = "{id=151434,Text='惨了惨了惨了！',Speaker=890752,Option='冷静点',}",
  [151435] = "{id=151435,Text='怎么冷静嘛！七七姐姐是穿越时空来的，说不定什么时候就回到未来去了，我们在一起的时间很宝贵的！',Speaker=890752,Option='{好啊,4},{没时间,3}',}",
  [151438] = "{id=151438,Text='算了！我自己去找！',Speaker=890752,}",
  [151439] = "{id=151439,Text='喂！那边那个一脸蠢相的冒险者！',Speaker=890741,Option='……',Emoji=19,}",
  [151440] = "{id=151440,Text='别看了，就是叫你，见到我还不快来问候！',Speaker=890741,Option='？',}",
  [151441] = "{id=151441,Text='难道你...不记得我了？好大的胆子，必须好好教训一下！',Speaker=890741,Emoji=12,}",
  [151442] = "{id=151442,Text='哼哼，怎么样，你的身体有没有记起我？',Speaker=890741,Option='……',Emoji=14,}",
  [151443] = "{id=151443,Text='不会吧！',Speaker=890741,}",
  [151444] = "{id=151444,Text='等等，现在是哪一年，这个时间线的你不会还没遇到我吧？让我查一下…',Speaker=890741,}",
  [151445] = "{id=151445,Text='我找找…',Speaker=890741,}",
  [151446] = "{id=151446,Text='七七姐！可找到你啦！',Speaker=890752,}",
  [151447] = "{id=151447,Text='哎呦，你撞到我了！',Speaker=890741,}",
  [151449] = "{id=151449,Text='因为施法被打断，时间魔杖失控了，不知道飞到哪里去了！',Speaker=890741,}",
  [151450] = "{id=151450,Text='咦？我、我不是故意的…',Speaker=890752,Emoji=15,}",
  [151451] = "{id=151451,Text='臭·小·子！',Speaker=890741,}",
  [151452] = "{id=151452,Text='要是找不到魔杖，我就把你的头拧下来做成台灯！',Speaker=890741,}",
  [151453] = "{id=151453,Text='冒险者，我不管你有没有想起我，有没有见过我，现在开始你要给我去找时间魔杖！为我效劳是给你的恩赐，先去附近找找，说不定只是被弹飞到广场上了。',Speaker=890741,}",
  [151454] = "{id=151454,Text='为什么我要被迫当苦力... ',}",
  [151455] = "{id=151455,Text='这里也没有...好想回家...',}",
  [151456] = "{id=151456,Text='冒险者，你...你找到时间魔杖了吗？',Speaker=890752,Option='没有',}",
  [151457] = "{id=151457,Text='说的也是，广场上人来人往，想找到一根魔杖也太难了。',Speaker=890752,}",
  [151458] = "{id=151458,Text='看你很不情愿的样子，别找啦。别看七七姐一副凶巴巴的样子，她还是很善良的，不会把你怎么样的。',Speaker=890752,Option='真的吗',Emoji=26,}",
  [151459] = "{id=151459,Text='真的真的，我最了解七七姐了！',Speaker=890752,}",
  [151460] = "{id=151460,Text='这么快就回来了？不愧是我选中的冒险者，你很能干嘛。',Speaker=890741,Option='过奖过奖',Emoji=26,}",
  [151461] = "{id=151461,Text='那快点交给我吧。',Speaker=890741,}",
  [151462] = "{id=151462,Text='时间魔杖，快交给我啊，你蠢到听不懂人话了吗？',Speaker=890741,Option='没找到...',Emoji=12,}",
  [151463] = "{id=151463,Text='……',Speaker=890741,}",
  [151464] = "{id=151464,Text='你敢耍我！',Speaker=890741,}",
  [151465] = "{id=151465,Text='还敢还手，反了你了！',Speaker=890741,}",
  [151466] = "{id=151466,Text='为史上最伟大的魔女卖命就那么不情愿吗！',Speaker=890741,}",
  [151467] = "{id=151467,Text='七七姐，这是怎么了？',Speaker=890752,}",
  [151468] = "{id=151468,Text='不关你事，走开！',Speaker=890741,}",
  [151469] = "{id=151469,Text='咳咳，冒险者是无辜的，饶了他吧。',Speaker=890752,}",
  [151470] = "{id=151470,Text='而且广场上我已经找遍了，时间魔杖不在这里。',Speaker=890752,}",
  [151471] = "{id=151471,Text='刚才，我在翻阅你的冒险旅途，时间魔杖连接了你曾经走过的时空。',Speaker=890741,}",
  [151472] = "{id=151472,Text='然后法术被打断，时间魔杖就消失了...我猜魔杖肯定被传送到了那些时空中，我要再次施法来查一查！',Speaker=890741,}",
  [151473] = "{id=151473,Text='再给你一次机会，帮我收集施法的材料，这次再偷懒我可饶不了你！',Speaker=890741,}",
  [151474] = "{id=151474,Text='蟾蜍眼球，蝙蝠翼膜，蜥蜴牙，快去找！',Speaker=890741,}",
  [151475] = "{id=151475,Text='这都是什么东西啊，听都没听过…',Emoji=15,}",
  [151476] = "{id=151476,Text='先去南门碰碰运气吧...',}",
  [151477] = "{id=151477,Text='冒险者！你看起来挺精神的嘛，我说的没错吧，七七姐还是很善良的！',Speaker=890752,Option='……',Emoji=14,}",
  [151478] = "{id=151478,Text='不要这副表情，不就是收集施法的材料吗，这种小事不难的！',Speaker=890752,Option='说得轻松…',}",
  [151479] = "{id=151479,Text='是真的，我都替你办好了，你看这是什么！',Speaker=890752,}",
  [151480] = "{id=151480,Text='袋子里是蟾蜍眼球，蝙蝠翼膜还有蜥蜴牙，怎么样，我干的不错吧~',Speaker=890752,Option='谢谢你',Emoji=17,}",
  [151481] = "{id=151481,Text='不过你可千万别告诉七七姐是我给你的啊，记住了！',Speaker=890752,Option='为什么',}",
  [151482] = "{id=151482,Text='因为...因为这是她给你的惩罚嘛，我帮你她会不高兴的。',Speaker=890752,Emoji=18,}",
  [151483] = "{id=151483,Text='这么快？你又空着手回来了吧！',Speaker=890741,Emoji=12,}",
  [151484] = "{id=151484,Text='看来之前对你太仁慈了，我必须好好让你体会史上最伟大魔女的可怕之处！',Speaker=890741,Option='递上袋子',Emoji=18,}",
  [151485] = "{id=151485,Text='咦？都收集好了？怎么可能？',Speaker=890741,}",
  [151486] = "{id=151486,Text='我看看...虽然这些东西外表看起来有点怪怪的，但是确实收集好了。',Speaker=890741,}",
  [151487] = "{id=151487,Text='哼，算你识相。我要开始施法了，你跟马蒂站到那边去。',Speaker=890741,}",
  [151488] = "{id=151488,Text='呼，太好了，七七姐没发现。我们安静地等她施法吧。',Speaker=890752,}",
  [151489] = "{id=151489,Text='柠檬香草黄奶油，冰糖芝士烤花生，向我展示时间魔杖的去向！',Speaker=890741,}",
  [151490] = "{id=151490,Text='我感受到了时间魔杖的能量，果然是掉到你曾经走过的时空，让我看看具体位置…',Speaker=890741,}",
  [151491] = "{id=151491,Text='失败了！怎么会这样…是不是你在施法材料里做了手脚？',Speaker=890741,Option='没有',Emoji=12,}",
  [151492] = "{id=151492,Text='七七姐别生气，反正已经找到了。',Speaker=890752,}",
  [151493] = "{id=151493,Text='哼，还要去这家伙的时空里一处处的找！',Speaker=890741,Option='…',}",
  [151494] = "{id=151494,Text='即使要走遍每一个角落，也要把时间魔杖找回来！明天准时来见我，不然有你好看的！',Speaker=890741,}",
  [151495] = "{id=151495,Text='呼，冒险者，我也先走了，明天记得来广场碰头。',Speaker=890752,}",
  [151496] = "{id=151496,Text='太慢了！怎么现在才来！你就这么不想见我吗！',Speaker=890741,Emoji=18,}",
  [151497] = "{id=151497,Text='哼，我们要进入你的时空去寻找时间魔杖，这些时空可能是你的过去，也可能是你的将来。',Speaker=890741,}",
  [151498] = "{id=151498,Text='要是我剧透了你的人生，可不要怪我。',Speaker=890741,Emoji=5,}",
  [151499] = "{id=151499,Text='第一站，苏克拉特沙漠，我们走！',Speaker=890741,}",
  [151500] = "{id=151500,Text='七七姐，我也要去！',Speaker=890752,}",
  [151501] = "{id=151501,Text='烦死了，随便你。',Speaker=890741,}",
  [151502] = "{id=151502,Text='前往苏克拉特沙漠的时空之门，要进入吗？',Speaker=807751,Option='进入',}",
  [151503] = "{id=151503,Text='看来，这里就是你冒险途中遇上的第一道难关，这段回忆在你的时空中很有分量。',Speaker=890741,Option='……',}",
  [151504] = "{id=151504,Text='虽然不知道现在的你有没有来过这里，但是探查的任务还是交给你了，快去！',Speaker=890741,}",
  [151505] = "{id=151505,Text='一片雄伟的建筑群，但已经化为废墟，这里...发生了什么？',Emoji=19,}",
  [151506] = "{id=151506,Text='一处简易聚落，聚集了很多商人和冒险者，他们在这里做什么？',Emoji=19,}",
  [151507] = "{id=151507,Text='一座壮观的雕像，几个表情坚毅的人在对抗一个可怕的恶魔，呃...头好痛，怎么回事…',Emoji=15,}",
  [151508] = "{id=151508,Text='探查的怎么样了？',Speaker=890741,Option='说明情况',}",
  [151509] = "{id=151509,Text='头痛？看来进入自己的时空领域导致你的记忆发生了混乱，你的过去与未来交织在一起，让你的认知混乱了。',Speaker=890741,}",
  [151510] = "{id=151510,Text='哼，真没用。',Speaker=890741,}",
  [151511] = "{id=151511,Text='感觉好多了，头不痛了。',}",
  [151512] = "{id=151512,Text='这是我对仆人的赏赐，不用谢我。',Speaker=890741,Emoji=5,}",
  [151513] = "{id=151513,Text='你这家伙派不上用场了，还得我自己去找魔杖的线索，哼。',Speaker=890741,}",
  [151514] = "{id=151514,Text='七七姐果然很善良吧，特意让你休息呢。',Speaker=890752,}",
  [151515] = "{id=151515,Text='你感觉怎么样了？',Speaker=890752,Option='{很好,2},{不太好,3}',}",
  [151516] = "{id=151516,Text='太好了，不愧是你！',Speaker=890752,}",
  [151517] = "{id=151517,Text='坚持一下，你可是强大的冒险者呀！',Speaker=890752,}",
  [151518] = "{id=151518,Text='我们还是快去找七七姐吧，可不能让她—',Speaker=890752,Option='不能让她？',}",
  [151519] = "{id=151519,Text='呃，不能让她遇到危险！对！我们快去找她吧！',Speaker=890752,}",
  [151520] = "{id=151520,Text='你来得正好，这家伙认识你，可以向他打听一下线索。',Speaker=890741,}",
  [151521] = "{id=151521,Text='[c][ffff00][PlayerName][-][/c]！是你！我们的英雄！',Speaker=3329,Option='英雄？',Emoji=19,}",
  [151522] = "{id=151522,Text='没想到还能再见到你，我实在太兴奋了！',Speaker=3329,Emoji=17,}",
  [151523] = "{id=151523,Text='咦？你在说谁？',Speaker=890741,}",
  [151524] = "{id=151524,Text='就是眼前这位冒险者！',Speaker=3329,}",
  [151525] = "{id=151525,Text='……',Speaker=890741,Emoji=1,}",
  [151526] = "{id=151526,Text='我的仆人这么伟大？你没搞错吧？',Speaker=890741,}",
  [151527] = "{id=151527,Text='你这家伙，怎么能对[c][ffff00][PlayerName][-][/c]这么无礼！他可是我们的英雄！',Speaker=3329,}",
  [151528] = "{id=151528,Text='既然你这么无知，就让我给你讲讲[c][ffff00][PlayerName][-][/c]的功绩。',Speaker=3329,}",
  [151529] = "{id=151529,Text='曾经有个恶毒的伪天使。',Speaker=3329,}",
  [151530] = "{id=151530,Text='它策划了一场邪恶的阴谋。',Speaker=3329,}",
  [151531] = "{id=151531,Text='它的阴谋一步步实施，整个梦罗克，不，整个世界都陷入危机！',Speaker=3329,}",
  [151532] = "{id=151532,Text='就在这时，一位勇敢的冒险者挺身而出，直面伪天使！',Speaker=3329,}",
  [151533] = "{id=151533,Text='最后，冒险者打败并封印了巴风特，拯救了这个世界。',Speaker=3329,}",
  [151534] = "{id=151534,Text='他就是[c][ffff00][PlayerName][-][/c]！',Speaker=3329,Emoji=27,}",
  [151535] = "{id=151535,Text='没想到你这家伙还挺厉害的嘛。',Speaker=890741,Option='记不太清',}",
  [151536] = "{id=151536,Text='嗯，他说的事情可能是你的过去，也可能是你的未来，反正肯定是你的事情。',Speaker=890741,}",
  [151537] = "{id=151537,Text='既然他这么崇拜你，那你的要求他一定会满足，快问问他有没有见过时间魔杖。',Speaker=890741,}",
  [151538] = "{id=151538,Text='[c][ffff00][PlayerName][-][/c]，有什么我能帮上忙的吗？',Speaker=3329,Option='时间魔杖',}",
  [151539] = "{id=151539,Text='你们在找一把魔杖？唔…',Speaker=3329,}",
  [151540] = "{id=151540,Text='昨天有人在南边的石碑附近看到了奇怪的闪光，以前从未见过，你们可以去那里看一看。',Speaker=3329,}",
  [151541] = "{id=151541,Text='在那！时间魔杖！',Speaker=890757,Option='太好了',}",
  [151542] = "{id=151542,Text='这里...够不到。喂，你从后面抓住我，我要把身子探出去。',Speaker=890757,}",
  [151543] = "{id=151543,Text='就快抓到了，还差一点。',Speaker=890757,}",
  [151544] = "{id=151544,Text='被什么东西撞到了…',}",
  [151545] = "{id=151545,Text='时间魔杖！又不见了！',Speaker=890757,Emoji=1,}",
  [151546] = "{id=151546,Text='你怎么搞的，为什么不好好抓着我！',Speaker=890757,Option='被人撞了',}",
  [151547] = "{id=151547,Text='你骗鬼啊，这里根本就没有别人！',Speaker=890757,}",
  [151548] = "{id=151548,Text='哼，要不是看你今天帮了不少忙，我现在就要把你的胳膊做成车轴！',Speaker=890757,}",
  [151549] = "{id=151549,Text='今天就到这吧，明天来普隆德拉的广场，我们继续找！',Speaker=890757,}",
  [151550] = "{id=151550,Text='马蒂，你昨天跑哪里去了？',Speaker=890741,}",
  [151551] = "{id=151551,Text='我...我在沙漠跟[c][ffff00][PlayerName][-][/c]走散了，一直找不到你们，就自己先回来了。',Speaker=890752,Emoji=18,}",
  [151552] = "{id=151552,Text='你这家伙，真不省心。',Speaker=890741,}",
  [151553] = "{id=151553,Text='哦，我的仆人来了。',Speaker=890741,Option='早上好',}",
  [151554] = "{id=151554,Text='什么早上好，这都几点了！',Speaker=890741,Emoji=12,}",
  [151555] = "{id=151555,Text='快点开始寻找时间魔杖吧。',Speaker=890741,}",
  [151556] = "{id=151556,Text='今天的目的地是古城，快跟上。',Speaker=890741,}",
  [151557] = "{id=151557,Text='今天我不会走丢啦！',Speaker=890752,}",
  [151558] = "{id=151558,Text='前往古城的时空之门，要进入吗？',Speaker=807751,Option='进入',}",
  [151559] = "{id=151559,Text='好了，今天还是老样子，快去你的时空里四处探查一下吧，如果又头痛了回来找我。',Speaker=890741,Option='好吧',}",
  [151560] = "{id=151560,Text='看起来像一处神圣的法坛，充满了强大的魔法力量，这里...发生过什么？',}",
  [151561] = "{id=151561,Text='一群训练有素、整装待发的勇士，他们...准备去干什么？',}",
  [151562] = "{id=151562,Text='从前方传来了阵阵邪恶的气息，似乎有什么危险的家伙，而且...头又开始痛了，先回去找七七吧。',}",
  [151563] = "{id=151563,Text='出现恶魔了！可恶，我现在头痛的没有办法战斗…',Emoji=18,}",
  [151564] = "{id=151564,Text='[c][ffff00][PlayerName][-][/c]，小心！',Speaker=6077,}",
  [151565] = "{id=151565,Text='[c][ffff00][PlayerName][-][/c]，你没事吧？',Speaker=6077,Option='你认识我？',}",
  [151566] = "{id=151566,Text='你在说什么呢？是在战斗中伤到脑袋了吗？',Speaker=6077,Emoji=8,}",
  [151567] = "{id=151567,Text='不说这个了，你怎么会在这里，你不是为了开通古城里的传送通道去执行任务了吗？',Speaker=6077,Option='忘记了',}",
  [151568] = "{id=151568,Text='你没事吧，怎么怪怪的？忘记了就去找莉莎吧，是她拜托你去做的。',Speaker=6077,}",
  [151569] = "{id=151569,Text='[c][ffff00][PlayerName][-][/c]，怎么这么快就回来了？传送通道都开通了？',Speaker=4273,Option='头好痛',Emoji=15,}",
  [151570] = "{id=151570,Text='头痛？你受伤了吗！',Speaker=4273,Option='现在是哪一年',}",
  [151571] = "{id=151571,Text='天呐，伤得这么重，必须尽快治疗。',Speaker=4273,Emoji=7,}",
  [151572] = "{id=151572,Text='[c][ffff00][PlayerName][-][/c]，你去了那么久还不回来，原来躲在这！',Speaker=890741,}",
  [151573] = "{id=151573,Text='恶魔！就是你干的吗！卑鄙的家伙！',Speaker=4273,}",
  [151574] = "{id=151574,Text='你怎么敢用这种口气和我说话，愚蠢的凡人！',Speaker=890741,Option='头更痛了',Emoji=37,}",
  [151575] = "{id=151575,Text='这两个人太合不来了，必须先阻止她们。',}",
  [151576] = "{id=151576,Text='你们不要再打了！',}",
  [151577] = "{id=151577,Text='[c][ffff00][PlayerName][-][/c]',Speaker=890741,}",
  [151578] = "{id=151578,Text='[c][ffff00][PlayerName][-][/c]',Speaker=4273,}",
  [151579] = "{id=151579,Text='好...痛…',Emoji=34,}",
  [151580] = "{id=151580,Text='[c][ffff00][PlayerName][-][/c]，为什么要阻止我。你忘记你的任务了吗？',Speaker=4273,}",
  [151581] = "{id=151581,Text='[c][ffff00][PlayerName][-][/c]，你还记得魔法师学徒庞德吧。',Speaker=4273,}",
  [151582] = "{id=151582,Text='庞德的灵魂被黑暗之王掳走，至今下落不明。而就在前不久，古城中举行了一场仪式，幕后主谋就是黑暗之王的手下迷幻之王！',Speaker=4273,}",
  [151583] = "{id=151583,Text='我们每多耽误一秒，庞德的处境就更危险一分。',Speaker=4273,}",
  [151584] = "{id=151584,Text='因此你前往古城墓地，准备开通传送通道。',Speaker=4273,}",
  [151585] = "{id=151585,Text='可是你现在却…',Speaker=4273,}",
  [151586] = "{id=151586,Text='（小声）看来这个时空的你正在执行一项艰巨任务，但是我们的目标是时间魔杖，你快套她的话，问问她知不知道。',Speaker=890741,}",
  [151587] = "{id=151587,Text='怎么会变成这样，你把我搞糊涂了。',Speaker=4273,Option='时间魔杖',}",
  [151588] = "{id=151588,Text='时间魔杖？那是什么？你就是为这个回来的？',Speaker=4273,Option='是的',Emoji=8,}",
  [151589] = "{id=151589,Text='是吗...听说昨天夜里，一个叫赛依连的剑士捡到了一把奇怪的法杖，你去问问他吧。',Speaker=4273,Option='谢谢你！',Emoji=6,}",
  [151590] = "{id=151590,Text='不用谢，你不要再跟那个奇怪的女人瞎跑了，快去执行任务！',Speaker=4273,}",
  [151591] = "{id=151591,Text='你说什么！我忍你很久了！',Speaker=890741,Option='算了算了',}",
  [151592] = "{id=151592,Text='哼，我们走。',Speaker=890741,}",
  [151593] = "{id=151593,Text='呦，[c][ffff00][PlayerName][-][/c]！我还以为你去执行任务了。',Speaker=4275,Option='时间魔杖',}",
  [151594] = "{id=151594,Text='什么魔杖？噢噢，说起来我是捡到一把奇怪的魔杖。',Speaker=4275,Option='太好了！',}",
  [151595] = "{id=151595,Text='不过已经不在我这了，哈哈哈。',Speaker=4275,Emoji=18,}",
  [151596] = "{id=151596,Text='刚才在路边看到一个奇怪的小男孩，古城里危机重重，我就把法杖送给他防身了。',Speaker=4275,}",
  [151597] = "{id=151597,Text='那个剑士看起来很强，怎么这么蠢！',Speaker=890741,Option='……',}",
  [151598] = "{id=151598,Text='我感受不到时间魔杖的能量了，它已经不在这个时空了，烦死了，只能明天再找了。',Speaker=890741,}",
  [151599] = "{id=151599,Text='你有见到马蒂吗？这小子又不见了。',Speaker=890741,Option='没有',}",
  [151600] = "{id=151600,Text='可能自己先回去了吧，哼，这小鬼。明天到广场上找我，不许迟到！',Speaker=890741,}",
  [151601] = "{id=151601,Text='[c][ffff00][PlayerName][-][/c]！',Speaker=890741,Option='抱歉来晚了',}",
  [151602] = "{id=151602,Text='别管这个了，马蒂失踪了！',Speaker=890741,Emoji=18,}",
  [151603] = "{id=151603,Text='昨天我以为他一个人先回来了，结果到处都找不到他！',Speaker=890741,Option='别急',}",
  [151604] = "{id=151604,Text='我...我才没急呢！不过是觉得有点奇怪。',Speaker=890741,Option='怎么了',}",
  [151605] = "{id=151605,Text='你仔细回忆一下，这两天他都很不对劲！',Speaker=890741,}",
  [151606] = "{id=151606,Text='第一天的时候我弄丢了时间魔杖，那是因为——',Speaker=890741,Option='{我的错,3},{你自己的错,3},{小马蒂的错,4}',}",
  [151607] = "{id=151607,Text='你是猪吗？再想想！',Speaker=890741,Emoji=12,}",
  [151608] = "{id=151608,Text='没错！然后我施法寻找时间魔杖，法术却出了差错，这肯定是施法材料的问题！',Speaker=890741,}",
  [151609] = "{id=151609,Text='你老实说，你给我的材料是怎么来的？',Speaker=890741,Option='{我买的,6},{我找来的,6},{小马蒂给的,7}',}",
  [151610] = "{id=151610,Text='我才不信呢，快给我说实话！',Speaker=890741,Emoji=12,}",
  [151611] = "{id=151611,Text='我就知道，你竟然敢骗我！',Speaker=890741,}",
  [151612] = "{id=151612,Text='第二天的时候，我们在梦罗克找到了时间魔杖，而且马上就要拿到了。',Speaker=890741,}",
  [151613] = "{id=151613,Text='但是你被人从后面偷袭，而我们又没看到对方的样子，这说明对方——',Speaker=890741,Option='{体型小,9},{是鬼魂,10}',}",
  [151614] = "{id=151614,Text='你的脑子被波利砸过吗？不对！',Speaker=890741,}",
  [151615] = "{id=151615,Text='没错，肯定是身型很小，所以很容易藏起来。',Speaker=890741,}",
  [151616] = "{id=151616,Text='最后是昨天，捡到魔杖的赛依连把魔杖送给了一个路边的小男孩。',Speaker=890741,}",
  [151617] = "{id=151617,Text='古城那么危险的地方怎么肯能会有小男孩！那一定是——',Speaker=890741,Option='{很强的小男孩,99},{小马蒂！,12}',}",
  [151618] = "{id=151618,Text='...你的脑袋一定能卖个好价钱，十成新，完全没用过！',Speaker=890741,}",
  [151619] = "{id=151619,Text='没错！这小鬼从一开始就在使坏！我怎么现在才发现，气死我了！',Speaker=890741,Option='怎么办',}",
  [151620] = "{id=151620,Text='我能感受到时间魔杖的能量，他应该还在普隆德拉，带上这个时间卷轴，它可以感应魔杖的能量，你去四处找找。',Speaker=890741,}",
  [151621] = "{id=151621,Text='呼，呼，太好了，我拿到时间魔杖了，先藏起来吧，可不能让七七姐发现了。',Speaker=890748,}",
  [151622] = "{id=151622,Text='这是小马蒂留下的痕迹，他往北边去了！',}",
  [151623] = "{id=151623,Text='我这么做是不是不太好...也许我应该直接向七七姐说？不行不行，她肯定不会同意的，我一定不能被她发现。',Speaker=890748,}",
  [151624] = "{id=151624,Text='这些能量比刚才强一些，他一定刚走没多久。',}",
  [151625] = "{id=151625,Text='呜...七七姐回到普隆德拉了，这样会被她发现的，我得离开这！',Speaker=890748,}",
  [151626] = "{id=151626,Text='小马蒂竟然一路向北出城了...先回去告诉七七吧。',}",
  [151627] = "{id=151627,Text='怎么样，找到小鬼了吗？',Speaker=890741,Option='说明情况',}",
  [151628] = "{id=151628,Text='向北出城了？他一个小鬼只身上路，肯定会去下一个城市，普隆德拉北边的城市是—',Speaker=890741,Option='{吉芬,14},{梦罗克,14},{伊斯鲁得,14},{艾尔帕兰,15}',}",
  [151629] = "{id=151629,Text='都什么时候了，你脑子不能清醒一点吗！',Speaker=890741,Emoji=12,}",
  [151630] = "{id=151630,Text='没错，肯定是艾尔帕兰，不知道这个时间线的你有没有去过，那里是我们三个初次相遇的地方！',Speaker=890741,}",
  [151631] = "{id=151631,Text='我真是被搞糊涂了，上次小马蒂拿了我的魔杖是去修理艾尔帕兰的大钟，这次又是怎么了？你有什么头绪吗？',Speaker=890741,Option='{大钟又坏了,17},{马蒂变坏了,18},{我也不知道,19}',}",
  [151632] = "{id=151632,Text='不，我去看过，没有坏。',Speaker=890741,}",
  [151633] = "{id=151633,Text='要真是那样就好了，可惜他是一个正义到令人作呕的小鬼！',Speaker=890741,}",
  [151634] = "{id=151634,Text='啊啊，烦死了！你真是一点都派不上用场！',Speaker=890741,Emoji=12,}",
  [151635] = "{id=151635,Text='哼，在这瞎想也没有用，还是当面向那小鬼问清楚好了。',Speaker=890741,Option='就这么办',}",
  [151636] = "{id=151636,Text='明天来找我，出发去艾尔帕兰！',Speaker=890741,}",
  [151637] = "{id=151637,Text='[c][ffff00][PlayerName][-][/c]，我们现在就走吧，去艾尔帕兰！你准备好了吗？',Speaker=890741,Option='{好了,3},{还没好,2}',}",
  [151638] = "{id=151638,Text='一定要把那小鬼抓出来好好教训一顿！',Speaker=890741,}",
  [151639] = "{id=151639,Text='又回到这个地方了，这个第一次与你和那小鬼相遇的地方，当时的情形你还记得吗？',Speaker=890741,Option='{记得,66},{不记得,66}',}",
  [151640] = "{id=151640,Text='不管记不记得都无所谓，反正你现在是我的仆人。',Speaker=890741,}",
  [151641] = "{id=151641,Text='开始找那小鬼吧，拿着时间卷轴去城里四处找找吧。',Speaker=890741,}",
  [151642] = "{id=151642,Text='没有小马蒂的踪影，但是能感受到时间魔法的波动…',}",
  [151643] = "{id=151643,Text='时间魔法的波动减弱了，完全找不到小马蒂的踪迹…',}",
  [151644] = "{id=151644,Text='彻底找不到时间魔法的波动了，回去问问七七吧。',}",
  [151645] = "{id=151645,Text='怎么样，有收获吗？',Speaker=890741,Option='说明情况',}",
  [151646] = "{id=151646,Text='魔杖能量消失…',Speaker=890741,Emoji=14,}",
  [151647] = "{id=151647,Text='我明白了，艾尔帕兰的钟楼有着比时间魔杖更强大的能量波动，所以我们没办法探测到魔杖的能量了。',Speaker=890741,Option='怎么办',}",
  [151648] = "{id=151648,Text='哼，我大概能猜到他在哪，我每次来艾尔帕兰都是在河边找到他的。[c][ffff00][PlayerName][-][/c]，去河边碰头！',Speaker=890741,}",
  [151649] = "{id=151649,Text='喂！臭小子！我知道你在这！快给我滚出来！',Speaker=890741,}",
  [151650] = "{id=151650,Text='看来你是铁了心要躲着我了，那就别怪我了！[c][ffff00][PlayerName][-][/c]，你过来！',Speaker=890741,}",
  [151651] = "{id=151651,Text='很好，我的仆人，发挥你作用的时候到了。',Speaker=890741,Option='怎么做',}",
  [151652] = "{id=151652,Text='马蒂！你再不出来，这家伙就没命啦！',Speaker=890741,Option='？？？',Emoji=1,}",
  [151653] = "{id=151653,Text='你闭嘴！',Speaker=890741,}",
  [151654] = "{id=151654,Text='我数到三！',Speaker=890741,}",
  [151655] = "{id=151655,Text='一！',Speaker=890741,Emoji=37,}",
  [151656] = "{id=151656,Text='二！',Speaker=890741,}",
  [151657] = "{id=151657,Text='七七姐住手！',Speaker=890752,}",
  [151658] = "{id=151658,Text='你总算愿意出来了。',Speaker=890741,}",
  [151659] = "{id=151659,Text='这些都是幻象，我没打算对他做什么。',Speaker=890741,}",
  [151660] = "{id=151660,Text='呜…',Speaker=890752,Emoji=18,}",
  [151661] = "{id=151661,Text='说！时间魔杖是不是在你那！',Speaker=890741,}",
  [151662] = "{id=151662,Text='是的…',Speaker=890752,}",
  [151663] = "{id=151663,Text='果然是你！哼，我知道你未来会成为一名正义的勇者，没想到现在已经看我这个魔女不爽，要跟我作对了！',Speaker=890741,}",
  [151664] = "{id=151664,Text='不是这样的！七七姐你误会了！',Speaker=890752,Emoji=42,}",
  [151665] = "{id=151665,Text='呜呜呜...不是这样的...七七姐…',Speaker=890752,Option='怎么回事',}",
  [151666] = "{id=151666,Text='我已经很久没见到七七姐了，这次她来找我玩我很开心。',Speaker=890752,}",
  [151667] = "{id=151667,Text='我舍不得七七姐走，所以我想...把时间魔杖藏起来，她就回不去未来，就可以多陪我玩一玩了…',Speaker=890752,}",
  [151668] = "{id=151668,Text='我好喜欢七七姐啊呜呜呜...',Speaker=890752,Option='原来如此',}",
  [151669] = "{id=151669,Text='可是现在，七七姐被我气跑了，她连时间魔杖都不要了！呜呜呜呜呜！',Speaker=890752,Option='别哭了',Emoji=42,}",
  [151670] = "{id=151670,Text='现在该怎么办啊…',Speaker=890752,Option='{去道歉,5},{找个新朋友,6},{忘了她吧,6}',}",
  [151671] = "{id=151671,Text='呜呜哇啊啊啊啊啊！',Speaker=890752,Emoji=42,}",
  [151672] = "{id=151672,Text='小马蒂哭得更厉害了…',}",
  [151673] = "{id=151673,Text='嗯，我也想向七七姐道歉，可是她现在在哪呢…',Speaker=890752,Option='时间魔杖',}",
  [151674] = "{id=151674,Text='对啊！用时间魔杖发出时间魔法的能量，七七姐就会被吸引过来了！',Speaker=890752,}",
  [151675] = "{id=151675,Text='时间魔杖被我藏在钟楼里了，我们在那见面！',Speaker=890752,}",
  [151676] = "{id=151676,Text='[c][ffff00][PlayerName][-][/c]，我好紧张啊，万一七七姐还不原谅我...你觉得我应该继续吗？',Speaker=890752,Option='{继续,7},{等等吧,8}',}",
  [151677] = "{id=151677,Text='好的！我要使用时间魔杖了！',Speaker=890752,}",
  [151678] = "{id=151678,Text='钟楼里汇集了强大的时间魔法能量，就算是我也可以使用这魔杖了。七七姐，你快出现吧，求你了。',Speaker=890752,}",
  [151681] = "{id=151681,Text='你们两个是白痴吗！脑子在运河里泡过吗！在钟楼使用时间魔杖肯定会招来这里的魔物的！[c][ffff00][PlayerName][-][/c]你就是这么带孩子的吗！',Speaker=890741,Emoji=12,}",
  [151682] = "{id=151682,Text='七七姐！原来你一直在我们身边，只是用魔法藏起来了吧！',Speaker=890752,}",
  [151683] = "{id=151683,Text='...你这小子就是这点惹人烦，在不恰当的时机变聪明。',Speaker=890741,Option='为什么藏起来',}",
  [151684] = "{id=151684,Text='这小子在运河边说了那种话，我怎么好意思现身！',Speaker=890741,Emoji=18,}",
  [151685] = "{id=151685,Text='哈哈哈，七七姐害羞了！那你原谅我了吧？',Speaker=890752,}",
  [151686] = "{id=151686,Text='我可没这么说！哼，我要去普隆德拉继续游玩了，你要是非要跟来，我也没办法。',Speaker=890741,}",
  [151687] = "{id=151687,Text='七七姐，等等我嘛！',Speaker=890752,}",
  [151688] = "{id=151688,Text='……',}",
  [151689] = "{id=151689,Text='哎呦！',Speaker=890741,}",
  [151690] = "{id=151690,Text='我隐约听到了小孩子的哭泣声，好在意啊，可是我现在没时间，要是有人能替我去看看就好了。',Speaker=890742,}",
  [151691] = "{id=151691,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=890742,Option='{我去吧,2},{没时间,3}',}",
  [151692] = "{id=151692,Text='那就拜托你了。',Speaker=890742,}",
  [151693] = "{id=151693,Text='[c][ffff00][PlayerName][-][/c]，你好啊。',Speaker=890753,}",
  [151694] = "{id=151694,Text='不认识我？没关系，我认识你，我可一直都在关注你哦。',Speaker=890753,}",
  [151695] = "{id=151695,Text='为了心中的爱踏上旅途，为了守护展开冒险。',Speaker=890753,}",
  [151696] = "{id=151696,Text='现在，RO三岁了，感谢你在这三年间做出的贡献，这份礼物是一点心意，请你收下吧。',Speaker=890753,}",
  [151697] = "{id=151697,Text='啾啾~新年！新年！',Speaker=890754,}",
  [151698] = "{id=151698,Text='啾啾~当钟声响起，过去的都已过去，是结束也是开始。',Speaker=890754,}",
  [151699] = "{id=151699,Text='元旦快乐！元旦快乐！啾~~~~',Speaker=890754,}",
  [151700] = "{id=151700,Text='好神奇的鸟...咦？地上有什么东西。',}",
  [151717] = "{id=151717,Text='春天的气息在复苏，阳光洒落花田，细雨滋润大地...少女的心在悸动！',Speaker=890760,Emoji=27,}",
  [151718] = "{id=151718,Text='冒险者！你准备好向心选之人表达爱意了吗！',Speaker=890760,Option='{准备就绪！,4},{还没...,5}',}",
  [151719] = "{id=151719,Text='迈出勇敢的一步，不要怂！',Speaker=890760,}",
  [151720] = "{id=151720,Text='这就对了！这才是我们的冒险者！心之所向，全世界都会为你加油！收下这份礼物吧，祝你幸福！',Speaker=890760,}",
  [151721] = "{id=151721,Text='时光如水，岁月如歌——',Speaker=890761,Option='少废话',}",
  [151722] = "{id=151722,Text='呃...冒险者？',Speaker=890761,Option='红包拿来！',}",
  [151723] = "{id=151723,Text='怎么这样...人家想了好久的祝贺词...让我说完嘛...',Speaker=890761,Emoji=42,}",
  [151725] = "{id=151725,Text='新年好！[c][ffff00][PlayerName][-][/c]，[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】。[-][/c]',Speaker=890761,Option='{好的,2},{我再想想,3}',}",
  [151726] = "{id=151726,Text='亲爱的[c][ffff00][PlayerName][-][/c]，[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】。[-][/c]',Speaker=890760,Option='{好的,2},{我再想想,3}',}",
  [151729] = "{id=151729,Text='当一年中最圆的月亮高悬天际，当团圆的灯火照亮大街小巷——',Speaker=890763,Action={actionid=91,num=1},}",
  [151730] = "{id=151730,Text='——本小姐就会闪亮登场！',Speaker=890763,Emoji=17,}",
  [151731] = "{id=151731,Text='元宵节快乐！冒险者~',Speaker=890763,Option='节日快乐',}",
  [151732] = "{id=151732,Text='我的现身不止会为大家送去祝福，还为每一个冒险者送上礼物呦~',Speaker=890763,}",
  [151733] = "{id=151733,Text='前提是，你能猜对我出的谜题，元宵灯谜~',Speaker=890763,}",
  [151734] = "{id=151734,Text='该任务账号绑定，只可以完成一次哦。',Speaker=890763,Option='{好,2},{没时间,3}',}",
  [151735] = "{id=151735,Text='礼物可是很棒的！你可别后悔~',Speaker=890763,}",
  [151736] = "{id=151736,Text='那我们快开始吧！',Speaker=890763,}",
  [151737] = "{id=151737,Text='第一题：冒险伊始，乐园之南。谜底是一个地名呦~',Speaker=890763,Option='{艾尔帕兰,4},{朱诺,4},{普隆德拉南门,0}',}",
  [151738] = "{id=151738,Text='第二题：粉粉嫩嫩，软软糯糯，蹦蹦跳跳，可可爱爱。谜底是一种很受欢迎的魔物呦~',Speaker=890763,Option='{波利,0},{达拉蛙,5},{溜溜猴,5}',}",
  [151739] = "{id=151739,Text='第三题：红红脸，圆又圆，喀嚓一口脆又甜。谜底是波利最爱吃的东西呦~',Speaker=890763,Option='{梨,6},{苹果,0},{葡萄,6}',}",
  [151740] = "{id=151740,Text='全部正确！你可真厉害！代表月亮表扬你！收下礼物吧，这是你应得的，另外不要忘了下个时段再来挑战哦~',Speaker=890763,}",
  [151741] = "{id=151741,Text='可惜，就差一点！代表月亮安慰你！别灰心，收下这份礼物，下个时段再来挑战！',Speaker=890763,}",
  [151742] = "{id=151742,Text='元宵节快乐~冒险者~',Speaker=890763,Action={actionid=91,num=1},}",
  [151743] = "{id=151743,Text='时间有限，我们快来进行新一轮的猜谜吧~',Speaker=890763,Option='好',}",
  [151744] = "{id=151744,Text='第一题：时间之砂缓缓落下，运河风光旖旎动人。谜底是一个地名呦~',Speaker=890763,Option='{艾尔帕兰,0},{朱诺,4},{普隆德拉南门,4}',}",
  [151745] = "{id=151745,Text='第二题：无心之人。谜底是一位城邦的主人呦~',Speaker=890763,Option='{雷藏,5},{大总统,5},{海拉,0}',}",
  [151746] = "{id=151746,Text='第三题：有黑也有白，同甘又共苦。谜底是艾尔帕兰的一种特产呦~',Speaker=890763,Option='{苹果汁,6},{枫糖咖啡,0},{暖身料理,6}',}",
  [151747] = "{id=151747,Text='全部正确！你可真厉害！代表月亮表扬你！收下礼物吧，这是你应得的，另外不要忘了下个时段再来挑战哦~',Speaker=890763,}",
  [151748] = "{id=151748,Text='可惜，就差一点！代表月亮安慰你！别灰心，收下这份礼物，下个时段再来挑战！',Speaker=890763,}",
  [151749] = "{id=151749,Text='元宵节快乐~冒险者~',Speaker=890763,Action={actionid=91,num=1},}",
  [151750] = "{id=151750,Text='时间有限，我们快来进行新一轮的猜谜吧~',Speaker=890763,Option='好',}",
  [151751] = "{id=151751,Text='第一题：两只耳朵长又尖，短短尾巴翘后边，两只眼睛像玛瑙，一张嘴巴分三片。谜底是一种可爱的魔物呦~',Speaker=890763,Option='{疯兔,0},{魔菇,4},{白鼠,4}',}",
  [151752] = "{id=151752,Text='第二题：仓储传送都会，居家旅行必备。谜底是常常帮助玩家的服务人员呦~',Speaker=890763,Option='{珠宝商人,5},{符文专家,5},{卡普拉小姐姐,0}',}",
  [151753] = "{id=151753,Text='第三题：长相俊俏，爱舞爱跳，想回家时，它就来到。谜底是冒险中常用的道具呦~',Speaker=890763,Option='{苍蝇翅膀,6},{蝴蝶翅膀,0},{冒险家硬币,6}',}",
  [151754] = "{id=151754,Text='全部正确！你可真厉害！代表月亮表扬你！收下礼物吧，这是你应得的，另外不要忘了下个时段再来挑战哦~',Speaker=890763,}",
  [151755] = "{id=151755,Text='可惜，就差一点！代表月亮安慰你！别灰心，收下这份礼物，下个时段再来挑战！',Speaker=890763,}",
  [151756] = "{id=151756,Text='元宵节快乐~冒险者~',Speaker=890763,Action={actionid=91,num=1},}",
  [151757] = "{id=151757,Text='时间有限，我们快来进行新一轮的猜谜吧~',Speaker=890763,Option='好',}",
  [151758] = "{id=151758,Text='第一题：身披一件金丝绒，聪明美丽惹人宠，手是脚来脚是手，爬高上树好威风。谜底是一种可爱的魔物呦~',Speaker=890763,Option='{溜溜猴,0},{克瑞米,4},{魔锅蛋,4}',}",
  [151759] = "{id=151759,Text='第二题：地下喷水水清清,日日夜夜流不停。谜底是普隆德拉的一处著名景点呦~',Speaker=890763,Option='{冒险者公会,5},{大教堂,5},{广场喷泉,0}',}",
  [151760] = "{id=151760,Text='第三题：一张薄饼圆又圆，一条小沟盘上面，针尖沿着沟儿走，动人歌声唱不完。谜底是冒险中常见的道具呦~',Speaker=890763,Option='{结晶石,6},{唱片,0},{枫之印记,6}',}",
  [151761] = "{id=151761,Text='全部正确！你可真厉害！代表月亮表扬你！收下礼物吧，这是你应得的，另外不要忘了下个时段再来挑战哦~',Speaker=890763,}",
  [151762] = "{id=151762,Text='可惜，就差一点！代表月亮安慰你！别灰心，收下这份礼物，下个时段再来挑战！',Speaker=890763,}",
  [151763] = "{id=151763,Text='冒险者，这是我最后的谜题了！',Speaker=890763,Action={actionid=91,num=1},}",
  [151764] = "{id=151764,Text='破解谜题，收下我最后的礼物吧~',Speaker=890763,Option='好',}",
  [151765] = "{id=151765,Text='第一题：黑漆漆，叫喵喵，不抓老鼠不吃鱼，只爱玩zeny。这题这么简单，不用提示了吧~',Speaker=890763,Option='{B格猫,0},{B格猫,0},{B格猫,0}',}",
  [151766] = "{id=151766,Text='第二题：载人不喊累，有翼不会飞。谜底是一种坐骑呦~',Speaker=890763,Option='{羊驼,5},{地龙,5},{大嘴鸟,0}',}",
  [151767] = "{id=151767,Text='第三题：拳打南门绿棉虫，脚踢斐扬大脚熊。嘻嘻嘻，这个也不用提示了吧~',Speaker=890763,Option='{冒险者,0},{英雄,0},{正是本人,0}',}",
  [151768] = "{id=151768,Text='全部正确！你可真厉害！代表月亮表扬你！收下礼物吧，这是你应得的。今年的元宵猜谜结束啦，不要怀恋我，明年再见呦~',Speaker=890763,}",
  [151769] = "{id=151769,Text='可惜，就差一点！代表月亮安慰你！别灰心，收下这份礼物，明年再见呦~',Speaker=890763,}",
  [152005] = "{id=152005,Text='谢谢，谢谢大家的祝福，哦？连冒险者都来了，你也是来祝福我的吗？',Speaker=890799,Option='发生了什么',}",
  [152006] = "{id=152006,Text='呜呜呜，我太感动了，我马上就要结婚了！',Speaker=890799,Emoji=27,}",
  [152007] = "{id=152007,Text='这都多亏了那个女孩的帮助！',Speaker=890799,Option='什么女孩？',}",
  [152008] = "{id=152008,Text='[c][ffff00]该任务同账号仅限一个角色接取，请谨慎考虑是否接取。[-][/c]',Speaker=890799,Option='{好啊,2},{没时间,3}',}",
  [152009] = "{id=152009,Text='就是站在那里的女孩，她简直就是天使！',Speaker=890799,}",
  [152010] = "{id=152010,Text='呵呵，看看那个未来的新郎，多么美好。',Speaker=890800,}",
  [152011] = "{id=152011,Text='你问我是谁？',Speaker=890800,Action={actionid=9,num=1},}",
  [152012] = "{id=152012,Text='爱与正义的化身，爱与和平的使者，爱与希望的曙光...怎样都好，你就叫我，爱之代理人吧。',Speaker=890800,Option='不是很懂',}",
  [152013] = "{id=152013,Text='...你这个冒险者，明明看起来很聪明。',Speaker=890800,Emoji=14,}",
  [152014] = "{id=152014,Text='既然如此，就让你来参与到我的工作中吧，这样你就能认识到爱之代理人的伟大之处了。',Speaker=890800,Option='好麻烦啊',}",
  [152015] = "{id=152015,Text='我同意你的想法，确实很麻烦。',Speaker=890800,Action={actionid=206,num=1},}",
  [152016] = "{id=152016,Text='可是，想想那对新人，他们脸上幸福的笑容就是最好的回报，你不想体验一下这种感觉吗？',Speaker=890800,Option='好吧',}",
  [152017] = "{id=152017,Text='这就对了！第一天先来点简单的，我们在东边的小路碰头！',Speaker=890800,}",
  [152018] = "{id=152018,Text='[c][ffff00][PlayerName][-][/c]，爱之代理人的工作内容有很多，而你的第一个任务就是去促成一对美好的缘分。',Speaker=890800,Option='要怎么做',}",
  [152019] = "{id=152019,Text='我身边的这位青年正陷于爱的烦恼之中，跟他谈谈吧~',Speaker=890800,}",
  [152020] = "{id=152020,Text='你、你好，冒险者，刚才这位自称爱之代理人的小姐说你们会帮助我，是真的吗？',Speaker=890809,Option='嗯',}",
  [152021] = "{id=152021,Text='谢谢，你们真是好人！',Speaker=890809,Option='……',}",
  [152022] = "{id=152022,Text='其实我...有一个暗恋了很久的女孩子…',Speaker=890809,}",
  [152023] = "{id=152023,Text='她永远充满活力，热情四射，简直就是我心中的小太阳！',Speaker=890809,Option='是谁',Emoji=29,}",
  [152024] = "{id=152024,Text='她就是...艾、艾莉儿...',Speaker=890809,}",
  [152025] = "{id=152025,Text='音乐少女艾莉儿？小伙子眼光不错嘛~',Speaker=890800,}",
  [152026] = "{id=152026,Text='可是...我没勇气向她诉说心意，我真没用…',Speaker=890809,Emoji=24,}",
  [152027] = "{id=152027,Text='哎呀哎呀，[c][ffff00][PlayerName][-][/c]，快安慰他一下。',Speaker=890800,}",
  [152028] = "{id=152028,Text='呜，谢谢，我感觉好些了，我能从你身上感受到勇气，真神奇。',Speaker=890809,}",
  [152029] = "{id=152029,Text='做的不错嘛，[c][ffff00][PlayerName][-][/c]，这么快就有成果了。',Speaker=890800,}",
  [152030] = "{id=152030,Text='可我还是不敢去告白…',Speaker=890809,}",
  [152031] = "{id=152031,Text='这样可不行，[c][ffff00][PlayerName][-][/c]，还得再帮帮他。',Speaker=890800,Option='怎么办',}",
  [152032] = "{id=152032,Text='呵呵，放心，我遇到过这种情况。',Speaker=890800,Action={actionid=203,num=1},}",
  [152033] = "{id=152033,Text='一瓶勇气药剂，包你药到病除。',Speaker=890800,Emoji=20,}",
  [152034] = "{id=152034,Text='去找艾尔帕兰炼金工会的赫恩肖吧，我是他的老顾客了，今天本来就是我们约好了取货的日子。',Speaker=890800,}",
  [152035] = "{id=152035,Text='我可以把你传送过去，准备好了吗？',Speaker=890800,Option='嗯',}",
  [152036] = "{id=152036,Text='你好，想成为炼金师吗？',Speaker=890801,Option='勇气药剂',}",
  [152037] = "{id=152037,Text='爱之代理人让你来的？我们确实约好了今天交货，但是她可没说派别人来，我不能就这么把东西交给你。',Speaker=890801,Option='……',}",
  [152038] = "{id=152038,Text='我问你，爱之代理人是一位——',Speaker=890801,Option='{温柔的小姐,2},{成熟的大叔,3}',}",
  [152039] = "{id=152039,Text='你根本不认识爱之代理人，骗子！',Speaker=890801,}",
  [152040] = "{id=152040,Text='她喜欢穿什么颜色的衣服？',Speaker=890801,Option='{蓝色,4},{红色,5}',}",
  [152041] = "{id=152041,Text='嗯，看来确实是她叫你来的，给，这是说好的药剂，记得替我向她问好~',Speaker=890801,Emoji=17,}",
  [152042] = "{id=152042,Text='拿到勇气药剂了吗？',Speaker=890800,Option='拿到了',}",
  [152043] = "{id=152043,Text='好，去他身边，趁他不注意把药剂洒出去！',Speaker=890800,Option='？？？',}",
  [152044] = "{id=152044,Text='你那是什么表情？这药剂就是外用的。',Speaker=890800,Option='好吧…',}",
  [152045] = "{id=152045,Text='我...明白了，再怎么烦恼也无济于事，我要像艾莉儿那样勇往直前，我要向她表达我的心意！',Speaker=890809,}",
  [152047] = "{id=152047,Text='艾莉儿！',Speaker=890809,}",
  [152048] = "{id=152048,Text='是谁在呼唤我~',Speaker=801222,Emoji=17,}",
  [152049] = "{id=152049,Text='是我！',Speaker=890809,}",
  [152050] = "{id=152050,Text='啊，是你，常来听我唱歌的男孩，有事吗？',Speaker=801222,}",
  [152051] = "{id=152051,Text='我、我……',Speaker=890809,}",
  [152052] = "{id=152052,Text='嗯？',Speaker=801222,Emoji=8,}",
  [152053] = "{id=152053,Text='我喜欢你！艾莉儿！可以做我的女朋友吗！',Speaker=890809,}",
  [152054] = "{id=152054,Text='...我...我竟然说出来了…',Speaker=890809,}",
  [152055] = "{id=152055,Text='！',Speaker=801222,Emoji=7,}",
  [152056] = "{id=152056,Text='诶？这个...这个...谢谢你，不过，我还要追逐我的梦想，还没有那种心思，所以抱歉，我不能答应你。',Speaker=801222,}",
  [152057] = "{id=152057,Text='这样啊...那也没办法，谢谢你回应我的感情，祝你...在梦想之路上一帆风顺！',Speaker=890809,}",
  [152058] = "{id=152058,Text='他哭着跑走了…',}",
  [152059] = "{id=152059,Text='[c][ffff00][PlayerName][-][/c]，恭喜你完成了第一次的工作。',Speaker=890800,Option='失败了吧？',Action={actionid=9,num=1},}",
  [152060] = "{id=152060,Text='不是呦。',Speaker=890800,}",
  [152061] = "{id=152061,Text='我们的工作是帮助那些在爱情中陷入苦恼的人，并不是给他们带去爱情。',Speaker=890800,}",
  [152062] = "{id=152062,Text='爱情这件事，别人是帮不上忙的呀。你明白了吗？',Speaker=890800,Option='好像懂了。',}",
  [152063] = "{id=152063,Text='呵呵，今天就到这里吧，明天再来找我呦。',Speaker=890800,}",
  [152064] = "{id=152064,Text='[c][ffff00][PlayerName][-][/c]，还在介意昨天的事吗？',Speaker=890800,Option='{嗯,0},{没有,0}',}",
  [152065] = "{id=152065,Text='呵呵，今天的工作会很开心哦。',Speaker=890800,Option='什么？',}",
  [152066] = "{id=152066,Text='有一对新人今天要举办婚礼了！',Speaker=890800,Action={actionid=39,num=1},}",
  [152067] = "{id=152067,Text='不过，这对新人囊中羞涩，他们的婚礼没有嘉宾，没有司仪，甚至没有神父，我们今天的工作就是——为她们的婚礼增添气氛！',Speaker=890800,}",
  [152068] = "{id=152068,Text='怎么样，是不是一份开心的工作~',Speaker=890800,Option='嗯',}",
  [152069] = "{id=152069,Text='那我们在教堂碰头~',Speaker=890800,}",
  [152070] = "{id=152070,Text='那边的就是新郎和新娘，唔...是有点冷清呢。你先去跟他们聊一聊吧。',Speaker=890800,}",
  [152071] = "{id=152071,Text='你就是来帮助我们的冒险者吗，今天要麻烦你了。',Speaker=890802,}",
  [152072] = "{id=152072,Text='冒险者，你愿意帮助我们可真好，谢谢你。',Speaker=890803,Option='不客气',}",
  [152073] = "{id=152073,Text='我们的婚礼确实有点冷清，如果能热闹一点就好了。',Speaker=890802,}",
  [152074] = "{id=152074,Text='现场也有点朴素，要是再华丽一点就好了。',Speaker=890803,Option='好的',}",
  [152075] = "{id=152075,Text='看着他们幸福的样子，心情都变得明媚了。',Speaker=890800,Option='说明情况',}",
  [152076] = "{id=152076,Text='呵呵，真是朴实的愿望啊，好久没有遇到了。那我们开始吧！',Speaker=890800,Option='怎么做',}",
  [152077] = "{id=152077,Text='先要让现场热闹起来，我找找…',Speaker=890800,}",
  [152078] = "{id=152078,Text='有了！用这个吧！拿着这个魔法卷轴，在大堂里召唤爱之天使~',Speaker=890800,}",
  [152079] = "{id=152079,Text='干得不错，接着要让大堂变得华丽！我已经提前在教堂中撒下了花灵之种，只要用那个就可以爆发出花雨~',Speaker=890800,}",
  [152080] = "{id=152080,Text='很好，你已经是一个熟练工了，有没有考虑过接我的班呀？',Speaker=890800,Option='……',}",
  [152081] = "{id=152081,Text='呵呵呵，我是开玩笑的，别在意。',Speaker=890800,Emoji=17,}",
  [152082] = "{id=152082,Text='好了，准备完毕，只剩最后一步了，在那之前——',Speaker=890800,Action={actionid=203,num=1},}",
  [152083] = "{id=152083,Text='我分给你一些祝福之力，在时机合适的时候，你只需要打一下响指——就能给他们祝福~',Speaker=890800,}",
  [152084] = "{id=152084,Text='现在，先去见证他们的感情吧！',Speaker=890800,}",
  [152085] = "{id=152085,Text='亲爱的，你愿意娶我为妻吗？',Speaker=890803,}",
  [152086] = "{id=152086,Text='是的，我愿意',Speaker=890802,}",
  [152087] = "{id=152087,Text='无论我将来富有还是贫穷，无论我将来身体健康还是疾病缠身，你都愿意和我永远在一起吗？',Speaker=890803,}",
  [152088] = "{id=152088,Text='是的，我愿意',Speaker=890802,}",
  [152089] = "{id=152089,Text='亲爱的，你愿意嫁给我吗？',Speaker=890802,}",
  [152090] = "{id=152090,Text='是的，我愿意',Speaker=890803,}",
  [152091] = "{id=152091,Text='无论我将来富有还是贫穷，无论我将来身体健康还是疾病缠身，你都愿意和我永远在一起吗？',Speaker=890802,}",
  [152092] = "{id=152092,Text='是的，我愿意',Speaker=890803,}",
  [152093] = "{id=152093,Text='就是现在了，送上祝福吧！',Option='打响指',}",
  [152094] = "{id=152094,Text='谢谢你，冒险者！',Speaker=890803,}",
  [152095] = "{id=152095,Text='谢谢你，冒险者！',Speaker=890802,}",
  [152096] = "{id=152096,Text='圆满结束！干得漂亮！',Speaker=890800,Emoji=20,}",
  [152097] = "{id=152097,Text='今天到此结束，明天来广场上找我哦~',Speaker=890800,}",
  [152098] = "{id=152098,Text='[c][ffff00][PlayerName][-][/c]！突发情况！',Speaker=890800,Option='怎么了？',}",
  [152099] = "{id=152099,Text='十万火急，先别问了！西边街道碰头！',Speaker=890800,}",
  [152100] = "{id=152100,Text='来这边，嘘！藏好！',Speaker=890800,Emoji=40,}",
  [152101] = "{id=152101,Text='好啦，别不开心啦，我保证没有下次了！',Speaker=890804,}",
  [152102] = "{id=152102,Text='唉，你总是这么花心，你让我怎么相信你。',Speaker=890805,}",
  [152103] = "{id=152103,Text='宝贝，我对天发誓，我以后一定对你一心一意！',Speaker=890804,}",
  [152104] = "{id=152104,Text='好吧...那我就相信你。',Speaker=890805,}",
  [152105] = "{id=152105,Text='嗯，爱你！',Speaker=890804,}",
  [152106] = "{id=152106,Text='那，我就去上班了，你在家等我吧。',Speaker=890805,}",
  [152107] = "{id=152107,Text='呼，总算搞定了，真麻烦。',Speaker=890804,}",
  [152108] = "{id=152108,Text='真是经典的对白啊，一时间我甚至以为在看老旧的舞台剧。',Speaker=890800,Option='？',}",
  [152109] = "{id=152109,Text='一会你就明白了，看，他走了，我们悄悄跟上！',Speaker=890800,}",
  [152110] = "{id=152110,Text='今天...就去喷泉旁逛逛吧，希望能交好运嘻嘻嘻~',Speaker=890804,}",
  [152111] = "{id=152111,Text='啊哈！那边有一位美丽的小姐，我来了！',Speaker=890804,}",
  [152112] = "{id=152112,Text='嗨~美丽的小姐，今天有没有空呀~',Speaker=890804,Action={actionid=32,num=1},}",
  [152113] = "{id=152113,Text='嘿嘿，状态不错，今天的我也光彩照人！',Speaker=890804,}",
  [152114] = "{id=152114,Text='这个渣男！',Speaker=890800,}",
  [152115] = "{id=152115,Text='太可恨了，这种人太可恨了！',Speaker=890800,Option='就是',}",
  [152116] = "{id=152116,Text='作为爱之代理人，必须给他惩罚！',Speaker=890800,Option='就是就是',}",
  [152117] = "{id=152117,Text='今天没有爱之代理人的工作，只有兴趣使然的正义执行！',Speaker=890800,Option='好！',}",
  [152118] = "{id=152118,Text='今天你就站在旁边不要动，放着我来！',Speaker=890800,}",
  [152119] = "{id=152119,Text='小姐，要不要来我家喝一杯悠闲的下午茶呀~',Speaker=890804,}",
  [152120] = "{id=152120,Text='还想喝茶？做梦去吧！',Speaker=890800,}",
  [152121] = "{id=152121,Text='咦？这是哪？我不是在上班吗？',Speaker=890805,Emoji=8,}",
  [152122] = "{id=152122,Text='嘿嘿嘿，小姐不要害羞嘛，快跟我一起走吧~',Speaker=890804,}",
  [152123] = "{id=152123,Text='……混蛋，你在做什么！！',Speaker=890805,}",
  [152124] = "{id=152124,Text='宝贝？？你、你怎么在这？你不是去上班了吗？',Speaker=890804,Emoji=1,}",
  [152125] = "{id=152125,Text='我再也不会相信你了，我再也不想见到你！',Speaker=890805,}",
  [152126] = "{id=152126,Text='呜呜呜……',Speaker=890805,}",
  [152127] = "{id=152127,Text='宝贝！别走！我还爱你！',Speaker=890804,}",
  [152128] = "{id=152128,Text='太不要脸了！[c][ffff00][PlayerName][-][/c]，拦住他！',Speaker=890800,}",
  [152129] = "{id=152129,Text='呃，可恶，你们是谁啊，碍事的家伙！',Speaker=890804,}",
  [152130] = "{id=152130,Text='诶！美丽的小姐，别走啊，等等我~~~',Speaker=890804,}",
  [152131] = "{id=152131,Text='呼，舒服了。',Speaker=890800,Option='舒服了',}",
  [152132] = "{id=152132,Text='今天就到这吧，明天记得来找我呦~',Speaker=890800,}",
  [152133] = "{id=152133,Text='[c][ffff00][PlayerName][-][/c]，你还记得昨天发生了什么吗？',Speaker=890800,Option='{记得,2},{不记得,3}',}",
  [152134] = "{id=152134,Text='嗯？再给你一次机会，好好想想。',Speaker=890800,Emoji=5,}",
  [152135] = "{id=152135,Text='那就好，让我们愉快地开始今天的工作吧！',Speaker=890800,Option='……',}",
  [152136] = "{id=152136,Text='今天的工作...啊，不太简单呢，来墓园碰头吧。',Speaker=890800,}",
  [152137] = "{id=152137,Text='我不太喜欢这里，虽然很宁静，但总有一种悲伤的气息。',Speaker=890800,Option='怎么了',}",
  [152138] = "{id=152138,Text='你看。',Speaker=890800,}",
  [152139] = "{id=152139,Text='嗨，我又来了。',Speaker=890807,}",
  [152140] = "{id=152140,Text='今天是个重要的日子，不是吗？我们就是在这一天相识的。',Speaker=890807,Action={actionid=206,num=1},}",
  [152141] = "{id=152141,Text='也是在这一天...分离的。',Speaker=890807,}",
  [152142] = "{id=152142,Text='自从你离开这个世界，已经过去一年了，我变强了很多哦，你看看我现在的样子。',Speaker=890807,}",
  [152143] = "{id=152143,Text='我现在已经能够保护你了。',Speaker=890807,}",
  [152144] = "{id=152144,Text='哦，太感人了…',Speaker=890800,Option='嘘',}",
  [152145] = "{id=152145,Text='这一年，我去了我们曾经走过的街道，去了我们曾并肩战斗的荒野，去了你最爱的料理店，那些地方都没变，只是...少了你…',Speaker=890807,}",
  [152146] = "{id=152146,Text='唉，可怜的人，陷入失去爱人的伤痛中无法自拔，我们今天的工作就是帮助他振作起来。',Speaker=890800,Option='怎么做',}",
  [152147] = "{id=152147,Text='忘记旧爱的最快途径...就是结识新欢了吧。',Speaker=890800,Action={actionid=9,num=1},}",
  [152148] = "{id=152148,Text='我就牺牲一下自己，亲自出马吧！',Speaker=890800,Option='行不行啊',}",
  [152149] = "{id=152149,Text='你就瞧好吧！',Speaker=890800,}",
  [152150] = "{id=152150,Text='咳咳，这位冒险者~',Speaker=890800,}",
  [152151] = "{id=152151,Text='有什么事吗。',Speaker=890807,}",
  [152152] = "{id=152152,Text='看得出来你很强大，但为何一脸悲伤呢？想去散散步吗？我很愿意倾听。',Speaker=890800,}",
  [152153] = "{id=152153,Text='不必了，请你离开吧。',Speaker=890807,}",
  [152154] = "{id=152154,Text='呃，其实我——',Speaker=890800,}",
  [152155] = "{id=152155,Text='你听不懂我的话吗？滚！',Speaker=890807,}",
  [152156] = "{id=152156,Text='……',Speaker=890800,Emoji=18,}",
  [152157] = "{id=152157,Text='……',Speaker=890800,Option='你没事吧？',}",
  [152158] = "{id=152158,Text='...没事，就是有点被打击到了...呜呜...',Speaker=890800,Option='别放心上',}",
  [152159] = "{id=152159,Text='没错，我要振作！这是工作！',Speaker=890800,}",
  [152160] = "{id=152160,Text='糟了，他身上的负面情感引来了怨灵！[c][ffff00][PlayerName][-][/c]，保护他！',Speaker=890800,}",
  [152161] = "{id=152161,Text='怨灵出现，那个男人都不为所动，看来他已经心如死灰了...',Speaker=890800,}",
  [152162] = "{id=152162,Text='可是...我已经想不出办法了，他的执念太深，旁人是帮不上忙的。',Speaker=890800,Option='那怎么办',}",
  [152163] = "{id=152163,Text='没办法。感情是不能勉强的，现在...只能靠他自己了。',Speaker=890800,}",
  [152164] = "{id=152164,Text='我们能做的，只有为他祝福了。用这个宝珠吧，交给你了。',Speaker=890800,}",
  [152165] = "{id=152165,Text='我不是在做梦吧！真的...真的是你...',Speaker=890807,}",
  [152166] = "{id=152166,Text='哈哈哈，瞧你一脸傻样！',Speaker=890808,}",
  [152167] = "{id=152167,Text='我这次不会再放手了！亲爱的，带我走吧。',Speaker=890807,}",
  [152168] = "{id=152168,Text='呵呵，傻瓜，这只过了一年而已，还早呢，你还有好长好长一段路。',Speaker=890808,Emoji=17,}",
  [152169] = "{id=152169,Text='没活过100岁不要来见我，明白了吗？',Speaker=890808,}",
  [152170] = "{id=152170,Text='呜...呜...我...我答应你！！',Speaker=890807,}",
  [152171] = "{id=152171,Text='说好了呦！',Speaker=890808,Action={actionid=35,num=1},}",
  [152172] = "{id=152172,Text='呜...我...一定会振作起来的啊啊啊！你放心！！一定！！！',Speaker=890807,}",
  [152173] = "{id=152173,Text='刚才发生了什么…？',Speaker=890800,Option='不知道',}",
  [152174] = "{id=152174,Text='也许，是强大的执念影响，再加上祝福魔法，唤回了那个女孩的灵魂吧。',Speaker=890800,}",
  [152175] = "{id=152175,Text='[c][ffff00][PlayerName][-][/c]，人类的爱究竟是什么呢？能够打破神明的律令，超脱生死的壁障...',Speaker=890800,Option='我也不懂',}",
  [152176] = "{id=152176,Text='呵呵，虽然我是什么爱之代理人，但也还有很多东西要学习呢。',Speaker=890800,}",
  [152177] = "{id=152177,Text='我要好好想一想。今天就到这吧，明天记得来找我哦。',Speaker=890800,}",
  [152178] = "{id=152178,Text='[c][ffff00][PlayerName][-][/c]。我考虑了很久，做了个决定。',Speaker=890800,Option='什么',}",
  [152179] = "{id=152179,Text='就是你今天的工作内容。',Speaker=890800,Action={actionid=9,num=1},}",
  [152180] = "{id=152180,Text='今天是最后一天了，我为你准备了特别项目！',Speaker=890800,Option='那是什么',Emoji=20,}",
  [152181] = "{id=152181,Text='嘿嘿嘿，保密，你做了就知道了。',Speaker=890800,}",
  [152182] = "{id=152182,Text='在那之前，我有几个问题要问你，你最好先仔细想想我们这几天的经历哦。',Speaker=890800,}",
  [152183] = "{id=152183,Text='我问你，不论结果如何，我们在对待爱时都应该鼓起勇气去面对吗？',Speaker=890800,Option='{是的,2},{不是,3}',}",
  [152184] = "{id=152184,Text='之前的见闻都忘记了吗？再想想。',Speaker=890800,}",
  [152185] = "{id=152185,Text='人生总有囊中羞涩的时候，这种情况下可能得到真爱吗？',Speaker=890800,Option='{不可能,4},{可能,5}',}",
  [152186] = "{id=152186,Text='之前的见闻都忘记了吗？再想想。',Speaker=890800,}",
  [152187] = "{id=152187,Text='爱情中也有禁忌，你如果犯了禁忌，就会受到惩罚，你知道是什么吗？',Speaker=890800,Option='{愚笨,7},{不忠,6},{懒惰,7},{贪婪,7}',}",
  [152188] = "{id=152188,Text='之前的见闻都忘记了吗？再想想。',Speaker=890800,}",
  [152189] = "{id=152189,Text='生老病死是这个世界难以避免的常理，是诸神降下的铁律，人类的爱，能够打破这一桎梏吗？',Speaker=890800,Option='{可以！,0},{能！,0} ',}",
  [152190] = "{id=152190,Text='达成共识！哈哈哈，跟你相处真愉快！',Speaker=890800,}",
  [152191] = "{id=152191,Text='好啦，开始最后的工作吧，刚才也说了，今天的工作有点特殊哦~',Speaker=890800,Option='怎么做',}",
  [152192] = "{id=152192,Text='你先要去南边的阶梯下站好，然后闭上眼，再数三个数。',Speaker=890800,Option='就这样？',}",
  [152193] = "{id=152193,Text='没错，可不要小瞧这工作，必须按我说的做，记住了，闭上眼数三个数！',Speaker=890800,}",
  [152194] = "{id=152194,Text='就这里吧，然后要闭上眼...',}",
  [152195] = "{id=152195,Text='然后要数三个数...',Option='三',}",
  [152196] = "{id=152196,Text='然后要数三个数...',Option='二',}",
  [152197] = "{id=152197,Text='然后要数三个数...',Option='一',}",
  [152198] = "{id=152198,Text='嗨！',Speaker=890800,}",
  [152199] = "{id=152199,Text='没想到吧嘿嘿嘿~',Speaker=890800,}",
  [152200] = "{id=152200,Text='呵呵呵，你一脸惊讶的样子真可爱。',Speaker=890800,Option='什么情况',}",
  [152201] = "{id=152201,Text='你最近协助了我的工作，帮助了不少人呢，他们都很感激你的。',Speaker=890800,}",
  [152202] = "{id=152202,Text='而且，作为爱之代理人，本身也是需要爱的哦~',Speaker=890800,}",
  [152203] = "{id=152203,Text='冒险者，祝你永远心怀勇气，一往无前！',Speaker=890809,Action={actionid=34,num=1},}",
  [152204] = "{id=152204,Text='冒险者，祝你能找到你心中的另一半！',Speaker=890802,Action={actionid=34,num=1},}",
  [152205] = "{id=152205,Text='不论贫富，永伴相随！',Speaker=890803,Action={actionid=34,num=1},}",
  [152206] = "{id=152206,Text='冒险者，有了另一半之后可不能花心哟。',Speaker=890805,Action={actionid=34,num=1},}",
  [152207] = "{id=152207,Text='冒险者，祝你有一份无悔的爱情！',Speaker=890807,Option='谢谢你们！',Action={actionid=34,num=1},}",
  [152208] = "{id=152208,Text='呵呵呵，人与人的相遇，相知，互相帮助，互相祝福，这也是一种爱呢。[c][ffff00][PlayerName][-][/c]，我们来合个影，纪念一下吧。',Speaker=890800,Option='好！',}",
  [152209] = "{id=152209,Text='这几天过得很开心，这都是你的功劳。',Speaker=890800,Option='我也是',}",
  [152210] = "{id=152210,Text='不过，终究是到了离别的日子。',Speaker=890800,Option='你要走了？',Action={actionid=9,num=1},}",
  [152211] = "{id=152211,Text='虽然舍不得，但是身为爱之代理人可是很忙的！可惜，你心中还燃烧着冒险者之魂，不然我说什么都要把你变成同事。',Speaker=890800,Option='…',}",
  [152212] = "{id=152212,Text='呵呵呵，那再来最后一遍：今天就到这吧，明天就不用来找我了，别想我哦~',Speaker=890800,Option='再见',}",
  [152213] = "{id=152213,Text='我送你回去，药剂拿好可别洒了。',Speaker=890801,Option='好的',}",
  [152327] = "{id=152327,Text='嗨嗨！冒险者，准备好迎接新年了吗？',Speaker=890762,Option='嗯',Action={actionid=82,num=1},}",
  [152328] = "{id=152328,Text='吼吼~我来帮你一把，今天开始到大年初七，我每天都会给你一份礼物呦！',Speaker=890762,Option='太好了',}",
  [152329] = "{id=152329,Text=' 不过是有条件的，你要猜对我每天出的谜题，然后在世界频道说出答案才行！',Speaker=890762,}",
  [152330] = "{id=152330,Text='要不要来猜猜啊~',Speaker=890762,Option='好啊',}",
  [152331] = "{id=152331,Text='很好！那么先来听听今天的谜题吧。',Speaker=890762,}",
  [152332] = "{id=152332,Text='白天睡觉，夜晚胡闹，鬼灵精怪，可可爱爱！',Speaker=890762,}",
  [152333] = "{id=152333,Text='给你个提示，谜底跟子神有关呦。',Speaker=890762,}",
  [152334] = "{id=152334,Text='新的一年始于今天，新年快乐！',Speaker=890762,Option='新年快乐',Action={actionid=82,num=1},}",
  [152335] = "{id=152335,Text='快来猜今天的谜题吧~',Speaker=890762,}",
  [152336] = "{id=152336,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='开始吧',Emoji=17,Action={actionid=84,num=1},}",
  [152337] = "{id=152337,Text='说像糖，它不甜，说像盐，又不咸，冬天有时一片，夏天谁都不见！',Speaker=890762,}",
  [152338] = "{id=152338,Text='给你个提示，这是在新年时常见的一种天气呦。',Speaker=890762,}",
  [152339] = "{id=152339,Text='大年初二，祝福到！',Speaker=890762,Action={actionid=82,num=1},}",
  [152340] = "{id=152340,Text='新年过得还愉快吗？有没有吃很多好吃的？',Speaker=890762,Option='吃了！',}",
  [152341] = "{id=152341,Text='吼吼~今天的谜题也和美食有关哦。',Speaker=890762,}",
  [152342] = "{id=152342,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='开始吧',Emoji=17,Action={actionid=84,num=1},}",
  [152343] = "{id=152343,Text='一群白鹅，漂水下河，腹中满满，捞起装碗！',Speaker=890762,}",
  [152344] = "{id=152344,Text='给你个提示，是新年常吃的美食哦。',Speaker=890762,}",
  [152345] = "{id=152345,Text='大年初三，幸福翻翻！',Speaker=890762,Action={actionid=82,num=1},}",
  [152346] = "{id=152346,Text='过年有没有尽情玩耍呀？',Speaker=890762,Option='有！',}",
  [152347] = "{id=152347,Text='吼吼~来准备应对今天的谜题吧~',Speaker=890762,}",
  [152348] = "{id=152348,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='好',Emoji=17,Action={actionid=84,num=1},}",
  [152349] = "{id=152349,Text='哼哼~那你听好啦~',Speaker=890762,}",
  [152350] = "{id=152350,Text='天空一簇花，见它人人夸，花开人欢乐，花谢人回家！',Speaker=890762,}",
  [152351] = "{id=152351,Text='唔，今天就不给你提示了，这是我害怕的东西！',Speaker=890762,}",
  [152352] = "{id=152352,Text='大年初四，平安一季！',Speaker=890762,Action={actionid=82,num=1},}",
  [152353] = "{id=152353,Text='不论是什么节日，玩耍的时候都要注意安全呦',Speaker=890762,Option='好的',}",
  [152354] = "{id=152354,Text='吼吼~来猜猜今天的谜题吧。',Speaker=890762,}",
  [152355] = "{id=152355,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='开始吧',Emoji=17,Action={actionid=84,num=1},}",
  [152356] = "{id=152356,Text='冒险伊始，城南之门，初心少女，常伴相随！',Speaker=890762,}",
  [152357] = "{id=152357,Text='给你个提示，是你在游戏中的第一个朋友哦。',Speaker=890762,}",
  [152358] = "{id=152358,Text='大年初五，生龙活虎！',Speaker=890762,Option='真有精神',Action={actionid=82,num=1},}",
  [152359] = "{id=152359,Text='新年的假期就应该充满活力，尽情玩耍！',Speaker=890762,}",
  [152360] = "{id=152360,Text='快来猜今天的谜题吧~',Speaker=890762,}",
  [152361] = "{id=152361,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='开始吧',Emoji=17,Action={actionid=84,num=1},}",
  [152362] = "{id=152362,Text='小小四方台，我们来打牌，劈里啪啦打落地，好友来相聚！',Speaker=890762,}",
  [152363] = "{id=152363,Text='给你个提示，是一种朋友聚会常玩的玩意哦。',Speaker=890762,}",
  [152364] = "{id=152364,Text='大年初六风车转，今年的冒险向前看！',Speaker=890762,Action={actionid=82,num=1},}",
  [152365] = "{id=152365,Text='假期快要结束了，紧不紧张，害不害怕~',Speaker=890762,Option='你闭嘴！',}",
  [152366] = "{id=152366,Text='不要这么凶嘛，来猜猜今天的谜题开心一下！',Speaker=890762,}",
  [152367] = "{id=152367,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='开始吧',Emoji=17,Action={actionid=84,num=1},}",
  [152368] = "{id=152368,Text='敲敲打打，叮叮当当，问他是谁，妙手匠心！',Speaker=890762,}",
  [152369] = "{id=152369,Text='给你个提示，是游戏中的一个职业哦！',Speaker=890762,}",
  [152370] = "{id=152370,Text='大年初七！',Speaker=890762,Action={actionid=82,num=1},}",
  [152371] = "{id=152371,Text='今天是最后一次给你出谜题了，还真有点舍不得…',Speaker=890762,Option='我也是',}",
  [152372] = "{id=152372,Text='吼吼~只要你的冒险还在持续，我们总有一天还会相遇。',Speaker=890762,}",
  [152373] = "{id=152373,Text='那就开始今天的猜谜吧。',Speaker=890762,}",
  [152374] = "{id=152374,Text='只要把答案在世界频道里说出来，就可以得到礼物呦~',Speaker=890762,Option='开始吧',Emoji=17,Action={actionid=84,num=1},}",
  [152375] = "{id=152375,Text='一年只上七天班，还向各位道平安，可可爱爱，耷着脑袋。',Speaker=890762,}",
  [152376] = "{id=152376,Text='给你个提示，远在天边，近在眼见！',Speaker=890762,}",
  [152385] = "{id=152385,Text='一道来自异时空的裂隙。',Speaker=890833,}",
  [152386] = "{id=152386,Text='带着一丝不同于这片大陆的气息。',Speaker=890833,}",
  [152387] = "{id=152387,Text='你想要触碰它么？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=890833,Option='{伸手触碰,4},{转身离开,3}',}",
  [152388] = "{id=152388,Text='这里是哪里？',Speaker=890834,}",
  [152389] = "{id=152389,Text='熊本熊，熊本熊，你没事吧！',Speaker=890834,Emoji=18,}",
  [152390] = "{id=152390,Text='呼……吓死杏子啦！还好你没有事！',Speaker=890834,}",
  [152391] = "{id=152391,Text='咦？这是哪里？',Speaker=890834,}",
  [152392] = "{id=152392,Text='请问你是？',Speaker=890834,Option='说明情况',}",
  [152393] = "{id=152393,Text='米德加尔特大陆？！',Speaker=890834,Emoji=7,}",
  [152394] = "{id=152394,Text='嗯嗯，看样子，这里就是熊本熊在信封上看到的地方了！',Speaker=890834,Option='信封？',}",
  [152395] = "{id=152395,Text='喏，在这里。',Speaker=890834,}",
  [152396] = "{id=152396,Text='信封中的内容是……',}",
  [152397] = "{id=152397,Text='“如果你真的是充满爱的人气偶像”',Speaker=890839,}",
  [152398] = "{id=152398,Text='“那就请你来到米德加尔特大陆进行一场爱的冒险者之旅！”',Speaker=890839,}",
  [152399] = "{id=152399,Text='“熊本熊，我在这里等你。”',Speaker=890839,}",
  [152400] = "{id=152400,Text='熊本熊看完了信后就触碰了里面的宝石。',Speaker=890834,}",
  [152401] = "{id=152401,Text='然后我们两个眼前一黑就来到了这里！',Speaker=890834,Option='原来如此',}",
  [152402] = "{id=152402,Text='啊……我明白你的意思了，熊本熊。',Speaker=890834,Option='怎么了？',}",
  [152403] = "{id=152403,Text='熊本熊很喜欢你，希望你能和我们一起冒险！',Speaker=890834,Option='欣然答应',}",
  [152404] = "{id=152404,Text='咕噜噜……',Speaker=890834,}",
  [152405] = "{id=152405,Text='咕噜噜……',Speaker=890834,Option='什么声音？',}",
  [152406] = "{id=152406,Text='抱歉……是我的肚子叫了……',Speaker=890834,Emoji=18,}",
  [152407] = "{id=152407,Text='我们来了这么久一直没有吃东西……',Speaker=890834,}",
  [152408] = "{id=152408,Text='可以麻烦你弄些料理来么？',Speaker=890834,}",
  [152409] = "{id=152409,Text='皇家料理协会的碧莎一定会制作美味的料理！',}",
  [152410] = "{id=152410,Text='料理是一件令人幸福的事情。',Speaker=6446,Option='说明情况',}",
  [152411] = "{id=152411,Text='原来是为了料理而来。',Speaker=6446,}",
  [152412] = "{id=152412,Text='我这边有刚做好的罗宋汤。',Speaker=6446,}",
  [152413] = "{id=152413,Text='不过，你要回答我几个问题。',Speaker=6446,}",
  [152414] = "{id=152414,Text='只要你是真心喜爱料理，我就可以把做好的罗宋汤交给你。',Speaker=6446,Option='好的',}",
  [152415] = "{id=152415,Text='能和你这样喜爱料理的人交流真是太好了。',Speaker=6446,}",
  [152416] = "{id=152416,Text='给你料理，希望熊本熊也可以体会到料理的美好！',Speaker=6446,}",
  [152417] = "{id=152417,Text='！！！',Speaker=890831,}",
  [152418] = "{id=152418,Text='熊本熊的意思是多谢款待！',Speaker=890834,}",
  [152419] = "{id=152419,Text='料理的味道很不错呢！',Speaker=890834,Emoji=20,}",
  [152420] = "{id=152420,Text='熊本熊！你终于来了！',Speaker=890836,}",
  [152421] = "{id=152421,Text='如果你真的是充满爱的人气偶像就跟我来吧！',Speaker=890836,}",
  [152422] = "{id=152422,Text='熊本熊！！',Speaker=890834,}",
  [152423] = "{id=152423,Text='抱歉，我要去追赶熊本熊了！',Speaker=890834,Option='那我呢？',}",
  [152424] = "{id=152424,Text='明天我们会在普隆德拉等你！',Speaker=890834,Emoji=18,}",
  [152425] = "{id=152425,Text='呼……',Speaker=890834,}",
  [152426] = "{id=152426,Text='昨天那个神秘人我们没追到……',Speaker=890834,}",
  [152427] = "{id=152427,Text='不过，我们捡到了他留下的信封……',Speaker=890834,}",
  [152428] = "{id=152428,Text='信封上的内容是……',}",
  [152429] = "{id=152429,Text='“我在艾尔帕兰等你，熊本熊”',Speaker=890839,Option='艾尔帕兰？',}",
  [152430] = "{id=152430,Text='熊本熊想请您随我们去艾尔帕兰！',Speaker=890834,Option='欣然答应',}",
  [152431] = "{id=152431,Text='这次的信封里也有着和之前一样的宝石。',Speaker=890834,}",
  [152432] = "{id=152432,Text='估计可以把我们直接送到那个叫艾尔帕兰的地方。',Speaker=890834,}",
  [152433] = "{id=152433,Text='准备好了么？[c][ffff00][PlayerName][-][/c]！',Speaker=890834,Option='{准备好了,4},{稍后再去,3}',}",
  [152434] = "{id=152434,Text='你到底是谁啊，不要再缠着我了好么！',Speaker=890838,}",
  [152435] = "{id=152435,Text='我是克莱恩啊，米雅！',Speaker=890837,}",
  [152436] = "{id=152436,Text='什么克莱恩！我根本就不认识你！',Speaker=890838,}",
  [152437] = "{id=152437,Text='离我远一点！',Speaker=890838,Emoji=12,}",
  [152438] = "{id=152438,Text='熊本熊有些担心那个男孩子，我们去看看吧！',Speaker=890834,}",
  [152439] = "{id=152439,Text='米雅……米雅……',Speaker=890837,}",
  [152440] = "{id=152440,Text='米雅……为什么你都忘了呢……',Speaker=890837,Option='询问情况',}",
  [152441] = "{id=152441,Text='你们是……',Speaker=890837,}",
  [152442] = "{id=152442,Text='您好，我的名字叫杏子。',Speaker=890834,}",
  [152443] = "{id=152443,Text='这位是熊本县的爱心大使——熊本熊！',Speaker=890834,}",
  [152444] = "{id=152444,Text='熊本熊很担心你。',Speaker=890834,}",
  [152445] = "{id=152445,Text='谢谢你们的关心。',Speaker=890837,}",
  [152446] = "{id=152446,Text='刚刚那个女孩子叫米雅，是我的爱人。',Speaker=890837,}",
  [152447] = "{id=152447,Text='我们因舞蹈而相识，是舞团里最恩爱的一对舞者。',Speaker=890837,}",
  [152448] = "{id=152448,Text='前阵子外出时，她为了保护我受到了魔物攻击。',Speaker=890837,}",
  [152449] = "{id=152449,Text='然后她就彻底忘了我们的故事……',Speaker=890837,}",
  [152450] = "{id=152450,Text='！！！',Speaker=890831,}",
  [152451] = "{id=152451,Text='！！！',Speaker=890831,}",
  [152452] = "{id=152452,Text='恩恩……',Speaker=890834,}",
  [152453] = "{id=152453,Text='好的，杏子明白了！',Speaker=890834,}",
  [152454] = "{id=152454,Text='克莱恩先生，熊本熊想问您。',Speaker=890834,}",
  [152455] = "{id=152455,Text='什么？',Speaker=890837,}",
  [152456] = "{id=152456,Text='既然您和米雅小姐相识于舞蹈。',Speaker=890834,}",
  [152457] = "{id=152457,Text='那么让你们坠入爱河的是哪段舞蹈呢？',Speaker=890834,}",
  [152458] = "{id=152458,Text='好像是《相遇海桥》……',Speaker=890837,}",
  [152459] = "{id=152459,Text='……！',Speaker=890831,}",
  [152460] = "{id=152460,Text='熊本熊希望与您一起为米雅跳这只舞呢！',Speaker=890834,}",
  [152461] = "{id=152461,Text='这样说不定会唤回米雅小姐的记忆呢！',Speaker=890834,Option='好主意',}",
  [152462] = "{id=152462,Text='好吧，那我跳一遍，你们看一下。',Speaker=890837,}",
  [152463] = "{id=152463,Text='第一步，向下弯腰。',Speaker=890837,Action={actionid=34,num=1},}",
  [152464] = "{id=152464,Text='第二是，挥手。',Speaker=890837,Action={actionid=35,num=1},}",
  [152465] = "{id=152465,Text='第三……&*（&最后是……单膝下跪。',Speaker=890837,Action={actionid=95,num=1},}",
  [152466] = "{id=152466,Text='都记住了么？',Speaker=890837,}",
  [152467] = "{id=152467,Text='记住了。',}",
  [152468] = "{id=152468,Text='那克莱恩先生，我们一起去找米雅小姐吧。',Speaker=890834,}",
  [152469] = "{id=152469,Text='米雅应该在舞团附近，我们过去吧。',Speaker=890837,}",
  [152470] = "{id=152470,Text='我说过你不要再缠着我了！',Speaker=890838,}",
  [152471] = "{id=152471,Text='米雅你听我说！',Speaker=890837,}",
  [152472] = "{id=152472,Text='拜托了，米雅给我一点时间就好。',Speaker=890837,}",
  [152473] = "{id=152473,Text='……好吧。',Speaker=890838,}",
  [152474] = "{id=152474,Text='拜托你们了！',Speaker=890837,}",
  [152475] = "{id=152475,Text='舞蹈的第一步是',Option='{向下弯腰,8},{踢腿,7},{转身,7}',}",
  [152476] = "{id=152476,Text='舞蹈的第二步是',Option='{向下弯腰,11},{挥手,12},{转身,11}',}",
  [152477] = "{id=152477,Text='舞蹈的最后一步是',Option='{单膝下跪,16},{转身,15},{回头,15}',}",
  [152478] = "{id=152478,Text='！！！',Speaker=890831,}",
  [152479] = "{id=152479,Text='！！！',Speaker=890831,}",
  [152480] = "{id=152480,Text='！！！',Speaker=890831,}",
  [152481] = "{id=152481,Text='克莱恩……',Speaker=890838,}",
  [152482] = "{id=152482,Text='《相遇海桥》……',Speaker=890838,}",
  [152483] = "{id=152483,Text='我想起来了！',Speaker=890838,Emoji=7,}",
  [152484] = "{id=152484,Text='熊本熊祝福克莱恩和米雅永远幸福下去！',Speaker=890834,Emoji=2,}",
  [152485] = "{id=152485,Text='谢谢你！熊本熊！',Speaker=890838,}",
  [152486] = "{id=152486,Text='也谢谢你，[c][ffff00][PlayerName][-][/c]。',Speaker=890837,}",
  [152487] = "{id=152487,Text='咦？这是信封？',Speaker=890834,}",
  [152488] = "{id=152488,Text='“很不错嘛，熊本熊，如果想找到我的话，就来吉芬吧！”',Speaker=890839,Option='去吉芬？',}",
  [152489] = "{id=152489,Text='熊本熊的意思是我们今天先去休息，明天再次出发。',Speaker=890834,Option='好的',}",
  [152490] = "{id=152490,Text='那我们明天在普隆德拉见！',Speaker=890834,}",
  [152491] = "{id=152491,Text='快来快来！',Speaker=890834,Action={actionid=39,num=1},}",
  [152492] = "{id=152492,Text='！！！~~~！！',Speaker=890831,}",
  [152493] = "{id=152493,Text='熊本熊问你昨天有没有休息好呢！',Speaker=890834,Option='表示很好',}",
  [152494] = "{id=152494,Text='？？？！！',Speaker=890831,}",
  [152495] = "{id=152495,Text='是哦，杏子也很想问吉芬到底是哪里呢？',Speaker=890834,Option='介绍吉芬',}",
  [152496] = "{id=152496,Text='！！！',Speaker=890831,}",
  [152497] = "{id=152497,Text='魔法之都？！熊本熊对吉芬产生了很大的兴趣呢！',Speaker=890834,}",
  [152498] = "{id=152498,Text='！！！~~~！！！',Speaker=890831,}",
  [152499] = "{id=152499,Text='嗯嗯！收到！',Speaker=890834,}",
  [152500] = "{id=152500,Text='熊本熊已经迫不及待了！',Speaker=890834,}",
  [152501] = "{id=152501,Text='让我们立刻朝着魔法之都吉芬出发！',Speaker=890834,}",
  [152502] = "{id=152502,Text='准备好了么？',Speaker=890834,Option='{准备好了,4},{稍后再去,3}',}",
  [152503] = "{id=152503,Text='所有人都不喜欢我……',Speaker=890840,}",
  [152504] = "{id=152504,Text='没有人会重视我……',Speaker=890840,}",
  [152505] = "{id=152505,Text='我就不应该来到这里学习魔法……',Speaker=890840,}",
  [152506] = "{id=152506,Text='啊！！！',Speaker=890840,}",
  [152507] = "{id=152507,Text='我不想活了！！！',Speaker=890840,}",
  [152508] = "{id=152508,Text='[c][ffff00][PlayerName][-][/c]！快去阻止那个孩子！',Speaker=890834,}",
  [152509] = "{id=152509,Text='哇！！！！',Speaker=890840,Emoji=1,}",
  [152510] = "{id=152510,Text='什么人？！！！',Speaker=890840,}",
  [152511] = "{id=152511,Text='您好，我的名字叫杏子。',Speaker=890834,}",
  [152512] = "{id=152512,Text='这位是熊本县的爱心大使——熊本熊！',Speaker=890834,}",
  [152513] = "{id=152513,Text='爱心大使？',Speaker=890840,}",
  [152514] = "{id=152514,Text='爱心大使又能怎样呢……',Speaker=890840,}",
  [152515] = "{id=152515,Text='谁都不能帮助我……',Speaker=890840,}",
  [152516] = "{id=152516,Text='请您重新拾起信心来！',Speaker=890834,}",
  [152517] = "{id=152517,Text='信心？你们说的容易……',Speaker=890840,}",
  [152518] = "{id=152518,Text='这只是我魔法课程上用的青蛙。',Speaker=890840,}",
  [152519] = "{id=152519,Text='老师告诉我们要用魔法将青蛙变大……',Speaker=890840,}",
  [152520] = "{id=152520,Text='而我呢……',Speaker=890840,}",
  [152521] = "{id=152521,Text='多普鲁乌兰科思！',Speaker=890840,}",
  [152522] = "{id=152522,Text='看到了吧……',Speaker=890840,}",
  [152523] = "{id=152523,Text='我根本就做不到……',Speaker=890840,}",
  [152524] = "{id=152524,Text='哇！熊本熊被你的魔法震惊到了呢！',Speaker=890834,}",
  [152525] = "{id=152525,Text='震惊？可是我根本就做不到老师说的……',Speaker=890840,Option='相信自己',}",
  [152526] = "{id=152526,Text='我果然是个魔法废材……',Speaker=890840,}",
  [152527] = "{id=152527,Text='你……你们……',Speaker=890840,}",
  [152528] = "{id=152528,Text='唔……你竟然会这么温柔的摸我的头顶。',Speaker=890840,}",
  [152529] = "{id=152529,Text='谢谢你……熊本熊……',Speaker=890840,Emoji=2,}",
  [152530] = "{id=152530,Text='我想起了我学习魔法的初心……',Speaker=890840,}",
  [152531] = "{id=152531,Text='我一定会坚持下去的！',Speaker=890840,}",
  [152532] = "{id=152532,Text='我要继续去和老师学习魔法……',Speaker=890840,Option='坚持下去',}",
  [152533] = "{id=152533,Text='哦！对了，之前有个怪人让我转告给带着腮红的人一句话。',Speaker=890840,}",
  [152534] = "{id=152534,Text='我想应该就是你们了……',Speaker=890840,Option='什么',}",
  [152535] = "{id=152535,Text='他说他在斐扬等你们……',Speaker=890840,}",
  [152536] = "{id=152536,Text='嗯嗯，杏子明白！[c][ffff00][PlayerName][-][/c]，我们回去整理好状态！',Speaker=890834,}",
  [152537] = "{id=152537,Text='明天一定要去斐扬将那个人找到！',Speaker=890834,Option='知道了',}",
  [152538] = "{id=152538,Text='昨天你走了之后，我和熊本熊又在吉芬参观了一会……',Speaker=890834,}",
  [152539] = "{id=152539,Text='杏子和熊本熊都是第一次见到这么神奇的地方呢。',Speaker=890834,}",
  [152540] = "{id=152540,Text='不仅如此，我们还找人询问了关于斐扬的故事。',Speaker=890834,}",
  [152541] = "{id=152541,Text='据说那里是美好又温柔的猎人家乡呢……',Speaker=890834,Option='确实如此',}",
  [152542] = "{id=152542,Text='！！！',Speaker=890831,}",
  [152543] = "{id=152543,Text='熊本熊不要急！',Speaker=890834,}",
  [152544] = "{id=152544,Text='我们这就出发吧！',Speaker=890834,}",
  [152545] = "{id=152545,Text='怕索尔屋恩多——斐扬！',Speaker=890834,Option='魔法？',Action={actionid=11,num=1},}",
  [152546] = "{id=152546,Text='这可是我们在吉芬学到的哦~',Speaker=890834,Option='{准备好了,4},{稍后再去,3}',}",
  [152547] = "{id=152547,Text='嘻嘻……',Speaker=890843,}",
  [152548] = "{id=152548,Text='驱赶我是没有用的！',Speaker=890843,}",
  [152549] = "{id=152549,Text='佩洛我又回来啦！！',Speaker=890843,}",
  [152550] = "{id=152550,Text='！！！',Speaker=890831,}",
  [152551] = "{id=152551,Text='熊本熊的意思是让我们快点追上那个小僵尸！',Speaker=890834,Option='好的',}",
  [152552] = "{id=152552,Text='[c][ffff00][PlayerName][-][/c],熊本熊指的那个方向！',Speaker=890834,}",
  [152553] = "{id=152553,Text='就在这里了！',Speaker=890834,}",
  [152554] = "{id=152554,Text='佩洛我一定要吓你们一跳！',Speaker=890843,}",
  [152555] = "{id=152555,Text='！！！',Speaker=890831,}",
  [152556] = "{id=152556,Text='什么东西？！！',Speaker=890843,}",
  [152557] = "{id=152557,Text='你拦着我干嘛！！！',Speaker=890843,}",
  [152558] = "{id=152558,Text='请不要这样做！',Speaker=890834,}",
  [152559] = "{id=152559,Text='你们懂什么！',Speaker=890843,}",
  [152560] = "{id=152560,Text='我变成僵尸后，村子里的人都忘了我！',Speaker=890843,}",
  [152561] = "{id=152561,Text='只有这样才能让他们想起我！',Speaker=890843,}",
  [152562] = "{id=152562,Text='懒得理你们，他们来了！嘻嘻嘻~',Speaker=890843,Emoji=5,}",
  [152563] = "{id=152563,Text='！！！',Speaker=890831,}",
  [152564] = "{id=152564,Text='阻止他，[c][ffff00][PlayerName][-][/c]。',Speaker=890834,}",
  [152565] = "{id=152565,Text='可恶！',Speaker=890843,}",
  [152566] = "{id=152566,Text='为什么总是要拦着我！',Speaker=890843,}",
  [152567] = "{id=152567,Text='他们明明都不记得我了……',Speaker=890843,}",
  [152568] = "{id=152568,Text='佩洛，这是你最喜欢的花。',Speaker=890844,}",
  [152569] = "{id=152569,Text='佩洛，我们好想你……',Speaker=890845,}",
  [152570] = "{id=152570,Text='这……你们……',Speaker=890843,}",
  [152571] = "{id=152571,Text='他们……',Speaker=890843,}",
  [152572] = "{id=152572,Text='呜呜呜……安娜……',Speaker=890843,}",
  [152573] = "{id=152573,Text='呜呜呜……雷诺……',Speaker=890843,}",
  [152574] = "{id=152574,Text='我知道错了……我会离开这个村子的……',Speaker=890843,}",
  [152575] = "{id=152575,Text='谢谢你们拦住了我……',Speaker=890843,}",
  [152576] = "{id=152576,Text='我……我……可能要离开了……',Speaker=890843,}",
  [152577] = "{id=152577,Text='？？？',Speaker=890831,}",
  [152578] = "{id=152578,Text='他一定是带着幸福离开的……',Speaker=890834,}",
  [152579] = "{id=152579,Text='熊本熊，你这几天的事情我都看到了。',Speaker=890836,}",
  [152580] = "{id=152580,Text='我会在樱花盛开的地方等你……',Speaker=890836,}",
  [152581] = "{id=152581,Text='！！！',Speaker=890831,}",
  [152582] = "{id=152582,Text='看来这次要真的和那个人见面了……',Speaker=890834,}",
  [152583] = "{id=152583,Text='你回去好好休息，明天我们一起前往！',Speaker=890834,Option='好的',}",
  [152584] = "{id=152584,Text='呼……准备好了么！',Speaker=890834,}",
  [152585] = "{id=152585,Text='熊本熊！',Speaker=890834,}",
  [152586] = "{id=152586,Text='！！！',Speaker=890831,}",
  [152587] = "{id=152587,Text='嘿咻！嘿咻！',Speaker=890834,Action={actionid=9,num=1},}",
  [152588] = "{id=152588,Text='杏子也准备好了。',Speaker=890834,}",
  [152589] = "{id=152589,Text='你怎样了，[c][ffff00][PlayerName][-][/c]！',Speaker=890834,Option='表示很好',}",
  [152590] = "{id=152590,Text='今天就要去那个神秘人所说的地方了！',Speaker=890834,}",
  [152591] = "{id=152591,Text='樱花盛开的地方……',Speaker=890834,}",
  [152592] = "{id=152592,Text='你知道这片大陆哪里有樱花盛开的地方么？',Speaker=890834,Option='{吉芬,3},{艾尔帕兰,3},{天津町,4}',}",
  [152593] = "{id=152593,Text='你确定那里是樱花盛开的地方么？',Speaker=890834,}",
  [152594] = "{id=152594,Text='天津町？很美好的名字呢！',Speaker=890834,}",
  [152595] = "{id=152595,Text='！！！',Speaker=890831,}",
  [152596] = "{id=152596,Text='那就让我们现在就出发吧！',Speaker=890834,}",
  [152597] = "{id=152597,Text='怕索尔屋恩多——天津町！',Speaker=890834,Option='{准备好了,8},{稍后再去,7}',Action={actionid=11,num=1},}",
  [152598] = "{id=152598,Text='你们是……？！！',Speaker=890846,Emoji=1,}",
  [152599] = "{id=152599,Text='您好，我的名字叫杏子。',Speaker=890834,}",
  [152600] = "{id=152600,Text='这位是熊本县的爱心大使——熊本熊！',Speaker=890834,}",
  [152601] = "{id=152601,Text='你们让开……',Speaker=890846,}",
  [152602] = "{id=152602,Text='不要打扰到我给婆婆收集花蜜……',Speaker=890846,}",
  [152603] = "{id=152603,Text='……',Speaker=890834,}",
  [152604] = "{id=152604,Text='我们来帮你！',}",
  [152605] = "{id=152605,Text='不要以为你帮我，我就会理你们！',Speaker=890846,Emoji=12,}",
  [152606] = "{id=152606,Text='给了我花蜜又怎样！',Speaker=890846,}",
  [152607] = "{id=152607,Text='哼！',Speaker=890846,Emoji=40,}",
  [152608] = "{id=152608,Text='[c][ffff00][PlayerName][-][/c]！我们快跟上她！',Speaker=890834,}",
  [152609] = "{id=152609,Text='奈绪小朋友！请您听我们说……',Speaker=890834,}",
  [152610] = "{id=152610,Text='……你们到底要干什么……',Speaker=890846,}",
  [152611] = "{id=152611,Text='我们想要和你做朋友……',Speaker=890846,}",
  [152612] = "{id=152612,Text='奈绪总是抱着那只脏的玩偶熊……',Speaker=890847,}",
  [152613] = "{id=152613,Text='没关系，我们不要和她玩了，晴子。',Speaker=890848,}",
  [152614] = "{id=152614,Text='菜菜子，你看我的洋娃娃可爱嘛。',Speaker=890847,Emoji=2,}",
  [152615] = "{id=152615,Text='奈绪……最讨厌玩偶熊了！',Speaker=890846,}",
  [152616] = "{id=152616,Text='！！！',Speaker=890831,}",
  [152617] = "{id=152617,Text='熊本熊！！！',Speaker=890834,}",
  [152618] = "{id=152618,Text='樱花啊樱花。',Speaker=890846,}",
  [152619] = "{id=152619,Text='请你让我的朋友喜欢我吧……',Speaker=890846,}",
  [152620] = "{id=152620,Text='……！！！',Speaker=890831,}",
  [152621] = "{id=152621,Text='你们怎么又出现了！！',Speaker=890846,}",
  [152622] = "{id=152622,Text='这是……地震……！！',Speaker=890846,}",
  [152623] = "{id=152623,Text='奈绪小心！！！',Speaker=890836,}",
  [152624] = "{id=152624,Text='奈绪，你没有受伤吧！',Speaker=890836,}",
  [152625] = "{id=152625,Text='屋顶的石砖怎么掉了下来，奈绪小朋友没有受伤吧！',Speaker=890834,}",
  [152626] = "{id=152626,Text='！！！！',Speaker=890831,}",
  [152627] = "{id=152627,Text='布丁……怎么是你……',Speaker=890846,}",
  [152628] = "{id=152628,Text='你不是被我丢弃了么……',Speaker=890846,}",
  [152629] = "{id=152629,Text='奈绪，我一直把你当成我最好的朋友！',Speaker=890850,}",
  [152630] = "{id=152630,Text='！！！',Speaker=890831,}",
  [152631] = "{id=152631,Text='我一直希望奈绪能和我和好……',Speaker=890850,}",
  [152632] = "{id=152632,Text='但是……这样的话，奈绪就会被其他朋友嘲笑。',Speaker=890850,}",
  [152633] = "{id=152633,Text='这个愿望是不对的，我太自私了……',Speaker=890850,}",
  [152634] = "{id=152634,Text='熊本熊谢谢你，很抱歉把你牵扯进来……',Speaker=890850,}",
  [152635] = "{id=152635,Text='我还妄想着你能帮助我……',Speaker=890850,}",
  [152636] = "{id=152636,Text='真正的朋友是不会抛弃彼此的……',Speaker=890834,}",
  [152637] = "{id=152637,Text='我相信奈绪心里也是舍不得你的……',Speaker=890834,}",
  [152638] = "{id=152638,Text='布丁……呜呜呜……我错了……',Speaker=890846,}",
  [152639] = "{id=152639,Text='奈绪……',Speaker=890850,}",
  [152640] = "{id=152640,Text='熊本熊，你是充满爱的人气偶像！',Speaker=890850,}",
  [152641] = "{id=152641,Text='这块传送石给你，有了它你们就可以回到原来的世界了……',Speaker=890850,}",
  [152642] = "{id=152642,Text='希望以后还能见到你们……',Speaker=890850,}",
  [152643] = "{id=152643,Text='熊本熊会祝福布丁和奈绪的友谊长长久久的~',Speaker=890834,}",
  [152644] = "{id=152644,Text='当然熊本熊和[c][ffff00][PlayerName][-][/c]的友谊也会哦！',Speaker=890834,Option='当然',}",
  [152645] = "{id=152645,Text='什么？你要在这片大陆玩一会再回去？',Speaker=890834,}",
  [152646] = "{id=152646,Text='喂喂！！等等我！！！！',Speaker=890834,}",
  [152647] = "{id=152647,Text='熊本熊递给你一张纸条。',}",
  [152648] = "{id=152648,Text='纸条上的内容是……',}",
  [152649] = "{id=152649,Text='我是熊本县的幸福部长——熊本熊！',Speaker=890852,}",
  [152650] = "{id=152650,Text='作为熊本县的大使！',Speaker=890852,}",
  [152651] = "{id=152651,Text='我要向可爱的你介绍我的朋友们——天草海豚！',Speaker=890852,}",
  [152652] = "{id=152652,Text='每当海面翻起~我的朋友们就会随着浪花而来！',Speaker=890852,}",
  [152653] = "{id=152653,Text='它们友善而温和，是我们熊本县的好朋友！',Speaker=890852,}",
  [152654] = "{id=152654,Text='听说在这片大陆，你们也有着这样的朋友！',Speaker=890852,}",
  [152655] = "{id=152655,Text='它们名字叫波利，生活在一个名为波利岛的地方。',Speaker=890852,}",
  [152656] = "{id=152656,Text='我也很想见识一下呢~',Speaker=890852,}",
  [152657] = "{id=152657,Text='你能拍张波利岛的照片给我么？',Speaker=890852,Option='{我再想想,10},{欣然同意,0}',}",
  [152658] = "{id=152658,Text='熊本熊递给你一张纸条。',}",
  [152659] = "{id=152659,Text='纸条上的内容是……',}",
  [152660] = "{id=152660,Text='在很久很久以前……',Speaker=890852,}",
  [152661] = "{id=152661,Text='熊本县的居民们因为农田与水渠的问题而烦恼！',Speaker=890852,}",
  [152662] = "{id=152662,Text='这时一座桥梁出现了！',Speaker=890852,}",
  [152663] = "{id=152663,Text='噔噔！就是我们熊本县最著名的——',Speaker=890852,}",
  [152664] = "{id=152664,Text='通润桥！！！',Speaker=890852,}",
  [152665] = "{id=152665,Text='据说在这片大陆也有一条同样伟岸的桥梁！',Speaker=890852,}",
  [152666] = "{id=152666,Text='好像叫跨海大桥，你能拍给我看看么？',Speaker=890852,Option='{我再想想,20},{欣然同意,0}',}",
  [152667] = "{id=152667,Text='熊本熊递给你一张纸条。',}",
  [152668] = "{id=152668,Text='纸条上的内容是……',}",
  [152669] = "{id=152669,Text='你听说过“杏城”么？',Speaker=890852,}",
  [152670] = "{id=152670,Text='让可爱的熊本熊来告诉你关于杏城的故事！',Speaker=890852,}",
  [152671] = "{id=152671,Text='杏城就是熊本县最坚固的熊本城！',Speaker=890852,}",
  [152672] = "{id=152672,Text='在战争时期它抵御了许多次敌人的攻击！',Speaker=890852,}",
  [152673] = "{id=152673,Text='而且在樱花开放的季节……',Speaker=890852,}",
  [152674] = "{id=152674,Text='整个熊本城都开满了樱花，是熊本县人最喜欢的地方！',Speaker=890852,}",
  [152675] = "{id=152675,Text='据说这里有个叫天津町的地方也有着很多樱花！',Speaker=890852,}",
  [152676] = "{id=152676,Text='熊本熊也想要看看天津町呢~',Speaker=890852,Option='{我再想想,30},{欣然同意,0}',}",
  [152677] = "{id=152677,Text='熊本熊递给你一张纸条。',}",
  [152678] = "{id=152678,Text='纸条上的内容是……',}",
  [152679] = "{id=152679,Text='啊~肚子好饿~',Speaker=890852,}",
  [152680] = "{id=152680,Text='好想念熊本县的美食啊！',Speaker=890852,}",
  [152681] = "{id=152681,Text='对啊~美食！',Speaker=890852,}",
  [152682] = "{id=152682,Text='尤其是熊本拉面！',Speaker=890852,}",
  [152683] = "{id=152683,Text='浓郁的骨汤，新鲜的蒜瓣~',Speaker=890852,}",
  [152684] = "{id=152684,Text='一想到就让我流口水！',Speaker=890852,}",
  [152685] = "{id=152685,Text='这片大陆也有很多好吃的吧！',Speaker=890852,}",
  [152686] = "{id=152686,Text='那个皇家料理协会！',Speaker=890852,}",
  [152687] = "{id=152687,Text='帮我去那里拍一张照片！',Speaker=890852,}",
  [152688] = "{id=152688,Text='就算吃不到也可以欣赏一下这里的美食！',Speaker=890852,Option='{我再想想,40},{欣然同意,0}',}",
  [152689] = "{id=152689,Text='熊本熊递给你一张纸条。',}",
  [152690] = "{id=152690,Text='纸条上的内容是……',}",
  [152691] = "{id=152691,Text='心情不好的时候去哪里？',Speaker=890852,}",
  [152692] = "{id=152692,Text='熊本温泉！',Speaker=890852,}",
  [152693] = "{id=152693,Text='感觉疲惫的时候去哪里！',Speaker=890852,}",
  [152694] = "{id=152694,Text='熊本温泉！',Speaker=890852,}",
  [152695] = "{id=152695,Text='与伙伴一起的时候去哪里！',Speaker=890852,}",
  [152696] = "{id=152696,Text='熊本温泉！',Speaker=890852,}",
  [152697] = "{id=152697,Text='在这片大陆是不是也有一个四处都是水流的地方！',Speaker=890852,}",
  [152698] = "{id=152698,Text='好像叫艾尔帕兰？',Speaker=890852,}",
  [152699] = "{id=152699,Text='我好想看一下那边的风景啊~',Speaker=890852,Option='{我再想想,50},{欣然同意,0}',}",
  [152700] = "{id=152700,Text='看来你还不是真的喜爱料理。',Speaker=6446,}",
  [152711] = "{id=152711,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Option='{确定,4},{转身离开,3}',}",
  [152736] = "{id=152736,Text='客人你好，是安娜委托你来找我的吗？',Speaker=890860,}",
  [152737] = "{id=152737,Text='呵呵，我听说安娜最近可是成为“女王大人”了呢！',Speaker=890860,Emoji=41,}",
  [152738] = "{id=152738,Text='不过，我们的女王大人没有像样的衣服怎么行呢？',Speaker=890860,}",
  [152739] = "{id=152739,Text='宴会上的晚礼服，茶会上的小裙子，骑马时的骑士服……这些可都要备好呢！',Speaker=890860,}",
  [152740] = "{id=152740,Text='这样吧，只要你提供给我足够的宝石，我就答应作为安娜女王的王室独家服饰供应商~',Speaker=890860,}",
  [152741] = "{id=152741,Text='当然啦，也不会亏待了你的，我也有为你准备了一些礼物哦！',Speaker=890860,}",
  [152778] = "{id=152778,Text='哟~[c][ffff00][PlayerName][-][/c]你来啦，我和我的好朋友菲菲小姐已经等了你好久啦~',Speaker=890881,}",
  [152779] = "{id=152779,Text='是这样的，我们现在打算再来重温一下从前我作为女王大人的情景。',Speaker=890881,}",
  [152780] = "{id=152780,Text='而菲菲小姐就是本女王的内务官哦~',Speaker=890881,}",
  [152781] = "{id=152781,Text='嘻嘻，我缠了安娜好久她才答应的呢。',Speaker=890880,}",
  [152782] = "{id=152782,Text='只是，在为女王大人搭配今天的衣饰这件事上，我有些拿不准，冒险者，你能帮帮我吗？',Speaker=890880,Option='{没问题,2},{再考虑一下,3}',Emoji=2,}",
  [152783] = "{id=152783,Text='考虑好了再来找我哦，这件事真是令人伤脑筋呢。',Speaker=890880,}",
  [152784] = "{id=152784,Text='事不宜迟，我们这就开始给女王大人挑选今天的搭配吧~',Speaker=890880,}",
  [152785] = "{id=152785,Text='今天是安娜女王的加冕典礼日，这是一个十分重大的场合呢，穿什么衣服好呢？',Speaker=890880,Option='{华丽的礼服,5},{舒适的常服,6},{时尚的连衣裙,7}',}",
  [152786] = "{id=152786,Text='嗯衣服挑好了，我这就来帮女王大人换上~',Speaker=890880,}",
  [152787] = "{id=152787,Text='衣服换好了，我们为女王大人准备什么头饰呢？',Speaker=890880,Option='{骄阳黄遮阳帽,51},{宝石王冠,52},{海棠花发夹,53}',}",
  [152788] = "{id=152788,Text='呼~挑选好了，当当当~女王大人驾到！',Speaker=890880,}",
  [152789] = "{id=152789,Text='衣服换好了，我们为女王大人准备什么头饰呢？',Speaker=890880,Option='{骄阳黄遮阳帽,61},{宝石王冠,62},{海棠花发夹,63}',}",
  [152790] = "{id=152790,Text='呼~挑选好了，当当当~女王大人驾到！',Speaker=890880,}",
  [152791] = "{id=152791,Text='衣服换好了，我们为女王大人准备什么头饰呢？',Speaker=890880,Option='{骄阳黄遮阳帽,71},{宝石王冠,72},{海棠花发夹,73}',}",
  [152792] = "{id=152792,Text='呼~挑选好了，当当当~女王大人驾到！',Speaker=890880,}",
  [152793] = "{id=152793,Text='搭配的好棒，这正是女王大人的威仪！',Speaker=890881,Emoji=9,}",
  [152794] = "{id=152794,Text='辛苦你了，这是一点谢礼，请收下吧~',Speaker=890881,}",
  [152795] = "{id=152795,Text='拜托，这可是堂堂的加冕典礼诶，你给我挑选的都是什么嘛！',Speaker=890881,Emoji=1,}",
  [152796] = "{id=152796,Text='算了，看在你辛苦为本女王大人挑选衣服的份上，这份小礼物还请收下吧~',Speaker=890881,}",
  [152797] = "{id=152797,Text='今天的安娜女王没有外出活动的安排，而是需要向王国的执政官莫奇大人请教了解身为女王的职责，学习女王的仪礼。',Speaker=890880,}",
  [152798] = "{id=152798,Text='也就是说，女王今天只会待在家里哦~穿什么衣服好呢？',Speaker=890880,Option='{华丽的礼服,5},{舒适的常服,6},{时尚的连衣裙,7}',}",
  [152799] = "{id=152799,Text='搭配的不错嘛，居家的女王大人也要如此美丽呢！',Speaker=890881,Emoji=9,}",
  [152800] = "{id=152800,Text='哼哼，你确定平日在家的女王大人也要穿成这个样子吗？',Speaker=890881,Emoji=1,}",
  [152801] = "{id=152801,Text='安娜女王今天要在阳光明媚的午后和英俊的维兰特王子结伴去野餐哦~',Speaker=890880,}",
  [152802] = "{id=152802,Text='这真是一个十分浪漫的场合呢~穿什么衣服好呢？',Speaker=890880,Option='{华丽的礼服,5},{舒适的常服,6},{时尚的连衣裙,7}',}",
  [152803] = "{id=152803,Text='搭配的不错嘛，今天是野餐会上清新靓丽的女王大人呢！',Speaker=890881,Emoji=9,}",
  [152804] = "{id=152804,Text='拜托了，谁会穿成这个样子去和王子野餐嘛！',Speaker=890881,Emoji=1,}",
  [152805] = "{id=152805,Text='冒险者，你有听说过普隆德拉植树节吗？',Speaker=807798,}",
  [152806] = "{id=152806,Text='在这个节日里，人们通常都会通过种树这种方式来表达对普隆德拉的祝福呢，祝福它永远美丽而富有生机！',Speaker=807798,Emoji=20,}",
  [152807] = "{id=152807,Text='冒险者，你愿意为这个节日做出你的一份贡献吗？',Speaker=807798,}",
  [152808] = "{id=152808,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=807798,Option='{我愿意,2},{稍等,3}',}",
  [152809] = "{id=152809,Text='呵呵，不要紧，等你有空再来找我吧！',Speaker=807798,}",
  [152810] = "{id=152810,Text='非常感谢，树苗的话，我这里就可以领到呢！',Speaker=807798,}",
  [152811] = "{id=152811,Text='现在可以去种树啦!',}",
  [152812] = "{id=152812,Text='树苗已经种下了,再浇点水吧。',}",
  [152813] = "{id=152813,Text='回去告诉K上校今天的树苗已经种下了吧!',}",
  [152814] = "{id=152814,Text='辛苦了，普隆德拉植树节因你而更精彩！',Speaker=807798,}",
  [152822] = "{id=152822,Text='冒险者，我是来自希尔帕恩国的使者，虽然你之前或许没听说过这个国家~',Speaker=890898,}",
  [152823] = "{id=152823,Text='不过呢，我们的女王大人，也就是安娜小姐，你是一定见过的呢！',Speaker=890898,}",
  [152824] = "{id=152824,Text='怎么样，想不想去找她聊聊？',Speaker=890898,}",
  [152825] = "{id=152825,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=890898,Option='{没问题,2},{现在没时间,3}',}",
  [152826] = "{id=152826,Text='想好了一定要再来找我哦~',Speaker=890898,}",
  [152827] = "{id=152827,Text='麻烦你啦~',Speaker=890898,}",
  [152828] = "{id=152828,Text='咦，我看看是谁来了……',Speaker=890899,}",
  [152829] = "{id=152829,Text='哎呀！这不是[c][ffff00][PlayerName][-][/c]吗？',Speaker=890899,Option='女王大人好',}",
  [152830] = "{id=152830,Text='啊呀……怎么你也叫我女王大人……',Speaker=890899,Emoji=38,}",
  [152831] = "{id=152831,Text='看来这件事都已经传到了你这里了，真叫人难为情呢……',Speaker=890899,}",
  [152833] = "{id=152833,Text='虽然我承认自己很喜欢看王室题材的话剧啦，那部《艾尔帕兰假日》我可是去剧场看了好多遍呢……',Speaker=890899,}",
  [152834] = "{id=152834,Text='可是，管我叫“女王大人”？！',Speaker=890899,}",
  [152835] = "{id=152835,Text='这也太夸张了吧，中间一定有什么误会！',Speaker=890899,Emoji=19,}",
  [152836] = "{id=152836,Text='嗯……我的朋友艾伦说他这两天要帮我调查一下这件事的。',Speaker=890899,}",
  [152837] = "{id=152837,Text='冒险者，我这里还在工作，你能帮我去找找艾伦，看看有什么能帮上忙的吗？',Speaker=890899,Emoji=27,}",
  [152838] = "{id=152838,Text='你好，冒险者。有什么事情吗？',Speaker=890900,Option='说明来意',}",
  [152839] = "{id=152839,Text='是安娜让你来找我的啊，是不是因为那只奇怪的黑猫？',Speaker=890900,}",
  [152840] = "{id=152840,Text='那只黑猫也是最近才突然冒出来的，说起来，我的确是有点担心这背后有什么对安娜不利的阴谋……',Speaker=890900,Option='我可以帮忙吗？',}",
  [152841] = "{id=152841,Text='啊啊真是太感谢了，我想拜托你去调查一下那只黑猫的事情~',Speaker=890900,}",
  [152842] = "{id=152842,Text='毕竟，那只黑猫见到过不少次我和安娜在一起~',Speaker=890900,}",
  [152843] = "{id=152843,Text='我过去找它的话它或许会提高警惕呢。',Speaker=890900,Emoji=15,}",
  [152844] = "{id=152844,Text='这只黑猫最近就在广场附近活动，我把昨天买好的猫粮送给你好了，也许撒些猫粮就能吸引它过来。',Speaker=890900,}",
  [152845] = "{id=152845,Text='只要把猫粮丢在这里，应该就可以了吧……',}",
  [152846] = "{id=152846,Text='退到远处看看莫奇会不会过来吧',}",
  [152847] = "{id=152847,Text='一只黑猫出现了，那是莫奇吗？',}",
  [152848] = "{id=152848,Text='喵喵喵，今天有幸投喂了本大人的就是你吗？',Speaker=890901,Option='你就是莫奇吗？',}",
  [152849] = "{id=152849,Text='喵喵喵你竟敢直呼本大人的名字。在你面前的可是希尔帕恩国首席执政官莫奇公爵大人！',Speaker=890901,}",
  [152850] = "{id=152850,Text='而安娜呢，就是我们的女王大人啦！',Speaker=890901,Option='表达怀疑',Emoji=27,}",
  [152851] = "{id=152851,Text='……咳咳，我们国家向来避世独存，你当然没听说过啦。',Speaker=890901,}",
  [152852] = "{id=152852,Text='不过，你有兴趣作为安娜女王的特使来协助我一同为女王大人服务吗？',Speaker=890901,}",
  [152853] = "{id=152853,Text='安娜女王大人那么可爱，你一定会愿意的！',Speaker=890901,Option='表示拒绝',Emoji=9,}",
  [152854] = "{id=152854,Text='喵喵喵你说什么？好吧，哪怕你不相信我，你就不想帮助每天都在辛苦工作的安娜小姐圆一次女王梦吗？',Speaker=890901,}",
  [152855] = "{id=152855,Text='这可是一件十分有意义的事情呢！',Speaker=890901,Option='好……好吧',}",
  [152856] = "{id=152856,Text='这才对嘛，不过今天已经时候不早了，明天再来找我吧。到时我有重要的工作交给你。',Speaker=890901,}",
  [152857] = "{id=152857,Text='你来啦，我们可以开始商议怎么为安娜女王效劳的事情了。',Speaker=890901,}",
  [152858] = "{id=152858,Text='现在就像你所看到的，安娜到现在还是对她是女王大人这件事半信半疑。',Speaker=890901,}",
  [152859] = "{id=152859,Text='你知道怎么能让安娜女王大人开心起来吗喵？',Speaker=890901,Option='不知道',}",
  [152860] = "{id=152860,Text='笨蛋，安娜最爱的《艾尔帕兰假日》你难道没看过吗喵喵喵？',Speaker=890901,}",
  [152861] = "{id=152861,Text='这部戏里的维尔达王子殿下可是安娜多年以来的梦中情人呢！',Speaker=890901,}",
  [152862] = "{id=152862,Text='唔……本大人虽然会魔法，但凭空变个王子出来还是有难度的……',Speaker=890901,}",
  [152863] = "{id=152863,Text='等等……有了，其实，我们可以来找个人扮演王子嘛！',Speaker=890901,}",
  [152864] = "{id=152864,Text='哼哼，在我高超的魔法下，我保证叫那个人摇身一变，成为一个魅力十足的王子！',Speaker=890901,}",
  [152865] = "{id=152865,Text='那么，女王特使阁下，在寻找王子这件事上，你有什么合适的人选吗？',Speaker=890901,Option='{没有,4},{似乎是艾伦？,5}',}",
  [152866] = "{id=152866,Text='喵喵喵，可你之前不是见过了安娜那位叫艾伦的朋友了嘛！',Speaker=890901,}",
  [152867] = "{id=152867,Text='没错没错，就是他。',Speaker=890901,}",
  [152868] = "{id=152868,Text='我经常看见安娜和那个艾伦打打闹闹的，看起来他们好像关系挺不错的喵。',Speaker=890901,}",
  [152869] = "{id=152869,Text='不如我们就找这位艾伦聊聊吧。',Speaker=890901,}",
  [152870] = "{id=152870,Text='是你啊冒险者，怎么样，那只黑猫调查得怎么样了？',Speaker=890900,Option='说明来意',}",
  [152871] = "{id=152871,Text='什么什么？它让我来扮演王子？！',Speaker=890900,Emoji=1,}",
  [152872] = "{id=152872,Text='开什么玩笑嘛，它一定是在捉弄我！',Speaker=890900,}",
  [152873] = "{id=152873,Text='喵喵喵，怎么，你不乐意扮演王子吗？太遗憾了，那本大人只好把[c][ffff00][PlayerName][-][/c]变成王子去和安娜女王一起野餐了。',Speaker=890901,}",
  [152874] = "{id=152874,Text='他或许会很乐意哦~',Speaker=890901,Option='……',}",
  [152875] = "{id=152875,Text='……等一下！',Speaker=890900,}",
  [152876] = "{id=152876,Text='好吧好吧，我答应就是了。',Speaker=890900,}",
  [152877] = "{id=152877,Text='不过，我可要好好为此准备一下。',Speaker=890900,Emoji=38,}",
  [152878] = "{id=152878,Text='前几天，一个淘气的波利抢走了我一顶很漂亮的礼帽。',Speaker=890900,}",
  [152879] = "{id=152879,Text='或许这顶礼帽能派上用场呢，你们能帮我夺回来吗？',Speaker=890900,}",
  [152880] = "{id=152880,Text='谢谢，辛苦了。我的礼帽回来了。',Speaker=890900,Emoji=17,}",
  [152881] = "{id=152881,Text='唉，不过，哪怕收回了这顶礼帽，我还是离一个王子差得远呢！',Speaker=890902,Emoji=15,}",
  [152882] = "{id=152882,Text='喵喵喵，看本公爵大人的魔法！',Speaker=890901,}",
  [152883] = "{id=152883,Text='当当当~从此刻起，你就是古里达尔国高贵的维兰特王子殿下啦！',Speaker=890901,}",
  [152884] = "{id=152884,Text='王子殿下，您对自己的仪表还满意吗？',Speaker=890901,}",
  [152885] = "{id=152885,Text='唔……能从繁忙的工作中抽身来变成一个王子来哄安娜开心……',Speaker=890903,}",
  [152886] = "{id=152886,Text='好像也挺有意义的……',Speaker=890903,}",
  [152887] = "{id=152887,Text='不过，我们说好了，这只是为了圆一次安娜成为女王的梦想，可不能做得太过火了惹她生气哦！',Speaker=890903,}",
  [152888] = "{id=152888,Text='没错没错，特使阁下，你快去找安娜女王，和她说她后天要和一位王子野餐啦！',Speaker=890901,}",
  [152889] = "{id=152889,Text='是你呀[c][ffff00][PlayerName][-][/c]，是关于那只黑猫的事情吗？',Speaker=890899,Option='说明来意',}",
  [152890] = "{id=152890,Text='哇哦~什么什么，你是说我后天就要见到一位王子了吗？',Speaker=890899,}",
  [152891] = "{id=152891,Text='而且，这位王子殿下还非常英俊？',Speaker=890899,Emoji=27,}",
  [152892] = "{id=152892,Text='呼呼~那就暂且相信一下那只叫莫奇的黑猫吧！明天我可一定要好好准备一下了！',Speaker=890899,}",
  [152893] = "{id=152893,Text='辛苦你了，冒险者，我们明天再会！',Speaker=890899,}",
  [152894] = "{id=152894,Text='你来啦[c][ffff00][PlayerName][-][/c]，喜欢我今天的装扮吗？',Speaker=890899,}",
  [152895] = "{id=152895,Text='这可是莫奇用它的魔法变出来的！呀呼~真是神奇呢！',Speaker=890899,}",
  [152896] = "{id=152896,Text='喵喵喵，很荣幸得到您的夸赞，萌萌女王大人！还有什么能为您服务的吗？',Speaker=890901,}",
  [152897] = "{id=152897,Text='唔……别的也没有什么，就是我的肚子有些饿了……',Speaker=890899,}",
  [152898] = "{id=152898,Text='我之前也没有当女王大人的经验啦，不过，成为女王的话，是不是就可以有很多好吃的了！',Speaker=890899,Emoji=17,}",
  [152899] = "{id=152899,Text='没问题，女王大人！喵喵喵，看来我们大家都有对美食的喜好呢！',Speaker=890901,}",
  [152900] = "{id=152900,Text='那就拜托你啦，特使阁下，去皇家料理协会订购一份料理回来吧！',Speaker=890901,}",
  [152901] = "{id=152901,Text='有什么事情吗，冒险者？',Speaker=890907,Option='说明来意',}",
  [152902] = "{id=152902,Text='啊，是想为安娜小姐订购一份美味料理呢~',Speaker=890907,}",
  [152903] = "{id=152903,Text='当然没有问题，只不过，料理的美味是易得的，可如果想让品尝料理的人感受到幸福感——',Speaker=890907,}",
  [152904] = "{id=152904,Text='朋友们的参与则是必不可少的呢！',Speaker=890907,}",
  [152905] = "{id=152905,Text='呵呵，怎么样，你想不想亲自为安娜小姐制作一份料理呢？',Speaker=890907,Option='可以吗？',}",
  [152906] = "{id=152906,Text='当然可以了，我们这里的食材和料理台都可以为你提供！',Speaker=890907,}",
  [152907] = "{id=152907,Text='料理台已经为你准备好了，去施展你的厨艺吧！',Speaker=890907,}",
  [152908] = "{id=152908,Text='呼~做好了，把料理带给安娜吧。',}",
  [152909] = "{id=152909,Text='谢谢你啦，不要客气，我们大家一起来享用吧！',Speaker=890899,}",
  [152910] = "{id=152910,Text='这真是太美味了！谢谢你们！',Speaker=890899,Emoji=9,}",
  [152911] = "{id=152911,Text='喵喵喵。特使阁下，你出色地完成了任务呢~',Speaker=890901,}",
  [152912] = "{id=152912,Text='最后，那就再麻烦你去见下维兰特王子吧，提醒他不要忘记明天的野餐会。',Speaker=890901,}",
  [152913] = "{id=152913,Text='有劳你啦，嘻嘻，本女王大人可是对此期待已久了呢！',Speaker=890899,}",
  [152914] = "{id=152914,Text='嗯，不过……如果你顺路的话，可不可以再劳烦你给我的朋友艾伦带一份料理呢。',Speaker=890899,}",
  [152915] = "{id=152915,Text='毕竟，我们可是经常一起分享美味的食物呢！',Speaker=890899,Option='……是顺路的',Emoji=38,}",
  [152916] = "{id=152916,Text='那真是太好啦！谢谢你！！',Speaker=890899,Emoji=17,}",
  [152917] = "{id=152917,Text='是明天野餐会的事情吗？',Speaker=890903,}",
  [152918] = "{id=152918,Text='好的我知道了，明天我会去赴约的。',Speaker=890903,}",
  [152919] = "{id=152919,Text='可是[c][ffff00][PlayerName][-][/c]啊，你没觉得那只黑猫有些不对劲吗？',Speaker=890903,Option='{确实不对劲,2},{没有觉得,3}',}",
  [152920] = "{id=152920,Text='你看，我就说嘛。它虽然自称是来自那个希尔帕恩国的公爵啦执政官啦~',Speaker=890903,}",
  [152921] = "{id=152921,Text='你再仔细想想，你看，它虽然自称是来自那个希尔帕恩国的公爵啦执政官啦~',Speaker=890903,}",
  [152922] = "{id=152922,Text='但我之前却从来没听说过希尔帕恩国，地图上也根本找不到它。',Speaker=890903,}",
  [152923] = "{id=152923,Text='不过……照目前看来，演下去似乎也没什么害处……那明天就当是为安娜圆一次女王梦好了。',Speaker=890903,}",
  [152924] = "{id=152924,Text='但辛苦你了，冒险者，谢谢你的料理，我们明天好好留意一下那只黑猫的举动。',Speaker=890903,}",
  [152925] = "{id=152925,Text='辛苦你了，冒险者，谢谢你的料理，我们明天见！',Speaker=890903,}",
  [152926] = "{id=152926,Text='你差点就迟到了喵，特使阁下！野餐会马上就要开始了！',Speaker=890901,}",
  [152927] = "{id=152927,Text='而在野餐会开始之前，我们还有一些前期的准备工作要做呢。',Speaker=890901,}",
  [152928] = "{id=152928,Text='准备好了吗？我们这就赶到野餐的地点。',Speaker=890901,Option='{准备好了,2},{再等等,3}',}",
  [152929] = "{id=152929,Text='喵喵喵你快点准备啦！',Speaker=890901,}",
  [152930] = "{id=152930,Text='喵喵喵那我们就出发吧！',Speaker=890901,Option='好的',}",
  [152931] = "{id=152931,Text='唔……这里的环境还是不错的嘛！',Speaker=890901,}",
  [152932] = "{id=152932,Text='我们要准备布置野餐现场了，你可以先去采集一些美丽的小野花吗？',Speaker=890901,}",
  [152933] = "{id=152933,Text='哟~还是挺快的嘛，辛苦你了！',Speaker=890901,}",
  [152934] = "{id=152934,Text='现在，我要开始布置了！',Speaker=890901,}",
  [152935] = "{id=152935,Text='唔……把你采集到的野花布置上去吧！',Speaker=890901,}",
  [152936] = "{id=152936,Text='这样子应该就可以了吧……瞧，他们来了！',Speaker=890901,}",
  [152937] = "{id=152937,Text='尊敬的女王陛下，您好。',Speaker=890903,}",
  [152938] = "{id=152938,Text='……幸会，王子殿下。',Speaker=890908,}",
  [152939] = "{id=152939,Text='……',Speaker=890903,}",
  [152940] = "{id=152940,Text='……',Speaker=890908,}",
  [152941] = "{id=152941,Text='现场的气氛就这样尴尬了许久。',}",
  [152942] = "{id=152942,Text='安娜……女王陛下，见到您十分的开心，只是我的身体突然有些不适，恐怕要先行告退了。',Speaker=890903,}",
  [152943] = "{id=152943,Text='嗯……好的。',Speaker=890908,}",
  [152944] = "{id=152944,Text='抱歉……希望您能拥有愉快的一天。',Speaker=890903,}",
  [152945] = "{id=152945,Text='怎么回事，真是令人苦恼，安娜好像并没有显得很高兴。',Speaker=890901,}",
  [152946] = "{id=152946,Text='可维兰特已经是世界上最英俊的王子了，难道她还不满意吗？',Speaker=890901,}",
  [152947] = "{id=152947,Text='喵喵喵，人类的心思可真难猜透呢……',Speaker=890901,}",
  [152948] = "{id=152948,Text='唉，[c][ffff00][PlayerName][-][/c]你能帮我再去打听一下安娜女王大人的心意吗？',Speaker=890901,}",
  [152949] = "{id=152949,Text='啊……王子殿下当然是英俊优雅的，但我总觉得他有些怪怪的。',Speaker=890908,}",
  [152950] = "{id=152950,Text='唉，话说这么多天都没见到艾伦了呢，我还有点想念那个家伙了呢……也不知道他现在怎么样了。',Speaker=890908,}",
  [152951] = "{id=152951,Text='不管怎么样，我要再好好想一想了……',Speaker=890908,}",
  [152952] = "{id=152952,Text='莫奇大人那边的话，明天我们再和他聊聊吧~',Speaker=890908,}",
  [152953] = "{id=152953,Text='[c][ffff00][PlayerName][-][/c]你终于来了~',Speaker=890903,}",
  [152954] = "{id=152954,Text='你知道吗？那只黑猫竟然还不打算停下来。接下来还要一直安排我和安娜的约会呢！',Speaker=890903,Option='你不愿意吗？',}",
  [152955] = "{id=152955,Text='好吧好吧，我承认我是很喜欢安娜啦~',Speaker=890903,}",
  [152956] = "{id=152956,Text='可是凭借着这副乔装后的面目去赢取安娜的芳心……',Speaker=890903,}",
  [152957] = "{id=152957,Text='这摆明了就是赤裸裸的瞒骗嘛！我才不要做这样的事情！',Speaker=890903,}",
  [152958] = "{id=152958,Text='嗯，我们这就去找安娜坦白一切吧，这场闹剧该结束了！',Speaker=890903,}",
  [152959] = "{id=152959,Text='我好像听到了有人计划反抗本大人的安排了呢喵喵喵！',Speaker=890901,}",
  [152960] = "{id=152960,Text='你做得已经太过分了，住手吧！',Speaker=890903,Emoji=12,Action={actionid=8,num=1},}",
  [152961] = "{id=152961,Text='喵喵喵，任何阻拦我实现安娜女王大人的梦想的人都是坏蛋！',Speaker=890901,}",
  [152962] = "{id=152962,Text='废话少说，你赶快把我变回艾伦！',Speaker=890903,}",
  [152963] = "{id=152963,Text='哼，为了安娜女王大人，我看你还是乖乖地当好王子吧。',Speaker=890901,}",
  [152964] = "{id=152964,Text='你对我做了什么？',Speaker=890903,Emoji=37,}",
  [152965] = "{id=152965,Text='咳咳，维兰特王子，你愿意接下来永远以王子的身份和安娜女王大人约会，赢得她的爱情吗？',Speaker=890901,}",
  [152966] = "{id=152966,Text='我不……呜呜呜呜……我愿意。',Speaker=890903,}",
  [152967] = "{id=152967,Text='哼哼，那现在就还剩下你了，冒险者，怎么，连你也要违抗我吗？',Speaker=890901,Option='赶快住手！',Emoji=5,}",
  [152968] = "{id=152968,Text='我才不要呢……唔……安娜女王大人怎么过来了？',Speaker=890901,}",
  [152969] = "{id=152969,Text='请等一下，莫奇，不，我不愿意哦~',Speaker=890899,Action={actionid=201,num=1},}",
  [152970] = "{id=152970,Text='什……什么……',Speaker=890901,}",
  [152971] = "{id=152971,Text='因为呢，我其实并不是什么女王大人，偶尔和王子野餐一次我当然很开心啦~',Speaker=890899,}",
  [152972] = "{id=152972,Text='可如果一直这样下去欺骗他的话，那就很不对了呢！',Speaker=890899,Emoji=38,}",
  [152973] = "{id=152973,Text='嗯，没有爱情的话，哪怕是和一位王子约会也要好好考虑一下呢！',Speaker=890899,}",
  [152974] = "{id=152974,Text='喵……为什么……一切努力都白费了吗……',Speaker=890901,}",
  [152975] = "{id=152975,Text='[c][ffff00][PlayerName][-][/c]莫奇的魔法好像失效了！快，抓住机会！',Speaker=890900,}",
  [152976] = "{id=152976,Text='喵喵喵，背叛了本大人的你们全都是坏蛋，不可饶恕！',Speaker=890901,Emoji=12,}",
  [152977] = "{id=152977,Text='我竟然失败了……',Speaker=890901,Emoji=24,}",
  [152978] = "{id=152978,Text='不用担心……我很好。安娜没事吗？',Speaker=890902,}",
  [152979] = "{id=152979,Text='是你！艾伦！',Speaker=890899,}",
  [152980] = "{id=152980,Text='……是我，安娜。',Speaker=890902,}",
  [152981] = "{id=152981,Text='哼哼，这一切原来就是你和那只黑猫合伙搞的恶作剧！',Speaker=890899,}",
  [152982] = "{id=152982,Text='噗嗤，不过，除去最后这个惊险的结尾，一切都还蛮有意思的嘛！',Speaker=890899,Emoji=9,}",
  [152983] = "{id=152983,Text='安娜……还好你没有生气。',Speaker=890902,}",
  [152984] = "{id=152984,Text='嘻嘻，哪里会生气呢，毕竟我已经体会到大家的心意了呢！',Speaker=890899,Emoji=27,}",
  [152985] = "{id=152985,Text='只是……莫奇好像躲在那里有些失落了呢，我们赶快过去安慰一下它吧。',Speaker=890899,Option='我这就去',}",
  [152986] = "{id=152986,Text='唉，我原本是一只喜欢潜入人们梦境的小精灵。',Speaker=890901,}",
  [152987] = "{id=152987,Text='每天晚上，我都会变成一只猫，趴在人家的窗外，品味他们的梦境。',Speaker=890901,}",
  [152988] = "{id=152988,Text='而安娜每个夜晚的美梦都令我感受到了快乐~',Speaker=890901,Emoji=17,}",
  [152989] = "{id=152989,Text='所以，我也想回报她，去将她的美梦在现实中实现。',Speaker=890901,}",
  [152990] = "{id=152990,Text='只是……或许方式可能真的有些不对呢。',Speaker=890901,}",
  [152991] = "{id=152991,Text='喵喵喵，可是不管怎样，祝你快乐呀，安娜小姐，即便你不再是女王大人了，在每个夜晚也要幸福常在哦~',Speaker=890901,}",
  [152992] = "{id=152992,Text='嗯，谢谢你啦，莫奇！',Speaker=890899,Emoji=27,}",
  [152994] = "{id=152994,Text='幸运的冒险者们，享受安娜女王赐予我们无上的祝福吧~',Speaker=890865,Emoji=5,}",
  [152995] = "{id=152995,Text='想知道你今日运势如何吗？那就来我这儿算上一卦吧...啊呸~',Speaker=890865,}",
  [152996] = "{id=152996,Text='本小姐专业占卜，童叟无欺~',Speaker=890865,Emoji=4,}",
  [153002] = "{id=153002,Text='嘿嘿，来猜拳吗？猜拳可是在旅途中用来解乏的好方法呢。想不想和我较量一下？',Speaker=890913,}",
  [153003] = "{id=153003,Text='如果你赢了，你就可以得到我准备的一份旅行纪念品，但也小心不要输到哭鼻子哦！',Speaker=890913,}",
  [153004] = "{id=153004,Text='嘿！一场精彩刺激的战斗要开始了！',Speaker=890913,}",
  [153005] = "{id=153005,Text='三！',Speaker=890913,}",
  [153006] = "{id=153006,Text='二！',Speaker=890913,}",
  [153007] = "{id=153007,Text='一！',Speaker=890913,}",
  [153008] = "{id=153008,Text='剪刀石头布——',Speaker=890913,Option='{石头,40},{剪刀,50},{布,60}',}",
  [153010] = "{id=153010,Text='嘿嘿，我赢了！',Speaker=890913,Emoji=17,}",
  [153011] = "{id=153011,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=890913,Option='{再玩一次,103},{不玩啦,102}',}",
  [153012] = "{id=153012,Text='[c][ffff00]【猜拳游戏同账号仅限一个角色接取，请谨慎考虑是否继续】[-][/c]',Speaker=890913,Option='{来吧,2},{再考虑一下,3}',Emoji=5,}",
  [153014] = "{id=153014,Text='哦吼？不简单嘛！',Speaker=890913,Emoji=1,}",
  [153015] = "{id=153015,Text='唔 … … 没有分出胜负呢。',Speaker=890913,}",
  [153016] = "{id=153016,Text='怎么样，要不要再来一局？',Speaker=890913,Option='{再玩一次,104},{不玩啦,101}',Emoji=8,}",
  [153017] = "{id=153017,Text='哟～你赢了呢！',Speaker=890913,Emoji=7,}",
  [153018] = "{id=153018,Text='好吧，我可不是赖帐的人，这份旅行纪念品就送给你了！',Speaker=890913,}",
  [153019] = "{id=153019,Text='太遗憾了，一个资深旅行家准备的纪念品礼物可是很诱人的呢！',Speaker=890913,}",
  [153020] = "{id=153020,Text='呵呵。倒也不失为明智的选择呢，毕竟我是这么厉害！',Speaker=890913,Emoji=17,}",
  [153021] = "{id=153021,Text='哎哟，看来你倒是挺有勇气的嘛，那我们来吧！',Speaker=890913,}",
  [153022] = "{id=153022,Text='就这么结束了吗 … …',Speaker=890913,Emoji=19,}",
  [153023] = "{id=153023,Text='没错，我们再来再来！',Speaker=890913,}",
  [153025] = "{id=153025,Text='你好啊冒险者，唔……有时间来做一道简单的病毒防护知识的问答题吗？',Speaker=890159,Option='{没问题,2},{再等等,3}',}",
  [153026] = "{id=153026,Text='没关系，等今天你有空再来找我吧！',Speaker=890159,}",
  [153027] = "{id=153027,Text='好啦，那我们这就开始吧，请听题——',Speaker=890159,}",
  [153028] = "{id=153028,Text='以下哪种不是新型冠状病毒肺炎的传播途径？',Speaker=890159,Option='{呼吸道飞沫传播,5},{接触传播,5},{眼神对视传播,6}',}",
  [153029] = "{id=153029,Text='唉，你答错了呢！病毒防护知识很重要哦，再去想一下吧！',Speaker=890159,Option='我再想想',}",
  [153030] = "{id=153030,Text='恭喜你答对了呢！呼吸道飞沫传播和接触传播都是新型冠状病毒肺炎的传播途径，可一定要记住哦！',Speaker=890159,Emoji=27,}",
  [153031] = "{id=153031,Text='感谢你对病毒防护大作战行动的支持，明天记得再来哦~',Speaker=890159,}",
  [153032] = "{id=153032,Text='以下哪种措施对于预防新冠病毒感染是无效的呢？',Speaker=890159,Option='{戴口罩,5},{晒太阳,6},{勤洗手,5}',}",
  [153033] = "{id=153033,Text='唉，你答错了呢！病毒防护知识很重要哦，再去想一下吧！',Speaker=890159,Option='我再想想',}",
  [153034] = "{id=153034,Text='恭喜你答对了呢！戴口罩和勤洗手都是有效的预防病毒的措施，可一定要记住哦！',Speaker=890159,Emoji=27,}",
  [153035] = "{id=153035,Text='进行外出活动时，有条件的话最好选择哪一种口罩？',Speaker=890159,Option='{纸质口罩,5},{棉布口罩,5},{医用外科口罩,6}',}",
  [153036] = "{id=153036,Text='唉，你答错了呢！病毒防护知识很重要哦，再去想一下吧！',Speaker=890159,Option='我再想想',}",
  [153037] = "{id=153037,Text='恭喜你答对了呢！纸质口罩和棉布口罩对病毒的防护力度都是不足的呢，有条件的话最好选择一次性的医用外科口罩哦！',Speaker=890159,Emoji=27,}",
  [153038] = "{id=153038,Text='以下哪种液体对于杀灭体外病毒是无效的呢？',Speaker=890159,Option='{盐水,6},{酒精,5},{84消毒液,5}',}",
  [153039] = "{id=153039,Text='唉，你答错了呢！病毒防护知识很重要哦，再去想一下吧！',Speaker=890159,Option='我再想想',}",
  [153040] = "{id=153040,Text='恭喜你答对了呢！酒精和84消毒液对于杀灭体外的病毒都是有效的，但要记得千万不要将它们混在一起使用哦！',Speaker=890159,Emoji=27,}",
  [153041] = "{id=153041,Text='以下哪种做法对于提高身体的免疫力是无效的呢？',Speaker=890159,Option='{多锻炼,5},{饮酒,6},{规律作息,5}',}",
  [153042] = "{id=153042,Text='唉，你答错了呢！病毒防护知识很重要哦，再去想一下吧！',Speaker=890159,Option='我再想想',}",
  [153043] = "{id=153043,Text='恭喜你答对了呢！多锻炼和规律作息都能有效提高身体的免疫力，所以从今天起我们大家一同健康生活吧！',Speaker=890159,Emoji=27,}",
  [153044] = "{id=153044,Text='感谢你对病毒防护大作战行动的支持，再见啦，亲爱的冒险者！',Speaker=890159,}",
  [153058] = "{id=153058,Text='冒险者，你有没有感觉到，最近普隆德拉城内气氛不太对劲啊？',Speaker=890919,Option='{感觉到了,2},{没有,3}',}",
  [153059] = "{id=153059,Text='好吧，那我告诉你吧',Speaker=890919,}",
  [153060] = "{id=153060,Text='果然你也感觉到了吗！',Speaker=890919,}",
  [153061] = "{id=153061,Text='最近城里接连发生了许多怪事。',Speaker=890919,}",
  [153062] = "{id=153062,Text='许多人报案说自己的家人朋友莫名失踪又突然回来了。',Speaker=890919,}",
  [153063] = "{id=153063,Text='但奇怪的是，这些人回来之后性情大变，像中了邪一样。',Speaker=890919,}",
  [153064] = "{id=153064,Text='我知道城里有个名侦探叫卡兹，你能去问问他到底怎么回事嘛？',Speaker=890919,}",
  [153065] = "{id=153065,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=890919,Option='{好呀,4},{我很忙,5}',}",
  [153066] = "{id=153066,Text='哎……好吧，那就不打扰你忙了。',Speaker=890919,}",
  [153067] = "{id=153067,Text='哎呀，真是感激不尽！',Speaker=890919,Emoji=6,}",
  [153068] = "{id=153068,Text='哇！是冒险者！我想请你帮个忙！',Speaker=890920,Option='我也是',}",
  [153069] = "{id=153069,Text='你找我什么事？',Speaker=890920,Option='说明来意',}",
  [153070] = "{id=153070,Text='巧了！我也正好因为这事找你呢。',Speaker=890920,}",
  [153071] = "{id=153071,Text='我在过来的路上看到几个卡普拉小姐姐在骂骂咧咧，好凶哦！',Speaker=890920,}",
  [153072] = "{id=153072,Text='我们赶紧过去看看怎么回事吧！',Speaker=890920,}",
  [153073] = "{id=153073,Text='我受够了啦！每天都像工具人一样给你们这些冒险者服务，真是烦死了！',Speaker=890921,}",
  [153074] = "{id=153074,Text='工资还那么低，吃饭都吃不起了！',Speaker=890922,}",
  [153075] = "{id=153075,Text='我不干了！',Speaker=890923,}",
  [153076] = "{id=153076,Text='我不干了！',Speaker=890921,}",
  [153077] = "{id=153077,Text='你看她们果然很奇怪！我从来没见过这么凶的卡普拉小姐姐。',Speaker=890920,Emoji=1,}",
  [153078] = "{id=153078,Text='我这儿有点药水，可以让她们暂时冷静下来，快给她们用吧，然后问问什么情况。',Speaker=890920,}",
  [153079] = "{id=153079,Text='唔……头好痛。我这是怎么了，怎么在这里？',Speaker=890921,}",
  [153080] = "{id=153080,Text='我不是在帮冒险者们开传送的么……',Speaker=890922,Emoji=19,}",
  [153081] = "{id=153081,Text='我好像说了什么不好的话……',Speaker=890923,}",
  [153082] = "{id=153082,Text='你问我怎么了？我也不知道呀~就觉得头好晕，回过神来就在这里了。',Speaker=890921,Emoji=19,}",
  [153083] = "{id=153083,Text='什么？！你说我骂人了？我的天哪……',Speaker=890922,Emoji=24,}",
  [153084] = "{id=153084,Text='我是如此热爱这份工作，怎么会说出罢工这样的鬼话呢！',Speaker=890923,Emoji=15,}",
  [153085] = "{id=153085,Text='会不会是最近加班太多累着了。',Speaker=890921,}",
  [153086] = "{id=153086,Text='骂了你们实在对不起！',Speaker=890922,}",
  [153087] = "{id=153087,Text='我们回去会继续好好工作为各位冒险者服务的！',Speaker=890923,}",
  [153088] = "{id=153088,Text='好了，我们要回去工作了，有需要欢迎随时找我们哦~再见~',Speaker=890921,Emoji=20,}",
  [153089] = "{id=153089,Text='看来这事情不简单啊……',Speaker=890920,}",
  [153090] = "{id=153090,Text='侦探的直觉告诉我，这绝非意外，一定有什么阴谋。',Speaker=890920,Option='我也这么觉得',}",
  [153091] = "{id=153091,Text='冒险者，我有一种感觉。',Speaker=890920,}",
  [153092] = "{id=153092,Text='冥冥之中有一种力量在推动着这个世界发展。',Speaker=890920,}",
  [153093] = "{id=153093,Text='但我和其他人都无法改变。',Speaker=890920,}",
  [153094] = "{id=153094,Text='只有你，拥有改变世界的力量。',Speaker=890920,Emoji=26,}",
  [153095] = "{id=153095,Text='所以，请你救救普隆德拉吧！',Speaker=890920,Option='{乐意帮忙,2},{这……,3}',}",
  [153096] = "{id=153096,Text='难道你要看着普隆德拉陷入危险而不顾吗？',Speaker=890920,}",
  [153097] = "{id=153097,Text='真的吗！太好了！谢谢你！',Speaker=890920,Emoji=17,}",
  [153098] = "{id=153098,Text='那么我们明天在普隆德拉喷泉边见面吧，我还有点事要调查，先走了哦~明天见！',Speaker=890920,Option='明天见',}",
  [153099] = "{id=153099,Text='呦，呦，你就是，传——说中的冒——险者么！',Speaker=890924,}",
  [153100] = "{id=153100,Text='今天换个方式讲故事，跟着美妙的律动一起来！呦、呦……',Speaker=890924,}",
  [153101] = "{id=153101,Text='（总感觉他身上哪里不对劲……）',}",
  [153102] = "{id=153102,Text='（似乎有一股不祥的魔法气息……叫卡兹来看看吧。）',}",
  [153103] = "{id=153103,Text='嗨，冒险者我们又见面了。',Speaker=890920,}",
  [153104] = "{id=153104,Text='怎么样？有什么新的发现吗？',Speaker=890920,Option='讲述情况',}",
  [153105] = "{id=153105,Text='什么？！又发现了不对劲的人？我们马上去看看！',Speaker=890920,}",
  [153106] = "{id=153106,Text='哟……哟，跟着我的节拍一起走，冒险者你们怎么又，回，来，咯……',Speaker=890924,}",
  [153107] = "{id=153107,Text='嗯……他看起来怎么比你说的还要严重啊。',Speaker=890920,}",
  [153108] = "{id=153108,Text='我们得让他恢复正常。冒险者，你有什么办法么？',Speaker=890920,Option='治愈魔法',}",
  [153109] = "{id=153109,Text='那么快点治好他吧！',Speaker=890920,}",
  [153110] = "{id=153110,Text='嗯……嗯？我是谁？我在哪儿？',Speaker=890924,}",
  [153111] = "{id=153111,Text='哎呀……我这是怎么了？',Speaker=890924,Option='你在那瞎吆喝呢',Emoji=8,}",
  [153112] = "{id=153112,Text='怎、怎么会这样？！',Speaker=890924,}",
  [153113] = "{id=153113,Text='我我我我堂堂吟游诗人竟然……我的人设崩塌了……',Speaker=890924,Emoji=37,}",
  [153114] = "{id=153114,Text='我没脸见人了！这里我呆不了了！',Speaker=890924,}",
  [153115] = "{id=153115,Text='没找到人……',}",
  [153116] = "{id=153116,Text='刚才还看见他往这里跑了，到底去哪儿了呢？',Speaker=890920,}",
  [153117] = "{id=153117,Text='他在这里！',Speaker=890920,}",
  [153118] = "{id=153118,Text='别拦着我，我要离开普隆德拉！',Speaker=890924,Option='冷静',Emoji=42,}",
  [153119] = "{id=153119,Text='哎……',Speaker=890924,}",
  [153120] = "{id=153120,Text='最近，我脑海中响起一个声音。',Speaker=890924,Option='什么声音',}",
  [153121] = "{id=153121,Text='说什么“既然这么无趣，何不换个又炫酷又吸引人的工作方式？”',Speaker=890924,}",
  [153122] = "{id=153122,Text='当时我不知怎么的，就按那个声音说的做了……',Speaker=890924,Emoji=24,}",
  [153123] = "{id=153123,Text='我所知道的传说中，有很多邪恶力量会感知人的消极情绪并将其放大。',Speaker=890924,}",
  [153124] = "{id=153124,Text='搞不好传说就是真的。',Speaker=890924,}",
  [153125] = "{id=153125,Text='冒险者，谢谢你救了我。你们也要小心啊。',Speaker=890924,}",
  [153126] = "{id=153126,Text='我相信自己还是喜欢弹琴唱歌讲故事的。',Speaker=890924,Emoji=21,}",
  [153127] = "{id=153127,Text='我要踏上旅途，找回对它们的热爱。我们有缘再见。',Speaker=890924,}",
  [153128] = "{id=153128,Text='果然不出我所料……阴谋的气息越来越重了。',Speaker=890920,}",
  [153129] = "{id=153129,Text='我们一定要把幕后推手揪出来！明天还要继续调查哦！',Speaker=890920,Option='好',}",
  [153130] = "{id=153130,Text='那么明天见啦！',Speaker=890920,}",
  [153131] = "{id=153131,Text='嗨，冒险者。',Speaker=890920,}",
  [153132] = "{id=153132,Text='我们继续调查吧！',Speaker=890920,Option='走吧',}",
  [153133] = "{id=153133,Text='嗯？我好像听到不远处有什么动静？我们过去看看！',Speaker=890920,}",
  [153134] = "{id=153134,Text='你这家伙！',Speaker=890925,}",
  [153135] = "{id=153135,Text='别拦着我！我要把这死小鬼狠狠教训一顿！竟然动我的照片！',Speaker=890925,Emoji=12,}",
  [153136] = "{id=153136,Text='呜哇哇哇哇~我只不过帮叔叔捡起他掉落的照片。',Speaker=890926,}",
  [153137] = "{id=153137,Text='就是他女朋友的照片嘛，为什么要打我！',Speaker=890926,}",
  [153138] = "{id=153138,Text='结果照片也弄丢了……',Speaker=890926,Emoji=42,}",
  [153139] = "{id=153139,Text='[c][ffff00]那位大人[-][/c]说过，你这种家伙绝不能原谅！',Speaker=890925,}",
  [153140] = "{id=153140,Text='呜呜呜呜~冒险者救救我啊！',Speaker=890926,}",
  [153141] = "{id=153141,Text='不好，克鲁小哥发狂攻击过来了！冒险者快阻止他！',Speaker=890920,}",
  [153142] = "{id=153142,Text='呃……我这是怎么了？',Speaker=890925,}",
  [153143] = "{id=153143,Text='我……我',Speaker=890925,}",
  [153144] = "{id=153144,Text='我竟然做出这样的事……小朋友，你没事吧？',Speaker=890925,Emoji=16,}",
  [153145] = "{id=153145,Text='啊……是平时的卫兵叔叔！',Speaker=890926,}",
  [153146] = "{id=153146,Text='我就知道卫兵叔叔不会这么坏的！',Speaker=890926,}",
  [153147] = "{id=153147,Text='冒险者，谢谢你！我回家了！',Speaker=890926,}",
  [153148] = "{id=153148,Text='照片……我的照片……',Speaker=890925,Emoji=24,}",
  [153149] = "{id=153149,Text='什么照片？',Speaker=890920,}",
  [153150] = "{id=153150,Text='我女朋友凯特的照片。我一直当护身符。',Speaker=890925,}",
  [153151] = "{id=153151,Text='没了……照片没了……凯特……',Speaker=890925,}",
  [153152] = "{id=153152,Text='别难过，我们帮你找回来！',Speaker=890920,Option='没错',}",
  [153153] = "{id=153153,Text='真的吗……你们真是太好了！',Speaker=890925,}",
  [153154] = "{id=153154,Text='嗯——好像不是这里，我们去其他地方看看吧。',Speaker=890920,}",
  [153155] = "{id=153155,Text='哎……好像也不是这里。嗯？那边有一群鸟在围着什么东西，我们去看看！',Speaker=890920,}",
  [153156] = "{id=153156,Text='是照片！',Speaker=890920,}",
  [153157] = "{id=153157,Text='叽叽叽！',Speaker=890927,Emoji=12,}",
  [153158] = "{id=153158,Text='拿到了！我们回去找卫兵吧！',Speaker=890920,}",
  [153159] = "{id=153159,Text='你们把照片拿回来了啊！谢谢！',Speaker=890925,}",
  [153160] = "{id=153160,Text='是这样的……',Speaker=890925,}",
  [153161] = "{id=153161,Text='最近我经常做噩梦。梦里经常出现一个黑影。',Speaker=890925,}",
  [153162] = "{id=153162,Text='梦里，黑影掳走了我的女朋友，还要挟我归降。',Speaker=890925,}",
  [153163] = "{id=153163,Text='这个梦一直缠着我，我都快疯了。',Speaker=890925,Option='然后呢',}",
  [153164] = "{id=153164,Text='我为了精神的解脱，放弃了思考，成了那个黑影的傀儡。',Speaker=890925,Emoji=24,}",
  [153165] = "{id=153165,Text='你刚才提到[c][ffff00]那位大人[-][/c]，会不会就是那个黑影？',Speaker=890920,}",
  [153166] = "{id=153166,Text='我觉得很有可能。最近城里人心惶惶的。你们也要小心啊。',Speaker=890925,}",
  [153167] = "{id=153167,Text='冒险者，谢谢你帮我。我会帮你留意城里的情况的，我走了！',Speaker=890925,}",
  [153168] = "{id=153168,Text='所有的受害者都有过被精神控制的情况啊……',Speaker=890920,}",
  [153169] = "{id=153169,Text='能控制这么多人，看来是个不得了的家伙。',Speaker=890920,}",
  [153170] = "{id=153170,Text='我能感觉到我们离真相越来越近了。',Speaker=890920,}",
  [153171] = "{id=153171,Text='案情很快就能水落石出！我们明天也要加油呀！',Speaker=890920,Option='明天见',Emoji=20,}",
  [153172] = "{id=153172,Text='嗨，冒险者！',Speaker=890920,}",
  [153173] = "{id=153173,Text='前天和昨天的一些线索都在城内民众身上。',Speaker=890920,}",
  [153174] = "{id=153174,Text='今天说不定还能在其他人身上挖出点东西。',Speaker=890920,}",
  [153175] = "{id=153175,Text='我们接着调查吧，一定要把事情查个明明白白！',Speaker=890920,}",
  [153176] = "{id=153176,Text='走吧！',Speaker=890920,}",
  [153177] = "{id=153177,Text='新料理，我想发明呀嘻嘻～',Speaker=890928,}",
  [153178] = "{id=153178,Text='嗨~冒险者，我想让你帮个忙呀～嘻嘻嘻～',Speaker=890928,Option='什么',}",
  [153179] = "{id=153179,Text='我想出了一道新料理，需要～5把新鲜蔬菜作为食材。',Speaker=890928,}",
  [153180] = "{id=153180,Text='所以～希望你～帮忙找一下～',Speaker=890928,}",
  [153181] = "{id=153181,Text='做好了可以给你试吃哦～怎么样～嘻嘻～',Speaker=890928,Option='好吧',Emoji=5,}",
  [153182] = "{id=153182,Text='哎呀～谢谢你呀～',Speaker=890928,}",
  [153183] = "{id=153183,Text='我这就开始做哦～',Speaker=890928,}",
  [153184] = "{id=153184,Text='好了嘻嘻～热乎的新料理呀～快来尝尝～',Speaker=890928,}",
  [153185] = "{id=153185,Text='感觉有点怪怪的……',}",
  [153186] = "{id=153186,Text='好......像中毒了......',}",
  [153187] = "{id=153187,Text='哈——哈哈哈！傻里傻气的冒险者！你上当了！',Speaker=890928,}",
  [153188] = "{id=153188,Text='我要报告[c][ffff00]那位大人[-][/c]，要把这料理散播到全城！',Speaker=890928,}",
  [153189] = "{id=153189,Text='让全城的人都中毒，然后听命于[c][ffff00]那位大人[-][/c]。',Speaker=890928,}",
  [153190] = "{id=153190,Text='这样[c][ffff00]那位大人[-][/c]就能统治这个世界了！',Speaker=890928,}",
  [153191] = "{id=153191,Text='黑暗的力量是无尽的！',Speaker=890928,}",
  [153192] = "{id=153192,Text='我要去见[c][ffff00]那位大人[-][/c]了！',Speaker=890928,}",
  [153193] = "{id=153193,Text='冒险者！给你治愈药水！',Speaker=890920,}",
  [153194] = "{id=153194,Text='啊……太好了，担心死我了。',Speaker=890920,}",
  [153195] = "{id=153195,Text='看来情况已经相当紧迫了，邪恶的力量已经严重侵蚀了人们的身心。',Speaker=890920,}",
  [153196] = "{id=153196,Text='我们要赶在全城民众都被控制之前找到幕后元凶呀。',Speaker=890920,Option='嗯',}",
  [153197] = "{id=153197,Text='明天普隆德拉城内可能会发生什么事情，我们得万分小心啊。',Speaker=890920,Option='好',}",
  [153198] = "{id=153198,Text='幕后真凶我有点眉目了，只是还没有确凿的证据。',Speaker=890920,}",
  [153199] = "{id=153199,Text='明天我们找出最后的证据，确定真凶吧！那么冒险者，明天也要准时来哦！',Speaker=890920,}",
  [153200] = "{id=153200,Text='冒险者，听我说！',Speaker=890920,}",
  [153201] = "{id=153201,Text='果然和我想的一样，昨天那个异常的料理商人刚跑过去了。',Speaker=890920,}",
  [153202] = "{id=153202,Text='我们快追上去吧！',Speaker=890920,}",
  [153203] = "{id=153203,Text='被控制的居民怎么这么多！连服装店和理发店的人也遭殃了……',Speaker=890920,}",
  [153204] = "{id=153204,Text='我要为大人做出最黑暗最霸气的服装！然后在此恭迎大人驾到！',Speaker=890930,}",
  [153205] = "{id=153205,Text='违抗大人的家伙一律剃光头！只有[c][ffff00]那位大人[-][/c]才能理解我的审美！',Speaker=890931,}",
  [153206] = "{id=153206,Text='怎么？我的料理还不够你受的？只有[c][ffff00]那位大人[-][/c]才能欣赏我的独特料理！',Speaker=890928,Option='…….',}",
  [153207] = "{id=153207,Text='看来他们不行了……完全被控制了。',Speaker=890920,}",
  [153208] = "{id=153208,Text='冒险者，你从他们身上感觉到什么了么？',Speaker=890920,}",
  [153209] = "{id=153209,Text='感受到了一股邪恶的魔力气息……',}",
  [153210] = "{id=153210,Text='难道是……是……',Option='{黑暗之王的幻影,2},{波利,3},{虎王,4}',}",
  [153211] = "{id=153211,Text='啊！果然是他！',Speaker=890920,}",
  [153212] = "{id=153212,Text='波利？不会吧，你再想想？',Speaker=890920,}",
  [153213] = "{id=153213,Text='嗯——好像也不太对',Speaker=890920,}",
  [153214] = "{id=153214,Text='这些人的身上都散发着黑暗之王的魔力残留的气息！',Speaker=890920,}",
  [153215] = "{id=153215,Text='黑暗之王万岁！',Speaker=890930,}",
  [153216] = "{id=153216,Text='黑暗之王万岁！',Speaker=890930,}",
  [153217] = "{id=153217,Text='哈——哈哈哈哈——',Speaker=890932,}",
  [153218] = "{id=153218,Text='没想到你们愚蠢的脑袋竟然也能猜到！',Speaker=890932,}",
  [153219] = "{id=153219,Text='我就是要让黑暗的能量充满这个世界——！',Speaker=890932,}",
  [153220] = "{id=153220,Text='所以我才要找那些心里面负面情绪多的人，趁其不备将他们控制！',Speaker=890932,}",
  [153221] = "{id=153221,Text='而现在，城里归降我的家伙越来越多了！我赢定了！',Speaker=890932,}",
  [153222] = "{id=153222,Text='你们看到的只是我的幻影，但我的幻影也是很强的！',Speaker=890932,}",
  [153223] = "{id=153223,Text='怎么样，愚蠢的冒险者，你要不要加入我啊？看你似乎还有两下子。',Speaker=890932,}",
  [153224] = "{id=153224,Text='我保证，等我统治了这里，我就把自己的力量分一半给你，这交易很香吧~',Speaker=890932,Emoji=5,}",
  [153225] = "{id=153225,Text='要不我们来猜个谜题吧，你猜对了，我就撤退。',Speaker=890932,}",
  [153226] = "{id=153226,Text='错了的话……哼哼，你就是我的奴隶了！',Speaker=890932,Option='来吧！',Emoji=5,}",
  [153227] = "{id=153227,Text='这个世上最强的是谁？',Speaker=890932,Option='{迷人的黑暗之王,0},{伟大的黑暗之王,0},{无敌的黑暗之王,0}',}",
  [153228] = "{id=153228,Text='哈哈，不错！看来你想归降我啊～',Speaker=890932,Option='我拒绝',}",
  [153229] = "{id=153229,Text='真是不识相的家伙！既然这样也没什么好谈的了，给我上！',Speaker=890932,}",
  [153230] = "{id=153230,Text='遵命！',Speaker=890930,}",
  [153231] = "{id=153231,Text='哎呀……我怎么在这里睡着了。我的店还没人看……我得赶紧回去！',Speaker=890930,Emoji=1,}",
  [153232] = "{id=153232,Text='呃……我都在干什么啊。好像做了一场噩梦。',Speaker=890931,}",
  [153233] = "{id=153233,Text='哎，不想了不想了，回去还要干活儿呢！',Speaker=890931,Emoji=25,}",
  [153234] = "{id=153234,Text='我……这是做的什么黑暗料理啊！以后还怎么当料理商人！',Speaker=890928,Emoji=1,}",
  [153235] = "{id=153235,Text='没......没想到你们还有两下子......',Speaker=890932,}",
  [153236] = "{id=153236,Text='不过你们可别高兴得太早！',Speaker=890932,}",
  [153237] = "{id=153237,Text='我黑暗之王绝不会善罢甘休的，你们就等着吧！',Speaker=890932,}",
  [153238] = "{id=153238,Text='哇！冒险者你真厉害！把黑暗之王的幻影的影子打败了！',Speaker=890920,}",
  [153239] = "{id=153239,Text='冒险者你果然是拯救世界的人！我就知道你一定能行的！',Speaker=890920,}",
  [153240] = "{id=153240,Text='这样一来普隆德拉城里的人们就安全啦！谢谢你，保护了普隆德拉！',Speaker=890920,Option='不用谢啦',Emoji=6,}",
  [153241] = "{id=153241,Text='跟你合作进行调查真的非常开心，你帮了我好多忙！',Speaker=890920,}",
  [153242] = "{id=153242,Text='如果还有机会的话我们可以再组成搭档调查案件吗？',Speaker=890920,Option='好啊',}",
  [153243] = "{id=153243,Text='嗯嗯！那太好了！',Speaker=890920,}",
  [153244] = "{id=153244,Text='那么我去普隆德拉城里看看大家的情况，我们下次再见吧！',Speaker=890920,}",
  [153245] = "{id=153245,Text='妈——妈——！我要回家——',Speaker=890926,}",
  [153246] = "{id=153246,Text='什……么?！你这家伙竟然！看来只能我亲自上阵了！',Speaker=890932,}",
  [153248] = "{id=153248,Text='也许我们这些NPC就是玩家最熟悉的陌生人吧，可能会被记住功能，却不配被记住姓名！',Speaker=890934,}",
  [153249] = "{id=153249,Text='这位冒险者，你是不是也有一样的想法？',Speaker=890934,Option='{才不是呢,3},{对啊对啊,4}',}",
  [153250] = "{id=153250,Text='如果有来生，希望我不要再做一个NPC',Speaker=890934,}",
  [153251] = "{id=153251,Text='你知道普隆德拉的美发师叫什么名字吗？',Speaker=890934,Option='{西莉雅,9},{艾诺斯,4}',}",
  [153252] = "{id=153252,Text='天呐，好感动，原来玩家一直默默地关注着我们！',Speaker=890934,}",
  [153253] = "{id=153253,Text='你知道吉芬的卡罗斯特的职责是什么吗？',Speaker=890934,Option='{吉芬塔守卫,10},{吉芬魔物学者,4}',}",
  [153254] = "{id=153254,Text='你知道南门的宠物协会会长穿着什么颜色的衣服吗？',Speaker=890934,Option='{绿色,11},{红色,4}',}",
  [153255] = "{id=153255,Text='你知道伊斯鲁德岛最喜欢吃三文鱼的家伙是谁吗？',Speaker=890934,Option='{尼尔,12},{雷德,4}',}",
  [153258] = "{id=153258,Text='哟，尊敬的冒险者，看见你一切安康，我就放心了！',Speaker=890935,Option='你好像在发愁',}",
  [153259] = "{id=153259,Text='啊……如你所见，我确实有些头疼啊！',Speaker=890935,}",
  [153260] = "{id=153260,Text='最近米德加尔特大陆出现了新型的冠状病毒，已经传染了很多人了。',Speaker=890935,}",
  [153261] = "{id=153261,Text='我们正准备集结国家全部紧急物资，前往援助抗疫前线。',Speaker=890935,}",
  [153262] = "{id=153262,Text='可是，即便是将军用物资都全部拿出来了，还是不太充裕。我们准备号召全民捐献物资！',Speaker=890935,}",
  [153263] = "{id=153263,Text='暂时比较稀缺的物资是用来制作口罩的【透明之布】。',Speaker=890935,}",
  [153264] = "{id=153264,Text='如果冒险者你身上有的话，欢迎捐献哦！我会赋予你丰厚的奖赏！',Speaker=890935,}",
  [153265] = "{id=153265,Text='确定要捐献一份【透明之布】吗？',Speaker=890935,Option='{确定,4},{再想想,3}',}",
  [153266] = "{id=153266,Text='冒险者，今天也来捐献物资吗！',Speaker=890935,}",
  [153267] = "{id=153267,Text='今天稀缺的物资是修复医疗器械的【零件】。',Speaker=890935,}",
  [153268] = "{id=153268,Text='确定要捐献一份【零件】吗？',Speaker=890935,Option='{确定,4},{再想想,3}',}",
  [153269] = "{id=153269,Text='今天稀缺的物资是研制新型药物的【化学气瓶】。',Speaker=890935,}",
  [153270] = "{id=153270,Text='确定要捐献30份【化学气瓶】吗？',Speaker=890935,Option='{确定,4},{再想想,3}',}",
  [153271] = "{id=153271,Text='今天稀缺的物资是拯救病危病人的【天地树果实】。',Speaker=890935,}",
  [153272] = "{id=153272,Text='确定要捐献1份【天地树果实】吗？',Speaker=890935,Option='{确定,4},{再想想,3}',}",
  [153273] = "{id=153273,Text='今天稀缺的物资是慰问一线医疗工作者的【苹果汁】。',Speaker=890935,}",
  [153274] = "{id=153274,Text='确定要捐献10份【苹果汁】吗？',Speaker=890935,Option='{确定,4},{再想想,3}',}",
  [153275] = "{id=153275,Text='尊敬的牧师冒险者，感谢你们为抗击疫病，救援伤病做出的努力与奉献。',Speaker=890936,}",
  [153276] = "{id=153276,Text='我们为您准备了丰厚的礼物，请您领取！',Speaker=890936,Option='{领取,2},{再等等,3}',}",
  [153277] = "{id=153277,Text='嘿！冒险者，非常感谢你们连日来积极捐献物资，助力抗击前线疫情！',Speaker=890935,}",
  [153278] = "{id=153278,Text='现在，物资基本已经能够稳定供给了！疫情已经得到了缓解。',Speaker=890935,}",
  [153279] = "{id=153279,Text='医护人员牺牲自我的功绩不朽，作为捐献物资的冒险者也功不可没！',Speaker=890935,}",
  [153280] = "{id=153280,Text='为此我们特意为您以及所有为控制疫情做出贡献的医护人员准备了丰厚的谢礼！',Speaker=890935,}",
  [153281] = "{id=153281,Text='请您领取哦！',Speaker=890935,Option='{领取,4},{再等等,3}',}",
  [153282] = "{id=153282,Text='冒险者，你如果有物资的话，也来捐献吧！',Speaker=890935,}",
  [153283] = "{id=153283,Text='该任务同账号仅限一个角色领取，请谨慎考虑是否领取',Speaker=890935,Option='{确定,2},{再想想,3}',}",
  [153284] = "{id=153284,Text='你要来捐献物资吗？',Speaker=890935,Option='需要什么物资',}",
  [153285] = "{id=153285,Text='感谢你的捐献！',Speaker=890935,}",
  [153286] = "{id=153286,Text='冒险者，你身上暂时没有足够的材料哦！请先去获取后再来捐献吧！',Speaker=890935,}",
  [153288] = "{id=153288,Text='在天地树构建的九大世界里，争斗从来就不曾停歇。',Speaker=890937,}",
  [153289] = "{id=153289,Text='而人类，这个最为渺小的种族，在强大势力的侵入之下，涌现出了一批又一批的冒险者奔波在世界各处。',Speaker=890937,}",
  [153290] = "{id=153290,Text='“回到世界的起点，寻找力量的根源。”这是先驱们给予后人们的指引。',Speaker=890937,Option='{后来呢,3},{骗人的吧,4}',}",
  [153291] = "{id=153291,Text='后来啊……你知道是谁在波拉与嘉莉那里得知了契约魔法的存在，并踏上了寻找契约者的道路吗？',Speaker=890937,Option='{唤灵者,9},{元素使,4}',}",
  [153292] = "{id=153292,Text='后来，皇家卫士将自己的身体化作了最强韧的盾牌，用一颗守护之心得到了女武神的圣光之力，他们成为了……',Speaker=890937,Option='{世界圣盾,10},{魔剑士,4}',}",
  [153293] = "{id=153293,Text='后来，大主教虔诚的祈祷得到了天神们的回应，他们接过了巴德尔之冠与霍德尔之翼，获得了黑暗与光明的力量，他们被称为……',Speaker=890937,Option='{神使,11},{龙神,4}',}",
  [153294] = "{id=153294,Text='后来，基因学者来到雷根贝勒的地下研究所，用自己的身体作为实验体，去探究生命的奥秘。从此他们成为了……',Speaker=890937,Option='{生命缔造者,12},{灵魂之刃,4}',}",
  [153295] = "{id=153295,Text='对！就是他们！我已经感觉到这股力量，越来越近了……',Speaker=890937,}",
  [153296] = "{id=153296,Text='冒险者，你对力量一无所知。',Speaker=890937,}",
  [153297] = "{id=153297,Text='冒险者，你来了！',Speaker=890940,}",
  [153298] = "{id=153298,Text='我昨天遇见了一个奇怪的委托人。',Speaker=890940,}",
  [153299] = "{id=153299,Text='自称维德，穿着打扮虽然普通，但似乎是认识你，还指名说他的这个委托是给你的。',Speaker=890940,}",
  [153300] = "{id=153300,Text='我和他说你是个大忙人，可能会没时间，想安排另一个冒险者接取他的委托。',Speaker=890940,}",
  [153301] = "{id=153301,Text='但是他拒绝了，一定要你去。',Speaker=890940,Option='他想委托什么',}",
  [153302] = "{id=153302,Text='这个是最让人费解的……虽然说是要申请委托，但是却并不告诉我们委托详情。',Speaker=890940,}",
  [153303] = "{id=153303,Text='只是留下话说要你去普隆德拉喷泉找他。',Speaker=890940,}",
  [153304] = "{id=153304,Text='你现在有时间吗？要不，你去看看吧！',Speaker=890940,}",
  [153305] = "{id=153305,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=890940,Option='{好,2},{再等等,3}',}",
  [153306] = "{id=153306,Text='冒险者，你可算是来啦！',Speaker=890941,Emoji=2,}",
  [153307] = "{id=153307,Text='在冒险者公会挂上委托以后，我可是在这里等你很久了！',Speaker=890941,Option='你想委托什么',}",
  [153308] = "{id=153308,Text='别心急嘛！说正事之前我想问你一个问题。',Speaker=890941,}",
  [153309] = "{id=153309,Text='你喜欢旅行么？',Speaker=890941,Option='{当然喜欢,2},{不喜欢,3}',}",
  [153310] = "{id=153310,Text='哈哈哈，看来你也是个喜欢旅行的人呢，果然我没有看错人！',Speaker=890941,Emoji=17,}",
  [153311] = "{id=153311,Text='那你觉得旅行的意义是什么呢？',Speaker=890941,Option='放松或者休息',}",
  [153312] = "{id=153312,Text='有这样的意义……不过旅行并不只是放松，也是回忆哦！',Speaker=890941,}",
  [153313] = "{id=153313,Text='每走一个地方都是在经历一段故事、留下一段回忆。',Speaker=890941,}",
  [153314] = "{id=153314,Text='所以，我准备开始为期一星期的旅行冒险。',Speaker=890941,}",
  [153315] = "{id=153315,Text='不过，一个人旅行太无聊啦，想邀请你跟我一起旅行兼职护卫我的安全。',Speaker=890941,}",
  [153316] = "{id=153316,Text='包括今天一共五天的时间，期间会去不同的地方，不知道你愿不愿意呢？',Speaker=890941,}",
  [153317] = "{id=153317,Text='顺带说一下，如果你愿意的话，我愿意提供丰厚的报酬哦！',Speaker=890941,Emoji=26,}",
  [153318] = "{id=153318,Text='想好了吗？',Speaker=890941,Option='我和你一起去',}",
  [153319] = "{id=153319,Text='那好，那接下来我们就准备一起旅行吧！',Speaker=890941,Option='有什么计划',Emoji=17,}",
  [153320] = "{id=153320,Text='嗯——本来想在王都里转转的，但人也实在是太多了！',Speaker=890941,}",
  [153321] = "{id=153321,Text='拥挤的感觉，实在是不好受！',Speaker=890941,}",
  [153322] = "{id=153322,Text='哦对了，说到拥挤，我有件事想要拜托你！',Speaker=890941,}",
  [153323] = "{id=153323,Text='今天来这里的时候，因为人实在是太多了，我的相机不小心丢了。',Speaker=890941,}",
  [153324] = "{id=153324,Text='没有相机的话，旅行就太单调乏味了，我得把它找回来！',Speaker=890941,}",
  [153325] = "{id=153325,Text='你能帮帮我么？相机应该就丢在附近了！',Speaker=890941,}",
  [153326] = "{id=153326,Text='怎么样，相机找回来了么？',Speaker=890941,}",
  [153327] = "{id=153327,Text='真是太好了！',Speaker=890941,}",
  [153328] = "{id=153328,Text='真是太感谢你了冒险者，有相机的话，就可以留住旅行时的美好回忆啦！',Speaker=890941,}",
  [153329] = "{id=153329,Text='今天真是辛苦你啦，明天你再来这里找我吧！',Speaker=890941,}",
  [153330] = "{id=153330,Text='我可是很期待我们的旅行哦！',Speaker=890941,}",
  [153331] = "{id=153331,Text='你可算来啦！',Speaker=890941,Emoji=2,}",
  [153332] = "{id=153332,Text='准备好和我一起旅行了么？',Speaker=890941,Option='今天打算去哪里',}",
  [153333] = "{id=153333,Text='相机修好了的话，我想去一个适合拍照的地方……',Speaker=890941,}",
  [153334] = "{id=153334,Text='普隆德拉历史悠久，而里面最具历史人文气息的，当属圣歌大教堂了吧！',Speaker=890941,}",
  [153335] = "{id=153335,Text='我们去圣歌大教堂看看吧！顺便也可以去学学圣歌！',Speaker=890941,}",
  [153336] = "{id=153336,Text='这就是圣歌大教堂么！还真是庄严肃穆啊！',Speaker=890941,Emoji=20,}",
  [153337] = "{id=153337,Text='真不愧是圣歌教会的圣地！',Speaker=890941,}",
  [153338] = "{id=153338,Text='我听说王都里结婚的年轻人都会来这里倾听祝祷，聆听圣歌以及祈祷未来。',Speaker=890941,}",
  [153339] = "{id=153339,Text='如果有一天我结婚的话，我也要来这里。',Speaker=890941,}",
  [153340] = "{id=153340,Text='沐浴从琉璃花窗透进来的金色阳光，踩着神圣的红地毯……',Speaker=890941,}",
  [153341] = "{id=153341,Text='聆听着修女们吟唱的圣歌，在女神面前接受大主教的祝祷……',Speaker=890941,Option='我也会来祝福你',}",
  [153342] = "{id=153342,Text='那样最好了！',Speaker=890941,Emoji=17,}",
  [153343] = "{id=153343,Text='对啦，光顾着说话了，这么好的地方不拍几张照片的话就太可惜了！',Speaker=890941,}",
  [153344] = "{id=153344,Text='帮我拍张照片留念下吧！',Speaker=890941,}",
  [153345] = "{id=153345,Text='拍好了么？',Speaker=890941,}",
  [153346] = "{id=153346,Text='拍得真不错。',Speaker=890941,Option='还要做什么吗',Emoji=20,}",
  [153347] = "{id=153347,Text='好了，我还打算去找菲尔吉恩大主教学唱圣歌呢！',Speaker=890941,}",
  [153348] = "{id=153348,Text='对了，要找菲尔吉恩大主教学唱圣歌的话，得准备些礼物才好！',Speaker=890941,}",
  [153349] = "{id=153349,Text='听说菲尔吉恩大主教在收集【疯兔】盛产的【优质柔毛】，你去帮我准备一些吧！',Speaker=890941,Option='我这就去',}",
  [153350] = "{id=153350,Text='优质柔毛都准备好了吗？',Speaker=890941,}",
  [153351] = "{id=153351,Text='太好了，辛苦你了，冒险者！',Speaker=890941,}",
  [153352] = "{id=153352,Text='接下来，我们去找菲尔吉恩大主教吧。',Speaker=890941,}",
  [153353] = "{id=153353,Text='欢迎来圣歌大教堂参观，冒险者、旅行者！',Speaker=5003,}",
  [153354] = "{id=153354,Text='菲尔吉恩大主教，能看见你真是太高兴了！我可是一直都想来拜访您！',Speaker=890941,}",
  [153355] = "{id=153355,Text='找我有什么事吗？',Speaker=5003,}",
  [153356] = "{id=153356,Text='我一直有个心愿，就是希望能向您学习圣歌。',Speaker=890941,}",
  [153357] = "{id=153357,Text='我想把圣歌带来的祝愿也带回我的家乡。',Speaker=890941,}",
  [153358] = "{id=153358,Text='嗯——如果是平时的话，我一定会欣然接受的啦，但今天可能有些遗憾哦！',Speaker=5003,}",
  [153359] = "{id=153359,Text='我马上要去一趟吉芬了，下午就得出发。',Speaker=5003,}",
  [153360] = "{id=153360,Text='那还真是遗憾呢……',Speaker=890941,}",
  [153361] = "{id=153361,Text='还真是遗憾啊。',Speaker=890941,Option='下次来就好了',}",
  [153362] = "{id=153362,Text='没事没事，我没事啦！',Speaker=890941,}",
  [153363] = "{id=153363,Text='旅行就是这样，总是会有很多超出计划以外的事情存在。',Speaker=890941,}",
  [153364] = "{id=153364,Text='但是，正是因为超出计划外的事情，才多了不确定性哦，这也是旅行的魅力之一。',Speaker=890941,}",
  [153365] = "{id=153365,Text='千万不要忘记了，遗憾也是旅行很宝贵的记忆哦！',Speaker=890941,Option='哈哈哈',}",
  [153366] = "{id=153366,Text='好了，既然学习圣歌的计划搁浅了，那接下来，我们就随便逛逛吧！',Speaker=890941,}",
  [153367] = "{id=153367,Text='冒险者，今天我们去一个稍微远一点的地方吧。',Speaker=890941,Option='去哪里',}",
  [153368] = "{id=153368,Text='我们到艾尔帕兰去看看！去看看那个四周被河流环绕、美丽宁静的运河之都。',Speaker=890941,}",
  [153369] = "{id=153369,Text='你听说过艾尔帕兰吗？',Speaker=890941,Option='知道一些',}",
  [153370] = "{id=153370,Text='艾尔帕兰不光是卡普拉公司总部的所在地、炼金术师的聚集处。',Speaker=890941,}",
  [153371] = "{id=153371,Text='据书上说城市中央的钟楼里，巨大的黄金时钟还隐藏着关于时空的神秘力量……',Speaker=890941,}",
  [153372] = "{id=153372,Text='不过——光说和书里看是体会不了其中魅力的，我们还是去实地看看吧！',Speaker=890941,}",
  [153373] = "{id=153373,Text='准备好了吗，准备好了的话就跟我一起出发吧！',Speaker=890941,Option='{准备好了,2},{再等等,3}',}",
  [153374] = "{id=153374,Text='艾尔帕兰真是名不虚传啊，人不是一般地多！',Speaker=890941,}",
  [153375] = "{id=153375,Text='哎！看那座钟楼！那就是有着黄金时钟的钟楼了吧！',Speaker=890941,Option='好像是',}",
  [153376] = "{id=153376,Text='你听过钟楼的传说吗？',Speaker=890941,}",
  [153377] = "{id=153377,Text='这座隐藏着关于时空力量的钟楼据说在艾尔帕兰建立之前就已经在这里了。',Speaker=890941,}",
  [153378] = "{id=153378,Text='只可惜，这么多年过去了，还没有人能够解开其中的奥妙。',Speaker=890941,}",
  [153379] = "{id=153379,Text='如果解开的话，说不定我们就能进行穿越时空的旅行了！',Speaker=890941,}",
  [153380] = "{id=153380,Text='好了，去帮我看看今天是不是能够开放参观吧！',Speaker=890941,}",
  [153381] = "{id=153381,Text='站住！',Speaker=6461,}",
  [153382] = "{id=153382,Text='钟楼今天需要进行临时检修，暂时不予开放！',Speaker=6461,}",
  [153383] = "{id=153383,Text='如果想要参观的话，请过几天再来吧！',Speaker=6461,}",
  [153384] = "{id=153384,Text='能进去吗？',Speaker=890941,Option='好像不太行',}",
  [153385] = "{id=153385,Text='旅行的时候也确实会遇见这样的情况啊——原本开放的景点因为一些特殊原因突然就封闭了。',Speaker=890941,Option='改变一下计划',}",
  [153386] = "{id=153386,Text='你是说改变一下游览计划吗？我也有这个想法！',Speaker=890941,}",
  [153387] = "{id=153387,Text='啊对了，听说炼金公会真正举行炼金术展览，干脆我们去参观炼金公会吧！',Speaker=890941,}",
  [153388] = "{id=153388,Text='炼金术也是很神奇的哦！',Speaker=890941,}",
  [153389] = "{id=153389,Text='欢迎来到炼金公会，旅行者！',Speaker=5400,}",
  [153390] = "{id=153390,Text='有什么我能帮助你们的吗？',Speaker=5400,}",
  [153391] = "{id=153391,Text='听说你们在做炼金术的展览，我们想来参观参观炼金术！',Speaker=890941,}",
  [153392] = "{id=153392,Text='作品都在前面的桌子上哦，请小心参观。',Speaker=5400,}",
  [153394] = "{id=153394,Text='炼金术的奥秘就在于物质元素的转换哦！化学技术也是来源于炼金术！',Speaker=5400,}",
  [153395] = "{id=153395,Text='我看你真的很喜欢炼金术呢！旅行者！',Speaker=5400,}",
  [153396] = "{id=153396,Text='不如，我送你个东西做纪念吧！稍等哦，我把它取出来！',Speaker=5400,}",
  [153397] = "{id=153397,Text='哇，这是什么？',Speaker=890941,}",
  [153398] = "{id=153398,Text='这是用炼金术制成的金晶石，很珍贵的哦！',Speaker=5400,}",
  [153399] = "{id=153399,Text='送给你吧！',Speaker=5400,}",
  [153400] = "{id=153400,Text='这么贵重的东西，我怎么能要？',Speaker=890941,}",
  [153401] = "{id=153401,Text='东西之所以珍贵是因为喜欢它的人才珍贵的哦，送给喜欢它的人才是体现它的价值！',Speaker=5400,}",
  [153402] = "{id=153402,Text='哈哈哈——那我就不客气啦！',Speaker=890941,}",
  [153403] = "{id=153403,Text='冒险者！能拜托你帮我收好它吗！',Speaker=890941,}",
  [153404] = "{id=153404,Text='收好了吗！',Speaker=890941,}",
  [153405] = "{id=153405,Text='今天真是大有收获啊！',Speaker=890941,}",
  [153406] = "{id=153406,Text='旅行就是这样，虽然会有遗憾，不过更多的还是惊喜！',Speaker=890941,}",
  [153407] = "{id=153407,Text='今天真是太愉快啦！',Speaker=890941,}",
  [153408] = "{id=153408,Text='你说后面的计划？',Speaker=890941,}",
  [153409] = "{id=153409,Text='现在艾尔帕兰随便逛逛吧！我想欣赏欣赏户外的运河风光！顺便带一些伴手礼。',Speaker=890941,}",
  [153410] = "{id=153410,Text='你也随便逛逛吧，不过，别忘记了明天的约定哦！',Speaker=890941,}",
  [153411] = "{id=153411,Text='你还是一如既往地守时啊，冒险者。',Speaker=890941,Option='今天要去哪里',}",
  [153412] = "{id=153412,Text='你知道姜饼城么？',Speaker=890941,Option='知道一些。',}",
  [153413] = "{id=153413,Text='听说姜饼城到处都是雪，是白雪中的童话幻想之乡。',Speaker=890941,}",
  [153414] = "{id=153414,Text='小时候我也听奶奶提起过关于姜饼城的故事，这次距离姜饼城这么近正好可以去看看！',Speaker=890941,}",
  [153415] = "{id=153415,Text='你准备好了吗，如果准备好了的话就跟我出发吧！',Speaker=890941,Option='{准备好了,2},{再等等,3}',}",
  [153416] = "{id=153416,Text='果然和传闻中的一样，这里还真是到处都是雪呢！',Speaker=890941,}",
  [153417] = "{id=153417,Text='快看！又有冒险者和旅行者来啦！',Speaker=890942,}",
  [153418] = "{id=153418,Text='冒险者冒险者！你们是从艾尔帕兰来的吗？！',Speaker=890942,Emoji=19,}",
  [153419] = "{id=153419,Text='对啊，我们就是从艾尔帕兰来的！',Speaker=890941,}",
  [153420] = "{id=153420,Text='真好！艾尔帕兰好玩吗！快跟我们说说艾尔帕兰吧！',Speaker=890942,}",
  [153421] = "{id=153421,Text='孩子们，你们这样会把冒险者和旅行者吓走的哦！',Speaker=890944,}",
  [153422] = "{id=153422,Text='冒险者、旅行者，请原谅孩子们的无礼。',Speaker=890944,}",
  [153423] = "{id=153423,Text='孩子们对外面的世界很向往，所以有外人来的时候，他们总喜欢黏着！别见怪！',Speaker=890944,}",
  [153424] = "{id=153424,Text='没事没事，不过，这里的孩子们还真是热情啊！',Speaker=890941,}",
  [153425] = "{id=153425,Text='看你们的样子是刚来姜饼城吧，要来我家做客吗？',Speaker=890944,}",
  [153426] = "{id=153426,Text='是啊是啊，去我们村子里玩吧！我们可以一起堆雪人！',Speaker=890942,}",
  [153427] = "{id=153427,Text='冒险者，你觉得呢？',Speaker=890941,Option='我没意见！',}",
  [153428] = "{id=153428,Text='孩子们，我先去准备宴席招待客人，你们要好好陪客人哦！',Speaker=890944,}",
  [153429] = "{id=153429,Text='好！',Speaker=890943,}",
  [153430] = "{id=153430,Text='我们打雪仗吧！',Speaker=890942,}",
  [153431] = "{id=153431,Text='不要，还是堆雪人吧，我们一起堆个高高大大的雪人！',Speaker=890943,}",
  [153432] = "{id=153432,Text='女士优先！我们还是堆雪人吧！',Speaker=890941,}",
  [153433] = "{id=153433,Text='堆个高高大大的雪人，让全姜饼城的小朋友都大吃一惊！好不好冒险者！',Speaker=890943,Option='没问题',}",
  [153434] = "{id=153434,Text='要堆一个大雪人的话，我们有必要做个分工。',Speaker=890941,}",
  [153435] = "{id=153435,Text='我带着孩子去取雪，堆雪，',Speaker=890941,}",
  [153436] = "{id=153436,Text='冒险者去帮忙找找装饰物吧！能请你去找3束鲜花来吗？',Speaker=890941,}",
  [153437] = "{id=153437,Text='东西都带回来了吗？',Speaker=890941,Option='带回来了',}",
  [153438] = "{id=153438,Text='太好了，冒险者，快把鲜花插上去吧！',Speaker=890941,}",
  [153439] = "{id=153439,Text='哇！插上鲜花后，雪人真漂亮！',Speaker=890942,}",
  [153440] = "{id=153440,Text='冒险者真厉害！',Speaker=890943,}",
  [153441] = "{id=153441,Text='我去找其他小朋友来看！',Speaker=890942,}",
  [153442] = "{id=153442,Text='我也去！',Speaker=890943,}",
  [153443] = "{id=153443,Text='你问我玩得开不开心？',Speaker=890941,}",
  [153444] = "{id=153444,Text='和这么有趣的孩子玩耍当然开心啦！姜饼城果然不愧是童话世界的幻想乡！',Speaker=890941,Option='接下来要做什么',}",
  [153445] = "{id=153445,Text='我想先留在这里陪孩子们玩，你可以去别处逛逛！',Speaker=890941,}",
  [153446] = "{id=153446,Text='不过，别忘了明天我们还要去别处旅行哦！',Speaker=890941,}",
  [153447] = "{id=153447,Text='最后一天的旅行，你准备好了吗？',Speaker=890941,}",
  [153448] = "{id=153448,Text='今天我们要去一个特别的地方。',Speaker=890941,}",
  [153449] = "{id=153449,Text='充满神秘色彩的魔法之都——吉芬！',Speaker=890941,}",
  [153450] = "{id=153450,Text='那可是一个充满魔法力量的地方，据说在那里能体会到属于魔法的奥秘。',Speaker=890941,}",
  [153451] = "{id=153451,Text='而且，传承神秘知识的魔法学院就在吉芬哦！',Speaker=890941,}",
  [153452] = "{id=153452,Text='准备好了吗，准备好了的话就跟我去吉芬看看吧！',Speaker=890941,Option='{准备好了,2},{再等等,3}',}",
  [153453] = "{id=153453,Text='总算是到了吉芬了！',Speaker=890941,}",
  [153454] = "{id=153454,Text='嘿！旅行者，冒险者，想见识魔法的奥妙么？',Speaker=890946,}",
  [153456] = "{id=153456,Text='真的吗？我早就想见识见识吉芬的魔法了！',Speaker=890941,}",
  [153457] = "{id=153457,Text='你想变什么？说出来吧！我一定帮你变出来！',Speaker=890946,}",
  [153458] = "{id=153458,Text='嗯——冒险者，你觉得变什么好呢？',Speaker=890941,Option='天使波利吧',}",
  [153459] = "{id=153459,Text='天使波利？没问题！',Speaker=890946,}",
  [153460] = "{id=153460,Text='波利！波利！天使波利！',Speaker=890946,}",
  [153461] = "{id=153461,Text='一',Speaker=890946,}",
  [153462] = "{id=153462,Text='二',Speaker=890946,}",
  [153463] = "{id=153463,Text='三',Speaker=890946,}",
  [153464] = "{id=153464,Text='变！',Speaker=890946,}",
  [153465] = "{id=153465,Text='冒险者！快看，真的是天使波利！',Speaker=890941,Option='看起来很像',}",
  [153466] = "{id=153466,Text='哎？难道你怀疑是假的吗？',Speaker=890941,}",
  [153467] = "{id=153467,Text='咳咳——魔法可不是幻术，我可不会用假东西骗人。',Speaker=890946,Option='怎么证明',}",
  [153468] = "{id=153468,Text='不信的话，你可以打打看！',Speaker=890946,}",
  [153469] = "{id=153469,Text='是不是真的天使波利，打一下就知道了！',Speaker=890946,}",
  [153470] = "{id=153470,Text='天使波利一溜烟地就跑了——',}",
  [153471] = "{id=153471,Text='怎么样怎么样？是真的天使波利吗！',Speaker=890941,Option='好像是真的',}",
  [153472] = "{id=153472,Text='我说了吧，我布兰登从不拿假东西骗人。',Speaker=890946,}",
  [153473] = "{id=153473,Text='我的东西可都是货真价实的，我的魔法也是货真价实的。',Speaker=890946,}",
  [153474] = "{id=153474,Text='真厉害！',Speaker=890941,Option='还是有些可疑',}",
  [153475] = "{id=153475,Text='不要在吉芬怀疑魔法哦！',Speaker=890946,}",
  [153476] = "{id=153476,Text='你问我的目的，你果然还是在怀疑我的动机嘛！',Speaker=890946,}",
  [153477] = "{id=153477,Text='我只是看你们都像是被魔法眷顾的人，所以想用祝祷魔法赐予你们祝福而已。',Speaker=890946,}",
  [153478] = "{id=153478,Text='祝祷魔法是什么？',Speaker=890941,}",
  [153479] = "{id=153479,Text='就是赐予物品以祝祷的力量，给予佩戴者祝福。',Speaker=890946,}",
  [153480] = "{id=153480,Text='我这里有刚刚加持过祝祷的秘银吊坠！只要给我一枚宝石，我现在就把它送给你！',Speaker=890946,}",
  [153481] = "{id=153481,Text='一枚宝石就能获得永恒的祝福，那可是打着灯笼都找不到的好事！',Speaker=890946,}",
  [153482] = "{id=153482,Text='布兰登先生，这枚红玛瑙石可以吗！',Speaker=890941,}",
  [153483] = "{id=153483,Text='虽然有些廉价，但看在你也是被魔法眷顾的人的份上，我就接受了！',Speaker=890946,}",
  [153484] = "{id=153484,Text='不客气！以后如果还需要帮忙的话，欢迎来找我！我给优惠哦！',Speaker=890946,}",
  [153485] = "{id=153485,Text='魔法真是太神奇啦！',Speaker=890941,Option='还是感觉上当了',Emoji=20,}",
  [153486] = "{id=153486,Text='一枚宝石就能见识魔法的奥妙，已经很划算啦！',Speaker=890941,Option='接下来要做什么',}",
  [153487] = "{id=153487,Text='我想差不多该结束了！',Speaker=890941,}",
  [153488] = "{id=153488,Text='这五天的旅行很愉快啊，我想是时候带着这份愉快的心情结束这次的旅程了！',Speaker=890941,}",
  [153489] = "{id=153489,Text='在普隆德拉的两天，虽然没能学到圣歌，但普隆德拉的繁华让我印象深刻。',Speaker=890941,}",
  [153490] = "{id=153490,Text='虽然因为人多而一度导致相机遗失，但失而复得也是一种难得的体验！',Speaker=890941,}",
  [153491] = "{id=153491,Text='艾尔帕兰也很不错！虽然没有进入钟楼，但炼金术也让人饱眼福！',Speaker=890941,}",
  [153492] = "{id=153492,Text='还意外地得到了帕乐米的赠礼！简直太幸运了！',Speaker=890941,}",
  [153493] = "{id=153493,Text='姜饼城的孩子那么天真无邪，童话幻想世界的魅力，我会永远记在脑海里！',Speaker=890941,}",
  [153494] = "{id=153494,Text='在吉芬的魔法奥秘，也让我更了解这个不可思议的世界……',Speaker=890941,}",
  [153495] = "{id=153495,Text='总之，我这次收获满满！已经心满意足啦！',Speaker=890941,Option='我也收获很大',}",
  [153496] = "{id=153496,Text='这五天很感谢你的陪伴哦，我玩得很开心！',Speaker=890941,}",
  [153497] = "{id=153497,Text='我们就此分别吧，再见，我的朋友！',Speaker=890941,}",
  [153498] = "{id=153498,Text='旅行真的是很有趣啊，希望以后我们还能一起旅行！',Speaker=890941,}",
  [153505] = "{id=153505,Text='收集齐了，回去交给维德吧。',Option='好的',}",
  [153507] = "{id=153507,Text='冒险者！冒险者！',Speaker=890941,Option='你好激动',Emoji=19,}",
  [153508] = "{id=153508,Text='其实，经过这几天的观察，我发现人们对出门旅行的心态是不一样的。',Speaker=890941,}",
  [153509] = "{id=153509,Text='很多时候，大多数人会在宅家和旅行之间犹豫不决。',Speaker=890941,}",
  [153510] = "{id=153510,Text='这种徘徊的心态很有意思！是我感兴趣的研究方向。',Speaker=890941,}",
  [153511] = "{id=153511,Text='所以啊，你愿不愿意帮我个忙？只要回答我一个问题就好了！',Speaker=890941,Option='{不了吧,2},{好的吧,3}',Emoji=9,}",
  [153512] = "{id=153512,Text='我、我们的友谊竟然这么脆弱吗？',Speaker=890941,}",
  [153513] = "{id=153513,Text='我就知道你不会拒绝！',Speaker=890941,}",
  [153514] = "{id=153514,Text='咳咳，那么，我今天想了解的是……',Speaker=890941,}",
  [153515] = "{id=153515,Text='某天早上你从被窝中醒来，下面两件事你会更倾向……',Speaker=890941,Option='{起床捕猎魔物,0},{躺着再睡一下,0}',}",
  [153516] = "{id=153516,Text='唔，原来你是这样想的！',Speaker=890941,Emoji=11,}",
  [153517] = "{id=153517,Text='好啦，今天就先这样。我们明天再见！',Speaker=890941,}",
  [153518] = "{id=153518,Text='出门旅行还是宅家呢？今天你会给我不同的答案吗？',Speaker=890941,}",
  [153519] = "{id=153519,Text='咳咳，那么，我想了解的是——',Speaker=890941,}",
  [153520] = "{id=153520,Text='某天你在散步时发现一处没见过的美景，你会……',Speaker=890941,Option='{凑过去看看,0},{还是不去了,0}',}",
  [153521] = "{id=153521,Text='下面两个地点，你会更倾向待在哪一处？',Speaker=890941,Option='{风景胜地,0},{熟悉的小屋,0}',}",
  [153522] = "{id=153522,Text='下面两件事，哪一件你会觉得更有兴趣？',Speaker=890941,Option='{寻找神秘宝藏,0},{玩喜欢的游戏,0}',}",
  [153523] = "{id=153523,Text='难得遇到了休息日，你有一天可以做自己想做的事。你会选择……',Speaker=890941,Option='{出门踏青,0},{倒头睡觉,0}',}",
  [153524] = "{id=153524,Text='对于远近闻名的风景胜地，你的看法是……',Speaker=890941,Option='{要去看看,0},{没什么意思,0}',}",
  [153525] = "{id=153525,Text='出门旅行还是宅家呢？今天是最后一日了，你会给我不同的答案吗？',Speaker=890941,}",
  [153526] = "{id=153526,Text='咳咳，那么，我想了解的是——',Speaker=890941,}",
  [153527] = "{id=153527,Text='对于“我可以坚持七天七夜不出门”的说法，你的态度是……',Speaker=890941,Option='{完全受不了,0},{点头赞同,0}',}",
  [153528] = "{id=153528,Text='唔，原来你是这样想的！',Speaker=890941,Emoji=11,}",
  [153529] = "{id=153529,Text='好啦冒险者，我的问题已经全部结束了~',Speaker=890941,}",
  [153530] = "{id=153530,Text='以你这几天的回答为参考，唔！选择困难是永远无法突破的矛盾啊……',Speaker=890941,Emoji=15,}",
  [153531] = "{id=153531,Text='我理解你的想法，冒险者。不管是宅家还是旅行，按照当下的心情去做决定就好了！',Speaker=890941,}",
  [153532] = "{id=153532,Text='期待你会遇见更多美好的事物……到时候别忘了和我分享哦！',Speaker=890941,}",
  [153534] = "{id=153534,Text='哥布灵喜欢恶作剧魔棒，更喜欢被恶作剧魔棒变身后的冒险者！',Speaker=890952,}",
  [153535] = "{id=153535,Text='如果你可以让我看看被恶作剧魔棒变身的冒险者的样子，我一定给你我珍藏已久的好东西！',Speaker=890952,}",
  [153536] = "{id=153536,Text='真的太好玩啦~虽然我很调皮，但是我也是一个遵守诺言的哥布灵哦~',Speaker=890952,}",
  [153539] = "{id=153539,Text='最近我从遥远的地方进了一批货~',Speaker=890953,}",
  [153540] = "{id=153540,Text='非常的物超所值哦~（疯狂的使眼色）',Speaker=890953,Option='{继续看看,2},{转头就走,3}',Emoji=13,}",
  [153541] = "{id=153541,Text='相信我，没错的！~',Speaker=890953,Emoji=20,}",
  [153542] = "{id=153542,Text='别走啊~人与人之间最重要的信任呢？',Speaker=890953,}",
  [153543] = "{id=153543,Text='哼，看看你葫芦里卖的是什么药~',}",
  [153544] = "{id=153544,Text='嗯~伙伴，你脸色不太好哇（布兰登眉头一簇）',Speaker=890953,Emoji=18,}",
  [153545] = "{id=153545,Text='你自己说过的：“人与人之间最重要的信任呢？”',}",
  [153546] = "{id=153546,Text='亏我还能相信你，收宝石还偷偷摸摸多要我一颗？',}",
  [153547] = "{id=153547,Text='是时候展现家乡的艺能了，肉蛋葱鸡~~',}",
  [153548] = "{id=153548,Text='别别别别……伙计，我真的错了，我再也不敢了！',Speaker=890953,Emoji=18,}",
  [153549] = "{id=153549,Text='为了表达我的诚意，我决定把我珍藏的宝贝在之后几天以最低的价格卖给你。',Speaker=890953,}",
  [153551] = "{id=153551,Text='“回到世界的起点，寻找力量的根源。”',Speaker=890959,}",
  [153552] = "{id=153552,Text='是诸天的指引，是神祇的眷顾，是突破生命的奇迹，是守护世界的核心……',Speaker=890959,}",
  [153553] = "{id=153553,Text='[c][ffff00][PlayerName][-][/c],你准备好接受这一份来自远古世纪的洗礼了吗？',Speaker=890959,Option='{准备好了,2},{还没有,3}',}",
  [153556] = "{id=153556,Text='米德加尔特大陆的新型的冠状病毒防控已初见成效，但是防疫措施不能松懈。',Speaker=807798,}",
  [153557] = "{id=153557,Text='让上校我来考考你对防护措施的了解程度吧。',Speaker=807798,Option='{尽管来吧,2},{先去学习一下,3}',}",
  [153558] = "{id=153558,Text='[c][ffff00][PlayerName][-][/c]，看来你对防疫措施已经非常了解了！',Speaker=807798,}",
  [153559] = "{id=153559,Text='疫情期间，出门记得带好口罩，勤洗手消毒哦~',Speaker=807798,}",
  [153560] = "{id=153560,Text='75%以上的酒精可以消灭病毒~',Speaker=807798,}",
  [153561] = "{id=153561,Text='一次性口罩用完记得要用热水消毒处理哦~',Speaker=807798,}",
  [153562] = "{id=153562,Text='平时记得多开窗通风~',Speaker=807798,Option='记住了！',}",
  [153563] = "{id=153563,Text='你来啦~前些日子,和你玩的很开心哦！',Speaker=890962,}",
  [153564] = "{id=153564,Text='我的小伙伴们听说了我的这段难忘的经历后，都纷纷表示了很大的兴趣呢！',Speaker=890962,}",
  [153565] = "{id=153565,Text='如果可以的话，去找到她们，陪她们玩一玩吧！',Speaker=890962,}",
  [153566] = "{id=153566,Text='谢谢你的陪伴！',Speaker=890962,}",
  [153567] = "{id=153567,Text='唔......不过如果有时间的话，也可以再多和我的伙伴们玩一玩哦！',Speaker=890962,}",
  [153568] = "{id=153568,Text='当当当~挑战升级了！',Speaker=890962,}",
  [153569] = "{id=153569,Text='需要完成的委托任务的数目现在提升到了三个！',Speaker=890962,}",
  [153570] = "{id=153570,Text='你果然是一个善良热心的冒险者！',Speaker=890962,Emoji=17,}",
  [153571] = "{id=153571,Text='想成为最受孩子们欢迎的冒险者吗？这将是终极的挑战—',Speaker=890962,}",
  [153572] = "{id=153572,Text='尝试着去完成我的小伙伴们所有的委托吧！',Speaker=890962,}",
  [153573] = "{id=153573,Text='辛苦你了！陪我们玩了这么久一定有些累了吧，快点去休息吧~',Speaker=890962,}",
  [153574] = "{id=153574,Text='谢谢你带给我们的欢乐时光！',Speaker=890962,}",
  [153575] = "{id=153575,Text='可以和我玩一玩猜拳的游戏吗？',Speaker=890964,}",
  [153576] = "{id=153576,Text='剪刀石头布——',Speaker=890964,Option='{剪刀,99},{石头,98},{布,97}',}",
  [153577] = "{id=153577,Text='你赢了！看来你的猜拳技艺还不赖哦！',Speaker=890964,Emoji=41,}",
  [153578] = "{id=153578,Text='哼哼~你输了哦！看来还是我更厉害一些！',Speaker=890964,Emoji=17,}",
  [153579] = "{id=153579,Text='竟然是平局！看来我们都很厉害呢！',Speaker=890964,Emoji=38,}",
  [153580] = "{id=153580,Text='我不喜欢那些吵吵闹闹的无聊的游戏......',Speaker=890963,}",
  [153581] = "{id=153581,Text='我喜欢听故事！你可以给我讲一个故事吗？',Speaker=890963,Option='{小公主的故事,99},{漫游骑士的故事,98},{魔法学徒的故事,97}',}",
  [153582] = "{id=153582,Text='这个勇敢善良的小公主的故事，或许有点老套了呢......',Speaker=890963,}",
  [153583] = "{id=153583,Text='不过，最后她击败邪恶魔法师的场景你还是描绘得很精彩呢！',Speaker=890963,Emoji=26,}",
  [153584] = "{id=153584,Text='哈哈哈这个漫游骑士真是一个滑稽可笑的角色！',Speaker=890963,}",
  [153585] = "{id=153585,Text='他怎么会把风车当成巨石怪呢！',Speaker=890963,Emoji=17,}",
  [153586] = "{id=153586,Text='很不错的故事呢，我很喜欢！',Speaker=890963,}",
  [153587] = "{id=153587,Text='虽然他的笨拙酿成了大祸，但是他的补救方法真是聪明极了！',Speaker=890963,Emoji=11,}",
  [153588] = "{id=153588,Text='我想带给我的小伙伴们一些波利岛美丽的小野花！',Speaker=890965,}",
  [153589] = "{id=153589,Text='你可以帮我采集一些吗？',Speaker=890965,Emoji=21,}",
  [153590] = "{id=153590,Text='谢谢你的帮助！',Speaker=890965,Emoji=27,}",
  [153591] = "{id=153591,Text='嘻嘻，等她们玩累了的时候，看到这些小野花一定会很开心的吧！',Speaker=890965,}",
  [153592] = "{id=153592,Text='那个，可以帮忙拍一张我的照片吗？',Speaker=890966,}",
  [153593] = "{id=153593,Text='记得要把我拍得好看一些！',Speaker=890966,Emoji=38,}",
  [153594] = "{id=153594,Text='把拍好的照片给我看看吧！',Speaker=890966,Option='展示照片',}",
  [153595] = "{id=153595,Text='唔，今天的我看起来好像有些精神不足呢......',Speaker=890966,}",
  [153596] = "{id=153596,Text='可是我讨厌睡觉啦！',Speaker=890966,Emoji=15,}",
  [153597] = "{id=153597,Text='唔，为什么今天的我看起来有些愁眉苦脸的......',Speaker=890966,}",
  [153598] = "{id=153598,Text='这肯定都是因为可可和茜茜的宠物今天又来烦我啦！',Speaker=890966,Emoji=16,}",
  [153599] = "{id=153599,Text='哇哦，今天的我看起来很美丽呢！',Speaker=890966,}",
  [153600] = "{id=153600,Text='当然也有你这位大摄影师的功劳啦！',Speaker=890966,Emoji=20,}",
  [153601] = "{id=153601,Text='咦~你是来找我玩的吗？嘿嘿，那你可要遇上麻烦了！',Speaker=890967,}",
  [153602] = "{id=153602,Text='是这样的，我养了一只淘气好斗的宠物，它叫戈戈，总是很喜欢向别人挑战......',Speaker=890967,}",
  [153603] = "{id=153603,Text='唉，如果你愿意的话，和它较量一下吧，一定要注意安全哦~',Speaker=890967,}",
  [153604] = "{id=153604,Text='虽然戈戈输给你了，但它好像玩得很开心~现在都乖乖回去休息了呢！',Speaker=890967,}",
  [153605] = "{id=153605,Text='谢谢你啦！',Speaker=890967,Emoji=17,}",
  [153606] = "{id=153606,Text='[c][ffff00][PlayerName][-][/c]，本小姐这里有份大大大大大礼物等着你哟~！',Speaker=890969,Option='什么礼物？',}",
  [153607] = "{id=153607,Text='别急别急，赢了我就告诉你，你想玩什么呢？',Speaker=890969,Option='{猜拳,0},{石头剪刀布,0},{都可以,0}',}",
  [153608] = "{id=153608,Text='哼哼~这些可都是我的强项~！',Speaker=890969,}",
  [153609] = "{id=153609,Text='你准备好了吗？[c][ffff00]【猜拳游戏同账号仅限一个角色接取，请谨慎考虑是否继续】[-][/c]',Speaker=890969,Option='{来吧,2},{再等等,3}',}",
  [153610] = "{id=153610,Text='三！',Speaker=890969,}",
  [153611] = "{id=153611,Text='二！',Speaker=890969,}",
  [153612] = "{id=153612,Text='一！',Speaker=890969,}",
  [153613] = "{id=153613,Text='不许快，不许慢，节奏要同步！————剪刀，石头，布！',Speaker=890969,Option='{石头,40},{剪刀,50},{布,60}',}",
  [153614] = "{id=153614,Text='哼哼，这就是“史上最强（猜）拳王”的实力！',Speaker=890969,}",
  [153615] = "{id=153615,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=890969,Option='{再玩一次,5},{不玩啦,6}',}",
  [153616] = "{id=153616,Text='哼~有点实力嘛！',Speaker=890969,Emoji=1,}",
  [153617] = "{id=153617,Text='......没有分出胜负，本小姐可是不会输的。',Speaker=890969,}",
  [153618] = "{id=153618,Text='怎么样，要不要再来一局？',Speaker=890969,Option='{再玩一次,5},{不玩啦,6}',Emoji=8,}",
  [153619] = "{id=153619,Text='哎呀！恭喜你赢了，看来本小姐放水还是很有效果的！',Speaker=890969,Emoji=7,}",
  [153620] = "{id=153620,Text='好吧，我可不是赖帐的人，这份大大大大大礼物就送给你吧！',Speaker=890969,}",
  [153621] = "{id=153621,Text='虽然本小姐很强，不过你也不用害怕，快去准备准备！',Speaker=890969,}",
  [153622] = "{id=153622,Text='变强以后记得来找我比试比试哟！',Speaker=890969,Emoji=17,}",
  [153672] = "{id=153672,Text='今天是个好日子呀～大家都有纯真的心～',Speaker=890969,Option='你是？',Emoji=17,}",
  [153673] = "{id=153673,Text='我是童心信使，在今天这个特别的日子，我要向大家传递儿时的快乐～',Speaker=890969,}",
  [153674] = "{id=153674,Text='这里有给你的一封信，收好哦！',Speaker=890969,}",
  [153675] = "{id=153675,Text='[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Speaker=890969,Option='{现在接取,95},{稍后接取,94}',}",
  [153677] = "{id=153677,Text='“请帮帮我......请阻止那个‘小恶魔’的恶作剧吧！”',}",
  [153678] = "{id=153678,Text='“具体情况我们面谈。我在普隆德拉广场门口等你，拜托了......”',}",
  [153679] = "{id=153679,Text='你是......你就是那个冒险者？',Speaker=890970,Option='没错',Emoji=7,}",
  [153680] = "{id=153680,Text='我们终于有救了呜呜呜。',Speaker=890970,Option='发生了什么',}",
  [153681] = "{id=153681,Text='本来城镇的居民们一如既往地过着安稳的日子。',Speaker=890970,}",
  [153682] = "{id=153682,Text='可是前不久，不知道从哪里冒出来一个小鬼，到处捣乱。',Speaker=890970,}",
  [153683] = "{id=153683,Text='那个可恶的小鬼自称是“Q”，一定要我们陪她玩，不陪就恶作剧......',Speaker=890970,}",
  [153684] = "{id=153684,Text='我的天呐，她真的破坏力太强了。',Speaker=890970,}",
  [153685] = "{id=153685,Text='偷居民家的食物、追着猫猫狗狗乱跑、拿石头砸玻璃......',Speaker=890970,}",
  [153686] = "{id=153686,Text='甚至在夜深人静大家都在睡觉的时候，扛着大喇叭大声吆喝......',Speaker=890970,}",
  [153687] = "{id=153687,Text='“不——许——睡——觉，快——起——来陪我玩——”这样子......',Speaker=890970,Emoji=15,}",
  [153688] = "{id=153688,Text='我真的都快吓出心脏病了！',Speaker=890970,Option='没人赶走她？',}",
  [153689] = "{id=153689,Text='谁不想快点赶走那个小鬼啊，可是......不知道为啥，她一个小孩子，竟然那么厉害！',Speaker=890970,}",
  [153690] = "{id=153690,Text='根本没人打得过她嘛。',Speaker=890970,Emoji=24,}",
  [153691] = "{id=153691,Text='听说你是普隆德拉有名的强者，于是我们想向你求助。',Speaker=890970,}",
  [153692] = "{id=153692,Text='可是又怕让那小鬼知道了加倍报复，迫不得已，这才写了匿名信。',Speaker=890970,}",
  [153693] = "{id=153693,Text='拜托你了，帮帮我们吧！',Speaker=890970,Option='{乐意效劳,99},{我有点忙,98}',Emoji=24,}",
  [153694] = "{id=153694,Text='真的吗！太好了！我替大伙儿谢谢你！',Speaker=890970,Emoji=6,}",
  [153695] = "{id=153695,Text='哎......好吧，那不打扰你忙了。',Speaker=890970,}",
  [153696] = "{id=153696,Text='那个可恶的小鬼“Q”，她就常常在普隆德拉的商店街休息 。',Speaker=890970,}",
  [153697] = "{id=153697,Text='那就拜托你啦！等着你的好消息！那我先去修被小鬼弄坏的房子了！',Speaker=890970,Option='我可以帮忙',}",
  [153698] = "{id=153698,Text='真的吗！那真是太谢谢你了！我先过去看看情况。',Speaker=890970,}",
  [153699] = "{id=153699,Text='修复房子需要5个木材，拜托你到教堂附近收集一下好不好？',Speaker=890970,}",
  [153700] = "{id=153700,Text='谢谢你！辛苦了！',Speaker=890970,}",
  [153701] = "{id=153701,Text='那么可以开始修房子了呢。',Speaker=890970,}",
  [153702] = "{id=153702,Text='哇！这么快就修好了！不愧是冒险者！',Speaker=890970,Option='不客气',Emoji=39,}",
  [153703] = "{id=153703,Text='那么阻止Q那个臭小鬼的事也就拜托你啦！真的感激不尽！',Speaker=890970,Emoji=6,}",
  [153704] = "{id=153704,Text='你是谁？是不是那群家伙派来赶我走的？',Speaker=890971,Option='……',}",
  [153705] = "{id=153705,Text='要我乖乖走？没门——你们快点找人跟我一起玩啦！',Speaker=890971,}",
  [153706] = "{id=153706,Text='你回去告诉他们，要是没人愿意陪我玩，我就一直搅得他们不得安宁，嘿嘿～',Speaker=890971,Emoji=5,}",
  [153707] = "{id=153707,Text='不过，你看～今天天气这～么好，出去一起玩多好啊！',Speaker=890971,}",
  [153708] = "{id=153708,Text='这样吧，要我停止恶作剧呢，可以。',Speaker=890971,}",
  [153709] = "{id=153709,Text='不过我有一个条件。',Speaker=890971,Option='什么条件',}",
  [153710] = "{id=153710,Text='既然那群人不愿意陪我玩，那么你来陪我一起玩吧！',Speaker=890971,Emoji=17,}",
  [153711] = "{id=153711,Text='你愿意和我一起玩吗？',Speaker=890971,Option='{好吧,92},{我拒绝,91}',Emoji=26,}",
  [153712] = "{id=153712,Text='哇啊啊啊！太好啦！我就知道你会答应的！',Speaker=890971,}",
  [153713] = "{id=153713,Text='呜呜呜！为什么！就因为我是个爱恶作剧的坏家伙吗？！那么我更要恶作剧了！',Speaker=890971,Emoji=12,}",
  [153714] = "{id=153714,Text='那我们我们说好了哦～明天就在这个地方见面！拉钩上吊一百年不许变！',Speaker=890971,}",
  [153715] = "{id=153715,Text='那么我们明天见啦！拜拜～',Speaker=890971,}",
  [153719] = "{id=153719,Text='你好慢哦！我等得都快睡着了......',Speaker=890973,Emoji=40,}",
  [153720] = "{id=153720,Text='不过你来了就好～',Speaker=890973,Option='乖哦宝宝～',}",
  [153721] = "{id=153721,Text='嗯？？？',Speaker=890973,Option='给你糖糖吃～',}",
  [153722] = "{id=153722,Text='你干嘛用这种傻里傻气的语气跟我说话啦？',Speaker=890973,}",
  [153723] = "{id=153723,Text='我可不是小孩子！',Speaker=890973,Option='可是你的样子……',Emoji=40,}",
  [153724] = "{id=153724,Text='这、这都是意外啦！其实我已经三千岁了呢！信不信由你！',Speaker=890973,Option='好吧我信……',}",
  [153725] = "{id=153725,Text='嗯嗯！那么我们快开始玩吧～玩点什么好呢？',Speaker=890973,}",
  [153726] = "{id=153726,Text='先玩捉迷藏吧！',Speaker=890973,}",
  [153727] = "{id=153727,Text='先猜拳决定谁当鬼咯～',Speaker=890973,Option='{石头,99},{剪刀,98},{布,97}',}",
  [153728] = "{id=153728,Text='耶！我赢啦！你来当鬼哦！要数10下～不许偷看！',Speaker=890973,Emoji=20,}",
  [153729] = "{id=153729,Text='嗯？这小妹妹到底跑到哪里去了？刚才好像看到她了呀......',Emoji=19,}",
  [153730] = "{id=153730,Text='波利～波～利～',Speaker=890974,}",
  [153731] = "{id=153731,Text='你看到一个小女孩了么？',}",
  [153732] = "{id=153732,Text='波～利～',Speaker=890974,Option='……你就是Q！',Emoji=18,}",
  [153733] = "{id=153733,Text='啊！这就被发现了？！我觉得我藏得很隐秘呀？',Speaker=890973,Emoji=19,}",
  [153734] = "{id=153734,Text='我感受到了你身上那股强烈的力量。',}",
  [153735] = "{id=153735,Text='哎呀～我怎么把这个给忘了！嗯～承认你有点聪明！',Speaker=890973,Option='好玩吗',}",
  [153736] = "{id=153736,Text='嗯——我玩腻了。我们换一个游戏玩吧！',Speaker=890973,Emoji=28,}",
  [153737] = "{id=153737,Text='诶对了，我要去打魔物！你也一起来！',Speaker=890973,}",
  [153738] = "{id=153738,Text='你跑得好慢哦！',Speaker=890973,}",
  [153739] = "{id=153739,Text='你看这边有这么多魔物呢！它们看上去呆头呆脑的好傻哦。',Speaker=890973,Emoji=17,}",
  [153740] = "{id=153740,Text='看我的！',Speaker=890973,Option='你在干什么！',}",
  [153741] = "{id=153741,Text='谁刚才打我？！',Speaker=890975,}",
  [153742] = "{id=153742,Text='哈哈哈哈好开心！原来打魔物这么好玩啊～',Speaker=890973,}",
  [153743] = "{id=153743,Text='你是不是也经常在玩这么好玩的游戏呀？',Speaker=890973,Option='……',}",
  [153744] = "{id=153744,Text='看来我选玩伴的眼光还是很好的嘛，找到这么好玩的家伙。',Speaker=890973,Emoji=26,}",
  [153745] = "{id=153745,Text='现在我正式认定你成为了我的好朋友！',Speaker=890973,}",
  [153746] = "{id=153746,Text='既然是我的好朋友，那么有秘密就可以共享了～',Speaker=890973,}",
  [153747] = "{id=153747,Text='其实......我听人说普隆德拉有着世界上最美的星空。',Speaker=890973,}",
  [153748] = "{id=153748,Text='但是星空我怎么带得走呢？怎么也带不走啊。',Speaker=890973,}",
  [153749] = "{id=153749,Text='那么至少也要带走天空中最美的一颗星星！',Speaker=890973,}",
  [153750] = "{id=153750,Text='你是我的好朋友，那么你一定会帮我的吧？帮我想想怎么带走那颗星星的方法吧！',Speaker=890973,Emoji=26,}",
  [153751] = "{id=153751,Text='不然我还要接着恶作剧！',Speaker=890973,Option='好好，知道啦',}",
  [153752] = "{id=153752,Text='哇！真的吗！你愿意帮我找？！耶——你真好～',Speaker=890973,}",
  [153753] = "{id=153753,Text='那么明天我们一起开始找吧！还在老地方集合，可别再迟到了哦！',Speaker=890973,}",
  [153754] = "{id=153754,Text='哈——唔，今天玩了一整天玩累了，我好困，先回去睡觉啦，明天见哦。',Speaker=890973,Emoji=36,}",
  [153758] = "{id=153758,Text='啊～昨晚睡得不错呢！你呢？',Speaker=890976,Option='还好啦',Emoji=26,}",
  [153759] = "{id=153759,Text='那么你有没有什么好办法摘星星呢？',Speaker=890976,Option='正要去找呢',}",
  [153760] = "{id=153760,Text='好吧，那我在这里等你，你加油找哦！',Speaker=890976,}",
  [153761] = "{id=153761,Text='你找我有什么事么？',Speaker=890977,Option='讲述经过',Emoji=8,}",
  [153762] = "{id=153762,Text='啊！这个我听普隆德拉南门的吟游诗人叔叔讲过！我也好想要呢～',Speaker=890977,}",
  [153763] = "{id=153763,Text='不过......真的可以摘到星星吗？',Speaker=890977,Option='或许有可能',}",
  [153764] = "{id=153764,Text='摘到了的话我也想看一眼！',Speaker=890977,Emoji=28,}",
  [153765] = "{id=153765,Text='那么你加油哦！我先去玩啦！',Speaker=890977,}",
  [153766] = "{id=153766,Text='远道而来的客人，请问找我有何事？',Speaker=890978,Option='讲述经过',}",
  [153767] = "{id=153767,Text='啊～原来是这个啊。你找对人了。',Speaker=890978,}",
  [153768] = "{id=153768,Text='我知道怎么摘星星。',Speaker=890978,Option='请告诉我',}",
  [153769] = "{id=153769,Text='告诉你是可以，不过你需要帮我一个忙。',Speaker=890978,}",
  [153770] = "{id=153770,Text='过两天我就要离开这里去往下一个城镇了，可是一群魔物挡住了去路。',Speaker=890978,}",
  [153771] = "{id=153771,Text='请你帮我赶跑那群魔物好吗？',Speaker=890978,Option='{乐意效劳,99},{这个……,98}',}",
  [153772] = "{id=153772,Text='非常感谢！',Speaker=890978,}",
  [153773] = "{id=153773,Text='难道你不想知道摘得星星的方法吗？',Speaker=890978,}",
  [153774] = "{id=153774,Text='那群魔物它们就在普隆德拉南门。你可要小心啊！',Speaker=890978,}",
  [153775] = "{id=153775,Text='不愧是冒险者！你太厉害了！真的很谢谢你！',Speaker=890978,}",
  [153776] = "{id=153776,Text='作为谢礼，我就告诉你怎么摘得星星吧！',Speaker=890978,Emoji=17,}",
  [153777] = "{id=153777,Text='你要和那个小女孩一起，弥补她的恶作剧给普隆德拉广场的大伙儿造成的损失。',Speaker=890978,Option='我去跟她说',}",
  [153778] = "{id=153778,Text='你把她带过来吧。',Speaker=890978,}",
  [153779] = "{id=153779,Text='你回来啦！怎么样？找到了吗？',Speaker=890976,Option='讲述经过',}",
  [153780] = "{id=153780,Text='什么？！竟然要我弥补那些家伙的损失？我不干我不干我不干嘛～～',Speaker=890976,Option='星星不要了吗',Emoji=1,}",
  [153781] = "{id=153781,Text='呜～～～～',Speaker=890976,}",
  [153782] = "{id=153782,Text='好吧！为了我的星星，我和你一起去！那个吟游诗人在哪？我们找他去。',Speaker=890976,}",
  [153783] = "{id=153783,Text='这就是那个吟游诗人么？看上去好可疑哦。',Speaker=890976,Emoji=40,}",
  [153784] = "{id=153784,Text='你把这个孩子带来了啊......',Speaker=890978,}",
  [153785] = "{id=153785,Text='原来你就是那个把普隆德拉闹得不得安宁的孩子......',Speaker=890978,}",
  [153786] = "{id=153786,Text='想要星星的话就要好好反省自己的错误哦！',Speaker=890978,}",
  [153787] = "{id=153787,Text='知、知道了啦！我错了还不行么。我去帮忙就好了吧。',Speaker=890976,}",
  [153788] = "{id=153788,Text='知错就改还是好孩子。不过你这样子去，大家肯定不开心。',Speaker=890978,}",
  [153789] = "{id=153789,Text='那怎么办嘛？我还想要星星呢。',Speaker=890976,Emoji=24,}",
  [153790] = "{id=153790,Text='这样吧，我帮你变装一下，这样大家就认不出你了。',Speaker=890978,}",
  [153791] = "{id=153791,Text='嗯——好吧。不过你要说到做到，做完了要给我星星哦！',Speaker=890976,}",
  [153792] = "{id=153792,Text='一言为定！我从不骗小孩子的。',Speaker=890978,}",
  [153793] = "{id=153793,Text='我不是小孩子！',Speaker=890976,Emoji=12,}",
  [153795] = "{id=153795,Text='原来真的可以摘到星星啊！好期待！',Speaker=890976,}",
  [153796] = "{id=153796,Text='明天你要按时来这里哦！我们一起努力吧！',Speaker=890976,}",
  [153797] = "{id=153797,Text='我现在......有点困了......我要回去睡觉了，拜拜啦。',Speaker=890976,}",
  [153801] = "{id=153801,Text='冒险者，你来得很准时呢。',Speaker=890981,}",
  [153802] = "{id=153802,Text='你又迟到啦！',Speaker=890979,Option='明明准点到的',Emoji=40,}",
  [153803] = "{id=153803,Text='她似乎很想要星星呢......所以才这么迫不及待吧。',Speaker=890981,}",
  [153804] = "{id=153804,Text='好啦，我帮你变装哦，准备好了吗？',Speaker=890981,}",
  [153805] = "{id=153805,Text='快点啦！',Speaker=890979,}",
  [153806] = "{id=153806,Text='这个变装......算啦，走吧！',Speaker=890980,}",
  [153807] = "{id=153807,Text='那边有人在哭......',Speaker=890980,}",
  [153808] = "{id=153808,Text='呜呜呜呜～咪咪，咪咪你在哪里啊，快回来呀......',Speaker=890982,Emoji=42,}",
  [153809] = "{id=153809,Text='你干嘛哭？好丢人哦。',Speaker=890980,}",
  [153810] = "{id=153810,Text='都怪那个叫什么“Q”的坏小孩啦！她整天恶作剧，把我的猫咪吓跑了......',Speaker=890982,}",
  [153811] = "{id=153811,Text='你......！是这样啊，我们帮你找，你别哭了好不好？',Speaker=890980,}",
  [153812] = "{id=153812,Text='真的吗！你们真好！',Speaker=890982,Emoji=17,}",
  [153813] = "{id=153813,Text='希望咪咪平安无事......',Speaker=890982,}",
  [153814] = "{id=153814,Text='猫猫～猫猫你快点出来～回家啦！',Speaker=890980,}",
  [153815] = "{id=153815,Text='没有反应......',Speaker=890980,}",
  [153816] = "{id=153816,Text='我感到了一股小动物的气息......猫猫应该就在这附近！',Speaker=890980,}",
  [153817] = "{id=153817,Text='果然！它果然在这里！',Speaker=890980,}",
  [153818] = "{id=153818,Text='喵～喵～',Speaker=890983,Emoji=24,}",
  [153819] = "{id=153819,Text='它好像很害怕的样子......得先让它冷静下来......',Speaker=890980,}",
  [153820] = "{id=153820,Text='对了！给它喂点吃的！',Speaker=890980,}",
  [153821] = "{id=153821,Text='喵～喵～咪咪',Speaker=890983,Emoji=17,}",
  [153822] = "{id=153822,Text='它好像愿意跟我们走了呢～',Speaker=890980,}",
  [153823] = "{id=153823,Text='我们把咪咪带回来了！',Speaker=890980,Emoji=26,}",
  [153824] = "{id=153824,Text='咪咪——咪咪！',Speaker=890982,Emoji=42,}",
  [153825] = "{id=153825,Text='谢谢你们！',Speaker=890982,}",
  [153826] = "{id=153826,Text='喵～喵～喵～',Speaker=890983,}",
  [153827] = "{id=153827,Text='太好了太好了～',Speaker=890982,}",
  [153828] = "{id=153828,Text='真的谢谢你哦，今后我们做朋友，一起和咪咪玩好不好？',Speaker=890982,}",
  [153829] = "{id=153829,Text='好的......',Speaker=890980,}",
  [153830] = "{id=153830,Text='啊，那边花店的叔叔好像很不开心的样子。他经常给我花的，我有点担心呢......',Speaker=890982,}",
  [153831] = "{id=153831,Text='咪咪你终于回来了呜呜呜呜呜......我去跟妈妈说咪咪回来了！',Speaker=890982,}",
  [153832] = "{id=153832,Text='没想到我把妮可的朋友吓跑了......感觉有点对不起她。',Speaker=890980,Option='看来你在反省嘛',}",
  [153833] = "{id=153833,Text='哼！我只是觉得妮可没有朋友的话有点可怜啦！',Speaker=890980,Option='好，好……',}",
  [153834] = "{id=153834,Text='嗯～两位看来做得不错。Q，你也认识到自己的错误了。很好很好。',Speaker=890981,}",
  [153835] = "{id=153835,Text='你怎么来了？！',Speaker=890980,}",
  [153836] = "{id=153836,Text='先别管我。Q，今天忙活了一天，你有什么感想？',Speaker=890981,}",
  [153837] = "{id=153837,Text='嗯......我、我不知道怎么说啦。只觉得很累，但是心里好像有一种吃了蛋糕一样幸福的感觉。',Speaker=890980,}",
  [153838] = "{id=153838,Text='以前恶作剧是很好玩啦，可是笑完了之后还是很无聊......今天不一样。',Speaker=809980,}",
  [153839] = "{id=153839,Text='今天你们忙活了一天，都累了吧。明天我们还在老地方会合。有份惊喜等着你们。',Speaker=890981,}",
  [153840] = "{id=153840,Text='那么我先回去了。',Speaker=890981,}",
  [153841] = "{id=153841,Text='哎～我以后不会到处恶作剧了。',Speaker=890979,}",
  [153842] = "{id=153842,Text='今天好累啊～困死我了。我要回去睡觉啦，你也早点休息吧。期待明天的惊喜～拜拜～',Speaker=890979,}",
  [153864] = "{id=153864,Text='冒险者，真是不好意思，又有事情要麻烦你了。',Speaker=891020,Option='什么事',Emoji=6,}",
  [153865] = "{id=153865,Text='米德加尔特有着我们那个世界难得一见的风景，我和我的伙伴们想在这里多驻留一段时间，进行各自的修行。',Speaker=891020,}",
  [153866] = "{id=153866,Text='所以我们想拜托你帮忙寻找适合我们的修行之地。',Speaker=891020,}",
  [153867] = "{id=153867,Text='我们每个人的铠甲都有各自的特性，在适合的地方修行，效果会更好！',Speaker=891021,Option='没问题',Emoji=21,}",
  [153868] = "{id=153868,Text='我从小在山上长大，后来在一次森林大火中获得了烈火铠甲。所以对树林有特别的情感。',Speaker=891020,}",
  [153869] = "{id=153869,Text='我想在一片[c][ffff00]树林[-][/c]里进行剑道的修行。',Speaker=891020,}",
  [153870] = "{id=153870,Text='听说在斐扬树林就有这样一个地方。',Speaker=891020,}",
  [153871] = "{id=153871,Text='能麻烦你去拍张照片给我吗？',Speaker=891020,Emoji=21,}",
  [153872] = "{id=153872,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891020,Option='{好的,3},{再想想,2}',}",
  [153873] = "{id=153873,Text='如果改变主意了再来找我。',Speaker=891020,}",
  [153874] = "{id=153874,Text='你好啊，冒险者。',Speaker=891020,}",
  [153875] = "{id=153875,Text='今天想拜托你帮秀寻找一个合适的修行之地。',Speaker=891020,Option='没问题',}",
  [153876] = "{id=153876,Text='金刚铠甲的绝技是岩铁碎。',Speaker=891021,}",
  [153877] = "{id=153877,Text='我一直都在我们原本世界的大雪山修行。',Speaker=891021,}",
  [153878] = "{id=153878,Text='我曾经梦到过在沙漠中修行，一击就将沙丘一分为二。',Speaker=891021,}",
  [153879] = "{id=153879,Text='这一次，我想在[c][ffff00]沙漠[-][/c]中修行，亲眼见证一下我梦中的[c][ffff00]沙之眼[-][/c]。',Speaker=891021,}",
  [153880] = "{id=153880,Text='听说苏克拉特沙漠就有这样一个地方。',Speaker=891021,}",
  [153881] = "{id=153881,Text='如果可以的话，快去拍张照片给我吧！',Speaker=891021,Option='好的',Emoji=20,}",
  [153882] = "{id=153882,Text='你好啊，冒险者。',Speaker=891020,}",
  [153883] = "{id=153883,Text='今天想拜托你帮伸寻找一个合适的修行之地。',Speaker=891020,Option='没问题',}",
  [153884] = "{id=153884,Text='我与海洋有特别的羁绊。',Speaker=891023,}",
  [153885] = "{id=153885,Text='守护海洋是我作为水神的职责。',Speaker=891023,}",
  [153886] = "{id=153886,Text='超流破是用心体会浪涛的波动后才能掌握的绝技。',Speaker=891023,}",
  [153887] = "{id=153887,Text='我需要在[c][ffff00]海洋[-][/c]里进行修行。想[c][ffff00]和海洋更亲密[-][/c]一些。',Speaker=891023,}",
  [153888] = "{id=153888,Text='听说海底洞穴就有一个地方就可以与海洋有更深的连结。',Speaker=891023,}",
  [153889] = "{id=153889,Text='方便的话，还请去拍张照片给我。',Speaker=891023,Option='好的',Emoji=21,}",
  [153890] = "{id=153890,Text='你好啊，冒险者。',Speaker=891020,}",
  [153891] = "{id=153891,Text='今天想拜托你帮征士寻找一个合适的修行之地。',Speaker=891020,Option='没问题',}",
  [153892] = "{id=153892,Text='无尽黑暗中，坚守的心就是那点希望的光。',Speaker=891022,}",
  [153893] = "{id=153893,Text='要有心去感受，而不是用双眼去看。',Speaker=891022,}",
  [153894] = "{id=153894,Text='相信心的力量，就是雷光斩的终极奥义。',Speaker=891022,}",
  [153895] = "{id=153895,Text='我准备在一个[c][ffff00]黑暗[-][/c]的地方来磨砺自己。',Speaker=891022,}",
  [153896] = "{id=153896,Text='听说在钟楼地下正好有这样一个地方。',Speaker=891022,}",
  [153897] = "{id=153897,Text='有时间的话，可以去拍张照片给我吗？',Speaker=891022,Option='好的',Emoji=21,}",
  [153898] = "{id=153898,Text='你好啊，冒险者。',Speaker=891020,}",
  [153899] = "{id=153899,Text='这几天你替大家找的修行之地都非常棒。太感谢你了！',Speaker=891020,Emoji=20,}",
  [153900] = "{id=153900,Text='今天还想想拜托你帮当麻寻找一个合适的修行之地。',Speaker=891020,Option='没问题',Emoji=17,}",
  [153901] = "{id=153901,Text='天空铠甲掌握着飞行的力量。',Speaker=891024,}",
  [153902] = "{id=153902,Text='越接近天空，对我的修行就越越有帮助',Speaker=891024,}",
  [153903] = "{id=153903,Text='在天空之上，透过云雾，遮蔽心的迷雾也会随之散去。',Speaker=891024,}",
  [153904] = "{id=153904,Text='请帮我寻找一个[c][ffff00]无人打扰[-][/c]，[c][ffff00]离天空更[-][/c]近的地方。',Speaker=891024,}",
  [153905] = "{id=153905,Text='听说妙勒尼山脉的天空岛是离天空非常近的地方。',Speaker=891024,}",
  [153906] = "{id=153906,Text='如果可以，希望能帮忙拍张照片给我。',Speaker=891024,Option='好的',Emoji=21,}",
  [153907] = "{id=153907,Text='[c][ffff00][PlayerName][-][/c]，我们近来又监测到了时空裂隙的波动，又有异世界的人来到了米德加尔特。',Speaker=891003,}",
  [153908] = "{id=153908,Text='他们现在就在普隆德拉的广场。麻烦你去看看吧。',Speaker=891003,}",
  [153909] = "{id=153909,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891003,Option='{好的,3},{再想想,2}',}",
  [153910] = "{id=153910,Text='哟，[c][ffff00][PlayerName][-][/c]，听说你是一位非常强大的冒险者。',Speaker=891000,Option='……',}",
  [153911] = "{id=153911,Text='哈哈，我们可是听说了很多关于你的了不起的事迹呢！',Speaker=891001,}",
  [153912] = "{id=153912,Text='没错。我们也是相当了不起的战士哦，拯救过世界的那种，哈哈。',Speaker=891000,}",
  [153913] = "{id=153913,Text='听说你的故事之后，我们的战士之魂也开始燃烧了，要不要和我们比试一下？',Speaker=891000,Option='{没时间,3},{好的,2}',}",
  [153914] = "{id=153914,Text='好吧，那真是太遗憾了。',Speaker=891000,}",
  [153915] = "{id=153915,Text='要不要和我们比试一下？',Speaker=891000,Option='{没时间,3},{好的,2}',}",
  [153916] = "{id=153916,Text='太好了，就让我先来吧！',Speaker=891001,Emoji=21,}",
  [153917] = "{id=153917,Text='哈哈，秀，你总是这样。',Speaker=891000,}",
  [153918] = "{id=153918,Text='不过这一次，就让我来吧。',Speaker=891000,Emoji=17,}",
  [153919] = "{id=153919,Text='果然实力非常强劲啊，[c][ffff00][PlayerName][-][/c]。',Speaker=891000,Emoji=20,}",
  [153920] = "{id=153920,Text='不过我们魔神坛斗士也不是徒有虚名。',Speaker=891000,}",
  [153921] = "{id=153921,Text='[c][ffff00][PlayerName][-][/c]，要小心了。双炎斩！',Speaker=891000,}",
  [153922] = "{id=153922,Text='嗯？！',Speaker=891000,Emoji=7,}",
  [153923] = "{id=153923,Text='烈火剑有些不对劲！',Speaker=891000,Option='什么情况',Emoji=1,}",
  [153924] = "{id=153924,Text='在刚刚战斗的过程中，我发现烈火剑失去了原本的威力。似乎，被什么力量封印住了的感觉。',Speaker=891000,Emoji=19,}",
  [153925] = "{id=153925,Text='烈火剑从我得到火焰神铠甲后就一直陪伴着我。对我有着特别的意义。',Speaker=891000,}",
  [153926] = "{id=153926,Text='他也曾经断过，但是最终还是修复了……',Speaker=891000,}",
  [153927] = "{id=153927,Text='可是这一次，我感觉我和烈火剑的羁绊断开了。',Speaker=891000,Option='让我看看吧',Emoji=18,}",
  [153928] = "{id=153928,Text='可以。',Speaker=891000,}",
  [153929] = "{id=153929,Text='看上去只是是一把普通的刀，除了更锋利一些感受不到其他特别的地方……',Option='回复火焰神',}",
  [153930] = "{id=153930,Text='怎么样？有头绪了吗？',Speaker=891000,Option='说明情况',}",
  [153931] = "{id=153931,Text='你也没有办法解决烈火剑的问题吗……',Speaker=891000,Emoji=14,}",
  [153932] = "{id=153932,Text='或许在吉芬可以找到解决办法。',}",
  [153933] = "{id=153933,Text='吉芬？',Speaker=891000,Option='介绍吉芬',}",
  [153934] = "{id=153934,Text='真的吗？太好了，快带我们一起去吧！',Speaker=891000,Option='{好的,4},{再想想,5}',}",
  [153935] = "{id=153935,Text='准备去吉芬了吗？',Speaker=891000,Option='{好的,4},{再想想,5}',}",
  [153936] = "{id=153936,Text='[c][ffff00][PlayerName][-][/c]，好久不见，有什么事情吗？',Speaker=1321,Option='说明情况',}",
  [153937] = "{id=153937,Text='原来是这样……可以把烈火剑给我看看吗？',Speaker=1321,}",
  [153938] = "{id=153938,Text='可以。',Speaker=891000,}",
  [153939] = "{id=153939,Text='（自言自语）唔……',Speaker=1321,Emoji=14,}",
  [153940] = "{id=153940,Text='（自言自语）居然……',Speaker=1321,Emoji=19,}",
  [153941] = "{id=153941,Text='（自言自语）真的没想到……',Speaker=1321,Emoji=14,}",
  [153942] = "{id=153942,Text='（自言自语）莫非……也许……',Speaker=1321,Emoji=19,}",
  [153943] = "{id=153943,Text='原来如此！',Speaker=1321,Option='怎么回事',Emoji=11,}",
  [153944] = "{id=153944,Text='烈火剑在穿越时空裂隙的过程中，沾染了时空裂隙的能量，自身的力量被阻隔。',Speaker=1321,}",
  [153945] = "{id=153945,Text='那应该怎么办呢？',Speaker=891000,}",
  [153946] = "{id=153946,Text='哈哈，不要急，只要驱除时空裂隙残留的能量就好了。',Speaker=1321,Option='该怎么做',}",
  [153947] = "{id=153947,Text='先回答我几个问题吧。',Speaker=1321,}",
  [153948] = "{id=153948,Text='嗯，我已经看到了你们的决心。',Speaker=1321,}",
  [153949] = "{id=153949,Text='这把烈火剑我已经去除了上面残留的时空裂隙能量，现在物归原主。',Speaker=1321,}",
  [153950] = "{id=153950,Text='希望你们能够一直铭记自己为之战斗的是什么。',Speaker=1321,}",
  [153951] = "{id=153951,Text='真是太感谢了。我们不会忘记的！',Speaker=891000,}",
  [153952] = "{id=153952,Text='[c][ffff00][PlayerName][-][/c]，也非常感谢你的帮助。',Speaker=891000,Option='没什么',}",
  [153953] = "{id=153953,Text='我还有一件事需要你的帮助。',Speaker=891000,Option='什么事',}",
  [153954] = "{id=153954,Text='其实来到这片大陆的不止我和秀，其他3位伙伴也来到了这里。',Speaker=891000,}",
  [153955] = "{id=153955,Text='甚至……妖邪界也……',Speaker=891000,}",
  [153956] = "{id=153956,Text='先不想那么多。我的其他3位伙伴，光辉神征士、水神伸、天空神当麻现在还不知所踪。',Speaker=891000,}",
  [153957] = "{id=153957,Text='我和秀非常担心他们的安危。',Speaker=891000,}",
  [153958] = "{id=153958,Text='请一定要帮我们找到他们啊！',Speaker=891001,Option='一定',}",
  [153959] = "{id=153959,Text='我们五个能够通过铠甲彼此感应……',Speaker=891000,}",
  [153960] = "{id=153960,Text='一旦找到一名同伴的讯息，我会立刻通知你。',Speaker=891000,Option='好的',}",
  [153961] = "{id=153961,Text='我们先离开这里吧。',Speaker=891000,}",
  [153962] = "{id=153962,Text='你来了啊，[c][ffff00][PlayerName][-][/c]。',Speaker=891000,Option='有其他人的消息了吗',}",
  [153963] = "{id=153963,Text='没错，我刚刚在修行的时候，突然感受到了迦雄须传递的信息。',Speaker=891000,}",
  [153964] = "{id=153964,Text='烈火，光轮正身处一片黑暗之中。沉睡在[c][ffff00]东南方[-][/c]幽暗的[c][ffff00]洞窟[-][/c]之下。快去唤醒光轮吧……',Speaker=891010,}",
  [153965] = "{id=153965,Text='征士似乎被铠甲送到了这片大陆的[c][ffff00]东南方向[-][/c]的一个地方。',Speaker=891000,}",
  [153966] = "{id=153966,Text='从迦雄须传递的信息看，那里似乎是一片非常幽暗的地下[c][ffff00]洞窟[-][/c]。',Speaker=891000,Option='斐扬洞窟',}",
  [153967] = "{id=153967,Text='那是什么样的地方？',Speaker=891000,Option='介绍斐扬洞窟',}",
  [153968] = "{id=153968,Text='听起来是一个和秋芳洞很像的地方啊。征士被铠甲送到这种地方也很合理了。',Speaker=891001,}",
  [153969] = "{id=153969,Text='辽、[c][ffff00][PlayerName][-][/c]我们快出发去救征士吧！',Speaker=891001,}",
  [153970] = "{id=153970,Text='我们还要继续寻找伸和当麻的下落。',Speaker=891000,Emoji=40,}",
  [153971] = "{id=153971,Text='啊，那只能麻烦[c][ffff00][PlayerName][-][/c]你去一趟斐扬洞窟帮助我们找到征士了。',Speaker=891001,Emoji=15,}",
  [153972] = "{id=153972,Text='不过白炎会跟随你一起去寻找征士的，他可是很聪明的。',Speaker=891000,}",
  [153973] = "{id=153973,Text='相信你们俩一定能相处的很好，哈哈。',Speaker=891000,Option='{好的,2},{再想想,3}',Emoji=20,}",
  [153974] = "{id=153974,Text='如果准备好了请和我说。',Speaker=891000,}",
  [153975] = "{id=153975,Text='准备去斐扬洞窟了吗？',Speaker=891000,Option='{好的,2},{再想想,3}',}",
  [153976] = "{id=153976,Text='白炎，这位是[c][ffff00][PlayerName][-][/c]，他会和你一起去寻找征士。',Speaker=891000,}",
  [153977] = "{id=153977,Text='吼——',Speaker=891005,}",
  [153978] = "{id=153978,Text='斐扬洞窟比平时更昏暗了……',}",
  [153979] = "{id=153979,Text='吼——',Speaker=891005,}",
  [153980] = "{id=153980,Text='嗯，先找到光辉神要紧。',}",
  [153981] = "{id=153981,Text='这里没有……',}",
  [153982] = "{id=153982,Text='去下一个地方看看吧。',}",
  [153983] = "{id=153983,Text='这里也没有。',}",
  [153984] = "{id=153984,Text='光辉神会在哪里呢……',}",
  [153985] = "{id=153985,Text='吼！',Speaker=891005,}",
  [153986] = "{id=153986,Text='怎么那么多魔物？！',}",
  [153987] = "{id=153987,Text='吼！吼！',Speaker=891005,}",
  [153988] = "{id=153988,Text='白炎似乎在向我示意什么。',}",
  [153990] = "{id=153990,Text='吼！！',Speaker=891005,}",
  [153991] = "{id=153991,Text='白炎似乎有危险。',}",
  [153992] = "{id=153992,Text='吼——',Speaker=891005,}",
  [153993] = "{id=153993,Text='白炎似乎发现了光辉神的下落。',}",
  [153994] = "{id=153994,Text='吼——',Speaker=891005,}",
  [153995] = "{id=153995,Text='是光辉神！',}",
  [153996] = "{id=153996,Text='……',Speaker=891006,}",
  [153997] = "{id=153997,Text='吼！',Speaker=891005,}",
  [153998] = "{id=153998,Text='从小至大，汇于中间，方可唤来光明。',Speaker=891010,}",
  [154000] = "{id=154000,Text='似乎不应该移动这个机关。',Speaker=891007,}",
  [154001] = "{id=154001,Text='移动到哪里？',Speaker=891008,Option='{中间,4},{算了,5}',}",
  [154002] = "{id=154002,Text='移动成功了。',}",
  [154003] = "{id=154003,Text='移动到哪里？',Speaker=891007,Option='{中间,6},{最左边,7},{算了,8}',}",
  [154004] = "{id=154004,Text='没有必要。',}",
  [154005] = "{id=154005,Text='移动成功了。',}",
  [154006] = "{id=154006,Text='刚刚是……迦雄须？',Speaker=891004,Emoji=14,}",
  [154007] = "{id=154007,Text='非常感谢你把我从黑暗中唤醒。',Speaker=891004,Option='没事就好',Emoji=6,}",
  [154008] = "{id=154008,Text='吼——',Speaker=891005,}",
  [154009] = "{id=154009,Text='哈哈哈，白炎，好久不见了，也要感谢你啊。',Speaker=891004,}",
  [154010] = "{id=154010,Text='啊，在黑暗中沉睡了那么久，可真让人不舒服，我们快去和辽他们会合吧。',Speaker=891004,Option='{立即传送,13},{自己回去,14}',}",
  [154011] = "{id=154011,Text='[c][ffff00][PlayerName][-][/c]，太感谢你了，帮我们把征士救了出来。',Speaker=891000,Option='应该的',}",
  [154012] = "{id=154012,Text='我们现在还没有伸和当麻的消息。',Speaker=891000,}",
  [154013] = "{id=154013,Text='不要急。',}",
  [154014] = "{id=154014,Text='嗯，如果有了他们的消息我会第一时间通知你的。',Speaker=891000,Option='好的',}",
  [154015] = "{id=154015,Text='你来了啊，[c][ffff00][PlayerName][-][/c]。',Speaker=891000,Option='有其他人消息吗',}",
  [154016] = "{id=154016,Text='迦雄须刚刚向我们传达了伸的消息。',Speaker=891000,Option='怎么样',}",
  [154017] = "{id=154017,Text='烈火，水浒已经苏醒。他正处于海洋极深之地。那里似乎有妖邪界活动的迹象……',Speaker=891010,}",
  [154018] = "{id=154018,Text='伸现在身处[c][ffff00]海底[-][/c]，他在那里感受到了妖邪力的存在。',Speaker=891000,Option='海底洞穴',}",
  [154019] = "{id=154019,Text='据迦雄须传递的消息，那里有大量的魔物受到妖邪力的影响变得狂暴了起来。',Speaker=891000,}",
  [154020] = "{id=154020,Text='伸他有可能和妖邪界的力量遭遇了。',Speaker=891000,}",
  [154021] = "{id=154021,Text='可恶，我们无法在海底的环境长久活动……伸可千万不要出事啊……',Speaker=891004,Emoji=14,}",
  [154022] = "{id=154022,Text='所以我们想拜托你前往支援伸。',Speaker=891000,Option='{没问题,2},{再等等,3}',}",
  [154023] = "{id=154023,Text='准备去支援伸了吗？',Speaker=891000,Option='{没问题,2},{再等等,3}',}",
  [154024] = "{id=154024,Text='千万小心。',Speaker=891000,}",
  [154025] = "{id=154025,Text='海底洞穴的魔物变得格外的多……',}",
  [154026] = "{id=154026,Text='先解决掉这些螃蟹吧。',}",
  [154027] = "{id=154027,Text='越往前走魔物越多……',}",
  [154028] = "{id=154028,Text='水神应该就在前方不远了。',}",
  [154029] = "{id=154029,Text='你是辽他们说的那位冒险者吗？',Speaker=891011,Option='没错',}",
  [154030] = "{id=154030,Text='太好了。你能过来真是帮了大忙了。',Speaker=891011,Option='现在什么情况',}",
  [154031] = "{id=154031,Text='这里魔物疯狂生长的根源就在前面了。',Speaker=891011,Option='是什么',}",
  [154032] = "{id=154032,Text='妖邪门，就隐藏在漩涡形成的天然屏障后面。',Speaker=891011,}",
  [154033] = "{id=154033,Text='妖邪界企图以这里作为通道，打通两个世界。',Speaker=891011,}",
  [154034] = "{id=154034,Text='所幸我们发现得早，这个妖邪门还尚未成型，妖邪武士不能通过。',Speaker=891011,}",
  [154035] = "{id=154035,Text='但从它外泄的妖邪力来看，再不解决这个问题，很快就来不及了。',Speaker=891011,Option='我该怎么做',}",
  [154036] = "{id=154036,Text='我的水浒装甲无惧漩涡，妖邪门交给我来处理。',Speaker=891011,}",
  [154037] = "{id=154037,Text='但是这些不断向这么涌过来的魔物我就没有精力去管了，替我挡住他们！解决了妖邪门，海底的危机也就解决了。',Speaker=891011,Option='好的',}",
  [154038] = "{id=154038,Text='魔物似乎不再涌过来了。',}",
  [154039] = "{id=154039,Text='妖邪门已经被我解决了，但是这些漩涡怎么还在？',Speaker=891011,Option='能解决吗',Emoji=14,}",
  [154040] = "{id=154040,Text='让我看看。',Speaker=891011,}",
  [154041] = "{id=154041,Text='这片海洋已经把解决漩涡的答案告诉了我。',Speaker=891011,Option='怎么解决',}",
  [154042] = "{id=154042,Text='只要将[c][ffff00]其中三个[-][/c]漩涡按照一个[c][ffff00]稳定的形状[-][/c]连通，就可以安全消除全部漩涡。',Speaker=891011,}",
  [154043] = "{id=154043,Text='但是如果连通了错误的漩涡，可能会导致漩涡的能量爆发，导致更大的灾难。',Speaker=891011,}",
  [154044] = "{id=154044,Text='我会用水浒铠甲的力量来尽可能稳定住漩涡的能量。',Speaker=891011,}",
  [154045] = "{id=154045,Text='剩下的，就交给你了！',Speaker=891011,Option='没问题',}",
  [154046] = "{id=154046,Text='不好！刚刚漩涡的能量变得不稳定了！',Speaker=891011,}",
  [154047] = "{id=154047,Text='不好！刚刚漩涡的能量变得不稳定了！',Speaker=891011,}",
  [154048] = "{id=154048,Text='不好！刚刚漩涡的能量变得不稳定了！',Speaker=891011,}",
  [154049] = "{id=154049,Text='呼，[c][ffff00][PlayerName][-][/c]，多亏了有你在这次危机才能顺利解决。',Speaker=891011,}",
  [154050] = "{id=154050,Text='现在漩涡消失了，妖邪门也已经被关闭了，这些魔物应该很快就能恢复正常了。',Speaker=891011,Option='那就好',}",
  [154051] = "{id=154051,Text='我们先去和辽他们会合吧，要真正解决妖邪界的威胁，我们单个魔神坛斗士的力量是远远不够的，',Speaker=891011,Option='{传送到普隆德拉,2},{自己回去,3}',}",
  [154052] = "{id=154052,Text='辛苦了，[c][ffff00][PlayerName][-][/c]。',Speaker=891000,}",
  [154053] = "{id=154053,Text='刚刚在海底洞穴，可多亏了[c][ffff00][PlayerName][-][/c]的帮助我才能顺利解决妖邪门。',Speaker=891011,}",
  [154054] = "{id=154054,Text='妖邪界果然也跟我们一样，来到了这个世界。甚至已经开始行动了。',Speaker=891004,}",
  [154055] = "{id=154055,Text='事情越来越严重了，可是当麻他……',Speaker=891000,}",
  [154056] = "{id=154056,Text='想要对付妖邪界，必须齐聚我们5个人的力量。所以现在最重要的是抓紧找到当麻的下落。',Speaker=891004,}",
  [154057] = "{id=154057,Text='当麻可是我们5个人里面最聪明的，他一定不会有事的。',Speaker=891001,}",
  [154058] = "{id=154058,Text='希望如此吧。',Speaker=891000,}",
  [154059] = "{id=154059,Text='[c][ffff00][PlayerName][-][/c]，如果有了当麻的消息我会第一时间通知你的。',Speaker=891000,Option='好的',}",
  [154060] = "{id=154060,Text='[c][ffff00][PlayerName][-][/c]，你来得太是时候了。',Speaker=891000,Option='怎么了',}",
  [154061] = "{id=154061,Text='我们已经和当麻联系上了。但是我们刚刚发现在这个城市的南边出现了疑似妖邪门的痕迹。',Speaker=891000,}",
  [154062] = "{id=154062,Text='我们必须去解决这个妖邪门。当麻那边就拜托你了！',Speaker=891000,Option='没问题',}",
  [154063] = "{id=154063,Text='当麻一直在用天空铠甲的能力寻找我们。',Speaker=891000,}",
  [154064] = "{id=154064,Text='他现在所在的地方，据说是一座高大的[c][ffff00]山脉[-][/c]，还有一座[c][ffff00]浮在天空中的岛[-][/c]。',Speaker=891000,Option='妙勒尼山脉',}",
  [154065] = "{id=154065,Text='那就麻烦你了，[c][ffff00][PlayerName][-][/c]。',Speaker=891000,Option='{好的,2},{没时间,3}',}",
  [154066] = "{id=154066,Text='麻烦你去妙勒尼山脉找到当麻。',Speaker=891000,Option='{好的,2},{没时间,3}',}",
  [154067] = "{id=154067,Text='我们也可以放心去解决妖邪门的威胁了！',Speaker=891000,}",
  [154068] = "{id=154068,Text='天空神会在哪呢？',}",
  [154069] = "{id=154069,Text='刚刚好像有流星向着妙勒尼[c][ffff00]山顶[-][/c]划过去了呢？',Speaker=891014,}",
  [154070] = "{id=154070,Text='我也看到了，不过这个流星和平常的不太一样，好像是[c][ffff00]蓝色[-][/c]的……',Speaker=891013,}",
  [154071] = "{id=154071,Text='我们快去[c][ffff00]山顶[-][/c]看看吧！',Speaker=891014,}",
  [154072] = "{id=154072,Text='难道是天空神吗？',}",
  [154073] = "{id=154073,Text='跟上去看看……',}",
  [154074] = "{id=154074,Text='啊，山顶上什么都没有嘛！',Speaker=891014,}",
  [154075] = "{id=154075,Text='哈哈……也许是我们看错了？',Speaker=891013,}",
  [154076] = "{id=154076,Text='快看快看！那个蓝色流星向着天空岛那边飞过去了！',Speaker=891013,}",
  [154077] = "{id=154077,Text='啊，快许愿！',Speaker=891014,Option='……',}",
  [154078] = "{id=154078,Text='我的愿望是今年能谈恋爱！',Speaker=891014,Emoji=2,}",
  [154079] = "{id=154079,Text='我的愿望是……啊？是……世界和平！',Speaker=891013,Emoji=24,}",
  [154080] = "{id=154080,Text='……',}",
  [154081] = "{id=154081,Text='看样子天空神去了天空岛。',}",
  [154082] = "{id=154082,Text='乘坐妙勒尼观光航班可以前往[c][ffff00]天空岛[-][/c]哦。',Speaker=1380,Option='{前往天空岛,5},{稍后再去,6}',}",
  [154083] = "{id=154083,Text='你是来找我的？',Speaker=891012,Option='说明情况',}",
  [154084] = "{id=154084,Text='原来如此。多谢你的帮助。希望辽他们没有给你添麻烦，哈哈。',Speaker=891012,Option='没有',}",
  [154085] = "{id=154085,Text='我从来到这片大陆，就一直在寻找其他几名伙伴。',Speaker=891012,}",
  [154086] = "{id=154086,Text='不久前终于感应到了辽他们，也安下了心。正好看到这座浮空的岛屿，就想停下来看看。',Speaker=891012,}",
  [154087] = "{id=154087,Text='这里确实很美。',}",
  [154088] = "{id=154088,Text='我们的那个世界可没有这样的奇观。自从成为了魔神坛斗士之后就很少有像这样欣赏风景的闲暇了。',Speaker=891012,}",
  [154089] = "{id=154089,Text='不停的战斗一度让我们被力量蒙蔽了内心，成为被铠甲所驱使的工具。',Speaker=891012,}",
  [154090] = "{id=154090,Text='所幸我们最终守住了我们的心，战胜了妖邪帝王。',Speaker=891012,}",
  [154091] = "{id=154091,Text='啊，不小心说多了，哈哈。',Speaker=891012,}",
  [154092] = "{id=154092,Text='[c][ffff00][PlayerName][-][/c]，能麻烦你帮我拍张照吗？我想留个纪念。',Speaker=891012,Option='好的',}",
  [154093] = "{id=154093,Text='小心！',Speaker=891012,}",
  [154094] = "{id=154094,Text='是妖邪力的气息……',Speaker=891012,Emoji=11,}",
  [154095] = "{id=154095,Text='看样子我要赶快去和我的伙伴们会合了。想必他们现在急需我的帮助。',Speaker=891012,Option='好的',}",
  [154096] = "{id=154096,Text='还好及时赶上，还能看到流星。',Speaker=891014,Emoji=25,}",
  [154097] = "{id=154097,Text='呼……呼……慢点……',Speaker=891013,Emoji=15,}",
  [154098] = "{id=154098,Text='咦？流星里好像有个人？哇！好帅啊！！',Speaker=891014,Emoji=29,}",
  [154099] = "{id=154099,Text='以后我的心上人也一定要乘着流星来见我……',Speaker=891014,Emoji=38,}",
  [154100] = "{id=154100,Text='啊？呜呜呜……',Speaker=891013,Emoji=24,}",
  [154101] = "{id=154101,Text='[c][ffff00][PlayerName][-][/c]，多亏了有你的帮助，我们5个人才终于又全部聚集到了一起。',Speaker=891000,}",
  [154102] = "{id=154102,Text='没错，这几天你的努力可是深深折服了我们几个。',Speaker=891012,Emoji=20,}",
  [154103] = "{id=154103,Text='吼——',Speaker=891005,}",
  [154104] = "{id=154104,Text='哈哈哈，白炎也在说你很可靠呢。',Speaker=891000,Option='过奖了',}",
  [154105] = "{id=154105,Text='这几天，我们已经发现了两处妖邪门，阿罗格似乎企图通过时空裂隙让妖邪界降临这片大陆。',Speaker=891000,Option='妖邪帝王？',}",
  [154106] = "{id=154106,Text='没错……',Speaker=891000,}",
  [154107] = "{id=154107,Text='魔神坛斗士们，你们终于又齐聚了5个人的力量。',Speaker=891018,}",
  [154108] = "{id=154108,Text='迦雄须！！',}",
  [154109] = "{id=154109,Text='你们做得很不错，你们已经完完全全成长起来了。',Speaker=891018,}",
  [154110] = "{id=154110,Text='还有[c][ffff00][PlayerName][-][/c]，感谢你这些天的努力与付出，从你身上我看到了心的力量。',Speaker=891018,}",
  [154111] = "{id=154111,Text='但是真正的挑战才刚刚到来。就在不久前，在这里东南方不远的地方，出现了强烈的妖邪力。',Speaker=891018,}",
  [154112] = "{id=154112,Text='恐怕阿罗格已经降临到了这片大陆。',Speaker=891018,}",
  [154113] = "{id=154113,Text='我们一定会打败阿罗格，阻止他的阴谋，守护这片大陆的。',Speaker=891004,}",
  [154114] = "{id=154114,Text='没错，和妖邪界对抗，这也是我们魔神坛斗士的职责所在！',Speaker=891000,Emoji=20,}",
  [154115] = "{id=154115,Text='很好，看到你们的决心我也放心许多。',Speaker=891018,}",
  [154116] = "{id=154116,Text='我无法维持出现在这里太久，接下来，只能靠你们自己的了。',Speaker=891018,}",
  [154117] = "{id=154117,Text='去吧，魔神坛斗士们！',Speaker=891018,}",
  [154118] = "{id=154118,Text='武装！',}",
  [154119] = "{id=154119,Text='我先去迦雄须说的方向查看一下。',Speaker=891012,}",
  [154120] = "{id=154120,Text='当麻，一定要小心！',Speaker=891000,Option='小心',}",
  [154121] = "{id=154121,Text='迦雄须所说的地方就在离这里不远的一座岛，那里生活着很多粉色的球状魔物。',Speaker=891012,Option='波利岛',}",
  [154122] = "{id=154122,Text='可能阿罗格想利用妖邪力驱使这些魔物。',Speaker=891000,}",
  [154123] = "{id=154123,Text='就像海底那一次，魔物们都变得狂暴异常。',Speaker=891011,}",
  [154124] = "{id=154124,Text='我们绝对不能让阿罗格的阴谋得逞！',Speaker=891004,}",
  [154125] = "{id=154125,Text='当麻，除此之外还有什么发现吗？',Speaker=891000,}",
  [154126] = "{id=154126,Text='暂时只发现了妖邪门，并没有看到阿罗格的踪影。',Speaker=891012,}",
  [154127] = "{id=154127,Text='无论如何我们必须阻止阿罗格。',Speaker=891000,}",
  [154128] = "{id=154128,Text='[c][ffff00][PlayerName][-][/c]，你也和我们一起吧。我们需要你的帮助。',Speaker=891000,Option='{好的,4},{再等等,5}',}",
  [154129] = "{id=154129,Text='如果你改变主意了再来找我。',Speaker=891000,}",
  [154130] = "{id=154130,Text='[c][ffff00][PlayerName][-][/c]，你准备好和我们一起对抗妖邪帝王了吗？',Speaker=891000,Option='{好的,4},{再等等,5}',}",
  [154131] = "{id=154131,Text='好！魔神坛斗士，武装！',Speaker=891000,}",
  [154132] = "{id=154132,Text='阿罗格！',Speaker=891000,}",
  [154133] = "{id=154133,Text='又见面了……魔神坛斗士。',Speaker=891015,}",
  [154134] = "{id=154134,Text='还有一个，陌生的小家伙。',Speaker=891015,}",
  [154135] = "{id=154135,Text='如果你轻视我们当中的任何一个，你的失败也就注定了，阿罗格！',Speaker=891012,}",
  [154136] = "{id=154136,Text='哈哈哈，这一次，你们休想再阻止我。你们的铠甲，这次我也要得到！',Speaker=891015,}",
  [154137] = "{id=154137,Text='休想！',Speaker=891001,}",
  [154138] = "{id=154138,Text='岩铁碎！',Speaker=891001,}",
  [154139] = "{id=154139,Text='秀！',}",
  [154140] = "{id=154140,Text='啊，可恶……',Speaker=891001,}",
  [154141] = "{id=154141,Text='呵呵，还是没有一点长进。',Speaker=891015,}",
  [154142] = "{id=154142,Text='你们先和这些魔物玩玩吧，本王还有更重要的事。',Speaker=891015,}",
  [154143] = "{id=154143,Text='等我真身降临，即便是你们召唤出辉煌帝都无法阻止我了，哈哈哈！',Speaker=891015,}",
  [154144] = "{id=154144,Text='可恶！当麻，快去支援秀！',Speaker=891000,}",
  [154145] = "{id=154145,Text='好！',Speaker=891012,}",
  [154146] = "{id=154146,Text='征士！伸！我们去阻止阿罗格！',Speaker=891000,}",
  [154147] = "{id=154147,Text='好！',}",
  [154148] = "{id=154148,Text='[c][ffff00][PlayerName][-][/c]，我们不能任由阿罗格实施他的计划，这个魔物就先交给你了！',Speaker=891000,Option='没问题',}",
  [154149] = "{id=154149,Text='[c][ffff00][PlayerName][-][/c]，不要过来！',Speaker=891000,}",
  [154150] = "{id=154150,Text='你一个人……无法战胜……啊！',Speaker=891000,Emoji=15,}",
  [154151] = "{id=154151,Text='哈哈哈，已经太晚了，魔神坛斗士。',Speaker=891015,}",
  [154152] = "{id=154152,Text='用你们的力量来稳定[c][ff0000]时空裂隙[-][/c]，最后再把你们的铠甲收回来。',Speaker=891015,}",
  [154153] = "{id=154153,Text='妖邪界会铭记你们的贡献的。',Speaker=891015,Emoji=5,}",
  [154154] = "{id=154154,Text='可恶……',Speaker=891000,}",
  [154155] = "{id=154155,Text='融入我的身体……成为我的一部分吧！',Speaker=891015,Option='还有我呢',}",
  [154156] = "{id=154156,Text='啊，都忘了还有一个小家伙呢？',Speaker=891015,}",
  [154157] = "{id=154157,Text='魔神坛斗士都不是我的对手，你又凭什么能够打败我？',Speaker=891015,Emoji=5,}",
  [154158] = "{id=154158,Text='哼，你这个小家伙还挺难缠。',Speaker=891015,}",
  [154159] = "{id=154159,Text='魔神坛斗士们，献出你们的力量，和我融为一体吧！',Speaker=891015,}",
  [154160] = "{id=154160,Text='啊——',}",
  [154161] = "{id=154161,Text='阿罗格，我们魔神坛斗士不会让你得逞的！',Speaker=891000,}",
  [154162] = "{id=154162,Text='为了大家，我必须继续战斗……',Speaker=891000,}",
  [154163] = "{id=154163,Text='我是……我是魔神坛斗士啊！',Speaker=891000,}",
  [154164] = "{id=154164,Text='辽！接受我们的力量吧！',}",
  [154165] = "{id=154165,Text='哈哈哈，一直都在等着辉煌帝铠甲的出现。',Speaker=891015,}",
  [154166] = "{id=154166,Text='辉煌帝铠甲才是唯一能够支配世界的铠甲。',Speaker=891015,}",
  [154167] = "{id=154167,Text='连同这把只有强大王者才配拥有的刚烈剑……',Speaker=891015,}",
  [154168] = "{id=154168,Text='我，阿罗格，作为这个世界新的帝王，收下他们了！',Speaker=891015,Emoji=5,}",
  [154169] = "{id=154169,Text='这件铠甲，是交给五个正义的灵魂的。',Speaker=891016,}",
  [154170] = "{id=154170,Text='绝不可能交给你！',Speaker=891016,}",
  [154171] = "{id=154171,Text='超弹动！闪煌斩——',Speaker=891016,}",
  [154172] = "{id=154172,Text='失败的是烈火，证明了我才配拥有辉煌帝铠甲。',Speaker=891015,}",
  [154173] = "{id=154173,Text='到此为止了，魔神坛斗士！',Speaker=891015,}",
  [154174] = "{id=154174,Text='住手！阿罗格！',Speaker=891001,}",
  [154175] = "{id=154175,Text='就凭你们也想反抗我吗？',Speaker=891015,}",
  [154176] = "{id=154176,Text='啊——',}",
  [154177] = "{id=154177,Text='辉煌帝铠甲啊，到我这里来吧！',Speaker=891015,Emoji=5,}",
  [154178] = "{id=154178,Text='什么？！',Speaker=891015,}",
  [154179] = "{id=154179,Text='阿罗格，你就那么想要辉煌帝铠甲吗？',Speaker=891016,}",
  [154180] = "{id=154180,Text='居然能反抗我的妖邪力？你想做什么？',Speaker=891015,Emoji=1,}",
  [154181] = "{id=154181,Text='既然这么想要，这铠甲给你也罢！',Speaker=891016,}",
  [154182] = "{id=154182,Text='但是要记住，这铠甲只有正义的灵魂才能拥有！',Speaker=891016,Emoji=1,}",
  [154183] = "{id=154183,Text='怎么回事……这不可思议的心的力量……',Speaker=891015,}",
  [154184] = "{id=154184,Text='伙伴们，阿罗格正被仁之心压制着……',Speaker=891016,}",
  [154185] = "{id=154185,Text='快趁现在攻击阿罗格！',Speaker=891016,}",
  [154186] = "{id=154186,Text='这样也会攻击到你的！',Speaker=891001,Emoji=37,}",
  [154187] = "{id=154187,Text='没关系，不能放过阿罗格！不然我们的努力就白费了！',Speaker=891016,}",
  [154188] = "{id=154188,Text='辽！',}",
  [154189] = "{id=154189,Text='快向我攻击！',Speaker=891016,Emoji=7,}",
  [154190] = "{id=154190,Text='超弹动——',Speaker=891004,}",
  [154191] = "{id=154191,Text='岩铁碎！',Speaker=891001,}",
  [154192] = "{id=154192,Text='超流破！',Speaker=891011,}",
  [154193] = "{id=154193,Text='雷光斩！',Speaker=891004,}",
  [154194] = "{id=154194,Text='真空波！',Speaker=891012,}",
  [154195] = "{id=154195,Text='不——！！',Speaker=891015,}",
  [154196] = "{id=154196,Text='辽！',}",
  [154197] = "{id=154197,Text='迦雄须，辽他……',Speaker=891004,}",
  [154198] = "{id=154198,Text='不用担心，生命之玉可以救回真田辽。',Speaker=891018,}",
  [154199] = "{id=154199,Text='我的使命也结束了，是时候离去了……',Speaker=891018,}",
  [154200] = "{id=154200,Text='迦雄须，谢谢你……',Speaker=891000,Emoji=26,}",
  [154201] = "{id=154201,Text='能战胜阿罗格多亏了有大家啊。尤其是你，[c][ffff00][PlayerName][-][/c]。',Speaker=891000,}",
  [154202] = "{id=154202,Text='你一个人挑战阿罗格的英姿，让我们觉醒了心的力量。我们才能够召唤出辉煌帝铠甲，战胜阿罗格的。',Speaker=891000,}",
  [154203] = "{id=154203,Text='真的太感谢你了。',Speaker=891000,}",
  [154204] = "{id=154204,Text='应该的。',}",
  [154205] = "{id=154205,Text='解决了妖邪界的威胁，我们也可以好好休息一下了。',Speaker=891000,}",
  [154206] = "{id=154206,Text='这片大陆有着我们那个世界看不到的风景呢。',Speaker=891012,}",
  [154207] = "{id=154207,Text='哈哈，那我可要好好看一下。',Speaker=891001,Emoji=20,}",
  [154208] = "{id=154208,Text='那我们就先离开这里吧。   ',Speaker=891000,}",
  [154209] = "{id=154209,Text='[c][ffff00][PlayerName][-][/c]，你来了啊。',Speaker=891000,Option='有什么事',}",
  [154210] = "{id=154210,Text='啊，这几天你帮了我们那么大的忙，还没有好好的感谢你呢。',Speaker=891000,}",
  [154211] = "{id=154211,Text='从你身上，我们感受到了相信人心、恪守正义这些难能可贵的品质。',Speaker=891000,}",
  [154212] = "{id=154212,Text='请一定要将这份心的力量，坚守并传承下去啊！',Speaker=891000,Option='一定',}",
  [154213] = "{id=154213,Text='好，时间不早了，我要和伙伴们好好休息一下了。',Speaker=891000,}",
  [154214] = "{id=154214,Text='咱们有缘再见吧！',Speaker=891000,}",
  [154215] = "{id=154215,Text='再见，魔神坛斗士。',}",
  [154216] = "{id=154216,Text='千年之前，我打败了入侵的阿罗格，并用心的力量将他的铠甲封印了起来。',Speaker=891018,}",
  [154217] = "{id=154217,Text='分别是：仁、义、礼、智、信、忠、孝、悌、忍。',Speaker=891018,}",
  [154218] = "{id=154218,Text='铠甲的力量各有不同，根据穿着者之心的不同，也会复苏成或善或恶的形式。',Speaker=891018,}",
  [154219] = "{id=154219,Text='阿罗格的复活正是因为忠、孝、悌、忍复苏了邪恶之心。',Speaker=891018,}",
  [154220] = "{id=154220,Text='阿罗格又将这四件铠甲变为了四魔将的铠甲。',Speaker=891018,}",
  [154221] = "{id=154221,Text='现在你获得了四魔将的铠甲，在你身上我看到了一颗坚定的正义之心。',Speaker=891018,}",
  [154222] = "{id=154222,Text='我把这4件铠甲合成为【[c][ffff00]妖邪帝王之盔[-][/c]】，就交由你来保管了。',Speaker=891018,Option='{好的,2},{我再想想,3}',}",
  [154223] = "{id=154223,Text='看来我给大家添了不少麻烦......今后我不会这么做了......',Speaker=890986,Option='你做得不错',Emoji=38,}",
  [154224] = "{id=154224,Text='昨天妮可说花店的大叔有麻烦，我们去看看吧！',Speaker=890986,}",
  [154225] = "{id=154225,Text='可恶的小鬼！把我的花店弄得乱七八糟，还抢走了我的花！',Speaker=890989,Emoji=12,}",
  [154226] = "{id=154226,Text='......',Speaker=890985,Option='什么样的花',}",
  [154227] = "{id=154227,Text='是那种红色的小花，很可爱的。',Speaker=890989,}",
  [154228] = "{id=154228,Text='别担心，我们再帮你采回来一些吧。',Speaker=890985,}",
  [154229] = "{id=154229,Text='真的吗？这孩子真懂事呢～那些花在普隆德拉南门有的。需要5朵。',Speaker=890989,}",
  [154230] = "{id=154230,Text='那么我等你们回来哦！',Speaker=890989,}",
  [154232] = "{id=154232,Text='我们带花回来啦！',Speaker=890985,}",
  [154233] = "{id=154233,Text='你们回来啦！哦！是我想要的红色小花！',Speaker=890989,Emoji=27,}",
  [154234] = "{id=154234,Text='这孩子真乖～给你一些点心吧！',Speaker=890989,Emoji=17,}",
  [154235] = "{id=154235,Text='谢、谢谢......',Speaker=890985,Emoji=38,}",
  [154236] = "{id=154236,Text='今后也要做个好孩子哦！那么我进店去打理花儿了。再见～',Speaker=890989,}",
  [154237] = "{id=154237,Text='你看到那个吟游诗人大叔了吗？',Speaker=890986,Option='没有',}",
  [154238] = "{id=154238,Text='不会吧......莫非他骗我们？',Speaker=890986,Option='我觉得不会',}",
  [154239] = "{id=154239,Text='好吧......那我再等等。',Speaker=890986,}",
  [154240] = "{id=154240,Text='怎么回事？！',Speaker=890986,}",
  [154241] = "{id=154241,Text='你快看......地上有一枚勋章诶！好漂亮！你也有哦！',Speaker=890986,}",
  [154242] = "{id=154242,Text='难道这就是那个吟游诗人给我们的惊喜？',Speaker=890986,}",
  [154243] = "{id=154243,Text='你们来了啊。啊，看来勋章你们收到了呢。',Speaker=890987,}",
  [154244] = "{id=154244,Text='实话告诉你们吧，天上的星星，是没有办法摘走的。',Speaker=890987,}",
  [154245] = "{id=154245,Text='夜空中的星星虽然无法摘下。',Speaker=890987,}",
  [154246] = "{id=154246,Text='但这枚星星勋章，是这片大陆给明白了责任、担当与爱的孩子的奖励。',Speaker=890987,}",
  [154247] = "{id=154247,Text='而我就是这一切的见证人，你俩都通过了考验。',Speaker=890987,}",
  [154248] = "{id=154248,Text='你们的心灵也获得了成长，所以获得了这枚星星勋章。',Speaker=890987,}",
  [154249] = "{id=154249,Text='这才是真正的、最美丽的星星。',Speaker=890987,}",
  [154250] = "{id=154250,Text='我的任务完成了，我得走了。希望你们今后也好好加油啊！',Speaker=890987,}",
  [154251] = "{id=154251,Text='......',Speaker=890986,}",
  [154252] = "{id=154252,Text='谢谢你，陪了我这么多天，答应我那么多任性的要求。我再也不恶作剧了。',Speaker=890986,}",
  [154253] = "{id=154253,Text='我要跟大家道歉，希望大家能原谅我......',Speaker=890986,Emoji=38,}",
  [154254] = "{id=154254,Text='什么事呀？',Speaker=890989,Option='讲述经过',}",
  [154255] = "{id=154255,Text='......！！哎，算了算了，都是过去的事了。以后可不许这么坏了呀！',Speaker=890989,}",
  [154256] = "{id=154256,Text='嗯嗯！我知道啦！',Speaker=890986,Emoji=21,}",
  [154257] = "{id=154257,Text='那我们去给其他人道歉了。',Speaker=890986,}",
  [154258] = "{id=154258,Text='好。那我先去忙了哦。',Speaker=890989,}",
  [154259] = "{id=154259,Text='哇哦，我的好朋友来啦！对了，上次还没问你的名字呢！',Speaker=890988,}",
  [154260] = "{id=154260,Text='我叫......Q。对不起......是我害得咪咪逃跑了。',Speaker=890986,}",
  [154261] = "{id=154261,Text='......不管怎么样，你帮我找回了咪咪啦。妈妈说过，朋友间吵架了要好好道歉。',Speaker=890988,}",
  [154262] = "{id=154262,Text='这样才能继续做好朋友。对不起，我昨天骂了你。',Speaker=890988,Emoji=16,}",
  [154263] = "{id=154263,Text='喵～喵～',Speaker=890983,}",
  [154264] = "{id=154264,Text='你看，咪咪都在说没关系哦。',Speaker=890988,}",
  [154265] = "{id=154265,Text='你已经是个很好的孩子啦！今后我们也要做好朋友哦～',Speaker=890988,}",
  [154266] = "{id=154266,Text='啊，妈妈在叫我吃饭呢。我先回家啦！拜拜～',Speaker=890988,}",
  [154267] = "{id=154267,Text='我想了很多很多，好不容易获得了这枚勋章。',Speaker=890986,}",
  [154268] = "{id=154268,Text='我要好好珍惜它。',Speaker=890986,}",
  [154269] = "{id=154269,Text='今后我会尽力帮助有困难的人。这样就会认识很多可以和我一起玩的朋友了吧？',Speaker=890986,Option='没错！',Emoji=20,}",
  [154270] = "{id=154270,Text='那么我去休息了。明天说不定能认识更多新朋友呢！',Speaker=890986,}",
  [154271] = "{id=154271,Text='拍到了适合剑道修行的地方，回去交给真田辽。',Option='{传送到西门,2},{自己回去,3}',}",
  [154272] = "{id=154272,Text='拍到了沙之眼，回去交给秀。',Option='{传送到西门,2},{自己回去,3}',}",
  [154273] = "{id=154273,Text='拍到了海底适合修炼的地方，回去交给伸。',Option='{传送到西门,2},{自己回去,3}',}",
  [154274] = "{id=154274,Text='这里是钟楼地下最适合的地方，拍下回去交给征士。',Option='{传送到西门,2},{自己回去,3}',}",
  [154275] = "{id=154275,Text='这里看起来很适合进行修行，照片拍好了，回去交给当麻。',Option='{传送到西门,2},{自己回去,3}',}",
  [154283] = "{id=154283,Text='拜托你了，帮帮我们吧！',Speaker=890970,Option='{乐意效劳,99},{我有点忙,98}',}",
  [154285] = "{id=154285,Text='[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Speaker=890962,Option='{现在接取,3},{稍后接取,7}',}",
  [154286] = "{id=154286,Text='你和小伙伴们玩得怎么样了呀~',Speaker=890962,}",
  [154287] = "{id=154287,Text='冒险者我们日后再见啦~',Speaker=890986,}",
  [154288] = "{id=154288,Text='我去找克里斯要星星啦，咱们一会儿见。',Speaker=890985,}",
  [154289] = "{id=154289,Text='谢谢你让我明白了什么是责任，什么是爱。',Speaker=890991,}",
  [154290] = "{id=154290,Text='明白了这些道理之后，我竟然一夜之间就长大了。',Speaker=890991,}",
  [154291] = "{id=154291,Text='距离我回去的日子已经不多了，我在想带些什么东西回去给那些可爱的朋友们呢。',Speaker=890991,}",
  [154292] = "{id=154292,Text='听说米德加尔特大陆上有很多闪闪发亮的宝石，如果你可以给我带来一些的话，我也会拿我的珍藏跟你交换哦~',Speaker=890991,}",
  [154293] = "{id=154293,Text='嘿，这位冒险者，你是不是在找什么有趣的东西啊？',Speaker=892000,Option='你…你怎么知道？',}",
  [154294] = "{id=154294,Text='我可是米德加尔特大陆上，无所不知的的情报贩子，想知道这些轻而易举！',Speaker=892000,}",
  [154295] = "{id=154295,Text='好啦~我这里有关「高额悬赏」的关键情报！可以给你唷~',Speaker=892000,Option='真的嘛？',}",
  [154296] = "{id=154296,Text='但是，这是有偿的......我最喜欢的事物就是那些耀眼的宝石了！如果你可以给我带来一些的话，我会拿「关键情报」给你交换的~',Speaker=892000,}",
  [154297] = "{id=154297,Text='你还记得之前的约定的嘛~有缘一定会再见的！',Speaker=892001,Option='记得',}",
  [154298] = "{id=154298,Text='果然，普隆德拉比想象中的要更热闹呢~',Speaker=892001,}",
  [154299] = "{id=154299,Text='哈哈哈哈哈，我会在这里好好玩上几天',Speaker=892001,}",
  [154300] = "{id=154300,Text='每天都记得来找我哟~',Speaker=892001,Option='好！',}",
  [154301] = "{id=154301,Text='我特意为你准备了特别的惊喜！相信你也一定会喜欢的。',Speaker=892001,}",
  [154302] = "{id=154302,Text='对了，这份惊喜是每日都有的哦，记得每天来领取~',Speaker=892001,Option='{领取,4},{再等等,2}',}",
  [154303] = "{id=154303,Text='嘿！冒险者，你来了~',Speaker=892001,}",
  [154304] = "{id=154304,Text='今天又会是怎样的惊喜呢？有一点点小期待呢',Speaker=892001,}",
  [154305] = "{id=154305,Text='这是今日的惊喜，我们明天再见！',Speaker=892001,Option='{领取,4},{再等等,2}',}",
  [154306] = "{id=154306,Text='这是今日的惊喜，请你领取哦！',Speaker=892001,Option='{领取,4},{再等等,2}',}",
  [154323] = "{id=154323,Text='谢谢你！！冒险者！',Speaker=891020,}",
  [154334] = "{id=154334,Text='冒险者，你听说过烈火剑吗？这原本是一件异世界的神器，在一次偶然的机会下，我得到了它的配方。',Speaker=892006,}",
  [154335] = "{id=154335,Text='传说只有被命运选中的勇士才能驾驭它，如果你想试试，可以来找我。',Speaker=892006,}",
  [154371] = "{id=154371,Text='（小声）这天气也太热了……该怎么办才好……',Speaker=892008,Option='你怎么了',Emoji=18,}",
  [154372] = "{id=154372,Text='啊！你好……我是从斐扬来的。最近太炎热，斐扬的作物都快无法生长了！',Speaker=892008,}",
  [154373] = "{id=154373,Text='有个大法师说，只要收集到一定的宝石，他就可以施展出降雨术，拯救米德加尔特！',Speaker=892008,}",
  [154374] = "{id=154374,Text='拜托你，可以帮帮我们吗？',Speaker=892008,Option='{捐献宝石,3},{现在不方便,2}',Emoji=42,}",
  [154375] = "{id=154375,Text='这样啊……唉。',Speaker=892008,}",
  [154376] = "{id=154376,Text='是闪亮亮的宝石~我来履行承诺啦！',Speaker=892009,}",
  [154377] = "{id=154377,Text='阿布拉嘎利卡西碰！',Speaker=892009,}",
  [154378] = "{id=154378,Text='下次再有宝石，我还会来哦~',Speaker=892009,Emoji=2,}",
  [154379] = "{id=154379,Text='谢谢你，冒险者！真的下雨啦！',Speaker=892008,}",
  [154380] = "{id=154380,Text='这是一点心意，请你收下~',Speaker=892008,Emoji=2,}",
  [154381] = "{id=154381,Text='哎，大法师说她太累了，不能来降雨了……',Speaker=892008,Emoji=18,}",
  [154382] = "{id=154382,Text='无论如何，还是谢谢你的帮助！',Speaker=892008,}",
  [154394] = "{id=154394,Text='冒险者等等~我有事找你！',Speaker=892010,Option='什么事呀',}",
  [154395] = "{id=154395,Text='请你帮帮我的朋友米夏好不好~他要准备普隆德拉的运动会。',Speaker=892010,}",
  [154396] = "{id=154396,Text='我也很想帮他，可是我自己的工作也特别多，抽不出空……',Speaker=892010,Emoji=24,}",
  [154397] = "{id=154397,Text='[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Speaker=892010,Option='{好的,2},{再等等,3}',}",
  [154398] = "{id=154398,Text='拜托你了！他就在广场上。',Speaker=892010,}",
  [154399] = "{id=154399,Text='啊，是冒险者！',Speaker=892011,}",
  [154400] = "{id=154400,Text='请你帮帮我吧~我实在忙不过啦……',Speaker=892011,Emoji=42,}",
  [154401] = "{id=154401,Text='普隆德拉马上就要举办运动会了，我负责前期工作的筹备。',Speaker=892011,}",
  [154402] = "{id=154402,Text='但是我一个人根本做不完这么多工作呀……',Speaker=892011,}",
  [154403] = "{id=154403,Text='好想办一场让大家都开心的运动会啊。',Speaker=892011,}",
  [154404] = "{id=154404,Text='冒险者，你愿意帮忙吗？',Speaker=892011,Option='{乐意效劳,2}{我还有事,3}',}",
  [154405] = "{id=154405,Text='真的吗！哇冒险者你真是太好了！大家都会感谢你的！',Speaker=892011,}",
  [154406] = "{id=154406,Text='哎……好吧。不知道我一个人什么时候才能弄完。',Speaker=892011,}",
  [154407] = "{id=154407,Text='那么我们赶紧开工吧！首先是选定运动会场地……',Speaker=892011,}",
  [154408] = "{id=154408,Text='希望能找到比较平整开阔的地方呀。',Speaker=892011,}",
  [154409] = "{id=154409,Text='嗯……这里好像不是很平坦，还有不少魔物打扰。',}",
  [154410] = "{id=154410,Text='还是再看看其他地方吧。下一个地点是……北门！',Option='{立刻传送,99},{自己过去,5}',}",
  [154411] = "{id=154411,Text='这里好像还不错的样子。就选这里吧！',}",
  [154412] = "{id=154412,Text='嗯？怎么有一股奇怪的气息……',}",
  [154413] = "{id=154413,Text='你这家伙是什么人！敢跑到我们的地盘上来！',Speaker=892012,Option='运动会……',Emoji=12,}",
  [154414] = "{id=154414,Text='一群胖子还想靠这点运动减肥？哈哈哈笑死了。',Speaker=892012,}",
  [154415] = "{id=154415,Text='你们永远没法像我们这么苗条的。',Speaker=892012,Option='……',}",
  [154416] = "{id=154416,Text='怎么？被我们说中了？不服就来打败我们呀！',Speaker=892012,Emoji=5,}",
  [154417] = "{id=154417,Text='啊啊我们不敢了……',Speaker=892012,Emoji=37,}",
  [154418] = "{id=154418,Text='我们收回刚才的话！我们什么都听你的！',Speaker=892012,}",
  [154419] = "{id=154419,Text='都听你的！',Speaker=892012,}",
  [154420] = "{id=154420,Text='正好运动会缺人手，你们就来当志愿者，出点力气。',}",
  [154421] = "{id=154421,Text='好，好！我们一定尽力当好志愿者！',Speaker=892012,}",
  [154422] = "{id=154422,Text='请你饶了我们吧……',Speaker=892012,Option='好吧好吧',Emoji=18,}",
  [154423] = "{id=154423,Text='那我们回去好好了解一下志愿者的工作……',Speaker=892012,}",
  [154424] = "{id=154424,Text='啊，冒险者你回来啦！辛苦你了~',Speaker=892011,Emoji=26,}",
  [154425] = "{id=154425,Text='怎么样？场地找好了吗？',Speaker=892011,Option='讲述经过',}",
  [154426] = "{id=154426,Text='哦！你还找了志愿者啊！那真是帮大忙啦，谢谢你！',Speaker=892011,}",
  [154427] = "{id=154427,Text='明天我们也会非常忙碌哦，今天就先到这里，好好休息一下吧~',Speaker=892011,}",
  [154428] = "{id=154428,Text='明天见~',Speaker=892011,}",
  [154429] = "{id=154429,Text='嘿！冒险者，今天也拜托你帮忙啦。',Speaker=892011,}",
  [154430] = "{id=154430,Text='今天我们要找几位赞助商解决食物和各种比赛用品的问题。',Speaker=892011,}",
  [154431] = "{id=154431,Text='就麻烦你在城内转转，看看有没有热心人士~',Speaker=892011,Emoji=21,}",
  [154432] = "{id=154432,Text='欢迎光临！请问你想吃些什么呢？',Speaker=892013,Option='说明情况',Emoji=9,}",
  [154433] = "{id=154433,Text='原来你想让我赞助这次运动会……',Speaker=892013,}",
  [154434] = "{id=154434,Text='（虽然这样是一笔不小的花费。）',Speaker=892013,}",
  [154435] = "{id=154435,Text='（不过也算是一次向全城人推广我的料理的大好机会诶。）',Speaker=892013,}",
  [154436] = "{id=154436,Text='（赞助就当宣传费好了……）',Speaker=892013,}",
  [154437] = "{id=154437,Text='冒险者，我可以赞助运动会。',Speaker=892013,}",
  [154438] = "{id=154438,Text='不过我有个条件。',Speaker=892013,Option='什么条件',}",
  [154439] = "{id=154439,Text='我最近在研究新的菜品，如果你能帮我做出来的话，我就答应你。',Speaker=892013,}",
  [154440] = "{id=154440,Text='首先我需要一些食材。请你去南门找[c][ffff00]2把新鲜蔬菜[-][/c]吧。',Speaker=892013,}",
  [154441] = "{id=154441,Text='没错！就是这个！接下来请你用它制作美味的新料理吧~',Speaker=892013,}",
  [154442] = "{id=154442,Text='先怎么做呢……',Option='{烤面包,2},{处理食材,3}',}",
  [154443] = "{id=154443,Text='好像把蔬菜处理好再烤面包也不迟……',Speaker=892013,}",
  [154444] = "{id=154444,Text='没错没错！期待你的成果~',Speaker=892013,}",
  [154445] = "{id=154445,Text='好了！',}",
  [154446] = "{id=154446,Text='哦？已经做好了吗？让我来尝尝。',Speaker=892013,Option='请用',}",
  [154447] = "{id=154447,Text='嗯~不错不错！很好吃！',Speaker=892013,Emoji=17,}",
  [154448] = "{id=154448,Text='我答应你做运动会的赞助商！你做的这道料理一定能大卖！',Speaker=892013,}",
  [154449] = "{id=154449,Text='那么我这就去做准备！我们之后再联络哦。',Speaker=892013,}",
  [154450] = "{id=154450,Text='哦，这不是冒险者嘛！今天想找些什么呢？',Speaker=892014,}",
  [154451] = "{id=154451,Text='我这里新来了不少好东西！',Speaker=892014,Option='说明来意',}",
  [154452] = "{id=154452,Text='原来如此！运动会的事我也听说了。',Speaker=892014,}",
  [154453] = "{id=154453,Text='如果有什么我能帮得上忙的尽管说！',Speaker=892014,}",
  [154454] = "{id=154454,Text='只是……我想请你帮个忙……',Speaker=892014,Option='你说',Emoji=38,}",
  [154455] = "{id=154455,Text='可不可以请你陪我儿子罗杰斯玩一会儿，帮他建立自信……',Speaker=892014,}",
  [154456] = "{id=154456,Text='他不太愿意和人交流，有点胆小。',Speaker=892014,}",
  [154457] = "{id=154457,Text='拜托你了！',Speaker=892014,Emoji=16,}",
  [154458] = "{id=154458,Text='他现在应该在附近……',Speaker=892014,}",
  [154459] = "{id=154459,Text='……',Speaker=892015,}",
  [154460] = "{id=154460,Text='你、你是谁……',Speaker=892015,Option='我是小熊使者',Emoji=8,}",
  [154461] = "{id=154461,Text='那……你、你会变……小熊吗……',Speaker=892015,Option='没问题',Emoji=19,}",
  [154462] = "{id=154462,Text='哇、哇——是小熊！',Speaker=892015,Emoji=17,}",
  [154463] = "{id=154463,Text='你好厉害！',Speaker=892015,}",
  [154464] = "{id=154464,Text='你愿意和我玩吗？',}",
  [154465] = "{id=154465,Text='嗯嗯！',Speaker=892015,}",
  [154466] = "{id=154466,Text='猜拳怎么样？',}",
  [154467] = "{id=154467,Text='嗯嗯！',Speaker=892015,}",
  [154468] = "{id=154468,Text='三！',Speaker=892015,}",
  [154469] = "{id=154469,Text='二！',Speaker=892015,}",
  [154470] = "{id=154470,Text='一！',Speaker=892015,}",
  [154471] = "{id=154471,Text='剪刀石头布——',Speaker=892015,Option='{石头,10},{剪刀,20},{布,30}',}",
  [154472] = "{id=154472,Text='……好、好玩……',Speaker=892015,Emoji=26,}",
  [154473] = "{id=154473,Text='下、下次还想和你一起玩……',Speaker=892015,}",
  [154474] = "{id=154474,Text='好，那么你想不想来参加运动会？好玩哦~',}",
  [154475] = "{id=154475,Text='唔……可是、可是我体育很差……',Speaker=892015,Emoji=24,}",
  [154476] = "{id=154476,Text='不过……志愿者的话，我会努力去做的。',Speaker=892015,}",
  [154477] = "{id=154477,Text='嗯！决定了！谢谢你小熊使者！我这就回去问爸爸怎么当志愿者~',Speaker=892015,}",
  [154478] = "{id=154478,Text='我们运动会上见！',Speaker=892015,Emoji=26,}",
  [154479] = "{id=154479,Text='哦哦哦！谢谢你冒险者！罗杰斯刚才回来了。',Speaker=892014,}",
  [154480] = "{id=154480,Text='他看上去好像很高兴呢！',Speaker=892014,Emoji=6,}",
  [154481] = "{id=154481,Text='他好像比以前有些自信了呢。',Speaker=892014,}",
  [154482] = "{id=154482,Text='那么我说到做到，普隆德拉运动会的赞助商就让我来负责吧！',Speaker=892014,Emoji=20,}",
  [154483] = "{id=154483,Text='那么我这就去准备！辛苦你啦！',Speaker=892014,}",
  [154484] = "{id=154484,Text='冒险者你回来啦！辛苦了！怎么样？赞助商找到了吗？',Speaker=892011,Option='都找好了',}",
  [154485] = "{id=154485,Text='哇！你真的太厉害了！',Speaker=892011,Emoji=39,}",
  [154486] = "{id=154486,Text='如果我一个人肯定没法这么快搞定……',Speaker=892011,}",
  [154487] = "{id=154487,Text='你一定很累了吧，那么赶紧好好休息吧。',Speaker=892011,}",
  [154488] = "{id=154488,Text='明天我们一起接着努力！一定要把这次运动会办好~',Speaker=892011,Emoji=20,}",
  [154489] = "{id=154489,Text='那么我先不打扰你啦~明天见！',Speaker=892011,}",
  [154490] = "{id=154490,Text='冒险者，今天也一起加油吧！',Speaker=892011,}",
  [154491] = "{id=154491,Text='今天呢~我们需要找到乐团乐手的人选~有了音乐助兴，会场的气氛一定会很热烈！',Speaker=892011,}",
  [154492] = "{id=154492,Text='我最近在南门总会听到悦耳的音乐，也许那里有不错的演奏者！麻烦你去看看吧！',Speaker=892011,}",
  [154493] = "{id=154493,Text='滴滴叭叭~滴滴~',Speaker=892022,}",
  [154494] = "{id=154494,Text='附近传来了音乐声……去找找吧。',Emoji=7,}",
  [154495] = "{id=154495,Text='滴滴——啵！你是谁呀啵！',Speaker=892018,Option='说明来意',Emoji=1,}",
  [154496] = "{id=154496,Text='唔嗯！刚才音乐的演奏者就是本波利啵！',Speaker=892018,}",
  [154497] = "{id=154497,Text='看你这么喜欢我的音乐，那我就答应你……去运动会演奏了啵！',Speaker=892018,Option='别的演奏者',}",
  [154498] = "{id=154498,Text='唔嗯……想要别的演奏者啵……',Speaker=892018,}",
  [154499] = "{id=154499,Text='附近有一群溜溜猴的鼓打的很好啵，也许你可以去找找他们啵~',Speaker=892018,Emoji=2,}",
  [154500] = "{id=154500,Text='那么，运动会见啵！',Speaker=892018,}",
  [154501] = "{id=154501,Text='那边有一群溜溜猴，波利号手说的会不会是它们？',}",
  [154502] = "{id=154502,Text='什么？什么事噫？',Speaker=892016,Option='说明来意',}",
  [154503] = "{id=154503,Text='！！！！',Speaker=892016,}",
  [154504] = "{id=154504,Text='你、你要找的，会打鼓的溜溜猴，就是我们啦——！！！',Speaker=892016,}",
  [154505] = "{id=154505,Text='听说运动会需要乐团乐手，我们一直在加紧练习噫！',Speaker=892016,Option='太好了',}",
  [154506] = "{id=154506,Text='溜溜猴！你们这群小偷！！原来在这里！',Speaker=892039,}",
  [154507] = "{id=154507,Text='不、不好了噫……',Speaker=892016,}",
  [154508] = "{id=154508,Text='冒险者，就是这群溜溜猴偷了我的鼓！',Speaker=892039,}",
  [154509] = "{id=154509,Text='呜呜噫，我们只是想演奏而已……',Speaker=892016,Option='那也不可以偷',}",
  [154510] = "{id=154510,Text='对不起……我们不该拿理查德的乐器噫。',Speaker=892016,}",
  [154511] = "{id=154511,Text='但是，我们去找过别的人类，他们都不愿意卖，还说魔物不可能演奏出好的音乐……',Speaker=892016,}",
  [154512] = "{id=154512,Text='我们不是真的要偷，只是借来运动会用一下，然后就还回去的噫……',Speaker=892016,}",
  [154513] = "{id=154513,Text='……算啦，那这次就借给你们一回。',Speaker=892039,}",
  [154514] = "{id=154514,Text='运动会结束以后要还给我哦！',Speaker=892039,}",
  [154515] = "{id=154515,Text='哇呀噫……！',Speaker=892016,}",
  [154516] = "{id=154516,Text='今天也辛苦你了冒险者！怎么样？乐团找到人了吗？',Speaker=892011,Option='找到了',}",
  [154517] = "{id=154517,Text='哇！冒险者你真是太可靠了！多亏有你，运动会的筹备工作才能这么顺利！',Speaker=892011,Emoji=25,}",
  [154518] = "{id=154518,Text='明天我们也一起努力吧~胜利就在眼前！',Speaker=892011,}",
  [154519] = "{id=154519,Text='你好像有点累了，快去休息吧~我去确认一下各个细节。明天见哦！',Speaker=892011,}",
  [154520] = "{id=154520,Text='冒险者，我们很快就能完成准备工作啦！加油哦！',Speaker=892011,}",
  [154521] = "{id=154521,Text='今天的计划是准备医药用品和寻找裁判人选~',Speaker=892011,}",
  [154522] = "{id=154522,Text='拜托你到南门去采[c][ffff00]3株草药[-][/c]来制作药品好不好呀。',Speaker=892011,}",
  [154523] = "{id=154523,Text='期待你的好消息！',Speaker=892011,}",
  [154524] = "{id=154524,Text='你回来啦！草药采到了吗？',Speaker=892011,Option='嗯嗯',}",
  [154525] = "{id=154525,Text='对对就是它！现在拜托你把草药做成药品吧~',Speaker=892011,Option='好',Emoji=26,}",
  [154526] = "{id=154526,Text='做好啦！',}",
  [154527] = "{id=154527,Text='噢噢噢噢！我真是太佩服你了冒险者！',Speaker=892011,Emoji=20,}",
  [154528] = "{id=154528,Text='接下来就麻烦你找找裁判的人选咯~',Speaker=892011,}",
  [154529] = "{id=154529,Text='其实我觉得吟游诗人穆勒先生还蛮合适的呢。',Speaker=892011,}",
  [154530] = "{id=154530,Text='只不过不知道他愿不愿意……',Speaker=892011,}",
  [154531] = "{id=154531,Text='你帮我去问问好不好~他就在南门~加油哦！',Speaker=892011,Option='{立刻传送,99},{自己过去,100}',Emoji=26,}",
  [154532] = "{id=154532,Text='冒险者，今天想听什么故事呀？',Speaker=892019,Option='不是',}",
  [154533] = "{id=154533,Text='那你来是？',Speaker=892019,Option='运动会的裁判',}",
  [154534] = "{id=154534,Text='嘿，原来你是为了这事来找我啊。抱歉，我没这个打算。',Speaker=892019,Option='你再考虑下',}",
  [154535] = "{id=154535,Text='嗯——好吧。如果你能回答我几个问题，给我满意的答复的话，我就答应你。',Speaker=892019,}",
  [154536] = "{id=154536,Text='那么我要出题了。',Speaker=892019,}",
  [154537] = "{id=154537,Text='普隆德拉谁的故事讲得最动人？',Speaker=892019,Option='{穆勒,2},{理发店商人,3},{冒险者,4}',}",
  [154538] = "{id=154538,Text='不愧是冒险者，果然好眼力~',Speaker=892019,Emoji=25,}",
  [154539] = "{id=154539,Text='你说什么？！你竟然觉得那个理发的比我会讲故事？',Speaker=892019,Emoji=1,}",
  [154540] = "{id=154540,Text='你的自我感觉是不是过于良好了？',Speaker=892019,Emoji=14,}",
  [154541] = "{id=154541,Text='第二个问题——为什么你觉得我适合当裁判？',Speaker=892019,Option='{大家尊敬你,6},{你长得好看,7},{以上皆是,8}',}",
  [154542] = "{id=154542,Text='那当然了，不过难道就没点别的原因了？',Speaker=892019,}",
  [154543] = "{id=154543,Text='你不说我也知道。还有什么嘛？',Speaker=892019,}",
  [154544] = "{id=154544,Text='嗯~这才对嘛！',Speaker=892019,}",
  [154545] = "{id=154545,Text='冒险者，你的回答我很满意！那我就答应你当裁判！',Speaker=892019,Emoji=26,}",
  [154546] = "{id=154546,Text='那我回去找找道具啊，我们到时候赛场上见！',Speaker=892019,}",
  [154547] = "{id=154547,Text='冒险者你回来啦！怎么样？穆勒先生同意了吗？',Speaker=892011,Option='同意了',Emoji=26,}",
  [154548] = "{id=154548,Text='耶！太棒啦！果然有冒险者你在什么事都能非常顺利呢！',Speaker=892011,}",
  [154549] = "{id=154549,Text='今天一天真是辛苦你了。我们的准备工作也马上进入尾声了呢。',Speaker=892011,}",
  [154550] = "{id=154550,Text='明天再加把劲就好啦！',Speaker=892011,Emoji=17,}",
  [154551] = "{id=154551,Text='我们明天见哦！',Speaker=892011,}",
  [154552] = "{id=154552,Text='冒险者，今天运动会的准备工作还差一点就能完成了哦！',Speaker=892011,}",
  [154553] = "{id=154553,Text='我们再最后加把劲吧！',Speaker=892011,}",
  [154554] = "{id=154554,Text='最后的任务就是准备奖品~我们需要鲜花和奖杯。',Speaker=892011,}",
  [154555] = "{id=154555,Text='鲜花就麻烦你摘来了。至于奖杯……',Speaker=892011,Option='怎么说',}",
  [154556] = "{id=154556,Text='首先要去北门采集一些矿石。可是那里有很危险的魔物……',Speaker=892011,}",
  [154557] = "{id=154557,Text='然后用采集来的矿石制作奖杯。',Speaker=892011,Option='交给我吧',}",
  [154558] = "{id=154558,Text='走开走开！你这个碍事的家伙！这些矿石可是我们的！',Speaker=892020,Option='我不',}",
  [154559] = "{id=154559,Text='那就让你尝尝我的厉害！',Speaker=892020,}",
  [154560] = "{id=154560,Text='冒险者你终于回来了！我好担心哦！',Speaker=892011,Option='没事啦',Emoji=24,}",
  [154561] = "{id=154561,Text='你没事就太好了……那么，工作进展得如何？',Speaker=892011,Option='都完成了',}",
  [154562] = "{id=154562,Text='哦！你都拿到了啊~辛苦你了！',Speaker=892011,}",
  [154563] = "{id=154563,Text='那么我们就赶紧把矿石做成漂亮的奖杯吧。',Speaker=892011,}",
  [154564] = "{id=154564,Text='奖杯做好啦！',}",
  [154565] = "{id=154565,Text='怎么样？奖杯做好了吗？',Speaker=892011,Option='给你',}",
  [154566] = "{id=154566,Text='哇！好漂亮呀！真是太谢谢你了冒险者！',Speaker=892011,}",
  [154567] = "{id=154567,Text='这样一来我们运动会的准备工作就全部完成啦！',Speaker=892011,Emoji=20,}",
  [154568] = "{id=154568,Text='真不敢想象如果没有你的协助，我一个人要怎么完成这么巨大的工作量……',Speaker=892011,}",
  [154569] = "{id=154569,Text='现在一切都准备就绪了，只等开幕的那天啦！',Speaker=892011,}",
  [154570] = "{id=154570,Text='到时候你一定要来开幕式呀！大家一定会热烈欢迎你的！',Speaker=892011,Emoji=39,}",
  [154571] = "{id=154571,Text='那么我再去最后确认一下。这次你就好好地休息一下吧~真的谢谢你了。',Speaker=892011,}",
  [154572] = "{id=154572,Text='拜拜~',Speaker=892011,}",
  [154573] = "{id=154573,Text='确定要捐献25枚黄宝石吗？',Speaker=892008,Option='确定',}",
  [154574] = "{id=154574,Text='啊……好像没有呢……',Speaker=892008,}",
  [154575] = "{id=154575,Text='感谢你的帮忙~',Speaker=892008,}",
  [154576] = "{id=154576,Text='欸？',Speaker=892008,}",
  [154577] = "{id=154577,Text='那可真是太棒了~',Speaker=892008,}",
  [154579] = "{id=154579,Text='好吧……太遗憾了。',Speaker=892010,}",
  [154580] = "{id=154580,Text='你准备去帮米夏了吗？',Speaker=892010,}",
  [154581] = "{id=154581,Text='再……再来……',Speaker=892015,}",
  [154582] = "{id=154582,Text='剪刀石头布——',Speaker=892015,Option='{石头,40},{剪刀,50},{布,60}',}",
  [154583] = "{id=154583,Text='剪刀石头布——',Speaker=892015,Option='{石头,70},{剪刀,80},{布,90}',}",
  [154585] = "{id=154585,Text='熊本熊自从回去了之后就特别想你。',Speaker=892029,}",
  [154586] = "{id=154586,Text='他也很想知道，你还记得他吗？',Speaker=892029,Option='{当然,3},{让我想想,4}',}",
  [154587] = "{id=154587,Text='你连超人气偶像熊本熊都记不起来了吗？！',Speaker=892029,}",
  [154588] = "{id=154588,Text='唔，那我考考你，谁最喜欢吃幸福拉面？',Speaker=892029,Option='{熊本熊,5},{拉链熊,4}',}",
  [154589] = "{id=154589,Text='不愧是超人气偶像呢！',Speaker=892029,}",
  [154590] = "{id=154590,Text='唔，那我考考你，熊本县的形象大使是哪个？',Speaker=892029,Option='{熊本熊,5},{玩偶熊,4}',}",
  [154591] = "{id=154591,Text='唔，那我考考你，哪个地方的樱花和天津町一样美丽？',Speaker=892029,Option='{熊本县,5},{吉芬,4}',}",
  [154592] = "{id=154592,Text='唔，那我考考你，熊本熊在熊本县的好朋友是什么？',Speaker=892029,Option='{海豚,5},{波利,4}',}",
  [154593] = "{id=154593,Text='【[c][ffff00]盔甲上交后将会从包裹中清除，已强化、精炼或附魔效果也会消失。[-][/c]】',Speaker=891018,}",
  [154594] = "{id=154594,Text='唔，那我考考你，超人气偶像的名字是什么？',Speaker=892029,Option='{熊本熊,5},{蓝疯兔,4}',}",
  [154595] = "{id=154595,Text='带齐4件盔甲后再来找我吧。',Speaker=891018,}",
  [154596] = "{id=154596,Text='听说今天是个特别（暧昧）的日子，那么我将给你双倍的快乐！',Speaker=892001,Option='{领取,4},{再等等,2}',}",
  [154597] = "{id=154597,Text='神棍熊的占卜开张啦~',Speaker=890819,}",
  [154598] = "{id=154598,Text='想要知道你今天的运势吗？',Speaker=890819,}",
  [154599] = "{id=154599,Text='想要了解你今天的幸运方位吗？',Speaker=890819,}",
  [154600] = "{id=154600,Text='找本熊就可以啦~记得每天都来找我哦~',Speaker=890819,}",
  [154601] = "{id=154601,Text='该去下一个地方了……目标：北门！',Option='{立刻传送,99},{自己过去,2}',}",
  [154603] = "{id=154603,Text='确定要捐献15枚紫宝石吗？',Speaker=892008,Option='确定',}",
  [154604] = "{id=154604,Text='确定要捐献20枚天蓝宝石吗？',Speaker=892008,Option='确定',}",
  [154605] = "{id=154605,Text='该回去了。',Option='{立刻传送,111},{自己过去,222}',}",
  [154607] = "{id=154607,Text='冒险者，快来帮帮忙吧！我快要被这大太阳晒化了。',Speaker=892037,Option='发生了什么事？',Emoji=22,}",
  [154608] = "{id=154608,Text='前几天我和研究所的同事们一起制造了一台超大型降雪机，只要打开，方圆10里都会飘下冰凉的雪花~！',Speaker=892037,}",
  [154609] = "{id=154609,Text='就算是这炎热的夏天也不用担心被晒成刚出锅的小龙虾了。',Speaker=892037,}",
  [154610] = "{id=154610,Text='可是在我们即将完成的时候，几只可恶的魔物把我们的降雪机破坏并抢走了重要零件。还把我们的实验品[c][ffff00]隐身药水[-][/c]抢走了！',Speaker=892037,}",
  [154611] = "{id=154611,Text='拜托你一定要帮我们抢回来啊！',Speaker=892037,Option='{交给我吧,2},{我还有事,3}',Emoji=24,}",
  [154612] = "{id=154612,Text='什么！你居然不想感受清凉的夏日吗！',Speaker=892037,Emoji=1,}",
  [154613] = "{id=154613,Text='[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Speaker=892037,Option='{好的,2},{暂时没空,3}',}",
  [154614] = "{id=154614,Text='太好了！大陆的夏天就靠你来拯救了！',Speaker=892037,Emoji=9,}",
  [154615] = "{id=154615,Text='那么我来给你提供一些重要线索吧~！',Speaker=892037,}",
  [154616] = "{id=154616,Text='它们抢走的隐身药水目前有2个缺陷，第一是只要被[c][ffff00]相机[-][/c]所拍到，就会从透明状态变成实体。',Speaker=892037,}",
  [154617] = "{id=154617,Text='第二隐身药水会留下特殊波长的光线，你只要拿着我这把[c][ffff00]追踪器[-][/c]就可以看到,并且可以传送到目标点附近。',Speaker=892037,}",
  [154618] = "{id=154618,Text='不过光线存在时间很短，事不宜迟，快点追踪它们吧！',Speaker=892037,}",
  [154619] = "{id=154619,Text='冒险者，你终于回来了，怎么样？拿回零件了吗？',Speaker=892037,Option='提交物品',}",
  [154620] = "{id=154620,Text='呃……这个不是我要的东西，可能是我的追踪器出了点问题。',Speaker=892037,}",
  [154621] = "{id=154621,Text='让我检查一下。',Speaker=892037,Emoji=10,}",
  [154622] = "{id=154622,Text='虽然拿错了东西，不过真是麻烦你这么辛苦帮我的忙，给你点礼物作为报酬吧。',Speaker=892037,}",
  [154623] = "{id=154623,Text='等我回去再改造一下，明天请再来帮助我！',Speaker=892037,}",
  [154624] = "{id=154624,Text='是否使用追踪器传送到目标点附近。',Speaker=892038,Option='{确认,5},{不用,6}',}",
  [154625] = "{id=154625,Text='痕迹在这里停止了，魔物应该就在这里，用相机看一下吧。',}",
  [154626] = "{id=154626,Text='魔物在向其他区域移动，继续追踪吧。',}",
  [154627] = "{id=154627,Text='是否使用追踪器传送到目标点附近。',Speaker=892038,Option='{确认,15},{不用,16}',}",
  [154628] = "{id=154628,Text='痕迹在这里停止了，魔物应该就在这里，用相机看一下吧。',}",
  [154629] = "{id=154629,Text='魔物还在向其他区域移动，继续追踪吧。',Speaker=892038,}",
  [154630] = "{id=154630,Text='是否使用追踪器传送到目标点附近。',Speaker=892038,Option='{确认,25},{不用,26}',}",
  [154631] = "{id=154631,Text='痕迹在这里停止了，魔物应该就在这里，用相机看一下吧。',}",
  [154633] = "{id=154633,Text='终于把完整的零件带回来了，这样我们就可以重新制造降雪机，真是太好了！',Speaker=892037,}",
  [154634] = "{id=154634,Text='我们可以有清爽的夏天啦~！',Speaker=892037,Emoji=27,}",
  [154636] = "{id=154636,Text='谢谢噫！',Speaker=892016,}",
  [154637] = "{id=154637,Text='行了行了，要好好演奏啊！',Speaker=892039,}",
  [154638] = "{id=154638,Text='那么————',Speaker=892016,}",
  [154639] = "{id=154639,Text='溜溜猴乐团——！',Speaker=892017,}",
  [154640] = "{id=154640,Text='诞生了噫——！',Speaker=892028,}",
  [154641] = "{id=154641,Text='你竟然愿意邀请我们！好开心！',Speaker=892017,}",
  [154642] = "{id=154642,Text='放心吧，我们一定会给运动会带来最棒的演出！',Speaker=892017,Option='鼓要记得还',}",
  [154643] = "{id=154643,Text='知、知道啦…………',Speaker=892017,Emoji=18,}",
  [154644] = "{id=154644,Text='那到时候再见！一定要期待哦！',Speaker=892017,}",
  [154657] = "{id=154657,Text='用仁义之心驱除邪恶，火焰神，参上！',Speaker=892046,}",
  [154658] = "{id=154658,Text='为了守护大家，我将继续战斗！我是，魔神坛斗士！',Speaker=892046,}",
  [154659] = "{id=154659,Text='新的危机已经来临，你愿意与我一同再次并肩作战吗？',Speaker=892046,Option='{立即前往,2},{现在没空,3}',}",
  [154660] = "{id=154660,Text='好吧，这个世界还是要让我来拯救！',Speaker=892046,}",
  [154665] = "{id=154665,Text='是否使用追踪器传送到目标点附近。',Speaker=892038,Option='{确认,8},{不用,9}',}",
  [154666] = "{id=154666,Text='是否使用追踪器传送到目标点附近。',Speaker=892038,Option='{确认,18},{不用,19}',}",
  [154668] = "{id=154668,Text='让魔物逃走了，不过地上好像掉了什么东西。',Option='过去看看',}",
  [154670] = "{id=154670,Text='太好了，终于下雨了！这种清凉的感觉，就像吃到了生日蛋糕一样开心！',Speaker=892008,}",
  [154671] = "{id=154671,Text='谢谢你，冒险者！这是一点心意，请你收下~',Speaker=892008,}",
  [154684] = "{id=154684,Text='唉，真伤脑筋……',Speaker=892051,}",
  [154685] = "{id=154685,Text='嗯，你是……接受委托的冒险者吗？[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取】[-][/c]',Speaker=892051,Option='{是的,4},{不是,3}',}",
  [154686] = "{id=154686,Text='真是可惜……',Speaker=892051,}",
  [154687] = "{id=154687,Text='太好啦！终于等到了！',Speaker=892051,Option='终于？',Emoji=9,}",
  [154688] = "{id=154688,Text='啊哈哈，情况有点复杂，我并不是委托的发布者。',Speaker=892051,}",
  [154689] = "{id=154689,Text='总之，请你先去见发布者本人吧。他叫沃雷特，就在不远处。',Speaker=892051,}",
  [154690] = "{id=154690,Text='我说了一万遍不要！你们都是听不懂人话吗！',Speaker=892049,Emoji=12,}",
  [154691] = "{id=154691,Text='爸爸！别任性了！大家也都是因为担心你！',Speaker=892050,}",
  [154692] = "{id=154692,Text='哼！',Speaker=892049,}",
  [154693] = "{id=154693,Text='喂，我说你！',Speaker=892049,Option='我？',}",
  [154694] = "{id=154694,Text='对，就是你。你觉得我看上去老得已经走不动路了吗？',Speaker=892049,Option='{您正当壮年呢,2},{您自己心里有数,3}',}",
  [154695] = "{id=154695,Text='哈哈哈，说得好！这才是有眼光的年轻人！',Speaker=892049,}",
  [154696] = "{id=154696,Text='爸爸！这是最后的底线，没有商量余地！',Speaker=892050,}",
  [154697] = "{id=154697,Text='本来就用不着商量！少管我！',Speaker=892049,}",
  [154698] = "{id=154698,Text='兔崽子怎么说话呢！！',Speaker=892049,Emoji=12,}",
  [154699] = "{id=154699,Text='基本的礼貌都不懂，没教养！',Speaker=892049,}",
  [154700] = "{id=154700,Text='唉……请不要介意，父亲他就是这样一个固执的急性子。',Speaker=892050,}",
  [154701] = "{id=154701,Text='明明年纪大了，却非要独自一人外出冒险，谁劝都不听。',Speaker=892050,}",
  [154702] = "{id=154702,Text='我迫不得已才发布了任务。',Speaker=892050,Option='任务到底是？',}",
  [154703] = "{id=154703,Text='其实，父亲年轻时也是一名冒险者。',Speaker=892050,}",
  [154704] = "{id=154704,Text='虽然后来成了商人，一直忙于生意……',Speaker=892050,}",
  [154705] = "{id=154705,Text='但他心里对冒险的渴望一直都没有消失。',Speaker=892050,}",
  [154706] = "{id=154706,Text='这几年，生意也稳定了，家庭也没有什么烦心事。',Speaker=892050,}",
  [154707] = "{id=154707,Text='父亲对于冒险的热情又燃烧了起来，说要去重温当年走过的地方，把那些著名的景点都拍下来做成手册，可以时时欣赏。',Speaker=892050,}",
  [154708] = "{id=154708,Text='可他毕竟年事已高，独自冒险是无论如何都让人没法放心的。',Speaker=892050,}",
  [154709] = "{id=154709,Text='所以我们希望你能代替我父亲，完成他的愿望。',Speaker=892050,Option='他看起来很抗拒',}",
  [154710] = "{id=154710,Text='呃，这确实是问题所在。',Speaker=892050,Emoji=15,}",
  [154711] = "{id=154711,Text='希望你能再去和他沟通一次。拜托了。',Speaker=892050,}",
  [154712] = "{id=154712,Text='别白费力气了，我不会改主意的。',Speaker=892049,}",
  [154713] = "{id=154713,Text='不过是重走一次年轻时的路，再看一眼风景。',Speaker=892049,}",
  [154714] = "{id=154714,Text='这点事我都做不了主，还有什么意思！',Speaker=892049,Option='{我可以帮助您,5},{您会用照相机吗？,6}',}",
  [154715] = "{id=154715,Text='帮我？哼哼，口气倒不小。',Speaker=892049,}",
  [154716] = "{id=154716,Text='你又知道我喜欢什么了？',Speaker=892049,}",
  [154717] = "{id=154717,Text='……唔，等等，倒是有一个办法。',Speaker=892049,}",
  [154718] = "{id=154718,Text='我可以学！',Speaker=892049,}",
  [154719] = "{id=154719,Text='怎么，觉得我老了就学不会吗？',Speaker=892049,Emoji=12,}",
  [154720] = "{id=154720,Text='你以为你年轻，会用照相机就敢小看我？',Speaker=892049,}",
  [154721] = "{id=154721,Text='我还嫌你拍的照片达不到我的要求呢！',Speaker=892049,}",
  [154722] = "{id=154722,Text='你，去拍一张大教堂的照片来给我。',Speaker=892049,Option='圣歌大教堂？',}",
  [154723] = "{id=154723,Text='普隆德拉最著名的景点之一，这个都拍不好的话，趁早别烦我。',Speaker=892049,}",
  [154724] = "{id=154724,Text='哦？这么快？我看看。',Speaker=892049,}",
  [154725] = "{id=154725,Text='……唔…………',Speaker=892049,Option='如何？',Emoji=14,}",
  [154726] = "{id=154726,Text='……哼，勉强还行。',Speaker=892049,Option='那么我算是过关了？',}",
  [154727] = "{id=154727,Text='要不是女儿坚持……哼，算了。',Speaker=892049,}",
  [154728] = "{id=154728,Text='就算是我决定委托你了吧！',Speaker=892049,Option='太好了！',}",
  [154729] = "{id=154729,Text='我回去需要准备准备。明天一早你再来找我！',Speaker=892049,}",
  [154730] = "{id=154730,Text='怎么这时候才来！',Speaker=892049,}",
  [154731] = "{id=154731,Text='年纪轻轻的，居然比我来得还晚像什么话！',Speaker=892049,Option='对不起',}",
  [154732] = "{id=154732,Text='哼，算了。',Speaker=892049,}",
  [154733] = "{id=154733,Text='教训你也是浪费时间。我们还是快出发。',Speaker=892049,Option='{出发？,2},{不是要拍照吗？,3}',}",
  [154734] = "{id=154734,Text='今天的目的地是依斯鲁得，剑士公会。',Speaker=892049,}",
  [154735] = "{id=154735,Text='直接从南门出发就好。',Speaker=892049,}",
  [154736] = "{id=154736,Text='没错。需要拍摄依斯鲁得的剑士公会。',Speaker=892049,}",
  [154737] = "{id=154737,Text='依斯鲁得是我当年作为冒险者的第一站。',Speaker=892049,}",
  [154738] = "{id=154738,Text='哼，这一路上，我也是击败过不少魔物的！',Speaker=892049,}",
  [154739] = "{id=154739,Text='我在南门等你，你可别拖我后腿！',Speaker=892049,Option='{现在出发,5},{待会再说,6}',}",
  [154741] = "{id=154741,Text='来吧，让我们快点出发！',Speaker=892049,Option='跟在我后面安全吧',Emoji=23,}",
  [154742] = "{id=154742,Text='小瞧我吗？我当年可是一个人从南门一路揍着魔物走到依斯鲁得的！',Speaker=892049,}",
  [154743] = "{id=154743,Text='我的冒险手册上可是清清楚楚记着战斗次数呢！',Speaker=892049,Option='……',}",
  [154744] = "{id=154744,Text='你那是什么表情！',Speaker=892049,}",
  [154745] = "{id=154745,Text='那你就试着击败这路上的魔物看看！',Speaker=892049,}",
  [154746] = "{id=154746,Text='看看能不能破我的记录！',Speaker=892049,}",
  [154747] = "{id=154747,Text='怎么样，被吓回来了吧？',Speaker=892049,Option='全部击败了',}",
  [154748] = "{id=154748,Text='………………',Speaker=892049,}",
  [154749] = "{id=154749,Text='什、什么，这么短的时间内……',Speaker=892049,}",
  [154750] = "{id=154750,Text='你、你不是用什么手段作弊了吧？',Speaker=892049,Option='{……,7},{我是年轻人嘛,8}',}",
  [154751] = "{id=154751,Text='……嘁，也没什么好得意的。',Speaker=892049,}",
  [154752] = "{id=154752,Text='为这点小事就自满的话，可是做不成大事的！',Speaker=892049,}",
  [154753] = "{id=154753,Text='……啊，哈哈，也对。',Speaker=892049,}",
  [154754] = "{id=154754,Text='我年轻的时候不也……算啦。',Speaker=892049,}",
  [154755] = "{id=154755,Text='既然道路安全了，那就快走吧。',Speaker=892049,}",
  [154756] = "{id=154756,Text='早些到依斯鲁得！',Speaker=892049,Option='{我明白了,10}{再等等……,11}',}",
  [154757] = "{id=154757,Text='真磨蹭！',Speaker=892049,}",
  [154758] = "{id=154758,Text='呼、呼……',Speaker=892049,Option='老爷子没事吧？',}",
  [154759] = "{id=154759,Text='我、我没事……',Speaker=892049,Option='{您太累了,13}{别死撑着,14}',Emoji=18,}",
  [154760] = "{id=154760,Text='呃……',Speaker=892049,}",
  [154761] = "{id=154761,Text='你、你说得、也有点道理……',Speaker=892049,}",
  [154762] = "{id=154762,Text='我先去找地方休息……',Speaker=892049,}",
  [154763] = "{id=154763,Text='你就先去剑士公会拍照，然后来找我吧。',Speaker=892049,}",
  [154764] = "{id=154764,Text='呼……你、你这家伙……',Speaker=892049,}",
  [154765] = "{id=154765,Text='不改改这说话口气，以后可交不到什么朋友……',Speaker=892049,}",
  [154766] = "{id=154766,Text='啊，剑士公会……还是那样……',Speaker=892049,}",
  [154767] = "{id=154767,Text='………………',Speaker=892049,Option='怎么了',Emoji=14,}",
  [154768] = "{id=154768,Text='和当时一模一样，就像是昨天刚来过。',Speaker=892049,}",
  [154769] = "{id=154769,Text='可惜呀，实际上已经那么多年过去啦。',Speaker=892049,}",
  [154770] = "{id=154770,Text='就算再怎么逞强，我也已经是个老头子。',Speaker=892049,Option='您是想重温青春吗',}",
  [154771] = "{id=154771,Text='别说的那么夸张，我就是想重新看看风景而已。',Speaker=892049,}",
  [154772] = "{id=154772,Text='不过，你说的……大概也对吧。',Speaker=892049,}",
  [154773] = "{id=154773,Text='唉，聊天还是等明天吧，我可撑不住了。',Speaker=892049,}",
  [154774] = "{id=154774,Text='明天，我再好好和你说我想要冒险的真正理由。',Speaker=892049,}",
  [154775] = "{id=154775,Text='哦！还不算迟！',Speaker=892049,}",
  [154776] = "{id=154776,Text='好啦，今天我们要去沙漠。事不宜迟。',Speaker=892049,Option='你昨天说过……',}",
  [154777] = "{id=154777,Text='嗯？',Speaker=892049,}",
  [154778] = "{id=154778,Text='哦，冒险的真正理由？',Speaker=892049,Emoji=14,}",
  [154779] = "{id=154779,Text='咳，也不是什么大事。',Speaker=892049,}",
  [154780] = "{id=154780,Text='虽然我的冒险生涯没有几年，但收获不少。',Speaker=892049,}",
  [154781] = "{id=154781,Text='不仅在冒险中积累了我的第一笔小小财富……',Speaker=892049,}",
  [154782] = "{id=154782,Text='关键是，遇到了……我第一次喜欢的女孩。',Speaker=892049,Option='{所以说,0}{就是想鸳梦重温啊！,0}',Emoji=38,}",
  [154783] = "{id=154783,Text='咳，想什么呢，我都成家这么久了。',Speaker=892049,}",
  [154784] = "{id=154784,Text='不过……我当时曾和她约定好了。',Speaker=892049,}",
  [154785] = "{id=154785,Text='要陪她走过她所向往的那些景点。',Speaker=892049,}",
  [154786] = "{id=154786,Text='金字塔下的法老王机关就是其中之一！',Speaker=892049,}",
  [154787] = "{id=154787,Text='来吧年轻人，为了尼娅的梦想。',Speaker=892049,}",
  [154788] = "{id=154788,Text='不要浪费时间，快点出发！',Speaker=892049,Option='好，走吧',}",
  [154789] = "{id=154789,Text='怎么这么吵吵嚷嚷的？',Speaker=892049,}",
  [154790] = "{id=154790,Text='不好好学经商，只会到处浪荡！',Speaker=892052,}",
  [154791] = "{id=154791,Text='我说了我只想当冒险者，不想当商人！',Speaker=892053,}",
  [154792] = "{id=154792,Text='什么冒险者，不要把四处溜达说得那么好听。',Speaker=892052,}",
  [154793] = "{id=154793,Text='你也成年了，总要变得成熟点。',Speaker=892052,}",
  [154794] = "{id=154794,Text='知道我成年了还对我的选择指手画脚！',Speaker=892053,Emoji=12,}",
  [154795] = "{id=154795,Text='你以为我想吗！我不管你的话你能养活自己吗！',Speaker=892052,}",
  [154796] = "{id=154796,Text='我为什么不能？',Speaker=892053,}",
  [154797] = "{id=154797,Text='你幼稚无知！',Speaker=892052,}",
  [154798] = "{id=154798,Text='你满身铜臭！',Speaker=892053,}",
  [154799] = "{id=154799,Text='住嘴！你们俩都太不像话了！',Speaker=892049,Option='老爷子你……',}",
  [154800] = "{id=154800,Text='为这点事吵成这样，只能说明你们的无能！',Speaker=892049,}",
  [154801] = "{id=154801,Text='老头你谁啊。',Speaker=892053,Emoji=12,}",
  [154802] = "{id=154802,Text='居然随随便便地教训起我们……',Speaker=892052,}",
  [154803] = "{id=154803,Text='实在听不下去了，你们的眼界也只有这么点！',Speaker=892049,}",
  [154804] = "{id=154804,Text='居然为了经商和冒险而争吵！',Speaker=892049,}",
  [154805] = "{id=154805,Text='谁说它们是对立的了？谁说冒险就不能赚钱了？',Speaker=892049,}",
  [154806] = "{id=154806,Text='胡说八道什么！',Speaker=892052,}",
  [154807] = "{id=154807,Text='米德加尔特是遍地宝藏的大陆！有勇气的人就能获利！',Speaker=892049,}",
  [154808] = "{id=154808,Text='那边的小子，你要做冒险者，就该证明这一点才对！',Speaker=892049,}",
  [154809] = "{id=154809,Text='而不是浪费时间和你父亲打嘴仗！',Speaker=892049,}",
  [154810] = "{id=154810,Text='哈？我……等一下……',Speaker=892053,Emoji=7,}",
  [154811] = "{id=154811,Text='跟过来吧，小子！让我们朝金字塔出发！',Speaker=892049,}",
  [154812] = "{id=154812,Text='金字塔下多的是无主的财富！拿来给你父亲看！',Speaker=892049,}",
  [154813] = "{id=154813,Text='呃……',Speaker=892053,}",
  [154814] = "{id=154814,Text='想一出是一出，让人不省心。',Option='跟上去保护他们',}",
  [154815] = "{id=154815,Text='好，这就差不多了。',Speaker=892049,}",
  [154816] = "{id=154816,Text='对了，别忘了拍照。',Speaker=892049,Option='{对哦,0},{你不说我都忘了,0}',}",
  [154817] = "{id=154817,Text='哼，这就是你和我的差距。',Speaker=892049,}",
  [154818] = "{id=154818,Text='嗯，都完成了，那就回去吧。',Speaker=892049,}",
  [154819] = "{id=154819,Text='这、这是……',Speaker=892052,}",
  [154820] = "{id=154820,Text='古代遗留的宝石啊！',Speaker=892052,}",
  [154821] = "{id=154821,Text='哼，还算你有点眼力。',Speaker=892049,}",
  [154822] = "{id=154822,Text='稍微做一下加工，就能在普隆德拉的市场卖个好价。',Speaker=892049,}",
  [154823] = "{id=154823,Text='那边的小子，懂了吗？怎么样才能让你父亲支持你。',Speaker=892049,}",
  [154824] = "{id=154824,Text='呃，懂了……虽然我觉得怪怪的……',Speaker=892053,}",
  [154825] = "{id=154825,Text='等一下……',Speaker=892052,}",
  [154826] = "{id=154826,Text='你……不、您是！',Speaker=892052,Emoji=7,}",
  [154827] = "{id=154827,Text='是黄金眼大人啊！',Speaker=892052,Option='黄金眼？',}",
  [154828] = "{id=154828,Text='普隆德拉的第一富商，沃雷特大人！',Speaker=892052,}",
  [154829] = "{id=154829,Text='您是我们全体商人的骄傲啊！',Speaker=892052,}",
  [154830] = "{id=154830,Text='乱七八糟的马屁少拍。',Speaker=892049,}",
  [154831] = "{id=154831,Text='只想着让儿子固守家产是没有出路的。',Speaker=892049,}",
  [154832] = "{id=154832,Text='你本应该鼓励他出去闯荡，创造自己的财富。',Speaker=892049,}",
  [154833] = "{id=154833,Text='身为父亲，你要做的是引导，而不是简单粗暴的阻拦！',Speaker=892049,}",
  [154834] = "{id=154834,Text='……您说得对。',Speaker=892052,Emoji=7,}",
  [154835] = "{id=154835,Text='我不会再阻拦他了，冒险者也是能做出事业的。',Speaker=892052,}",
  [154836] = "{id=154836,Text='哼，知道就好。',Speaker=892049,}",
  [154837] = "{id=154837,Text='你们自己谈吧，我走了。',Speaker=892049,}",
  [154838] = "{id=154838,Text='可累死我了。',Speaker=892049,Option='{没想到老爷子你,0},{真有两下子,0}',}",
  [154839] = "{id=154839,Text='哼，是其他人太蠢了。',Speaker=892049,}",
  [154840] = "{id=154840,Text='我太累了，明天你再来找我。',Speaker=892049,Option='好的',}",
  [154841] = "{id=154841,Text='哦哦，法老王机关拍好了对吧。',Speaker=892049,}",
  [154842] = "{id=154842,Text='……嗯，也就这样吧。',Speaker=892049,Option='眼界可真高',}",
  [154843] = "{id=154843,Text='哈哈，你也不必泄气。还算不错啦。',Speaker=892049,Option='今天要去哪里',}",
  [154844] = "{id=154844,Text='今天我们要去吉芬。',Speaker=892049,Option='魔法都市吉芬？',}",
  [154845] = "{id=154845,Text='对，确切来说，要去吉芬塔的图书馆。',Speaker=892049,}",
  [154846] = "{id=154846,Text='我年轻时在那里藏了点……怎么说呢，很重要的东西。',Speaker=892049,Option='重要的东西？',}",
  [154847] = "{id=154847,Text='咳，具体情况到了吉芬我再和你说吧。出发！',Speaker=892049,Option='唔……',}",
  [154848] = "{id=154848,Text='这里就是吉芬塔了。嗯……',Speaker=892049,}",
  [154849] = "{id=154849,Text='当年，我来这里的时候，还是个毛头小子呢。',Speaker=892049,Option='藏着的东西是什么？',}",
  [154850] = "{id=154850,Text='唔，嗯，这个……',Speaker=892049,}",
  [154851] = "{id=154851,Text='……其实是，我手写的，情书。',Speaker=892049,Option='{……,2},{太没新意了,3}',Emoji=38,}",
  [154852] = "{id=154852,Text='你那是什么表情！情书有什么奇怪的！',Speaker=892049,}",
  [154853] = "{id=154853,Text='那时候我又温柔又腼腆，没勇气当面告白，用书信表达不是最合适的方法吗！',Speaker=892049,}",
  [154854] = "{id=154854,Text='说什么呢，情感最重要的就是朴素真实。',Speaker=892049,}",
  [154855] = "{id=154855,Text='如果对方不喜欢你，靠花里胡哨的东西就能打动吗？',Speaker=892049,}",
  [154856] = "{id=154856,Text='咳，多说无益。还是快去图书馆找找吧。',Speaker=892049,}",
  [154857] = "{id=154857,Text='那么多年了，也不知道还在不在……',Speaker=892049,}",
  [154858] = "{id=154858,Text='我当年是把情书藏在一本叫《人生的哲学》的书里。',Speaker=892049,}",
  [154859] = "{id=154859,Text='怎么现在找不到了呢……',Speaker=892049,}",
  [154860] = "{id=154860,Text='你去问问看管理员？',Speaker=892049,}",
  [154861] = "{id=154861,Text='烦死了！',Speaker=892054,Option='我想找一本书',}",
  [154862] = "{id=154862,Text='没看到我忙成这样吗！闪开闪开我还有活要干！',Speaker=892054,}",
  [154863] = "{id=154863,Text='什么？',Speaker=892049,Emoji=12,}",
  [154864] = "{id=154864,Text='这是什么态度？！',Speaker=892049,Option='对我发火也没用',}",
  [154865] = "{id=154865,Text='被人这么恶劣地对待，如果不反击回去，那就是纵容！',Speaker=892049,}",
  [154866] = "{id=154866,Text='我倒要去纠正纠正这混蛋！',Speaker=892049,}",
  [154867] = "{id=154867,Text='你就这么对待我？！',Speaker=892049,Emoji=12,}",
  [154868] = "{id=154868,Text='对一个闲得发慌的老头，这态度不错了！',Speaker=892054,}",
  [154869] = "{id=154869,Text='你、你再说一遍！',Speaker=892049,}",
  [154870] = "{id=154870,Text='你以为这里是什么地方，找情书这种要求也能厚脸皮提出来！',Speaker=892054,}",
  [154871] = "{id=154871,Text='这里可是米德加尔特的知识宝库，不是风花雪月的垃圾场，情书这种东西别往这儿乱倒！',Speaker=892054,}",
  [154872] = "{id=154872,Text='你、你居然——',Speaker=892049,Option='啊，完蛋了',}",
  [154873] = "{id=154873,Text='我要买下你们这破图书馆！！',Speaker=892049,}",
  [154874] = "{id=154874,Text='然后我来好好管教你这个目中无人的态度！！',Speaker=892049,}",
  [154875] = "{id=154875,Text='你这老头说什么胡话呢！',Speaker=892054,Emoji=12,}",
  [154876] = "{id=154876,Text='是不是胡话你很快就会知道！',Speaker=892049,}",
  [154877] = "{id=154877,Text='对、对不起，我可能没听清——',Speaker=892055,}",
  [154878] = "{id=154878,Text='我要买下这个图书馆！快开价！',Speaker=892049,Emoji=12,}",
  [154879] = "{id=154879,Text='这个实在是……',Speaker=892055,Option='是这么回事',}",
  [154880] = "{id=154880,Text='……嗯，我了解事情大概了。',Speaker=892055,}",
  [154881] = "{id=154881,Text='很抱歉让您遇到如此不顺心的事情。',Speaker=892055,}",
  [154882] = "{id=154882,Text='其实，图书馆近日在清理旧书，工作量非常大。',Speaker=892055,}",
  [154883] = "{id=154883,Text='馆内工作人员连续加班非常疲惫，难免会脾气暴躁。',Speaker=892055,}",
  [154884] = "{id=154884,Text='虽然我不是想为对方开脱……',Speaker=892055,Emoji=15,}",
  [154885] = "{id=154885,Text='但现下要在茫茫书海里寻找多年前的情书，真是非常非常困难的工作。',Speaker=892055,Option='{我明白,0},{我们自己来就好,0}',}",
  [154886] = "{id=154886,Text='哈哈，图书馆内的图书都有各自的摆放规律，找一本特定的书还是交给我们最快。',Speaker=892055,}",
  [154887] = "{id=154887,Text='不过，若您能顺便帮忙清理其余散落的书籍就太感谢了。',Speaker=892055,Option='好的',}",
  [154888] = "{id=154888,Text='………………',Speaker=892055,Option='书籍清理完了',}",
  [154889] = "{id=154889,Text='啊，辛苦了。我这里刚好也找到《人生的哲学》了。',Speaker=892055,}",
  [154890] = "{id=154890,Text='那么，请自便。',Speaker=892055,}",
  [154891] = "{id=154891,Text='………………',Speaker=892049,Option='{怎么了？,0},{找到情书了？,0}',}",
  [154892] = "{id=154892,Text='……书里没有。',Speaker=892049,Option='什么？',}",
  [154893] = "{id=154893,Text='我写的那封情书，这本书里没有。',Speaker=892049,}",
  [154894] = "{id=154894,Text='但是……有这个。',Speaker=892049,Option='“我在姜饼城等你”？',}",
  [154895] = "{id=154895,Text='这是尼娅的笔迹。',Speaker=892049,}",
  [154896] = "{id=154896,Text='她说……她在姜饼城等我。我却一直没有发现这张纸……',Speaker=892049,}",
  [154897] = "{id=154897,Text='…………多少年了。',Speaker=892049,Option='老爷子……',}",
  [154898] = "{id=154898,Text='别和我说话，我想静静。',Speaker=892049,}",
  [154899] = "{id=154899,Text='…………',Speaker=892049,Option='心情好点了吗',}",
  [154900] = "{id=154900,Text='也、也没有什么心情不好……',Speaker=892049,Option='那今天去哪里呢',}",
  [154901] = "{id=154901,Text='去哪里……我也不知道……',Speaker=892049,}",
  [154902] = "{id=154902,Text='很累，动都不想动了。',Speaker=892049,Option='……',}",
  [154903] = "{id=154903,Text='就让我在这里呆着吧。',Speaker=892049,Option='不去姜饼城吗',}",
  [154904] = "{id=154904,Text='为为为为为什么要去姜饼城啊！',Speaker=892049,Option='但是纸条写着',Emoji=7,}",
  [154905] = "{id=154905,Text='那可是几十年前的纸条啊！现在去有什么用！',Speaker=892049,}",
  [154906] = "{id=154906,Text='就为了让我知道我这个蠢货错过了喜欢的女孩吗！',Speaker=892049,Option='{这也未必,0},{也许她还在等你,0}',}",
  [154907] = "{id=154907,Text='你、你这个——',Speaker=892049,}",
  [154908] = "{id=154908,Text='你这个大傻瓜！怎么可能会有这种事！',Speaker=892049,Emoji=12,}",
  [154909] = "{id=154909,Text='捉弄我一个老头子有意思吗！',Speaker=892049,Option='不去看看怎知道',}",
  [154910] = "{id=154910,Text='不！我不要去！',Speaker=892049,Option='{抱歉了,0}{由不得你,0}',}",
  [154911] = "{id=154911,Text='喂、喂！你做什么？别拉我！救命啊——',Speaker=892049,}",
  [154912] = "{id=154912,Text='呼、呼……',Speaker=892049,}",
  [154913] = "{id=154913,Text='我说！你一路把我这老骨头都要颠散了！',Speaker=892049,Emoji=12,}",
  [154914] = "{id=154914,Text='给我道歉啊，道歉！',Speaker=892049,Option='{对不起,0},{不过你很有精神,0}',}",
  [154915] = "{id=154915,Text='哼！大老远地跑来姜饼城，我累死了，要去休息！',Speaker=892049,Option='{所以,0},{尼娅在哪儿呢,0}',}",
  [154916] = "{id=154916,Text='………………',Speaker=892049,}",
  [154917] = "{id=154917,Text='不要问我！！',Speaker=892049,Option='那就到处找找看吧',}",
  [154918] = "{id=154918,Text='…………圣夜礼拜堂。',Speaker=892049,}",
  [154919] = "{id=154919,Text='我们第一次来姜饼城那天，礼拜堂里正传来圣歌……',Speaker=892049,}",
  [154920] = "{id=154920,Text='老实说，那还真是……',Speaker=892049,}",
  [154921] = "{id=154921,Text='……我第一次觉得圣歌好听。',Speaker=892049,}",
  [154922] = "{id=154922,Text='啊，玩具工厂！',Speaker=892049,}",
  [154923] = "{id=154923,Text='是真的很好玩，也真的很可怕。',Speaker=892049,Emoji=10,}",
  [154924] = "{id=154924,Text='那会儿我拉着尼娅的手躲避玩具士兵……',Speaker=892049,}",
  [154925] = "{id=154925,Text='一边跑一边心砰砰跳……',Speaker=892049,}",
  [154926] = "{id=154926,Text='圣诞老人小屋……',Speaker=892049,}",
  [154927] = "{id=154927,Text='想起来就生气！',Speaker=892049,Option='生气？',Emoji=12,}",
  [154928] = "{id=154928,Text='那时候尼娅喜欢一个毛绒小熊啊，我就去问那个红鼻子的老头要。',Speaker=892049,}",
  [154929] = "{id=154929,Text='他居然不给我！',Speaker=892049,Option='{这可有点过分,0},{你付钱了吗？,0}',}",
  [154930] = "{id=154930,Text='这是钱的问题吗！他可是直接越过我把小熊给了尼娅啊！',Speaker=892049,}",
  [154931] = "{id=154931,Text='还厚着脸皮说什么给最美丽的冒险者小姐！',Speaker=892049,}",
  [154932] = "{id=154932,Text='油腔滑调，嬉皮笑脸！',Speaker=892049,}",
  [154933] = "{id=154933,Text='我真想把这里买下来好好教导一下什么叫廉耻！',Speaker=892049,Option='……',}",
  [154934] = "{id=154934,Text='……算了。都多少年前了。',Speaker=892049,}",
  [154935] = "{id=154935,Text='尼娅……也不在这里。',Speaker=892049,}",
  [154936] = "{id=154936,Text='够了，年轻人。',Speaker=892049,Option='……',}",
  [154937] = "{id=154937,Text='别浪费时间了，尼娅不在这里。',Speaker=892049,}",
  [154938] = "{id=154938,Text='谢谢你陪我这么长一段路，我年轻时候走过的路，差不多都重温一遍啦。',Speaker=892049,Option='可是……',}",
  [154939] = "{id=154939,Text='够啦，我很满足了。毕竟——',Speaker=892049,}",
  [154940] = "{id=154940,Text='………………………………',Speaker=892049,Option='老爷子？',Emoji=14,}",
  [154941] = "{id=154941,Text='尼、尼娅…………',Speaker=892049,Option='呃？',}",
  [154942] = "{id=154942,Text='尼娅！',Speaker=892049,}",
  [154943] = "{id=154943,Text='呀！',Speaker=892056,}",
  [154944] = "{id=154944,Text='对、对不起……',Speaker=892049,}",
  [154945] = "{id=154945,Text='你叫我尼娅……请问，是沃雷特先生吗？',Speaker=892056,}",
  [154946] = "{id=154946,Text='你、你知道我？',Speaker=892049,Emoji=7,}",
  [154947] = "{id=154947,Text='我一直在这里等你。',Speaker=892056,}",
  [154948] = "{id=154948,Text='等我？你是……',Speaker=892049,}",
  [154949] = "{id=154949,Text='尼娅是我的外祖母，她几年前已经去世了。',Speaker=892056,}",
  [154950] = "{id=154950,Text='…………',Speaker=892049,}",
  [154951] = "{id=154951,Text='去世前，她恳求我每天在这冰之河上等待一个人。她说，虽然不知道现在他在哪里，但如果他来到这姜饼城却找不到我，那也太可怜了。',Speaker=892056,}",
  [154952] = "{id=154952,Text='…………',Speaker=892049,}",
  [154953] = "{id=154953,Text='她让我转达一句话。',Speaker=892056,}",
  [154954] = "{id=154954,Text='“少女时期和你一起经历过的冒险，是我最棒的人生。”',Speaker=892056,}",
  [154955] = "{id=154955,Text='“接下来，让我们彼此都享受与家人在一起的幸福吧——”',Speaker=892056,}",
  [154956] = "{id=154956,Text='……尼娅……',Speaker=892049,}",
  [154957] = "{id=154957,Text='尼娅她……你的外祖母，生活得幸福吗？',Speaker=892049,}",
  [154958] = "{id=154958,Text='是的，外祖母生活得非常幸福。我……最喜欢外祖母了。',Speaker=892056,}",
  [154959] = "{id=154959,Text='是吗……谢谢你。',Speaker=892049,}",
  [154960] = "{id=154960,Text='哈哈哈，好啦！我终于完成外祖母的愿望啦！',Speaker=892056,}",
  [154961] = "{id=154961,Text='接下来，我能没有负担地外出冒险了！',Speaker=892056,}",
  [154962] = "{id=154962,Text='你对冒险有兴趣？',Speaker=892049,}",
  [154963] = "{id=154963,Text='是！我特别想去普隆德拉看一看！',Speaker=892056,Emoji=26,}",
  [154964] = "{id=154964,Text='是吗……太好了，尼娅，你的孩子也是一样的开朗……',Speaker=892049,}",
  [154965] = "{id=154965,Text='小姑娘，加油！等你来到普隆德拉，可以来找我老头子！',Speaker=892049,}",
  [154966] = "{id=154966,Text='嘿嘿，谢谢！',Speaker=892056,}",
  [154967] = "{id=154967,Text='…………',Speaker=892049,Option='{太好了,0},{她确实还在等你,0}',}",
  [154968] = "{id=154968,Text='嗯……',Speaker=892049,}",
  [154969] = "{id=154969,Text='谢谢你年轻人，不是你的话，我是不可能再听到尼娅对我说的话的。',Speaker=892049,}",
  [154970] = "{id=154970,Text='这一次的冒险……我确实没有任何遗憾了。',Speaker=892049,Option='那就好',}",
  [154971] = "{id=154971,Text='接下来我要回普隆德拉了。',Speaker=892049,}",
  [154972] = "{id=154972,Text='就像尼娅说的，我也一把年纪了，要去享受和家人在一起生活得幸福。',Speaker=892049,}",
  [154973] = "{id=154973,Text='让我们在普隆德拉再见面吧！',Speaker=892049,}",
  [154977] = "{id=154977,Text='哟！年轻人，又见面啦。',Speaker=892074,}",
  [154978] = "{id=154978,Text='[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Option='{好的,3},{暂时没空,2}',}",
  [154979] = "{id=154979,Text='今天又有点事想交给你来做。',Speaker=892074,Option='可真会指使人',}",
  [154980] = "{id=154980,Text='咳，反正也不是什么大事，你就顺手帮一把嘛。',Speaker=892074,Option='帮？',}",
  [154981] = "{id=154981,Text='呃，呃……其实任务很简单！',Speaker=892074,}",
  [154982] = "{id=154982,Text='圣歌大教堂最近新来了一批唱诗班成员，但是主教总觉得人数对不上。',Speaker=892074,}",
  [154983] = "{id=154983,Text='他怀疑是不是有人借着唱诗班的名义来骗吃骗喝。',Speaker=892074,}",
  [154984] = "{id=154984,Text='所以！你的任务就是找出这个唱诗班中可疑的人！',Speaker=892074,Option='……',}",
  [154985] = "{id=154985,Text='傻呆着干嘛，还不快去完成任务！',Speaker=892074,Option='{虽说如此,0},{真是可疑,0}',}",
  [154986] = "{id=154986,Text='哎呀，你先去嘛！有什么疑问之后再说！',Speaker=892074,}",
  [154987] = "{id=154987,Text='你是……',Speaker=892064,Option='唱诗班里可疑的人',}",
  [154988] = "{id=154988,Text='哦……！是为这件事……',Speaker=892064,Emoji=7,}",
  [154989] = "{id=154989,Text='嗯……我已经把他们聚集起来了。就是这几位。',Speaker=892064,}",
  [154990] = "{id=154990,Text='问题是，唱诗班是歌颂诸神的纯洁存在。',Speaker=892064,}",
  [154991] = "{id=154991,Text='虽然我怀疑有人冒名顶替，但很难直接质问每个人的身份。',Speaker=892064,}",
  [154992] = "{id=154992,Text='对于信仰纯洁的人来说，这无疑是对他们虔诚之心的践踏。',Speaker=892064,Option='{嗯……,0}{可以理解,0}',}",
  [154993] = "{id=154993,Text='所以，需要找一个方法，在大家都不知情的情况下，分辨出是谁在冒名顶替。',Speaker=892064,Option='具体怎么做呢',}",
  [154994] = "{id=154994,Text='我会带领大家一起唱诗。',Speaker=892064,}",
  [154995] = "{id=154995,Text='如果并非真正的唱诗班成员，对于圣歌不会那么熟悉，难免会有走神的时刻。',Speaker=892064,}",
  [154996] = "{id=154996,Text='虽然可以混在合唱里不被别人察觉，但仔细分辨的话还是会有差别的。',Speaker=892064,Option='{我明白了,0},{我来做观察者,0}',}",
  [154997] = "{id=154997,Text='麻烦你了。那么——',Speaker=892064,}",
  [154998] = "{id=154998,Text='——当海姆达尔挥起了手中的宝剑——',Speaker=892064,}",
  [154999] = "{id=154999,Text='当海姆达尔挥起了手中的宝剑',Speaker=892059,}",
  [155000] = "{id=155000,Text='当海姆达尔挥起了手中的宝剑',Speaker=892060,}",
  [155001] = "{id=155001,Text='当海姆达尔挥起了手中的宝剑',Speaker=892061,}",
  [155002] = "{id=155002,Text='当海姆达尔挥起了手中的宝剑',Speaker=892062,}",
  [155003] = "{id=155003,Text='当海姆达尔挥起了手中的宝剑',Speaker=892063,}",
  [155004] = "{id=155004,Text='——洛基的失败已被注定——',Speaker=892064,}",
  [155005] = "{id=155005,Text='洛基的失败已被注定',Speaker=892059,}",
  [155006] = "{id=155006,Text='洛基的失败已被注定',Speaker=892060,}",
  [155007] = "{id=155007,Text='洛基的失败已被注定',Speaker=892061,}",
  [155008] = "{id=155008,Text='洛基的失败已被注定',Speaker=892062,}",
  [155009] = "{id=155009,Text='洛基的失败已被注定',Speaker=892063,}",
  [155010] = "{id=155010,Text='怎么样，知道了吗？谁是可疑的人？',Speaker=892064,Option='{艾琳,2},{莎拉,2},{玛利亚,2},{戴文,2},{兰斯凯特,3}',}",
  [155011] = "{id=155011,Text='呃……真的吗？和我的感觉有些不太一样……',Speaker=892064,Emoji=10,}",
  [155012] = "{id=155012,Text='要不要再确认一下？',Speaker=892064,}",
  [155013] = "{id=155013,Text='果然你也这么觉得啊。',Speaker=892064,}",
  [155014] = "{id=155014,Text='那么，应该就没错了。',Speaker=892064,}",
  [155015] = "{id=155015,Text='谢谢了，年轻的冒险者。',Speaker=892064,}",
  [155016] = "{id=155016,Text='我会想办法处理这件事的。',Speaker=892064,}",
  [155017] = "{id=155017,Text='哟！年轻人，又见面啦。',Speaker=892074,}",
  [155018] = "{id=155018,Text='今天又有点事想交给你来做。',Speaker=892074,Option='昨天你也这么说',}",
  [155019] = "{id=155019,Text='哎呀，计较这些小事做什么。',Speaker=892074,}",
  [155020] = "{id=155020,Text='听好了，今天的任务可是关系到正义的！',Speaker=892074,}",
  [155021] = "{id=155021,Text='这几天，依斯鲁得的公园里出现了奇怪的家伙。',Speaker=892074,}",
  [155022] = "{id=155022,Text='他自称猜拳之王，专门收钱来和人猜拳。',Speaker=892074,}",
  [155023] = "{id=155023,Text='如果有人猜对了，可以返还一千倍的奖金。',Speaker=892074,}",
  [155024] = "{id=155024,Text='……当然，到目前为止，没有人猜对过。',Speaker=892074,}",
  [155025] = "{id=155025,Text='这不就是诈骗吗！退一万步说，也是恶意的赌博！',Speaker=892074,Option='这也太武断了',}",
  [155026] = "{id=155026,Text='去吧年轻人！快去依斯鲁得搞清楚这人的底细！',Speaker=892074,Option='{知道了,3},{我才不要,2}',}",
  [155027] = "{id=155027,Text='结束了快来找我！',Speaker=892074,}",
  [155028] = "{id=155028,Text='莫名其妙闹什么脾气，乖啊乖啊。',Speaker=892074,}",
  [155029] = "{id=155029,Text='嚯！来挑战一下千倍奖金吗！只要在猜拳中胜过我，就能赚大钱哦！',Speaker=892065,Option='{先不了,4},{我来试试,5}',}",
  [155030] = "{id=155030,Text='嘿嘿嘿，机会不等人的！',Speaker=892065,}",
  [155031] = "{id=155031,Text='好，有魄力！',Speaker=892065,}",
  [155032] = "{id=155032,Text='我这人很大度的，先交1000zeny，三局两胜或者五局三胜，随便你挑！',Speaker=892065,Option='我都不要',}",
  [155033] = "{id=155033,Text='啊？小子，你什么意思？',Speaker=892065,Option='一局定胜负',}",
  [155034] = "{id=155034,Text='对自己这么有信心啊……',Speaker=892065,Emoji=5,}",
  [155035] = "{id=155035,Text='行啊，一局定胜负。可如果你输了怎么办？',Speaker=892065,Option='打个欠条行吗',}",
  [155036] = "{id=155036,Text='欠条有什么用啊！我为了讨债还要追你到天涯海角吗！',Speaker=892065,Option='落款名字沃雷特',}",
  [155037] = "{id=155037,Text='沃雷特？！黄金眼沃雷特？！',Speaker=892065,Emoji=7,}",
  [155038] = "{id=155038,Text='…………好吧，小子，来吧！',Speaker=892065,Option='{石头,6},{剪刀,7},{布,8}',}",
  [155039] = "{id=155039,Text='不、不可能……我竟然……',Speaker=892065,}",
  [155040] = "{id=155040,Text='这不可能！',Speaker=892065,Option='{好了,0},{那么奖金呢,0}',}",
  [155041] = "{id=155041,Text='呃，这个……',Speaker=892065,}",
  [155042] = "{id=155042,Text='你、你根本就没有付钱啊！',Speaker=892065,}",
  [155043] = "{id=155043,Text='没有下注，就算赢了也没有奖金啊！',Speaker=892065,Option='{但是,0},{你已被盯上了,0}',}",
  [155044] = "{id=155044,Text='明白！我这就离开！绝不会再做这种事了！',Speaker=892065,}",
  [155045] = "{id=155045,Text='哎呀，真可惜！',Speaker=892065,}",
  [155046] = "{id=155046,Text='没事，我是很大度的。只要有心，你都可以再挑战！',Speaker=892065,}",
  [155047] = "{id=155047,Text='只要你愿意写欠条~落款名字沃雷特的那种~',Speaker=892065,Option='{我要试一次,10},{先等等……,11}',Emoji=13,}",
  [155048] = "{id=155048,Text='没问题！',Speaker=892065,Option='{石头,6},{剪刀,7},{布,8}',}",
  [155049] = "{id=155049,Text='没事，随时恭候！',Speaker=892065,}",
  [155050] = "{id=155050,Text='怎么样？成功了吗？',Speaker=892074,Option='诉说情况',}",
  [155051] = "{id=155051,Text='什么啊，就这么轻易地放他走了吗？',Speaker=892074,Option='还有欠条的事',}",
  [155052] = "{id=155052,Text='欠条！你居然打着这个算盘！！',Speaker=892074,}",
  [155053] = "{id=155053,Text='……嗯，算了，至少确实阻止依斯鲁得的居民被骗了。这点钱嘛，对我确实不是问题。',Speaker=892074,}",
  [155054] = "{id=155054,Text='年轻人，你可是做了件好事。我代替她谢谢你。',Speaker=892074,Option='{果然,0},{出现了，“她”,0}',}",
  [155055] = "{id=155055,Text='哎、哎呀，天气又潮湿起来了……',Speaker=892074,Emoji=10,}",
  [155056] = "{id=155056,Text='老头子我的关节又开始痛了！',Speaker=892074,Option='……',}",
  [155057] = "{id=155057,Text='不行了不行了……我必须得走了，再见年轻人！',Speaker=892074,}",
  [155058] = "{id=155058,Text='哟！年轻人……',Speaker=892074,Option='{“又见面啦”,0},{“今天又有事”,0}',}",
  [155059] = "{id=155059,Text='哎呀……哎呀，年轻人记忆力真是好啊。',Speaker=892074,Option='打哈哈是没用的',Emoji=18,}",
  [155060] = "{id=155060,Text='那就直接说正事。姜饼城的雪人公园出了点问题。麻烦你去一趟，解决一下。',Speaker=892074,Option='……',}",
  [155061] = "{id=155061,Text='你看，很简单吧。',Speaker=892074,Option='{老爷子,0},{这就太过分了,0}',}",
  [155062] = "{id=155062,Text='不不不，我没打算欺负你。',Speaker=892074,}",
  [155063] = "{id=155063,Text='完成这个任务后，我会告诉你我的目的，这样可以吗？',Speaker=892074,Option='你说呢？',}",
  [155064] = "{id=155064,Text='哎呀，别生气啦。说到底这件事和你也不是完全没关系的。',Speaker=892074,}",
  [155065] = "{id=155065,Text='去姜饼城吧，回来我就和你说实话！',Speaker=892074,Option='{姑且相信你,3},{没空,2}',}",
  [155066] = "{id=155066,Text='嗯嗯嗯，相信我相信我！',Speaker=892074,Option='前往姜饼城',}",
  [155067] = "{id=155067,Text='别这样呀……',Speaker=892074,}",
  [155068] = "{id=155068,Text='到底怎么办呢……',Speaker=892066,Option='{你好,0},{需要帮忙吗,0}',}",
  [155069] = "{id=155069,Text='啊，是冒险者！我想拜托你一件事！',Speaker=892066,}",
  [155070] = "{id=155070,Text='我前几天和朋友来这里堆雪人，结果把妈妈给我的娃娃弄丢了……',Speaker=892066,}",
  [155071] = "{id=155071,Text='你能帮我找回来吗？',Speaker=892066,Option='包在我身上',}",
  [155072] = "{id=155072,Text='谢谢！',Speaker=892066,}",
  [155073] = "{id=155073,Text='找到了吗？可以给我吗？',Speaker=892066,Option='{冰冷的甜甜圈,4},{空酒瓶,4},{陈旧的娃娃,5}',}",
  [155074] = "{id=155074,Text='啊！我的娃娃！',Speaker=892066,Emoji=2,}",
  [155075] = "{id=155075,Text='谢谢你能把它找回来！我要回家把它清洗干净！',Speaker=892066,Option='{加油哦,0},{别再丢了,0}',}",
  [155076] = "{id=155076,Text='嗯！',Speaker=892066,}",
  [155077] = "{id=155077,Text='呃？这不是我要的东西……',Speaker=892066,}",
  [155078] = "{id=155078,Text='沃雷特先生！请不要再这样了！',Speaker=892076,}",
  [155079] = "{id=155079,Text='伊、伊丽莎！',Speaker=892074,Emoji=7,}",
  [155080] = "{id=155080,Text='我说为什么这几天接到的委托都莫名其妙地被解决了！',Speaker=892076,}",
  [155081] = "{id=155081,Text='原来是您在背后捣的鬼！',Speaker=892076,}",
  [155082] = "{id=155082,Text='捣鬼！我明明是——',Speaker=892074,}",
  [155083] = "{id=155083,Text='我决定接受委托，不是因为可以得到报酬，是我自己真的喜欢帮助别人！',Speaker=892076,}",
  [155084] = "{id=155084,Text='您这样的“帮助”，让我的冒险变得没有意义了！',Speaker=892076,}",
  [155085] = "{id=155085,Text='………………',Speaker=892074,}",
  [155086] = "{id=155086,Text='总而言之，请不要再干涉我了！',Speaker=892076,Emoji=12,}",
  [155087] = "{id=155087,Text='………………',Speaker=892074,Option='{原来如此,0},{我早该想到,0}',}",
  [155088] = "{id=155088,Text='哇！你、你什么时候……',Speaker=892074,Option='从一开始哦',Emoji=7,}",
  [155089] = "{id=155089,Text='唉……我……是不是被讨厌了啊。',Speaker=892074,}",
  [155090] = "{id=155090,Text='我只是想帮帮尼娅的外孙女而已嘛。',Speaker=892074,Option='她不喜欢这样',}",
  [155091] = "{id=155091,Text='我……道歉有用吗？',Speaker=892074,Option='……',}",
  [155092] = "{id=155092,Text='唉，让我想想……',Speaker=892074,}",
  [155093] = "{id=155093,Text='如果我想出了方法，你会帮我吧？',Speaker=892074,Option='{是的,0},{这个嘛,0}',}",
  [155094] = "{id=155094,Text='拜托了！我只能靠你！让我再想想！',Speaker=892074,}",
  [155095] = "{id=155095,Text='哟！年轻人，又见面啦。',Speaker=892074,Option='伊丽莎？',}",
  [155096] = "{id=155096,Text='啊，你好！我有事拜托你！',Speaker=892076,Option='我？',}",
  [155097] = "{id=155097,Text='嗯哼。其实就是你之前的那个几个委托，根本没做好嘛。',Speaker=892074,Option='什么？',}",
  [155098] = "{id=155098,Text='沃雷特先生，请不要用那种会引起歧义的说法。',Speaker=892076,Emoji=40,}",
  [155099] = "{id=155099,Text='其实，大教堂的主教又紧急传来了委托。',Speaker=892076,Option='{怎么回事,0},{难道又有可疑人,0}',}",
  [155100] = "{id=155100,Text='具体情况还是得去问主教。这次，能请你和我一起完成委托吗？',Speaker=892076,Option='{好吧,3},{与我无关,2}',}",
  [155101] = "{id=155101,Text='那就说定了！快去大教堂吧！',Speaker=892076,Emoji=20,}",
  [155102] = "{id=155102,Text='你说什么呢！怎么可以拒绝伊丽莎！',Speaker=892074,Option='不想被你这么说',}",
  [155103] = "{id=155103,Text='（小声）你如果帮助伊丽莎的话，会拿到一亿zeny的报酬，怎么样？',Speaker=892074,Option='{……,0},{……爸爸！,0}',Emoji=13,}",
  [155104] = "{id=155104,Text='那就说定了！快去大教堂吧！',Speaker=892074,}",
  [155105] = "{id=155105,Text='哦哦，你们来了！',Speaker=892064,Option='发生什么事了',}",
  [155106] = "{id=155106,Text='说来真是不好意思，上次虽然已经确认了可疑者……',Speaker=892064,Emoji=15,}",
  [155107] = "{id=155107,Text='但我私下和她交流时，她却死活不承认，说我没有证据。',Speaker=892064,Option='脸皮真厚',}",
  [155108] = "{id=155108,Text='我虽然很生气，但不得不承认，我确实没有直接的证据说明她的身份有问题。',Speaker=892064,}",
  [155109] = "{id=155109,Text='您的意思是，如果有证据的话就可以合理地把她开除了？',Speaker=892076,}",
  [155110] = "{id=155110,Text='理论上是这样，但证据又从哪儿来呢……',Speaker=892064,Option='{我倒是有个办法,0},{需要伊丽莎协助,0}',}",
  [155111] = "{id=155111,Text='嗯？',Speaker=892076,Option='使用照相机',}",
  [155112] = "{id=155112,Text='原来如此！就像上次一样，在对方走神时把照片拍下来！',Speaker=892064,Emoji=11,}",
  [155113] = "{id=155113,Text='这下她就不能抵赖了！',Speaker=892064,Option='{以防万一,0},{让伊丽莎接近她,0}',}",
  [155114] = "{id=155114,Text='啊……对！对方没见过我，应该不会有戒心！',Speaker=892076,Option='就是这样',}",
  [155115] = "{id=155115,Text='我明白了！拍照的事交给我！',Speaker=892076,}",
  [155116] = "{id=155116,Text='那么——',Speaker=892064,}",
  [155117] = "{id=155117,Text='——当海姆达尔挥起了手中的宝剑——',Speaker=892064,}",
  [155118] = "{id=155118,Text='——洛基的失败已被注定——',Speaker=892064,}",
  [155119] = "{id=155119,Text='成功了吗？',Speaker=892064,Option='是的',}",
  [155120] = "{id=155120,Text='谢谢你们了，这件事终于结束了。',Speaker=892064,}",
  [155121] = "{id=155121,Text='嘿嘿，不客气！',Speaker=892076,}",
  [155122] = "{id=155122,Text='我们先回去吧！明天再在西门碰头！',Speaker=892076,}",
  [155123] = "{id=155123,Text='早上好！',Speaker=892076,Option='早上好',}",
  [155124] = "{id=155124,Text='我说，年轻人啊，多余的仁慈有时候也会成为傲慢的愚蠢。',Speaker=892074,Option='{嗯？,0},{什么意思,0}',}",
  [155125] = "{id=155125,Text='之前在依斯鲁得猜拳骗钱的那个人。',Speaker=892074,}",
  [155126] = "{id=155126,Text='——他又出现在普隆德拉南门啦！',Speaker=892074,}",
  [155127] = "{id=155127,Text='靠着出千到处捞钱。骗子果然是世界上脸皮最厚的人，商人的脸皮都没他厚。',Speaker=892074,Option='{……,0},{真是绝妙的自嘲,0}',}",
  [155128] = "{id=155128,Text='哎呀在这里叨唠也没用啊。我们快去南门抓住他吧！',Speaker=892076,Option='好好',}",
  [155129] = "{id=155129,Text='嚯！来挑战一下千倍奖金吗！只要在猜拳中胜过我，就能赚大钱哦！',Speaker=892065,Option='又是你',}",
  [155130] = "{id=155130,Text='呃，又是你。',Speaker=892065,Emoji=1,}",
  [155131] = "{id=155131,Text='哼，怎么，这次又要来挑战我的实力吗？',Speaker=892065,Option='{你可真是,0},{不撞南墙不回头,0}',}",
  [155132] = "{id=155132,Text='干什么！我只是老老实实地和人猜拳而已！你能拿我怎么样！',Speaker=892065,}",
  [155133] = "{id=155133,Text='不要以为搬出黄金眼的名头我就会怕！',Speaker=892065,}",
  [155134] = "{id=155134,Text='脸皮是真的很厚呢。',Speaker=892076,Emoji=12,}",
  [155135] = "{id=155135,Text='（小声）我们还是使用昨天的战术吧。',Speaker=892076,Option='{明白了,0},{那么还是我来,0}',}",
  [155136] = "{id=155136,Text='嗯！',Speaker=892076,}",
  [155137] = "{id=155137,Text='嘀嘀咕咕说些什么！',Speaker=892065,Option='{没什么,0},{我想再来挑战你,0}',}",
  [155138] = "{id=155138,Text='哼！来就来！我不怕你！',Speaker=892065,Option='{石头,2},{剪刀,3},{布,4}',}",
  [155139] = "{id=155139,Text='第、第二次……我竟然……',Speaker=892065,Option='{很遗憾,0},{没有第三次了,0}',}",
  [155140] = "{id=155140,Text='啊？',Speaker=892065,}",
  [155141] = "{id=155141,Text='嘿嘿嘿，果然……',Speaker=892065,Option='{不要得意太早,0},{我本来就不想赢,0}',}",
  [155142] = "{id=155142,Text='什么意思？这胸有成竹的笑容……',Speaker=892065,}",
  [155143] = "{id=155143,Text='因为，你刚才猜拳时出千的动作，我可是都拍下来了。',Speaker=892076,}",
  [155144] = "{id=155144,Text='什！什么……出千……',Speaker=892065,Emoji=7,}",
  [155145] = "{id=155145,Text='抵赖是没用的。我马上就把照片提交给骑士团和冒险家公会。',Speaker=892076,}",
  [155146] = "{id=155146,Text='想来你以后不管去哪儿都会被盯着吧……',Speaker=892076,}",
  [155147] = "{id=155147,Text='不！我求求你们！我不要被追着打！',Speaker=892065,}",
  [155148] = "{id=155148,Text='那你就洗心革面好好做人吧！不然的话，把柄一直都在我手里。',Speaker=892076,}",
  [155149] = "{id=155149,Text='我、我明白了！',Speaker=892065,}",
  [155150] = "{id=155150,Text='又解决了一件事情！太好啦！',Speaker=892076,}",
  [155151] = "{id=155151,Text='谢谢你前辈！明天我们再见！',Speaker=892076,}",
  [155152] = "{id=155152,Text='前辈！今天我们去姜饼城！',Speaker=892076,Option='{所以,0},{又是雪人,0}',}",
  [155153] = "{id=155153,Text='咳，这叫世事无常……',Speaker=892074,}",
  [155154] = "{id=155154,Text='嗯？你们在说什么？',Speaker=892076,}",
  [155155] = "{id=155155,Text='不管是什么样的委托，都要充满干劲地完美完成才对！',Speaker=892076,Option='{确实,0},{理论上是这样,0}',}",
  [155156] = "{id=155156,Text='好啦，先别说大道理，我们去姜饼城吧！',Speaker=892076,Option='也是',}",
  [155158] = "{id=155158,Text='上次是我和米迪一起堆的雪人……',Speaker=892066,}",
  [155159] = "{id=155159,Text='结果她说，她家的茶壶也丢了。',Speaker=892066,Emoji=10,}",
  [155160] = "{id=155160,Text='我明白了。你不用再说了。',Speaker=892076,}",
  [155161] = "{id=155161,Text='总之我们把茶壶找回来就好，对吧？',Speaker=892076,}",
  [155162] = "{id=155162,Text='嗯！',Speaker=892066,Option='回答得真干脆',}",
  [155163] = "{id=155163,Text='好吧，好吧。这也是工作。',Speaker=892076,}",
  [155164] = "{id=155164,Text='稍等，我们很快就给你找回来！',Speaker=892076,}",
  [155165] = "{id=155165,Text='找到了吗？找到了吗？',Speaker=892066,Option='{啃过的骨头,2},{发霉的面包,2},{精致的神灯,3}',}",
  [155166] = "{id=155166,Text='对对！就是这个！',Speaker=892066,}",
  [155167] = "{id=155167,Text='呃……你管这个……叫茶壶吗？',Speaker=892076,Emoji=10,}",
  [155168] = "{id=155168,Text='难道这不是茶壶吗？',Speaker=892066,}",
  [155169] = "{id=155169,Text='这可真难回答……好像说是茶壶也没差……',Speaker=892076,}",
  [155170] = "{id=155170,Text='不管这是什么，米迪都会开心的！',Speaker=892066,}",
  [155171] = "{id=155171,Text='谢谢你们！我先还给米迪去！',Speaker=892066,}",
  [155172] = "{id=155172,Text='不是的！这种东西米迪不会要的！',Speaker=892066,}",
  [155173] = "{id=155173,Text='呼……这下终于算是全部完成了吧。',Speaker=892076,}",
  [155174] = "{id=155174,Text='没想到冒险者的工作也没那么轻松呢……',Speaker=892076,Option='{确实如此,0}{偶尔要拯救世界,0}',}",
  [155175] = "{id=155175,Text='哈哈哈哈，拯救世界也太夸张了吧！',Speaker=892076,}",
  [155176] = "{id=155176,Text='不管怎样……很感谢你。',Speaker=892076,}",
  [155177] = "{id=155177,Text='以后我们或许还会有缘再见吧！',Speaker=892076,Option='希望如此',}",
  [155178] = "{id=155178,Text='我会心怀希望的！再见！',Speaker=892076,}",
  [155179] = "{id=155179,Text='（回西门向沃雷特汇报一声）',Option='出发',}",
  [155180] = "{id=155180,Text='（告诉沃雷特已经完成了委托）',Option='前往西门',}",
  [155182] = "{id=155182,Text='是谁！竟敢打扰吾的美梦！',Speaker=892083,}",
  [155183] = "{id=155183,Text='一个住在壶里的东西……居然说话了？！',}",
  [155184] = "{id=155184,Text='真是无知的蝼蚁！吾乃伟大的魔王，跪拜吧！祈祷吧！期待吾的怜悯不会现在就把你吃掉！',Speaker=892083,Option='{请求原谅,2}{毫不畏惧,3}',}",
  [155185] = "{id=155185,Text='没错，这就对了，现在向吾臣服吧~',Speaker=892083,}",
  [155186] = "{id=155186,Text='不过在这壶里吾待得太久了，真的无聊啊，与吾来一场猜谜游戏吧~猜对的话，吾会给予你无上奖励！但是如果答错……嘿嘿嘿~',Speaker=892083,}",
  [155187] = "{id=155187,Text='可恶你这嚣张的蝼蚁！要不是被一个该死的勇者封印在这壶里，吾现在就把你吃掉！',Speaker=892083,}",
  [155188] = "{id=155188,Text='如果想要获得吾的原谅就来一场猜谜游戏吧~',Speaker=892083,}",
  [155189] = "{id=155189,Text='猜对的话，吾不但会原谅你的无理，还会给予你无上奖励！但是如果答错……嘿嘿嘿~',Speaker=892083,}",
  [155190] = "{id=155190,Text='很好，那么我要开始出题了：',Speaker=892083,}",
  [155191] = "{id=155191,Text='你这个懦夫，居然连一道小小的谜题都不敢面对吗！',Speaker=892083,}",
  [155192] = "{id=155192,Text='哼，你很不错，居然能答对吾的谜题，那么这是你应得的奖励，记得明天再来找吾，如果你还想要的话，嘿嘿嘿~',Speaker=892083,}",
  [155193] = "{id=155193,Text='哈哈哈哈哈哈，吾看你真是有做小丑的天赋，太好笑了，就连这么简单的谜题都能答错，给吾变成章鱼！',Speaker=892083,}",
  [155194] = "{id=155194,Text='啊，你看起来很好吃的样子，不过吾现在没法吃掉你真是太遗憾了。',Speaker=892083,}",
  [155195] = "{id=155195,Text='但是看在你给吾带来了这么点微不足道的快乐份上，稍稍给你点赏赐吧，记得明天再来找吾，如果你还想要奖励的话。',Speaker=892083,}",
  [155196] = "{id=155196,Text='他出生于自然，在林木中生长，他带领人族发展却又常常令他们绝望，',Speaker=892083,Option='{火焰,6},{哥布灵之王,7},{忽克连,8}',}",
  [155197] = "{id=155197,Text='四处游荡从不停歇，时而温柔时而愤怒，时而呢喃低语时而咆哮怒吼，能带来凉爽也会带来炎热。',Speaker=892083,Option='{风,6},{岩浆,7},{溜溜猴,8}',}",
  [155198] = "{id=155198,Text='每个主城都能看到她的身影，在你受伤的时候总会不遗余力的为你提供帮助，她是最美的音乐精灵。',Speaker=892083,Option='{艾莉儿,6},{吟游诗人,7},{五郎,8}',}",
  [155199] = "{id=155199,Text='我是水中的拳击手，我从不会感到孤单，只要有人前来冒犯，必将遭受我的重拳攻击。',Speaker=892083,Option='{螃蟹,6},{转转蛋,7},{狂暴野猫,8}',}",
  [155200] = "{id=155200,Text='古老国度的主神之一，已故之人的统治者，同时也是金字塔的主人。',Speaker=892083,Option='{俄赛里斯,6},{蛇女伊丝,7},{死灵,8}',}",
  [155201] = "{id=155201,Text='圆圆脑袋像波利，八条长腿如藤蔓，平生最爱钻瓶罐，东躲西藏无处见。',Speaker=892083,Option='{章鱼,6},{蜘蛛,7},{海葵,8}',}",
  [155202] = "{id=155202,Text='他是水中的游泳健将，他是远洋之船的心底噩梦，他是深海中的骑士。',Speaker=892083,Option='{剑鱼,6},{血腥骑士,7},{海神,8}',}",
  [155214] = "{id=155214,Text='真是的，为什么要找我帮这个忙嘛。',Speaker=892092,}",
  [155215] = "{id=155215,Text='我可是忙的很……',Speaker=892092,Option='{你好,0},{有什么烦恼吗,0}',}",
  [155216] = "{id=155216,Text='哦，是冒险者啊。正好有事要拜托你，现在有空吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892092,Option='{有空,4},{现在不行,3}',}",
  [155217] = "{id=155217,Text='嘁，那算了。',Speaker=892092,}",
  [155218] = "{id=155218,Text='那可太棒了。',Speaker=892092,Emoji=11,}",
  [155219] = "{id=155219,Text='喏，我有个学长，人挺好的，平时作业啊论文什么的都受他指教，欠了很多人情。',Speaker=892092,}",
  [155220] = "{id=155220,Text='现在这个学长找我，要我帮他追女孩子！',Speaker=892092,}",
  [155221] = "{id=155221,Text='虽然我是挺想帮他的，但这太难为人了，我要是这么有本事何至于到今天还单身……',Speaker=892092,Emoji=10,}",
  [155222] = "{id=155222,Text='所以，拜托你！',Speaker=892092,Option='{我可以试试,0},{但不一定成功,0}',}",
  [155223] = "{id=155223,Text='嗨，这种事哪有百分百成功的。反正试试就行了。',Speaker=892092,}",
  [155224] = "{id=155224,Text='那你先去见我的那位学长吧！他叫雅尼克，就在靠近南门的地方等着。',Speaker=892092,Option='好的',}",
  [155225] = "{id=155225,Text='谢啦！',Speaker=892092,}",
  [155226] = "{id=155226,Text='到底怎么做才好呢……',Speaker=892084,Option='请问是雅尼克吗？',}",
  [155227] = "{id=155227,Text='是的，请问你是……',Speaker=892084,Option='诺尔拜托我',}",
  [155228] = "{id=155228,Text='原来如此。',Speaker=892084,Option='能详细说一说吗',}",
  [155229] = "{id=155229,Text='嗯……让我整理一下思维。',Speaker=892084,Emoji=14,}",
  [155230] = "{id=155230,Text='开门见山吧。今年的新生典礼上，我遇见了蕾欧娜，对她一见钟情。',Speaker=892084,}",
  [155231] = "{id=155231,Text='她比我小四岁，是刚入学的新生。虽然时间很短，但她已经成为了学院里的话题人物，很多男生都想要追她。',Speaker=892084,Option='{那么,0},{告白就好了吧,0}',}",
  [155232] = "{id=155232,Text='……你没听懂我的话吗。她的追求者数不胜数……',Speaker=892084,}",
  [155233] = "{id=155233,Text='毫无经验的人冒冒失失直接去告白，你认为能有多少成功率？',Speaker=892084,Option='{毫无经验？,0},{你没恋爱过吗？,0}',}",
  [155234] = "{id=155234,Text='…………',Speaker=892084,}",
  [155235] = "{id=155235,Text='确实没有。',Speaker=892084,}",
  [155236] = "{id=155236,Text='被女生告白倒是有过几次，但当时的我毫无恋爱的想法。',Speaker=892084,}",
  [155237] = "{id=155237,Text='蕾欧娜……是唯一一个我发自内心想要进一步接近的女孩。',Speaker=892084,Option='我明白了',Emoji=38,}",
  [155238] = "{id=155238,Text='嗯，总之我的原则就是，要做就要做到最好。',Speaker=892084,}",
  [155239] = "{id=155239,Text='为了能够成功追求到蕾欧娜，我们必须详细策划，做出一个可靠的追求方案。',Speaker=892084,Option='{……,0},{真不愧是学霸,0}',}",
  [155240] = "{id=155240,Text='所以我想先拜托你，帮我收集一些可行的恋爱攻略，然后再作分析。',Speaker=892084,Option='包在我身上吧',}",
  [155241] = "{id=155241,Text='那我在这里等你。请你尽快收集完毕以后写成报告给我。',Speaker=892084,}",
  [155242] = "{id=155242,Text='什么事？',Speaker=892086,Option='关于恋爱攻略',}",
  [155243] = "{id=155243,Text='追女孩？这种事我可给不了你经验。',Speaker=892086,}",
  [155244] = "{id=155244,Text='毕竟我从来没追过，只有女孩追我的~',Speaker=892086,Emoji=5,}",
  [155245] = "{id=155245,Text='什么，追女孩的方法吗？',Speaker=892087,}",
  [155246] = "{id=155246,Text='只要有颗真挚的心，表白出来就好了吧？',Speaker=892087,Option='{这么简单吗,0},{如果对方不接受,0}',}",
  [155247] = "{id=155247,Text='不接受……那就多告白几次？',Speaker=892087,}",
  [155248] = "{id=155248,Text='（再问问别人吧）',}",
  [155249] = "{id=155249,Text='追女孩？哈哈，真是青春的烦恼。',Speaker=892088,}",
  [155250] = "{id=155250,Text='我建议你读一读《恋爱圣经》。',Speaker=892088,Option='{《恋爱圣经》？,0},{居然有这种书,0}',}",
  [155251] = "{id=155251,Text='毕竟和女孩子交往也是一门很重要的本事嘛。',Speaker=892088,}",
  [155252] = "{id=155252,Text='这本书里从理论到实践都有，你可以根据自己的情况对号入座。',Speaker=892088,Option='哪里能弄到这本书',}",
  [155253] = "{id=155253,Text='我这里有一本旧的，先给你吧。',Speaker=892088,Option='可以吗？',}",
  [155254] = "{id=155254,Text='哈哈哈，我留着它也没用了，不如给年轻人。',Speaker=892088,}",
  [155255] = "{id=155255,Text='报告写好了吗？',Speaker=892084,Option='{呃,0},{直接说可以吗,0}',}",
  [155256] = "{id=155256,Text='……也行吧。请简洁明了地说明重点。',Speaker=892084,Emoji=14,}",
  [155257] = "{id=155257,Text='《恋爱圣经》？这种可疑的书籍……',Speaker=892084,}",
  [155258] = "{id=155258,Text='…………',Speaker=892084,}",
  [155259] = "{id=155259,Text='出乎意料，还挺有条理的。还有各种参考案例……',Speaker=892084,}",
  [155260] = "{id=155260,Text='呃……',Speaker=892084,}",
  [155261] = "{id=155261,Text='这、这部分我还先用不到！',Speaker=892084,Emoji=18,}",
  [155262] = "{id=155262,Text='呼。',Speaker=892084,Option='{怎么样,0},{有帮助吗？,0}',}",
  [155263] = "{id=155263,Text='作为参考来说……还算有价值。',Speaker=892084,}",
  [155264] = "{id=155264,Text='今天先到这里吧。我需要回去好好研究一下，明天我们再确定攻略方案吧。',Speaker=892084,}",
  [155265] = "{id=155265,Text='请不要迟到。',Speaker=892084,}",
  [155266] = "{id=155266,Text='你迟到了。',Speaker=892084,Option='{啊？,0},{没有吧？！,0}',}",
  [155267] = "{id=155267,Text='迟到了一分三十秒。',Speaker=892084,}",
  [155268] = "{id=155268,Text='算了，先说重要的事。',Speaker=892084,}",
  [155269] = "{id=155269,Text='昨天我把《恋爱圣经》都读完了，根据具体的情况，我选出了最合适的方案。',Speaker=892084,Option='是什么？',}",
  [155270] = "{id=155270,Text='先攻略蕾欧娜的朋友。',Speaker=892084,Option='能不能解释一下',}",
  [155271] = "{id=155271,Text='攻略最需要的就是投其所好。但我们现在对蕾欧娜的审美喜好完全不了解。',Speaker=892084,}",
  [155272] = "{id=155272,Text='所以，先和蕾欧娜最好的朋友发展关系，从她那里得到我们所需的情报，再进行下一步。',Speaker=892084,}",
  [155273] = "{id=155273,Text='这是成功率最高的方法。',Speaker=892084,Option='{那么,0},{这个好朋友是？,0}',}",
  [155274] = "{id=155274,Text='这我也已经打听清楚了。',Speaker=892084,}",
  [155275] = "{id=155275,Text='蕾欧娜最好的朋友，是和她从小一起长大的拉比特。',Speaker=892084,}",
  [155276] = "{id=155276,Text='现在，我们就一起去见见她吧。',Speaker=892084,Option='好',}",
  [155277] = "{id=155277,Text='咦……我记得应该是够的呀……',Speaker=892089,}",
  [155278] = "{id=155278,Text='为什么会少呢……',Speaker=892089,Option='{你好,0},{是拉比特吗？,0}',}",
  [155279] = "{id=155279,Text='啊……你好。我是拉比特。请问你——',Speaker=892089,}",
  [155280] = "{id=155280,Text='雅尼克……学长。',Speaker=892089,}",
  [155281] = "{id=155281,Text='啊……拉比特，你好。你认识我？',Speaker=892084,}",
  [155282] = "{id=155282,Text='……',Speaker=892089,}",
  [155283] = "{id=155283,Text='连续三年夺得学院最高奖学金的学长，怎么会不认识呢。',Speaker=892089,Emoji=26,}",
  [155284] = "{id=155284,Text='那么，雅尼克学长，找我有什么事吗？',Speaker=892089,}",
  [155285] = "{id=155285,Text='呃……这个……',Speaker=892084,Option='想问问蕾欧娜的事',}",
  [155286] = "{id=155286,Text='（小声）你这也太直接了！',Speaker=892084,}",
  [155287] = "{id=155287,Text='我猜也是。',Speaker=892089,Emoji=17,}",
  [155288] = "{id=155288,Text='蕾欧娜太受欢迎了，很多人都来找过我，想要从我这里知道蕾欧娜的喜好。',Speaker=892089,Option='你告诉他们了吗',}",
  [155289] = "{id=155289,Text='请不要小看女孩子的友谊。',Speaker=892089,}",
  [155290] = "{id=155290,Text='我是不会做出让蕾欧娜困扰甚至哭泣的事情的。',Speaker=892089,}",
  [155291] = "{id=155291,Text='……',Speaker=892084,}",
  [155292] = "{id=155292,Text='不过……雅尼克学长，却是个例外。',Speaker=892089,}",
  [155293] = "{id=155293,Text='咦？',Speaker=892084,}",
  [155294] = "{id=155294,Text='如果学长愿意帮助我收集跳跳菇的话，我可以把蕾欧娜的事情告诉学长哟。',Speaker=892089,Emoji=5,}",
  [155295] = "{id=155295,Text='真、真的吗？',Speaker=892084,}",
  [155296] = "{id=155296,Text='因为我也很苦恼嘛。明天要和蕾欧娜出去野餐，我要准备三明治。',Speaker=892089,}",
  [155297] = "{id=155297,Text='明明应该是准备了足够的食材，突然发现跳跳菇还差一些……',Speaker=892089,}",
  [155298] = "{id=155298,Text='能帮我取来吧？',Speaker=892089,}",
  [155299] = "{id=155299,Text='没、没问题的！',Speaker=892084,Option='{我去吧,0},{你们等着就好,0}',}",
  [155300] = "{id=155300,Text='诶？可以吗？',Speaker=892084,Option='毕竟我熟练些',}",
  [155301] = "{id=155301,Text='嗯呐、嗯呐~我不介意的~',Speaker=892089,}",
  [155302] = "{id=155302,Text='那我就等着啦~',Speaker=892089,}",
  [155303] = "{id=155303,Text='诶，这就采好啦？',Speaker=892089,Option='{很简单,0},{数量够吗？,0}',}",
  [155304] = "{id=155304,Text='够了够了。真可靠呢~',Speaker=892089,}",
  [155305] = "{id=155305,Text='拉比特，关于蕾欧娜……',Speaker=892084,}",
  [155306] = "{id=155306,Text='嗯呐，嗯呐，请不要心急~',Speaker=892089,}",
  [155307] = "{id=155307,Text='蕾欧娜并不喜欢急性子的人。',Speaker=892089,}",
  [155308] = "{id=155308,Text='……嗯，我明白。其实我一直很沉稳的。',Speaker=892084,Emoji=40,}",
  [155309] = "{id=155309,Text='嘿嘿嘿。',Speaker=892089,}",
  [155310] = "{id=155310,Text='好吧，既然答应了学长，我就把蕾欧娜喜欢的类型告诉你吧！',Speaker=892089,}",
  [155311] = "{id=155311,Text='好的！',Speaker=892084,}",
  [155312] = "{id=155312,Text='嗯……',Speaker=892089,}",
  [155313] = "{id=155313,Text='唔……',Speaker=892089,}",
  [155314] = "{id=155314,Text='怎么了？为什么不说话？',Speaker=892084,}",
  [155315] = "{id=155315,Text='唔……',Speaker=892089,}",
  [155316] = "{id=155316,Text='太多了啦，一时难以归纳……',Speaker=892089,}",
  [155317] = "{id=155317,Text='啊，请不要误会，我是打算好好说的！',Speaker=892089,}",
  [155318] = "{id=155318,Text='只不过，果然还是好好思考以后慢慢写下来才比较完整！',Speaker=892089,}",
  [155319] = "{id=155319,Text='学长，你没那么急性子吧？我今天晚上写完然后交给你好吗？',Speaker=892089,}",
  [155320] = "{id=155320,Text='……啊……没事，我不急的。',Speaker=892084,}",
  [155321] = "{id=155321,Text='那就好那就好。',Speaker=892089,}",
  [155322] = "{id=155322,Text='毕竟蕾欧娜不喜欢急性子的人嘛。',Speaker=892089,Emoji=5,}",
  [155323] = "{id=155323,Text='那我就先回家写啦~拜拜~',Speaker=892089,}",
  [155324] = "{id=155324,Text='……',Speaker=892084,Option='{完全地,0},{被玩弄于掌心了,0}',}",
  [155325] = "{id=155325,Text='果然，你也这么觉得……',Speaker=892084,Option='太明显了',}",
  [155326] = "{id=155326,Text='但是，为什么呢？如果她不想告诉我们，直接拒绝就好了啊。',Speaker=892084,}",
  [155327] = "{id=155327,Text='既然答应了，为什么还要这么拖拖拉拉敷衍我们呢。',Speaker=892084,}",
  [155328] = "{id=155328,Text='女孩子真的好难懂啊。',Speaker=892084,Option='{请不要,0},{一棍子打死所有,0}',Emoji=25,}",
  [155329] = "{id=155329,Text='算了，想这些也没用。',Speaker=892084,}",
  [155330] = "{id=155330,Text='我们还是等拉比特兑现承诺吧。',Speaker=892084,}",
  [155331] = "{id=155331,Text='我想，她总不至于故意欺骗我们，让我们出丑吧。',Speaker=892084,Option='{不会吧,0},{说不定哦,0}',Emoji=10,}",
  [155332] = "{id=155332,Text='……我觉得应该不会。',Speaker=892084,}",
  [155333] = "{id=155333,Text='总之明天再继续吧。今天也辛苦你了。',Speaker=892084,}",
  [155334] = "{id=155334,Text='糟糕，这方面我完全不懂……',Speaker=892084,Option='{早上好,0},{今天我可没迟到,0}',}",
  [155335] = "{id=155335,Text='嗯？啊，你来了……',Speaker=892084,Option='{你怎么了,0},{拉比特的情报呢？,0}',}",
  [155336] = "{id=155336,Text='情报她倒是给了……',Speaker=892084,}",
  [155337] = "{id=155337,Text='就是实行起来……有点难度。',Speaker=892084,Option='让我看看',Emoji=15,}",
  [155338] = "{id=155338,Text='“蕾欧娜喜欢[c][ffff00]外表整洁精致[-][/c]的男生。”',}",
  [155339] = "{id=155339,Text='“蕾欧娜偏爱[c][ffff00]红色[-][/c]。”',}",
  [155340] = "{id=155340,Text='“蕾欧娜喜欢[c][ffff00]甜度不太高[-][/c]的甜品。”',}",
  [155341] = "{id=155341,Text='外表整洁精致的意思是，穿学校制服不行吗？',Speaker=892084,Option='{也许吧,0},{我觉得是,0}',}",
  [155342] = "{id=155342,Text='……这方面我可完全没有任何头绪。',Speaker=892084,}",
  [155343] = "{id=155343,Text='冒险者，你认识什么对穿衣打扮很有研究的人吗？',Speaker=892084,Option='{这个我也,0},{诺尔会知道吗？,0}',}",
  [155344] = "{id=155344,Text='啊，对！诺尔朋友多，可能会认识。',Speaker=892084,Option='那我先去问问他',}",
  [155345] = "{id=155345,Text='拜托了，我只能信赖你。',Speaker=892084,}",
  [155346] = "{id=155346,Text='我就在这等你，请尽快！',Speaker=892084,}",
  [155347] = "{id=155347,Text='嗯？哟，在帮雅尼克学长跑腿？',Speaker=892092,Option='{算是吧,0},{想问问你,0}',}",
  [155348] = "{id=155348,Text='对穿衣打扮有研究的人啊……',Speaker=892092,}",
  [155349] = "{id=155349,Text='啊，确实有一个！',Speaker=892092,Emoji=11,}",
  [155350] = "{id=155350,Text='她叫喀秋莎，是个挺腼腆的女孩子，平时一般在圣歌大教堂附近。',Speaker=892092,}",
  [155351] = "{id=155351,Text='去吧！',Speaker=892092,}",
  [155352] = "{id=155352,Text='哎呀，你是……',Speaker=892090,Option='{你好,0},{我是诺尔的朋友,0}',}",
  [155353] = "{id=155353,Text='有什么事呢。',Speaker=892090,Option='关于雅尼克',}",
  [155354] = "{id=155354,Text='…………',Speaker=892090,}",
  [155355] = "{id=155355,Text='我明白了……',Speaker=892090,}",
  [155356] = "{id=155356,Text='请……先带我去见那个男生吧。',Speaker=892090,Option='{太好了！,0},{谢谢你！,0}',}",
  [155357] = "{id=155357,Text='找到了吗？',Speaker=892084,Option='就是这位',}",
  [155358] = "{id=155358,Text='啊……你好！',Speaker=892084,Emoji=18,}",
  [155359] = "{id=155359,Text='让、让你抽空来为我的事烦心真是对不起……',Speaker=892084,}",
  [155360] = "{id=155360,Text='哎呀，没事……',Speaker=892090,}",
  [155361] = "{id=155361,Text='真是……懂礼貌的男生呢。',Speaker=892090,}",
  [155362] = "{id=155362,Text='过、过奖了……那么，关于我的形象问题，怎么改进比较好？',Speaker=892084,}",
  [155363] = "{id=155363,Text='嗯……',Speaker=892090,}",
  [155364] = "{id=155364,Text='你心仪的那个女孩……喜欢红色系对吧。',Speaker=892090,}",
  [155365] = "{id=155365,Text='是的。',Speaker=892084,}",
  [155366] = "{id=155366,Text='唔……我有了设计方案了……',Speaker=892090,}",
  [155367] = "{id=155367,Text='不过，需要材料。',Speaker=892090,Option='什么材料？',}",
  [155368] = "{id=155368,Text='是一种叫做以法兰的花。这种花是制红色染料最好的原料……',Speaker=892090,Option='那它生长在哪里？',}",
  [155369] = "{id=155369,Text='以法兰花……在普勒花园附近有……',Speaker=892090,Option='好，我这就去',}",
  [155370] = "{id=155370,Text='好的，这下就可以了……',Speaker=892090,}",
  [155371] = "{id=155371,Text='哦哦，可以制作衣服了对吗！',Speaker=892084,Option='{咦？,0},{你好像不一样了,0}',}",
  [155372] = "{id=155372,Text='也、也没有什么。',Speaker=892084,}",
  [155373] = "{id=155373,Text='只是趁你去采花的时候，喀秋莎小姐帮我整理了下头发和仪容……',Speaker=892084,Option='很棒呢',}",
  [155374] = "{id=155374,Text='只差衣服了……我现在就准备……',Speaker=892090,}",
  [155375] = "{id=155375,Text='成功了……！',Speaker=892090,}",
  [155376] = "{id=155376,Text='好了……拿去……',Speaker=892090,}",
  [155377] = "{id=155377,Text='这衣服比较贴身，你帮他穿上吧……',Speaker=892090,}",
  [155378] = "{id=155378,Text='那我先回去了……',Speaker=892090,}",
  [155379] = "{id=155379,Text='就是这件吗？',Speaker=892084,}",
  [155380] = "{id=155380,Text='……好精致，我从来没穿过这样的衣服。',Speaker=892084,}",
  [155381] = "{id=155381,Text='这个衣袖，还有衣摆……',Speaker=892084,}",
  [155382] = "{id=155382,Text='我真的穿得上吗……',Speaker=892084,Option='{相信喀秋莎,0},{试试看嘛,0}',}",
  [155383] = "{id=155383,Text='好、好的。',Speaker=892084,}",
  [155384] = "{id=155384,Text='我看起来怎么样？',Speaker=892091,Option='{非常棒,0},{判若两人,0}',}",
  [155385] = "{id=155385,Text='嘿嘿，是吗。那就好。',Speaker=892091,}",
  [155386] = "{id=155386,Text='不过……',Speaker=892091,}",
  [155387] = "{id=155387,Text='这衣服还真的有点紧。',Speaker=892091,Option='忍一下吧',Emoji=15,}",
  [155388] = "{id=155388,Text='嗯，我会努力适应的。',Speaker=892091,}",
  [155389] = "{id=155389,Text='今天谢谢你了。明天我们再准备蕾欧娜喜欢的甜点吧。',Speaker=892091,}",
  [155390] = "{id=155390,Text='早上好！',Speaker=892091,Option='{早上好,0},{你看起来真不错,0}',Emoji=9,}",
  [155391] = "{id=155391,Text='哈哈。改变一下形象，确实还挺新鲜的。',Speaker=892091,}",
  [155392] = "{id=155392,Text='甚至连心态都有了变化。',Speaker=892091,Option='比如更有勇气？',}",
  [155393] = "{id=155393,Text='嗯。所以，我昨晚已经邀请了蕾欧娜。',Speaker=892091,Option='{她怎么回复？,0},{我猜到她的答案,0}',}",
  [155394] = "{id=155394,Text='她说……她愿意接受邀请。',Speaker=892091,Option='{果然如此,0},{难怪你心情好,0}',Emoji=38,}",
  [155395] = "{id=155395,Text='我们已经约好明天在天津町碰面。',Speaker=892091,}",
  [155396] = "{id=155396,Text='如果顺利的话，我希望能在天津町的樱花树下对她告白。',Speaker=892091,Option='祝你成功',}",
  [155397] = "{id=155397,Text='谢谢。不过，我们还有一个需要准备的礼物。',Speaker=892091,}",
  [155398] = "{id=155398,Text='拉比特的笔记上写过，蕾欧娜喜欢那种甜度不太高的甜点。',Speaker=892091,Option='我明白了',}",
  [155399] = "{id=155399,Text='我也做了一下功课。普隆德拉最受好评的甜点就是黑森林蛋糕，正好符合蕾欧娜的要求。',Speaker=892091,}",
  [155400] = "{id=155400,Text='你能帮我买到这个蛋糕吗？',Speaker=892091,Option='小事一桩',}",
  [155401] = "{id=155401,Text='太好了。那请快去吧。',Speaker=892091,}",
  [155402] = "{id=155402,Text='据说黑森林蛋糕每天都是限量的……',Speaker=892091,Option='{稍等一会儿,3},{我现在就去,4}',}",
  [155403] = "{id=155403,Text='辛苦你了。',Speaker=892091,}",
  [155404] = "{id=155404,Text='……也好。等你有空了请务必尽快。',Speaker=892091,}",
  [155405] = "{id=155405,Text='普隆德拉最好吃的甜点不要错过！',Speaker=892094,Option='黑森林蛋糕',}",
  [155406] = "{id=155406,Text='哎呀，真可惜！',Speaker=892094,}",
  [155407] = "{id=155407,Text='黑森林蛋糕已经卖完啦~其他的点心需要吗？',Speaker=892094,Option='{真的没了吗？,0},{非得是黑森林不可,0}',}",
  [155408] = "{id=155408,Text='这可难为我了。黑森林蛋糕每天都是第一个卖光的，根本不可能剩下来。',Speaker=892094,}",
  [155409] = "{id=155409,Text='要不你明天再早点来买？',Speaker=892094,Option='明天就来不及了',}",
  [155410] = "{id=155410,Text='唔……',Speaker=892094,}",
  [155411] = "{id=155411,Text='这样吧，看客人这么急，我立刻现做一个也可以。',Speaker=892094,}",
  [155412] = "{id=155412,Text='但问题是，现在原料也不齐啊……',Speaker=892094,Option='{什么原料？,0},{我来想办法,0}',Emoji=19,}",
  [155413] = "{id=155413,Text='普通的原料我都有存货，主要是黑森林蛋糕使用特殊的蜂蜜。',Speaker=892094,}",
  [155414] = "{id=155414,Text='这种蜂蜜我都是从依斯鲁得订购的，现在一时半会儿也联系不到供货商……',Speaker=892094,Option='我去联系',}",
  [155415] = "{id=155415,Text='好吧。你去依斯鲁得找蜂蜜商人，如果有那种特殊的蜂蜜，我就能做出黑森林蛋糕。',Speaker=892094,Option='{好，我这就去,7},{待会儿过去,6}',}",
  [155416] = "{id=155416,Text='行嘞！',Speaker=892094,}",
  [155417] = "{id=155417,Text='行啊，反正我不急。',Speaker=892094,}",
  [155418] = "{id=155418,Text='哎哟，哎哟……疼死我了……',Speaker=892095,Option='我想要蜂蜜',Emoji=24,}",
  [155419] = "{id=155419,Text='什么？走开走开，我要关张了，不卖了！',Speaker=892095,Option='{怎么突然,0},{发生什么事了？,0}',}",
  [155420] = "{id=155420,Text='我受够了！受够了！天天被那群蜜蜂追着蛰！',Speaker=892095,}",
  [155421] = "{id=155421,Text='我卖的哪里是蜂蜜啊，根本是我的血！',Speaker=892095,}",
  [155422] = "{id=155422,Text='疼死了……',Speaker=892095,Option='那些蜂蜜在哪儿？',Emoji=24,}",
  [155423] = "{id=155423,Text='你问这个干嘛？',Speaker=892095,Option='我需要蜂蜜',}",
  [155424] = "{id=155424,Text='哼，不怕死的……',Speaker=892095,}",
  [155425] = "{id=155425,Text='那些可恶的虫子就在街道上游荡。你要是有本事取得蜂蜜，那就拿走吧。',Speaker=892095,}",
  [155426] = "{id=155426,Text='你还真拿到了啊！',Speaker=892094,Option='一言难尽',}",
  [155427] = "{id=155427,Text='嘿嘿，好。那我现在就做一个。你等等啊。',Speaker=892094,}",
  [155428] = "{id=155428,Text='完成啦！',Speaker=892094,}",
  [155429] = "{id=155429,Text='好了，这就是我今天做的最后一个蛋糕！',Speaker=892094,}",
  [155430] = "{id=155430,Text='拿去吧！',Speaker=892094,Option='谢谢',}",
  [155431] = "{id=155431,Text='嘿嘿，下次再来光顾啊！',Speaker=892094,}",
  [155432] = "{id=155432,Text='蛋糕拿到了吗？',Speaker=892091,}",
  [155433] = "{id=155433,Text='哇……你怎么这一脸疲惫的样子……',Speaker=892091,Option='{一言难尽,0},{蛋糕给你,0}',Emoji=1,}",
  [155434] = "{id=155434,Text='黑森林蛋糕！',Speaker=892091,}",
  [155435] = "{id=155435,Text='……谢谢。',Speaker=892091,}",
  [155436] = "{id=155436,Text='这样，明天的告白，我就更有信心了。',Speaker=892091,}",
  [155437] = "{id=155437,Text='你这样为我东跑西跑，真的非常感谢。',Speaker=892091,}",
  [155438] = "{id=155438,Text='如果可以的话……明天，能请你和我们一起去天津町吗？',Speaker=892091,Option='{这样好吗？,0},{当电灯泡吗？,0}',}",
  [155439] = "{id=155439,Text='我的意思是……我想把你作为朋友，介绍给我喜欢的女孩。',Speaker=892091,Option='我很荣幸',Emoji=38,}",
  [155440] = "{id=155440,Text='真的吗！那太好了。',Speaker=892091,}",
  [155441] = "{id=155441,Text='明天，我们直接在天津町那里见面，好吗？',Speaker=892091,}",
  [155442] = "{id=155442,Text='千万不要忘记！',Speaker=892091,Option='好',}",
  [155443] = "{id=155443,Text='那我就期待明天的到来了。',Speaker=892091,}",
  [155444] = "{id=155444,Text='呼~吸~',Speaker=892099,}",
  [155445] = "{id=155445,Text='放轻松、放轻松~',Speaker=892099,Option='{早啊,0},{紧张了？,0}',}",
  [155446] = "{id=155446,Text='太好了，你来了！',Speaker=892099,}",
  [155447] = "{id=155447,Text='我确实……有点紧张。',Speaker=892099,Option='{可以理解,0},{蕾欧娜呢？,0}',Emoji=15,}",
  [155448] = "{id=155448,Text='我们约好在杂货屋面前见面，只是我想先安定一下自己的心情，别搞砸了这个难得的机会。',Speaker=892099,}",
  [155449] = "{id=155449,Text='呃，我看起来怎么样？还行吗？',Speaker=892099,Option='{很帅气呢,0},{会迷倒蕾欧娜的,0}',}",
  [155450] = "{id=155450,Text='你这么一说，我反而更紧张了……',Speaker=892099,Option='{别多想,0},{快去吧,0}',Emoji=10,}",
  [155451] = "{id=155451,Text='嗯、嗯……不能让蕾欧娜等太久……',Speaker=892099,}",
  [155452] = "{id=155452,Text='那……我先去打个招呼了。',Speaker=892099,Option='{嗯,0},{我会随机应变,0}',}",
  [155453] = "{id=155453,Text='那拜托了！我去了！',Speaker=892091,}",
  [155454] = "{id=155454,Text='啊……早、早上好啊！蕾欧娜……小姐。',Speaker=892091,}",
  [155455] = "{id=155455,Text='早上好，雅尼克学长。',Speaker=892085,}",
  [155456] = "{id=155456,Text='谢谢你今天邀请我出来。',Speaker=892085,}",
  [155457] = "{id=155457,Text='……没、没什么。能够和蕾欧娜……小姐约会是我的荣幸。',Speaker=892091,}",
  [155458] = "{id=155458,Text='噗嗤……哈哈哈哈哈！',Speaker=892085,}",
  [155459] = "{id=155459,Text='怎、怎么了？我说错什么话了？',Speaker=892091,Emoji=18,}",
  [155460] = "{id=155460,Text='学长太紧张啦。叫我蕾欧娜就可以了，小姐什么的听起来太别扭了。',Speaker=892085,}",
  [155461] = "{id=155461,Text='哦、好……蕾……欧娜。',Speaker=892091,}",
  [155462] = "{id=155462,Text='嗯……我们该不会就这样在杂货店前站一天吧？既然来了天津町，到处逛逛好吗？',Speaker=892085,}",
  [155463] = "{id=155463,Text='没、没问题！',Speaker=892091,}",
  [155464] = "{id=155464,Text='（被对方掌握节奏了啊……）',}",
  [155465] = "{id=155465,Text='那我们走吧。',Speaker=892085,}",
  [155466] = "{id=155466,Text='学长知道吗，樱花是恋爱之花呢。',Speaker=892085,}",
  [155467] = "{id=155467,Text='开时绚烂，花期却非常短……正如恋爱的保鲜期也不会长久。',Speaker=892085,}",
  [155468] = "{id=155468,Text='呃……你不相信永恒的真爱吗？',Speaker=892091,Emoji=18,}",
  [155469] = "{id=155469,Text='……我并不相信有永恒的恋情。因为，我曾经那么喜欢学长你，如今真的和你站在樱花树下，反而没有那种心动的感觉了。',Speaker=892085,}",
  [155470] = "{id=155470,Text='咦？！你、你说什么……',Speaker=892091,}",
  [155471] = "{id=155471,Text='学长果然已经不记得我了。',Speaker=892085,}",
  [155472] = "{id=155472,Text='不过，三年前的我和现在差别也确实太大了。',Speaker=892085,Emoji=17,}",
  [155473] = "{id=155473,Text='三年前……？',Speaker=892091,}",
  [155474] = "{id=155474,Text='嘿嘿。我们去那棵樱花树下看看吧！',Speaker=892085,}",
  [155475] = "{id=155475,Text='等、等一下！你刚才说的……',Speaker=892091,}",
  [155476] = "{id=155476,Text='蕾欧娜！你说你喜欢我……？',Speaker=892091,}",
  [155477] = "{id=155477,Text='……是的。三年前，我曾经向学长告白过。',Speaker=892085,}",
  [155478] = "{id=155478,Text='那时候的我，又胆小，又内向，又不自信，简直可以说毫无优点。',Speaker=892085,}",
  [155479] = "{id=155479,Text='所以，被沉稳又聪明的学长拒绝了也是理所当然的。',Speaker=892085,}",
  [155480] = "{id=155480,Text='……但是，拉比特却很生气地和我说，我才不是毫无优点的女孩。',Speaker=892085,}",
  [155481] = "{id=155481,Text='拉比特……？',Speaker=892091,}",
  [155482] = "{id=155482,Text='学长也见过拉比特吧，是不是感觉到她当时的态度不太好？',Speaker=892085,}",
  [155483] = "{id=155483,Text='拉比特要我向学长传达“对不起”。她也知道这样不对，但就是忍不住有点生气。',Speaker=892085,}",
  [155484] = "{id=155484,Text='毕竟当时我被拒绝以后，确实自暴自弃了一段时间。',Speaker=892085,}",
  [155485] = "{id=155485,Text='……',Speaker=892091,}",
  [155486] = "{id=155486,Text='不过，也正是拉比特的鼓励，我才会从沮丧里走出来，为了成为“更好的我”而一直努力到现在。',Speaker=892085,Emoji=26,}",
  [155487] = "{id=155487,Text='我很开心学长终于注意到了我，开始喜欢我。',Speaker=892085,}",
  [155488] = "{id=155488,Text='但我不能欺骗你。我对学长已经没有那么心动了。',Speaker=892085,}",
  [155489] = "{id=155489,Text='所以学长，请不要把你的第一次恋爱浪费在我身上了。',Speaker=892085,}",
  [155490] = "{id=155490,Text='再见，学长。',Speaker=892085,}",
  [155491] = "{id=155491,Text='……………………',Speaker=892091,Option='{喂，喂？,0},{回过神了吗？,0}',}",
  [155492] = "{id=155492,Text='……啊，是你……',Speaker=892091,Option='你还好吧',}",
  [155493] = "{id=155493,Text='我不知道……脑子一团乱……',Speaker=892091,}",
  [155494] = "{id=155494,Text='好像清醒又好像迷糊着……不知道发生了什么事……',Speaker=892091,Option='醒醒神',}",
  [155495] = "{id=155495,Text='嗯、嗯，让我调整一下——',Speaker=892091,}",
  [155496] = "{id=155496,Text='我受够啦！这一团乱麻的少年忧伤！',Speaker=892097,}",
  [155497] = "{id=155497,Text='在天津町这个浪漫的恋爱之地发生的失恋！简直是对恋爱的亵渎！',Speaker=892097,}",
  [155498] = "{id=155498,Text='我现在就要替爱行道，消灭你们这种不解风情的臭小子！',Speaker=892097,}",
  [155499] = "{id=155499,Text='嘁！被甩一次两次都不算什么！',Speaker=892097,}",
  [155500] = "{id=155500,Text='爱情就是要经受挫折的！赶紧调整心态再去追逐才是正道！',Speaker=892097,}",
  [155501] = "{id=155501,Text='嘶……',Speaker=892091,Option='你还好吗？',}",
  [155502] = "{id=155502,Text='嗯……我没事。只是稍微擦破点皮。',Speaker=892091,}",
  [155503] = "{id=155503,Text='谢谢。今天幸好有你在。',Speaker=892091,}",
  [155504] = "{id=155504,Text='不然我可能就要被失恋的情绪打倒了。',Speaker=892091,Option='你不会的',}",
  [155505] = "{id=155505,Text='……我现在的心情，蕾欧娜也有过。但是她能在朋友的鼓励下重新出发，我觉得这很了不起。',Speaker=892091,}",
  [155506] = "{id=155506,Text='不过，我还是想告诉她，我喜欢她。',Speaker=892091,}",
  [155507] = "{id=155507,Text='即使她最终会拒绝我，这也不是浪费。',Speaker=892091,}",
  [155508] = "{id=155508,Text='这是我必须跨越的必经之路。',Speaker=892091,Option='{说得好,0},{需要我再陪你吗,0}',}",
  [155509] = "{id=155509,Text='谢谢你。但是不必了。',Speaker=892091,}",
  [155510] = "{id=155510,Text='恋爱本就是我自己的事，我应该独立完成才对。',Speaker=892091,}",
  [155511] = "{id=155511,Text='不过，我还是很高兴能交到你这样的朋友。',Speaker=892091,Option='我也是',}",
  [155512] = "{id=155512,Text='以后如果有什么事需要我帮忙，请不要踌躇，直接来找我吧。',Speaker=892091,Emoji=20,}",
  [155513] = "{id=155513,Text='那我先回普隆德拉了。再见。',Speaker=892091,}",
  [155514] = "{id=155514,Text='[c][ffff00]【猜谜游戏每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Speaker=892083,Option='{没问题,4}{还是算了,5}',}",
  [155528] = "{id=155528,Text='怎么办呢，马上节日就要到了……',Speaker=892102,Option='你好',}",
  [155529] = "{id=155529,Text='啊，是冒险者！你……能帮我一个忙吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892102,Option='{现在不太行,2},{没问题！,3}',}",
  [155530] = "{id=155530,Text='啊……哦……',Speaker=892102,}",
  [155531] = "{id=155531,Text='谢谢，谢谢你！其实是为了音乐情人节的事情……',Speaker=892102,Option='{音乐情人节？,0},{没听说过,0}',}",
  [155532] = "{id=155532,Text='哈哈，这个节日是最近才开始流行起来的，据说来源于某个遥远的城市，是所有相爱的人们的节日。',Speaker=892102,}",
  [155533] = "{id=155533,Text='其实来源什么的都无所谓，主要是想借这个理由和喜欢的人在一起啦。',Speaker=892102,Option='{我明白,0},{然后呢？,0}',}",
  [155534] = "{id=155534,Text='嗯……我女朋友非常喜欢炸鸡，所以我去拜托了炸鸡农场的K上校，请求他帮忙准备一份特殊的礼物。',Speaker=892102,}",
  [155535] = "{id=155535,Text='但是，炸鸡农场这几天出了意外的状况，K上校分身乏术，好像来不及在音乐情人节前完成我的委托了。',Speaker=892102,Emoji=24,}",
  [155536] = "{id=155536,Text='虽然临时准备普通的礼物也可以，但真的很不甘心。',Speaker=892102,Option='{明白啦,0},{我来试着解决,0}',}",
  [155537] = "{id=155537,Text='非常感谢！K上校就在普隆德拉北部的炸鸡农场哦。',Speaker=892102,Option='{我现在就去,2},{那我待会儿过去,3}',}",
  [155538] = "{id=155538,Text='那就拜托了！',Speaker=892102,}",
  [155539] = "{id=155539,Text='没问题！',Speaker=892102,}",
  [155540] = "{id=155540,Text='哦，冒险者，你好！',Speaker=807798,Option='{你好,0},{有关吉姆的事,0}',}",
  [155541] = "{id=155541,Text='啊，是那个青年啊……他确实委托我，要发明一种吃不胖的炸鸡。',Speaker=807798,}",
  [155542] = "{id=155542,Text='可惜我现在根本没办法做到。',Speaker=807798,Option='有什么问题吗？',}",
  [155543] = "{id=155543,Text='是这样，最近农场里的小鸡崽每天都会丢失……',Speaker=807798,Option='{咦,0},{这件事似乎曾经,0}',}",
  [155544] = "{id=155544,Text='你说的没错，这件事以前也发生过。这次，农场里留下了这样的留言。',Speaker=807798,}",
  [155545] = "{id=155545,Text='鸡崽有了，就差特辣花朵。去普隆德拉北部看看。',Option='传送到普隆德拉北部',}",
  [155546] = "{id=155546,Text='炸鸡什么的都快去死吧！——炸鸡怪盗',Speaker=892121,}",
  [155548] = "{id=155548,Text='恶魔的诱惑，不要再留在米德加尔特残害别人！——炸鸡怪盗',Speaker=892121,}",
  [155549] = "{id=155549,Text='差不多都是这样的留言。',Speaker=807798,Option='{……,0},{这个炸鸡怪盗,0}',}",
  [155550] = "{id=155550,Text='嗯，这个炸鸡怪盗，应该就是上一次偷窃小鸡的人吧。但不知道他这次为什么又来了……',Speaker=807798,Option='是摩根',}",
  [155551] = "{id=155551,Text='唉，其实我最急的还不是这个怪盗。马上就是音乐情人节了，我手头没有鸡崽，吉姆的委托也没法完成。',Speaker=807798,}",
  [155552] = "{id=155552,Text='年轻人，能拜托你帮我收集一些鸡崽和调味品吗？我想至少先让吉姆在音乐情人节前能拿到给他女友的礼物。',Speaker=807798,Option='{没问题,0},{包在我身上,0}',}",
  [155553] = "{id=155553,Text='一共需要[c][ffff00]5只小鸡崽[-][/c]和[c][ffff00]5份特辣花朵[-][/c]。鸡崽可以问普隆德拉的活鸡商人购买，花朵的话在农场北面采摘。',Speaker=807798,}",
  [155554] = "{id=155554,Text='都取得后记得尽快交给我。',Speaker=807798,Option='{那我现在就去,5},{让我准备一下,6}',}",
  [155555] = "{id=155555,Text='路上小心！',Speaker=807798,}",
  [155556] = "{id=155556,Text='好！',Speaker=807798,}",
  [155557] = "{id=155557,Text='刚孵出来的健康小鸡！皮脆肉嫩！不要错过！',Speaker=892105,Option='{你好,0},{我想要五只,0}',}",
  [155558] = "{id=155558,Text='哎呀！终于……',Speaker=892105,Emoji=24,}",
  [155559] = "{id=155559,Text='终于……有人肯要我的鸡崽了！',Speaker=892105,Option='嗯？',}",
  [155560] = "{id=155560,Text='普隆德拉怎么回事嘛！开了个炸鸡农场，就人人只吃K上校的炸鸡了！',Speaker=892105,}",
  [155561] = "{id=155561,Text='我的小鸡都没销路了……',Speaker=892105,}",
  [155562] = "{id=155562,Text='难道我的鸡崽就比不上炸鸡农场的吗！你说！',Speaker=892105,Option='{呃，这个,8},{当然是你的更好,9}',}",
  [155563] = "{id=155563,Text='又是这个表情！都不信我！',Speaker=892105,Emoji=12,}",
  [155564] = "{id=155564,Text='可恶！明明我的鸡崽也不差！',Speaker=892105,}",
  [155565] = "{id=155565,Text='五只是吧，送你送你，带回家自己做成炸鸡试试！',Speaker=892105,}",
  [155566] = "{id=155566,Text='你会回来求着我继续要的！哼！',Speaker=892105,}",
  [155567] = "{id=155567,Text='你可真是……',Speaker=892105,Emoji=27,}",
  [155568] = "{id=155568,Text='识货的人啊！',Speaker=892105,}",
  [155569] = "{id=155569,Text='我太激动了，终于遇到知音了……来来来，鸡崽送你。',Speaker=892105,}",
  [155570] = "{id=155570,Text='好吃的话再回来找我啊~',Speaker=892105,}",
  [155571] = "{id=155571,Text='不对不对，还差一点……',Speaker=807798,Option='K上校？',}",
  [155572] = "{id=155572,Text='哦……原来你来了啊。',Speaker=807798,Option='眼圈黑了呢',}",
  [155573] = "{id=155573,Text='哈哈，没事，只是熬了一夜有些累。毕竟必须赶在音乐情人节前把委托完成啊。',Speaker=807798,}",
  [155574] = "{id=155574,Text='嗯……还有关于炸鸡怪盗的事。',Speaker=807798,Option='{我明白,0},{我会阻止他,0}',}",
  [155575] = "{id=155575,Text='那就拜托你了。不过你打算怎么找呢？',Speaker=807798,Option='我认识他',}",
  [155576] = "{id=155576,Text='嗯，也对，上次也是你找到的……那次是拜托魔法师法里斯，这次也直接去找法里斯帮忙如何？',Speaker=807798,Option='好主意',}",
  [155577] = "{id=155577,Text='那你快去吧，我得留下来继续完成委托。',Speaker=807798,Option='{好的,2},{再等会儿,3}',}",
  [155578] = "{id=155578,Text='嗯，先蒸后炸……',Speaker=807798,}",
  [155579] = "{id=155579,Text='好了，开始做实验吧。',Speaker=892107,Option='{你好,0},{又来麻烦了,0}',}",
  [155580] = "{id=155580,Text='嗯？你是……',Speaker=892107,Option='关于炸鸡怪盗',}",
  [155581] = "{id=155581,Text='怎么又是炸鸡怪盗？炸鸡怪盗和我到底有什么关系？能不能别老是用这种无聊事来烦我？',Speaker=892107,Emoji=12,}",
  [155582] = "{id=155582,Text='上次炸鸡怪盗在普拉德隆北部，这次你也去那个地方找找不行吗？',Speaker=892107,}",
  [155583] = "{id=155583,Text='总之别折腾我就行了！',Speaker=892107,Option='{我去找找看,4},{你确定？,5}',}",
  [155584] = "{id=155584,Text='快去快去别烦我！',Speaker=892107,}",
  [155585] = "{id=155585,Text='确定不确定你自己去看一眼不就行了吗！',Speaker=892107,}",
  [155586] = "{id=155586,Text='噫！',Speaker=892109,Emoji=7,}",
  [155587] = "{id=155587,Text='怎、怎么又撞见你！',Speaker=892109,}",
  [155588] = "{id=155588,Text='难不成你跟踪我们？',Speaker=892109,Option='{虽然不是,0},{也差不多,0}',}",
  [155589] = "{id=155589,Text='什、什什什么——',Speaker=892109,}",
  [155590] = "{id=155590,Text='跟踪什么的真是太卑鄙啦！',Speaker=892109,Option='{摩根在哪里,0},{有事找他,0}',}",
  [155591] = "{id=155591,Text='我才不会告诉你！我也是有自尊的！不能你问什么我就回答！',Speaker=892109,Option='{那么,0},{揍一顿就好了,0}',}",
  [155592] = "{id=155592,Text='呜呜呜呜呜……为什么受伤的总是我……',Speaker=892109,}",
  [155593] = "{id=155593,Text='哪个家伙又欺负了我的宠物！',Speaker=892101,Option='是我',}",
  [155594] = "{id=155594,Text='你？！',Speaker=892101,Emoji=1,}",
  [155595] = "{id=155595,Text='你发什么疯，欺负它上瘾了吗？',Speaker=892101,Option='{你才是,0},{偷鸡崽上瘾了吗,0}',}",
  [155596] = "{id=155596,Text='什么，偷什么鸡崽？',Speaker=892101,Option='炸鸡农场',}",
  [155597] = "{id=155597,Text='炸鸡农场？',Speaker=892101,}",
  [155598] = "{id=155598,Text='上次那个误会不是已经解开了吗，你有完没完？',Speaker=892101,Option='{不是上次,0},{是这次,0}',Emoji=12,}",
  [155599] = "{id=155599,Text='什么这次？我什么时候又偷过鸡崽？',Speaker=892101,Option='{……,0},{难道你没有？,0}',}",
  [155600] = "{id=155600,Text='这段时间我忙着收集洛阳风格的女装，忙得要死，哪里有空去炸鸡农场！',Speaker=892101,}",
  [155601] = "{id=155601,Text='你凭什么说是我干的？',Speaker=892101,Option='{可是,0},{炸鸡怪盗的留言,0}',}",
  [155602] = "{id=155602,Text='胡说八道，我从来没写过这种话！',Speaker=892101,}",
  [155603] = "{id=155603,Text='再说这写的都是什么啊，炸鸡这么美味的东西，我怎么可能想要消灭它呢！当然是多多益善！',Speaker=892101,Option='{这个理由,0},{倒是很有说服力,0}',}",
  [155604] = "{id=155604,Text='……这也太奇怪了，是谁冒充炸鸡怪盗做这种事，不搞清楚，黑锅就莫名其妙给我背。',Speaker=892101,}",
  [155605] = "{id=155605,Text='喂，你！是打算找出真正的犯人吧！',Speaker=892101,Option='没错',}",
  [155606] = "{id=155606,Text='好，目标一致，那就暂时联手！',Speaker=892101,}",
  [155607] = "{id=155607,Text='我回去准备些东西，明天我们在炸鸡农场，一定要把这个假借怪盗名义的混蛋抓住！',Speaker=892101,}",
  [155608] = "{id=155608,Text='你来了，太好了。快点快点。',Speaker=892101,Option='怎么这么急',}",
  [155609] = "{id=155609,Text='能不急吗！',Speaker=892101,}",
  [155610] = "{id=155610,Text='这可是事关我名誉清白的超级事件！如果不能解决，我连觉都睡不好！',Speaker=892101,Option='{好好好,0},{要怎么做,0}',Emoji=12,}",
  [155611] = "{id=155611,Text='经过一个晚上的思考，我已经有了万全的方案！',Speaker=892101,}",
  [155612] = "{id=155612,Text='犯人既然每天都会来炸鸡农场偷鸡崽，那我们就在他的必经之路上设下埋伏。',Speaker=892101,}",
  [155613] = "{id=155613,Text='只要对方一进入农场，就一定会掉入陷阱中！',Speaker=892101,Option='这么简单',}",
  [155614] = "{id=155614,Text='哼，那是因为有我这个最强魔法师的摩根在！',Speaker=892101,}",
  [155615] = "{id=155615,Text='所以你的任务就是设置好陷阱。',Speaker=892101,Option='你不做吗',}",
  [155616] = "{id=155616,Text='我又出脑力又出魔法还不够吗！这种只需要动动身体的简单活你做不就好啦。',Speaker=892101,}",
  [155617] = "{id=155617,Text='我可要养精蓄锐……',Speaker=892101,Option='……',Emoji=36,}",
  [155618] = "{id=155618,Text='呼……',Speaker=892101,}",
  [155619] = "{id=155619,Text='嗅、嗅……好香……',Speaker=892101,Option='{喂,0},{拍肩,0}',}",
  [155620] = "{id=155620,Text='谁、谁啊！',Speaker=892101,Option='{陷阱设置完毕,0},{你在做什么,0}',Emoji=7,}",
  [155621] = "{id=155621,Text='哦，是你啊……没啊，我什么都没干！',Speaker=892101,Option='{……,0},{怀疑的眼神,0}',}",
  [155622] = "{id=155622,Text='好吧好吧，我只是闻到了特~别香的炸鸡味道，有点着迷嘛……',Speaker=892101,}",
  [155623] = "{id=155623,Text='……你说，那是不是K上校的新作品啊？',Speaker=892101,Option='应该是',}",
  [155624] = "{id=155624,Text='果然！我想先吃一口——',Speaker=892101,Option='{还没完成,0},{你的名誉问题呢,0}',}",
  [155625] = "{id=155625,Text='哎……呀……这个……行吧大事为重！等抓住犯人我可要好好大吃一顿！',Speaker=892101,Emoji=37,}",
  [155626] = "{id=155626,Text='陷阱既然设置好了，我们找个合适的地方隐藏起来，只等对方落网就好。',Speaker=892101,}",
  [155627] = "{id=155627,Text='农场附近有两条必经之路，我们就一边埋伏一个好了！',Speaker=892101,}",
  [155630] = "{id=155630,Text='我就偷偷地……尝一口~',Speaker=892101,Option='人呢！',}",
  [155631] = "{id=155631,Text='什、什、什么人？',Speaker=892101,Option='{刚刚的,0},{偷鸡崽的犯人！,0}',}",
  [155632] = "{id=155632,Text='什么？犯人来了吗？',Speaker=892101,Option='摩——根——',}",
  [155633] = "{id=155633,Text='哎呀、哎呀……K上校的炸鸡太香了嘛，我一时走神就没注意……',Speaker=892101,}",
  [155634] = "{id=155634,Text='别生气别生气，要不你也来一块？',Speaker=892101,Option='{……,0},{已经气饱了,0}',}",
  [155635] = "{id=155635,Text='真的吗？那我可全吃了……',Speaker=892101,}",
  [155636] = "{id=155636,Text='哎呀真好吃~我先带回去吃了啊~',Speaker=892101,Emoji=17,}",
  [155637] = "{id=155637,Text='冒险者，你好！',Speaker=807798,}",
  [155638] = "{id=155638,Text='你的面色很沮丧啊……',Speaker=807798,Option='{对不起,0},{没有抓到犯人,0}',}",
  [155639] = "{id=155639,Text='……嗯，原来如此。',Speaker=807798,}",
  [155640] = "{id=155640,Text='年轻人，不用这么自责。昨晚这一闹，对方应该也不敢再来农场偷窃了。',Speaker=807798,}",
  [155641] = "{id=155641,Text='能保护好鸡崽，这都是你的功劳。',Speaker=807798,Option='谢谢……',}",
  [155642] = "{id=155642,Text='而且，我也终于赶在音乐情人节之前把魔法瘦身炸鸡完成了！',Speaker=807798,Emoji=11,}",
  [155643] = "{id=155643,Text='这下吉姆也不用担心礼物的事了。',Speaker=807798,Option='太好了',}",
  [155644] = "{id=155644,Text='冒险者，你能把魔法瘦身炸鸡送给吉姆吗？我熬了两夜，实在有点吃不消……',Speaker=807798,Option='{交给我吧,2},{现在有点没空,3}',}",
  [155645] = "{id=155645,Text='拜托你了。',Speaker=807798,}",
  [155646] = "{id=155646,Text='没关系，我可以等。',Speaker=807798,}",
  [155647] = "{id=155647,Text='啊，冒险者！',Speaker=892102,Option='你要的东西',}",
  [155648] = "{id=155648,Text='太好了~这样我终于能给麦琪她想要的礼物了。',Speaker=892102,}",
  [155649] = "{id=155649,Text='能让我看看吗？',Speaker=892102,}",
  [155650] = "{id=155650,Text='谢谢！嗯……这炸鸡……',Speaker=892102,Option='怎么了',}",
  [155651] = "{id=155651,Text='不，我在想，既然是礼物，多少还是要包装得精美一点才像样……',Speaker=892102,Option='有道理',}",
  [155652] = "{id=155652,Text='是吧！',Speaker=892102,}",
  [155653] = "{id=155653,Text='我知道有个人特别擅长这样的事！不过我还需要时间整理一下仪容……你能不能代替我去一下？',Speaker=892102,Option='好',}",
  [155654] = "{id=155654,Text='好！谢谢！',Speaker=892102,}",
  [155655] = "{id=155655,Text='哎……你是……',Speaker=892110,Option='{拜托你,0},{把炸鸡包装一下,0}',}",
  [155656] = "{id=155656,Text='炸鸡……？给炸鸡做包装还是头一回……',Speaker=892110,}",
  [155657] = "{id=155657,Text='先给我看看吧。',Speaker=892110,}",
  [155658] = "{id=155658,Text='是K上校的炸鸡……红与白……',Speaker=892110,}",
  [155659] = "{id=155659,Text='我知道该怎么搭配了。',Speaker=892110,Emoji=11,}",
  [155660] = "{id=155660,Text='好了，这样看起来就合适了。',Speaker=892110,}",
  [155661] = "{id=155661,Text='快去吧，别让送礼的人等急了。',Speaker=892110,}",
  [155662] = "{id=155662,Text='……所以，我想送给你一样特殊的礼物。',Speaker=892102,}",
  [155663] = "{id=155663,Text='特殊的礼物？',Speaker=892104,Emoji=8,}",
  [155664] = "{id=155664,Text='嗯，就是……啊，你来了。',Speaker=892102,}",
  [155665] = "{id=155665,Text='这姑娘不就是昨晚的……',}",
  [155666] = "{id=155666,Text='啊！！',Speaker=892104,}",
  [155667] = "{id=155667,Text='麦琪！麦琪！',Speaker=892102,}",
  [155668] = "{id=155668,Text='怎么回事？！',Speaker=892102,Option='待会儿和你说',}",
  [155669] = "{id=155669,Text='啊……啊……怎么办，这下怎么办……',Speaker=892104,}",
  [155670] = "{id=155670,Text='吉姆……',Speaker=892104,}",
  [155671] = "{id=155671,Text='你、你不要过来！',Speaker=892104,Option='冷静一点',}",
  [155672] = "{id=155672,Text='我不是……我没有……',Speaker=892104,}",
  [155673] = "{id=155673,Text='嘁，甩锅的混蛋，抓住你啦！',Speaker=892101,}",
  [155674] = "{id=155674,Text='啊！！',Speaker=892104,}",
  [155675] = "{id=155675,Text='总算搞定了……嗯，竟然是个小丫头。',Speaker=892101,Option='你怎么会在这里',}",
  [155676] = "{id=155676,Text='我可是最强大的魔法师摩根！虽说昨晚一时大意，但只要被我的魔法触碰过，追踪起来不过小事一桩！',Speaker=892101,}",
  [155677] = "{id=155677,Text='等她醒了我可要好好教训她！',Speaker=892101,Option='{竟然是她,0},{她很喜欢炸鸡啊,0}',}",
  [155678] = "{id=155678,Text='很喜欢炸鸡还留下那种留言？莫名其妙。',Speaker=892101,}",
  [155679] = "{id=155679,Text='算啦，她被我的魔法击中，怎么样也要明天才能醒了。我先把她带到K上校那儿，明天再弄清楚她的动机吧。',Speaker=892101,}",
  [155680] = "{id=155680,Text='太气人了！这丫头死活就是不肯开口！',Speaker=892101,}",
  [155681] = "{id=155681,Text='……',Speaker=892104,}",
  [155682] = "{id=155682,Text='不会是被我魔法打傻了吧……',Speaker=892101,Option='我去问问她',Emoji=10,}",
  [155683] = "{id=155683,Text='太好了，那就交给你了。一早上和她大眼瞪小眼，我都饿了。',Speaker=892101,}",
  [155684] = "{id=155684,Text='我先去吃炸鸡~',Speaker=892101,Emoji=28,}",
  [155685] = "{id=155685,Text='……',Speaker=892104,Option='{为什么偷鸡崽,0},{你不是喜欢炸鸡吗,0}',}",
  [155686] = "{id=155686,Text='……',Speaker=892104,Option='听到我说话了吗',}",
  [155687] = "{id=155687,Text='……总而言之是我的错，请不要再问了。',Speaker=892104,Emoji=24,}",
  [155688] = "{id=155688,Text='冒险者，有什么困难吗？',Speaker=807798,Option='关于炸鸡怪盗',}",
  [155689] = "{id=155689,Text='……没想到，偷鸡崽的竟然就是吉姆喜欢的女孩。',Speaker=807798,}",
  [155690] = "{id=155690,Text='吉姆说她非常喜欢炸鸡，这应该不会是假的。',Speaker=807798,Option='{既然如此,0},{为什么仇恨炸鸡,0}',}",
  [155691] = "{id=155691,Text='嗯……现在她心情非常沮丧，如果能让她心情好点，或许她就会开口。',Speaker=807798,Option='这么说来',}",
  [155692] = "{id=155692,Text='哦！魔法瘦身炸鸡还在你这里对吗。那快交给她吧。',Speaker=807798,}",
  [155693] = "{id=155693,Text='这是……给我……？',Speaker=892104,}",
  [155694] = "{id=155694,Text='不！不要给我炸鸡！快拿走！',Speaker=892104,Emoji=37,}",
  [155695] = "{id=155695,Text='如果……如果再吃炸鸡的话……吉姆就会讨厌我了！',Speaker=892104,Option='什么？',}",
  [155696] = "{id=155696,Text='……我非常非常喜欢吃炸鸡。',Speaker=892104,}",
  [155697] = "{id=155697,Text='以前，我经常一顿饭只吃炸鸡，其他什么都不吃……',Speaker=892104,}",
  [155698] = "{id=155698,Text='虽然自己也知道这样营养不太均衡，但还是停不下嘴。',Speaker=892104,}",
  [155699] = "{id=155699,Text='可是，前几天我被朋友说……',Speaker=892104,}",
  [155700] = "{id=155700,Text='说我胖了！',Speaker=892104,Emoji=42,}",
  [155701] = "{id=155701,Text='说我已经肥得不像话了！',Speaker=892104,}",
  [155702] = "{id=155702,Text='说我再胖下去吉姆就会讨厌我了！',Speaker=892104,Option='{就因为这个,0},{你问过吉姆吗,0}',}",
  [155703] = "{id=155703,Text='……我本来也不会信这句话的。',Speaker=892104,}",
  [155704] = "{id=155704,Text='可是可是，上次我吃炸鸡时，吉姆真的笑着说了一句，你这么吃炸鸡就不怕胖吗？',Speaker=892104,}",
  [155705] = "{id=155705,Text='他真的嫌弃我了！',Speaker=892104,Emoji=37,}",
  [155706] = "{id=155706,Text='我真的不想被吉姆讨厌！可是又无法停止对炸鸡的渴望！',Speaker=892104,}",
  [155707] = "{id=155707,Text='所以，我在想……如果米德加尔特没有炸鸡这种东西就好了。',Speaker=892104,}",
  [155708] = "{id=155708,Text='你觉得很可笑是吧！恋爱中的少女心你根本就不懂！',Speaker=892104,Option='{你误会了,0},{吉姆不讨厌你,0}',Emoji=24,}",
  [155709] = "{id=155709,Text='你凭什么这么说！你别乱猜吉姆的心思！',Speaker=892104,Option='{那么,0},{我们直接去问他,0}',}",
  [155710] = "{id=155710,Text='啊？',Speaker=892104,Option='走吧',}",
  [155711] = "{id=155711,Text='不要！我不能去见他……',Speaker=892104,Option='{没关系的,0},{他很担心你,0}',}",
  [155712] = "{id=155712,Text='……好、好吧……',Speaker=892104,}",
  [155713] = "{id=155713,Text='麦琪！你没事吗！！',Speaker=892102,}",
  [155714] = "{id=155714,Text='吉、吉姆……',Speaker=892104,}",
  [155715] = "{id=155715,Text='你怎么了？表情那么不开心？昨天为什么要突然跑开？',Speaker=892102,}",
  [155716] = "{id=155716,Text='我、我……',Speaker=892104,Option='情况是这样',}",
  [155717] = "{id=155717,Text='不！你不要说！',Speaker=892104,Emoji=1,}",
  [155718] = "{id=155718,Text='麦琪？',Speaker=892102,}",
  [155719] = "{id=155719,Text='……',Speaker=892104,}",
  [155720] = "{id=155720,Text='我、我自己来告诉你，吉姆……',Speaker=892104,}",
  [155721] = "{id=155721,Text='……原来，是这样……',Speaker=892102,}",
  [155722] = "{id=155722,Text='对不起，吉姆。我做了这么过分的事。越想不被你讨厌，就越走上错误的道路……越会被你讨厌了……',Speaker=892104,Emoji=24,}",
  [155723] = "{id=155723,Text='麦琪……',Speaker=892102,}",
  [155724] = "{id=155724,Text='别瞎想，我从来没有讨厌过你。',Speaker=892102,}",
  [155725] = "{id=155725,Text='可是……我变胖了……',Speaker=892104,}",
  [155726] = "{id=155726,Text='没有啊。我觉得麦琪的身材很好呢！',Speaker=892102,Emoji=3,}",
  [155727] = "{id=155727,Text='可是……我吃了那么多的炸鸡……还偷了鸡崽……',Speaker=892104,}",
  [155728] = "{id=155728,Text='嗯，偷了鸡崽这件事是不对的，一定要和K上校道歉。',Speaker=892102,}",
  [155729] = "{id=155729,Text='不过，喜欢吃炸鸡一点问题都没有呀。',Speaker=892102,}",
  [155730] = "{id=155730,Text='咦……',Speaker=892104,}",
  [155731] = "{id=155731,Text='我觉得吃炸鸡的麦琪非常可爱！不过为了健康着想，蔬菜和主食也不能不吃就是了。',Speaker=892102,Emoji=5,}",
  [155732] = "{id=155732,Text='……你果然还是嫌我炸鸡吃太多了！',Speaker=892104,}",
  [155733] = "{id=155733,Text='麦琪，你喜欢吃炸鸡，我就陪你吃炸鸡；你想要减肥，我就陪你减肥。你做什么我都支持，只要你能幸福快乐。',Speaker=892102,}",
  [155734] = "{id=155734,Text='我的这份心意……至少能有一样东西证明。',Speaker=892102,}",
  [155735] = "{id=155735,Text='什、什么东西？',Speaker=892104,}",
  [155736] = "{id=155736,Text='冒险者，能拜托你把那个证明交给麦琪吗？',Speaker=892102,Option='{我知道了,0},{炸鸡礼盒,0}',}",
  [155737] = "{id=155737,Text='这个礼盒……',Speaker=892104,}",
  [155738] = "{id=155738,Text='这是我拜托K上校专门为麦琪发明的魔法瘦身炸鸡。',Speaker=892102,}",
  [155739] = "{id=155739,Text='这样，你吃炸鸡就不会有任何心理负担了！',Speaker=892102,}",
  [155740] = "{id=155740,Text='我喜欢……吃炸鸡时你幸福的笑容。',Speaker=892102,Emoji=38,}",
  [155741] = "{id=155741,Text='吉姆……',Speaker=892104,}",
  [155742] = "{id=155742,Text='好啦！现在让我们享受这米德加尔特最让人幸福的食物吧！',Speaker=892102,}",
  [155743] = "{id=155743,Text='冒险者你要不要来一块？',Speaker=892102,Option='{谢谢，不用了,0},{狗粮已经吃饱了,0}',}",
  [155744] = "{id=155744,Text='哈哈哈哈。真的很感谢你。之后我会陪麦琪一起去向K上校道歉的。',Speaker=892102,}",
  [155745] = "{id=155745,Text='给K上校和那位魔法师添的麻烦，我会去补偿的……还有，谢谢你！',Speaker=892104,Option='{太好了,0},{祝你们幸福,0}',}",
  [155746] = "{id=155746,Text='嗯！',Speaker=892102,}",
  [155785] = "{id=155785,Text='你好呀，你也是来参加开学庆典的寻找宝藏的活动的吗？',Speaker=892112,}",
  [155786] = "{id=155786,Text='我这边有好多不同的藏宝图哦',Speaker=892112,}",
  [155787] = "{id=155787,Text='今天是这个藏宝图~一定要找到宝藏呀！',Speaker=892112,}",
  [155788] = "{id=155788,Text='雅尼克已经在天津町等你了，我送你过去吧！',Speaker=892092,Option='谢谢！',}",
  [155789] = "{id=155789,Text='蜂蜜的数量已经足够了，现在就回普隆德拉吧。',}",
  [155790] = "{id=155790,Text='复习好了再来哦。',Speaker=892113,}",
  [155791] = "{id=155791,Text='考试地点在波利岛，会有两种类型的题目可以选择，加油！',Speaker=892113,}",
  [155792] = "{id=155792,Text='这位考生，请出示一下准考证。',Speaker=892114,}",
  [155793] = "{id=155793,Text='什么！？没带？算了算了，直接考试吧，反正你一看就是差生。',Speaker=892114,}",
  [155794] = "{id=155794,Text='下面这两种题型，你想选择哪种类型进行考试呢？',Speaker=892114,Option='{道具,4},{战斗,5}',}",
  [155795] = "{id=155795,Text='能够清除所有负面效果的道具是？ ',Speaker=892114,Option='{万能药,60},{天地树果实,61}',}",
  [155796] = "{id=155796,Text='能够加满hp的道具是？ ',Speaker=892114,Option='{万能药,61},{天地树果实,60}',}",
  [155797] = "{id=155797,Text='安全精炼从+4到+10需要多少个装备作为材料  ',Speaker=892114,Option='{26个,60},{28个,61}',}",
  [155798] = "{id=155798,Text='可以获得双倍base和job经验的道具是？  ',Speaker=892114,Option='{波利生长秘药,61},{波利成长秘药,60}',}",
  [155799] = "{id=155799,Text='可以在微笑小姐处使用的兑换道具是什么？  ',Speaker=892114,Option='{乐园币,60},{莫拉硬币,61}',}",
  [155800] = "{id=155800,Text='可以移动至储存的位置的道具是？  ',Speaker=892114,Option='{蝴蝶翅膀,60},{苍蝇翅膀,61}',}",
  [155801] = "{id=155801,Text='安全精炼的最高等级是？',Speaker=892114,Option='{+10,60},{+15,61}',}",
  [155802] = "{id=155802,Text='当己方属性克制对方的时候，对对方造成的伤害增加？',Speaker=892114,Option='{50%,61},{100%,60},',}",
  [155803] = "{id=155803,Text='素质点Agi的作用是？  ',Speaker=892114,Option='{增加攻速和闪避,60},{增加远程物理攻击,61}',}",
  [155804] = "{id=155804,Text='可以将咏唱时间降到0的是？  ',Speaker=892114,Option='{可变咏唱,60},{固定咏唱,61}',}",
  [155805] = "{id=155805,Text='ro战斗时长最多可以叠加到多少分钟？  ',Speaker=892114,Option='{300分钟,61},{450分钟,60}',}",
  [155806] = "{id=155806,Text='战斗过程中队友死亡时使用什么技能可以复活队友。  ',Speaker=892114,Option='{复活术,60},{神威祈福,61}',}",
  [155807] = "{id=155807,Text='什么道具可以加速消耗4倍战斗时长？   ',Speaker=892114,Option='{锁链雷锭,61},{坠星陨铁,60}',}",
  [155808] = "{id=155808,Text='什么道具可以加速消耗2倍战斗时长？   ',Speaker=892114,Option='{锁链雷锭,60},{坠星陨铁,61}',}",
  [155809] = "{id=155809,Text='答对啦！接下来是实战！加油呀！',Speaker=892114,}",
  [155810] = "{id=155810,Text='答错啦！接下来是实战！加油呀！',Speaker=892114,}",
  [155811] = "{id=155811,Text='你已经完成了今天的入学考试，表现不错，再接再厉哦。',Speaker=892114,}",
  [155812] = "{id=155812,Text='你是来参加入学考试的吗？复习好了吗？不管结果怎么样都要全力以赴哟！',Speaker=892113,}",
  [155813] = "{id=155813,Text='[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取。】[-][/c]',Speaker=892113,Option='{好的,2},{暂时没空,3}',}",
  [155822] = "{id=155822,Text='为了给广大冒险者带来更多惊喜，黄金美食节期间，美食节组委会设置了特别活动。',Speaker=892115,}",
  [155823] = "{id=155823,Text='普隆德拉城内藏着神秘宝藏，幸运的冒险者还能获得上届料理大赛冠军的同款厨具——[c][ffff00]旋风锅铲[-][/c]。',Speaker=892115,}",
  [155824] = "{id=155824,Text='冒险者，收下这颗引路四叶草，成为寻宝者的一员吧！[c][ffff00]【本次任务同账号仅限一个角色接取，请慎重考虑是否使用该角色接取。】[-][/c]',Speaker=892115,Option='{是,3},{否,2}',}",
  [155825] = "{id=155825,Text='请开启你的寻宝之旅吧，愿你好运相随。',Speaker=892115,}",
  [155826] = "{id=155826,Text='好的，幸运小姐恭候你再次拜访。',Speaker=892115,}",
  [155827] = "{id=155827,Text='咔哒咔哒，你发现我啦！',Speaker=892116,}",
  [155828] = "{id=155828,Text='咕噜噜，猜猜我肚子里有什么宝贝？',Speaker=892116,}",
  [155829] = "{id=155829,Text='找到我，我就让你嘿嘿嘿……',Speaker=892116,}",
  [155830] = "{id=155830,Text='只要我藏得够好，就没人能……诶？你怎么在这儿！',Speaker=892116,}",
  [155831] = "{id=155831,Text='可、可恶，居然被冒险者找到了！',Speaker=892116,}",
  [155832] = "{id=155832,Text='冒险者，你好啊。你知道今天是什么日子吗？',Speaker=892117,Option='鬼怪节！',}",
  [155833] = "{id=155833,Text='对，每年的鬼怪节，尼夫海姆的亡魂都会重返人间。',Speaker=892117,}",
  [155834] = "{id=155834,Text='大部分亡魂都是温和无害的，只想重见家人和朋友。',Speaker=892117,}",
  [155835] = "{id=155835,Text='但另一些因为生前留有执念，脾气古怪。如果人们不实现它们的心愿，就会纠缠对方，甚至在节日最后一天将这个人的灵魂带走。',Speaker=892117,}",
  [155836] = "{id=155836,Text='作为普隆德拉的守卫，我是绝对不会允许这种事情发生的，但我一个人力量有限。',Speaker=892117,}",
  [155837] = "{id=155837,Text='冒险者，你能和我一起陪这些鬼怪过节，平息他们的怨气吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892117,Option='{没问题,3},{再考虑考虑,2}',}",
  [155839] = "{id=155839,Text='好，那就拜托你了。',Speaker=892117,}",
  [155840] = "{id=155840,Text='没关系，你改变主意的话，可以再来找我。',Speaker=892117,}",
  [155841] = "{id=155841,Text='跳舞给我看，跳的不好，我就拆掉你的脑袋，把里面的棉花掏光。',Speaker=892118,}",
  [155842] = "{id=155842,Text='呵，让你做什么就做什么，真是个人偶似的乖孩子呢。',Speaker=892118,}",
  [155843] = "{id=155843,Text='和我猜拳吧，不让我玩到高兴……就别想走。',Speaker=892119,}",
  [155844] = "{id=155844,Text='石头，剪刀，布！',Speaker=892119,Option='{石头,98},{剪刀,99},{布,97}',}",
  [155845] = "{id=155845,Text='哈哈，你输了，你逃不了的~逃不了的~',Speaker=892119,}",
  [155846] = "{id=155846,Text='哼，竟然是平局。再来一次！',Speaker=892119,}",
  [155847] = "{id=155847,Text='你、你居然赢了！我不接受，我不接受——！',Speaker=892119,}",
  [155848] = "{id=155848,Text='妾身才不是那群小鬼呢。成年人有成年人的“游戏”。',Speaker=892120,}",
  [155849] = "{id=155849,Text='来吧，记得把妾身拍得美一点哦，不然……',Speaker=892120,}",
  [155850] = "{id=155850,Text='嗯，拍得不错呢。你就继续好好活着，做妾身的摄影师吧。',Speaker=892120,}",
  [155851] = "{id=155851,Text='这几个亡灵都消失了吗？',Speaker=892117,Option='是的',}",
  [155852] = "{id=155852,Text='做得很好，多谢你了，冒险者。',Speaker=892117,}",
  [155853] = "{id=155853,Text='今天的考试你准备好了吗？',Speaker=892113,Option='{准备好了,2},{没有,3}',}",
  [155854] = "{id=155854,Text='嘻嘻嘻……你敢不敢啊？',Speaker=892119,Option='{当然敢！,0},{有什么不敢？,0}',}",
  [155855] = "{id=155855,Text='那来吧！',Speaker=892119,}",
  [155856] = "{id=155856,Text='准备好了就出发寻找鬼怪们吧。',Speaker=892117,Option='没问题',}",
  [155857] = "{id=155857,Text='都收集齐了吗？',Speaker=807798,Option='都在这里了',}",
  [155858] = "{id=155858,Text='太好了，这下我能安心地创作不会胖的炸鸡了。',Speaker=807798,Option='关于那个怪盗',}",
  [155859] = "{id=155859,Text='啊，怪盗啊……',Speaker=807798,Emoji=10,}",
  [155860] = "{id=155860,Text='算了，头痛的事先不想了，今天我必须全力以赴，先把吉姆的委托完成。',Speaker=807798,}",
  [155861] = "{id=155861,Text='冒险者你也先回去休息吧。怪盗的问题我们明天再解决。',Speaker=807798,}",
  [155873] = "{id=155873,Text='[c][ffff00][PlayerName][-][/c]，欢迎参加黄金美食节。本次美食料理大赛的主题为“重现传说秘方：黄金蛋炒饭”。',Speaker=892122,Option='{传说秘方？,0},{黄金蛋炒饭？,0}',}",
  [155874] = "{id=155874,Text='千年前抗击魔王梦罗克时，存在着一种神奇的料理，食用后能让人“充满力量”。',Speaker=892122,}",
  [155875] = "{id=155875,Text='然而美食家协会只找到秘方残卷，始终没能做出具有特殊功效的蛋炒饭。',Speaker=892122,}",
  [155876] = "{id=155876,Text='因此我们将它设为大赛主题，重现黄金蛋炒饭的人将成为冠军，获得丰厚奖励。',Speaker=892122,}",
  [155877] = "{id=155877,Text='[c][ffff00][PlayerName][-][/c]，你有兴趣参加吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892122,Option='{我很感兴趣,4},{不是很感兴趣,3}',}",
  [155878] = "{id=155878,Text='明白了。如果您改变主意，可以再来找我。',Speaker=892122,}",
  [155879] = "{id=155879,Text='感谢您的参与。顺带一提，比赛是可以组队参加的。',Speaker=892122,}",
  [155880] = "{id=155880,Text='目前有大量参赛者聚集在普隆德拉，您不妨找找有无心仪的伙伴。',Speaker=892122,Option='好的',}",
  [155881] = "{id=155881,Text='[c][ffff00][PlayerName][-][/c]，你也没找到搭档吗？不如和我组队吧！',Speaker=892123,}",
  [155882] = "{id=155882,Text='自我介绍下，我叫特蕾莎，学习烹饪已经有十年啦，在我家乡也算小有名气的天才厨师啦。',Speaker=892123,}",
  [155883] = "{id=155883,Text='诶？没、没听说过也不要紧啦，总之我真的很擅长做菜啦。',Speaker=892123,Emoji=24,}",
  [155884] = "{id=155884,Text='而且两个人的赢面总比一个人大，请和我组队吧，拜托了！',Speaker=892123,Option='好吧',}",
  [155885] = "{id=155885,Text='耶！现在起我们就是搭档啦。我来准备米饭和佐料，你去收集[c][ffff00]10份魔锅蛋液[-][/c]好吗？',Speaker=892123,}",
  [155886] = "{id=155886,Text='这个特制的蝴蝶翅膀送给你，快去快回呀！',Speaker=892123,Option='{我这就去,2},{我还有事,3}',}",
  [155887] = "{id=155887,Text='好吧……等你有空一定要去呀，我会在这里等你的。',Speaker=892123,}",
  [155888] = "{id=155888,Text='收集得好快，不愧是我的最佳搭档！',Speaker=892123,}",
  [155889] = "{id=155889,Text='不过烹制之前，还需要把食材处理一下。',Speaker=892123,}",
  [155890] = "{id=155890,Text='魔锅蛋终究是魔物，不想吃到奇怪的东西，就一定要做好清洁工作。',Speaker=892123,}",
  [155891] = "{id=155891,Text='接下来还要对食材进行调味和腌制，需要等上小半天功夫。',Speaker=892123,}",
  [155892] = "{id=155892,Text='有事的话，你可以先走一步。',Speaker=892123,}",
  [155893] = "{id=155893,Text='等食材腌制好后，我会再联系你的。',Speaker=892123,Option='那再见啦',}",
  [155894] = "{id=155894,Text='嗯嗯，[c][ffff00][PlayerName][-][/c]再见~',Speaker=892123,}",
  [155895] = "{id=155895,Text='[c][ffff00][PlayerName][-][/c]，你来啦。食材全都处理完了，可以准备烹饪啦。',Speaker=892123,}",
  [155896] = "{id=155896,Text='不过在开工之前，我想请你先去打听一下其他参赛组的情报。',Speaker=892123,Option='这不太好吧',}",
  [155897] = "{id=155897,Text='只是借鉴，借鉴啦！为了夺冠，拜托你啦。',Speaker=892123,}",
  [155898] = "{id=155898,Text='放辣椒炒也不对、放洋葱炒也不对。',Speaker=892124,}",
  [155899] = "{id=155899,Text='……等等，莫非黄金蛋炒饭是[c][ffff00]甜口[-][/c]的而不是咸口吗？！',Speaker=892124,}",
  [155900] = "{id=155900,Text='又失败了，一定是你的错，居然炒了33秒！',Speaker=892125,}",
  [155901] = "{id=155901,Text='3秒有什么影响？错的是你的极速翻炒大法吧，食物根本[c][ffff00]没熟[-][/c]！',Speaker=892126,}",
  [155902] = "{id=155902,Text='太难了，我放弃了。',Speaker=892127,}",
  [155903] = "{id=155903,Text='黄金蛋炒饭根本就不存在的吧，人类怎么可能做出那种神奇的食物。',Speaker=892127,}",
  [155904] = "{id=155904,Text='[c][ffff00][PlayerName][-][/c]你回来啦！',Speaker=892123,Option='分享情报',}",
  [155905] = "{id=155905,Text='唔，传说中的料理果然没这么容易重现啊。',Speaker=892123,}",
  [155906] = "{id=155906,Text='但无论如何，我们先尝试一下。',Speaker=892123,}",
  [155907] = "{id=155907,Text='好了，[c][ffff00][PlayerName][-][/c]，快来试吃一下。',Speaker=892123,}",
  [155908] = "{id=155908,Text='这、这份蛋炒饭居然是甜的！',}",
  [155909] = "{id=155909,Text='这份还是夹生的！',}",
  [155910] = "{id=155910,Text='这份蛋炒饭看似普通，却意外地非常好吃。',}",
  [155911] = "{id=155911,Text='你竟然觉得第三份最好吃吗？那是我按自己想法做的蛋炒饭啦。',Speaker=892123,Emoji=38,}",
  [155912] = "{id=155912,Text='可是你吃完后，有感受到身体充满力量吗？',Speaker=892123,Option='没有',}",
  [155913] = "{id=155913,Text='啊，果然，我还是做不到呢。',Speaker=892123,}",
  [155914] = "{id=155914,Text='不过是个乡下小地方出生的女孩，却妄想得到认可、成为顶级厨师……',Speaker=892123,Option='{你可以的,3},{别人做的更难吃,2}',Emoji=14,}",
  [155915] = "{id=155915,Text='真的吗？你不是在安慰我吧？',Speaker=892123,}",
  [155916] = "{id=155916,Text='哈哈，你的安慰方式真独特。',Speaker=892123,}",
  [155917] = "{id=155917,Text='自从离开家，这是我第一次得到认可。',Speaker=892123,}",
  [155918] = "{id=155918,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。我觉得自己又能坚持下去了。',Speaker=892123,Emoji=26,}",
  [155919] = "{id=155919,Text='今天你先回去吧，我想想料理的问题出在哪里。',Speaker=892123,}",
  [155920] = "{id=155920,Text='[c][ffff00][PlayerName][-][/c]，我找到失败的原因了。',Speaker=892123,}",
  [155921] = "{id=155921,Text='传奇料理肯定要用到传奇食材，是我们选用的魔锅蛋太普通了。',Speaker=892123,}",
  [155922] = "{id=155922,Text='刚好，这位行脚商有传奇魔锅蛋的线索，你也来听听吧。',Speaker=892123,}",
  [155923] = "{id=155923,Text='想捕获传奇魔锅蛋？那你们可真是找对人了。',Speaker=892128,}",
  [155924] = "{id=155924,Text='我手里刚好有特殊的诱饵，只要放置在正确地点，就能引诱传奇魔锅蛋现身。',Speaker=892128,Option='正确地点在？',}",
  [155925] = "{id=155925,Text='哈哈哈……这个我也不清楚呢。总之就在苏克拉特沙漠某地吧。',Speaker=892128,}",
  [155926] = "{id=155926,Text='诱饵原价[c][ffff00]9999zeny[-][/c]，目前限时折扣[c][ffff00]999zeny[-][/c]。想入手可要抓紧哦。',Speaker=892128,}",
  [155927] = "{id=155927,Text='诱饵对我们很重要，我打算买下它！',Speaker=892123,Option='{感觉不太靠谱,0},{恐怕是奸商,0}',}",
  [155928] = "{id=155928,Text='或许你是对的，但这是我们唯一的机会，总要尝试一下。',Speaker=892123,}",
  [155929] = "{id=155929,Text='这是999zeny，请收下。',Speaker=892123,}",
  [155930] = "{id=155930,Text='好嘞，东西您拿好，再见再见~',Speaker=892128,}",
  [155931] = "{id=155931,Text='诱饵已经拿到了，接下来我们去一趟苏克拉特沙漠吧。',Speaker=892123,Option='{唉，好吧,3},{暂时没空,2}',}",
  [155932] = "{id=155932,Text='唔，那你有空了要赶紧过来哦。',Speaker=892123,}",
  [155933] = "{id=155933,Text='沙漠那么大，不如我们分头寻找传奇魔锅蛋。',Speaker=892123,}",
  [155934] = "{id=155934,Text='等过一段时间，再回到这里碰头。',Speaker=892123,Option='没问题',}",
  [155935] = "{id=155935,Text='视线里只有黄沙漫漫，没有东西出现。',}",
  [155936] = "{id=155936,Text='你回来啦，有找到传奇魔锅蛋吗？',Speaker=892123,}",
  [155937] = "{id=155937,Text='一无所获吗？唉，我这边也是。',Speaker=892123,}",
  [155938] = "{id=155938,Text='算了，诱饵还没用光，你再拿走些试试吧。',Speaker=892123,Option='{我心好累,0},{我还有别的事,0}',}",
  [155939] = "{id=155939,Text='……你这人怎么这样啊！',Speaker=892123,Emoji=40,}",
  [155940] = "{id=155940,Text='离料理大赛结束只有几天时间了，你还想着偷懒！',Speaker=892123,Option='{可你在白费功夫,0},{我们被骗了啊,0}',}",
  [155942] = "{id=155942,Text='不，不会的……我赌上了所有积蓄，绝对不能中途放弃。',Speaker=892123,}",
  [155943] = "{id=155943,Text='想走你自己走，不找到传奇魔锅蛋，我是不会离开的！',Speaker=892123,Option='{走就走,0},{别执迷不悟了,0}',}",
  [155944] = "{id=155944,Text='我一个人也能搞定的，呜呜呜，算我看错你了。',Speaker=892123,Emoji=42,}",
  [155945] = "{id=155945,Text='张望了一下，没有发现特蕾莎的身影……',}",
  [155946] = "{id=155946,Text='嗯？你问那个傻乎乎的、满身油烟味儿的村姑啊。',Speaker=892132,}",
  [155947] = "{id=155947,Text='她上次和你一起离开后，就再没回来过。',Speaker=892132,}",
  [155948] = "{id=155948,Text='怎么，你也没再见过她？',Speaker=892132,Option='{有不详的预感,0},{她恐怕出事了,0}',}",
  [155949] = "{id=155949,Text='是否立即前往苏克拉特沙漠寻找特蕾莎？',Option='{是,3},{否,2}',}",
  [155950] = "{id=155950,Text='并没有特蕾莎的踪影……',}",
  [155951] = "{id=155951,Text='这里也没有特蕾莎，再深入沙漠看看吧……',}",
  [155952] = "{id=155952,Text='特蕾莎似乎失去了意识。',}",
  [155953] = "{id=155953,Text='和你分开以后，我独自寻找传奇魔锅蛋，却被魔物所伤。',Speaker=892123,}",
  [155954] = "{id=155954,Text='只能在它们的追杀下一路逃跑。',Speaker=892123,}",
  [155955] = "{id=155955,Text='还好这片遗迹比较特殊，它们不敢过来。否则我现在肯定已经死掉了。',Speaker=892123,}",
  [155956] = "{id=155956,Text='呜呜呜，[c][ffff00][PlayerName][-][/c]，对不起。你说的对，我可能真的被骗了。',Speaker=892123,Option='{我原谅你了,0},{我也不该丢下你,0}',}",
  [155957] = "{id=155957,Text='但现在怎么办呢？传奇魔锅蛋是假的，大赛也要结束了。',Speaker=892123,}",
  [155958] = "{id=155958,Text='我大概只能灰头土脸地回家、继续被人看不起吧……',Speaker=892123,Option='{还有机会的,0},{再努力努力吧,0}',}",
  [155959] = "{id=155959,Text='真、真的吗？你愿意和我再尝试一次？',Speaker=892123,}",
  [155960] = "{id=155960,Text='[c][ffff00][PlayerName][-][/c]！你真是我最好的搭档！！！',Speaker=892123,Emoji=27,}",
  [155961] = "{id=155961,Text='既然你这么说，那我们就再尝试一次吧。',Speaker=892123,}",
  [155962] = "{id=155962,Text='有你陪伴着，哪怕仍旧失败，也没那么难以接受了呢。',Speaker=892123,}",
  [155963] = "{id=155963,Text='真的出现了！',Speaker=892123,}",
  [155964] = "{id=155964,Text='这就是传奇魔锅蛋吗？仔细看看，除了生长年份长、个头足够大，和普通魔锅蛋也没区别啊……',Speaker=892123,}",
  [155965] = "{id=155965,Text='等等。[c][ffff00][PlayerName][-][/c]你看，这是什么？',Speaker=892123,}",
  [155966] = "{id=155966,Text='热乎乎、香喷喷。你做的蛋炒饭太好吃了。',Speaker=892133,}",
  [155967] = "{id=155967,Text='过奖啦，只是家常炒法而已。',Speaker=892134,}",
  [155968] = "{id=155968,Text='我又充满力量了！感觉能干翻魔王梦罗克！',Speaker=892135,}",
  [155969] = "{id=155969,Text='这么好吃的蛋炒饭一定要写进日记，就叫黄金蛋炒饭吧。',Speaker=892136,}",
  [155970] = "{id=155970,Text='刚才的幻象，是魔锅蛋的记忆吗？',Speaker=892123,}",
  [155971] = "{id=155971,Text='料理大赛负责人提到过，黄金蛋炒饭的秘方，正是在千年前对抗魔王时期发明的。',Speaker=892123,}",
  [155972] = "{id=155972,Text='你也听到了吧！[c][ffff00]充满力量[-][/c]只是夸张说法，[c][ffff00]传说秘方[-][/c]不过是美食日记罢了。',Speaker=892123,Emoji=26,}",
  [155973] = "{id=155973,Text='黄金蛋炒饭，只是普普通通的料理而已啊。',Speaker=892123,Option='是的',}",
  [155974] = "{id=155974,Text='可是这样的话，料理大赛该怎么办呀？',Speaker=892123,}",
  [155975] = "{id=155975,Text='传说中的功效根本不存在，我们又该如何胜出呢？',Speaker=892123,Option='{告诉大家真相,7},{做你想做的料理,6}',}",
  [155976] = "{id=155976,Text='你说的对，确实应该把真相告诉大家。',Speaker=892123,}",
  [155977] = "{id=155977,Text='除此之外，我还想按自己的心意做一份黄金蛋炒饭。我要用我真实的能力获得认可。',Speaker=892123,}",
  [155978] = "{id=155978,Text='你说的对。既然不存在传说中秘方，那不如按自己的心意，全力做到最好。',Speaker=892123,}",
  [155979] = "{id=155979,Text='除此之外，还需要把真相告诉大家。',Speaker=892123,}",
  [155980] = "{id=155980,Text='[c][ffff00][PlayerName][-][/c]，今天辛苦你啦。食材我带走了，剩下的就交给我吧。',Speaker=892123,}",
  [155981] = "{id=155981,Text='下次，我们普隆德拉再见！',Speaker=892123,}",
  [155982] = "{id=155982,Text='[c][ffff00][PlayerName][-][/c]，最后时刻到了，我们协力制作这份黄金蛋炒饭吧。',Speaker=892123,}",
  [155984] = "{id=155984,Text='辛苦你了[c][ffff00][PlayerName][-][/c]，你再尝尝看这份调味。',Speaker=892123,}",
  [155985] = "{id=155985,Text='你觉得需要……',Speaker=892123,Option='{加盐,0},{加胡椒粉,0}',Emoji=19,}",
  [155986] = "{id=155986,Text='好的，接下来我就要烹饪啦。祝福我发挥到最好吧！',Speaker=892123,}",
  [155987] = "{id=155987,Text='锵锵！这下就完成了。',Speaker=892123,}",
  [155988] = "{id=155988,Text='麻烦你将料理送去给评委试吃吧。',Speaker=892123,}",
  [155989] = "{id=155989,Text='哦，居然还有挑战者呀~我还以为所有人都放弃了呢。',Speaker=892131,Option='递上蛋炒饭',}",
  [155990] = "{id=155990,Text='诶呀，意外地非常美味呢。火候精妙绝伦，炒蛋嫩滑的口感真的太棒了。',Speaker=892131,}",
  [155991] = "{id=155991,Text='唉，又来了，本少爷的舌头都被这群疯子做的东西毒麻了。',Speaker=892129,Option='递上蛋炒饭',}",
  [155992] = "{id=155992,Text='美味，太美味了！丰富的佐料搭配鲜爽的调味，我的味觉恢复！',Speaker=892129,}",
  [155993] = "{id=155993,Text='集举国之力，竟然仍旧不能重现传说料理吗……',Speaker=892130,Option='递上蛋炒饭',}",
  [155994] = "{id=155994,Text='唔……虽然和秘方中记载的功效不尽相同，但确实有一股温暖人心的力量。',Speaker=892130,}",
  [155995] = "{id=155995,Text='[c][ffff00][PlayerName][-][/c]，能告诉我你如何制作的这份蛋炒饭吗？',Speaker=892130,Option='{制作者另有其人,0},{我还有个搭档,0}',}",
  [155996] = "{id=155996,Text='是嘛，那麻烦你把她也带过来吧。我们评委会想见见她。',Speaker=892130,}",
  [155997] = "{id=155997,Text='评委们的评价怎么样？',Speaker=892123,Option='告知结果',Emoji=18,}",
  [155998] = "{id=155998,Text='竟然得到了如此之高的评价吗，天啊，我好激动！',Speaker=892123,Option='你确实是个天才',}",
  [155999] = "{id=155999,Text='也离不开你的帮助呢，[c][ffff00][PlayerName][-][/c]。我们快去比赛现场吧。',Speaker=892123,}",
  [156000] = "{id=156000,Text='小姑娘不要紧张，我们只想知道，你们是如何做出能带给人力量的蛋炒饭的。',Speaker=892130,}",
  [156001] = "{id=156001,Text='那、那我说了啊。（吸气）',Speaker=892123,}",
  [156002] = "{id=156002,Text='——其实，根本就没有传说秘方，黄金蛋炒饭也只是再普通不过的料理！',Speaker=892123,}",
  [156003] = "{id=156003,Text='收集魔锅蛋液的过程中，我们遇到了一只存活千年的魔锅蛋，并看到了它的记忆。',Speaker=892123,}",
  [156004] = "{id=156004,Text='所谓的秘方，只是千年前勇士们的美食日记。',Speaker=892123,}",
  [156005] = "{id=156005,Text='黄金蛋炒饭，也不过是就地取材的行军伙食。',Speaker=892123,}",
  [156006] = "{id=156006,Text='真正让食用者充满力量的，是厨师烹制蛋炒饭时的爱，是伙伴并肩作战产生的勇气。',Speaker=892123,}",
  [156007] = "{id=156007,Text='爱和勇气的味道，才是蛋炒饭美味的真谛！',Speaker=892123,}",
  [156008] = "{id=156008,Text='爱和勇气的味道，说的很好。',Speaker=892130,}",
  [156009] = "{id=156009,Text='美食料理大赛的初衷，本就是为了推动料理进步，为世人献上更多温暖和享受。',Speaker=892130,}",
  [156010] = "{id=156010,Text='我认为，本次料理大赛的冠军已经出现了，各位都没有异议吧。',Speaker=892130,}",
  [156011] = "{id=156011,Text='没有没有！',Speaker=892129,}",
  [156012] = "{id=156012,Text='名至实归！',Speaker=892131,}",
  [156013] = "{id=156013,Text='那么我宣布，本届美食料理大赛的冠军即为：[c][ffff00][PlayerName][-][/c]与特蕾莎组！',Speaker=892130,}",
  [156014] = "{id=156014,Text='[c][ffff00][PlayerName][-][/c]，大成功！我们真的获得冠军啦！',Speaker=892123,Emoji=17,}",
  [156015] = "{id=156015,Text='……可是，分别的时候也到了。皇家料理学院向我发来通知书，你也要继续其他冒险啦。',Speaker=892123,Option='{别难过,0},{有机会再见,0}',}",
  [156016] = "{id=156016,Text='嗯嗯。分别之前，我要告诉你一个小秘密：',Speaker=892123,}",
  [156017] = "{id=156017,Text='做这份黄金蛋炒饭的时候，我想的全是你~~~',Speaker=892123,Option='！！！',Emoji=2,}",
  [156018] = "{id=156018,Text='哈哈哈哈~[c][ffff00][PlayerName][-][/c]，再见啦！',Speaker=892123,}",
  [156019] = "{id=156019,Text='等我毕业，再给你做黄金蛋炒饭呀！',Speaker=892123,Option='再见，特蕾莎。',}",
  [156076] = "{id=156076,Text='魔锅蛋液的数量已经足够了，现在就回去找特蕾莎吧。',}",
  [156088] = "{id=156088,Text='太可疑了，真是太可疑了……',Speaker=892145,}",
  [156089] = "{id=156089,Text='一定有秘密的说~',Speaker=892145,Option='{有什么烦恼嘛,0},{秘密，什么秘密,0}',}",
  [156090] = "{id=156090,Text='（惊吓）啊！是冒险者……emmmm……（压低声音）露比有事想要拜托冒险者的说，可不可以帮帮露比？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892145,Option='{好呀,2},{现在不行,3}',}",
  [156091] = "{id=156091,Text='唔……那算了……',Speaker=892145,}",
  [156092] = "{id=156092,Text='露比就知道冒险者最好了！',Speaker=892145,Emoji=2,}",
  [156093] = "{id=156093,Text='唔，露比最好的朋友伊莉雅，最近怪怪的！',Speaker=892145,Option='哦？',}",
  [156094] = "{id=156094,Text='伊莉雅被称为“银月魔女”，以前总是板着脸不苟言笑的，但最近会莫名其妙傻笑的说~还有还有，伊莉雅最近总是心不在焉的，配置魔药的时候失误了好多次的说~',Speaker=892145,}",
  [156095] = "{id=156095,Text='唔，伊莉雅一定是瞒着露比，有了自己的小秘密！',Speaker=892145,Option='{有秘密很正常吧,0},{……,0}',}",
  [156096] = "{id=156096,Text='不行不行，好姐妹之间怎么能有小秘密呢！',Speaker=892145,Emoji=12,}",
  [156097] = "{id=156097,Text='唔，总之拜托你了冒险者！去帮露比调查清楚伊莉雅的秘密吧~',Speaker=892145,Option='{行吧,0},{我尽力,0}',}",
  [156098] = "{id=156098,Text='冒险者可真是个好人呢~',Speaker=892145,Emoji=6,}",
  [156099] = "{id=156099,Text='伊莉雅就住在普隆德拉西南的说~快去快回哦~',Speaker=892145,Option='好的',}",
  [156100] = "{id=156100,Text='（害羞）……',Speaker=892146,Emoji=38,}",
  [156101] = "{id=156101,Text='（这应该就是伊莉雅了，看看她在干什么）',}",
  [156102] = "{id=156102,Text='真是的……突然说这么让人害羞的话……',Speaker=892146,}",
  [156103] = "{id=156103,Text='我也想你啦………我昨晚………………还梦到你了……',Speaker=892146,Emoji=2,}",
  [156104] = "{id=156104,Text='（……似乎说了什么……）',Speaker=892148,Emoji=14,}",
  [156105] = "{id=156105,Text='（害羞）讨厌啦………………',Speaker=892146,Emoji=38,}",
  [156106] = "{id=156106,Text='（伊莉雅在和谁说话？嗯……拍下来给露比看看吧）',}",
  [156107] = "{id=156107,Text='（把照片拿给露比看吧……）',}",
  [156108] = "{id=156108,Text='冒险者回来了~有没有调查出伊莉雅为什么这么反常的说~',Speaker=892145,}",
  [156109] = "{id=156109,Text='唔……这是——一台通讯仪？',Speaker=892145,}",
  [156110] = "{id=156110,Text='露比知道！即使距离很远的两人也可以用它通话，是很方便的通讯工具哦~',Speaker=892145,}",
  [156111] = "{id=156111,Text='所以所以——伊莉雅在跟什么人通话呢？',Speaker=892145,Option='转述的通话内容',Emoji=8,}",
  [156112] = "{id=156112,Text='这是……难道伊莉雅她？',Speaker=892145,Option='{我觉得她恋爱了,0},{孩子长大了,0}',}",
  [156113] = "{id=156113,Text='恋……恋爱……怎么会……',Speaker=892145,Emoji=1,}",
  [156114] = "{id=156114,Text='可是……伊莉雅为什么不告诉露比呢？',Speaker=892145,Option='{可能是害羞吧,0},{表面姐妹？,0}',}",
  [156115] = "{id=156115,Text='不行！露比要去找伊莉雅问清楚！',Speaker=892145,Emoji=12,}",
  [156116] = "{id=156116,Text='（不知道她们沟通得怎样……明天再来问问露比吧！）',}",
  [156117] = "{id=156117,Text='露比该怎么办呢……',Speaker=892145,Option='怎么了？',Emoji=19,}",
  [156118] = "{id=156118,Text='诶~是冒险者呀！唔，还是因为伊莉雅的事……',Speaker=892145,}",
  [156119] = "{id=156119,Text='冒险者也知道的，伊莉雅恋爱了！不过是跟一个素未谋面的人的说~',Speaker=892145,}",
  [156120] = "{id=156120,Text='伊莉雅会不会被坏人欺骗啊，露比好担心的说~',Speaker=892145,Emoji=37,}",
  [156121] = "{id=156121,Text='冒险者，能不能帮露比调查一下伊莉雅的恋爱对象呢？',Speaker=892145,Option='好的',}",
  [156122] = "{id=156122,Text='太好了！',Speaker=892145,}",
  [156123] = "{id=156123,Text='这是一台“魔力追踪器”，可以用来追踪通讯仪的信号哦~',Speaker=892145,}",
  [156124] = "{id=156124,Text='有了它就能找到伊莉雅的恋爱对象了哦~',Speaker=892145,Option='明白了',Emoji=39,}",
  [156125] = "{id=156125,Text='（害羞）其实……我也想早点见到你呢~',Speaker=892146,Emoji=2,}",
  [156126] = "{id=156126,Text='（伊莉雅正在与恋人通话，快使用追踪器追踪讯号吧）',}",
  [156127] = "{id=156127,Text='（追踪器的指针指向西北）',}",
  [156128] = "{id=156128,Text='（追踪器的指针指向西面）',}",
  [156129] = "{id=156129,Text='（前面有一台通讯仪，但没有人在使用）',}",
  [156130] = "{id=156130,Text='（到附近看看吧）',}",
  [156131] = "{id=156131,Text='（一只溜溜猴正在被蝗虫围攻……）',}",
  [156132] = "{id=156132,Text='救……救命！',Speaker=892147,Emoji=37,}",
  [156133] = "{id=156133,Text='（它会说话？或许可以问问他看没看到有人使用过通讯仪，先救下它吧）',}",
  [156134] = "{id=156134,Text='呼……吓死猴了……',Speaker=892147,}",
  [156135] = "{id=156135,Text='谢谢你，勇敢的冒险者。我是溜溜猴乐团的团长鲁邦（自豪脸），溜溜猴乐团会铭记你的帮助！',Speaker=892147,Option='溜溜猴乐团？',Emoji=6,}",
  [156136] = "{id=156136,Text='你不知道嘛？我们可是普隆德拉最著名的乐队！',Speaker=892147,Option='询问通讯仪',}",
  [156137] = "{id=156137,Text='使用通讯仪的——人——类？',Speaker=892147,}",
  [156138] = "{id=156138,Text='如果想要情报的话，最好拿别的东西来交换哦……',Speaker=892147,Option='{啊？？,0},{什么东西,0}',Emoji=5,}",
  [156139] = "{id=156139,Text='其实我到这里来是为了采集一些鲜花，但是因为那些烦人的蝗虫，我的腿受了点伤……',Speaker=892147,}",
  [156140] = "{id=156140,Text='如果你能给我带来一些鲜花，我就把我看到的告诉你。',Speaker=892147,Option='好吧',}",
  [156142] = "{id=156142,Text='动作还挺快的呢，谢谢你了~',Speaker=892147,Option='可以告诉我了吧',Emoji=9,}",
  [156143] = "{id=156143,Text='不要心急嘛………',Speaker=892147,}",
  [156144] = "{id=156144,Text='其实……[c][ffff00]我没有——看到——人——使用那台通讯仪哦！[-][/c]',Speaker=892147,Option='{？？？,0},{那你还……,0}',}",
  [156145] = "{id=156145,Text='别生气啊，“没有看到人使用通讯仪”不也是一条情报嘛？',Speaker=892147,Emoji=26,}",
  [156146] = "{id=156146,Text='总之，今天谢谢你了~',Speaker=892147,}",
  [156147] = "{id=156147,Text='（可恶的猴子…………）',Emoji=12,}",
  [156148] = "{id=156148,Text='（没有找到伊莉雅的恋爱对象……明天去广场找露比回报情况~）',}",
  [156149] = "{id=156149,Text='可恶，气死猴了！',Speaker=892147,Option='{你怎么了,0},{生气伤肝哦,0}',Emoji=12,}",
  [156150] = "{id=156150,Text='哦，是你呀？昨天谢谢你了。',Speaker=892147,Option='为什么生气',}",
  [156151] = "{id=156151,Text='可恶的莫里亚蒂，不肯把那个[c][ffff00]玩偶[-][/c]卖给我！哼，我还非要不可了！',Speaker=892147,}",
  [156152] = "{id=156152,Text='冒险者，可以请你帮我一个忙嘛？',Speaker=892147,Option='{什么忙？,0},{好呀,0}',}",
  [156153] = "{id=156153,Text='南门的玩具商人莫里亚蒂新进了一个[c][ffff00]巨型波利玩偶[-][/c]，整个普隆德拉只有他那里才能买到……',Speaker=892147,}",
  [156154] = "{id=156154,Text='可是莫里亚蒂竟然说我只是一只溜溜猴，根本不懂得欣赏他的“艺术品”，不肯将玩偶卖给我！',Speaker=892147,Emoji=12,}",
  [156155] = "{id=156155,Text='冒险者，你可以帮我把那个玩偶买回来嘛？',Speaker=892147,Option='好吧',}",
  [156156] = "{id=156156,Text='谢谢你~莫里亚蒂的商店就在普隆德拉南门。',Speaker=892147,}",
  [156157] = "{id=156157,Text='我这儿的玩具可都是万里挑一的珍藏！',Speaker=892149,}",
  [156158] = "{id=156158,Text='年轻的冒险者，要不要看看我的藏品？',Speaker=892149,Option='巨大波利玩偶',}",
  [156159] = "{id=156159,Text='啊……你喜欢那个！那可是我藏品中最顶尖的！保证整个普隆德拉都找不出第二个来！',Speaker=892149,}",
  [156160] = "{id=156160,Text='不过这么珍贵的玩偶，可不是花钱就能买得到的……刚刚有只溜溜猴，想花1000WZeny，我都没有卖给它，它的美感……岂是一只猴子能明白的……',Speaker=892149,Option='卖给我可以嘛',Emoji=12,}",
  [156161] = "{id=156161,Text='嗯……想要买也不是不可以。（压低声音）我最近有一个新的企划：我要做一个卡普拉小姐的等身手办……',Speaker=892149,}",
  [156162] = "{id=156162,Text='但是创作嘛，总不能凭空捏造，所以我需要卡普拉小姐姐的身材数据……',Speaker=892149,Emoji=5,}",
  [156163] = "{id=156163,Text='（听起来……怪怪的……这不会是在犯罪吧…………不过为了鲁邦的玩偶……）',Option='好吧',}",
  [156164] = "{id=156164,Text='帮我拍几张小姐姐的照片作为参考就可以了，一定要拍得清~楚~点~哦~~~',Speaker=892149,Emoji=20,}",
  [156165] = "{id=156165,Text='…………',}",
  [156166] = "{id=156166,Text='啊~你回来了！拿到我想要的东西了吗？',Speaker=892149,Option='在这儿',}",
  [156167] = "{id=156167,Text='让我看看……干得不错！',Speaker=892149,}",
  [156168] = "{id=156168,Text='好吧，我就遵守约定把玩偶卖给你吧？不过钱可一分都不能少哦！',Speaker=892149,Emoji=17,}",
  [156169] = "{id=156169,Text='想不到你还挺有钱的……玩偶就交给你了，以后常来照顾我的生意啊~',Speaker=892149,}",
  [156170] = "{id=156170,Text='你回来了！',Speaker=892147,}",
  [156171] = "{id=156171,Text='你成功把玩偶买回来了？十分感谢！',Speaker=892147,}",
  [156172] = "{id=156172,Text='我就知道那个可恶的商人，就是因为我是溜溜猴才不把玩偶卖给我的，这是赤裸裸的歧视！',Speaker=892147,Emoji=12,}",
  [156173] = "{id=156173,Text='（沉默）…………',Speaker=892147,Emoji=14,}",
  [156174] = "{id=156174,Text='（鲁邦看起来有些落寞）',Option='你怎么了',}",
  [156175] = "{id=156175,Text='其实——这个玩偶是我准备送给一个女孩子的礼物。她最喜欢这样可爱的玩偶了……',Speaker=892147,}",
  [156176] = "{id=156176,Text='但是……我有些担心，她会不会也歧视我溜溜猴的身份呢……',Speaker=892147,Option='安慰',Emoji=25,}",
  [156177] = "{id=156177,Text='……好吧，总之今天要谢谢你，冒险者！',Speaker=892147,}",
  [156178] = "{id=156178,Text='那个，明天我想把这个玩偶送给那个女孩子，可以请你替我加油打气嘛？',Speaker=892147,Option='{……,0},{……行吧,0}',}",
  [156179] = "{id=156179,Text='太好了，明天在普拉德隆广场，一定要来啊~',Speaker=892147,}",
  [156180] = "{id=156180,Text='糟糕，真是太糟糕了……',Speaker=892145,Option='怎么了',Emoji=15,}",
  [156181] = "{id=156181,Text='伊莉雅跟“恋爱对象”约定好了今天见面……',Speaker=892145,Option='{诶？,0},{这是好事啊~,0}',}",
  [156182] = "{id=156182,Text='但是对方到现在还没有来的说~',Speaker=892145,Option='……',}",
  [156183] = "{id=156183,Text='（沮丧）',Speaker=892146,Emoji=14,}",
  [156184] = "{id=156184,Text='露比很担心的说……可以帮露比安慰一下伊莉雅嘛？',Speaker=892145,Option='我该怎么做',}",
  [156185] = "{id=156185,Text='伊莉雅很喜欢吃甜食的说。露比刚刚在广场附近看到了卖冰淇淋的小哥哥，不如就给伊莉雅买些冰淇淋吧~',Speaker=892145,}",
  [156186] = "{id=156186,Text='（地面上似乎有什么东西？）',}",
  [156187] = "{id=156187,Text='（这不是……鲁邦的玩偶和花束嘛？这是怎么回事……）',Emoji=8,}",
  [156188] = "{id=156188,Text='这位冒险者，要点清爽美味的冰淇淋嘛~',Speaker=892150,Option='好呀',}",
  [156189] = "{id=156189,Text='好嘞~您拿好……诶？',Speaker=892150,Option='怎么了',Emoji=8,}",
  [156190] = "{id=156190,Text='您手里这个玩偶……看着很眼熟……',Speaker=892150,Option='追问',}",
  [156191] = "{id=156191,Text='我刚刚看到一只溜溜猴，好像拿着一样的玩偶，在广场附近鬼鬼祟祟的……',Speaker=892150,}",
  [156192] = "{id=156192,Text='（拿着玩偶的溜溜猴……那应该就是……）',Option='鲁邦',}",
  [156193] = "{id=156193,Text='（难道…………鲁邦就是……）',Option='{伊莉雅的恋人？,2},{恰巧路过的猴子,3}',}",
  [156194] = "{id=156194,Text='（这样就说得通了！快把这个发现告诉伊莉雅把！）',Emoji=11,}",
  [156195] = "{id=156195,Text='（鲁邦”恰巧“出现在通讯仪附近，”恰巧“出现在伊莉雅约定的见面地点，又恰巧带着送给心爱女孩的礼物——不对，这并不是巧合！）',Emoji=31,}",
  [156196] = "{id=156196,Text='（沮丧）……',Speaker=892146,Emoji=14,}",
  [156197] = "{id=156197,Text='冒险者，你有什么事嘛？',Speaker=892146,Option='告知推测',}",
  [156198] = "{id=156198,Text='（震惊）你是说……鲁邦其实是一只溜溜猴？',Speaker=892146,Emoji=7,}",
  [156199] = "{id=156199,Text='…………',Speaker=892146,}",
  [156200] = "{id=156200,Text='………………',Speaker=892146,}",
  [156201] = "{id=156201,Text='（叹气）其实，我并不太在乎他是人类还是溜溜猴……',Speaker=892146,Option='诶？',Emoji=25,}",
  [156202] = "{id=156202,Text='从谈话中，我可以感受到它的温柔……我相信它一定能够保护好我，让我幸福……',Speaker=892146,}",
  [156203] = "{id=156203,Text='所以……为什么它不肯出现呢？',Speaker=892146,Option='担心他的身份',}",
  [156204] = "{id=156204,Text='……',Speaker=892146,}",
  [156205] = "{id=156205,Text='哼！它就这么不信任我嘛！',Speaker=892146,Emoji=12,}",
  [156206] = "{id=156206,Text='第一次见面就敢放女孩子鸽子！实在是太过分了！我不想理它了！',Speaker=892146,}",
  [156207] = "{id=156207,Text='（鲁邦究竟去哪儿了，去附近找找他吧）',}",
  [156208] = "{id=156208,Text='（难道…………鲁邦就是……）',Option='{伊莉雅的恋人？,2}',}",
  [156209] = "{id=156209,Text='（呃——伊莉雅真的生气了吗……女孩子的心思很难猜呢……）',}",
  [156210] = "{id=156210,Text='（草丛中有个熟悉的身影，不正是鲁邦吗？）',}",
  [156211] = "{id=156211,Text='冒险者……你怎么在这儿？',Speaker=892147,Option='{伊莉雅生气了哦,0},{为什么逃避呢,0}',}",
  [156212] = "{id=156212,Text='呃……原来你知道了啊……',Speaker=892147,}",
  [156213] = "{id=156213,Text='伊莉雅是我心爱的女孩子，我担心她会在意我的身份……',Speaker=892147,Option='伊莉雅的心意',}",
  [156214] = "{id=156214,Text='（惊讶）什么？你说伊莉雅并不在乎的我种族……',Speaker=892147,}",
  [156215] = "{id=156215,Text='……我真傻…………伊莉雅怎么会是那样的人呢……',Speaker=892147,Emoji=42,}",
  [156216] = "{id=156216,Text='…………',Speaker=892147,}",
  [156217] = "{id=156217,Text='冒险者，我想做点什么向伊莉雅道歉，可以请你明天到西门来找我嘛？',Speaker=892147,Option='好的',}",
  [156218] = "{id=156218,Text='[c][ffff00][PlayerName][-][/c]，你终于来了，我等你好久了！',Speaker=892147,}",
  [156219] = "{id=156219,Text='我决定了，要为伊莉雅准备一场音乐会，向她表达心意！',Speaker=892147,Option='{诶？,0},{不错的想法,0}',Emoji=2,}",
  [156220] = "{id=156220,Text='但现在有很多准备工作要做，能不能请你帮助我？',Speaker=892147,}",
  [156221] = "{id=156221,Text='我需要一些鲜花布置场地，可以帮我采集一些鲜花嘛？',Speaker=892147,Option='好吧',}",
  [156222] = "{id=156222,Text='谢谢~现在乐团成员也到齐了，让我们排练一下吧~',Speaker=892147,}",
  [156223] = "{id=156223,Text='可以为我们提些意见嘛，冒险者？',Speaker=892147,Option='好的',}",
  [156224] = "{id=156224,Text='感觉有些不整齐呢，是谁没跟上节奏呢？？',Speaker=892147,Option='{安迪,2},{艾伦,2},{里昂,3}',}",
  [156225] = "{id=156225,Text='不对不对……他们唱的没有问题哦~',Speaker=892147,}",
  [156226] = "{id=156226,Text='嗯，没错！就是你，里昂！注意跟上大家的节奏！',Speaker=892147,}",
  [156227] = "{id=156227,Text='那么再听一遍吧，冒险者！',Speaker=892147,}",
  [156228] = "{id=156228,Text='（鸣叫）',Speaker=892156,}",
  [156229] = "{id=156229,Text='（鸣叫）（鸣叫）',Speaker=892156,}",
  [156230] = "{id=156230,Text='这些可恶的蝗虫，总是叫个不停……一定会影响到音乐会的效果……',Speaker=892147,}",
  [156231] = "{id=156231,Text='冒险者，可以请你将它们赶走嘛！',Speaker=892147,Option='好的',}",
  [156232] = "{id=156232,Text='现在——一切都准备就绪了！',Speaker=892147,}",
  [156233] = "{id=156233,Text='可以请你帮我邀请伊莉雅过来嘛？',Speaker=892147,Option='好的',}",
  [156234] = "{id=156234,Text='一定不要说是我请她来的哦，要给她一个惊喜！',Speaker=892147,Emoji=20,}",
  [156235] = "{id=156235,Text='鲁邦他……在干什么呢？',Speaker=892146,Emoji=14,}",
  [156236] = "{id=156236,Text='为什么它还不跟我通话……',Speaker=892146,}",
  [156237] = "{id=156237,Text='啊，你好冒险者，有什么事嘛？',Speaker=892146,Option='邀请',Emoji=7,}",
  [156238] = "{id=156238,Text='你……邀请我去西门？',Speaker=892146,Option='嗯',}",
  [156239] = "{id=156239,Text='（狐疑）真的是你邀请我嘛？',Speaker=892146,Option='总之，快去啦',Emoji=8,}",
  [156240] = "{id=156240,Text='好吧……',Speaker=892146,}",
  [156241] = "{id=156241,Text='是你太美丽~~~是我过分的在意~~',Speaker=892191,}",
  [156242] = "{id=156242,Text='遇见你~~只愿我用一生去爱你~~',Speaker=892191,}",
  [156243] = "{id=156243,Text='你眼中有我~~我梦中有你~~~',Speaker=892191,}",
  [156244] = "{id=156244,Text='你像天使突然降临~~是我太大意~~',Speaker=892147,}",
  [156245] = "{id=156245,Text='原来你一直~~都在我心里~~~',Speaker=892147,}",
  [156246] = "{id=156246,Text='想要靠近~~想要把你抱紧~~~啦啦~~~',Speaker=892147,}",
  [156247] = "{id=156247,Text='…………',Speaker=892146,}",
  [156248] = "{id=156248,Text='伊莉雅，可以原谅我吗？',Speaker=892147,Emoji=38,}",
  [156249] = "{id=156249,Text='……',Speaker=892146,}",
  [156250] = "{id=156250,Text='虽然……我们种族不同，但爱情跟音乐一样，没有种族的界限！',Speaker=892147,}",
  [156251] = "{id=156251,Text='可以给我个机会，让我保护你嘛！',Speaker=892147,Emoji=27,}",
  [156252] = "{id=156252,Text='……',Speaker=892146,}",
  [156253] = "{id=156253,Text='（笑）其实我并不在乎你是溜溜猴还是人类啦，这么长时间的相处，我已经感受到你的善良还有温柔……我相信你~',Speaker=892146,Emoji=38,}",
  [156254] = "{id=156254,Text='其实溜溜猴挺好的，毛茸茸的多可爱……',Speaker=892146,}",
  [156255] = "{id=156255,Text='伊莉雅，谢谢你……',Speaker=892147,}",
  [156256] = "{id=156256,Text='…………',}",
  [156257] = "{id=156257,Text='啊！冒险者，你来了~',Speaker=892147,}",
  [156258] = "{id=156258,Text='我和伊莉雅的事多亏了你帮忙~~',Speaker=892147,}",
  [156259] = "{id=156259,Text='要不是你，我们很可能就错过了~~',Speaker=892147,}",
  [156260] = "{id=156260,Text='不会的，哪怕在茫茫人海中，我们也能找到彼此！',Speaker=892146,}",
  [156261] = "{id=156261,Text='（酸到掉牙了……）',}",
  [156262] = "{id=156262,Text='希望你也能早日找到自己那个TA~',Speaker=892147,Option='{会的会的,0},{已经找到了~,0}',}",
  [156263] = "{id=156263,Text='那~再见了~~',Speaker=892147,}",
  [156655] = "{id=156655,Text='冒险者，你留意到了吗？',Speaker=892196,Option='异常的天象？',}",
  [156656] = "{id=156656,Text='没错，这实在是太奇怪了。',Speaker=892196,}",
  [156657] = "{id=156657,Text='而形状这样古怪的彩虹流星，我也是第一次见到……',Speaker=892196,}",
  [156658] = "{id=156658,Text='眼前的这一切都和我已有的天文知识相悖……',Speaker=892196,}",
  [156659] = "{id=156659,Text='真是令人困惑……',Speaker=892196,Emoji=19,}",
  [156660] = "{id=156660,Text='冒险者，你有没有觉得事态有些异常？',Speaker=892200,Option='怎么了',}",
  [156661] = "{id=156661,Text='我也说不准，但总有种不妙的预感……',Speaker=892200,}",
  [156662] = "{id=156662,Text='灾祸要降临了吗？',Speaker=892200,Emoji=37,}",
  [156663] = "{id=156663,Text='最近，我们大家都在议论天上奇怪的流星……',Speaker=892212,}",
  [156664] = "{id=156664,Text='虽然各式各样的说法都有，但我都不相信。',Speaker=892212,}",
  [156665] = "{id=156665,Text='要依我看的话，还是再等些日子吧。',Speaker=892212,}",
  [156666] = "{id=156666,Text='如果那时彩虹还不消散，再担心也不晚嘛！',Speaker=892212,Emoji=17,}",
  [156667] = "{id=156667,Text='快看，天上的星星状的东西！',Speaker=892213,}",
  [156668] = "{id=156668,Text='在洛阳生活了许多年了，我可从来没有见过这样奇怪的流星！',Speaker=892213,Emoji=19,}",
  [156669] = "{id=156669,Text='不仅如此，天气也变得诡异了……',Speaker=892213,}",
  [156670] = "{id=156670,Text='这一切究竟是怎么了！',Speaker=892213,Emoji=15,}",
  [156671] = "{id=156671,Text='如你所见，冒险者，我们开始行动了。',Speaker=892226,}",
  [156672] = "{id=156672,Text='魔导炮必须要尽快准备好并按时发射。',Speaker=892226,}",
  [156673] = "{id=156673,Text='但愿一切都还来得及……',Speaker=892226,}",
  [156674] = "{id=156674,Text='看来情势比我想象中的要严重……人们已经在议论着魔导炮计划了。',Speaker=892239,}",
  [156675] = "{id=156675,Text='唉，现如今也只好走一步看一步了。',Speaker=892239,}",
  [156676] = "{id=156676,Text='希望我们大家最终都可以渡过难关……',Speaker=892239,Emoji=15,}",
  [156677] = "{id=156677,Text='不知道是不是我的幻觉，为什么觉得天上的彩虹变得越来越大了……',Speaker=892240,}",
  [156678] = "{id=156678,Text='等等，该不会是，它是在往下掉吧！',Speaker=892240,Emoji=42,}",
  [156679] = "{id=156679,Text='太可怕了，就没人想想办法吗！',Speaker=892240,}",
  [156680] = "{id=156680,Text='比如，我们可以在洛阳撑起魔法结界什么的挡住它……',Speaker=892240,}",
  [156681] = "{id=156681,Text='或者，也可以把天上的彩虹击碎成碎片！',Speaker=892240,}",
  [156682] = "{id=156682,Text='你好，冒险者，我们是吉芬魔法公会的特派魔法师。',Speaker=892253,}",
  [156683] = "{id=156683,Text='魔导炮的修建与维护将由我们负责！',Speaker=892253,}",
  [156684] = "{id=156684,Text='这次的魔导炮计划是集结了整个王国的智慧与力量的壮举。',Speaker=892253,}",
  [156685] = "{id=156685,Text='我们一定会带领大家从这次彩虹危机中走出来的！',Speaker=892253,}",
  [156686] = "{id=156686,Text='事情的进展已经到了一个让我看不懂的地步了……',Speaker=892307,}",
  [156687] = "{id=156687,Text='以后我可再也不敢说自己是一个经验丰富的猎人了。',Speaker=892307,}",
  [156688] = "{id=156688,Text='最近这里涌现了许多彩虹色的魔物，拜托，是彩虹色的！',Speaker=892307,}",
  [156689] = "{id=156689,Text='我说，你见过彩虹色的波利吗？',Speaker=892307,Emoji=1,}",
  [156690] = "{id=156690,Text='没有想到斐扬树林的大瀑布有一天也会倒流！',Speaker=892308,}",
  [156691] = "{id=156691,Text='王国的学者们正在研究这一现象，据他们说，是彩虹碎片的能量使重力规则发生了改变',Speaker=892308,}",
  [156692] = "{id=156692,Text='嘿，先别管这个，我们还是先快去欣赏一下吧！',Speaker=892308,}",
  [156693] = "{id=156693,Text='这可是百年难遇的奇观呢！',Speaker=892308,Emoji=17,}",
  [156694] = "{id=156694,Text='冒险者，在你眼前的是一道壮丽的奇观！',Speaker=892311,}",
  [156695] = "{id=156695,Text='大海被分成了两半，这可真是太惊人了。',Speaker=892311,Emoji=7,}",
  [156696] = "{id=156696,Text='我可要多拍些照片回去，我的同行可就没有这样的幸运了嘿嘿。',Speaker=892311,}",
  [156697] = "{id=156697,Text='你问我这里发生了什么？',Speaker=892319,}",
  [156698] = "{id=156698,Text='我什么都不记得了……',Speaker=892319,}",
  [156699] = "{id=156699,Text='彩虹坠落在这里的那天，我好像昏睡了过去。',Speaker=892319,}",
  [156700] = "{id=156700,Text='等再醒来的时候，我就变成这样子了……',Speaker=892319,Emoji=36,}",
  [156701] = "{id=156701,Text='彩虹流星坠落在了妙勒尼山脉山顶……',Speaker=892316,}",
  [156702] = "{id=156702,Text='似乎把那里的房屋给击碎了！',Speaker=892316,}",
  [156703] = "{id=156703,Text='而这还不算什么，最诡异的是，时间就好像静止了一样……',Speaker=892316,}",
  [156704] = "{id=156704,Text='房屋的残骸都飘荡在空中！',Speaker=892316,Emoji=1,}",
  [156705] = "{id=156705,Text='冒险者，你可得去看看！',Speaker=892316,}",
  [156706] = "{id=156706,Text='伊达平原如今处于一种奇异的状况中。',Speaker=892321,}",
  [156707] = "{id=156707,Text='整个原野都是一半白天一半黑天。',Speaker=892321,}",
  [156708] = "{id=156708,Text='不得不说，白日与黑夜交织的原野有很强的压迫感……',Speaker=892321,}",
  [156709] = "{id=156709,Text='我都有些喘不过气来了！',Speaker=892321,Emoji=37,}",
  [156710] = "{id=156710,Text='你好，来自远方的客人。',Speaker=892325,}",
  [156711] = "{id=156711,Text='你也是为了御神樱的事才来这儿的？',Speaker=892325,}",
  [156712] = "{id=156712,Text='彩虹流星从天而降，正砸在这棵樱花树上，于是它就变成了现在的样子。',Speaker=892325,}",
  [156713] = "{id=156713,Text='附近的居民觉得这是天神显现的神迹，将它称为“御神樱”。',Speaker=892325,}",
  [156714] = "{id=156714,Text='只是——这真的是神迹吗？我只希望它的存在不会威胁天津町的安全！',Speaker=892325,}",
  [156715] = "{id=156715,Text='喵~我要回去和大家怎么汇报呢？',Speaker=892207,}",
  [156716] = "{id=156716,Text='有了，我就这么和大家说—',Speaker=892207,}",
  [156717] = "{id=156717,Text='在大陆的上空，出现了一块大大的彩虹糖！',Speaker=892207,Option='……',}",
  [156718] = "{id=156718,Text='怎么样，是不是被我的观察力折服了！',Speaker=892207,Emoji=17,}",
  [156719] = "{id=156719,Text='等彩虹糖掉下来的时候，我们就可以围着它举办一次宴会了！',Speaker=892207,}",
  [156750] = "{id=156750,Text='咦，天空中出现了什么东西？流星吗？',Speaker=1373,}",
  [156751] = "{id=156751,Text='要来一份料理吗……咦，天空是怎么了？',Speaker=1196,}",
  [156752] = "{id=156752,Text='卡普拉为您服务……您看见今天的星星了吗？',Speaker=1269,}",
  [156753] = "{id=156753,Text='孩子们都跑去看天上奇怪的星星去了……',Speaker=2158,}",
  [156754] = "{id=156754,Text='天空中闪闪发光的是什么？好耀眼！',Speaker=2628,}",
  [156755] = "{id=156755,Text='喵，天上的是什么东西？真叫人搞不懂……',Speaker=2157,}",
  [156756] = "{id=156756,Text='洛阳出现了异常的天象……',Speaker=809004,}",
  [156757] = "{id=156757,Text='我们需要尽快调查天空中的异常状况。',Speaker=809001,}",
  [156886] = "{id=156886,Text='你好，来自远方的冒险者！',Speaker=892389,}",
  [156887] = "{id=156887,Text='你是来参观御神樱的？怎么样，很神奇吧？',Speaker=892389,}",
  [156888] = "{id=156888,Text='不过它过去可不是这样子的哦~~',Speaker=892389,Emoji=19,}",
  [156889] = "{id=156889,Text='之所以变成这样，还要从彩虹桥碎片的陨落说起……',Speaker=892389,}",
  [156898] = "{id=156898,Text='现在，王国最艰巨的任务没有登记在我的看板上，而是由哥布灵森林的督造官发布的哦！',Speaker=1373,}",
  [156899] = "{id=156899,Text='就算天上的星星有即将坠落的危险，今天的料理也请一定要认真享用哦！',Speaker=1196,}",
  [156900] = "{id=156900,Text='欢迎来到普隆德拉，天上的星星要掉下来了，现在的大家都在议论这件事呢！',Speaker=1269,}",
  [156901] = "{id=156901,Text='孩子们似乎很喜欢那个魔导炮嘛，嘿嘿，我想我知道要推出什么新玩具了。',Speaker=2158,}",
  [156902] = "{id=156902,Text='吉芬的许多魔法师都前往了哥布灵森林，听说是为了魔导炮计划……',Speaker=2628,}",
  [156903] = "{id=156903,Text='喵，可以告诉小友，魔导炮是什么吗？是不是一件需要小友附魔的强大武器哟？',Speaker=2157,}",
  [156904] = "{id=156904,Text='客人要来一份料理吗？解决这次彩虹危机也要有本料理大师的助力哦！',Speaker=809004,}",
  [156905] = "{id=156905,Text='那颗奇怪的星星似乎离我们越来越近了，必须要尽快想办法解决这次危机……',Speaker=809001,}",
  [156906] = "{id=156906,Text='那颗奇怪的星星消失了，王国的魔导炮计划成功了！',Speaker=1373,}",
  [156907] = "{id=156907,Text='碎片坠落后，战斗就接连不断地到来……冒险者们的补给可一定要备足哦！',Speaker=1196,}",
  [156908] = "{id=156908,Text='卡普拉竭诚为您服务，您要前往彩虹奇观的发生地吗？',Speaker=1269,}",
  [156909] = "{id=156909,Text='有孩子告诉我他看到过彩虹色的波利，这可真是稀奇的事情……',Speaker=2158,}",
  [156910] = "{id=156910,Text='那天的流星雨可真漂亮，冒险者，你也看到了吗？',Speaker=2628,}",
  [156911] = "{id=156911,Text='喵，奇怪的事情一件接一件发生，听说现在又出现了彩虹奇观……',Speaker=2157,}",
  [156912] = "{id=156912,Text='一场流星雨带走了洛阳上空的阴霾，我们大家得救了！',Speaker=809004,}",
  [156913] = "{id=156913,Text='彩虹桥碎片的陨落造成了一些魔物的变异……',Speaker=809001,}",
  [156968] = "{id=156968,Text='[c][ffff00][PlayerName][-][/c]，要开始演唱了，注意仔细听哦~',Speaker=892147,}",
  [156969] = "{id=156969,Text='3~2~1~预备~',Speaker=892147,}",
  [156970] = "{id=156970,Text='唱！',Speaker=892147,}",
  [156973] = "{id=156973,Text='兔叽兔叽！我在月亮上看到这里那么热闹，就下来看看，结果，差点就被当做肉兔，一去不反复~',Speaker=892400,}",
  [156974] = "{id=156974,Text='还好[c][ffff00][PlayerName][-][/c]出现，吓走了这些对我图谋不轨的人呢~',Speaker=892400,}",
  [156975] = "{id=156975,Text='[c][ffff00][PlayerName][-][/c],这份礼物就送给你啦~兔叽！',Speaker=892400,Option='{谢谢,2},{我才不要,3}',}",
  [156980] = "{id=156980,Text='这是我作为主席筹办的最后一届美食节，不得不说，年纪确实大了~',Speaker=892401,}",
  [156981] = "{id=156981,Text='感谢[c][ffff00][PlayerName][-][/c]为这次美食节做出的贡献，希望以后你也可以一如既往的喜欢这个盛大的节日！',Speaker=892401,}",
  [156982] = "{id=156982,Text='[c][ffff00][PlayerName][-][/c],这份礼物也希望你可以喜欢！',Speaker=892401,Option='{谢谢,2},{这多不好意思,3}',}",
  [157006] = "{id=157006,Text='你好，冒险者。想必斯宾诺学士已经说过，魔导炮必须要在[c][ffff00]一周内[-][/c]建造完成并进行发射。',Speaker=891026,}",
  [157007] = "{id=157007,Text='时间紧迫，建造魔导炮所需的海量材料我们一时难以调度……',Speaker=891026,Emoji=15,}",
  [157008] = "{id=157008,Text='因此作为这次魔导炮计划的督造官，我代表王国郑重地寻求你们的帮助。',Speaker=891026,}",
  [157009] = "{id=157009,Text='王国将感谢你们的奉献！',Speaker=891026,}",
  [157010] = "{id=157010,Text='经过前一天的筹备，魔导炮建造所需的材料已初步收集完成。',Speaker=891027,}",
  [157011] = "{id=157011,Text='如你所见，魔导炮将从今日起正式开始建造了。',Speaker=891027,}",
  [157012] = "{id=157012,Text='还请继续向我们提供材料方面的支援！',Speaker=891027,}",
  [157013] = "{id=157013,Text='魔导炮的建造还在紧张地进行当中……',Speaker=891028,}",
  [157014] = "{id=157014,Text='王国最出色的工匠以及魔法师们正在为此不懈地努力。',Speaker=891028,}",
  [157015] = "{id=157015,Text='我们还需要更多的材料！',Speaker=891028,}",
  [157016] = "{id=157016,Text='感谢你们的支援，我将向你们传达一个令人振奋的消息——',Speaker=891029,}",
  [157017] = "{id=157017,Text='魔导炮的修建即将完成！',Speaker=891029,Emoji=20,}",
  [157018] = "{id=157018,Text='请继续向我们提供材料！',Speaker=891029,}",
  [157019] = "{id=157019,Text='建造已经基本完毕了，魔导炮计划进入了筹备发射阶段。',Speaker=891030,}",
  [157020] = "{id=157020,Text='魔导炮的发射需要极为庞大的能量。',Speaker=891030,}",
  [157021] = "{id=157021,Text='所以，请继续向我们提供材料，拜托了！',Speaker=891030,}",
  [157022] = "{id=157022,Text='魔导炮的计划已经进入收尾阶段。',Speaker=891031,}",
  [157023] = "{id=157023,Text='在这些日子里，我们齐心协力，付出了汗水与辛劳。',Speaker=891031,}",
  [157024] = "{id=157024,Text='请允许我表达对你们的敬意。',Speaker=891031,}",
  [157025] = "{id=157025,Text='今天是斯宾诺学士预言的最后一天了……',Speaker=891032,}",
  [157026] = "{id=157026,Text='不论准备的如何，在今晚的八时我们都必须发射。',Speaker=891032,}",
  [157027] = "{id=157027,Text='愿神庇佑我们。',Speaker=891032,}",
  [157028] = "{id=157028,Text='干得漂亮！',Speaker=891033,}",
  [157029] = "{id=157029,Text='我们做到了，魔导炮成功击碎了彩虹桥残骸！',Speaker=891033,}",
  [157030] = "{id=157030,Text='这来之不易的胜利属于我们每一个人！',Speaker=891033,Emoji=20,}",
  [157031] = "{id=157031,Text='吉芬魔法公会的魔法师们用水晶球记录下了这珍贵的一幕，你想要再回看一次吗？',Speaker=891033,Option='{观看,2},{拒绝,3}',}",
  [157032] = "{id=157032,Text='想要重温的话可以再来找我！',Speaker=891033,}",
  [157033] = "{id=157033,Text='冒险者们在这次彩虹桥危机中给予了我们极大的帮助。',Speaker=891034,}",
  [157034] = "{id=157034,Text='为了表达对你们的敬意和感谢，我会记录下你们在这次危机中所做出的贡献。',Speaker=891034,}",
  [157035] = "{id=157035,Text='冒险者们每天可以到我这里来查看。',Speaker=891034,}",
  [157036] = "{id=157036,Text='受到斯宾诺学士的委托，近期我都会驻扎在哥布灵森林。',Speaker=891041,}",
  [157037] = "{id=157037,Text='为了表达对你们的敬意和感谢，我会记录下你们在这次危机中所做出的贡献。',Speaker=891041,}",
  [157038] = "{id=157038,Text='冒险者们每天可以到我这里来查看。',Speaker=891041,}",
  [157039] = "{id=157039,Text='美食节还需要一些物资，如果正好你有，欢迎来为美食节做出一些贡献~',Speaker=892402,}",
  [157040] = "{id=157040,Text='当然，作为回报，我也准备了一些小礼物给你~',Speaker=892402,}",
  [157041] = "{id=157041,Text='我终于查清楚了，出现在这里的居然是逝去的英灵！不过这英灵收到了控制，真是邪恶的手段！但是我也有应对的方法。',Speaker=892406,}",
  [157042] = "{id=157042,Text='我可以使用[c][ffff00]净化之息[-][/c]来净化英灵，让他恢复自身的意志，你可以提供给我这种特殊材料吗？',Speaker=892406,Option='当然',}",
  [157043] = "{id=157043,Text='很遗憾，你没有足够的[c][ffff00]净化之息[-][/c]，快去进行收集吧！',Speaker=892406,}",
  [157124] = "{id=157124,Text='奥丁在上！冒险者，你现在感觉怎么样？',Speaker=892466,Option='讲述经过',}",
  [157125] = "{id=157125,Text='唔，情况不妙啊……听你的讲述，诺曼有可能是被某种黑暗力量控制，内心深处的回忆遭到了篡改。这样下去可不成，必须想办法唤醒他！',Speaker=892466,Option='应该是这样',Emoji=18,}",
  [157126] = "{id=157126,Text='……冒险者，你愿意再试一次吗？进入诺曼的心灵，找到解救他的办法！',Speaker=892466,Option='{愿意,9},{再等等,8}',}",
  [157191] = "{id=157191,Text='冒险者们，到了说再见的时候了~',Speaker=892429,Emoji=24,}",
  [157192] = "{id=157192,Text='大叔我是真的很舍不得你们呢~',Speaker=892429,Emoji=24,}",
  [157193] = "{id=157193,Text='不过离开之前我想问你几个问题可以吗？',Speaker=892429,}",
  [157194] = "{id=157194,Text='当然我也有一些小礼品作为谢礼哦~',Speaker=892429,}",
  [157195] = "{id=157195,Text='创界山共有几层？',Speaker=892429,Option='{4,4},{6,4},{7,3}',}",
  [157196] = "{id=157196,Text='龙神丸获得了空神丸的力量后变成了战无不胜的______',Speaker=892429,Option='{超神丸,4},{战神丸,4},{龙王丸,3}',}",
  [157197] = "{id=157197,Text='火美子擅长什么？',Speaker=892429,Option='{剑术,4},{飞行,4},{忍术,3}',}",
  [157198] = "{id=157198,Text='多恶达洗去了创界山王子——翔龙子的记忆，改造成了魔界王子，他是？',Speaker=892429,Option='{战部渡,4},{古拿马,4},{虎王,3}',}",
  [157199] = "{id=157199,Text='龙王丸的必杀技是？',Speaker=892429,Option='{必杀！登龙剑,4},{必杀！龙牙拳,4},{必杀！凤龙剑,3}',}",
  [157200] = "{id=157200,Text='希里冰花的作用是？',Speaker=892429,Option='{观赏,4},{增强力量,4},{复活,3}',}",
  [157201] = "{id=157201,Text='勇者之剑的进阶版是？',Speaker=892429,Option='{炽热之剑,4},{亡者之剑,4},{王者之剑,3}',}",
  [157202] = "{id=157202,Text='哈哈，多谢冒险者的热心解答，我们有缘再见喽~',Speaker=892429,Emoji=20,}",
  [157203] = "{id=157203,Text='啊嘞，好像和我所知的有些差异啊。。。',Speaker=892429,Emoji=1,}",
  [157456] = "{id=157456,Text='感谢奥丁，能解决英灵发疯的人终于来了！',Speaker=892466,Option='{？？,2},{谁发疯？,2}',Emoji=24,}",
  [157457] = "{id=157457,Text='咳咳……抱歉，这段时间我压力太大，有点失态了。',Speaker=892466,}",
  [157458] = "{id=157458,Text='唉，你近日应该也有所感受才对……本应该象征着正义与勇气的英灵，不知道为何陷入了黑暗中，竟然开始攻击米德加尔特了！',Speaker=892466,}",
  [157459] = "{id=157459,Text='是的，就是那些曾在战争中为了世界而牺牲的人。',Speaker=892466,}",
  [157460] = "{id=157460,Text='……虽然难以置信，但彩虹桥坠落后，这里发生了太多难以解释的事件。',Speaker=892466,}",
  [157461] = "{id=157461,Text='英灵们生前都是品性高洁的英雄……怎么会突然变成这样？在这幕后，一定有不为人知的原因。',Speaker=892466,Option='我能帮上忙吗',}",
  [157462] = "{id=157462,Text='……奥丁在上！不可思议，你的身上，竟然有着英灵[c][ffff00]诺曼[-][/c]遗留的记忆碎片！这或许是个转机……',Speaker=892466,Option='需要怎么做？',Emoji=11,}",
  [157463] = "{id=157463,Text='唔。我会尝试让你身上的碎片，与英灵的魔力共鸣。',Speaker=892466,}",
  [157464] = "{id=157464,Text='如果成功，你就可以进入[c][ffff00]诺曼[-][/c]的内心世界……这样或许就能找到他陷入黑暗的原因！',Speaker=892466,Option='！！',}",
  [157465] = "{id=157465,Text='准备好了吗，冒险者？这可能是我们唯一的机会了……',Speaker=892466,Option='{现在还不行 ,4},{好的,5}',Emoji=15,}",
  [157466] = "{id=157466,Text='事情紧急，请尽快解决，拜托了！',Speaker=892466,}",
  [157467] = "{id=157467,Text='事关米德加尔特的安危，等你做好准备请一定要来找我！',Speaker=892466,}",
  [157469] = "{id=157469,Text='你好，[c][ffff00][PlayerName][-][/c]。最近你一定留意到了侵扰整个米德加尔特大陆的异常天气状况……',Speaker=891048,}",
  [157470] = "{id=157470,Text='而我们在数日忙碌的观测调查之后，终于有了些许进展。',Speaker=891048,}",
  [157471] = "{id=157471,Text='不过在此之前，我想先听听你的看法……',Speaker=891048,}",
  [157472] = "{id=157472,Text='请试着观测一下天空，然后告诉我你的答案。',Speaker=891048,Option='好的',}",
  [157473] = "{id=157473,Text='做得好，你已经进行了一次简单的天象观测！',Speaker=891048,}",
  [157474] = "{id=157474,Text='那么在你看来，天空中久久不落的流星状的东西应该是什么呢？',Speaker=891048,Option='像是一块陨石',}",
  [157476] = "{id=157476,Text='没错，虽然更确切的说，它只是近似于陨石的东西……',Speaker=891048,}",
  [157477] = "{id=157477,Text='但我们已经觉察到，天空中的这块陨石似乎正在往下坠落！',Speaker=891048,Emoji=15,}",
  [157478] = "{id=157478,Text='关于这次事态的报告我已经提交给王国的斯宾诺学士了。',Speaker=891048,}",
  [157479] = "{id=157479,Text='他提出了一个惊人的计划……或许你可以去找他的助手贝奇聊聊。',Speaker=891048,}",
  [157480] = "{id=157480,Text='你好，[c][ffff00][PlayerName][-][/c]。对于这次的危机，我们正在寻找解决的方案……',Speaker=891049,}",
  [157481] = "{id=157481,Text='借助于天文观测员提交的报告，王国图书馆的典籍等……斯宾诺学士已经可以确认——',Speaker=891049,}",
  [157482] = "{id=157482,Text='天空中久久不落的正是传说中古老的[c][ffff00]彩虹桥[-][/c]的残骸！',Speaker=891049,}",
  [157483] = "{id=157483,Text='虽然不知道因为什么原因，它的碎片出现在了米德加尔特大陆的上空……',Speaker=891049,Emoji=15,}",
  [157484] = "{id=157484,Text='可现在的情况很不乐观……如果我们还不做些什么的话，天空中的彩虹桥碎片将会在[c][ffff00]一周内[-][/c]坠落！',Speaker=891049,}",
  [157485] = "{id=157485,Text='到那时，彩虹桥碎片将会坠落到大陆上产生巨大的冲击波，造成遮天蔽日的烟尘，以及海水的倒灌……',Speaker=891049,}",
  [157486] = "{id=157486,Text='也就是说，米德加尔特大陆将面临毁灭的深渊！没有任何人可以从这场灾难中幸存。',Speaker=891049,Option='我们该怎么做',Emoji=15,}",
  [157487] = "{id=157487,Text='如果能集结我们的力量把天空中巨大的[c][ffff00]彩虹桥[-][/c]残骸击碎的话，我们就能把影响降低到最轻微的程度。',Speaker=891049,}",
  [157488] = "{id=157488,Text='这也就是我们决心实施[c][ffff00]魔导炮计划[-][/c]的原因。',Speaker=891049,}",
  [157489] = "{id=157489,Text='魔导炮计划，简而言之，就是建成一尊可以容纳海量魔力的魔导炮，再借由魔法水晶对其进行共振增幅……',Speaker=891049,}",
  [157490] = "{id=157490,Text='在准备完毕后，向天空发射强大的魔力脉冲，击碎彩虹残骸，化解这场危机！',Speaker=891049,Emoji=41,}",
  [157491] = "{id=157491,Text='我们选定了开阔的[c][ffff00]哥布灵森林[-][/c]作为筹备这次发射的地点。',Speaker=891049,}",
  [157492] = "{id=157492,Text='这个计划的成功实施需要工匠，魔法师，士兵乃至大陆所有人的努力！',Speaker=891049,}",
  [157493] = "{id=157493,Text='现在事不宜迟，我们必须马上开始行动。',Speaker=891049,}",
  [157494] = "{id=157494,Text='王国已经任命奥斯特学士为这次魔导炮计划的督造官，他将会即日起驻扎在哥布灵森林，负责代号[c][ffff00]“海姆达尔之眼”[-][/c]的魔导炮的建造。',Speaker=891049,}",
  [157495] = "{id=157495,Text='奥斯特督造官正在寻求王国的冒险者们的帮助，还请尽快和他联络，拜托了。',Speaker=891049,}",
  [157496] = "{id=157496,Text='另外，斯宾诺学士和已经前往哥布灵森林的埃尔加学士，也会记录下冒险者们在本次魔导炮计划中的出色功绩。',Speaker=891049,Emoji=30,}",
  [157497] = "{id=157497,Text='如果你们需要查看自己的功绩，请与斯宾诺学士联络。',Speaker=891049,}",
  [157498] = "{id=157498,Text='现在，我可以把你送到[c][ffff00]哥布灵森林[-][/c]那里，你准备好了吗？',Speaker=891049,Option='{准备好了,6},{再等等,7}',}",
  [157499] = "{id=157499,Text='等你准备好了再来找我吧！',Speaker=891049,}",
  [157500] = "{id=157500,Text='你好，我是督造官奥斯特大人的助手特鲁林。',Speaker=891144,}",
  [157501] = "{id=157501,Text='我的伙伴贝奇已经告诉我了，[c][ffff00][PlayerName][-][/c]，你是为了魔导炮计划而来的吗？',Speaker=891144,Option='是的',}",
  [157502] = "{id=157502,Text='没错，米德加尔特大陆正需要你们的帮助！',Speaker=891033,}",
  [157503] = "{id=157503,Text='魔导炮计划已经启动，我们正在紧急调度“海姆达尔之眼”的修建所需的材料……',Speaker=891033,}",
  [157504] = "{id=157504,Text='没错，在奥斯特大人这里每天都会有材料征集令下发，还请踊跃向我们捐献材料哦！',Speaker=891144,}",
  [157506] = "{id=157506,Text='冒险者，米德加尔特大陆需要你们的帮助！',Speaker=892468,Option='发生了什么',}",
  [157507] = "{id=157507,Text='异界冒险者「罗安」的到来连通了两个世界，随着时间的推移，造成的时空错乱愈发严重……',Speaker=892468,}",
  [157508] = "{id=157508,Text='现在，异界的魔物正趁机入侵我们的世界，米德加尔特大陆亟需被拯救！',Speaker=892468,Option='我们该怎么做',}",
  [157509] = "{id=157509,Text='我们已经将异界魔物吸引至「荒芜之地」，前往此地，消灭它们！一定要小心啊……',Speaker=892468,}",
  [157510] = "{id=157510,Text='那么，等你们准备好了,就来找我吧！我传送你们过去。',Speaker=892468,Option='知道了',}",
  [157514] = "{id=157514,Text='感谢奥丁，能解决英灵发疯的人终于来了！',Speaker=892466,Option='{？？,0},{谁发疯？,0}',Emoji=24,}",
  [157515] = "{id=157515,Text='咳咳……抱歉，这段时间我压力太大，有点失态了。',Speaker=892466,}",
  [157516] = "{id=157516,Text='唉，你近日应该也有所感受才对……本应该象征着正义与勇气的英灵，不知道为何陷入了黑暗中，竟然开始攻击米德加尔特了！',Speaker=892466,}",
  [157517] = "{id=157517,Text='是的，就是那些曾在战争中为了世界而牺牲的人。',Speaker=892466,}",
  [157518] = "{id=157518,Text='……虽然难以置信，但彩虹桥坠落后，这里发生了太多难以解释的事件。',Speaker=892466,}",
  [157519] = "{id=157519,Text='英灵们生前都是品性高洁的英雄……怎么会突然变成这样？在这幕后，一定有不为人知的原因。',Speaker=892466,Option='我能帮上忙吗',}",
  [157520] = "{id=157520,Text='……奥丁在上！不可思议，你的身上，竟然有着英灵[c][ffff00]梅丽尔[-][/c]遗留的魔法残片！这或许是个转机……',Speaker=892466,Option='需要怎么做？',Emoji=11,}",
  [157521] = "{id=157521,Text='唔。我会尝试让你身上的碎片，与英灵的魔力共鸣。',Speaker=892466,}",
  [157522] = "{id=157522,Text='如果成功，你就可以进入[c][ffff00]梅丽尔[-][/c]的内心世界……这样或许就能找到他陷入黑暗的原因！',Speaker=892466,Option='！！',}",
  [157523] = "{id=157523,Text='准备好了吗，冒险者？这可能是我们唯一的机会了……',Speaker=892466,Option='{现在还不行 ,3},{好的,4}',Emoji=15,}",
  [157524] = "{id=157524,Text='事情紧急，请尽快解决，拜托了！',Speaker=892466,}",
  [157525] = "{id=157525,Text='事关米德加尔特的安危，等你做好准备请一定要来找我！',Speaker=892466,}",
  [157537] = "{id=157537,Text='[c][ffff00][PlayerName][-][/c]，感谢你们的贡献。彩虹桥残骸已经被成功击碎了！',Speaker=891050,}",
  [157538] = "{id=157538,Text='然而，尽管我们通过魔导炮计划避免了一次大灾难的发生——',Speaker=891050,}",
  [157539] = "{id=157539,Text='但必须也要承认，意料之外的状况也同时发生了。',Speaker=891050,Option='出什么事了',}",
  [157540] = "{id=157540,Text='在彩虹碎片的降落之处——[c][ffff00]依斯鲁得岛，斐扬树林，伊达平原，妙勒尼山脉，天津町[-][/c]，这些地方出现了种种奇观。似乎，世界的运行规则在那里被改写了。',Speaker=891050,Emoji=15,}",
  [157541] = "{id=157541,Text='对这样近乎于神迹的奇观的调查，已经超出了我的小队的能力范围……',Speaker=891050,}",
  [157542] = "{id=157542,Text='你愿意接受我的委托前往那些地方看一看吗？我的队员会在那里等你的。',Speaker=891050,Option='好的',}",
  [157543] = "{id=157543,Text='不知道在彩虹桥碎片降落的地方是否会有危险存在，请务必小心行事。',Speaker=891050,}",
  [157544] = "{id=157544,Text='你的第一站是斐扬树林，准备好了吗？我可以把你送过去！',Speaker=891050,Option='{传送过去,3},{再等等,4}',}",
  [157545] = "{id=157545,Text='准备好了再来找我吧。',Speaker=891050,}",
  [157546] = "{id=157546,Text='彩虹碎片落入了大海里，海中升起了一座山脉……',}",
  [157547] = "{id=157547,Text='哈哈，落入大海的彩虹桥碎片，是不是十分壮观！',Speaker=891051,}",
  [157548] = "{id=157548,Text='如果不是近期这里禁止通航，我可真想出海去那里看看，冒险总是能让我热血沸腾！',Speaker=891051,Emoji=17,}",
  [157549] = "{id=157549,Text='对了，我的同伴格里告诉我，在妙勒尼山脉似乎也出现了令人惊奇的景象。',Speaker=891051,}",
  [157550] = "{id=157550,Text='嘿，不要犹疑了，快点过去看看，勇于探险才像话嘛！',Speaker=891051,Option='{传送过去,3},{再等等,4}',}",
  [157551] = "{id=157551,Text='彩虹碎片的坠落摧毁了房屋，但房屋的残骸却漂浮在空中……',}",
  [157552] = "{id=157552,Text='我的同伴杰克总是和我吹嘘他在依斯鲁得岛见过的景象。',Speaker=891052,}",
  [157553] = "{id=157553,Text='呵呵，要是他来妙勒尼山脉这里看一看，恐怕下巴都要惊掉了吧！',Speaker=891052,Emoji=5,}",
  [157554] = "{id=157554,Text='在这里，山顶的房屋残骸悬浮在空中，时间似乎都像静止了一样……',Speaker=891052,}",
  [157555] = "{id=157555,Text='不过，我的另一位同伴塞隆托人传来消息说，在伊达平原也同样出现了时间规律的异常状况。',Speaker=891052,}",
  [157556] = "{id=157556,Text='哼，塞隆是个喜欢捉弄人的家伙，我可信不过他，不过看他这次说得倒挺像回事的——',Speaker=891052,}",
  [157557] = "{id=157557,Text='要不，你可以到那里去看一看？如果他骗你的话，我会好好教训他的！',Speaker=891052,Option='{传送过去,3},{再等等,4}',Emoji=21,}",
  [157558] = "{id=157558,Text='以彩虹碎片为界，伊达平原的一半是白天，一半是黑夜……',}",
  [157559] = "{id=157559,Text='自从彩虹碎片坠落在伊达平原后，整个原野就是日夜并行了。',Speaker=891053,}",
  [157560] = "{id=157560,Text='呵呵，以前驻扎在这里勘察的时候，我总是抱怨这里白天太短，工作的时间不够用……',Speaker=891053,}",
  [157561] = "{id=157561,Text='如今这里有半边似乎永远都是白天了，这可有些叫我吃不消了。',Speaker=891053,Emoji=15,}",
  [157562] = "{id=157562,Text='对了，我的远赴天津町的同伴罗斯写信告诉我，在天津町似乎也出现了奇怪的状况。',Speaker=891053,}",
  [157563] = "{id=157563,Text='不过你猜怎么着？在这之后，天津町的游人却反而更多了！',Speaker=891053,Emoji=19,}",
  [157564] = "{id=157564,Text='想知道是为什么吗？略略，我可不告诉你，这得你自己亲自去看看才成！',Speaker=891053,Option='{传送过去,3},{再等等,4}',}",
  [157565] = "{id=157565,Text='樱花树的一半已经变成了水晶，水晶与樱花交相映照……',}",
  [157566] = "{id=157566,Text='受到彩虹碎片能量的影响，樱花树上竟然长出了水晶……真是不可思议。',Speaker=891054,}",
  [157567] = "{id=157567,Text='等到下次休假的时候，我一定要带我的女儿来这里看一看。',Speaker=891054,}",
  [157568] = "{id=157568,Text='璀璨的水晶与绚烂的樱花的交织，她一定会很喜欢吧……',Speaker=891054,Emoji=30,}",
  [157569] = "{id=157569,Text='呵呵，话扯远了……说到这里，你的调查之旅到这里已经结束了。',Speaker=891054,}",
  [157570] = "{id=157570,Text='你可以回去向科林迪队长汇报了，要我送你回普隆德拉吗？',Speaker=891054,Option='{传送过去,30},{再等等,40}',}",
  [157571] = "{id=157571,Text='受到彩虹碎片能量的影响，斐扬树林的瀑布开始倒流……',}",
  [157572] = "{id=157572,Text='呵呵，倒流的斐扬树林瀑布，是不是很神奇？',Speaker=891055,}",
  [157573] = "{id=157573,Text='这么看来，彩虹桥碎片似乎有着强大的内在引力，会不会和磁石的特性有一些相似之处……',Speaker=891055,}",
  [157574] = "{id=157574,Text='嗯，我要记下这一点，回去继续研究……没错，这个值得好好思考一下……',Speaker=891055,}",
  [157575] = "{id=157575,Text='……等等，你怎么还在这里？斐扬树林的调查已经结束，你可以前往你的下一站——依斯鲁得岛了！',Speaker=891055,Emoji=19,}",
  [157576] = "{id=157576,Text='我的同伴杰克会在那里等待着你。',Speaker=891055,Option='{传送过去,3},{再等等,4}',}",
  [157586] = "{id=157586,Text='你已经见过这些奇异的景观了。',Speaker=891050,}",
  [157587] = "{id=157587,Text='在接下来的这段时间里，我们需要做的就是对它们进行更深入的调查……',Speaker=891050,Option='说明情况',}",
  [157588] = "{id=157588,Text='什么？从另一个世界到来的敌人？！难道是降落在那里的彩虹桥碎片激活了[c][ffff00]时空裂隙[-][/c]……',Speaker=891050,Emoji=1,}",
  [157589] = "{id=157589,Text='我们必须立刻前往支援！',Speaker=891050,}",
  [157590] = "{id=157590,Text='但恐怕这种空间的不稳定性绝不只存在于斐扬树林，或许，未知的敌人还会从其他几个彩虹桥碎片落下的地方陆续到来……',Speaker=891050,}",
  [157591] = "{id=157591,Text='打起精神来，勇敢的[c][ffff00][PlayerName][-][/c]，或许战斗从此刻已经开始了！',Speaker=891050,}",
  [157593] = "{id=157593,Text='忍法·火美子的占卜术！砰！哈哈哈哈哈哈~~~~',Speaker=892431,}",
  [157594] = "{id=157594,Text='火美子的占卜术很厉害哟~冒险者一起来玩嘛~~',Speaker=892431,Option='{好呀,3},{没兴趣,4}',}",
  [157595] = "{id=157595,Text='（不，我很有兴趣！我怎么可能会拒绝火美子？）',Emoji=7,}",
  [157596] = "{id=157596,Text='那选牌吧~~',Speaker=892431,}",
  [157616] = "{id=157616,Text='对了，我额外准备了一些感想，悄悄写在了「初心者手账」里……记得查看！',Speaker=892642,Emoji=17,}",
  [157640] = "{id=157640,Text='准备好了再来找我吧。',Speaker=891051,}",
  [157641] = "{id=157641,Text='准备好了再来找我吧。',Speaker=891052,}",
  [157642] = "{id=157642,Text='准备好了再来找我吧。',Speaker=891053,}",
  [157643] = "{id=157643,Text='准备好了再来找我吧。',Speaker=891054,}",
  [157679] = "{id=157679,Text='唉，你竟然会犹豫不决……我真的没有想到。',Speaker=892466,Emoji=14,}",
  [157699] = "{id=157699,Text='你就是大名鼎鼎的冒险者[c][ffff00][PlayerName][-][/c]吧！',Speaker=891100,Option='[c][ffff00]什么？[-][/c]',}",
  [157700] = "{id=157700,Text='我想找一个靠得住的人来帮助我，大家都向我推荐了你！',Speaker=891100,}",
  [157701] = "{id=157701,Text='“虽然刚认识[c][ffff00][PlayerName][-][/c]的时候，他还很稚嫩，可现在他已经成为一个传奇冒险者了！”',Speaker=891100,}",
  [157702] = "{id=157702,Text='大家都这么说呢！',Speaker=891100,}",
  [157703] = "{id=157703,Text='你愿意帮助我吗？',Speaker=891100,Option='{需要我做什么？,3},{暂时没有时间,4}',}",
  [157704] = "{id=157704,Text='[c][ffff00]啊，那等你有空了再来吧！[-][/c]',Speaker=891100,}",
  [157706] = "{id=157706,Text='我就知道你一定会答应的！',Speaker=891100,}",
  [157707] = "{id=157707,Text='[c][ffff00]我其实不是这个世界的人！[-][/c]',Speaker=891100,}",
  [157708] = "{id=157708,Text='[c][ffff00]我之前经历过类似的事情，降临到一座叫“创界山”的异世界。可这一次有一些不一样。[-][/c]',Speaker=891100,}",
  [157709] = "{id=157709,Text='到现在为止我还不知道我要做什么。',Speaker=891100,}",
  [157710] = "{id=157710,Text='我想，既然这样的话，我还是先找回家的方法吧。',Speaker=891100,}",
  [157711] = "{id=157711,Text='可是，我对这个世界完全不熟悉，所以我找到了你，传奇冒险者！',Speaker=891100,}",
  [157713] = "{id=157713,Text='[c][ffff00]对了，我当时醒来的地方好像是叫普隆德拉附近的，靠南边的地方。[-][/c]',Speaker=891100,}",
  [157714] = "{id=157714,Text='这是我能提供的唯一信息了，如果是你的话，或许能找到一些线索。',Speaker=891100,}",
  [157715] = "{id=157715,Text='这里确实有一些不寻常的能量波动。',}",
  [157717] = "{id=157717,Text='哈哈哈哈哈！终于等到你了！小救星！',Speaker=891105,}",
  [157718] = "{id=157718,Text='[c][ffff00]你谁啊？[-][/c]',Speaker=891109,}",
  [157719] = "{id=157719,Text='不管了！先抓起来！',Speaker=891105,}",
  [157720] = "{id=157720,Text='讲不讲道理啊！',}",
  [157721] = "{id=157721,Text='就我们这点实力，还是别动不动打打杀杀了……',Speaker=891109,}",
  [157722] = "{id=157722,Text='呀哈哈哈哈！好好玩哦！来陪我玩嘛！',Speaker=891102,}",
  [157723] = "{id=157723,Text='火美子美子美子火美子美子……忍法！熊猫波利变身术！',Speaker=891102,}",
  [157724] = "{id=157724,Text='[c][ffff00]有没有搞错！快住手啊。我们都已经倒下了！[-][/c]',Speaker=891109,}",
  [157725] = "{id=157725,Text='还有一个！还有一个！嘿嘿嘿哈哈哈！',Speaker=891102,}",
  [157726] = "{id=157726,Text='火美子美子美子火美子美子……忍法！野猪变身术！',Speaker=891102,}",
  [157727] = "{id=157727,Text='哇啊啊！住手啊！',}",
  [157728] = "{id=157728,Text='呼噜噜……呼噜噜噜！呼噜！乌拉！',}",
  [157729] = "{id=157729,Text='嘿嘿嘿哈哈哈！',Speaker=891102,}",
  [157730] = "{id=157730,Text='火美子！你也来了！',Speaker=891100,}",
  [157731] = "{id=157731,Text='火美子美子美子火美子美子……忍法！野猪变身术！',Speaker=891102,}",
  [157732] = "{id=157732,Text='停停停！是我！',Speaker=891100,}",
  [157733] = "{id=157733,Text='哎呀！搞错了！搞错了！',Speaker=891102,}",
  [157734] = "{id=157734,Text='是伙伴！是伙伴！嘿嘿嘿哈哈哈！',Speaker=891102,}",
  [157735] = "{id=157735,Text='哈……吓死我了！冒险者呢？',Speaker=891100,}",
  [157736] = "{id=157736,Text='冒险者？',Speaker=891102,}",
  [157737] = "{id=157737,Text='呼噜噜……呼噜噜噜！呼噜！乌拉！',}",
  [157738] = "{id=157738,Text='冒险者！你怎么了！你怎么变成野猪了？',Speaker=891100,}",
  [157739] = "{id=157739,Text='……',}",
  [157740] = "{id=157740,Text='我变的！我变的！',Speaker=891102,}",
  [157741] = "{id=157741,Text='他是帮助我的冒险者，快帮他变回原样。',Speaker=891100,}",
  [157742] = "{id=157742,Text='好的！好的！对不起！',Speaker=891102,}",
  [157743] = "{id=157743,Text='忍法！野猪变身术！解！',Speaker=891102,}",
  [157744] = "{id=157744,Text='既然火美子也来到了这个世界，那么其他的伙伴可能也来了！',Speaker=891100,}",
  [157745] = "{id=157745,Text='接下来先试着找找其他的伙伴吧！',Speaker=891100,}",
  [157746] = "{id=157746,Text='辛苦你了冒险者，等有线索了我会再次找你帮忙的！',Speaker=891100,}",
  [157747] = "{id=157747,Text='[c][ffff00][PlayerName][-][/c]！你来的正好！我们有了新的线索！',Speaker=891100,}",
  [157748] = "{id=157748,Text='火美子说她刚醒来的时候，隐隐约约闻到过其他伙伴的味道！',Speaker=891100,}",
  [157749] = "{id=157749,Text='[c][ffff00]火美子说她是在一个叫斐扬？的地方醒来的！[-][/c]',Speaker=891100,}",
  [157750] = "{id=157750,Text='时间不等人，我们快去看看吧！',Speaker=891100,Option='{现在过去,3},{暂时没空,4}',}",
  [157751] = "{id=157751,Text='[c][ffff00]怎么又这么忙啊，那有空了再来吧。[-][/c]',Speaker=891100,}",
  [157752] = "{id=157752,Text='这不是冒险者吗？有什么事吗？',Speaker=891111,}",
  [157753] = "{id=157753,Text='[c][ffff00]你好，我们在找人。[-][/c]',Speaker=891100,}",
  [157754] = "{id=157754,Text='[c][ffff00]最近有陌生人来过这里吗？[-][/c]',Speaker=891100,}",
  [157755] = "{id=157755,Text='有的。最近来了一位热心的大帅哥！',Speaker=891111,}",
  [157756] = "{id=157756,Text='嘿嘿嘿哈哈哈！大帅哥！大帅哥！',Speaker=891102,}",
  [157757] = "{id=157757,Text='这位大帅哥帮了我们很多。既能打败魔物，又会陪小孩子玩耍。',Speaker=891111,}",
  [157758] = "{id=157758,Text='他可真是魅力四射啊。',Speaker=891111,}",
  [157759] = "{id=157759,Text='[c][ffff00]哦，帅哥啊。还是先去见见他吧。[-][/c]',Speaker=891100,}",
  [157760] = "{id=157760,Text='[c][ffff00]现在他应该在南边某个地方吧。[-][/c]',Speaker=891111,}",
  [157761] = "{id=157761,Text='谢谢，我们去找找看。',Speaker=891100,}",
  [157762] = "{id=157762,Text='冒险者，我们分头行动吧',Speaker=891100,Option='好',}",
  [157763] = "{id=157763,Text='呜呜呜呜呜……',Speaker=891110,}",
  [157764] = "{id=157764,Text='我听到了孩童的哭声！正义的武一郎绝不会让孩子继续哭泣！',Speaker=891103,}",
  [157765] = "{id=157765,Text='呜呜呜呜呜……',Speaker=891110,}",
  [157766] = "{id=157766,Text='（大声）你在哪里？我来救你！',Speaker=891103,}",
  [157767] = "{id=157767,Text='这位穿着华丽的冒险者，你有听到一位小孩的哭声吗？',Speaker=891103,Option='听到了',}",
  [157768] = "{id=157768,Text='你能听出来声音来自哪里吗？',Speaker=891103,Option='我试试',}",
  [157769] = "{id=157769,Text='呜呜呜呜呜呜……',Speaker=891110,}",
  [157770] = "{id=157770,Text='我大概知道了，你跟我来！',}",
  [157771] = "{id=157771,Text='真是位靠得住的冒险者啊！',Speaker=891103,}",
  [157772] = "{id=157772,Text='找到了！',}",
  [157773] = "{id=157773,Text='找到了！',Speaker=891108,}",
  [157774] = "{id=157774,Text='火美子？',Speaker=891103,}",
  [157775] = "{id=157775,Text='小孩子！',Speaker=891102,}",
  [157776] = "{id=157776,Text='[c][ffff00]咦，武一郎老师？[-][/c]',Speaker=891100,}",
  [157777] = "{id=157777,Text='你们是？',Speaker=891108,}",
  [157778] = "{id=157778,Text='战部渡？',Speaker=891103,}",
  [157779] = "{id=157779,Text='小孩子！！',Speaker=891102,}",
  [157780] = "{id=157780,Text='[c][ffff00][PlayerName]和武一郎老师？[-][/c]',Speaker=891100,}",
  [157781] = "{id=157781,Text='大帅哥？',Speaker=891108,}",
  [157782] = "{id=157782,Text='大帅哥！',Speaker=891108,}",
  [157783] = "{id=157783,Text='小芳芳，你怎么跑这里来了！',Speaker=891103,}",
  [157784] = "{id=157784,Text='这里的魔物长的好吓人……我躲着躲着就迷路了呜呜呜呜……',Speaker=891113,}",
  [157785] = "{id=157785,Text='[c][ffff00]咦？原来大帅哥就是武一郎老师啊？[-][/c]',Speaker=891100,}",
  [157786] = "{id=157786,Text='是啊！武一郎老师是我们公认的大帅哥！',Speaker=891113,}",
  [157787] = "{id=157787,Text='嘿嘿嘿……',Speaker=891103,}",
  [157788] = "{id=157788,Text='呀哈哈哈哈！大叔！人家说你是大帅哥哦！',Speaker=891102,}",
  [157789] = "{id=157789,Text='嗯……真的是嘛！',Speaker=891100,}",
  [157790] = "{id=157790,Text='谢谢你冒险者，不光找到了孩子，还带来了我们的伙伴！',Speaker=891103,}",
  [157791] = "{id=157791,Text='[c][ffff00][PlayerName][-][/c]可是传奇冒险者！',Speaker=891100,}",
  [157792] = "{id=157792,Text='等有了其他线索，我会再找你帮忙的。',Speaker=891100,}",
  [157793] = "{id=157793,Text='[c][ffff00]真靠谱。那我们先送小芳芳回家吧！[-][/c]',Speaker=891103,}",
  [157794] = "{id=157794,Text='线索好像断掉了，但我们不能浪费时间空等。',Speaker=891100,}",
  [157795] = "{id=157795,Text='我们先在附近搜索一下吧。',Speaker=891100,}",
  [157796] = "{id=157796,Text='第一个目标，普隆德拉西门。',Speaker=891100,}",
  [157797] = "{id=157797,Text='冒险者，我们一起去吧！',Speaker=891100,Option='{现在出发,3},{没有时间,4}',}",
  [157798] = "{id=157798,Text='[c][ffff00]又来？不要老是忙的时候和我说话！[-][/c]',Speaker=891100,}",
  [157799] = "{id=157799,Text='[c][ffff00]嗯？前方好像有吵闹。[-][/c]',Speaker=891100,}",
  [157800] = "{id=157800,Text='鸟人！鸟人！',Speaker=891102,}",
  [157801] = "{id=157801,Text='哎呀！',Speaker=891100,}",
  [157802] = "{id=157802,Text='哎呀呀呀呀！',Speaker=891102,}",
  [157803] = "{id=157803,Text='哎呀呀！',Speaker=891103,}",
  [157804] = "{id=157804,Text='小心啊！',Speaker=891112,}",
  [157805] = "{id=157805,Text='[c][ffff00]那是，古拿马？[-][/c]',Speaker=891100,}",
  [157806] = "{id=157806,Text='战部渡！火美子！武一郎！',Speaker=891112,}",
  [157807] = "{id=157807,Text='快跑，有人追我！',Speaker=891112,}",
  [157808] = "{id=157808,Text='来不及了！哈哈哈哈哈哈！',Speaker=891105,}",
  [157809] = "{id=157809,Text='没想到啊！追一只鸟人，还能引出这么多目标！太幸运了！',Speaker=891105,}",
  [157810] = "{id=157810,Text='[c][ffff00]真的幸运吗？我可不想碰到他们啊。[-][/c]',Speaker=891105,}",
  [157811] = "{id=157811,Text='[c][ffff00]同感。我们是不是应该搞清楚自己的定位？[-][/c]',Speaker=891105,}",
  [157812] = "{id=157812,Text='[c][ffff00]你们和我一起上，捉住他们！[-][/c]',Speaker=891105,}",
  [157813] = "{id=157813,Text='为什么他们这么弱还这么有自信？',}",
  [157814] = "{id=157814,Text='呼……终于可以放心了，谢谢你。',Speaker=891112,Option='发生了什么',}",
  [157815] = "{id=157815,Text='我醒来没多久，就被黑暗魔兵盯上了。',Speaker=891112,}",
  [157816] = "{id=157816,Text='击败一波，又来一波，没完没了。所以我想甩开他们。',Speaker=891112,}",
  [157817] = "{id=157817,Text='我们几个在一起，他们应该不敢轻易过来了！',Speaker=891112,}",
  [157818] = "{id=157818,Text='大家都来到了这个世界，感觉事情没这么简单。',Speaker=891100,}",
  [157819] = "{id=157819,Text='[c][ffff00]的确如此，我已经闻到了阴谋的味道，这背后一定有人在主导。[-][/c]',Speaker=891112,}",
  [157820] = "{id=157820,Text='只要我们在一起，就不会被打倒。',Speaker=891103,}",
  [157821] = "{id=157821,Text='不怕！不怕！鸟人不要怕！',Speaker=891102,}",
  [157822] = "{id=157822,Text='我才没有怕呢！',Speaker=891112,}",
  [157823] = "{id=157823,Text='[c][ffff00][PlayerName][-][/c]！谢谢你，能遇到你真的太好了！',Speaker=891100,}",
  [157824] = "{id=157824,Text='你是我们在新世界的第一个朋友！明天见！',Speaker=891100,}",
  [157825] = "{id=157825,Text='听说你是这里最优秀的冒险者！',Speaker=891101,Option='一般般啦',}",
  [157826] = "{id=157826,Text='[c][ffff00]还挺谦虚！我中意你！[-][/c]',Speaker=891101,}",
  [157827] = "{id=157827,Text='我来自创界山！是一位王子！',Speaker=891101,Option='创界山？',}",
  [157828] = "{id=157828,Text='是的！你听说过创界山？',Speaker=891101,Option='跟我来！',}",
  [157829] = "{id=157829,Text='很好！很霸气！',Speaker=891101,}",
  [157830] = "{id=157830,Text='[c][ffff00]虎王！你也来了这里！[-][/c]',Speaker=891100,}",
  [157831] = "{id=157831,Text='火美子！你也来到了这里！太好了！',Speaker=891101,}",
  [157832] = "{id=157832,Text='……虎王！',Speaker=891100,}",
  [157839] = "{id=157839,Text='你有什么办法吗？冒险者。',Speaker=891101,Option='七色花',}",
  [157840] = "{id=157840,Text='……',Speaker=891100,}",
  [157841] = "{id=157841,Text='七色花！七种颜色吗？听起来不错！在哪里有？',Speaker=891101,Option='我帮你找',}",
  [157842] = "{id=157842,Text='我果然没有看错人！谢了！',Speaker=891101,}",
  [157844] = "{id=157844,Text='好美丽的花朵！这次一定会成功的！谢谢你！',Speaker=891101,Option='不客气',}",
  [157845] = "{id=157845,Text='[c][ffff00]今后也要靠你多照顾了，我的好朋友！[-][/c]',Speaker=891101,}",
  [157846] = "{id=157846,Text='[c][ffff00]（我们什么时候变成好朋友了）[-][/c]',}",
  [157848] = "{id=157848,Text='我有点紧张！你说我会成功吗？',Speaker=891101,Option='一定会的！',}",
  [157849] = "{id=157849,Text='好！那我去了！',Speaker=891101,}",
  [157850] = "{id=157850,Text='这是我最好的朋友帮我找来的七色花。',Speaker=891101,}",
  [157851] = "{id=157851,Text='我想把它送给你！',Speaker=891101,}",
  [157852] = "{id=157852,Text='希望你能做本王子的王妃！',Speaker=891101,}",
  [157853] = "{id=157853,Text='……',Speaker=891102,}",
  [157854] = "{id=157854,Text='我……',Speaker=891102,}",
  [157855] = "{id=157855,Text='我拒绝！我拒绝！',Speaker=891102,}",
  [157856] = "{id=157856,Text='好的！但是我是不会放弃的！',Speaker=891101,Emoji=24,}",
  [157858] = "{id=157858,Text='[c][ffff00]这感觉是……邪虎丸！？[-][/c]',Speaker=891101,}",
  [157859] = "{id=157859,Text='我们一起过去！说不定会有新的线索！',Speaker=891101,Option='{马上出发,2},{再等等,3}',}",
  [157860] = "{id=157860,Text='好！我等着你！',Speaker=891101,}",
  [157861] = "{id=157861,Text='嘿嘿嘿哈哈哈哈！邪虎丸！邪虎丸！',Speaker=891102,}",
  [157862] = "{id=157862,Text='果然是邪虎丸！',Speaker=891101,}",
  [157863] = "{id=157863,Text='好久不见啊！',Speaker=891100,}",
  [157864] = "{id=157864,Text='……好像有敌人来了。',Speaker=891112,}",
  [157865] = "{id=157865,Text='这股气息……',Speaker=891101,}",
  [157868] = "{id=157868,Text='哈哈哈哈哈哈！可笑！',Speaker=891104,}",
  [157869] = "{id=157869,Text='多恶达！',Speaker=891100,}",
  [157870] = "{id=157870,Text='看来都到齐了！今天你们一个也别想走！',Speaker=891104,}",
  [157871] = "{id=157871,Text='邪虎丸！',Speaker=891101,}",
  [157872] = "{id=157872,Text='战部渡我带你走！他的目标是你！',Speaker=891106,}",
  [157873] = "{id=157873,Text='哼！你这个没用的儿子！',Speaker=891104,}",
  [157874] = "{id=157874,Text='永远的留在这里吧！',Speaker=891104,}",
  [157875] = "{id=157875,Text='该我出手了！',}",
  [157876] = "{id=157876,Text='你……你怎么会这么强？',Speaker=891104,}",
  [157877] = "{id=157877,Text='不愧是传奇冒险者！',Speaker=891107,}",
  [157878] = "{id=157878,Text='不愧是本大爷最好的朋友！',Speaker=891101,}",
  [157879] = "{id=157879,Text='好厉害！好厉害！',Speaker=891102,}",
  [157880] = "{id=157880,Text='一山更比一山高！',Speaker=891103,}",
  [157881] = "{id=157881,Text='[c][ffff00]你们不要得意的太早，你们都不知道这个世界的黑暗远超你们的想象！[-][/c]',Speaker=891104,}",
  [157882] = "{id=157882,Text='咳咳……你们的运气不会一直都这么好。',Speaker=891104,}",
  [157883] = "{id=157883,Text='冒险者！经过这次的事，我想我们已经知道自己的使命了。',Speaker=891107,}",
  [157884] = "{id=157884,Text='那就是对抗这个世界的黑暗。',Speaker=891107,}",
  [157885] = "{id=157885,Text='谢谢你这段时间得照顾，[c][ffff00][PlayerName][-][/c]！',Speaker=891107,}",
  [157886] = "{id=157886,Text='我们也要去冒险！去变强！总有一天我们会再相见！',Speaker=891107,}",
  [157887] = "{id=157887,Text='保重！',Speaker=891107,}",
  [157888] = "{id=157888,Text='再见！再见！会再相见！',Speaker=891102,}",
  [157889] = "{id=157889,Text='以后再见了！好朋友！',Speaker=891101,}",
  [157890] = "{id=157890,Text='下次见面我会和你一样强！再见！',Speaker=891112,}",
  [157891] = "{id=157891,Text='路还很长，我们一定会再见！',Speaker=891103,Option='再见！',}",
  [157924] = "{id=157924,Text='你的气息很不寻常，一定是一位有名的强者吧。',Speaker=891118,}",
  [157925] = "{id=157925,Text='我感受到我们命运的交汇，你是否愿意开启这段羁绊？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891118,Option='{愿意,2} ,{我再想想,3}',}",
  [157926] = "{id=157926,Text='你考虑好再来找我吧。',Speaker=891118,}",
  [157929] = "{id=157929,Text='妈妈啊，奥丁啊，我一定是为了这一刻才诞生的！',Speaker=892505,Option='那个…',}",
  [157930] = "{id=157930,Text='彩虹桥！激光炮！坠落！奇观！',Speaker=892505,Option='请问…',}",
  [157931] = "{id=157931,Text='这究竟是世界线的变动，还是平面位的——',Speaker=892505,Option='听见了吗？',}",
  [157932] = "{id=157932,Text='不要打扰我！……啊，我有点激动过头了。',Speaker=892505,Option='{没关系,0},{我是——,0}',Emoji=7,}",
  [157933] = "{id=157933,Text='我当然知道你是冒险者啦，我这么聪明又不是傻瓜。',Speaker=892505,}",
  [157934] = "{id=157934,Text='听着，冒险者，现在有一件空前绝后的事情等着我们去调查！',Speaker=892505,Option='那是什么',}",
  [157935] = "{id=157935,Text='还用说吗？当然是彩虹桥坠落之后，各地出现的壮丽绝伦的奇观啊！',Speaker=892505,}",
  [157936] = "{id=157936,Text='目睹这样神奇的景色，你难道不感到心潮澎湃吗！',Speaker=892505,Option='具体要我做什么',}",
  [157937] = "{id=157937,Text='……我发现你这人还真是无趣诶。',Speaker=892505,Emoji=40,}",
  [157938] = "{id=157938,Text='唔，算了，可能你这种性格做调查会更谨慎点。',Speaker=892505,}",
  [157939] = "{id=157939,Text='那就直说吧，我很想去亲自调查一下各地的奇观，但有事没法去。',Speaker=892505,}",
  [157940] = "{id=157940,Text='所以，请你代替我，去调查一下除了奇观本身以外，当地是不是还有其他的特殊变化。',Speaker=892505,Option='{就这样？,0},{听起来不难,0}',}",
  [157941] = "{id=157941,Text='说说当然不难。彩虹桥坠落毕竟是从来没有过的大事，如果真的还有其他的异变，风险也是很大的。',Speaker=892505,}",
  [157942] = "{id=157942,Text='你确定你有这个能力应对吗？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑！】[-][/c]',Speaker=892505,Option='{当然,3},{我不太行……,2}',}",
  [157943] = "{id=157943,Text='挺自信啊。',Speaker=892505,Emoji=41,}",
  [157944] = "{id=157944,Text='不行你还站在这儿浪费我时间！快去加强了自己的实力再来！',Speaker=892505,Emoji=15,}",
  [157945] = "{id=157945,Text='那先快去目标地点吧。第一次的探索地……就定在斐扬树林好了！',Speaker=892505,}",
  [157946] = "{id=157946,Text='如果有什么发现，请一定要报告给我！',Speaker=892505,}",
  [157947] = "{id=157947,Text='（那么，先前往奇观看看吧。）',}",
  [157948] = "{id=157948,Text='（姑且先把奇观拍摄下来，交给阿尔夫吧。）',}",
  [157949] = "{id=157949,Text='呵呵……感受到了吗？冒险者。',Speaker=892506,}",
  [157950] = "{id=157950,Text='米德加尔特发生的巨变，所预示的未来变化……',Speaker=892506,}",
  [157951] = "{id=157951,Text='循环倒流的瀑布，一如逆转往复的命运。',Speaker=892506,}",
  [157952] = "{id=157952,Text='这正是米德加尔特的现在，或许也可能是米德加尔特的未来。',Speaker=892506,Option='什么意思？',}",
  [157953] = "{id=157953,Text='……',Speaker=892506,}",
  [157954] = "{id=157954,Text='你会明白的。',Speaker=892506,}",
  [157955] = "{id=157955,Text='那么，后会有期。',Speaker=892506,}",
  [157956] = "{id=157956,Text='……',}",
  [157957] = "{id=157957,Text='现在回普隆德拉找阿尔夫吗？',Option='{回去,2},{暂等一会儿,3}',}",
  [157958] = "{id=157958,Text='怎么样？斐扬树林那里有什么神奇事情的发生？',Speaker=892505,Option='交出拍摄的照片',}",
  [157959] = "{id=157959,Text='哦！倒流的瀑布中矗立着巨大的彩虹桥碎片！这景色真是太神奇了……还有呢，还有呢？',Speaker=892505,Option='告知神秘人的话',Emoji=28,}",
  [157960] = "{id=157960,Text='什么乱七八糟的？我需要的是有趣、严谨、具体的报告！',Speaker=892505,}",
  [157961] = "{id=157961,Text='……唔。仔细一想，这些事情也是在奇观之后才出现的，说不定和彩虹桥也有关联。',Speaker=892505,}",
  [157962] = "{id=157962,Text='这样吧，冒险者，拜托你多多调查类似的事情，然后统一报告给我。',Speaker=892505,}",
  [157963] = "{id=157963,Text='我要利用这些情报来搞清楚奇观之谜！',Speaker=892505,Emoji=20,}",
  [157964] = "{id=157964,Text='那我们每天都在这里碰头，你如果获得了任何有关奇观的情报都告诉我！',Speaker=892505,Option='我明白了',}",
  [157965] = "{id=157965,Text='就这么说定啦，搭档！',Speaker=892505,Emoji=17,}",
  [157966] = "{id=157966,Text='你好，搭档！今天的状况也不错呢！',Speaker=892505,}",
  [157967] = "{id=157967,Text='准备好去调查奇观了吗？',Speaker=892505,Option='{是的，准备好了,3},{再等一下,2}',}",
  [157968] = "{id=157968,Text='很好，要的就是这样的气势！去吧！',Speaker=892505,Emoji=20,}",
  [157969] = "{id=157969,Text='我等你到什么时候都行，不过时间可不等你哦。',Speaker=892505,Emoji=5,}",
  [157970] = "{id=157970,Text='（找找哪里还有不同寻常的事情发生吧）',}",
  [157972] = "{id=157972,Text='…………',Speaker=892530,}",
  [157973] = "{id=157973,Text='……吼……！！！！！',Speaker=892530,Emoji=12,}",
  [157974] = "{id=157974,Text='吼……！！不要碰我！！',Speaker=892530,Emoji=12,}",
  [157975] = "{id=157975,Text='（现在回普隆德拉找阿尔夫吗？）',Option='{回去 ,4},{暂等一会儿,5}',}",
  [157976] = "{id=157976,Text='辛苦了，搭档！今天的收获应该也不错吧？',Speaker=892505,Option='告知今天的内容',Emoji=21,}",
  [157977] = "{id=157977,Text='收到了！下次还要继续麻烦你了！',Speaker=892505,}",
