  [157995] = "{id=157995,Text='海中的奇观真是太美了！人家从来没见过这么壮观的景象！',Speaker=892511,}",
  [157996] = "{id=157996,Text='嘿嘿嘿，不管怎么说，那可是彩虹桥的碎片啊！',Speaker=892512,}",
  [157997] = "{id=157997,Text='对了，刚才沉船附近的码头上，那个圆头圆脑的石头人是什么东西？',Speaker=892511,}",
  [157998] = "{id=157998,Text='那个叫地藏灵，好像是天水之国那边的东西吧。',Speaker=892512,}",
  [157999] = "{id=157999,Text='嘻嘻，你懂得可真多~我们去吃饭吧！人家好饿了！',Speaker=892511,Emoji=26,}",
  [158000] = "{id=158000,Text='好好好~',Speaker=892512,}",
  [158001] = "{id=158001,Text='这里什么时候多了这么一个地藏灵？以前明明没有的。',Emoji=8,}",
  [158003] = "{id=158003,Text='你在说我吗？',Speaker=892513,}",
  [158004] = "{id=158004,Text='世间本有大智慧，却无法以人言传达。',Speaker=892513,}",
  [158005] = "{id=158005,Text='冒险者，当奇观从天而降，也正是米德加尔特巨变之时……',Speaker=892513,Option='{巨变？,4},{说说清楚,4}',}",
  [158006] = "{id=158006,Text='我言尽于此……',Speaker=892513,}",
