  [158014] = "{id=158014,Text='很好，要的就是这样的气势！去吧！',Speaker=892505,Emoji=41,}",
  [158015] = "{id=158015,Text='我等你到什么时候都行，不过时间可不等你哦。',Speaker=892505,Emoji=5,}",
  [158016] = "{id=158016,Text='（找找哪里还有不同寻常的事情发生吧）',}",
  [158017] = "{id=158017,Text='（或许可以去岸边再找找？）',}",
