  [158029] = "{id=158029,Text='（找找哪里还有不同寻常的事情发生吧）',}",
  [158032] = "{id=158032,Text='怎么回事？街上居然多了这么多凶神恶煞的精灵！',Speaker=892515,Emoji=37,}",
  [158033] = "{id=158033,Text='看它们到处搜寻的样子，好像是找什么东西……',Speaker=892516,}",
  [158034] = "{id=158034,Text='怎么了，有什么事？',Speaker=892516,Option='有关精灵',}",
  [158035] = "{id=158035,Text='我们也不知道是什么原因啊！突然之间就来了一大群，在中心广场上横行霸道的！生意都没法做了！',Speaker=892515,Emoji=12,}",
  [158036] = "{id=158036,Text='你要去看看吗？当心被揍哦……',Speaker=892516,}",
  [158037] = "{id=158037,Text='（果然是生活在妙勒尼山脉的精灵，它们怎么会跑到依斯鲁得来呢？……看来有必要去一趟妙勒尼山脉。）',}",
