  [158128] = "{id=158128,Text='（找找哪里还有不同寻常的事情发生吧。）',}",
  [158129] = "{id=158129,Text='呵呵，年轻的冒险者……',Speaker=892506,Option='{……,0},{说正事,0}',}",
  [158130] = "{id=158130,Text='……好歹配合一下，保持一下神秘的氛围！',Speaker=892506,Option='{……,0},{不说我走了,0}',Emoji=10,}",
  [158131] = "{id=158131,Text='哎、等、等一下！真是的，没耐心的人真是太讨厌了。',Speaker=892506,}",
  [158132] = "{id=158132,Text='听我把话说完！',Speaker=892506,}",
  [158133] = "{id=158133,Text='年轻的冒险者哟，感受到了吗？',Speaker=892506,Option='{……,0},{（要忍耐）,0}',}",
  [158134] = "{id=158134,Text='这里正是日与夜的分界，明与暗的抗争……',Speaker=892506,}",
  [158135] = "{id=158135,Text='正如米德加尔特的现状。',Speaker=892506,}",
  [158136] = "{id=158136,Text='冒险者啊，愿你在这巨变的时刻，能做出正确的选择……',Speaker=892506,Option='{……,0},{好好（棒读）,0}',}",
  [158137] = "{id=158137,Text='能不能不要把“终于完了”这几个字写在脸上？',Speaker=892506,}",
  [158138] = "{id=158138,Text='哼！顺便告诉你一句，伊达平原已经没有其他异常了！你自己想想接下来去哪里！',Speaker=892506,}",
  [158139] = "{id=158139,Text='（伊达平原没有异常，那不是只剩天津町了吗，还需要想吗……）',Emoji=15,}",
