  [158159] = "{id=158159,Text='我等你到什么时候都行，不过时间可不等你哦。',Speaker=892505,Emoji=5,}",
  [158160] = "{id=158160,Text='（找找哪里还有不同寻常的事情发生吧。）',}",
  [158162] = "{id=158162,Text='呜呜呜，我的坠子……我的坠子怎么办呢……',Speaker=892528,Option='发生什么事了？',Emoji=24,}",
  [158163] = "{id=158163,Text='我、我喜欢的人……送了我一个好看的坠子……',Speaker=892528,}",
  [158164] = "{id=158164,Text='可是昨天有奇怪的式神追着要打我，我吓得跑了，坠子就掉了。',Speaker=892528,Option='我来帮你找坠子',Emoji=37,}",
  [158165] = "{id=158165,Text='真的吗！',Speaker=892528,Option='你掉在哪里了？',}",
  [158166] = "{id=158166,Text='掉在哪里……我也不清楚。总之我是在樱花树下遇到那些式神，然后一直跑到远离樱花树的地方，它们才散开。',Speaker=892528,}",
  [158167] = "{id=158167,Text='也许就掉落在路上了吧……',Speaker=892528,Emoji=19,}",
  [158168] = "{id=158168,Text='（总之先一路沿着阶梯找找看吧）',}",
  [158169] = "{id=158169,Text='（这其中哪个才是青叶掉落的坠子呢？带回去给她看看。）',}",
  [158170] = "{id=158170,Text='怎么样，找到坠子了吗？',Speaker=892528,Option='交出找到的物品',}",
  [158171] = "{id=158171,Text='……对不起，这里面没有一个是我掉落的坠子。',Speaker=892528,}",
  [158172] = "{id=158172,Text='看来我的坠子果真是不可能找到了……',Speaker=892528,Emoji=42,}",
  [158173] = "{id=158173,Text='那我就只好——',Speaker=892528,Option='小心！！！',}",
  [158174] = "{id=158174,Text='哇、哇哇……',Speaker=892528,Option='受伤了吗？',Emoji=37,}",
  [158175] = "{id=158175,Text='受伤倒是没有……啊！',Speaker=892528,Option='怎么了？',}",
  [158176] = "{id=158176,Text='坠、坠子……原来……缠在我的发带上了。',Speaker=892528,}",
  [158177] = "{id=158177,Text='难怪找不到！',Speaker=892528,}",
  [158178] = "{id=158178,Text='哈哈哈……我可真是个笨蛋呀。',Speaker=892528,Option='找到就好',}",
  [158179] = "{id=158179,Text='只不过，为什么最近会出现那种可怕的式神呢……',Speaker=892528,}",
  [158180] = "{id=158180,Text='算了，想也想不明白。总而言之，谢谢你，冒险者！',Speaker=892528,}",
  [158181] = "{id=158181,Text='（要回普隆德拉吗？）',Option='{回去,4},{暂等一会儿,5}',}",
