  [158186] = "{id=158186,Text='准备好去调查奇观了吗？',Speaker=892505,Option='{是的，准备好了,3},{再等一下,2}',}",
  [158187] = "{id=158187,Text='很好，要的就是这样的气势！去吧！',Speaker=892505,Emoji=41,}",
  [158188] = "{id=158188,Text='我等你到什么时候都行，不过时间可不等你哦。',Speaker=892505,Emoji=5,}",
  [158189] = "{id=158189,Text='（找找哪里还有不同寻常的事情发生吧。）',}",
  [158190] = "{id=158190,Text='年轻的冒险者，你终于来到这里了。',Speaker=892506,Emoji=5,}",
  [158191] = "{id=158191,Text='这一路的指引与帮助，想必你也已经铭记在心、感恩不已吧。',Speaker=892506,Option='{指引和帮助？,0},{感恩不已？,0}',}",
  [158192] = "{id=158192,Text='那是什么表情！过了河就想拆桥？',Speaker=892506,Emoji=12,}",
  [158193] = "{id=158193,Text='哼！今天是最后一次和你打交道，给我好好回答问题！',Speaker=892506,}",
  [158194] = "{id=158194,Text='第一个问题：彩虹桥的碎片一共坠落在几个地方？',Speaker=892506,Option='{3个,5},{4个,5},{5个,6}',}",
  [158195] = "{id=158195,Text='……你这猪脑子彻底没救了！再好好回忆一下！',Speaker=892506,Emoji=12,}",
  [158198] = "{id=158198,Text='哼哼哼哼，这么简单的问题你当然应该答对，如果答不对我就打算骂你了！',Speaker=892506,Emoji=5,}",
  [158199] = "{id=158199,Text='来吧，我们来下一题。',Speaker=892506,}",
  [158200] = "{id=158200,Text='第二个问题：你觉得我是敌是友？',Speaker=892506,Option='{是敌,8},{是友,9}',}",
  [158201] = "{id=158201,Text='……………………………………………………',Speaker=892506,Option='{…………,0}',}",
  [158202] = "{id=158202,Text='……我可以当做没有听见，再给你一次机会。',Speaker=892506,}",
  [158203] = "{id=158203,Text='我、是、敌、是、友？！（超大声）',Speaker=892506,Option='{是敌,8},{是友,9}',}",
  [158204] = "{id=158204,Text='叮咚！答对了！',Speaker=892506,Emoji=11,}",
  [158205] = "{id=158205,Text='因为你答对了所以第三问就不问了！',Speaker=892506,Option='太草率了吧……',}",
  [158206] = "{id=158206,Text='人生苦短，浪费时间在问答上毫无意义！',Speaker=892506,Option='（那还问那么多）',}",
  [158207] = "{id=158207,Text='这样就结束了！我不会再以这个形象出现了！',Speaker=892506,}",
  [158208] = "{id=158208,Text='但是不要担心！我们将来还会有机会再见面的！',Speaker=892506,}",
  [158209] = "{id=158209,Text='后会有期啦，亲爱的冒险者！',Speaker=892506,}",
  [158210] = "{id=158210,Text='（要回普隆德拉吗？）',Option='{回去,10},{暂等一会儿,11}',}",
  [158211] = "{id=158211,Text='辛苦了，搭档！今天的收获应该也不错吧？',Speaker=892505,Option='告知今天的内容',}",
  [158212] = "{id=158212,Text='收到了！',Speaker=892505,Option='{这么多天了,0},{你有进展了吗,0}',}",
  [158213] = "{id=158213,Text='我……',Speaker=892505,Option='{好了好了,0},{我明白了,0}',}",
  [158214] = "{id=158214,Text='……即使这样，我也在努力地研究着！请再多给我点时间！',Speaker=892505,}",
  [158215] = "{id=158215,Text='我不会辜负你的辛苦的，搭档！',Speaker=892505,Option='{但愿吧,0},{你加油,0}',}",
  [158216] = "{id=158216,Text='嗯！等我有结果了就通知你！',Speaker=892505,}",
  [158217] = "{id=158217,Text='（但愿真有那一天……）',}",
  [158230] = "{id=158230,Text='哎？你就是传奇冒险者[c][ffff00][PlayerName][-][/c]吧！',Speaker=892529,}",
  [158231] = "{id=158231,Text='我来自创界山！即将在这个世界展开新的冒险！',Speaker=892529,}",
  [158232] = "{id=158232,Text='和我一起吧！',Speaker=892529,Option='{立即前往,2}{暂时不去,3}',}",
  [158234] = "{id=158234,Text='小渡小渡，我一直有个问题，想亲自问问你！',}",
  [158235] = "{id=158235,Text='什么问题丫？',Speaker=892432,}",
  [158236] = "{id=158236,Text='你是怎么召唤出龙神丸的呢？',}",
  [158237] = "{id=158237,Text='这个呀，跟我一起做一遍就会啦~',Speaker=892432,}",
  [158238] = "{id=158238,Text='[c][ffff00]打开聊天界面，举起龙神之剑，大喊一声：“龙神丸！”[-][/c]',Speaker=892432,}",
  [158239] = "{id=158239,Text='好了，请开始你的表演~~~',Speaker=892432,Emoji=25,}",
  [158240] = "{id=158240,Text='。。。。。。',Emoji=1,}",
  [158243] = "{id=158243,Text='可恶！',Speaker=892514,}",
  [158244] = "{id=158244,Text='（找找哪里还有不同寻常的事情发生吧）',}",
  [158245] = "{id=158245,Text='（对了，昨天见到的彩虹石波利，可能还有同伴？）',}",
  [158247] = "{id=158247,Text='成长的如此快，你一定经历了不少难忘的冒险吧！',Speaker=891125,}",
  [158248] = "{id=158248,Text='但是还没不能松懈，这个世界远不止表面这么简单。',Speaker=891125,}",
  [158249] = "{id=158249,Text='这是我在这个世界历险时得到的东西，希望能对你有用。',Speaker=891125,}",
  [158250] = "{id=158250,Text='继续加油！',Speaker=891125,}",
  [158251] = "{id=158251,Text='我果然没有看错你！',Speaker=891125,}",
  [158252] = "{id=158252,Text='有你这样的伙伴，我安心了许多。',Speaker=891125,}",
  [158253] = "{id=158253,Text='继续变强吧！你会是改变世界的那个人。',Speaker=891125,}",
  [158254] = "{id=158254,Text='收下这些，不要推辞。',Speaker=891125,}",
  [158255] = "{id=158255,Text='若是集齐了[限时联动]扭蛋机中的所有宝物，我将给予你恩赐。',Speaker=891126,Option='{我已集齐,2},{离开,3}',}",
  [158256] = "{id=158256,Text='你会是那颗搅动命运的救星。',Speaker=891126,}",
  [158257] = "{id=158257,Text='我将我的灵体分身赠予你，它将常伴在你左右。',Speaker=891126,}",
  [158258] = "{id=158258,Text='等你集齐[限时联动]扭蛋机中的所有宝物后，再来找我吧。',Speaker=891126,}",
  [158260] = "{id=158260,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为龙神丸了吗？',Speaker=891125,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [158261] = "{id=158261,Text='转职过程中是不是遇到困难了，继续加油！',Speaker=891125,}",
  [158262] = "{id=158262,Text='继续加油，转职成功后来找我！',Speaker=891125,}",
  [158263] = "{id=158263,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为龙王丸了吗？',Speaker=891125,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [158469] = "{id=158469,Text='梅菲尔林瑟的战斗就这么结束了，在生命中最后的时刻，他曾被失败与遗憾的阴影所缠绕，有过犹疑与惶惑。',}",
  [158470] = "{id=158470,Text='但他最终还是驱逐了他的心魔，找回了光明之心与忠诚之心，无愧于一个战士的职责。',}",
  [158471] = "{id=158471,Text='海边的教堂是他与玛琳娜的约定，而这一场最后的战斗，则是你与他一同并肩走过的……',}",
  [158473] = "{id=158473,Text='为什么呢喵……',Speaker=892563,Emoji=19,}",
  [158474] = "{id=158474,Text='呀！是[c][ffff00][PlayerName][-][/c]！欢迎光临GOOD NEWS冷饮店喵~',Speaker=892563,}",
  [158475] = "{id=158475,Text='要试试本店的镇店之宝[c][ffff00]波利冰淇淋[-][/c]嘛，有多种口味可以选择的喵~~',Speaker=892563,Option='看起来有些苦恼',Emoji=7,}",
  [158476] = "{id=158476,Text='被发现了喵~其实啊——有件事困扰我很久了。',Speaker=892563,}",
  [158477] = "{id=158477,Text='[c][ffff00][PlayerName][-][/c]，你可以帮帮我吗喵？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892563,Option='{好的,2},{现在没空,3}',}",
  [158478] = "{id=158478,Text='不要跟别人说哦~其实啊——',Speaker=892563,}",
  [158479] = "{id=158479,Text='（小声）是本店[c][ffff00]最最经典的波利冰淇淋[-][/c]最近的销量变差了。',Speaker=892563,}",
  [158480] = "{id=158480,Text='以前这可是罗扎纳最畅销的冷饮，可是现在天气越来越热，来买冰淇淋的人却变少了……',Speaker=892563,}",
  [158481] = "{id=158481,Text='[c][ffff00][PlayerName][-][/c]，你可以帮我做一下市场调研，看看究竟是哪里出了问题喵~',Speaker=892563,Option='好的',}",
  [158482] = "{id=158482,Text='你可以带一些免费的冰淇淋给大家品尝，然后问问大家的看法喵！',Speaker=892563,}",
  [158483] = "{id=158483,Text='（酒吧附近人流密集，到那边去做调查吧！）',}",
  [158484] = "{id=158484,Text='（首先把来往的客人聚集起来吧~）',}",
  [158485] = "{id=158485,Text='这是在干什么呀？促销活动吗？',Speaker=892565,}",
  [158486] = "{id=158486,Text='我好像闻到食物的味道了喵~',Speaker=892566,}",
  [158487] = "{id=158487,Text='喵~好多人……我也来凑个热闹！',Speaker=892567,}",
  [158488] = "{id=158488,Text='（人群聚集过来了~分发试吃品吧！）',}",
  [158489] = "{id=158489,Text='是波利冰淇淋！',}",
  [158490] = "{id=158490,Text='怎么还是这些口味，都已经吃腻了喵……',Speaker=892566,Emoji=15,}",
  [158491] = "{id=158491,Text='喵~是啊是啊，怎么不开发些新口味呢！',Speaker=892567,Emoji=31,}",
  [158492] = "{id=158492,Text='虽然有新口味更好，不过经典款也还不错……',Speaker=892565,Option='用户调研',Emoji=9,}",
  [158493] = "{id=158493,Text='诶？还需要做调研吗？算了算了……我没时间……',Speaker=892565,}",
  [158494] = "{id=158494,Text='（客人都走光了，只剩下一人，只好去问问他了）',}",
  [158495] = "{id=158495,Text='诶？试吃冰淇淋的感受嘛……',Speaker=892564,}",
  [158496] = "{id=158496,Text='其实我一直都是波利冰淇淋的忠实粉丝啦……',Speaker=892564,}",
  [158497] = "{id=158497,Text='波利冰淇淋的品质也没有下降，保持着一如既往的经典口感，只不过——',Speaker=892564,Option='只不过？',}",
  [158498] = "{id=158498,Text='经典虽好，但大家有时候也想尝试下新口味嘛！',Speaker=892564,Option='明白了',}",
  [158499] = "{id=158499,Text='总算换岗了……热死了，热死喵了。',Speaker=892568,Emoji=7,}",
  [158500] = "{id=158500,Text='是波利冰淇淋！太好了！快给喵来一点！',Speaker=892568,Option='给予',}",
  [158501] = "{id=158501,Text='（吞）谢谢……（吞）天气这么热……（狂吞）巡逻一上午都快把喵烤干了……',Speaker=892568,Emoji=18,}",
  [158502] = "{id=158502,Text='喵还是热……好像不够……',Speaker=892568,Option='都给你吧',Emoji=28,}",
  [158503] = "{id=158503,Text='太好了！',Speaker=892568,}",
  [158504] = "{id=158504,Text='（可洛蒂亚将剩下的各种口味的冰淇淋一口气吞下去了……）',Emoji=10,}",
  [158505] = "{id=158505,Text='这个味道——我从没吃过这么好吃的波利冰淇淋！',Speaker=892568,}",
  [158506] = "{id=158506,Text='不同口味的冰淇淋混合在一起，形成了全新的口味~就像——魔法一样！',Speaker=892568,}",
  [158507] = "{id=158507,Text='太谢谢你了！不但帮喵降温，还让喵吃到了这么美味的冰淇淋！',Speaker=892568,}",
  [158508] = "{id=158508,Text='（试吃品已经分发完了，去向奈似报告结果吧）',}",
  [158509] = "{id=158509,Text='[c][ffff00][PlayerName][-][/c]，你回来了喵！怎么样，有结果了嘛喵？',Speaker=892563,Option='新口味',}",
  [158510] = "{id=158510,Text='你是说大家希望吃到新口味的冰淇淋嘛……',Speaker=892563,Emoji=19,}",
  [158511] = "{id=158511,Text='因为过去销量不错所以一直没有意识到这点呢喵……',Speaker=892563,}",
  [158512] = "{id=158512,Text='不过现在已经是夏天了，研发新口味已经来不及了喵！',Speaker=892563,Option='混合',Emoji=24,}",
  [158513] = "{id=158513,Text='让可洛蒂亚赞不绝口的混合冰淇淋？！',Speaker=892563,Emoji=14,}",
  [158514] = "{id=158514,Text='嗯！确实是个值得尝试的方法！不如我们现在就来试试吧喵！',Speaker=892563,Option='准备好了！',}",
  [158515] = "{id=158515,Text='首先……加入一些奈似抹茶口味的冰淇淋喵！',Speaker=892563,Option='{少许,0},{适量,0},{加得多多的,0}',}",
  [158516] = "{id=158516,Text='接下来……加入一些黑糖奶茶口味的喵……',Speaker=892563,Option='{一点点就够了,0},{适量,0},{有多少加多少,0}',}",
  [158517] = "{id=158517,Text='在清新草莓、芒果百香果、黑巧珍珠三种口味中选择一种加入喵……',Speaker=892563,Option='{清新草莓,2},{芒果百香果,2},{黑巧珍珠,3}',}",
  [158518] = "{id=158518,Text='完成了喵~快尝试一下吧！',Speaker=892563,}",
  [158519] = "{id=158519,Text='（这坨不可名状之物的味道颠覆了你对冷饮的认知，感觉这辈子都不想吃冰淇淋了……）',}",
  [158520] = "{id=158520,Text='失败了喵……不过这个思路应该没有问题！只需要多尝试几次……',Speaker=892563,}",
  [158521] = "{id=158521,Text='今天真是太感谢你了喵~这是一点小小的心意——等我研发出了新口味的冰淇淋，一定要来尝尝喵！',Speaker=892563,Emoji=6,}",
  [158522] = "{id=158522,Text='完成了喵~快尝试一下吧！',Speaker=892563,}",
  [158523] = "{id=158523,Text='（不可思议，这款冰淇淋的口味清新脱俗，令你打开了新世界的大门……）',}",
  [158524] = "{id=158524,Text='看来成功了喵……我要把这个配方记录下来……',Speaker=892563,}",
  [158525] = "{id=158525,Text='今天真是太感谢你了喵~这是一点小小的心意——等我新口味冰淇淋上市了，一定要来光顾喵！',Speaker=892563,Emoji=6,}",
  [158526] = "{id=158526,Text='那就等你有空了再来吧喵~',Speaker=892563,}",
  [158527] = "{id=158527,Text='太感谢你了喵！',Speaker=892563,}",
  [158651] = "{id=158651,Text='战部渡？你也在？',Speaker=891101,}",
  [158652] = "{id=158652,Text='我……我一直都在啊……',Speaker=891100,}",
  [158653] = "{id=158653,Text='哈哈哈！好兄弟！见到你很开心！一会儿再叙旧！先回到正题！',Speaker=891101,}",
  [158654] = "{id=158654,Text='火美子！',Speaker=891101,}",
  [158655] = "{id=158655,Text='嫁给我！做我的新娘吧！',Speaker=891101,}",
  [158656] = "{id=158656,Text='嘻嘻嘻哈哈哈哈！新娘？结婚？结婚是个啥？',Speaker=891102,}",
  [158657] = "{id=158657,Text='你……你这也太唐突了吧！',Speaker=891100,}",
  [158658] = "{id=158658,Text='冒险者！战部渡！你们帮帮我！我不会放弃的！我要向火美子求婚！',Speaker=891101,}",
  [158659] = "{id=158659,Text='求婚的气氛和礼物很重要。',}",
  [158660] = "{id=158660,Text='（小声）希望虎王这次能成功吧……',Speaker=891100,}",
  [158661] = "{id=158661,Text='虎王还真是一点没变啊……',Speaker=891100,}",
  [158670] = "{id=158670,Text='感谢奥丁，能解决英灵发疯的人终于来了！',Speaker=892466,Option='？？',Emoji=24,}",
  [158671] = "{id=158671,Text='咳咳……抱歉，这段时间我压力太大，有点失态了。',Speaker=892466,}",
  [158672] = "{id=158672,Text='唉，你近日应该也有所感受才对……本应该象征着正义与勇气的英灵，不知道为何陷入了黑暗中，竟然开始攻击米德加尔特了！',Speaker=892466,Option='你说英灵？！',}",
  [158673] = "{id=158673,Text='是的，就是那些曾在战争中为了世界而牺牲的人。',Speaker=892466,}",
  [158674] = "{id=158674,Text='……虽然难以置信，但彩虹桥坠落后，这里发生了太多难以解释的事件。',Speaker=892466,}",
  [158675] = "{id=158675,Text='英灵们生前都是品性高洁的英雄……怎么会突然变成这样？在这幕后，一定有不为人知的原因。',Speaker=892466,Option='我能帮上忙吗',Emoji=15,}",
  [158676] = "{id=158676,Text='……奥丁在上！不可思议，你的身上，竟然有着英灵[c][ffff00]梅菲尔林瑟[-][/c]遗留的魔法残片！这或许是个转机……',Speaker=892466,Option='需要怎么做？',Emoji=11,}",
  [158677] = "{id=158677,Text='只有那些生前就有着坚定信仰的人才有可能成为英灵。找到他们的初心，或许就能让他们清醒过来。',Speaker=892466,}",
  [158678] = "{id=158678,Text='唔。我会尝试让你身上的碎片，与英灵的魔力共鸣。',Speaker=892466,}",
  [158679] = "{id=158679,Text='如果成功，你就可以进入[c][ffff00]梅菲尔林瑟[-][/c]的内心世界……这样或许就能找到他陷入黑暗的原因！',Speaker=892466,Option='！！',}",
  [158680] = "{id=158680,Text='事情紧急，请尽快解决，拜托了！',Speaker=892466,}",
  [158681] = "{id=158681,Text='事关米德加尔特的安危，等你做好准备请一定要来找我！',Speaker=892466,}",
  [158750] = "{id=158750,Text='魔导炮计划的实施，是王国的一次富有想象力与勇气的壮举。',Speaker=892250,}",
  [158751] = "{id=158751,Text='先是通过提取晶石中的魔力，将其提纯凝聚—',Speaker=892250,}",
  [158752] = "{id=158752,Text='再借由魔力水晶的共振增幅，最终向天空中的彩虹桥残骸发出无比强大的一击！',Speaker=892250,}",
  [158753] = "{id=158753,Text='发射的机会只有一次，我们务必要做到一击成功！',Speaker=892250,Emoji=30,}",
  [158754] = "{id=158754,Text='冒险者，如今我们来到了前所未有的危急时刻……',Speaker=892251,}",
  [158755] = "{id=158755,Text='魔导炮正在紧张的筹备过程中，我们大家都在为魔导炮的修建以及发射能量的集聚而忙碌。',Speaker=892251,}",
  [158756] = "{id=158756,Text='王国对我们委以重任，这次代号为“海姆达尔之眼”的魔导炮计划不容有失。',Speaker=892251,}",
  [158757] = "{id=158757,Text='但或许越是处于危急的时刻，我们越需要笃定与勇气……',Speaker=892251,Emoji=20,}",
  [158758] = "{id=158758,Text='你好呀，冒险者，欢迎来到“海姆达尔之眼”计划的启动地—哥布灵森林！',Speaker=892252,}",
  [158759] = "{id=158759,Text='要知道，哥布灵森林是我们久经考虑才选定的地方哦！',Speaker=892252,}",
  [158760] = "{id=158760,Text='这里地形开阔，远离城市，非常适合魔导炮的安置。',Speaker=892252,}",
  [158761] = "{id=158761,Text='而我和我的伙伴们也都来到了这片森林里，一同解决这场危机！',Speaker=892252,}",
  [158762] = "{id=158762,Text='嘿，再加把劲儿呀大家！',Speaker=892252,Emoji=23,}",
  [158763] = "{id=158763,Text='哼，哥布灵森林最近变得好热闹，许多人类都涌入了这里……',Speaker=892261,}",
  [158764] = "{id=158764,Text='可恶可恶，你们这些家伙，真是，吵死啦！',Speaker=892261,Emoji=22,}",
  [158765] = "{id=158765,Text='拿着锤子叮里哐啷的工匠，用魔法一通乱砸的魔法师……',Speaker=892261,}",
  [158766] = "{id=158766,Text='天啊，这种日子什么时候是个头啊！',Speaker=892261,Emoji=15,}",
  [158767] = "{id=158767,Text='喵~大家都工作得好认真~',Speaker=892595,}",
  [158768] = "{id=158768,Text='在我的眼里，你们都是一些可以靠得住的人呢。',Speaker=892595,Emoji=27,}",
  [158769] = "{id=158769,Text='等到危机解除的那一天，我要请你们来我的家乡做客哦，那里的美食与景色都很不错呢！',Speaker=892595,}",
  [158770] = "{id=158770,Text='那颗挂在天空中的奇怪的星星迟迟没有消失……',Speaker=892598,}",
  [158771] = "{id=158771,Text='洛阳也笼罩在这次的彩虹桥危机之中……',Speaker=892598,}",
  [158772] = "{id=158772,Text='这次的魔导炮计划，关系到我们所有人的命运。',Speaker=892598,}",
  [158773] = "{id=158773,Text='拜托了，米德加尔特的冒险者们，洛阳的人们也会和你们站在一起！',Speaker=892598,Emoji=39,}",
  [158774] = "{id=158774,Text='冒险者，幸会……',Speaker=892594,}",
  [158775] = "{id=158775,Text='我从天水之国而来，受我的首领的委派来访此地。',Speaker=892594,}",
  [158776] = "{id=158776,Text='我们大家都在密切关注着这场危机可能产生的后续影响。',Speaker=892594,}",
  [158777] = "{id=158777,Text='而你们的魔导炮计划对解除这场危机有着至关重要的作用。',Speaker=892594,}",
  [158778] = "{id=158778,Text='圣歌教会也已经关注到了这次彩虹桥危机。',Speaker=892590,}",
  [158779] = "{id=158779,Text='我和玛丽萨修女受到教会的委派来到了这里。',Speaker=892590,}",
  [158780] = "{id=158780,Text='勇士们，请继续为你们的魔导炮计划而劳作吧……',Speaker=892590,}",
  [158781] = "{id=158781,Text='我坚信，神会庇护我们大家的。',Speaker=892590,Emoji=30,}",
  [158782] = "{id=158782,Text='魔导炮的建设既需要有工匠和魔法师的工作，也需要有保障魔导炮计划运作正常的其他人员哦！',Speaker=892587,}",
  [158783] = "{id=158783,Text='这中间，有督造官和学士的统筹管理，有神父修女的祈祷抚慰，也要有我们巡守队的存在！',Speaker=892587,}",
  [158784] = "{id=158784,Text='哼，有我们在，那些讨厌的哥布灵们是不会扰乱这里的建设秩序的！',Speaker=892587,Emoji=23,}",
  [158785] = "{id=158785,Text='魔导炮的设计定型需要精密的规划和计算，绝不能有任何的疏忽遗漏。',Speaker=892589,}",
  [158786] = "{id=158786,Text='不过，请放心交给我吧！',Speaker=892589,}",
  [158787] = "{id=158787,Text='嘿呀，确保工程设计的完美无瑕可是一名天才工程师的自我修养哦！',Speaker=892589,Emoji=20,}",
  [158788] = "{id=158788,Text='魔导炮计划成功了！冒险者，你看见那场流星雨了吗？',Speaker=892292,Emoji=26,}",
  [158789] = "{id=158789,Text='那正是日夜坚守在哥布灵森林的大家的杰作！',Speaker=892292,}",
  [158790] = "{id=158790,Text='而这一次的胜利也是属于你们的！',Speaker=892292,}",
  [158791] = "{id=158791,Text='冒险者们，你们是魔导炮计划的坚强后盾！',Speaker=892292,Emoji=30,}",
  [158792] = "{id=158792,Text='冒险者，有没有觉得这里顿时冷清了许多？',Speaker=892293,}",
  [158793] = "{id=158793,Text='没错，前些天到这里来的客人们都已经回去啦，只有我和我的伙伴们在进行最后的收尾工作。',Speaker=892293,}",
  [158794] = "{id=158794,Text='客人们不辞辛劳地赶赴到这里，与我们一同见证了魔导炮计划的胜利实施。',Speaker=892293,}",
  [158795] = "{id=158795,Text='临别的时候，他们都表示，这可真是一趟令人难忘的旅程呢！',Speaker=892293,Emoji=20,}",
  [158805] = "{id=158805,Text='怎么了，伊莉雅，你看上去很焦虑呀~',}",
  [158806] = "{id=158806,Text='怎么会呢。。。',Speaker=892609,Emoji=1,}",
  [158807] = "{id=158807,Text='刚好，冒险者你能帮我试吃一下我新做的蛋糕嘛？',Speaker=892609,}",
  [158808] = "{id=158808,Text='哦~这个时候做蛋糕，不会是。。。嗯嗯？（疯狂暗示）',Emoji=5,}",
  [158809] = "{id=158809,Text='（害羞）讨厌啦………………',Speaker=892609,Emoji=25,}",
  [158810] = "{id=158810,Text='这段时间我都会做蛋糕哦~你要是喜欢的话每天都可以过来吃哦~',Speaker=892609,}",
  [158812] = "{id=158812,Text='我不做单身猴啦~',Speaker=892610,Option='{恭喜恭喜,3},{旁边这两位…,4}',}",
  [158813] = "{id=158813,Text='嗨呀~旁边这两个猴是我乐团的“哼哈二将！”',Speaker=892610,}",
  [158814] = "{id=158814,Text='哎呀~突然发现找个对象也不是很难的事儿嘛~',Speaker=892610,Option='可不是么（心虚）',Emoji=26,}",
  [158815] = "{id=158815,Text='嗯？冒险者你不对劲？',Speaker=892610,}",
  [158816] = "{id=158816,Text='你怎么这么闲？',Speaker=892610,}",
  [158817] = "{id=158817,Text='难道你还没找到对象？',Speaker=892610,Option='……',}",
  [158818] = "{id=158818,Text='不会吧~不会吧~不会有人还没对象吧~',Speaker=892610,Option='揍他！！！',Emoji=5,}",
  [158833] = "{id=158833,Text='你好，冒险者。',Speaker=891138,}",
  [158834] = "{id=158834,Text='感谢你们多日来的奋战，迷失的英灵们已经逐步摆脱了邪恶力量的控制，选择不再与我们为敌。',Speaker=891138,}",
  [158835] = "{id=158835,Text='现在，英灵的威胁已经离我们远去，只有那些来自未知世界的精灵生物们还在继续与我们战斗。',Speaker=891138,}",
  [158836] = "{id=158836,Text='在天津町，那里的守护者们正在请求我们的支援。',Speaker=891138,}",
  [158837] = "{id=158837,Text='你愿意赶往天津町帮助他们吗？',Speaker=891138,Option='没问题',}",
  [158838] = "{id=158838,Text='如果一切进展顺利的话，但愿这将是最后的战斗……',Speaker=891138,}",
  [158839] = "{id=158839,Text='天水之国的守护者们正在呼唤着我们。',Speaker=891138,}",
  [158840] = "{id=158840,Text='准备好前往那里的战场了吗？',Speaker=891138,Option='{准备好了,2},{再等等,3}',}",
  [158841] = "{id=158841,Text='大战在即，充足的准备是赢得胜利的关键。',Speaker=891138,}",
  [158842] = "{id=158842,Text='米德加尔特的勇士，欢迎。',Speaker=891127,}",
  [158843] = "{id=158843,Text='过去的日子里，我们一直在与那些来自未知世界的精灵生物们作战。',Speaker=891127,}",
  [158844] = "{id=158844,Text='可他们像是没有自己的意志一样，只是在向我们一次次发起无谓的进攻。',Speaker=891127,}",
  [158845] = "{id=158845,Text='与此同时，还有一些精灵生物似乎正在暗暗筹备一种神秘的献祭仪式……',Speaker=891127,Option='询问敌人意图',}",
  [158846] = "{id=158846,Text='经由这种仪式，这些精灵生物似乎就可以与他们的世界产生更稳定更强大的联结，从而就可以召集源源不断的援军……',Speaker=891127,}",
  [158847] = "{id=158847,Text='献祭仪式即将完成，我们必须阻止他们。',Speaker=891127,}",
  [158848] = "{id=158848,Text='事不宜迟，我们这就出发，请协助我们！',Speaker=891127,Option='义不容辞',}",
  [158849] = "{id=158849,Text='这些精灵看上去好像有些不对劲，他们的表情显得很痛苦，似乎是被控制了……',}",
  [158850] = "{id=158850,Text='仪式马上就要完成了，快去阻止他们！',Speaker=891128,}",
  [158851] = "{id=158851,Text='“阿卡娜……阿卡娜……”',}",
  [158852] = "{id=158852,Text='不好，他们所祈求的强大存在要降临了！',Speaker=891129,Emoji=37,}",
  [158853] = "{id=158853,Text='这将是我们至今为止所遇到的最强大的敌人，准备应战！',Speaker=891127,}",
  [158854] = "{id=158854,Text='哼，没想到过了这么久我的属下还没取得胜利，真是没用呢。',Speaker=891130,}",
  [158855] = "{id=158855,Text='看来，为了达成大人的心愿，只好要我亲自出场了。',Speaker=891130,Option='你到底是谁？',Emoji=26,}",
  [158856] = "{id=158856,Text='呵，冒险者，在你们面前的是[c][ffff00]来自至高神界的神使阿卡娜大人[-][/c]。',Speaker=891130,}",
  [158857] = "{id=158857,Text='彩虹桥碎片的能量不是你们该拥有的，不要做无谓的反抗了。',Speaker=891130,}",
  [158858] = "{id=158858,Text='现在，既然本神使已经降临，你们的反抗也该终止了。',Speaker=891131,}",
  [158859] = "{id=158859,Text='……刚刚来到这个世界，我的力量还没有恢复完全，这次先放过你们。',Speaker=891131,}",
  [158860] = "{id=158860,Text='米德加尔特的可怜虫们，耐心迎候你们的末日吧！',Speaker=891131,Emoji=5,}",
  [158861] = "{id=158861,Text='阿卡娜，你必须要为入侵我们的家园付出代价！',Speaker=891127,}",
  [158862] = "{id=158862,Text='忍者们，集结战斗！',Speaker=891127,}",
  [158863] = "{id=158863,Text='忍者们，凝结我们的力量，结为坚固的术阵！',Speaker=891128,}",
  [158864] = "{id=158864,Text='如同御神樱永不凋零的樱花，绽放我们的“魂之力”！',Speaker=891129,}",
  [158865] = "{id=158865,Text='魂之力集聚！',}",
  [158866] = "{id=158866,Text='忍道的奥义，既是强袭，也是守护——',Speaker=891128,}",
  [158867] = "{id=158867,Text='神明啊，请赐予我们结界之力！',Speaker=891129,}",
  [158868] = "{id=158868,Text='结界封印！',}",
  [158869] = "{id=158869,Text='可恶……一群不自量力的家伙，你们以为这样就能困住我吗？',Speaker=891131,}",
  [158870] = "{id=158870,Text='米德加尔特的勇士，我们暂时用术阵封印住了她，现在是击败她的最好机会！',Speaker=891127,}",
  [158871] = "{id=158871,Text='我会同你一起作战！',Speaker=891127,}",
  [158872] = "{id=158872,Text='阿卡娜，尝尝这一击吧！',Speaker=891127,}",
  [158873] = "{id=158873,Text='啊啊啊啊啊啊啊啊——',Speaker=891131,}",
  [158874] = "{id=158874,Text='够了，这场闹剧该结束了！',Speaker=891131,}",
  [158875] = "{id=158875,Text='你们这些讨厌的家伙倒还算是些棘手的敌人，如果可以的话，我也不想与你们为敌。',Speaker=891131,}",
  [158876] = "{id=158876,Text='但很可惜，我也有不得不赢得这场战争的理由……',Speaker=891131,}",
  [158877] = "{id=158877,Text='只要把彩虹碎片抢夺回来，大人一定会很高兴吧。',Speaker=891131,Emoji=21,}",
  [158878] = "{id=158878,Text='我是不会让她失望的，哼，我们来日见！',Speaker=891131,}",
  [158879] = "{id=158879,Text='阿卡娜还会卷土重来的，我们必须做好准备。',Speaker=891127,}",
  [158880] = "{id=158880,Text='敌人的意图我们已经明了——她们似乎就是奔着彩虹桥碎片而来的。',Speaker=891127,}",
  [158881] = "{id=158881,Text='所以不止是在这里，在彩虹桥碎片坠落的其他地方；斐扬树林，伊斯鲁得岛，妙勒尼山脉，伊达平原……',Speaker=891127,}",
  [158882] = "{id=158882,Text='阿卡娜统领的精灵部队都有可能从那里登陆！',Speaker=891127,Option='新的危机出现了',Emoji=15,}",
  [158883] = "{id=158883,Text='没错，事态紧急，冒险者，请即刻返回普隆德拉，向那里的人们传达这个消息！',Speaker=891127,}",
  [158884] = "{id=158884,Text='在天津町出现了什么状况吗？',Speaker=891138,Option='报告情况',}",
  [158885] = "{id=158885,Text='我知道了，看来，这场阴谋的主使者终于登场了么……',Speaker=891138,}",
  [158886] = "{id=158886,Text='阿卡娜……阿卡娜……我会向王国报告这一紧急事件的。',Speaker=891138,}",
  [158887] = "{id=158887,Text='蔓延至今日的彩虹桥危机已经到了最危急的时刻……决战已经开始。',Speaker=891138,}",
  [158888] = "{id=158888,Text='冒险者们，我们一定会赢得胜利的！',Speaker=891138,}",
  [158892] = "{id=158892,Text='因为你，我才能拥有这么美好的一段恋爱~',Speaker=892610,}",
  [158893] = "{id=158893,Text='因为你，我才能与伊莉雅温暖不言殇，倾心相遇，安暖相陪~',Speaker=892610,Option='{啊这,0},{我想加件衣服,0}',}",
  [158894] = "{id=158894,Text='（我受不了了）',}",
  [158895] = "{id=158895,Text='嘻嘻~我不说了，这是我一点心意，请收下把~',Speaker=892610,}",
  [158896] = "{id=158896,Text='唉哟，我错了，请接受我的道歉~',Speaker=892610,Option='哼！',}",
  [158899] = "{id=158899,Text='嗨！亲爱的冒险者~我又又又双叒叕来了呢~',Speaker=892615,Option='{热烈欢迎,0},{鼓掌,0}',Emoji=17,}",
  [158900] = "{id=158900,Text='在过去的几个月呢，我跨越了无数的位面~',Speaker=892615,}",
  [158901] = "{id=158901,Text='有见证过可歌可泣、荡气回肠的爱情；',Speaker=892615,}",
  [158902] = "{id=158902,Text='也见过平淡如水却又地久天长的爱情；',Speaker=892615,}",
  [158903] = "{id=158903,Text='有看似天造地设，但结果却是无疾而终…',Speaker=892615,Emoji=19,}",
  [158904] = "{id=158904,Text='我在想那些注定没有结果的，能叫爱情吗？最多只能叫做缘分吧~',Speaker=892615,Option='……',}",
  [158905] = "{id=158905,Text='喂~这可不是“爱神”的发言啊！',}",
  [158906] = "{id=158906,Text='嘻嘻~言归正传，冒险者！这次我要“验证”一个猜想~',Speaker=892615,Emoji=17,}",
  [158907] = "{id=158907,Text='最近我们大家都在讨论天空中的异象……',Speaker=892198,}",
  [158908] = "{id=158908,Text='冒险者，你注意到了吗？',Speaker=892198,Option='{已经看到了,2},{还没看到,3}',}",
  [158909] = "{id=158909,Text='呵呵，当然了，普隆德拉或许已经没有人还没注意到天空中的异象了。',Speaker=892198,}",
  [158910] = "{id=158910,Text='毕竟，这实在是太过反常了……',Speaker=892198,}",
  [158911] = "{id=158911,Text='呵呵，忙碌的冒险者，这两天你似乎错过了些什么。',Speaker=892198,}",
  [158912] = "{id=158912,Text='不要紧，让我来指给你看吧—',Speaker=892198,}",
  [158913] = "{id=158913,Text='你瞧！',Speaker=892198,}",
  [158914] = "{id=158914,Text='看到了吗？天空中的异象！',Speaker=892198,}",
  [158915] = "{id=158915,Text='呵呵，我们大家在第一次看见这样的天空的时候也是被吓了一跳……',Speaker=892198,}",
  [158916] = "{id=158916,Text='彩虹色的流星，大大的如同陨石状的东西，这到底是怎么一回事呢……',Speaker=892198,}",
  [158917] = "{id=158917,Text='最近我们大家都在讨论天空中的异象……',Speaker=892225,}",
  [158918] = "{id=158918,Text='冒险者，你注意到了吗？',Speaker=892225,Option='{已经看到了,2},{还没看到,3}',}",
  [158919] = "{id=158919,Text='呵呵，当然了，普隆德拉或许已经没有人还没注意到天空中的异象了。',Speaker=892225,}",
  [158920] = "{id=158920,Text='毕竟，这实在是太过反常了……',Speaker=892225,}",
  [158921] = "{id=158921,Text='呵呵，忙碌的冒险者，这两天你似乎错过了些什么。',Speaker=892225,}",
  [158922] = "{id=158922,Text='不要紧，让我来指给你看吧—',Speaker=892225,}",
  [158923] = "{id=158923,Text='你瞧！',Speaker=892225,}",
  [158924] = "{id=158924,Text='看到了吗？天空中的异象！',Speaker=892225,}",
  [158925] = "{id=158925,Text='呵呵，我们大家在第一次看见这样的天空的时候也是被吓了一跳……',Speaker=892225,}",
  [158926] = "{id=158926,Text='彩虹色的流星，大大的如同陨石状的东西，这到底是怎么一回事呢……',Speaker=892225,}",
  [158927] = "{id=158927,Text='冒险者，你听说过龙神之剑吗？这原本是一件异世界的神器，在一次偶然的机会下，我得到了它的配方。',Speaker=892433,}",
  [158928] = "{id=158928,Text='传说只有被命运选中的勇士才能驾驭它，如果你想试试，可以来找我。',Speaker=892433,}",
  [158929] = "{id=158929,Text='队长，在斐扬树林有紧急情况发生！',Speaker=891055,}",
  [158930] = "{id=158930,Text='大量未知的魔物涌入了那里，向我们发起了进攻！',Speaker=891055,Emoji=37,}",
  [158931] = "{id=158931,Text='其中……还有一位[c][ffff00]神秘的女魔法师[-][/c]。，她……似乎并不是人类！',Speaker=891055,}",
  [158932] = "{id=158932,Text='我们需要增援！',Speaker=891055,}",
  [158983] = "{id=158983,Text='[c][ffff00][PlayerName][-][/c]！感谢你一直以来的支持，请你收下这份礼物吧。',Speaker=892639,}",
  [158984] = "{id=158984,Text='我曾听过一句话：',Speaker=892615,Emoji=28,}",
  [158985] = "{id=158985,Text='“鉴别他（她）是否爱你通常可以通过他（她）是否肯为你有所付出。”',Speaker=892615,}",
  [158986] = "{id=158986,Text='比如穷人如果肯为你付出金钱，富人肯为你付出时间等等。。。',Speaker=892615,}",
  [158987] = "{id=158987,Text='所以……你愿意为我付出“宝石”吗？嗯？（阴险）',Speaker=892615,Option='{我拒绝,0},{算你狠,0}',}",
  [158993] = "{id=158993,Text='勇敢的冒险者……这或许是我最后一次呼唤你的到来了。',Speaker=891040,}",
  [158994] = "{id=158994,Text='在我们的勇士多日的奋战之后，阿卡娜所役使的仆从部队已全部败退到了天津町！',Speaker=891040,Emoji=39,}",
  [158995] = "{id=158995,Text='这段日子里，我们一同经历了彩虹桥残骸的坠落，迷失英灵的来袭，阿卡娜的进犯……并通过了我们的智慧和力量逐步解除了这些危机。',Speaker=891040,}",
  [158996] = "{id=158996,Text='如今，与阿卡娜的决战已经开始了，天津町将是我们最后的战场。',Speaker=891040,}",
  [158997] = "{id=158997,Text='请去支援天津町的勇士们吧，让我们一起迎接最终的胜利！',Speaker=891040,Option='好的',Emoji=27,}",
  [158998] = "{id=158998,Text='天津町的忍者们正需要我们的援助。',Speaker=891040,}",
  [158999] = "{id=158999,Text='你准备好了吗？',Speaker=891040,Option='{准备好了,2},{再等等,3}',}",
  [159000] = "{id=159000,Text='最后的战斗更需要充足的准备！',Speaker=891040,}",
  [159001] = "{id=159001,Text='眼前的几位忍者似乎被魔物围困住了，快去解救他们！',}",
  [159002] = "{id=159002,Text='感谢你的援助，米德加尔特的勇士。',Speaker=891132,}",
  [159003] = "{id=159003,Text='敌人还在进行最后的攻势……',Speaker=891132,}",
  [159004] = "{id=159004,Text='我们的首领百地正在与阿卡娜交战。',Speaker=891132,}",
  [159005] = "{id=159005,Text='阿卡娜的强大远超我们的想象，战况十分激烈……',Speaker=891132,}",
  [159006] = "{id=159006,Text='如果可以的话，请去协助他。',Speaker=891132,Option='尽快过去',}",
  [159007] = "{id=159007,Text='拜托了！',Speaker=891132,}",
  [159008] = "{id=159008,Text='阿卡娜，你奴役的仆从已经被尽数击退了！',Speaker=891127,}",
  [159009] = "{id=159009,Text='我们所信仰的神明给了我们祝福与庇护，胜利已经是我们的了！',Speaker=891127,}",
  [159010] = "{id=159010,Text='哼，胜利？什么可笑的胜利？你们能够赢得的只有悲惨的血与火！',Speaker=891131,}",
  [159011] = "{id=159011,Text='我们之间的战斗该结束了……阿卡娜，你输了。',Speaker=891127,}",
  [159012] = "{id=159012,Text='这不可能……不……我是不会输的……',Speaker=891131,}",
  [159013] = "{id=159013,Text='你们还没有见识过我真正的力量！',Speaker=891131,}",
  [159014] = "{id=159014,Text='一群无知的家伙，感受神使的震怒吧！',Speaker=891131,}",
  [159015] = "{id=159015,Text='不好，阿卡娜要做殊死一搏了！',Speaker=891127,}",
  [159016] = "{id=159016,Text='忍者们，战斗警戒！',Speaker=891127,}",
  [159017] = "{id=159017,Text='等等，出现了什么？',Speaker=891131,Emoji=37,}",
  [159018] = "{id=159018,Text='不……怎么会有一股令我战栗的气息……',Speaker=891131,}",
  [159019] = "{id=159019,Text='阿卡娜！',Speaker=891146,}",
  [159020] = "{id=159020,Text='怎么会……这个声音是……不不……这不可能……',Speaker=891131,Emoji=37,}",
  [159021] = "{id=159021,Text='这不是我能抵抗的力量……我必须要在他降临前撤离了……',Speaker=891131,}",
  [159022] = "{id=159022,Text='（精灵守卫们彼此说着晦涩难懂的话语，似乎是在欢庆他们摆脱了阿卡娜的控制）',Speaker=891148,}",
  [159023] = "{id=159023,Text='此刻降临到天水之国的，是……神王奥丁？！',}",
  [159024] = "{id=159024,Text='年轻的冒险者，我们终于见面了……',Speaker=891145,}",
  [159025] = "{id=159025,Text='不必惶恐，你如今所见到的只不过是吾降临到这个世界上的幻影。',Speaker=891145,}",
  [159026] = "{id=159026,Text='你们的战斗吾已见证，阿卡娜掠夺彩虹桥碎片的图谋被挫败了，这是你们不朽的功绩。',Speaker=891145,}",
  [159027] = "{id=159027,Text='吾为你们感到欣慰，你们英勇坚韧的品性无愧于这片英雄们所生活过的土地……',Speaker=891145,}",
  [159028] = "{id=159028,Text='你们，赢得了胜利。',Speaker=891145,}",
  [159029] = "{id=159029,Text='是啊，我们保卫了我们的家园！',Speaker=891128,Emoji=20,}",
  [159030] = "{id=159030,Text='呼~天水之国的和平终于到来了！',Speaker=891129,Emoji=17,}",
  [159031] = "{id=159031,Text='而除却见证你们的战斗，吾此次降临，尚还有一事向你们宣告——',Speaker=891145,}",
  [159032] = "{id=159032,Text='阿卡娜的这次来袭，是借助着陨落的彩虹桥碎片与你们的世界的碰撞而产生的时空波动……',Speaker=891145,}",
  [159033] = "{id=159033,Text='但这样的联结只不过是微弱而短暂的存在，彩虹桥碎片所孕育的真正的通道则还在慢慢的形成过程当中。',Speaker=891145,}",
  [159034] = "{id=159034,Text='吾已经感知到了……在不久的将来，这条通道就会孕育完成。',Speaker=891145,}",
  [159035] = "{id=159035,Text='自此，你们的世界与另一个世界就会建立起真正的联结……',Speaker=891145,Option='那个世界是？',}",
  [159036] = "{id=159036,Text='无需多问，命运的图卷自会向你展开。那里，是一片古老而遍布光辉的土地……',Speaker=891145,}",
  [159037] = "{id=159037,Text='年轻的冒险者，吾已有预感，你的命运是和那里紧密相连的……',Speaker=891145,}",
  [159038] = "{id=159038,Text='在胜利之夜，无畏的英雄们会纵情享受欢庆的酒宴，并在翌日太阳升起的时候踏上他们崭新的征途。',Speaker=891145,}",
  [159039] = "{id=159039,Text='年轻的冒险者，愿胜利的欢乐与希望的曙光始终与你同在……',Speaker=891145,Option='表示谢意',}",
  [159040] = "{id=159040,Text='米德加尔特的冒险者，你永远都是忍者们的朋友。',Speaker=891127,}",
  [159041] = "{id=159041,Text='再见了哟，[c][ffff00][PlayerName][-][/c]，我们勇敢的冒险者伙伴！',Speaker=891128,Emoji=27,}",
  [159042] = "{id=159042,Text='天水之国的樱花静静绽放着，它们曾给过这里的守护者们以战斗的决心与勇气，并陪伴着他们走到了最后的胜利。',}",
  [159043] = "{id=159043,Text='而在大战过后，樱花也迎来了它们自己的盛放……',}",
  [159044] = "{id=159044,Text='飞舞的樱花勾勒出了一副美丽的图景，就像奥丁所宣告的那样，新的命运的图卷也早已从此刻展开……',}",
  [159049] = "{id=159049,Text='好久不见啦，[c][ffff00][PlayerName][-][/c]。',Speaker=891151,}",
  [159050] = "{id=159050,Text='经过了这些天的游历，我们也要到了和你说再见的时候了。',Speaker=891151,}",
  [159051] = "{id=159051,Text='充满爱的人气偶像——熊本熊很快就要回到它的家乡去了……',Speaker=891151,}",
  [159052] = "{id=159052,Text='你看，熊本熊很喜欢你哦！所以在临别前，我们也有你为你准备了一些礼物~',Speaker=891151,Emoji=30,}",
  [159053] = "{id=159053,Text='只需要答对我的问题，就可以获得~怎么样，你准备好了吗？',Speaker=891151,}",
  [159054] = "{id=159054,Text='[c][ffff00][该任务仅限一个账号接取，请慎重考虑后再接取！][-][/c]',Speaker=891151,Option='{准备好了,2},{再等等,3}',}",
  [159055] = "{id=159055,Text='那么请听题——',Speaker=891151,}",
  [159056] = "{id=159056,Text='你知道熊本熊的家乡是哪里呢？',Speaker=891151,Option='{熊本县,5},{依斯鲁得,6},{普隆德拉,6}',}",
  [159057] = "{id=159057,Text='你答错了，再来好好想想吧~',Speaker=891151,}",
  [159058] = "{id=159058,Text='没错，熊本熊说你答对了哦，正是美丽的熊本县！',Speaker=891151,}",
  [159059] = "{id=159059,Text='那这份礼物就送给你啦~明天记得再来哦！',Speaker=891151,}",
  [159060] = "{id=159060,Text='[c][ffff00][PlayerName][-][/c]，你又来啦~',Speaker=891151,}",
  [159061] = "{id=159061,Text='今天的问题是和吃有关哦，在你们的世界的这段日子，我和熊本熊都品尝到了许多美食呢！',Speaker=891151,}",
  [159062] = "{id=159062,Text='那么，你知道在熊本熊的家乡熊本县有什么著名的美食吗？',Speaker=891151,Option='{皇家料理,3},{熊本拉面,2},{狸猫饼干,3}',}",
  [159063] = "{id=159063,Text='你答错了，再来好好想想吧~',Speaker=891151,}",
  [159064] = "{id=159064,Text='回答对了哦，正是熊本拉面！那就请收好这份礼物吧~',Speaker=891151,Emoji=27,}",
  [159065] = "{id=159065,Text='明天见啦！',Speaker=891151,}",
  [159066] = "{id=159066,Text='冒险者，不知不觉，今天就是熊本熊停留在这个世界的最后一天了……',Speaker=891151,}",
  [159067] = "{id=159067,Text='真是一段令人难忘的时光！',Speaker=891151,}",
  [159068] = "{id=159068,Text='唔……因为是最后一天了，我和熊本熊也会为你准备了额外的礼物哦～',Speaker=891151,}",
  [159069] = "{id=159069,Text='那么就请回答最后一个问题吧！',Speaker=891151,}",
  [159070] = "{id=159070,Text='你知道吗？熊本熊的家乡熊本县因为临近火山，所以有着“火之国”的别名～',Speaker=891151,}",
  [159071] = "{id=159071,Text='[c][ffff00][PlayerName][-][/c]，你可以告诉我和熊本熊，在你们的世界，那个同样也与“火山”有关的地方是哪里吗？',Speaker=891151,Option='{艾尔帕兰,3},{斐扬树林,3},{熔岩洞窟,2}',}",
  [159072] = "{id=159072,Text='似乎并不是这个地方哦，再来好好想想吧~',Speaker=891151,}",
  [159073] = "{id=159073,Text='原来那个地方是叫做“熔岩洞窟”啊，谢谢你啦，冒险者～',Speaker=891151,}",
  [159074] = "{id=159074,Text='再见啦，[c][ffff00][PlayerName][-][/c]，希望有机会我们还可以再见到你！',Speaker=891151,}",
  [159075] = "{id=159075,Text='准备好了吗，冒险者？这可能是我们唯一的机会了……',Speaker=892466,Option='{准备好了,4},{还没有,3}',Emoji=15,}",
  [159289] = "{id=159289,Text='……奥丁在上！不可思议，你的身上，竟然有着英灵[c][ffff00]塞壬[-][/c]遗留的魔法残片！这或许是个转机……',Speaker=892466,Option='需要怎么做？',Emoji=11,}",
  [159290] = "{id=159290,Text='如果成功，你就可以进入[c][ffff00]塞壬[-][/c]的内心世界……这样或许就能找到他陷入黑暗的原因！',Speaker=892466,Option='！！',}",
  [159305] = "{id=159305,Text='……奥丁在上！不可思议，你的身上，竟然有着英灵[c][ffff00]赫德[-][/c]遗留的记忆碎片！这或许是个转机……',Speaker=892466,Option='需要怎么做？',Emoji=11,}",
  [159307] = "{id=159307,Text='如果成功，你就可以进入赫德的内心世界……这样或许就能找到他陷入黑暗的原因！',Speaker=892466,Option='！！',}",
  [159312] = "{id=159312,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=892680,}",
  [159313] = "{id=159313,Text='想必你也对这位风格迥异的冒险者以及这巨大的裂缝感到好奇~',Speaker=892680,Emoji=5,}",
  [159314] = "{id=159314,Text='唔……可此事事关重大，需要经验丰富的冒险者才能进一步了解后续的事件。',Speaker=892680,Option='需要怎么做？',}",
  [159315] = "{id=159315,Text='我这里有不少前辈的[c][ffff00]「冒险经验」[-][/c]可以帮助到更多的冒险者。只要你愿意将[c][ffff00]「冒险经验」[-][/c]传递出去，帮助到其他人，想必就可以获得认可。',Speaker=892680,}",
  [159318] = "{id=159318,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「学习笔记」x1）[-][/c]\n那么，今天需要分享出去的[c][ffff00]「冒险经验」[-][/c]是：',Speaker=892680,}",
  [159338] = "{id=159338,Text='你好，冒险者！我是裂隙监视者洛奇！',Speaker=891158,Option='奇怪的笔记本',}",
  [159339] = "{id=159339,Text='这本手账的气息……似乎来自异界。我需要好好研究一下……',Speaker=891158,}",
  [159340] = "{id=159340,Text='但是刚刚传来报告，城外的魔物出现了异变，可能跟普隆德拉的时空裂隙有关，能不能请你替我调查一下呢？',Speaker=891158,Option='好的！',}",
  [159341] = "{id=159341,Text='去网吧……吱吱……充点卡……吱吱……@#￥%@！……',Speaker=891159,}",
  [159342] = "{id=159342,Text='（这只胡言乱语的溜溜猴应该发生了异变，抓住它调查一下……）',}",
  [159343] = "{id=159343,Text='吱——！干什么吱！',Speaker=891159,Option='（抓紧）',Emoji=37,}",
  [159344] = "{id=159344,Text='吱！吱吱！（兄弟们，一起揍他！）',Speaker=891159,Emoji=12,}",
  [159345] = "{id=159345,Text='（逃走的溜溜猴似乎遗落了什么东西……）',}",
  [159346] = "{id=159346,Text='你回来了，[c][ffff00][PlayerName][-][/c]！调查得怎样？',Speaker=891158,Option='报告',}",
  [159347] = "{id=159347,Text='闪闪发光的晶体？让我看看……',Speaker=891158,}",
  [159348] = "{id=159348,Text='（棱镜……融入到了手账中？）',}",
  [159349] = "{id=159349,Text='太神奇了！这块晶体竟然被手账吸收了？',Speaker=891158,}",
  [159350] = "{id=159350,Text='……感觉手账的封印似乎松动了一些！',Speaker=891158,}",
  [159351] = "{id=159351,Text='如果能打开这本手账，说不定就能知道引发魔物异变的真正原因了——真是个好主意！',Speaker=891158,}",
  [159352] = "{id=159352,Text='从大陆各处的魔物身上收集[c][ffff00]记忆棱镜[-][/c]，然后[c][ffff00]交给洛克[-][/c]，解锁这本手账吧！',Speaker=891158,Option='好的！',}",
  [159353] = "{id=159353,Text='加油哦，[c][ffff00][PlayerName][-][/c]！',Speaker=891158,}",
  [159394] = "{id=159394,Text='嘿，冒险者，快过来~',Speaker=892683,Option='怎么了？',}",
  [159395] = "{id=159395,Text='我是裂隙监视者洛林，职责是消除时空裂隙对大陆带来的威胁。',Speaker=892683,}",
  [159396] = "{id=159396,Text='可以请你帮我个忙吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892683,Option='{可以,3},{现在没时间,2}',}",
  [159397] = "{id=159397,Text='真是太遗憾了，如果你改主意了可以随时来找我！',Speaker=892683,}",
  [159398] = "{id=159398,Text='万分感谢！就在几天前，普隆德拉突然出现了一道时空裂隙，但从裂隙中出现的并不是魔物，而是一个人类。',Speaker=892683,}",
  [159399] = "{id=159399,Text='他叫罗安，自称是一名冒险者，来自米德加尔特……',Speaker=892683,Option='{什么意思？,0},{你说错了？,0}',}",
  [159400] = "{id=159400,Text='虽然听上去有些不可思议，但如果罗安的描述属实，似乎存在着另一个米德加尔特大陆。',Speaker=892683,}",
  [159401] = "{id=159401,Text='吉芬的法师们将这一发现命名为“平行世界”。',Speaker=892683,}",
  [159402] = "{id=159402,Text='这个罗安……好像有些冒失，而且他“原本的世界”与这个世界也有很多不同，我怕他惹出麻烦，所以能不能请你跟着他，防止他乱来呢？',Speaker=892683,Option='义不容辞',}",
  [159403] = "{id=159403,Text='这个法术可以让你感知到罗安身上的异界气息，这样你永远都不会跟丢他！',Speaker=892683,}",
  [159404] = "{id=159404,Text='一定要让他遵守这个世界的规则，不能乱来哦！',Speaker=892683,}",
  [159405] = "{id=159405,Text='（前面发生了争吵……）',}",
  [159406] = "{id=159406,Text='（那个画风完全不同的应该就是罗安了……）',}",
  [159407] = "{id=159407,Text='骗子！',Speaker=892684,Emoji=12,}",
  [159408] = "{id=159408,Text='……',Speaker=892702,}",
  [159409] = "{id=159409,Text='你好大的胆子，竟然这么明目张胆的拿假货骗人！',Speaker=892684,}",
  [159410] = "{id=159410,Text='没骗你，真货。',Speaker=892702,}",
  [159411] = "{id=159411,Text='你还敢狡辩！看我不教训你这个骗子！',Speaker=892684,Emoji=12,}",
  [159412] = "{id=159412,Text='（洛林说不能让罗安惹麻烦）',Option='住手！',Emoji=15,}",
  [159413] = "{id=159413,Text='你有什么事吗，冒险者？不要耽误我教训这个骗子！',Speaker=892684,Option='发生了什么？',}",
  [159414] = "{id=159414,Text='这个骗子，在这里摆摊卖月卡，结果都是假货！',Speaker=892684,}",
  [159415] = "{id=159415,Text='都是真的……',Speaker=892702,Option='误会？',}",
  [159416] = "{id=159416,Text='不信你看！',Speaker=892684,}",
  [159417] = "{id=159417,Text='（看上去就是普通的限定特典……）',}",
  [159418] = "{id=159418,Text='怎么样，是不是假得离谱了！',Speaker=892684,Option='{应该是真的,0},{难以分辨,0}',}",
  [159419] = "{id=159419,Text='这还不够假？你们不会是一伙的吧！',Speaker=892684,}",
  [159420] = "{id=159420,Text='卡普拉……',Speaker=892702,}",
  [159421] = "{id=159421,Text='（既然是卡普拉公司发行的，找卡普拉服务人员应该可以辨别真伪）',}",
  [159422] = "{id=159422,Text='诶，你要干嘛去？',Speaker=892684,}",
  [159423] = "{id=159423,Text='冒险者你等等我……哦呼~是卡普拉小姐姐',Speaker=892684,}",
  [159424] = "{id=159424,Text='（小声）哎呀，来这里之前好像忘了给小姐姐们投票了……',Speaker=892684,}",
  [159425] = "{id=159425,Text='卡普拉竭诚为您提供最全面的服务~',Speaker=892693,}",
  [159426] = "{id=159426,Text='这位冒险者，您看起来似乎需要帮助的样子~告诉莉莉，莉莉会尽~全~力~帮助您哦！',Speaker=892693,}",
  [159427] = "{id=159427,Text='需要鉴定限定特典的真伪？还真是个奇怪的需求呢！不过莉莉可以做到哦~请稍等~',Speaker=892693,}",
  [159428] = "{id=159428,Text='emmm…………',Speaker=892693,Emoji=14,}",
  [159429] = "{id=159429,Text='鉴定完成了哦~冒险者要不要猜猜结果~',Speaker=892693,Option='{是真的,5},{是假的,4}',}",
  [159430] = "{id=159430,Text='答错了哦~这可是如假包换的“月卡”哦~',Speaker=892693,Emoji=31,}",
  [159431] = "{id=159431,Text='Bingo~这确实是如假包换的“月卡”哦！',Speaker=892693,Emoji=32,}",
  [159432] = "{id=159432,Text='怎么会这样……明明……没有增加游戏时间啊？',Speaker=892684,}",
  [159433] = "{id=159433,Text='（游戏时间？会不会是——）',Option='不同世界',Emoji=11,}",
  [159434] = "{id=159434,Text='啊！你的意思是……两个世界的月卡并不是同一种东西？',Speaker=892684,Option='是的',Emoji=1,}",
  [159435] = "{id=159435,Text='尴尬了……',Speaker=892684,}",
  [159436] = "{id=159436,Text='啊！那就是说我完全错怪了“地摊小哥”！我得赶快去跟他道个歉！',Speaker=892684,}",
  [159437] = "{id=159437,Text='再见了，莉莉小姐！我永远支持卡普拉公司哦！',Speaker=892684,}",
  [159438] = "{id=159438,Text='诶？',Speaker=892693,Emoji=19,}",
  [159439] = "{id=159439,Text='呃，实在不好意思啊，小哥~刚刚我鲁莽了……',Speaker=892684,}",
  [159440] = "{id=159440,Text='明明什么都不知道，却错怪了你……我向你道歉',Speaker=892684,Emoji=16,}",
  [159441] = "{id=159441,Text='……',Speaker=892702,}",
  [159442] = "{id=159442,Text='……',Speaker=892684,}",
  [159443] = "{id=159443,Text='……哦……',Speaker=892702,}",
  [159444] = "{id=159444,Text='收摊，走了。',Speaker=892702,}",
  [159445] = "{id=159445,Text='（真是个冷漠的人啊……）',}",
  [159446] = "{id=159446,Text='他是不是生气了……',Speaker=892684,Option='{个性如此,0},{不爱说话,0}',Emoji=16,}",
  [159447] = "{id=159447,Text='原来是这样吗！',Speaker=892684,}",
  [159448] = "{id=159448,Text='啊，对了！还没自我介绍，我叫罗安。是来自异世界的冒险者！',Speaker=892684,Option='自我介绍',}",
  [159449] = "{id=159449,Text='[c][ffff00][PlayerName][-][/c]，真是个帅气的名字！今天谢谢你的帮忙啦~',Speaker=892684,}",
  [159450] = "{id=159450,Text='我们算是朋友了对吧！',Speaker=892684,}",
  [159451] = "{id=159451,Text='我在这个世界得到了一件超~棒~的好东西！明天带给你一起玩儿~',Speaker=892684,Option='好呀~',}",
  [159452] = "{id=159452,Text='说好了，明天见！',Speaker=892684,}",
  [159453] = "{id=159453,Text='（哼歌）噜噜噜~',Speaker=892684,Option='怎么这么开心',}",
  [159454] = "{id=159454,Text='啊~是[c][ffff00][PlayerName][-][/c]！快来，给你看我得到的“好东西”。',Speaker=892684,}",
  [159455] = "{id=159455,Text='怎么样，猜得到它是什么吗？',Speaker=892684,Option='相机？',}",
  [159456] = "{id=159456,Text='诶？你知道它是什么啊——也对哦，这本来就是“这个世界”的道具！',Speaker=892684,Option='一起去拍照',}",
  [159457] = "{id=159457,Text='好呀好呀！',Speaker=892684,}",
  [159458] = "{id=159458,Text='我要去南门拍摄魔物，完成魔物图鉴！',Speaker=892684,Emoji=23,}",
  [159459] = "{id=159459,Text='哇，好大的是留声机！',Speaker=892684,}",
  [159460] = "{id=159460,Text='[c][ffff00][PlayerName][-][/c]，快帮我拍照~~',Speaker=892684,}",
  [159461] = "{id=159461,Text='啊~真棒！今天的我依旧是那么的帅气呀！',Speaker=892684,Emoji=20,}",
  [159462] = "{id=159462,Text='诶~那是——（小声）稀有魔物狸猫！',Speaker=892684,Emoji=7,}",
  [159463] = "{id=159463,Text='这种生物非常狡猾~只要稍有风吹草动，它就会逃跑哦！我们慢慢移动过去拍照~',Speaker=892684,}",
  [159464] = "{id=159464,Text='嗯？',Speaker=892684,}",
  [159465] = "{id=159465,Text='（低声）我怎么感觉有人在盯着我……',Speaker=892684,Option='好像没人诶',}",
  [159466] = "{id=159466,Text='（低声）我的感觉不会错的！说不定是用了伪装技能……糟糕，我好像不会破除伪装的技能……',Speaker=892684,}",
  [159467] = "{id=159467,Text='有办法了！',Speaker=892684,Emoji=11,}",
  [159468] = "{id=159468,Text='快去帮我打些水来~',Speaker=892684,}",
  [159469] = "{id=159469,Text='准备好，听我的指令，面向那个方向！',Speaker=892684,}",
  [159470] = "{id=159470,Text='泼水！',Speaker=892684,}",
  [159471] = "{id=159471,Text='啊……',Speaker=892686,}",
  [159472] = "{id=159472,Text='！！！！',Speaker=892701,Emoji=7,}",
  [159473] = "{id=159473,Text='（狸猫被吓跑了……）',}",
  [159474] = "{id=159474,Text='…………',Speaker=892686,Option='怎么……',Emoji=12,}",
  [159475] = "{id=159475,Text='（抢话）地摊小哥，你怎么在这儿？',Speaker=892684,}",
  [159476] = "{id=159476,Text='拍照。',Speaker=892686,Option='拍什……',}",
  [159477] = "{id=159477,Text='（抢话）拍照？拍什么呀！',Speaker=892684,}",
  [159478] = "{id=159478,Text='狸猫。跑了。',Speaker=892686,Emoji=12,}",
  [159479] = "{id=159479,Text='小哥你为什么要来拍狸猫的照片呀？',Speaker=892684,}",
  [159480] = "{id=159480,Text='（对方递过来一份报纸……）',}",
  [159481] = "{id=159481,Text='（普城娱乐报………………征集稀有魔物狸猫照片，必有重酬…………）',}",
  [159482] = "{id=159482,Text='呃，好像又闯祸了……',Speaker=892684,Emoji=16,}",
  [159483] = "{id=159483,Text='（叹气）算了。没事。',Speaker=892686,}",
  [159484] = "{id=159484,Text='真的抱歉，我能做什么补偿你吗？',Speaker=892684,}",
  [159485] = "{id=159485,Text='帮我拍照。狸猫。',Speaker=892686,}",
  [159486] = "{id=159486,Text='我装死，当诱饵。你拍照……',Speaker=892686,}",
  [159487] = "{id=159487,Text='啊？这能行吗……',Speaker=892684,}",
  [159488] = "{id=159488,Text='[c][ffff00][PlayerName][-][/c]，你说地摊小哥的办法真的有用嘛？稀有魔物真的会被装死的人吸引？',Speaker=892684,Option='真的',}",
  [159489] = "{id=159489,Text='…………要不然咱们换换，你负责装死，我负责拍照？',Speaker=892684,Option='{容我拒绝,0},{我没学这个技能,0}',Emoji=24,}",
  [159490] = "{id=159490,Text='好吧！',Speaker=892684,}",
  [159491] = "{id=159491,Text='（找个隐蔽的地方藏起来）',Option='跟上罗安',}",
  [159492] = "{id=159492,Text='（低声）怎么样……出现了吗……',Speaker=892684,Option='耐心点',}",
  [159493] = "{id=159493,Text='（低声）它会不会直接把我打死啊QAQ',Speaker=892684,}",
  [159494] = "{id=159494,Text='啊！',Speaker=892684,}",
  [159495] = "{id=159495,Text='救命，这狸猫疯了！',Speaker=892684,Emoji=22,}",
  [159496] = "{id=159496,Text='呼……得救了……',Speaker=892684,Option='没事吧？',}",
  [159497] = "{id=159497,Text='我没事~快把照片给地摊小哥带回去吧！',Speaker=892684,}",
  [159498] = "{id=159498,Text='没错。是狸猫。',Speaker=892686,Emoji=41,}",
  [159499] = "{id=159499,Text='谢了。',Speaker=892686,}",
  [159500] = "{id=159500,Text='嘿嘿，不客气，毕竟是我有错在先……',Speaker=892684,}",
  [159501] = "{id=159501,Text='对了！还不知道小哥你的名字呢',Speaker=892684,}",
  [159502] = "{id=159502,Text='…………',Speaker=892686,}",
  [159503] = "{id=159503,Text='伊鲁卡……',Speaker=892686,}",
  [159504] = "{id=159504,Text='真是个不错的名字，我是罗安，这位是[c][ffff00][PlayerName][-][/c]。',Speaker=892684,}",
  [159505] = "{id=159505,Text='嗯',Speaker=892686,}",
  [159506] = "{id=159506,Text='……',Speaker=892684,}",
  [159507] = "{id=159507,Text='（……）',}",
  [159508] = "{id=159508,Text='先走了，还有事。',Speaker=892686,}",
  [159509] = "{id=159509,Text='呃，好的——再见！',Speaker=892684,Option='再见',}",
  [159510] = "{id=159510,Text='嗯……',Speaker=892686,}",
  [159511] = "{id=159511,Text='还是那么沉默寡言，好酷啊……',Speaker=892684,}",
  [159512] = "{id=159512,Text='[c][ffff00][PlayerName][-][/c]，我也该走了，被狸猫抓了两下还挺疼的……明天再一起玩耍吧~',Speaker=892684,}",
  [159513] = "{id=159513,Text='再见！',Speaker=892684,}",
  [159514] = "{id=159514,Text='（小声）[c][ffff00][PlayerName][-][/c]，你快看那边……',Speaker=892684,}",
  [159515] = "{id=159515,Text='（小声）看到了吗！',Speaker=892684,Option='交易所？',}",
  [159516] = "{id=159516,Text='是大巴帽！闪闪发光的大巴帽！羡慕！',Speaker=892684,Emoji=29,}",
  [159517] = "{id=159517,Text='从很久以前，在“原本的世界”时我就想要一顶大巴帽！可是一直没有打到——简直是怨念物……',Speaker=892684,}",
  [159518] = "{id=159518,Text='[c][ffff00][PlayerName][-][/c]，你知道大巴帽是怎样获得的吗？',Speaker=892684,Option='{打巴风特,5},{打米诺斯,4},{问就是扭蛋机！,3}',}",
  [159519] = "{id=159519,Text='扭蛋机？那是什么？',Speaker=892684,Emoji=19,}",
  [159520] = "{id=159520,Text='答错啦！米诺斯是牛角，大巴帽是羊角哦！',Speaker=892684,}",
  [159521] = "{id=159521,Text='没错！就是打巴风特………………',Speaker=892684,}",
  [159522] = "{id=159522,Text='我决定了！今天的冒险行程——就是讨伐巴风特！',Speaker=892684,Option='呃，好吧',}",
  [159523] = "{id=159523,Text='大巴帽~大巴帽~~',Speaker=892684,Emoji=29,}",
  [159524] = "{id=159524,Text='巴风特出没在——古城大厅！准备好了吗，我们现在就赶过去！',Speaker=892684,Option='{现在传送,6},{慢慢走过去,7}',}",
  [159525] = "{id=159525,Text='就是这里了，估计还没有刷新，等它出现了，我们就把它嘿嘿嘿嘿……',Speaker=892684,Emoji=5,}",
  [159526] = "{id=159526,Text='那个……我好像……又有那种被人盯着的感觉了……',Speaker=892684,Option='不会吧……',}",
  [159527] = "{id=159527,Text='（大喊）伊鲁卡是你吗？伊鲁卡是你吗？伊鲁卡是你吗？',Speaker=892684,}",
  [159528] = "{id=159528,Text='…………',Speaker=892686,}",
  [159529] = "{id=159529,Text='果然是你啊，伊鲁卡小哥~',Speaker=892684,}",
  [159530] = "{id=159530,Text='…………',Speaker=892686,}",
  [159531] = "{id=159531,Text='小哥你怎么不说话——你，不会是在跟踪我吧！',Speaker=892684,}",
  [159532] = "{id=159532,Text='………巴风特……',Speaker=892686,}",
  [159533] = "{id=159533,Text='小哥你也是来打巴风特的！那我们合作吧！',Speaker=892684,}",
  [159534] = "{id=159534,Text='（盯）我不要！',Speaker=892686,}",
  [159535] = "{id=159535,Text='（好果断的拒绝啊……）',}",
  [159536] = "{id=159536,Text='那好吧……',Speaker=892684,Emoji=19,}",
  [159537] = "{id=159537,Text='就让伊鲁卡小哥先打吧！',Speaker=892684,Option='头饰不要了？',}",
  [159538] = "{id=159538,Text='毕竟有先来后到嘛！大巴帽，总有机会得到的！',Speaker=892684,}",
  [159539] = "{id=159539,Text='啊……大巴帽！',Speaker=892684,Emoji=24,}",
  [159540] = "{id=159540,Text='（吼）！！',Speaker=892694,Emoji=12,}",
  [159541] = "{id=159541,Text='啊，巴风特好像生气了！',Speaker=892684,}",
  [159542] = "{id=159542,Text='！！快去帮忙！',Speaker=892684,}",
  [159543] = "{id=159543,Text='我没事，不用帮忙',Speaker=892686,}",
  [159544] = "{id=159544,Text='都这个时候了，还嘴硬个什么劲！大家合力击败它！',Speaker=892684,}",
  [159545] = "{id=159545,Text='咦？怎么没爆东西？是不是出BUG了？',Speaker=892684,Option='直接进背包',}",
  [159546] = "{id=159546,Text='啊~原来这个世界是这样的设定嘛！所以最后是被谁打死的？',Speaker=892684,}",
  [159547] = "{id=159547,Text='……',Speaker=892686,}",
  [159548] = "{id=159548,Text='是我……（展示战利品）',Speaker=892686,}",
  [159549] = "{id=159549,Text='镰刀……华丽金属……巴风特卡…………哦哦哦，这是……',Speaker=892684,}",
  [159550] = "{id=159550,Text='山羊头盔——大巴帽！！',Speaker=892684,Emoji=29,}",
  [159551] = "{id=159551,Text='…………',Speaker=892686,}",
  [159552] = "{id=159552,Text='（叹气）送给你了。',Speaker=892686,}",
  [159553] = "{id=159553,Text='诶？真的可以吗！说好这个魔物让给你的啊！',Speaker=892684,Emoji=7,}",
  [159554] = "{id=159554,Text='合作报酬……',Speaker=892686,}",
  [159555] = "{id=159555,Text='伊鲁卡你真是个好人啊！',Speaker=892684,}",
  [159556] = "{id=159556,Text='得偿所愿，泪目！伊鲁卡，我们是朋友了对吧！',Speaker=892685,Emoji=20,}",
  [159557] = "{id=159557,Text='……都说了，合作报酬……',Speaker=892686,}",
  [159558] = "{id=159558,Text='哈哈，别害羞嘛伊鲁卡~',Speaker=892685,}",
  [159559] = "{id=159559,Text='嗯……',Speaker=892686,}",
  [159560] = "{id=159560,Text='先走了，卖材料……',Speaker=892686,}",
  [159561] = "{id=159561,Text='真有个性啊！好像从没听他说过完整的句子呢……',Speaker=892685,}",
  [159562] = "{id=159562,Text='（哼歌）我有一个大巴帽~真呀真开心~我有两个新朋友~真呀真开心~',Speaker=892685,}",
  [159563] = "{id=159563,Text='今天的冒险真充实啊，不但拿到了梦寐以求的大巴帽，还认识了新朋友！',Speaker=892685,}",
  [159564] = "{id=159564,Text='希望明天也能这样快乐的冒险！明天见了，朋友~',Speaker=892685,Option='拜拜',}",
  [159652] = "{id=159652,Text='成功了！我感觉到英灵的意志在慢慢恢复，那么请你再去收集更多的净化之息吧，现在就让我把你传送回西门。',Speaker=892406,}",
  [159653] = "{id=159653,Text='（罗安在那边……伊鲁卡也在）',}",
  [159654] = "{id=159654,Text='[c][ffff00][PlayerName][-][/c]，你来了！快看伊鲁卡，他喝醉了哈哈哈！',Speaker=892685,}",
  [159655] = "{id=159655,Text='嘿嘿……我……我还能……能喝！干杯！',Speaker=892686,Option='什么情况',Emoji=17,}",
  [159656] = "{id=159656,Text='我也不知道啊，我刚刚经过，就看到他醉倒在这儿了',Speaker=892685,}",
  [159657] = "{id=159657,Text='……呜呜……朱莉叶……要嫁……嫁人了！新郎……郎不是我！',Speaker=892686,Option='{朱丽叶？,0},{发生什么了,0}',}",
  [159658] = "{id=159658,Text='朱丽叶是我……女朋……朋友…………我赚钱…………想、想、想娶她。',Speaker=892686,}",
  [159659] = "{id=159659,Text='他妈妈妈妈妈……嫌弃我口——吃。要朱丽叶嫁、嫁、嫁给别人！婚礼……快……快开始了……',Speaker=892686,}",
  [159660] = "{id=159660,Text='原来这就是你不爱说的原因啊！',Speaker=892685,Option='白他一眼',}",
  [159661] = "{id=159661,Text='嘿嘿，开个玩笑…………（正经）我罗安怎么能放任朋友的女友被别人抢走呢！我决定了——',Speaker=892685,}",
  [159662] = "{id=159662,Text='我们去抢亲！',Speaker=892685,}",
  [159663] = "{id=159663,Text='抢亲！',Speaker=892686,}",
  [159664] = "{id=159664,Text='抢亲！',Speaker=892685,}",
  [159665] = "{id=159665,Text='大教堂！',Speaker=892686,}",
  [159666] = "{id=159666,Text='出发！',Speaker=892685,}",
  [159667] = "{id=159667,Text='（一个莽夫和一个醉鬼，不跟上去恐怕他们会惹上麻烦，快点赶去教堂吧）',Option='{出发！,2},{先冷静一下,3}',}",
  [159697] = "{id=159697,Text='伊鲁卡，我没想到你竟然为了我来抢婚！你真是我的英雄~',Speaker=892688,}",
  [159698] = "{id=159698,Text='真肉麻……他们估计很快就会追上来，我们分散离开！',Speaker=892685,}",
  [159699] = "{id=159699,Text='明天我们在这里汇合！',Speaker=892685,}",
  [159700] = "{id=159700,Text='好！',Speaker=892686,Option='好！',}",
  [159701] = "{id=159701,Text='（奇怪，罗安怎么不在？）',}",
  [159702] = "{id=159702,Text='罗安被、被那个叫洛林的人带、带、带走了！',Speaker=892686,}",
  [159703] = "{id=159703,Text='说罗安昨天在、在教堂搞破——坏。要被强、强制遣返！',Speaker=892686,Option='强制遣返？',}",
  [159704] = "{id=159704,Text='我们快去找、找罗安，他们去普——隆德拉了！',Speaker=892686,Option='{立刻前往,2},{准备一下,3}',}",
  [159754] = "{id=159754,Text='谢天谢地！你们击退了梦罗克！还有有你们，不然整个大陆都要遭殃了！',Speaker=892683,}",
  [159755] = "{id=159755,Text='你们是整个大陆的英雄！',Speaker=892683,Emoji=39,}",
  [159756] = "{id=159756,Text='嘿嘿，感觉怪不好意思的……',Speaker=892685,}",
  [159757] = "{id=159757,Text='不过这个时空裂隙彻底失效了，恐怕我要另想办法才能回到原本的世界了……',Speaker=892685,}",
  [159758] = "{id=159758,Text='没关系，罗安！没有人会想送走他们的英雄，你想在这里待多久都可以——当然，我们也会尽快找到通向你的世界的通道~',Speaker=892683,}",
  [159759] = "{id=159759,Text='那这么说——',Speaker=892685,}",
  [159760] = "{id=159760,Text='我们三个又可以一起去冒险了？',Speaker=892685,}",
  [159761] = "{id=159761,Text='一起冒险！',Speaker=892686,Option='一起去冒险',}",
  [159810] = "{id=159810,Text='……冒险者，你愿意再试一次吗？进入诺曼的心灵，找到解救他的办法！',Speaker=892466,Option='{愿意,11},{再等等,31}',}",
  [159811] = "{id=159811,Text='……冒险者，你愿意再试一次吗？进入诺曼的心灵，找到解救他的办法！',Speaker=892466,Option='{愿意,15},{再等等,14}',}",
  [159816] = "{id=159816,Text='[c][ffff00][PlayerName][-][/c]，我来考你一个问题吧！',Speaker=892684,Emoji=11,}",
  [160004] = "{id=160004,Text='你来啦，[c][ffff00][PlayerName][-][/c]~',Speaker=892642,Emoji=9,}",
  [160005] = "{id=160005,Text='今天的普隆德拉好热闹啊！不知道发生了什么，快去看看吧！',Speaker=892642,Option='好的！',}",
  [160006] = "{id=160006,Text='事不宜迟，出发！',Speaker=892642,Emoji=23,}",
  [160007] = "{id=160007,Text='你来啦，[c][ffff00][PlayerName][-][/c]~',Speaker=892642,}",
  [160008] = "{id=160008,Text='有没有去参加庆典活动啊~感觉非常不错的样子！',Speaker=892642,Option='感觉不错',Emoji=28,}",
  [160009] = "{id=160009,Text='一定不要错过哦~',Speaker=892642,}",
  [160010] = "{id=160010,Text='[c][ffff00][PlayerName][-][/c]，你来了！我在庆典上发现了很多有趣的东西~',Speaker=892642,Emoji=20,}",
  [160011] = "{id=160011,Text='有空的话你也去庆典转转吧~',Speaker=892642,Option='好呀！',}",
  [160012] = "{id=160012,Text='庆典好热闹啊，好像还有很多福利呢！',Speaker=892642,Option='是呀！',Emoji=20,}",
  [160013] = "{id=160013,Text='好想买买买啊……感觉快忍不住了~',Speaker=892642,}",
  [160014] = "{id=160014,Text='……………………',}",
  [160015] = "{id=160015,Text='哟~[c][ffff00][PlayerName][-][/c]！',Speaker=892642,}",
  [160016] = "{id=160016,Text='今天的普隆德拉还是一如既往的热闹啊！',Speaker=892642,}",
  [160017] = "{id=160017,Text='不过再热闹的庆典也有结束的一天，一定不要错过精彩的活动哦~',Speaker=892642,}",
  [160018] = "{id=160018,Text='[c][ffff00][PlayerName][-][/c]，你来了~~',Speaker=892642,}",
  [160019] = "{id=160019,Text='有时间的话就去庆典活动那儿看看~说不定有意外收获哦~~',Speaker=892642,}",
  [160020] = "{id=160020,Text='（他好像对庆典很熟的样子……）',Emoji=18,}",
  [160021] = "{id=160021,Text='[c][ffff00][PlayerName][-][/c]，你来了！',Speaker=892642,Option='怎么了？',Emoji=24,}",
  [160022] = "{id=160022,Text='钱包空了……',Speaker=892642,Option='……',Emoji=24,}",
  [160023] = "{id=160023,Text='不过没办法嘛……想买的东西实在太多了，庆典结束就没有了！',Speaker=892642,}",
  [160024] = "{id=160024,Text='（…………）',}",
  [160025] = "{id=160025,Text='今天没有额外的内容了！明天见！',Speaker=892642,}",
  [160040] = "{id=160040,Text='愁人啊！真愁人！',Speaker=891170,Option='怎么了？',}",
  [160041] = "{id=160041,Text='芙洛丝女王已经驾临普隆德拉，可是我却找不到合适的人去迎接她！',Speaker=891170,Option='我可以帮忙！',}",
  [160042] = "{id=160042,Text='真的吗？那太好了！',Speaker=891170,}",
  [160043] = "{id=160043,Text='女王从异世界远道而来，你一定要有礼貌哦！[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891170,Option='{暂时不去,2},{好勒！,3}',}",
  [160044] = "{id=160044,Text='嘿，[c][ffff00][PlayerName][-][/c]!',Speaker=891161,}",
  [160045] = "{id=160045,Text='你有去过冰雪的世界吗？',Speaker=891161,Option='没有',}",
  [160046] = "{id=160046,Text='那是我家哦~',Speaker=891161,}",
  [160047] = "{id=160047,Text='我从遥远的冰雪世界进入米德加尔特。',Speaker=891161,}",
  [160048] = "{id=160048,Text='就是为了将我家乡的冰雪盛典也带入普隆德拉哦~',Speaker=891161,Option='冰雪盛典？',}",
  [160049] = "{id=160049,Text='没错，这是我们冰雪世界最盛大的节日。',Speaker=891161,}",
  [160050] = "{id=160050,Text='你想参加吗？',Speaker=891161,Option='当然',}",
  [160051] = "{id=160051,Text='那真是太好了~~！',Speaker=891161,}",
  [160052] = "{id=160052,Text='那就让我们度过一个愉快的冰雪盛典吧！',Speaker=891161,}",
  [160053] = "{id=160053,Text='别听这个女人胡说！',Speaker=891162,}",
  [160054] = "{id=160054,Text='她是个大恶魔！',Speaker=891162,}",
  [160055] = "{id=160055,Text='见、见笑了！',Speaker=891161,Option='这个是？',}",
  [160056] = "{id=160056,Text='这是我的水晶，里面养着我的“宠物”安吉拉。',Speaker=891161,Option='宠物？',}",
  [160057] = "{id=160057,Text='是的，安吉拉原本是很乖巧的。',Speaker=891161,}",
  [160058] = "{id=160058,Text='最近也不知道它怎么了，变得特别奇怪！',Speaker=891161,Option='特别奇怪？',}",
  [160059] = "{id=160059,Text='它开始不认识我了！',Speaker=891161,Emoji=42,}",
  [160060] = "{id=160060,Text='原本，只要我问它问题，它都会回答我。',Speaker=891161,}",
  [160061] = "{id=160061,Text='现在，它天天咒骂我！',Speaker=891161,Option='难以置信',}",
  [160062] = "{id=160062,Text='不信你看。',Speaker=891161,}",
  [160063] = "{id=160063,Text='安吉拉，谁是这个世界上，对你最好的主人？',Speaker=891161,}",
  [160064] = "{id=160064,Text='肯定不是你，别自作多情！',Speaker=891162,}",
  [160065] = "{id=160065,Text='我真是，我真的太伤心了…………',Speaker=891161,}",
  [160066] = "{id=160066,Text='呜呜呜呜呜……',Speaker=891161,}",
  [160067] = "{id=160067,Text='安吉拉变了！！！',Speaker=891161,Option='可能吧',}",
  [160068] = "{id=160068,Text='安吉拉最喜欢冰雪盛典了！',Speaker=891161,}",
  [160069] = "{id=160069,Text='我想，如果我们能办一个盛大的冰雪盛典，也许安吉拉就会好起来。',Speaker=891161,}",
  [160070] = "{id=160070,Text='不可能！',Speaker=891162,}",
  [160071] = "{id=160071,Text='安吉拉还在闹别扭！',Speaker=891161,}",
  [160072] = "{id=160072,Text='真是个坏孩子，一点也不乖！',Speaker=891161,}",
  [160073] = "{id=160073,Text='我要去好好教育这个坏孩子~',Speaker=891161,}",
  [160074] = "{id=160074,Text='我们改天再一起玩哦~',Speaker=891161,}",
  [160075] = "{id=160075,Text='这个年轻人是什么情况，骑士？',Speaker=1221,}",
  [160076] = "{id=160076,Text='是今天早上在城门口发现的居民。',Speaker=1222,}",
  [160077] = "{id=160077,Text='他穿着最厚的棉袄，全身却在冒寒气。',Speaker=1222,}",
  [160078] = "{id=160078,Text='去看看他，到底发生了什么事情。',Speaker=1221,}",
  [160079] = "{id=160079,Text='太冷了，我真是太冷了。',Speaker=891165,Option='发生了什么事情',}",
  [160080] = "{id=160080,Text='我也不知道啊。我就是从南门进城的时候，看见地上有块亮晶晶的石头。',Speaker=891165,}",
  [160081] = "{id=160081,Text='我一时财迷心窍，把石头捡了起来。',Speaker=891165,}",
  [160082] = "{id=160082,Text='捡起来一刹那！',Speaker=891165,Option='怎么了',}",
  [160083] = "{id=160083,Text='我就感觉，自己身体里好像冒着一股寒意……',Speaker=891165,}",
  [160084] = "{id=160084,Text='我的手指，开始结冰……',Speaker=891165,Option='然后呢',}",
  [160085] = "{id=160085,Text='然后我就什么都不知道了！',Speaker=891165,}",
  [160086] = "{id=160086,Text='真是奇怪，已经有好几例冰冻症状了，到底是怎么回事？',Speaker=1221,Option='水晶碎片',}",
  [160087] = "{id=160087,Text='确实，每个被冻住的人，都曾经接触过奇怪的水晶。',Speaker=1221,}",
  [160088] = "{id=160088,Text='这水晶还挺眼熟的……',Speaker=1221,}",
  [160089] = "{id=160089,Text='好像，我在哪见过！',Speaker=1221,Option='芙洛丝',}",
  [160090] = "{id=160090,Text='对了！这水晶的质地，不就跟冰雪女王芙洛丝的冰雪水晶一样吗！',Speaker=1221,}",
  [160091] = "{id=160091,Text='……真是可疑啊！',Speaker=1221,Option='是的',}",
  [160092] = "{id=160092,Text='去问问芙洛丝女王，到底这些水晶碎片，跟她有没有关系！',Speaker=1221,}",
  [160093] = "{id=160093,Text='头疼，安吉拉真是坏孩子！',Speaker=891161,Option='女王',}",
  [160094] = "{id=160094,Text='哦~是[c][ffff00][PlayerName][-][/c]，我现在比较繁忙哦~',Speaker=891161,}",
  [160095] = "{id=160095,Text='如果想找我玩要过一段时间哦~',Speaker=891161,Option='询问寒冻症',}",
  [160096] = "{id=160096,Text='寒、寒冻症？那跟安吉拉一点关系都没有！',Speaker=891161,Option='安吉拉？',}",
  [160097] = "{id=160097,Text='对、对的！安吉拉真是特别乖！',Speaker=891161,}",
  [160098] = "{id=160098,Text='它今天还在水晶里跟我说话呢！',Speaker=891161,}",
  [160099] = "{id=160099,Text='水晶也没有被它打破！',Speaker=891161,Option='{水晶？,0},{打破？,0}',}",
  [160100] = "{id=160100,Text='没有打破！',Speaker=891161,}",
  [160101] = "{id=160101,Text='你有没有认真听我说话！',Speaker=891161,Option='水晶呢？',}",
  [160102] = "{id=160102,Text='水、水晶在我的房间里，是完整的哦~',Speaker=891161,}",
  [160103] = "{id=160103,Text='一点也没有破碎！',Speaker=891161,Option='解释情况',Emoji=18,}",
  [160104] = "{id=160104,Text='很多人都被冻住了吗？',Speaker=891161,}",
  [160105] = "{id=160105,Text='真是糟糕！',Speaker=891161,}",
  [160106] = "{id=160106,Text='……我，我也许能帮帮忙……',Speaker=891161,Option='怎么帮？',}",
  [160107] = "{id=160107,Text='那是我的秘密！你干嘛问这么清楚？',Speaker=891161,}",
  [160108] = "{id=160108,Text='我、我还有事情，你别来找我了！',Speaker=891161,}",
  [160109] = "{id=160109,Text='芙洛丝女王的表现如此奇怪！',Speaker=1221,}",
  [160110] = "{id=160110,Text='最近几天，你都要好好监视她！',Speaker=1221,}",
  [160111] = "{id=160111,Text='一定要把所有的事情调查清楚！',Speaker=1221,}",
  [160112] = "{id=160112,Text='我先去处理了，如果有人问到我的事情。',Speaker=891161,}",
  [160113] = "{id=160113,Text='你们就告诉他们，我生病了！',Speaker=891161,}",
  [160114] = "{id=160114,Text='记住了吗？',Speaker=891161,}",
  [160115] = "{id=160115,Text='记住啦！女王大人！',}",
  [160116] = "{id=160116,Text='终于找到了一枚碎片！',Speaker=891161,}",
  [160117] = "{id=160117,Text='小波利，别靠近碎片！',Speaker=891161,}",
  [160118] = "{id=160118,Text='额，小波利，你有什么想不开的呢……',Speaker=891161,}",
  [160119] = "{id=160119,Text='快走吧！小波利！',Speaker=891161,}",
  [160120] = "{id=160120,Text='真是糟糕，冰雪水晶的碎片魔力太强了。',Speaker=891161,}",
  [160121] = "{id=160121,Text='再这样下去，肯定会对这里的人都造成影响的。',Speaker=891161,}",
  [160122] = "{id=160122,Text='安吉拉，你知道自己闯了多大的祸吗？',Speaker=891161,}",
  [160123] = "{id=160123,Text='什么？那个碎片是冰雪水晶的碎片吗？',Speaker=1221,Option='没错',}",
  [160124] = "{id=160124,Text='芙洛丝！她来到普隆德拉，到底是为了什么？？',Speaker=1221,}",
  [160125] = "{id=160125,Text='等她回城以后，我们一起抓住她！',Speaker=1221,}",
  [160126] = "{id=160126,Text='不能让这么危险的人在普隆德拉游荡。',Speaker=1221,}",
  [160127] = "{id=160127,Text='芙洛丝女王，我怀疑你跟皇城里的寒冻症有直接关系。',Speaker=891171,}",
  [160128] = "{id=160128,Text='希望你能好好配合我们！',Speaker=891171,}",
  [160129] = "{id=160129,Text='不要造成不必要的困扰！',Speaker=891171,}",
  [160130] = "{id=160130,Text='这，我并不知道什么寒冻症！',Speaker=891161,}",
  [160131] = "{id=160131,Text='你看是不是找错人了！',Speaker=891161,}",
  [160132] = "{id=160132,Text='我是为了冰雪盛典才进入普隆德拉的哦~',Speaker=891161,}",
  [160133] = "{id=160133,Text='我有直接证据！',Speaker=891171,}",
  [160134] = "{id=160134,Text='如果你再试图掩盖事实，我只能按照普隆德拉的法律，将你逮捕！',Speaker=891171,}",
  [160135] = "{id=160135,Text='我、我错了！我说实话……',Speaker=891161,}",
  [160136] = "{id=160136,Text='是安吉拉，打破了冰雪水晶，从水晶里逃了出来。',Speaker=891161,}",
  [160137] = "{id=160137,Text='它可能把冰雪水晶的碎片带到了各个地方。',Speaker=891161,}",
  [160138] = "{id=160138,Text='才让大家都生病了……',Speaker=891161,}",
  [160139] = "{id=160139,Text='安吉拉，真的很乖的，它从来都不会做这么过分的事情。',Speaker=891161,}",
  [160140] = "{id=160140,Text='你把它关在水晶里，它怎么可能会开心？',Speaker=891171,}",
  [160141] = "{id=160141,Text='安吉拉是宝石灵兽啊，它最喜欢水晶了。',Speaker=891161,}",
  [160142] = "{id=160142,Text='它待在我的水晶里是非常开心的！',Speaker=891161,Option='好像不是',}",
  [160143] = "{id=160143,Text='它之前不是一直在骂你？？',Speaker=891171,}",
  [160144] = "{id=160144,Text='安吉拉原来从来不这样，它真的是突然变成这么粗鲁的！',Speaker=891161,}",
  [160145] = "{id=160145,Text='我已经找回大部分的水晶碎片了。',Speaker=891161,}",
  [160146] = "{id=160146,Text='还差最大的一块碎片……',Speaker=891161,}",
  [160147] = "{id=160147,Text='我怀疑，是安吉拉偷偷把碎片藏起来了。',Speaker=891161,}",
  [160148] = "{id=160148,Text='毕竟它是个喜欢水晶的孩子。',Speaker=891161,Option='那怎么办？',}",
  [160149] = "{id=160149,Text='只要找回最后一枚碎片和安吉拉，我就可以恢复冰雪水晶。',Speaker=891161,}",
  [160150] = "{id=160150,Text='普隆德拉患了寒冻症的人，都会恢复健康的！',Speaker=891161,}",
  [160151] = "{id=160151,Text='再给我一点点时间。',Speaker=891161,}",
  [160152] = "{id=160152,Text='为什么你不早点说？',Speaker=891171,}",
  [160153] = "{id=160153,Text='我不希望破坏冰雪盛典的气氛TT',Speaker=891161,Option='我来帮你',Emoji=24,}",
  [160154] = "{id=160154,Text='哎？',Speaker=891161,Option='我去帮你找安吉拉',}",
  [160155] = "{id=160155,Text='真的吗？',Speaker=891161,}",
  [160156] = "{id=160156,Text='[c][ffff00][PlayerName][-][/c]真是太好了！',Speaker=891161,}",
  [160157] = "{id=160157,Text='最后一枚水晶碎片！',Speaker=891161,}",
  [160158] = "{id=160158,Text='安吉拉，安吉拉在哪里？',Speaker=891161,}",
  [160159] = "{id=160159,Text='你是谁？为什么你会有冰雪水晶的碎片',Speaker=891161,}",
  [160160] = "{id=160160,Text='又是你！你、你、你、你不要过来！',Speaker=891164,}",
  [160161] = "{id=160161,Text='我好不容易逃出去了，不会这么容易在被你抓住！',Speaker=891164,}",
  [160162] = "{id=160162,Text='谁要抓你？安吉拉呢？',Speaker=891161,}",
  [160163] = "{id=160163,Text='都说了好多次，我不叫安吉拉！',Speaker=891164,}",
  [160164] = "{id=160164,Text='你抓错人了！！',Speaker=891164,}",
  [160165] = "{id=160165,Text='怎么回事？',Speaker=891161,}",
  [160166] = "{id=160166,Text='你还问我怎么回事？',Speaker=891164,}",
  [160167] = "{id=160167,Text='我是个炎火恶魔！',Speaker=891164,}",
  [160168] = "{id=160168,Text='你把我关在那颗冰冰凉的水晶里。',Speaker=891164,}",
  [160169] = "{id=160169,Text='我的魔力都没了！',Speaker=891164,}",
  [160170] = "{id=160170,Text='？？？',Speaker=891161,Emoji=8,}",
  [160171] = "{id=160171,Text='我是把安吉拉放在水晶里！你别胡说！',Speaker=891161,Option='可能',}",
  [160172] = "{id=160172,Text='可能什么？',Speaker=891161,}",
  [160173] = "{id=160173,Text='感觉，之前水晶里一直是眼前的小恶魔！',Speaker=891168,}",
  [160174] = "{id=160174,Text='你怎么知道？',Speaker=891161,}",
  [160175] = "{id=160175,Text='因为说话声音，一模一样！',Speaker=891168,}",
  [160176] = "{id=160176,Text='对啊！！！',Speaker=891164,}",
  [160177] = "{id=160177,Text='这个人类都知道，我不是你的安吉拉！',Speaker=891164,}",
  [160178] = "{id=160178,Text='不可能，安吉拉只是嗓子坏了！',Speaker=891161,}",
  [160179] = "{id=160179,Text='我就是这个声音！',Speaker=891164,}",
  [160180] = "{id=160180,Text='嗓子一点没坏！',Speaker=891164,}",
  [160181] = "{id=160181,Text='…………',Speaker=891161,}",
  [160182] = "{id=160182,Text='难道，安吉拉真的不见了吗？',Speaker=891161,}",
  [160183] = "{id=160183,Text='嘿嘿~我在哦~芙洛丝~',Speaker=891169,}",
  [160184] = "{id=160184,Text='我一直跟着这个坏蛋哦！',Speaker=891169,}",
  [160185] = "{id=160185,Text='安吉拉！！我就知道，你不会离开我！',Speaker=891161,}",
  [160186] = "{id=160186,Text='当然不会啦~最喜欢芙洛丝啦~~',Speaker=891169,}",
  [160187] = "{id=160187,Text='这个小恶魔是准备偷水晶的小偷！',Speaker=891169,}",
  [160188] = "{id=160188,Text='为了惩罚它，我才把它关在水晶里哦~',Speaker=891169,}",
  [160189] = "{id=160189,Text='原来是这样~',Speaker=891161,}",
  [160190] = "{id=160190,Text='你们说谁是小偷？？',Speaker=891164,}",
  [160191] = "{id=160191,Text='啊啊啊啊！！',Speaker=891164,}",
  [160192] = "{id=160192,Text='生气了！',Speaker=891164,}",
  [160193] = "{id=160193,Text='[c][ffff00][PlayerName][-][/c]，太厉害了！！！',Speaker=891161,}",
  [160194] = "{id=160194,Text='看来误会都解除……',Speaker=1221,}",
  [160195] = "{id=160195,Text='造成寒冻症的，并不是芙洛丝女王！',Speaker=1221,}",
  [160196] = "{id=160196,Text='当然不是啦，芙洛丝最可爱了~~~',Speaker=891169,}",
  [160197] = "{id=160197,Text='还有一件事情哦~要把水晶恢复成原样，这样大家都不会生病了！',Speaker=891169,}",
  [160198] = "{id=160198,Text='看我的哦~~',Speaker=891169,}",
  [160199] = "{id=160199,Text='哇~~安吉拉真是太厉害了！！',Speaker=891161,Emoji=27,}",
  [160200] = "{id=160200,Text='终于解决问题了。',Speaker=1221,}",
  [160201] = "{id=160201,Text='[c][ffff00][PlayerName][-][/c]做得不错~',Speaker=1221,}",
  [160202] = "{id=160202,Text='这些是给你的奖励！',Speaker=1221,}",
  [160203] = "{id=160203,Text='要去冰雪盛典玩哦~~',Speaker=891161,}",
  [160205] = "{id=160205,Text='我们又见面了，冒险者。经过这次漫长的战斗你应该也感到非常疲惫了吧，接下来想必会有一段平和美妙的时光在等待我们。',Speaker=892749,}",
  [160206] = "{id=160206,Text='为了庆祝这份来之不易的胜利，斯宾诺学士准备了一份礼物，在最后一场战斗中我们收集到了许多战利品，里面有些还不错的东西可能对你有所帮助',Speaker=892749,}",
  [160207] = "{id=160207,Text='学士委托我交给你，如果不嫌弃的话就收下吧，之后的几天里如果你还来找我说不定会有惊喜呢。',Speaker=892749,Option='收下礼物',}",
  [160210] = "{id=160210,Text='感谢奥丁，我们终于将那枚可怕的碎片击落了！',Speaker=891040,Option='太好了',}",
  [160211] = "{id=160211,Text='但冒险者，我必须说，现在的情况并不乐观……',Speaker=891040,}",
  [160212] = "{id=160212,Text='碎片被海姆达尔之眼击中后，裂成了五枚更小的碎片。',Speaker=891040,}",
  [160213] = "{id=160213,Text='虽说它们已经不再会威胁整个大陆的安全了，但小碎片们依旧坠落到了五个不同的地方……',Speaker=891040,Option='哪些地方？！',Emoji=15,}",
  [160214] = "{id=160214,Text='（叹气）分别是[c][ffff00]斐扬树林[-][/c]、[c][ffff00]依斯鲁得[-][/c]、[c][ffff00]妙勒尼山脉[-][/c]、[c][ffff00]伊达平原[-][/c]和[c][ffff00]天津町[-][/c]。',Speaker=891040,Emoji=25,}",
  [160215] = "{id=160215,Text='与此同时，我们检测到一股[c][ffff00]异常的魔力波动[-][/c]，似乎也于这些彩虹桥的碎片有关！',Speaker=891040,}",
  [160216] = "{id=160216,Text='也许有什么[c][ffff00]未知的“客人”[-][/c]，就要来到米德加尔特了……',Speaker=891040,}",
  [160217] = "{id=160217,Text='冒险者，这次的危机还远远没有结束。我有预感，未来一定还充满着难以预知的危险，请你务必做好准备。',Speaker=891040,}",
  [160218] = "{id=160218,Text='愿米德加尔特大陆能平安度过这次劫难……',Speaker=891040,}",
  [160219] = "{id=160219,Text='彩虹桥的坠落，竟让你们如此惊慌。',Speaker=892751,}",
  [160220] = "{id=160220,Text='真是弱小的种族。',Speaker=892751,}",
  [160221] = "{id=160221,Text='你们之中，有人知道真相。',Speaker=892751,}",
  [160222] = "{id=160222,Text='他或许失去了这一段记忆，但他知晓一切真相。',Speaker=892751,}",
  [160223] = "{id=160223,Text='让他去往发乐斯灯塔。',Speaker=892751,}",
  [160224] = "{id=160224,Text='他的名字是——[c][ffff00][PlayerName][-][/c]。',Speaker=892751,}",
  [160226] = "{id=160226,Text='经过学士几天的计算，根据各位的功绩，以公会为整体的奖励都已发放，请及时查看自己的邮箱。另外我这里还有一份特殊礼包请收下吧。',Speaker=892749,}",
  [160232] = "{id=160232,Text='hello~',Speaker=891172,}",
  [160233] = "{id=160233,Text='看看我的小蹄子~啊~真是灵活~',Speaker=891172,}",
  [160234] = "{id=160234,Text='看看我的小鼻子~红彤彤的，真是好可爱哦~',Speaker=891172,Option='？？？',}",
  [160235] = "{id=160235,Text='有没有~爱上我呢？？',Speaker=891172,}",
  [160236] = "{id=160236,Text='嘿嘿~',Speaker=891172,Option='{转身就走,0},{冷漠对待,0},{若有所思,0}',}",
  [160237] = "{id=160237,Text='别，别走啊~别走！',Speaker=891172,Emoji=24,}",
  [160238] = "{id=160238,Text='我只是希望有人陪我玩游戏而已T  T',Speaker=891172,}",
  [160239] = "{id=160239,Text='小老弗、兔兔和小冰龙都不喜欢搭理我……',Speaker=891172,}",
  [160240] = "{id=160240,Text='呜呜呜呜……',Speaker=891172,Option='可以理解',}",
  [160241] = "{id=160241,Text='你陪我玩游戏吧，我会给你很丰厚的奖励哦~',Speaker=891172,Option='好吧',}",
  [160242] = "{id=160242,Text='划拳！划拳！三、二、一，出手！',Speaker=891172,Option='{石头,10},{剪刀,20},{布,30}',}",
  [160243] = "{id=160243,Text='哈哈哈哈~我赢了，我从来没有赢过哎~',Speaker=891172,}",
  [160244] = "{id=160244,Text='你真是鲁道夫的好朋友~',Speaker=891172,}",
  [160245] = "{id=160245,Text='为什么，我从来没有赢过呢……',Speaker=891172,}",
  [160246] = "{id=160246,Text='难道我们长蹄子的，不配赢吗？',Speaker=891172,}",
  [160247] = "{id=160247,Text='可惜可惜！',Speaker=891172,}",
  [160248] = "{id=160248,Text='下次我一定会打败你的！',Speaker=891172,}",
  [160249] = "{id=160249,Text='我有点不开心。',Speaker=891173,Option='怎么了？',}",
  [160250] = "{id=160250,Text='其他的小老弗都有属于自己的牛仔帽。',Speaker=891173,}",
  [160251] = "{id=160251,Text='我只有一个光秃秃的头顶……',Speaker=891173,}",
  [160252] = "{id=160252,Text='我感觉，特别寒冷……',Speaker=891173,Option='那怎么办',}",
  [160253] = "{id=160253,Text='不知道会不会，有好心人，送给我一顶牛仔帽呢……',Speaker=891173,}",
  [160254] = "{id=160254,Text='哟~你知道这个世界上，最好看的头饰是什么吗？',Speaker=891176,Option='牛仔帽？',}",
  [160255] = "{id=160255,Text='哟~看来你很懂嘛！',Speaker=891176,}",
  [160256] = "{id=160256,Text='没错！就是牛仔帽！你有牛仔帽嘛？',Speaker=891176,Option='没有',}",
  [160257] = "{id=160257,Text='那真是太可惜了！',Speaker=891176,}",
  [160258] = "{id=160258,Text='如果你愿意为我找到一个牛仔帽信徒，我倒是愿意送给你一顶。',Speaker=891176,Option='没问题',}",
  [160259] = "{id=160259,Text='就，就是这顶帽子。',Speaker=891173,}",
  [160260] = "{id=160260,Text='我想象中的牛仔帽！最时尚的牛仔帽！',Speaker=891173,}",
  [160261] = "{id=160261,Text='果然，普隆德拉的人都是潮流先锋啊。',Speaker=891173,}",
  [160262] = "{id=160262,Text='哟~这只小动物，你很懂的嘛~',Speaker=891176,}",
  [160263] = "{id=160263,Text='太、太好看了！我什么时候才能拥有这样一顶牛仔帽呢？',Speaker=891173,}",
  [160264] = "{id=160264,Text='现在就可以！',Speaker=891176,}",
  [160265] = "{id=160265,Text='哇！哇！我太感动了！',Speaker=891173,Emoji=42,}",
  [160266] = "{id=160266,Text='谢谢你们！',Speaker=891173,}",
  [160267] = "{id=160267,Text='兔兔这么可爱，怎么会有人欺负兔兔？？',Speaker=891174,}",
  [160268] = "{id=160268,Text='哭哭！',Speaker=891174,Option='谁欺负你？',Emoji=24,}",
  [160269] = "{id=160269,Text='是那些吸血的虫子！每天都会来咬兔兔！',Speaker=891174,Option='我帮你赶走它们！',}",
  [160270] = "{id=160270,Text='真的嘛？',Speaker=891174,}",
  [160271] = "{id=160271,Text='哇！讨厌的虫子都被赶跑了！',Speaker=891174,}",
  [160272] = "{id=160272,Text='谢谢你！',Speaker=891174,}",
  [160273] = "{id=160273,Text='没有虫子欺负兔兔了！',Speaker=891174,}",
  [160274] = "{id=160274,Text='难道冰龙就不能有梦想吗？',Speaker=891175,Option='{可以有,0},{什么梦想？,0}',}",
  [160275] = "{id=160275,Text='我想成为一个，忧郁的情歌王子。',Speaker=891175,}",
  [160276] = "{id=160276,Text='我想写一首流行的金曲！',Speaker=891175,Option='挺好的',}",
  [160277] = "{id=160277,Text='但是，有一句歌词，我还在斟酌中。',Speaker=891175,}",
  [160278] = "{id=160278,Text='我在思考，哪个词更容易彰显我忧郁的气质。',Speaker=891175,}",
  [160279] = "{id=160279,Text='你愿意帮我看看吗？',Speaker=891175,Option='没问题',}",
  [160280] = "{id=160280,Text='在这___的天地，有谁能陪伴我？',Speaker=891175,Option='{孤独,2},{寂寥,3},{炫酷,4},{热情,5}',}",
  [160281] = "{id=160281,Text='孤独的天地！这个好，符合我的气质。',Speaker=891175,}",
  [160282] = "{id=160282,Text='这个听上去还不错。',Speaker=891175,}",
  [160283] = "{id=160283,Text='炫酷并不是我想要的气质，我想做一个忧郁的冰龙。',Speaker=891175,}",
  [160284] = "{id=160284,Text='你确定应该用热情吗？我怎么觉得怪怪的。',Speaker=891175,}",
  [160285] = "{id=160285,Text='是你吗？这世界上另一个___?',Speaker=891175,Option='{舞蹈高手,7},{潮流先锋,8},{独行之人,9},{头号玩家,10}',}",
  [160286] = "{id=160286,Text='舞蹈高手……可能不够忧郁……',Speaker=891175,}",
  [160287] = "{id=160287,Text='我虽然也希望有一点时尚的品味，可我最想要的气质还是忧郁。',Speaker=891175,}",
  [160288] = "{id=160288,Text='额，玩游戏可是一件快乐的事情，这可不够忧郁啊！',Speaker=891175,}",
  [160289] = "{id=160289,Text='这就是我想要的忧郁歌词！',Speaker=891175,}",
  [160290] = "{id=160290,Text='谢谢你！',Speaker=891175,}",
  [160355] = "{id=160355,Text='还差一点点，就能把冰雪盛典装饰的更好看了。',Speaker=892762,}",
  [160356] = "{id=160356,Text='要是亮晶晶的宝石再多一些就好了……',Speaker=892762,}",
  [160357] = "{id=160357,Text='嘿，你！可以给我一点亮晶晶的吗？',Speaker=892762,}",
  [160395] = "{id=160395,Text='也不是所有长胡子的人，都是圣诞老人！',Speaker=892770,Option='怎么了？',}",
  [160396] = "{id=160396,Text='普隆德拉城里这帮熊孩子，最近老追着我要礼物！',Speaker=892770,}",
  [160397] = "{id=160397,Text='我也不是圣诞老人啊~真是令人头疼！',Speaker=892770,Option='息怒息怒',}",
  [160398] = "{id=160398,Text='他们还揪我的胡子！',Speaker=892770,}",
  [160399] = "{id=160399,Text='很疼啊！',Speaker=892770,Option='别生气',}",
  [160400] = "{id=160400,Text='还是你，比较乖，是个好孩子！',Speaker=892770,}",
  [160401] = "{id=160401,Text='这些礼物就送给你了！',Speaker=892770,}",
  [160409] = "{id=160409,Text='好消息， 我们成功净化了英灵，现在他已经恢复了神智并答应与我们共同战斗，只要在战场中向我提出请求即可。那么先收下这些[c][ffff00]虹光碎片[-][/c]吧，愿大陆平安！',Speaker=892485,}",
  [160437] = "{id=160437,Text='花儿纷飞，微风轻抚，色彩散落，灯火燃起……',Speaker=892776,Action={actionid=39,num=1},}",
  [160438] = "{id=160438,Text='这片神奇的大陆令我感到由衷的喜爱，不过我似乎也看到了一丝黑暗之火在悄悄蔓延。',Speaker=892776,}",
  [160439] = "{id=160439,Text='冒险者，你愿意与我一起扑灭这火焰拯救你们的大陆吗？',Speaker=892776,Option='{我愿意,2},{没兴趣,3}',}",
  [160440] = "{id=160440,Text='不愧是我看中的人，你的勇气令我很满意，那么现在就出发吧！',Speaker=892776,}",
  [160441] = "{id=160441,Text='比起被任何人承认，首先必须能承认自己，即便不能一起战斗，我也希望你能找到自己真正的道路。',Speaker=892776,}",
  [160462] = "{id=160462,Text='（哭声）呜呜呜……',Speaker=891063,Option='{怎么全身湿了,0},{你去游泳了？,0}',Emoji=24,}",
  [160463] = "{id=160463,Text='等等……您是……那位出名的冒险者！？',Speaker=891063,Option='{正是在下,0},{有事吗,0}',Emoji=25,}",
  [160464] = "{id=160464,Text='不是海盗！不是海盗就好啊！害，瞧我这记性，我在广场，也没啥海盗呢，呵呵。但情况危急，我想委托您一件事……[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891063,Option='{你说吧,2},{下次一定,3}',}",
  [160465] = "{id=160465,Text='听起来怪怪的。我可没在广场上卖艺啊……算了算了。',Speaker=891063,}",
  [160466] = "{id=160466,Text='您答应就好，我快没饭吃了！',Speaker=891063,}",
  [160467] = "{id=160467,Text='是这样，我在依斯鲁得办了家美食杂志，叫【生鲜精选】。我平时吃点章鱼料理，写点儿评测，赚点小钱~',Speaker=891063,Emoji=15,}",
  [160471] = "{id=160471,Text='但我和手下在取材时，章鱼们发狂了，我们就慌忙逃命了。',Speaker=891063,Option='{你该不会,0},{输给了章鱼？,0}',}",
  [160472] = "{id=160472,Text='是变异章鱼！不止是变了异的章鱼，其他魔物都异变了，变得非常凶暴！',Speaker=891063,}",
  [160473] = "{id=160473,Text='就算来到依斯鲁得也一直被追着！我刚逃到岸上，它们从后面扑了过来。转眼我就在普隆德拉，接着便是你看到的样子。',Speaker=891063,Option='{转眼？,0},{怎么过来的？,0}',Emoji=24,}",
  [160474] = "{id=160474,Text='我想想……一个[c][ffff00]红发女海盗[-][/c]……还有个爽朗的[c][ffff00]金发男骑士[-][/c]用传送术丢我来的。',Speaker=891063,}",
  [160475] = "{id=160475,Text='说来奇怪，那里是我老家，我没在岛上见过那两人。他们的打扮也和你们这些冒险者不一样。',Speaker=891063,}",
  [160476] = "{id=160476,Text='现在，多亏了两位救命恩人……我和我的下属失散了。他现在一定在责怪我吧。',Speaker=891063,}",
  [160479] = "{id=160479,Text='这有一张我刚买的蝴蝶翅膀，您能赶过去用这张蝴蝶翅膀救他吗？',Speaker=891063,Option='{好的,0},{助人为乐,0}',}",
  [160480] = "{id=160480,Text='多谢你了，好去好回，冒险者！',Speaker=891063,}",
  [160481] = "{id=160481,Text='那边那位，海里发生了异变，请尽快回到安全地带！',Speaker=891068,Emoji=8,}",
  [160482] = "{id=160482,Text='原来是冒险者！',Speaker=891068,}",
  [160483] = "{id=160483,Text='我想来的路上你也应该知道了。海底生物发生了异变。现在军队正在帮助这里的人类观察员们进行疏散。',Speaker=891068,}",
  [160484] = "{id=160484,Text='那么，请你帮我个忙，找找【生鲜精选】的记者们，好吗？',Speaker=891068,Option='{正好,0},{肯定的,0}',}",
  [160487] = "{id=160487,Text='谢谢。如果你找到他们，我会以私人名义来还你人情的。',Speaker=891068,}",
  [160488] = "{id=160488,Text='我们也在找，但是想想到现在还没有找到的话，有可能两人现在在变异的中心地区里！真是让人担心！',Speaker=891068,}",
  [160489] = "{id=160489,Text='海神，是海神！海神他蹦出来了，然后……',Speaker=891065,Option='{冷静点,0},{是我,0}',Emoji=22,}",
  [160490] = "{id=160490,Text='快救救我！我的工作只是负责吃，而不是被吃啊！',Speaker=891065,Emoji=24,}",
  [160491] = "{id=160491,Text='……等等，你是老板派来的？',Speaker=891065,Option='点头',}",
  [160492] = "{id=160492,Text='我把我们身上带的唯一的蝴蝶翅膀留给他，让他先到依斯鲁得。之后，我才被困在这里的。',Speaker=891065,}",
  [160493] = "{id=160493,Text='他保证会带领协会的人马上来救我。怎么只有你一个？',Speaker=891065,Option='他遇到了麻烦',Emoji=18,}",
  [160494] = "{id=160494,Text='……',}",
  [160495] = "{id=160495,Text='那是真的吗？我不信。',Speaker=891065,Emoji=40,}",
  [160496] = "{id=160496,Text='他还说过在第一作者栏上署我名字之类的，不过那只是喝醉时说的话了',Speaker=891065,}",
  [160497] = "{id=160497,Text='告诉你也可以，但我想看到你的诚意呢！能让我看到你满满的诚意的话，可以考虑告诉你。',Speaker=891065,}",
  [160498] = "{id=160498,Text='这个诚意，就是我逃跑时洒在东边的章鱼腿。请你给我捡回来。',Speaker=891065,Option='{呃,0},{好,0},{行吧,0}',}",
  [160499] = "{id=160499,Text='嘿嘿，使唤别人捞章鱼的感觉真爽快~',Speaker=891065,Emoji=25,}",
  [160501] = "{id=160501,Text='我看看，漂亮！……嘎嘣（咬一口）。你要尝一口吗？很Q弹的。',Speaker=891065,Option='{还是不用了,0},{谢谢但不用,0}',}",
  [160506] = "{id=160506,Text='老板，我好感动！你没忘了我，还有我们的章鱼事业！',Speaker=891065,Emoji=24,}",
  [160507] = "{id=160507,Text='海神大人出没在海神雕像边，看他样子你就知道了。',Speaker=891065,}",
  [160508] = "{id=160508,Text='我就拿着章鱼回去了，你当心！',Speaker=891065,}",
  [160509] = "{id=160509,Text='什么，又来个捣乱的。',Speaker=891064,Emoji=7,}",
  [160510] = "{id=160510,Text='（上下打量）不够塞牙缝的样子。',Speaker=891064,Option='{你忘了我？,0},{我可是英雄,0}',}",
  [160511] = "{id=160511,Text='你很出名吗？那么小英雄，将你的身体和荣耀献祭过来吧。',Speaker=891064,Option='{你休想,0},{喝西北风吧,0}',}",
  [160512] = "{id=160512,Text='害羞什么~我就馋你的身子。',Speaker=891064,}",
  [160513] = "{id=160513,Text='毁灭……真知……阴影……',Speaker=891069,}",
  [160514] = "{id=160514,Text='住手啊！海神大人……（换行）达尔菲照耀万世',Speaker=891069,}",
  [160515] = "{id=160515,Text='恭启海王，“无”乃万物流向所终（换行）发……发生了什么？',Speaker=891069,}",
  [160516] = "{id=160516,Text='哈哈哈哈哈哈。',Speaker=891064,}",
  [160517] = "{id=160517,Text='这个空间真有趣呀。',Speaker=891064,}",
  [160519] = "{id=160519,Text='他就是异变的源头，只能下手了！',Option='下手',}",
  [160520] = "{id=160520,Text='稍等。异世界之人。',Speaker=891071,}",
  [160521] = "{id=160521,Text='这是你们的民风吗？你们的兴趣就是像海盗一样破坏所有东西吗？',Speaker=891071,Option='你在旁观吗？',}",
  [160522] = "{id=160522,Text='那不是？你连神都能够下杀手，我才出手，心想要说些什么补救的。',Speaker=891071,Option='{他发生了什么,0},{你要告诉我什么,0}',Emoji=19,}",
  [160523] = "{id=160523,Text='异变。',Speaker=891071,}",
  [160524] = "{id=160524,Text='海底的生物会异变，神也会异变。',Speaker=891071,}",
  [160525] = "{id=160525,Text='想想他刚才的话，都能称得上邪恶了。定是寄宿了别处到来的邪恶之魂吧。',Speaker=891071,}",
  [160526] = "{id=160526,Text='此处的他只是个幻影。不去他本尊所在的空间，你不仅无法消灭他，他还会找上别的苦主哦。',Speaker=891071,}",
  [160527] = "{id=160527,Text='将它逐出神身，消灭它此世的影子，便可将它送回原来的世界。',Speaker=891071,}",
  [160528] = "{id=160528,Text='米德加尔特已具备驱邪的所有要素，只待你有心寻找。',Speaker=891071,Option='{你是谁,0},{你怎么知道,0}',}",
  [160529] = "{id=160529,Text='这就是个秘密了~',Speaker=891071,}",
  [160530] = "{id=160530,Text='虽然我想说，自己只是一介神官而已，但我既不侍奉阿斯加德，这个问题对你便没有答案。',Speaker=891071,}",
  [160531] = "{id=160531,Text='总之，解铃还须系铃人。不妨试试“别的”魔法。',Speaker=891071,}",
  [160532] = "{id=160532,Text='他的身体已经被你重伤，需要恢复几天。等到明天钻研也不晚。',Speaker=891071,Option='{谢谢你,0},{你真聪明,0}',}",
  [160533] = "{id=160533,Text='哪里哪里。',Speaker=891071,Emoji=38,}",
  [160534] = "{id=160534,Text='那么再会了，到处跑的人。',Speaker=891071,Emoji=17,}",
  [160535] = "{id=160535,Text='他最后叫的什么呀？到处跑的人？',}",
  [160536] = "{id=160536,Text='真的十分感谢您昨天救了他，冒险者。',Speaker=891068,}",
  [160537] = "{id=160537,Text='多亏了你，【生鲜精选】终于能够继续连载下去了！',Speaker=891068,Option='{你好开心,0},{有什么稀奇？,0}',Emoji=2,}",
  [160538] = "{id=160538,Text='依斯鲁得年度精选料理——生蘸芥末章鱼，会在下一期公布秘方啊！',Speaker=891068,}",
  [160539] = "{id=160539,Text='但是海中异变还没平息，魔法公会和我们城防军都还在联合调查当中。料理协会得停摆了。',Speaker=891068,Option='说明海神的情况',Emoji=24,}",
  [160540] = "{id=160540,Text='海神？',Speaker=891068,}",
  [160541] = "{id=160541,Text='您很有经验。按平常发挥，让海神大人变回原来的样子吧。',Speaker=891068,Option='{请问你知道,0},{驱邪的方法吗？,0}',}",
  [160542] = "{id=160542,Text='驱邪？一般的白魔法没有作用吗？时空魔法和咒术呢？',Speaker=891068,Emoji=19,}",
  [160543] = "{id=160543,Text='……哎，我也不好说清楚。',Speaker=891068,}",
  [160544] = "{id=160544,Text='城防军把工作放在了岸上，我们还抽不出身。',Speaker=891068,}",
  [160545] = "{id=160545,Text='这样吧。我把你传到海底神庙。古迹之类的地方会有线索。',Speaker=891068,Option='{我这就去,2},{等会儿再说,3}',}",
  [160546] = "{id=160546,Text='小心那里的海族们。一路顺风。',Speaker=891068,}",
  [160548] = "{id=160548,Text='好，我就在这里。',Speaker=891068,}",
  [160549] = "{id=160549,Text='“驱逐灵魂，可能得在[c][ffff00]一瞬之间[-][/c]发出[c][ffff00]庞大的能量[-][/c]。”',}",
  [160550] = "{id=160550,Text='“关键的人物，你很快就会找到了。而[c][ffff00]关键的道具……[-][/c]\n——By 杰洛士”',}",
  [160551] = "{id=160551,Text='……周围传来了凶险的杀气。',}",
  [160552] = "{id=160552,Text='所有力量的源泉',Speaker=891066,}",
  [160553] = "{id=160553,Text='灿烂燃烧的赤红火炎',Speaker=891066,}",
  [160554] = "{id=160554,Text='集中我手，化为我的力量吧',Speaker=891066,Action={actionid=23,num=1},}",
  [160555] = "{id=160555,Text='火——炎——球……',Speaker=891066,}",
  [160556] = "{id=160556,Text='果然没用！',Speaker=891066,}",
  [160557] = "{id=160557,Text='烦死了！为什么到了别的空间，我还是不能在水底使用黑魔法呀！',Speaker=891066,Emoji=37,}",
  [160558] = "{id=160558,Text='不过这里的人鱼们都是挺漂亮的。',Speaker=891066,}",
  [160559] = "{id=160559,Text='莉娜，还没好吗，我想吃螃蟹……',Speaker=891067,Emoji=19,}",
  [160560] = "{id=160560,Text='不要总惦记着这一道菜。早解决海族，才能早吃到章鱼……不，早点儿回去。',Speaker=891066,}",
  [160561] = "{id=160561,Text='可你现在不是连烟花都没放嘛……',Speaker=891067,}",
  [160562] = "{id=160562,Text='咦。谁？！',Speaker=891067,}",
  [160563] = "{id=160563,Text='莉娜，后退！这家伙应该……好像……很可能，不是海族！',Speaker=891067,Emoji=1,}",
  [160564] = "{id=160564,Text='看你个头啦！连魔物也不是，笨蛋！是冒险者！',Speaker=891066,}",
  [160565] = "{id=160565,Text='你好！居然敢一个人来异变之海，看来你对自己还是蛮有信心的嘛。',Speaker=891066,Option='{因为海神中邪了,0},{说明海神的情况,0}',}",
  [160566] = "{id=160566,Text='难怪海族们源源不断。',Speaker=891066,Option='{你是谁,0},{尊姓大名,0}',}",
  [160567] = "{id=160567,Text='哈~？差点忘了，异世界没几个人见过我。',Speaker=891066,}",
  [160568] = "{id=160568,Text='我就是天才美少女魔导士，伶俐过人、体现似火般意志的……',Speaker=891066,Option='{海盗,6},{小女孩,7}',}",
  [160569] = "{id=160569,Text='居然敢把我跟虫子比较！',Speaker=891066,Emoji=40,}",
  [160570] = "{id=160570,Text='你在教人时，手法还远远不够温柔，莉娜。',Speaker=891067,Emoji=18,}",
  [160571] = "{id=160571,Text='没错，这就是青春的意志！',Speaker=891066,Emoji=14,}",
  [160572] = "{id=160572,Text='等等，你没在讽刺人家性感的身材，对吧？',Speaker=891066,}",
  [160573] = "{id=160573,Text='重新自我介绍。',Speaker=891066,}",
  [160574] = "{id=160574,Text='我是莉娜，莉娜·因巴斯，也是个魔导士。既然到了异世界，直接称呼我的名字就够了。',Speaker=891066,}",
  [160575] = "{id=160575,Text='我是高里·加布列夫，现在担任起了监护人的重任，和她旅行中，哈哈哈。',Speaker=891067,Option='{异世界魔法？,0},{会用魔法吗？,0}',Emoji=17,}",
  [160576] = "{id=160576,Text='当然！比你们的魔法要更容易理解呢! 有机会给你见识见识，而那位海神是个不错的沙包。',Speaker=891066,Emoji=5,}",
  [160577] = "{id=160577,Text='莉娜，悠着点。',Speaker=891067,Option='能驱除邪恶意志？',}",
  [160578] = "{id=160578,Text='……连这个世界的神灵都可以附身吗？看来并不只是普通的魔族。',Speaker=891066,Emoji=14,}",
  [160579] = "{id=160579,Text='前提是我能在水里放出高等黑魔法，凭借巨大的能量，说不定就能帮你们驱魔了。',Speaker=891066,}",
  [160580] = "{id=160580,Text='……没光之剑的上场机会吗？',Speaker=891067,Emoji=19,}",
  [160581] = "{id=160581,Text='不要啊笨蛋，你的剑会把海神自己的精神都消灭的。',Speaker=891066,}",
  [160582] = "{id=160582,Text='风水不好。',Speaker=891067,Option='{我来想办法,0},{要我帮忙吗？,0}',}",
  [160583] = "{id=160583,Text='真的？',Speaker=891066,Option='{免费的,0},{专业的,0}',}",
  [160584] = "{id=160584,Text='谢啦，不管怎么看你都是好人嘛！那么接下来也请多指教~明天再想想办法。',Speaker=891066,Emoji=40,}",
  [160585] = "{id=160585,Text='哎哟，背好痛，杀都杀不完，我先上岸了。',Speaker=891066,}",
  [160586] = "{id=160586,Text='明天在那什么……那什么……',Speaker=891067,}",
  [160587] = "{id=160587,Text='……[c][ffff00]普隆德拉[-][/c]。',Speaker=891066,}",
  [160588] = "{id=160588,Text='对，[c][ffff00]普隆德拉[-][/c]！我们买好海鲜去[c][ffff00]普隆德拉[-][/c]找你。',Speaker=891067,}",
  [160589] = "{id=160589,Text='拜拜，好人冒险者~！',Speaker=891066,Emoji=25,}",
  [160590] = "{id=160590,Text='嗷呜！螃蟹壳好难剥。剑借我一下，只借一下哦。',Speaker=891066,Emoji=40,}",
  [160591] = "{id=160591,Text='光剑不是这样用的……哎，冒险者来了。',Speaker=891067,}",
  [160592] = "{id=160592,Text='我们从依斯鲁得过来了。久等了吧！什么？要不要睡个回笼觉？',Speaker=891067,Option='{不必,0},{早起有虫吃,0}',}",
  [160593] = "{id=160593,Text='（放下螃蟹）时间观念很强呢。准备好了吗，好心人？',Speaker=891066,Option='{准备好了,2},{还没有,3}',Emoji=17,}",
  [160594] = "{id=160594,Text='我昨晚拜访了魔法公会，收集一些情报，想出了计策。准备好，我们就走。',Speaker=891066,}",
  [160595] = "{id=160595,Text='是吗……有点扫兴，你快点做准备哦。',Speaker=891066,}",
  [160596] = "{id=160596,Text='今天的异变也在继续……',Speaker=891067,}",
  [160597] = "{id=160597,Text='（悄悄）看到我俩周围的屏障了吗，这是莉娜的魔法哦！',Speaker=891067,}",
  [160598] = "{id=160598,Text='说来有点耍赖：为什么你们没有泡泡也能在水下呼吸啊？',Speaker=891067,Option='{我也在思考,0},{为什么呢,0},{要不你也试试？,0}',}",
  [160602] = "{id=160602,Text='你们俩，说什么呢？',Speaker=891066,}",
  [160603] = "{id=160603,Text='话说回来冒险者，你是有什么好主意吗？',Speaker=891066,Option='{隔开海水,0},{制造空气,0}',}",
  [160604] = "{id=160604,Text='没错，想到一块儿去了！',Speaker=891066,}",
  [160605] = "{id=160605,Text='可是，为了在水下呼吸，我维持翔封界，这样一来，只是烟花……啊不，火炎球什么的应该没问题。',Speaker=891066,}",
  [160606] = "{id=160606,Text='要是风之精灵魔法使在就好了。',Speaker=891066,Option='我知道气泡做法',}",
  [160607] = "{id=160607,Text='雄马克的卵？卵，卵卵卵……！好吃吗？',Speaker=891066,Option='很好吃，但得这么用',}",
  [160608] = "{id=160608,Text='该不会香波的泡泡的素材也是卵吧？',Speaker=891066,}",
  [160609] = "{id=160609,Text='你们世界我不是很懂……那么，采集方面，就请有经验的你来~！',Speaker=891066,Option='{好的！,0},{包在我身上,0}',}",
  [160610] = "{id=160610,Text='这东西黏糊糊的，我还挺可怜里边的小生命。',Speaker=891066,}",
  [160611] = "{id=160611,Text='不行，太小了~手放不进去。',Speaker=891066,}",
  [160612] = "{id=160612,Text='哎，不能扩大吗……你们的魔法真不方便！',Speaker=891066,}",
  [160613] = "{id=160613,Text='我查过，大致分成破坏、治疗、辅助三类，没有改变物品体积一类的魔法？',Speaker=891066,}",
  [160614] = "{id=160614,Text='喂喂莉娜，只会黑魔法的你没资格对这个世界的魔法说三道四吧？',Speaker=891067,}",
  [160615] = "{id=160615,Text='……等等，提到破坏……莉娜，只要隔开水就行了，对吧！',Speaker=891067,Emoji=11,}",
  [160616] = "{id=160616,Text='你有主意了，高里？！……不，冒险者，不听也没什么关系，他只会想到一些蠢东西。',Speaker=891066,}",
  [160617] = "{id=160617,Text='跟掰开螃蟹壳是一个道理！请让我用这把[c][ffff00]光之剑[-][/c]，把海面劈开。',Speaker=891067,}",
  [160618] = "{id=160618,Text='呃……他的“主意”你怎么看，冒险者。',Speaker=891066,Option='{像神话贤者！,0},{总得试试吧,0}',}",
  [160619] = "{id=160619,Text='既然你也说了……概率再小，也只有赌一把了。',Speaker=891066,}",
  [160620] = "{id=160620,Text='那为了防止把一般人和封印卷进来，我们去一个更宽广的地方吧！跟我来！',Speaker=891067,}",
  [160621] = "{id=160621,Text='来了~！',Speaker=891067,}",
  [160622] = "{id=160622,Text='这就是我的力量！',Speaker=891067,}",
  [160623] = "{id=160623,Text='有戏！虽然他只是在用纯粹的蛮力劈开海水！',Speaker=891066,}",
  [160624] = "{id=160624,Text='很好！只要他越认真，那把剑的力量就会越强大！',Speaker=891066,}",
  [160625] = "{id=160625,Text='加了个油！高里——！把海劈开啊——！',Speaker=891066,Option='{加油！,0},{你最帅了！,0}',Emoji=39,}",
  [160626] = "{id=160626,Text='哈啊啊啊啊啊啊!',Speaker=891067,}",
  [160627] = "{id=160627,Text='就是现在！快！',Speaker=891067,}",
  [160628] = "{id=160628,Text='比黄昏还要昏暗之物！比血液还要鲜红之物！埋没于时间之流中&(*&)%@$#%$*(&)^*)_(+_)$%^赐与他们平等的毁灭吧！',Speaker=891066,Option='{好快,0},{绕口令吗！,0}',}",
  [160629] = "{id=160629,Text='……又吃瘪了。',Speaker=891066,}",
  [160630] = "{id=160630,Text='别放弃！再撑一会儿！',Speaker=891067,Emoji=1,}",
  [160631] = "{id=160631,Text='可能……我念太快了？人家好怕咬到舌头！',Speaker=891066,}",
  [160632] = "{id=160632,Text='对呀，是意志力，莉娜！全神贯注才能使用龙破斩！',Speaker=891067,Emoji=15,}",
  [160633] = "{id=160633,Text='我破罐子破摔！……听好了，莉娜……海底洞穴有一处宝藏。',Speaker=891067,}",
  [160634] = "{id=160634,Text='什么……？！',Speaker=891066,Emoji=14,}",
  [160635] = "{id=160635,Text='怪不得！每次你都直接把我传到神庙。我到现在都不知道这里有两层！',Speaker=891066,Option='{说起来,0},{我刚才也是,0},{被传送过来的,0}',}",
  [160636] = "{id=160636,Text='为什么…一到奇怪的地方你的脑回路就转的特别快呢！',Speaker=891066,}",
  [160637] = "{id=160637,Text='现在不是吵架的时候。地图给我，宝藏大概在这个位置。',Speaker=891067,}",
  [160638] = "{id=160638,Text='想象一下吧，我们用这股劲儿把宝物给……',Speaker=891067,}",
  [160639] = "{id=160639,Text='喂，上哪儿去！等等我！莉娜！',Speaker=891067,}",
  [160640] = "{id=160640,Text='亲眼看到，宝箱、金币、珠宝，一应俱全，非常、非常地……珠光宝气！',Speaker=891066,Option='{金山银海,0},{灿灿生辉,0}',Emoji=13,}",
  [160641] = "{id=160641,Text='现在！再来！',Speaker=891067,}",
  [160642] = "{id=160642,Text='哈、哈……（深呼吸）',Speaker=891066,Option='{口水都流下来了,0},{眼珠子都掉下来了,0}',}",
  [160643] = "{id=160643,Text='比黄昏还要昏暗之物！比血液还要鲜红之物！',Speaker=891066,}",
  [160644] = "{id=160644,Text='埋没于时间之流中！在您的伟大的名下！我在这黑暗中起誓！',Speaker=891066,}",
  [160645] = "{id=160645,Text='把阻挡在我们前方！所有的愚蠢之物！',Speaker=891066,}",
  [160646] = "{id=160646,Text='集合你我之力！',Speaker=891066,}",
  [160647] = "{id=160647,Text='赐与他们平等的毁灭吧！',Speaker=891066,}",
  [160648] = "{id=160648,Text='龙……破……斩！',Speaker=891066,}",
  [160649] = "{id=160649,Text='咦？怎么总感觉样子有点奇怪？',Speaker=891067,}",
  [160650] = "{id=160650,Text='一想到这些宝藏入不了手……',Speaker=891066,}",
  [160651] = "{id=160651,Text='我，燃不起来……',Speaker=891066,Emoji=24,}",
  [160652] = "{id=160652,Text='你的手累了吗？我来替你扛。',Speaker=891067,}",
  [160653] = "{id=160653,Text='你个笨蛋！',Speaker=891066,}",
  [160654] = "{id=160654,Text='这些宝藏能光明正大地放在这里，肯定不能随便碰。',Speaker=891066,}",
  [160655] = "{id=160655,Text='怎么回事？冒险者？',Speaker=891067,Option='{宝藏不能拿,0},{宝藏有主了,0}',}",
  [160656] = "{id=160656,Text='哦！那可真是遗憾了呢……',Speaker=891067,Emoji=16,}",
  [160657] = "{id=160657,Text='他的脑袋就好像周围的水母，对吧？一下上升、一下下沉。',Speaker=891066,Option='{长的帅就够了,0},{有蛮力就够了,0},{不，大多时候是“下”,0}',}",
  [160658] = "{id=160658,Text='莉娜她好像站不起来了。',Speaker=891067,Option='{点头,0},{我也累,0}',}",
  [160659] = "{id=160659,Text='哎，够了！',Speaker=891066,}",
  [160660] = "{id=160660,Text='这个世界的规则跟我真是水火不容呢……！',Speaker=891066,Emoji=1,}",
  [160661] = "{id=160661,Text='看来，只要莉娜站在海底，就不能使用高等魔法。',}",
  [160662] = "{id=160662,Text='陆地……才是人家的猎场。',Speaker=891066,Option='{下次让我来吧,0},{莉娜先休息,0}',Emoji=16,}",
  [160663] = "{id=160663,Text='让人头疼啊~你下一步怎么做呢~？',Speaker=891067,Option='{明天再说,0},{要好好想想,0}',}",
  [160664] = "{id=160664,Text='好，先回旅馆，明儿，广场上老地方见。',Speaker=891067,}",
  [160667] = "{id=160667,Text='冒险者，回家之后可是要好好把脚给洗干净哦！海水里的盐分可是很不得了的东西呢！',Speaker=891067,Option='{谢谢,0},{你也是,0}',}",
  [160668] = "{id=160668,Text='火炎球！火炎球！',Speaker=891066,Emoji=1,}",
  [160669] = "{id=160669,Text='请不要烧我的报纸……',Speaker=891067,}",
  [160670] = "{id=160670,Text='哎，冒险者你来啦!……你看，莉娜那家伙，完全把昨天的东西忘了，今天总感觉她十分……',Speaker=891067,Option='{火辣？,0},{热情？,0}',}",
  [160671] = "{id=160671,Text='对对！不服输对吧？别看她现在这样，实际上她的内心是非常勤……没什么，你那边怎么样呢？',Speaker=891067,Option='{主意想好了,2},{还要考虑,3}',}",
  [160672] = "{id=160672,Text='好，有事叫我们。随时待命。',Speaker=891067,}",
  [160673] = "{id=160673,Text='出发吧。',Speaker=891067,Option='{别急着传送,0},{不要去海底,0}',Emoji=23,}",
  [160674] = "{id=160674,Text='难道我们要锻炼身体，游到柏伊亚岚？',Speaker=891067,Option='{是这样的,0},{听我说,0}',}",
  [160675] = "{id=160675,Text='把海神骗上岸，让莉娜在地上使用龙破斩？',Speaker=891067,Emoji=19,}",
  [160676] = "{id=160676,Text='总感觉不大明白……这样就好了吗？',Speaker=891067,}",
  [160677] = "{id=160677,Text='很好！地形战术！',Speaker=891066,}",
  [160678] = "{id=160678,Text='我不会出糗了。你放心去办。',Speaker=891066,}",
  [160679] = "{id=160679,Text='我是不懂啊，但她好像赞成你的战术了。',Speaker=891067,}",
  [160680] = "{id=160680,Text='那么，我们到依斯鲁得岛的港口守着，等你的好消息。',Speaker=891066,}",
  [160681] = "{id=160681,Text='唔……这个是冒险者的道具背包吧？我看看，啊有了！蝴蝶翅膀！……哟，我会给你来回份的，这样就足够了吧！',Speaker=891067,Option='谢谢',}",
  [160682] = "{id=160682,Text='该死……这具躯体……被伤得不轻。',Speaker=891064,}",
  [160683] = "{id=160683,Text='人类……我要人类……',Speaker=891064,}",
  [160684] = "{id=160684,Text='还好对海族的洗脑没有带来很大影响。不过，就算这具身体坏掉了，我还会继续附身在其他家伙身上吧。',Speaker=891064,}",
  [160685] = "{id=160685,Text='那么接下来的受害者是谁呢……有趣。',Speaker=891064,Emoji=7,}",
  [160686] = "{id=160686,Text='哎呀，我还以为是谁呢？原来是小英雄呀。两天不见终于下定决心来处置我了吗？',Speaker=891064,Option='{抱歉,0},{大前天伤了您,0}',Emoji=17,}",
  [160687] = "{id=160687,Text='你的话风怎么完全转了？',Speaker=891064,Option='{您不是海神,0},{您很有想法,0}',}",
  [160688] = "{id=160688,Text='呵……？说来也是，你这家伙好像平时杀了不少海族？你该不会其实内心是个很糟糕的家伙吧？',Speaker=891064,Option='{这是变强的代价,4},{我只是为了自保,4 },{不小心就点到了,5}',Emoji=5,}",
  [160689] = "{id=160689,Text='不用找借口。重复的失误只会带来必然。如果这样的话，你在陆上可也经常“不小心”呢！',Speaker=891064,Option='您也一样',}",
  [160690] = "{id=160690,Text='那如果对方不是海族，而是人类的话又怎么样呢？',Speaker=891064,Option='{人类都在岸上,0},{我给您抓来,0}',}",
  [160691] = "{id=160691,Text='不不不。别看我馋人类身子，我也是个美食家。',Speaker=891064,}",
  [160692] = "{id=160692,Text='因为人类会给零食跟甜点注入独特的魔力，大爷我可是很喜欢人类的食物的！',Speaker=891064,}",
  [160693] = "{id=160693,Text='听海族的人说，现在流行一种叫做“生蘸芥末章鱼”的料理？你们这些家伙会做吗？',Speaker=891064,Option='{好的,0},{我会在岸上,0},{准备餐桌,0}',}",
  [160694] = "{id=160694,Text='看样子你会的还挺多的。我等着你哦。',Speaker=891064,}",
  [160695] = "{id=160695,Text='冒险者，真有缘分呐！谢谢您救了我！',Speaker=891065,}",
  [160696] = "{id=160696,Text='说奥丁，奥丁就来了！我最近找老板商量了一下，让你当下期的封面人物……',Speaker=891065,}",
  [160697] = "{id=160697,Text='等等，您样子很着急，找我来是有事吗？',Speaker=891065,Option='询问生蘸芥末章鱼的秘方',}",
  [160698] = "{id=160698,Text='哎……您救了我！只要您不说出去，我当然可以只告诉您一个人。但姑且问一下，您要放在什么用途上呢。',Speaker=891065,Option='{拯救依斯鲁得,0},{引诱假海神上岸,0}',}",
  [160699] = "{id=160699,Text='听上去不大对劲，方便多说一些吗？',Speaker=891065,Option='{是这样的……,0},{海神他……,0}',}",
  [160700] = "{id=160700,Text='他在耍你呢！',Speaker=891065,}",
  [160701] = "{id=160701,Text='这几天在依斯鲁得，我没听过食材和料理被抢走的新闻，只有海族伤人的事儿！',Speaker=891065,}",
  [160702] = "{id=160702,Text='可是，这生蘸芥末章鱼听上是道简餐，做法却很麻烦，得把章鱼腿腌到山葵里去腥……',Speaker=891065,Emoji=18,}",
  [160703] = "{id=160703,Text='差点泄露啦！咳咳。',Speaker=891065,}",
  [160704] = "{id=160704,Text='我嘛，这边有个建议，不知当不当讲：我刚好带了一件工具，本来是给我们捕鱼用的……',Speaker=891065,Option='{是什么,0},{继续说,0}',Emoji=11,}",
  [160705] = "{id=160705,Text='一盏热潜水灯。这好东西能够把海水像温泉一样加热，然后鱼就会漂到陆地上来，变得更加容易捕捉了。',Speaker=891065,}",
  [160706] = "{id=160706,Text='不过在海神他身上，管用吗？',Speaker=891065,Option='{可以试试,0},{死马当活马医,0}',}",
  [160707] = "{id=160707,Text='得咧，我们渔船有整整一箱！这个送给你，拿去拿去！',Speaker=891065,Option='谢谢！',}",
  [160708] = "{id=160708,Text='好去好回！冒险者！',Speaker=891065,Emoji=20,}",
  [160709] = "{id=160709,Text='地势很高，易于扩散，离海神很近。选这里了。',}",
  [160710] = "{id=160710,Text='好热，已经分不清身上流的是海水还是汗水了。',}",
  [160711] = "{id=160711,Text='挺热的。冒险者你不觉得热吗？',Speaker=891064,Option='不上去透风吗 ',}",
  [160712] = "{id=160712,Text='吃点清爽的解火，比如生蘸芥末章鱼。',Speaker=891064,Option='{对不起,0},{我没有,0}',}",
  [160713] = "{id=160713,Text='你又为什么回来呢？',Speaker=891064,Option='{伟大的海神…,0},{伟大之灵,0}',}",
  [160714] = "{id=160714,Text='伟大吗，我算不上，但听着舒心。',Speaker=891064,Option='{我知道,0},{您只吃人类,0}',}",
  [160715] = "{id=160715,Text='想不到你不笨。',Speaker=891064,Option='我会在岸上',}",
  [160716] = "{id=160716,Text='……？',Speaker=891064,Option='将人类献给你',Emoji=39,}",
  [160717] = "{id=160717,Text='说的真好听！',Speaker=891064,}",
  [160718] = "{id=160718,Text='再说一点！再大声一点！小英雄！',Speaker=891064,}",
  [160719] = "{id=160719,Text='不过上岸这件事就免谈了……毕竟你的意图显而易见啊！哇哈哈哈哈！',Speaker=891064,}",
  [160720] = "{id=160720,Text='等等……难道难道难道，难道您就是传说中那种笨蛋?',Speaker=891064,Emoji=37,}",
  [160721] = "{id=160721,Text='哈哈哈，你看你摩拳擦掌的，也不敢动手呀。',Speaker=891064,Emoji=5,}",
  [160722] = "{id=160722,Text='再热我都在老地方等你，和你的生蘸芥末章鱼咧。',Speaker=891064,}",
  [160723] = "{id=160723,Text='这家伙不蠢还很机灵。先回去吧。',}",
  [160724] = "{id=160724,Text='来了！',Speaker=891067,}",
  [160725] = "{id=160725,Text='呼，我们在港口站了好久。好想海钓。',Speaker=891067,}",
  [160726] = "{id=160726,Text='不过，除了骚扰海岸的变异海族，海面真的好平静呐。',Speaker=891067,}",
  [160727] = "{id=160727,Text='呜呜呜……我含了橘子皮，随时龙破斩！',Speaker=891066,Emoji=25,}",
  [160728] = "{id=160728,Text='所以，海神多久上岸？难道要我们偷袭吗？',Speaker=891066,Emoji=21,}",
  [160729] = "{id=160729,Text='在他脑袋冒出海面的一瞬间……',Speaker=891066,Option='其实……',}",
  [160730] = "{id=160730,Text='把情况告诉了两人。',}",
  [160731] = "{id=160731,Text='气死我了！要是我见到他一定会揍得他落花流水！',Speaker=891066,Emoji=12,}",
  [160732] = "{id=160732,Text='别垂头丧气的，又不是你的错！',Speaker=891066,}",
  [160733] = "{id=160733,Text='但是，他玩弄人心的脾性，比起吃人的怪物，更接近魔族。',Speaker=891066,Option='{魔族？,0},{和魔物有区别吗？,0}',}",
  [160734] = "{id=160734,Text='那我简要说明一下，魔族是一种玩弄人心、以人类……还有多兰的负面情绪为食的存在。',Speaker=891066,Option='{理解了,0},{像恶魔一样,0}',}",
  [160735] = "{id=160735,Text='……太阳都下山了，莉娜。冒险者流了一身汗，累了吧？',Speaker=891067,}",
  [160736] = "{id=160736,Text='好！我们先消费一下。难得来到依斯鲁得，一起去吃螃蟹吧。我找到了一家不错的餐馆。',Speaker=891066,Option='{好啊！,0},{我可以,0}',}",
  [160737] = "{id=160737,Text='对了！我要炸螃蟹！蟹黄炒饭要留给我！',Speaker=891066,Emoji=28,}",
  [160738] = "{id=160738,Text='和莉娜她们在冷清但美味的餐厅里吃了热饭。',}",
  [160739] = "{id=160739,Text='最后是我结账。',}",
  [160740] = "{id=160740,Text='明天再和她们讨论吧。',}",
  [160741] = "{id=160741,Text='……高里，你快点回来……',Speaker=891066,}",
  [160742] = "{id=160742,Text='莉娜一个人对水面梳着头发，似乎很困扰的模样，要去搭话吗？',Option='{询问莉娜,2},{等会儿,3}',}",
  [160743] = "{id=160743,Text='你也……来啦？',Speaker=891066,Option='{你怎么,0},{好严肃,0},{高里呢,0}',}",
  [160744] = "{id=160744,Text='今天，依斯鲁得又有一艘游船沉没了。',Speaker=891066,}",
  [160745] = "{id=160745,Text='那家伙就正义爆棚，想学昨天的你，把海神骗上岸来。',Speaker=891066,Option='{太危险了,0},{他更不行,0}',}",
  [160746] = "{id=160746,Text='我也这么想。而且，那家伙也不太清楚这里的魔物，脑袋也……蠢蠢的。',Speaker=891066,}",
  [160747] = "{id=160747,Text='可恶，一定因为我要还饭钱和旅费，接了城防军的悬赏单，上午不能下海，他才按捺不住冲进去的。',Speaker=891066,Option='{他怎么呼吸？,0},{他能下海了？,0}',}",
  [160748] = "{id=160748,Text='其实，只有我在水下不能呼吸……',Speaker=891066,}",
  [160749] = "{id=160749,Text='可能因为高里不会魔法，他没有被我们的规则所束缚。',Speaker=891066,}",
  [160750] = "{id=160750,Text='要在平时我不会管他。没准，他哪一天会傻笑着回来的。但现在……',Speaker=891066,Emoji=14,}",
  [160751] = "{id=160751,Text='高里……！',Speaker=891066,Option='{我去找他,0},{我把他带回来,0}',Emoji=24,}",
  [160754] = "{id=160754,Text='多谢，辛苦你跑一趟了。',Speaker=891066,}",
  [160755] = "{id=160755,Text='（嘀咕）那个杰洛士……在这种关头不该冒出来说一堆胡话吗。那家伙到底去了哪儿！',Speaker=891066,}",
  [160756] = "{id=160756,Text='你到底上不上岸，邪魔外道！',Speaker=891067,}",
  [160757] = "{id=160757,Text='威胁我也无用。我知道你们没法对我下杀手，但能动粗。所以，我才让海族抓了船上的人质。',Speaker=891064,}",
  [160758] = "{id=160758,Text='小家伙，伤害我的话，我可是能把人类给捏死呢。',Speaker=891064,Emoji=5,}",
  [160759] = "{id=160759,Text='你……！',Speaker=891067,Emoji=12,}",
  [160760] = "{id=160760,Text='哎呀，小英雄也来了？',Speaker=891064,Option='{我来是为了,0},{送生蘸芥末章鱼,0}',}",
  [160761] = "{id=160761,Text='现在不是说笑的场合！',Speaker=891067,}",
  [160762] = "{id=160762,Text='没想到气氛这么严肃。为了缓和一下，我要不要现在就捏死一个人质？',Speaker=891064,}",
  [160763] = "{id=160763,Text='只是少了个筹码，而且少费根牙签，不是更好吗？哈哈哈哈！话说回来，这具身体有牙齿吗？',Speaker=891064,}",
  [160764] = "{id=160764,Text='混账！',Speaker=891067,Emoji=37,}",
  [160765] = "{id=160765,Text='您最好别给魔族丢人现眼。',Speaker=891071,}",
  [160766] = "{id=160766,Text='杰洛士……你终于来了！',Speaker=891067,}",
  [160767] = "{id=160767,Text='等一下，为什么要说“终于”。',Speaker=891067,Option='{杰洛士？,0},{你留下了讯息？,0}',Emoji=1,}",
  [160768] = "{id=160768,Text='大人，你一下救我，一下又打断我，到底站哪队？',Speaker=891064,}",
  [160769] = "{id=160769,Text='我的立场是个秘密，值得您猜来猜去吗，阁下。',Speaker=891071,Emoji=5,}",
  [160770] = "{id=160770,Text='我先给你个提醒，你应该先感应那些被您控制的生物，好吗？',Speaker=891071,}",
  [160771] = "{id=160771,Text='……什么？没人？！难道是你……救了人类？',Speaker=891064,Emoji=7,}",
  [160772] = "{id=160772,Text='您只放纵自己内心的快乐来杀人。这种制造恐怖的手段，实在下品之极。',Speaker=891071,}",
  [160775] = "{id=160775,Text='嘁，随便你们。',Speaker=891064,}",
  [160776] = "{id=160776,Text='又跑掉了，以为自己只是华丽丽的战略撤退吗！',Speaker=891067,Emoji=12,}",
  [160777] = "{id=160777,Text='没必要追。',Speaker=891071,Option='你们认识？',}",
  [160778] = "{id=160778,Text='杰洛士……！冒险者，这家伙虽然可疑，但说完一些对我们有益的话，就会主动消失哦。',Speaker=891067,Option='{是的,0},{我也见过,0}',}",
  [160779] = "{id=160779,Text='我在你们那儿是这种印象吗……好伤心。',Speaker=891071,Emoji=10,}",
  [160780] = "{id=160780,Text='算了算了，我是来补充之前讯息的，四处跑的人。',Speaker=891071,Option='{叫什么？,0},{尊重点,0}',}",
  [160781] = "{id=160781,Text='抱歉让你不快了，[c][ffff00][PlayerName][-][/c]~言归正传。你虽然找到关键人物，却缺少关键道具。',Speaker=891071,}",
  [160782] = "{id=160782,Text='记得吗？驱除邪恶，有两个条件：除了“[c][ffff00]庞大的能量[-][/c]”，还要在“[c][ffff00]一瞬之间[-][/c]”。',Speaker=891071,}",
  [160783] = "{id=160783,Text='在异世界，这种能量传导形式也是存在的。',Speaker=891071,Option='{不要卖关子,0},{难道是……,0}',}",
  [160784] = "{id=160784,Text='那就是……',Speaker=891071,}",
  [160785] = "{id=160785,Text='光。',Speaker=891071,}",
  [160786] = "{id=160786,Text='还好没说“秘密”！不过，既然都念“光”……我的光之剑就不行吗？',Speaker=891067,Option='{你的剑不是波,0},{你的剑不够快,0}',}",
  [160787] = "{id=160787,Text='诚如冒险者所言。',Speaker=891071,}",
  [160788] = "{id=160788,Text='莉娜的魔法满足了前面的条件，但你们还需要一个道具。对应到这个世界上，它指的便是“[c][ffff00]反射之镜[-][/c]”。',Speaker=891071,}",
  [160789] = "{id=160789,Text='由它作中转，莉娜·因巴斯的绝招就能改变轨道，直接打入水体中。',Speaker=891071,Option='{怎么找到,0},{它在哪儿,0}',}",
  [160790] = "{id=160790,Text='毕竟是年代久远的古物。现在那些碎片已经四散海中了吧。',Speaker=891071,}",
  [160791] = "{id=160791,Text='那不就可以说是……那个词、叫什么来着……？',Speaker=891067,Option='{“大海捞针”？,0},{TAN90°？,0}',}",
  [160792] = "{id=160792,Text='给个提示，它是亮晶晶的。海中有种生物的习性就是收集此类物品吧？',Speaker=891071,Option='{古蟹！,0},{一种长命的螃蟹,0}',}",
  [160793] = "{id=160793,Text='对！是这个呢！',Speaker=891067,Option='你又没见过……',}",
  [160794] = "{id=160794,Text='想起来，这里是异世界来着。',Speaker=891067,Emoji=19,}",
  [160795] = "{id=160795,Text='反射之镜裂成了数以千计的片数，但原来它就体积庞大。只要收集不到两位数的碎片，便可拼出反射混沌之力的面积。',Speaker=891071,}",
  [160796] = "{id=160796,Text='那么……我的提示就到此为止了。Bye~Bye！替我向莉娜问好，高里。',Speaker=891071,}",
  [160797] = "{id=160797,Text='你看，他消失了吧。',Speaker=891067,Option='{事不宜迟,0},{我们走,0}',}",
  [160798] = "{id=160798,Text='好！顺便屯些蟹肉。',Speaker=891067,}",
  [160799] = "{id=160799,Text='高里那边应该也收集了一些，去找他。',}",
  [160800] = "{id=160800,Text='真轻松，螃蟹巢里刨到了。',Speaker=891067,}",
  [160801] = "{id=160801,Text='你那边呢？',Speaker=891067,Option='{够了,0},{很顺利,0}',}",
  [160802] = "{id=160802,Text='行，我们去找莉娜。',Speaker=891067,}",
  [160803] = "{id=160803,Text='高里——！',Speaker=891066,}",
  [160804] = "{id=160804,Text='作为监护人，你失职了高里！',Speaker=891066,Emoji=25,}",
  [160805] = "{id=160805,Text='好！下次不……一定不乱跑了！',Speaker=891067,Emoji=17,}",
  [160806] = "{id=160806,Text='[c][ffff00][PlayerName][-][/c]，谢谢你，把他带回来了。',Speaker=891066,Option='{小意思,0},{不用谢,0}',Emoji=2,}",
  [160807] = "{id=160807,Text='……',Option='{高里，不要忘了,0},{杰洛士拜托过你,0}',}",
  [160808] = "{id=160808,Text='哦对了，莉娜，我们遇到了杰洛士，他要我替他向你问好。',Speaker=891067,Emoji=19,}",
  [160809] = "{id=160809,Text='……你们在海底遭遇了什么吗？',Speaker=891066,Option='{向莉娜说明情况,0},{找到办法了！,0}',}",
  [160810] = "{id=160810,Text='那么，修复镜子和计算弹道，这两件事我来。',Speaker=891066,}",
  [160811] = "{id=160811,Text='只有一个重要步骤，还是得靠你完成。',Speaker=891066,}",
  [160812] = "{id=160812,Text='我希望你把海神引到一个固定、空旷的地带，我才能发动那种最强的黑魔法——龙破斩。',Speaker=891066,Option='{好的,0},{交给我,0}',}",
  [160813] = "{id=160813,Text='很好，爽快。',Speaker=891066,Emoji=9,}",
  [160814] = "{id=160814,Text='这把[c][ffff00]信号箭[-][/c]给你，准备好就朝海面发射。看到信号后，我就会使用魔法！',Speaker=891066,}",
  [160815] = "{id=160815,Text='另外……玩弄感情的混球，你千万不能放过，冒险者。',Speaker=891066,}",
  [160816] = "{id=160816,Text='这里很空旷。',Option='{出来吧,0},{海神！,0}',}",
  [160817] = "{id=160817,Text='只有你一个人吗？',Speaker=891064,Option='{气氛不对,0},{太容易了？,}0',}",
  [160818] = "{id=160818,Text='海中空间即我本身，我无处不在。',Speaker=891064,Option='（这位置正好）',}",
  [160819] = "{id=160819,Text='那么……小英雄。',Speaker=891064,Emoji=38,}",
  [160820] = "{id=160820,Text='其实我想了很多。',Speaker=891064,}",
  [160821] = "{id=160821,Text='这几天在侵略人类的过程中，我感受到了米德加尔特蕴藏的神奇魔力。',Speaker=891064,}",
  [160822] = "{id=160822,Text='那股魔力深深地感染了我，使我的灵魂构造发生了些许变化。',Speaker=891064,}",
  [160823] = "{id=160823,Text='依斯鲁得城是那么的自由。这里的人们热爱美食、互相关心、团结一致。',Speaker=891064,}",
  [160824] = "{id=160824,Text='那个叫杰洛士的魔族愿意帮助你们，一定是因为这个原因吧。',Speaker=891064,Option='杰洛士也是魔族？',}",
  [160825] = "{id=160825,Text='也许你无法察觉，人类。在我们的世界，上位魔族拥有化成人类的能力。',Speaker=891064,}",
  [160826] = "{id=160826,Text='故而，基于以上原因，我决心放弃这具身体，转而去附身一名人类，做几天实实在在的凡人。',Speaker=891064,}",
  [160827] = "{id=160827,Text='因为我想亲自尝一口……[c][ffff00]生蘸芥末章鱼[-][/c]。',Speaker=891064,Option='{海神大人……,0},{邪恶意志大人……,0}',}",
  [160828] = "{id=160828,Text='你能成全我这个心愿吗？',Speaker=891064,Option='{我……,4},{你……,5}',Emoji=16,}",
  [160829] = "{id=160829,Text='……',Option='才，不',}",
  [160830] = "{id=160830,Text='……',Option='滚，吧',}",
  [160831] = "{id=160831,Text='什么声音……？！',Speaker=891064,}",
  [160832] = "{id=160832,Text='这凡人笨蛋，竟不配合我的悲情战术————！',Speaker=891064,}",
  [160833] = "{id=160833,Text='啊啊啊啊——！',Speaker=891064,}",
  [160834] = "{id=160834,Text='太棒了！管用了！',Speaker=891067,Option='{异变结束了?,0},{海神还好吗？,0}',}",
  [160835] = "{id=160835,Text='可恶，竟然敢妨碍我？！',Speaker=891070,Emoji=10,}",
  [160836] = "{id=160836,Text='饶不了你们！',Speaker=891070,Emoji=12,}",
  [160837] = "{id=160837,Text='比黄昏还要昏暗之物！',Speaker=891066,}",
  [160838] = "{id=160838,Text='……埋没于时间之流中！',Speaker=891066,}",
  [160839] = "{id=160839,Text='在您的伟大的名下！我在这黑暗中起誓！',Speaker=891066,}",
  [160840] = "{id=160840,Text='什么？……不、不可能的！这里是海底！',Speaker=891070,Emoji=12,}",
  [160841] = "{id=160841,Text='不要阻止我！',Speaker=891070,}",
  [160842] = "{id=160842,Text='吾在此向汝立誓！对於阻挡在吾前方……',Speaker=891066,Emoji=5,}",
  [160843] = "{id=160843,Text='你的对手是我们！',Speaker=891067,Option='{休想打断莉娜,0},{老实再吃一发,0}',}",
  [160844] = "{id=160844,Text='一切愚妄之物！将以吾及汝之力！赐予其同等的毁灭！',Speaker=891066,Emoji=23,}",
  [160845] = "{id=160845,Text='呀啊啊啊！不想回到原来的世界啊——！',Speaker=891070,}",
  [160846] = "{id=160846,Text='它回去了！',Speaker=891067,}",
  [160847] = "{id=160847,Text='我说过会让你见识见识的。感想怎样？',Speaker=891066,Option='{很强大！,0},{很美丽！,0},{很有破坏性！,0}',}",
  [160848] = "{id=160848,Text='嘻嘻嘻！谢谢，我挺不好意思的！',Speaker=891066,Option='{所以……,0},{你为什么,0},{在海底……,0}',Emoji=38,}",
  [160849] = "{id=160849,Text='……',Option='放龙破斩了？',}",
  [160850] = "{id=160850,Text='好厉害！冒险者你发现了一个巨大的矛盾呢！',Speaker=891067,Option='{你才反应过来？,0},{呃，谢谢,0}',}",
  [160851] = "{id=160851,Text='刚才，有人暗中给我施展翔封界。',Speaker=891066,}",
  [160852] = "{id=160852,Text='和我们一起卷入异世界、除了我以外，唯一会使用我们魔法的人物，也就只有他了。',Speaker=891066,Option='{难道是…… ,0},{我也猜到了,0},{杰洛士,0}',}",
  [160853] = "{id=160853,Text='果然，他只是在利用我们，把那坨邪物打回原来的世界而已。……也不能说他，毕竟我也把冒险者你当成……',Speaker=891066,Option='{什么？,0},{怎么了?,0}',Emoji=40,}",
  [160854] = "{id=160854,Text='没、没什么哦。',Speaker=891066,Option='{不管怎样,0},{问题解决就好,0}',}",
  [160855] = "{id=160855,Text='呜呜呜……',Speaker=891064,}",
  [160856] = "{id=160856,Text='小心！海神他好像醒了！',Speaker=891067,}",
  [160857] = "{id=160857,Text='……本尊似乎……做了一场大梦……',Speaker=891083,Emoji=19,}",
  [160858] = "{id=160858,Text='……冒险者？！',Speaker=891083,}",
  [160859] = "{id=160859,Text='又来进犯本尊吗？',Speaker=891083,Option='{我不是,0},{我没有,0}',}",
  [160860] = "{id=160860,Text='妖言惑众！汝等污染海洋、滥杀海底生物，休想平息本尊的怒火！',Speaker=891083,}",
  [160861] = "{id=160861,Text='不料今日本尊身体虚弱，暂且饶了汝等！八个海里都不要见了！',Speaker=891083,Option='{是真货,0},{好熟悉,0}',}",
  [160862] = "{id=160862,Text='终于告一段落了。',Speaker=891067,}",
  [160863] = "{id=160863,Text='(伸懒腰)嗯……',Speaker=891066,Emoji=26,}",
  [160864] = "{id=160864,Text='异变的海族也减少了！',Speaker=891067,}",
  [160865] = "{id=160865,Text='这样就好，几天的功夫没有白费。我们能回到原来的地方了。',Speaker=891066,Option='{要回异世界了吗,0},{要分别了吗,0}',}",
  [160866] = "{id=160866,Text='说什么呢！是回“依斯鲁得”！人家对生蘸芥末章鱼念念不忘呢！',Speaker=891066,Emoji=28,}",
  [160867] = "{id=160867,Text='你是说，我们不急着回到原来的世界？',Speaker=891067,}",
  [160868] = "{id=160868,Text='这个世界多么宽广啊！新的美食、新的财宝，最重要的是，东西也不是那么贵！',Speaker=891066,}",
  [160869] = "{id=160869,Text='……没办法。我就保护你到底吧，莉娜。',Speaker=891067,}",
  [160870] = "{id=160870,Text='高里……你、你说什么话呢。',Speaker=891066,Emoji=38,}",
  [160871] = "{id=160871,Text='只要你来掏钱！',Speaker=891067,}",
  [160872] = "{id=160872,Text='[c][ffff00][PlayerName][-][/c]，如果你要寻求秘宝，又是请教魔法，尽管找我。',Speaker=891066,Option='{好的,0},{后会有期,0}',}",
  [160873] = "{id=160873,Text='日后再见，冒险者。真正的旅程才刚刚开始。',Speaker=891067,Option='没错！',}",
  [160880] = "{id=160880,Text='他们或勇敢、或善良、或幽默、或强大……他们有一个共同点——都是从这里出发，',Speaker=892789,}",
  [160881] = "{id=160881,Text='从我脚下既熟悉又陌生的土地出发，去探索每一个隐秘的角落，',Speaker=892789,}",
  [160882] = "{id=160882,Text='他们在冒险的途中成长，他们在成长的路上结识新的伙伴，他们把友情作为羁绊，把爱与勇气作为战胜困难的力量！',Speaker=892789,}",
  [160883] = "{id=160883,Text='现在，[c][ffff00][PlayerName][-][/c]，你准备好冒险了吗！带着我精心为你准备的礼物，去开启属于你的篇章吧！[c][ffff00]【该任务在同账号下只能完成一次，请确认账号角色后谨慎接取！】[-][/c]',Speaker=892789,Option='{准备好了,3},{还没有,2}',}",
  [160884] = "{id=160884,Text='我相信，那一天不远了，我就在这里等你，等你准备好了，请记得来找我。',Speaker=892789,}",
  [160885] = "{id=160885,Text='希望你能喜欢我的礼物~',Speaker=892789,}",
  [160886] = "{id=160886,Text='什么，难道你不是我要找的那个人？',Speaker=892529,}",
  [160896] = "{id=160896,Text='你好，尊贵的客人！天水之国感谢你的帮助！',Speaker=892792,}",
  [160897] = "{id=160897,Text='在与阿卡娜的战斗后，我们决定不再隐瞒身份。',Speaker=892792,}",
  [160898] = "{id=160898,Text='忍者们因为经常要隐秘执行任务，伪装总是必不可少的。',Speaker=892792,}",
  [160899] = "{id=160899,Text='过去，我们习惯于身处暗影之中，但如今敌人来袭，我们必须要挺身而出，守护这里……',Speaker=892792,}",
  [160908] = "{id=160908,Text='[c][ffff00][PlayerName][-][/c]，现在我们是一起战斗过的伙伴了！',Speaker=892797,}",
  [160909] = "{id=160909,Text='相信现在的你已经对我们有所了解了——',Speaker=892797,}",
  [160910] = "{id=160910,Text='我们是忍者，天水之国最隐秘的守护者。',Speaker=892797,}",
  [160911] = "{id=160911,Text='如今危机已经到来，请与我们一同应对吧！',Speaker=892797,Emoji=27,}",
  [160921] = "{id=160921,Text='真好啊，看着你们这些冒险者年轻有活力的样子，十分令我羡慕……',Speaker=892775,}",
  [160922] = "{id=160922,Text='咳咳，忘了说正事：最近因为莉娜一行人的到来，整个大陆好像掀起了一股学习魔法的浪潮。',Speaker=892775,}",
  [160923] = "{id=160923,Text='但是学习魔法并不能一蹴而就，需要数年如一日的坚持与努力，因此莉娜有些话想委托我转达给你，希望你们之后也能成为并肩战斗的队友。',Speaker=892775,}",
  [160924] = "{id=160924,Text='下面我会让你随意抽选一张卡牌，上面有莉娜的赠言，同时你也能获得一份带有魔力的祝福哦~',Speaker=892775,}",
  [160927] = "{id=160927,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为混沌魔导士了吗？',Speaker=892806,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [160928] = "{id=160928,Text='转职过程中是不是遇到困难了，继续加油！',Speaker=892806,}",
  [160929] = "{id=160929,Text='继续加油，转职成功后来找我！',Speaker=892806,}",
  [160930] = "{id=160930,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为秀逗魔导士了吗？',Speaker=892806,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [160931] = "{id=160931,Text='成长的如此快，你一定经历了不少难忘的冒险吧！',Speaker=892806,}",
  [160932] = "{id=160932,Text='但是还没不能松懈，这个世界远不止表面这么简单。',Speaker=892806,}",
  [160933] = "{id=160933,Text='这是我在这个世界历险时得到的东西，希望能对你有用。',Speaker=892806,}",
  [160934] = "{id=160934,Text='继续加油！',Speaker=892806,}",
  [160935] = "{id=160935,Text='我果然没有看错你！',Speaker=892806,}",
  [160936] = "{id=160936,Text='有你这样的伙伴，我安心了许多。',Speaker=892806,}",
  [160937] = "{id=160937,Text='继续变强吧！你会是改变世界的那个人。',Speaker=892806,}",
  [160938] = "{id=160938,Text='收下这些，不要推辞。',Speaker=892806,}",
  [160940] = "{id=160940,Text='诶？你好呀！你是——这个世界的冒险者？',Speaker=892802,}",
  [160941] = "{id=160941,Text='对呀，我是优法！是从另一个米德加尔特来的！',Speaker=892802,}",
  [160942] = "{id=160942,Text='（另一个，米德加尔特？）',Speaker=892802,}",
  [160943] = "{id=160943,Text='大概是……两个平行的世界？总之，我们是来自那个世界的冒险者！',Speaker=892802,}",
  [160944] = "{id=160944,Text='跟我一起来的还有我的同伴罗安！',Speaker=892802,}",
  [160945] = "{id=160945,Text='如果见到他的话，记得帮我转告他，让他早点回来，不要乱跑了！',Speaker=892802,}",
  [160946] = "{id=160946,Text='我在裂隙附近捡到一本奇怪的手账本，不知道有什么用。冒险者，就交给你调查一下吧！',Speaker=892680,}",
  [160947] = "{id=160947,Text='若是点亮了[异次元扭蛋机]中的所有宝物，我就有一份神秘大礼送给你哦~',Speaker=892808,Option='{我已点亮,2},{离开,3}',}",
  [160949] = "{id=160949,Text='希望它能陪着你冒险~',Speaker=892808,}",
  [160950] = "{id=160950,Text='等你点亮了[异次元扭蛋机]中的所有宝物后，再来找我吧。',Speaker=892808,}",
  [160952] = "{id=160952,Text='过去的一年，[c][ffff00][PlayerName][-][/c]有什么不一样的收获吗？是不是也面临了很多不一样的挑战？',Speaker=892810,}",
  [160953] = "{id=160953,Text='新的一年，要继续加油哦~希望这份礼物可以带给你好运，陪伴你往更美好的方向，大步前进！',Speaker=892810,Option='{谢谢,2},{过会再领,3}',}",
  [160954] = "{id=160954,Text='——龙破斩！',Speaker=891066,}",
  [161184] = "{id=161184,Text='肚子好饿呀。',Speaker=891189,}",
  [161185] = "{id=161185,Text='呀！[c][ffff00][PlayerName][-][/c]，我遇上了早餐危机！',Speaker=891189,}",
  [161186] = "{id=161186,Text='别看我兼职运货员。我的真实身份是一名爱好料理的冒险者。所以这个忙，你说什么也要帮！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891189,Option='{先说给我听,2},{你在道德绑架,3}',}",
  [161187] = "{id=161187,Text='我以为我们冒险者都挺关注血糖的！',Speaker=891189,Emoji=37,}",
  [161188] = "{id=161188,Text='……太可惜了，找谁好呢。',Speaker=891189,Option='……',}",
  [161189] = "{id=161189,Text='是这样的。一只叫Miao先森的小猫带着一位女孩来到异世界，他们还带来各种方便又美味的异界食品，深受冒险者的喜爱。',Speaker=891189,}",
  [161190] = "{id=161190,Text='于是，Miao先森就计划在罗森开一种叫“冒险驿站”的商店，让冒险者们歇脚、购物和……吃东西。',Speaker=891189,}",
  [161191] = "{id=161191,Text='好吃当然要去吃！为了能够第一时间研究菜品，我应聘当上了罗森店员，结果……',Speaker=891189,Option='怎么了？',}",
  [161192] = "{id=161192,Text='早餐被某个神秘小偷抢走了。',Speaker=891189,Option='小偷？',}",
  [161193] = "{id=161193,Text='是呀。这世道……连早餐都要偷！',Speaker=891189,}",
  [161194] = "{id=161194,Text='他还偷了从冰面包到双皮奶的全部便当，这已经不能用大胃王或有钱人来形容了。',Speaker=891189,}",
  [161195] = "{id=161195,Text='虽然……虽然他付了钱，严格上说不算小偷？',Speaker=891189,Option='唔',}",
  [161196] = "{id=161196,Text='但是呀，让我和其他冒险者们在喜庆的开店日白白扑空，这就是犯罪。',Speaker=891189,Option='你说是就是吧',Emoji=12,}",
  [161197] = "{id=161197,Text='那么我代表全体顾客……呃，没买到早餐的顾客……现在就把你传送到艾尔帕兰。',Speaker=891189,Option='{等等,6},{我准备好了,5}',}",
  [161198] = "{id=161198,Text='非常好，请你将“抢购侠”的身份查清并公诸于世！',Speaker=891189,Option='好的！',}",
  [161199] = "{id=161199,Text='哎呀，我的低血糖快复发了。你可得快点做足准备哟！',Speaker=891189,}",
  [161200] = "{id=161200,Text='欢迎光临！',Speaker=891187,}",
  [161201] = "{id=161201,Text='不知为什么，和这个女孩打招呼时，心中响起了自动门的声音和一小段轻快的旋律。',}",
  [161202] = "{id=161202,Text='今天是开店日。免费送藤椒鸡排，来一份吗？',Speaker=891187,Option='{好呀,8},{算了,9}',}",
  [161203] = "{id=161203,Text='顾名思义的藤椒鸡排，香酥鸡皮中掺杂了藤椒的浓郁口感。',}",
  [161204] = "{id=161204,Text='这是我们今天最后一份鸡排了。',Speaker=891187,}",
  [161205] = "{id=161205,Text='今早，有个小哥抢购了所有早餐，这块是我偷偷藏的。',Speaker=891187,Option='他是谁？',}",
  [161206] = "{id=161206,Text='这个呀……这是用户的隐私，我不能说的。',Speaker=891187,}",
  [161207] = "{id=161207,Text='……也不是啦！只是大家好像很生他的气，我想了想，还是不告诉你好一些……',Speaker=891187,Option='也对呀',}",
  [161208] = "{id=161208,Text='得找找别的办法了。',}",
  [161209] = "{id=161209,Text='观察了下四周，不远处有一张长长的小票。',}",
  [161210] = "{id=161210,Text='小票上印了行显然是化名的名字：方便面先生。',}",
  [161211] = "{id=161211,Text='但是抬头数字的开头是卡普拉的组织编号。',}",
  [161212] = "{id=161212,Text='也许可以去卡普拉总部前，找相关人员问一问。',}",
  [161213] = "{id=161213,Text='外卖部最近业务繁忙，往我们服务部门抽调了很多人。搞得我们这边也焦头烂额。',Speaker=891190,}",
  [161214] = "{id=161214,Text='所以，您是要储存呢，还是运输呢？麻烦您交代快点。',Speaker=891190,Option='这个给你',}",
  [161215] = "{id=161215,Text='这张是……',Speaker=891190,Option='认识这串代码吗？',}",
  [161216] = "{id=161216,Text='谢谢你问我业务以外的问题，但我对数字不敏感，也没带公司的相关资料，不一定能想起来。',Speaker=891190,Option='没关系',}",
  [161217] = "{id=161217,Text='那我看看呀。唔，等等……',Speaker=891190,}",
  [161218] = "{id=161218,Text='你问对了！我对这编号……不，小票上的化名有印象……！',Speaker=891190,}",
  [161219] = "{id=161219,Text='是外卖部部长！奇斯介！',Speaker=891190,}",
  [161220] = "{id=161220,Text='我记得，他与罗森合作开冒险驿站时就爱用这个代号。',Speaker=891190,Option='方便面先生？',}",
  [161221] = "{id=161221,Text='对！方便面作为一种罗森带来的异世界产品，可是那家伙的最爱呢！',Speaker=891190,}",
  [161222] = "{id=161222,Text='……说来，他今天去运河边装货物，好像要走水路寄东西。',Speaker=891190,}",
  [161223] = "{id=161223,Text='你真幸运，不用预约。',Speaker=891190,Option='谢谢',}",
  [161224] = "{id=161224,Text='不过我看到他被冒险者包围了。看来他非~常~地~忙唷。',Speaker=891190,Option='知道了',}",
  [161225] = "{id=161225,Text='奇斯介被没买到早餐的愤怒冒险者们包围了！',}",
  [161226] = "{id=161226,Text='为了跟他说上话，先让冒险者们冷静下来吧。',}",
  [161227] = "{id=161227,Text='这家伙好像就是那个抢购侠“方便面先生”！我们不会善罢甘休的！',Speaker=891191,Option='我来替你们出气',}",
  [161228] = "{id=161228,Text='……好吧，我们相信[c][ffff00][PlayerName][-][/c]你的判断。',Speaker=891191,}",
  [161229] = "{id=161229,Text='只不过把所有早餐买走了，怎么这群冒险者个个要追杀我似的！',Speaker=891184,Option='谁叫你在开店日……',}",
  [161230] = "{id=161230,Text='等等，你是那个[c][ffff00][PlayerName][-][/c]？这帮冒险者的代表居然要来教训我了……',Speaker=891184,Option='{我不是,0},{我没有,0}',}",
  [161231] = "{id=161231,Text='不要动手啊！',Speaker=891184,}",
  [161232] = "{id=161232,Text='我没有用传送，而是走水路运输这种可疑的方式……',Speaker=891184,}",
  [161233] = "{id=161233,Text='加上今早，我利用钥匙潜入店面抢购早餐……',Speaker=891184,}",
  [161234] = "{id=161234,Text='这些我都是为了我妹妹……',Speaker=891184,Option='你妹妹？',}",
  [161236] = "{id=161236,Text='我抢购早餐，不是为了填饱肚子，而是收集试吃样品，用来寄给诸国的分部部长和各大家族……',Speaker=891184,}",
  [161237] = "{id=161237,Text='至于我妹妹……她和你一样，是个云游四方的冒险者。',Speaker=891184,}",
  [161238] = "{id=161238,Text='但她一次在里希塔乐食物中毒后，患上了厌食症。',Speaker=891184,}",
  [161239] = "{id=161239,Text='健康时的她很喜欢冰凉的糕点，也对异世界的罗森食品充满期待。',Speaker=891184,}",
  [161240] = "{id=161240,Text='所以我想尽快让罗森开遍米德加尔特，情急之下，就把所有早餐都买来充做样品了……',Speaker=891184,Option='我觉得……',}",
  [161241] = "{id=161241,Text='什么？',Speaker=891184,Option='{叫你妹妹过来,0},{亲自照顾最好,0}',}",
  [161242] = "{id=161242,Text='对，你说的太对了！就是这样！',Speaker=891184,}",
  [161243] = "{id=161243,Text='……谢谢……！',Speaker=891184,Option='谢什么？',}",
  [161244] = "{id=161244,Text='谢谢你说出了我一直没有勇气做的事。',Speaker=891184,}",
  [161245] = "{id=161245,Text='生活节奏太快，连我都想找捷径……明明多陪陪她才是正确做法。',Speaker=891184,}",
  [161246] = "{id=161246,Text='那好，我马上写封信叫她来艾尔帕兰，让她回乡修养一阵子。',Speaker=891184,Option='嗯，你加油',}",
  [161247] = "{id=161247,Text='不过……我希望你也来。',Speaker=891184,Option='我来干什么？',}",
  [161248] = "{id=161248,Text='给我当保镖。不然那些没吃到早餐的冒险者再逮住我，我可会名誉扫地的。',Speaker=891184,Option='好吧好吧',}",
  [161249] = "{id=161249,Text='谢谢你，我回去处理工作啦！',Speaker=891184,}",
  [161250] = "{id=161250,Text='我妹妹已经从修发兹来到艾尔帕兰了，[c][ffff00][PlayerName][-][/c]。她的厌食症真的很严重。',Speaker=891184,}",
  [161251] = "{id=161251,Text='如你所见，罗森在举办香芋、巧克力、草莓、流心蛋黄芝士、白桃乌龙五种口味的冰皮月亮蛋糕试吃会。',Speaker=891184,}",
  [161252] = "{id=161252,Text='月亮蛋糕可是超受好评的！人类对食物的感觉存在公约数，我想她会喜欢的……',Speaker=891184,}",
  [161253] = "{id=161253,Text='不说了，试吃会快开始了，我们先去卡普拉总部门口接娜塔，再回罗森。',Speaker=891184,Option='好的',}",
  [161254] = "{id=161254,Text='吃……',Speaker=891185,}",
  [161255] = "{id=161255,Text='不吃！',Speaker=891185,}",
  [161256] = "{id=161256,Text='我可怜的娜塔……居然饿到自己和自己猜拳。',Speaker=891184,Option='{都饿晕了,0},{都饿瘦了,0},{都饿青了,0}',}",
  [161257] = "{id=161257,Text='啊，你们来了……',Speaker=891185,}",
  [161258] = "{id=161258,Text='抱歉，哥哥，你给我的藤椒鸡排我啃不动。',Speaker=891185,}",
  [161259] = "{id=161259,Text='别灰心，可能是因为你吃不下辣的。',Speaker=891184,Emoji=40,}",
  [161260] = "{id=161260,Text='记得吗？你以前最爱吃甜食，我们现在就去罗森试吃会。',Speaker=891184,}",
  [161261] = "{id=161261,Text='有什么……甜食呢？',Speaker=891185,}",
  [161262] = "{id=161262,Text='冰皮月亮蛋糕！',Speaker=891184,}",
  [161263] = "{id=161263,Text='噢噢……冰、凉的……',Speaker=891185,}",
  [161264] = "{id=161264,Text='不行，我流不出一滴口水。',Speaker=891185,}",
  [161265] = "{id=161265,Text='没关系，等你看到大家喜爱的样子，你也会找回品尝美味的能力。',Speaker=891184,Emoji=20,}",
  [161266] = "{id=161266,Text='对吧，[c][ffff00][PlayerName][-][/c]!',Speaker=891184,Option='对',}",
  [161267] = "{id=161267,Text='大家，试吃会已经开始了哦！这次的主角是我们的网红经典“冰皮月亮蛋糕”！',Speaker=891187,}",
  [161268] = "{id=161268,Text='通过共三题的小问答环节，就能获得试吃品。\n 准备好了吗？',Speaker=891187,Option='奇斯介？',}",
  [161269] = "{id=161269,Text='哎哟……我的脑子全用来给卡普拉外卖小哥们出章程了，思绪非常混乱。',Speaker=891184,}",
  [161270] = "{id=161270,Text='请你来吧，[c][ffff00][PlayerName][-][/c]！',Speaker=891184,Option='好',}",
  [161271] = "{id=161271,Text='嘿嘿，请听题！——第一题，试吃会上的冰皮月亮蛋糕一共有几种口味？',Speaker=891187,Option='{五种,2},{八种,3},{三种,3}',}",
  [161272] = "{id=161272,Text='不对呀……请注意题干部分：是“摆在试吃会上的口味数量”哦！',Speaker=891187,Option='馋了',Emoji=31,}",
  [161273] = "{id=161273,Text='嗯嗯，草莓、白桃乌龙、巧克力、香芋、流心蛋黄芝士……入口即化，香软无比。',Speaker=891187,Emoji=32,}",
  [161274] = "{id=161274,Text='还有三种：芒果、咸蛋黄、梅子，将在今后推出！',Speaker=891187,}",
  [161275] = "{id=161275,Text='那么，引出第二题！这道题……和亿亿酱我有关哟。有点害羞呢。',Speaker=891187,}",
  [161276] = "{id=161276,Text='其实我也和大家一样喜欢冰皮月亮蛋糕，',Speaker=891187,}",
  [161277] = "{id=161277,Text='但我最偏爱的还是一种水果口味。大家猜猜是什么口味呢？',Speaker=891187,}",
  [161278] = "{id=161278,Text='这个我知道！',Speaker=891184,}",
  [161279] = "{id=161279,Text='不能求助场外嘉宾哟！',Speaker=891187,}",
  [161280] = "{id=161280,Text='………那只能给你默默加油了，[c][ffff00][PlayerName][-][/c]。',Speaker=891184,Option='谢谢',}",
  [161281] = "{id=161281,Text='亿亿酱最喜欢哪种水果口味？也许可以从[c][ffff00]亿亿酱对某种蛋糕的反应[-][/c]看出来……',}",
  [161282] = "{id=161282,Text='虽然没有展示芒果和梅子，但从亿亿酱的反应来看，她最喜欢的口味就在现场蛋糕之中。',}",
  [161283] = "{id=161283,Text='请问一下……大家看出来了吗？我最喜欢吃的是哪种水果口味的蛋糕呢？',Speaker=891187,Option='{芒果,5},{白桃乌龙,5},{梅子,5}{草莓,6}',}",
  [161284] = "{id=161284,Text='虽然这几种口味各有千秋，但人家是个女孩子，最喜欢的还是那·一·种口味吧！',Speaker=891187,Emoji=31,}",
  [161285] = "{id=161285,Text='是的，整颗草莓的汁水和糖分都很多。',Speaker=891187,}",
  [161286] = "{id=161286,Text='草莓是我最爱的口味，但可能马上要变成第二名了。',Speaker=891187,Option='为什么？',}",
  [161287] = "{id=161287,Text='因为我们正要从天水之国进口材料，推出一款米德加尔特特供的新口味！',Speaker=891187,}",
  [161288] = "{id=161288,Text='于是来到最后一题！与天津町合作的新款冰皮月亮蛋糕是什么口味呢？',Speaker=891187,Option='{樱花味儿,8},{狐狸味儿,9},{芥末味儿,9}',}",
  [161289] = "{id=161289,Text='哇！不像前两道题目，这道是填空题吧。你的想象力还真猎奇……不，我是说，很丰富，呵呵。',Speaker=891187,Option='我再想想',Emoji=18,}",
  [161290] = "{id=161290,Text='嘿嘿没错，那就是樱花蛋糕的\"冰皮月亮蛋糕\"版本，敬请期待！',Speaker=891187,Emoji=20,}",
  [161291] = "{id=161291,Text='恭喜你全部通过，现在你能领取流心蛋黄芝士味的冰皮月亮蛋糕了！',Speaker=891187,}",
  [161292] = "{id=161292,Text='娜塔！你闻到这股远远就弥漫过来的香味了吗！',Speaker=891184,}",
  [161293] = "{id=161293,Text='面皮薄薄的……',Speaker=891185,}",
  [161294] = "{id=161294,Text='[c][ffff00][PlayerName][-][/c]，可以让我看看……里面的馅儿吗？',Speaker=891185,}",
  [161295] = "{id=161295,Text='冒险者，来，切开它，让流心汹涌而出！',Speaker=891184,Option='好！',}",
  [161296] = "{id=161296,Text='轻轻用餐刀分开薄皮，黄油油的芝士浓郁流出……',}",
  [161297] = "{id=161297,Text='哎呀，我等不及了，冒险者，也分我一块呀！',Speaker=891184,Emoji=28,}",
  [161298] = "{id=161298,Text='最后切成了三份，剖面金白橙颜色相间，看上去分明诱人。',}",
  [161299] = "{id=161299,Text='来，娜塔，尝一口！这流心蛋黄挺软的，小心弄脏领子！',Speaker=891184,}",
  [161300] = "{id=161300,Text='太好吃了！要不是我是个有身份的人，我都想舔手指上的芝士了！',Speaker=891184,Option='我也是',}",
  [161301] = "{id=161301,Text='那我也……从面皮咬下去吧。',Speaker=891185,Option='嗯，口感很好',}",
  [161302] = "{id=161302,Text='娜塔……你还好吗！',Speaker=891184,}",
  [161303] = "{id=161303,Text='唔哦哦……',Speaker=891185,Emoji=24,}",
  [161304] = "{id=161304,Text='你居然全吐干净了！这是何等美味，你太浪费了！',Speaker=891184,}",
  [161305] = "{id=161305,Text='饶了我吧。我只要喝水就够了，哥哥。',Speaker=891185,}",
  [161306] = "{id=161306,Text='怎么回事？！……难道说……娜塔你想减肥吗？',Speaker=891184,}",
  [161307] = "{id=161307,Text='还是在故意气我？气我当初反对你离家冒险？',Speaker=891184,Option='奇斯介！',}",
  [161308] = "{id=161308,Text='哥哥，别生气……我是真的、真的吞不下去。',Speaker=891185,}",
  [161309] = "{id=161309,Text='但你刚才看到蛋糕切开的那面，明明露出了一副流口水的表情！',Speaker=891184,}",
  [161310] = "{id=161310,Text='呜呜……',Speaker=891185,Option='好了啦',}",
  [161311] = "{id=161311,Text='抱歉，是我太急了……',Speaker=891184,}",
  [161312] = "{id=161312,Text='哥哥只是想……娜塔你还没遇到合心意的美食。我们再好好看看吧。',Speaker=891184,}",
  [161313] = "{id=161313,Text='[c][ffff00][PlayerName][-][/c]，我们该再见了，我今天带娜塔回去，明天带她散散心。',Speaker=891184,Option='好，交给你了',Emoji=41,}",
  [161314] = "{id=161314,Text='嗨，我今天计划带娜塔去运河边划船。',Speaker=891184,}",
  [161315] = "{id=161315,Text='我翻了书，书上说厌食症的来源大多是心因。',Speaker=891184,}",
  [161316] = "{id=161316,Text='让她游览家乡美景，调养心情，也许能改善她的症状？',Speaker=891184,Option='试一试？',}",
  [161317] = "{id=161317,Text='但是……昨天她吐了冰皮月亮蛋糕，让我这个做哥哥的很不自信。',Speaker=891184,}",
  [161318] = "{id=161318,Text='所以冒险者，你要不花点时间来陪我们一趟？',Speaker=891184,Option='{好,2},{下次吧,3}',}",
  [161319] = "{id=161319,Text='哎呀，娜塔真的吃不下任何东西呀！',Speaker=891184,}",
  [161320] = "{id=161320,Text='走吧，娜塔在河边等着。',Speaker=891184,}",
  [161321] = "{id=161321,Text='……我今天可不能划船了，哥哥。',Speaker=891185,}",
  [161322] = "{id=161322,Text='怎么了？',Speaker=891184,}",
  [161323] = "{id=161323,Text='我肚子很不舒服……',Speaker=891185,Option='怎么不舒服？',}",
  [161324] = "{id=161324,Text='看到水，呃，就反胃……',Speaker=891185,}",
  [161325] = "{id=161325,Text='怎么会！明明你小时候那么善水性，又钓鱼又打水枪的！',Speaker=891184,}",
  [161326] = "{id=161326,Text='连你爱用的子弹元素都是水属性。',Speaker=891184,}",
  [161327] = "{id=161327,Text='子弹……水属性？等一等……',Speaker=891185,}",
  [161328] = "{id=161328,Text='哥哥你知道得太多了吧？',Speaker=891185,Option='怎么回事？',}",
  [161329] = "{id=161329,Text='因为……因为我担心你，有什么不对吗？',Speaker=891184,}",
  [161330] = "{id=161330,Text='我怕你冒险旅程中受伤，就托各服务人员给我报告你的一举一动……',Speaker=891184,}",
  [161331] = "{id=161331,Text='哥哥！你怎么能……',Speaker=891185,Option='别吵架',}",
  [161332] = "{id=161332,Text='抱歉……的确是我比较过分一点？',Speaker=891184,}",
  [161333] = "{id=161333,Text='哼……告诉你吧，我只是……只是因为罗森的食物中毒，对水反胃。',Speaker=891185,}",
  [161334] = "{id=161334,Text='食物中毒？罗森？既然这架劝不动，也许该抽些时间去问亿亿酱。',}",
  [161335] = "{id=161335,Text='欢迎光临！',Speaker=891187,Option='说明情况',}",
  [161336] = "{id=161336,Text='呀，其实，食物中毒不是罗森的责任。',Speaker=891187,}",
  [161337] = "{id=161337,Text='不止冒险驿站，当时，梅尔区的餐馆和家庭菜都出了水质引起的食物中毒问题。',Speaker=891187,}",
  [161338] = "{id=161338,Text='原来……因为里希塔乐上班族很多，Miao先森就把首家冒险驿站的地址定在那里……',Speaker=891187,Option='后来呢？',}",
  [161339] = "{id=161339,Text='后来，街头试喝饮料的顾客身体都出现了不适，那个患上厌食症的冒险者可能也在其中。',Speaker=891187,}",
  [161340] = "{id=161340,Text='再之后，雷根贝勒从水源中查出了不明毒素，目前这件事还在调查当中。',Speaker=891187,Option='明白了',}",
  [161341] = "{id=161341,Text='能帮上你忙就好啦~！不过……',Speaker=891187,Option='怎么了？',}",
  [161342] = "{id=161342,Text='没什么，有机会再说吧~！',Speaker=891187,}",
  [161343] = "{id=161343,Text='哎，娜塔到底闹什么脾气！',Speaker=891184,Option='说明情况',}",
  [161344] = "{id=161344,Text='娜塔……你是因为以前里希塔乐的水源污染，现在才会看到河水就反胃吗？',Speaker=891184,}",
  [161345] = "{id=161345,Text='……没、没错。',Speaker=891185,}",
  [161346] = "{id=161346,Text='我真是有病，像个怕流水的吸血鬼一样……',Speaker=891185,}",
  [161347] = "{id=161347,Text='对不起，娜塔，不要怪自己，有什么就怪我这不讲理的哥哥吧。',Speaker=891184,}",
  [161348] = "{id=161348,Text='哥哥……',Speaker=891185,}",
  [161349] = "{id=161349,Text='对了，你小时候最喜欢吃天津町的凉面了。',Speaker=891184,}",
  [161350] = "{id=161350,Text='我这就去找亿亿酱借调料，用河水给你冲一碗。',Speaker=891184,}",
  [161351] = "{id=161351,Text='嗯！',Speaker=891185,}",
  [161352] = "{id=161352,Text='冒险者，麻烦你帮我打些家乡上游的河水，带到店里来。',Speaker=891184,Option='好',}",
  [161353] = "{id=161353,Text='啊，冒险者~我正用大豆调料疯狂拌面！就差水了，给我端过来！',Speaker=891184,Option='好咧',}",
  [161354] = "{id=161354,Text='看，娜塔。',Speaker=891184,}",
  [161355] = "{id=161355,Text='不像修发兹，不管是普隆德拉北部的大豆、还是艾尔帕兰的河水，都是取自家乡的纯天然原料，清澈又安全，可谓诸神的恩赐~！',Speaker=891184,}",
  [161356] = "{id=161356,Text='对，我也感受到了，哥哥。',Speaker=891185,}",
  [161357] = "{id=161357,Text='记得吗？这是我们兄妹俩夏天最喜欢的食物。我们动筷子一起吃吧。',Speaker=891184,}",
  [161358] = "{id=161358,Text='嗯~！有哥哥在，我一定……一定能吃下去的……',Speaker=891185,}",
  [161359] = "{id=161359,Text='汤真好喝！',Speaker=891185,}",
  [161360] = "{id=161360,Text='是啊，这鲜味儿无可匹敌。',Speaker=891184,}",
  [161361] = "{id=161361,Text='我……',Speaker=891185,}",
  [161362] = "{id=161362,Text='呜呜呜……',Speaker=891185,Emoji=27,}",
  [161363] = "{id=161363,Text='怎么了娜塔！',Speaker=891184,}",
  [161364] = "{id=161364,Text='唔……喔喔喔！',Speaker=891185,}",
  [161365] = "{id=161365,Text='娜塔！',Speaker=891184,}",
  [161366] = "{id=161366,Text='难道运河水也有毒？！',Speaker=891184,}",
  [161367] = "{id=161367,Text='不大可能！水质是经过美食协会严格把关的！',Speaker=891187,}",
  [161368] = "{id=161368,Text='娜塔喝完了汤，却不吃面条。可能面条才是害她晕倒的原因？',Speaker=891187,}",
  [161369] = "{id=161369,Text='娜塔……这哪里是厌食症，这都是惧食症了！不，一定是诅咒！',Speaker=891184,Option='先冷静',}",
  [161370] = "{id=161370,Text='对，先送娜塔去教会解咒！奥丁和医神艾尔呀，请你们保佑娜塔不要出事啊！',Speaker=891184,Emoji=24,}",
  [161371] = "{id=161371,Text='这对兄妹……到底发生了什么事呢？',Speaker=891187,}",
  [161372] = "{id=161372,Text='这样吧，冒险者……如果你有时间，明天来趟店里好吗？\n不是让你照顾生意，我可能有别的事情要告诉你呢！',Speaker=891187,Option='好的',}",
  [161373] = "{id=161373,Text='欢迎光临~今天来买早餐吗？',Speaker=891187,Option='昨天你想说什么？',}",
  [161374] = "{id=161374,Text='噢，想起来了——关于食物中毒的原因，我有一些可能并不准确的猜测……',Speaker=891187,Emoji=18,}",
  [161375] = "{id=161375,Text='——我刚从异世界来到米德加尔特时，同我穿越而来的还有方便面恶魔',Speaker=891187,Option='方便面恶魔？',}",
  [161376] = "{id=161376,Text='它是一块用心险恶的方便面，随快节奏时代而生的邪灵！',Speaker=891187,}",
  [161377] = "{id=161377,Text='简言之，它反营养、反美味、反DIY。',Speaker=891187,}",
  [161378] = "{id=161378,Text='鉴于女孩子的第六感，我怀疑他就是幕后元凶！',Speaker=891187,}",
  [161379] = "{id=161379,Text='不过，不管它做了什么，“见其面则扑杀之”永远是不二之选！',Speaker=891187,Option='好的',Emoji=40,}",
  [161380] = "{id=161380,Text='说起来，奇斯介小哥和他妹妹早上来买早餐了……',Speaker=891187,}",
  [161381] = "{id=161381,Text='那个得了厌食症的女孩……好像有所隐瞒。',Speaker=891187,}",
  [161382] = "{id=161382,Text='但哥哥在她身边，她不大好说呀……',Speaker=891187,Option='对呢',}",
  [161383] = "{id=161383,Text='我看到他们往教堂前方向走了。',Speaker=891187,Option='我这就去',}",
  [161384] = "{id=161384,Text='教会那边揪不出娜塔的问题，[c][ffff00][PlayerName][-][/c]，所以我带娜塔又去划船了。',Speaker=891184,}",
  [161385] = "{id=161385,Text='但这对治好她的厌食症依然不起任何作用。',Speaker=891184,Emoji=42,}",
  [161386] = "{id=161386,Text='娜塔似乎藏了心事，又不敢说。',Option='奇斯介？',}",
  [161387] = "{id=161387,Text='怎么，你想到什么好方法吗？',Speaker=891184,Option='撒谎支开奇斯介',}",
  [161388] = "{id=161388,Text='什么？艾莉森要把我是方便面先生的事情说出去？！',Speaker=891184,}",
  [161389] = "{id=161389,Text='早知道，在她还是我秘书时，我就不要老让她洗咖啡杯了。',Speaker=891184,Option='……',}",
  [161390] = "{id=161390,Text='别说了！——前几天没抢到早餐的冒险家们还记着仇呢，这怎么办？',Speaker=891184,}",
  [161391] = "{id=161391,Text='哥哥，你回总部办公室躲一阵吧。',Speaker=891185,}",
  [161392] = "{id=161392,Text='再说有冒险者陪我呀。',Speaker=891185,}",
  [161393] = "{id=161393,Text='没办法，只能先拜托你照顾好娜塔了，[c][ffff00][PlayerName][-][/c]！',Speaker=891184,Option='好的',}",
  [161394] = "{id=161394,Text='娜塔，保重！',Speaker=891184,}",
  [161395] = "{id=161395,Text='嘻嘻……虽然胃很难受，但哥哥不在了之后，我反而松了一口气。',Speaker=891185,}",
  [161396] = "{id=161396,Text='抱歉，冒险者，还要让你照顾我。',Speaker=891185,Option='没关系',Emoji=16,}",
  [161397] = "{id=161397,Text='呀，说起来……我居然叫你冒险者……',Speaker=891185,}",
  [161398] = "{id=161398,Text='明明我自己也是个冒险者，在艾尔帕兰修养这几天，我都快忘了自己的本职……',Speaker=891185,Option='要不去冒险吧？',}",
  [161399] = "{id=161399,Text='嗯！既然都是冒险者，我们一起去试试身手。',Speaker=891185,}",
  [161400] = "{id=161400,Text='我太饿，走不了多远，我们就近去国境北大战蝗虫吧！',Speaker=891185,Option='{现在就去,2},{再等会儿,3}',}",
  [161401] = "{id=161401,Text='走！',Speaker=891185,}",
  [161402] = "{id=161402,Text='哎……好的，我等着。不过别给我买吃的哦，我担心你白花钱……',Speaker=891185,}",
  [161403] = "{id=161403,Text='我们分头挑战重金属蝗虫，再回去罗森碰头！',Speaker=891185,Option='好耶！',Emoji=25,}",
  [161404] = "{id=161404,Text='去罗森找收获颇丰的娜塔吧。',}",
  [161405] = "{id=161405,Text='[c][ffff00][PlayerName][-][/c]，累了吧？我给你买了照烧鸡排的便当，已经加热过了。',Speaker=891185,Option='谢谢你',Emoji=2,}",
  [161406] = "{id=161406,Text='真羡慕消化食物的感觉~',Speaker=891185,}",
  [161407] = "{id=161407,Text='哎，可现在，就连重金属蝗虫啃艾草也啃得比我有味。',Speaker=891185,Option='问娜塔厌食原因',}",
  [161408] = "{id=161408,Text='……咦？',Speaker=891185,}",
  [161409] = "{id=161409,Text='……你的直觉是对的，[c][ffff00][PlayerName][-][/c]。',Speaker=891185,}",
  [161410] = "{id=161410,Text='我吃不下东西，确实是因为食物中毒。',Speaker=891185,}",
  [161411] = "{id=161411,Text='但是……不是因为食物中毒本身，而是因为、因为……',Speaker=891185,Option='因为什么？',}",
  [161412] = "{id=161412,Text='因为害大家食物中毒的，就是我呀！',Speaker=891185,Option='咦！',}",
  [161413] = "{id=161413,Text='都是我太软弱了，不管在哥哥面前，还是在会说话的方便面前！',Speaker=891185,Option='方便面？！',}",
  [161414] = "{id=161414,Text='等等，我听到了什么？说话的方便面？',Speaker=891187,Emoji=7,}",
  [161415] = "{id=161415,Text='我当时在里希塔乐……',Speaker=891185,}",
  [161416] = "{id=161416,Text='……不经意路过水沟旁边，看到一块会走路的方便面。',Speaker=891185,}",
  [161417] = "{id=161417,Text='然后它……它……！',Speaker=891185,}",
  [161418] = "{id=161418,Text='它怎么了？',Speaker=891187,}",
  [161419] = "{id=161419,Text='它往我这儿笑了笑，随后变出长长的油炸面管，把雷根贝勒的排污管接到了生活用水区！',Speaker=891185,Option='场面太魔幻了',Emoji=15,}",
  [161420] = "{id=161420,Text='居然用这种恶心的变形方式……毫无疑问，娜塔看到的就是方便面恶魔。',Speaker=891187,}",
  [161421] = "{id=161421,Text='那是……恶魔吗？！',Speaker=891185,}",
  [161422] = "{id=161422,Text='不错，它是一种反美味的极恶怪物。',Speaker=891187,}",
  [161423] = "{id=161423,Text='是……吗？',Speaker=891185,}",
  [161424] = "{id=161424,Text='即便面对的是恶魔，作为一名强大的冒险者，我也必须在当时挺身阻止他。',Speaker=891185,}",
  [161425] = "{id=161425,Text='……人们食物中毒，归根结底还是我缺乏勇气的缘故。',Speaker=891185,Option='娜塔，别自责',}",
  [161426] = "{id=161426,Text='关键不在这里。娜塔，请你站好。',Speaker=891187,Emoji=14,}",
  [161427] = "{id=161427,Text='咦？……好。',Speaker=891185,}",
  [161428] = "{id=161428,Text='我看清楚了。恶魔牢牢贴在你的灵魂之上。',Speaker=891187,}",
  [161429] = "{id=161429,Text='它寄宿于你的负面情绪之中，不仅让你拒绝美食，还吸取相关的负面能量，以此变得愈发强大。',Speaker=891187,}",
  [161430] = "{id=161430,Text='这么说，我的厌食症……也是它捣的鬼？',Speaker=891185,Emoji=8,}",
  [161431] = "{id=161431,Text='是的。娜塔，你要赶快驱魔了！',Speaker=891187,Option='怎么做？',}",
  [161432] = "{id=161432,Text='战胜恐惧最好的方法就是面对恐惧。',Speaker=891187,}",
  [161433] = "{id=161433,Text='不过嘛，听Miao先森说，我们只是需要投入大量材料，进行一场仪式——也就是好好地做道菜而已。',Speaker=891187,Option='看来需要时间',}",
  [161434] = "{id=161434,Text='我懂了，我会认真对待明天的驱魔。',Speaker=891185,Option='我也来帮忙',}",
  [161435] = "{id=161435,Text='谢谢你，但……麻烦再帮我一下。',Speaker=891185,}",
  [161436] = "{id=161436,Text='一切因我而起，所以这是我一人的挑战。请你不要告诉哥哥，好吗？',Speaker=891185,Option='好',}",
  [161437] = "{id=161437,Text='我也会帮你保密的。',Speaker=891187,Emoji=20,}",
  [161438] = "{id=161438,Text='谢谢你们！',Speaker=891185,Option='不用',}",
  [161439] = "{id=161439,Text='那么……我今天先回家整理一下思绪了，再见。',Speaker=891185,}",
  [161440] = "{id=161440,Text='嗯，我也要给Miao先森申请，借用一下店里的锅碗瓢盆和烤架了！',Speaker=891187,}",
  [161441] = "{id=161441,Text='[c][ffff00][PlayerName][-][/c]，我准备好驱魔仪式了。',Speaker=891186,}",
  [161442] = "{id=161442,Text='看~亿亿酱贴心地帮我买了材料。',Speaker=891186,}",
  [161443] = "{id=161443,Text='我还带了铲子和锅！',Speaker=891186,Option='很正式呀',Emoji=25,}",
  [161444] = "{id=161444,Text='我们开始驱魔仪式，把方便面恶魔从我体内弄出去吧！',Speaker=891186,Option='{好,2},{等一会儿,3}',}",
  [161445] = "{id=161445,Text='还要等吗？好吧，我会控制住这股空腹感，等你回来的。',Speaker=891186,}",
  [161446] = "{id=161446,Text='好的！好久没有做菜了！',Speaker=891186,Emoji=2,}",
  [161447] = "{id=161447,Text='[c][ffff00][PlayerName][-][/c]和娜塔都准时到了。',Speaker=891187,}",
  [161448] = "{id=161448,Text='亿亿酱，只用制作一道料理就够了吗？',Speaker=891186,}",
  [161449] = "{id=161449,Text='没错，驱逐方便面这种恶魔，得让宿主沉下心来，用心准备一道美食。',Speaker=891187,}",
  [161450] = "{id=161450,Text='保险起见，我给娜塔选了一道好上手的料理：葱油拌面，以便更顺利地举行驱魔仪式。',Speaker=891187,Emoji=26,}",
  [161451] = "{id=161451,Text='我们用到的材料有：面条、葱花、香油……',Speaker=891187,}",
  [161452] = "{id=161452,Text='……你还让我借用了店里的设备，谢谢你为我做了那么多，亿亿酱。',Speaker=891186,Emoji=6,}",
  [161453] = "{id=161453,Text='小事。',Speaker=891187,}",
  [161454] = "{id=161454,Text='不过，驱魔仪式有风险。[c][ffff00][PlayerName][-][/c]，你要保护好娜塔！',Speaker=891187,Option='我会守护好她的',}",
  [161455] = "{id=161455,Text='我会展现冒险者应有的手艺！',Speaker=891186,Option='加油，娜塔！',}",
  [161456] = "{id=161456,Text='呀，下意识多倒了点香油。',Speaker=891186,Option='{不好意思,0},{不打扰你了,0}',}",
  [161457] = "{id=161457,Text='是我不够专注，我再加把劲！',Speaker=891186,Emoji=10,}",
  [161458] = "{id=161458,Text='等等，娜塔……有股恶臭的魔力波动！',Speaker=891187,}",
  [161459] = "{id=161459,Text='方便面恶魔现形了！——记得我说的吗？见其“面”则扑杀之！',Speaker=891187,Option='我上了！',}",
  [161460] = "{id=161460,Text='啧啧啧，我只不过在陪你们玩玩。仅仅打趴我，不代表你们能驱逐我。',Speaker=891188,Emoji=20,}",
  [161461] = "{id=161461,Text='果然很恶心……',Speaker=891186,}",
  [161462] = "{id=161462,Text='一股比地沟油还难闻的恶臭熏起来了！',Speaker=891187,}",
  [161463] = "{id=161463,Text='谢谢夸奖。但是啊！',Speaker=891188,}",
  [161464] = "{id=161464,Text='娜塔，你就得敞开你那可爱的胃，吞下这样恶臭的我哦~！',Speaker=891188,}",
  [161465] = "{id=161465,Text='你不是自诩厨师的冒险者吗？那为什么对自己做的东西一点都不心动？',Speaker=891188,Emoji=5,}",
  [161466] = "{id=161466,Text='内心的软弱让你停留在区区厌食症上。就算一时削弱我的力量，我也会从你身上重生。',Speaker=891188,}",
  [161467] = "{id=161467,Text='毕竟我的存在，我的一举一动，我的终极目的，就是要让人们对垃圾食品无法自拔！哈哈哈哈哈！',Speaker=891188,}",
  [161468] = "{id=161468,Text='别被他糊弄了，娜塔！拍倒他，把他做成方便面料理，我相信你能行！',Speaker=891187,Option='我也相信',Emoji=18,}",
  [161469] = "{id=161469,Text='对不起，亿亿酱、[c][ffff00][PlayerName][-][/c]……',Speaker=891186,Emoji=42,}",
  [161470] = "{id=161470,Text='一想到，月亮蛋糕我都吞不进去。对这面条……我就更不行……',Speaker=891186,Option='娜塔？',}",
  [161471] = "{id=161471,Text='别灰心，娜塔！',Speaker=891184,}",
  [161472] = "{id=161472,Text='——哥哥？！',Speaker=891186,}",
  [161473] = "{id=161473,Text='不好意思，昨天我没回办公室，放心不下就跟踪了你们。',Speaker=891184,}",
  [161474] = "{id=161474,Text='对不起娜塔，我又一次擅自介入了。',Speaker=891184,}",
  [161475] = "{id=161475,Text='不，哥哥 ，这次你很勇敢，毕竟你冒着被其他冒险家追杀的风险啊。',Speaker=891186,Option='噗',}",
  [161476] = "{id=161476,Text='打扰一下，能从感人的重逢里抽出空来，听我一言吗，方便面先生？',Speaker=891188,Emoji=14,}",
  [161477] = "{id=161477,Text='作为嗜好方便面的外卖部长，你不才最该赏识垃圾食品吗？',Speaker=891188,}",
  [161478] = "{id=161478,Text='速食、无营养、不卫生才是大势所趋、利润的根本、人类的未来！',Speaker=891188,}",
  [161479] = "{id=161479,Text='错了，错了，大错特错啊！你这小麦蠕虫！',Speaker=891184,Emoji=12,}",
  [161480] = "{id=161480,Text='我——是为了把罗森开遍王国，才努力当上外卖部长的！',Speaker=891184,}",
  [161481] = "{id=161481,Text='你说什么？',Speaker=891188,}",
  [161482] = "{id=161482,Text='不管是外卖，还是便利店的包装早餐，都必须经过精心烹制，安全达标，以美味打动人心。',Speaker=891184,}",
  [161483] = "{id=161483,Text='而我认为，以上三条，罗森皆能做到。',Speaker=891184,}",
  [161484] = "{id=161484,Text='为了帮上亿亿酱，为了将罗森驿站开遍王国，我才有闲心去当什么外卖部长。',Speaker=891184,Emoji=20,}",
  [161485] = "{id=161485,Text='没错，非常感谢你的大力支持，奇斯介小哥哥~',Speaker=891187,}",
  [161486] = "{id=161486,Text='而且——层级调动使我离开了暗恋的秘书艾莉森！',Speaker=891184,}",
  [161487] = "{id=161487,Text='我来到了这里，而她回到了服务人员的岗位上，我还想有机会见见她啊。',Speaker=891184,Option='喂，说太多啦',}",
  [161488] = "{id=161488,Text='啊，不知不觉！不过，咳，我还要点题……',Speaker=891184,}",
  [161489] = "{id=161489,Text='——我做这一切的一切，只有一个目的：让冒险者们在各个冒险驿站吃到美味！',Speaker=891184,}",
  [161490] = "{id=161490,Text='哥哥，原来你怀着和我一样的梦想。',Speaker=891186,}",
  [161491] = "{id=161491,Text='什么？你们到底在说什么？只有我听不懂吗！',Speaker=891188,Emoji=1,}",
  [161492] = "{id=161492,Text='不，我是说……只凭理想，你们是没法把我做成美味，娜塔也是没法吃下我的！',Speaker=891188,}",
  [161493] = "{id=161493,Text='不好意思，亿亿酱送我的调料包和干葱包还留着呢~！有了它，葱油拌面的滋味儿会更上一层楼！',Speaker=891184,}",
  [161494] = "{id=161494,Text='——娜塔，接住！',Speaker=891184,}",
  [161495] = "{id=161495,Text='好的！哥哥！',Speaker=891186,}",
  [161496] = "{id=161496,Text='快帮我把那块恶魔端过来，冒险者！',Speaker=891186,}",
  [161497] = "{id=161497,Text='等等……这可就……大事不妙了。',Speaker=891188,}",
  [161498] = "{id=161498,Text='[c][ffff00][PlayerName][-][/c]，你不要过来啊！我不要被吃掉！',Speaker=891188,Option='不行哟',Emoji=22,}",
  [161499] = "{id=161499,Text='——看我的！',Speaker=891186,}",
  [161500] = "{id=161500,Text='你成功了，娜塔！这就是你和你哥哥梦想的力量吧！',Speaker=891187,}",
  [161501] = "{id=161501,Text='现在，亲口品尝你的作品吧。',Speaker=891184,}",
  [161502] = "{id=161502,Text='嗯嗯，我要汤汁连带面条，行云流水地唑下去。',Speaker=891186,}",
  [161503] = "{id=161503,Text='太棒了，方便面恶魔终于被驱除了！',Speaker=891187,}",
  [161504] = "{id=161504,Text='我……自由了！我能吃下东西了，哥哥！',Speaker=891186,}",
  [161505] = "{id=161505,Text='是呀，娜塔！你非常勇敢！',Speaker=891184,Emoji=30,}",
  [161506] = "{id=161506,Text='亿亿酱，让我再告诉你件好事！',Speaker=891184,}",
  [161507] = "{id=161507,Text='各分部部长和贵族都对你们的自制料理评赞有加！',Speaker=891184,}",
  [161508] = "{id=161508,Text='谢谢！加上方便面恶魔的消失……',Speaker=891187,}",
  [161509] = "{id=161509,Text='罗森和卡普拉的合作计划也能走上正轨了！我会把这个好消息告诉Miao先森的！',Speaker=891187,Option='{能问一下,0},{是什么计划吗？,0}',}",
  [161510] = "{id=161510,Text='我们要以艾尔帕兰为中心，通过卡普拉魔法传送网，将美食从各地的冒险驿站带给你们。',Speaker=891184,}",
  [161511] = "{id=161511,Text='一切开展顺利，得多亏我对娜塔的爱啊！',Speaker=891184,}",
  [161512] = "{id=161512,Text='得了吧，哥哥！别忘了[c][ffff00][PlayerName][-][/c]！',Speaker=891186,}",
  [161513] = "{id=161513,Text='没错，还少不了你和[c][ffff00][PlayerName][-][/c]勇敢又乐于助人的冒险精神！',Speaker=891184,Option='是的',}",
  [161514] = "{id=161514,Text='那么我要赶紧回归工作，将开张罗森的计划推进下去！',Speaker=891184,Option='祝你顺利',Emoji=20,}",
  [161515] = "{id=161515,Text='Miao先森也在叫我回去站岗了！以后常来呀，[c][ffff00][PlayerName][-][/c]！',Speaker=891187,Option='好~！',Emoji=6,}",
  [161517] = "{id=161517,Text='真恶心',Speaker=891186,}",
  [161518] = "{id=161518,Text='它现出混沌一团的原形了！',Speaker=891187,}",
  [161519] = "{id=161519,Text='呵呵……',Speaker=891184,}",
  [161523] = "{id=161523,Text='嘿，有缘人，快过来看看！',Speaker=2620,}",
  [161524] = "{id=161524,Text='我这新增了「咒言护手[1]」的制作配方，很适合你哟~',Speaker=2620,}",
  [161549] = "{id=161549,Text='一块公告版，上面写着“斐扬的请求”。',Speaker=892838,Option='斐扬的请求',}",
  [161550] = "{id=161550,Text='看上去是来自斐扬村落的公告板，上面贴着条奇怪的告示。',Speaker=892838,}",
  [161551] = "{id=161551,Text='告示上的请求下笔很重，像是在很生气的情况下写出来的。',Speaker=892838,Option='看下开头',}",
  [161552] = "{id=161552,Text='勇敢的旅行者，斐扬有事拜托你，现在有空吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=892838,Option='{有空,3},{下次再说,2}',}",
  [161553] = "{id=161553,Text='有只名叫路奇的年兽，前几日袭击了斐扬的小朋友。',Speaker=892838,}",
  [161554] = "{id=161554,Text='请帮助村民抓到伤害斐扬的罪魁祸首吧！',Speaker=892838,}",
  [161555] = "{id=161555,Text='这是村民提供的线索：',Speaker=892838,Emoji=20,}",
  [161556] = "{id=161556,Text='路奇从斐扬附近逃离，消失在普隆德拉南门。',Speaker=892838,}",
  [161557] = "{id=161557,Text='冒险者请出发前往[c][ffff00]普隆德拉南门[-][/c]吧！',Speaker=892838,Option='好的',}",
  [161558] = "{id=161558,Text='嘿，那个瞎转悠的！',Speaker=892817,Option='你喊我？',Emoji=5,}",
  [161559] = "{id=161559,Text='不然呢？这附近除了我和你还有别人吗？',Speaker=892817,Action={actionid=215,num=1},}",
  [161560] = "{id=161560,Text='我来猜猜，你也是来找路奇的是不是？',Speaker=892817,Option='你怎么知道？',}",
  [161561] = "{id=161561,Text='因为我也是！',Speaker=892817,}",
  [161562] = "{id=161562,Text='罗布颇有深意地看着冒险者笑了笑。',Option='你笑什么？',Emoji=5,}",
  [161563] = "{id=161563,Text='笑你什么都没有就想抓年兽。',Speaker=892817,Action={actionid=215,num=1},}",
  [161564] = "{id=161564,Text='喂，听我的，你去林子里找来个波波利饼干盒，只要有这个，路奇百分百会出现。',Speaker=892817,}",
  [161565] = "{id=161565,Text='在哪里打还要问我嘛？波波利饼干盒欸，当然就在[c][ffff00]波波利[-][/c]身上咯！',Speaker=892817,Option='为什么告诉我？',}",
  [161566] = "{id=161566,Text='这不是很明显嘛，我脚受伤了，只能把这个秘籍传授你，你可是要行大礼感谢我才是！',Speaker=892817,Option='……',Emoji=40,}",
  [161567] = "{id=161567,Text='啊不要你管！你只管去抓路奇就是了，哼！',Speaker=892817,}",
  [161568] = "{id=161568,Text='还真的有波波利饼干盒，捡起来看看。',}",
  [161569] = "{id=161569,Text='哦哟，看不出来你还挺有能耐。',Speaker=892817,}",
  [161570] = "{id=161570,Text='干得不错冒险者，看来你是很希望[c][ffff00]立刻！马上！抓到路奇呢哼哼！[-][/c]',Speaker=892817,Option='路奇咬伤小孩子',}",
  [161571] = "{id=161571,Text='呸！咳咳，我是说，没错！',Speaker=892817,Emoji=40,}",
  [161572] = "{id=161572,Text='接下来赶快把波波利饼干盒放到地上吧。',Speaker=892817,}",
  [161573] = "{id=161573,Text='哈哈哈，成功了！',Speaker=892817,Option='怎么回事！！！',Emoji=5,}",
  [161574] = "{id=161574,Text='我终于能让大嘴鸟带我离开这个鬼地方了！',Speaker=892817,}",
  [161575] = "{id=161575,Text='之前被村民打伤，哪里都去不了。',Speaker=892817,}",
  [161576] = "{id=161576,Text='所以你是……',Option='{赏金猎人,6},{斐扬村民,6},{路奇,7}',}",
  [161577] = "{id=161577,Text='哼，我都说得这么明白了你还猜不出哦？',Speaker=892817,}",
  [161578] = "{id=161578,Text='没错！',Speaker=892817,}",
  [161579] = "{id=161579,Text='想抓我，门儿也没有！',Speaker=892816,Option='你要去道歉！',}",
  [161580] = "{id=161580,Text='哼，是他们要向我道歉才是。',Speaker=892816,Emoji=40,}",
  [161581] = "{id=161581,Text='无凭无据就说我咬伤小孩子，再也不要和他们做朋友了！',Speaker=892816,}",
  [161582] = "{id=161582,Text='这么一说，你也是帮助他们的坏人！',Speaker=892816,}",
  [161583] = "{id=161583,Text='我要惩罚你！',Speaker=892816,Emoji=5,}",
  [161584] = "{id=161584,Text='哈哈，真好玩！',Speaker=892816,}",
  [161585] = "{id=161585,Text='你就留在这里，等着村民来抓你吧。',Speaker=892816,}",
  [161586] = "{id=161586,Text='是路奇！快抓住他！',Speaker=892820,}",
  [161587] = "{id=161587,Text='要被误认为是路奇了，现在唯一能做的就是……',Option='{躲起来,10},{等被抓,9}',}",
  [161588] = "{id=161588,Text='这或许不是个好主意。',}",
  [161589] = "{id=161589,Text='快来！来我这儿！',Speaker=892815,}",
  [161590] = "{id=161590,Text='冒险者你好……咦，你脸上怎么那么多灰尘？',Speaker=892815,Option='路奇害的！',}",
  [161591] = "{id=161591,Text='你见到路奇了？！！',Speaker=892815,Option='交代情况',Emoji=7,}",
  [161593] = "{id=161593,Text='不过冒险者，我正好有事相求。',Speaker=892815,}",
  [161594] = "{id=161594,Text='是关于路奇的，希望你能帮我救他。',Speaker=892815,Option='为什么？',}",
  [161595] = "{id=161595,Text='哎，一言难尽。冒险者，如果你愿意，明天可以来斐扬找我嘛？',Speaker=892815,}",
  [161596] = "{id=161596,Text='其实路奇咬伤孩子这件事，根本不是你之前了解到的那个样子。',Speaker=892815,Option='{好的,15},{我再想想,14}',Emoji=24,}",
  [161597] = "{id=161597,Text='太好了，请收下这份谢礼，我们明天见。',Speaker=892815,Action={actionid=200,num=1},}",
  [161598] = "{id=161598,Text='拜托你，感觉只有你能帮助我和路奇。',Speaker=892815,Action={actionid=200,num=1},}",
  [161599] = "{id=161599,Text='[c][ffff00][PlayerName][-][/c]，谢谢你能来。',Speaker=892815,Option='到底怎么了？',}",
  [161600] = "{id=161600,Text='我也想好好讲给你听，但在那之前，能拜托你帮我收集来3个木叶灵嘛？',Speaker=892815,}",
  [161601] = "{id=161601,Text='路奇身上的伤很重，我需要一味名叫木叶灵的草药来做药剂。',Speaker=892815,Option='为什么要帮他？',}",
  [161602] = "{id=161602,Text='他是我最重要的朋友，更是一个好孩子。',Speaker=892815,}",
  [161603] = "{id=161603,Text='虽然我还没有确凿的证据，但我不相信路奇是伤害孩子的凶手。',Speaker=892815,}",
  [161604] = "{id=161604,Text='冒险者，你愿意帮助我吗？',Speaker=892815,Option='{愿意,3},{现在有点忙,10}',}",
  [161605] = "{id=161605,Text='谢谢你[c][ffff00][PlayerName][-][/c]，我就在这里等你。',Speaker=892815,Action={actionid=200,num=1},}",
  [161606] = "{id=161606,Text='希望你能给路奇一个机会，我会努力证明他是无辜的！',Speaker=892815,}",
  [161607] = "{id=161607,Text='太感谢了，这样路奇的伤很快就会好。',Speaker=892815,Option='到底发生了什么',Action={actionid=200,num=1},}",
  [161608] = "{id=161608,Text='路奇是我的好朋友，但大人不喜欢年兽，路奇就变成人的样子来找我玩。',Speaker=892815,}",
  [161609] = "{id=161609,Text='路奇性格很好，大家都很喜欢他……除了，有点爱做恶作剧。',Speaker=892815,Option='感受到了',}",
  [161610] = "{id=161610,Text='但路奇不是坏人！',Speaker=892815,}",
  [161611] = "{id=161611,Text='……就在前几天，村民说路奇咬伤了两个孩子。',Speaker=892815,}",
  [161612] = "{id=161612,Text='我不相信，但等我到现场的时候，路奇已经变回了年兽的样子，两个孩子狂哭不止。',Speaker=892815,}",
  [161613] = "{id=161613,Text='生气的大人们合力把路奇赶了出去。',Speaker=892815,Emoji=42,}",
  [161614] = "{id=161614,Text='虽然我不断强调路奇身上也有伤，坏人一定是别人，但大家都不相信。',Speaker=892815,Option='路奇是有伤',}",
  [161615] = "{id=161615,Text='所以你也相信他是无辜的对不对？',Speaker=892815,Option='{这太牵强了,4},{逃走弄伤的吧,5}',Emoji=26,}",
  [161616] = "{id=161616,Text='证据确实不够充分，所以我会努力找出新证据，还路奇清白！',Speaker=892815,}",
  [161617] = "{id=161617,Text='你也看到那个伤势很严重，不太像是逃走弄伤的。',Speaker=892815,}",
  [161618] = "{id=161618,Text='所以冒险者，我能拜托你帮我把药品带给路奇吗？有了这个他的伤很快就会好。',Speaker=892815,Option='{我可以试试,7},{但不一定成功,8}',}",
  [161619] = "{id=161619,Text='那就辛苦你了！',Speaker=892815,Action={actionid=200,num=1},}",
  [161620] = "{id=161620,Text='告诉他你是我的朋友，就不会有问题的！',Speaker=892815,}",
  [161621] = "{id=161621,Text='怎么又是你？',Speaker=892816,Option='……',Action={actionid=11,num=1},}",
  [161622] = "{id=161622,Text='好啦，我召唤大嘴鸟，就是想让它驮我回斐扬附近，那又怎么样呢？',Speaker=892816,Emoji=40,}",
  [161623] = "{id=161623,Text='……我担心村民会不会因为我的事情伤害米娅！',Speaker=892816,}",
  [161624] = "{id=161624,Text='就想在这里守着……不可以吗？',Speaker=892816,Option='……有点可爱！',}",
  [161625] = "{id=161625,Text='哼！要……要你管！你要干什么？还是要抓我去拿赏金嘛？',Speaker=892816,Option='米娅给你的药',Emoji=40,}",
  [161626] = "{id=161626,Text='哼，我凭什么相信你？',Speaker=892816,Option='解释前因',}",
  [161627] = "{id=161627,Text='就算是这样吧，你也是个想抓到我去拿赏金的坏家伙。',Speaker=892816,}",
  [161628] = "{id=161628,Text='如果你是真的想帮我，就去帮我弄点吃的来吧！',Speaker=892816,Option='波波利饼干？',}",
  [161629] = "{id=161629,Text='波波利饼干是给大嘴鸟吃的！',Speaker=892816,Emoji=40,}",
  [161630] = "{id=161630,Text='既然有人跑腿，那……你去帮我搞来[c][ffff00]4罐大脚熊私藏的蜂蜜吧[-][/c]！',Speaker=892816,Option='什么？',}",
  [161631] = "{id=161631,Text='反悔了是不是？我就知道你根本不是什么好人。',Speaker=892816,Option='我去！',}",
  [161632] = "{id=161632,Text='耶！要当心哦，嘻嘻……',Speaker=892816,}",
  [161633] = "{id=161633,Text='防止大脚熊抢回蜂蜜！',}",
  [161634] = "{id=161634,Text='哎，突然想念米娅用甜甜蜂蜜水给我烧的元宵了。',Speaker=892816,Option='米娅没怪你。',Emoji=24,Action={actionid=84,num=1},}",
  [161635] = "{id=161635,Text='我也从来没有怪过米娅，当大家都说我是坏人的时候，她一个小姑娘，能做的毕竟有限。',Speaker=892816,Option='你还能这么想？',}",
  [161636] = "{id=161636,Text='哼，我可是很成熟的！',Speaker=892816,Emoji=40,}",
  [161637] = "{id=161637,Text='不过，米娅最近在村子里一定也不好过。你说米娅还给我准备了药？',Speaker=892816,}",
  [161638] = "{id=161638,Text='（叼走药瓶）全世界还是只有米娅最好。',Speaker=892816,Action={actionid=84,num=1},}",
  [161639] = "{id=161639,Text='冒险者，愿意接受一个挑战自己极限的任务嘛？',Speaker=892816,Option='让帮忙就直说',}",
  [161640] = "{id=161640,Text='跟你聊天真是没劲，我想拜托你帮我找到真正伤害斐扬孩子的凶手。',Speaker=892816,Emoji=40,}",
  [161641] = "{id=161641,Text='我倒是不介意大家误解我。',Speaker=892816,}",
  [161642] = "{id=161642,Text='但查不出真相的话，他们会觉得米娅是坏孩子吧。',Speaker=892816,}",
  [161643] = "{id=161643,Text='那我可不答应。',Speaker=892816,Option='你脸红了！',}",
  [161644] = "{id=161644,Text='我没有！好啦你快去吧。',Speaker=892816,}",
  [161645] = "{id=161645,Text='哦不过，为了表达感谢……这是我送给你的小礼物，你收下吧。',Speaker=892816,Emoji=5,Action={actionid=11,num=1},}",
  [161646] = "{id=161646,Text='哈哈哈，惊不惊喜意不意外？我等你回来咯！',Speaker=892816,Action={actionid=82,num=1},}",
  [161647] = "{id=161647,Text='路奇可不是一般的爱做恶作剧啊。',}",
  [161648] = "{id=161648,Text='[c][ffff00][PlayerName][-][/c]你回来了，路奇还好嘛？',Speaker=892815,Option='交代情况',}",
  [161649] = "{id=161649,Text='我就说路奇不是坏人!',Speaker=892815,}",
  [161650] = "{id=161650,Text='我也一直想要找到凶手，但该从何开始呢？',Speaker=892815,Option='{去案发现场,3},{去问问村民,2}',}",
  [161651] = "{id=161651,Text='千万别，你也会被赶出村子的。',Speaker=892815,}",
  [161652] = "{id=161652,Text='我明白了，前面就是咬伤事件发生的地方，请认真观察找出蛛丝马迹吧！',Speaker=892815,}",
  [161653] = "{id=161653,Text='这里似乎有魔物打斗过的痕迹。',}",
  [161654] = "{id=161654,Text='回去跟米娅商量下吧。',}",
  [161655] = "{id=161655,Text='这个粘液，莫非是枯树精留下的？',Speaker=892815,Option='枯树精？',Emoji=7,}",
  [161656] = "{id=161656,Text='枯树精表皮受伤的话会流出一种粘液，遇水会膨胀变大。',Speaker=892815,}",
  [161657] = "{id=161657,Text='幸亏这几日下雨，不然这种粘液根本没办法被发现。',Speaker=892815,}",
  [161658] = "{id=161658,Text='所以小孩是枯树精咬伤的？',Emoji=11,}",
  [161659] = "{id=161659,Text='很有可能，我们需要尝试引诱他们出现。',Speaker=892815,}",
  [161660] = "{id=161660,Text='枯树精非常喜欢甜甜圈的味道。',Speaker=892815,}",
  [161661] = "{id=161661,Text='村子里的猪猪身上会带着甜甜圈。',Speaker=892815,}",
  [161662] = "{id=161662,Text='[c][ffff00][PlayerName][-][/c]，能拜托你能帮我找来5个甜甜圈嘛？',Speaker=892815,Option='{愿意,5},{不愿意,4}',}",
  [161663] = "{id=161663,Text='谢谢你[c][ffff00][PlayerName][-][/c]，我就在这里等你回来。',Speaker=892815,}",
  [161664] = "{id=161664,Text='拜托你了[c][ffff00][PlayerName][-][/c]，现在只有你能帮助我和路奇了。',Speaker=892815,}",
  [161665] = "{id=161665,Text='很好，我们现在一起去把甜甜圈放在黏液旁边。',Speaker=892815,}",
  [161666] = "{id=161666,Text='我们先躲在这边等一下。',Speaker=892815,}",
  [161667] = "{id=161667,Text='咕叽咕叽!',Speaker=892824,Emoji=28,}",
  [161668] = "{id=161668,Text='就是他！抓住他就能证明路奇是无辜的！',Speaker=892815,}",
  [161669] = "{id=161669,Text='它速度太快溜掉了!',Speaker=892815,Option='好可惜',Emoji=42,}",
  [161670] = "{id=161670,Text='但这也证明路奇就是被污蔑的！我要把这件事情告诉大家。',Speaker=892815,Option='{但我们没有证据,0},{大家会信吗？,0}',}",
  [161671] = "{id=161671,Text='你说得对。但至少，我们能先去告诉路奇我们的发现。',Speaker=892815,Option='但你被禁足了',}",
  [161672] = "{id=161672,Text='我这几天找到了一个溜出去的好办法……',Speaker=892815,}",
  [161673] = "{id=161673,Text='[c][ffff00][PlayerName][-][/c]请明天在[c][ffff00]斐扬南部河水旁边[-][/c]等我。我们一起去见路奇。',Speaker=892815,}",
  [161674] = "{id=161674,Text='但是在这之前，请帮我拍下粘液的照片，作为证据。',Speaker=892815,Option='好',}",
  [161675] = "{id=161675,Text='太好了，这样就没问题了！谢谢你[c][ffff00][PlayerName][-][/c]，那我们明天见！',Speaker=892815,Action={actionid=201,num=1},}",
  [161676] = "{id=161676,Text='[c][ffff00][PlayerName][-][/c]，你过来这边一切顺利吧？',Speaker=892815,Option='你真跑出来了？',}",
  [161677] = "{id=161677,Text='当然！快带我去见路奇吧，希望它的伤已经好了。',Speaker=892815,}",
  [161678] = "{id=161678,Text='这里怎么会有飞龙？',Speaker=892815,}",
  [161679] = "{id=161679,Text='但前面就是路奇休息的地方，只能硬上了。',Emoji=15,}",
  [161680] = "{id=161680,Text='这些七彩飞龙无法被打死。',}",
  [161681] = "{id=161681,Text='住手!',Speaker=892818,}",
  [161682] = "{id=161682,Text='你们在做什么？',Speaker=892818,Option='{想往前走,0},{没干什么,0}',}",
  [161683] = "{id=161683,Text='！！您难道就是，一直隐居在山林的弗雷尔爷爷！',Speaker=892815,Emoji=42,Action={actionid=200,num=1},}",
  [161684] = "{id=161684,Text='米娅，你这段日子辛苦了。',Speaker=892818,Action={actionid=201,num=1},}",
  [161685] = "{id=161685,Text='[c][ffff00][PlayerName][-][/c]，请允许我为你介绍，这位就是离开斐扬隐居大山的智者弗雷尔爷爷……',Speaker=892815,}",
  [161686] = "{id=161686,Text='如同传说一般聪明智慧的长者。',Speaker=892815,Option='{您好,0},{很高兴见到您,0}',}",
  [161687] = "{id=161687,Text='太多冒险者来打扰路奇，不得已我才把他藏了起来，同时召唤飞龙保护这片地方。',Speaker=892818,}",
  [161688] = "{id=161688,Text='弗雷尔爷爷，路奇现在在哪里？',Speaker=892815,}",
  [161689] = "{id=161689,Text='稍等。',Speaker=892818,}",
  [161690] = "{id=161690,Text='路奇！',Speaker=892815,Emoji=42,Action={actionid=201,num=1},}",
  [161691] = "{id=161691,Text='米娅！',Speaker=892816,Emoji=42,Action={actionid=84,num=1},}",
  [161692] = "{id=161692,Text='路奇，我和[c][ffff00][PlayerName][-][/c]找到了能证明你清白的证据。',Speaker=892815,}",
  [161693] = "{id=161693,Text='真的吗！真的吗！',Speaker=892816,}",
  [161694] = "{id=161694,Text='居然是枯树精？',Speaker=892816,Emoji=7,}",
  [161695] = "{id=161695,Text='你没有印象嘛？',Speaker=892815,}",
  [161696] = "{id=161696,Text='没有，我去的时候孩子已经受伤了。',Speaker=892816,}",
  [161697] = "{id=161697,Text='我光顾着救他们，没想到有东西在我身上狠狠咬了一口。',Speaker=892816,Emoji=12,}",
  [161698] = "{id=161698,Text='我下意识拿尾巴狠拍了他一下，他就消失了。',Speaker=892816,}",
  [161699] = "{id=161699,Text='所以你们抓到他了？',Speaker=892816,}",
  [161700] = "{id=161700,Text='没有……',Speaker=892815,Emoji=24,}",
  [161701] = "{id=161701,Text='你们应该是遇到了年幼的枯树精，他们的速度很快，要想抓住他们，需要[c][ffff00]掺杂醒醒草[-][/c]的甜甜圈。',Speaker=892818,}",
  [161702] = "{id=161702,Text='冒险者，拜托你去采集些醒醒草交给米娅。',Speaker=892818,}",
  [161703] = "{id=161703,Text='谢谢你，[c][ffff00][PlayerName][-][/c]我回去就把醒醒草放进甜甜圈里。',Speaker=892815,Option='诡异的搭配',}",
  [161704] = "{id=161704,Text='但这样小枯树精就会出现眩晕，走不快。',Speaker=892818,}",
  [161705] = "{id=161705,Text='不过，我要劝告你们，所有的生灵都有善良的一面，',Speaker=892818,}",
  [161706] = "{id=161706,Text='如果这些枯树精是无心伤害到了孩子，希望你们也能原谅他们。',Speaker=892818,Option='没问题！',Action={actionid=84,num=1},}",
  [161707] = "{id=161707,Text='啊！',}",
  [161708] = "{id=161708,Text='哈哈，感觉自己又厉害了些！',Speaker=892816,}",
  [161709] = "{id=161709,Text='路奇！不能一直做恶作剧！',Speaker=892818,}",
  [161710] = "{id=161710,Text='斐扬的村民就是因为你之前恶作剧做得太多，才会不分青红皂白认定你是伤害孩子的凶手。',Speaker=892818,}",
  [161711] = "{id=161711,Text='才不是呢。',Speaker=892816,Option='{我也觉得不是,2},{爷爷说得对！,3}',}",
  [161712] = "{id=161712,Text='哼哼！还是[c][ffff00][PlayerName][-][/c]明事理。',Speaker=892816,}",
  [161713] = "{id=161713,Text='哼哼！我可是很记仇的哦！',Speaker=892816,}",
  [161714] = "{id=161714,Text='好啦路奇，确实要乖一点哦。',Speaker=892815,Action={actionid=201,num=1},}",
  [161715] = "{id=161715,Text='[c][ffff00][PlayerName][-][/c]我正好刚把甜甜圈做好。',Speaker=892815,Option='路奇呢？',}",
  [161716] = "{id=161716,Text='这里就是路奇受伤的地方。',Speaker=892818,Action={actionid=200,num=1},}",
  [161717] = "{id=161717,Text='嗯……那个魔物的味道一点都闻不到了。',Speaker=892816,Emoji=14,Action={actionid=84,num=1},}",
  [161718] = "{id=161718,Text='冒险者，请收下这些甜甜圈，放到仓库旁边，引诱小枯树精出现吧。',Speaker=892815,}",
  [161719] = "{id=161719,Text='不知道这次他会不会很快出现。',Speaker=892815,}",
  [161720] = "{id=161720,Text='你们看！',Speaker=892815,}",
  [161721] = "{id=161721,Text='咕叽咕叽咕叽咕叽！',Speaker=892824,}",
  [161722] = "{id=161722,Text='咕叽咕叽！',Speaker=892824,Emoji=28,}",
  [161723] = "{id=161723,Text='就是现在，[c][ffff00][PlayerName][-][/c]！',Speaker=892816,}",
  [161724] = "{id=161724,Text='咕叽咕叽！',Speaker=892824,Emoji=42,}",
  [161725] = "{id=161725,Text='[c][ffff00][PlayerName][-][/c]，住手。',Speaker=892818,}",
  [161726] = "{id=161726,Text='咕叽咕叽！',Speaker=892824,Emoji=42,}",
  [161727] = "{id=161727,Text='你说，我在听。',Speaker=892818,}",
  [161728] = "{id=161728,Text='咕叽咕叽咕叽咕叽！',Speaker=892824,Option='爷爷能听懂？！',}",
  [161729] = "{id=161729,Text='我之前还听到他和飞龙聊天来着。',Speaker=892816,}",
  [161730] = "{id=161730,Text='我明白了……',Speaker=892818,}",
  [161731] = "{id=161731,Text='爷爷！你怎么能把它放了！？',Speaker=892816,Emoji=1,}",
  [161732] = "{id=161732,Text='米娅，能拜托你把村民都叫来嘛？我有事要宣布。',Speaker=892818,}",
  [161733] = "{id=161733,Text='好的爷爷。',Speaker=892815,}",
  [161734] = "{id=161734,Text='斐扬的村民们！',Speaker=892815,}",
  [161735] = "{id=161735,Text='弗雷尔爷爷有一个重要的消息要告诉大家。',Speaker=892815,}",
  [161736] = "{id=161736,Text='什么？弗雷尔爷爷！！智者弗雷尔爷爷回来了？！',Speaker=892820,}",
  [161737] = "{id=161737,Text='是关于年兽路奇的消息。',}",
  [161738] = "{id=161738,Text='路奇？它又出现了？',Speaker=892821,}",
  [161739] = "{id=161739,Text='等我我要去拿武器！',Speaker=892822,}",
  [161740] = "{id=161740,Text='封锁村门！',Speaker=892821,}",
  [161741] = "{id=161741,Text='大家冷静。',Speaker=892818,}",
  [161742] = "{id=161742,Text='爷爷身边不是路奇嘛！',Speaker=892820,}",
  [161743] = "{id=161743,Text='爷爷您为什么要护着路奇，他是伤害孩子的罪人！',Speaker=892821,}",
  [161744] = "{id=161744,Text='旁边的魔物是什么？',Speaker=892822,}",
  [161745] = "{id=161745,Text='弗雷尔爷爷，这到底是怎么回事？',Speaker=892819,}",
  [161746] = "{id=161746,Text='大家一直都以为是路奇伤害了小朋友。',Speaker=892818,}",
  [161747] = "{id=161747,Text='但实际上他恰恰是为了保护小朋友不被枯树精伤害，才暴露了真实身份。',Speaker=892818,}",
  [161748] = "{id=161748,Text='他身上的伤口就是证据，枯树精也承认孩子身上的伤口是他造成的！',Speaker=892818,}",
  [161749] = "{id=161749,Text='咕叽咕叽咕叽咕叽！',Speaker=892824,Emoji=42,}",
  [161751] = "{id=161751,Text='那请爷爷把枯树精交给我们！',Speaker=892819,}",
  [161752] = "{id=161752,Text='伤害孩子的魔物我们斐扬绝不姑息。',Speaker=892819,}",
  [161753] = "{id=161753,Text='魔物也是有灵性的，这个枯树精不是有意要伤害孩子。',Speaker=892818,}",
  [161754] = "{id=161754,Text='他是发现你们忙于劳作，没人陪伴孩子玩耍，才想做恶作剧逗孩子开心。',Speaker=892818,}",
  [161755] = "{id=161755,Text='恶作剧……',Speaker=892816,Emoji=19,}",
  [161756] = "{id=161756,Text='只是没想到恶作剧伤害了孩子……',Speaker=892818,}",
  [161757] = "{id=161757,Text='他很诚恳向大家道歉了，我也希望大家能原谅他。',Speaker=892818,}",
  [161758] = "{id=161758,Text='咕叽咕叽咕叽咕叽。',Speaker=892824,Emoji=42,Action={actionid=7,num=1},}",
  [161759] = "{id=161759,Text='居然是这样。',Speaker=892821,}",
  [161760] = "{id=161760,Text='我选择相信爷爷说的话。',Speaker=892820,Emoji=39,}",
  [161761] = "{id=161761,Text='斐扬会原谅他的。',Speaker=892822,}",
  [161762] = "{id=161762,Text='爷爷，只要枯树精不是有意为之，斐扬会原谅他的。',Speaker=892819,}",
  [161763] = "{id=161763,Text='包括路奇，我代表斐扬向他道歉。',Speaker=892819,}",
  [161764] = "{id=161764,Text='既然真相大白，为了表达歉意，[c][ffff00][PlayerName][-][/c]能邀请你和各位参加我们的元宵晚宴嘛？',Speaker=892819,}",
  [161765] = "{id=161765,Text='太好了，路奇最喜欢吃元宵。',Speaker=892815,}",
  [161766] = "{id=161766,Text='我，我也要道歉！',Speaker=892816,}",
  [161767] = "{id=161767,Text='路奇？',Speaker=892815,}",
  [161768] = "{id=161768,Text='之前的我也喜欢恶作剧，给大家带来了很多麻烦，今后我再也不会了！对不起。',Speaker=892816,Emoji=42,Action={actionid=84,num=1},}",
  [161769] = "{id=161769,Text='路奇！',Speaker=892815,Emoji=30,}",
  [161770] = "{id=161770,Text='没有证据就怪罪你也是我们的不对！感谢你照顾米娅救下小朋友，你会是斐扬永远的朋友！',Speaker=892819,}",
  [161771] = "{id=161771,Text='那我们就一起做元宵团圆饭吧！',Speaker=892818,}",
  [161772] = "{id=161772,Text='冒险者，希望你能加入我们。',Speaker=892818,}",
  [161773] = "{id=161773,Text='和朋友一起制作的食物，是最好的护身符。',Speaker=892818,}",
  [161774] = "{id=161774,Text='相信这份食物，能带给你一整年的好运。',Speaker=892818,Option='非常愿意！',}",
  [161775] = "{id=161775,Text='做的好，年轻人！',Speaker=892818,}",
  [161776] = "{id=161776,Text='路奇也有帮忙做元宵！',Speaker=892816,Emoji=26,}",
  [161777] = "{id=161777,Text='咕叽咕叽咕叽咕叽！',Speaker=892824,Emoji=26,}",
  [161778] = "{id=161778,Text='路奇和枯树精都帮了很大的忙。那么冒险者，准备好享用元宵团圆晚餐了吗？',Speaker=892819,Option='{我没准备好,4},{准备好了,3}',}",
  [161779] = "{id=161779,Text='准备好了记得跟我说，不过别太晚，饭菜冷了可就不好吃了。',Speaker=892819,}",
  [161780] = "{id=161780,Text='吃起来~！',Speaker=892816,Emoji=28,}",
  [161781] = "{id=161781,Text='汇集当季新鲜蔬果做出的拼盘，轻脆甜香，每口都能吃到满满健康。',Speaker=892829,Option='吃掉',}",
  [161782] = "{id=161782,Text='在这个季节最想喝到的热气腾腾暖汤锅，营养一百分！',Speaker=892830,Option='吃掉 ',}",
  [161783] = "{id=161783,Text='为了元宵节很早就开始酿造的米酒，好喝但不要贪杯哦！',Speaker=892831,Option='喝掉',}",
  [161784] = "{id=161784,Text='元宵节的重头菜，吃完新的一整年都会是甜甜的！',Speaker=892832,Option='吃掉',}",
  [161785] = "{id=161785,Text='冒险者，谢谢你为我做的这一切，希望你接下来的旅行一切顺利！',Speaker=892816,Action={actionid=82,num=1},}",
  [161794] = "{id=161794,Text='冒险者，有件事情想要拜托你，现在有空吗？',Speaker=891214,Option='{暂时没空,2},{有什么烦恼吗,3}',}",
  [161795] = "{id=161795,Text='我可是爱神“菲尔”，我要拜托你的，当然不是什么烦心事。',Speaker=891214,}",
  [161796] = "{id=161796,Text='相反，会是很甜蜜的事情！',Speaker=891214,Option='那是什么？',Emoji=27,}",
  [161797] = "{id=161797,Text='你知道嘛？今年的情人节可不一般。',Speaker=891214,}",
  [161798] = "{id=161798,Text='在这天，恰逢代表爱情的金星入驻灵魂宫，这可是百年难遇的好日子！',Speaker=891214,}",
  [161799] = "{id=161799,Text='在这一天结婚的恋人，会获得爱神最大的祝福。',Speaker=891214,Option='这么棒！',}",
  [161800] = "{id=161800,Text='所以冒险者，拜托你代表爱神，去帮助拉切尔神父和两位新人吧。',Speaker=891214,}",
  [161801] = "{id=161801,Text='希望在你的帮助下，这对恋人会获得一场最完美的婚礼。',Speaker=891214,}",
  [161802] = "{id=161802,Text='当然， 我也会有相应的祝福给到善良的你！你愿意嘛？',Speaker=891214,Option='{愿意,4},{暂时没空,5}',}",
  [161803] = "{id=161803,Text='那我在这里等你，要快哦，美好的时光总是转瞬即逝。',Speaker=891214,}",
  [161804] = "{id=161804,Text='太好了，他们现在应该都在普隆德拉的教堂，快去吧！',Speaker=891214,}",
  [161805] = "{id=161805,Text='勇敢的年轻人，欢迎来到普隆德拉教堂。',Speaker=891215,}",
  [161806] = "{id=161806,Text='我奉主的圣名为虔诚的你们祝福，愿你享受所得，',Speaker=891215,}",
  [161807] = "{id=161807,Text='愿你的工作得到称赞和荣耀，直到永远，阿门。',Speaker=891215,Option='谢谢您！',Emoji=26,}",
  [161808] = "{id=161808,Text='不用客气，这里本就是给予信仰者祝福的地方。',Speaker=891215,}",
  [161809] = "{id=161809,Text='我很高兴今天有对新人，来这里举办他们的婚礼。',Speaker=891215,}",
  [161810] = "{id=161810,Text='新娘和新郎都是非常纯洁善良的孩子，一直帮助教堂救济贫苦人家。',Speaker=891215,Option='爱神也这么说！',}",
  [161811] = "{id=161811,Text='爱神也告诉你这件事情了，没错！他们就是这样一对让人喜爱的孩子。',Speaker=891215,}",
  [161812] = "{id=161812,Text='所以，冒险者，能拜托你帮教堂，为他们准备个礼物嘛？',Speaker=891215,Option='礼物？',Emoji=8,}",
  [161813] = "{id=161813,Text='我想拜托你去搜集来两顶月桂树之冠。',Speaker=891215,}",
  [161814] = "{id=161814,Text='月桂树之冠是有灵性的，我想在婚礼仪式上，给两位新人戴上月桂树之冠。',Speaker=891215,}",
  [161815] = "{id=161815,Text='愿神保佑他们，往后生活可获得各样美善的恩赐。',Speaker=891215,Option='{愿意,6},{不愿意,7}',}",
  [161816] = "{id=161816,Text='冒险者是还有别的事情是吗？那也没关系。',Speaker=891215,}",
  [161817] = "{id=161817,Text='那么就拜托了，月桂树之冠会藏在鸟人哈比身上。',Speaker=891215,}",
  [161818] = "{id=161818,Text='你走到教堂的中心，默念咒语，鸟人哈比就会出现。',Speaker=891215,}",
  [161819] = "{id=161819,Text='请记住这段咒语：',Speaker=891215,}",
  [161820] = "{id=161820,Text='响应教会的召唤，遵从这意志的使者，回应我！',Speaker=891215,}",
  [161821] = "{id=161821,Text='你乃成就世间一切的[c][ffff00]善行者[-][/c]，',Speaker=891215,}",
  [161822] = "{id=161822,Text='在[c][ffff00]圣光[-][/c]的拥抱中，',Speaker=891215,}",
  [161823] = "{id=161823,Text='穿越[c][ffff00]时间之轮[-][/c]显现吧！！！',Speaker=891215,}",
  [161824] = "{id=161824,Text='记住了吗？',Speaker=891215,Option='{大概,0},{记住了,0}',}",
  [161825] = "{id=161825,Text='不用害怕，我相信你可以的，我就在这里等你。',Speaker=891215,Emoji=30,}",
  [161826] = "{id=161826,Text='就在这里吟诵咒语吧。',}",
  [161827] = "{id=161827,Text='响应教会的召唤，遵从这意志的使者，回应我！',}",
  [161828] = "{id=161828,Text='你乃成就世间一切的______。',Option='{善行者,8},{旅行者,9}',}",
  [161829] = "{id=161829,Text='神父当时说的应该是[c][ffff00]善行者[-][/c]。',}",
  [161830] = "{id=161830,Text='在______的拥抱中。',Option='{日光,12},{圣光,11}',}",
  [161831] = "{id=161831,Text='神父当时说的应该是[c][ffff00]圣光[-][/c]。',}",
  [161832] = "{id=161832,Text='穿越______显现吧！！！',Option='{写轮之眼,13},{时间之轮,14}',}",
  [161833] = "{id=161833,Text='神父当时说的应该是[c][ffff00]时间之轮[-][/c]。',}",
  [161834] = "{id=161834,Text='是你在召唤我嘛，冒险者？',Speaker=891220,Option='是我',}",
  [161835] = "{id=161835,Text='何事？',Speaker=891220,Option='解释情况',}",
  [161836] = "{id=161836,Text='既然是神父和教堂的拜托，我自然会答应，但花冠不能白给你。',Speaker=891220,}",
  [161837] = "{id=161837,Text='最近教堂内出现了些蓝鼠，帮我消灭掉他们吧！',Speaker=891220,}",
  [161838] = "{id=161838,Text='很不错冒险者！',Speaker=891220,}",
  [161839] = "{id=161839,Text='那我就把象征幸福的花冠给你。',Speaker=891220,}",
  [161840] = "{id=161840,Text='请代我向拉切尔问好，并送上对新人的祝福。',Speaker=891220,Option='好',}",
  [161841] = "{id=161841,Text='你确实做到的！[c][ffff00][PlayerName][-][/c]，非常感谢你的努力。',Speaker=891215,}",
  [161842] = "{id=161842,Text='我还想邀请你作为两位新人婚礼的见证人。',Speaker=891215,}",
  [161843] = "{id=161843,Text='不知道你愿不愿意。',Speaker=891215,Option='{愿意,16},{暂时不愿意,17}',}",
  [161844] = "{id=161844,Text='虽然我无意勉强，但还是希望你能接受邀请。',Speaker=891215,}",
  [161845] = "{id=161845,Text='很好冒险者，那请换上证婚人的服装，我们一起迎接新郎新娘的到来吧！',Speaker=891215,}",
  [161846] = "{id=161846,Text='[c][ffff00][PlayerName][-][/c]，这身衣服在你身上还挺合适。',Speaker=891215,}",
  [161847] = "{id=161847,Text='最后，在新郎新娘到来之前，请帮我开启管风琴。',Speaker=891215,}",
  [161848] = "{id=161848,Text='今天是个特殊的日子，会有精灵帮我们弹奏管风琴。',Speaker=891215,}",
  [161849] = "{id=161849,Text='你要做的就只是点开它即可。',Speaker=891215,}",
  [161850] = "{id=161850,Text='新娘好漂亮！',Speaker=891221,Emoji=29,}",
  [161851] = "{id=161851,Text='新郎也好帅啊！',Speaker=891222,Emoji=17,}",
  [161852] = "{id=161852,Text='真是般配的一对！',Speaker=891223,}",
  [161853] = "{id=161853,Text='我亲爱的孩子！我代表教会在上帝面前问你。',Speaker=891215,}",
  [161854] = "{id=161854,Text='你愿真心诚意与新娘伊莎贝拉结为夫妇，与她一生一世敬虔度日。',Speaker=891215,}",
  [161855] = "{id=161855,Text='无论安乐困苦、富贵贫穷、或顺或逆、或健康或病弱，你都尊重她，帮助她，关怀她，一心爱她；终身忠诚地与她共建家庭吗？！ ',Speaker=891215,}",
  [161856] = "{id=161856,Text='我愿意！',Speaker=891216,Emoji=26,}",
  [161857] = "{id=161857,Text='伊莎贝拉，你愿真心诚意与新郎艾维德结为夫妇，与他一生一世敬虔度日；',Speaker=891215,}",
  [161858] = "{id=161858,Text='无论安乐困苦、富贵贫穷、或顺或逆、或健康或病弱，你都尊重他，帮助他，关怀他，一心爱他；终身忠诚地与他共建家庭吗？！ ',Speaker=891215,}",
  [161859] = "{id=161859,Text='我愿意！',Speaker=891218,Emoji=38,}",
  [161860] = "{id=161860,Text='今天，我将把代表圣洁和幸福的月桂花冠，赐予两位新人。',Speaker=891215,}",
  [161861] = "{id=161861,Text='冒险者，请帮我把花冠戴在两位新人的头上吧。',Speaker=891215,}",
  [161862] = "{id=161862,Text='冒险者，谢谢你！',Speaker=891216,}",
  [161863] = "{id=161863,Text='谢谢你，冒险者！',Speaker=891218,}",
  [161864] = "{id=161864,Text='天哪！亲爱的……',Speaker=891218,Emoji=7,}",
  [161865] = "{id=161865,Text='爱神真得出现了！',Speaker=891216,Option='今天很特殊',}",
  [161866] = "{id=161866,Text='今天确实是特殊的日子！',Speaker=891214,}",
  [161867] = "{id=161867,Text='但再特殊的日子都没有真正相爱的灵魂难能可贵。',Speaker=891214,}",
  [161868] = "{id=161868,Text='我会出现，因为我面前站着的……',Speaker=891214,}",
  [161869] = "{id=161869,Text='是一对真正相爱，并愿意把美好分享给所有人的情侣。',Speaker=891214,Option='没错！',Emoji=27,}",
  [161870] = "{id=161870,Text='我会按照约定，送上我的祝福。',Speaker=891214,}",
  [161871] = "{id=161871,Text='谢谢爱神！',Speaker=891218,}",
  [161872] = "{id=161872,Text='也谢谢你们一直以来为普隆德拉所做的善事。',Speaker=891214,}",
  [161873] = "{id=161873,Text='冒险者，我同样也要祝你幸福。\n愿你能不受琐事烦扰，\n愿你在尘世收获爱与快乐。',Speaker=891214,Option='谢谢',}",
  [161874] = "{id=161874,Text='除了婚礼上的仪式，我想，艾维德可能还有礼物想要送给她的新娘。',Speaker=891215,Option='是吗？',}",
  [161875] = "{id=161875,Text='真的嘛？',Speaker=891218,}",
  [161876] = "{id=161876,Text='伊莎贝拉，我给你准备了你最喜欢的烟花。',Speaker=891216,}",
  [161877] = "{id=161877,Text='希望你能喜欢。',Speaker=891216,}",
  [161878] = "{id=161878,Text='亲爱的，谢谢你！我太开心了！',Speaker=891218,Emoji=27,}",
  [161879] = "{id=161879,Text='对了，冒险者，',Speaker=891216,}",
  [161880] = "{id=161880,Text='如果你愿意，我也非常希望你能跟我们一起去外面赏烟花。',Speaker=891216,Option='愿意',}",
  [161881] = "{id=161881,Text='太好了，我们期待你的到来。',Speaker=891216,}",
  [161882] = "{id=161882,Text='欢迎你冒险者，我们正说要开始燃放烟花。',Speaker=891216,}",
  [161884] = "{id=161884,Text='在燃放烟花的时候，能拜托你帮大家拍一张照片留作纪念嘛？',Speaker=891218,Option='好',}",
  [161885] = "{id=161885,Text='太好了！那么我们开始了。',Speaker=891216,Emoji=17,}",
  [161886] = "{id=161886,Text='那么就趁现在给大家拍一张照片吧！',}",
  [161887] = "{id=161887,Text='谢谢你冒险者，这会是我生命中最难忘的一天！',Speaker=891218,}",
  [161888] = "{id=161888,Text='也祝你幸福！',Speaker=891218,}",
  [161889] = "{id=161889,Text='那请你帮我们搜集[c][ffff00]3份小转转蛋[-][/c]吧，这是做秘制炖汤最好的材料。',Speaker=892818,}",
  [161890] = "{id=161890,Text='那么[c][ffff00][PlayerName][-][/c]，我们明天斐扬见！',Speaker=892815,Option='好！',}",
  [161891] = "{id=161891,Text='冒险者，爷爷让我们去把村民都叫来，我们一起去吧？',Speaker=892815,Option='好的',}",
  [161892] = "{id=161892,Text='亲爱的冒险者，新年快乐哦~',Speaker=892845,}",
  [161893] = "{id=161893,Text='在此为您献上B格猫的一份大礼~',Speaker=892845,}",
  [161894] = "{id=161894,Text='祝您生活美满，幸福安康哦',Speaker=892845,}",
  [161896] = "{id=161896,Text='冒险者你来啦，要不要猜一猜我手里的灯谜啊？',Speaker=891225,}",
  [161897] = "{id=161897,Text='我这里的灯谜难度很高，但同样奖品也很丰厚哦！',Speaker=891225,}",
  [161898] = "{id=161898,Text='那我们开始答题获赠神秘礼盒吧！要立刻开始嘛？',Speaker=891225,Option='{先等一等,2},{开始猜灯谜,3}',}",
  [161899] = "{id=161899,Text='身体圆圆红通通，陪你夜行走西东。竹丝纸皮虽然密，只怕旁人吐口风。（打一物品）',Speaker=891225,Option='{灯笼,4},{鬼火,5},{汽泡虫,5}',}",
  [161900] = "{id=161900,Text='我不觉得这是正确答案呢。',Speaker=891225,Option='我再想想',}",
  [161901] = "{id=161901,Text='恭喜你答对啦，这是你的奖励。',Speaker=891225,}",
  [161902] = "{id=161902,Text='平日不思，元宵想你。有皮有馅，又甜又蜜。（打一食物）',Speaker=891225,Option='{汤圆,4},{月饼,5},{蜂蜜,5}',}",
  [161903] = "{id=161903,Text='个儿不小穿红袄，脾气性子特别暴；上了火，蹦得高，大叫大嚷真热闹。 ',Speaker=891225,Option='{年兽路奇,4},{喷射哥布灵,5},{溜溜猴,5}',}",
  [161904] = "{id=161904,Text='又是水果又是糖，颗颗透红串成行！冷冷北风刺骨寒，吃它最解馋！（猜一种好吃的）',Speaker=891225,Option='{沙漠肉排,5},{斐扬蔬菜串,5},{冰糖葫芦,4}',}",
  [161905] = "{id=161905,Text='小小筷桶藏线头，点火烧出星满空！（打一物品）',Speaker=891225,Option='{天津町樱花,5},{绵绵云彩虹,5},{普隆德拉烟花,4}',}",
  [161906] = "{id=161906,Text='大眼睛，黑眼圈，圆圆胖胖逗人爱。攀得高，爬得快，竹笋野果当饭菜!（打一动物）',Speaker=891225,Option='{熊猫波利,4},{疯兔,5},{哥布灵,5}',}",
  [161907] = "{id=161907,Text='池里一只碗，大雨落不满，小雨纷纷下，珍珠一串串。（打一物品）',Speaker=891225,Option='{洛阳荷花灯,4},{尼夫海姆烟雾,5},{斐扬魔碑,5}',}",
  [161909] = "{id=161909,Text='嘿！冒险者，嗯……我在你身上闻到了材料的味道！',Speaker=891224,}",
  [161910] = "{id=161910,Text='各式各样，五花八门的材料混合在一起的香味！',Speaker=891224,Emoji=28,}",
  [161911] = "{id=161911,Text='真让人愉悦！',Speaker=891224,}",
  [161912] = "{id=161912,Text='今天算你运气好碰到我！你可以把用不到的材料拿给我。',Speaker=891224,}",
  [161913] = "{id=161913,Text='当然如果你愿意去搜集来材料给我我也会很高兴！啊呜啊呜！奖励路奇恶作剧一次！',Speaker=891224,Emoji=5,}",
  [161914] = "{id=161914,Text='哈哈哈，我开玩笑的。',Speaker=891224,}",
  [161915] = "{id=161915,Text='好啦不要生气啦，要么这样，我特别允许你[c][ffff00]每天可以跟我换两次材料[-][/c]，好不好？',Speaker=891224,Option='这还差不多',}",
  [161916] = "{id=161916,Text='[c][ffff00]不过我每天喜欢的材料可是不一样的，你得多注意！[-][/c]',Speaker=891224,}",
  [161917] = "{id=161917,Text='那么你有想交给我的材料吗？',Speaker=891224,Option='{柔毛500个,3},{慧眼500个,4},{幻想花500个,5},{远古遗骸300个,6}',}",
