  [161940] = "{id=161940,Text='好啦不要生气啦，我特许你每天可以跟我换两种材料，怎么样？',Speaker=891224,}",
  [161941] = "{id=161941,Text='材料我拿得也足够多了，那么冒险者我们有缘再见啦！',Speaker=891224,}",
  [161942] = "{id=161942,Text='一切就交给你了，[c][ffff00][PlayerName][-][/c]。',Speaker=892815,Action={actionid=200,num=1},}",
  [161950] = "{id=161950,Text='[c][ffff00][PlayerName][-][/c]，我们来寻找幸福之蛋吧！',Speaker=891227,}",
  [161951] = "{id=161951,Text='我会用魔法感知它的位置，找到它的任务就交给你了~',Speaker=891227,}",
  [161952] = "{id=161952,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161953] = "{id=161953,Text='（唔……这好像并不是幸福之蛋？）',}",
  [161954] = "{id=161954,Text='啊！找到了，我的蛋！',Speaker=891231,Emoji=7,}",
  [161955] = "{id=161955,Text='这位好心人，是你在守护我的蛋吗？真是太感谢您了！',Speaker=891231,}",
  [161956] = "{id=161956,Text='之前只是小小的失误！这次一定可以找到幸福之蛋的！',Speaker=891227,Emoji=26,}",
  [161957] = "{id=161957,Text='我会再次用魔法感知幸福之蛋的位置！这次应该不会错了~',Speaker=891227,}",
  [161958] = "{id=161958,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161959] = "{id=161959,Text='（唔……这好像也不是幸福之蛋？）',}",
  [161960] = "{id=161960,Text='啊！找到了嘎！这是我的蛋嘎~',Speaker=891232,Emoji=7,}",
  [161961] = "{id=161961,Text='这位好心人，是你在守护我的蛋吗？真是太感谢您了嘎！这是我的一份心意嘎~',Speaker=891232,}",
  [161962] = "{id=161962,Text='（低声）奇怪，我的魔法怎么不灵了呢……',Speaker=891227,Emoji=8,}",
  [161963] = "{id=161963,Text='呼，再试一次，这次一定要找到幸福之蛋！',Speaker=891227,}",
  [161964] = "{id=161964,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161965] = "{id=161965,Text='（唔……我似乎能猜到结局了……）',}",
  [161966] = "{id=161966,Text='啊！找到了，我珍贵的蛋！',Speaker=891233,Emoji=7,}",
  [161967] = "{id=161967,Text='（呃，狼也会生蛋？？）',}",
  [161968] = "{id=161968,Text='喂喂喂，你在想什么呢？这是食材，是食材懂不懂？！',Speaker=891233,}",
  [161969] = "{id=161969,Text='看在你拾蛋不昧的份上，这是给你的谢礼！',Speaker=891233,Emoji=6,}",
  [161970] = "{id=161970,Text='你要相信我哦，[c][ffff00][PlayerName][-][/c]！我们一定可以找到幸福之蛋的！',Speaker=891227,}",
  [161971] = "{id=161971,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161972] = "{id=161972,Text='（唔……这很明显不是幸福之蛋……）',}",
  [161973] = "{id=161973,Text='（低声）噫~一颗蛋！嘻嘻，偷偷把它拿走！',Speaker=891234,Emoji=5,}",
  [161974] = "{id=161974,Text='（匆忙逃走的窃蛋龙似乎掉落了什么东西……）',Option='拾取',}",
  [161975] = "{id=161975,Text='[c][ffff00][PlayerName][-][/c]，我们直接进入正题吧！这次一定要成功！',Speaker=891227,}",
  [161976] = "{id=161976,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161977] = "{id=161977,Text='（唔……这颗蛋——看起来可真圆啊……）',Emoji=8,}",
  [161978] = "{id=161978,Text='找到了！我心爱的皮球！',Speaker=891235,}",
  [161979] = "{id=161979,Text='（竟然是个皮球嘛，希尔芬的魔法越来越离谱了……）',}",
  [161980] = "{id=161980,Text='是你捡到我的皮球吗？真是太感谢你了！',Speaker=891235,Emoji=6,}",
  [161981] = "{id=161981,Text='我会用魔法找出幸福之蛋的位置，嗯，应该、可能、也许会成功吧……',Speaker=891227,}",
  [161982] = "{id=161982,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161983] = "{id=161983,Text='（唔……有些期待接下来的展开了……）',Emoji=10,}",
  [161984] = "{id=161984,Text='妈妈，妈妈，我要找妈妈！',Speaker=891236,}",
  [161985] = "{id=161985,Text='阿姨，你长得跟我好像！你是我妈妈吗？',Speaker=891236,Emoji=8,}",
  [161986] = "{id=161986,Text='傻孩子，我不是你的妈妈，我是你大舅达拉蛙。',Speaker=891237,}",
  [161987] = "{id=161987,Text='你妈妈罗达蛙是绿色的！',Speaker=891237,}",
  [161988] = "{id=161988,Text='好吧，谢谢舅舅，我要去找妈妈了！',Speaker=891236,}",
  [161989] = "{id=161989,Text='（地上似乎掉落了什么东西……）',Option='拾取',}",
  [161990] = "{id=161990,Text='我会用魔法定位幸福之蛋的位置！相信我，这是最后一次了！',Speaker=891227,}",
  [161991] = "{id=161991,Text='喂喂喂，你那副表情，不会是不信任我吧！',Speaker=891227,}",
  [161992] = "{id=161992,Text='我在那个方向感知到了“蛋”的气息，快去看看吧，冒险者！',Speaker=891227,Emoji=23,}",
  [161993] = "{id=161993,Text='（咦，那不是库玛？——还有幸福之蛋！）',}",
  [161994] = "{id=161994,Text='让你想吃我，砸晕你！砸晕你！',Speaker=891229,Emoji=12,}",
  [161995] = "{id=161995,Text='不要胡闹了，蛋仔！',Speaker=891227,}",
  [161996] = "{id=161996,Text='麻烦的人来了……',Speaker=891229,Emoji=1,}",
  [161997] = "{id=161997,Text='唔，发生了啥事啊，头好痛……',Speaker=891228,}",
  [161998] = "{id=161998,Text='蛋仔，你消失的这几天森林里乱得不成样子，快回去做你该做的事！',Speaker=891227,}",
  [161999] = "{id=161999,Text='好吧好吧~天天做守护森林的工作，真是无聊~',Speaker=891229,}",
  [162000] = "{id=162000,Text='冒险者，谢谢你这些天的帮助，这是为你准备的谢礼！这次森林危机的解决，全靠你的努力！',Speaker=891227,Emoji=6,}",
  [162001] = "{id=162001,Text='你好，尊敬的冒险者[c][ffff00][PlayerName][-][/c]。你身上貌似有很多材料。',Speaker=891226,}",
  [162002] = "{id=162002,Text='希望你帮忙守护我们的神树！',Speaker=891226,Emoji=28,}",
  [162003] = "{id=162003,Text='我在这里等了你很久了。',Speaker=891226,}",
  [162004] = "{id=162004,Text='今天我终于等到你了!这就是命运吧~',Speaker=891226,}",
  [162005] = "{id=162005,Text='神树每天需要不同的材料用来修复结界。',Speaker=891226,Emoji=5,}",
  [162006] = "{id=162006,Text='不然神树的魔力会越来越衰弱，后果不堪设想。',Speaker=891226,}",
  [162007] = "{id=162007,Text='冒险者！你可以[c][ffff00]每天捐献两次材料[-][/c]吗，我会用丰厚的奖励答谢你的！',Speaker=891226,Option='好的！我明白了',}",
  [162008] = "{id=162008,Text='[c][ffff00]不过神树每天需要的材料可是不一样的，你得多注意！[-][/c]',Speaker=891226,}",
  [162009] = "{id=162009,Text='那么你有想交给我的材料吗？',Speaker=891226,Option='{柔毛500个,3},{恶魔角500个,4},{微风的传言250个,5},{远古遗骸250个,6}',}",
  [162010] = "{id=162010,Text='确定咯？那就搜集齐后交给我吧！',Speaker=891226,}",
