  [162032] = "{id=162032,Text='冒险者！你可以今天捐献两次材料哦，我会用丰厚的奖励答谢你的！',Speaker=891226,}",
  [162033] = "{id=162033,Text='材料我拿得也足够多了，神树一定会好的！',Speaker=891226,}",
  [162042] = "{id=162042,Text='呜~~您就是那位冒险者[c][ffff00][PlayerName][-][/c]吗？终于见到你了啵！',Speaker=891240,}",
  [162043] = "{id=162043,Text='斐扬森林遇到了大麻烦，请您一定要帮帮我们啵~[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891240,Option='{好吧,2},{我没空,3}',Emoji=22,}",
  [162044] = "{id=162044,Text='真是太感谢了啵！',Speaker=891240,Option='发生了什么？',Emoji=6,}",
  [162045] = "{id=162045,Text='供奉在神树下的幸福之蛋不见了！神树的力量被削弱，无法压制住森林中的魔物了啵……',Speaker=891240,}",
  [162046] = "{id=162046,Text='那些魔物……他们想要彻底毁掉神树，女王大人派我来找传说中的冒险者求助啵~',Speaker=891240,Option='{女王大人？,2},{神树？,3},{幸福之蛋？,4}',}",
  [162047] = "{id=162047,Text='女王大人是斐扬森林的守护者啵！',Speaker=891240,}",
  [162048] = "{id=162048,Text='她依靠神树的力量守护森林中所有善良的生灵，让我们不受魔物的侵害啵~',Speaker=891240,}",
  [162049] = "{id=162049,Text='不过现在幸福之蛋不见了，神树的力量变弱了啵！女王大人很是焦急呢啵~',Speaker=891240,Emoji=15,}",
  [162050] = "{id=162050,Text='神树拥有着圣洁的力量啵~',Speaker=891240,}",
  [162051] = "{id=162051,Text='它依靠幸福之蛋的能量，压制魔物们的力量，保护善良的生灵不被伤害……',Speaker=891240,}",
  [162052] = "{id=162052,Text='但是现在幸福之蛋不见了！守护森林的女王大人很是焦急呢啵~',Speaker=891240,Emoji=15,}",
  [162053] = "{id=162053,Text='幸福之蛋是具备强大力量的宝物啵~',Speaker=891240,}",
  [162054] = "{id=162054,Text='它为神树提供能量，让神树能够压制住魔物们的力量，保护善良的生灵不被伤害……',Speaker=891240,}",
  [162055] = "{id=162055,Text='但是现在幸福之蛋不见了！守护森林的女王大人很是焦急呢啵~',Speaker=891240,Emoji=15,}",
  [162056] = "{id=162056,Text='女王大人就在斐扬等您呢啵~~如果准备好了，我就送您过去啵！',Speaker=891240,Option='{准备好了,6},{自行前往,7}',}",
  [162057] = "{id=162057,Text='哼哼~希尔芬！你们已经无路可退了！',Speaker=891245,Emoji=5,}",
  [162058] = "{id=162058,Text='如果现在乖乖让开，让我们毁掉神树——我还可以考虑放你一条生路！',Speaker=891245,}",
  [162059] = "{id=162059,Text='守护神树、守护这片森林，是我的职责。我不会放任你破坏这片森林的安宁！',Speaker=891241,}",
  [162060] = "{id=162060,Text='哼，这可是你自找的——女、王、大、人！',Speaker=891245,Emoji=5,}",
  [162061] = "{id=162061,Text='杀光她们！',Speaker=891245,}",
  [162062] = "{id=162062,Text='保护女王大人！',Speaker=891247,}",
  [162063] = "{id=162063,Text='（森林女王正在遭受魔物围攻，先把她救下来吧！）',}",
  [162064] = "{id=162064,Text='嗯？？你是什么人，竟然敢坏我的好事？',Speaker=891245,Emoji=12,}",
  [162065] = "{id=162065,Text='是[c][ffff00][PlayerName][-][/c]，传说中的冒险者来拯救我们了！',Speaker=891241,}",
  [162066] = "{id=162066,Text='哼，我管你是什么冒险者，一块杀了便是！',Speaker=891245,}",
  [162067] = "{id=162067,Text='小心啊，冒险者！',Speaker=891241,}",
  [162068] = "{id=162068,Text='可恶，竟然这么强……先撤退吧！',Speaker=891245,Emoji=12,}",
  [162069] = "{id=162069,Text='别想逃！',Speaker=891241,}",
  [162070] = "{id=162070,Text='可……可恶！放我出去！',Speaker=891245,Emoji=7,}",
  [162071] = "{id=162071,Text='感谢你的帮助，冒险者！',Speaker=891241,Emoji=6,}",
  [162072] = "{id=162072,Text='幸福之蛋才刚刚丢失，这些树精就急不可耐的前来破坏神树。',Speaker=891241,}",
  [162073] = "{id=162073,Text='我想幸福之蛋的失窃一定与它们有关——',Speaker=891241,}",
  [162074] = "{id=162074,Text='冒险者，能不能请你审问一下那个树精，问出幸福之蛋的下落……',Speaker=891241,}",
  [162075] = "{id=162075,Text='噢对了，那边有一些小“道具”，或许能在审问时帮上忙~',Speaker=891241,Emoji=7,}",
  [162076] = "{id=162076,Text='哼，你想干什么？',Speaker=891245,Option='幸福之蛋的下落',}",
  [162077] = "{id=162077,Text='别白费力气了，我什么都不会说的！',Speaker=891245,}",
  [162078] = "{id=162078,Text='（要不……试试希尔芬的“道具”？）',}",
  [162079] = "{id=162079,Text='你，你，你这是要干什么？！',Speaker=891245,Emoji=7,}",
  [162080] = "{id=162080,Text='我警告你，你不要过来啊，我——我会叫的！',Speaker=891245,Option='{使用皮鞭,9},{使用蜡烛,10}',}",
  [162081] = "{id=162081,Text='啊！啊！',Speaker=891245,}",
  [162082] = "{id=162082,Text='可恶，你不会是有什么奇怪的癖好吧！啊！',Speaker=891245,}",
  [162083] = "{id=162083,Text='停！别打了，我告诉你还不行嘛！',Speaker=891245,Emoji=42,}",
  [162084] = "{id=162084,Text='喂喂，你想干什么？把它拿开，别靠近我！',Speaker=891245,}",
  [162085] = "{id=162085,Text='啊啊啊啊啊！！着火了！！',Speaker=891245,}",
  [162086] = "{id=162086,Text='杀人——杀树了！！',Speaker=891245,}",
  [162087] = "{id=162087,Text='快停手，我告诉你还不行吗！',Speaker=891245,Option='灭火',}",
  [162088] = "{id=162088,Text='呼，得救了……',Speaker=891245,}",
  [162089] = "{id=162089,Text='太气人了，到底谁才是反派啊……',Speaker=891245,Option='少说废话',Emoji=12,}",
  [162090] = "{id=162090,Text='唉，那颗蛋确实是我们偷走的，不过我们用了很多办法都没能毁掉它——',Speaker=891245,}",
  [162091] = "{id=162091,Text='只能先把它藏在了我哥哥疤皮的巢穴……',Speaker=891245,}",
  [162092] = "{id=162092,Text='哼，不要想着把蛋抢回来了，我哥哥可不是好惹的！',Speaker=891245,Option='展示“道具”',}",
  [162093] = "{id=162093,Text='呃，你当我没说——',Speaker=891245,Emoji=1,}",
  [162094] = "{id=162094,Text='（小声）年轻人不讲道德，玩得这么刺激……',Speaker=891245,}",
  [162095] = "{id=162095,Text='既然已经知道了幸福之蛋的下落，我会派人将它寻回！',Speaker=891241,}",
  [162096] = "{id=162096,Text='真是太感谢你的帮助了，[c][ffff00][PlayerName][-][/c]！',Speaker=891241,}",
  [162097] = "{id=162097,Text='[c][ffff00][PlayerName][-][/c]，你总算来了！',Speaker=891241,}",
  [162098] = "{id=162098,Text='我派去寻找幸福之蛋的守卫到现在也没有回来，我担心发生了什么意外。',Speaker=891241,Emoji=37,}",
  [162099] = "{id=162099,Text='能不能请你去“疤皮”的巢穴调查一下，看看究竟发生了什么？',Speaker=891241,}",
  [162100] = "{id=162100,Text='魔物随时可能卷土重来，袭击神树，我必须守在这里……',Speaker=891241,}",
  [162101] = "{id=162101,Text='寻找失踪的守卫，找回幸福之蛋的重任就交给你了！',Speaker=891241,Option='好的',}",
  [162102] = "{id=162102,Text='（这里发生了什么？守卫和树精为什么都倒在地上？）',}",
  [162103] = "{id=162103,Text='（从伤势来看，他们似乎被什么人从背后偷袭，打晕了……）',}",
  [162104] = "{id=162104,Text='（幸福之蛋也不在这里……）',}",
  [162105] = "{id=162105,Text='唔……',Speaker=891247,}",
  [162106] = "{id=162106,Text='（迷糊）嘶……',Speaker=891247,Option='发生了什么',Emoji=14,}",
  [162107] = "{id=162107,Text='（迷糊）……龙……偷袭…………',Speaker=891247,Option='幸福之蛋呢？',}",
  [162108] = "{id=162108,Text='（迷糊）……偷袭……拿走…………',Speaker=891247,}",
  [162109] = "{id=162109,Text='（幸福之蛋可能被偷袭者拿走了，看看有没有什么线索能找到偷袭者……）',}",
  [162110] = "{id=162110,Text='（地上的足迹跟这只地龙的足迹吻合，快追上去问问！）',}",
  [162111] = "{id=162111,Text='你……你干什么，找我有什么事吗？',Speaker=891244,Option='关于幸福之蛋',Emoji=7,}",
  [162112] = "{id=162112,Text='蛋？什么蛋，我没见过，不关我的事，你别瞎说啊！',Speaker=891244,Option='地上的足迹',}",
  [162113] = "{id=162113,Text='可恶，竟然留下证据了！',Speaker=891244,}",
  [162114] = "{id=162114,Text='不过这里四下无人，就算蛋是我拿的，你又能拿我怎么样？',Speaker=891244,}",
  [162115] = "{id=162115,Text='别，别打了！那颗蛋不在我这儿！',Speaker=891244,Option='蛋呢？',}",
  [162116] = "{id=162116,Text='我把蛋偷出来，刚想看看这蛋到底有什么神奇的地方，为什么那几个树精这么紧张的守护着……',Speaker=891244,}",
  [162117] = "{id=162117,Text='结果就遇到了“那位大人”！',Speaker=891244,Option='那位大人？',}",
  [162118] = "{id=162118,Text='就是那个自称“黑风大王”的黑熊精！',Speaker=891244,}",
  [162119] = "{id=162119,Text='这个贪心的家伙，看到我手里的蛋，觉得好看，就逼着我把蛋“孝敬给他”！',Speaker=891244,Emoji=12,}",
  [162120] = "{id=162120,Text='那家伙看上去傻乎乎的，却是这片森林里最强的野兽，一百个我也打不过他呀，只能把蛋给他了！',Speaker=891244,}",
  [162121] = "{id=162121,Text='真是晦气，好不容易偷的东西没保住，还白挨你一顿打！',Speaker=891244,}",
  [162122] = "{id=162122,Text='（看来幸福之蛋被黑熊精抢走了，留意一下有没有黑熊精的消息吧！）',}",
  [162123] = "{id=162123,Text='[c][ffff00][PlayerName][-][/c]，森林中正在流传一个消息——',Speaker=891241,}",
  [162124] = "{id=162124,Text='黑风大王库玛获得了一枚“神奇的宝蛋”，他想要举办一场“鉴蛋大会”。',Speaker=891241,}",
  [162125] = "{id=162125,Text='邀请森林中的野兽们一同品鉴这枚宝蛋。这枚蛋，应该就是失窃的幸福之蛋了！',Speaker=891241,Emoji=7,}",
  [162126] = "{id=162126,Text='冒险者，请你一定要想办法进入“鉴蛋大会”，取回幸福之蛋！',Speaker=891241,}",
  [162127] = "{id=162127,Text='（那边有一只狸猫，向它打听一下鉴蛋大会的消息吧~）',}",
  [162128] = "{id=162128,Text='嗯？你鬼鬼祟祟的干什么呢？',Speaker=891250,Option='关于鉴蛋大会',Emoji=8,}",
  [162129] = "{id=162129,Text='唔……鉴蛋大会吗？好像是有这么回事……',Speaker=891250,}",
  [162130] = "{id=162130,Text='不过这跟我又有什么关系呢？我只是一只无辜的小狸猫……',Speaker=891250,}",
  [162131] = "{id=162131,Text='你不会以为黑风大王会邀请我这样弱小又没什么名气的动物参加他的宴会吧？',Speaker=891250,Emoji=19,}",
  [162132] = "{id=162132,Text='只有那些强大的“名兽”才能得到鉴蛋大会的邀请函，你要想知道鉴蛋大会的消息，还是去……',Speaker=891250,}",
  [162135] = "{id=162135,Text='嘁……该死的狐狸……',Speaker=891250,Emoji=12,}",
  [162136] = "{id=162136,Text='天天跟着老虎混在一起，就觉得自己真的很强大了……',Speaker=891250,}",
  [162137] = "{id=162137,Text='你要是想知道鉴蛋大会的事，不妨去问问他，看起来他很想找人炫耀一番呢！',Speaker=891250,}",
  [162138] = "{id=162138,Text='老虎大哥，您可真是了不起，连黑风大王那样强大的野兽也邀请您参加他举办的宴会呢~',Speaker=891251,}",
  [162139] = "{id=162139,Text='哼！他不是也邀请你了吗？',Speaker=891252,}",
  [162140] = "{id=162140,Text='诶呀，我这不是沾了您的光嘛！要不是有您罩着，哪有人知道我是谁呢！',Speaker=891251,Emoji=38,}",
  [162141] = "{id=162141,Text='哼，你知道就好！',Speaker=891252,}",
  [162142] = "{id=162142,Text='明天的鉴蛋大会，去的都是森林里有头有脸的野兽，你可小心点，别给我丢了面子！',Speaker=891252,}",
  [162143] = "{id=162143,Text='怎么会呢！',Speaker=891251,}",
  [162144] = "{id=162144,Text='（需要邀请函才能进入鉴蛋大会，想办法得到邀请函）',Option='{抢过来,0},{偷过来,0}',}",
  [162145] = "{id=162145,Text='什么人？！',Speaker=891252,}",
  [162146] = "{id=162146,Text='（糟了，被发现了！看来只能强抢了！）',}",
  [162147] = "{id=162147,Text='（他们被打晕了！快取走他们的邀请函！）',}",
  [162148] = "{id=162148,Text='（获得了鉴蛋大会邀请函）',}",
  [162149] = "{id=162149,Text='（鉴蛋大会将在明天举办，记得要准时参加啊！）',}",
  [162150] = "{id=162150,Text='你你你，干什么的！',Speaker=891254,Emoji=7,}",
  [162151] = "{id=162151,Text='库玛大人正在里面举办“鉴蛋大会”，闲杂人等不得入内！',Speaker=891254,Option='出示邀请函',}",
  [162152] = "{id=162152,Text='嗯？这……让我看看……',Speaker=891254,}",
  [162153] = "{id=162153,Text='这邀请函竟然是真的？奇怪，我还以为这次宴会只邀请了森林里的动物呢……',Speaker=891254,Emoji=19,}",
  [162154] = "{id=162154,Text='嗯——这位客人里面请！库玛大人已经在恭候了！',Speaker=891254,}",
  [162155] = "{id=162155,Text='（低声）真是太奇怪了……',Speaker=891254,}",
  [162156] = "{id=162156,Text='（不能太引人注目，找个角落待着吧……）',}",
  [162157] = "{id=162157,Text='前几天，我偶然得到了一件宝物！',Speaker=891242,Emoji=17,}",
  [162158] = "{id=162158,Text='是一颗非常漂亮的蛋！',Speaker=891242,}",
  [162159] = "{id=162159,Text='所以我举办了这个“鉴蛋大会”，邀请大家来，一起品鉴品鉴这件宝贝！',Speaker=891242,}",
  [162160] = "{id=162160,Text='库玛老大，到底是个什么样的蛋，快拿出来让我们见识见识吧！',Speaker=891253,}",
  [162161] = "{id=162161,Text='对呀，能被黑风大王称为宝物的蛋，一定非常特别吧！',Speaker=891258,}",
  [162162] = "{id=162162,Text='哼哼~那是当然！',Speaker=891242,Emoji=17,}",
  [162163] = "{id=162163,Text='既然你们都想见识一下，那我就让你们开开眼界！',Speaker=891242,}",
  [162164] = "{id=162164,Text='哇！果然是一颗漂亮的蛋！',Speaker=891253,}",
  [162165] = "{id=162165,Text='这种花纹，这种样式——这是什么生物的蛋？我从来没有见过。',Speaker=891257,}",
  [162166] = "{id=162166,Text='我感觉到这颗蛋里似乎有着非常浓郁的生命力！',Speaker=891258,}",
  [162167] = "{id=162167,Text='说的没错，所以——',Speaker=891242,}",
  [162168] = "{id=162168,Text='大人，已经准备好了！',Speaker=891255,}",
  [162169] = "{id=162169,Text='“品鉴”一枚蛋，当然不能只靠看的，评价一枚蛋好不好，当然要尝尝它的味道啦！',Speaker=891242,}",
  [162170] = "{id=162170,Text='既然它有着那么浓郁的生命能量，一定是非常滋补的食物吧！',Speaker=891242,Emoji=28,}",
  [162171] = "{id=162171,Text='嘿嘿，我让厨师把它做成料理，大家一起尝尝！',Speaker=891242,}",
  [162172] = "{id=162172,Text='听起来不错的样子……',Speaker=891256,}",
  [162173] = "{id=162173,Text='这么大一颗蛋，应该会挺好吃的吧！',Speaker=891253,}",
  [162174] = "{id=162174,Text='（幸福之蛋……似乎动了一下？）',}",
  [162175] = "{id=162175,Text='我都有些迫不及待了——厨师，快动手，把这颗蛋烹饪了！',Speaker=891242,Emoji=28,}",
  [162176] = "{id=162176,Text='无知的凡……兽！竟然要吃本蛋！你知道本蛋是谁嘛！',Speaker=891243,}",
  [162177] = "{id=162177,Text='本蛋可是庇护着这片森林的幸福之蛋！',Speaker=891243,}",
  [162178] = "{id=162178,Text='天哪，这颗蛋竟然还会说话！！',Speaker=891242,}",
  [162179] = "{id=162179,Text='我还是第一次吃会说话的蛋！好期待啊！！',Speaker=891242,Emoji=28,}",
  [162180] = "{id=162180,Text='除了吃你就不想点别的嘛……',Speaker=891243,}",
  [162181] = "{id=162181,Text='算了，这里太危险了，还是先跑路吧！',Speaker=891243,}",
  [162182] = "{id=162182,Text='嗯？我的蛋，没了？！！',Speaker=891242,}",
  [162183] = "{id=162183,Text='森林女王？为什么你会在这儿？',Speaker=891242,Emoji=7,}",
  [162184] = "{id=162184,Text='我感觉到了幸福之蛋的气息，但很快消失不见了……',Speaker=891241,}",
  [162185] = "{id=162185,Text='它去哪儿了？',Speaker=891241,Option='被吓跑了',Emoji=7,}",
  [162186] = "{id=162186,Text='你是说——这只蠢熊——想要吃掉幸福之蛋？！',Speaker=891241,}",
  [162187] = "{id=162187,Text='气死我了！[c][ffff00][PlayerName][-][/c]，一定要帮我狠狠教训这只蠢熊一顿！',Speaker=891241,}",
  [162188] = "{id=162188,Text='唔？森林女王，你怎么能叫我蠢熊呢——',Speaker=891242,}",
  [162189] = "{id=162189,Text='不会是因为我举办鉴蛋大会没邀请你，你不开心了吧？',Speaker=891242,}",
  [162190] = "{id=162190,Text='冒险者，千万别手下留情……',Speaker=891241,Emoji=12,}",
  [162191] = "{id=162191,Text='森林女王，咱们无仇无怨的，你为什么要叫这个冒险者打我呀……',Speaker=891242,Emoji=24,}",
  [162192] = "{id=162192,Text='幸福之蛋，那可是整片森林的命脉，竟然想要吃了它？！',Speaker=891241,}",
  [162193] = "{id=162193,Text='呃，原来是这个事儿啊……唔，蛋存在的意义不就是用来吃的吗？',Speaker=891242,}",
  [162194] = "{id=162194,Text='你除了吃还知道什么？！',Speaker=891241,Emoji=12,}",
  [162195] = "{id=162195,Text='没有幸福之蛋，神树就无法压制森林中那些邪恶的魔物。',Speaker=891241,}",
  [162196] = "{id=162196,Text='你们这些动物的生存空间也会被侵占！',Speaker=891241,}",
  [162197] = "{id=162197,Text='有点复杂，你说的这是啥意思？',Speaker=891242,Option='食物要没了',}",
  [162198] = "{id=162198,Text='啊？！这——这么严重！！',Speaker=891242,Emoji=7,}",
  [162199] = "{id=162199,Text='我们好不容易找到了幸福之蛋的下落，竟然被你吓跑了！真是太过分了……',Speaker=891241,}",
  [162200] = "{id=162200,Text='那个——嘿嘿，森林女王，其实我也不是故意的，要不——我去把那个宝贝蛋找回来赔罪，嘿嘿……',Speaker=891242,}",
  [162201] = "{id=162201,Text='（低声）看它呆头呆脑的样子，怎么可能找到幸福之蛋啊……',Speaker=891241,}",
  [162202] = "{id=162202,Text='既然你想要赔罪——那就表现出一点诚意吧！',Speaker=891241,}",
  [162203] = "{id=162203,Text='跟我走吧，我有一项任务要交给你！',Speaker=891241,}",
  [162204] = "{id=162204,Text='[c][ffff00][PlayerName][-][/c]，多亏了你的帮助，神树暂时安全了呢！',Speaker=891241,Option='发生了什么',Emoji=6,}",
  [162205] = "{id=162205,Text='冲呀，毁掉神树！统治森林！！',Speaker=891246,}",
  [162206] = "{id=162206,Text='小的们，保护神树！击退这些魔物！',Speaker=891242,}",
  [162207] = "{id=162207,Text='看！这就是我想到的保护神树的办法！',Speaker=891241,Emoji=20,}",
  [162208] = "{id=162208,Text='让库玛和他的手下组成巡逻队，消灭魔物，保护神树！',Speaker=891241,}",
  [162209] = "{id=162209,Text='作为报酬，只要给他们提供食物就可以了！',Speaker=891241,}",
  [162210] = "{id=162210,Text='又能打架，又有饭吃，这份工作真是太棒了！',Speaker=891242,Emoji=41,}",
  [162211] = "{id=162211,Text='嗯，差不多到吃饭的时间了！冒险者，帮我把准备好的料理发给正在巡逻的巡逻队员吧！',Speaker=891241,}",
  [162212] = "{id=162212,Text='我也要我也要！',Speaker=891242,Emoji=28,}",
  [162213] = "{id=162213,Text='打工，吃饭，快乐无限！',Speaker=891242,Option='分发料理',}",
  [162214] = "{id=162214,Text='噢噢噢噢~~吃饭了吃饭了！',Speaker=891242,Emoji=28,}",
  [162215] = "{id=162215,Text='唔，你是什么人，是不是想对神树图谋不轨？！',Speaker=891254,Option='分发料理',}",
  [162216] = "{id=162216,Text='噢噢噢噢~~兄弟们吃饭啦！',Speaker=891254,}",
  [162217] = "{id=162217,Text='跟着库玛老大混有饭吃~~',Speaker=891254,Emoji=28,}",
  [162218] = "{id=162218,Text='[c][ffff00][PlayerName][-][/c]，你回来了~',Speaker=891241,}",
  [162219] = "{id=162219,Text='虽然现在神树的危机暂时解除了，但是还是要找回幸福之蛋，压制猖獗的魔物。',Speaker=891241,}",
  [162220] = "{id=162220,Text='它似乎没有跑远，应该还在斐扬森林中……',Speaker=891241,}",
  [162221] = "{id=162221,Text='现在神树有库玛保护，我可以放心释放感知幸福之蛋的位置……',Speaker=891241,}",
  [162222] = "{id=162222,Text='寻找幸福之蛋的任务，也要交给你了哦~冒险者！',Speaker=891241,Emoji=41,}",
  [162225] = "{id=162225,Text='你好呀，[c][ffff00][PlayerName][-][/c]。多亏了你的帮助，神树暂时安全了。女王大人对你称赞不已呢！',Speaker=892854,Emoji=41,}",
  [162226] = "{id=162226,Text='但我们还需要让神树恢复力量，压制森林中魔物的扩张。',Speaker=892854,}",
  [162227] = "{id=162227,Text='或许那个传说中的道具能帮的上忙！',Speaker=892854,Option='什么道具',Emoji=7,}",
  [162228] = "{id=162228,Text='神树的汁液！它饱含的生命能量一定能够帮助神树恢复元气！',Speaker=892854,}",
  [162229] = "{id=162229,Text='[c][ffff00][PlayerName][-][/c]，我将神树的汁液的制作方法交给你，这样你就可以通过料理书制作它了！制作的时候注意哦，最好是先提高一下料理制作的等级，这样成功率会大大增加！',Speaker=892854,}",
  [162230] = "{id=162230,Text='每天来我这儿，将神树的汁液供奉给神树，可以获得神树的馈赠哦！',Speaker=892854,Emoji=6,}",
  [162231] = "{id=162231,Text='[c][ffff00][PlayerName][-][/c]，神树需要你的供养，你准备好神树的汁液了吗？',Speaker=892854,Option='{准备好了,2},{还没有,3}',}",
  [162233] = "{id=162233,Text='在你的帮助下，神树又恢复了一些力量呢！',Speaker=892854,Emoji=6,}",
  [162234] = "{id=162234,Text='噫？你好像没有带着神树的汁液，神树的汁液可以使用料理书进行制作哦！',Speaker=892854,}",
  [162235] = "{id=162235,Text='嗨！冒险者，还记得我吗，你的老朋友罗伯特！',Speaker=892853,}",
  [162236] = "{id=162236,Text='这次我来到普隆德拉依旧会停留一周，每天你都可以从我这获得一件礼物。当然，前提是你必须要回答我的谜语才行~',Speaker=892853,}",
  [162237] = "{id=162237,Text='现在你准备好了吗？我们开始吧！',Speaker=892853,}",
  [162238] = "{id=162238,Text='[c][ffff00]【该任务在同账号下只能完成一次，请确认账号角色后谨慎接取！】[-][/c]',Speaker=892853,Option='{没问题！,4},{再考虑一下,3}',}",
  [162239] = "{id=162239,Text='太好了，那么开始我们今天的谜语吧：',Speaker=892853,Emoji=5,}",
  [162240] = "{id=162240,Text='以下哪个魔物、mini、MVP最容易害羞？',Speaker=892853,Option='{俄赛里斯,3},{狸猫,2},{蚁后,4}',}",
  [162241] = "{id=162241,Text='恭喜你答对了！把绷带缠满全身，至今没人知道俄赛里斯里面到底长什么样……你要不要去发现他的真面目？',Speaker=892853,Emoji=20,}",
  [162242] = "{id=162242,Text='唔……狸猫吗，或许你是对的，可能他也会脸红，只是被毛发挡住了？',Speaker=892853,Emoji=8,}",
  [162243] = "{id=162243,Text='唔……真的很难想象蚁后害羞的样子呢……如果你幸运的拍到了，可以把照片给我看看吗？',Speaker=892853,Emoji=8,}",
  [162244] = "{id=162244,Text='太好了，那么开始我们今天的谜语吧：',Speaker=892853,Emoji=5,}",
  [162245] = "{id=162245,Text='以下哪个魔物、mini、MVP体重最轻？',Speaker=892853,Option='{白幽灵,3},{摇滚蝗虫,2},{天使波利,4}',}",
  [162246] = "{id=162246,Text='恭喜你答对了！虽然看起来体型很大，但是白幽灵的真实重量等于一张床单……',Speaker=892853,Emoji=20,}",
  [162247] = "{id=162247,Text='唔……不管怎么看摇滚蝗虫都不像体重最轻的吧。',Speaker=892853,Emoji=1,}",
  [162248] = "{id=162248,Text='唔……不管怎么看天使波利都不像体重最轻的吧。',Speaker=892853,Emoji=1,}",
  [162250] = "{id=162250,Text='以下哪个魔物、mini、MVP年龄最小？',Speaker=892853,Option='{兽人婴儿,4},{长老树精,3},{小巴风特,2}',}",
  [162251] = "{id=162251,Text='恭喜你答对了！作为兽族中天资聪慧、力量惊人的存在，重点在于他还是个宝宝！',Speaker=892853,Emoji=20,}",
  [162252] = "{id=162252,Text='唔……不管怎么看长老树精的年纪都不小了吧。',Speaker=892853,Emoji=18,}",
  [162253] = "{id=162253,Text='唔…虽然带了一个“小”字，但是小巴风特可不是年龄最小的。',Speaker=892853,Emoji=18,}",
  [162255] = "{id=162255,Text='以下哪个魔物一般作为早餐食用？',Speaker=892853,Option='{魔锅蛋,3},{螃蟹,4},{魔菇,2}',}",
  [162256] = "{id=162256,Text='恭喜你答对了！一天一个煎蛋，医生远离你！',Speaker=892853,Emoji=20,}",
  [162257] = "{id=162257,Text='唔……在普隆德拉流行早餐吃螃蟹吗？',Speaker=892853,Emoji=25,}",
  [162258] = "{id=162258,Text='唔……在普隆德拉流行早餐吃魔菇吗？',Speaker=892853,Emoji=25,}",
  [162260] = "{id=162260,Text='以下哪个魔物、mini、MVP最准时？',Speaker=892853,Option='{黑魔女,2},{时间管理人,4},{黄金虫,3}',Emoji=5,}",
  [162261] = "{id=162261,Text='唔……依照黑魔女的个性，她才不会管现在几点了。',Speaker=892853,Emoji=14,}",
  [162262] = "{id=162262,Text='恭喜你答对了！正如其名，时间管理人的时间观念可是很严谨的！',Speaker=892853,Emoji=20,}",
  [162263] = "{id=162263,Text='唔……作为一只虫子也需要准时吗？',Speaker=892853,Emoji=14,}",
  [162265] = "{id=162265,Text='以下哪个角色最会讲故事？',Speaker=892853,Option='{威廉·哈萨特,4},{猫小友,3},{雷藏,2}',Emoji=5,}",
  [162266] = "{id=162266,Text='恭喜你答对了！作为吉芬鼎鼎大名的吟游诗人，他知道的故事可多了~',Speaker=892853,Emoji=20,}",
  [162267] = "{id=162267,Text='唔……猫小友只会帮你附魔，如果想找他讲故事，可能会把你赶走哦。',Speaker=892853,Emoji=1,}",
  [162268] = "{id=162268,Text='虽然雷藏是个了不起的魔法师，但是他可没时间给你讲故事。',Speaker=892853,Emoji=1,}",
  [162270] = "{id=162270,Text='以下哪个魔物、mini、MVP最喜欢拍黑白照片？',Speaker=892853,Option='{熊猫波利,4},{人鱼,2},{颚枭男爵,3}',}",
  [162271] = "{id=162271,Text='恭喜你答对了！这个问题可太简单了，不是吗？',Speaker=892853,Emoji=20,}",
  [162272] = "{id=162272,Text='唔……爱美的人鱼最喜欢的还是彩色照片，快去给她拍照吧。',Speaker=892853,Emoji=18,}",
  [162273] = "{id=162273,Text='唔……不只是黑白照片， 颚枭男爵根本就不喜欢拍照……',Speaker=892853,Emoji=10,}",
  [162275] = "{id=162275,Text='嗨！冒险者，你又来了，看来我的礼物还算令你满意，是不是还想要更多？那么开始我们今天的猜谜吧~',Speaker=892853,Option='{我等不及了！,6},{稍等一下,7}',}",
  [162277] = "{id=162277,Text='那就等你准备好了再来吧~',Speaker=892853,}",
  [162278] = "{id=162278,Text='感谢你今天的回答，明天我还会在这里等你哟~',Speaker=892853,}",
  [162281] = "{id=162281,Text='是你啊冒险者，看起来今天也是精神满满的样子呢！',Speaker=892855,}",
  [162282] = "{id=162282,Text='不像我，明明还什么都没有做，却感觉浑身疲惫，只想坐在这发发呆，看看天空飘过的白云，一朵又一朵……（哈欠）',Speaker=892855,}",
  [162283] = "{id=162283,Text='你说打起精神来？唉……你不知道我每天都在遭遇什么样的事情，比如说上周阿尔勒先生让我去收集一些特殊的火灵原石，只有在熔岩洞窟2F的爆炎小恶魔★★那才能获取。',Speaker=892855,}",
  [162284] = "{id=162284,Text='可怜我在那呆了一周，脸都被烤黑了也没见到一个爆炎小恶魔★★的影子，还被阿尔勒先生臭骂了一顿……',Speaker=892855,Emoji=15,}",
  [162285] = "{id=162285,Text='然后就是这周一，他让我去斐扬树林采集一些幻想花，这花只在月圆之夜才会绽放，其他时间看起来就只是一株普通的野草，根本无法区分。',Speaker=892855,}",
  [162286] = "{id=162286,Text='我在斐扬树林苦等到了昨天晚上，然后心血来潮看了下月相预告……这周根本没有满月！我白等了！',Speaker=892855,Emoji=24,}",
  [162287] = "{id=162287,Text='唉……让我像个废人一样躺在这里吧……',Speaker=892855,}",
  [162288] = "{id=162288,Text='什么？你说愿意帮我完成阿尔勒先生的任务？如果你真的帮我完成了任务，我可以给你一些礼物，虽然薪水微薄，但我还是有一些私藏品的……[c][ffff00]【该任务在同账号下只能完成一次，请确认账号角色后谨慎接取！】[-][/c]',Speaker=892855,Option='{小事一桩！,4},{我没说过……,3}',}",
  [162290] = "{id=162290,Text='唉……是我幻听了吗……我好惨啊……',Speaker=892855,}",
  [162291] = "{id=162291,Text='太好了！那么接下来让我告诉你今天的工作吧，你现在已经不能反悔了哦！',Speaker=892855,Emoji=7,}",
  [162292] = "{id=162292,Text='“亲爱的助手阿尔勒，虽然你已经几次没有完成我的任务了，但我还是决定给你下一次的机会，',Speaker=892855,}",
  [162293] = "{id=162293,Text='明天早上5点前交给我5个星空陨石，它们在克特森林的犬妖聚集地才会存在。祝你一路顺风~”',Speaker=892855,}",
  [162294] = "{id=162294,Text='切，还说什么一路顺风，有本事给我点简单的工作啊！不过既然这次是你帮我做，那我也只能祝你一路顺风哟，让我帮你传送过去吧~',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162295] = "{id=162295,Text='没问题，等你准备好了再来找我哦~',Speaker=892855,}",
  [162296] = "{id=162296,Text='准备好了吗？我要传送了~',Speaker=892855,}",
  [162297] = "{id=162297,Text='冒险者你回来的好快啊，东西都收集齐了吗？',Speaker=892855,}",
  [162298] = "{id=162298,Text='很好，确认无误，那我就交给阿尔勒先生了。如果可以的话明天请继续帮我完成任务哦~',Speaker=892855,}",
  [162299] = "{id=162299,Text='冒险者你真的来了！难道你很喜欢做任务？',Speaker=892855,}",
  [162300] = "{id=162300,Text='“亲爱的助手，昨天的任务完成的很不错，明天5点前帮我收集一些女人的胡须吧，你可以从兽人村落的兽人女战士那里获得，不过千万小心，她们的脾气可不太好~”',Speaker=892855,}",
  [162301] = "{id=162301,Text='女人的胡须？你听听这是人说的话吗？让我找女人要胡须不如让我在太阳上游泳！',Speaker=892855,}",
  [162302] = "{id=162302,Text='不过……嘻嘻，现在就让我把你传送过去吧，一定要小心哦~',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162303] = "{id=162303,Text='你回来啦~什么？你真的拿到了！还好有你帮我，不然我可没信心能得到这种东西……',Speaker=892855,}",
  [162304] = "{id=162304,Text='非常感谢，请明天再来哦~',Speaker=892855,}",
  [162305] = "{id=162305,Text='很高兴你又来了~是不是很期待今天的任务呀，让我来告诉你吧：',Speaker=892855,}",
  [162306] = "{id=162306,Text='“亲爱的助手，我需要赫瓦格密尔的不竭泉水和一些微光之鳞，麻烦你帮我收集一下吧，快去快回哦~”',Speaker=892855,}",
  [162307] = "{id=162307,Text='好吧，赫瓦格密尔?这可真够远的，不过有我帮你传送可就不一样了，是不是？',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162308] = "{id=162308,Text='这就是不竭泉水和微光之鳞？看起来很普通嘛，不知道他又在搞什么东西。',Speaker=892855,}",
  [162309] = "{id=162309,Text='不过他开心就好，感谢你的帮助，明天我们不见不散哦~',Speaker=892855,}",
  [162310] = "{id=162310,Text='终于把你等来了，这么好的天气不来做个任务吗~来看看今天新鲜出炉的吧：',Speaker=892855,}",
  [162311] = "{id=162311,Text='“亲爱的助手，感觉你最近状态不错啊，任务完成的又快又好，那我就可以放心的交给你这次的任务了。',Speaker=892855,}",
  [162312] = "{id=162312,Text='听说德古拉男爵出现在妙勒尼山脉，我需要他身上的吸血面具，就拜托你帮我借一下啦~”',Speaker=892855,}",
  [162313] = "{id=162313,Text='据说戴上吸血面具的人可以不老不死，阿尔勒先生莫非是不想做人了？',Speaker=892855,}",
  [162314] = "{id=162314,Text='呃……我先把你传送过去拿面具，之后得详细问问他。',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162315] = "{id=162315,Text='传说中的面具，好想戴上去试一下啊……别！我只是开个玩笑，请把武器收起来……',Speaker=892855,}",
  [162316] = "{id=162316,Text='我问了阿尔勒先生，原来是一个叫迪奥的朋友拜托他收集的，只是想挂在墙上收藏起来。希望他不要尝试戴上啊……',Speaker=892855,}",
  [162317] = "{id=162317,Text='哈~欠~冒险者早啊，昨天休息的可好？让我们看看阿尔勒先生今天又搞了什么幺蛾子出来吧：',Speaker=892855,}",
  [162318] = "{id=162318,Text='“亲爱的助手，迪奥先生很感谢你帮他得到吸血面具，挂在墙上简直是一件完美的装饰品。接下来他想让你为他获得永生玫瑰，一种生长在极寒冰原中的特殊花朵。”',Speaker=892855,}",
  [162319] = "{id=162319,Text='极寒冰原？让我想想，圣域冰洞怎么样？或者冰霜领域？让我送你去圣域冰洞附近碰碰运气吧~',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162320] = "{id=162320,Text='已经收集好了吗，让我数一数',Speaker=892855,}",
  [162321] = "{id=162321,Text='一、二、三、四、五。刚刚好，真是太感谢了。',Speaker=892855,}",
  [162322] = "{id=162322,Text='我想迪奥先生一定会满意的，那么我们明天见！',Speaker=892855,}",
  [162323] = "{id=162323,Text='冒险者，大事不好了！你还记得阿尔勒先生的朋友迪奥先生吗？',Speaker=892855,}",
  [162324] = "{id=162324,Text='他昨天没忍住好奇心戴上了那个吸血面具，现在变成了一个可怕的魔物，正在古城郊外附近攻击无辜的路人！',Speaker=892855,}",
  [162325] = "{id=162325,Text='请你赶快去那里制止他吧，根据他的行进路线和速度判断，很快他就会到达普隆德拉了！',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162327] = "{id=162327,Text='成功了吗？呼~真是太惊险了，确认已经把面具破坏了？不然被别人捡到恐怕又会横生祸患。',Speaker=892855,}",
  [162328] = "{id=162328,Text='时候不早了，先去好好休息吧，这次的事件实在让人胆战心惊，以后我得劝阿尔勒先生谨慎交友……',Speaker=892855,}",
  [162329] = "{id=162329,Text='新的一天又开始了，这是我最后一次找你寻求帮助了，经过这一周来的思考人生，我感觉不能这么依赖他人，并且这种行为也是在欺骗阿尔勒先生。',Speaker=892855,Option='你终于意识到了',}",
  [162330] = "{id=162330,Text='……，虽然他总是提一些奇奇怪怪的要求，但是作为助手我应该努力支持他的工作，设计出更好的作品！',Speaker=892855,}",
  [162331] = "{id=162331,Text='所以……最后一天的请求是，让我与你一同完成阿尔勒先生今天的任务吧，我要学习一下怎么成为一个任务达人！',Speaker=892855,Option='没问题',}",
  [162332] = "{id=162332,Text='太好了，让我们看下任务内容吧：',Speaker=892855,}",
  [162333] = "{id=162333,Text='“亲爱的助手，迪奥先生给我留了一封信，他有一堆财宝埋藏在吉芬地下2F，如果他不幸去世，就让我把财宝取出来，可自行安排。”',Speaker=892855,}",
  [162334] = "{id=162334,Text='财宝……自行安排……意思是说……我有可能会涨薪了？！',Speaker=892855,Option='你在做梦',}",
  [162335] = "{id=162335,Text='哼，我才不信！让我们快点出发取宝吧！',Speaker=892855,Option='{抓紧时间吧,2},{再准备一下,4}',}",
  [162336] = "{id=162336,Text='没想到迪奥先生居然拥有这么多宝物。唉，人死了，钱没花完， 太悲哀了，怪不得他想长生不老。只可惜变成了魔物……',Speaker=892855,}",
  [162337] = "{id=162337,Text='不过现在，这些财宝都是我的啦~才怪……',Speaker=892855,}",
  [162338] = "{id=162338,Text='刚刚阿尔勒先生说要把这些财宝都捐赠给贫困儿童，我到底什么时候才能涨薪，好不想工作啊……',Speaker=892855,}",
  [162339] = "{id=162339,Text='对了，回来的时候可以使用蝴蝶翅膀，请收下吧~',Speaker=892855,}",
  [162364] = "{id=162364,Text='[c][ffff00][PlayerName][-][/c]，好久不见！',Speaker=891263,Option='生意如何',}",
  [162365] = "{id=162365,Text='很景气。只是……',Speaker=891263,}",
  [162366] = "{id=162366,Text='我缺了一辆咖啡车，它能抵挡住风吹雨打……还能让我把饮料推到人流多的地方贩卖。',Speaker=891263,}",
  [162367] = "{id=162367,Text='听美纳斯说，西门外有许多废弃不用的旧推车，我完全能把它改造成一辆咖啡车。',Speaker=891263,}",
  [162368] = "{id=162368,Text='怎么办呢……嗒啦啦拉不动车……而且忙得抽不出身……有没有哪位好心的冒险者帮帮我这个柔弱的小女孩呀……',Speaker=891263,Option='好吧，我去',}",
  [162369] = "{id=162369,Text='嘻嘻，谢谢你哇！',Speaker=891263,}",
  [162370] = "{id=162370,Text='记住，要有两层以上抽屉、带遮阳棚、不涂漆的……',Speaker=891263,Option='（不理并出发）',}",
  [162371] = "{id=162371,Text='前方是一架旧餐车，有三层抽屉、带遮阳棚、不涂漆。',}",
  [162372] = "{id=162372,Text='大家，快看快看~',Speaker=891266,}",
  [162373] = "{id=162373,Text='这家伙在碰咱们存香蕉过冬的秘密基地欸。',Speaker=891266,}",
  [162374] = "{id=162374,Text='滚滚滚！私人用品！概不退换！',Speaker=891266,Option='{词用错了……,0},{这不是私人用品,0}',}",
  [162375] = "{id=162375,Text='看来……我无意中的话惹它们更生气了。',}",
  [162376] = "{id=162376,Text='它们就像点燃引线的火药桶……我只能上了！——为了推车！',}",
  [162377] = "{id=162377,Text='即退即换！立即退换！',Speaker=891266,}",
  [162378] = "{id=162378,Text='(溜溜猴们一边嚷着古怪的商业用语，一边从餐车中紧急撤出了香蕉，逃进了大森林里。)',}",
  [162379] = "{id=162379,Text='哎哟喂！',}",
  [162380] = "{id=162380,Text='[c][ffff00][PlayerName][-][/c]活该该！[c][ffff00][PlayerName][-][/c]摔倒的样子真好看！嘻嘻嘻！',Speaker=891266,}",
  [162381] = "{id=162381,Text='不小心踩到了溜溜猴的香蕉皮，摔倒了……！',}",
  [162382] = "{id=162382,Text='叽嘻嘻嘻嘻嘻嘻……',Speaker=891266,}",
  [162383] = "{id=162383,Text='——？！[c][ffff00][PlayerName][-][/c]的救兵来啦！清仓大处理！',Speaker=891266,}",
  [162384] = "{id=162384,Text='[c][ffff00][PlayerName][-][/c]，怎么去了那么久都没回来……',Speaker=891263,}",
  [162385] = "{id=162385,Text='——[c][ffff00][PlayerName][-][/c]……？你刚才晕倒了？你还好吗？！',Speaker=891263,Option='只是摔了一跤',}",
  [162386] = "{id=162386,Text='呼，看上去没什么大碍……太好了……',Speaker=891263,}",
  [162387] = "{id=162387,Text='……话说，这是……你给我选的推车吗？',Speaker=891263,Option='嗯',}",
  [162388] = "{id=162388,Text='唔……',Speaker=891263,Option='怎么样？',}",
  [162389] = "{id=162389,Text='——嗯~！很、很合我心意！',Speaker=891263,}",
  [162390] = "{id=162390,Text='不过，你今天为我栽了个大跟头，我想我……还是不能再麻烦你了。',Speaker=891263,}",
  [162391] = "{id=162391,Text='这架推车，还是我自己推回去吧。',Speaker=891263,Option='没问题？',}",
  [162392] = "{id=162392,Text='没问题！我可是高玩！办法总能找到！',Speaker=891263,}",
  [162393] = "{id=162393,Text='另外，这点礼物给你犒劳犒劳。',Speaker=891263,}",
  [162394] = "{id=162394,Text='以后走路时当心路面哦~',Speaker=891263,Option='嗯！',}",
  [162395] = "{id=162395,Text='锵锵锵锵——改造完成——',Speaker=891263,}",
  [162396] = "{id=162396,Text='看，这就是TapTap咖啡车！',Speaker=891263,}",
  [162397] = "{id=162397,Text='怎么样？',Speaker=891263,Option='点赞',}",
  [162398] = "{id=162398,Text='很好……离正式修复噗噜噜只有一步了！',Speaker=891263,}",
  [162399] = "{id=162399,Text='——但在此之前，唔哼哼。',Speaker=891263,}",
  [162400] = "{id=162400,Text='我要在这里的角落放周边展销架！还要到那边挂块大屏幕，承接各色广告……！',Speaker=891263,}",
  [162401] = "{id=162401,Text='嘀咕什么呢！让我站这里老半天！不做生意了？！',Speaker=891269,}",
  [162402] = "{id=162402,Text='对、对不起，我刚刚在和朋友聊天。',Speaker=891263,}",
  [162403] = "{id=162403,Text='稍等一会儿，我马上……',Speaker=891263,}",
  [162404] = "{id=162404,Text='好时髦的装修！',Speaker=891270,}",
  [162405] = "{id=162405,Text='——请问？你们这里最便宜的饮料是……？',Speaker=891270,}",
  [162406] = "{id=162406,Text='喂，别插队啊？！',Speaker=891269,}",
  [162407] = "{id=162407,Text='不好意思！',Speaker=891270,}",
  [162408] = "{id=162408,Text='——这位顾客，请您耐心等待叫号！',Speaker=891263,}",
  [162409] = "{id=162409,Text='哇，嗒啦啦忙碌的样子也好可爱，俺要拍下来珍藏啊……',Speaker=891271,}",
  [162410] = "{id=162410,Text='……谢、谢谢！但店前是不能随便拍照的！',Speaker=891263,Emoji=15,}",
  [162411] = "{id=162411,Text='——菜单呢？你要我喝空气吗？！',Speaker=891269,}",
  [162412] = "{id=162412,Text='请您稍等一下，我马上把调配手册……',Speaker=891263,}",
  [162413] = "{id=162413,Text='你好？有人吗？我想点杯TapTap能量……',Speaker=891283,}",
  [162414] = "{id=162414,Text='来啦————！',Speaker=891263,Emoji=18,}",
  [162415] = "{id=162415,Text='呜呜……拜托你……[c][ffff00][PlayerName][-][/c]，给这边的三位客人挑三种适合的饮料吧。',Speaker=891263,Option='怎么做',}",
  [162416] = "{id=162416,Text='抽屉里放着三种饮料的调配手册……你可以翻来看看！',Speaker=891263,}",
  [162417] = "{id=162417,Text='TapTap拿铁：咖啡因饮料。苦苦滋味，提振精神，舒缓心情。\nTapTap能量：碳酸饮料。流行饮品，穿心冲击力，全天嗨皮！\nTapTap特调：乳酸菌饮料。有助肠道消化，使人健康自信。面向儿童顾客，非常便宜。',Speaker=891272,}",
  [162418] = "{id=162418,Text='这就是以上三款饮料的特性……如果记不住，可以随时回来查看。',}",
  [162419] = "{id=162419,Text='奥丁啊奥丁！我不过想业余时放松放松心情……怎么总有人浪费我时间……',Speaker=891269,}",
  [162420] = "{id=162420,Text='给这位冒险者递上哪款饮料呢？',Option='{TapTap拿铁,2},{TapTap能量,3},{TapTap特调,3}',}",
  [162421] = "{id=162421,Text='这什么呀？黑乎乎的，不会是给小朋友尝的巧克力吧……',Speaker=891269,}",
  [162422] = "{id=162422,Text='嗯？…………嗯~~！（点头默叹）',Speaker=891269,}",
  [162423] = "{id=162423,Text='苦而不涩，浓郁且回味无穷。头脑也清醒了不少。',Speaker=891269,Emoji=9,}",
  [162424] = "{id=162424,Text='哼，这家店的员工很懂顾客心嘛。回头我会给你们好评推荐的！',Speaker=891269,Option='谢谢惠顾',}",
  [162425] = "{id=162425,Text='少拿小孩口味糊弄我！我还要通宵赶报表呢！',Speaker=891269,}",
  [162426] = "{id=162426,Text='我第一次来这种店……请问最实惠的饮料是……？',Speaker=891270,}",
  [162427] = "{id=162427,Text='给这位冒险者递上哪款饮料呢？',Option='{TapTap拿铁,4},{TapTap能量,4},{TapTap特调,5}',}",
  [162428] = "{id=162428,Text='价格正好。只是……味道会不会也跟着打折呢……（吸一口）',Speaker=891270,}",
  [162429] = "{id=162429,Text='——太·感·动·了！！！',Speaker=891270,}",
  [162430] = "{id=162430,Text='我长这么大都没喝过这么好喝的东西！',Speaker=891270,}",
  [162431] = "{id=162431,Text='这就是大家常说的童年和健康的味道吗！要是以后喝不到怎么办啊啊啊啊！',Speaker=891270,Emoji=18,}",
  [162432] = "{id=162432,Text='我、我这就带回去给妈妈尝尝！',Speaker=891270,Option='欢迎下次再来',}",
  [162433] = "{id=162433,Text='钱没带够……呃，请给我换一杯健康系饮品，谢谢！',Speaker=891270,}",
  [162434] = "{id=162434,Text='其实俺也不想给嗒啦啦添麻烦……但俺平时真的好孤独，好忧郁……',Speaker=891271,}",
  [162435] = "{id=162435,Text='只有嗒啦啦的元气才能治愈俺的心灵。',Speaker=891271,}",
  [162436] = "{id=162436,Text='给这位冒险者递上哪款饮料呢？',Option='{TapTap拿铁,6},{TapTap能量,7},{TapTap特调,6}',}",
  [162437] = "{id=162437,Text='咕噜咕噜咕噜……（一口气喝了半杯的气泡声）',Speaker=891271,}",
  [162438] = "{id=162438,Text='哈啊~！（满足又销魂地打嗝）',Speaker=891271,}",
  [162439] = "{id=162439,Text='啊，嗒啦啦亲手调配的饮料……',Speaker=891271,}",
  [162440] = "{id=162440,Text='这杯快乐水中一定蕴含了嗒啦啦的少女元气吧❤',Speaker=891271,Emoji=29,}",
  [162441] = "{id=162441,Text='抱、抱歉！打包就不用了，俺要捧在手心里带走，慢·慢·回·味！',Speaker=891271,Option='祝您用餐愉快',}",
  [162442] = "{id=162442,Text='谢谢，但俺要……更有元气的……嘿嘿……',Speaker=891271,}",
  [162443] = "{id=162443,Text='终于忙完了——你这边怎样？',Speaker=891263,Option='交付账单',}",
  [162444] = "{id=162444,Text='建了咖啡车后，生意果然红火不少！',Speaker=891263,Emoji=13,}",
  [162445] = "{id=162445,Text='这点礼物发给你，当开门红福利啦~可敬可爱的打工人！',Speaker=891263,Option='谢谢老板',}",
  [162446] = "{id=162446,Text='嗯，测试结果正常！',Speaker=891263,Option='嗒啦啦？',}",
  [162447] = "{id=162447,Text='咚咚咚咚！（反复敲击）',Speaker=891263,Option='嗯？',}",
  [162448] = "{id=162448,Text='你好，我的名字是反魔法金属探测器。',Speaker=891273,Option='呃，你好',}",
  [162449] = "{id=162449,Text='也许……我就是为了与你相遇，而诞生到这世上的。',Speaker=891273,Option='……？',}",
  [162450] = "{id=162450,Text='[c][ffff00][PlayerName][-][/c]，不用理它。这家伙真的只是台普通探测器而已！',Speaker=891263,Option='探测器？',}",
  [162451] = "{id=162451,Text='修复噗噜噜需要隔绝魔法元素。但是反魔法的材料非常稀有……',Speaker=891263,}",
  [162452] = "{id=162452,Text='我于是专门做了台探测器，跟着它就能探测到就近的[c][ffff00]反魔法金属[-][/c]了。',Speaker=891263,}",
  [162453] = "{id=162453,Text='但是呀，探测的功能算得上完整，人格却怪怪的……',Speaker=891263,}",
  [162454] = "{id=162454,Text='要我再调整一下社交度吗……？',Speaker=891263,Option='能用就行',}",
  [162455] = "{id=162455,Text='那么～采矿的事可以拜托你吗？',Speaker=891263,Option='好好好',}",
  [162456] = "{id=162456,Text='高高的天空颇为深远，仿佛你的眼睛，让我无法洞穿而愈想遨游……',Speaker=891273,Option='什么？',}",
  [162457] = "{id=162457,Text='我是说，这里没有我们要找的东西。我们往另一边前进。',Speaker=891273,Option='好吧',}",
  [162458] = "{id=162458,Text='我感受到了——我灵魂中的热忱即将熄灭，幸运的是，你的爱之火最终追逐而上，将我点燃。',Speaker=891273,Option='什么？！',}",
  [162459] = "{id=162459,Text='我是说，刚才那里没有信号，但这边有反应了，请继续前进。',Speaker=891273,Option='好',}",
  [162460] = "{id=162460,Text='斑斓的宝石，仿佛璀璨的星辰……',Speaker=891273,Option='说人话',}",
  [162461] = "{id=162461,Text='我检测到：魔力波动在此处出现了明显的断层。',Speaker=891273,Option='说人话',}",
  [162462] = "{id=162462,Text='那位女士要买的钻戒中，蕴藏有[c][ffff00]反魔法金属[-][/c]。',Speaker=891273,Option='哦',}",
  [162463] = "{id=162463,Text='呵呵，见到绝美之物的心情有多急切，我完全能够理解。',Speaker=891274,}",
  [162464] = "{id=162464,Text='但小可爱，独独这枚戒指，我可万万不能交给你。',Speaker=891274,}",
  [162465] = "{id=162465,Text='唯独我这朵红艳艳的高岭之花，有资格让这颗钻石绽出光彩……',Speaker=891274,}",
  [162466] = "{id=162466,Text='千万别忘了——要买下它的经济实力，你是不会有的哦！',Speaker=891274,Option='……',}",
  [162467] = "{id=162467,Text='所以我是绝对要戴在手上、绝对绝对不会摘下来的！',Speaker=891274,}",
  [162468] = "{id=162468,Text='您确定，您不会摘下来？',Speaker=891273,}",
  [162469] = "{id=162469,Text='当然！……等等……？',Speaker=891274,}",
  [162470] = "{id=162470,Text='这是你家的玩具？太没礼貌了！怎么能对我要买什么指指点点……',Speaker=891274,}",
  [162471] = "{id=162471,Text='如果您决定戴上戒指，那么，您就会成为我的毕生意义。',Speaker=891273,}",
  [162472] = "{id=162472,Text='好端端地……你在对淑女人家说什么呢你……！',Speaker=891274,}",
  [162473] = "{id=162473,Text='您灵魂中的独特引力，仿佛湖水一般，映照着吾之所指。',Speaker=891273,}",
  [162474] = "{id=162474,Text='可是——真的好动听啊…啊啊啊……',Speaker=891274,}",
  [162475] = "{id=162475,Text='如果您是湖，那我，便将化身汪洋！',Speaker=891273,}",
  [162476] = "{id=162476,Text='我的弗雷呀……连我爸爸都没夸过我……',Speaker=891274,}",
  [162477] = "{id=162477,Text='（已经完全沉溺在甜言蜜语中了）',}",
  [162478] = "{id=162478,Text='抱歉抱歉，我收回刚才的话！——我要用钻戒换你的玩具！',Speaker=891274,Option='为什么……',Emoji=29,}",
  [162479] = "{id=162479,Text='没什么比一生难遇的挚爱更珍贵了！',Speaker=891274,}",
  [162480] = "{id=162480,Text='万万个钻戒我都可以给你！',Speaker=891274,Option='不用了',}",
  [162481] = "{id=162481,Text='噢，您可真是个大善人，那、那还能请您……',Speaker=891274,}",
  [162482] = "{id=162482,Text='请您快拿出相机！纪念与吾爱的初遇时刻！',Speaker=891274,Option='好呀',}",
  [162483] = "{id=162483,Text='（抢过相片）成交！',Speaker=891274,}",
  [162484] = "{id=162484,Text='亲爱的！我们这就回家去！',Speaker=891274,}",
  [162485] = "{id=162485,Text='真的是反魔法金属！太谢谢你啦！',Speaker=891263,}",
  [162486] = "{id=162486,Text='呃，但是这枚钻戒……是不是要付很多钱……？',Speaker=891263,Option='告知详情',}",
  [162487] = "{id=162487,Text='不需要花钱是挺好……总感觉有些可惜。',Speaker=891263,}",
  [162488] = "{id=162488,Text='我还计划把那台探测器改装成执事型服务机器人，吸引新客源呢……',Speaker=891263,}",
  [162489] = "{id=162489,Text='不管怎么说，材料顺利找到了。[c][ffff00][PlayerName][-][/c]，让我犒劳一下你吧~',Speaker=891263,Option='谢谢老板',}",
  [162490] = "{id=162490,Text='怎么办呢……没有别的办法了吗……',Speaker=891263,Option='嗒啦啦？',}",
  [162491] = "{id=162491,Text='啊，[c][ffff00][PlayerName][-][/c]，你能帮我……帮我……',Speaker=891263,}",
  [162492] = "{id=162492,Text='——摸一条狗吗？',Speaker=891263,Option='摸狗？',}",
  [162493] = "{id=162493,Text='我需要防磁材料来修复噗噜噜……',Speaker=891263,}",
  [162494] = "{id=162494,Text='于是阿格尼斯会长就给我送来一只机械猎犬，让我取走它项圈中的防磁吸波片。',Speaker=891263,}",
  [162495] = "{id=162495,Text='但我发现它有了自我意识，算得上一条活生生的生命了……',Speaker=891263,}",
  [162496] = "{id=162496,Text='我本想安全地取下项圈，所以破解了它的认证程序。它却还是凶凶的……',Speaker=891263,}",
  [162497] = "{id=162497,Text='我不想强行让它停机，所以冒险者，拜托你了。',Speaker=891263,Option='我试试吧',}",
  [162498] = "{id=162498,Text='（狂吠）入侵警告！入侵警告！！！',Speaker=891275,Option='咬过来啦！',}",
  [162499] = "{id=162499,Text='嗷……嗷呜（沮丧地趴下了）',Speaker=891275,}",
  [162500] = "{id=162500,Text='（它充满冷漠地望着这边，尾巴下垂着）',Speaker=891275,Option='{抚摸,2},{摘项圈,3}',}",
  [162501] = "{id=162501,Text='汪、汪！（戒备地夹着平衡索尾，亮起了警告红灯）',Speaker=891275,Action={actionid=23,num=1},}",
  [162502] = "{id=162502,Text='唔……汪。（两个声音接收器耷拉着）',Speaker=891275,Emoji=30,}",
  [162503] = "{id=162503,Text='（有种预感：还不能拿走项圈，否则它会撇过头来咬人的）',}",
  [162504] = "{id=162504,Text='（它期待地歪过头，试图用摄像头从另一条轴观察这里）',Speaker=891275,Option='{抚摸,5},{摘项圈,6}',}",
  [162505] = "{id=162505,Text='呜~~~呜！（它的头部关节收紧，进入防御模式）',Speaker=891275,Action={actionid=23,num=1},}",
  [162506] = "{id=162506,Text='（得重来一遍了）',}",
  [162507] = "{id=162507,Text='嗷、嗷……（试探性地翻起肚子）',Speaker=891275,Action={actionid=7,num=1},}",
  [162508] = "{id=162508,Text='（黄灯停闪了，绿灯闪烁着）',}",
  [162509] = "{id=162509,Text='（下次也许能够拿走项圈！）',}",
  [162510] = "{id=162510,Text='（它懒洋洋地露出了腹舱的燃油缸）',Speaker=891275,Option='{抚摸,7},{摘项圈,8}',}",
  [162511] = "{id=162511,Text='哼呜呜……（舒服地晃动着四只起动关节）',Speaker=891275,}",
  [162512] = "{id=162512,Text='（之后，它不明白你的意图，重新站起来面对这边）',}",
  [162513] = "{id=162513,Text='（黄灯亮起，它又回到警戒模式了）',}",
  [162514] = "{id=162514,Text='呜呜……嗷……（项圈上，闪烁绿色光线的安全阀有响动）',Speaker=891275,Emoji=30,}",
  [162515] = "{id=162515,Text='(轻轻按下安全阀按钮，项圈一段段松开！)',}",
  [162516] = "{id=162516,Text='（成功拿到项圈了！）',}",
  [162517] = "{id=162517,Text='这硬狗狗可一点都不毛茸茸。你是怎么把它摸到服服帖帖的！',Speaker=891263,Option='一点耐心',}",
  [162518] = "{id=162518,Text='我想[c][ffff00][PlayerName][-][/c]你的手，也可能存在着某种……非凡的“耐性”……？',Speaker=891263,}",
  [162519] = "{id=162519,Text='不说了！我要给它找户好心人家，不然它会被送到零件回收厂的……',Speaker=891263,}",
  [162520] = "{id=162520,Text='对了，这是今天的酬劳哦！',Speaker=891263,Option='谢谢老板',}",
  [162521] = "{id=162521,Text='[c][ffff00][PlayerName][-][/c]，呜呜呜……',Speaker=891263,Option='怎么回事？',}",
  [162522] = "{id=162522,Text='噗噜噜它……可能再也修不好了……',Speaker=891263,}",
  [162523] = "{id=162523,Text='我现在需要……高科技！对，这个世界最好的高科技！',Speaker=891263,}",
  [162524] = "{id=162524,Text='这样我才能找到不受干涉的环境修好量子核心！',Speaker=891263,Option='听不懂',}",
  [162525] = "{id=162525,Text='冒险者……我有个小建议！',Speaker=891265,}",
  [162526] = "{id=162526,Text='（悄悄话）虽然我听不懂嗒啦啦说的什么……“量子力学”……？',Speaker=891265,}",
  [162527] = "{id=162527,Text='但我猜，她是想要制造一种……',Speaker=891265,}",
  [162528] = "{id=162528,Text='时间与空间上，都较为隔绝的环境……？',Speaker=891265,Option='比如钟塔？',}",
  [162529] = "{id=162529,Text='说到钟塔——对了，你还记得[c][ffff00]破碎时空指针[-][/c]吗！',Speaker=891265,}",
  [162530] = "{id=162530,Text='它是钟塔魔物独有的零件，也许能制造出嗒啦啦想要的……“相对不受干扰”的环境！',Speaker=891265,Option='收集一些试试',}",
  [162531] = "{id=162531,Text='好！',Speaker=891265,}",
  [162532] = "{id=162532,Text='准备好前往艾尔帕兰了吗！',Speaker=891265,Option='{准备好了,2},{再等等,3}',}",
  [162533] = "{id=162533,Text='我们直接去钟楼入口吧！',Speaker=891265,}",
  [162534] = "{id=162534,Text='没关系，今天是我在卡普拉的调休日，我可以全天候等你~！',Speaker=891265,}",
  [162535] = "{id=162535,Text='钟楼内危机遍布，不想冒险的话，记得别走出前廊~！',Speaker=891265,Option='明白了',}",
  [162536] = "{id=162536,Text='是钟怪！可以击杀它们，拿到嗒啦啦想要的东西！',Speaker=891265,Option='上吧',}",
  [162537] = "{id=162537,Text='我感受到周围正形成一股稳定的时间场……快去收集三种破碎时空指针吧。',Speaker=891265,}",
  [162538] = "{id=162538,Text='空间状态稳定！我们能重置噗噜噜内部的时空场了~！',Speaker=891265,Option='在说什么……',}",
  [162539] = "{id=162539,Text='我自己也不清楚自己在说什么……',Speaker=891265,}",
  [162540] = "{id=162540,Text='但是，出于女孩子的第六感，我相信这正是嗒啦啦要的东西！',Speaker=891265,}",
  [162541] = "{id=162541,Text='谢谢你，冒险者，这些小礼物给你。',Speaker=891265,}",
  [162542] = "{id=162542,Text='我先回去，把指针带给嗒啦啦了~！',Speaker=891265,Option='再见！',}",
  [162543] = "{id=162543,Text='[c][ffff00][PlayerName][-][/c]，多亏了你呀！',Speaker=891263,}",
  [162544] = "{id=162544,Text='你和美纳斯给我的指针，让我成功修好了噗噜噜！',Speaker=891263,}",
  [162545] = "{id=162545,Text='DU DU DI DU DONG DA LA LA \n[c][ffff00][PlayerName][-][/c]，谢谢你帮助嗒啦啦。',Speaker=891264,Emoji=6,}",
  [162546] = "{id=162546,Text='对啦噗噜噜，要吃些什么庆祝一下吗……',Speaker=891263,}",
  [162547] = "{id=162547,Text='等等！不用你说，我猜到啦！那一定是……！',Speaker=891263,}",
  [162548] = "{id=162548,Text='DU DU! \n[c][ffff00]没错，正是——“桃子”！[-][/c]',Speaker=891264,Option='机器能吃东西？',}",
  [162549] = "{id=162549,Text='BI BU BONG \n[c][ffff00]吃不了，我也可以观赏它。[-][/c]',Speaker=891264,Option='好吧',Emoji=29,}",
  [162550] = "{id=162550,Text='我逛了许多家水果摊，没一家有卖桃子。店家甚至从没听过桃子这种水果……',Speaker=891263,Option='卢恩没有桃子',}",
  [162551] = "{id=162551,Text='是吗？但我……在下水道里捞起好多桃子……',Speaker=891263,Option='下水道？',}",
  [162552] = "{id=162552,Text='DI BU BI BU BONG \n[c][ffff00]脏桃子是不能吃的。[-][/c]',Speaker=891264,Emoji=1,}",
  [162553] = "{id=162553,Text='而且，捞到的不是桃核，是有肉的、完完整整的、没有被尝一口的桃子……！',Speaker=891263,}",
  [162554] = "{id=162554,Text='DA BI LA！\n[c][ffff00]谁这么浪费！[-][/c]',Speaker=891264,}",
  [162555] = "{id=162555,Text='据我分析，桃子是从西门的下水道口扔下去的！',Speaker=891263,}",
  [162556] = "{id=162556,Text='DI DU DONG DA BI！\n[c][ffff00]找出乱丢桃子的元凶！[-][/c]',Speaker=891264,Option='重点在这里？',Emoji=12,}",
  [162557] = "{id=162557,Text='好了好了……别生气了，噗噜噜。',Speaker=891263,}",
  [162558] = "{id=162558,Text='结论是：西门似乎有新鲜桃子~[c][ffff00][PlayerName][-][/c]，请你帮我俩弄些来~！',Speaker=891263,Option='好的',}",
  [162559] = "{id=162559,Text='奇了怪了……我们的猴子明明很喜欢桃子呀？',Speaker=891277,}",
  [162560] = "{id=162560,Text='（一个斐扬人？等等，他的手里有个桃子……）',}",
  [162561] = "{id=162561,Text='来得正好呀，当地冒险者。我是一位云游的斐扬人。',Speaker=891277,}",
  [162562] = "{id=162562,Text='请问一下，要怎样才能逗这群猴子们开心呢？',Speaker=891277,Emoji=8,}",
  [162563] = "{id=162563,Text='我喂了很多桃子，但没什么用……',Speaker=891277,Option='桃子？',}",
  [162564] = "{id=162564,Text='差点忘了，你们没怎么见过桃子吧——',Speaker=891277,}",
  [162565] = "{id=162565,Text='这桃子，是我们斐扬常见的一种水果。',Speaker=891277,}",
  [162566] = "{id=162566,Text='斐扬人酿桃花酒，种植桃树。',Speaker=891277,}",
  [162567] = "{id=162567,Text='神话中的溜溜猴英雄“哈奴曼”更以嗜吃桃子闻名。',Speaker=891277,}",
  [162568] = "{id=162568,Text='但你们卢恩的猴子——却完全不吃这一套！',Speaker=891277,}",
  [162569] = "{id=162569,Text='那个红红的果子闻起来哪里有芭娜娜香啊！爷要吃芭娜娜才不吃它！',Speaker=891266,Option='噫',}",
  [162570] = "{id=162570,Text='脏脏！不吃不吃！扔下水道啦！',Speaker=891266,}",
  [162571] = "{id=162571,Text='怎么办……这是我此行带来的最后一个桃子了……',Speaker=891277,Option='我来和你换',Emoji=15,}",
  [162572] = "{id=162572,Text='噢，你要用什么食物和我的桃子换，好满足这些溜溜猴呢！',Speaker=891277,Option='{曲奇盒,2},{香蕉,3},{软糖罐,2}',}",
  [162573] = "{id=162573,Text='硬硬的，开不了！不要不要！',Speaker=891266,}",
  [162574] = "{id=162574,Text='诶嘿嘿！芭娜娜！——昂昂昂昂昂！',Speaker=891266,}",
  [162575] = "{id=162575,Text='原来是这样啊！',Speaker=891277,}",
  [162576] = "{id=162576,Text='我们的溜溜猴嗜好桃子,你们的溜溜猴却偏爱香蕉！！',Speaker=891277,}",
  [162577] = "{id=162577,Text='这就是文化差异吧！',Speaker=891277,}",
  [162578] = "{id=162578,Text='谢谢你，冒险者！这个桃子就送给你了~！',Speaker=891277,}",
  [162579] = "{id=162579,Text='哎哟……！',Speaker=891277,}",
  [162580] = "{id=162580,Text='(他怎么也滑了一跤……溜溜猴们太调皮了。)',}",
  [162581] = "{id=162581,Text='只有这一颗桃子了吗？',Speaker=891263,Option='只剩一个啦',}",
  [162582] = "{id=162582,Text='那么……噗噜噜……我想把这颗桃子让给[c][ffff00][PlayerName][-][/c]吃~！',Speaker=891263,Option='我吗',}",
  [162583] = "{id=162583,Text='DU DU \n[c][ffff00]好主意。[-][/c]',Speaker=891264,Option='好哇',}",
  [162584] = "{id=162584,Text='DI DU DONG DO DU DU! \n[c][ffff00]你品尝桃子的甜蜜笑容，使我非常开心！[-][/c]',Speaker=891264,Option='谢谢',}",
  [162585] = "{id=162585,Text='我也很开心。谢谢你一直以来的帮助……[c][ffff00][PlayerName][-][/c]。',Speaker=891263,}",
  [162586] = "{id=162586,Text='对我来说，你就像实现愿望的万事屋一样……',Speaker=891263,}",
  [162587] = "{id=162587,Text='……我、我在说什么让人舍不得的话呢！',Speaker=891263,}",
  [162588] = "{id=162588,Text='明明马上要分别了……',Speaker=891263,}",
  [162589] = "{id=162589,Text='DA LA LA \n[c][ffff00]嗒啦啦……[-][/c]',Speaker=891264,}",
  [162590] = "{id=162590,Text='DO DI DU DONG…… \n[c][ffff00]我想，我们可以继续留在这里……[-][/c]',Speaker=891264,}",
  [162591] = "{id=162591,Text='——对啊……也不是非要现在回去！',Speaker=891263,}",
  [162592] = "{id=162592,Text='我宣布——TapTap咖啡车，明天照常开业。',Speaker=891263,}",
  [162593] = "{id=162593,Text='为了庆祝噗噜噜恢复，送你一些小礼物当纪念~',Speaker=891263,}",
  [162594] = "{id=162594,Text='要记得多多常来喔！',Speaker=891263,Option='好耶！',}",
  [162596] = "{id=162596,Text='冒险者，告诉你一个好消息~',Speaker=891263,}",
  [162597] = "{id=162597,Text='从今天开始我每天都会免费为你提供一杯TapTap特饮~不过只能当场饮用哦。',Speaker=891263,Option='{我要一杯~,2},{等会再来,3}',}",
  [162598] = "{id=162598,Text='那么从以下三种饮品中选择一个吧~',Speaker=891263,Option='{TapTap 拿铁,4},{TapTap 能量,5},{TapTap 特调,6}',}",
  [162599] = "{id=162599,Text='好的，我一直都在这里哟~',Speaker=891263,}",
  [162600] = "{id=162600,Text='嗨！冒险者，我有了新的饰品设计方案，但是我还缺少一些材料才能把这些饰品制作出来。',Speaker=892862,}",
  [162601] = "{id=162601,Text='虽然有个助手帮我工作，但是他最近总是摸鱼，指望他的话明年我都没办法把这些饰品完成，请问你可以帮助进行收集吗？',Speaker=892862,}",
  [162602] = "{id=162602,Text='每天我都会收取两次材料，如果你向我提供，我可以给你一些报酬哦~',Speaker=892862,}",
  [162603] = "{id=162603,Text='[c][ffff00]不过我需要的材料数量和种类可是很多的哦，你可要好好收集~[-][/c]',Speaker=892862,}",
  [162604] = "{id=162604,Text='那么你有想要交给我的材料吗？',Speaker=892862,Option='{腐烂绷带500个,3},{恶魔角500个,4},{远古遗骸250个,5},{微风的传言300个,6}',}",
  [162605] = "{id=162605,Text='确定咯？那就搜集齐后交给我吧！',Speaker=892862,}",
  [162606] = "{id=162606,Text='亲爱的冒险者，材料收集好了吗？',Speaker=892862,Option='{上交,31},{还没有,3}',}",
  [162607] = "{id=162607,Text='数量好像不对呀~',Speaker=892862,Emoji=40,}",
