  [162619] = "{id=162619,Text='那么你有想要交给我的材料吗？',Speaker=892862,Option='{腐烂绷带500个,3},{不死心脏500个,4},{微风的传言300个,5},{远古遗骸250个,6}',}",
  [162620] = "{id=162620,Text='不愧是我认可冒险者，那你今天还有想交给我的材料吗？',Speaker=892862,Option='{腐烂绷带500个,3},{不死心脏500个,4},{微风的传言300个,5},{远古遗骸250个,6}',Emoji=28,}",
  [162621] = "{id=162621,Text='那么你有想要交给我的材料吗？',Speaker=892862,Option='{亡者遗物500个,3},{恶魔角500个,4},{微风的传言300个,5},{远古遗骸250个,6}',}",
  [162622] = "{id=162622,Text='不愧是我认可冒险者，那你今天还有想交给我的材料吗？',Speaker=892862,Option='{亡者遗物500个,3},{恶魔角500个,4},{微风的传言300个,5},{远古遗骸250个,6}',Emoji=28,}",
  [162623] = "{id=162623,Text='那么你有想要交给我的材料吗？',Speaker=892862,Option='{铁矿石500个,3},{慧眼500个,4},{微风的传言300个,5},{远古遗骸250个,6}',}",
  [162624] = "{id=162624,Text='不愧是我认可冒险者，那你今天还有想交给我的材料吗？',Speaker=892862,Option='{铁矿石500个,3},{慧眼500个,4},{微风的传言300个,5},{远古遗骸250个,6}',Emoji=28,}",
  [162626] = "{id=162626,Text='材料收集的差不多了，你就尽情期待我的大作完成吧！',Speaker=892862,}",
  [162630] = "{id=162630,Text='呜哇，交稿死线没几天了……再找不到人的话就惨了！',Speaker=891289,Option='{交稿？,2},{找人？,2}',}",
  [162631] = "{id=162631,Text='啊呀？你是过来帮忙的冒险者吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891289,Option='{是的,3},{不是,4}',}",
  [162632] = "{id=162632,Text='太好了！拜托你！一定要把阿尔勒大人给我找到啊啊啊啊——',Speaker=891289,}",
  [162633] = "{id=162633,Text='呃，你不知道阿尔勒大人？',Speaker=891289,Option='是谁？',Emoji=8,}",
  [162634] = "{id=162634,Text='不会吧？！不会吧不会吧？！米德加尔特知名设计师阿尔勒大人你不知道？！',Speaker=891289,}",
  [162635] = "{id=162635,Text='“天上之人”系列饰品你不知道？！就连皇室也青睐的超~有名作品，你不知道？！',Speaker=891289,Option='{不知道,0},{（好像听说过）,0}',}",
  [162636] = "{id=162636,Text='简直震撼，我从来没想过有人居然没听过阿尔勒大人的名字……',Speaker=891289,Option='{那么,0},{这个阿尔勒,0}',Emoji=1,}",
  [162637] = "{id=162637,Text='哦，是的。我跟随阿尔勒大人来普隆德拉旅游，但是……',Speaker=891289,}",
  [162638] = "{id=162638,Text='……阿尔勒大人，走失了。',Speaker=891289,}",
  [162639] = "{id=162639,Text='我们昨天刚到的普隆德拉。休息了一晚，阿尔勒大人就打算去西门了。',Speaker=891289,}",
  [162640] = "{id=162640,Text='因为走得很匆忙，感觉又累又渴，我就去买饮料，但是回来以后就找不到人了……',Speaker=891289,Emoji=10,}",
  [162641] = "{id=162641,Text='我把阿尔勒大人丢了啊啊啊！！',Speaker=891289,Option='{冷静点,6},{他会去哪里,7}',}",
  [162642] = "{id=162642,Text='好的好的，呼……呼……',Speaker=891289,}",
  [162643] = "{id=162643,Text='如果我能知道他会去哪里我就自己找了好吗！',Speaker=891289,}",
  [162644] = "{id=162644,Text='……因为阿尔勒大人来普隆德拉是为了取材的，所以我觉得，他肯定会去自己感兴趣的地方。',Speaker=891289,Option='比如？',}",
  [162645] = "{id=162645,Text='嗯，大概就是西门那边历史悠久、名声在外的一些著名景点吧？',Speaker=891289,}",
  [162646] = "{id=162646,Text='可惜我不是生活在这里，并不知道这里哪些地方最有名。',Speaker=891289,Option='{我明白了,0},{交给我吧,0}',}",
  [162647] = "{id=162647,Text='那就看你的了！',Speaker=891289,}",
  [162648] = "{id=162648,Text='阿尔勒大人……还有设计稿没完成啊！',Speaker=891289,}",
  [162649] = "{id=162649,Text='（这里有没有呢……）',}",
  [162650] = "{id=162650,Text='普勒花园……不过如此。',Speaker=891296,}",
  [162651] = "{id=162651,Text='（真有气质啊……难道那个人就是？）',}",
  [162652] = "{id=162652,Text='………………………………',Speaker=891288,Option='{请问……,0},{您是阿尔——,0}',}",
  [162653] = "{id=162653,Text='………………………………（充耳不闻）',Speaker=891288,}",
  [162654] = "{id=162654,Text='你跟踪我？',Speaker=891296,}",
  [162655] = "{id=162655,Text='跟踪我做什么？要钱？',Speaker=891296,}",
  [162656] = "{id=162656,Text='不是？那为什么？',Speaker=891296,Option='{阿尔勒……小姐？,9},{阿尔勒……先生？,10}',Emoji=18,}",
  [162657] = "{id=162657,Text='——先生。',Speaker=891288,Option='啊？',}",
  [162658] = "{id=162658,Text='阿尔勒“先生”。',Speaker=891288,}",
  [162659] = "{id=162659,Text='算了，我习惯了。',Speaker=891288,}",
  [162660] = "{id=162660,Text='我是。你是哪位？',Speaker=891288,}",
  [162661] = "{id=162661,Text='找我有什么事吗？',Speaker=891288,Option='您的助理',}",
  [162662] = "{id=162662,Text='那个笨蛋……我又不是三岁小孩子，只是想自己四处走走而已。',Speaker=891288,}",
  [162663] = "{id=162663,Text='你是冒险者对吧？对普隆德拉很熟悉咯？',Speaker=891288,Option='{没错,0},{还算熟悉,0}',}",
  [162664] = "{id=162664,Text='那么，拜托你一件事。明天来南门接我，带我参观普伊跨海大桥。',Speaker=891288,Option='这么突然',}",
  [162665] = "{id=162665,Text='就这样。明天见。',Speaker=891288,}",
  [162666] = "{id=162666,Text='（真高傲啊……不愧是”天上之人“）',}",
  [162667] = "{id=162667,Text='你来了。走吧，去普伊跨海大桥。',Speaker=891288,Option='为什么要去那里？',}",
  [162668] = "{id=162668,Text='不是说那个大桥很壮观、很有名吗？',Speaker=891288,Option='我还是不明白',Emoji=8,}",
  [162669] = "{id=162669,Text='唉……',Speaker=891288,}",
  [162670] = "{id=162670,Text='你应该听我助理说了吧，我来普隆德拉是为了取材，完成我的设计作品。',Speaker=891288,}",
  [162671] = "{id=162671,Text='我去普伊跨海大桥，纯粹就是想看看能不能从中得到灵感，就这么简单。',Speaker=891288,Option='{原来如此,0},{你灵感枯竭了,0}',}",
  [162672] = "{id=162672,Text='少废话。快走。',Speaker=891288,Emoji=40,}",
  [162673] = "{id=162673,Text='这就是普伊跨海大桥……',Speaker=891288,Option='{是的,0},{很壮观吧？,0}',}",
  [162674] = "{id=162674,Text='帮我拍张照吧。',Speaker=891288,Option='好',}",
  [162675] = "{id=162675,Text='………………没有………………',Speaker=891288,Option='{嗯？,0},{没有什么？,0}',}",
  [162676] = "{id=162676,Text='完全、没有、灵感！！！',Speaker=891288,Emoji=12,}",
  [162677] = "{id=162677,Text='不管去哪里都没有用！不管做什么……灵感都不会来了！',Speaker=891288,}",
  [162678] = "{id=162678,Text='我再也设计不出新的东西了！！',Speaker=891288,}",
  [162679] = "{id=162679,Text='呜哇~~~~！！',Speaker=891288,Emoji=42,}",
  [162680] = "{id=162680,Text='呀啊啊啊啊啊！！！',Speaker=891288,Option='{躲我身后！,0},{别动！,0}',}",
  [162681] = "{id=162681,Text='啊……呜呜……',Speaker=891288,Option='{（被吓得发抖）,0},{你不要紧吧？,0}',}",
  [162682] = "{id=162682,Text='…………谢谢你……我、我还以为自己要死了……',Speaker=891288,Option='不会的不会的',}",
  [162683] = "{id=162683,Text='不，我还是觉得自己死了更好……',Speaker=891288,Emoji=24,}",
  [162684] = "{id=162684,Text='没有灵感的设计师，没有存在的意义……',Speaker=891288,Option='{不至于,0},{你需要休息,0}',}",
  [162685] = "{id=162685,Text='你不懂！我已经完蛋了！',Speaker=891288,}",
  [162686] = "{id=162686,Text='呜呜……',Speaker=891288,Option='今天先回去吧',}",
  [162687] = "{id=162687,Text='…………好，回去………………',Speaker=891288,Option='明天还在南门',}",
  [162688] = "{id=162688,Text='不！为什么还来？！我不要再被魔物——',Speaker=891288,Option='为了找回灵感',}",
  [162689] = "{id=162689,Text='你别开玩笑。',Speaker=891288,Option='{我是认真的,0},{明天见,0}',Emoji=7,}",
  [162690] = "{id=162690,Text='…………………………好。',Speaker=891288,}",
  [162691] = "{id=162691,Text='………………………………（心不在焉）',Speaker=891288,Option='{早安,0},{在想什么呢？,0}',}",
  [162692] = "{id=162692,Text='早安。……你说要帮我找回灵感，是真的吗？',Speaker=891288,Option='是真的',Emoji=7,}",
  [162693] = "{id=162693,Text='那要怎么做……',Speaker=891288,Option='跟我来吧',}",
  [162694] = "{id=162694,Text='去哪里？喂，回答我啊！',Speaker=891288,Option='跟着我就对了',}",
  [162695] = "{id=162695,Text='真是的！',Speaker=891288,}",
  [162696] = "{id=162696,Text='来这里干什么？',Speaker=891288,}",
  [162697] = "{id=162697,Text='小孩？',Speaker=891288,}",
  [162698] = "{id=162698,Text='我们来玩抓鬼游戏。我说“一二三”，之后，你们就躲起来，我来做抓人的鬼！',Speaker=891292,}",
  [162699] = "{id=162699,Text='好呀好呀！米莎喜欢这个！',Speaker=891291,Emoji=11,}",
  [162700] = "{id=162700,Text='我才不会轻易被抓到！',Speaker=891290,Option='{那个,0},{我们也想参加,0}',}",
  [162701] = "{id=162701,Text='哈？！',Speaker=891288,}",
  [162702] = "{id=162702,Text='大人也想玩啊……好，那你们来当鬼！',Speaker=891292,}",
  [162703] = "{id=162703,Text='哈？！！！（咆哮）',Speaker=891288,}",
  [162704] = "{id=162704,Text='那么就，一——二——三——',Speaker=891290,}",
  [162705] = "{id=162705,Text='大家藏起来！',}",
  [162706] = "{id=162706,Text='你到底在想些什么！',Speaker=891288,Option='{放松心情,0},{单纯地去玩,0}',}",
  [162707] = "{id=162707,Text='现在哪里是玩的时候！我还有一堆的死线！',Speaker=891288,Option='{先别想那些,0},{你不会玩吗？,0}',}",
  [162708] = "{id=162708,Text='可恶——小鬼们，看我一个一个把你们抓出来！',Speaker=891288,}",
  [162709] = "{id=162709,Text='这一个也太容易了。',Speaker=891288,}",
  [162710] = "{id=162710,Text='怎么会！我明明藏得这么好！',Speaker=891290,}",
  [162711] = "{id=162711,Text='藏得顾头不顾尾的，谁都能轻易找到啦！你赶紧回去，我们要去找下一个！',Speaker=891288,}",
  [162712] = "{id=162712,Text='哼。',Speaker=891290,}",
  [162713] = "{id=162713,Text='那个小姑娘应该藏在这里。',Speaker=891288,}",
  [162714] = "{id=162714,Text='啊，大哥哥好厉害，一下子就找到米莎了！',Speaker=891291,}",
  [162715] = "{id=162715,Text='大人比小孩厉害那不是当然的吗？快回去吧，我们会把最后一个小孩找到。',Speaker=891288,}",
  [162716] = "{id=162716,Text='嘻嘻嘻，拉姆斯很会藏的啦，大哥哥加油了。',Speaker=891291,}",
  [162717] = "{id=162717,Text='……应该是这里了。',Speaker=891288,}",
  [162718] = "{id=162718,Text='……………………（无声无息）',Speaker=891292,}",
  [162719] = "{id=162719,Text='你就算装死也是没用的，快现身吧。',Speaker=891288,}",
  [162720] = "{id=162720,Text='…………………………………………唔。',Speaker=891292,}",
  [162721] = "{id=162721,Text='为什么会发觉？',Speaker=891292,}",
  [162722] = "{id=162722,Text='你藏得是很小心了。',Speaker=891288,}",
  [162723] = "{id=162723,Text='可是，这一带都是泥土地，要追踪脚印不是什么难事——尤其是那些故意往偏僻地方走的脚印就更明显了。',Speaker=891288,Emoji=5,}",
  [162724] = "{id=162724,Text='大哥哥，观察力真惊人啊。',Speaker=891292,}",
  [162725] = "{id=162725,Text='没有观察力的话怎么做设计师。快回去吧，找你的伙伴去一起玩。',Speaker=891288,}",
  [162726] = "{id=162726,Text='嗯。大哥哥再见。',Speaker=891292,}",
  [162727] = "{id=162727,Text='嗯？和小孩子玩玩而已，哪有什么感想？',Speaker=891288,}",
  [162728] = "{id=162728,Text='一定要说的话……还……挺轻松的。',Speaker=891288,}",
  [162729] = "{id=162729,Text='很久没这么运动过了，感觉身体也好心也好，都轻快了一些。',Speaker=891288,Option='{很好,0},{那明天继续,0}',}",
  [162730] = "{id=162730,Text='诶？什么，还要继续？',Speaker=891288,Option='明天去北门',}",
  [162731] = "{id=162731,Text='普隆德拉的北门吗？……好吧，就相信你吧，明天见。',Speaker=891288,}",
  [162732] = "{id=162732,Text='今天你来得有点迟。',Speaker=891288,Option='{对不起,2},{是你来早了,3}',}",
  [162733] = "{id=162733,Text='不用那么毕恭毕敬。这点小失误我还是可以包容的，下不为例就是。',Speaker=891288,}",
  [162734] = "{id=162734,Text='我才没有！……不过是早到了十分钟而已。',Speaker=891288,}",
  [162735] = "{id=162735,Text='那么，今天预定要做什么呢？',Speaker=891288,Option='野餐BBQ',}",
  [162736] = "{id=162736,Text='BBQ？你是说，在户外架起一堆火，弄一些奇怪的肉，涂一点可疑的佐料，烤得半生不熟的……那种吗？',Speaker=891288,}",
  [162737] = "{id=162737,Text='那个……卫生吗？吃了会死人吗？',Speaker=891288,}",
  [162738] = "{id=162738,Text='我说得不对？',Speaker=891288,Option='{说法很滑稽,0},{有点差异,0}',}",
  [162739] = "{id=162739,Text='嗯……算了，反正交给你了。直接告诉我需要做什么吧！',Speaker=891288,Option='跟我来吧',}",
  [162740] = "{id=162740,Text='我刚刚就想问了，木柴我知道用来烧火，但你要棉虫肉干什么？',Speaker=891288,Option='{当然是,0},{用来烤,0}',}",
  [162741] = "{id=162741,Text='果然！',Speaker=891288,}",
  [162742] = "{id=162742,Text='你、你是认真的吗！要我吃这种恶心的虫子肉？！',Speaker=891288,Option='{这是优质蛋白,0},{口感很棒的,0}',}",
  [162743] = "{id=162743,Text='别开玩笑了！我就算是饿死，也绝对不会吃这种东西的！！',Speaker=891288,}",
  [162744] = "{id=162744,Text='你自己一个人去BBQ吧！',Speaker=891288,}",
  [162745] = "{id=162745,Text='你有其他可以交给我的材料吗？',Speaker=892862,Option='{腐烂绷带500个,3},{不死心脏500个,4},{微风的传言300个,5},{远古遗骸250个,6}',}",
  [162746] = "{id=162746,Text='你有其他可以交给我的材料吗？',Speaker=892862,Option='{亡者遗物500个,3},{恶魔角500个,4},{微风的传言300个,5},{远古遗骸250个,6}',}",
  [162747] = "{id=162747,Text='你有其他可以交给我的材料吗？',Speaker=892862,Option='{铁矿石500个,3},{慧眼500个,4},{微风的传言300个,5},{远古遗骸250个,6}',}",
  [162748] = "{id=162748,Text='亲爱的冒险者，材料收集好了吗？',Speaker=892862,Option='{上交,41},{还没有,4}',}",
  [162749] = "{id=162749,Text='亲爱的冒险者，材料收集好了吗？',Speaker=892862,Option='{上交,51},{还没有,5}',}",
  [162750] = "{id=162750,Text='亲爱的冒险者，材料收集好了吗？',Speaker=892862,Option='{上交,61},{还没有,6}',}",
  [162751] = "{id=162751,Text='（那么首先……）',}",
  [162752] = "{id=162752,Text='（然后再……）',}",
  [162753] = "{id=162753,Text='（然后就可以……）',}",
  [162754] = "{id=162754,Text='………………………………………………',Speaker=891288,Option='{欢迎回来,0},{要尝尝吗？,0}',}",
  [162755] = "{id=162755,Text='……………………………………咕嘟。（咽口水）',Speaker=891288,}",
  [162756] = "{id=162756,Text='我就……勉为其难，试试你的手艺。',Speaker=891288,}",
  [162757] = "{id=162757,Text='这、这个口味……！',Speaker=891288,}",
  [162758] = "{id=162758,Text='这种朴素又令人怀念的口味……！',Speaker=891288,Option='如何？',}",
  [162759] = "{id=162759,Text='我、我脑子里仿佛出现了光芒！',Speaker=891288,}",
  [162760] = "{id=162760,Text='对不住！我先走了！脑子里好像开始爆炸了！',Speaker=891288,}",
  [162761] = "{id=162761,Text='如果不抓紧时间记下来，灵感就会消失——！',Speaker=891288,}",
  [162762] = "{id=162762,Text='（看起来非常成功啊。）',}",
  [162763] = "{id=162763,Text='工作室已经等急了……',Speaker=891288,}",
  [162764] = "{id=162764,Text='啊，是你。',Speaker=891288,Option='{你好,0},{设计完成了吗？,0}',}",
  [162765] = "{id=162765,Text='托你那顿BBQ的福，昨晚我拉了三次肚子。',Speaker=891288,}",
  [162766] = "{id=162766,Text='……但是，灵感也纷至沓来，简直像洪水一样把我淹没了。',Speaker=891288,}",
  [162767] = "{id=162767,Text='我……一直都过着优越的生活，接触的都是上流社会，为王室啊富人啊设计着符合他们审美的设计作品。',Speaker=891288,}",
  [162768] = "{id=162768,Text='但是，你替我打开了一扇新大门。',Speaker=891288,}",
  [162769] = "{id=162769,Text='我并不是只能设计这些啊！米德加尔特这么广大的世界，并不是只有华丽的上流社会。',Speaker=891288,}",
  [162770] = "{id=162770,Text='以前，我是靠“天上之人”这样的作品得到了称赞……',Speaker=891288,}",
  [162771] = "{id=162771,Text='从现在开始，我要回归这个普通却美丽的凡世，为更多的普通人来设计作品！',Speaker=891288,Option='{恭喜你,0},{跨越了瓶颈,0}',}",
  [162772] = "{id=162772,Text='谢谢。不过，还有一件事想拜托你。',Speaker=891288,}",
  [162773] = "{id=162773,Text='说起来不好意思，之前因为灵感枯竭，迟迟画不出作品。',Speaker=891288,}",
  [162774] = "{id=162774,Text='现在设计稿我倒是都画好了，但截稿日也迫在眉睫，工作室急得要命，我却找不到靠谱的渠道把它送回去。',Speaker=891288,Option='你要我帮你送？',}",
  [162776] = "{id=162776,Text='虽然我确实很信赖你……不过这也太不好意思了。',Speaker=891288,Emoji=15,}",
  [162777] = "{id=162777,Text='你只需要把设计稿送到南门我的助理那里，让她尽快送回去就行了。',Speaker=891288,Option='{咦？,0},{你的助理不是——,0}',Emoji=8,}",
  [162779] = "{id=162779,Text='哦，你说我身旁的人？',Speaker=891288,}",
  [162781] = "{id=162781,Text='他只是我的助理之一，负责我的行程安排。',Speaker=891288,}",
  [162782] = "{id=162782,Text='另一个助理是负责作品保管的。',Speaker=891288,}",
  [162784] = "{id=162784,Text='总之就是这样。设计稿对我来说非常重要，拜托你了。',Speaker=891288,}",
  [162785] = "{id=162785,Text='（收下了设计稿。）',}",
  [162786] = "{id=162786,Text='我还需要继续游历米德加尔特，找寻更多的灵感。日后有缘再见！',Speaker=891288,}",
  [162788] = "{id=162788,Text='真诚地……向你献上我的谢意。',Speaker=891288,Option='有缘再见',}",
  [162790] = "{id=162790,Text='对不起，您是哪位？',Speaker=891295,Option='阿尔勒要我转交',}",
  [162791] = "{id=162791,Text='咦？阿尔勒大人的设计稿已经完成了吗！',Speaker=891295,}",
  [162792] = "{id=162792,Text='太好了~~工作室的人都快急哭了呢！',Speaker=891295,}",
  [162793] = "{id=162793,Text='现在他们可以放心了！谢谢你，冒险者！',Speaker=891295,}",
  [162796] = "{id=162796,Text='所以，先需要插秧……不对不对，施肥吗？',Speaker=891298,Option='{阿尔勒？,0},{你在说什么？,0}',}",
  [162797] = "{id=162797,Text='[c][ffff00][PlayerName][-][/c]，我问你，你有没有种过稻子？',Speaker=891298,Emoji=7,}",
  [162798] = "{id=162798,Text='我听说斐扬那里已经开始种稻了，特别好奇……种稻到底是个怎样的流程，你清楚吗？',Speaker=891298,Option='我不熟悉……',}",
  [162799] = "{id=162799,Text='唉，我从没见过这种农活，真好奇啊。',Speaker=891298,}",
  [162800] = "{id=162800,Text='这样吧，[c][ffff00][PlayerName][-][/c]，不如你代替我去实际体验一下种稻，然后告诉我。说不定能激发我的新灵感呢！',Speaker=891298,Option='你自己不去？',}",
  [162801] = "{id=162801,Text='我倒是想，但是……',Speaker=891298,Emoji=10,}",
  [162802] = "{id=162802,Text='阿尔勒大人——你在哪里——（远处传来）',Speaker=891296,}",
  [162803] = "{id=162803,Text='距离截稿期——还有三天——（远处传来）',Speaker=891296,}",
  [162804] = "{id=162804,Text='……就是这么回事。你愿意帮我这个忙吗？',Speaker=891298,Option='{好吧，我去,3},{我也有自己的事,2}',Emoji=10,}",
  [162805] = "{id=162805,Text='这样啊……那没办法了。',Speaker=891298,}",
  [162806] = "{id=162806,Text='谢谢！斐扬应该有专门种稻的农民，具体事项你去问她就好。',Speaker=891298,}",
  [162808] = "{id=162808,Text='加油哦。',Speaker=891298,}",
  [162809] = "{id=162809,Text='那你尽快。',Speaker=891298,}",
  [162810] = "{id=162810,Text='之前下了好几场雪，今年收成应该不错吧！',Speaker=891299,Option='{你就是,0},{斐扬的农民？，0}',}",
  [162811] = "{id=162811,Text='什么农民？我可是专家！关于种稻的知识全部精通的那种超级专家！',Speaker=891299,Option='{对不起对不起,0},{失敬了,0}',Emoji=12,}",
  [162812] = "{id=162812,Text='闲话少说，这段时间我忙得很。找我什么事？',Speaker=891299,Option='体验种稻',}",
  [162813] = "{id=162813,Text='你想体验种稻？',Speaker=891299,}",
  [162814] = "{id=162814,Text='太~好了，非常欢迎新人加入！',Speaker=891299,}",
  [162815] = "{id=162815,Text='你来的正是时候，斐扬这里的春耕刚刚开始，来吧，和我们一起为美味的大米而努力！',Speaker=891299,}",
  [162816] = "{id=162816,Text='如果有任何不明白的都可以问我！',Speaker=891299,}",
  [162817] = "{id=162817,Text='要种植水稻，第一步就是“整地”。',Speaker=891299,}",
  [162818] = "{id=162818,Text='经历了一个冬天的空闲，土地变得硬邦邦，还有不少石头。',Speaker=891299,}",
  [162819] = "{id=162819,Text='我们需要做的，就是想办法把土地变得松软，把石头除掉。这可是个力气活。',Speaker=891299,}",
  [162820] = "{id=162820,Text='具体怎么做都可以哟，直接对着土地攻击也行！',Speaker=891299,}",
  [162821] = "{id=162821,Text='去吧种稻新人！用尽你的力量，去完成整地吧！',Speaker=891299,}",
  [162822] = "{id=162822,Text='（好像还不够松软。要继续吗？）',Option='{是,2},{否,3}',}",
  [162823] = "{id=162823,Text='（还差一点，继续）',}",
  [162824] = "{id=162824,Text='（非常完美！）',}",
  [162825] = "{id=162825,Text='唔，我看看你的成果。',Speaker=891299,}",
  [162826] = "{id=162826,Text='天啊，太惊人了！你真的是新人吗！',Speaker=891299,}",
  [162827] = "{id=162827,Text='明天也要来哦！我们要开始正式插秧了！',Speaker=891299,}",
  [162828] = "{id=162828,Text='今天我们要插秧了。',Speaker=891299,}",
  [162829] = "{id=162829,Text='秧苗已经长得足够强壮，可以移植。',Speaker=891299,}",
  [162830] = "{id=162830,Text='你需要做的，就是从秧田里采集秧苗，然后到稻田里把它们重新种植下去。',Speaker=891299,}",
  [162831] = "{id=162831,Text='秧苗可是很脆弱的，插秧的时候要小心一点哦。',Speaker=891299,}",
  [162832] = "{id=162832,Text='（腰好酸啊……要继续吗？）',Option='{是,2},{否,3}',}",
  [162833] = "{id=162833,Text='（再坚持一下，加油！）',}",
  [162834] = "{id=162834,Text='（终于……完成了……）',}",
  [162835] = "{id=162835,Text='唔，我看看你的成果。',Speaker=891299,}",
  [162836] = "{id=162836,Text='天啊，太惊人了！你真的是新人吗！',Speaker=891299,}",
  [162838] = "{id=162838,Text='今天的任务没什么难度啦，就是除草而已。',Speaker=891299,}",
  [162839] = "{id=162839,Text='插秧结束后，就要防止稻田里的杂草生长过剩，夺走水稻的养分。',Speaker=891299,}",
  [162840] = "{id=162840,Text='杂草清除得越干净，水稻就长得越好。',Speaker=891299,}",
  [162841] = "{id=162841,Text='加油吧，种稻新人！',Speaker=891299,}",
  [162842] = "{id=162842,Text='我没说错吧，今天的任务很简单！',Speaker=891299,}",
  [162843] = "{id=162843,Text='辛苦辛苦，明天也要来哦，稻田里需要施肥。',Speaker=891299,}",
  [162844] = "{id=162844,Text='嗨~今天我们——',Speaker=891299,Option='{呃,0},{什么味道？,0}',}",
  [162845] = "{id=162845,Text='啊哈哈哈，稍微有点臭臭的？那是当然啦。',Speaker=891299,}",
  [162846] = "{id=162846,Text='今天要做的就是施肥，我当然得提前把肥料准备好，所以会稍微有点味道。',Speaker=891299,}",
  [162847] = "{id=162847,Text='你用不着一脸为难，肥料经过熟成后基本已经没有臭味了。',Speaker=891299,}",
  [162848] = "{id=162848,Text='来，伸手~',Speaker=891299,Option='勉为其难地伸手',}",
  [162849] = "{id=162849,Text='你看，不臭吧？',Speaker=891299,}",
  [162850] = "{id=162850,Text='现在，你需要把肥料均匀地撒到稻田里，让土地吸收就行啦。加油吧！',Speaker=891299,}",
  [162851] = "{id=162851,Text='嗨，施肥已经完成了？',Speaker=891299,Option='是的',}",
  [162852] = "{id=162852,Text='嗯嗯，虽然已经没什么味道，不过那毕竟是肥料，记得抽空尽快洗个澡哦~',Speaker=891299,}",
  [162853] = "{id=162853,Text='明天再过来，开始给稻子灌水。',Speaker=891299,}",
  [162854] = "{id=162854,Text='唉，雨水少真是让人头疼……',Speaker=891299,Option='怎么了？',}",
  [162855] = "{id=162855,Text='是你啊。你看，现在稻子已经开始抽穗了，但是最近雨水却不够多。',Speaker=891299,}",
  [162856] = "{id=162856,Text='这个时期，稻子对水的需求量很大，没有充足水分就长不好。',Speaker=891299,Option='交给我吧',}",
  [162857] = "{id=162857,Text='哇，真是可靠的发言。那就交给你啦！',Speaker=891299,Emoji=11,}",
  [162858] = "{id=162858,Text='（利用水属性的技能，给稻田灌水吧！）',}",
  [162859] = "{id=162859,Text='太惊人了，原来还有这种灌水方法！',Speaker=891299,}",
  [162860] = "{id=162860,Text='这可是个新思路，让我记下来~',Speaker=891299,Emoji=11,}",
  [162861] = "{id=162861,Text='现在稻子已经抽穗，水分也很充足，应该会长得很好！',Speaker=891299,}",
  [162863] = "{id=162863,Text='哇！你来得正好，快快，和我一起把这些虫子都除掉！',Speaker=891299,Option='虫子？',}",
  [162864] = "{id=162864,Text='为了让水稻长得好，又施肥又灌水的，现在稻田营养特别丰富，稻子长势也很喜人，虫子自然都来了。',Speaker=891299,Emoji=15,}",
  [162865] = "{id=162865,Text='虽然除虫是个必然步骤，但今年这些虫子也长得太大啦！',Speaker=891299,Option='那很严重啊',}",
  [162866] = "{id=162866,Text='就是说嘛！快点，把这些虫子都打掉！',Speaker=891299,}",
  [162867] = "{id=162867,Text='呼……终于把这些稻虱都消灭了。累死人了……',Speaker=891299,}",
  [162868] = "{id=162868,Text='不过，好消息是，今年这些稻虱这么多，看来稻子质量很好呢~',Speaker=891299,}",
  [162869] = "{id=162869,Text='距离丰收已经不远了！加油啊，种稻新人！明天再见！',Speaker=891299,}",
  [162870] = "{id=162870,Text='告诉你个好消息，稻子马上开始结穗，丰收的时节快来了！',Speaker=891299,}",
  [162871] = "{id=162871,Text='不过，各种害虫也专门盯着这个时节。',Speaker=891299,}",
  [162872] = "{id=162872,Text='所以为了防止大规模的虫害，我们需要给稻子用上合适的药物。',Speaker=891299,}",
  [162873] = "{id=162873,Text='来，伸手~',Speaker=891299,}",
  [162874] = "{id=162874,Text='快去把药水均匀撒到稻田里。千万别撒多了啊！撒多了对稻子也有害。',Speaker=891299,}",
  [162875] = "{id=162875,Text='真棒！种稻体验就到此为止啦！',Speaker=891299,Option='{呃？,0},{可是大米……,0}',}",
  [162876] = "{id=162876,Text='哦？你的意思是，你还想体验在骄阳下挥汗如雨地割稻、一整天挥着筛子扬谷、把脚蹬到麻木的碾磨吗？',Speaker=891299,Option='不了不了',Emoji=8,}",
  [162877] = "{id=162877,Text='我很欢迎哦~丰收之后我们人手正嫌不够——',Speaker=891299,Option='我还有事……',Emoji=5,}",
  [162878] = "{id=162878,Text='好啦，不逗你了。下次如果对种稻感兴趣，可以再来找我！',Speaker=891299,}",
  [162879] = "{id=162879,Text='哦，种稻原来是这么辛苦的事啊。',Speaker=891298,Option='{没错,0},{真的很辛苦,0}',}",
  [162880] = "{id=162880,Text='……以后吃饭时也要心怀感恩了。',Speaker=891298,}",
  [162881] = "{id=162881,Text='谢谢你，让我获得了新知识。大概也会激发新的灵感吧。',Speaker=891298,}",
  [162884] = "{id=162884,Text='为了感谢你对我的支持，请收下这份礼包吧~如果你还想得到更多礼包的话，就继续参与我的猜谜活动吧~',Speaker=892853,}",
  [162885] = "{id=162885,Text='感谢你这一周以来的答题，让我们以后再见吧~',Speaker=892853,}",
  [162886] = "{id=162886,Text='算了，反正跟我也没关系，既然来都来了，那就看看今天的任务吧：',Speaker=892855,}",
  [162909] = "{id=162909,Text='嗨，[c][ffff00][PlayerName][-][/c]，在这里果然能遇见你。还记得我吗？',Speaker=891309,Option='你是……',}",
  [162910] = "{id=162910,Text='我是宠物协会的迪恩。有事提醒你，所以就专程跑了一趟。',Speaker=891309,Option='谢谢！什么事？',Emoji=14,}",
  [162911] = "{id=162911,Text='但是……你真的从没发觉到吗？',Speaker=891309,Option='发觉什么？',}",
  [162912] = "{id=162912,Text='看呀，今天是美好的一天。花朵在绽放，鸟儿在歌唱。所以…………',Speaker=891309,}",
  [162913] = "{id=162913,Text='你真的决定好勇敢面对真相了吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891309,Option='{决定好了,2},{暂时不了,3}',}",
  [162914] = "{id=162914,Text='哎呀呀，很尽兴嘛！祝你继续享受灿烂春光！',Speaker=891309,Emoji=12,}",
  [162915] = "{id=162915,Text='那我说了哦！（清清嗓子）',Speaker=891309,}",
  [162916] = "{id=162916,Text='[c][ffff00][PlayerName][-][/c]，打开你的宠物蛋包包，仔细检查一遍。',Speaker=891309,Option='打开包包',}",
  [162917] = "{id=162917,Text='怎样？是不是少了某个重要的东西？',Speaker=891309,}",
  [162918] = "{id=162918,Text='（我的波利不见了。）',}",
  [162919] = "{id=162919,Text='不知道你发现了什么……但我有个推测——',Speaker=891309,}",
  [162920] = "{id=162920,Text='你加入宠物协会时，我说过一件事吧？',Speaker=891309,Option='什么事？',}",
  [162921] = "{id=162921,Text='[c][ffff00]没能得到主人充分关爱的宠物，最后会心灰意冷地选择离开。[-][/c]',Speaker=891309,}",
  [162922] = "{id=162922,Text='你的波利……它选择离开你了！！！',Speaker=891309,Option='{万一是我弄丢了,0},{它可能有危险,0}',}",
  [162923] = "{id=162923,Text='你真的很在乎它吗？',Speaker=891309,Option='它是我重要的伙伴',}",
  [162924] = "{id=162924,Text='……“重要的伙伴”……吗？啧啧啧……',Speaker=891309,}",
  [162925] = "{id=162925,Text='协会没有失物招领处，我也帮不上忙。',Speaker=891309,}",
  [162926] = "{id=162926,Text='不过……你可以去[c][ffff00]南门的波利群中[-][/c]找找你的小波利。',Speaker=891309,}",
  [162927] = "{id=162927,Text='毕竟，波利生性害怕寂寞，离开了你，它可能就回到家人的身边了。',Speaker=891309,}",
  [162928] = "{id=162928,Text='还记得[c][ffff00]你和波利的相遇吗[-][/c]？',Speaker=891309,}",
  [162929] = "{id=162929,Text='那时，就是因为会长告诉它，找个主人就可以一直有人陪伴，它才愿意参加你的入会训练的呢！',Speaker=891309,Option='什么？',}",
  [162930] = "{id=162930,Text='还有这回事！幻灭了……',}",
  [162931] = "{id=162931,Text='好了，我知道的就这么多了，祝你顺利找到你的波利！',Speaker=891309,Option='谢谢你的建议',}",
  [162932] = "{id=162932,Text='啾~有怪人，大家快跑呐!',Speaker=891311,}",
  [162933] = "{id=162933,Text='要被一口吞下去啦，啵琳！',Speaker=891311,}",
  [162934] = "{id=162934,Text='人家不是软糖~~~Σ(っ °Д °;)っ',Speaker=891311,}",
  [162935] = "{id=162935,Text='波利群又被围殴了？但也不至于……逃到城门口吧？',}",
  [162936] = "{id=162936,Text='前面出了什么乱子？只有看看才知道了。',}",
  [162937] = "{id=162937,Text='你没事吧？',Speaker=891306,}",
  [162938] = "{id=162938,Text='我……又不是……游戏角色，不可能……24小时不吃不喝……',Speaker=891302,}",
  [162939] = "{id=162939,Text='是时空裂隙！但在旁边的古怪装扮少女是……？',}",
  [162940] = "{id=162940,Text='太好了！是一名冒险者！——',Speaker=891306,Option='怎么回事？',}",
  [162941] = "{id=162941,Text='这位小姐突然从这个时空裂隙中出现，说自己叫嗒啦啦。',Speaker=891306,}",
  [162942] = "{id=162942,Text='再多的我也就不知道了，你看她现在饿到眼睛冒星星、无法动弹。',Speaker=891306,Option='给她找点食物吧',}",
  [162943] = "{id=162943,Text='你能不能帮忙从我的卡普拉同事手中购买一份“暖心料理”？',Speaker=891306,Option='好',}",
  [162944] = "{id=162944,Text='我、只想吃桃子软糖！咩呲咩呲（咀嚼声）！',Speaker=891302,}",
  [162945] = "{id=162945,Text='（害怕到变大）快放开我Ｏ(≧口≦)Ｏ',Speaker=891311,}",
  [162946] = "{id=162946,Text='看来这个女孩不仅爱吃软糖，还把南门的波利们统统误认成软糖了。',Emoji=10,}",
  [162947] = "{id=162947,Text='卡普拉竭诚为您服务！这位冒险者，您需要些什么吗？',Speaker=802002,Option='来一份暖心料理',}",
  [162948] = "{id=162948,Text='好的！暖心料理一份，请您拿好~',Speaker=802002,}",
  [162949] = "{id=162949,Text='╰(*°▽°*)╯终、于、解、脱、了~~',Speaker=891311,}",
  [162950] = "{id=162950,Text='满血复活！各属性恢复正常！',Speaker=891302,}",
  [162951] = "{id=162951,Text='是你救了我吧，这位玩……不，冒险者！我能知道你的名字吗……！',Speaker=891302,Option='回答名字',Emoji=15,}",
  [162952] = "{id=162952,Text='[c][ffff00][PlayerName][-][/c]……？',Speaker=891302,}",
  [162953] = "{id=162953,Text='[c][ffff00][PlayerName][-][/c]？你在找……[c][ffff00]一只宠物波利[-][/c]吗？',Speaker=891302,Option='你有它的线索？',}",
  [162954] = "{id=162954,Text='我倒是也想有线索……',Speaker=891302,}",
  [162955] = "{id=162955,Text='毕竟，他和我最重要的伙伴——噗噜噜一起消失了……',Speaker=891302,Option='怎么回事？',}",
  [162956] = "{id=162956,Text='具体的我也不清楚，只知道噗噜噜和你的那只波利是一起穿越到这个世界的！',Speaker=891302,}",
  [162957] = "{id=162957,Text='我也是为了找到它们，才制造时空裂隙来到这里的！',Speaker=891302,}",
  [162958] = "{id=162958,Text='不行了，我实在是太晕了……我得好好休息一下。',Speaker=891302,}",
  [162959] = "{id=162959,Text='我还得留着体力检测噗噜噜的信号，说不定就能找到他俩了。',Speaker=891302,Option='检测信号？',}",
  [162960] = "{id=162960,Text='这位小姐在说些什么，我为什么都听不懂？',Speaker=891306,Emoji=10,}",
  [162961] = "{id=162961,Text='咦，有吗？',Speaker=891302,Emoji=18,}",
  [162962] = "{id=162962,Text='（微笑）不用介意。嗒啦啦这么可爱，有什么问题都可以找我帮忙哦！我叫美纳斯，是个卡普拉。',Speaker=891306,Option='嗯',}",
  [162963] = "{id=162963,Text='谢谢你，美纳斯小姐姐！好啦，我去收集信号去了！明天见！',Speaker=891302,}",
  [162964] = "{id=162964,Text='（抚摸波利中……）看来，你也不是呀。',Speaker=891302,}",
  [162965] = "{id=162965,Text='啊，[c][ffff00][PlayerName][-][/c]，你来了！决定要和我一起寻找小波利和噗噜噜了吗？',Speaker=891302,Option='{当然,0},{必须找到,0}',}",
  [162966] = "{id=162966,Text='原来你真的什么都不知道……',Speaker=891302,}",
  [162967] = "{id=162967,Text='波利它……确实是自己选择离开你的。',Speaker=891302,Option='为什么？！',}",
  [162968] = "{id=162968,Text='小波利穿越到我们世界时，暂住在了我的家里。',Speaker=891302,Action={actionid=201,num=1},}",
  [162969] = "{id=162969,Text='我一直很奇怪，它为什么不回到自己的世界中去呢？',Speaker=891302,}",
  [162970] = "{id=162970,Text='它总是支支吾吾的，不告诉我原因，但它一直把你挂在嘴边，说它讨厌你。',Speaker=891302,Option='……',}",
  [162971] = "{id=162971,Text='它的长相在我们的世界太奇特了！我怕它不小心就被抓走做实验。',Speaker=891302,}",
  [162972] = "{id=162972,Text='我就只能安排噗噜噜看好它。',Speaker=891302,}",
  [162973] = "{id=162973,Text='但噗噜噜……突然和波利一起消失了。',Speaker=891302,}",
  [162974] = "{id=162974,Text='我从噗噜噜的行为日志备份记录里，检测出他和小波利正位于这个世界。',Speaker=891302,}",
  [162975] = "{id=162975,Text='但是来到这个世界之后，连噗噜噜的信号都消失了……',Speaker=891302,}",
  [162976] = "{id=162976,Text='你们这的世界这么大……要找到他们两个，简直就是大海捞针啊……',Speaker=891302,Option='怎么办',}",
  [162977] = "{id=162977,Text='美纳斯小姐姐一定有办法！我们去城里找她吧！',Speaker=891302,}",
  [162978] = "{id=162978,Text='我还是第一次进首都，请你帮我领路吧！',Speaker=891302,Option='好',}",
  [162979] = "{id=162979,Text='哇！这不是扭蛋机吗？没想到这个世界也有这样的东西！',Speaker=891302,}",
  [162980] = "{id=162980,Text='啊！我控制不了自己的手！它怎么自己动了！这就是扭蛋机的魔法嘛！',Speaker=891302,}",
  [162981] = "{id=162981,Text='当当当当——一发入魂！不愧是我！',Speaker=891302,Option='……',}",
  [162982] = "{id=162982,Text='嗯？那个是什么？可以去看看吗？',Speaker=891302,Option='好',}",
  [162983] = "{id=162983,Text='哇哦哦~这模块组装有够硬核！',Speaker=891302,}",
  [162985] = "{id=162985,Text='——投币即可乘坐！20zeny试乘10分钟！',Speaker=4683,}",
  [162986] = "{id=162986,Text='我、还是看看吧……平时可没少坐在噗噜噜身上飞来飞去的。',Speaker=891302,Emoji=15,}",
  [162987] = "{id=162987,Text='……没关系！欢迎您下次再来！',Speaker=4683,Emoji=26,}",
  [162988] = "{id=162988,Text='嗯？[c][ffff00][PlayerName][-][/c]，怎么了？',Speaker=891302,Option='噗噜噜会飞？',}",
  [162989] = "{id=162989,Text='噗噜噜是机械体，具有反重力和喷射飞行能力。比这大块头要圆一些。',Speaker=891302,}",
  [162990] = "{id=162990,Text='（其实不明白在说什么）',Option='快找美纳斯',}",
  [162991] = "{id=162991,Text='嗒啦啦，还没收到噗噜噜的信号吗？',Speaker=891306,}",
  [162992] = "{id=162992,Text='还没有。',Speaker=891302,}",
  [162993] = "{id=162993,Text='但我也许发现信号丢失的原因了!',Speaker=891302,}",
  [162994] = "{id=162994,Text='[c][ffff00][PlayerName][-][/c]，我在闲逛时观察了各仪器的运作原理……',Speaker=891302,}",
  [162995] = "{id=162995,Text='扭蛋使用了魔法机关，机甲的动力停留在内燃机水平……',Speaker=891302,}",
  [162996] = "{id=162996,Text='我推导出来，这里——至少是卢恩王国地区——的科技树，还没有点到发明通讯中继器的阶段。',Speaker=891302,Option='科技树？',}",
  [162997] = "{id=162997,Text='呃……大概就是说，我怎样都检测不到噗噜噜的信号啦！',Speaker=891302,}",
  [162998] = "{id=162998,Text='我听着不大明白……',Speaker=891306,Option='跟着点头',}",
  [162999] = "{id=162999,Text='要不，我将你们送到修发兹共和国那里，毕竟他们那边的人比较喜欢研究这些。',Speaker=891306,}",
  [163000] = "{id=163000,Text='嗯……我需要一个足够高的地方，这样可以更加准确的收到噗噜噜的信号。',Speaker=891302,Option='去朱诺',}",
  [163001] = "{id=163001,Text='这是个好主意，朱诺恐怕是这片大陆上最高的地方了。',Speaker=891306,}",
  [163002] = "{id=163002,Text='我现在就将你们传送过去。',Speaker=891306,}",
  [163003] = "{id=163003,Text='好壮观……！',Speaker=891302,}",
  [163004] = "{id=163004,Text='整座城市都漂浮在高空中，周围就是蓝天和白云！',Speaker=891302,}",
  [163005] = "{id=163005,Text='——传说的天空之城！我真的到了天空之城！',Speaker=891302,Option='{这是个奇迹,2},{在这很常见,3}',}",
  [163006] = "{id=163006,Text='没错！这就是魔法！这就是奇迹！',Speaker=891302,Emoji=20,}",
  [163007] = "{id=163007,Text='怎么做到的？我也想学会这样的魔法！',Speaker=891302,Emoji=19,}",
  [163008] = "{id=163008,Text='我真的好羡慕你们……可以在这样的游戏……不，奇幻的世界里冒险~',Speaker=891302,Option='发射中继器',Emoji=27,}",
  [163009] = "{id=163009,Text='啊对！要先找到噗噜噜和小波利。',Speaker=891302,}",
  [163010] = "{id=163010,Text='很好！区域信号质量·稳定！',}",
  [163011] = "{id=163011,Text='准备好了吗？[c][ffff00]——要发射了！[-][/c]',Speaker=891302,Emoji=26,}",
  [163012] = "{id=163012,Text='中继器已经开始运作了。',Speaker=891302,}",
  [163013] = "{id=163013,Text='噗——噜噜……噗……噜噗……',Speaker=891316,}",
  [163014] = "{id=163014,Text='收到信号了！',Speaker=891302,Option='你的发卡在响？',}",
  [163015] = "{id=163015,Text='我的发卡就是信号接收器~！（摁紧发卡）',Speaker=891302,}",
  [163016] = "{id=163016,Text='噗噜噜！噗噜噜！能听到吗？这里是嗒啦啦！收到请回复！',Speaker=891302,}",
  [163017] = "{id=163017,Text='噗……——',Speaker=891316,}",
  [163018] = "{id=163018,Text='喂——喂——喂——！',Speaker=891316,}",
  [163019] = "{id=163019,Text='……',Speaker=891316,}",
  [163020] = "{id=163020,Text='难道……它关机了？！',Speaker=891302,}",
  [163021] = "{id=163021,Text='信号消失的地点在……里希塔乐镇……',Speaker=891302,Option='我们快过去',}",
  [163022] = "{id=163022,Text='今天天色也不早了……为了找到关机的噗噜噜我也还需要做些准备。',Speaker=891302,}",
  [163023] = "{id=163023,Text='明天南门老地方见，我们一起去里希塔乐镇！',Speaker=891302,Option='好',}",
  [163024] = "{id=163024,Text='（抚摸波利中……）你也不是那只小波利呢……',Speaker=891302,Option='嗒啦啦？',}",
  [163025] = "{id=163025,Text='冒险者，很准时哦！——噗噜噜的信号最后消失的位置在里希塔乐镇。',Speaker=891302,}",
  [163026] = "{id=163026,Text='我们要出发啦！在那里一定有噗噜噜和小波利的线索！',Speaker=891302,Option='出发！',}",
  [163027] = "{id=163027,Text='……不过，说到波利，我有一个建议……',Speaker=891302,}",
  [163028] = "{id=163028,Text='如果……小波利不想回到你身边，又或者……因为别的原因，要永远离开你了……',Speaker=891302,}",
  [163029] = "{id=163029,Text='不要沮丧。你还可以再抓一只波利，陪你一起冒险！',Speaker=891302,Option='你要我找替代品？',}",
  [163030] = "{id=163030,Text='“[c][ffff00]替代品[-][/c]”？',Speaker=891302,}",
  [163031] = "{id=163031,Text='我没有这个意思……！',Speaker=891302,}",
  [163032] = "{id=163032,Text='……抱歉，我的确说了让人误会的话。',Speaker=891302,}",
  [163033] = "{id=163033,Text='在我心中，每个伙伴都是独一无二的。',Speaker=891302,}",
  [163034] = "{id=163034,Text='即便再捕捉一只波利，[c][ffff00][PlayerName][-][/c]……你也不可以把它当成任何[c][ffff00]替代品[-][/c]。',Speaker=891302,Option='知道啦，嗒啦啦',}",
  [163035] = "{id=163035,Text='其实我以前也…………——哎，还是不说伤感的话啦。',Speaker=891302,}",
  [163036] = "{id=163036,Text='我们这就出发，前往里希塔乐镇寻找噗噜噜吧。',Speaker=891302,Option='{立刻出发,2},{再等等,3}',}",
  [163037] = "{id=163037,Text='好呀，赶紧出发！',Speaker=891302,Emoji=23,}",
  [163038] = "{id=163038,Text='刚到这里，我发卡中的信号接收器就开始发出轻微声响，你仔细听。',Speaker=891302,Emoji=9,}",
  [163039] = "{id=163039,Text='滴…………滴…………',Speaker=891316,}",
  [163040] = "{id=163040,Text='发卡型的信号接收器传来微弱的滴滴声。',}",
  [163041] = "{id=163041,Text='我的发卡信号接收器越靠近噗噜噜，声音就会越响，频率也会越频繁。',Speaker=891302,}",
  [163042] = "{id=163042,Text='看来……噗噜噜他们一定在这里。',Speaker=891302,}",
  [163043] = "{id=163043,Text='赶紧找到它们~!',Speaker=891302,}",
  [163044] = "{id=163044,Text='现在该往哪边走呢？',Speaker=891302,Option='{西边,4},{东边,5}',}",
  [163045] = "{id=163045,Text='滴滴……滴滴滴……滴滴滴滴……',Speaker=891316,}",
  [163046] = "{id=163046,Text='…………',Speaker=891316,}",
  [163047] = "{id=163047,Text='现在该往哪边走呢？',Speaker=891302,Option='{北边,6},{南边,7}',}",
  [163048] = "{id=163048,Text='滴滴滴滴……滴滴滴滴——滴滴滴滴！',Speaker=891316,}",
  [163049] = "{id=163049,Text='…………',Speaker=891316,}",
  [163050] = "{id=163050,Text='滴滴滴滴滴滴滴滴滴滴！',Speaker=891316,}",
  [163051] = "{id=163051,Text='啊！发卡反应非常强烈！',Speaker=891302,}",
  [163052] = "{id=163052,Text='噗噜噜一定就在附近！',Speaker=891302,}",
  [163053] = "{id=163053,Text='咦？地上这串脚印是……好像是……人类的脚印？',Speaker=891302,}",
  [163054] = "{id=163054,Text='（顺着脚印向前方看去）啊！那不是！噗噜噜嘛！',Speaker=891302,}",
  [163055] = "{id=163055,Text='客人，您这批货的成色真棒啊。',Speaker=891310,}",
  [163056] = "{id=163056,Text='那就别啰嗦！痛快开个价！',Speaker=891307,}",
  [163057] = "{id=163057,Text='古尤佩雷斯都没这芯片的技术……让我想想啊……',Speaker=891310,}",
  [163058] = "{id=163058,Text='你们！你们怎么能把噗噜噜……',Speaker=891302,Emoji=18,}",
  [163059] = "{id=163059,Text='快把我的噗噜噜还给我！！！',Speaker=891302,Option='我也来帮忙',}",
  [163060] = "{id=163060,Text='谁啊？！别妨碍我做生意！',Speaker=891310,Emoji=7,}",
  [163061] = "{id=163061,Text='嘿，有好戏可看！',Speaker=891307,Emoji=5,}",
  [163062] = "{id=163062,Text='停、停停、停手！我有个提议！',Speaker=891310,Emoji=37,}",
  [163063] = "{id=163063,Text='反正，您看上的只是这圈金属外壳吧？',Speaker=891310,}",
  [163064] = "{id=163064,Text='我认识雷根贝勒最好的工程师，还能引进导热性更好的材料……',Speaker=891310,}",
  [163065] = "{id=163065,Text='谁都不能替代我的噗噜噜！',Speaker=891302,}",
  [163066] = "{id=163066,Text='嘁！不识相的死脑筋！',Speaker=891310,}",
  [163067] = "{id=163067,Text='对不住了客人，我先溜了！',Speaker=891310,}",
  [163068] = "{id=163068,Text='噗噜噜！你没事吧？噗噜噜！',Speaker=891302,Emoji=24,}",
  [163069] = "{id=163069,Text='（圆形的屏幕漆黑一片，没有任何反应。它已经关机了。）',Speaker=891304,}",
  [163070] = "{id=163070,Text='看够了没有！别想挡我的财路！',Speaker=891307,}",
  [163071] = "{id=163071,Text='噗噜噜——————！',Speaker=891302,Emoji=18,}",
  [163072] = "{id=163072,Text='嘿嘿嘿！',Speaker=891307,}",
  [163073] = "{id=163073,Text='——这台大铁球之前全副武装地紧跟着一个波、波利，不许任何人接近波、波利。',Speaker=891307,Option='波利？',}",
  [163074] = "{id=163074,Text='还好，他能源耗尽了。我要把它拆来卖掉！保护我的波、波利小弟！',Speaker=891307,Option='你怎么能……',}",
  [163075] = "{id=163075,Text='嘿嘿，很着急呀？',Speaker=891307,}",
  [163076] = "{id=163076,Text='——难不成你们是大铁球的同伴？',Speaker=891307,Option='还是波利的伙伴！',}",
  [163077] = "{id=163077,Text='不好意思，和大铁球是一伙的你们，说什么我都不会信！',Speaker=891307,}",
  [163078] = "{id=163078,Text='这只波利……怎么不像其他正常波利一样软软萌萌的……',Speaker=891302,Option='{还蛮狠不讲理,0},{还和人类做交易,0}',}",
  [163079] = "{id=163079,Text='喂喂！当我面对我说三道四，实在太没礼貌了！',Speaker=891307,}",
  [163080] = "{id=163080,Text='还有还有！别“波、波利”、“波、波利”地把我叫来叫去，我的名字叫“软糖”！',Speaker=891307,}",
  [163081] = "{id=163081,Text='没错！我正是你最爱嚼的食物——“软糖”哦，电波小姐！',Speaker=891307,Option='你怎么知道？',}",
  [163082] = "{id=163082,Text='诶诶诶！！！——糟了……还是说漏嘴了！咳咳……总之！',Speaker=891307,}",
  [163083] = "{id=163083,Text='破铁球在我手中，波、波利也在我的保护下。',Speaker=891307,Emoji=37,}",
  [163084] = "{id=163084,Text='真心地想再见他们？那就第二天来[c][ffff00]普隆德拉南门[-][/c]找我接受[c][ffff00]考验[-][/c]，我才能放心把伙伴交给你们！',Speaker=891307,}",
  [163085] = "{id=163085,Text='……[c][ffff00][PlayerName][-][/c]，你怎么看？',Speaker=891302,Option='充满了疑点',}",
  [163086] = "{id=163086,Text='嗯。它不仅可以踩出人类的脚印，还能长出手拆开噗噜噜……',Speaker=891302,Option='它不会长出手',}",
  [163087] = "{id=163087,Text='说不定呢！……总之，不管他搞什么古怪……',Speaker=891302,}",
  [163088] = "{id=163088,Text='……我，一定会夺回噗噜噜！',Speaker=891302,Option='我也要找到波利',Emoji=33,}",
  [163089] = "{id=163089,Text='那我们明天南门再见，[c][ffff00][PlayerName][-][/c]！',Speaker=891302,Option='再见',}",
  [163090] = "{id=163090,Text='嘿嘿嘿，你来接受我的考验了。',Speaker=891307,}",
  [163091] = "{id=163091,Text='不交出噗噜噜的话，就算你“现在”是可爱的波利，我也会打你哦！',Speaker=891302,}",
  [163092] = "{id=163092,Text='你的心情，我明白，我很明白！电波小姐！',Speaker=891307,}",
  [163093] = "{id=163093,Text='但我想考验的，只有这位冒险者！',Speaker=891307,}",
  [163094] = "{id=163094,Text='如果你真的是那只波、波利的小伙伴，那你一定非常了解它的方方面面吧！',Speaker=891307,}",
  [163095] = "{id=163095,Text='所以——让我们重走一遍最基本魔物训练！',Speaker=891307,}",
  [163096] = "{id=163096,Text='通过考验，才说明你有魔物训练师的资格。自然，波、波利小弟也会交给你。',Speaker=891307,Option='明白',}",
  [163097] = "{id=163097,Text='准备好接受考验了吗？！',Speaker=891307,Option='{准备好了,2},{再等等,3}',}",
  [163098] = "{id=163098,Text='“等等”？真是经典的口头禅！我也会奉陪到底的！',Speaker=891307,}",
  [163099] = "{id=163099,Text='[c][ffff00][PlayerName][-][/c]……我很担心噗噜噜，不要让这坨大叔等太久。',Speaker=891302,}",
  [163100] = "{id=163100,Text='嘿嘿，走起！',Speaker=891307,}",
  [163101] = "{id=163101,Text='现在由我来带你们入场。事先声明……',Speaker=891309,}",
  [163102] = "{id=163102,Text='这回可不是什么友好的新人指导，而是货真价实的“考验”。',Speaker=891309,}",
  [163103] = "{id=163103,Text='所以，我不会给你任何提示。',Speaker=891309,}",
  [163104] = "{id=163104,Text='你要走的流程也会稍有不同，且更具挑战性。',Speaker=891309,Option='明白',}",
  [163105] = "{id=163105,Text='现在，你的面前摆着三样礼物。',Speaker=891309,}",
  [163106] = "{id=163106,Text='你要在当中挑选出波利们最喜欢的那个，你应该知道的吧？',Speaker=891309,}",
  [163107] = "{id=163107,Text='劝你小心。碰了不该碰的东西，你可是要尝点苦头的！',Speaker=891309,}",
  [163108] = "{id=163108,Text='(糟了，好像拿错了！)',}",
  [163109] = "{id=163109,Text='（悄悄提示）[c][ffff00][PlayerName][-][/c]，下次别选错了！',Speaker=891302,}",
  [163110] = "{id=163110,Text='（悄悄提示）我记得——波利最喜欢的食物，是白雪公主童话里最常出现的水果！蔷薇科！',Speaker=891302,}",
  [163111] = "{id=163111,Text='嗯——还不错。',Speaker=891309,}",
  [163112] = "{id=163112,Text='那么——请严肃面对下一关的挑战吧。',Speaker=891309,}",
  [163113] = "{id=163113,Text='这三个立牌上，只有一个写了你那伙伴说过的话。',Speaker=891309,}",
  [163114] = "{id=163114,Text='你能慢念默写，认真回想你们度过的时光。',Speaker=891309,}",
  [163116] = "{id=163116,Text='这是小波利说过的话吗？',Option='{就是这个,4},{不是这个,5}',}",
  [163117] = "{id=163117,Text='哎呀呀，原来你忘掉的，不只有丢掉了波利这回事。',Speaker=891309,}",
  [163118] = "{id=163118,Text='没想到，你还记得……',Speaker=891309,}",
  [163119] = "{id=163119,Text='事到如今，我也不得不说实话了。',Speaker=891309,}",
  [163120] = "{id=163120,Text='虽然你上面所有的考验都通过了，',Speaker=891309,}",
  [163121] = "{id=163121,Text='但我，不得不分享一个令人悲伤的消息！',Speaker=891309,}",
  [163122] = "{id=163122,Text='——你的波利，不会再回来了。',Speaker=891309,}",
  [163123] = "{id=163123,Text='毕竟，我们必须要尊重波利的想法，它已经不想再回到你身边了。',Speaker=891309,}",
  [163124] = "{id=163124,Text='你也别太难过，幸运的是！',Speaker=891309,}",
  [163125] = "{id=163125,Text='波利这种魔物，要抓多少，就有多少！',Speaker=891309,}",
  [163126] = "{id=163126,Text='何况你是我们的协会会员！我们完全可以为你寻找一只更适合你的波利。',Speaker=891309,}",
  [163127] = "{id=163127,Text='你看，之前的那只你不是一直觉得它太弱小了，一直在锻炼它嘛！',Speaker=891309,}",
  [163128] = "{id=163128,Text='你放心，这次我们为你找寻的，都是由我们会长一手训练出来的特级波利！',Speaker=891309,}",
  [163129] = "{id=163129,Text='它们个个素质优秀，比你以前的波利强百万倍！',Speaker=891309,}",
  [163130] = "{id=163130,Text='去挑选一只吧，它将会取代那只波利，陪你继续冒险。',Speaker=891309,}",
  [163132] = "{id=163132,Text='我一定可以协助你冒险的！',Speaker=891312,}",
  [163133] = "{id=163133,Text='（脑海里响起了波利曾经说的话）',}",
  [163134] = "{id=163134,Text='主人！加油！主人！加油！你一定可以打过恶魔波利的！',Speaker=891305,}",
  [163136] = "{id=163136,Text='这些料理可以让你在冒险中变得更强大！',Speaker=891313,}",
  [163137] = "{id=163137,Text='（脑海里响起了波利曾经说的话）',}",
  [163138] = "{id=163138,Text='主人在做什么呢？是不是要给我买好吃的东西呀？',Speaker=891305,}",
  [163140] = "{id=163140,Text='更重要的是，我胖，所以耐抗！',Speaker=891314,}",
  [163141] = "{id=163141,Text='（脑海里响起了波利曾经说的话）',}",
  [163142] = "{id=163142,Text='嘤嘤嘤……主人……魔物好可怕……我们一起逃吧……',Speaker=891305,}",
  [163143] = "{id=163143,Text='怎么样，想好了吗？要选择哪只？',Speaker=891309,Option='{1号,0},{2号,0},{3号,0}',}",
  [163144] = "{id=163144,Text='（不知为何，波利的声音一直回荡在耳边）',}",
  [163145] = "{id=163145,Text='主人主人你在做什么呢~',Speaker=891305,}",
  [163146] = "{id=163146,Text='即使不拆开礼物，波利也会很兴奋的跳起来呢！',Speaker=891305,}",
  [163147] = "{id=163147,Text='咕噜咕噜……咕噜咕噜……',Speaker=891305,}",
  [163148] = "{id=163148,Text='喂喂——你怎么了？决定好了吗？',Speaker=891309,Option='{1号,7},{2号,7},{3号,7},{都不选,8}',}",
  [163149] = "{id=163149,Text='不，它们都不是我的波利。',}",
  [163150] = "{id=163150,Text='没错，它们都不是我的波利。',}",
  [163151] = "{id=163151,Text='嗯？你说什么？',Speaker=891309,Option='都不选',}",
  [163152] = "{id=163152,Text='这……这……可真是惊天大逆转啊……',Speaker=891309,}",
  [163153] = "{id=163153,Text='为什么不选我为什么不选我为什么不选我为什么不选我为什么不选我为什么不选我为什么不选我为什么不选我！？！？！？',Speaker=891314,}",
  [163154] = "{id=163154,Text='它它它它竟然是恶魔波利伪装的！',Speaker=891307,}",
  [163155] = "{id=163155,Text='为什么不选我！！！！！！！',Speaker=891315,}",
  [163156] = "{id=163156,Text='等、等等！',Speaker=891305,}",
  [163157] = "{id=163157,Text='（小声）不……不能怕！吸吸呼~吸吸呼~！',Speaker=891305,}",
  [163158] = "{id=163158,Text='————要伤害主人，先过我这关……！！！！',Speaker=891305,}",
  [163159] = "{id=163159,Text='主人主人！我们一起击败它！',Speaker=891305,}",
  [163160] = "{id=163160,Text='呜呜呜……为什么……不选我……',Speaker=891315,Option='你不是它 ',}",
  [163161] = "{id=163161,Text='呜呜呜……',Speaker=891315,Emoji=24,}",
  [163162] = "{id=163162,Text='主人……我刚才……是不是保护了你呢……',Speaker=891305,Option='{你真厉害,0},{谢谢你诶,0}',}",
  [163163] = "{id=163163,Text='咳咳！~虽然刚刚很害怕，但是现在却有点害羞呢！',Speaker=891305,}",
  [163164] = "{id=163164,Text='……呵，呵呵……',Speaker=891307,}",
  [163165] = "{id=163165,Text='想不到啊……这简直……',Speaker=891307,}",
  [163166] = "{id=163166,Text='——精彩绝伦！',Speaker=891307,}",
  [163167] = "{id=163167,Text='没错，我正是——“只要名字里换一个字，就会和传说美少年同名”的阿格尼斯！',Speaker=891308,}",
  [163168] = "{id=163168,Text='对魔物的爱如同熔岩波利般，爆炸、燃烧、旋转为余烬的宠物协会会长！',Speaker=891308,}",
  [163169] = "{id=163169,Text='[c][ffff00][PlayerName][-][/c]，你的波利，现在还给你了。',Speaker=891308,}",
  [163170] = "{id=163170,Text='哈哈哈哈哈哈！！！我好~~~~~~~~~~~好开心哟\\(^o^)/~',Speaker=891305,}",
  [163171] = "{id=163171,Text='今天是我的幸运日！',Speaker=891305,Emoji=24,}",
  [163172] = "{id=163172,Text='主人心里一直都有我！还有……………………',Speaker=891305,}",
  [163173] = "{id=163173,Text='对不起……我从你身边跑开了啾……！',Speaker=891305,Option='对不起，我没能发现',}",
  [163174] = "{id=163174,Text='嘿嘿，那我们~扯平了~！',Speaker=891305,}",
  [163175] = "{id=163175,Text='嘤嘤嘤！',Speaker=891308,}",
  [163176] = "{id=163176,Text='魔物和主人的羁绊，果真跨越了次元的距离！——感动死我啦！',Speaker=891308,Emoji=27,}",
  [163177] = "{id=163177,Text='找到了小波利我也很高兴……但……这是怎么一回事？我的噗噜噜呢？？？',Speaker=891302,}",
  [163178] = "{id=163178,Text='噢~！先前拆开噗噜噜太过火了！您的伙伴，现在就还给您！',Speaker=891308,}",
  [163179] = "{id=163179,Text='噗噜噜……！',Speaker=891302,}",
  [163180] = "{id=163180,Text='呜呜啾QAQ真的很对不起，[c][ffff00][PlayerName][-][/c]……',Speaker=891305,}",
  [163181] = "{id=163181,Text='你一直逼着我锻炼，想让我变得更强……',Speaker=891305,}",
  [163182] = "{id=163182,Text='但是我只想和你一起玩耍！',Speaker=891305,}",
  [163183] = "{id=163183,Text='我觉得你根本不在乎我的感受，只把我当作一只用来战斗的宠物！',Speaker=891305,}",
  [163184] = "{id=163184,Text='我当时我好~~生气！好~~不甘心！就偷偷离开了。',Speaker=891305,Emoji=19,}",
  [163185] = "{id=163185,Text='但是啊！但是啊！我遇到了嗒啦啦和噗噜噜！',Speaker=891305,}",
  [163186] = "{id=163186,Text='我这才意识到，原来……原来主人你一直把我当做你的伙伴！',Speaker=891305,}",
  [163187] = "{id=163187,Text='是我太不懂事了，以为只有一起玩耍才叫做伙伴。',Speaker=891305,}",
  [163188] = "{id=163188,Text='真正的伙伴，是要彼此依靠，一起努力的！',Speaker=891305,}",
  [163189] = "{id=163189,Text='主人，你以前一直保护我，信任我……',Speaker=891305,Emoji=41,}",
  [163190] = "{id=163190,Text='所以，我也要变得强大起来，帮助你，保护你，在冒险旅途中，一起前行！',Speaker=891305,}",
  [163191] = "{id=163191,Text='只有这样，我们才是真正的伙伴吧！——就像嗒啦啦和噗噜噜一样！',Speaker=891305,}",
  [163192] = "{id=163192,Text='嘻嘻~我呀~以后还想吃好多好多好吃的苹果！',Speaker=891305,}",
  [163193] = "{id=163193,Text='只有这样，我才能变得越来越强大！——才能好好守护你！',Speaker=891305,Option='{拉钩,0},{答应,0}',}",
  [163194] = "{id=163194,Text='我来帮波、波利解释吧！',Speaker=891308,}",
  [163195] = "{id=163195,Text='波、波利回来之后，就找到了我。',Speaker=891308,}",
  [163196] = "{id=163196,Text='它想接受训练，想要变得能够和你一起战斗，一起成长，互相守护。',Speaker=891308,}",
  [163197] = "{id=163197,Text='这段时间，它受了不少苦。',Speaker=891308,}",
  [163198] = "{id=163198,Text='本来训练完成后，就想把它送回你身边。',Speaker=891308,}",
  [163199] = "{id=163199,Text='但是迪恩却发现，你完全没有发现波、波利不见了！',Speaker=891308,Emoji=12,}",
  [163200] = "{id=163200,Text='为了保护波、波利，我们决定考验你一下，看看你是否有资格继续成为它的主人。',Speaker=891308,}",
  [163201] = "{id=163201,Text='我很高兴，你通过了考验。',Speaker=891308,}",
  [163202] = "{id=163202,Text='找到了小波利我也好高兴……但噗噜噜为什么会没电啦！？',Speaker=891302,}",
  [163203] = "{id=163203,Text='啊……它纯粹是在保护波、波利和狸猫战斗时，电量耗尽了……',Speaker=891308,}",
  [163204] = "{id=163204,Text='噗噜噜……',Speaker=891302,Emoji=24,}",
  [163205] = "{id=163205,Text='美纳斯说过，她送我来到这个世界是单向的，无法将我送回去。',Speaker=891302,}",
  [163206] = "{id=163206,Text='我和噗噜噜要回原来的世界，恐怕还需要噗噜噜的力量。',Speaker=891302,Option='那怎么办？',}",
  [163207] = "{id=163207,Text='看来需要在这个世界寻找能为噗噜噜充电的能源了……',Speaker=891302,}",
  [163208] = "{id=163208,Text='我得好好研究一下……',Speaker=891302,}",
  [163209] = "{id=163209,Text='噗噜噜是为了我才……',Speaker=891305,Emoji=42,}",
  [163210] = "{id=163210,Text='不是小波利的错！放心好了~我一定能让噗噜噜活蹦乱跳的！',Speaker=891302,}",
  [163211] = "{id=163211,Text='我先带它回南门了！美纳斯一直在担心着我们呢~',Speaker=891302,}",
  [163212] = "{id=163212,Text='[c][ffff00][PlayerName][-][/c]，噗噜噜充能完毕，随时就绪。',Speaker=891317,Option='很好',Emoji=17,}",
  [163213] = "{id=163213,Text='嗯！还好我从仓库里搞到了发电机！',Speaker=891306,}",
  [163214] = "{id=163214,Text='波利从宠物蛋包包中跳了出来。',}",
  [163215] = "{id=163215,Text='噗噜噜！又可以见到噗噜噜了哟！',Speaker=891305,}",
  [163216] = "{id=163216,Text='[c][ffff00][PlayerName][-][/c]，你这几天帮我真是太感谢了~现在，你得亲手启动噗噜噜！',Speaker=891317,Option='为什么',Emoji=9,}",
  [163217] = "{id=163217,Text='不只是为了感谢。我们要启程回到原来世界了，出发前要让你和它打个招呼。',Speaker=891317,Option='好',}",
  [163218] = "{id=163218,Text='……小心点哦，别碰到其他键了……',Speaker=891317,Option='我注意',}",
  [163219] = "{id=163219,Text='DI DU DONG DU DU！\n[c][ffff00]重启成功！[-][/c]',Speaker=891303,}",
  [163220] = "{id=163220,Text='DA LA LA BI DA！\n[c][ffff00]重要目标确认：嗒啦啦和波利！[-][/c]',Speaker=891303,}",
  [163221] = "{id=163221,Text='噗噜噜！你终于回来啦！',Speaker=891317,}",
  [163222] = "{id=163222,Text='DI BU  DONG BA DA LA LA！\n[c][ffff00]嗒啦啦，我知道你会来找我。[-][/c]',Speaker=891303,}",
  [163223] = "{id=163223,Text='呜呜呜~！噗噜噜！我好想你！',Speaker=891317,Option='嗒啦啦……',Emoji=24,}",
  [163224] = "{id=163224,Text='DI BU  DONG BA DA！\n[c][ffff00]这就是你的主人吧，波利？[-][/c]',Speaker=891303,}",
  [163225] = "{id=163225,Text='嗯嗯！',Speaker=891305,}",
  [163226] = "{id=163226,Text='DI BU  DONG BA DA！\n[c][ffff00]看来，你愿意回到[c][ffff00][PlayerName][-][/c]身边了……[-][/c]',Speaker=891303,}",
  [163227] = "{id=163227,Text='DI DONG BA DI DA！\n[c][ffff00]但是，嗒啦啦，由于指令限制，我不能离波利太远。[-][/c]',Speaker=891303,}",
  [163228] = "{id=163228,Text='BI DI DONG DI DANG！\n[c][ffff00]我试过自我骇入，但没效果。看来要认证你的声纹了，嗒啦啦。[-][/c]',Speaker=891303,}",
  [163229] = "{id=163229,Text='噗噜噜！指令解除！',Speaker=891317,}",
  [163230] = "{id=163230,Text='DI DU DONG DU DU！\n[c][ffff00]解除成功！[-][/c]',Speaker=891303,}",
  [163231] = "{id=163231,Text='太好了！噗噜噜！赶紧演算时空通道数据！开出时空裂隙！我们就能回家了！',Speaker=891317,Option='什么？',}",
  [163232] = "{id=163232,Text='DI BU  DONG BA DA！\n[c][ffff00]演算机能受限，检测到非法第三方程序。[-][/c]',Speaker=891303,}",
  [163233] = "{id=163233,Text='这是……之前那个奸商动的手脚！',Speaker=891317,}",
  [163234] = "{id=163234,Text='他加入了恶意程序，需要输入反编译后的指令才可以删除！',Speaker=891317,}",
  [163235] = "{id=163235,Text='[c][ffff00][PlayerName][-][/c]，能帮忙输入一下指令吗？我肚子好饿……没有力气了……',Speaker=891317,Emoji=15,}",
  [163236] = "{id=163236,Text='DI BU  DONG BA DA！\n[c][ffff00]我想值得一试[-][/c]',Speaker=891303,}",
  [163237] = "{id=163237,Text='……[c][ffff00][PlayerName][-][/c]，先来找我问指令吧。',Speaker=891317,Emoji=41,}",
  [163238] = "{id=163238,Text='指令很长，分三段说就是……',Speaker=891317,}",
  [163239] = "{id=163239,Text='[c][ffff00]上上下下[-][/c]。',Speaker=891317,Option='上上下下？',}",
  [163240] = "{id=163240,Text='[c][ffff00]左右左右[-][/c]！',Speaker=891317,Option='左右左右！',}",
  [163241] = "{id=163241,Text='[c][ffff00]BABA[-][/c]！',Speaker=891317,Option='爸爸？!',}",
  [163242] = "{id=163242,Text='[c][ffff00][PlayerName][-][/c]你怎么还不如我反应快呢！是[c][ffff00]B、A、B、A[-][/c]啦！',Speaker=891305,Option='……记住了',}",
  [163243] = "{id=163243,Text='第一段指令是？',Option='{上上下下？,2},{上下上下？,3}',}",
  [163244] = "{id=163244,Text='是[c][ffff00]上上下下[-][/c]！用图来说就是“↑ ↑ ↓ ↓”！',Speaker=891317,Option='噢……',}",
  [163245] = "{id=163245,Text='BA DI DONG DI BI DA LA！\n[c][ffff00]检测到逻辑单元响应！继续输入，[c][ffff00][PlayerName][-][/c]！[-][/c]',Speaker=891303,}",
  [163246] = "{id=163246,Text='第二段指令是？',Option='{左右左右？,4},{上下左右？,5}',}",
  [163247] = "{id=163247,Text='是[c][ffff00]左右左右[-][/c]！用图来说就是“← → ← →”！',Speaker=891317,}",
  [163248] = "{id=163248,Text='DI BU  DONG BA DA！\n[c][ffff00]调试无警告。继续输入。[-][/c]',Speaker=891303,}",
  [163249] = "{id=163249,Text='第三段指令是？',Option='{爸爸,6},{妈妈,6},{BABA,7}',}",
  [163250] = "{id=163250,Text='主人主人……❤————虽然你是傻的，但我也爱你凸(艹皿艹 )',Speaker=891305,}",
  [163251] = "{id=163251,Text='DI BU  DONG BA DA！\n[c][ffff00]指令解除，正在计算数据[-][/c]',Speaker=891303,}",
  [163252] = "{id=163252,Text='BI DI DU！\n[c][ffff00]我想我无法计算数据[-][/c]！',Speaker=891303,}",
  [163253] = "{id=163253,Text='DI DI——————————！\n[c][ffff00]警告，侦侦侦侦侦测到故障………[-][/c]！',Speaker=891303,}",
  [163254] = "{id=163254,Text='这！这！是怎么一回事啊啊啊啊啊啊啊！',Speaker=891317,}",
  [163255] = "{id=163255,Text='噗噜噜！停止运算！',}",
  [163256] = "{id=163256,Text='……',Speaker=891303,}",
  [163257] = "{id=163257,Text='呜……这是怎么回事！',Speaker=891306,Action={actionid=24,num=1},}",
  [163258] = "{id=163258,Text='……难道，是魔法元素！',Speaker=891317,}",
  [163259] = "{id=163259,Text='我大意了……任何的魔法元素都会导致噗噜噜的故障……',Speaker=891317,}",
  [163260] = "{id=163260,Text='而这个世界！充满魔法元素！',Speaker=891317,}",
  [163261] = "{id=163261,Text='怎么会这样！',Speaker=891306,}",
  [163262] = "{id=163262,Text='我要怎么才能修好你呀，噗噜噜……',Speaker=891317,}",
  [163263] = "{id=163263,Text='这次我不仅回不去，还少了一起打游戏的伙伴……人生都可以说失去了意义！',Speaker=891317,}",
  [163264] = "{id=163264,Text='——嗒啦啦！不要灰心！',Speaker=891306,}",
  [163265] = "{id=163265,Text='既然是魔法元素导致的，就让魔法来解决吧！',Speaker=891306,}",
  [163266] = "{id=163266,Text='在我们这个世界里，不仅有很多的魔法材料，还有很多擅长魔法的天才！',Speaker=891306,}",
  [163267] = "{id=163267,Text='一定有人能帮助你的！',Speaker=891306,}",
  [163268] = "{id=163268,Text='先在普隆德拉安顿下来吧~你不是有一个绝佳的生存手段吗？',Speaker=891306,}",
  [163269] = "{id=163269,Text='我的咖啡车！',Speaker=891317,}",
  [163270] = "{id=163270,Text='走吧~我带你去看看店面~',Speaker=891317,}",
  [163271] = "{id=163271,Text='我和波利，似乎被遗忘了……',}",
  [163272] = "{id=163272,Text='啊！抱歉！刚刚满脑子想着如何帮助嗒啦啦，把你们两个忘记了~！',Speaker=891306,}",
  [163273] = "{id=163273,Text='[c][ffff00][PlayerName][-][/c]，你来的正好！快看！',Speaker=891317,}",
  [163274] = "{id=163274,Text='当当当！！！准备完毕！',Speaker=891317,}",
  [163275] = "{id=163275,Text='哈哈！刚开始，装潢是很简陋……但我已迈出了第一步！',Speaker=891317,}",
  [163276] = "{id=163276,Text='从这个小小摊点开始我的事业！',Speaker=891306,}",
  [163277] = "{id=163277,Text='嗒啦啦真是乐观呢！',Speaker=891306,}",
  [163278] = "{id=163278,Text='难得开业，来拍张纪念照吧！',Speaker=891317,}",
  [163279] = "{id=163279,Text='好耶！我最喜欢拍照了！',Speaker=891305,}",
  [163280] = "{id=163280,Text='我要把这张照片挂到店门口！',Speaker=891317,}",
  [163281] = "{id=163281,Text='你和小波利也要当送材料的主力，经常过来哟……',Speaker=891317,Option='好呀！',}",
  [163282] = "{id=163282,Text='（碎碎念）咦，没见过的牌子？',Speaker=891318,}",
  [163283] = "{id=163283,Text='这家店是新开张的吗？',Speaker=891318,}",
  [163284] = "{id=163284,Text='你好——有人吗？招牌的TapTap能量来一杯！',Speaker=891318,}",
  [163285] = "{id=163285,Text='没想到第一个冒险者这么快就上门了！',Speaker=891317,}",
  [163286] = "{id=163286,Text='等等————马——上——来！',Speaker=891317,Emoji=18,}",
  [163287] = "{id=163287,Text='这……与其说是招待，不如说更像是在客人和后厨之间来回赛跑呢……',Speaker=891306,Option='也许这就是高玩',Emoji=10,}",
  [163288] = "{id=163288,Text='你看，嗒啦啦正拼命忙呢！',Speaker=891306,}",
  [163289] = "{id=163289,Text='之后也拜托你多多照顾嗒啦啦的生意了~',Speaker=891306,}",
  [163290] = "{id=163290,Text='虽然舍不得，但是还是希望它们能早日回到自己的世界。',Speaker=891306,Option='没问题',}",
  [163517] = "{id=163517,Text='你好呀，这位[c][ffff00]善良[-][/c]的冒险者~~',Speaker=891330,}",
  [163518] = "{id=163518,Text='波利一族需要你的帮助，你一定不会拒绝的吧！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891330,Option='{好的吧,2},{不，我拒绝！,3}',Emoji=22,}",
  [163519] = "{id=163519,Text='感谢你的帮助！事情是这样的~~',Speaker=891330,Emoji=6,}",
  [163520] = "{id=163520,Text='5月是属于劳动者的月份~波利们又兢兢业业的工作了一整年~',Speaker=891330,}",
  [163521] = "{id=163521,Text='我想请冒险者你作为使者，巡视生活在世界各地的波利族群~看看它们过的是否幸福！',Speaker=891330,}",
  [163522] = "{id=163522,Text='麻烦你拍一些照片，纪录波利们的生活……',Speaker=891330,}",
  [163523] = "{id=163523,Text='我还会在波利岛举办一场表彰大会，对这一年付出辛苦劳作的波利们作出嘉奖……',Speaker=891330,}",
  [163524] = "{id=163524,Text='也请冒险者你替我邀请波利们前来参加哦~~',Speaker=891330,Option='我明白了',}",
  [163525] = "{id=163525,Text='波利一族感谢你的帮助~加油哦，跑腿人！',Speaker=891330,Emoji=27,}",
  [163526] = "{id=163526,Text='（那么，就从南门的波利族群开始吧！）',}",
  [163527] = "{id=163527,Text='不打工了！',Speaker=891331,}",
  [163528] = "{id=163528,Text='我们要罢工！！',Speaker=891331,}",
  [163529] = "{id=163529,Text='我们每天兢兢业业工作，被新人冒险者们[c][ffff00]哒哒哒[-][/c]！送给他们宝贵的经验值！',Speaker=891331,}",
  [163530] = "{id=163530,Text='可是待遇一直不提高，每天只能分到1个[c][ffff00]苹果[-][/c]！',Speaker=891331,}",
  [163531] = "{id=163531,Text='我们要罢工！！',Speaker=891331,}",
  [163532] = "{id=163532,Text='我们要更多的苹果！！',Speaker=891331,}",
  [163533] = "{id=163533,Text='（那边有一只天使波利，去问问它发生了什么吧？）',}",
  [163534] = "{id=163534,Text='诶？这位冒险者，你是国王派来的使者？',Speaker=891332,Option='波利为何罢工？',Emoji=25,}",
  [163535] = "{id=163535,Text='嗯……其实也没有多复杂的原因——就是每天被冒险者们哒哒哒、工作压力大……可是报酬，已经好多年没有增加了……',Speaker=891332,}",
  [163536] = "{id=163536,Text='本来最近是有计划给波利们增加报酬的……可是作为报酬的苹果，竟然被一群捣蛋的小恶魔抢走了！',Speaker=891332,}",
  [163537] = "{id=163537,Text='真是的、小恶魔为什么会出现在这里啊！',Speaker=891332,}",
  [163538] = "{id=163538,Text='诶？这位冒险者，你看起来挺强大的样子，不如……',Speaker=891332,}",
  [163539] = "{id=163539,Text='你去把苹果抢回来！这样就可以给波利们增加报酬，它们应该就没有怨言了！',Speaker=891332,Option='好吧……',}",
  [163540] = "{id=163540,Text='那些小恶魔往大桥的方向跑了，你可以到那边去找它们！',Speaker=891332,}",
  [163541] = "{id=163541,Text='哈哈，我们抢到了这么多苹果！',Speaker=891345,}",
  [163542] = "{id=163542,Text='大哥一定会很高兴的！',Speaker=891345,}",
  [163543] = "{id=163543,Text='什么人？',Speaker=891345,Emoji=8,}",
  [163544] = "{id=163544,Text='哼，可恶的冒险者！大哥一定会替我们报仇的！',Speaker=891345,Emoji=12,}",
  [163545] = "{id=163545,Text='哇，这么快就把苹果找回来了？真不愧是你啊，冒险者！',Speaker=891332,Emoji=41,}",
  [163546] = "{id=163546,Text='诶？大家都到了吗？',Speaker=891332,}",
  [163547] = "{id=163547,Text='这位冒险者为我们找回了丢失的苹果，从今天开始……',Speaker=891332,}",
  [163548] = "{id=163548,Text='所有的波利每天可以多获得一个苹果作为报酬！',Speaker=891332,}",
  [163549] = "{id=163549,Text='除此之外，为了感谢大家这一年的辛勤工作，我们决定给予大家一天休假！',Speaker=891332,}",
  [163550] = "{id=163550,Text='休假就在明天，大家要好好享受自己的假期哦~~',Speaker=891332,}",
  [163551] = "{id=163551,Text='太棒了！',}",
  [163552] = "{id=163552,Text='苹果万岁！',}",
  [163553] = "{id=163553,Text='假期万岁！',}",
  [163554] = "{id=163554,Text='冒险者万岁！',}",
  [163555] = "{id=163555,Text='感谢你的帮助哦，冒险者~',Speaker=891332,Option='{邀请参加,0},{表彰大会,0}',Emoji=6,}",
  [163556] = "{id=163556,Text='诶？是嘉奖[c][ffff00]辛苦劳作的波利们[-][/c]的表彰大会啊！那我们一定会参加的！',Speaker=891332,}",
  [163557] = "{id=163557,Text='南门的波利族群，可是全世界最勤奋的哦！',Speaker=891332,}",
  [163558] = "{id=163558,Text='对了，明天是波利们的假期！冒险者不如一起来吧？',Speaker=891332,}",
  [163559] = "{id=163559,Text='能跟冒险者一起玩耍，大家一定会很高兴！还能遇到很多新朋友哦~',Speaker=891332,Option='好的呀',}",
  [163560] = "{id=163560,Text='那么明天见了，冒险者！',Speaker=891332,}",
  [163561] = "{id=163561,Text='冒险者你终于来了~快来和我们一起玩儿吧！',Speaker=891332,}",
  [163562] = "{id=163562,Text='（竟然有这么多不同颜色的波利……）',}",
  [163563] = "{id=163563,Text='波利一族可是个大家族~有各种各样不同属性、不同颜色的波利~',Speaker=891332,}",
  [163564] = "{id=163564,Text='我们分布在世界各地，会在冒险者你的旅程中一直陪伴着你哦~',Speaker=891332,}",
  [163565] = "{id=163565,Text='那么一起来玩吧，冒险者！',Speaker=891332,}",
  [163566] = "{id=163566,Text='玩点什么好呢……',Speaker=891332,}",
  [163567] = "{id=163567,Text='就玩捉迷藏好了！',Speaker=891332,}",
  [163568] = "{id=163568,Text='波利们最擅长捉迷藏了！',Speaker=891332,}",
  [163569] = "{id=163569,Text='就由波利们藏，冒险者，你来找吧？',Speaker=891332,Option='好呀',}",
  [163570] = "{id=163570,Text='你可要仔细一些，波利们——真的很擅长[c][ffff00]隐藏自己[-][/c]的哦~',Speaker=891332,}",
  [163571] = "{id=163571,Text='那么……我们开始吧！',Speaker=891332,}",
  [163572] = "{id=163572,Text='（波利们应该藏好了，快去找到它们吧！）',}",
  [163573] = "{id=163573,Text='（明明往这个方向跑了……藏在哪儿了呢……）',}",
  [163574] = "{id=163574,Text='（摇晃）',Speaker=891350,}",
  [163575] = "{id=163575,Text='（嗯？那个礼物盒……好像动了一下……）',}",
  [163576] = "{id=163576,Text='糟糕，被发现了……',Speaker=891334,}",
  [163577] = "{id=163577,Text='藏在那么显眼的地方肯定会被发现的啦……',Speaker=891333,}",
  [163578] = "{id=163578,Text='糟了……',Speaker=891333,}",
  [163579] = "{id=163579,Text='（那边的天使波利玩偶……看起来怪怪的……）',}",
  [163580] = "{id=163580,Text='看不见我、看不见我……诶呀，被发现了！',Speaker=891332,}",
  [163581] = "{id=163581,Text='没想到你也很擅长捉迷藏嘛，冒险者！',Speaker=891332,}",
  [163582] = "{id=163582,Text='不过其他伙伴可没那么好找到哦~毕竟……',Speaker=891332,}",
  [163583] = "{id=163583,Text='波利可以依靠自己的肤色，完美的融入环境哦~~',Speaker=891332,}",
  [163584] = "{id=163584,Text='冒险者你要加油哦！找到伙伴们就回来找我们吧~',Speaker=891332,}",
  [163585] = "{id=163585,Text='（融入环境？就像保护色那样？那么……剩下的波利会藏在哪儿呢？）',}",
  [163586] = "{id=163586,Text='（这里有很多树木和草丛，正适合藏身……那么最有可能藏在这里的波利是？）',Option='{水啵啵,2},{红啵啵,3},{啵啵绿,4}',}",
  [163587] = "{id=163587,Text='（水啵啵是蓝色的，在这里太显眼了……应该不是它）',Emoji=31,}",
  [163588] = "{id=163588,Text='（红啵啵是红色的，在这里太显眼了……应该不是它）',Emoji=31,}",
  [163589] = "{id=163589,Text='（树叶和草丛都是绿色的，正适合啵啵绿藏身！）',Emoji=32,}",
  [163590] = "{id=163590,Text='（摇晃……）',Speaker=891351,}",
  [163591] = "{id=163591,Text='（草丛里好像有声音……）',}",
  [163592] = "{id=163592,Text='诶呀……被发现了！',Speaker=891335,}",
  [163593] = "{id=163593,Text='我藏的这么好都能被发现……冒险者你真厉害！',Speaker=891335,}",
  [163594] = "{id=163594,Text='快点把其他伙伴也找出来吧！',Speaker=891335,}",
  [163595] = "{id=163595,Text='（还剩水啵啵和红啵啵……水啵啵可能藏在哪儿呢？）',Option='{天上,5},{水中,6}',}",
  [163596] = "{id=163596,Text='（呃，水啵啵应该不会飞……它应该是，藏在了水里？）',}",
  [163597] = "{id=163597,Text='（没错，最适合水啵啵隐藏自己的地方就是水中……）',}",
  [163598] = "{id=163598,Text='（到附近的河边看看吧！）',}",
  [163599] = "{id=163599,Text='诶呦！',Speaker=891333,}",
  [163600] = "{id=163600,Text='你踩到我了，冒险者！',Speaker=891336,Option='对不起',}",
  [163601] = "{id=163601,Text='不过你真厉害呀，竟然想到到水边来找我！',Speaker=891336,}",
  [163602] = "{id=163602,Text='你找到所有藏起来的波利了吗？',Speaker=891336,Option='{还没有,0},{还差红啵啵,0}',}",
  [163603] = "{id=163603,Text='红啵啵嘛……它可是波利中玩捉迷藏的高手！',Speaker=891336,}",
  [163604] = "{id=163604,Text='如果实在找不到的话……不如去问问其他波利伙伴哦~~',Speaker=891336,}",
  [163605] = "{id=163605,Text='你回来了~冒险者！咦？你还没找到红啵啵嘛？',Speaker=891332,Option='{是的,0},{它藏在哪儿了,0}',}",
  [163606] = "{id=163606,Text='毕竟红啵啵是我们中最擅长捉迷藏的呢！',Speaker=891332,}",
  [163607] = "{id=163607,Text='连我们都想不到它会藏在哪儿呢！',Speaker=891332,Option='{真厉害,0},{我放弃了,0}',}",
  [163608] = "{id=163608,Text='嘻嘻……',Speaker=891337,}",
  [163609] = "{id=163609,Text='SURPRISE！',Speaker=891337,}",
  [163610] = "{id=163610,Text='惊不惊喜，冒险者！没想到我藏在这堆炸弹里面吧~~',Speaker=891337,Emoji=7,}",
  [163611] = "{id=163611,Text='虽然没有找到红啵啵，不过冒险者也很厉害了呢！',Speaker=891332,}",
  [163612] = "{id=163612,Text='和冒险者一起玩耍，真的很开心啊~',Speaker=891332,Emoji=27,}",
  [163613] = "{id=163613,Text='那么我们一起拍一张留作纪念的合照吧~',Speaker=891332,}",
  [163614] = "{id=163614,Text='（？？照片中……竟然有一只幽灵波利！）',Emoji=8,}",
  [163615] = "{id=163615,Text='啊！我好惨啊！',Speaker=891341,}",
  [163616] = "{id=163616,Text='我竟然连自己怎么死的都不知道，突然就变成幽灵了！',Speaker=891341,Option='诶？',}",
  [163617] = "{id=163617,Text='这位冒险者，你能看的到我？',Speaker=891341,}",
  [163618] = "{id=163618,Text='那么……能不能请你帮我调查我的死因呢？',Speaker=891341,Option='可以哦~',Emoji=22,}",
  [163619] = "{id=163619,Text='那么明天请你到里希塔乐原野来找我~',Speaker=891341,}",
  [163620] = "{id=163620,Text='我依稀记得，自己好像就是在那儿死掉的……',Speaker=891341,}",
  [163621] = "{id=163621,Text='冒险者，你终于来了！准备好帮我调查死亡的原因了吗！',Speaker=891341,}",
  [163622] = "{id=163622,Text='可能是变成幽灵波利的过程中丢失了一部分记忆……',Speaker=891341,}",
  [163623] = "{id=163623,Text='我现在只记得自己是在几天前死在里希塔乐原野……',Speaker=891341,}",
  [163624] = "{id=163624,Text='其他生前的记忆……已经完全没有了！',Speaker=891341,}",
  [163625] = "{id=163625,Text='不如你去附近调查一下，或许能找到一些我死亡时的线索？',Speaker=891341,Emoji=22,}",
  [163626] = "{id=163626,Text='我是最硬的波利！',Speaker=891338,}",
  [163627] = "{id=163627,Text='我才是',Speaker=891339,}",
  [163628] = "{id=163628,Text='石头再坚硬能比得过金属吗？',Speaker=891338,}",
  [163629] = "{id=163629,Text='虽然你是金属波利——但是一看就知道你是空心的！而我们石头波利可是实心的哦！',Speaker=891339,}",
  [163630] = "{id=163630,Text='别吵了……我觉得，冰也是很坚硬的呀——而且我也是实心的！',Speaker=891340,}",
  [163631] = "{id=163631,Text='可惜……“坚硬大赛”被叫停了！不然只要参加比赛，就知道我们谁是最坚硬的波利啦！',Speaker=891339,Emoji=25,}",
  [163632] = "{id=163632,Text='嗯，毕竟谁也没想到会发生那种意外嘛……不过即使参加比赛，你也赢不过我的！',Speaker=891338,}",
  [163633] = "{id=163633,Text='（坚硬大赛？意外？？）',}",
  [163634] = "{id=163634,Text='（不知道跟幽啵啵有没有关系……）',}",
  [163635] = "{id=163635,Text='嗯？一个突然出现的冒险者！',Speaker=891339,}",
  [163636] = "{id=163636,Text='你有什么事吗？',Speaker=891339,Option='关于坚硬大赛',}",
  [163637] = "{id=163637,Text='你对这个感兴趣？坚硬大赛——是为了选出最坚硬波利的赛事哦~~',Speaker=891339,}",
  [163638] = "{id=163638,Text='比赛的方法很简单哦~~就是爬到高处，然后从上面跳下来，保证自己完好无损~',Speaker=891339,}",
  [163639] = "{id=163639,Text='谁跳的地方最高，谁就是比赛的胜利者了！',Speaker=891339,}",
  [163640] = "{id=163640,Text='（这、这么粗暴？！）',Emoji=7,}",
  [163641] = "{id=163641,Text='不过……',Speaker=891339,}",
  [163642] = "{id=163642,Text='前几天的比赛中出了一些意外……导致名叫西啵啵的波利伙伴身亡了……',Speaker=891339,}",
  [163643] = "{id=163643,Text='坚硬大赛因为这件事被无限期的终止了……',Speaker=891339,}",
  [163644] = "{id=163644,Text='（西啵啵……应该就是幽啵啵生前的名字吧？）',}",
  [163645] = "{id=163645,Text='（它竟然是在这么……勇猛的比赛中丧生的吗？）',}",
  [163646] = "{id=163646,Text='我有个好主意！',Speaker=891339,}",
  [163647] = "{id=163647,Text='或许这位冒险者可以帮我们评判谁才是最坚硬的！',Speaker=891339,}",
  [163648] = "{id=163648,Text='只要我们轮流让这位冒险者攻击——谁能坚持的时间最长、谁就是最坚硬的！',Speaker=891339,}",
  [163649] = "{id=163649,Text='天哪——',Speaker=891338,}",
  [163650] = "{id=163650,Text='真是个好主意！',Speaker=891338,}",
  [163651] = "{id=163651,Text='没错，你虽然不一定是最坚硬的，但你一定是最机智的波利！',Speaker=891340,}",
  [163652] = "{id=163652,Text='冒险者，你可以帮助我们吗？',Speaker=891339,Option='好的',}",
  [163653] = "{id=163653,Text='呼……我竟然在这么强大的冒险者手中坚持了5次攻击，我真是太厉害了！',Speaker=891338,}",
  [163654] = "{id=163654,Text='5、6次？竟然比我多坚持了一次攻击！怎么会这样？！',Speaker=891338,Emoji=1,}",
  [163655] = "{id=163655,Text='嘿嘿，我就说我比你更坚硬啦~',Speaker=891339,Emoji=5,}",
  [163656] = "{id=163656,Text='接下来看我的吧！',Speaker=891340,}",
  [163657] = "{id=163657,Text='怎、怎么会这样……',Speaker=891340,}",
  [163658] = "{id=163658,Text='噗……竟然只坚持了1次……',Speaker=891339,Emoji=17,}",
  [163659] = "{id=163659,Text='噗……你也太脆了吧！我刚刚好像都听到冰块破碎的声音了……',Speaker=891338,Emoji=17,}",
  [163660] = "{id=163660,Text='哈哈，所以果然——我才是最坚硬的波利呀~',Speaker=891339,}",
  [163661] = "{id=163661,Text='（看他们这么有活力，看起来应该生活的很幸福吧！）',}",
  [163662] = "{id=163662,Text='（不如拍张照片给国王波利看看吧~）',}",
  [163663] = "{id=163663,Text='（接下来……回去将死亡的真相告诉幽啵啵吧！）',}",
  [163664] = "{id=163664,Text='你回来了，冒险者！知道我到底是怎么死的了吗？',Speaker=891341,Option='告知',}",
  [163665] = "{id=163665,Text='…………',Speaker=891341,}",
  [163666] = "{id=163666,Text='原来……我的真名叫西啵啵啊……',Speaker=891341,}",
  [163667] = "{id=163667,Text='我竟然是死在“坚硬大赛”中——我是死在证明自己的过程中的！',Speaker=891341,}",
  [163668] = "{id=163668,Text='这么想来，我也算是无怨无悔了！',Speaker=891341,Emoji=25,}",
  [163669] = "{id=163669,Text='谢谢你，冒险者！',Speaker=891341,}",
  [163670] = "{id=163670,Text='如果可以的话，希望你明天还能过来……',Speaker=891341,}",
  [163671] = "{id=163671,Text='我想去祭拜一下我自己……',Speaker=891341,}",
  [163672] = "{id=163672,Text='你来了呀，冒险者。我们……到埋葬我尸体的地方去看看吧！',Speaker=891341,}",
  [163673] = "{id=163673,Text='在这之前，可不可以帮我准备一些鲜花……',Speaker=891341,}",
  [163674] = "{id=163674,Text='毕竟祭拜亡者……总是需要带些东西的……',Speaker=891341,}",
  [163675] = "{id=163675,Text='（这话怎么听着怪怪的……哪有人有机会祭拜自己啊……）',Emoji=10,}",
  [163676] = "{id=163676,Text='既然准备好了鲜花，我们就出发去我的墓地吧！',Speaker=891341,}",
  [163677] = "{id=163677,Text='（总觉得……怪怪的啊……）',Emoji=10,}",
  [163678] = "{id=163678,Text='我感觉到了，就是这里了……',Speaker=891341,Emoji=7,}",
  [163679] = "{id=163679,Text='你是谁？为什么会在这里？',Speaker=891333,}",
  [163680] = "{id=163680,Text='你是……我哥哥的朋友？',Speaker=891344,Option='哥哥？',Emoji=8,}",
  [163681] = "{id=163681,Text='没错，这里埋葬的就是我的亲生哥哥西啵啵……',Speaker=891344,}",
  [163682] = "{id=163682,Text='（等等，如果西啵啵是它的哥哥，那岂不就是……）',Option='你哥哥的种族？',}",
  [163683] = "{id=163683,Text='我哥哥就是一只波利啊，不然呢？',Speaker=891344,}",
  [163684] = "{id=163684,Text='等等，如果我生前只是一只普通波利、而不是金属波利或者石波利……那我怎么会死在坚硬大赛呢？',Speaker=891341,Emoji=7,}",
  [163685] = "{id=163685,Text='这位冒险者，你还有什么事吗？',Speaker=891344,Emoji=19,}",
  [163686] = "{id=163686,Text='（呃，幽啵啵是幽灵，它说的话好像没法被南啵啵听到……）',Option='幽啵啵的疑惑',}",
  [163687] = "{id=163687,Text='诶？你是说我哥哥的死因吗……',Speaker=891344,}",
  [163688] = "{id=163688,Text='说起来怪不好意思的……我和哥哥旅行时听说这里的“坚硬大赛”非常有名，所以前来参观……',Speaker=891344,}",
  [163689] = "{id=163689,Text='结果刚走到观众席，哥哥就被一位从天而降的选手——',Speaker=891344,}",
  [163690] = "{id=163690,Text='给砸死了！！',Speaker=891344,}",
  [163691] = "{id=163691,Text='呜呜……想起来就伤心，我要回家，把这个消息告诉妈妈了……',Speaker=891344,}",
  [163692] = "{id=163692,Text='怎、怎么会这样……',Speaker=891341,}",
  [163693] = "{id=163693,Text='怎么会这样……我原本以为自己是参加坚硬大赛，为了追求“最坚硬”之名而死，还算是英勇、有追求……',Speaker=891341,Option='安慰',}",
  [163694] = "{id=163694,Text='可是现在这样的死法……实在是太憋屈了！我的波生毫无意义啊55555555……',Speaker=891341,}",
  [163695] = "{id=163695,Text='不能这么想哦~新人！',Speaker=891342,}",
  [163696] = "{id=163696,Text='虽然你活着的时候确实没做出什么惊天动地的大事……',Speaker=891342,}",
  [163697] = "{id=163697,Text='但是死后的日子——却是可以过的很有意义呢！',Speaker=891342,}",
  [163698] = "{id=163698,Text='我们幽灵波利一族一直在辛勤的工作着，为冒险者们奉献宝贵的经验！',Speaker=891342,}",
  [163699] = "{id=163699,Text='那些牧师冒险者，非常喜欢我们呢！',Speaker=891342,}",
  [163700] = "{id=163700,Text='虽然你没有参加坚硬大赛，但我们幽灵波利一族中，也有“比谁死的更惨”的比惨大赛呢！',Speaker=891342,}",
  [163701] = "{id=163701,Text='我相信以你的经历，一定可以在大赛中夺冠的！',Speaker=891342,}",
  [163702] = "{id=163702,Text='真的吗？我竟然可以在这样的比赛中夺冠！',Speaker=891341,Emoji=7,}",
  [163703] = "{id=163703,Text='（但幽灵波利们看起来很有干劲的样子，应该也很幸福吧！）',}",
  [163704] = "{id=163704,Text='（拍摄它们的照片给国王波利看看吧！）',}",
  [163705] = "{id=163705,Text='（国王波利出现了！）',}",
  [163706] = "{id=163706,Text='（将这几天探访的结果告诉他吧！）',}",
  [163707] = "{id=163707,Text='啊，尊敬的冒险者！我交给你的使命完成的如何呀？',Speaker=891330,Option='告知',}",
  [163708] = "{id=163708,Text='唔……',Speaker=891330,}",
  [163709] = "{id=163709,Text='听起来还不错——看来这一年波利们都生活的幸福又充实呢！',Speaker=891330,Emoji=26,}",
  [163710] = "{id=163710,Text='真是让人欣慰，那么——',Speaker=891330,}",
  [163711] = "{id=163711,Text='冒险者，可以邀请你作为特别嘉宾参加明天的表彰大会吗！',Speaker=891330,}",
  [163712] = "{id=163712,Text='看到你出席，波利们一定会非常高兴的！',Speaker=891330,Option='欣然接受',}",
  [163713] = "{id=163713,Text='明天表彰大会将在波利岛举办，一定要来参加哦！',Speaker=891330,Emoji=17,}",
  [163714] = "{id=163714,Text='唔……已经到齐了嘛……',Speaker=891330,}",
  [163715] = "{id=163715,Text='那么——第一届劳动者波利表彰大会正式开始！',Speaker=891330,Emoji=17,}",
  [163716] = "{id=163716,Text='首先要向大家介绍我们的特邀嘉宾——冒险者[c][ffff00][PlayerName][-][/c]！',Speaker=891330,}",
  [163717] = "{id=163717,Text='在过去的一年里，大家辛苦了！',Speaker=891330,}",
  [163718] = "{id=163718,Text='在这一年中，有数以千亿计的波利伙伴被冒险者哒哒哒……',Speaker=891330,}",
  [163719] = "{id=163719,Text='为他们的成长奉献了数不胜数的经验、材料、卡片和Zeny！',Speaker=891330,}",
  [163720] = "{id=163720,Text='所以，在这次大会上，最辛勤波利的奖项，我将要颁给……',Speaker=891330,}",
  [163721] = "{id=163721,Text='那么这个奖项我会颁给——',Speaker=891330,}",
  [163722] = "{id=163722,Text='获得这个奖项的将会是……',Speaker=891330,}",
  [163723] = "{id=163723,Text='嗯嗯，现在我要宣布——',Speaker=891330,}",
  [163724] = "{id=163724,Text='先别急着颁奖哦~',Speaker=891333,}",
  [163725] = "{id=163725,Text='恶魔波利？你来这里做什么？！',Speaker=891330,}",
  [163726] = "{id=163726,Text='…………',Speaker=891346,}",
  [163727] = "{id=163727,Text='…………',Speaker=891346,}",
  [163728] = "{id=163728,Text='为[c][ffff00]波利们[-][/c]筹备的表彰大会嘛…………',Speaker=891346,}",
  [163729] = "{id=163729,Text='哼！既然是这样的盛会，我当然是来捣乱的！',Speaker=891346,}",
  [163730] = "{id=163730,Text='毕竟给大家带来麻烦和不愉快——可是我[c][ffff00]恶魔波利[-][/c]的天职啊！',Speaker=891346,}",
  [163731] = "{id=163731,Text='小弟们，拿出你们的看家本领！搅乱这场大会！',Speaker=891346,}",
  [163732] = "{id=163732,Text='好的，大哥！',Speaker=891345,}",
  [163733] = "{id=163733,Text='可恶……竟然被冒险者打乱了我的计划！',Speaker=891346,}",
  [163734] = "{id=163734,Text='你、你们别得意，我、我还会回来的！',Speaker=891346,}",
  [163735] = "{id=163735,Text='等等！',Speaker=891332,}",
  [163736] = "{id=163736,Text='你其实——不是为了捣乱才来的吧！',Speaker=891332,}",
  [163737] = "{id=163737,Text='虽然你喜欢给别人添麻烦，但那是你作为[c][ffff00]恶魔波利的职责[-][/c]，所以……',Speaker=891332,}",
  [163738] = "{id=163738,Text='作为恶魔波利一直兢兢业业的你，也想来参加今天的大会吧！',Speaker=891332,}",
  [163739] = "{id=163739,Text='我、我、我不是，我没有，别瞎说啊！',Speaker=891346,}",
  [163740] = "{id=163740,Text='真的吗？在场的波利们都是被冒险者邀请来的……',Speaker=891332,}",
  [163741] = "{id=163741,Text='如果你[c][ffff00]真的[-][/c]不想参加的话……就拒绝他的邀请咯~',Speaker=891332,}",
  [163742] = "{id=163742,Text='哼……',Speaker=891346,}",
  [163743] = "{id=163743,Text='既然你们这么诚心诚意的邀请我参加……',Speaker=891346,}",
  [163744] = "{id=163744,Text='那我就勉为其难的接受了吧，虽然我本来是没什么兴趣的……',Speaker=891346,}",
  [163745] = "{id=163745,Text='但是像我这种在波利族群中举足轻重的波利，要是不来参加的话，这场大会可要失色不少的！',Speaker=891346,}",
  [163746] = "{id=163746,Text='嗯嗯……',Speaker=891330,}",
  [163747] = "{id=163747,Text='天下波利一家亲，这次忘记邀请恶魔波利一族是我们的失误啦~',Speaker=891330,}",
  [163748] = "{id=163748,Text='只是一点小插曲，大家不要在意！',Speaker=891330,}",
  [163749] = "{id=163749,Text='那么，言归正传……诶，刚刚我讲到哪儿来着？',Speaker=891330,Option='{最辛勤的波利,0},{颁奖,0}',}",
  [163750] = "{id=163750,Text='哦，对对对，颁奖、颁奖！',Speaker=891330,}",
  [163751] = "{id=163751,Text='这个最辛勤波利的奖项，我思前想后……',Speaker=891330,}",
  [163752] = "{id=163752,Text='决定颁给————所有的波利！！',Speaker=891330,}",
  [163753] = "{id=163753,Text='嘿嘿，没想到吧！',Speaker=891330,}",
  [163754] = "{id=163754,Text='就像我刚才说的，天下波利一家亲！我们所有波利都完成了自己的工作！',Speaker=891330,}",
  [163755] = "{id=163755,Text='在不同地区帮助了冒险者们的成长，各个族群的成就没有谁大谁小之分！',Speaker=891330,}",
  [163756] = "{id=163756,Text='所以，每一个伙伴，都是最辛勤的波利！',Speaker=891330,}",
  [163757] = "{id=163757,Text='在新的一年里，希望大家也可以继续努力哦！',Speaker=891330,}",
  [163758] = "{id=163758,Text='毕竟这个世界，可不能缺少我们波利呢！',Speaker=891330,}",
  [163760] = "{id=163760,Text='劳动使波利快乐~[c][ffff00][PlayerName][-][/c]，你也是一个喜欢劳动的人吧~',Speaker=892893,}",
  [163761] = "{id=163761,Text='打塔啊、神谕啊，这些可都是了不起的劳动呢~',Speaker=892893,}",
  [163762] = "{id=163762,Text='[c][ffff00][PlayerName][-][/c],这份礼物就送给热爱劳动的你啦~啵！',Speaker=892893,Option='{谢谢,2},{我不喜欢劳动,3}',}",
  [163764] = "{id=163764,Text='在这个特殊的日子里，我们要把最美好的祝福，送给世界上最伟大的人~',Speaker=892894,}",
  [163765] = "{id=163765,Text='你还记得上一次陪她大笑是在什么时候吗？',Speaker=892894,}",
  [163766] = "{id=163766,Text='[c][ffff00][PlayerName][-][/c],要记得多陪陪妈妈哦~',Speaker=892894,Option='{谢谢,2},{才不需要你提醒,3}',}",
  [163768] = "{id=163768,Text='你好呀，冒险者，你还记得第一个复活你的牧师叫什么名字吗？',Speaker=892895,}",
  [163769] = "{id=163769,Text='在冒险的路上，你只管在前冲锋，剩下的，就交给我们吧！',Speaker=892895,}",
  [163770] = "{id=163770,Text='[c][ffff00][PlayerName][-][/c],这份礼物也请你收下哦~',Speaker=892895,Option='{谢谢,2},{礼物就不必啦,3}',}",
  [163772] = "{id=163772,Text='我们波利一族可是米德加尔特大陆最庞大的家族！',Speaker=892896,}",
  [163773] = "{id=163773,Text='你对我们波利一族有足够的了解吗？',Speaker=892896,Option='{那当然,3},{波利是什么,4}',}",
  [163774] = "{id=163774,Text='看来我连特意准备的奖励都省下了波~',Speaker=892896,}",
  [163775] = "{id=163775,Text='波利是植物种族的吗？',Speaker=892896,Option='{是,9},{不是,4}',}",
  [163776] = "{id=163776,Text='这都能答对，真棒！请收下我的奖励波~',Speaker=892896,}",
  [163777] = "{id=163777,Text='恶魔波利是中体型的魔物吗？',Speaker=892896,Option='{是,10},{不是,4}',}",
  [163778] = "{id=163778,Text='圣天使波利的等级是130级吗？',Speaker=892896,Option='{是,11},{不是,4}',}",
  [163779] = "{id=163779,Text='金属波利是为了不想天天被打而主动接受变异的吗？',Speaker=892896,Option='{是,12},{不是,4}',}",
  [163781] = "{id=163781,Text='想不到我在这你都能找到，那就赶紧看看有没有什么你想要的东西吧~',Speaker=892897,}",
  [163812] = "{id=163812,Text='你，就是你。过来。',Speaker=892898,Option='{我不？,0},{（转头就走）,0}',}",
  [163813] = "{id=163813,Text='别走。我觉得你是个品味很好的人。',Speaker=892898,}",
  [163814] = "{id=163814,Text='我很欣赏你的着装。接下来我要去看一场至关重要的烟花表演。',Speaker=892898,}",
  [163815] = "{id=163815,Text='——请你担任我的私人时尚顾问。',Speaker=892898,Option='{有报酬吗？,0},{好吧……,0}',}",
  [163816] = "{id=163816,Text='报酬亏待不了你。',Speaker=892898,}",
  [163817] = "{id=163817,Text='现在帮我挑一身去看烟花的衣服吧。',Speaker=892898,}",
  [163818] = "{id=163818,Text='你觉得，我应该穿——',Speaker=892898,Option='{西装,2},{泳装,3}',}",
  [163819] = "{id=163819,Text='不错。那劳烦你再帮我挑一挑头饰吧。',Speaker=892898,Option='{羽毛帽,4},{蝴蝶结,5}',}",
  [163820] = "{id=163820,Text='何等优越的审美！我要好好感谢你！',Speaker=892899,}",
  [163821] = "{id=163821,Text='……我觉得，你可能是在糊弄我。',Speaker=892900,}",
  [163822] = "{id=163822,Text='还请你认真对待这份工作。下不为例。',Speaker=892900,}",
  [163823] = "{id=163823,Text='不错。那劳烦你再帮我挑一挑头饰吧。',Speaker=892898,Option='{遮阳帽,6},{兔耳朵,7}',}",
  [163824] = "{id=163824,Text='何等优越的审美！我要好好感谢你！',Speaker=892901,}",
  [163825] = "{id=163825,Text='……我觉得，你可能是在糊弄我。',Speaker=892902,}",
  [163826] = "{id=163826,Text='还请你认真对待这份工作。下不为例。',Speaker=892902,}",
  [163827] = "{id=163827,Text='嚯！逮住了逮住了！是落单的倒霉蛋！',Speaker=892903,}",
  [163828] = "{id=163828,Text='你现在立刻、马上去拍一张附近烟花秀的照片！',Speaker=892903,}",
  [163829] = "{id=163829,Text='只有绚烂和缤纷的烟花，才能表现出本艺术家的烂漫心境！',Speaker=892903,Option='什么东西？',}",
  [163830] = "{id=163830,Text='……你敢吐槽我？你要无视大艺术家邦德阁下的要求？',Speaker=892903,Option='你在教我做事？ ',}",
  [163831] = "{id=163831,Text='哼，别以为我不知道你那点小心思！等你把照片给我，报酬大大的有！',Speaker=892903,}",
  [163832] = "{id=163832,Text='快、去、拍、烟、花、的、照、片！！',Speaker=892903,}",
  [163833] = "{id=163833,Text='让我看看嚯——不错！',Speaker=892903,}",
  [163834] = "{id=163834,Text='看在你完美配合了本大艺术家的审美……那就赏你点小东西！',Speaker=892903,}",
  [163835] = "{id=163835,Text='要、心、存、感、激、哦！！！',Speaker=892903,Emoji=5,}",
  [163836] = "{id=163836,Text='（手推车上挂了个牌子。上面写着：“烟花滞销，救救孩子”。）',Speaker=892904,Option='这是什么……',Emoji=14,}",
  [163837] = "{id=163837,Text='噢哟噗里！是冒险者噗里！你找到了我的烟花手推车——',Speaker=892905,}",
  [163838] = "{id=163838,Text='帮我个忙噗里！看我真诚的眼神噗里！',Speaker=892905,Option='{什么事,0},{我还要看烟花,0}',}",
  [163839] = "{id=163839,Text='烟花……我也想看烟花！可是我的烟花滞销了！不卖掉它们就不能看烟花！',Speaker=892905,Option='绕口令？',Emoji=42,}",
  [163840] = "{id=163840,Text='噗里想看烟花！想看烟花噗里！',Speaker=892905,}",
  [163841] = "{id=163841,Text='想去看烟花，就必须先[c][ffff00]把烟花卖掉[-][/c]噗里！',Speaker=892905,}",
  [163842] = "{id=163842,Text='好心的冒险者，救救噗里吧！！',Speaker=892905,}",
  [163843] = "{id=163843,Text='烟花滞销！救救孩子！',}",
  [163844] = "{id=163844,Text='（一个小女孩好奇地走了过来……）',}",
  [163845] = "{id=163845,Text='大声喊一喊，很快就会有客人来的！我先走啦，剩下的就拜托你了！',Speaker=892905,}",
  [163846] = "{id=163846,Text='有没有那种，可爱的、小动物形状的烟花呀！',Speaker=892906,Option='{疯兔烟花！,14},{绿棉虫烟花！,12},{星星烟花！,13}',}",
  [163847] = "{id=163847,Text='绿、绿棉虫……呜呜呜呜……呜哇——！！！！',Speaker=892906,Emoji=42,}",
  [163848] = "{id=163848,Text='（小女孩被吓跑了……）',}",
  [163850] = "{id=163850,Text='星星……不是小动物呀？冒险者，该不会是笨蛋吧！',Speaker=892906,}",
  [163851] = "{id=163851,Text='（小女孩不开心地走掉了……）',}",
  [163853] = "{id=163853,Text='谢谢！是疯兔宝宝——这就是我要找的烟花！',Speaker=892906,}",
  [163854] = "{id=163854,Text='（一个小男孩好奇地走了过来……）',}",
  [163855] = "{id=163855,Text='哦哦哦！！有客人了！加油噗里！交给你了噗里！',Speaker=892905,}",
  [163856] = "{id=163856,Text='我想要那种！！帅气的！！强大的！！酷炫的烟花！！',Speaker=892907,Option='{罗达蛙烟花 ！,22},{米米怪烟花！,23},{虎王烟花！,24}',}",
  [163857] = "{id=163857,Text='……罗达蛙？？？你是不是在糊弄我！',Speaker=892907,Emoji=42,}",
  [163858] = "{id=163858,Text='（小男孩被气跑了……）',}",
  [163860] = "{id=163860,Text='米米怪！！哪里！！强大帅气了！！',Speaker=892907,}",
  [163861] = "{id=163861,Text='（小男孩不开心地走掉了……）',}",
  [163863] = "{id=163863,Text='虎王——嗷呜！！谢谢！这就是我要找的烟花！',Speaker=892907,Emoji=17,}",
  [163864] = "{id=163864,Text='小偷！小偷！小偷啊————',Speaker=892908,Option='不是我！',Emoji=12,}",
  [163865] = "{id=163865,Text='慌什么，又没说是你……等等，你不是那个，冒……冒险者吗？',Speaker=892908,}",
  [163866] = "{id=163866,Text='太好了冒险者，快点去帮我把小偷抓回来！',Speaker=892908,Option='什么小偷',}",
  [163867] = "{id=163867,Text='防晒油小偷！没有防晒油，我是绝对不会去参加烟花秀的！',Speaker=892908,}",
  [163868] = "{id=163868,Text='我就说一遍！那个小偷长得[c][ffff00]绿油油[-][/c]、头上戴着[c][ffff00]红帽子[-][/c]！一定要把他抓住啊！',Speaker=892908,}",
  [163869] = "{id=163869,Text='（出现了三个疑似是防晒油小偷的家伙……）',}",
  [163870] = "{id=163870,Text='（之前爱酱好像说过小偷的特征……要抓哪一个？）',Option='{嫌疑人1号,3},{嫌疑人2号,4},{嫌疑人3号,5}',}",
  [163871] = "{id=163871,Text='防晒油小偷，别想跑！',}",
  [163872] = "{id=163872,Text='可恶……被发现了！',Speaker=892909,}",
  [163873] = "{id=163873,Text='好吧，被你抓到了。',Speaker=892929,Option='不要偷东西',}",
  [163874] = "{id=163874,Text='是借！不是偷！我用完会还回去的！',Speaker=892929,Emoji=15,}",
  [163875] = "{id=163875,Text='人家也想美美的去看烟花秀嘛……爱酱那个小气鬼！',Speaker=892929,}",
  [163876] = "{id=163876,Text='防晒油小偷，别想跑！',}",
  [163877] = "{id=163877,Text='你在说什么……我听不懂……',Speaker=892910,}",
  [163878] = "{id=163878,Text='呜呜呜……你做什么呀……',Speaker=892910,}",
  [163879] = "{id=163879,Text='我才不是小偷！而且，谁会偷防晒油啊——',Speaker=892910,Emoji=42,}",
  [163880] = "{id=163880,Text='（哎呀，这次好像搞错了……）',}",
  [163881] = "{id=163881,Text='防晒油小偷，别想跑！',}",
  [163882] = "{id=163882,Text='你在说什么……我听不懂……',Speaker=892911,}",
  [163883] = "{id=163883,Text='呜呜呜……你做什么呀……',Speaker=892911,}",
  [163884] = "{id=163884,Text='我才不是小偷！而且，谁会偷防晒油啊——',Speaker=892911,Emoji=42,}",
  [163885] = "{id=163885,Text='（哎呀，这次好像搞错了……）',}",
  [163886] = "{id=163886,Text='嘿哟！这不是冒险者吗？你也来看烟花秀？',Speaker=892912,Option='是啊',}",
  [163887] = "{id=163887,Text='我是这里的警备队长。能不能拜托你件事？',Speaker=892912,}",
  [163888] = "{id=163888,Text='之前准备的烟花里，好像混进了残次品。',Speaker=892912,}",
  [163889] = "{id=163889,Text='求求你帮忙把残次品找出来吧！要是我的胆子和头一样大就好了……',Speaker=892912,Option='交给我',}",
  [163890] = "{id=163890,Text='（要开哪个烟花盒？）',Option='{蓝色烟花盒,12},{红色烟花盒,13}',}",
  [163891] = "{id=163891,Text='（找到了！蓝色盒子里就是残次品烟花吧……）',}",
  [163892] = "{id=163892,Text='嘿哟！谢谢啊冒险者，这就是出问题的烟花。我们会仔细检查的！',Speaker=892912,}",
  [163893] = "{id=163893,Text='（看来红色盒子里的烟花是合格的！）',}",
  [163894] = "{id=163894,Text='嘿哟！谢了，看来旁边的盒子里的就是有问题的烟花了！',Speaker=892912,}",
  [163895] = "{id=163895,Text='（要开哪个烟花盒？）',Option='{蓝色烟花盒,22},{红色烟花盒,23}',}",
  [163896] = "{id=163896,Text='（看来蓝色盒子里的烟花是合格的！）',}",
  [163897] = "{id=163897,Text='嘿哟！谢了，看来旁边的盒子里的就是有问题的烟花了！',Speaker=892912,}",
  [163898] = "{id=163898,Text='（找到了！红色盒子里就是残次品烟花吧……）',}",
  [163899] = "{id=163899,Text='嘿哟！谢谢啊冒险者，这就是出问题的烟花。我们会仔细检查的！',Speaker=892912,}",
  [163900] = "{id=163900,Text='呼呼呼嘿嘿嘿——想在夏天的热浪中体验何为真正的挑战吗？',Speaker=892915,}",
  [163901] = "{id=163901,Text='呼呼呼嘿嘿嘿——想在克魔岛的拥抱中与太阳亲密接触吗？',Speaker=892915,}",
  [163902] = "{id=163902,Text='呼呼呼嘿嘿嘿——来参加汗水与激情的碰撞——[c][ffff00]环岛海岸拉力赛[-][/c]吧！',Speaker=892915,Emoji=2,}",
  [163903] = "{id=163903,Text='这位路过的冒险者，我看你很有拼搏冠军的潜质。要不要参加？现在报名免费！',Speaker=892915,Option='{来！,3},{没意思,2}',}",
  [163904] = "{id=163904,Text='呼呼呼嘿嘿嘿——尽情享受吧！',Speaker=892915,}",
  [163905] = "{id=163905,Text='呼呼呼嘿嘿嘿——不过你需要穿上克魔岛的特殊服饰！就让我为你好·好·打扮一番，送你去赛场！',Speaker=892915,}",
  [163914] = "{id=163914,Text='呼呼呼嘿嘿嘿——这不是优秀的[c][ffff00][PlayerName][-][/c]吗？',Speaker=892915,}",
  [163915] = "{id=163915,Text='呼呼呼嘿嘿嘿——准备前往拉力赛的会场了吗？今天是残酷的复赛！',Speaker=892915,Option='{准备好了,3},{再等等,2}',}",
  [163923] = "{id=163923,Text='（没能拿到复赛的优胜……看来只能到此为止了。）',Emoji=24,}",
  [163929] = "{id=163929,Text='呼呼呼嘿嘿嘿——[c][ffff00][PlayerName][-][/c]！很高兴又见到你！',Speaker=892915,}",
  [163930] = "{id=163930,Text='呼呼呼嘿嘿嘿——准备前往拉力赛的会场了吗？今天是最后的决赛！',Speaker=892915,Option='{准备好了,3},{再等等,2}',}",
  [163938] = "{id=163938,Text='（没能拿到决赛的优胜……看来只能到此为止了。）',Emoji=24,}",
  [163943] = "{id=163943,Text='母亲节期间，各位冒险者身上如果有多余的材料，',Speaker=892894,}",
  [163944] = "{id=163944,Text='可以来找我兑换各类限定礼物哦~',Speaker=892894,}",
  [163972] = "{id=163972,Text='（没能拿到初赛的优胜……看来只能到此为止了。）',Emoji=24,}",
  [164004] = "{id=164004,Text='（看起来很普通的扭蛋机。要扭一下看看吗？）',Speaker=891361,}",
  [164005] = "{id=164005,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891361,Option='{扭一扭,2},{算了,3}',}",
  [164006] = "{id=164006,Text='哔啵——哔啵哔——',Speaker=891361,}",
  [164007] = "{id=164007,Text='乌拉！！他抽到了金色传说！！！',Speaker=891362,}",
  [164008] = "{id=164008,Text='乌拉！金、金色传说——那他就是被选中的[c][ffff00]勇者[-][/c]——',Speaker=891363,Option='{什么勇者？,0},{先等一下？,0}',}",
  [164009] = "{id=164009,Text='乌拉！这台幸运E号扭蛋机抽到金色传说的概率是0.00000001%！这个人，绝对是勇者没错！',Speaker=891364,}",
  [164010] = "{id=164010,Text='乌拉——————————[c][ffff00]救救克魔岛[-][/c]吧！！！！！！勇者大人！！！！！！！',Speaker=891365,}",
  [164011] = "{id=164011,Text='乌拉！您听我们说！克魔岛上！出现了一个[c][ffff00]怪物[-][/c]！',Speaker=891365,}",
  [164012] = "{id=164012,Text='乌拉！它的嘴！有100个海贝壳那么大！它的脑袋！比岛上最茁壮的椰子树还要绿！',Speaker=891365,Option='[c][ffff00]绿油油的大魔物…[-][/c]',}",
  [164013] = "{id=164013,Text='乌拉！没错！[c][ffff00]讨伐怪物[-][/c]的重任！就交给你了！带上金色传说的宝物！冲吧！',Speaker=891365,}",
  [164014] = "{id=164014,Text='（好像被赋予了奇怪的使命……）',Emoji=18,}",
  [164015] = "{id=164015,Text='从扭蛋里掉出了一把剑。',Option='捡起',Action={actionid=38,num=1},}",
  [164016] = "{id=164016,Text='获得 【 [c][ffff00]全岛最好的剑 x 1 [-][/c]】',Emoji=11,}",
  [164017] = "{id=164017,Text='（出现了！绿油油的大魔物——）',}",
  [164018] = "{id=164018,Text='呱……呱呱咕呱呱咕呱呱呱呱呱……咕咕咕咕咕呜呜呜呜呜……',Speaker=891367,}",
  [164020] = "{id=164020,Text='（仔细看，这家伙不就是一只穿西装的青蛙吗……）',}",
  [164021] = "{id=164021,Text='呱呜呜……呱呜呜……为什么、要打我！',Speaker=891369,}",
  [164022] = "{id=164022,Text='呱呜呜……呜呜呱……不要再、打了呱！',Speaker=891369,Option='{你竟然？,0},{会说话？,0}',}",
  [164023] = "{id=164023,Text='呱！天啦呱——我能够、说话了！！！',Speaker=891369,Emoji=7,}",
  [164024] = "{id=164024,Text='呱！之前都、不能的！我只能、呱呱叫！',Speaker=891369,Option='……你是谁？',}",
  [164025] = "{id=164025,Text='呱！我来自、卢恩国！是一个、好骑士！',Speaker=891369,}",
  [164026] = "{id=164026,Text='请叫我、克、克、克……',Speaker=891369,Option='克克克？',Emoji=18,}",
  [164027] = "{id=164027,Text='不是的！！我是叫、克、克——',Speaker=891369,}",
  [164028] = "{id=164028,Text='喵呀——这位话都说不利索的蠢货，叫[c][ffff00]克鲁克斯[-][/c]。',Speaker=891370,}",
  [164029] = "{id=164029,Text='喵呀~他叫克鲁克斯，从普隆德拉来。是个[c][ffff00]人类骑士[-][/c]。',Speaker=891370,}",
  [164030] = "{id=164030,Text='呱！你怎么，会知道！',Speaker=891371,}",
  [164031] = "{id=164031,Text='喵呀！我还知道，这人是在寻宝路上，突然就变成青蛙了。还把行李和坐骑也丢了，喵~',Speaker=891370,Option='寻宝？',}",
  [164032] = "{id=164032,Text='（舔了舔爪子）喵呀~是的，克鲁克斯在找来自[c][ffff00]异世界的宝贝[-][/c]。只可惜，找错地方了。',Speaker=891370,Option='异世界的宝贝？？',Emoji=9,}",
  [164033] = "{id=164033,Text='喵呀~去[c][ffff00]姜饼城[-][/c]吧！克鲁克斯，和旁边的冒牌勇者！',Speaker=891370,}",
  [164034] = "{id=164034,Text='姜饼城是童心和浪漫的地方。有宝物喜欢那里。运气好的话，还能解决你这副呱里呱气的模样。',Speaker=891370,}",
  [164035] = "{id=164035,Text='喵呀——当然啦、前提是、你们能、找得到！',Speaker=891370,Option='奇怪的黑猫…',Emoji=5,}",
  [164036] = "{id=164036,Text='呱！！不要学、我说话！',Speaker=891371,}",
  [164037] = "{id=164037,Text='呱……黑猫它、没说错。我确实、在寻宝。',Speaker=891371,Option='什么宝物？',}",
  [164038] = "{id=164038,Text='呱。异世界的宝物……很珍贵。要送给、重要的人。',Speaker=891371,}",
  [164039] = "{id=164039,Text='但现在！！我变成、青蛙……没办法、自己去！',Speaker=891371,Emoji=42,}",
  [164040] = "{id=164040,Text='——拜托你、帮帮我吧！求求了！呱！',Speaker=891371,Option='答应',}",
  [164041] = "{id=164041,Text='呱……谢、谢谢你！那我们、明天……[c][ffff00]姜饼城再会[-][/c]！',Speaker=891371,}",
  [164042] = "{id=164042,Text='呱呱！我一直、在等你！',Speaker=891371,Option='去寻宝吗',Emoji=17,}",
  [164043] = "{id=164043,Text='呱！我……我已经感觉到！宝贝它、就在附近！',Speaker=891371,}",
  [164044] = "{id=164044,Text='跟上我、呱！',Speaker=891371,}",
  [164045] = "{id=164045,Text='（认真地）宝贝它、就在这！',Speaker=891371,}",
  [164046] = "{id=164046,Text='玩、玩具熊身上！有特殊……的魔力！',Speaker=891371,Option='异世界的魔力？',}",
  [164047] = "{id=164047,Text='呱！嗯嗯呱！',Speaker=891371,Emoji=26,}",
  [164048] = "{id=164048,Text='（的确，玩具熊身上散发出了不属于这个世界的气息！）',}",
  [164049] = "{id=164049,Text='呱——它拥有强烈的愿望。也、也许，帮它实现愿望！就能找到、异世界的宝贝……',Speaker=891371,}",
  [164050] = "{id=164050,Text='……跳舞……我想跳舞……',Speaker=891372,}",
  [164051] = "{id=164051,Text='（看起来快晕过去了）…………',Speaker=891372,}",
  [164052] = "{id=164052,Text='咕呱！！快来看、它的脚！',Speaker=891371,Emoji=18,}",
  [164053] = "{id=164053,Text='（这只玩具熊，左脚的布料里空空荡荡。）',Emoji=7,}",
  [164054] = "{id=164054,Text='我一定要跳舞……为了洁茜……',Speaker=891372,}",
  [164055] = "{id=164055,Text='……它好像、缺少了、一只脚，呱呱。',Speaker=891371,Option='这可没法跳舞',}",
  [164056] = "{id=164056,Text='（玩具工厂附近，也许会有散落的零件可以修复小熊的脚。）',Emoji=11,}",
  [164057] = "{id=164057,Text='[c][ffff00][PlayerName][-][/c]！没想到在这遇到你！',Speaker=891373,Option='忽克连！！？',Emoji=17,}",
  [164058] = "{id=164058,Text='别惊讶，我是来休假的。每天都在精炼，手都锤麻了……倒是你在这做什么？',Speaker=891373,Option='讲述来意',}",
  [164059] = "{id=164059,Text='哦——要去修理玩具熊！你应该会需要这个。',Speaker=891373,}",
  [164060] = "{id=164060,Text='锤子上写着：“安全精炼，开心精炼。锤出[c][ffff00]绿色[-][/c]，给冒险者稳稳的幸福。”',Emoji=10,}",
  [164061] = "{id=164061,Text='获得 【 [c][ffff00]忽克连的快乐锤 x 1 [-][/c]】',Emoji=11,}",
  [164062] = "{id=164062,Text='那么，我继续度假了！祝你好运！',Speaker=891373,}",
  [164063] = "{id=164063,Text='呱呱！看起来、你已经，有……有办法、修理它了！',Speaker=891371,Option='快乐锤',}",
  [164064] = "{id=164064,Text='忽克连……我知道！你用它，锤出，[c][ffff00]绿颜色[-][/c]！然后就能，修理好！',Speaker=891371,Emoji=9,}",
  [164066] = "{id=164066,Text='是你们修好了我的脚吗？谢谢……我终于可以跳舞给洁茜看了。',Speaker=891372,Option='洁茜是谁',}",
  [164067] = "{id=164067,Text='姜饼城的孩子里，只有洁茜愿意带我回家。可能因为她和我一样，腿脚都不好……',Speaker=891372,Emoji=24,}",
  [164068] = "{id=164068,Text='但、但是我知道，洁茜，她想要成为舞蹈家！',Speaker=891372,}",
  [164069] = "{id=164069,Text='（小声）所以……我想跳一支舞送给她……鼓励她实现自己的梦想。你们能先看我跳一遍吗？',Speaker=891372,}",
  [164070] = "{id=164070,Text='希望你鼓起勇气……洁茜……',Speaker=891372,}",
  [164071] = "{id=164071,Text='跳得真好，小波比！',Speaker=891374,}",
  [164072] = "{id=164072,Text='洁茜……',Speaker=891372,}",
  [164073] = "{id=164073,Text='我都看到啦，小波比。谢谢你为了我这么努力。',Speaker=891374,}",
  [164074] = "{id=164074,Text='还有小青蛙和勇者大人。你们治好了波比的脚，我都不知道该怎么感谢才好……',Speaker=891374,Option='去实现梦想',}",
  [164075] = "{id=164075,Text='我……我这样的人……真的也可以跳起舞……成为舞者吗……',Speaker=891374,Option='试一试',Emoji=2,}",
  [164076] = "{id=164076,Text='嗯！',Speaker=891374,}",
  [164077] = "{id=164077,Text='洁茜！你跳起来啦！',Speaker=891372,}",
  [164078] = "{id=164078,Text='咕咕呱！加油呱！你会成为、很棒的舞者！',Speaker=891371,Option='没错！',Emoji=17,}",
  [164079] = "{id=164079,Text='谢谢……希望到那时，可以在舞台下见到你们……',Speaker=891374,}",
  [164080] = "{id=164080,Text='有了波比的鼓励，我现在什么也不害怕了。',Speaker=891374,}",
  [164081] = "{id=164081,Text='（洁茜抱着她的玩具熊离开了。下一刻，雪地上似乎出现了一件闪光的东西……）',}",
  [164082] = "{id=164082,Text='呱、呱啊啊啊啊啊啊！！！！出现了呱！就是它呱！',Speaker=891371,}",
  [164083] = "{id=164083,Text='我懂了呱。实现心愿、就能得到玩偶！',Speaker=891371,}",
  [164084] = "{id=164084,Text='异世界——异世界的宝物——[c][ffff00]神奇的玩偶[-][/c]——呱呱呱咕呱——！！！！',Speaker=891371,}",
  [164085] = "{id=164085,Text='那只黑猫、竟然没有骗我们！还有、四只玩偶！我们明天、再继续呱！',Speaker=891371,Option='还有四只！',}",
  [164086] = "{id=164086,Text='没错呱！明天、姜饼城——不见不散呱！',Speaker=891371,}",
  [164087] = "{id=164087,Text='呱！我已经……发现了、今天的目标！',Speaker=891371,Option='在哪里',}",
  [164088] = "{id=164088,Text='呱呱——看、看那里！正在玩球的小丑！',Speaker=891371,}",
  [164089] = "{id=164089,Text='（笑眯眯地）谢谢~谢谢孩子们喜欢我的演出~',Speaker=891378,}",
  [164090] = "{id=164090,Text='（笑眯眯地）今天~就到这里啦~欢迎下次再来~',Speaker=891378,Emoji=17,}",
  [164091] = "{id=164091,Text='下次见，卡拉特先生！',Speaker=891398,}",
  [164092] = "{id=164092,Text='你怎么了~爱利欧~',Speaker=891378,}",
  [164093] = "{id=164093,Text='卡拉特先生……我、我要离开姜饼城了。之后，就看不到你的表演了……',Speaker=891379,}",
  [164094] = "{id=164094,Text='我、我呜呜呜呜呜——我会想念你的——',Speaker=891379,Emoji=24,}",
  [164095] = "{id=164095,Text='（笑眯眯地）这样啊~祝你一路顺风~再见~',Speaker=891378,}",
  [164096] = "{id=164096,Text='——呜呜呜呜哇啊啊啊啊啊我再也不喜欢卡拉特先生了！！！',Speaker=891379,Emoji=42,}",
  [164097] = "{id=164097,Text='（笑眯眯地）哎呀~好像又被讨厌了。旁边偷看的冒险者~你觉得这是为什么~？',Speaker=891378,}",
  [164098] = "{id=164098,Text='（笑眯眯地）偷看是不礼貌的~需要警告你一下。尝试接住我的球吧~',Speaker=891378,}",
  [164099] = "{id=164099,Text='（笑眯眯地）哎呀~看来你的技巧不错。我知道你在疑惑什么，来聊聊吧~',Speaker=891378,}",
  [164100] = "{id=164100,Text='（笑眯眯地）很奇怪~我好像，[c][ffff00]感觉不到悲伤的情绪[-][/c]~不管别人做什么，我都会一直很开心~',Speaker=891378,}",
  [164101] = "{id=164101,Text='（笑眯眯地）我这样……是不是生病了~？',Speaker=891378,Option='{是有点……,0},{有大问题,0}',Emoji=17,}",
  [164102] = "{id=164102,Text='（笑眯眯地）所以~我想明白~悲伤的感觉是什么。你可以帮帮我吗~',Speaker=891378,}",
  [164103] = "{id=164103,Text='（笑眯眯地）请帮我找一件~能表现悲伤的东西吧。越悲伤越好~',Speaker=891378,}",
  [164104] = "{id=164104,Text='唔……唔呱。虽然、他说话，非常讨打……但是他、这样的情况，确实是生病了。呱呱。',Speaker=891371,}",
  [164105] = "{id=164105,Text='家主大人、曾经说过，这叫[c][ffff00]情感缺失[-][/c]，呱呱。是心理疾病、也不能怪他……',Speaker=891371,Emoji=15,}",
  [164106] = "{id=164106,Text='快乐小丑的愿望、是感受到悲伤吗……我知道了呱呱！这就带你去呱！',Speaker=891371,}",
  [164107] = "{id=164107,Text='为什么……为什么……',Speaker=891382,}",
  [164108] = "{id=164108,Text='啊啊……冒险者……你看起来……气色真好。这真令我难过……',Speaker=891382,Option='……啊？',Emoji=24,}",
  [164109] = "{id=164109,Text='呱？',Speaker=891371,}",
  [164110] = "{id=164110,Text='啊啊……还有小青蛙……你的呱声很嘹亮。这真令我难过……',Speaker=891382,}",
  [164111] = "{id=164111,Text='啊啊……我永远……不能和你们一样快乐……这真令我难过……',Speaker=891382,}",
  [164112] = "{id=164112,Text='啊啊……还有这里的雪花……多么美丽……可是它总会融化……这真令我难过……',Speaker=891382,Emoji=24,}",
  [164113] = "{id=164113,Text='（小声）看到吧呱，他和那个小丑、是反过来的。他、他[c][ffff00]感觉不到、快乐[-][/c]。',Speaker=891371,}",
  [164114] = "{id=164114,Text='啊啊……对不起。我知道，这样是生病了。但是……没有人能治好我……这真令我难过……',Speaker=891382,}",
  [164115] = "{id=164115,Text='我天生……就只会哭泣。我的心……缺了一半。',Speaker=891382,}",
  [164116] = "{id=164116,Text='（锡兵的眼泪落到了雪地里，变成了半颗锡心。）',Emoji=14,}",
  [164117] = "{id=164117,Text='获得 【 [c][ffff00]半颗哭泣的锡心 x 1 [-][/c]】 ',}",
  [164118] = "{id=164118,Text='（小声）呱呱。我、我们，把这个锡心，带回去给小丑吧。',Speaker=891371,}",
  [164119] = "{id=164119,Text='这……这半颗锡心，你们是在哪里找到的！',Speaker=891378,Option='悲伤的锡兵',}",
  [164120] = "{id=164120,Text='……说起来，很奇怪。看着它，我感觉原本空洞的心，渐渐被填满了。',Speaker=891378,}",
  [164121] = "{id=164121,Text='制作我的工匠说过，我是由一半废弃的锡石做出来的。',Speaker=891378,}",
  [164122] = "{id=164122,Text='而这颗锡心的主人……应该和我，属于同一块锡石的正反两面。',Speaker=891378,Option='所以……',}",
  [164123] = "{id=164123,Text='——所以，他和我加在一起，才拥有一个[c][ffff00]完整的灵魂[-][/c]。',Speaker=891378,}",
  [164124] = "{id=164124,Text='缺少一半的灵魂，感受不到快乐的生活，一定很辛苦。我……我想去看看他。',Speaker=891378,}",
  [164125] = "{id=164125,Text='谢谢你们找到了我丢失的心。',Speaker=891378,Emoji=24,}",
  [164126] = "{id=164126,Text='（快乐小丑在离开时，流下了眼泪……）',}",
  [164127] = "{id=164127,Text='获得【 [c][ffff00]一颗温暖的锡心 x 1[-][/c] 】',}",
  [164128] = "{id=164128,Text='……呱呱，这次、出现了一对玩偶呱。',Speaker=891371,}",
  [164129] = "{id=164129,Text='呱。希望小丑和锡兵、见面后，可以互相温暖，找回缺失的情感……',Speaker=891371,Option='一定会的',Emoji=24,}",
  [164130] = "{id=164130,Text='没错，呱呱。',Speaker=891371,}",
  [164131] = "{id=164131,Text='还剩下、一只玩偶了呱！辛苦了呱！',Speaker=891371,}",
  [164132] = "{id=164132,Text='我们明天，再继续呱！',Speaker=891371,}",
  [164133] = "{id=164133,Text='（小声）快看！快看呱。那对甜饼人，和绿饼人！',Speaker=891371,Option='怎么了？',}",
  [164134] = "{id=164134,Text='（小声）他们、就是今天的目标呱！但是现在，好像在吵架……',Speaker=891371,Emoji=15,}",
  [164135] = "{id=164135,Text='（严肃）在你开口之前，先回答我一个问题。',Speaker=891386,}",
  [164136] = "{id=164136,Text='——[c][ffff00]你是甜党还是咸党[-][/c]？',Speaker=891386,Option='{甜党,2},{咸党,3}',}",
  [164137] = "{id=164137,Text='太好了！你果然也觉得甜味最棒吧！',Speaker=891386,}",
  [164138] = "{id=164138,Text='糖果、冰淇淋、甜甜圈……甜食就是天堂！',Speaker=891386,}",
  [164139] = "{id=164139,Text='（瞥了眼扭扭）只有固执的绿饼人，才会选择咸味！',Speaker=891386,}",
  [164140] = "{id=164140,Text='（瞪着你）异端！异端！你这异端份子！',Speaker=891387,}",
  [164141] = "{id=164141,Text='咸味才是永远的光！我舍不得伤害丢丢，但你这异端，我今天一定让你得到教训！',Speaker=891387,Option='来啊！',}",
  [164142] = "{id=164142,Text='哼……讨厌的甜味异端！！',Speaker=891387,Emoji=12,}",
  [164143] = "{id=164143,Text='你说什么————你竟然和绿饼人一样，喜欢吃咸的？',Speaker=891386,}",
  [164144] = "{id=164144,Text='冒险者说得对！当然还是咸味最棒啦~汉堡薯条和披萨饼，咸味才是美食的正道！！',Speaker=891387,Option='咸味万岁！',}",
  [164145] = "{id=164145,Text='呜呜……扭扭，你拉着外人一起欺负我！！最讨厌你啦！！',Speaker=891386,Emoji=24,}",
  [164146] = "{id=164146,Text='丢丢，你别哭啊……',Speaker=891387,}",
  [164147] = "{id=164147,Text='（瞪着你）可恶的咸味怪物，让你尝尝我的厉害！',Speaker=891386,}",
  [164148] = "{id=164148,Text='呜哇哇啊啊啊啊扭扭和咸味怪物最讨厌啦！！',Speaker=891386,Emoji=42,}",
  [164149] = "{id=164149,Text='（严肃）咕咕呱。看、看到了吗。[c][ffff00]甜咸大战[-][/c]。这、这足以，让米德加尔特毁灭。',Speaker=891371,Option='到底怎么回事',}",
  [164150] = "{id=164150,Text='甜饼人，只吃甜食。绿饼人，只吃咸味。而甜饼人丢丢，和绿饼人扭扭，是一对[c][ffff00]情侣[-][/c]。呱呱。',Speaker=891371,}",
  [164151] = "{id=164151,Text='呱，据我调查。这……这是他们两个，恋爱，第一百天。现在面临着，分手危机。',Speaker=891371,Option='所以……',}",
  [164152] = "{id=164152,Text='（严肃）所以今天，我们的目标——是让他们，恩恩爱爱。呱呱。',Speaker=891371,}",
  [164153] = "{id=164153,Text='咕呱。我有一个，冒险的计划。',Speaker=891371,}",
  [164154] = "{id=164154,Text='我、我们做出，[c][ffff00]甜咸共存的食物[-][/c]，给他俩尝一尝——',Speaker=891371,Option='具体一点？',}",
  [164155] = "{id=164155,Text='海盐冰淇淋，和黄瓜薯片。呱呱。',Speaker=891371,}",
  [164156] = "{id=164156,Text='黄瓜薯片，我已经做好了呱。至于海盐冰淇淋，就拜托你了！',Speaker=891371,}",
  [164157] = "{id=164157,Text='获得 【[c][ffff00] 黄瓜薯片 x1 [-][/c]】',}",
  [164158] = "{id=164158,Text='制作海盐冰淇淋，第一步要放——',Speaker=891388,Option='{淀粉和牛奶,4},{辣椒和芥末,5}',}",
  [164159] = "{id=164159,Text='（不对不对，再想想。）',}",
  [164160] = "{id=164160,Text='接下来，第二步要放——',Speaker=891388,Option='{海盐和奶酪,6},{啤酒和西瓜,7}',}",
  [164161] = "{id=164161,Text='（不对不对，再想想。）',}",
  [164162] = "{id=164162,Text='搅拌和烘焙！',}",
  [164163] = "{id=164163,Text='冷冻完成！',}",
  [164164] = "{id=164164,Text='获得 【 [c][ffff00]海盐冰淇淋 x1 [-][/c]】',Emoji=11,}",
  [164165] = "{id=164165,Text='这是什么？',Speaker=891397,Option='{自制食品,0},{恋爱神器,0}',}",
  [164166] = "{id=164166,Text='这个味道……',Speaker=891386,}",
  [164167] = "{id=164167,Text='真不错呀……',Speaker=891387,}",
  [164168] = "{id=164168,Text='这、这海风一样爽朗的味道，是海盐的咸味吗？',Speaker=891386,}",
  [164169] = "{id=164169,Text='那……薯片里清新又悠闲的味道，难道是黄瓜的甜味？',Speaker=891387,}",
  [164170] = "{id=164170,Text='——你喜欢的味道真好吃！',Speaker=891397,}",
  [164171] = "{id=164171,Text='我可以理解你为什么热衷咸味了。',Speaker=891386,}",
  [164172] = "{id=164172,Text='……我也是，甜食好像也蛮不错的。',Speaker=891387,}",
  [164173] = "{id=164173,Text='太好了呱！和解了呱！',Speaker=891371,}",
  [164174] = "{id=164174,Text='谢谢你们做出了这么美妙的食物！我们不会再为了甜咸吵架啦！',Speaker=891397,}",
  [164175] = "{id=164175,Text='……哎呀呱。果然，爱一个人、也要尊、尊重对方的爱好嘛，呱。',Speaker=891371,}",
  [164176] = "{id=164176,Text='（甜饼人和绿饼人手拉手离开了。地上似乎出现了什么发光的东西！）',}",
  [164177] = "{id=164177,Text='呱咕咕咕呱呱咕————！！！！我们成功了呱！！',Speaker=891371,Option='{你高兴得,0},{都不结巴了,0}',}",
  [164178] = "{id=164178,Text='呃呜呱！我终于可以把，五个玩偶，送给[c][ffff00]家主大人[-][/c]了呱——',Speaker=891371,Option='家主大人？',}",
  [164179] = "{id=164179,Text='喵哇！！克鲁克斯——我才不会让你如愿以偿！！',Speaker=891370,}",
  [164180] = "{id=164180,Text='（是第一天见到的那只黑猫！！）',}",
  [164181] = "{id=164181,Text='哼哼喵。青蛙骑士、还有冒牌勇者！！你们要想夺回玩偶，就明天[c][ffff00]姜饼城决战[-][/c]吧！',Speaker=891370,Emoji=12,}",
  [164182] = "{id=164182,Text='五……五个神奇玩偶、都被猫，抢走了呱————！！！',Speaker=891371,Emoji=42,}",
  [164183] = "{id=164183,Text='呃呜，好不容易，给、给家主大人的，礼物呱……',Speaker=891371,Option='明天抢回来',}",
  [164184] = "{id=164184,Text='呜呜呱。那只黑猫……到底，为什么这么干啊！',Speaker=891371,}",
  [164185] = "{id=164185,Text='（克鲁克斯失魂落魄，呱声连连地跑开了。看来，明天在姜饼城还有一场恶战……）',}",
  [164186] = "{id=164186,Text='呱！我今天，一定要把五只玩偶，夺回来呱！',Speaker=891371,Option='相信你呱',}",
  [164187] = "{id=164187,Text='赌、赌上我克鲁克斯，王室骑士之名——',Speaker=891371,}",
  [164188] = "{id=164188,Text='喵哇——让你的骑士之名见鬼去吧！！',Speaker=891370,}",
  [164189] = "{id=164189,Text='克鲁克斯！今天一定要让你知道，我心中对你的怒火……！！！！',Speaker=891370,Option='{炸毛了！,0},{要打架了！,0}',Emoji=12,}",
  [164190] = "{id=164190,Text='你这坏猫！把玩偶，还给我呱——',Speaker=891371,}",
  [164191] = "{id=164191,Text='……做梦去吧！结巴蛤蟆！',Speaker=891370,}",
  [164192] = "{id=164192,Text='喵哇！吃我这招！',Speaker=891370,}",
  [164193] = "{id=164193,Text='别小看、骑士啊！呱——',Speaker=891371,}",
  [164194] = "{id=164194,Text='（一猫一蛙原地乱窜，这样下去会没完没了的……）',}",
  [164195] = "{id=164195,Text='（先尝试用魔法把他们分开。）',}",
  [164196] = "{id=164196,Text='……为什么阻止我，冒牌勇者？',Speaker=891370,Option='{有话好说,0},{打架灰太多,0}',}",
  [164197] = "{id=164197,Text='喵哼哼，随便你。反正，我可不会放过这个负心汉！',Speaker=891370,Option='你们认识？',}",
  [164198] = "{id=164198,Text='（小声）呜呱。黑猫啊……你确实给我，很熟悉的感觉……如、如果我们，真的认识……',Speaker=891371,}",
  [164199] = "{id=164199,Text='哼。你看来真的是把我忘得一干二净！',Speaker=891370,}",
  [164200] = "{id=164200,Text='吼—————！！！白痴克鲁克斯，还不快睁开眼睛看看本大爷是谁！！',Speaker=891393,}",
  [164201] = "{id=164201,Text='看什么看，冒牌勇者！大爷我就是未来狮鹫一族的王——哈尼！',Speaker=891393,Option='{哈尼哈尼~,0},{哦——,0}',}",
  [164202] = "{id=164202,Text='呃呜……呱呱……',Speaker=891371,}",
  [164203] = "{id=164203,Text='克鲁克斯！别想跑，你这混账骑士！',Speaker=891393,}",
  [164204] = "{id=164204,Text='如果不是被你坑来当[c][ffff00]坐骑[-][/c]，本大爷早就回山里快活自在去了！',Speaker=891393,Option='坐骑？？',}",
  [164205] = "{id=164205,Text='（小声）哈……哈尼，是我的坐、坐骑。呱。',Speaker=891371,}",
  [164206] = "{id=164206,Text='冒牌勇者，你评评理。当初我觉得这人心眼好，又正直，是个值得交往的对象，这才给他当坐骑。',Speaker=891393,}",
  [164207] = "{id=164207,Text='结果……这家伙为了工作，[c][ffff00]每次都忘记给我喂吃的[-][/c]！每、次！我都是自己！半夜去厨房扒拉肉吃！',Speaker=891393,Option='好惨……',}",
  [164208] = "{id=164208,Text='（咬牙切齿）这回为了给那个臭丫头找礼物，克鲁克斯已经第一百三十次忘记给我喂饭了————！！！',Speaker=891393,Option='{怎会如此！,0},{太过分了！,0}',Emoji=12,}",
  [164209] = "{id=164209,Text='（瑟缩）我、我错了，呱……',Speaker=891371,}",
  [164210] = "{id=164210,Text='所以嘛，为了给这人一点教训，我就把他变成了青蛙。',Speaker=891393,Option='是你干的！',}",
  [164211] = "{id=164211,Text='是啊，正是在下。',Speaker=891393,}",
  [164212] = "{id=164212,Text='哈尼……对、对不起呱。以后我，一定记得，给你喂饭……',Speaker=891371,}",
  [164213] = "{id=164213,Text='我、我[c][ffff00]不应该，为了工作，忽视朋友的感受[-][/c]……呱……以后，再也不会了。',Speaker=891371,Emoji=42,}",
  [164214] = "{id=164214,Text='……那我要吃烤肉。',Speaker=891393,}",
  [164215] = "{id=164215,Text='吃！',Speaker=891371,}",
  [164216] = "{id=164216,Text='（严肃）要七分熟的，多一分少一分都不行。',Speaker=891393,}",
  [164217] = "{id=164217,Text='听你的！',Speaker=891371,}",
  [164218] = "{id=164218,Text='哼哼……那好吧，暂时原谅你了。但是——',Speaker=891393,}",
  [164219] = "{id=164219,Text='（看着你）冒牌勇者，过来跟我们拍张照。要把青蛙骑士的光辉瞬间记下，让克鲁克斯永世难忘！',Speaker=891393,}",
  [164220] = "{id=164220,Text='消气了吗，呱呱？',Speaker=891371,}",
  [164221] = "{id=164221,Text='哼，算是吧。',Speaker=891393,}",
  [164222] = "{id=164222,Text='……重新自我介绍，我是隶属卢恩王室-奈利乌斯家的骑士，[c][ffff00]克鲁克斯·冯德尔[-][/c]。',Speaker=891394,Option='{帅哥你谁…,0},{你不结巴了,0}',}",
  [164223] = "{id=164223,Text='看来哈尼已经把对我施加的魔法取消了。',Speaker=891394,}",
  [164224] = "{id=164224,Text='哼。',Speaker=891393,}",
  [164225] = "{id=164225,Text='（是克鲁克斯一直在找的异世界玩偶！）',Emoji=7,}",
  [164226] = "{id=164226,Text='谢谢你们，[c][ffff00][PlayerName][-][/c]，哈尼。如果不是你们俩，我是无法集齐这五只玩偶的……',Speaker=891394,}",
  [164227] = "{id=164227,Text='这些玩偶来自异世界，只会被心灵纯洁的人所吸引……会给得到它们的人，带来勇气与幸福。',Speaker=891394,}",
  [164228] = "{id=164228,Text='……所以，我想把它们送给现任的家主大人，[c][ffff00]丝碧卡·奈利乌斯[-][/c]。她经历了许多严苛的考验，才走到了今天。',Speaker=891394,}",
  [164229] = "{id=164229,Text='希望这些玩偶，能为丝碧卡大人带来祝福，帮她更好胜任未来的工作。',Speaker=891394,Emoji=17,}",
  [164230] = "{id=164230,Text='啰啰嗦嗦的，快去送吧！',Speaker=891393,}",
  [164231] = "{id=164231,Text='……嗯，我马上就回来。',Speaker=891394,}",
  [164232] = "{id=164232,Text='干嘛，冒牌勇者？还不走？',Speaker=891393,Option='你好像不开心？',}",
  [164233] = "{id=164233,Text='哼……其实，我把克鲁克斯变成蛙……是想让这家伙知道，如果没有我帮忙，他就是个笨青蛙，再也当不了家主的骑士。',Speaker=891393,}",
  [164234] = "{id=164234,Text='（瞪了你一眼）……结果他还是顺利找到了玩偶嘛。我当时一生气，就约他决斗了。还好没有弄出什么伤来……',Speaker=891393,Option='都过去啦',}",
  [164235] = "{id=164235,Text='嗯哼。以后克鲁克斯只要记得给我喂吃的，我就还是他的好搭档。',Speaker=891393,}",
  [164236] = "{id=164236,Text='……不好了！不好了！',Speaker=891394,}",
  [164237] = "{id=164237,Text='家主、家主大人她——[c][ffff00]失踪[-][/c]了！！',Speaker=891394,}",
  [164238] = "{id=164238,Text='哈尼，我们必须立刻返回普隆德拉！奈利乌斯家已经乱成了一团……',Speaker=891394,}",
  [164239] = "{id=164239,Text='大惊小怪，我跟你走就是了。',Speaker=891393,}",
  [164240] = "{id=164240,Text='而这五个玩偶——[c][ffff00][PlayerName][-][/c]，就拜托你了！',Speaker=891394,Option='我？？',}",
  [164241] = "{id=164241,Text='没错。拜托你，务必找到丝碧卡大人——将这五只玩偶送给到她手中。',Speaker=891394,}",
  [164242] = "{id=164242,Text='情况紧急，先就此告别了，我的朋友。希望在未来的某日，我们还能再次重逢！',Speaker=891394,Option='再会！',}",
  [164258] = "{id=164258,Text='（成功通过预选赛了！去参加庆祝仪式吧！）',Emoji=17,}",
  [164259] = "{id=164259,Text='（获得决赛资格了，去参加庆祝仪式吧！）',Emoji=17,}",
  [164260] = "{id=164260,Text='（终于取得了最后的胜利！去参加颁奖仪式吧！）',Emoji=17,}",
  [164275] = "{id=164275,Text='哼哼，你这样盯着我看，一定是觉得这件玩偶服很不错吧！',Speaker=821271,}",
  [164276] = "{id=164276,Text='虽然我很喜欢吐槽没错啦……但是这件衣服太可爱了，我只剩下赞美！',Speaker=821271,}",
  [164277] = "{id=164277,Text='嗯？好奇我怎么得到它的？我才不会告诉你是在克魔岛上！',Speaker=821271,Emoji=2,}",
  [164278] = "{id=164278,Text='冒险者！看到我的新衣服了吗？',Speaker=821270,}",
  [164279] = "{id=164279,Text='之前我和妹妹去了一趟[c][ffff00]克魔岛[-][/c]，那里正在举行庆典！这套衣服就是在庆典上获得的哦！',Speaker=821270,Emoji=9,}",
  [164280] = "{id=164280,Text='喜欢的话，不如去克魔岛瞧瞧？你也可以得到属于自己的惊喜！',Speaker=821270,}",
  [164281] = "{id=164281,Text='嗯嗯，我是克魔岛旅行团的团长喵！',Speaker=891399,}",
  [164282] = "{id=164282,Text='克魔岛上正在举办非常有趣的庆典，想去的人特别多！所以，我就组织了这个旅行团喵~',Speaker=891399,}",
  [164283] = "{id=164283,Text='等所有报名的游客到齐，我们就会出发啦！',Speaker=891399,}",
  [164289] = "{id=164289,Text='你！就是你！为什么一直盯着咱的爱车！羡慕吗？嫉妒吗？',Speaker=891401,}",
  [164290] = "{id=164290,Text='这辆车只有去克魔岛参加庆典的客人可以乘坐！是超·VVVVVVVIP豪华限定版！',Speaker=891401,Emoji=12,}",
  [164291] = "{id=164291,Text='哼……哼……要是你也想要，有本事也去克魔岛赢一辆啊！',Speaker=891401,}",
  [164294] = "{id=164294,Text='啰哈！我是培卜杜拉，克魔岛光荣的形象大使！',Speaker=891403,}",
  [164295] = "{id=164295,Text='阳光、海滩和清爽啤酒，你想要的一切都可以在克魔岛找到~',Speaker=891403,}",
  [164296] = "{id=164296,Text='最近岛上在举办丰富的庆典活动，有机会请务必去看看，啰哈！',Speaker=891403,Emoji=17,}",
  [164301] = "{id=164301,Text='唔喵。被选中的冒险者，你可听说过克魔岛？',Speaker=891407,}",
  [164302] = "{id=164302,Text='岛上举办的游园庆典，似乎有着着令人快乐的魔法。',Speaker=891407,}",
  [164303] = "{id=164303,Text='喵呀。看到我旁边的小猴子了吗？那就是我在庆典上找到的伙伴。',Speaker=891407,}",
  [164304] = "{id=164304,Text='如果你感兴趣，一定不能错过这个绝妙的庆典喵。',Speaker=891407,}",
  [164305] = "{id=164305,Text='就算你是个刚入学的小家伙，也是可以去愉快玩耍的喵~',Speaker=891407,}",
  [164308] = "{id=164308,Text='你好呀同学~一直盯着人家看，想必是被烧烤香气迷住了吧？',Speaker=891410,}",
  [164309] = "{id=164309,Text='哼哼，这可是人家特意去克魔岛学习的成果！要知道，那里的烧烤可是世界上最棒的~',Speaker=891410,}",
  [164310] = "{id=164310,Text='看到这台完美无瑕的烧烤架了吗？这也是人家去克魔岛得来的哦！',Speaker=891410,}",
  [164311] = "{id=164311,Text='不和你多说了，人家要继续研究烧烤的精妙啦~',Speaker=891410,}",
  [164322] = "{id=164322,Text='（它们要打起来了！得赶快躲远点……）',}",
  [164324] = "{id=164324,Text='（好耶，顺利把烟花卖完了！继续享受庆典吧！）',}",
  [164325] = "{id=164325,Text='连小偷都抓不到，怎么会有这么没用的冒险者啊！',Speaker=892908,}",
  [164326] = "{id=164326,Text='我再说一遍，那个小偷长得[c][ffff00]绿油油[-][/c]、头上戴着[c][ffff00]红帽子[-][/c]！',Speaker=892908,Emoji=12,}",
  [164327] = "{id=164327,Text='（没办法，只好重新去抓小偷了……）',}",
  [164332] = "{id=164332,Text='听说这个神灯可以实现人的愿望~~',Speaker=821517,Emoji=28,}",
  [164333] = "{id=164333,Text='许个什么愿望好呢~~希望到岛上旅行的游客越来越多吧！',Speaker=821517,}",
  [164334] = "{id=164334,Text='…………',Speaker=821517,}",
  [164335] = "{id=164335,Text='嗯……怎么没反应？是不是要念什么咒语……',Speaker=821517,}",
  [164339] = "{id=164339,Text='看啊，冒险者！老爹送我的新背包~',Speaker=821521,}",
  [164340] = "{id=164340,Text='是不是跟庆典的氛围很相配啊~',Speaker=821521,Emoji=17,}",
  [164341] = "{id=164341,Text='快到庆典现场转转吧~要是发生了有趣的事，一定要来告诉我哦！',Speaker=821521,}",
  [164343] = "{id=164343,Text='你可真有眼光，客人~请稍等……',Speaker=821555,Emoji=11,}",
  [164344] = "{id=164344,Text='完成了~请品尝——庆典特供烤鸟腿~~',Speaker=821555,}",
  [164361] = "{id=164361,Text='发射成功！',}",
  [164375] = "{id=164375,Text='我给你也准备了一份！送给你了！',Speaker=892973,Option='{接受礼物,2},{傲娇拒绝,3}',}",
  [164376] = "{id=164376,Text='咕咕~~已经拿到礼物了！',Speaker=892973,}",
  [164377] = "{id=164377,Text='像你这么傲娇的，我也不常见到。好吧，如果有需要可以再来找我。',Speaker=892973,}",
  [164378] = "{id=164378,Text='确定选择这个是波利最喜欢的东西吗？',Option='{确定,2},{再想想,3}',}",
  [164387] = "{id=164387,Text='完蛋了，马上就要表演了，我的女演员要辞演。',Speaker=891420,}",
  [164388] = "{id=164388,Text='请你一定要帮帮我啊！',Speaker=891420,}",
  [164389] = "{id=164389,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891420,Option='{乐意之至,2},{委婉拒绝,3}',}",
  [164390] = "{id=164390,Text='太感谢了！',Speaker=891420,}",
  [164391] = "{id=164391,Text='唉，看来这件事情真没办法了。',Speaker=891420,}",
  [164392] = "{id=164392,Text='事情是这样的，我带着演员们来到普隆德拉，为了庆祝仲夏之夜表演节目。',Speaker=891420,}",
  [164393] = "{id=164393,Text='临演前，我的女主角妮可情绪特别低落。',Speaker=891420,Option='怎么办',}",
  [164394] = "{id=164394,Text='甚至还跟我提出不想演出了。',Speaker=891420,}",
  [164395] = "{id=164395,Text='请你帮我安慰她。',Speaker=891420,Option='没问题呢',}",
  [164396] = "{id=164396,Text='她就在稻田里，我带你过去吧！',Speaker=891420,Option='{好的,2},{暂时不去,3}',}",
  [164397] = "{id=164397,Text='这位是多兰族的妮可小姐。',Speaker=891420,Option='你好，妮可',}",
  [164398] = "{id=164398,Text='我没办法表演！不行不行，我根本做不到。',Speaker=891421,}",
  [164399] = "{id=164399,Text='我唱歌跳舞都不熟练！',Speaker=891421,}",
  [164400] = "{id=164400,Text='完全做不到！',Speaker=891421,}",
  [164401] = "{id=164401,Text='你看看你，都做了些什么？',Speaker=891420,Option='什么都没做',}",
  [164402] = "{id=164402,Text='你把女主角吓跑了！',Speaker=891420,Option='？？',}",
  [164403] = "{id=164403,Text='我不管，你一定要把我的女主角找回来！',Speaker=891420,}",
  [164404] = "{id=164404,Text='没用的，我已经放弃做女主角了。',Speaker=891421,}",
  [164405] = "{id=164405,Text='你不用劝我。',Speaker=891421,}",
  [164406] = "{id=164406,Text='谢谢你，这些鲜花真好看。',Speaker=891421,Option='劝解',}",
  [164407] = "{id=164407,Text='我确实很没自信。',Speaker=891421,Option='我来帮忙吧',}",
  [164408] = "{id=164408,Text='真的吗？你愿意帮助我吗？',Speaker=891421,Option='当然',}",
  [164409] = "{id=164409,Text='那我们明天见！一定要来哦~',Speaker=891421,}",
  [164410] = "{id=164410,Text='你来教我演出了吗？',Speaker=891421,Option='是的',}",
  [164411] = "{id=164411,Text='让我看看，应该怎么做！',Speaker=891421,}",
  [164412] = "{id=164412,Text='所以，怎么唱歌呢？',Speaker=891421,Option='{大嗓门,2},{飙高音,3},{用哭腔,4}',}",
  [164413] = "{id=164413,Text='啦啦啦啦啦啦啦！！！！',}",
  [164414] = "{id=164414,Text='噢噢噢噢噢噢噢',}",
  [164415] = "{id=164415,Text='嘤嘤嘤……',}",
  [164416] = "{id=164416,Text='有点难听……',Speaker=891421,}",
  [164417] = "{id=164417,Text='我没办法成为女主角了……T T……',Speaker=891421,Option='别哭别哭',}",
  [164418] = "{id=164418,Text='没办法了。',Speaker=891421,Option='我跳舞可以',}",
  [164419] = "{id=164419,Text='那你跳一段我看看。',Speaker=891421,}",
  [164420] = "{id=164420,Text='看我这个律动，这个感觉……',}",
  [164421] = "{id=164421,Text='我没有救了……',Speaker=891421,}",
  [164422] = "{id=164422,Text='看得出来，你已经用尽全力了。',Speaker=891420,Option='唉',}",
  [164423] = "{id=164423,Text='但是没用在点子上啊。',Speaker=891420,Option='什么？',}",
  [164424] = "{id=164424,Text='我听说[c][ffff00]音乐蝗虫[-][/c]的乐感很强，[c][ffff00]舞娘[-][/c]的舞蹈姿势优美。',Speaker=891420,}",
  [164425] = "{id=164425,Text='为什么你不请这些老师教导妮可呢？',Speaker=891420,Option='那你怎么不请',}",
  [164426] = "{id=164426,Text='我是一个作家，不擅长跟其他人打交道。',Speaker=891420,}",
  [164427] = "{id=164427,Text='明天你去找老师教导妮可吧！',Speaker=891420,}",
  [164428] = "{id=164428,Text='哎，[c][ffff00][PlayerName][-][/c]，我正准备找埃斯库罗斯呢。',Speaker=891421,}",
  [164429] = "{id=164429,Text='我准备放弃女主角的位置。',Speaker=891421,}",
  [164430] = "{id=164430,Text='你帮我转达给埃斯库罗斯。',Speaker=891421,Option='为什么',}",
  [164431] = "{id=164431,Text='我没有能力演好',Speaker=891421,Option='我帮你！',}",
  [164432] = "{id=164432,Text='我很感谢你，只是你唱歌比我还难听！',Speaker=891421,}",
  [164433] = "{id=164433,Text='恐怕帮不了我。',Speaker=891421,Option='我帮你找老师！',}",
  [164434] = "{id=164434,Text='有人需要我指导吗？',Speaker=891422,}",
  [164435] = "{id=164435,Text='没问题！音乐家应该多多帮助其他人！',Speaker=891422,}",
  [164436] = "{id=164436,Text='有人愿意教我唱歌吗？',Speaker=891421,}",
  [164437] = "{id=164437,Text='我愿意！我愿意！美丽的多兰族小姐呀！',Speaker=891422,}",
  [164438] = "{id=164438,Text='教你唱歌这种事情，就交给我吧！',Speaker=891422,}",
  [164439] = "{id=164439,Text='试试看！跟上我的节奏！',Speaker=891422,}",
  [164440] = "{id=164440,Text='啦啦啦~啦啦啦啦~',}",
  [164442] = "{id=164442,Text='美丽的小姐，一定要有自信呀！',Speaker=891422,}",
  [164443] = "{id=164443,Text='终于放心一点了，明天再会！',Speaker=891421,}",
  [164444] = "{id=164444,Text='快来跳舞呀~',Speaker=891423,}",
  [164445] = "{id=164445,Text='看见了吗？我观察好几天了！',Speaker=891421,}",
  [164446] = "{id=164446,Text='这位大师的舞蹈水平出神入化。',Speaker=891421,}",
  [164447] = "{id=164447,Text='如果我能像她一样，就有自信做女主角了！',Speaker=891421,Option='交给我吧！',}",
  [164448] = "{id=164448,Text='有人想学习跳舞吗？',Speaker=891423,}",
  [164449] = "{id=164449,Text='好啊~我愿意教！',Speaker=891423,}",
  [164450] = "{id=164450,Text='可爱的多兰族小姐，仔细观察我的动作哦~',Speaker=891423,}",
  [164451] = "{id=164451,Text='让我想一想！好像是这么跳的！',Speaker=891421,}",
  [164452] = "{id=164452,Text='是这样吗？',Speaker=891421,Option='差、差不多吧',}",
  [164453] = "{id=164453,Text='yeah！都学会了，明天就是表演的日子了。',Speaker=891421,}",
  [164454] = "{id=164454,Text='拭目以待吧！',Speaker=891421,}",
  [164455] = "{id=164455,Text='已经准备好了吗？',Speaker=891420,Option='没问题',}",
  [164456] = "{id=164456,Text='没问题！经过高级音乐家和舞蹈大师的帮助，我已经是一名合格的女演员了！',Speaker=891421,}",
  [164457] = "{id=164457,Text='啦啦啦啦~啦啦啦~~',Speaker=891421,}",
  [164458] = "{id=164458,Text='这位多兰族小姐叫什么名字？',}",
  [164459] = "{id=164459,Text='唱歌可真好听啊！',}",
  [164460] = "{id=164460,Text='安可！安可！安可！',}",
  [164461] = "{id=164461,Text='跳舞跳舞~妮可~',Speaker=891420,}",
  [164462] = "{id=164462,Text='额……这好像不是我设计的动作。',Speaker=891420,}",
  [164463] = "{id=164463,Text='真好看！',}",
  [164464] = "{id=164464,Text='太美了！',}",
  [164465] = "{id=164465,Text='这样也挺好嘛~',Speaker=891420,}",
  [164466] = "{id=164466,Text='哎呀，我又忘记了。',Speaker=891425,Option='忘记了什么？',}",
  [164467] = "{id=164467,Text='都忘记了，怎么能知道忘记了什么……',Speaker=891425,}",
  [164468] = "{id=164468,Text='好像是跟音乐蝗虫有关系吧。',Speaker=891425,}",
  [164469] = "{id=164469,Text='去帮我拍一张音乐蝗虫的照片吧！也许我看见能想起来我忘了些什么。',Speaker=891425,}",
  [164470] = "{id=164470,Text='啊！看见照片我就想起来了！',Speaker=891425,}",
  [164471] = "{id=164471,Text='我要跟音乐蝗虫准备儿童节音乐会的事情！',Speaker=891425,}",
  [164472] = "{id=164472,Text='谢谢你，冒险者！这些礼物就作为赠礼交给你了。',Speaker=891425,}",
  [164473] = "{id=164473,Text='来吧！为了仲夏夜之梦，与我一战吧！',Speaker=891426,Option='战什么？',}",
  [164474] = "{id=164474,Text='看我的名字就知道，肯定是猜拳啊！',Speaker=891426,}",
  [164475] = "{id=164475,Text='哈哈哈哈哈！好久没人让我有这种感觉了！',Speaker=891426,}",
  [164476] = "{id=164476,Text='你，不错。',Speaker=891426,}",
  [164477] = "{id=164477,Text='这些奖励是我送给你的！',Speaker=891426,}",
  [164478] = "{id=164478,Text='完蛋了，完蛋了！',Speaker=891427,Option='什么完蛋了？',}",
  [164479] = "{id=164479,Text='我中了女巫的魔法，从一个王子变成了青蛙。',Speaker=891427,Option='那怎么办？',}",
  [164480] = "{id=164480,Text='可能要个亲亲就能变回来吧。',Speaker=891427,Option='{有点不想亲,2},{恶心,2},{那勉为其难,4}',}",
  [164481] = "{id=164481,Text='完蛋完蛋！变不回来了！',Speaker=891427,}",
  [164482] = "{id=164482,Text='太感动了，等我变回王子一定会奖励你！',Speaker=891427,}",
  [164483] = "{id=164483,Text='总算变回帅气的我了！',Speaker=891434,Emoji=17,}",
  [164484] = "{id=164484,Text='真王子都应该是粉红色。',Speaker=891434,}",
  [164485] = "{id=164485,Text='谢谢你冒险者，这些礼物交给你！',Speaker=891434,}",
  [164486] = "{id=164486,Text='三！',Speaker=891426,}",
  [164487] = "{id=164487,Text='二！',Speaker=891426,}",
  [164488] = "{id=164488,Text='一！',Speaker=891426,}",
  [164489] = "{id=164489,Text='剪刀石头布——',Speaker=891426,Option='{石头,40},{剪刀,50},{布,60}',}",
  [164490] = "{id=164490,Text='太遗憾了，再接再厉！',Speaker=891426,}",
  [164491] = "{id=164491,Text='嘿嘿，我赢了！',Emoji=17,}",
  [164492] = "{id=164492,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=891426,Option='{再玩一次,21},{不玩啦,102}',}",
  [164498] = "{id=164498,Text='下次一定要再多招几个临时工……啊，你是谁？',Speaker=892976,Option='自报姓名',}",
  [164499] = "{id=164499,Text='没听过的名字。是临时工吗？',Speaker=892976,}",
  [164500] = "{id=164500,Text='唉，不管是不是，都要麻烦你了。能不能帮我收集一下修补花车的材料？',Speaker=892976,Option='修补花车？',}",
  [164501] = "{id=164501,Text='刚才有两个没素质的游客，喝醉了在花车上大打出手，惊扰了别人不说，还弄坏了车子。',Speaker=892976,}",
  [164502] = "{id=164502,Text='虽然整体功能没受到影响，但是车内装饰什么的都需要小修小补。',Speaker=892976,}",
  [164503] = "{id=164503,Text='我这边忙得团团转，实在分不出时间去搞这种额外的破事。',Speaker=892976,Option='{包在我身上,3},{我还有别的事,2}',}",
  [164504] = "{id=164504,Text='这么爽快的年轻人真让人省心！',Speaker=892976,}",
  [164505] = "{id=164505,Text='我需要骨螺壳，两个就够了！',Speaker=892976,}",
  [164506] = "{id=164506,Text='什么？走走走！不帮忙就别碍事了！',Speaker=892976,}",
  [164507] = "{id=164507,Text='这么快就完成了？',Speaker=892976,}",
  [164508] = "{id=164508,Text='哎哎，别糊弄老头子我啊，这点完全不够。',Speaker=892976,}",
  [164509] = "{id=164509,Text='临时工也要有点责任心！',Speaker=892976,}",
  [164510] = "{id=164510,Text='你这年轻人实在是让人省心！现在我能轻松修补花车了！',Speaker=892976,}",
  [164511] = "{id=164511,Text='好好干！有机会我一定会帮你申请正式员工待遇的！',Speaker=892976,}",
  [164512] = "{id=164512,Text='下次有问题再麻烦你啊！',Speaker=892976,}",
  [164513] = "{id=164513,Text='干嘛！我现在心情很不好！',Speaker=892977,Option='{请问,0},{出什么事了？,0}',}",
  [164514] = "{id=164514,Text='还不都是那些没素质的游客！',Speaker=892977,}",
  [164515] = "{id=164515,Text='喝醉了在花车上打架吵闹，影响别人；我去阻止他们，结果我最喜欢的耳坠都被扯掉了！',Speaker=892977,}",
  [164516] = "{id=164516,Text='明明是梦幻美好的花车巡游，都被这种人搞砸了！气得我只想揍他们一顿！',Speaker=892977,Option='{你是车长……,0},{千万不要,0}',}",
  [164517] = "{id=164517,Text='我当然知道不行！我就是生气，说说也不可以吗！',Speaker=892977,Option='{别生气了,0},{我帮你找耳坠,0}',}",
  [164518] = "{id=164518,Text='你……？哼，好吧，那你就试试看吧。',Speaker=892977,}",
  [164519] = "{id=164519,Text='不过，我要负责花车的驾驶，你只能自己去找了！',Speaker=892977,}",
  [164520] = "{id=164520,Text='（或许就是这个了吧？）',}",
  [164521] = "{id=164521,Text='（拿去给米兰达看看。）',}",
  [164522] = "{id=164522,Text='怎么样，找到了吗？',Speaker=892977,Option='{请看看,0},{是不是这个,0}',}",
  [164523] = "{id=164523,Text='确、确实是我丢失的耳坠！',Speaker=892977,}",
  [164524] = "{id=164524,Text='谢谢你！！我还以为再也找不回来了呢！总觉得对不起亚斯科夫……',Speaker=892977,}",
  [164525] = "{id=164525,Text='……呃，不是，我没说什么啊。',Speaker=892977,}",
  [164526] = "{id=164526,Text='请、请稍等，我把耳坠戴上，不然又容易掉了……',Speaker=892977,}",
  [164527] = "{id=164527,Text='……咳，总而言之，谢谢你了。我也要预防以后再发生这种事。',Speaker=892977,}",
  [164528] = "{id=164528,Text='还是要和埃德加大叔商量，整治一下这些无良游客……（嘀嘀咕咕）',Speaker=892977,Option='（但愿能成功吧）',}",
  [164529] = "{id=164529,Text='啊，要走了吗？再见！',Speaker=892977,}",
  [164530] = "{id=164530,Text='哎呀，冒险者，请帮帮我。',Speaker=892980,Option='{不要急,0},{怎么了？,0}',}",
  [164531] = "{id=164531,Text='我按照花车长的要求，在巡游路线上做装饰……',Speaker=892980,}",
  [164532] = "{id=164532,Text='但是有些慕名而来的观众太热情了，把我用来装饰的花朵都挤散了。',Speaker=892980,}",
  [164533] = "{id=164533,Text='我好不容易把花朵找回，有些已经被踩得完全不能用，有些干脆已经被观众当做小礼物拿走了。',Speaker=892980,}",
  [164534] = "{id=164534,Text='虽然我能理解观众的热情，但是这下我的工作就不能完成了呀。',Speaker=892980,Option='{我懂了,0},{有什么补救办法,0}',}",
  [164535] = "{id=164535,Text='嗯……',Speaker=892980,}",
  [164536] = "{id=164536,Text='如果你能帮我采摘花朵，把缺少的部分补齐，应该就没事了。',Speaker=892980,Option='那我去了',}",
  [164537] = "{id=164537,Text='嗯，请小心哟！',Speaker=892980,}",
  [164538] = "{id=164538,Text='哎呀！你手中的花朵……',Speaker=892980,Option='给你',}",
  [164539] = "{id=164539,Text='谢谢你！这样我就能完成我的工作了！',Speaker=892980,}",
  [164540] = "{id=164540,Text='只要不再遇到那样狂热的观众……',Speaker=892980,}",
  [164541] = "{id=164541,Text='温柔的人啊，请尽情期待马上开始的花车巡游吧！',Speaker=892980,}",
  [164542] = "{id=164542,Text='……………………（用严肃的目光打量着）',Speaker=892982,Option='{怎、怎么了？,0},{（被死死盯着看）,0}',}",
  [164543] = "{id=164543,Text='……啊，抱歉，我在目测你的体格，毕竟接下来要做的事，可能会有一点点危险。',Speaker=892982,}",
  [164544] = "{id=164544,Text='……不，你也不用那么紧张，只是协助我而已。',Speaker=892982,Option='{说是逮捕痴汉,0},{到底要怎么做……,0}',}",
  [164545] = "{id=164545,Text='我简单说明一下吧。',Speaker=892982,}",
  [164546] = "{id=164546,Text='我想你也知道，花车巡游是极具人气的活动，每次都会引来大批观众。',Speaker=892982,}",
  [164547] = "{id=164547,Text='问题是，偶尔会出现一些行为过激的人，给其他普通观众带来困扰。',Speaker=892982,Option='过激行为是指？',}",
  [164548] = "{id=164548,Text='比如脱得光光地到处走啦、骚扰女乘客啦、扮鬼吓唬幼年观众啦……',Speaker=892982,}",
  [164549] = "{id=164549,Text='最可气的是，很多时候我们抓住对方，却没有证据证明。对方只要拒不承认，也不能把他们怎么样。',Speaker=892982,}",
  [164550] = "{id=164550,Text='所以！我希望你能假扮普通观众，在巡游中观察这些过激乘客，然后把他们的行为拍下来。',Speaker=892982,Option='{原来如此,2},{不，听起来太危险,3}',}",
  [164551] = "{id=164551,Text='很好，穿上这套衣服，扮成普通观众吧。',Speaker=892982,}",
  [164552] = "{id=164552,Text='现在，拿上你的相机。在花车巡游开始后，观察周围人并且记得拍照。',Speaker=892982,}",
  [164553] = "{id=164553,Text='结束后，把照片交给我就可以了。',Speaker=892982,}",
  [164554] = "{id=164554,Text='只是拍照而已，有什么危险的。算了，我也不勉强你。',Speaker=892982,}",
  [164555] = "{id=164555,Text='如何？有好好地把工作完成吗？',Speaker=892982,Option='{完成了,0},{（交出照片）,0}',}",
  [164556] = "{id=164556,Text='很好！这样我们终于能对这些不要脸的家伙下狠手了！',Speaker=892982,}",
  [164557] = "{id=164557,Text='这样米兰达也肯定很开心！',Speaker=892982,Option='{花车长吗？,0},{原来你对她……,0}',}",
  [164558] = "{id=164558,Text='啊、不……我的意思是，其他的普通观众也不会被困扰了，她的工作也会轻松不少吧啊哈哈哈……',Speaker=892982,Option='{哦~~,0},{没错没错,0}',}",
  [164559] = "{id=164559,Text='总、总之，谢谢你的协助。我准备了一些谢礼，请收下。',Speaker=892982,}",
  [164560] = "{id=164560,Text='嘿咻嘿咻……对不起，请把脚抬下……',Speaker=892980,Option='{（默默抬脚）,0},{很辛苦呢,0}',}",
  [164561] = "{id=164561,Text='咦……是冒险者啊。',Speaker=892980,Option='你在做什么',}",
  [164562] = "{id=164562,Text='如你所见，在清理垃圾。每次花车巡游过后，都必须要做的。',Speaker=892980,}",
  [164563] = "{id=164563,Text='对了，冒险者，如果你有空的话，能不能帮我把另一边的垃圾也清理一下呢？',Speaker=892980,Option='{好啊,2},{那是你的工作吧？,3}',}",
  [164564] = "{id=164564,Text='真的？谢谢你哦~',Speaker=892980,}",
  [164565] = "{id=164565,Text='我在这边也会加油干的！',Speaker=892980,}",
  [164566] = "{id=164566,Text='是、是哦……对不起，冒昧地向你说这种话。',Speaker=892980,}",
  [164567] = "{id=164567,Text='嘿咻嘿咻……',Speaker=892980,}",
  [164568] = "{id=164568,Text='好了！全部清理完成了！',Speaker=892980,}",
  [164569] = "{id=164569,Text='冒险者，你那边也结束了吗？',Speaker=892980,Option='{是的,0},{好累……,0}',}",
  [164570] = "{id=164570,Text='啊哈哈，这工作的确很累，但也是必须做的。',Speaker=892980,}",
  [164571] = "{id=164571,Text='不过，如果观众再自觉一点把垃圾带走，我们的工作量也会减轻吧（小声）',Speaker=892980,Option='没错',}",
  [164572] = "{id=164572,Text='嗯嗯，我就知道你会同意我的！谢谢！',Speaker=892980,}",
  [164573] = "{id=164573,Text='不要哭了嘛，姐姐带你去吃好吃的好不好？不要？那要不要玩玩具？',Speaker=892977,Option='{（米兰达……）,0},{（竟然在哄孩子）,0}',}",
  [164574] = "{id=164574,Text='（低声）你、你别光在那边看热闹！过来帮我！',Speaker=892977,Option='{好的好的,0},{这孩子是？,0}',}",
  [164575] = "{id=164575,Text='我也不知道这孩子怎么了！总之就是和家人走失了吧，问她她也不回答。',Speaker=892977,}",
  [164576] = "{id=164576,Text='哄小孩这种事我不拿手，你快上！',Speaker=892977,}",
  [164577] = "{id=164577,Text='………………',Speaker=892993,Option='和妈妈走散了吗？',}",
  [164578] = "{id=164578,Text='……妈妈说，不能和陌生人说话……',Speaker=892993,}",
  [164579] = "{id=164579,Text='……妈妈说，不能告诉陌生人自己的名字……',Speaker=892993,}",
  [164580] = "{id=164580,Text='（戒备心很强……还是再和米兰达商量一下）',}",
  [164581] = "{id=164581,Text='哈？你也问不出来？',Speaker=892977,Option='{我们都是陌生人,0},{要找一个她信赖的人,0}',}",
  [164582] = "{id=164582,Text='信赖的人？小孩子能信赖的人……除了家人以外……',Speaker=892977,Option='{老师……,3},{朋友……,4},{警察……,5}',}",
  [164583] = "{id=164583,Text='我哪知道她的老师朋友在哪里啦！',Speaker=892977,}",
  [164584] = "{id=164584,Text='就是这个！警察！',Speaker=892977,Option='{说到警察,0},{亚斯科夫,0}',}",
  [164585] = "{id=164585,Text='没错没错，去找亚斯科夫来对付这小家伙！',Speaker=892977,}",
  [164586] = "{id=164586,Text='亚斯科夫！！（超大声）',Speaker=892977,}",
  [164587] = "{id=164587,Text='米兰达！你叫我吗？',Speaker=892982,Option='{好快！,0},{你是召唤兽吗！,0}',}",
  [164588] = "{id=164588,Text='这里有个和家人走散的小女孩啦……',Speaker=892977,}",
  [164589] = "{id=164589,Text='……唔唔，我明白了，我来问问她。',Speaker=892982,}",
  [164590] = "{id=164590,Text='我已经知道了。这孩子和家人一起来看花车，结果被人流挤散了。',Speaker=892982,}",
  [164591] = "{id=164591,Text='我会负责找到她的父母，不过这需要等待一段时间。关键是，这孩子已经饿了很久……',Speaker=892982,Option='我来做菜',}",
  [164592] = "{id=164592,Text='呜呜呜……肚子好饿……想吃东西……',Speaker=892993,}",
  [164593] = "{id=164593,Text='（为孩子制作一份丰盛大餐吧。）',}",
  [164594] = "{id=164594,Text='好，做点好吃的，让孩子打起精神来！',Speaker=892982,}",
  [164595] = "{id=164595,Text='………………………………',Speaker=892993,}",
  [164596] = "{id=164596,Text='啊姆啊姆…………好、好吃……！',Speaker=892993,}",
  [164597] = "{id=164597,Text='谢谢……谢谢你！',Speaker=892993,Option='{吃饱了吗,0},{开心了吗,0}',}",
  [164598] = "{id=164598,Text='嗯、嗯……',Speaker=892993,}",
  [164599] = "{id=164599,Text='小妹妹，好消息，你的妈妈已经找到了，我现在就带你去见她。',Speaker=892982,}",
  [164600] = "{id=164600,Text='真的吗！我要去我要去！',Speaker=892993,}",
  [164601] = "{id=164601,Text='好，那么——',Speaker=892982,}",
  [164602] = "{id=164602,Text='米兰达！这孩子就交给我吧， 你放心！',Speaker=892982,}",
  [164603] = "{id=164603,Text='啰嗦，快去干正事！',Speaker=892977,}",
  [164604] = "{id=164604,Text='嘿嘿！',Speaker=892982,}",
  [164605] = "{id=164605,Text='……好了，这件事总算告一段落了。亚斯科夫做事靠谱，一定会把孩子安全送回去的。',Speaker=892977,Option='{是啊,0},{很靠谱呢,0}',}",
  [164606] = "{id=164606,Text='别、别用那种意味深长的语气，我不过是就事论事。',Speaker=892977,}",
  [164607] = "{id=164607,Text='话说回来，你也满靠谱的。如果再遇到事情，我会记得，还有你可以帮忙。',Speaker=892977,Option='不胜荣幸',}",
  [164608] = "{id=164608,Text='谢了啊！',Speaker=892977,}",
  [164609] = "{id=164609,Text='勇敢的冒险家！请帮帮我！',Speaker=892969,}",
  [164610] = "{id=164610,Text='我是美食店店长费雷德，为迎接即将到来的节日，我准备了「秘制糯米蛋糕」，却不料在运输的途中，被哥布灵盗宝团截走T T',Speaker=892969,}",
  [164611] = "{id=164611,Text='所以——拜托你！请找到那些盗宝团，夺回被他们抢走的美食吧！！\n这期间，如果能提供额外的材料给我，我将有额外的谢礼！',Speaker=892969,}",
  [164615] = "{id=164615,Text='………………',Speaker=892993,Option='  你叫什么名字？',}",
  [164624] = "{id=164624,Text='你就是里奇请来的侦探吗？',Speaker=892995,Option='是啊',}",
  [164625] = "{id=164625,Text='[c][ffff00]我跟你说哦，里奇每过一段时间似乎都会消失几天呢~这是为什么呢……难道说，里奇有生理期吗？[-][/c]',Speaker=892995,Option='生理期？（认真记下来）',}",
  [164626] = "{id=164626,Text='我知道的就这些啦！',Speaker=892995,}",
  [164627] = "{id=164627,Text='你好呀，你应该不是侦探吧！看起来不像。',Speaker=892996,Option='我是！',}",
  [164628] = "{id=164628,Text='[c][ffff00]我上次有见到一个奇怪的波利！居然、长着两条腿！长着腿的波利！[-][/c]',Speaker=892996,Option='波利怎么可能有腿呢？',}",
  [164629] = "{id=164629,Text='那我怎么知道，可能是新的物种吧，或者是…你不是侦探吗！自己去想啊！',Speaker=892996,}",
  [164630] = "{id=164630,Text='其实……要不要说呢？',Speaker=892997,Option='放心，我会解决的',}",
  [164631] = "{id=164631,Text='[c][ffff00]其实，我有件事一直藏在心里没说……我，我有一次偶然遇到里奇，他好像变了个人似的，整个人特别粗暴。我和平时一样跟他打招呼，结果他二话不说就要攻击我……但是第二天里奇又变回原来的里奇了。如果不是逃跑路上摔了一跤留下疤痕，我真要以为自己是做梦了……[-][/c]',Speaker=892997,Option='额…',}",
  [164632] = "{id=164632,Text='是不是很奇怪呀，到底是怎么回事呢？',Speaker=892997,}",
  [164633] = "{id=164633,Text='没想到啊，没想到啊，恶魔瑞克竟然……',Speaker=892998,Option='它怎么了？',}",
  [164634] = "{id=164634,Text='[c][ffff00]……其实，恶魔瑞克啊，有自己的弱点哦……据说，他有喜欢的对象，而且超级痴情的……难以想象对吧……[-][/c]',Speaker=892998,Option='怎么会呢？',}",
  [164635] = "{id=164635,Text='真的呀，你再去调查一下它！',Speaker=892998,}",
  [164636] = "{id=164636,Text='侦探先生你好呀！',Speaker=892999,Option='说一说你知道的事情',}",
  [164637] = "{id=164637,Text='[c][ffff00]话说，莉莉丝跟着里奇多久了呀？[-][/c]',Speaker=892999,Option='...（我怎么知道）',}",
  [164638] = "{id=164638,Text='好像很长时间了吧~',Speaker=892999,}",
  [164643] = "{id=164643,Text='正义的冒险者，可以帮助我解决这棘手的问题吗？[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取】[-][/c]',Speaker=891435,Option='{可以,2},{拒绝,3}',}",
  [164644] = "{id=164644,Text='如果没有意向的话，不要浪费彼此的时间！',Speaker=891435,}",
  [164645] = "{id=164645,Text='如果不尽快处理，恐怕我必须动用武力了……',Speaker=891435,Option='出什么事了？',}",
  [164646] = "{id=164646,Text='因为这是很严重的问题，如果你战力不够，我也是没法信任你的。',Speaker=891435,}",
  [164647] = "{id=164647,Text='请至少击败五只绿毛虫，展现你的实力再来和我谈吧！',Speaker=891435,}",
  [164648] = "{id=164648,Text='不错，看来正义的冒险者还是有实力的。',Speaker=891443,Option='你遇到什么棘手的事？',}",
  [164649] = "{id=164649,Text='……如你所见，我是天使波利队长里奇。最近我的队里发生了不少怪事。',Speaker=891443,}",
  [164650] = "{id=164650,Text='一些队员原因不明地开始异变成了恶魔！',Speaker=891443,}",
  [164651] = "{id=164651,Text='大约一周以前，有些队员性格变得古怪，经常胡言乱语，让其他队员感到困扰。',Speaker=891443,}",
  [164652] = "{id=164652,Text='我找他们谈话时，他们竟然在我面前变成了恶魔波利！',Speaker=891443,}",
  [164653] = "{id=164653,Text='这太出乎意料了，我也从来没遇到过，一时措手不及。',Speaker=891443,Option='恶魔波利……？',}",
  [164654] = "{id=164654,Text='你不知道吗？我们天使波利和恶魔波利是互相敌对的。我们代表着正义和光明，他们是邪恶和黑暗的化身。',Speaker=891443,}",
  [164655] = "{id=164655,Text='所以这件事必须尽快解决，不然，邪恶的力量会越来越强大！',Speaker=891443,Option='你有头绪吗？',}",
  [164656] = "{id=164656,Text='……不瞒你说，我怀疑这件事的背后黑手，就是恶魔波利的统帅。',Speaker=891443,}",
  [164657] = "{id=164657,Text='恶魔波利的统帅名叫瑞克。如果说恶魔波利代表着邪恶，他就是其中最坏的那一个。',Speaker=891443,}",
  [164658] = "{id=164658,Text='他一定是趁我的队员不注意，用黑暗魔法偷袭他们，把他们变成了恶魔波利！',Speaker=891443,Option='{听起来有点道理,2},{你有证据吗？,3}',}",
  [164659] = "{id=164659,Text='对吧？',Speaker=891443,}",
  [164660] = "{id=164660,Text='这还需要什么证据？天使波利变成恶魔波利，肯定对瑞克最有好处啊！',Speaker=891443,}",
  [164661] = "{id=164661,Text='像他那种不择手段的恶魔，为了增强邪恶的力量，做出这种事一点都不奇怪！',Speaker=891443,}",
  [164662] = "{id=164662,Text='当然，重要的不是推断，而是彻底阻止我的队员继续变成恶魔波利。所以，我想拜托你去和瑞克交涉。',Speaker=891443,}",
  [164663] = "{id=164663,Text='——莉莉丝！（大喊）',Speaker=891443,}",
  [164664] = "{id=164664,Text='波利波利~队长，莉莉丝报到哩？',Speaker=891438,}",
  [164665] = "{id=164665,Text='这是的我的副队长莉莉丝。莉莉丝，你带冒险者去瑞克的地盘。',Speaker=891443,}",
  [164666] = "{id=164666,Text='波利波利~收到哩~',Speaker=891438,}",
  [164667] = "{id=164667,Text='跟着莉莉丝就能找到瑞克的所在地。拜托你了，正义的冒险者。',Speaker=891443,}",
  [164668] = "{id=164668,Text='波利波利~再前面一点就是恶魔波利的地方哩~',Speaker=891438,}",
  [164669] = "{id=164669,Text='他们很好斗哩~冒险者，要小心~',Speaker=891438,Option='你不陪我进去吗？',}",
  [164670] = "{id=164670,Text='波利波利~恶魔波利很敏感哩，莉莉丝和你一起去很快就会被发现哩~',Speaker=891438,}",
  [164671] = "{id=164671,Text='再说哩，瑞克今天应该不在这里~',Speaker=891438,Option='{啊？,0},{你不早说！,0}',}",
  [164672] = "{id=164672,Text='因为我们天使波利对恶魔波利的味道也很敏感哩~你只要知道地方，明天自己就能来找瑞克哩~',Speaker=891438,}",
  [164673] = "{id=164673,Text='只有你一个人的话，瑞克应该会愿意见你哩~',Speaker=891438,Option='好吧……',}",
  [164674] = "{id=164674,Text='祝你好运！波利波利~',Speaker=891438,}",
  [164675] = "{id=164675,Text='（明天做好准备，来和瑞克交涉吧。）',}",
  [164676] = "{id=164676,Text='呜噜呜噜呜噜呜噜——',Speaker=891442,}",
  [164677] = "{id=164677,Text='（被偷袭了！）',}",
  [164678] = "{id=164678,Text='哦嚯嚯嚯嚯——里奇找的那个冒险者，就是你咯？',Speaker=891436,}",
  [164679] = "{id=164679,Text='被那种杂碎指使来指使去，你脑子进水了吗？',Speaker=891436,}",
  [164680] = "{id=164680,Text='哦嚯嚯嚯嚯——你是打算和恶魔波利讲天使波利的事情吗？',Speaker=891436,Option='你有动机',}",
  [164681] = "{id=164681,Text='哈哈哈哈哈哈！里奇把你忽悠得可真够彻底的！',Speaker=891436,Option='打算为自己辩解吗？',}",
  [164682] = "{id=164682,Text='扯淡！我们恶魔波利就爱干坏事！',Speaker=891436,}",
  [164683] = "{id=164683,Text='恶魔波利变多了我高兴还来不及呢！',Speaker=891436,Option='{果然是你做的吗？,0},{不要拐弯抹角,0}',}",
  [164684] = "{id=164684,Text='嘁……里奇说我用黑暗魔法把天使波利变成了恶魔波利啊……',Speaker=891436,}",
  [164685] = "{id=164685,Text='要是……真是这么回事呢？',Speaker=891436,}",
  [164686] = "{id=164686,Text='（不好！）',}",
  [164687] = "{id=164687,Text='啊哈哈哈哈哈哈哈哈！！！！！',Speaker=891436,}",
  [164688] = "{id=164688,Text='蠢货！想明白了再来找我吧！',Speaker=891436,}",
  [164689] = "{id=164689,Text='（瑞克不见了？）',}",
  [164690] = "{id=164690,Text='（被耍了……明天再想办法打听一下瑞克的事吧。）',}",
  [164691] = "{id=164691,Text='（……似乎被什么注视着。）',}",
  [164692] = "{id=164692,Text='（错觉吗？）',}",
  [164693] = "{id=164693,Text='（…………不是错觉，真的被跟踪了。）',}",
  [164694] = "{id=164694,Text='嗷~~~~~！！',Speaker=891437,Option='你跟踪我？',}",
  [164695] = "{id=164695,Text='没没没没有！在下只是一般通过的路人杰斯哒！',Speaker=891437,Option='{我都看到了,0},{跟踪的理由？,0}',}",
  [164696] = "{id=164696,Text='……在下，听说了！你是里奇委托的那个冒险者，[c][ffff00][PlayerName][-][/c]！',Speaker=891437,Option='所以呢？',}",
  [164697] = "{id=164697,Text='在下也想知道，天使波利变成恶魔波利的真相！',Speaker=891437,}",
  [164698] = "{id=164698,Text='拜托了，请告诉在下！',Speaker=891437,Option='要知道这个干什么',}",
  [164699] = "{id=164699,Text='…………………………',Speaker=891437,Option='装死无用！',}",
  [164700] = "{id=164700,Text='呜呜呜，为什么对在下这么残忍……',Speaker=891437,}",
  [164701] = "{id=164701,Text='…………………………（坚持不肯开口）',Speaker=891437,}",
  [164702] = "{id=164702,Text='（或许找点食物能让它心情好点？）',}",
  [164703] = "{id=164703,Text='……………………这是什么？苹果！',Speaker=891437,}",
  [164704] = "{id=164704,Text='啊~在下舒服多了！',Speaker=891437,}",
  [164705] = "{id=164705,Text='在下精通变身术，对所有的变身方法都很熟悉。',Speaker=891437,}",
  [164706] = "{id=164706,Text='但是，天使波利变成恶魔波利！这种变身术在下可是连听都没听过！',Speaker=891437,}",
  [164707] = "{id=164707,Text='在下无论如何也想知道这种变身是怎么做到哒！',Speaker=891437,Option='{那是变身术吗？,0},{压根就不是变身术,0}',}",
  [164708] = "{id=164708,Text='从本来的形态变成了完全不一样的形态，怎么想都是变身术！在下就是这么认为哒！',Speaker=891437,Option='{（难以吐槽）,0},{你说是就是吧,0}',}",
  [164709] = "{id=164709,Text='所以、所以？为什么天使波利能变成恶魔波利？',Speaker=891437,Option='{暂时不明,2},{或许是黑暗魔法,3}',}",
  [164710] = "{id=164710,Text='啊哒，好失望哒……',Speaker=891437,}",
  [164711] = "{id=164711,Text='魔法？是魔法吗？在下对魔法完全不擅长哒！',Speaker=891437,}",
  [164712] = "{id=164712,Text='啊哒，难道真的不是变身术？在下觉得，事物不能只看表面哒……',Speaker=891437,Option='什么意思？',}",
  [164713] = "{id=164713,Text='就是一句听起来很有哲理的话哒！杰斯一直都想说说看！',Speaker=891437,}",
  [164714] = "{id=164714,Text='啊，瑞克。',Speaker=891437,}",
  [164715] = "{id=164715,Text='（又被耍了……明天还是先去找里奇报告情况吧。）',}",
  [164716] = "{id=164716,Text='正义的冒险者，和瑞克交涉过了吗？情况怎么样？',Speaker=891443,Option='{扑朔迷离,0},{更加复杂,0}',}",
  [164717] = "{id=164717,Text='没想到，还有其他人被牵扯进来啊。你觉得谁更可疑？',Speaker=891443,Option='{或许是瑞克……,0},{或许是杰斯……,0}',}",
  [164718] = "{id=164718,Text='我的看法还是不变。瑞克自己也说了，现在这个事态他高兴还来不及，也就是说——',Speaker=891443,}",
  [164719] = "{id=164719,Text='波利波利~不好哩~！！！',Speaker=891438,}",
  [164720] = "{id=164720,Text='好多波利波利~在南门打起来哩！它们打得好凶哩！',Speaker=891438,}",
  [164721] = "{id=164721,Text='正义的冒险者！我们快去！',Speaker=891443,}",
  [164722] = "{id=164722,Text='（真厉害！）',}",
  [164723] = "{id=164723,Text='冒险者！拦住他们！',Speaker=891443,}",
  [164724] = "{id=164724,Text='幸亏你在这里，多谢。',Speaker=891443,Option='{这没什么,0}，{到底是怎么回事？，0}',}",
  [164725] = "{id=164725,Text='我不知道。这些天使波利好像失去了理智，突然就互相攻击起来——',Speaker=891443,}",
  [164726] = "{id=164726,Text='波利波利——哩——',Speaker=891438,}",
  [164727] = "{id=164727,Text='啊哈哈哈哈哈哈！！！！太绝了！',Speaker=891436,}",
  [164728] = "{id=164728,Text='谢谢你给我们送人啊，里奇！',Speaker=891436,}",
  [164729] = "{id=164729,Text='……事情已经很明显了。',Speaker=891443,}",
  [164730] = "{id=164730,Text='一定就是瑞克搞的鬼。那些波利已经没救了。',Speaker=891443,Option='”没救了“？',}",
  [164731] = "{id=164731,Text='恶魔波利就应当被消灭。正义必将战胜邪恶。这一点我深信不疑。',Speaker=891443,}",
  [164732] = "{id=164732,Text='冒险者，这段时间辛苦你了。接下来，就由我来对付瑞克吧。',Speaker=891443,}",
  [164733] = "{id=164733,Text='（里奇真的打算全部消灭恶魔波利吗？……明天再问问吧。）',}",
  [164734] = "{id=164734,Text='冒险者，能不能拜托你去西门的花园摘取一些传信花来？',Speaker=891443,Option='我明白了',}",
  [164735] = "{id=164735,Text='谢谢！',Speaker=891443,}",
  [164736] = "{id=164736,Text='太好了。现在我可以让莉莉丝向整个米德加尔特的天使波利用传信花发出征召了。',Speaker=891443,Option='征召？',}",
  [164737] = "{id=164737,Text='等集结完毕，我们就会向恶魔波利宣布开战！',Speaker=891443,Option='那些变身的天使波利？',}",
  [164738] = "{id=164738,Text='他们已经成为恶魔波利了，不需要任何特殊对待。',Speaker=891443,}",
  [164739] = "{id=164739,Text='对于邪恶的化身，消灭他们才是最好的解决方法。',Speaker=891443,}",
  [164740] = "{id=164740,Text='冒险者，你还有什么疑问吗？',Speaker=891443,Option='{太激进了吧,2},{还不能确定真相,3}',}",
  [164741] = "{id=164741,Text='你是不是搞错了？我早就告诉过你，天使波利和恶魔波利是互相敌对的。',Speaker=891443,}",
  [164742] = "{id=164742,Text='消灭恶魔波利是我们的天职！',Speaker=891443,}",
  [164743] = "{id=164743,Text='事到如今你还想为瑞克开脱吗？如果不是他搞的鬼，昨天那些天使波利变身后，他为什么会出现？',Speaker=891443,}",
  [164744] = "{id=164744,Text='瑞克的动机也非常充分。如果不是他做的，那还能是谁？',Speaker=891443,}",
  [164745] = "{id=164745,Text='……总之，这件事已经没有你插手的余地了。',Speaker=891443,}",
  [164746] = "{id=164746,Text='请不要阻止我。',Speaker=891443,}",
  [164747] = "{id=164747,Text='波利波利~队长一直都这样哩~',Speaker=891438,}",
  [164748] = "{id=164748,Text='波利波利~队长很严格哩~',Speaker=891438,}",
  [164749] = "{id=164749,Text='总是把“正义战胜邪恶！”挂在嘴边哩~其实我们也没有那么想找恶魔波利麻烦哩~',Speaker=891438,Option='你们不是互相敌对吗？',}",
  [164750] = "{id=164750,Text='虽然是会看恶魔波利不太爽哩~但是看不到也就没那么碍眼~',Speaker=891438,}",
  [164751] = "{id=164751,Text='队长提到恶魔波利就想干仗哩~在这方面他简直比瑞克还要好斗哩~',Speaker=891438,}",
  [164752] = "{id=164752,Text='跟着队长，莉莉丝也很辛苦哩~',Speaker=891438,}",
  [164753] = "{id=164753,Text='（看来，要阻止波利们的战争，只有尽快查明真相才行。）',}",
  [164754] = "{id=164754,Text='（到底谁才是把天使波利变成恶魔波利的幕后黑手呢？是瑞克，杰斯，还是……）',}",
  [164755] = "{id=164755,Text='（…………里奇…………？）',}",
  [164757] = "{id=164757,Text='冒险者，啰哈！欢迎来到游园会~',Speaker=893004,Emoji=9,}",
  [164758] = "{id=164758,Text='咱们这次，可是准备了超——级——多的游戏和奖励，还请务必去体验一下！',Speaker=893004,}",
  [164759] = "{id=164759,Text='这回还来了很多特殊的客人，也许你们可以结个伴一起玩哦~',Speaker=893004,}",
  [164760] = "{id=164760,Text='啰哈！怎么样呀冒险者，玩得开心吗？',Speaker=893004,Option='{很开心,0},{还行吧,0}',}",
  [164761] = "{id=164761,Text='无论如何，都希望你度过快乐的一天~期待下次还能见到你哦！',Speaker=893004,}",
  [164764] = "{id=164764,Text='这不是[c][ffff00][PlayerName][-][/c]吗？你也来参加游园会？',Speaker=893005,Option='你怎么在这！',Emoji=9,}",
  [164765] = "{id=164765,Text='嘿嘿，这不是听说克魔岛有特别活动，我想来放松放松嘛。',Speaker=893005,}",
  [164766] = "{id=164766,Text='话说，我正在挑战游园会的[c][ffff00]神秘项目[-][/c]，有没有兴趣一起呀？',Speaker=893005,Option='{说来听听,4},{还是算了,3}',}",
  [164767] = "{id=164767,Text='嘿！你怎么怂了？平时精炼的时候可不是这样！',Speaker=893005,}",
  [164768] = "{id=164768,Text='咳咳咳——其实是有个“你心中的克魔岛”主题摄影大赛啦。',Speaker=893005,}",
  [164769] = "{id=164769,Text='在游园会拍摄克魔岛的照片，就能得到组委会的特别奖励！',Speaker=893005,}",
  [164770] = "{id=164770,Text='不和你多说啦，我要先去拍照了！',Speaker=893005,}",
  [164771] = "{id=164771,Text='（说起克魔岛，不得不提到这里的海洋风光了……拍一张照片纪念吧。）',}",
  [164772] = "{id=164772,Text='没想到冒险者拍起照片来有模有样的嘛。',Speaker=893006,Emoji=2,}",
  [164773] = "{id=164773,Text='照片给我吧~这是你参加游园会摄影大赛的奖励。记得下次也要来玩哦！',Speaker=893006,}",
  [164774] = "{id=164774,Text='（克魔岛上这些热情又充满活力的岛民，也是一道不错的风景……吧？）',}",
  [164775] = "{id=164775,Text='嚯嚯嚯，真没想到冒险者如此有眼光！这一定是张绝妙的照片！',Speaker=893009,}",
  [164776] = "{id=164776,Text='来，这是你参加游园会摄影大赛的奖励。',Speaker=893009,}",
  [164777] = "{id=164777,Text='（克魔岛的烧烤可是一绝。就以它作为拍摄题材吧。）',}",
  [164778] = "{id=164778,Text='嘎，阿嘎嘎嘎嘎嘎，嘎嘎嘎嘎嘎嘎嘎嘎嘎！',Speaker=893008,Option='你在说什么？？',}",
  [164779] = "{id=164779,Text='（啤酒企鹅夺走了照片！它把一堆奖品塞了过来，然后一溜烟地跑开了……）',}",
  [164785] = "{id=164785,Text='[c][ffff00][PlayerName][-][/c]——是我，是我呀，赫蒂！',Speaker=893010,Option='好久不见！',Emoji=2,}",
  [164786] = "{id=164786,Text='能在这里见到你，真是太好啦！你也是来参加游园会的吗？',Speaker=893010,Option='是呀',}",
  [164787] = "{id=164787,Text='那这样的话……我、我能不能请你帮我一个忙！',Speaker=893010,}",
  [164788] = "{id=164788,Text='这个射击游戏……我尝试打了好几次，都击不中一个礼物……',Speaker=893010,Emoji=15,}",
  [164789] = "{id=164789,Text='可以请你帮我打一次吗？打不打中都可以！！',Speaker=893010,Option='{交给我吧,2},{我还有事,3}',}",
  [164804] = "{id=164804,Text='哟。',Speaker=893011,Option='{布布！,0},{布兰迪斯！,0}',}",
  [164805] = "{id=164805,Text='好久不见，[c][ffff00][PlayerName][-][/c]。',Speaker=893011,Emoji=9,}",
  [164806] = "{id=164806,Text='最近大祭司的工作轻松不少，我就来克魔岛转转。',Speaker=893011,}",
  [164807] = "{id=164807,Text='要来一起修行吗？',Speaker=893011,Option='修行？',}",
  [164808] = "{id=164808,Text='嗯……严格一点的称呼，应该是“游戏”？',Speaker=893011,}",
  [164809] = "{id=164809,Text='看到地上了吗？有个未完成的法阵。',Speaker=893011,}",
  [164810] = "{id=164810,Text='据说只要完成这个法阵，就可以得到游园会的超级大奖。',Speaker=893011,Option='{让我试试,2},{我还有事,3}',}",
  [164811] = "{id=164811,Text='……看来是时间让我们变得生疏了。',Speaker=893011,}",
  [164812] = "{id=164812,Text='好。只要按照顺序绘制法阵就可以。',Speaker=893011,}",
  [164813] = "{id=164813,Text='……怎么还是这么笨。冷静点，再来一次吧。',Speaker=893011,Option='{再来！,2},{算了,3}',}",
  [164814] = "{id=164814,Text='呼噜~呼噜噜噜噜~',Speaker=893013,}",
  [164815] = "{id=164815,Text='小……野……猪……气球？',Speaker=893011,}",
  [164816] = "{id=164816,Text='这就是克魔岛的超级大奖？？真是奇特的品味……',Speaker=893011,}",
  [164817] = "{id=164817,Text='……为什么这里会有风栖幼龙啊！！',Speaker=893011,}",
  [164818] = "{id=164818,Text='咕咕~咕啾~布布大祭司，巨人密米尔让我来向您问候~',Speaker=893012,}",
  [164819] = "{id=164819,Text='他说——”别摸鱼了布兰迪斯！！快回艾卡拉奇干活去！“',Speaker=893012,}",
  [164820] = "{id=164820,Text='呃呜……',Speaker=893012,}",
  [164821] = "{id=164821,Text='（淡淡地）刚才什么都没发生，你也什么都没看见。',Speaker=893011,}",
  [164822] = "{id=164822,Text='下次再见，[c][ffff00][PlayerName][-][/c]。',Speaker=893011,}",
  [164823] = "{id=164823,Text='哎哟喂，来来来，快过来，就是你！',Speaker=893014,Emoji=9,}",
  [164824] = "{id=164824,Text='一看你穿的就不是咱们克魔岛的人，是来参加游园会的吧？',Speaker=893014,}",
  [164825] = "{id=164825,Text='有没有兴趣试试“克魔岛三千问”呀~',Speaker=893014,}",
  [164826] = "{id=164826,Text='别慌别慌，不是真的有三千个问题！只是一个笼统的数字啦。',Speaker=893014,}",
  [164827] = "{id=164827,Text='我会问你关于克魔岛的一些简单问题，答对了就可以得到游园会的的奖励啦。',Speaker=893014,Option='{熟悉的套路,2},{没意思,3}',}",
  [164828] = "{id=164828,Text='哼，你怎么这样说话呢！',Speaker=893014,Emoji=12,}",
  [164829] = "{id=164829,Text='准备好了就开始啦！',Speaker=893014,}",
  [164830] = "{id=164830,Text='请听题——哪种克魔岛的魔物嘴里叼着吸管？',Speaker=893014,Option='{奈吉鸟,32},{啤酒企鹅,33},{卡拉蟹,32}',}",
  [164831] = "{id=164831,Text='噢哟，答对了！不愧是你呀！',Speaker=893014,Emoji=9,}",
  [164832] = "{id=164832,Text='呃……要不你再想想？',Speaker=893014,Emoji=11,}",
  [164833] = "{id=164833,Text='请听题——卡拉蟹是什么颜色的？',Speaker=893014,Option='{红,43},{金,42},{蓝,42},{黑,42}',}",
  [164834] = "{id=164834,Text='噢哟，答对了！不愧是你呀！',Speaker=893014,Emoji=9,}",
  [164835] = "{id=164835,Text='呃……要不你再想想？',Speaker=893014,Emoji=11,}",
  [164836] = "{id=164836,Text='请听题——以下哪种魔物的脑袋最多？',Speaker=893014,Option='{矿石精,52},{大脚龙虾,52},{塔奥群卡,53}',}",
  [164837] = "{id=164837,Text='噢哟，答对了！不愧是你呀！',Speaker=893014,Emoji=9,}",
  [164838] = "{id=164838,Text='呃……要不你再想想？',Speaker=893014,Emoji=11,}",
  [164841] = "{id=164841,Text='…是你。好久不见。',Speaker=893016,Option='{古恩？？,0},{你怎么在这！,0}',}",
  [164842] = "{id=164842,Text='神树沉光的力量稳定了。乌洛闹着要出来玩，我就陪他一起了。',Speaker=893016,Emoji=14,}",
  [164843] = "{id=164843,Text='……放心。在这里的，只是我们的一缕意识。不会对洛阳有影响。',Speaker=893016,}",
  [164844] = "{id=164844,Text='只是现在，我找不到乌洛了。',Speaker=893016,}",
  [164845] = "{id=164845,Text='你能帮我把他带回来么？',Speaker=893016,Option='{没问题,2},{我还有事,3}',}",
  [164846] = "{id=164846,Text='好。我不强求。',Speaker=893016,}",
  [164847] = "{id=164847,Text='哎呀……[c][ffff00][PlayerName][-][/c]，是古恩让你来抓我的吧？',Speaker=893015,Option='你快回去',}",
  [164848] = "{id=164848,Text='别急。看我发现了什么好东西。',Speaker=893015,}",
  [164849] = "{id=164849,Text='——是游园会的工作人员藏起来的礼物！别告诉古恩我在这，它就归你啦。',Speaker=893015,Emoji=5,}",
  [164852] = "{id=164852,Text='哎呀咪，是[c][ffff00][PlayerName][-][/c]！我们又见面啦！！',Speaker=893017,Option='{艾比！,0},{喵喵！,0}',Emoji=17,}",
  [164853] = "{id=164853,Text='咪呀！！快看，我把嘉莉带出来玩啦！！',Speaker=893017,}",
  [164854] = "{id=164854,Text='森林的诅咒正在变淡，嘉莉现在可以离开森林一段时间！',Speaker=893017,}",
  [164855] = "{id=164855,Text='她、她现在是我的小猫咪哦！你可不要多看她咪！',Speaker=893017,Option='知道了喵~',}",
  [164856] = "{id=164856,Text='……笨蛋。',Speaker=893018,}",
