  [164871] = "{id=164871,Text='……一样啊。',Speaker=893018,Emoji=14,}",
  [164872] = "{id=164872,Text='呃呜……看来还是没能让嘉莉喵喵叫……只能下次再说了！',Speaker=893017,Emoji=42,}",
  [164873] = "{id=164873,Text='哎呀咪！怎么办、怎么办咪！',Speaker=893017,Option='你怎么了？',Emoji=15,}",
  [164874] = "{id=164874,Text='咪呀！！是、是没见过的冒险者！',Speaker=893017,}",
  [164875] = "{id=164875,Text='我是喜欢旅行的艾比……和小女巫嘉莉一起来游园会玩。',Speaker=893017,}",
  [164876] = "{id=164876,Text='她、她是我的小猫咪哦！你可不要多看她咪！',Speaker=893017,Option='知道了~',}",
  [164877] = "{id=164877,Text='……笨蛋。',Speaker=893018,}",
  [164878] = "{id=164878,Text='（小声）咪呀……冒险者，可不可以请你帮我一个忙？',Speaker=893017,Option='嗯？',}",
  [164879] = "{id=164879,Text='嘉莉是很特别的小猫咪……她、她不喜欢喵喵叫！',Speaker=893017,}",
  [164880] = "{id=164880,Text='但是嘉莉说，只要猜拳赢了她，她就喵一声！',Speaker=893017,}",
  [164881] = "{id=164881,Text='拜托拜托咪！！你去和嘉莉猜拳好不好！我想、我想听她喵喵叫！',Speaker=893017,Option='{好吧,12},{我还有事……,11}',Emoji=42,}",
  [164882] = "{id=164882,Text='呜呜呜咪……',Speaker=893017,}",
  [164883] = "{id=164883,Text='……开始？',Speaker=893018,Option='{拳头,40},{剪刀,50},{布,60}',}",
  [164884] = "{id=164884,Text='……………………',Speaker=893018,}",
  [164885] = "{id=164885,Text='……………………………………',Speaker=893018,Emoji=14,}",
  [164886] = "{id=164886,Text='嘉、嘉莉……',Speaker=893017,}",
