  [164891] = "{id=164891,Text='呃呜……看来还是没能让嘉莉喵喵叫……只能下次再说了！',Speaker=893017,Emoji=42,}",
  [164892] = "{id=164892,Text='……一样啊。',Speaker=893018,Emoji=14,}",
  [164893] = "{id=164893,Text='呃呜……看来还是没能让嘉莉喵喵叫……只能下次再说了！',Speaker=893017,Emoji=42,}",
  [164895] = "{id=164895,Text='呼、呼、呼——哇喵。逮到了一位落单的客人。要来占卜吗？',Speaker=893019,Option='准吗？',}",
  [164896] = "{id=164896,Text='当然。也许你见过另一只会占卜的猫。她叫诺恩·薇，是我的小姨妈。',Speaker=893019,Emoji=5,}",
  [164897] = "{id=164897,Text='我的占卜，就是跟小姨妈学的喵……要试试吗？',Speaker=893019,Option='{想要占卜,3},{还是算了,2}',}",
  [164898] = "{id=164898,Text='告诉我，你觉得眼前的这片黑色是什么？',Speaker=893019,Option='{没有星星的夜晚,31},{打翻的可乐,32},{关灯的房间,33},{我不知道,34},{眼前的黑不是黑,35}',}",
  [164899] = "{id=164899,Text='喵~闭上眼睛，你看到了没有星星的夜晚。',Speaker=893019,}",
  [164900] = "{id=164900,Text='——那么睁开眼，啪！你的眼里就都是星星喵！',Speaker=893019,Emoji=2,}",
  [164901] = "{id=164901,Text='喵呼。好浪漫。我很喜欢浪漫的家伙。',Speaker=893019,}",
  [164902] = "{id=164902,Text='你的一天都会被幸运星所环绕。',Speaker=893019,}",
  [164903] = "{id=164903,Text='可乐？',Speaker=893019,}",
  [164904] = "{id=164904,Text='可乐？？',Speaker=893019,}",
  [164905] = "{id=164905,Text='可乐？？？嗯…………',Speaker=893019,}",
  [164906] = "{id=164906,Text='喵呼。看得出来，你很喜欢吃吃吃喝喝。是个悠哉的家伙。',Speaker=893019,}",
  [164907] = "{id=164907,Text='下次要一起交流食谱吗？',Speaker=893019,Emoji=2,}",
  [164908] = "{id=164908,Text='关灯的房间……哦。好写实、好正常的回答喵！',Speaker=893019,}",
  [164909] = "{id=164909,Text='喵呼~你一定是个严谨、稳重又认真的家伙！',Speaker=893019,}",
  [164910] = "{id=164910,Text='我不擅长和你相处。但是我尊敬你喵。',Speaker=893019,Emoji=15,}",
  [164911] = "{id=164911,Text='你会收获充实而美满的一天~',Speaker=893019,}",
  [164912] = "{id=164912,Text='嗯……不知道喵？',Speaker=893019,}",
  [164913] = "{id=164913,Text='嗯嗯……你很诚实。虽然这个答案不怎么样，但是我欣赏你的诚实。',Speaker=893019,Emoji=14,}",
  [164914] = "{id=164914,Text='祝你度过顺利的一天。',Speaker=893019,}",
  [164915] = "{id=164915,Text='喵喵喵？眼前的黑不是黑……？',Speaker=893019,}",
  [164916] = "{id=164916,Text='啊我懂了。你在用一个哲学问题反问我的疑惑。',Speaker=893019,Emoji=11,}",
  [164917] = "{id=164917,Text='眼前的黑不是真正纯粹的黑，它也可能是许多个颜色混在了一起。',Speaker=893019,}",
  [164918] = "{id=164918,Text='呼——喵。没想到你竟然有一颗如此有深度的脑壳。我要开始崇拜你了。',Speaker=893019,}",
  [164919] = "{id=164919,Text='不如转行做占卜师吧。你会比我的小姨妈还出色的！',Speaker=893019,}",
  [164931] = "{id=164931,Text='嘿，冒险者。我是喜欢夏天的雪人~想尝尝游园会的特供甜品吗？',Speaker=893020,Option='{想要！,4},{没兴趣,3}',}",
  [164932] = "{id=164932,Text='嘁~真无聊~',Speaker=893020,}",
  [164933] = "{id=164933,Text='好耶！这可是克魔岛特供甜品！看到我面前的5只箱子了吗？',Speaker=893020,}",
  [164934] = "{id=164934,Text='也许你可以找找看甜品藏在了哪里！',Speaker=893020,Emoji=17,}",
  [164959] = "{id=164959,Text='喵喵？你想要挑战多兰大侦探波罗先生的推理吗？',Speaker=893035,Option='{正是这样,3},{我没信心……,2}',}",
  [164960] = "{id=164960,Text='好喵！终于等到了喵！',Speaker=893035,}",
  [164961] = "{id=164961,Text='喵~没信心的不止你一个，不用自卑~',Speaker=893035,}",
  [164962] = "{id=164962,Text='在正式的推理之前，我要考考你对本案的基本情况是否熟悉喵~',Speaker=893035,}",
  [164963] = "{id=164963,Text='不行不行！基本案情都搞错了喵，没资格挑战大侦探波罗先生！',Speaker=893035,}",
  [164964] = "{id=164964,Text='很好！看来你并不是个划水的假冒侦探！',Speaker=893035,Emoji=20,}",
  [164965] = "{id=164965,Text='看起来你也是对这个案件做过详细调查的喵！',Speaker=893035,}",
  [164966] = "{id=164966,Text='那么，把你的推理结果展现出来吧！你认为本案的真正犯人是——？\n[c][ffff00]最终的选择会影响获得的奖励哦，请慎重选择哦！[-][/c]',Speaker=893035,Option='{恶魔波利瑞克,6},{狸猫杰斯,6},{天使波利里奇,7}',}",
  [164967] = "{id=164967,Text='确认无误吗？',Speaker=893035,Option='{是的,8},{等等，让我在想想,9}',}",
  [164968] = "{id=164968,Text='很有信心喵！那么……',Speaker=893035,Emoji=14,}",
  [164969] = "{id=164969,Text='侦探需要谨言慎行！好好想想再发表结论喵！',Speaker=893035,}",
  [164970] = "{id=164970,Text='大侦探波罗先生宣布，真正的犯人是——',Speaker=893035,}",
  [164971] = "{id=164971,Text='[c][ffff00]天使波利里奇[-][/c]喵！',Speaker=893035,}",
  [164972] = "{id=164972,Text='你推理得不错！里奇本身具有双重人格喵！平时是个超严格的正义队长，偶尔会变身邪恶的暴力战斗狂！',Speaker=893035,}",
  [164973] = "{id=164973,Text='喵~真遗憾你推理完全错了呢！',Speaker=893035,}",
  [164974] = "{id=164974,Text='真正的原因是，里奇本身是个具有双重人格的天使波利喵！平时是个超严格的正义队长，偶尔会变身邪恶的暴力战斗狂！',Speaker=893035,}",
  [164975] = "{id=164975,Text='以前里奇的邪恶人格只是会欺负天使波利，现在升级到了会用黑暗魔法把它们变成恶魔波利！瑞克对此超开心的喵！',Speaker=893035,}",
  [164976] = "{id=164976,Text='里奇自己也开始意识到了这点，却找不到方法抑制邪恶人格喵……',Speaker=893035,}",
  [164977] = "{id=164977,Text='不过，大侦探波罗先生已经把真相告诉了莉莉丝！现在天使波利们暂时把里奇和其他天使波利隔离开来，这件事会告一段落喵~',Speaker=893035,}",
  [164978] = "{id=164978,Text='真相就是这样！喵！',Speaker=893035,}",
  [164979] = "{id=164979,Text='确认无误吗？',Speaker=893035,Option='{是的,10},{等等，让我在想想,11}',}",
  [164981] = "{id=164981,Text='喵~没信心的不止你一个，不用自卑~',Speaker=893035,}",
  [164982] = "{id=164982,Text='喵呼，闭上眼睛。',Speaker=893019,}",
  [164990] = "{id=164990,Text='波利波利~队长今天好像去吉芬了哩~',Speaker=891454,}",
  [164991] = "{id=164991,Text='莉莉丝也不知道他为啥要去哩~',Speaker=891454,}",
  [164992] = "{id=164992,Text='[c][ffff00][PlayerName][-][/c]要找队长？莉莉丝可以送你去哦~',Speaker=891454,Option='{好啊，麻烦你,2},{不必了，谢谢,3}',}",
  [164993] = "{id=164993,Text='波利波利~',Speaker=891454,}",
  [164995] = "{id=164995,Text='（是在看书啊。）',}",
  [164997] = "{id=164997,Text='（今天就到这里吧。）',}",
  [164998] = "{id=164998,Text='（今天瑞克不在这里……）',}",
  [164999] = "{id=164999,Text='（在附近找找看吧。）',}",
  [165000] = "{id=165000,Text='做个恶魔很爽！',Speaker=891452,}",
  [165001] = "{id=165001,Text='想干嘛就干嘛！什么规矩都不用理！',Speaker=891452,}",
  [165002] = "{id=165002,Text='嘁……蠢货连话都听不懂！',Speaker=891452,}",
  [165003] = "{id=165003,Text='（瑞克在诱惑别的波利加入恶魔波利的队伍。）',}",
  [165004] = "{id=165004,Text='（今天就到这里吧。）',}",
  [165005] = "{id=165005,Text='（瑞克今天没用黑暗魔法把对方变成恶魔波利呢……）',}",
  [165006] = "{id=165006,Text='（啊，杰斯！）',}",
  [165007] = "{id=165007,Text='（那是？）',}",
  [165008] = "{id=165008,Text='（哇！是摩根……而且是女装……！）',}",
  [165009] = "{id=165009,Text='前辈前辈！变身非常成功哒！',Speaker=891453,}",
  [165010] = "{id=165010,Text='在下差点认不出来哒！',Speaker=891453,}",
  [165011] = "{id=165011,Text='哎~死相啦！对淑女这么露骨的追捧是不行的！',Speaker=891459,}",
  [165012] = "{id=165012,Text='那、那应该怎么做呢？请务必教诲在下！',Speaker=891453,}",
  [165013] = "{id=165013,Text='（杰斯……真的很喜欢变身术啊……）',}",
  [165014] = "{id=165014,Text='（今天就到这里吧。杰斯似乎没什么可疑的地方。）',}",
  [165015] = "{id=165015,Text='（找到了。）',}",
  [165016] = "{id=165016,Text='（他们好像在讨论什么问题……）',}",
  [165017] = "{id=165017,Text='拉赫的传说中没有这种事情吗？',Speaker=891451,}",
  [165018] = "{id=165018,Text='分裂灵魂？不，至少我从来没有听到过。',Speaker=891458,}",
  [165019] = "{id=165019,Text='这样啊……',Speaker=891451,}",
  [165020] = "{id=165020,Text='难道你身边有人的灵魂分裂了吗？',Speaker=891458,}",
  [165021] = "{id=165021,Text='不是，我就是随便问问……',Speaker=891451,}",
  [165022] = "{id=165022,Text='（灵魂分裂？里奇为什么会问这个问题？）',}",
  [165023] = "{id=165023,Text='（他不是在考虑天使波利变成恶魔波利的事情吗？）',}",
  [165024] = "{id=165024,Text='（今天就到这里吧。）',}",
  [165025] = "{id=165025,Text='（里奇的行为有点让人摸不着头脑。）',}",
  [165026] = "{id=165026,Text='（和瑞克在一起的是哥布灵王吗？！）',}",
  [165027] = "{id=165027,Text='那个脑瓜裂开的蠢货居然还……你在听吗！',Speaker=891452,}",
  [165028] = "{id=165028,Text='哇哈哈哈哈哈！！',Speaker=891460,}",
  [165029] = "{id=165029,Text='有完没完？会变身了不起？谁不会啊？',Speaker=891452,}",
  [165030] = "{id=165030,Text='（今天就到这里吧。）',}",
  [165031] = "{id=165031,Text='（瑞克提到的脑瓜裂开的蠢货是谁？）',}",
  [165032] = "{id=165032,Text='（哇，卡鲁船长~~）',}",
  [165033] = "{id=165033,Text='拜托了！这是在下一生的请求！',Speaker=891453,}",
  [165034] = "{id=165034,Text='不要硬来啊……',Speaker=1098,}",
  [165035] = "{id=165035,Text='可是，前辈就在天津町等着在下~',Speaker=891453,}",
  [165036] = "{id=165036,Text='那和我无关！',Speaker=1098,}",
  [165037] = "{id=165037,Text='（杰斯是想去见摩根啊。）',}",
  [165038] = "{id=165038,Text='（今天就到这里吧。……杰斯或许真的和那件事没有关系。）',}",
  [165039] = "{id=165039,Text='哇~[c][ffff00][PlayerName][-][/c]哩~',Speaker=891454,}",
  [165040] = "{id=165040,Text='队长杀气腾腾地跑去梦罗克哩~',Speaker=891454,Option='{去干什么？,0},{杀气腾腾？,0}',}",
  [165041] = "{id=165041,Text='波利波利~队长偶尔也会去梦罗克的金字塔里清除魔物，他自己说是练练手~',Speaker=891454,}",
  [165042] = "{id=165042,Text='不过今天他好像比平时更杀气腾腾哩~莉莉丝有点担心会不会出事哩~',Speaker=891454,}",
  [165043] = "{id=165043,Text='莉莉丝送你去金字塔，帮忙看看队长情况，好不好哩？',Speaker=891454,Option='{没问题！,3},自行前往,2}',}",
  [165044] = "{id=165044,Text='波利波利~',Speaker=891454,}",
  [165046] = "{id=165046,Text='（里奇……被包围了！）',}",
  [165047] = "{id=165047,Text='（魔物层出不穷！要去帮忙吗？）',}",
  [165048] = "{id=165048,Text='（好像用不着我出场……）',}",
  [165049] = "{id=165049,Text='（三人的行踪收集得足够了。）',}",
  [165050] = "{id=165050,Text='（仔细想一想，这其中最可疑的果然是……）',}",
  [165051] = "{id=165051,Text='每参与一个小游戏，就会得到一颗[c][ffff00]欢乐豆[-][/c]！只要收集到[c][ffff00]六颗[-][/c]欢乐豆，就可以找我兑换奖励啦！',Speaker=893004,Emoji=9,}",
  [165052] = "{id=165052,Text='祝你游玩愉快~',Speaker=893004,Emoji=9,}",
  [165053] = "{id=165053,Text='好了喵！我们来开始第二个问题：平时会偏好什么类型的音乐喵？',Speaker=893019,Option='{甜蜜小情歌,41},{古典纯音乐,42},{悲伤抒情曲,43},{我不喜欢音乐,44}',}",
  [165054] = "{id=165054,Text='喵呼。甜甜的、腻腻的、美滋滋的小情歌喔！',Speaker=893019,}",
  [165055] = "{id=165055,Text='（歪着头）看起来确实是你的类型喵。',Speaker=893019,}",
  [165056] = "{id=165056,Text='放心吧。世界上一定会有首小情歌，是只为你而唱的。',Speaker=893019,Emoji=9,}",
  [165057] = "{id=165057,Text='嗯……嗯。纯音乐……我知道了喵。',Speaker=893019,}",
  [165058] = "{id=165058,Text='你的心灵，平静、温和……像雨后的湖泊一样喵。',Speaker=893019,}",
  [165059] = "{id=165059,Text='有空的话，还请多和我聊聊天。我喜欢你这样的家伙喔。',Speaker=893019,Emoji=2,}",
  [165060] = "{id=165060,Text='……那个，总是听悲伤的歌曲，可能整个脑袋都会不开心的喔？',Speaker=893019,}",
  [165061] = "{id=165061,Text='我知道的……保持开心是一件很难做到的事情喵……',Speaker=893019,}",
  [165062] = "{id=165062,Text='但是、但是喵……我永远祝福你，远离所有的不开心喔。',Speaker=893019,Emoji=26,}",
  [165063] = "{id=165063,Text='喔哟喵！不喜欢音乐！看来你非常极端！',Speaker=893019,}",
  [165064] = "{id=165064,Text='——我喜欢！',Speaker=893019,}",
  [165065] = "{id=165065,Text='感谢你保持自己的态度。这是很可贵的品德喵！',Speaker=893019,Emoji=9,}",
  [165066] = "{id=165066,Text='嗯嗯，现在只剩下最后一个问题了。你喜欢什么样的房屋风格？',Speaker=893019,Option='{极简设计,51},{复古华丽,52},{都可以,53},{都不喜欢,54}',}",
  [165067] = "{id=165067,Text='喵呼。喜欢简单干净的风格喵？',Speaker=893019,}",
  [165068] = "{id=165068,Text='你一定是个做事很干练、很有效率的好家伙。',Speaker=893019,}",
  [165069] = "{id=165069,Text='嗯喵……应该也不喜欢花里胡哨的装饰，和所有复杂的条条框框。',Speaker=893019,}",
  [165070] = "{id=165070,Text='喵呼！复古华丽吗？你好富贵喵！',Speaker=893019,}",
  [165071] = "{id=165071,Text='下次也许我可以为你推荐一些不错的古董商……？',Speaker=893019,}",
  [165072] = "{id=165072,Text='出现了，什么都可以的客人！',Speaker=893019,}",
  [165073] = "{id=165073,Text='我很喜欢什么都可以的客人，但是有时候……随便也是一种为难喵。',Speaker=893019,}",
  [165074] = "{id=165074,Text='嗯、嗯……你真是一点面子也不给我喵。',Speaker=893019,Emoji=9,}",
  [165075] = "{id=165075,Text='不过没关系，我很喜欢你坦诚的这一面。',Speaker=893019,}",
  [165076] = "{id=165076,Text='喵呼呼。那么，今天的占卜就到此结束了。',Speaker=893019,}",
  [165077] = "{id=165077,Text='谢谢你的参与喵！这是奖励，请收好喔！',Speaker=893019,}",
  [165081] = "{id=165081,Text='呼哈呼哈~',Speaker=893000,Option='你好？',}",
  [165082] = "{id=165082,Text='呼哈呼哈~',Speaker=893000,Option='…',}",
  [165083] = "{id=165083,Text='走开走开，别打扰我做运动。',Speaker=893000,}",
  [165084] = "{id=165084,Text='你是？我是刚来波利岛的居民~',Speaker=893001,Option='（感觉问错人了）',}",
  [165085] = "{id=165085,Text='我，还是第一次来到这里呢！',Speaker=893001,Option='…',}",
  [165086] = "{id=165086,Text='好美啊！',Speaker=893001,}",
  [165099] = "{id=165099,Text='你来了侦探！这里是今天的调查币，使用后可以选择任意一个调查状去对相关人员进行调查问话，每个人只能问一次哦！',Speaker=893042,}",
  [165170] = "{id=165170,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893044,}",
  [165171] = "{id=165171,Text='你是不是很好奇为什么我又出现在了普隆德拉？',Speaker=893044,Emoji=5,}",
  [165172] = "{id=165172,Text='哈~还不是因为这儿的单身实在太多，我需要你来帮我教教他们怎么样去表白~',Speaker=893044,Option='我要怎么做？',}",
  [165173] = "{id=165173,Text='我这里有不少热心人的[c][ffff00]「暖心情话」[-][/c]可以帮助到更多的冒险者。只要你愿意将[c][ffff00]「暖心情话」[-][/c]传递出去，想必就会有更多的人脱单啦！',Speaker=893044,}",
  [165176] = "{id=165176,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「真爱告白礼盒」x1）[-][/c]\n那么，今天需要分享出去的[c][ffff00]「暖心情话」[-][/c]是：',Speaker=893044,}",
  [165177] = "{id=165177,Text='听说最近某个神秘的东方国度中的情侣都在庆祝些什么！',Speaker=893044,}",
  [165178] = "{id=165178,Text='其实只要爱对了人，每一天都是值得珍惜的节日~',Speaker=893044,}",
  [165179] = "{id=165179,Text='[c][ffff00][PlayerName][-][/c],也请接受我对你的爱吧~',Speaker=893044,Option='{谢谢,2},{拒绝接受,3}',}",
  [165180] = "{id=165180,Text='两个人之间的关系需要双方的付出哦~让我们来模拟一下这样的相处方式吧~',Speaker=893046,}",
  [165181] = "{id=165181,Text='你送我礼物的话，我也会回馈给你礼物哦~这就是礼尚往来~记得每天都来找我哦~',Speaker=893046,}",
  [165183] = "{id=165183,Text='谁说占卜都是些骗人的玩意，在本喵这占卜，可是能实实在在的有所收获的喵~',Speaker=893045,}",
  [165184] = "{id=165184,Text='所以，记得每天都来找本喵哦~',Speaker=893045,}",
  [165205] = "{id=165205,Text='你就是里奇队长雇来的侦探吗？你找到线索了吗？我可是每天都可以找到线索的哦！',Speaker=893048,Option='什么线索？',}",
  [165206] = "{id=165206,Text='本来不想给你看的，算啦！解决事情要紧，给你看看今天我获得的线索碎片吧！',Speaker=893048,}",
  [165207] = "{id=165207,Text='想要去「恋爱圣地」打卡拍照吗？',Speaker=891469,Option='{是的,2},{不想去,3}',}",
  [165208] = "{id=165208,Text='选择「恋爱圣地」：',Speaker=891469,Option='{花海,10},{水族馆,20},{摩天轮,30},{蜜悦之家,40},{落日浅滩,50},{夜莺剧院,60},{离开,80}',}",
  [165209] = "{id=165209,Text='准备出发吧！',Speaker=891469,Option='{出发！,0},{稍等一下,80}',}",
  [165210] = "{id=165210,Text='哎…菲尔也好想要去「恋爱圣地」打卡拍照，谁能来帮我拍照呢？',Speaker=891469,Option='{我可以,2},{稍后再来,3}',Emoji=5,}",
  [165211] = "{id=165211,Text='你可得把我拍得好看一点哦！那么，咱们今天就把所有的恋爱圣地都拍一遍吧！',Speaker=891469,Option='所有？！',}",
  [165212] = "{id=165212,Text='姑且饶了你吧！毕竟打卡恋爱圣地，也要讲究劳逸结合！今天，我想要打卡「最有人气」的恋爱圣地——花海！',Speaker=891469,}",
  [165213] = "{id=165213,Text='现在就出发吧！',Speaker=891469,}",
  [165214] = "{id=165214,Text='拍得还算马马虎虎，作为奖励，这份小礼物就给你了！',Speaker=891470,}",
  [165215] = "{id=165215,Text='如果有想要去恋爱圣地打卡的冒险者，记得来普隆德拉广场找我哦~',Speaker=891470,Emoji=9,}",
  [165216] = "{id=165216,Text='今天我最想去的恋爱圣地是——水族馆！在水族馆拍摄可是很考验技术的哦，做好准备出发了吗？',Speaker=891469,Option='{我可以,2},{稍后再来,3}',}",
  [165217] = "{id=165217,Text='那么，咱们现在就出发吧！',Speaker=891469,}",
  [165218] = "{id=165218,Text='比起昨天，稍微有所进步，作为奖励，这份小礼物就给你了！',Speaker=891470,}",
  [165219] = "{id=165219,Text='如果有想要去恋爱圣地打卡的冒险者，记得来普隆德拉广场找我哦~',Speaker=891470,Emoji=9,}",
  [165220] = "{id=165220,Text='我昨天看到一张冒险者与摩天轮的合影，拍得好漂亮！我也想要拥有！',Speaker=891469,Option='{我可以,2},{稍后再来,3}',}",
  [165221] = "{id=165221,Text='要拍得比我看到的那张还要好哦！',Speaker=891469,}",
  [165224] = "{id=165224,Text='说到恋爱圣地，怎么能少了蜜月之家呢？出发去打卡吧！',Speaker=891469,Option='{我可以,2},{稍后再来,3}',}",
  [165225] = "{id=165225,Text='快点走吧。',Speaker=891469,}",
  [165226] = "{id=165226,Text='果然在蜜月之家怎么拍都好看，作为奖励，这份小礼物就给你了！',Speaker=891470,}",
  [165227] = "{id=165227,Text='如果有想要去恋爱圣地打卡的冒险者，记得来普隆德拉广场找我哦~',Speaker=891470,Emoji=9,}",
  [165228] = "{id=165228,Text='你知道法纳族和毒牙族最喜爱的恋爱圣地是哪里吗？',Speaker=891469,Option='{不知道,2},{稍后再来,3}',}",
  [165229] = "{id=165229,Text='你怎么这都不知道，是——落日浅滩！跟我一起去看落日吧！',Speaker=891469,}",
  [165230] = "{id=165230,Text='这照片逆光了呀！算了算了，作为奖励，这份小礼物就给你了！',Speaker=891470,}",
  [165231] = "{id=165231,Text='如果有想要去恋爱圣地打卡的冒险者，记得来普隆德拉广场找我哦~',Speaker=891470,Emoji=9,}",
  [165232] = "{id=165232,Text='夜莺剧院最近都没有安排演出，我们可以乘此机会到舞台上拍照！走吗？',Speaker=891469,Option='{我可以,2},{稍后再来,3}',}",
  [165233] = "{id=165233,Text='今天，我就要在夜莺剧院的舞台上出道！',Speaker=891469,}",
  [165234] = "{id=165234,Text='只有夜莺剧院这华丽的舞台才配得上我爱神菲尔，作为奖励，这份小礼物就给你了！',Speaker=891470,}",
  [165235] = "{id=165235,Text='如果有想要去恋爱圣地打卡的冒险者，记得来普隆德拉广场找我哦~',Speaker=891470,Emoji=9,}",
  [165240] = "{id=165240,Text='嗨！勇敢的冒险者，帮我找寻生命中最重要的人吧！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893049,Option='{暂时不想,2},{好的,3}',}",
  [165241] = "{id=165241,Text='唉，真是太遗憾了……',Speaker=893049,}",
  [165242] = "{id=165242,Text='太好了，我，克鲁克斯代表奈利乌斯家族感谢你！',Speaker=893049,}",
  [165243] = "{id=165243,Text='我最珍视的大小姐丝碧卡马上就要做家主了，却突然离家出走，请务必帮我找到她！',Speaker=893049,Option='怎么找',}",
  [165244] = "{id=165244,Text='丝碧卡心心念念一个叫[c][ffff00]克魔岛[-][/c]的地方，冒险者请务必出发去寻找看看吧！',Speaker=893049,Option='好',}",
  [165245] = "{id=165245,Text='这是我给丝碧卡找来的玩偶，还请务必帮我交给她！',Speaker=893049,}",
  [165246] = "{id=165246,Text='作为丝碧卡的骑士，我暂时把守护丝碧卡的忠诚的剑交给你！',Speaker=893049,}",
  [165247] = "{id=165247,Text='[c][ffff00]去克魔岛，寻找那位穿蓝色裙子的金发女孩吧！[-][/c]',Speaker=893049,}",
  [165252] = "{id=165252,Text='你就是给我写情书的人？',Speaker=891471,Option='{什么情书？,0},{不是我！,0}',}",
  [165253] = "{id=165253,Text='搞错了吗？太气人了！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891471,Option='{询问,2},{默默离开,3}',}",
  [165254] = "{id=165254,Text='昨天，我捡到了一封情书！这是我人生第一次收到情书！于是就来赴约了，结果那个人居然没有出现！',Speaker=891471,Option='搞错了吗？',}",
  [165255] = "{id=165255,Text='不可能！那封情书上写着我的名字，来参加狩猎季的猎人中，只有我叫阿维娃！',Speaker=891471,}",
  [165256] = "{id=165256,Text='明天狩猎比赛就要开始了，为了来赴约，我今天都没有与学生一起进行赛前准备，没想到那个人还迟到了！',Speaker=891471,Option='{不要紧吗？,0},{打算如何？,0}',}",
  [165257] = "{id=165257,Text='我担心我那个笨学生一个人忙不过来，不过……我还是想知道究竟是谁给我写的情书！',Speaker=891471,}",
  [165258] = "{id=165258,Text='你能替我去狩猎场，帮助亚伯一同完成狩猎比赛的赛前准备工作吗？',Speaker=891471,Option='可以',}",
  [165259] = "{id=165259,Text='谢谢你！另外，千万别告诉亚伯这件事！',Speaker=891471,}",
  [165260] = "{id=165260,Text='哎呀，还有好多东西没有准备好呢，老师怎么还没回来呀。',Speaker=891472,}",
  [165261] = "{id=165261,Text='你也是来参加狩猎比赛的吗？哈哈，今年是我第一次参赛，好紧张呢。哎，偏偏这时候，老师还不在……',Speaker=891472,Option='她让我来帮忙',}",
  [165262] = "{id=165262,Text='太好了，老师她没有忘记我。谢谢你能来帮忙，我一个人真的是忙不过来。',Speaker=891472,Option='不客气！',}",
  [165263] = "{id=165263,Text='有什么我能帮你做的事吗？',}",
  [165264] = "{id=165264,Text='能不能采集一些醉象花呀？老师说，醉象花的花蜜是最天然的迷药，药效强劲，就连大象都能迷晕呢。',Speaker=891472,}",
  [165265] = "{id=165265,Text='老师还说，狩猎的时候，将花蜜涂在弓箭上，无论多么强悍的猛兽都会倒下……',Speaker=891472,Option='{花在哪？,0},{别总老师说,0}',}",
  [165266] = "{id=165266,Text='嘿嘿，不好意思，我总是三句绕不开我尊敬的老师……东面那片树荫下面，说不定就有一些醉象花。',Speaker=891472,}",
  [165267] = "{id=165267,Text='麻烦你多采一些啦。',Speaker=891472,}",
  [165268] = "{id=165268,Text='……弓箭也准备好了……诶？绳子去了哪？我记得我带在身上的呀，怎么找不到了……',Speaker=891472,}",
  [165269] = "{id=165269,Text='哇，收集了这么多醉象花，太好了，老师要是知道一定会很开心的。',Speaker=891472,Option='询问绳子',}",
  [165270] = "{id=165270,Text='明天狩猎比赛要用的东西，都准备得差不多了，就是绳子找不到了，我明明记得带在身上的……',Speaker=891472,Option='我来帮你！',}",
  [165271] = "{id=165271,Text='哎，那么就要麻烦你了，我真是太不小心了……',Speaker=891472,}",
  [165272] = "{id=165272,Text='原来就在这附近呀，真亏你能找到呢。',Speaker=891472,}",
  [165273] = "{id=165273,Text='这下老师不会来骂我啦，太感谢你了。',Speaker=891472,}",
  [165274] = "{id=165274,Text='啊，老师回来了。',Speaker=891472,}",
  [165275] = "{id=165275,Text='老师，我们已经把明天比赛要用的东西准备妥当啦。',Speaker=891472,}",
  [165276] = "{id=165276,Text='诶，发生了什么事呀？老师，你看上去心情不太好？',Speaker=891472,}",
  [165277] = "{id=165277,Text='没什么！就是生气！',Speaker=891487,}",
  [165278] = "{id=165278,Text='我……我难道又做了什么事情惹老师生气了吗？',Speaker=891472,}",
  [165279] = "{id=165279,Text='不关你的事！既然忙完了，就早点回去休息吧！',Speaker=891487,}",
  [165280] = "{id=165280,Text='好的……老师居然不是在生我的气，真不知道该高兴还是不高兴……',Speaker=891472,}",
  [165281] = "{id=165281,Text='……',Speaker=891487,Option='{发生了什么事？,0},{等到他了吗？,0}',}",
  [165282] = "{id=165282,Text='没有！太气人了，那个家伙居然放我鸽子！',Speaker=891487,}",
  [165283] = "{id=165283,Text='如果让我知道他是谁！我一定饶不了他！',Speaker=891487,}",
  [165284] = "{id=165284,Text='不过现在比赛当前，我需要专心投入到狩猎比赛中！',Speaker=891487,Option='{这样就对了！,0},{真期待比赛！,0}',}",
  [165285] = "{id=165285,Text='对了，你做得不错！有兴趣加入我和亚伯的队伍，参与到这次的狩猎比赛中吗？',Speaker=891487,Option='有兴趣！',}",
  [165286] = "{id=165286,Text='太好了！有了你的加入，我对这次的比赛更有信心了！',Speaker=891487,}",
  [165287] = "{id=165287,Text='现在赛前的准备已经一切就绪了，明天一早，记得来普隆德拉西门狩猎场找我们！',Speaker=891487,Option='明白！',}",
  [165288] = "{id=165288,Text='你来啦！狩猎比赛的开幕仪式马上就要开始了，去看看吧！',Speaker=891487,}",
  [165289] = "{id=165289,Text='一年一度的狩猎季正式开幕啦！',Speaker=891480,}",
  [165290] = "{id=165290,Text='我接到今天的比赛任务了，任务比较简单，我先带着你们一起熟悉一下！',Speaker=891487,Option='任务是什么？',}",
  [165291] = "{id=165291,Text='今天，要猎捕一些疯兔，跟我来！',Speaker=891487,}",
  [165292] = "{id=165292,Text='来分工一下，[c][ffff00][PlayerName][-][/c]，你就负责猎捕10只疯兔吧！',Speaker=891487,}",
  [165293] = "{id=165293,Text='完成后记得来找我，我会叫裁判过来统计狩猎成果！',Speaker=891487,}",
  [165294] = "{id=165294,Text='哼！',Speaker=891487,Option='{为什么生气？,0},{你在看什么？,0}',}",
  [165295] = "{id=165295,Text='我本来担心亚伯是否能独自完成任务，想过去帮帮他！没想到，他根本不需要我的帮忙！',Speaker=891487,}",
  [165296] = "{id=165296,Text='为什么那小子桃花运那么好！就连参加狩猎比赛都能有艳遇！难道就因为他是善良的精灵？',Speaker=891487,}",
  [165297] = "{id=165297,Text='作为猎手，我可比他优秀一百倍！',Speaker=891487,}",
  [165298] = "{id=165298,Text='不过，这不重要！没有什么能比得过狩猎比赛！',Speaker=891487,}",
  [165299] = "{id=165299,Text='跟我来！我们去狩猎今天比赛的终极猎物！',Speaker=891487,}",
  [165300] = "{id=165300,Text='让我们一起干掉它！',Speaker=891487,}",
  [165301] = "{id=165301,Text='干得漂亮！',Speaker=891487,}",
  [165302] = "{id=165302,Text='我果然没有看错，你拥有着成为优秀猎手的潜质！',Speaker=891487,}",
  [165303] = "{id=165303,Text='今天的狩猎任务达成了，我会去向裁判汇报今天的狩猎成果！',Speaker=891487,}",
  [165304] = "{id=165304,Text='明天的比赛会在星泪森林的狩猎场进行，不要忘记过来参赛哦！',Speaker=891487,Option='不会忘！',}",
  [165305] = "{id=165305,Text='早！我已经接到今天的比赛任务了，还是按照老规矩，我们分工进行狩猎！',Speaker=891487,Emoji=17,}",
  [165306] = "{id=165306,Text='狩猎幼狼的任务就交给你了！有问题吗？',Speaker=891487,Option='没问题！',}",
  [165307] = "{id=165307,Text='那么，开始吧！希望一切顺利！',Speaker=891487,}",
  [165308] = "{id=165308,Text='已经完成任务了？你果然不会让我失望！亚伯还没有回来，我们一起去看看他那边的情况吧！',Speaker=891487,Option='好的',}",
  [165309] = "{id=165309,Text='呜哇，怎么办？好多野狼，老师救救我……',Speaker=891472,}",
  [165310] = "{id=165310,Text='亚伯，小心！',Speaker=891487,}",
  [165311] = "{id=165311,Text='哎呀，还好老师及时赶到……',Speaker=891472,}",
  [165312] = "{id=165312,Text='亚伯！！！我说了多少次，狼是群居动物，要想办法让狼离开狼群，再进行狩猎！',Speaker=891487,}",
  [165313] = "{id=165313,Text='下次可千万别再犯这样的错误！太危险了！',Speaker=891487,}",
  [165314] = "{id=165314,Text='我知道了。',Speaker=891472,}",
  [165315] = "{id=165315,Text='你们在这等一下，我去找裁判统计今天的狩猎成果！',Speaker=891487,}",
  [165316] = "{id=165316,Text='什么！！！！！！',Speaker=891487,}",
  [165317] = "{id=165317,Text='刚才裁判公布了决赛的狩猎目标，是要捕捉梦蛾。老师说，要完成狩猎任务，捕梦网是不可或缺的道具。',Speaker=891472,Emoji=24,}",
  [165318] = "{id=165318,Text='唉，但是，我把捕梦网弄丢了……',Speaker=891472,}",
  [165319] = "{id=165319,Text='我从来没见过老师如此生气，怎么办呀，她会不会再也不理我了……',Speaker=891472,Option='{安慰亚伯,0},{责备亚伯,0}',}",
  [165320] = "{id=165320,Text='你能帮我去向老师说说情，让她不要再生我的气了吗？',Speaker=891472,Option='一口答应',}",
  [165321] = "{id=165321,Text='我真是太生气了！！！',Speaker=891487,Option='{生气伤身,0},{比赛要紧,0}',}",
  [165322] = "{id=165322,Text='没错！！！当务之急还是得想办法解决这个问题！！！',Speaker=891487,}",
  [165323] = "{id=165323,Text='现在，还有时间重新制作捕梦网。明天的狩猎任务由我来负责，你和亚伯去收集制作捕梦网的材料！',Speaker=891487,}",
  [165324] = "{id=165324,Text='希望亚伯这次真的吸取教训，改掉他的坏毛病！',Speaker=891487,}",
  [165325] = "{id=165325,Text='还好有你在，重新制作捕梦网的事就交给你们了！',Speaker=891487,Option='没问题！',}",
  [165326] = "{id=165326,Text='要重新制作捕梦网？好的，好的，那么明天一早，我们在狩猎场会合哦，希望老师早点消气。',Speaker=891472,Option='好的',}",
  [165327] = "{id=165327,Text='zzz……',Speaker=891472,Option='叫醒他',Emoji=36,}",
  [165328] = "{id=165328,Text='哎呀，你来了呀，不好意思我都睡着了……',Speaker=891472,}",
  [165329] = "{id=165329,Text='呜啊，嗯……我今天起了个大早，材料我已经收集得差不多啦，就剩下冬眠草啦。',Speaker=891472,}",
  [165330] = "{id=165330,Text='冬眠草生长在冰霜领域，那个地方比较危险，我一个人不敢去……就在这儿等你，没想到居然睡着了……',Speaker=891472,Option='{夸赞亚伯,0},{吐槽亚伯,0}',}",
  [165331] = "{id=165331,Text='嘿嘿，昨天老师生气的样子真的把我吓坏了，真的好害怕她突然不要我……',Speaker=891472,}",
  [165332] = "{id=165332,Text='比赛越来越激烈了，老师一个人完成今天的狩猎任务压力很大，我们快点去采集冬眠草吧。',Speaker=891472,}",
  [165333] = "{id=165333,Text='现在就出发吧。',Speaker=891472,Option='{好的,2},{稍等,3}',}",
  [165334] = "{id=165334,Text='太好了，制作捕梦网的材料都已经集齐了呢，我们快回到狩猎场去找老师吧。',Speaker=891472,}",
  [165335] = "{id=165335,Text='你们已经把制作捕梦网的材料集齐了？这么快？',Speaker=891487,Option='亚伯的功劳  ',}",
  [165336] = "{id=165336,Text='做的不错，亚伯！',Speaker=891487,}",
  [165337] = "{id=165337,Text='那么我们开始制作捕梦网吧！',Speaker=891487,}",
  [165338] = "{id=165338,Text='捕梦网做好了！',Speaker=891487,}",
  [165339] = "{id=165339,Text='这下终于安心了！今天的狩猎任务我已经完成得差不多了，只剩下终极猎物——狼王！',Speaker=891487,}",
  [165340] = "{id=165340,Text='慢着，慢着，老师说，在开始狩猎之前，要想办法让狼离开狼群。',Speaker=891472,}",
  [165341] = "{id=165341,Text='我去吸引狼王的注意吧。',Speaker=891472,}",
  [165342] = "{id=165342,Text='就是现在啦。',Speaker=891472,}",
  [165343] = "{id=165343,Text='干得漂亮！没有想到不依靠我的帮助，只靠你们两个人，就能完成狼王的狩猎！',Speaker=891487,}",
  [165344] = "{id=165344,Text='亚伯的表现尤其让我惊喜，看得出来，你真的有在认真反省！',Speaker=891487,Option='如何？',}",
  [165345] = "{id=165345,Text='期待你们决赛时的表现！我先去找裁判统计今天的狩猎成果。',Speaker=891487,}",
  [165346] = "{id=165346,Text='（亚伯似乎有什么心事。）',}",
  [165347] = "{id=165347,Text='偷偷跟你说一个事哦……',Speaker=891472,Option='什么事？',Emoji=38,}",
  [165348] = "{id=165348,Text='我喜欢着一位善良、美丽的女猎手，她是我心目中的女神……',Speaker=891472,}",
  [165349] = "{id=165349,Text='我想要乘着明天狩猎比赛结束之前，向她表白……不知道她会不会接受我的心意……',Speaker=891472,Option='我可以帮你',}",
  [165350] = "{id=165350,Text='（是前几日与亚伯聊天的女猎手吗？）',}",
  [165351] = "{id=165351,Text='真的吗？太感谢你了，你真是个好人，那么，明天再见啦。',Speaker=891472,}",
  [165352] = "{id=165352,Text='早上好！真激动呢，终于来到决赛环节了！让我们一起去狩猎梦蛾吧！',Speaker=891487,Option='{好呀！,0},{我也很激动！,0}',}",
  [165353] = "{id=165353,Text='小心！不要吸入梦蛾翅膀上的鳞粉！那会让人进入到昏睡状态！',Speaker=891487,Option='该怎么办？',}",
  [165354] = "{id=165354,Text='今天的风向是……有了！跟我来！',Speaker=891487,}",
  [165355] = "{id=165355,Text='就是这里！根据今天的风向，在这个位置攻击梦蛾，就不会吸入鳞粉！等它虚弱以后，就用捕梦网捕捉它！',Speaker=891487,Option='好的',}",
  [165356] = "{id=165356,Text='我们成功了！我们捕捉到了梦蛾！',Speaker=891487,}",
  [165357] = "{id=165357,Text='这次狩猎比赛的冠军，是我们的了！我现在就去找裁判！',Speaker=891487,}",
  [165358] = "{id=165358,Text='裁判认可了我们的成绩，我们获得了狩猎比赛的冠军！颁奖典礼就要开始了，我们一起去领奖吧！',Speaker=891487,Option='太好了！',}",
  [165359] = "{id=165359,Text='恭喜阿维娃、亚伯、[c][ffff00][PlayerName][-][/c]率先捕获梦蛾，获得今年狩猎比赛的冠军！',Speaker=891480,}",
  [165360] = "{id=165360,Text='有请他们，上前领奖！',Speaker=891480,}",
  [165361] = "{id=165361,Text='谢谢你，[c][ffff00][PlayerName][-][/c]！',Speaker=891487,}",
  [165362] = "{id=165362,Text='还有亚伯！',Speaker=891487,}",
  [165363] = "{id=165363,Text='等一下，我还没有准备好……不过，我可以先告诉你，我想表白的人，就是老师啦。',Speaker=891472,}",
  [165364] = "{id=165364,Text='之前，我都写好情书了，可是怎么也找不到了……那封情书去了哪里呢？',Speaker=891472,Option='阿维娃捡到了',}",
  [165365] = "{id=165365,Text='什么？老师等了半天，还生气了……原来，那天还是我惹老师生气了……我突然不敢告白了……',Speaker=891472,Option='鼓励亚伯',}",
  [165366] = "{id=165366,Text='哎，也是，我已经忍耐太久了，今天就豁出去吧。[c][ffff00][PlayerName][-][/c]，你可要帮帮我呀！',Speaker=891472,}",
  [165367] = "{id=165367,Text='什么！给我写情书的人，居然是你！',Speaker=891487,}",
  [165368] = "{id=165368,Text='是我，老师你不要生气，我以后再也不会马虎大意，惹你生气了，所以请接受我的爱意吧！',Speaker=891472,}",
  [165369] = "{id=165369,Text='我可不信！不对，问题是……我可是你老师！比你大多了！我们种族还不一样！',Speaker=891487,}",
  [165370] = "{id=165370,Text='种族不一样就不能在一起了吗？虽然我年纪比你小，现在也不强大，但我会努力成为优秀猎手的！',Speaker=891472,}",
  [165371] = "{id=165371,Text='（是时候帮一把亚伯了！）',}",
  [165372] = "{id=165372,Text='既然你这么说，那么……',Speaker=891487,}",
  [165373] = "{id=165373,Text='从明天开始，亚伯，你的训练量加倍！等你超越我的那一天，我就接受你的告白！',Speaker=891487,}",
  [165374] = "{id=165374,Text='（亚伯的幸福？痛苦生活，似乎就要开始了呢。）',}",
  [165386] = "{id=165386,Text='嘿，[c][ffff00][PlayerName][-][/c]！又到了新生入学的日子，作为克里图拉学院最优秀的毕业生——',Speaker=891494,}",
  [165387] = "{id=165387,Text='有没有兴趣成为这一届新生的[c][ffff00]接引人[-][/c]呀！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891494,Option='{好呀好呀,2},{没有兴趣,3}',}",
  [165388] = "{id=165388,Text='好的，接引人[c][ffff00][PlayerName][-][/c]！一定要让这些新人们明白同伴的意义啊！',Speaker=891494,}",
  [165389] = "{id=165389,Text='现在——到学院去迎接前来报道的新生吧！',Speaker=891494,Option='马上出发！',}",
  [165390] = "{id=165390,Text='诶？你就是[c][ffff00][PlayerName][-][/c]吧？那位学院最优秀的毕业生？',Speaker=891500,Option='{没错，正是在下,2},{（挠头）,3}',}",
  [165391] = "{id=165391,Text='果然是你呢！听教务长说你要来做这一届新生的接引人，那我们以后就是同事了！',Speaker=891500,}",
  [165392] = "{id=165392,Text='我叫苏拉，是学院[c][ffff00]新来的老师[-][/c]。真是可惜呢，今年才刚刚来到学院……',Speaker=891500,}",
  [165393] = "{id=165393,Text='不然就有机会教导你这样优秀的冒险者了，哈哈！',Speaker=891500,}",
  [165394] = "{id=165394,Text='啊，新生们来了！',Speaker=891500,}",
  [165395] = "{id=165395,Text='入学的新生们，请到我这边来！',Speaker=891500,}",
  [165396] = "{id=165396,Text='欢迎各位来到克里图拉学院！我是你们的老师苏拉，这位——是你们的接引人[c][ffff00][PlayerName][-][/c]。',Speaker=891500,Option='（打个招呼）',}",
  [165397] = "{id=165397,Text='[c][ffff00][PlayerName][-][/c]！！就是那位有名的冒险者！偶、偶像啊！我竟然亲眼见到偶像了！',Speaker=891496,Emoji=27,}",
  [165398] = "{id=165398,Text='嘁……（小声）大呼小叫的，一看就没见过世面的样子……',Speaker=891503,}",
  [165399] = "{id=165399,Text='我们以后也会成为这么优秀的冒险者么喵呜~~',Speaker=891498,}",
  [165400] = "{id=165400,Text='……唔、真了不起，这座学院好像有好多厉害的魔法装置、值得好好研究一下！',Speaker=891497,}",
  [165401] = "{id=165401,Text='好了，各位学员，有什么话待会儿再说。大家排好队，来合个影吧！',Speaker=891500,}",
  [165402] = "{id=165402,Text='竟然可是和偶像合影，真是太棒了！',Speaker=891496,}",
  [165403] = "{id=165403,Text='好了各位，现在你们已经是克里图拉学院的正式学员了，从今以后，要努力提升自己、磨砺自己的冒险技巧哦！',Speaker=891500,}",
  [165404] = "{id=165404,Text='在这之前——我们是不是有一件很重要的事情没有做？！',Speaker=891496,}",
  [165405] = "{id=165405,Text='是自我介绍啊！自我介绍！我们现在是同伴了，怎么能连彼此的名字都不知道啊！',Speaker=891496,}",
  [165406] = "{id=165406,Text='我叫布鲁斯，是从龙之城洛阳来这里修行的，我的目标是成为像[c][ffff00][PlayerName][-][/c]那样强大的冒险者！',Speaker=891496,}",
  [165407] = "{id=165407,Text='我是薇兰·雪耳，来自罗扎纳喵~',Speaker=891498,}",
  [165408] = "{id=165408,Text='唔，薇兰毛绒绒的，看起来好可爱啊~',Speaker=891496,}",
  [165409] = "{id=165409,Text='诶，到我了吗？你们可以叫我凯奇，我很喜欢机械——如果你们有兴趣的话，我可以给你们看看我收藏的机械巴风特模型……',Speaker=891497,}",
  [165410] = "{id=165410,Text='伊洛斯……',Speaker=891499,}",
  [165411] = "{id=165411,Text='咦？就这么多？你为什么不爱说话呀？为什么呀？',Speaker=891496,}",
  [165412] = "{id=165412,Text='……遗、遗传……',Speaker=891499,}",
  [165413] = "{id=165413,Text='哼、我是尼尔，尼尔·巴尔特——跟你们这些乡下来的不一样，我来自尊贵的巴尔特家族！',Speaker=891503,}",
  [165414] = "{id=165414,Text='巴尔特家族？虽然我没听说过，不过听起来真厉害！',Speaker=891496,}",
  [165415] = "{id=165415,Text='嘁、没见识的土包子……',Speaker=891503,}",
  [165416] = "{id=165416,Text='那么——既然大家已经熟悉了，我们就组成一个冒险小队吧！',Speaker=891496,}",
  [165417] = "{id=165417,Text='就像苏拉老师刚刚说的那样，我们要努力提升自己，对于冒险者来说，冒险不就是提升自己最好的方式吗！',Speaker=891496,}",
  [165418] = "{id=165418,Text='小队的名字我已经想好了，就叫“白银之风”！',Speaker=891496,Option='为什么？',}",
  [165419] = "{id=165419,Text='我也不知道，但总觉得听起来很霸气的样子，嘿嘿嘿……',Speaker=891496,}",
  [165420] = "{id=165420,Text='哼，我可没兴趣陪你玩小孩子的冒险游戏，你们请便吧！',Speaker=891503,}",
  [165421] = "{id=165421,Text='唔……好吧，那么你们呢？',Speaker=891496,}",
  [165422] = "{id=165422,Text='冒险队……听起来还挺有趣的！',Speaker=891498,}",
  [165423] = "{id=165423,Text='我……可以……',Speaker=891499,}",
  [165424] = "{id=165424,Text='冒险的话、应该能见到很多有趣的机械怪物吧！',Speaker=891497,}",
  [165425] = "{id=165425,Text='那么，我宣布——“白银之风”小队，正式成立！',Speaker=891496,}",
  [165426] = "{id=165426,Text='嘿~嘿~[c][ffff00][PlayerName][-][/c]前辈！',Speaker=891496,Option='你在干嘛',}",
  [165427] = "{id=165427,Text='前辈不知道吗？学院的各大社团正在招新、吸引新生加入呢！我们“白银之风”小队当然也要招募更多队员啊！',Speaker=891496,}",
  [165428] = "{id=165428,Text='我们现在要去散发招新传单了~',Speaker=891496,}",
  [165429] = "{id=165429,Text='啊啊啊啊……我不想去啊，我只想待在宿舍里研究机械啊啊……',Speaker=891497,}",
  [165430] = "{id=165430,Text='作为冒险者要多运动、多交朋友啊，凯奇！薇兰和伊洛斯都已经开工了，我们也不能落后呀！',Speaker=891496,}",
  [165431] = "{id=165431,Text='[c][ffff00][PlayerName][-][/c]前辈作为“白银之风”的一员，也一起来帮忙吧！',Speaker=891496,Option='{我也是成员？,0},{好吧,0}',}",
  [165432] = "{id=165432,Text='没错！！让我们努力招收新人吧，前辈！',Speaker=891496,}",
  [165433] = "{id=165433,Text='“白银之风”招收新人了，大家不要错过哦！',Speaker=891496,}",
  [165434] = "{id=165434,Text='白银之风？那是什么……',Speaker=891504,}",
  [165435] = "{id=165435,Text='听起来像是个炼金协会？好像挺有意思的……',Speaker=891505,}",
  [165436] = "{id=165436,Text='冒险小队，还有这种组织吗？',Speaker=891506,}",
  [165437] = "{id=165437,Text='滚远点，你这乡下来的土猫！',Speaker=891503,}",
  [165438] = "{id=165438,Text='那边发生了什么，我们过去看看！',Speaker=891496,}",
  [165439] = "{id=165439,Text='哼，你这只杂毛土猫！',Speaker=891503,}",
  [165440] = "{id=165440,Text='我们“黄金茶会”可是高端俱乐部，只有王国贵族的后代才有资格加入……',Speaker=891503,}",
  [165441] = "{id=165441,Text='你这样的乡巴佬，竟敢到我们这儿散发传单？当这里是什么地方！',Speaker=891503,}",
  [165442] = "{id=165442,Text='我、我不知道喵……',Speaker=891498,Emoji=24,}",
  [165443] = "{id=165443,Text='哼，“白银之风”？这么土气的名字也不知道你们怎么想出来的！还是什么冒险小队？',Speaker=891503,}",
  [165444] = "{id=165444,Text='简直是小孩子过家家的玩意儿！我们这些王国的未来才不需要这种幼稚的游戏！',Speaker=891503,}",
  [165445] = "{id=165445,Text='只有你们这种没有资格进入王国权力中心的人，才需要参加这种垃圾小队！',Speaker=891503,}",
  [165446] = "{id=165446,Text='不许你侮辱我的朋友！',Speaker=891496,}",
  [165447] = "{id=165447,Text='你、你敢打我？！你知道有什么后果吗？！',Speaker=891503,}",
  [165448] = "{id=165448,Text='你们干什么呢！',Speaker=891500,}",
  [165449] = "{id=165449,Text='够了！你们都别太过分！',Speaker=891500,}",
  [165450] = "{id=165450,Text='学院明令禁止在校园内私斗——你们想要被开除吗！',Speaker=891500,}",
  [165451] = "{id=165451,Text='可是——',Speaker=891496,}",
  [165452] = "{id=165452,Text='嘁，多管闲事……',Speaker=891503,}",
  [165453] = "{id=165453,Text='大家都是一个学院的同伴，我希望你们可以彼此包容、相互帮助，而不是像现在这样，因为偏见而争斗！',Speaker=891500,}",
  [165454] = "{id=165454,Text='如果下次再有这样的事，我一定严惩不贷！',Speaker=891500,}",
  [165455] = "{id=165455,Text='哼……',Speaker=891503,}",
  [165456] = "{id=165456,Text='还有你，[c][ffff00][PlayerName][-][/c]。身为接引人，竟然带头打架！真是——不知道该怎么说你！下不为例！',Speaker=891500,}",
  [165457] = "{id=165457,Text='大家都没事吧？',Speaker=891496,}",
  [165458] = "{id=165458,Text='唔，苏拉老师似乎没有追究我们打架的事呢，他真是个好人啊喵~',Speaker=891498,}",
  [165459] = "{id=165459,Text='不过真是看不出来啊——凯奇，你看起来弱不经风的，打架还挺勇猛的！',Speaker=891496,}",
  [165460] = "{id=165460,Text='哼，我只是不屑于用暴力解决问题啦！',Speaker=891497,}",
  [165461] = "{id=165461,Text='也要谢谢你啊，前辈！帮我们教训了那帮家伙！',Speaker=891496,}",
  [165462] = "{id=165462,Text='那么我们作为“白银之风”的第一场战斗，就算以胜利告终了！',Speaker=891496,}",
  [165463] = "{id=165463,Text='（这里发生了什么？）',}",
  [165464] = "{id=165464,Text='[c][ffff00][PlayerName][-][/c]，你总算来了——学院发生了袭击事件！',Speaker=891500,Option='{诶？,0},{袭击事件？,0}',}",
  [165465] = "{id=165465,Text='尼尔好像被什么人袭击了！',Speaker=891500,}",
  [165466] = "{id=165466,Text='天哪，太可拍了！',Speaker=891504,}",
  [165467] = "{id=165467,Text='这究竟是谁干的……',Speaker=891505,}",
  [165468] = "{id=165468,Text='…………',Speaker=891501,}",
  [165469] = "{id=165469,Text='一定是“白银之风”的人干的！一定是他们为了报复之前的事袭击了尼尔！',Speaker=891501,}",
  [165470] = "{id=165470,Text='你在胡说什么！怎么可能是我们做的！',Speaker=891496,Emoji=37,}",
  [165471] = "{id=165471,Text='虽然那家伙很讨厌，但我们就算揍他，也是光明正大的揍！不会用偷袭的手段的！',Speaker=891496,}",
  [165472] = "{id=165472,Text='看，他们已经承认自己的动机了！一定就是他们做的！',Speaker=891501,}",
  [165473] = "{id=165473,Text='好吧，伙伴们！',Speaker=891496,}",
  [165474] = "{id=165474,Text='我们去把袭击那家伙的真凶揪出来！这样总没有人能冤枉我们了吧！',Speaker=891496,}",
  [165475] = "{id=165475,Text='（调查一下附近，看看有没有袭击者的线索吧！）',}",
  [165476] = "{id=165476,Text='嗯……如果是需要追踪痕迹的话，或许我可以帮得上忙！',Speaker=891497,}",
  [165477] = "{id=165477,Text='这是我制作的扫描仪，用它——或许可以看到一些难以发觉的线索！',Speaker=891497,Option='好耶！',}",
  [165478] = "{id=165478,Text='这是……脚印吗？跟着这些痕迹找找看有什么线索！',Speaker=891496,}",
  [165479] = "{id=165479,Text='（痕迹到这里就消失了……看来那个袭击者应该是藏在星泪森林中……）',}",
  [165480] = "{id=165480,Text='（克鲁鲁？不会是这家伙干的吧？毕竟它也有过袭击学院学生的前科……）',}",
  [165481] = "{id=165481,Text='诶？？[c][ffff00][PlayerName][-][/c]！！',Speaker=891507,}",
  [165482] = "{id=165482,Text='你不是已经毕业了吗？怎么又回到学院了！你、你、你不会是要暴揍我一顿吧？',Speaker=891507,}",
  [165483] = "{id=165483,Text='你、你竟然还带了帮手！可恶，我不会给你这个机会的！',Speaker=891507,Emoji=12,}",
  [165484] = "{id=165484,Text='你、你想干什么！',Speaker=891507,}",
  [165485] = "{id=165485,Text='不要以为我真的怕了你啊！',Speaker=891507,Option='{为什么,0},{袭击学员,0}',}",
  [165486] = "{id=165486,Text='诶？你在说什么啊——我这几天一直在妙勒尼山脉旅行，刚刚才回来，怎么可能会是我袭击了学员啊！',Speaker=891507,}",
  [165487] = "{id=165487,Text='不过说起来……学院里好像确实混进了一些奇怪的东西，散发出的味道让我很不舒服呢……',Speaker=891507,}",
  [165488] = "{id=165488,Text='总之，这件事跟我没关系！真是的，也不调查清除就来揍我，还传说中的冒险者呢——嘶，好痛……',Speaker=891507,Emoji=15,}",
  [165489] = "{id=165489,Text='看来……还是要从学院内部开始调查啊！',Speaker=891496,}",
  [165490] = "{id=165490,Text='不好了，[c][ffff00][PlayerName][-][/c]前辈！',Speaker=891496,}",
  [165491] = "{id=165491,Text='伊洛斯她不见了！整个学院我都找遍了，都没有找到她，有的学员说——',Speaker=891496,Option='{有的学员？,0},{说了什么？,0}',}",
  [165492] = "{id=165492,Text='他们说就是伊洛斯袭击了尼尔，所以畏罪潜逃了！',Speaker=891496,}",
  [165493] = "{id=165493,Text='伊洛斯绝不是那样的人！她一定是受不了那些风言风语，所以才躲起来了！',Speaker=891496,Option='{别担心,0},{我们去找她,0}',}",
  [165494] = "{id=165494,Text='不要着急，我们兵分两路——我去改进一下我的扫描仪，看看能不能用它找到伊洛斯的踪迹……',Speaker=891497,}",
  [165495] = "{id=165495,Text='你们可以先去找站岗的卫兵问问，看看他们有没有见过伊洛斯！',Speaker=891497,}",
  [165496] = "{id=165496,Text='你有什么事吗，冒险者？',Speaker=891509,Option='关于伊洛斯',}",
  [165497] = "{id=165497,Text='一个新生小姑娘？嗯……我有可能看到了——也有可能没看到……',Speaker=891509,}",
  [165498] = "{id=165498,Text='你也知道我一直在这里站岗，连饭都顾不上吃，如果有哪个好心人愿意帮我弄点吃的……',Speaker=891509,}",
  [165499] = "{id=165499,Text='我就可以在这里认真的想一想到底有没有见过那个小姑娘了……',Speaker=891509,Option='{好吧……,0},{我懂了,0}',Emoji=5,}",
  [165500] = "{id=165500,Text='嗯，如果是制作料理的话！我或许帮得上忙喵~',Speaker=891498,}",
  [165501] = "{id=165501,Text='在来学院前，我在罗扎纳的餐厅做过一段时间兼职厨师呢！',Speaker=891498,}",
  [165502] = "{id=165502,Text='嗯，我们到紫藤萝餐厅看看有没有什么食材吧！',Speaker=891498,}",
  [165503] = "{id=165503,Text='唔，一些海鲜嘛喵~烹饪海鲜我可是最擅长的！',Speaker=891498,}",
  [165504] = "{id=165504,Text='完成！',Speaker=891498,}",
  [165505] = "{id=165505,Text='（狼吞虎咽）唔、唔、唔……真好吃啊……',Speaker=891509,}",
  [165506] = "{id=165506,Text='（狼吞虎咽）唔、唔、唔……',Speaker=891509,Option='关于伊洛斯',}",
  [165507] = "{id=165507,Text='嗝——说起来，我确实见过你说的那个、很容易害羞的小姑娘呢，她今天到藏书馆来过，不过不久前已经离开了……',Speaker=891509,}",
  [165508] = "{id=165508,Text='走的时候看起来还挺匆忙的，不知道是不是有什么急事。',Speaker=891509,}",
  [165509] = "{id=165509,Text='立刻封锁藏书馆！排查这几天所有进出过藏书馆的人！',Speaker=891495,}",
  [165510] = "{id=165510,Text='究竟是谁干的……',Speaker=891495,Option='发生什么事了',}",
  [165511] = "{id=165511,Text='藏书馆的禁书室发生了失窃事件，丢失了一本魔法秘典……',Speaker=891495,}",
  [165512] = "{id=165512,Text='那上面记载了很多堪称禁忌的魔法，要是流传开了，麻烦可就大了！',Speaker=891495,}",
  [165513] = "{id=165513,Text='教务长，已经核查过这几天进出藏书馆的人员——其他人目前都在学院中，只有一个叫伊洛斯的学员不见了……',Speaker=891509,}",
  [165514] = "{id=165514,Text='教务长，伊洛斯是我们的同伴，我相信她！她绝不会作出那种事！',Speaker=891496,}",
  [165515] = "{id=165515,Text='但是现在所有线索都指向了她……',Speaker=891495,}",
  [165516] = "{id=165516,Text='我一定会找到证据，证明她是无辜的！',Speaker=891496,}",
  [165517] = "{id=165517,Text='前辈！我调查过了！有人看到伊洛斯到星泪森林去了！那边有不少魔物，她不会遇到危险吧？',Speaker=891496,}",
  [165518] = "{id=165518,Text='我们快去把她找回来吧！',Speaker=891496,}",
  [165519] = "{id=165519,Text='伊洛斯！',Speaker=891496,}",
  [165520] = "{id=165520,Text='布、布鲁斯……还有[c][ffff00][PlayerName][-][/c]前辈？你、你、你们……怎、怎么来了？',Speaker=891499,}",
  [165521] = "{id=165521,Text='我们是来找你的呀！你突然就不见了，有人怀疑你偷了学院的魔法书，快回去跟大家解释清楚……',Speaker=891496,}",
  [165522] = "{id=165522,Text='我……没有……',Speaker=891499,}",
  [165523] = "{id=165523,Text='伊洛斯，原来你在这儿！',Speaker=891500,}",
  [165524] = "{id=165524,Text='快跟我回去，把魔法书还给学院，我会向教务长求情，让他对你从轻发落！',Speaker=891500,}",
  [165525] = "{id=165525,Text='我、我没有……偷、偷书……',Speaker=891499,}",
  [165526] = "{id=165526,Text='前、前前辈，我、真……真的没有偷书！',Speaker=891499,Option='为什么在这儿',Emoji=24,}",
  [165527] = "{id=165527,Text='我……图……图书馆……有人……鬼鬼鬼鬼鬼鬼……',Speaker=891499,}",
  [165528] = "{id=165528,Text='鬼鬼祟祟？',Speaker=891496,}",
  [165529] = "{id=165529,Text='对！然后我、我就跟他……跟踪他……到这里！',Speaker=891499,}",
  [165530] = "{id=165530,Text='所以，你是说你在图书馆看到有人鬼鬼祟祟、形迹可疑，所以就跟踪他到了这里？',Speaker=891496,}",
  [165531] = "{id=165531,Text='可是这附近我已经搜查过了——只有你们几个人啊……',Speaker=891500,}",
  [165532] = "{id=165532,Text='算了，快跟我回学院，有什么话——直接跟教务长说吧！',Speaker=891500,}",
  [165533] = "{id=165533,Text='等等！',Speaker=891497,}",
  [165534] = "{id=165534,Text='既然伊洛斯说她是追踪一个鬼鬼祟祟的人到的这里，那我们或许可以用“扫描仪”找到那个人——',Speaker=891497,}",
  [165535] = "{id=165535,Text='只要抓住了他，就能证明伊洛斯的清白了！',Speaker=891497,}",
  [165536] = "{id=165536,Text='排除伊洛斯自己的脚印——有一行陌生的脚印，快跟上去看看！',Speaker=891497,}",
  [165537] = "{id=165537,Text='（脚印蔓延到了苏拉脚下，这是怎么回事！）',}",
  [165538] = "{id=165538,Text='哼，被发现了吗？',Speaker=891500,}",
  [165539] = "{id=165539,Text='本来还想把偷书的罪名推给你们的——看来，只能先杀你们灭口了！',Speaker=891500,}",
  [165540] = "{id=165540,Text='现在——面对死亡吧！',Speaker=891500,}",
  [165541] = "{id=165541,Text='苏、苏拉老师变成怪物了！！',Speaker=891498,}",
  [165542] = "{id=165542,Text='大家小心！',Speaker=891496,}",
  [165543] = "{id=165543,Text='可恶，就差一步……',Speaker=891508,}",
  [165544] = "{id=165544,Text='不过不要得意，我的主人……会替我报仇的……',Speaker=891508,}",
  [165545] = "{id=165545,Text='到那时——整个克里图拉学院，都会为我陪葬，呃哈哈哈哈哈！！',Speaker=891508,}",
  [165546] = "{id=165546,Text='嗯？这究竟发生了什么？',Speaker=891495,}",
  [165547] = "{id=165547,Text='禁忌魔典竟然在这里——还有，这个怪物是？？',Speaker=891495,Option='{告知实情,0},{出示证据,0}',}",
  [165548] = "{id=165548,Text='什么？！苏拉竟然是魔物假扮的？！',Speaker=891495,}",
  [165549] = "{id=165549,Text='他混入学院就是为了偷魔法书吗？还是有什么更邪恶的目的？',Speaker=891495,Option='他提到主人',}",
  [165550] = "{id=165550,Text='主人？好的，我明白了，这件事我会追查下去的！',Speaker=891495,}",
  [165551] = "{id=165551,Text='这次真是辛苦你们了——我记得，你们的组合好像叫白银……？',Speaker=891495,}",
  [165552] = "{id=165552,Text='白银之风！',Speaker=891496,Emoji=5,}",
  [165553] = "{id=165553,Text='嗯，真不错！王国需要的就是你们这样有干劲的冒险者！',Speaker=891495,}",
  [165554] = "{id=165554,Text='你们的事迹我们可要好好宣扬一番：勇敢的新生为了守护学院战胜了邪恶的恶魔奸细！应该是个不错的宣传题材吧！',Speaker=891495,}",
  [165555] = "{id=165555,Text='这样的话——是不是会有很多人加入我们的队伍？',Speaker=891496,}",
  [165556] = "{id=165556,Text='好耶！',}",
  [165557] = "{id=165557,Text='未来守护学院的重任，也要交给你们哦！要加油哦，孩子们！',Speaker=891495,}",
  [165558] = "{id=165558,Text='哟，[c][ffff00][PlayerName][-][/c]！今天我有一个问题要考考你，回答正确可以获得礼物哦~',Speaker=893071,Option='放马过来吧',}",
  [165559] = "{id=165559,Text='“辛勤的蜜蜂永没有时间悲哀。”，你知道这句话中的蜜蜂是指什么吗？',Speaker=893071,Option='{就是蜜蜂,2},{老师,3},{礼物我不要了,4}',}",
  [165560] = "{id=165560,Text='不会吧，这么简单的问题你也会答错？',Speaker=893071,}",
  [165561] = "{id=165561,Text='恭喜你答对了，没错，我们的老师就像蜜蜂一样勤劳的教授我们各种知识，每年的9月10日都是教师节哦，快去向你的老师们表达感谢吧！',Speaker=893071,}",
  [165562] = "{id=165562,Text='不会吧，这么简单的问题你也答不出来？',Speaker=893071,}",
  [165563] = "{id=165563,Text='真希望今晚的夜空可以晴朗无云啊……',Speaker=893070,}",
  [165564] = "{id=165564,Text='嗨！是你呀[c][ffff00][PlayerName][-][/c]！你问我在念叨什么？',Speaker=893070,}",
  [165565] = "{id=165565,Text='当然是期望今天有一个完美的夜晚啊！你不知道吗？马上就要月圆了，到了最适合赏花观月的时候~',Speaker=893070,}",
  [165566] = "{id=165566,Text='对了，我准备了一个礼物送给你，希望你的生活跟月亮一样圆满~！',Speaker=893070,}",
  [165567] = "{id=165567,Text='嗨[c][ffff00][PlayerName][-][/c]，我终于等到你了！',Speaker=893071,Option='有什么事吗？',}",
  [165568] = "{id=165568,Text='我们不是刚刚成立了一个“白银之风”冒险小队吗，布鲁斯那个家伙就立马急匆匆的开始探索学院周边有没有发生什么奇怪的事情了。',Speaker=893071,}",
  [165569] = "{id=165569,Text='没想到真让他找到一件可疑的事情：有个神官学姐说最近在公共盥洗室总能听到悉悉索索的声音，不时还会传出低沉的呜咽声……',Speaker=893071,}",
  [165570] = "{id=165570,Text='那间盥洗室已经没人敢再进去使用了，因此神官学姐请求我们进去调查一下里面到底发生了什么，如果能顺利解决的话可以给我们一些报酬。',Speaker=893071,Option='听起来很可怕',}",
  [165571] = "{id=165571,Text='但是布鲁斯已经满口答应下来了，感觉这个满脑子肌肉的家伙根本不知道什么叫害怕……',Speaker=893071,}",
  [165572] = "{id=165572,Text='不管怎么说既然已经接下了这份活，我们也只能进行下去了，作为“白银之风”的一员，[c][ffff00][PlayerName][-][/c]也要和我们一起行动哦！',Speaker=893071,Option='好吧……',}",
  [165573] = "{id=165573,Text='那就让我们先去找布鲁斯吧，他应该已经在盥洗室等我们了，就让我带你过去吧……',Speaker=893071,}",
  [165574] = "{id=165574,Text='别走！我来找你们了！',Speaker=893069,}",
  [165575] = "{id=165575,Text='不是说好在盥洗室门口集合吗，你怎么过来了？',Speaker=893071,}",
  [165576] = "{id=165576,Text='还说我呢，你们的速度也太慢了，我已经进盥洗室搜查过一遍，里面满地的食物残渣和各种垃圾，简直没地方下脚。',Speaker=893069,}",
  [165577] = "{id=165577,Text='说重点，你发现什么没有？',Speaker=893071,}",
  [165578] = "{id=165578,Text='那是当然的，在我进最深处搜索的时候一个黑影朝我撞了过来，还好我反应快躲了过去。',Speaker=893069,}",
  [165579] = "{id=165579,Text='虽然没抓住，让它破窗逃了，不过我和薇兰·雪耳一路追着它到了普隆德拉北部，并把它堵在了角落里。',Speaker=893069,}",
  [165580] = "{id=165580,Text='我让薇兰·雪耳看住它之后就来找你们了~',Speaker=893069,}",
  [165581] = "{id=165581,Text='你这个笨蛋怎么放心薇兰·雪耳独自在那，快带我们过去帮忙！',Speaker=893071,}",
  [165582] = "{id=165582,Text='你可不要小看她啊……好吧，我这就带你们过去。',Speaker=893069,}",
  [165583] = "{id=165583,Text='薇兰·雪耳，你还好吗！',Speaker=893071,}",
  [165584] = "{id=165584,Text='怎么了凯奇，我很好喵。',Speaker=893070,}",
  [165585] = "{id=165585,Text='没事就行，都怪布鲁斯这个肌肉脑袋把你自己留在这，现在那个黑影还在这里吗？',Speaker=893071,}",
  [165586] = "{id=165586,Text='和布鲁斯没关系的，是我自己想在这帮忙喵，还有那个黑影其实是一只灯笼鱼人，它被我堵在里面不敢出来。',Speaker=893070,}",
  [165587] = "{id=165587,Text='看来是我错怪你了布鲁斯，对不起。',Speaker=893071,}",
  [165588] = "{id=165588,Text='哈哈哈，没关系，现在让我们来那看看那个小灯笼鱼人是怎么回事吧。',Speaker=893069,}",
  [165589] = "{id=165589,Text='wwwwooo￥*&……%weeeii……&￥……yuuuu!!!!',Speaker=893050,}",
  [165590] = "{id=165590,Text='他在说什么，是我耳朵出毛病了吗？',Speaker=893069,}",
  [165591] = "{id=165591,Text='呃，它好像在说主人、饿、控制什么的，对不起，我也听不太懂喵……',Speaker=893070,}",
  [165592] = "{id=165592,Text='没关系薇兰，能听懂这些已经很不错了，但是线索还是太少，我们需要再进一步的调查。',Speaker=893071,}",
  [165593] = "{id=165593,Text='bieeeedaaaa￥*&……%weeeii……&￥……yuuuu!!!!',Speaker=893050,}",
  [165594] = "{id=165594,Text='这灯笼鱼人突然发疯了，快点打倒它喵！',Speaker=893070,}",
  [165595] = "{id=165595,Text='这是怎么回事？',Speaker=893071,}",
  [165596] = "{id=165596,Text='我也不知道，它突然大叫着“嘟啦嘟啦嘟啦”冲过来，好可怕喵……',Speaker=893070,}",
  [165597] = "{id=165597,Text='我知道了！它肯定是被勇猛的我吓到疯狂了。',Speaker=893069,}",
  [165598] = "{id=165598,Text='你不要这么一惊一乍的瞎说胡话好吗……今天我们先解散，我回去研究一下有没有什么办法进行后续调查。',Speaker=893071,}",
  [165599] = "{id=165599,Text='哟，[c][ffff00][PlayerName][-][/c]，我等你很久咯~',Speaker=893069,}",
  [165600] = "{id=165600,Text='凯奇现在正制作一种追踪器与翻译机，可以帮助我们了解灯笼鱼人到底说了什么还有从哪里来',Speaker=893069,}",
  [165601] = "{id=165601,Text='其他的队员们也在分头行动进行调查，所以今天就我们组成小队啦！',Speaker=893069,Option='为什么是跟你……',}",
  [165602] = "{id=165602,Text='别这样嘛，我可是很强的，不管有什么问题统统一拳搞定！',Speaker=893069,Option='说正事！',}",
  [165603] = "{id=165603,Text='好吧……昨天分开后神官姐姐找到我说在我们走后盥洗室里又冲出了一堆灯笼鱼人和其他魔物，现在它们聚集在普隆德拉北部攻击路过的旅人',Speaker=893069,}",
  [165604] = "{id=165604,Text='所以，我们今天的任务就是把它们驱逐，最好能搞清楚它们的意图，现在我们就……',Speaker=893069,Option='走吧',}",
  [165605] = "{id=165605,Text='这里还有痕迹，看来方向没错，我们继续前进喵',Speaker=893070,}",
  [165607] = "{id=165607,Text='waggaagaweeeeiiyuuuu!!!!!!',Speaker=893050,}",
  [165608] = "{id=165608,Text='它们来了，开始攻击！',Speaker=893069,}",
  [165609] = "{id=165609,Text='做得好啊[c][ffff00][PlayerName][-][/c]，只是可惜，虽然把它们都打倒了却没有获得什么有用的线索',Speaker=893069,}",
  [165610] = "{id=165610,Text='我去凯奇那看看他的机器做的怎么样了，今天就解散吧，明天见~',Speaker=893069,}",
  [165611] = "{id=165611,Text='你好[c][ffff00][PlayerName][-][/c]喵~，今天我们是小分队的队友喵~',Speaker=893070,Option='太好了',}",
  [165612] = "{id=165612,Text='什么太好了喵？',Speaker=893070,Option='没什么~',}",
  [165613] = "{id=165613,Text='好吧喵，今天我们的任务是寻找失物喵！昨天逃窜的那些魔物抢夺了一些学生的物品，我们需要帮助他们把东西找回来喵。',Speaker=893070,}",
  [165614] = "{id=165614,Text='关于任务还有其他什么问题喵？',Speaker=893070,Option='没有',}",
  [165615] = "{id=165615,Text='那么目的地，星泪森林，出发喵！',Speaker=893070,Option='出发！',}",
  [165616] = "{id=165616,Text='根据学生报告他就是在这里被抢的，我们检查一下有没有留下什么痕迹喵',Speaker=893070,}",
  [165617] = "{id=165617,Text='探险家先生，你昨天有看到过一些奇怪的魔物喵？',Speaker=893070,}",
  [165618] = "{id=165618,Text='喂，你有没有搞错？你觉得我现在这个样子可能看到别的魔物吗？我现在的眼里只有这几个可恶的食人花！看我怎么打倒他们！',Speaker=893067,}",
  [165619] = "{id=165619,Text='好吧，那您继续忙喵……',Speaker=893067,}",
  [165620] = "{id=165620,Text='这里好像有什么东西在发光喵？好像是魔物逃走时掉落的痕迹，让我们跟着光点走喵。',Speaker=893070,}",
  [165621] = "{id=165621,Text='找到了喵！就是这串项链，翠绿的宝石加上纯金的底框，和描述完全一样喵。',Speaker=893070,}",
  [165622] = "{id=165622,Text='现在我就把项链还给那个学生喵，辛苦你了[c][ffff00][PlayerName][-][/c]，这是我们的任务报酬喵~',Speaker=893070,}",
  [165623] = "{id=165623,Text='[c][ffff00][PlayerName][-][/c],我有新的发现了！',Speaker=893071,Option='你发现了什么？',}",
  [165624] = "{id=165624,Text='昨天我终于制作好了追踪+翻译一体机，正好布鲁斯抓来了一只灯笼鱼人帮我检测了一下机器是否好用。',Speaker=893071,Option='结果怎么样？',}",
  [165625] = "{id=165625,Text='我的发明当然毫无问题！通过一体机我们知道了这些魔物受到一个怪人的控制，它们通过下水道进入盥洗室，等待那个怪人的进一步指示。',Speaker=893071,}",
  [165626] = "{id=165626,Text='但是从进入到等待过去了好多天一直没有任何消息，因为饥渴困顿，它们只能在夜间偷偷溜出去找吃的，然后就是我们所知道的了。',Speaker=893071,Option='原来如此',}",
  [165627] = "{id=165627,Text='有了这些线索我们找到了它们进入的地方——下水道2F。接下来就让我们去下水道调查一下吧~',Speaker=893071,Option='没问题',}",
  [165628] = "{id=165628,Text='应该就是这里了，追踪器上显示有灯笼鱼人们留下的痕迹！',Speaker=893071,}",
  [165629] = "{id=165629,Text='waggaagaweeeeiiyuuuu!!!!',Speaker=893050,}",
  [165630] = "{id=165630,Text='你们是什么人，怎么会找到我们的中转处！难道你们想要破坏那位大人的计划？绝对不允许！',Speaker=893052,}",
  [165631] = "{id=165631,Text='不好，它们要打过来了，布鲁斯这个肌肉脑袋不在有点麻烦！',Speaker=893071,Option='交给我吧',}",
  [165632] = "{id=165632,Text='呼~还好有你在，它们的对话有点让我在意……那位大人？到底是谁在操纵这些魔物？他又想做些什么？',Speaker=893071,}",
  [165633] = "{id=165633,Text='并且那个无颅武士说这里是它们的中转处？那就说明还有另外的据点咯？看来我们还有的忙呢。',Speaker=893071,}",
  [165634] = "{id=165634,Text='今天就到这里吧，明天在普隆德拉继续调查吧~',Speaker=893071,}",
  [165635] = "{id=165635,Text='准备好出发了吗，[c][ffff00][PlayerName][-][/c]？',Speaker=893069,}",
  [165636] = "{id=165636,Text='昨天我又回到下水道做了点简单的调查，发现魔物们的移动轨迹源头在艾尔帕兰的钟楼附近，今天我们就从那开始吧。',Speaker=893071,Option='现在就走吧',}",
  [165637] = "{id=165637,Text='终于见到你了，[c][ffff00][PlayerName][-][/c]',Speaker=893057,Option='你认识我？',}",
  [165638] = "{id=165638,Text='呵呵呵，前几天感应不到我控制的那些魔物后就知道事情有变，接着做了些调查发现原来是你在妨碍我。没错，我在这里等着你过来呢。',Speaker=893057,}",
  [165639] = "{id=165639,Text='你们这些废物，快点给我控制住他们，我要让他成为我最棒的傀儡杰作！',Speaker=893057,}",
  [165640] = "{id=165640,Text='看来以我现在的力量还无法让你屈服呢，不过后会有期，我还会回来的……',Speaker=893057,Option='等等',}",
  [165641] = "{id=165641,Text='嗯？你有什么问题吗？我可以让你提问一次。',Speaker=893057,Option='{你是什么人？,2},{你想做什么？,3}',}",
  [165642] = "{id=165642,Text='我的名字是法尼瓦·伦泰，你会永远记住的……',Speaker=893059,Option='你在说什么？',}",
  [165643] = "{id=165643,Text='你只有一次提问机会……',Speaker=893059,}",
  [165644] = "{id=165644,Text='法尼瓦·伦泰？我好像在哪听到过这个名字，我需要回去查阅一下资料，今天就到这里吧，明天不见不散~',Speaker=893071,}",
  [165645] = "{id=165645,Text='很准时嘛，[c][ffff00][PlayerName][-][/c]',Speaker=893071,Option='有结果了吗',}",
  [165646] = "{id=165646,Text='我查到了，法尼瓦·伦泰，他是学院最早一届的学生会长，品学兼优，在当时的老师与学生中拥有很高的评价。',Speaker=893071,}",
  [165647] = "{id=165647,Text='可是在一次对魔物的讨伐中失踪了，学院搜寻了很久也只能无功而返，没想到他现在又重新出现并变成了现在这个样子……',Speaker=893071,Option='知道当时发生了什么吗',}",
  [165648] = "{id=165648,Text='不知道，他的那次消失在老师中一直讳莫如深，调查记录也被放在绝密档案中，我根本无法翻阅。',Speaker=893071,Option='那只能当面询问了',}",
  [165649] = "{id=165649,Text='没错，既然他说了还会再见，我们也不能坐以待毙，先进行战斗准备吧。',Speaker=893071,}",
  [165650] = "{id=165650,Text='[c][ffff00][PlayerName][-][/c]，凯奇，快跟我走！',Speaker=893069,}",
  [165651] = "{id=165651,Text='发生什么事了，布鲁斯？',Speaker=893071,}",
  [165652] = "{id=165652,Text='有多个区域遭到了魔物攻击，学院的老师和学生们都去进行战斗了，我们被分配到南门负责击退那里的魔物！',Speaker=893069,}",
  [165653] = "{id=165653,Text='事不宜迟，我们现在就前往南门！',Speaker=893071,}",
  [165654] = "{id=165654,Text='waggaagaweeeeiiyuuuu!!!!!!',Speaker=893050,}",
  [165655] = "{id=165655,Text='怎么又是这些灯笼鱼人，烦死了。',Speaker=893069,}",
  [165656] = "{id=165656,Text='别啰嗦，快点把魔物消灭掉',Speaker=893071,}",
  [165657] = "{id=165657,Text='这些魔物数量也太多了吧，法尼瓦·伦泰到底是怎么控制他们的？',Speaker=893071,}",
  [165658] = "{id=165658,Text='反正他休想控制我！',Speaker=893069,}",
  [165659] = "{id=165659,Text='这倒是实话，肌肉可没有思维可以控制……',Speaker=893071,Option='我有种感觉……',}",
  [165660] = "{id=165660,Text='大决战就要来临了，是吗？准备好迎接明天吧，希望可以安全度过……',Speaker=893071,}",
  [165661] = "{id=165661,Text='今天的天气不是很好呢，不过箭在弦上不得不发，据说法尼瓦·伦泰出现在了普隆德拉北部，我们现在就过去吧！',Speaker=893071,Option='当然',}",
  [165662] = "{id=165662,Text='呵呵呵，观众和配角们都到齐了是吗，太好了，现在开始我们的表演吧！',Speaker=893059,}",
  [165671] = "{id=165671,Text='我们……被传送出来了？',Speaker=893071,Option='看起来是这样',}",
  [165672] = "{id=165672,Text='可恶，明明已经快要打败他了！',Speaker=893069,}",
  [165673] = "{id=165673,Text='我们不能就这样坐以待毙，我去学院询问老师们有没有办法可以进入那个封锁空间里。必须在法尼瓦·伦泰进一步强大之前阻止他！',Speaker=893071,}",
  [165674] = "{id=165674,Text='希望可以听到好消息，我也要回去锻炼肌肉了，明天见！',Speaker=893069,}",
  [165675] = "{id=165675,Text='我的名字是法尼瓦·伦泰，你会永远记住的……',Speaker=893059,Option='你在答非所问？',}",
  [165678] = "{id=165678,Text='听说一年一度的黄金美食节即将开启！这种时候，怎能少了本喵？',Speaker=893065,}",
  [165679] = "{id=165679,Text='来都来了，本喵也顺手给你准备了一份特别的礼物！',Speaker=893065,}",
  [165680] = "{id=165680,Text='[c][ffff00][PlayerName][-][/c]，来吧！接受本喵的馈赠！',Speaker=893065,Option='{谢谢,2},{拒绝,3}',}",
  [165683] = "{id=165683,Text='黄金美食节召开在即，可是昨天清点物资时，我突然发现铁矿石还少了500份、远古遗骸也少了500份……可我还要布置场地，现在脱不开身……',Speaker=893066,Option='交给我吧！',}",
  [165684] = "{id=165684,Text='那么，我在这里等你，将收集到的材料交给我吧！',Speaker=893066,Emoji=21,}",
  [165685] = "{id=165685,Text='黄金美食节为每位萌友提供今日特选喵喵挚爱点心！只有和喵喵们一样讨人喜欢的小伙伴才能领取哦~',Speaker=893082,}",
  [165687] = "{id=165687,Text='[c][ffff00][PlayerName][-][/c],这份奖励送给今天最萌的你！',Speaker=893082,Option='{谢谢,2},{拒绝,3}',Emoji=20,}",
  [165710] = "{id=165710,Text='和法尼瓦·伦泰一战过后真是让我们认清了自己的实力，看来人外有人天外有天，如果没有你的帮助，我们可能会输的很惨……',Speaker=893068,}",
  [165711] = "{id=165711,Text='因此“白银之风”还有一个委托想要交给你，麻烦帮我们收集各种材料以此加强自身实力。',Speaker=893068,}",
  [165712] = "{id=165712,Text='我想制作出功能更加强大的机器，伊洛斯想要一件+15的武器，布鲁斯想新的健身器材锻炼肌肉。拜托你了！',Speaker=893068,}",
  [165713] = "{id=165713,Text='你好，[c][ffff00][PlayerName][-][/c]，我叫杰洛，是现任“黄金茶会”的会长',Speaker=893072,Option='有事吗？',}",
  [165714] = "{id=165714,Text='尼尔因为自己侮辱同学的言行已经被我们永久开除了，我们“黄金茶会”绝不容忍这种恶劣行为，在此也向你们表达深深的歉意。',Speaker=893072,}",
  [165715] = "{id=165715,Text='另外我们也想和“白银之风”达成合作，通过这次事件，我们感受到了你们的旺盛生命力与深藏的潜力，希望你们可以接受。',Speaker=893072,Option='什么合作？',}",
  [165716] = "{id=165716,Text='维护学院秩序，加强彼此之间的学习交流，等等……',Speaker=893072,Option='听起来不错',}",
  [165717] = "{id=165717,Text='正巧，“黄金茶会”正在学习占卜课程，你可以帮助我们进行课后练习吗？',Speaker=893072,Option='没问题',}",
  [165718] = "{id=165718,Text='太好了，我们也不会无偿让你帮忙，练习结束后你可以获得一些小小的报酬~',Speaker=893072,Option='好的',}",
  [165719] = "{id=165719,Text='[c][ffff00][PlayerName][-][/c]，今天也来帮助我们进行练习吗？',Speaker=893072,Option='当然',}",
  [165720] = "{id=165720,Text='很好，让我们开始吧！',Speaker=893072,}",
  [165721] = "{id=165721,Text='[c][ffff00][PlayerName][-][/c]，今天没有占卜练习哦，不过有个别的需求。',Speaker=893072,}",
  [165722] = "{id=165722,Text='“黄金茶会”的成员报告说南门出现了从[c][ffff00]黑暗空间[-][/c]中逃离的魔物，需要我们前去清除，你可以独自完成这项任务吗？当然我们依旧会提供报酬~',Speaker=893072,Option='没问题',}",
  [165723] = "{id=165723,Text='很好，那我把具体的位置告诉你吧。',Speaker=893072,}",
  [165724] = "{id=165724,Text='做的不错，收下你应得的报酬吧！',Speaker=893072,}",
  [165725] = "{id=165725,Text='没想到我们的合作能坚持到第一阶段结束呢~[c][ffff00][PlayerName][-][/c]',Speaker=893072,Option='什么意思？',}",
  [165726] = "{id=165726,Text='我们暂时已经没有更多可以进行交流学习的内容了，不过相信之后还会有机会的。',Speaker=893072,}",
  [165727] = "{id=165727,Text='“黄金茶会”的成员们对本次合作都很满意，因此他们希望你我作为代表拍一张合照，挂在俱乐部的墙上纪念此次的合作，怎么样？',Speaker=893072,Option='当然可以',}",
  [165728] = "{id=165728,Text='好的，那就一起合影吧~',Speaker=893072,}",
  [165729] = "{id=165729,Text='很好，我们看起来都很精神呢，这次合作是“黄金茶会”的一座里程碑，希望以后也可以合作愉快~',Speaker=893072,}",
  [165730] = "{id=165730,Text='[c][ffff00][PlayerName][-][/c]，我已经得到学院的老师帮助了！',Speaker=893073,}",
  [165731] = "{id=165731,Text='我们上次进入的是法尼瓦·伦泰创造的一个[c][ffff00]黑暗空间[-][/c]，理论上只有他这个主人才可以随意控制其他生命的进出，我们无法主动进入。',Speaker=893073,}",
  [165732] = "{id=165732,Text='不过我最近在南门检测到一些来历不明的黑暗元素，老师们推论是黑暗空间可能出现了一些裂缝，黑暗元素由此泄露。',Speaker=893073,}",
  [165733] = "{id=165733,Text='因此老师们决定让你组建一支队伍利用这些裂缝突袭法尼瓦·伦泰！如果你准备好了，就让我帮你传送进去吧。',Speaker=893073,}",
  [165740] = "{id=165740,Text='黄金美食节吗……我还是个孩子的时候，可盼着这种热闹了。',Speaker=893075,}",
  [165741] = "{id=165741,Text='一晃眼这么多年了。冒险者，我还有个请求。',Speaker=893075,}",
  [165742] = "{id=165742,Text='我的初恋——戴茜·安奈，上次美食节时搬去了天津町，我心中一直想要补偿她。',Speaker=893075,}",
  [165743] = "{id=165743,Text='请你问问她，还愿意与我一道参加吗？',Speaker=893075,Option='交给我吧',}",
  [165744] = "{id=165744,Text='对了，我这里有几位旧友地址，你明天可以前往拜访一下，或许能邀请到更多朋友。',Speaker=893075,Option='没问题',}",
  [165746] = "{id=165746,Text='没错，我就是希伦里亚。萨维奇的旧友。什么事？',Speaker=893076,Option='戴茜与美食节',}",
  [165747] = "{id=165747,Text='你说戴茜·安奈？没想到萨维奇还惦记着她。那时候我们三个是最好的朋友，时间真是过得飞快。',Speaker=893076,}",
  [165748] = "{id=165748,Text='我已经很多年没见过他俩了，感谢你的邀请，能让我们老友重聚。我一定会去的。',Speaker=893076,}",
  [165749] = "{id=165749,Text='对了，你去拜访一下天津町的丽娜·琼斯，或许能打听到戴茜的消息。',Speaker=893076,Option='我会去拜访',}",
  [165751] = "{id=165751,Text='没想到这么大年纪了，竟然还有外人来拜访。',Speaker=893077,Option='戴茜与美食节',}",
  [165752] = "{id=165752,Text='谢谢你的邀请，我还是第一次有机会去普隆德拉看看呢。',Speaker=893077,}",
  [165753] = "{id=165753,Text='至于戴茜·安奈……她已经搬走很多年啦，听说是去了沙漠。',Speaker=893077,Option='沙漠？',}",
  [165754] = "{id=165754,Text='对，就是梦罗克。风沙又大，真不理解为什么要去那么远的地方……',Speaker=893077,}",
  [165756] = "{id=165756,Text='我们这批人居然还没有被普隆德拉遗忘，真是令人欣慰',Speaker=893078,Option='戴茜与美食节',}",
  [165757] = "{id=165757,Text='不过你说的戴茜·安奈……只怕我能告诉你的是个坏消息……',Speaker=893078,}",
  [165758] = "{id=165758,Text='多年前，一场突如其来的沙尘暴将她所在的帐篷群卷走了……',Speaker=893078,}",
  [165759] = "{id=165759,Text='从此再也没有人知道他们的下落，但多半……唉……',Speaker=893078,Option='唉……',}",
  [165760] = "{id=165760,Text='你还要继续你的旅途吗？请去顺便拜访一下尼夫海姆的耶迪哈尔吧……',Speaker=893078,}",
  [165761] = "{id=165761,Text='这个老家伙也是许久未见了……不知道，唉……',Speaker=893078,Option='好',}",
  [165763] = "{id=165763,Text='诶？你说是奇穆委托你来的？没想到他还记得我，哈哈哈哈哈哈！',Speaker=893079,Option='美食节的邀请',}",
  [165764] = "{id=165764,Text='普隆德拉的美食节啊……要不要叫上海娜一起去呢……',Speaker=893079,Option='梦罗克？',}",
  [165765] = "{id=165765,Text='你问我为什么穿着梦罗克的衣服？',Speaker=893079,}",
  [165766] = "{id=165766,Text='很久以前，有一群沙漠的移民被沙尘暴卷来了尼夫海姆，她们就在城外定居，有时候会送我们一些衣服食物。',Speaker=893079,Option='戴茜·安奈？',}",
  [165767] = "{id=165767,Text='噢？我不认识这个人。不过你可以去城外问问，或许有人知道她。 ',Speaker=893079,Option='好',}",
  [165769] = "{id=165769,Text='戴茜·安奈？那是我姐姐，你找她有什么事吗？',Speaker=893080,Option='萨维奇的邀请',}",
  [165770] = "{id=165770,Text='如果她还在，听到这个消息一定非常高兴。可惜，她已经病逝很多年了……',Speaker=893080,Option='美食节的邀请',}",
  [165771] = "{id=165771,Text='我会去参加的，也替我姐姐谢谢他吧。',Speaker=893080,}",
  [165773] = "{id=165773,Text='太难得了！你是个新面孔！',Speaker=893081,Option='美食节的邀请',}",
  [165774] = "{id=165774,Text='黄金美食节是好事情，可是你看起来为什么有些沮丧？',Speaker=893081,Option='萨维奇的故事',}",
  [165775] = "{id=165775,Text='原来是这样。不过，等到我这个年纪，你就明白了。',Speaker=893081,}",
  [165776] = "{id=165776,Text='随着年岁渐长，身边的人注定会逐渐离去，这是世间恒久不变的常理。',Speaker=893081,}",
  [165777] = "{id=165777,Text='我们要做的，只有珍惜身边的每时每刻，多陪伴在爱人身边，不要等到对方离开了才追悔莫及。',Speaker=893081,}",
  [165778] = "{id=165778,Text='你心中有什么挂念的人吗？不妨今日就去看看他吧。',Speaker=893081,}",
  [165799] = "{id=165799,Text='今年的黄金美食节格外隆重，筹备工作可把我们忙坏了。',Speaker=893082,}",
  [165800] = "{id=165800,Text='冒险者，B格猫厨师长那里似乎遇到了些困难，你能去帮帮他吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893082,Option='{交给我吧,2},{我再想想,3}',}",
  [165801] = "{id=165801,Text='他就在普隆德拉的街道上等着，麻烦你了，冒险者！',Speaker=893082,}",
  [165802] = "{id=165802,Text='阿嚏！这可怎么办啊阿嚏！',Speaker=893083,Option='发生什么事了？',}",
  [165803] = "{id=165803,Text='啊，你就是传说中的冒险者吧阿嚏！',Speaker=893083,}",
  [165804] = "{id=165804,Text='我承办了这一届的黄金美食节，可是新品还没研究出来，我突然感冒了阿嚏！',Speaker=893083,}",
  [165805] = "{id=165805,Text='我的舌头尝不出味道了阿嚏！',Speaker=893083,Option='{黄金美食节？,2},{新品？,3}',}",
  [165806] = "{id=165806,Text='黄金美食节是一年一度的重要节日阿嚏！',Speaker=893083,}",
  [165807] = "{id=165807,Text='只有最厉害的厨师才能担任黄金美食节的主厨！没错正是在下阿嚏！',Speaker=893083,Option='{新品？,3}',}",
  [165808] = "{id=165808,Text='阿嚏！你没有听说过“秘密罐头”吗？',Speaker=893083,}",
  [165809] = "{id=165809,Text='曾经风靡梦幻之岛的超感动美食！正是由在下研发的阿嚏！',Speaker=893083,}",
  [165810] = "{id=165810,Text='可是你看现在的产品！“秘藏罐头”！“绝密罐头”！“神秘罐头”！',Speaker=893083,}",
  [165811] = "{id=165811,Text='我再不出手的话，猫咪们就只能吃这些拙劣的模仿品了阿嚏！',Speaker=893083,}",
  [165812] = "{id=165812,Text='冒险者，你能代替我的舌头，帮我尝尝新鲜的食材吗阿嚏？',Speaker=893083,Option='{交给我吧,5},{我再想想,6}',}",
  [165813] = "{id=165813,Text='感谢您，勇敢的冒险者阿嚏！让我们出发吧阿嚏！',Speaker=893083,}",
  [165814] = "{id=165814,Text='第一站，热情好客的狸猫们的家！',Speaker=893083,Option='{准备好了,8},{自行前往,9}',}",
  [165815] = "{id=165815,Text='来，小朋友们排好队，一人一颗哦~',Speaker=893084,}",
  [165816] = "{id=165816,Text='啊~张嘴！',Speaker=893084,}",
  [165817] = "{id=165817,Text='真好吃！',}",
  [165818] = "{id=165818,Text='（小狸猫们好像在分享什么美味，快上前看看吧！）',}",
  [165819] = "{id=165819,Text='咦，是没见过的客人呢！你们也要来一颗我手工制作的糖果吗？',Speaker=893084,}",
  [165820] = "{id=165820,Text='这可是狸猫族最有特色最受欢迎的特色食物呢！',Speaker=893084,}",
  [165821] = "{id=165821,Text='冒险者，快尝尝吧阿嚏！',Speaker=893083,}",
  [165822] = "{id=165822,Text='你可是这种糖果招待的第一个客人呢！',Speaker=893084,}",
  [165823] = "{id=165823,Text='吃完一定要把感想告诉我哦阿嚏！',Speaker=893083,}",
  [165824] = "{id=165824,Text='怎么样？',Speaker=893083,}",
  [165825] = "{id=165825,Text='辣、辣、辣死我了！怎、怎么会这么辣！',}",
  [165826] = "{id=165826,Text='我要烧起来了！',}",
  [165827] = "{id=165827,Text='快给他找点薄荷冰水！',Speaker=893083,}",
  [165828] = "{id=165828,Text='好、好，我马上准备！',Speaker=893084,}",
  [165829] = "{id=165829,Text='糟、糟了，没有薄荷了，得去院子里采一点！',Speaker=893084,}",
  [165830] = "{id=165830,Text='稍等，薄荷冰水马上好！',Speaker=893084,}",
  [165831] = "{id=165831,Text='快、薄荷冰水！',Speaker=893084,}",
  [165832] = "{id=165832,Text='（得、得救了……）',}",
  [165833] = "{id=165833,Text='怎么会这样，我引以为傲的手工糖果……',Speaker=893084,}",
  [165834] = "{id=165834,Text='别哭别哭阿嚏！',Speaker=893083,}",
  [165835] = "{id=165835,Text='我想这一定是因为你的糖果太有特色了，冒险者不太习惯！',Speaker=893083,}",
  [165836] = "{id=165836,Text='不过辣味糖果点亮了我的灵感，我会改良出适合大众的口味的！',Speaker=893083,}",
  [165837] = "{id=165837,Text='真的吗？',}",
  [165838] = "{id=165838,Text='那当然！我可是B格猫厨师长！',Speaker=893083,}",
  [165839] = "{id=165839,Text='阿嚏！',}",
  [165840] = "{id=165840,Text='冒险者，真是辛苦你了阿嚏！',Speaker=893083,}",
  [165841] = "{id=165841,Text='明天我们早些出发，去狂暴野猫的领地收集食材吧！',Speaker=893083,}",
  [165842] = "{id=165842,Text='狂暴野猫脾气很差，我们最好早点出发。',Speaker=893083,}",
  [165843] = "{id=165843,Text='明天在普隆德拉，不见不散阿嚏！',Speaker=893083,}",
  [165844] = "{id=165844,Text='阿嚏！朝着狂暴野猫的领地进发！',Speaker=893083,}",
  [165845] = "{id=165845,Text='出发之前，拿这个喷喷自己！',Speaker=893083,}",
  [165846] = "{id=165846,Text='（这是……一瓶喷雾？）',}",
  [165848] = "{id=165848,Text='然后再带上这块木板！',Speaker=893083,Option='为什么？',}",
  [165849] = "{id=165849,Text='要是它们发狂抓你，就用这块木板挡住阿嚏！',Speaker=893083,}",
  [165850] = "{id=165850,Text='准备万全了，我们这就出发吧！',Speaker=893083,Option='{走！,4},{再等等,2}',}",
  [165851] = "{id=165851,Text='什么人！',}",
  [165853] = "{id=165853,Text='我是B格猫厨师长！',Speaker=893083,}",
  [165854] = "{id=165854,Text='B格猫厨师长！那你一定有很多秘密罐头了！',Speaker=893088,}",
  [165855] = "{id=165855,Text='是的是的，如果你能为我介绍你们的美食，我可以送你很多秘密罐头！',Speaker=893083,}",
  [165856] = "{id=165856,Text='那你跟我来吧！',Speaker=893088,}",
  [165857] = "{id=165857,Text='（好像……比想象中要顺利……）',}",
  [165858] = "{id=165858,Text='这是什么人？',Speaker=893089,}",
  [165859] = "{id=165859,Text='报告队长！是B格猫厨师长！',Speaker=893088,}",
  [165860] = "{id=165860,Text='你们要去哪儿？',Speaker=893089,}",
  [165861] = "{id=165861,Text='报告队长！我们去水果皮仓库！',Speaker=893088,}",
  [165862] = "{id=165862,Text='你们是笨蛋吗？！居然把水果皮可以吃的秘密告诉别人？',Speaker=893089,}",
  [165863] = "{id=165863,Text='列阵！',}",
  [165864] = "{id=165864,Text='小贼还挺厉害，看我的！',Speaker=893089,}",
  [165865] = "{id=165865,Text='哼，你以为这样就赢了么？小的们，一起上！',Speaker=893089,}",
  [165866] = "{id=165866,Text='木板可挡不住这些……',Speaker=893083,}",
  [165867] = "{id=165867,Text='还是赶紧撤退吧阿嚏！',Speaker=893083,}",
  [165868] = "{id=165868,Text='事到如今可不能打退堂鼓！我们偷偷溜进狂暴野猫的仓库看看阿嚏！',Speaker=893083,}",
  [165869] = "{id=165869,Text='悄悄的，别惊动守卫……等等，那是怎么回事阿嚏？',Speaker=893083,}",
  [165870] = "{id=165870,Text='呼……呼……这一次一定能起来！',Speaker=893090,}",
  [165871] = "{id=165871,Text='它好像被脚下的水果皮绊倒了！',Speaker=893083,}",
  [165872] = "{id=165872,Text='太感谢了！如果不是你们，我不知什么时候才能爬起来！这些水果皮送给你们吧！',Speaker=893090,}",
  [165873] = "{id=165873,Text='小心不要踩上去滑倒！',Speaker=893090,}",
  [165874] = "{id=165874,Text='没想到这么轻松就得到了狂暴野猫的特色食材阿嚏！',Speaker=893083,}",
  [165875] = "{id=165875,Text='为了避免昨天那样的意外，这些食材就留到下厨那天再尝吧！',Speaker=893083,}",
  [165876] = "{id=165876,Text='明天我们去群叶猫那儿看看，同一时间，不见不散哦！',Speaker=893083,}",
  [165877] = "{id=165877,Text='[c][ffff00][PlayerName][-][/c]，终于等到你啦！',Speaker=893083,}",
  [165878] = "{id=165878,Text='今天的旅程应该相当轻松，群叶猫可是这群小家伙里最温和的一个！',Speaker=893083,}",
  [165879] = "{id=165879,Text='因为，它们全部都是“吃素的”！',Speaker=893083,}",
  [165880] = "{id=165880,Text='走吧走吧，早去早回~',Speaker=893083,Option='{早去早回~,4},{我还有事,2}',}",
  [165881] = "{id=165881,Text='呜呜，肚子好饿……',Speaker=893091,}",
  [165882] = "{id=165882,Text='（似乎有什么情况，向它们打听一下吧。）',}",
  [165883] = "{id=165883,Text='呜呜呜，我已经整整三天没吃饭了……',Speaker=893091,}",
  [165884] = "{id=165884,Text='可恶的虎王！你再坚持一下，我一定能找到办法的！',Speaker=893092,Option='我们能帮忙吗',}",
  [165885] = "{id=165885,Text='看起来是很厉害的冒险者！太好了，能请你们帮忙采一些丝状叶来吗？',Speaker=893092,}",
  [165886] = "{id=165886,Text='就是那种长得像丝线一样的树叶！',Speaker=893092,Option='包在我身上',}",
  [165887] = "{id=165887,Text='注意动作轻一点，千万不要惊动虎王！',Speaker=893092,}",
  [165888] = "{id=165888,Text='好耶！冒险者把食物带回来了！',Speaker=893092,}",
  [165889] = "{id=165889,Text='呜呜呜，终于有吃的了……球球已经饿得头晕眼花了……',Speaker=893091,}",
  [165890] = "{id=165890,Text='谢谢你们，冒险家！',Speaker=893092,Option='说明来意',}",
  [165891] = "{id=165891,Text='啊呜啊呜……',}",
  [165892] = "{id=165892,Text='特色食材吗……难得来了客人，却不能拿出羽状叶来招待了……呜……都怪虎王……',Speaker=893091,Option='{虎王？,5},{羽状叶？,6}',}",
  [165893] = "{id=165893,Text='虎王！超大！超可怕！',Speaker=893092,}",
  [165894] = "{id=165894,Text='虎王霸占了整片羽状叶林，再也没有好吃的羽状叶了……',Speaker=893091,}",
  [165895] = "{id=165895,Text='香香的、软软的羽状叶……',Speaker=893091,}",
  [165896] = "{id=165896,Text='可是现在，整片羽状叶林都被讨厌的虎王霸占着了！',Speaker=893092,}",
  [165897] = "{id=165897,Text='我们连去采丝状叶都很害怕……',Speaker=893091,Option='帮忙赶走虎王',}",
  [165898] = "{id=165898,Text='真的？你可真是我见过的最棒的冒险者！到时候我们一定送你好多好多羽状叶！',Speaker=893092,}",
  [165899] = "{id=165899,Text='（脚印往那个方向去了！）',}",
  [165901] = "{id=165901,Text='（这家伙……睡着了？）',}",
  [165902] = "{id=165902,Text='谁？！',Speaker=893093,}",
  [165903] = "{id=165903,Text='（刚才的虎王，变成了一只群叶猫？）',}",
  [165904] = "{id=165904,Text='别……别过来！',Speaker=893094,Option='你就是虎王？',}",
  [165905] = "{id=165905,Text='人家、人家的确是……呜哇……',Speaker=893094,}",
  [165906] = "{id=165906,Text='小小！怎么是你！',Speaker=893092,}",
  [165907] = "{id=165907,Text='大家都说人家长得太小了……人家就想多吃一点……',Speaker=893095,}",
  [165908] = "{id=165908,Text='快跟我回去！看族长到时候怎么拧你耳朵！',Speaker=893092,}",
  [165909] = "{id=165909,Text='不好意思，让冒险者见笑了！这里的羽状叶，想要多少就请拿多少！',Speaker=893092,}",
  [165910] = "{id=165910,Text='呼，没想到还会有这种波折……',Speaker=893083,}",
  [165911] = "{id=165911,Text='[c][ffff00][PlayerName][-][/c]，明天我们要去往最后一站，神秘的月夜猫基地，记得早来哦~',Speaker=893083,}",
  [165912] = "{id=165912,Text='月夜猫生活在很~暗~很~暗的洞窟里，所以它们的视力不太好。',Speaker=893083,}",
  [165913] = "{id=165913,Text='冒险者你也要小心呀！',Speaker=893083,}",
  [165914] = "{id=165914,Text='准备好了的话，我们就出发吧！',Speaker=893083,Option='{准备好了,2},{再等一等,3}',}",
  [165916] = "{id=165916,Text='站住！',}",
  [165917] = "{id=165917,Text='小贼哪里跑！',Speaker=893096,}",
  [165918] = "{id=165918,Text='这小贼还挺强！',Speaker=893096,}",
  [165919] = "{id=165919,Text='等等等等，谁是小贼啊阿嚏！',Speaker=893083,}",
  [165920] = "{id=165920,Text='你们、你们不就是偷走了宝贝的奶酪铃铛的小贼吗？',Speaker=893098,}",
  [165921] = "{id=165921,Text='我堂堂B格猫厨师长，精心挑选最珍贵的食材时，怎么可能用偷的阿嚏！',Speaker=893083,}",
  [165922] = "{id=165922,Text='（是谁之前说要偷狂暴野猫的水果皮来着……）',}",
  [165923] = "{id=165923,Text='emmm，不管怎么说，你们还是很可疑！',Speaker=893096,}",
  [165924] = "{id=165924,Text='把、把你们带去让族长审问！',Speaker=893098,}",
  [165925] = "{id=165925,Text='咦，这不是超——有名的美食大厨，B格猫厨师长吗？',Speaker=893097,}",
  [165926] = "{id=165926,Text='我是你的粉丝！超级粉丝！能给我签个名吗？就签在这里就行！',Speaker=893097,}",
  [165927] = "{id=165927,Text='啊，这自然没有问题，只是……阿嚏！',Speaker=893083,}",
  [165928] = "{id=165928,Text='只是？',Speaker=893097,Option='说明来意',}",
  [165929] = "{id=165929,Text='唉，这可有点为难了。我带你们去看看吧……',Speaker=893097,}",
  [165930] = "{id=165930,Text='这里是月夜猫族储藏奶酪铃铛的仓库。',Speaker=893097,}",
  [165931] = "{id=165931,Text='最近，我们的仓库守卫总会莫名其妙睡着，奶酪铃铛被搬走了大半。',Speaker=893097,}",
  [165932] = "{id=165932,Text='我们反复检查过仓库守卫的食物，都没有发现异常。',Speaker=893097,}",
  [165933] = "{id=165933,Text='我很乐意送给偶像一些奶酪铃铛，可是再这么下去，我们自己都要填不饱肚子了。',Speaker=893097,Option='帮忙抓贼',}",
  [165934] = "{id=165934,Text='天呐，偶像果然人美心善！！',Speaker=893097,}",
  [165935] = "{id=165935,Text='（咦，这儿怎么有个强光法杖……）',}",
  [165936] = "{id=165936,Text='（这儿好像有个脚印……光线太暗了，看不清，拍个照试试吧）',}",
  [165937] = "{id=165937,Text='啊！',Speaker=893099,}",
  [165938] = "{id=165938,Text='呼噜……呼噜……',}",
  [165939] = "{id=165939,Text='（咦，为什么我一拍照它就睡着了？）',}",
  [165940] = "{id=165940,Text='（月夜猫害怕强光！遇到强光就会昏睡过去！）',}",
  [165941] = "{id=165941,Text='（所以是耗子们用强光法杖让月夜猫昏迷，然后搬走了奶酪铃铛。）',}",
  [165942] = "{id=165942,Text='（把这个结论告诉月夜猫族长叮当吧。）',}",
  [165943] = "{id=165943,Text='原来如此！叮咚，叮铃，快去采购一批防强光眼镜！',Speaker=893097,}",
  [165944] = "{id=165944,Text='是！',}",
  [165945] = "{id=165945,Text='多谢偶像的帮助，这些奶酪铃铛聊表我们的感激之情，请您一定要收下！',Speaker=893097,}",
  [165946] = "{id=165946,Text='小意思、小意思阿嚏！',Speaker=893083,}",
  [165947] = "{id=165947,Text='（好像是我查出真相的吧……）',}",
  [165948] = "{id=165948,Text='这样，我们的特色食材就收集齐啦！',Speaker=893083,}",
  [165949] = "{id=165949,Text='明天我们在普隆德拉正式开始新食谱的研制，等你哦~阿嚏！',Speaker=893083,}",
  [165950] = "{id=165950,Text='准备完毕！就等[c][ffff00][PlayerName][-][/c]你来品尝啦~',Speaker=893083,}",
  [165951] = "{id=165951,Text='我们收集到的第一份食材——狸猫族的辣味糖果！要再尝一口吗阿嚏？',Speaker=893083,Option='（用力摇头）',}",
  [165952] = "{id=165952,Text='哈哈哈，我还记得你被辣得满头冒烟的样子阿嚏！',Speaker=893083,}",
  [165953] = "{id=165953,Text='那么，就从狂暴野猫的水果皮开始吧阿嚏！',Speaker=893083,}",
  [165954] = "{id=165954,Text='怎么样，怎么样？',Speaker=893083,Option='滑溜溜的',}",
  [165955] = "{id=165955,Text='口味呢阿嚏？',Speaker=893083,Option='没有味道',}",
  [165956] = "{id=165956,Text='性格暴躁的狂暴野猫居然会喜欢这种平淡的食物……无用的知识又增加了呢阿嚏！',Speaker=893083,}",
  [165957] = "{id=165957,Text='接下来尝尝羽状叶吧，形状看起来很是优美呢阿嚏！',Speaker=893083,}",
  [165958] = "{id=165958,Text='你的表情有点奇怪，发生什么了阿嚏？',Speaker=893083,Option='好……苦……',}",
  [165959] = "{id=165959,Text='看来羽状叶的口味很难接受呢阿嚏！',Speaker=893083,}",
  [165960] = "{id=165960,Text='还有最后一份奶酪铃铛，快试试吧阿嚏！',Speaker=893083,}",
  [165961] = "{id=165961,Text='这个怎么样？',Speaker=893083,Option='浓浓的奶香味',}",
  [165962] = "{id=165962,Text='嗯，和我想象中一样阿嚏！',Speaker=893083,}",
  [165963] = "{id=165963,Text='我的看法？你是不是没想到这些食物的口味如此古怪阿嚏？',Speaker=893083,}",
  [165964] = "{id=165964,Text='其实我在以前的研究中早就发现啦，不同地区的大家偏好的口味……阿嚏！',Speaker=893083,}",
  [165965] = "{id=165965,Text='也完全不同。大家最喜欢的还是自己家乡的味道呢阿嚏！',Speaker=893083,}",
  [165966] = "{id=165966,Text='不过作为B格猫厨师长，还是要拿出最适合大众的食物来阿嚏！',Speaker=893083,}",
  [165967] = "{id=165967,Text='奶酪铃铛作为主要的食材口味，再模拟水果皮的口感，制作成羽状叶的形状……',Speaker=893083,}",
  [165968] = "{id=165968,Text='这会是完美的料理阿嚏！我现在就去制作阿嚏！只可惜辣味糖果用不上了阿嚏！',Speaker=893083,}",
  [165969] = "{id=165969,Text='（小心！——）',}",
  [165970] = "{id=165970,Text='（B格猫厨师长踩上水果皮摔倒了！）',}",
  [165971] = "{id=165971,Text='好辣！阿嚏阿嚏阿嚏！',}",
  [165972] = "{id=165972,Text='（辣味糖果不小心掉进了他嘴里！）',}",
  [165973] = "{id=165973,Text='阿嚏阿嚏阿嚏！',Speaker=893083,}",
  [165974] = "{id=165974,Text='咦，我的鼻塞好像治好了！',Speaker=893083,}",
  [165975] = "{id=165975,Text='看来辣味糖果也有它的妙用呢~',Speaker=893083,}",
  [165976] = "{id=165976,Text='期待吧，我会为你们呈上最独特的黄金美食节！',Speaker=893083,}",
  [165977] = "{id=165977,Text='感谢你的帮助，冒险者。',Speaker=893075,}",
  [165978] = "{id=165978,Text='今天是个特别的日子，因为你的善意让我们这些老人感受到了温暖。',Speaker=893075,}",
  [165979] = "{id=165979,Text='[c][ffff00][PlayerName][-][/c],这是给你的谢礼。',Speaker=893075,Option='{谢谢,2},{拒绝,3}',}",
  [165982] = "{id=165982,Text='嗨~能请你帮我和老人家们拍个照吗？',Speaker=893107,}",
  [165983] = "{id=165983,Text='他们好不容易从远方来到普隆德拉参加黄金美食节狂欢，让我们记录下这珍贵的时刻吧！',Speaker=893107,}",
  [165984] = "{id=165984,Text='拍得真不错，完全展现了我的风姿~',Speaker=893107,}",
  [165986] = "{id=165986,Text='活动期间，每天都可以来品尝一次「喵喵蛋糕」噢！',Speaker=893108,Option='咬下去！',}",
  [165987] = "{id=165987,Text='甜甜的味道，软糯的口感，一口下去大大的满足！啊，这就是幸福的感觉！\n[c][ffff00]获得Buff：击杀魔物Job经验＋50%[-][/c]',Speaker=893108,}",
  [165995] = "{id=165995,Text='美食节还有许多其他有意思的活动，[c][ffff00][PlayerName][-][/c]你也别错过哦~',Speaker=893107,}",
  [165996] = "{id=165996,Text='所以、我就用变身药水变成了虎王……可是吃得好撑、怎么都变不大！',Speaker=893095,}",
  [165997] = "{id=165997,Text='（先收着，继续观察四周吧。）',}",
  [166022] = "{id=166022,Text='这次爆米花嘉年华多亏了[c][ffff00][PlayerName][-][/c]的帮助呢~',Speaker=893119,}",
  [166023] = "{id=166023,Text='这点心意还请务必收下~！',Speaker=893119,Option='{谢谢,2},{过会再领,3}',}",
  [166024] = "{id=166024,Text='由于魔物的侵袭，本次嘉年华需要大量的材料来重建……',Speaker=893119,}",
  [166025] = "{id=166025,Text='[c][ffff00][PlayerName][-][/c]如果能出手相助的话，组委会一定会感激你的！',Speaker=893119,}",
  [166026] = "{id=166026,Text='那些可恶的魔物偷走了嘉年华的原料，真是急死我了！',Speaker=893120,Option='哪些魔物？',}",
  [166027] = "{id=166027,Text='[c][ffff00]「料理精粹」[-][/c]被[c][ffff00]水属性的魔物[-][/c]偷走了，[c][ffff00]「玉米粒」[-][/c]被[c][ffff00]动物种族的魔物[-][/c]偷走了',Speaker=893120,}",
  [166028] = "{id=166028,Text='如果冒险者能追回一些料理精粹的话，我会给你一些嘉年华特制的奖励哦~',Speaker=893120,}",
  [166029] = "{id=166029,Text='确定要提交[c][ffff00]「料理精粹」[-][/c]x10吗？',Speaker=893120,Option='{确定,4},{再想想,3}',}",
  [166030] = "{id=166030,Text='冒险者，你身上暂时没有足够的材料哦！请先去获取后再来捐献吧！击败[c][ffff00]水属性的魔物[-][/c]就有概率获得「料理精粹」哦~',Speaker=893120,}",
  [166031] = "{id=166031,Text='感谢你的付出！',Speaker=893120,}",
  [166041] = "{id=166041,Text='你好呀，冒险者！我是多兰族美食家罗利·曲奇，人们都叫我“黄油先生”。',Speaker=891519,}",
  [166042] = "{id=166042,Text='有没有兴趣和我一起举办今年的“[c][ffff00]爆米花嘉年华[-][/c]”呀？',Speaker=891519,Option='爆米花嘉年华',}",
  [166043] = "{id=166043,Text='爆米花嘉年华是为了庆祝丰收而在大陆各地举办的超大型庆典，我们会将收获的玉米做成美味的爆米花供大家享用！',Speaker=891519,}",
  [166044] = "{id=166044,Text='怎么样，有没有兴趣加入我们？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891519,Option='{好呀好呀,2},{我再想想,3}',}",
  [166045] = "{id=166045,Text='这样的机会不常有，可不要错过哦！',Speaker=891519,}",
  [166046] = "{id=166046,Text='那就请你替我巡查一下各地的庆典筹备情况吧！就从南门开始吧，去找我的下属妙卡·银牙吧，他是那里的负责人~',Speaker=891519,}",
  [166047] = "{id=166047,Text='可恶啊喵！究竟是谁干的喵！',Speaker=891520,}",
  [166048] = "{id=166048,Text='可恶的小偷喵……竟然连玉米都要……',Speaker=891520,}",
  [166049] = "{id=166049,Text='诶？你是黄油先生派来巡查庆典筹备状况的喵？唉……',Speaker=891520,Option='怎么了？',}",
  [166050] = "{id=166050,Text='用于南门庆典的爆米花原料——玉米被盗了喵！',Speaker=891520,}",
  [166051] = "{id=166051,Text='真是太可恶了，如果没有那些玉米，南门的庆典就办不成了喵！',Speaker=891520,}",
  [166052] = "{id=166052,Text='冒险者，能不能请你去现场调查一下，看看小偷有没有留下什么线索……',Speaker=891520,}",
  [166053] = "{id=166053,Text='（地上有很多杂乱无章的脚印——那是什么？）',}",
  [166054] = "{id=166054,Text='这是——一块怀表？上面还刻着一个名字：詹姆——这很有可能是犯人遗落的！',}",
  [166055] = "{id=166055,Text='你回来了，冒险者！调查的怎么样喵？',Speaker=891520,Option='提交证物',}",
  [166056] = "{id=166056,Text='遗留在现场的脚印和怀表嘛……是很关键的证据呢喵~依靠这些，应该很快就能找到盗窃玉米的犯人了喵！',Speaker=891520,}",
  [166057] = "{id=166057,Text='哼，给我们带来这么大的麻烦，等我抓到他，一定要好好教训他一顿喵！',Speaker=891520,Emoji=12,}",
  [166058] = "{id=166058,Text='昨天晚上，在接到了盗贼名叫詹姆的消息后，我就前往詹姆家——毕竟这附近只有他一个叫詹姆的人……',Speaker=891523,}",
  [166059] = "{id=166059,Text='在他家附近，我看到他[c][ffff00]晃晃悠悠[-][/c]地走回了自己家……',Speaker=891523,}",
  [166061] = "{id=166061,Text='你说的这些我完全没有印象……',Speaker=891521,}",
  [166062] = "{id=166062,Text='反对无效！听证人继续陈述证词……',Speaker=891539,}",
  [166063] = "{id=166063,Text='唉，还在嘴硬吗喵~[c][ffff00]天都黑了[-][/c]，快点认罪吧，大家都要睡觉呢！',Speaker=891520,}",
  [166064] = "{id=166064,Text='……我跟着詹姆进了房间，发现他非常奇怪……',Speaker=891523,Option='奇怪？',}",
  [166065] = "{id=166065,Text='我发现他——表情呆滞，嘴里还一直念叨着：我好饿啊，我好饿啊……',Speaker=891523,}",
  [166066] = "{id=166066,Text='等等，证人……你说的——是不是就是这个样子？！',Speaker=891539,}",
  [166067] = "{id=166067,Text='我好饿啊，我好饿啊……',Speaker=891521,}",
  [166068] = "{id=166068,Text='我好饿啊，我好饿啊……',Speaker=891521,}",
  [166069] = "{id=166069,Text='啊啊啊！我要食物！！',Speaker=891521,}",
  [166070] = "{id=166070,Text='食物……',Speaker=891521,}",
  [166071] = "{id=166071,Text='这是怎么回事……他的举止太诡异了，就像是中了邪一样……',Speaker=891539,Emoji=19,}",
  [166072] = "{id=166072,Text='看来这边事并不是盗窃案那么简单，我需要好好调查一下！之后可能还需要你的协助，冒险者！',Speaker=891539,}",
  [166074] = "{id=166074,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为猎灵士了吗？',Speaker=893122,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [166075] = "{id=166075,Text='转职过程中遇到困难了吗？看来你需要继续修炼。',Speaker=893122,}",
  [166076] = "{id=166076,Text='继续加油，成功就在眼前啦！',Speaker=893122,}",
  [166077] = "{id=166077,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为双生魔灵了吗？',Speaker=893122,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [166078] = "{id=166078,Text='哇，你成功了！现在是出色的猎灵士了。',Speaker=893122,}",
  [166079] = "{id=166079,Text='但是还不能松懈，你可以更上一层楼！',Speaker=893122,}",
  [166080] = "{id=166080,Text='这是我小小的心意，请你收下。',Speaker=893122,}",
  [166082] = "{id=166082,Text='你已经成为了这么强大的双生魔灵了！',Speaker=893122,}",
  [166083] = "{id=166083,Text='成长的如此快，你一定经历了不少难忘的冒险吧！',Speaker=893122,}",
  [166084] = "{id=166084,Text='不要忘了继续修炼自己，成为更强大的冒险家！',Speaker=893122,}",
  [166085] = "{id=166085,Text='这是我的心意，请你收下。',Speaker=893122,}",
  [166086] = "{id=166086,Text='冒险者，快来！我找到了！我找到詹姆举止诡异、盗窃玉米的原因了！',Speaker=891539,}",
  [166087] = "{id=166087,Text='我翻阅了许多资料才查到——这是一种诅咒！来自夜枭恶魔的诅咒！',Speaker=891539,}",
  [166088] = "{id=166088,Text='夜枭恶魔是传说中出没在夜色中的魔物，它会散播带来饥饿感的诅咒……',Speaker=891539,}",
  [166089] = "{id=166089,Text='被它诅咒的人在夜晚到来时样貌会被扭曲，思想会被控制，会变成被饥饿感驱使、只会跟随本能寻找食物的行尸走肉！',Speaker=891539,}",
  [166090] = "{id=166090,Text='更可怕的是，这种诅咒——会在人与人之间传播，所以……',Speaker=891539,Option='{所以？,0},{难道说……,0}',}",
  [166091] = "{id=166091,Text='现在的普隆德拉——也许有很多人被诅咒感染了！！',Speaker=891539,}",
  [166092] = "{id=166092,Text='天快要黑了！',Speaker=891539,}",
  [166094] = "{id=166094,Text='食物……食物……',Speaker=891528,}",
  [166095] = "{id=166095,Text='这些被诅咒的居民……一定是被这里囤积的玉米食材吸引过来的！',Speaker=891539,}",
  [166096] = "{id=166096,Text='我好饿啊……我好饿啊……',Speaker=891528,}",
  [166098] = "{id=166098,Text='又来了！数量太多了……',Speaker=891539,}",
  [166099] = "{id=166099,Text='撑到天亮，等诅咒的影响消退吧！',Speaker=891539,}",
  [166100] = "{id=166100,Text='呼……终于，天亮了！',Speaker=891539,}",
  [166101] = "{id=166101,Text='夜枭恶魔的诅咒是可以解除的！',Speaker=891539,}",
  [166102] = "{id=166102,Text='只要能想办法消除被诅咒者的饥饿感，诅咒自然也就消失了……',Speaker=891539,}",
  [166103] = "{id=166103,Text='我需要找找消除人们饥饿感的方法！冒险者，等我的好消息吧！',Speaker=891539,}",
  [166104] = "{id=166104,Text='冒险者，我找到清除诅咒的方法了！',Speaker=891539,}",
  [166105] = "{id=166105,Text='我听说……雷根贝勒公司最近在培育一种神奇的玉米……',Speaker=891539,}",
  [166106] = "{id=166106,Text='这种玉米只要吃上一口，就能三天不饿！',Speaker=891539,Option='{真的假的？,0},{这么神奇！,0}',}",
  [166107] = "{id=166107,Text='嗯，相信凭借这种玉米，应该可以消除夜枭恶魔的诅咒！',Speaker=891539,}",
  [166108] = "{id=166108,Text='准备好了，我们就去“收集”一些这种玉米吧！',Speaker=891539,Option='{准备好了,2},{稍后再去,3}',}",
  [166109] = "{id=166109,Text='就是这里了！小心点，别被发现了！',Speaker=891539,}",
  [166110] = "{id=166110,Text='什么人！',Speaker=891538,}",
  [166111] = "{id=166111,Text='糟糕，被发现了！',Speaker=891539,}",
  [166112] = "{id=166112,Text='趁现在，赶快离开吧！',Speaker=891539,}",
  [166113] = "{id=166113,Text='太好了~超级玉米到手了！',Speaker=891539,}",
  [166114] = "{id=166114,Text='有了它，就能根除夜枭恶魔的诅咒了！',Speaker=891539,Option='怎么做呢？',}",
  [166115] = "{id=166115,Text='我已经有了一个好主意！等着瞧吧，冒险者~~',Speaker=891539,}",
  [166116] = "{id=166116,Text='你好呀，冒险者！欢迎来到爆米花嘉年华的开幕式！',Speaker=891539,}",
  [166117] = "{id=166117,Text='哼哼，你是不是觉得因为诅咒的影响，爆米花嘉年华没法如期举行了呀？',Speaker=891539,}",
  [166118] = "{id=166118,Text='不过是小小诅咒……怎么能难得到我“黄油先生”，没有什么能够阻止爆米花嘉年华的召开！',Speaker=891539,}",
  [166119] = "{id=166119,Text='冒险者，快帮我把爆米花需要的材料放入爆米花机里！',Speaker=891539,Option='爆米花机？',}",
  [166120] = "{id=166120,Text='啊，虽然长得很像大炮，但是它们真的是爆米花机哦！',Speaker=891539,}",
  [166121] = "{id=166121,Text='很好~这样就准备完成了！正好……天也快黑了……',Speaker=891539,}",
  [166122] = "{id=166122,Text='（天黑？？？）',Emoji=8,}",
  [166123] = "{id=166123,Text='我好饿啊……我好饿啊……',Speaker=891528,}",
  [166127] = "{id=166127,Text='终于来了……你们的饥饿感，就由我来消除！',Speaker=891539,}",
  [166128] = "{id=166128,Text='冒险者，准备开炮！',Speaker=891539,}",
  [166129] = "{id=166129,Text='（你不是说那是爆米花机吗……）',}",
  [166130] = "{id=166130,Text='开火！',Speaker=891539,}",
  [166131] = "{id=166131,Text='（这是……）',}",
  [166132] = "{id=166132,Text='（漫天的爆米花如同雪花般飘落，在地面上铺散出一条金黄色的道路……）',}",
  [166133] = "{id=166133,Text='嗷嗷嗷……食物…………食物…………',Speaker=891528,}",
  [166134] = "{id=166134,Text='……不……不饿了……嗝~',Speaker=891528,}",
  [166135] = "{id=166135,Text='这是哪儿？我怎么会在这里……',Speaker=891530,}",
  [166136] = "{id=166136,Text='欢迎各位来到爆米花嘉年华的开幕式！！',Speaker=891539,}",
  [166137] = "{id=166137,Text='我们为大家准备了很多美味的爆米花哦~好好享受这场狂欢吧！！',Speaker=891539,}",
  [166138] = "{id=166138,Text='可恶的家伙，竟敢坏我的好事！',Speaker=891533,}",
  [166139] = "{id=166139,Text='不可饶恕！竟敢破坏我统治世界的计划！我要杀了你们！！',Speaker=891533,}",
  [166140] = "{id=166140,Text='（这就是……夜枭恶魔？）',}",
  [166141] = "{id=166141,Text='这……这家伙是谁啊？',Speaker=891531,}",
  [166142] = "{id=166142,Text='我一定……会回来的……',Speaker=891533,}",
  [166143] = "{id=166143,Text='（小声）终于解决了……',Speaker=891539,}",
  [166144] = "{id=166144,Text='各位居民~这是我们为大家准备的余兴节目~希望大家喜欢！',Speaker=891539,}",
  [166145] = "{id=166145,Text='原来是这样……这表演还挺逼真的！',Speaker=891532,}",
  [166146] = "{id=166146,Text='虽然有很多波折，但是事件总算圆满解决了！',Speaker=891539,}",
  [166147] = "{id=166147,Text='这一届的爆米花嘉年华顺利举办了~冒险者，好好享受庆典吧！',Speaker=891539,}",
  [166149] = "{id=166149,Text='冒险者，快来！一起见证对“玉米小偷”的审判吧喵！',Speaker=891520,}",
  [166150] = "{id=166150,Text='詹姆，昨晚南门仓库遗失了一批用于爆米花嘉年华的玉米……',Speaker=891539,}",
  [166151] = "{id=166151,Text='根据我们掌握的证据——你就是最大的嫌疑人！',Speaker=891539,}",
  [166152] = "{id=166152,Text='我是冤枉的！我只是一个住在这附近的普通平民！',Speaker=891521,}",
  [166153] = "{id=166153,Text='[c][ffff00]昨晚我很早就睡觉了，根本没有去过仓库附近啊！[-][/c]',Speaker=891521,}",
  [166154] = "{id=166154,Text='哼……还嘴硬喵！冒险者，出示证据，拆穿他的谎言吧喵！',Speaker=891520,Option='{出示照片,2},{出示怀表,3}',}",
  [166155] = "{id=166155,Text='异议！！',}",
  [166156] = "{id=166156,Text='这是……一些脚印？虽然看上去跟我的鞋子差不多大……可是也证明不了这是我的脚印啊！',Speaker=891521,}",
  [166157] = "{id=166157,Text='还真是难缠的对手，冒险者，出示那项能证明他在现场的关键证据吧！',Speaker=891520,Option='出示怀表',}",
  [166158] = "{id=166158,Text='异议！！',}",
  [166159] = "{id=166159,Text='这、、、这是我的怀表？！为什么会出现在你手上？',Speaker=891521,Option='案发现场',}",
  [166160] = "{id=166160,Text='这怎么可能？！昨晚我真的早早地睡觉了，没有去过那里啊！！',Speaker=891521,Emoji=24,}",
  [166161] = "{id=166161,Text='还不承认吗？！那就请昨晚逮捕你的卫兵作为证人，前来作证吧！',Speaker=891539,}",
  [166162] = "{id=166162,Text='冒险者，在你帮助下，这次的爆米花嘉年华我们制作了很多爆米花喵~',Speaker=891551,}",
  [166163] = "{id=166163,Text='那些在冒险旅途中帮助过我们的伙伴，这一年来也辛苦了喵~',Speaker=891551,}",
  [166164] = "{id=166164,Text='不如送一些爆米花给他们慰劳一下吧喵~~',Speaker=891551,}",
  [166165] = "{id=166165,Text='这几天的爆米花嘉年华可真热闹啊！',Speaker=1014,}",
  [166166] = "{id=166166,Text='要是能去参加就好了！',Speaker=1014,Option='{送给你的,0},{辛苦了！,0}',}",
  [166167] = "{id=166167,Text='诶？这爆米花是送给我的嘛！真是太感谢了！感觉更有干劲了，一定要多来精炼装备哦，[c][ffff00][PlayerName][-][/c]！',Speaker=1014,}",
  [166168] = "{id=166168,Text='交易所全年无休~~为您提供最最安全最便捷的服务！',Speaker=2159,}",
  [166169] = "{id=166169,Text='没有休息，连爆米花嘉年华都去不了呢……',Speaker=2159,Option='{送给你的,0},{辛苦了！,0}',}",
  [166170] = "{id=166170,Text='送给我的吗？谢谢你，冒险者！真好吃啊，感觉所有辛苦都是值得的呢……',Speaker=2159,}",
  [166171] = "{id=166171,Text='城里正在举办爆米花嘉年华~~',Speaker=1679,}",
  [166172] = "{id=166172,Text='大家要记得去参加哦！',Speaker=1679,Option='{送给你的,0},{辛苦了！,0}',}",
  [166173] = "{id=166173,Text='真是太感谢您了，冒险者！未来也要继续支持卡普拉公司哦~',Speaker=1679,}",
  [166174] = "{id=166174,Text='啊，好无聊……',Speaker=2195,}",
  [166175] = "{id=166175,Text='好想去参加爆米花嘉年华……',Speaker=2195,Option='{送给你的,0},{辛苦了！,0}',}",
  [166176] = "{id=166176,Text='唔，看上去挺好吃的~谢谢你咯，冒险者！',Speaker=2195,}",
  [166177] = "{id=166177,Text='身为普隆德拉最好的造型师……',Speaker=1017,}",
  [166178] = "{id=166178,Text='即使是爆米花嘉年华时，也要坚守自己的岗位呢！',Speaker=1017,Option='{送给你的,0},{辛苦了！,0}',}",
  [166179] = "{id=166179,Text='这是送给我的？真是太感谢了！有空来找我，给你做个漂亮的新发型！',Speaker=1017,}",
  [166180] = "{id=166180,Text='城里最近好热闹啊，听说是举办了爆米花嘉年华……',Speaker=1694,}",
  [166181] = "{id=166181,Text='本王也想参加啊，爆米花应该很美味吧……',Speaker=1694,Option='{送给你的,0},{辛苦了！,0}',}",
  [166182] = "{id=166182,Text='唔，果然很美味啊……嗯嗯，谢谢你冒险者！祝你合成出自己想要的卡片！',Speaker=1694,}",
  [166183] = "{id=166183,Text='喵！每天来附魔的冒险者那么多！',Speaker=2157,}",
  [166184] = "{id=166184,Text='根本没时间去参加爆米花嘉年华呀！~喵~',Speaker=2157,Option='{送给你的,0},{辛苦了！,0}',}",
  [166185] = "{id=166185,Text='喵？这爆米花看起来真不错！谢谢你冒险者！你这样的好人附魔一定出极品属性的哦！',Speaker=2157,}",
  [166189] = "{id=166189,Text='爆米花嘉年华中有很多味道的爆米花，我都还没有尝过，你能帮我找一些吗？',Speaker=893120,Option='好呀好呀',}",
  [166190] = "{id=166190,Text='听说这次有[c][ffff00]原味爆米花[-][/c]、[c][ffff00]孜然爆米花[-][/c]、[c][ffff00]麻辣爆米花[-][/c]、[c][ffff00]奶油爆米花[-][/c]和[c][ffff00]焦糖爆米花[-][/c]5种口味……',Speaker=893120,}",
  [166191] = "{id=166191,Text='你能找齐5种口味带给我吗？我会准备一份神秘礼物给你！',Speaker=893120,Option='{拿出爆米花,2},{我这就去,3}',}",
  [166192] = "{id=166192,Text='你身上并没有5种爆米花，再去找找吧！',Speaker=893120,}",
  [166193] = "{id=166193,Text='哇！！这就是我想要的爆米花，谢谢你[c][ffff00][PlayerName][-][/c]。这是为你准备礼物，去享受嘉年华吧！',Speaker=893120,}",
  [166211] = "{id=166211,Text='唉，这讨厌的卡拉特小丑！',Speaker=893131,Option='发生什么事了？',}",
  [166212] = "{id=166212,Text='原来是你啊，冒险者，你还不知道吗？卡拉特小丑又在姜饼城捣乱了！',Speaker=893131,Option='他做了什么？',}",
  [166213] = "{id=166213,Text='我好不容易布置的圣诞氛围被他破坏的一塌糊涂。要是短期内修不好我就要被扣酬金了！',Speaker=893131,}",
  [166214] = "{id=166214,Text='太好了，那就让我帮你准备一下吧。',Speaker=893131,}",
  [166215] = "{id=166215,Text='好吧，那就等你有空了再说……',Speaker=893131,}",
  [166216] = "{id=166216,Text='你已经帮我制止他了？太感谢了，希望现在能让我好好布置氛围……',Speaker=893131,}",
  [166237] = "{id=166237,Text='（盒子上贴着一个闪亮的封条。要揭开吗？）',Speaker=891552,}",
  [166238] = "{id=166238,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891552,Option='{揭开封条,2},{掉头就走,3}',}",
  [166239] = "{id=166239,Text='（礼盒里飞出来漫天的姜饼仔！！）',}",
  [166240] = "{id=166240,Text='完蛋噜——姜饼仔爆炸噜——',Speaker=891554,}",
  [166241] = "{id=166241,Text='捉住噜！！',Speaker=891554,}",
  [166242] = "{id=166242,Text='犯人！！就是你噜！！',Speaker=891554,}",
  [166243] = "{id=166243,Text='你这个犯人，我看到你偷偷捡姜饼仔了，快交出来噜！',Speaker=891554,Emoji=12,}",
  [166244] = "{id=166244,Text='这才像话噜！这些姜饼仔是给[c][ffff00]圣诞魔法师[-][/c]的礼物，可不能弄丢了噜！',Speaker=891554,Option='圣诞魔法师？',}",
  [166245] = "{id=166245,Text='是的噜！据说他住在姜饼城，可以实现人们关于圣诞的愿望……',Speaker=891554,Option='听起来不错',}",
  [166246] = "{id=166246,Text='…………你难道也对圣诞魔法师感兴趣噜？',Speaker=891554,}",
  [166247] = "{id=166247,Text='咱有非常重要的事，必须要找到他噜！如果你要一起的话，我们明天就[c][ffff00]姜饼城[-][/c]碰头噜！',Speaker=891554,}",
  [166248] = "{id=166248,Text='（和奇怪的驯鹿许下了约定。明天记得去姜饼城看看吧。）',}",
  [166249] = "{id=166249,Text='你来了噜！昨天忘记自我介绍，咱是鲁鲁道夫，一个圣诞特工！',Speaker=891554,Option='圣诞特工？？',}",
  [166250] = "{id=166250,Text='没错噜，咱负责处理圣诞相关的特殊委托噜……不多说了，得快去找圣诞魔法师噜！',Speaker=891554,Option='要怎么做？',}",
  [166251] = "{id=166251,Text='还记得昨天的姜饼仔噜？先把它们洒在地上吧！',Speaker=891554,}",
  [166252] = "{id=166252,Text='干得好噜！再来两只！',Speaker=891554,}",
  [166253] = "{id=166253,Text='不错噜！最后一只！',Speaker=891554,}",
  [166254] = "{id=166254,Text='（这些姜饼仔真的能引来圣诞魔法师吗……）',Emoji=14,}",
  [166255] = "{id=166255,Text='嘘……好像有什么要来了！快躲到一边噜！',Speaker=891554,}",
  [166256] = "{id=166256,Text='是谁——放了这些姜饼仔——嗝？',Speaker=891563,}",
  [166257] = "{id=166257,Text='救命啊……白熊！白熊会说话噜…………',Speaker=891554,}",
  [166258] = "{id=166258,Text='（白熊慢悠悠地撇了撇嘴。）',}",
  [166259] = "{id=166259,Text='大……大惊小怪。你这只小鹿崽子，不也会说话吗。嗝。',Speaker=891563,Option='……你是谁啊？',}",
  [166260] = "{id=166260,Text='咳咳咳。我，是传说中的，圣诞魔法师——西洛尼亚——嗝。',Speaker=891563,Option='{圣诞魔法师？,0},{西洛尼亚嗝,0}',}",
  [166261] = "{id=166261,Text='……叫我西洛就行了！话说，你们准备这些姜饼仔，难道是有事要求我？……嗝。',Speaker=891563,}",
  [166262] = "{id=166262,Text='没错没错噜！',Speaker=891554,}",
  [166263] = "{id=166263,Text='那个……我很愿意，很愿意帮助你们。不过，我今天太累了。嗝。',Speaker=891556,Option='{难道是吃多了？,0},{你需要休息吗？,0}',Emoji=15,}",
  [166264] = "{id=166264,Text='好了，我们明……[c][ffff00]明天[-][/c]，见……嗝！',Speaker=891556,}",
  [166265] = "{id=166265,Text='……那头熊是吃撑了吧？一定是吧？是吧是吧？',Speaker=891554,}",
  [166266] = "{id=166266,Text='看来只能明天再找它了噜。如果它不能实现我的愿望……我就把它做成烤熊饼噜！',Speaker=891554,}",
  [166267] = "{id=166267,Text='又见面了，姜饼仔小可爱。',Speaker=891556,Option='{你不打隔了？,2},{姜饼仔小可爱？,3}',}",
  [166268] = "{id=166268,Text='咳咳。我今天，很注意饮食。',Speaker=891556,}",
  [166269] = "{id=166269,Text='只是一个爱称。别介意，姜饼仔小可爱。',Speaker=891556,}",
  [166270] = "{id=166270,Text='怎么只来了你一个？那只小鹿崽子呢？',Speaker=891556,}",
  [166271] = "{id=166271,Text='什么小鹿崽子！大爷我是圣诞特工鲁鲁道夫！噜！',Speaker=891554,}",
  [166272] = "{id=166272,Text='好的，鲁鲁道夫大爷。所以您是有什么了不得的圣诞愿望，要来找本魔法师？',Speaker=891556,}",
  [166273] = "{id=166273,Text='………………',Speaker=891554,}",
  [166274] = "{id=166274,Text='………………[c][ffff00]我想要一场雪[-][/c]。',Speaker=891554,}",
  [166275] = "{id=166275,Text='我想要一场雪……准确来说，是想要[c][ffff00]克魔岛[-][/c]上下一场雪。',Speaker=891554,}",
  [166276] = "{id=166276,Text='（虽然但是，克魔岛不是热带吗？？）',}",
  [166277] = "{id=166277,Text='是的噜。我是圣诞特工，我的最高级别任务，就是给克魔岛带来一场雪噜。',Speaker=891554,}",
  [166278] = "{id=166278,Text='……唔唔，也不是做不到。这样吧，你们先去找几朵冰霜花来。记住要[c][ffff00]闪着蓝光的冰霜花[-][/c]啊，采错了会被冻伤的！',Speaker=891556,}",
  [166279] = "{id=166279,Text='好吧，我先去旁边找找噜。',Speaker=891554,}",
  [166280] = "{id=166280,Text='（被冰霜花冻伤了！要采集[c][ffff00]闪着蓝光[-][/c]的花才行……）',}",
  [166281] = "{id=166281,Text='嗯哼，你们动作很快嘛！姜饼仔小可爱，你找到花了吗？',Speaker=891556,}",
  [166282] = "{id=166282,Text='喂，你这头熊，我也找到了噜！',Speaker=891554,}",
  [166283] = "{id=166283,Text='就是这些了！我今天回去准备，明天克魔岛见！',Speaker=891556,}",
  [166284] = "{id=166284,Text='……好怪啊噜。这头熊竟然答应的这么爽快？',Speaker=891554,Emoji=14,}",
  [166285] = "{id=166285,Text='明天去克魔岛看它想耍什么花招噜！',Speaker=891554,}",
  [166286] = "{id=166286,Text='你好慢噜。',Speaker=891554,}",
  [166287] = "{id=166287,Text='那只熊在海滩旁边鬼鬼祟祟的，不知道想干什么噜。',Speaker=891554,}",
  [166288] = "{id=166288,Text='我们快去看看噜！',Speaker=891554,}",
  [166289] = "{id=166289,Text='还差一点……就差一点了呼！',Speaker=891556,}",
  [166290] = "{id=166290,Text='你在做什么噜？',Speaker=891554,}",
  [166291] = "{id=166291,Text='……是姜饼仔小可爱，还有话很多的鹿崽子啊。',Speaker=891556,Option='遇到什么事了',}",
  [166292] = "{id=166292,Text='我想提取克魔岛的水元素，但是这里的水元素——竟然是烫的！！简直是在难为我这只北方熊！！',Speaker=891556,}",
  [166293] = "{id=166293,Text='没办法噜，看来你还是需要超级圣诞特工和伟大冒险者的帮助吧？',Speaker=891554,Emoji=5,}",
  [166294] = "{id=166294,Text='[c][ffff00][PlayerName][-][/c]，就一起帮这头熊把水元素提取出来噜！',Speaker=891554,}",
  [166295] = "{id=166295,Text='你们两只到底行不行啦？',Speaker=891556,}",
  [166296] = "{id=166296,Text='小、小失误而已！你这头熊不要叭叭叭叭的噜！',Speaker=891554,Emoji=18,}",
  [166297] = "{id=166297,Text='[c][ffff00][PlayerName][-][/c]，我都不知道你竟然这么厉害噜！！',Speaker=891554,}",
  [166298] = "{id=166298,Text='……嗯哼，现在准备都齐全了，那本魔法师就让你们见识一下！',Speaker=891556,}",
  [166299] = "{id=166299,Text='成功了！！',Speaker=891556,Option='这是雪人？！',}",
  [166300] = "{id=166300,Text='嗯哼……萨曼莎凝结了姜饼城的雪和克魔岛的水元素，是即使在夏天也不会融化的雪人！只有靠它才能下雪！',Speaker=891556,}",
  [166301] = "{id=166301,Text='具体会发生什么……你们明天再来这里就知道了！诶嘿嘿嘿嘿嘿……',Speaker=891556,Option='你为什么帮我们',}",
  [166302] = "{id=166302,Text='哼哼。我以前得到过克魔岛岛民的帮助，所以只要是这里的委托，我一定会想办法完成吼。',Speaker=891556,Emoji=9,}",
  [166303] = "{id=166303,Text='不跟你们聊了，明天见吧！',Speaker=891556,}",
  [166304] = "{id=166304,Text='古怪的很噜。这只熊竟然真的在想办法让克魔岛下雪噜……它不是骗子魔法师啊。',Speaker=891554,}",
  [166305] = "{id=166305,Text='明天看看它到底想干什么噜！一定要让克魔岛下雪噜！！！为了完成婆婆的愿望……',Speaker=891554,}",
  [166306] = "{id=166306,Text='（婆婆的愿望？看来鲁鲁道夫也是为了某个人，才想让克魔岛下雪的哇……）',Emoji=11,}",
  [166307] = "{id=166307,Text='等到你了，姜饼仔小可爱！',Speaker=891556,}",
  [166308] = "{id=166308,Text='你来了噜。',Speaker=891554,}",
  [166309] = "{id=166309,Text='人齐了，那我就要开始了！',Speaker=891556,}",
  [166310] = "{id=166310,Text='（自言自语）…………雪花姜饼  清蒸姜饼 水煮姜饼 红烧姜饼 烤姜饼 酸菜姜饼………………',Speaker=891556,}",
  [166311] = "{id=166311,Text='…这头熊在干嘛噜？？',Speaker=891554,}",
  [166312] = "{id=166312,Text='（自言自语）…………糖醋姜饼 酥炸姜饼 清炖姜饼 花椒姜饼 煲姜饼 砂锅姜饼………………',Speaker=891556,Option='{这难道是！,0},{是某种咒语！,0}',}",
  [166313] = "{id=166313,Text='————全米德加尔特的姜饼仔啊！！请助我一臂之力吧！！',Speaker=891556,}",
  [166314] = "{id=166314,Text='（天空中飞舞着姜饼仔……还有漂浮的闪光雪花？！）',}",
  [166315] = "{id=166315,Text='嗯哼，怎么样？这就是本圣诞魔法师的杰作——姜饼仔雪花！',Speaker=891556,}",
  [166316] = "{id=166316,Text='勉、勉勉强强合格吧——不知道克魔岛的人们喜不喜欢噜！',Speaker=891554,}",
  [166317] = "{id=166317,Text='哎哟……真是不可思议，天上下姜饼仔了……',Speaker=891555,}",
  [166318] = "{id=166318,Text='嚯嚯！今天这是怎么了？克魔岛的天上竟然漂着这么有趣的东西！',Speaker=891555,Emoji=17,}",
  [166319] = "{id=166319,Text='村长！！下雪了，你看，下雪了呀！！',Speaker=891557,Emoji=1,}",
  [166320] = "{id=166320,Text='什么下雪，明明是下姜饼仔……',Speaker=891558,Emoji=14,}",
  [166321] = "{id=166321,Text='姜饼仔的魔力雪花……西洛，鲁鲁道夫，这都是你们做的吗？',Speaker=891559,Emoji=17,}",
  [166322] = "{id=166322,Text='卡梅阿婆婆！',Speaker=891554,}",
  [166323] = "{id=166323,Text='是我们！',Speaker=891556,}",
  [166324] = "{id=166324,Text='我在克魔岛这么多年，从来没见过这样的雪花呢…………难为你们费心了……',Speaker=891559,}",
  [166325] = "{id=166325,Text='婆婆！没有什么费心的噜！',Speaker=891554,}",
  [166326] = "{id=166326,Text='哈哈，小驯鹿，你越来越可爱了。',Speaker=891559,}",
  [166327] = "{id=166327,Text='婆婆！不能偏心这只鹿啊！',Speaker=891556,}",
  [166328] = "{id=166328,Text='哎呀呀，小白熊，你还是这么有精神。',Speaker=891559,Emoji=2,}",
  [166329] = "{id=166329,Text='这不是[c][ffff00][PlayerName][-][/c]吗？看来这次的魔法雪花，你也参与了呀。',Speaker=891559,Option='是的',Emoji=7,}",
  [166330] = "{id=166330,Text='这下克魔岛上可以迎接圣诞了！',Speaker=891555,}",
  [166331] = "{id=166331,Text='圣诞的魔法……真不错啊！',Speaker=891557,}",
  [166332] = "{id=166332,Text='我更喜欢姜饼仔。',Speaker=891558,}",
  [166333] = "{id=166333,Text='没想到我一句想让克魔岛下雪的玩笑话，竟然真的实现了……辛苦你们啦。',Speaker=891559,}",
  [166334] = "{id=166334,Text='哼，根本不辛苦！要不是当年婆婆救了拉雪橇摔下来的我，我早就掉到海里喂鲨鱼了噜——',Speaker=891554,Emoji=40,}",
  [166335] = "{id=166335,Text='呸，根本轻轻松松！要不是当年婆婆给我喂了大半桶小鱼干，我早就饿死被做成烤熊掌了——',Speaker=891556,Emoji=40,}",
  [166336] = "{id=166336,Text='——所以说，卡梅阿婆婆最喜欢的是我！！',Speaker=891562,}",
  [166337] = "{id=166337,Text='哈哈哈，这两个孩子……',Speaker=891559,}",
  [166338] = "{id=166338,Text='（克魔岛的冬天到来了，之后还会发生什么故事呢……）',}",
  [166342] = "{id=166342,Text='亲爱的[c][ffff00][PlayerName][-][/c]，可以陪我跳支舞吗？',Speaker=821691,}",
  [166343] = "{id=166343,Text='你陪我跳完的话，我就给你一份礼物哦~（请在[c][ffff00]组队[-][/c]状态下参与活动）',Speaker=821691,}",
  [166344] = "{id=166344,Text='嘎……啊嘎！了不起的[c][ffff00][PlayerName][-][/c]啊！',Speaker=891566,Emoji=18,}",
  [166345] = "{id=166345,Text='吾辈……吾辈有个请求想要拜托您，啊嘎！',Speaker=891566,}",
  [166346] = "{id=166346,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Option='{答应潘潘,2},{离开,3}',}",
  [166347] = "{id=166347,Text='谢……谢谢，啊嘎！请问可以！帮忙拍一些和[c][ffff00]冰雪有关的照片[-][/c]吗？',Speaker=891566,Emoji=2,}",
  [166348] = "{id=166348,Text='只要是冰雪就好！这几天就麻烦你啦！如果不知道拍什么的话，我想你应该可以问一下别人，啊嘎！',Speaker=891566,}",
  [166349] = "{id=166349,Text='（奇怪的啤酒企鹅，好端端的为什么要拍冰雪呢？）',}",
  [166350] = "{id=166350,Text='（这张照片还不错。希望它会喜欢！）',}",
  [166351] = "{id=166351,Text='（姜饼城的雪花果然很温暖……）',}",
  [166352] = "{id=166352,Text='（真是富有节日气息的雪花啊……）',}",
  [166353] = "{id=166353,Text='（圣域冰洞的冰雪是浅蓝色的，真不错啊。）',}",
  [166354] = "{id=166354,Text='（冰洞里可真冷啊……不过拍出来照片的效果还是很完美的！）',}",
  [166355] = "{id=166355,Text='（果然不同地方的雪，给人的感觉都不太一样呢……）',}",
  [166356] = "{id=166356,Text='啊嘎！[c][ffff00][PlayerName][-][/c]，谢谢你帮助我拍照！',Speaker=891566,Option='为什么拍摄冰雪',}",
  [166357] = "{id=166357,Text='……其实，克魔岛上！新来了一位雪人小姐！！',Speaker=891566,}",
  [166358] = "{id=166358,Text='她说她叫萨曼莎……是那只圣诞魔法熊创造出来的！',Speaker=891566,}",
  [166359] = "{id=166359,Text='萨曼莎在克魔岛很想念冰雪的样子……所以……所以我就来拜托你了嘎！',Speaker=891566,Option='原来如此',}",
  [166360] = "{id=166360,Text='嘿嘿……谢谢你，了不起的[c][ffff00][PlayerName][-][/c]！',Speaker=891566,}",
  [166362] = "{id=166362,Text='可是这捣蛋鬼太狡猾了，我一直抓不到他，你能帮助我制止他吗？',Speaker=893131,Option='{交给我吧,3},{现在没空,2}',}",
  [166363] = "{id=166363,Text='嗨~冒险者，你对普隆德拉的圣诞氛围还满意吗？这可都是我前段时间的努力成果哦~',Speaker=893131,Option='感觉不错',}",
  [166364] = "{id=166364,Text='哈哈哈，感谢你的认可，我准备了一份礼物送给你哦~',Speaker=893131,Option='还有这种好事？',}",
  [166366] = "{id=166366,Text='真是太遗憾了，我的考验可是非常简单的哦~',Speaker=893131,}",
  [166368] = "{id=166368,Text='冒险者你还在这傻站着干什么，不来一杯芬芳浓郁的咖啡吗？有很多种品类哦~',Speaker=893131,}",
  [166369] = "{id=166369,Text='只要用你手里的材料换取就可以了，我可不收Zeny！',Speaker=893131,}",
  [166370] = "{id=166370,Text='嘿冒险者，有没有选好送给亲友们的圣诞节礼物呢？',Speaker=893126,}",
  [166371] = "{id=166371,Text='没有选好也没关系，不能投其所好的话虽然会被吐槽，但是心意才是最重要的不是吗？',Speaker=893126,}",
  [166372] = "{id=166372,Text='唔，不要这么看着我，送礼物可是一门大学问，一时半会我也教不了你，还是自己慢慢领悟吧。',Speaker=893126,}",
  [166373] = "{id=166373,Text='嘿嘿，也别这么垂头丧气的，我这里有一份额外的圣诞礼物送给你哦，希望这个圣诞节你可以过的开开心心~',Speaker=893126,}",
  [166374] = "{id=166374,Text='三！',Speaker=893126,}",
  [166375] = "{id=166375,Text='二！',Speaker=893126,}",
  [166376] = "{id=166376,Text='一！',Speaker=893126,}",
  [166377] = "{id=166377,Text='剪刀石头布——',Speaker=893126,Option='{石头,40},{剪刀,50},{布,60}',}",
  [166378] = "{id=166378,Text='太遗憾了，我还有好多圣诞礼物没有送出去呢。',Speaker=893126,}",
  [166379] = "{id=166379,Text='嘿嘿，我赢了！',Speaker=893126,}",
  [166380] = "{id=166380,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=893126,Option='{再试一次,103},{不玩啦,102}',}",
  [166382] = "{id=166382,Text='哦吼？不简单嘛！',Speaker=893126,}",
  [166383] = "{id=166383,Text='唔 … … 没有分出胜负呢。',Speaker=893126,}",
  [166384] = "{id=166384,Text='怎么样，要不要再来一局？',Speaker=893126,Option='{再试一次,104},{不玩啦,101}',}",
  [166385] = "{id=166385,Text='哟～你赢了呢！',Speaker=893126,}",
  [166386] = "{id=166386,Text='好吧，我可不是赖帐的人，这份圣诞礼物就送给你了！',Speaker=893126,}",
  [166388] = "{id=166388,Text='呵呵。倒也不失为明智的选择呢，毕竟我是这么厉害！',Speaker=893126,}",
  [166389] = "{id=166389,Text='哎哟，看来你倒是挺有勇气的嘛，那我们来吧！',Speaker=893126,}",
  [166390] = "{id=166390,Text='就这么结束了吗 … …',Speaker=893126,}",
  [166391] = "{id=166391,Text='没错，我们再来，再来！',Speaker=893126,}",
  [166392] = "{id=166392,Text='不过……有一份小小的条件：跟我猜拳决胜负吧！',Speaker=893126,}",
  [166393] = "{id=166393,Text='赢了，你就可以拿到这份礼物，输了……那就再来一次吧，嘿嘿。',Speaker=893126,Option='{赶快开始吧,2},{那还是算了,3}',}",
  [166403] = "{id=166403,Text='不过……我想稍微考验你一下，看你是否真的感受到了冬日的美好~',Speaker=893131,Option='{尽管考验吧,3},{那还是算了,2}',}",
  [166404] = "{id=166404,Text='那我要开始提问了，请在3秒内快速回答（开玩笑的啦~）：',Speaker=893131,}",
  [166405] = "{id=166405,Text='我在普隆德拉一共摆放了几个可爱的雪人？',Speaker=893131,Option='{3个,10},{5个,9},{8个,10},{12个,10},{让我想想,11}',}",
  [166406] = "{id=166406,Text='不知道也没关系，我给你一点小提示吧，看到地图上的蓝色圆圈了吗？根据这些提示去寻找雪人吧~',Speaker=893131,}",
  [166407] = "{id=166407,Text='怎么样，现在知道有多少雪人了吗？',Speaker=893131,Option='{3个,2},{5个,12},{8个,2},{12个,2}',}",
  [166408] = "{id=166408,Text='答对了！这是你的礼物，快打开看看吧，希望你会喜欢~',Speaker=893131,}",
  [166409] = "{id=166409,Text='不对哦，你再仔细寻找一下雪人吧。',Speaker=893131,}",
  [166411] = "{id=166411,Text='作为新一年的代表，本王当然给你准备了一些礼物！',Speaker=893144,}",
  [166412] = "{id=166412,Text='希望你在新的一年跟本王一样，虎虎生威！嗷呜~',Speaker=893144,}",
  [166414] = "{id=166414,Text='我代表所有B格猫星人，祝你在新的一年里，事业顺风，步步高升！',Speaker=893145,}",
  [166415] = "{id=166415,Text='当然，最重要的，祝你在新的一年里，不用再看忽克连的脸色。',Speaker=893145,}",
  [166416] = "{id=166416,Text='在交易所里，抢啥得啥；在猫小友那，词条满满~！',Speaker=893145,}",
  [166419] = "{id=166419,Text='嘿，冒险者，你回来了！好久不见，米德加尔特的伙伴们都很怀念与你一起冒险的日子呢！',Speaker=893123,Option='真令人怀念',}",
  [166420] = "{id=166420,Text='果然这个世界的冒险故事，还是不能少了你啊！',Speaker=893123,}",
  [166421] = "{id=166421,Text='最近这片大陆可有不少新变化，全新职业“拳圣”开放了——看，是不是很帅？',Speaker=893123,}",
  [166422] = "{id=166422,Text='新区域“流星林”开放了，等你完成了「旅途再续」，我们一起去那儿冒险吧！',Speaker=893123,}",
  [166423] = "{id=166423,Text='如果准备好了，就开始你的旅途吧！',Speaker=893123,}",
  [166424] = "{id=166424,Text='等等……！我还有一招忘记告诉你啦，打开聊天，进入[c][ffff00]「回归」[-][/c]频道，你可以找到更多志同道合的伙伴噢！快去打个招呼吧！',Speaker=893123,Option='好！',}",
  [166427] = "{id=166427,Text='快来帮帮小爱神吧！小爱神想要降临普隆德拉，给各位冒险者带来祝福，但是以他目前的力量却不足以支撑喵~',Speaker=893153,}",
  [166428] = "{id=166428,Text='所以，冒险者，赶紧为小爱神捐献出你的能量吧~',Speaker=893153,}",
  [166430] = "{id=166430,Text='喵嘻嘻嘻嘻嘻~本财神喵祝你财运满满~',Speaker=893154,}",
  [166431] = "{id=166431,Text='这份小礼物，务必请你收下喵~',Speaker=893154,}",
  [166432] = "{id=166432,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893144,}",
  [166433] = "{id=166433,Text='在这个特殊的节日里，本王有最诚挚的祝福想要送给大家嗷呜~',Speaker=893144,Emoji=5,}",
  [166434] = "{id=166434,Text='一个人送祝福实在太没意思了，不如你来跟本王一起吧~',Speaker=893144,Option='我要怎么做？',}",
  [166435] = "{id=166435,Text='只要你将[c][ffff00]「新春祝福」[-][/c]传递出去，米德加尔特的冒险者们就会收到本王的祝福了！',Speaker=893144,}",
  [166438] = "{id=166438,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「拜年红包」x5）[-][/c]\n那么，幸运虎王的[c][ffff00]「新春祝福」[-][/c]是：',Speaker=893144,}",
  [166444] = "{id=166444,Text='姜饼城应该会有很美的雪景，但是想要拍有特色的的冰雪照片……先问问最靠谱的圣诞老人吧。',}",
  [166445] = "{id=166445,Text='你好啊冒险者，你说想拍雪景？还要好看的？这可太简单了！我可是姜饼城万事通，找我就对了。要知道姜饼城的每一处角落都有我的脚印哦……',Speaker=6944,Option='求求你快说地点吧！',}",
  [166446] = "{id=166446,Text='咳，好吧，你也知道年纪大了就喜欢和别人讲话。',Speaker=6944,}",
  [166447] = "{id=166447,Text='你要问我最喜欢姜饼城的哪里，当然是我这边的圣诞树啦，他可是我亲手种下的哦……',Speaker=6944,Option='好的我明白了！',}",
  [166448] = "{id=166448,Text='今天也还是在姜饼城找一下雪景吧，那边的孩子没准能发现一些特别的景色',}",
  [166449] = "{id=166449,Text='小朋友你好啊，我想问你一个问题，你可以回答一下吗？',}",
  [166450] = "{id=166450,Text='碧可我们回家吧，妈妈说不要跟陌生人说话！',Speaker=6965,}",
  [166451] = "{id=166451,Text='没关系的尤莉，这位一看就是冒险者，妈妈说冒险者是最正义，最勇敢的人，他一定不会伤害我们的！',Speaker=6963,}",
  [166452] = "{id=166452,Text='你想问什么问题呀，只要我知道一定回答你。',Speaker=6963,}",
  [166453] = "{id=166453,Text='嗯……雪景照片？你可以去看看这里的圣诞老人房间哦，我们最喜欢圣诞老人了！',Speaker=6963,}",
  [166454] = "{id=166454,Text='谢谢你，可爱的孩子，一定要好好听妈妈的话哦！',}",
  [166455] = "{id=166455,Text='在姜饼城转了好多圈，感觉还是问问当地人比较靠谱啊……去问问那边的圣诞少女吧。',}",
  [166456] = "{id=166456,Text='嗨~冒险者，你想要围巾吗？',Speaker=6984,}",
  [166457] = "{id=166457,Text='什么，你只想拍美丽的雪景？好吧，我做了好多围巾和手套呢。想拍雪景的话你就去教堂看看吧~',Speaker=6984,Option='谢谢~',}",
  [166458] = "{id=166458,Text='感觉姜饼城逛得差不多了，来圣域冰洞看看吧。',}",
  [166459] = "{id=166459,Text='这里感觉不错，拍一张照片吧~',}",
  [166460] = "{id=166460,Text='不知道圣域冰洞里还有什么好地方，还是问问万能的艾莉儿吧',}",
  [166461] = "{id=166461,Text='你想拍一张冰雪照片？嗯，要不试试吊桥的另一边，那里有一个不错的冰雕呢~',Speaker=1058,Option='我去找一下',}",
  [166462] = "{id=166462,Text='好像这里的景色还不错，拍一张照片看看吧。',}",
  [166463] = "{id=166463,Text='呼，今天拍完就有7张照片了，啤酒企鹅应该会满意吧。不过最后一张要拍点什么呢？',}",
  [166464] = "{id=166464,Text='穿梭世界的冒险者啊，我感受到了你的烦恼。你想拍一张冰雪的照片为何不直接问我呢？这里可没有我不知道的事~',Speaker=808300,Option='说来听听？',}",
  [166465] = "{id=166465,Text='哼，看到我面前的那个冰雕了吗，我已经观察它很久了，里面一定有什么秘密，你拍下来回去研究没准可以得到惊人的财富呢。',Speaker=808300,Option='真的吗？',}",
  [166466] = "{id=166466,Text='不信就算了！',Speaker=808300,}",
  [166476] = "{id=166476,Text='哈咯冒险者，我是《普隆德拉周末》的主编卡尔斯，最近的普隆德拉可多新鲜事了。',Speaker=891569,ModelAct=0,BlackMask=0,ActType=0}",
  [166477] = "{id=166477,Text='你要不要和我们一起挖掘些“大新闻”？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891569,Option='{好的,2},{我再想想,3}',ModelAct=0,BlackMask=0,ActType=0}",
  [166478] = "{id=166478,Text='啊……真得不再考虑一下嘛，报酬可是相当丰厚哦~',Speaker=891569,ModelAct=0,BlackMask=0,ActType=0}",
  [166479] = "{id=166479,Text='太好了，那就请你去找我们的记者[c][ffff00]弗兰克[-][/c]吧，他最近跑[c][ffff00]国王选举和七王室[-][/c]的新闻跑到头痛，一定会感谢你的帮助。',Speaker=891569,Option='{选举？,0},{七王室？,0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166480] = "{id=166480,Text='对啊，也许你已经听说了，米德加尔特·卢恩王国将举办选王仪式，这本身就是一件大新闻，（小声）加上仪式临近，发生了些奇奇怪怪的事情，身为新闻工作者更是不敢放松。',Speaker=891569,ModelAct=0,BlackMask=0,ActType=0}",
  [166481] = "{id=166481,Text='如果你想知道更多的信息，可以去找附近的居民打听一下，这也是做“记者”要迈出的第一步。[c][ffff00]搜集完信息后就去找弗兰克吧，他会在广场守卫雕像附近等你[-][/c]。',Speaker=891569,ModelAct=0,Emoji=17,BlackMask=0,ActType=0}",
  [166482] = "{id=166482,Text='你在跟我开玩笑嘛？你竟然不知道国王选举？',Speaker=891570,ModelAct=0,BlackMask=0,ActType=0}",
  [166483] = "{id=166483,Text='从几百年前开始，卢恩王国的国王就是从七个贵族家族中选举产生的……',Speaker=891570,ModelAct=0,BlackMask=0,ActType=0}",
  [166484] = "{id=166484,Text='那七个家族，有个叫巴什么的，好像还有个叫韦什么的——嗨，不重要，国王选举是那些贵族老爷们该关心的事，跟我有什么关系……',Speaker=891570,ModelAct=0,BlackMask=0,ActType=0}",
  [166485] = "{id=166485,Text='拥有竞选资格的七个家族被称为“七王室”——他们可不是被随便选出来的。他们的祖先都是跟随着特里斯坦一世国王立下赫赫战功的！',Speaker=891571,ModelAct=0,BlackMask=0,ActType=0}",
  [166486] = "{id=166486,Text='精通魔法的[c][ffff00]海涅家族[-][/c]、由女性掌权的[c][ffff00]奈利乌斯家族[-][/c]、执掌海上贸易的[c][ffff00]巴尔特家族[-][/c]、重视传统的[c][ffff00]韦格纳家族[-][/c]……',Speaker=891571,ModelAct=0,BlackMask=0,ActType=0}",
  [166487] = "{id=166487,Text='擅长制作机械的[c][ffff00]力哈尔特家族[-][/c]、崇尚武力的[c][ffff00]雷根弗莱格家族[-][/c]以及目前执政的[c][ffff00]凯沃宝乐格家族[-][/c]……',Speaker=891571,ModelAct=0,BlackMask=0,ActType=0}",
  [166488] = "{id=166488,Text='呵呵呵……这七大家族，或许就代表了王国至高的荣耀吧……',Speaker=891571,ModelAct=0,BlackMask=0,ActType=0}",
  [166489] = "{id=166489,Text='国王选举？唉……七大家族为了竞选陆续进入普隆德拉……',Speaker=891572,ModelAct=0,BlackMask=0,ActType=0}",
  [166490] = "{id=166490,Text='可苦了我们这些当兵的，每天兢兢业业、生怕出半点儿问题……',Speaker=891572,ModelAct=0,BlackMask=0,ActType=0}",
  [166491] = "{id=166491,Text='我们队长头发都愁白了，唉……',Speaker=891572,ModelAct=0,BlackMask=0,ActType=0}",
  [166492] = "{id=166492,Text='不对，我跟你说这些干嘛！快走开，别打搅我工作！',Speaker=891572,ModelAct=0,BlackMask=0,ActType=0}",
  [166493] = "{id=166493,Text='一定要趁这次机会，报道个“大新闻”啊……',Speaker=891568,Option='讲述情况',ModelAct=0,BlackMask=0,ActType=0}",
  [166494] = "{id=166494,Text='太好了，以你冒险者的身份，想必打探消息会更容易——我的“大新闻”有着落了！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166495] = "{id=166495,Text='自我介绍下，我，[c][ffff00]大名鼎鼎的弗兰克[-][/c]，《普隆德拉周末》的记者！我的报道是城里最有价值的文字，我拍摄的照片永远都会印在报纸的扉页上！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166496] = "{id=166496,Text='你刚才已经打听到了关于国王选举的消息，做得不错。探听消息收集线索可是记者最基本的职业素养。不过我还要考察一下你另一方面的能力，作为记者，必须要会的技能——[c][ffff00]拍照片[-][/c]！',Speaker=891568,Option='拍照？',ModelAct=0,BlackMask=0,ActType=0}",
  [166497] = "{id=166497,Text='没错，试着去拍摄点拍摄能够作为“独家新闻“的照片吧！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166498] = "{id=166498,Text='要记得[c][ffff00]把重要的信息都拍进画面里[-][/c]！这可是记者最基本的职业素养！',Speaker=891568,Option='好的',ModelAct=0,BlackMask=0,ActType=0}",
  [166499] = "{id=166499,Text='究竟该拍摄什么样的照片呢？',ModelAct=0,BlackMask=0,ActType=0}",
  [166500] = "{id=166500,Text='跟上去看看呢？',ModelAct=0,BlackMask=0,ActType=0}",
  [166501] = "{id=166501,Text='黑衣人和这个卫兵交头接耳像是在谈论什么？把它拍下来交给弗兰克吧。',ModelAct=0,BlackMask=0,ActType=0}",
  [166502] = "{id=166502,Text='试着去拍摄点拍摄能够作为“独家新闻“的照片吧！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166503] = "{id=166503,Text='唔……让我看看你拍了些什么……',Speaker=891568,Option='讲述情况',ModelAct=0,BlackMask=0,ActType=0}",
  [166504] = "{id=166504,Text='天哪冒险者，你真有做记者的天赋！这太超出我想象了。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166505] = "{id=166505,Text='宠物狗？那一定是[c][ffff00]恰克[-][/c]了，它可是个好孩子。哎，要不是我的工作有时候挺危险，我倒是真愿意把它养在身边。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166506] = "{id=166506,Text='冒险者，你一定能帮我查明事情真相，明天来找我呢，不见不散！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166507] = "{id=166507,Text='嗨，有天分的冒险者[c][ffff00][PlayerName][-][/c]，准备好搞些大新闻了嘛？',Speaker=891568,Option='{好的,2},{我再想想,3}',ModelAct=0,BlackMask=0,ActType=0}",
  [166508] = "{id=166508,Text='嗨，这还有什么好想的，冲就完事儿了。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166509] = "{id=166509,Text='太好了，还记得之前你拍下的那个黑衣人吗？他一直是我跟踪的目标。',Speaker=891568,Option='跟踪他做什么？',ModelAct=0,BlackMask=0,ActType=0}",
  [166510] = "{id=166510,Text='一言难尽，我给你看样东西。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166511] = "{id=166511,Text='这是王室为了布置庆典准备的烟花，但是最近大量丢失了！',Speaker=891568,Option='{大量丢失？,5},{你偷的？,6}',ModelAct=0,BlackMask=0,ActType=0}",
  [166512] = "{id=166512,Text='对啊，烟花这东西可不只是好看这么简单，只要这样这样，再那样那样，就能变成超级厉害的危险品。',Speaker=891568,Option='……',ModelAct=0,BlackMask=0,ActType=0}",
  [166513] = "{id=166513,Text='你怎么不信……好吧，我们来把烟花拆开重组，让你看到他的可怕真面目吧。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166514] = "{id=166514,Text='！！！！！怎么会是我偷的？？！！！好吧虽然我想法子搞到了一个，但那也是为了查清真相， 那些丢失的跟我没有关系！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166515] = "{id=166515,Text='可能你还没意识到，烟花这东西不只是好看，只要稍加处理，就会变成超级厉害的危险品。',Speaker=891568,Option='……',ModelAct=0,BlackMask=0,ActType=0}",
  [166516] = "{id=166516,Text='你怎么不信……好吧，我们来把烟花拆开重组，让你看到他的可怕真面目吧。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166517] = "{id=166517,Text='我们用匕首把烟花拆开吧。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166518] = "{id=166518,Text='就是这样，接下来我们去旁边捡些[c][ffff00]低温磷火[-][/c]。这东西很常见也没危险，但要小心要把聚集在它们周围的吸血蝠消灭掉，这东西咬人可疼了。',Speaker=891568,Option='好',ModelAct=0,BlackMask=0,ActType=0}",
  [166521] = "{id=166521,Text='那帮吸血蝠还挺麻烦的对吧。正好在你忙的时候，我也调配好了药剂。把低温磷火给我呢？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166522] = "{id=166522,Text='你看，只要稍加改造，这东西就会变成可怕的炸弹。',Speaker=891568,Option='感觉它要爆炸',ModelAct=0,BlackMask=0,ActType=0}",
  [166523] = "{id=166523,Text='当然，这是炸弹啊，所以你想，王国竞选即将开始，这些人又偷去了能改成炸弹的魔法烟花，这里面一定有阴谋！！！！',Speaker=891568,Option='它要爆炸了！',ModelAct=0,Emoji=1,BlackMask=0,ActType=0}",
  [166524] = "{id=166524,Text='这可不仅仅是一个新闻这么简单，他很有可能关乎到王室成员甚至市民百姓的安危……我们不能坐视不理……',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166525] = "{id=166525,Text='弗兰克完全没在听，危机之下还是拉着他离开，免得受伤。',ModelAct=0,BlackMask=0,ActType=0}",
  [166526] = "{id=166526,Text='诶诶我还没说完。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166527] = "{id=166527,Text='冒险者，咳……你没事吧，你看，我就说这炸药很危险，咳咳，所以我们更要找到偷烟花的人，咳咳，不能让他，咳咳，落到坏人手里。',Speaker=891568,Option='你还好吗',ModelAct=0,BlackMask=0,ActType=0}",
  [166528] = "{id=166528,Text='我没事，我就是……呵呵，有点后怕腿软站不起来。趴一会儿就好，咱们明天普隆德拉不见不散呢？',Speaker=891568,ModelAct=0,Emoji=17,BlackMask=0,ActType=0}",
  [166529] = "{id=166529,Text='呜呜呜……汪汪汪……呜呜呜！',Speaker=891573,Option='{你饿了？,2},{弗兰克呢？,3}',ModelAct=0,BlackMask=0,ActType=0}",
  [166530] = "{id=166530,Text='呜呜呜……呜呜呜~~',Speaker=891573,ModelAct=0,BlackMask=0,ActType=0}",
  [166531] = "{id=166531,Text='恰克好像并不饿。',ModelAct=0,BlackMask=0,ActType=0}",
  [166532] = "{id=166532,Text='汪！汪汪！',Speaker=891573,ModelAct=0,BlackMask=0,ActType=0}",
  [166533] = "{id=166533,Text='恰克像是在寻求帮助，跟过去看看呢。',ModelAct=0,BlackMask=0,ActType=0}",
  [166534] = "{id=166534,Text='快救救我，我被他们关起来了。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166535] = "{id=166535,Text='恰克！冒险者[c][ffff00][PlayerName][-][/c]！你们可算是来了，呜呜呜。我们分开后，我就被抓了。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166536] = "{id=166536,Text='还能有谁，就是昨天的黑衣人。他发现了我们偷拍他，还拿烟花做了炸药。就把我抓了起来。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166537] = "{id=166537,Text='你赶紧救我出去，这个家伙可厉害着呢。要是被他误认为我们是偷烟花的贼，那我们的大新闻可就没戏了。',Speaker=891568,Option='……',ModelAct=0,BlackMask=0,ActType=0}",
  [166538] = "{id=166538,Text='你就是这家伙的同伙吧？',Speaker=891576,ModelAct=0,BlackMask=0,ActType=0}",
  [166539] = "{id=166539,Text='偷窃皇室财物、有意扰乱王室治安。我正好可以把你们交给彻查此事的近卫兵。',Speaker=891576,Option='不是我们',ModelAct=0,BlackMask=0,ActType=0}",
  [166540] = "{id=166540,Text='哼……每一个犯人都会这样为自己开脱，好在我知道你们。',Speaker=891576,ModelAct=0,BlackMask=0,ActType=0}",
  [166541] = "{id=166541,Text='善良坚毅的冒险者，和一个[c][ffff00]本不该如此落魄的小报记者[-][/c]。',Speaker=891576,Option='嗯？',ModelAct=0,BlackMask=0,ActType=0}",
  [166542] = "{id=166542,Text='……你到底是谁？',Speaker=891568,Option='（黑衣人掀起帽子）',ModelAct=0,BlackMask=0,ActType=0}",
  [166543] = "{id=166543,Text='（轻声）……拉斯玛导师？！',Speaker=891568,Option='拉斯玛导师？',ModelAct=0,BlackMask=0,ActType=0}",
  [166544] = "{id=166544,Text='冒险者，这位就是[c][ffff00]可自由穿梭于现世与心象世界的寄灵者[-][/c]。七王室[c][ffff00]尼希尔·海涅大人的导师[-][/c]，拉斯玛先生！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166545] = "{id=166545,Text='尼希尔确实是我的学生，但他也教会我永远尊重所有事物存在的根源之力。所以二位不用担心，我不会伤害你们。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166546] = "{id=166546,Text='那你倒是放我出来啊先，关我干什么？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166547] = "{id=166547,Text='不仅如此，近日我追查的案件也与这件事情有关，希望二位能帮助我。',Speaker=891575,Option='什么事情',ModelAct=0,BlackMask=0,ActType=0}",
  [166548] = "{id=166548,Text='我很乐意告诉你一切，但现在要解决的，是这帮讨厌的[c][ffff00]教国异端[-][/c]。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166549] = "{id=166549,Text='抓住他们。',Speaker=891579,ModelAct=0,BlackMask=0,ActType=0}",
  [166550] = "{id=166550,Text='这些教国人已经跟踪我们很久了，冒险者，一起击退他们吧。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166551] = "{id=166551,Text='救命啊我会不会被这些异教狂热分子杀掉？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166552] = "{id=166552,Text='往笼子后面躲，他们打不开笼子，就伤害不到你。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166553] = "{id=166553,Text='这么说我还要感谢您咯？！呜……恰克，你也要当心啊！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166554] = "{id=166554,Text='汪！汪！',Speaker=891573,ModelAct=0,BlackMask=0,ActType=0}",
  [166555] = "{id=166555,Text='冒险者，我来帮你。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166556] = "{id=166556,Text='可恶，你们给我等着。',Speaker=891579,ModelAct=0,BlackMask=0,ActType=0}",
  [166557] = "{id=166557,Text='好……好厉害，这是什么能力？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166558] = "{id=166558,Text='超脱这个世界生死规律本源的能力。冒险者，教国人们好像掉落了些东西，我们看看是什么吧。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166559] = "{id=166559,Text='一封密信，上面的信息或许是线索。把它交给拉斯玛吧。',ModelAct=0,BlackMask=0,ActType=0}",
  [166560] = "{id=166560,Text='这！！！',Speaker=891575,Option='有线索？',ModelAct=0,BlackMask=0,ActType=0}",
  [166561] = "{id=166561,Text='我没想到这件事情会和他有关……',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166562] = "{id=166562,Text='谁啊？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166563] = "{id=166563,Text='冒险者，明天和弗兰克来普隆德拉找我吧。我们一起去会会这个想把王室搞得天翻地覆的可怜人。',Speaker=891575,Option='好',ModelAct=0,BlackMask=0,ActType=0}",
  [166564] = "{id=166564,Text='果然，[c][ffff00]活人的世界里，有着比死亡更加糟糕的事情[-][/c]。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166565] = "{id=166565,Text='不是，先把我放出去再走啊！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166566] = "{id=166566,Text='可恶……拉斯玛这个家伙。要不是看在能搞到大新闻的份儿上，我一定不会原谅他，那怕是他是德高望重的导师也不行。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166567] = "{id=166567,Text='冒险者，我们要找到的人就在这里，我们一起去见他吧？',Speaker=891575,Option='{好,2},{我再想想,3}',ModelAct=0,BlackMask=0,ActType=0}",
  [166568] = "{id=166568,Text='我们等你。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166569] = "{id=166569,Text='我们要找的人是[c][ffff00]普隆德拉卫兵队长哈里斯[-][/c]。',Speaker=891575,Option='他是谁',ModelAct=0,BlackMask=0,ActType=0}",
  [166570] = "{id=166570,Text='就是冒险者之前偷拍到的人。说到这儿。拉斯玛老师，你之前乔装打扮和哈里斯见面，到底是为什么？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166571] = "{id=166571,Text='之前只是受王室和尼希尔委托，想从哈里斯那里获得烟花丢失的信息。没想到事情远比这个复杂。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166572] = "{id=166572,Text='冒险者，哈里斯对我有着莫名的憎恶，所以我把这件物品交给你。请帮我说服哈里斯，放弃和教国人的勾结，不要做出伤害民众的事情。',Speaker=891575,Option='好',ModelAct=0,BlackMask=0,ActType=0}",
  [166573] = "{id=166573,Text='你好，陌生人。',Speaker=891574,Option='讲述情况 ',ModelAct=0,BlackMask=0,ActType=0}",
  [166574] = "{id=166574,Text='！……我不清楚你在说什么，请立刻离开这里。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166575] = "{id=166575,Text='把认为有用的证据拿给哈里斯吧。',Option='{之前的照片,4},{密信,5}',ModelAct=0,BlackMask=0,ActType=0}",
  [166576] = "{id=166576,Text='只是和不认识的人聊天被拍到而已。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166577] = "{id=166577,Text='……我不知道这是什么，请你立刻离开这里。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166578] = "{id=166578,Text='好像没办法让哈里斯认罪。',ModelAct=0,BlackMask=0,ActType=0}",
  [166579] = "{id=166579,Text='看来只有用别的方法了解哈里斯到底在密谋什么了。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166580] = "{id=166580,Text='抓他啊！我这么清白的人都被你关起来过，这种几乎可以断定有罪的人你居然不动手？',Speaker=891568,Option='冷静',ModelAct=0,BlackMask=0,ActType=0}",
  [166581] = "{id=166581,Text='清白？那你交代下你做炸药的烟花哪里来的……',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166582] = "{id=166582,Text='哎呀一点小事儿，拉斯玛大人怎么那么计较。我们刚才在聊什么来着，对了，怎么了解哈里斯的秘密啊？',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166583] = "{id=166583,Text='只剩下[c][ffff00]洞察人心[-][/c]，这一条路可走了。',Speaker=891575,Option='洞察人心',ModelAct=0,BlackMask=0,ActType=0}",
  [166584] = "{id=166584,Text='冒险者[c][ffff00][PlayerName][-][/c]，哈里斯虽然强硬，但并没有将内心封锁，我可以用魔法带你们进入他的内心。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166585] = "{id=166585,Text='不愧是拉斯玛老师。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166586] = "{id=166586,Text='虽说进入内心之后会遇到些危险，但我相信你一定可以解决。',Speaker=891575,Option='{好,7},{我再想想,8}',ModelAct=0,BlackMask=0,ActType=0}",
  [166587] = "{id=166587,Text='决定好了就跟我说吧。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166607] = "{id=166607,Text='冒险者，我先回皇宫查明贝蒂的事情，明天我们再见吧。',Speaker=891575,Option='明天见',ModelAct=0,BlackMask=0,ActType=0}",
  [166608] = "{id=166608,Text='冒险者，我在王宫查到了些重要信息，希望今天我们齐心协力说服哈里斯。',Speaker=891575,Option='{好,2},{我再想想,3}',ModelAct=0,BlackMask=0,ActType=0}",
  [166609] = "{id=166609,Text='我们等你。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166610] = "{id=166610,Text='拉斯玛先生，我不知道您叫我来这里是为了什么？',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166611] = "{id=166611,Text='为了让你知道贝蒂真正的死因。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166612] = "{id=166612,Text='你们……哼，你认为我会相信贼喊捉贼的鬼话嘛？',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166613] = "{id=166613,Text='像拉斯玛大人这样高高在上的贵族，若不是为了自己的利益，怎么会对我们平民产生一丝怜悯。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166614] = "{id=166614,Text='海涅家族确实会守护王室安定，但这并不代表就会包庇犯罪。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166615] = "{id=166615,Text='贝蒂确实死于王室内一位奸臣之手，我也拿到了证据。不久就会将他交于你处置。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166616] = "{id=166616,Text='拉斯玛把一个盒子交给了哈里斯，哈里斯有些触动。',ModelAct=0,BlackMask=0,ActType=0}",
  [166617] = "{id=166617,Text='但这位奸臣之所以会杀掉贝蒂，恰恰是他以为贝蒂看到了[c][ffff00]自己和教国人的密谋[-][/c]。',Speaker=891575,Option='又是教国人',ModelAct=0,BlackMask=0,ActType=0}",
  [166618] = "{id=166618,Text='！！！什么，我不相信！',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166619] = "{id=166619,Text='哈里斯，教国人其实是在利用你想要为妹妹报仇的心，达到他们毁掉王室和民众安宁的目的。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166620] = "{id=166620,Text='我不信。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166621] = "{id=166621,Text='贝蒂也不会愿意你被人利用，她有东西拜托我们交给你。',Speaker=891575,Option='纪念品',ModelAct=0,BlackMask=0,ActType=0}",
  [166622] = "{id=166622,Text='什么纪念品？',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166624] = "{id=166624,Text='一个被存放在玻璃瓶中信，隐约能看到幼稚的字迹。拿给哈里斯吧。',ModelAct=0,BlackMask=0,ActType=0}",
  [166625] = "{id=166625,Text='最近心里好像一直有人在安抚我让我放下仇恨，我好矛盾，我的妹妹，我可怜的妹妹，哥哥该怎么办。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166626] = "{id=166626,Text='这是我多年前和妹妹一起写下的，你是怎么拿到的？',Speaker=891574,Option='讲述情况',ModelAct=0,BlackMask=0,ActType=0}",
  [166627] = "{id=166627,Text='贝蒂，哥哥好想你，哥哥答应你，绝对不会做让你苦恼的事。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166628] = "{id=166628,Text='冒险者，教国人在沙漠搭建了制作炸弹的营地。要赶快赶过去，不然他们今晚就会行动。',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166629] = "{id=166629,Text='冒险者，我们快出发。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166630] = "{id=166630,Text='我和你们一起去！',Speaker=891574,ModelAct=0,BlackMask=0,ActType=0}",
  [166632] = "{id=166632,Text='他们的守卫好多，冒险者，我们来围住他们，你把躲在最后的首领制服吧！',Speaker=891568,Option='好',ModelAct=0,BlackMask=0,ActType=0}",
  [166633] = "{id=166633,Text='我们投降！',Speaker=891578,ModelAct=0,BlackMask=0,ActType=0}",
  [166634] = "{id=166634,Text='谢谢大家的努力，避免了教国的袭击，拯救了大量无辜平民和王室成员的性命。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166635] = "{id=166635,Text='我可是拿到了绝对一手的好新闻好故事！之前的心结，也就此结束了。',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166636] = "{id=166636,Text='谢谢你们为我妹妹做出的努力和牺牲。我一定会好好弥补自己的过错。',Speaker=891574,ModelAct=0,Emoji=24,BlackMask=0,ActType=0}",
  [166637] = "{id=166637,Text='至于冒险者，接下来的旅行你有任何需要，欢迎随时来找我。或者可以跟[c][ffff00]尼希尔[-][/c]联络，他是一个正直善良的好孩子，他一定会不遗余力帮助你。',Speaker=891574,Option='没问题',ModelAct=0,BlackMask=0,ActType=0}",
  [166638] = "{id=166638,Text='啊，突然感觉今天值得好好庆祝一下！',Speaker=891568,ModelAct=0,BlackMask=0,ActType=0}",
  [166639] = "{id=166639,Text='没错。冒险者，[c][ffff00]有时候言语不自觉会变成了这个世界上最不可靠的东西，但好在我们还有内心的坚持与正义[-][/c]。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166640] = "{id=166640,Text='那是生命之源的东西，所有事物存在的根源之力。',Speaker=891575,ModelAct=0,BlackMask=0,ActType=0}",
  [166641] = "{id=166641,Text='[c][ffff00]愿我们都能遵从内心最真实的声音，让它成为支柱，成为护身符，成为安身之所，成为一往无前的光[-][/c]。',Speaker=891568,Option='（爱心符号）',ModelAct=0,BlackMask=0,ActType=0}",
  [166642] = "{id=166642,Text='新的一年来咯~这次，由我来给大家送礼物了~！',Speaker=893161,}",
  [166643] = "{id=166643,Text='过去的一年，[c][ffff00][PlayerName][-][/c]有什么不一样的收获吗？是不是也面临了很多不一样的挑战？',Speaker=893161,}",
  [166644] = "{id=166644,Text='新的一年，要继续加油哦~希望这份礼物可以带给你好运，陪伴你往更美好的方向，大步前进！',Speaker=893161,Option='{谢谢,2},{过会再领,3}',}",
  [166645] = "{id=166645,Text='嘿，有心的冒险者，你还记得今天是个特别的日子吗？',Speaker=893161,Option='嗯？',Emoji=8,}",
  [166646] = "{id=166646,Text='今天适合做任何事！挂机必出卡，精炼把把15，扭蛋一发入魂！',Speaker=893161,}",
  [166647] = "{id=166647,Text='对了！今天值得好好庆祝一下！我想趁这个机会，给大家都送上一份祝福！[c][ffff00]【该礼物同账号仅限一个角色领取，请谨慎考虑是否领取】[-][/c]',Speaker=893161,Option='{谢谢,2},{过会再领,3}',}",
  [166649] = "{id=166649,Text='嘿嘿，想要我这身「时装」？集齐[c][ffff00]「周年庆所有头饰」[-][/c]，我就送给你！',Speaker=893161,Option='{集齐了！,2},{过会再来,3}',}",
  [166650] = "{id=166650,Text='果然！你就是那个最强冒险者！',Speaker=893161,}",
  [166651] = "{id=166651,Text='它是你的了，希望它能陪着你冒险~',Speaker=893161,}",
  [166652] = "{id=166652,Text='若你集齐了[c][ffff00]「周年庆所有头饰」[-][/c]，再来找我吧。',Speaker=893161,}",
  [166654] = "{id=166654,Text='我，帕罗船长，有数不尽的宝物！但是，却想要一些奇奇怪怪的道具……',Speaker=893162,}",
  [166655] = "{id=166655,Text='快来快来，跟我交换吧！兴许，你能换到点好东西呢！',Speaker=893162,}",
  [166656] = "{id=166656,Text='嘿[c][ffff00][PlayerName][-][/c]，要来参加七王室的有奖竞猜吗？',Speaker=893165,ModelAct=0,Emoji=5,BlackMask=0,ActType=0}",
  [166657] = "{id=166657,Text='我们杂志社为了让民众更多的了解我们的王室，做了这个竞猜，只要答对了就能获得丰厚的奖励哦。',Speaker=893165,Option='{好,2},{我再想想,4}',ModelAct=0,Emoji=5,BlackMask=0,ActType=0}",
  [166658] = "{id=166658,Text='真是聪明的选择，在猜谜前，我来先做个简单的解释。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166659] = "{id=166659,Text='我们所拥有的分别是：[c][ffff00]凯沃宝乐格[-][/c]、[c][ffff00]雷根弗莱格[-][/c]、[c][ffff00]力哈尔特[-][/c]、[c][ffff00]奈利乌斯[-][/c]、[c][ffff00]海涅[-][/c]、[c][ffff00]韦格纳[-][/c]、[c][ffff00]巴尔特[-][/c]七个王室家族。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166660] = "{id=166660,Text='我出的谜题会对应其中某一个家族，只要答对了，就能获得奖励。',Speaker=893165,Option='没问题',ModelAct=0,BlackMask=0,ActType=0}",
  [166661] = "{id=166661,Text='那么我来看看，今天的谜题是：',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166662] = "{id=166662,Text='“可以吞噬海洋的巨大蟒蛇，被七彩缤纷的老鹰吞咽，光荣的老鹰在大海上筑巢。”这里的巨大蟒蛇，说的是哪个家族呢？',Speaker=893165,Option='{这太难了,5},{我不知道,6}',ModelAct=0,BlackMask=0,ActType=0}",
  [166663] = "{id=166663,Text='嗯……好像确实有点难，偷偷告诉你，你可以去找非常有名的历史学家凯尔寻求帮助。这时候的他差不多在普隆德拉的居民区附近做调研。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
