  [166755] = "{id=166755,Text='认真整理所见所闻算是聪明人干的苦差事。年轻人，你找我？',Speaker=893166,Option='讲述情况',ModelAct=0,BlackMask=0,ActType=0}",
  [166757] = "{id=166757,Text='是更喜欢姐姐，还是更喜欢妹妹呢？！诶，你找我？',Speaker=893167,Option='讲述情况',ModelAct=0,BlackMask=0,ActType=0}",
