  [166763] = "{id=166763,Text='那可太遗憾了，我的奖品只能送给勇敢聪明的冒险者……',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166764] = "{id=166764,Text='无所谓王权是否傍身，那更像无常的命运给与的暂时的宠幸，神圣的自由才是永恒的家园。你猜猜是哪个家族呢？',Speaker=893165,Option='容我想想',ModelAct=0,BlackMask=0,ActType=0}",
  [166765] = "{id=166765,Text='说到最不在意权利的家族……你可以去找格雷迪聊聊。他曾经服侍过这个家族，一定会非常乐意和你聊聊过往，毕竟那也是他人生的高光时刻。哦对了，他基本上都会在十字军总部门口转悠。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166766] = "{id=166766,Text='等你问到答案，再来回答问题吧。毕竟奖励丰厚，还是要谨慎回答的不是吗？',Speaker=893165,Option='好',ModelAct=0,BlackMask=0,ActType=0}",
  [166767] = "{id=166767,Text='无所谓王权是否傍身，那更像无常的命运给与的暂时的宠幸，神圣的自由才是永恒的家园。你猜猜是哪个家族呢？',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166768] = "{id=166768,Text='你可以去找格雷迪聊聊。他曾经服侍过这个家族，一定会非常乐意和你聊聊过往，毕竟那也是他人生的高光时刻。他基本上都会在十字军总部门口转悠。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166770] = "{id=166770,Text='年轻人，你是来跟我聊天的？',Speaker=893168,Option='讲述情况',ModelAct=0,BlackMask=0,ActType=0}",
  [166771] = "{id=166771,Text='那你可真是问对人了，这说的就是我的老主人们啊！他们才是真正的贵族！！精致、优雅、传统、高贵！！！现在的这些王室，哼，不是我说，哪里还有点贵族的样子。',Speaker=893168,Option='所以？',ModelAct=0,BlackMask=0,ActType=0}",
  [166772] = "{id=166772,Text='崇高的韦格纳家族！！尊敬的罗比艾乐大人，我永远的赞美您！！能服侍您真是我格雷迪一生的荣耀！',Speaker=893168,Option='是韦格纳',ModelAct=0,BlackMask=0,ActType=0}",
  [166773] = "{id=166773,Text='哦你回来了冒险者，相信你一定获得了答案，那我们开始吧。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166774] = "{id=166774,Text='无所谓王权是否傍身，那更像无常的命运给与的暂时的宠幸，神圣的自由才是永恒的家园。你猜猜是哪个家族呢？',Speaker=893165,Option='{韦格纳,5},{巴尔特,4},{力哈尔特,4}',ModelAct=0,BlackMask=0,ActType=0}",
  [166775] = "{id=166775,Text='我想你肯定是记错了。',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166776] = "{id=166776,Text='好耶！回答正确！恭喜你可以拿到奖励了！',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166777] = "{id=166777,Text='最近我都在在这边进行有奖竞猜，明天也要记得来哈~！',Speaker=893165,ModelAct=0,BlackMask=0,ActType=0}",
  [166795] = "{id=166795,Text='你好呀！冒险者~！卡普拉公司为普隆德拉这次的庆典准备了一个大大的惊喜仪式~',Speaker=891587,ModelAct=0,BlackMask=0,ActType=0}",
  [166796] = "{id=166796,Text='怎么样？有没有兴趣参加呀？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=891587,Option='{是,2},{否,3} ',ModelAct=0,BlackMask=0,ActType=0}",
  [166797] = "{id=166797,Text='塞莉安娜就知道你一定有兴趣！~到时候一定要来参加哦！',Speaker=891587,Option='什么惊喜仪式？',ModelAct=0,BlackMask=0,ActType=0}",
  [166798] = "{id=166798,Text='这个塞莉安娜也不知道呢！~塞莉安娜也很期待！',Speaker=891587,ModelAct=0,BlackMask=0,ActType=0}",
  [166799] = "{id=166799,Text='唔……对了，狄波萝太姐姐去西门好久还没回来，也不知道怎么样了……能拜托你去看看吗？',Speaker=891587,Option='好的，我这就去',ModelAct=0,BlackMask=0,ActType=0}",
  [166800] = "{id=166800,Text='冒险者！你怎么过来了？',Speaker=891589,Option='塞莉安娜的请求',ModelAct=0,BlackMask=0,ActType=0}",
  [166801] = "{id=166801,Text='唔……本来我们就是为了准备惊喜仪式来这里运送一些道具的……没想到……',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166802] = "{id=166802,Text='看，我们在这里发现了一个还未成型的时空裂隙。',Speaker=891591,ModelAct=0,BlackMask=0,ActType=0}",
  [166803] = "{id=166803,Text='是我发现的！~',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166804] = "{id=166804,Text='（微笑）……让你不要乱跑，你还记得吗？',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166805] = "{id=166805,Text='呜呜……笑着说这话的狄波萝太姐姐好可怕……对不起嘛~可要不是我乱跑怎么能发现时空裂隙呢！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166806] = "{id=166806,Text='[c][ffff00][PlayerName][-][/c]，你既然来了~我们不是正好有一个对付时空裂隙超有经验的冒险者吗？',Speaker=891590,ModelAct=0,BlackMask=0,ActType=0}",
  [166807] = "{id=166807,Text='加油~把这个还未成型的时空裂隙掐死在摇篮里吧！',Speaker=891593,ModelAct=0,BlackMask=0,ActType=0}",
  [166808] = "{id=166808,Text='哇~[c][ffff00][PlayerName][-][/c]，你真厉害！哗的一下，就解决了！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166809] = "{id=166809,Text='嗯，放任这个时空裂隙发展，怕会影响到时空秘石的激活仪式。',Speaker=891592,Option='{时空秘石？,0},{激活仪式？,0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166810] = "{id=166810,Text='就是惊喜仪式啦！总部的负责人不知道从哪里获得了这个宝贝，准备在这次的庆典上邀请吉芬的大法师们来激活它！',Speaker=891590,ModelAct=0,BlackMask=0,ActType=0}",
  [166811] = "{id=166811,Text='听说啊~激活这块秘石，可以提升我们卡普拉职员们的空间法术！',Speaker=891593,ModelAct=0,BlackMask=0,ActType=0}",
  [166812] = "{id=166812,Text='不仅可以为顾客们提供更为快捷的传送服务，还能将客户传到更远的地方呢！',Speaker=891590,ModelAct=0,BlackMask=0,ActType=0}",
  [166813] = "{id=166813,Text='到时候魔法的波动会发出奇异的光芒~听说会很好看！希望你也能来观看~',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166814] = "{id=166814,Text='？？？怎么回事！！从时空裂隙里掉出了个人？你快去看看，这是怎么回事！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166815] = "{id=166815,Text='这个人似乎因为冲击晕了过去！……但是这个女子的长相……',ModelAct=0,BlackMask=0,ActType=0}",
  [166816] = "{id=166816,Text='长的有点眼熟……',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166817] = "{id=166817,Text='这发型……这发色……',Speaker=891593,ModelAct=0,BlackMask=0,ActType=0}",
  [166818] = "{id=166818,Text='长得像W……',Speaker=891590,ModelAct=0,BlackMask=0,ActType=0}",
  [166819] = "{id=166819,Text='这是……这是怎么回事？',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166820] = "{id=166820,Text='不会吧！？难不成我有个亲姐姐但是我却不知道？',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166821] = "{id=166821,Text='嗯……她晕了过去。现在也只能先把她带回普隆德拉安置起来，等她醒了，我们可能才会知道是怎么回事了。',Speaker=891592,ModelAct=0,BlackMask=0,ActType=0}",
  [166822] = "{id=166822,Text='今天真是麻烦你了，冒险者。',Speaker=891592,ModelAct=0,BlackMask=0,ActType=0}",
  [166823] = "{id=166823,Text='昨天那个长得和我很像的姐姐还没有醒过来……',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166824] = "{id=166824,Text='她好像在梦里……很痛苦……嘴里只低呼着你的名字：[c][ffff00][PlayerName][-][/c]……[c][ffff00][PlayerName][-][/c]……',Speaker=891588,Option='{她认识我？,0},{我不认识她！,0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166825] = "{id=166825,Text='谁知道呢！你每天都和那么多人打交道……说不定……她很有可能是我失散多年的亲姐姐！你现在必须对她负责！',Speaker=891588,Option='{？？？,0},{！！！,0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166826] = "{id=166826,Text='好了！什么都不用说~我懂~',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166827] = "{id=166827,Text='狄波萝太姐姐让我找草药商人购买一些醒醒草~但是我去了好多地方都没有售卖！快帮我去西城门附近采一些！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166828] = "{id=166828,Text='哇~好厉害！这么快就带着醒醒草回来了！不愧是[c][ffff00][PlayerName][-][/c]~',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166829] = "{id=166829,Text='我们快把醒醒草带回去吧！狄波萝太姐姐她们还在等着呢！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166830] = "{id=166830,Text='（微笑）怎么去了这么久~赶紧把醒醒草给她用上~',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166831] = "{id=166831,Text='狄波萝太姐姐~这醒醒草哪里都买不到~还是[c][ffff00][PlayerName][-][/c]帮忙才采集到的！我马上给这个姐姐用上！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166832] = "{id=166832,Text='我这是……成功了……？',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166833] = "{id=166833,Text='[c][ffff00][PlayerName][-][/c]！呜呜呜呜呜……呜呜呜呜……',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166834] = "{id=166834,Text='我就说有故事吧！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166835] = "{id=166835,Text='狄波萝太姐姐！呜呜呜！泰依琳姐姐！！呜呜呜！绣灵姐姐！呜呜呜！克拉丽丝姐姐！！呜呜呜！妼妮姐姐！！',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166836] = "{id=166836,Text='我终于又见到你们了！……太好了！呜呜呜呜呜呜！！',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166837] = "{id=166837,Text='（微笑）不哭了不哭了~哭成这样可就不好看了~你先告诉我们你的名字吧！',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166838] = "{id=166838,Text='让她先哭一会冷静一下吧。',Speaker=891592,ModelAct=0,BlackMask=0,ActType=0}",
  [166839] = "{id=166839,Text='我……呜呜呜……我的名字是W。',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166840] = "{id=166840,Text='等一下等一下！我才是W！你们可被她骗了！而且！她怎么看年纪都比我大！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166841] = "{id=166841,Text='我没有要冒充你的意思。你是W……而我是……八年后的你……',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166842] = "{id=166842,Text='（微笑）一时间不知道说什么好……',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166843] = "{id=166843,Text='嗯……事情居然出乎意料的发展了。',Speaker=891592,ModelAct=0,BlackMask=0,ActType=0}",
  [166844] = "{id=166844,Text='我好不容易从未来回到这里！请你们相信我！',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166845] = "{id=166845,Text='很可疑。',Speaker=891591,ModelAct=0,BlackMask=0,ActType=0}",
  [166846] = "{id=166846,Text='我以为是我失散多年的亲姐姐结果是我自己？',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166847] = "{id=166847,Text='打扰了，各位卡普拉小姐。',Speaker=891600,ModelAct=0,BlackMask=0,ActType=0}",
  [166848] = "{id=166848,Text='怎么了？发生什么事了？你们是总部的保安？',Speaker=891593,ModelAct=0,BlackMask=0,ActType=0}",
  [166849] = "{id=166849,Text='我们收到命令，要将计划破坏秘宝的魔女带回总部。',Speaker=891600,ModelAct=0,BlackMask=0,ActType=0}",
  [166850] = "{id=166850,Text='这位准它公司派来的，假扮成W的小姐，请你跟我们走一趟。',Speaker=891600,ModelAct=0,BlackMask=0,ActType=0}",
  [166851] = "{id=166851,Text='不！我没有假扮！我说的都是真的！！……',Speaker=891597,ModelAct=0,BlackMask=0,ActType=0}",
  [166852] = "{id=166852,Text='打扰各位了，把人带走……',Speaker=891600,ModelAct=0,BlackMask=0,ActType=0}",
  [166853] = "{id=166853,Text='事情……又出乎意料的发展了……',Speaker=891592,ModelAct=0,BlackMask=0,ActType=0}",
  [166854] = "{id=166854,Text='总部的决议，应该没有问题。',Speaker=891591,ModelAct=0,BlackMask=0,ActType=0}",
  [166855] = "{id=166855,Text='（微笑）好了好了，大家都回到自己的岗位上吧~冒险者，今天也麻烦你了呢~',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166856] = "{id=166856,Text='（微笑）到时候一定要来看我们的惊喜仪式哦！',Speaker=891589,ModelAct=0,BlackMask=0,ActType=0}",
  [166857] = "{id=166857,Text='嘠~冒险者！有您的新信件！有您的信件！',Speaker=1025,}",
  [166858] = "{id=166858,Text='这位冒险者是要前往艾尔帕兰吗？',Speaker=1679,Option='{现在前往,2},{自行前往,3}',}",
  [166859] = "{id=166859,Text='这是我和W之间的小秘密~不要外传哦！',Speaker=1679,}",
  [166860] = "{id=166860,Text='啊~[c][ffff00][PlayerName][-][/c]~我还以为你不来了呢~',Speaker=891588,Option='怎么回事？',ModelAct=0,BlackMask=0,ActType=0}",
  [166861] = "{id=166861,Text='就和我信里写的一样，我还想再见见这个魔女。我想听完她昨天没说完的话……',Speaker=891588,Option='{你相信她？,0},{为什么？,0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166862] = "{id=166862,Text='我并不是完全相信她，只是她给我一种很熟悉的感觉……',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166863] = "{id=166863,Text='她现在被安置在公司旁边的小仓库里……门口有人看守！拜托你了嘛~只有你这样的冒险者才能帮助我见到她！',Speaker=891588,Option='{真拿你没办法,0},{交给我吧！,0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166864] = "{id=166864,Text='耶！~我就知道你最好了！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166865] = "{id=166865,Text='就在那个小房子里！你看……门口全是保安在巡逻……',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166866] = "{id=166866,Text='[c][ffff00][PlayerName][-][/c]加油！潜行这种事情~你最在行了！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166881] = "{id=166881,Text='呼……还好没有被人发现！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166882] = "{id=166882,Text='我会在公司内打听一下关于秘石的事情。明天，我在艾尔帕兰等你。',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166883] = "{id=166883,Text='[c][ffff00][PlayerName][-][/c]：\n我思来想去，都想再去见那位自称来自未来的魔女见一面，我需要你的帮助，能否请你到艾尔帕兰与我商议？',ModelAct=0,BlackMask=0,ActType=0}",
  [166884] = "{id=166884,Text='PS：我已经和普隆德拉的卡普拉小姐姐说好啦！给你~免~费~传~送~哦~\n你亲爱的W',ModelAct=0,BlackMask=0,ActType=0}",
  [166885] = "{id=166885,Text='嗯哼~就等你来了！我可是都打听清楚了！这块秘石，是思朗特先生带来的！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166886] = "{id=166886,Text='他最近每天午休都会在运河旁的“三只船咖啡店”休息，我们去那边就可以找到他！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166887] = "{id=166887,Text='在那边喝咖啡的，就是思特郎先生。我们快过去问问他。',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166888] = "{id=166888,Text='你们在一旁观察我很久了，找我有什么事？',Speaker=891596,ModelAct=0,BlackMask=0,ActType=0}",
  [166889] = "{id=166889,Text='抱歉打扰到您，我是卡普拉W，我想找你了解一下关于“魔女”的事。',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166890] = "{id=166890,Text='原来是你。我知道你会很困扰，毕竟，那个魔女声称自己是未来的你。这放到谁身上，都会充满疑惑吧。',Speaker=891596,ModelAct=0,BlackMask=0,ActType=0}",
  [166891] = "{id=166891,Text='但她是准它公司派来破坏时空秘石的！她编出了那样的谎言是为了取得你们的信任，接近你们，伺机破坏秘石！',Speaker=891596,ModelAct=0,BlackMask=0,ActType=0}",
  [166892] = "{id=166892,Text='可……她什么都还没来得及做，就被抓了起来……你们是怎么知道的。',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166893] = "{id=166893,Text='W，我的好孩子，我们在准它公司也是有自己人的，他们准确的为我提供了情报。',Speaker=891596,ModelAct=0,BlackMask=0,ActType=0}",
  [166895] = "{id=166895,Text='好了，我的午休时间要结束了。好孩子，回到你的岗位上工作吧。一切都有我在，不用担心。',Speaker=891596,ModelAct=0,BlackMask=0,ActType=0}",
  [166896] = "{id=166896,Text='唔……不对劲……',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166897] = "{id=166897,Text='虽然，姐姐们都说我很迟钝！但是这次我感觉出来了！这个思特郎在说谎！',Speaker=891588,Option='{我也觉得,2},{我不认为,3}',ModelAct=0,BlackMask=0,ActType=0}",
  [166898] = "{id=166898,Text='嘿嘿~你是不是也发现了？',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166899] = "{id=166899,Text='嗯……看来你比我还要迟钝呢~！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166900] = "{id=166900,Text='我们卡普拉公司！是非常正直的！才不会做出在准它公司窃取情报的事情！这事一定有古怪！',Speaker=891588,Option='{这么简单？,0},{无法反驳！，0}',ModelAct=0,BlackMask=0,ActType=0}",
  [166901] = "{id=166901,Text='走，我们去找魔女，我还有话要问她。',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166919] = "{id=166919,Text='魔女姐姐……没事吧……',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166920] = "{id=166920,Text='我们应该相信她吗？……她说的一切，实在是太不可思议了了！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [166921] = "{id=166921,Text='我需要好好想一想……我们明天庆典仪式上见吧！',Speaker=891588,ModelAct=0,BlackMask=0,ActType=0}",
  [167041] = "{id=167041,Text='喵嘻嘻嘻嘻嘻~本财神喵祝你财运满满~',Speaker=893174,}",
  [167042] = "{id=167042,Text='这份小礼物，务必请你收下喵~',Speaker=893174,}",
  [167043] = "{id=167043,Text='喵嘻嘻嘻嘻嘻~本财神喵祝你财运满满~',Speaker=893175,}",
  [167044] = "{id=167044,Text='这份小礼物，务必请你收下喵~',Speaker=893175,}",
  [167045] = "{id=167045,Text='喵嘻嘻嘻嘻嘻~本财神喵祝你财运满满~',Speaker=893176,}",
  [167046] = "{id=167046,Text='这份小礼物，务必请你收下喵~',Speaker=893176,}",
  [167050] = "{id=167050,Text='听说「锦鲤赐福」可以给人带来一整年的好运！',Speaker=893144,Option='哪些魔物？',}",
  [167051] = "{id=167051,Text='南门的贺岁锦鲤携带了大量的[c][ffff00]「锦鲤赐福」[-][/c]哦~',Speaker=893144,}",
  [167052] = "{id=167052,Text='如果冒险者能给我带回来一个的话，我会给你我的珍藏哦~',Speaker=893144,}",
  [167053] = "{id=167053,Text='确定要提交[c][ffff00]「锦鲤赐福」[-][/c]x1吗？',Speaker=893144,Option='{确定,4},{再想想,3}',}",
  [167054] = "{id=167054,Text='冒险者，你身上暂时没有足够的材料哦！请先去获取后再来捐献吧！击败[c][ffff00]贺岁锦鲤[-][/c]就有概率获得「锦鲤赐福」哦~',Speaker=893144,}",
  [167055] = "{id=167055,Text='感谢你~祝你新年快乐~！',Speaker=893144,}",
  [167056] = "{id=167056,Text='呐~想知道你今天的恋爱运势吗？赶紧来占卜一下吧~',Speaker=893155,}",
  [167057] = "{id=167057,Text='占卜的时候一定要心诚哦~',Speaker=893155,}",
  [167066] = "{id=167066,Text='新年快乐，[c][ffff00][PlayerName][-][/c]！今年是虎年，理所当然的，虎王是我们今年的守护神。',Speaker=891612,}",
  [167067] = "{id=167067,Text='一片祥和的节日气氛里，虎王也能好好休息一下吧！',Speaker=891612,}",
  [167068] = "{id=167068,Text='去斐扬拜访他吧，为他送上节日祝福，感谢他为保一方太平做出的贡献！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=891612,Option='{好啊,2},{等下再说,3}',}",
  [167069] = "{id=167069,Text='虎王的家在斐扬的虎宅，要让我送你一程吗？',Speaker=891612,Option='{那就拜托了,4},{我自己去,5}',}",
  [167070] = "{id=167070,Text='啊啊啊啊啊！',Speaker=891614,}",
  [167071] = "{id=167071,Text='说什么调查诈骗案没法回家！',Speaker=891614,}",
  [167072] = "{id=167072,Text='他自己才是大骗子！说好的陪我一起过年呢！？气死我了！',Speaker=891614,}",
  [167073] = "{id=167073,Text='他应该把自己抓起来关！以欺骗自己亲闺女的罪名！',Speaker=891614,Option='{新年好……,2},{出了什么事,3}',}",
  [167074] = "{id=167074,Text='才不好呢！',Speaker=891614,}",
  [167075] = "{id=167075,Text='还不是因为那个混账老爹！',Speaker=891614,Option='你是虎王的女儿',}",
  [167076] = "{id=167076,Text='本来平时就忙的不着家，就盼着过年他能陪我玩……',Speaker=891614,}",
  [167077] = "{id=167077,Text='这下可倒好，说什么忙着查案，连过年都不回来了！',Speaker=891614,Option='他是守护神',}",
  [167078] = "{id=167078,Text='守护神有什么好！连自己亲闺女都不管！',Speaker=891614,}",
  [167079] = "{id=167079,Text='呵呵呵……我不会运气这么好吧？',Speaker=891614,}",
  [167080] = "{id=167080,Text='碰上混帐老爹……',Speaker=891614,}",
  [167081] = "{id=167081,Text='变成不良少虎……',Speaker=891614,}",
  [167082] = "{id=167082,Text='凄凉的渡过一生？',Speaker=891614,Option='不至于不至于……',}",
  [167083] = "{id=167083,Text='算了！不管混帐老爹了，反正我早就习惯他不在家了。可是……',Speaker=891614,}",
  [167084] = "{id=167084,Text='家里一点年味儿都没有……',Speaker=891614,}",
  [167085] = "{id=167085,Text='陪我去办点儿年货吧？怎么样？咱俩一起过年！',Speaker=891614,Option='好啊！',}",
  [167086] = "{id=167086,Text='说到过年，最先想到的应该是……？',Speaker=891614,Option='{贴春联,4},{吃饺子,5}',}",
  [167087] = "{id=167087,Text='当然是先吃饱才行啊！我们先吃饺子吧！',Speaker=891614,}",
  [167088] = "{id=167088,Text='果然是吃饺子对吧！要论饺子，整个斐扬，属桂奶奶包的饺子最好吃！',Speaker=891614,}",
  [167089] = "{id=167089,Text='咱们去挖点新鲜的竹笋，找桂奶奶包成饺子！',Speaker=891614,Option='老虎吃竹笋吗',}",
  [167090] = "{id=167090,Text='超好吃！你吃了就能明白。',Speaker=891614,}",
  [167091] = "{id=167091,Text='想想口水都流下来了！',Speaker=891614,Option='好吧',}",
  [167092] = "{id=167092,Text='我知道有个秘密的地方，可以挖到品质很好的竹笋。在地图上指给你看~',Speaker=891614,}",
  [167093] = "{id=167093,Text='加油吧！',Speaker=891614,}",
  [167094] = "{id=167094,Text='是品质很好的竹笋！',Speaker=891614,}",
  [167095] = "{id=167095,Text='我们去找桂奶奶吧。',Speaker=891614,}",
  [167096] = "{id=167096,Text='唉……',Speaker=891615,}",
  [167097] = "{id=167097,Text='奶奶！竹笋给你！包饺子！',Speaker=891614,}",
  [167098] = "{id=167098,Text='这不是虎妞吗……对不起，今天没准备包饺子的材料……',Speaker=891615,}",
  [167099] = "{id=167099,Text='啊？为什么？……咦？奶奶你咋哭了？',Speaker=891614,}",
  [167100] = "{id=167100,Text='唉……早上有人送来一封信，是我儿子寄来的，说回家过年的路上遇到麻烦，需要钱。',Speaker=891615,}",
  [167101] = "{id=167101,Text='我就赶忙去寄了钱，连食材都没准备。正担心的时候，儿子就回来了……',Speaker=891615,Option='所以他没遇到麻烦？',}",
  [167102] = "{id=167102,Text='是啊！他压根不知道信的事。果然是被骗了啊……老糊涂不中用了……',Speaker=891615,}",
  [167103] = "{id=167103,Text='可恶的骗子！！',Speaker=891614,}",
  [167104] = "{id=167104,Text='先是害得我爹不能回家过年！',Speaker=891614,}",
  [167105] = "{id=167105,Text='又是害我吃不到饺子！',Speaker=891614,}",
  [167106] = "{id=167106,Text='我非把他抓住不可！[c][ffff00][PlayerName][-][/c] ！帮我吧！',Speaker=891614,Option='没问题！',}",
  [167107] = "{id=167107,Text='那么……先问问桂奶奶骗子长什么样吧！',Speaker=891614,}",
  [167108] = "{id=167108,Text='唉……',Speaker=891615,}",
  [167109] = "{id=167109,Text='奶奶，你还记得是谁送来的信吗？',Speaker=891614,}",
  [167110] = "{id=167110,Text='是一个陌生的外来者送来的……送完信，往竹杨酒家那边去了。',Speaker=891615,}",
  [167111] = "{id=167111,Text='记不清长什么样子……但再看到的话，应该能认出来。',Speaker=891615,Option='知道了',}",
  [167112] = "{id=167112,Text='新年好啊！吃点什么吗？',Speaker=3061,}",
  [167113] = "{id=167113,Text='新年好！老板，最近看到什么可疑的陌生人吗？',Speaker=891614,}",
  [167114] = "{id=167114,Text='有啊！早些时候，有个人来买酒。付钱的时候，居然说钱不够。',Speaker=3061,}",
  [167115] = "{id=167115,Text='哼！这点小伎俩，还糊弄不了我。',Speaker=3061,Option='后来呢？',}",
  [167116] = "{id=167116,Text='他说他也觉得奇怪，说明天会带够钱来买酒，谁知道呢。',Speaker=3061,}",
  [167117] = "{id=167117,Text='太可疑了……肯定是他！',Speaker=891614,}",
  [167118] = "{id=167118,Text='没办法了，看来只能明天一早来这里蹲守他……',Speaker=891614,}",
  [167119] = "{id=167119,Text='可恶！他明天只要敢露面，我就狠狠修理他！',Speaker=891614,}",
  [167120] = "{id=167120,Text='没有饺子了……吃点什么呢？',Speaker=891614,}",
  [167121] = "{id=167121,Text='你怎么才来呀，我可是一早就等在这儿了。',Speaker=891614,}",
  [167122] = "{id=167122,Text='快看！来了！是他！',Speaker=891614,}",
  [167123] = "{id=167123,Text='老板，我来买酒，这次是货真价实的zeny！',Speaker=891616,}",
  [167124] = "{id=167124,Text='噢！是你呀，让我看看……',Speaker=3061,}",
  [167125] = "{id=167125,Text='就是他了！抓住他！',Speaker=891614,Option='还不能确定',}",
  [167126] = "{id=167126,Text='怎么啦？',Speaker=891614,Option='要找桂奶奶核实一下',}",
  [167127] = "{id=167127,Text='唔……那就快一点吧！否则他要逃跑了！',Speaker=891614,}",
  [167128] = "{id=167128,Text='啊，是这个人，我想起来了，他戴着头巾。是他没错！',Speaker=891615,}",
  [167129] = "{id=167129,Text='（虎妞和陌生外来者都不见了……）',}",
  [167130] = "{id=167130,Text='他们往那边去了。你们在做什么游戏吗？',Speaker=3061,Option='是在抓诈骗嫌疑犯',}",
  [167131] = "{id=167131,Text='诈骗犯？哈哈，肯定是太不聪明了才会被骗。换做是我，肯定不会上当。',Speaker=3061,}",
  [167132] = "{id=167132,Text='（这里好像有脚印……是往那边去的）',}",
  [167133] = "{id=167133,Text='（这里也有……）',}",
  [167134] = "{id=167134,Text='站住！',Speaker=891614,}",
  [167135] = "{id=167135,Text='大胆的骗子！不要逃跑！',Speaker=891614,}",
  [167136] = "{id=167136,Text='谁是骗子啊？谁逃跑了？根本听不懂你在说什么啊……',Speaker=891616,}",
  [167137] = "{id=167137,Text='搞什么啊，一路纠缠过来！',Speaker=891616,}",
  [167138] = "{id=167138,Text='你是不是给卖饺子的桂奶奶送过一封信？',Speaker=891614,}",
  [167139] = "{id=167139,Text='是啊……',Speaker=891616,}",
  [167140] = "{id=167140,Text='桂奶奶被那封信骗了钱！',Speaker=891614,}",
  [167141] = "{id=167141,Text='什么！？有这种事？说起来我也碰上了怪事。',Speaker=891616,}",
  [167142] = "{id=167142,Text='我是梦罗克的烟花商人，新年期间在四处卖烟花。',Speaker=891616,}",
  [167143] = "{id=167143,Text='昨天来斐扬买酒，在大街上碰到一个人，很着急的样子，拜托我把信送过去。',Speaker=891617,}",
  [167144] = "{id=167144,Text='本来不想去，他给我了一大把zeny……于是我就去了。',Speaker=891617,}",
  [167145] = "{id=167145,Text='送完信去买酒的时候，发现他给我的zeny全都变成了竹叶。',Speaker=891617,}",
  [167146] = "{id=167146,Text='酒也没买成，只好今天再跑一趟。果然没有天上掉馅饼的事对吧？',Speaker=891617,Option='天上不会掉馅饼',}",
  [167147] = "{id=167147,Text='唔……果然只是被骗子利用了吗……',Speaker=891614,}",
  [167148] = "{id=167148,Text='对了，叫我送信的人，有条狐狸尾巴。现在想想果然很奇怪。抱歉，只能想起来这么多了。',Speaker=891617,}",
  [167149] = "{id=167149,Text='呜……没有找到骗子……',Speaker=891614,}",
  [167150] = "{id=167150,Text='还冤枉了别人……',Speaker=891614,}",
  [167151] = "{id=167151,Text='呵呵呵……我不会运气这么好吧？',Speaker=891614,}",
  [167152] = "{id=167152,Text='遗传了混账老爹的笨蛋基因',Speaker=891614,Option='安慰安慰',}",
  [167153] = "{id=167153,Text='好漂亮……',Speaker=891614,}",
  [167154] = "{id=167154,Text='总之，不小心帮了骗子的忙，我也感到很抱歉……',Speaker=891617,}",
  [167155] = "{id=167155,Text='这些烟花送给你吧，打起精神来！',Speaker=891617,Option='谢谢',}",
  [167156] = "{id=167156,Text='对了，我想到了！线索还没有完全断掉！',Speaker=891614,Option='什么？',}",
  [167157] = "{id=167157,Text='桂奶奶那里，应该还有骗子的信！',Speaker=891614,Option='对呀！',}",
  [167158] = "{id=167158,Text='我去问桂奶奶要信，我们明天从信入手，接着调查吧！',Speaker=891614,Option='好',}",
  [167159] = "{id=167159,Text='虎妞：烟花真好看……',Speaker=891614,}",
  [167160] = "{id=167160,Text='呜……',Speaker=891614,Option='你看起来很憔悴',}",
  [167161] = "{id=167161,Text='研究了一晚上。什么都没研究出来。',Speaker=891614,Option='怎么研究的？',}",
  [167162] = "{id=167162,Text='当然是……先闻闻',Speaker=891614,Option='闻出什么了？',}",
  [167163] = "{id=167163,Text='一股饺子味儿……然后还有！',Speaker=891614,Option='还有什么？',}",
  [167164] = "{id=167164,Text='一个字一个字的查字典……',Speaker=891614,Option='……',}",
  [167165] = "{id=167165,Text='那要怎么查嘛！',Speaker=891614,Option='找个书法大师',}",
  [167166] = "{id=167166,Text='对呀！这样就有可能分析出信的出处了！可是哪里有这种大师呢？',Speaker=891614,Option='我带你去个地方',}",
  [167167] = "{id=167167,Text='哇！太美了……',Speaker=891614,}",
  [167168] = "{id=167168,Text='这里的年味儿也太足了吧！',Speaker=891614,Option='这里每天都像过年一样',}",
  [167169] = "{id=167169,Text='等抓住了骗子，我们在这里逛逛吧！',Speaker=891614,Option='先办案吧',}",
  [167170] = "{id=167170,Text='说的也是，走吧。',Speaker=891614,}",
  [167171] = "{id=167171,Text='恭喜发财！春联可是过春节的必需品，选一副喜庆的春联贴在门上，美好的祝愿伴随一整年。',Speaker=811187,}",
  [167172] = "{id=167172,Text='小老妹儿，看上哪一副啦？',Speaker=811187,}",
  [167173] = "{id=167173,Text='[c][ffff00][PlayerName][-][/c]，帮我选一副吧？',Speaker=891614,Option='{祝愿健康的,2},{祝愿平安的,3},{祝愿团圆的,4}',}",
  [167174] = "{id=167174,Text='这幅选的好呀！臭老爹总是吃太多吃太快，怎么劝都不听……',Speaker=891614,}",
  [167175] = "{id=167175,Text='我也想选这幅！臭老爹总是在外面奔波，要让他注意安全……',Speaker=891614,}",
  [167176] = "{id=167176,Text='这幅很好……希望臭老爹能多回家陪陪我……',Speaker=891614,}",
  [167177] = "{id=167177,Text='好咧！',Speaker=811187,}",
  [167178] = "{id=167178,Text='上联……下联……',Speaker=811187,}",
  [167179] = "{id=167179,Text='大功告成！',Speaker=811187,}",
  [167180] = "{id=167180,Text='写的真不赖！',Speaker=891614,}",
  [167181] = "{id=167181,Text='那当然，行家一出手，就知有没有。',Speaker=811187,}",
  [167182] = "{id=167182,Text='你也来试试吧？横批留给你写。',Speaker=811187,}",
  [167183] = "{id=167183,Text='很不错！',Speaker=811187,}",
  [167184] = "{id=167184,Text='对了老板，您帮我看看这个。',Speaker=891614,Option='交出信',}",
  [167185] = "{id=167185,Text='唔……这是封要钱的信？要我帮忙写回信吗？',Speaker=811187,Option='这是一封诈骗信',}",
  [167186] = "{id=167186,Text='什么！？这是封诈骗信？',Speaker=811187,Option='道出事情原委',}",
  [167187] = "{id=167187,Text='……我明白了。骗子是最可恶的……请让我尽绵薄之力帮助你们。',Speaker=811187,}",
  [167188] = "{id=167188,Text='让我仔细看看……',Speaker=811187,}",
  [167189] = "{id=167189,Text='等等，墨迹里有一根……狐狸毛？不会有错的，这封信是用狐毛笔写的。',Speaker=811187,}",
  [167190] = "{id=167190,Text='暂时看不出其他的信息。但请你放心，我会尽全力帮你。',Speaker=811187,}",
  [167191] = "{id=167191,Text='我要去问问我那些……卖笔墨纸砚的朋友。他们会知道答案……明天就能告诉你结果。',Speaker=811187,}",
  [167192] = "{id=167192,Text='看来只好等到明天了……明天在我家碰头吧！',Speaker=891614,}",
  [167193] = "{id=167193,Text='表哥！你来看看这个……',Speaker=811187,}",
  [167194] = "{id=167194,Text='字画店老板来消息了！',Speaker=891614,}",
  [167195] = "{id=167195,Text='诈骗信的出处查到了……居然是斐扬南部的竹子制成的纸！',Speaker=891614,Option='不亏是书法大师',}",
  [167196] = "{id=167196,Text='事不宜迟！我们去斐扬南部看看吧！',Speaker=891614,}",
  [167197] = "{id=167197,Text='这里比想象中的大多了……从哪里找起呢？',Speaker=891614,}",
  [167198] = "{id=167198,Text='咦？那边有人在哭？',Speaker=891614,}",
  [167199] = "{id=167199,Text='是酒馆老板？',Speaker=891614,}",
  [167200] = "{id=167200,Text='呜呜呜……我的钱……',Speaker=891618,Option='询问情况',}",
  [167201] = "{id=167201,Text='昨天你们走后，有个人找上门来，说急着出售城外的一间大宅子……',Speaker=891618,}",
  [167202] = "{id=167202,Text='价钱太令人心动了！刚好年底有些钱，所以就……',Speaker=891618,}",
  [167203] = "{id=167203,Text='没想到刚付了钱，富丽堂皇的大宅子，就当着我的面变成了废墟！',Speaker=891618,}",
  [167204] = "{id=167204,Text='卖房子的人变成一只大狐狸，身后还有一大群凶恶的狼！吓得我玩儿命似的逃回来……',Speaker=891618,}",
  [167205] = "{id=167205,Text='可我又心疼我的钱，只好在这里原地乱转……',Speaker=891618,}",
  [167206] = "{id=167206,Text='又是狐狸！我们快去看看，这下说不定能抓到他！',Speaker=891614,}",
  [167207] = "{id=167207,Text='哼哼，没想到堂堂守护神，居然这么好骗！',Speaker=891621,}",
  [167208] = "{id=167208,Text='一说你女儿有危险，就立刻奋不顾身，跳进陷阱里。',Speaker=891621,}",
  [167209] = "{id=167209,Text='就凭你也想抓到我！？',Speaker=891621,}",
  [167210] = "{id=167210,Text='伙计们！干掉保护神，咱们往后就不用辛辛苦苦骗啦！想要什么动手抢！',Speaker=891621,}",
  [167211] = "{id=167211,Text='吼……',Speaker=891613,}",
  [167212] = "{id=167212,Text='臭老爹！！老爹！我在这里呀！那个是假的呀！',Speaker=891614,}",
  [167213] = "{id=167213,Text='怎么办！他听不见！',Speaker=891614,}",
  [167214] = "{id=167214,Text='对了！用这个吸引他们的注意力，我去救我爹！',Speaker=891614,}",
  [167215] = "{id=167215,Text='妞妞！？你怎么在这儿？那这是谁？',Speaker=891613,}",
  [167216] = "{id=167216,Text='那是假的啊臭老爹！',Speaker=891614,}",
  [167217] = "{id=167217,Text='你们……居然连我都敢骗！！付出代价吧！',Speaker=891613,}",
  [167218] = "{id=167218,Text='妞妞！你怎么来啦？',Speaker=891613,}",
  [167219] = "{id=167219,Text='我要是没来你这回可就糗大啦！臭老爹！',Speaker=891614,}",
  [167220] = "{id=167220,Text='你怎么样？没事吧？',Speaker=891614,}",
  [167221] = "{id=167221,Text='我怎么可能有事，快看看犯人有没有事！',Speaker=891613,}",
  [167222] = "{id=167222,Text='它好像昏过去了……搜他的身',Speaker=891614,}",
  [167223] = "{id=167223,Text='这是……竹纸和狐毛笔！还有桂奶奶的信！果然是他！',Speaker=891614,}",
  [167224] = "{id=167224,Text='……唉呀！总之，要立刻把他关起来审问！受骗的人还在着急呢！',Speaker=891613,}",
  [167225] = "{id=167225,Text='是啊，要赶快把钱找出来，还给他们！',Speaker=891614,}",
  [167226] = "{id=167226,Text='……包在我身上！你快回家吧，等我回家吃饭，晚饭前保证让他交代！',Speaker=891613,}",
  [167227] = "{id=167227,Text='太好了，犯人总算抓住了！多亏你！',Speaker=891614,Option='是你的功劳',}",
  [167228] = "{id=167228,Text='臭老爹又在乱保证……按我的经验来看，他晚饭前肯定回不来。',Speaker=891614,Option='安慰',}",
  [167229] = "{id=167229,Text='但是……能稍微……理解他了。',Speaker=891614,}",
  [167230] = "{id=167230,Text='明天来我家吃饭吧！我要好好向臭老爹炫耀一番！你来给我作证！',Speaker=891614,Option='好啊',}",
  [167231] = "{id=167231,Text='奋不顾身保护我的样子好帅……',Speaker=891614,}",
  [167232] = "{id=167232,Text='你来啦！臭老爹把被骗走的钱全找回来啦！',Speaker=891614,}",
  [167233] = "{id=167233,Text='当然是找了一晚上，基本上都还给受害者了……臭老爹现在还睡着呢。',Speaker=891614,}",
  [167234] = "{id=167234,Text='还有最后两家没有归还，我们去帮他搞定吧？',Speaker=891614,Option='走吧',}",
  [167235] = "{id=167235,Text='唉……',Speaker=891615,}",
  [167236] = "{id=167236,Text='桂奶奶！钱追回来了！',Speaker=891614,}",
  [167237] = "{id=167237,Text='啊？是真的吗？',Speaker=891615,}",
  [167238] = "{id=167238,Text='骗子已经被我们给逮着了~看看，是这些吗？',Speaker=891614,Option='交出钱',}",
  [167239] = "{id=167239,Text='是这些，是这些……没想到还能找回来……你们真厉害！',Speaker=891615,}",
  [167240] = "{id=167240,Text='大过年的，我这个老糊涂，给你们添了不少麻烦……真是不应该。唉……',Speaker=891615,}",
  [167241] = "{id=167241,Text='别这么说，桂奶奶，可耻的是那些骗子。',Speaker=891614,Option='要谨防骗子',}",
  [167242] = "{id=167242,Text='再收到奇怪的信，可不能慌张啦！要核实清楚才行！',Speaker=891614,}",
  [167243] = "{id=167243,Text='知道了，知道了。谢谢你们……谢谢……',Speaker=891615,}",
  [167244] = "{id=167244,Text='这日子没法过了……',Speaker=3061,}",
  [167245] = "{id=167245,Text='老板！',Speaker=891614,}",
  [167246] = "{id=167246,Text='呜呜呜……',Speaker=3061,}",
  [167247] = "{id=167247,Text='别哭了，我们把你的钱找回来了~',Speaker=891614,Option='交出钱',}",
  [167248] = "{id=167248,Text='啊！钱！白花花的zeny！',Speaker=3061,}",
  [167249] = "{id=167249,Text='谢谢你们的大恩大德！以后再也不贪小便宜了！',Speaker=3061,Option='不能掉以轻心',}",
  [167250] = "{id=167250,Text='没错，要提高警惕！',Speaker=891614,}",
  [167251] = "{id=167251,Text='不知道怎么感谢你们才好……这就送一桌好菜到你府上！还有你爹最爱喝的酒！',Speaker=3061,}",
  [167252] = "{id=167252,Text='这怎么好意思……',Speaker=891614,}",
  [167253] = "{id=167253,Text='感谢你们为了大家的安宁作出的奉献。',Speaker=3061,}",
  [167254] = "{id=167254,Text='谢谢……',Speaker=3061,}",
  [167255] = "{id=167255,Text='那就不客气啦！',Speaker=891614,}",
  [167256] = "{id=167256,Text='钱都还给失主了，我们回去吧！',Speaker=891614,}",
  [167257] = "{id=167257,Text='老爹！你醒啦！我们已经把最后两家的钱还回去了。',Speaker=891614,}",
  [167258] = "{id=167258,Text='……唔，辛苦了。',Speaker=891613,}",
  [167259] = "{id=167259,Text='妞妞昨天都告诉我了，你们俩真的很厉害。',Speaker=891613,Option='虎妞很棒',}",
  [167260] = "{id=167260,Text='居然连夜蹲守，跟踪嫌犯，然后一口气击败了两百多个坏蛋。',Speaker=891613,Option='这部分有点夸张',}",
  [167261] = "{id=167261,Text='真不得了。我的妞妞长大了……',Speaker=891613,}",
  [167262] = "{id=167262,Text='事情解决了！终于可以好好陪我的妞妞过个年了！',Speaker=891613,Option='好耶',}",
  [167263] = "{id=167263,Text='好耶！',Speaker=891614,}",
  [167264] = "{id=167264,Text='很抱歉，没能够经常在家陪你……',Speaker=891613,}",
  [167265] = "{id=167265,Text='算啦，老爹……这次的事件之后，我完全能理解你的初衷了……',Speaker=891614,}",
  [167266] = "{id=167266,Text='换做是我，也会挺身而出的！',Speaker=891614,}",
  [167267] = "{id=167267,Text='毕竟我是虎王的闺女嘛！',Speaker=891614,}",
  [167268] = "{id=167268,Text='感谢你，[c][ffff00][PlayerName][-][/c]，她很幸运，有你这样的朋友陪伴着她。',Speaker=891613,Option='不客气',}",
  [167269] = "{id=167269,Text='对了！还有剩下的烟花！我们放烟花吧！',Speaker=891614,}",
  [167270] = "{id=167270,Text='真好看……',Speaker=891614,}",
  [167271] = "{id=167271,Text='不知不觉的，与你度过了快乐的新年，还解决了诈骗案。真开心啊~',Speaker=891614,Option='我也是',}",
  [167272] = "{id=167272,Text='老爹刚刚说……十二年后又是虎年……',Speaker=891614,}",
  [167273] = "{id=167273,Text='那年的守护神……',Speaker=891614,}",
  [167274] = "{id=167274,Text='就是我啦！',Speaker=891614,Option='太好了！',}",
  [167275] = "{id=167275,Text='到时候要让你看到我威风的样子！',Speaker=891614,}",
  [167276] = "{id=167276,Text='这个！送给你！',Speaker=891614,}",
  [167277] = "{id=167277,Text='那我们约好了！十二年后见！',Speaker=891614,Option='嗯！',}",
  [167278] = "{id=167278,Text='爹！带我去洛阳逛花楼吧！',Speaker=891614,}",
  [167279] = "{id=167279,Text='啥！？那是啥地方？小孩子能去吗？',Speaker=891613,}",
  [167280] = "{id=167280,Text='恭喜恭喜，恭喜发财！',Speaker=893145,}",
  [167281] = "{id=167281,Text='真热闹呀！每年过年的时候，就是我最忙的时候……',Speaker=893145,}",
  [167282] = "{id=167282,Text='为了筹备各种庆祝活动，都抽不出功夫去给平时关照我的朋友们拜年……',Speaker=893145,}",
  [167283] = "{id=167283,Text='你能帮我去吗？会有红包作为感谢哟~[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893145,Option='{交给我吧,2},{还有别的事,3}',}",
  [167284] = "{id=167284,Text='那可真是帮大忙了！',Speaker=893145,}",
  [167285] = "{id=167285,Text='请帮我把这份礼物交给《国家地理》特派员娜莎吧！她应该就在普隆德拉广场喷泉附近。',Speaker=893145,Option='好的',}",
  [167286] = "{id=167286,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167287] = "{id=167287,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893145,}",
  [167288] = "{id=167288,Text='新年快乐~',Speaker=1078,Option='递交礼物',}",
  [167289] = "{id=167289,Text='是B格猫星人送来的新年礼物！谢谢！',Speaker=1078,}",
  [167290] = "{id=167290,Text='辛苦你了！请收下。',Speaker=1078,}",
  [167291] = "{id=167291,Text='新年快乐~',Speaker=1078,}",
  [167292] = "{id=167292,Text='请帮我把这份礼物交给时空旅行者埃尔维斯吧！他应该就在艾尔帕兰的钟楼附近。',Speaker=893145,}",
  [167293] = "{id=167293,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167294] = "{id=167294,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893145,}",
  [167295] = "{id=167295,Text='新年的钟声敲响了。',Speaker=6560,Option='递交礼物',}",
  [167296] = "{id=167296,Text='是B格猫星人送来的新年礼物。嗯……',Speaker=6560,}",
  [167297] = "{id=167297,Text='谢谢。给你这个。',Speaker=6560,}",
  [167298] = "{id=167298,Text='新年快乐。',Speaker=6560,}",
  [167299] = "{id=167299,Text='请帮我把这份礼物交给魔法公会的雷藏吧！他应该就在吉芬的法师塔附近。',Speaker=893145,}",
  [167300] = "{id=167300,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167301] = "{id=167301,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893145,}",
  [167302] = "{id=167302,Text='新年的钟声敲响了。',Speaker=1321,Option='递交礼物',}",
  [167303] = "{id=167303,Text='哦？……原来如此，是礼物啊。',Speaker=1321,}",
  [167304] = "{id=167304,Text='我也有回礼，请收下。',Speaker=1321,}",
  [167305] = "{id=167305,Text='新年快乐！',Speaker=1321,}",
  [167306] = "{id=167306,Text='请帮我把这份礼物交给辛格拉吧！他应该就在里希塔乐镇工作。',Speaker=893145,}",
  [167307] = "{id=167307,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167308] = "{id=167308,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893145,}",
  [167309] = "{id=167309,Text='城里变得更热闹了！',Speaker=803273,Option='递交礼物',}",
  [167310] = "{id=167310,Text='啊！是新年礼物！',Speaker=803273,}",
  [167311] = "{id=167311,Text='真高兴~这个送给你！',Speaker=803273,}",
  [167312] = "{id=167312,Text='新年快乐~',Speaker=803273,}",
  [167313] = "{id=167313,Text='请帮我把这份礼物交给吟游诗人博宁·马格努斯吧！他应该就在斐扬城的城南。',Speaker=893145,}",
  [167314] = "{id=167314,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167315] = "{id=167315,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893145,}",
  [167316] = "{id=167316,Text='恭喜你发财~',Speaker=1592,Option='递交礼物',}",
  [167317] = "{id=167317,Text='啊！真没想到我也有礼物呢！真是太感谢了！',Speaker=1592,}",
  [167318] = "{id=167318,Text='请收下这个，希望能与你分享我的喜悦之情。',Speaker=1592,}",
  [167319] = "{id=167319,Text='祝你新年快乐！',Speaker=1592,}",
  [167320] = "{id=167320,Text='请帮我把这份礼物交给梦罗克商旅会财政官阿布吧！你能在梦罗克城中雕像西边找到他。',Speaker=893145,}",
  [167321] = "{id=167321,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167322] = "{id=167322,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893145,}",
  [167323] = "{id=167323,Text='新年快乐，外乡人。',Speaker=1566,Option='递交礼物',}",
  [167324] = "{id=167324,Text='真是太客气了。',Speaker=1566,}",
  [167325] = "{id=167325,Text='请收下谢礼。',Speaker=1566,}",
  [167326] = "{id=167326,Text='祝你新年发大财！',Speaker=1566,}",
  [167327] = "{id=167327,Text='请帮我把这份礼物交给总统秘书昆特吧！他肯定还在朱诺城西的总统府加班呢。',Speaker=893145,}",
  [167328] = "{id=167328,Text='谢谢你，真是帮大忙了！',Speaker=893145,}",
  [167329] = "{id=167329,Text='过年也不能休息啊……',Speaker=4757,Option='递交礼物',}",
  [167330] = "{id=167330,Text='让我看看是什么礼物！啊……生发水吗？',Speaker=4757,}",
  [167331] = "{id=167331,Text='挺好的……谢谢你，请收下这个。',Speaker=4757,}",
  [167332] = "{id=167332,Text='新年快乐！',Speaker=4757,}",
  [167333] = "{id=167333,Text='啊，那些被爱神之箭射中的爱侣们……此刻不知道是否终成眷属呢？',Speaker=893155,}",
  [167334] = "{id=167334,Text='情人节到了，他们现在是不是甜蜜的相互依偎着呢？',Speaker=893155,}",
  [167335] = "{id=167335,Text='好想知道啊……',Speaker=893155,}",
  [167336] = "{id=167336,Text='亲爱的[c][ffff00][PlayerName][-][/c]，帮我找到他们，用相机记录下他们甜蜜的瞬间吧！[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893155,Option='{好,2},{稍后再说,3}',}",
  [167337] = "{id=167337,Text='那么，我来看看他们此刻身在何方……',Speaker=893155,}",
  [167338] = "{id=167338,Text='嗯……第一对爱侣是一对毛茸茸……',Speaker=893155,}",
  [167339] = "{id=167339,Text='的多兰族。在罗扎纳东南方的海岸边。去找找吧！',Speaker=893155,}",
  [167340] = "{id=167340,Text='要我送你去罗扎纳吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167341] = "{id=167341,Text='看他们相互依偎，眺望着海面，多么安静，多么甜蜜……什么都无法打扰他们。',Speaker=893155,}",
  [167342] = "{id=167342,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。明天我们再去找下一对情侣吧！',Speaker=893155,}",
  [167343] = "{id=167343,Text='下一对情侣在里希塔乐……东北方向的梅尔区。',Speaker=893155,}",
  [167344] = "{id=167344,Text='要我送你去里希塔乐镇吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167345] = "{id=167345,Text='哎呀……看热情奔放的爱普洛，杰克就算是座冰山，也会被她爱情的火焰融化掉吧！',Speaker=893155,}",
  [167346] = "{id=167346,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。明天我们再去找下一对情侣吧！',Speaker=893155,}",
  [167347] = "{id=167347,Text='下一对情侣在拉赫城的……最南端。好像站在高处。',Speaker=893155,}",
  [167348] = "{id=167348,Text='要我送你去拉赫吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167349] = "{id=167349,Text='不在乎世俗的眼光，勇于追求爱情……至于对错，由别人说吧。',Speaker=893155,}",
  [167350] = "{id=167350,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。明天我们再去找下一对情侣吧！',Speaker=893155,}",
  [167351] = "{id=167351,Text='下一对情侣也在拉赫……在西南端的女神雕像旁边。',Speaker=893155,}",
  [167352] = "{id=167352,Text='要我送你去拉赫吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167353] = "{id=167353,Text='看似冷静的二人，已经被名为爱情的磁铁牢牢吸住，矜持只是瞬间，激情才是永远。',Speaker=893155,}",
  [167354] = "{id=167354,Text='……要不我再给他们补一箭吧？',Speaker=893155,}",
  [167355] = "{id=167355,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。明天我们再去找下一对情侣吧！',Speaker=893155,}",
  [167356] = "{id=167356,Text='下一对情侣在克魔岛……海滩上，挂满漂流瓶的海灵树的东边。',Speaker=893155,}",
  [167357] = "{id=167357,Text='要我送你去克魔岛吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167358] = "{id=167358,Text='瞧他们多默契！每次看到爱侣终成眷属，我都感动的眼眶湿润呢！',Speaker=893155,}",
  [167359] = "{id=167359,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。明天我们再去找下一对情侣吧！',Speaker=893155,}",
  [167360] = "{id=167360,Text='下一对情侣在洛阳花街的西边……嗯……酒的香气。',Speaker=893155,}",
  [167361] = "{id=167361,Text='要我送你去洛阳花街吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167362] = "{id=167362,Text='酒不醉人人自醉，他们已经酿成了名为爱的佳酿，只不过，他们自己不知道。',Speaker=893155,}",
  [167363] = "{id=167363,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。明天我们再去找下一对情侣吧！',Speaker=893155,}",
  [167364] = "{id=167364,Text='下一对情侣在梦幻城的西北角……他们小小的，要仔细找找。',Speaker=893155,}",
  [167365] = "{id=167365,Text='要我送你去梦幻城吗？',Speaker=893155,Option='{好,2},{自己去,3}',}",
  [167366] = "{id=167366,Text='跨越种族的禁断之恋……天呐！对他们来说，究竟是王子还是青蛙，仿佛已经无关紧要了。',Speaker=893155,}",
  [167367] = "{id=167367,Text='谢谢你，[c][ffff00][PlayerName][-][/c]。看样子他们都很幸福，那我也就放心了。这些天辛苦你了，我们一起来祝福天下有情人终成眷属吧！',Speaker=893155,}",
  [167368] = "{id=167368,Text='当一年中最圆的月亮高悬天际，当团圆的灯火照亮大街小巷——',Speaker=891634,}",
  [167369] = "{id=167369,Text='——本小姐就会闪亮登场',Speaker=891634,}",
  [167370] = "{id=167370,Text='元宵节快乐！冒险者~',Speaker=891634,Option='元宵节快乐~',}",
  [167371] = "{id=167371,Text='除了送上祝福，还为你们准备了礼物~猜对了灯谜就能拿到~',Speaker=891634,}",
  [167372] = "{id=167372,Text='谜题和礼物都在我面前的花灯里！快去试试吧~',Speaker=891634,}",
  [167373] = "{id=167373,Text='答错了也不要紧，只要丢100zeny进去就能再试一次~现在就开始吧？',Speaker=891634,Option='{好啊!,2},{等一下,3}',}",
  [167374] = "{id=167374,Text='灯谜：身穿雪白外衣，心里香甜如蜜。感觉是好吃的？',Option='{元宵,2},{天使波利,3}',}",
  [167375] = "{id=167375,Text='灯谜：是什么动物，眼睛像猫，尾巴像猫，全身都像猫，但却不是猫？',Option='{虎王,2},{犬妖,3}',}",
  [167376] = "{id=167376,Text='灯谜：每逢春节，噼里啪啦，飞上天空，火树银花。我也很喜欢看呢！',Option='{快板,3},{烟花,2}',}",
  [167377] = "{id=167377,Text='灯谜：一只篮子两个口，不装水来只装火。是新年经常见到的东西吧？',Option='{灯笼,2},{喇叭,3}',}",
  [167382] = "{id=167382,Text='灯谜：下一题！年兽宝宝害怕什么呢？',Option='{年糕,3},{鞭炮,2}',}",
  [167383] = "{id=167383,Text='灯谜：巴风特手里的武器是什么呢？',Option='{镰刀,2},{菜刀,3}',}",
  [167384] = "{id=167384,Text='灯谜：有点难度哦~普隆德拉最有名的铁匠是谁呢？',Option='{忽克雷,3},{忽克莱,3},{忽克连,2}',}",
  [167385] = "{id=167385,Text='100zeny没准备好，去打些魔物再来吧~',}",
  [167386] = "{id=167386,Text='答对了！这是奖励，请收下~',Speaker=891634,}",
  [167387] = "{id=167387,Text='（好像答错了……）',Speaker=891634,}",
  [167388] = "{id=167388,Text='（要投100zeny再试一次吗？）',Speaker=891634,Option='{投100zeny,4},{过会儿再试,10}',}",
  [167389] = "{id=167389,Text='这么快就答完了全部的灯谜！真厉害呀！不愧是你！',Speaker=891634,}",
  [167390] = "{id=167390,Text='今年的元宵猜灯谜就告一段落啦~那么我们明年再见吧！不要太想我哟~元宵节快乐！',Speaker=891634,}",
  [167393] = "{id=167393,Text='灯谜：什么东西明明长了四条腿，却用尾巴走路？',Option='{蛇女伊丝,3},{耶梦加得,2},{芬里尔,3}',}",
  [167394] = "{id=167394,Text='灯谜：女仆姐姐真可爱，仓储传送全靠他。说的是哪家公司呢？',Option='{卡普拉,2},{准它,3},{B格猫星人,3}',}",
  [167403] = "{id=167403,Text='好像就在附近，再仔细找找吧！',Speaker=893155,}",
  [167404] = "{id=167404,Text='（带虎妞去花街。要现在去吗？）',Option='{现在去,20},{稍等一会儿,21}',}",
  [167428] = "{id=167428,Text='你好！你就是大名鼎鼎的冒险者吧！我是普隆德拉新上任的事务官[c][ffff00]卡洛·史蒂芬[-][/c]！可以帮助我寻找适合踏春的地点嘛！',Speaker=893213,Option='踏春？',}",
  [167429] = "{id=167429,Text='王室准备举办[c][ffff00]踏春之旅[-][/c]呢！而寻找适合地点的重任，就交给了我哦！但是我不知道哪里适合啊！',Speaker=893213,}",
  [167430] = "{id=167430,Text='可以和我一起寻找合适的地点吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893213,Option='{我来帮你,2},{我还有事,3}',}",
  [167431] = "{id=167431,Text='太可惜了……如果有时间一定要帮帮我呀！',Speaker=893213,}",
  [167432] = "{id=167432,Text='太好了！那我们现在就出发吧！',Speaker=893213,Option='要去哪里',Emoji=23,}",
  [167433] = "{id=167433,Text='为了这次活动，我可是做了功课的！听说[c][ffff00]克克魔海滩[-][/c]的景色很美，我们就先去那里吧！',Speaker=893213,Option='走过去？？',}",
  [167434] = "{id=167434,Text='当然不是！我可是认真学习了传送魔法的，我们这就过去！',Speaker=893213,Option='{好的,5},{等一下,6}',}",
  [167435] = "{id=167435,Text='好美的海滩啊！原来外面的风景这么好看！',Speaker=893180,}",
  [167436] = "{id=167436,Text='原来冒险者每天看到的风景都这么美丽，我也想出来冒险了！……咳咳，不对不对，我们是来寻找春游地点的……',Speaker=893180,Option='哈哈哈哈',}",
  [167437] = "{id=167437,Text='那我们就分头考察一下环境吧，我看这里有很多螃蟹，如果可以抓一些螃蟹在春游的时候吃就最好了！',Speaker=893180,}",
  [167438] = "{id=167438,Text='这里怎么会有混乱的脚印？？',Emoji=8,}",
  [167439] = "{id=167439,Text='哎呦…最近真是诸事不顺啊…',Speaker=893205,Option='你是谁？',}",
  [167440] = "{id=167440,Text='呦~哪里的人到我们几个的地盘来了！保护费交没交呢？',Speaker=893205,Option='凭什么给你钱',}",
  [167441] = "{id=167441,Text='不给钱？竟然有人在我们地盘撒野，兄弟们揍他！',Speaker=893205,Option='哎哎哎？？',Emoji=1,}",
  [167442] = "{id=167442,Text='哎呦…哎呦…',Speaker=893205,}",
  [167443] = "{id=167443,Text='哎呦…哎呦…',Speaker=893206,}",
  [167444] = "{id=167444,Text='哎呦…哎呦…',Speaker=893207,}",
  [167445] = "{id=167445,Text='英雄，别打了英雄，我们知道错了…',Speaker=893205,Option='放过你们',Emoji=42,}",
  [167446] = "{id=167446,Text='谢谢英雄！',Speaker=893205,}",
  [167447] = "{id=167447,Text='谢谢英雄！',Speaker=893206,}",
  [167448] = "{id=167448,Text='谢谢英雄！',Speaker=893207,}",
  [167449] = "{id=167449,Text='又是亡灵生物又是不知哪里来的人，这年头流氓也不好干了啊…',Speaker=893205,Option='亡灵生物？',}",
  [167450] = "{id=167450,Text='看来得回去告诉卡洛·史蒂芬这里的情况了……',}",
  [167451] = "{id=167451,Text='你回来了，冒险者！怎么样，海滩作为踏春地点是不是很棒！',Speaker=893180,Option='说明情况',Emoji=9,}",
  [167452] = "{id=167452,Text='咿呀！怎么会有亡…亡灵生物的！',Speaker=893180,Emoji=37,}",
  [167453] = "{id=167453,Text='克克魔海滩绝对不能当作踏春地点了！如果民众遇到亡灵可怎么办啊！',Speaker=893180,}",
  [167454] = "{id=167454,Text='没办法了，我们明天再去找更合适的地点吧~',Speaker=893180,Option='好的',}",
  [167455] = "{id=167455,Text='你来啦冒险者！我又想到了合适的地点！',Speaker=893213,Option='在哪里？',}",
  [167456] = "{id=167456,Text='嘻嘻~我们到了就知道啦！你准备好了嘛~',Speaker=893213,Option='{现在出发,2},{再等一下,3}',}",
  [167457] = "{id=167457,Text='抓紧时间哦~等你准备好了就来找我吧！',Speaker=893213,}",
  [167458] = "{id=167458,Text='好的！那我现在就带你传送过去！让我想想传送魔法怎么用来着……',Speaker=893213,}",
  [167459] = "{id=167459,Text='额…我记得克特森林不是这样的啊…',Speaker=893180,Option='……',Emoji=8,}",
  [167460] = "{id=167460,Text='全队原地休息！',Speaker=893208,}",
  [167461] = "{id=167461,Text='这是…什么声音！？传送魔法是不是出了什么问题，这里怎么会有军队？？',Speaker=893180,Option='…这是风息之野',}",
  [167462] = "{id=167462,Text='呜呜呜…我的传送魔法还不熟练，我不是故意的…',Speaker=893180,}",
  [167463] = "{id=167463,Text='我们偷偷看看情况吧，我有种不详的预感…',Speaker=893180,}",
  [167464] = "{id=167464,Text='克克魔海滩太阳太猛烈了，还不如这里的环境好！',Speaker=893181,}",
  [167465] = "{id=167465,Text='今天我们在这里踏春，虽然离开了古城，大家的军心也不能乱！都明白吗！',Speaker=893181,}",
  [167466] = "{id=167466,Text='明白！',Speaker=893182,}",
  [167467] = "{id=167467,Text='明白！',Speaker=893183,}",
  [167468] = "{id=167468,Text='明白！',Speaker=893184,}",
  [167469] = "{id=167469,Text='大家整理一下身上的东西，我们一会找一个地方收集物资！',Speaker=893181,}",
  [167470] = "{id=167470,Text='呜…怎么会有这么多的亡灵生物啊，好可怕！难道克克魔海滩的痕迹也是他们…',Speaker=893180,Option='安慰',}",
  [167471] = "{id=167471,Text='不过，没想到亡灵生物也会踏春呀！在这种没有阳光的时候出来踏春算什么踏春嘛！真是不懂踏春的精髓！',Speaker=893180,}",
  [167472] = "{id=167472,Text='谁！是谁在那！',Speaker=893181,}",
  [167473] = "{id=167473,Text='你们对我们的踏春地点有什么意见吗？',Speaker=893181,Option='没…没有',}",
  [167474] = "{id=167474,Text='你们什么品味嘛！哪有人在这种暗得什么都看不到的地方踏春的！真没品位！',Speaker=893180,Option='（开始祈祷）',}",
  [167475] = "{id=167475,Text='偷听我们的谈话，嘲笑我们的品味，不管你是谁，为你的行为付出代价吧！',Speaker=893181,}",
  [167476] = "{id=167476,Text='全军列阵！',Speaker=893181,}",
  [167480] = "{id=167480,Text='重整部队，全军冲锋！',Speaker=893181,Emoji=12,}",
  [167481] = "{id=167481,Text='不打了不打了！太可怕了呜呜呜！冒险者我们快走！',Speaker=893180,}",
  [167482] = "{id=167482,Text='没想到居然会发生这种事…啊呀！',Speaker=893213,Option='怎么了',}",
  [167483] = "{id=167483,Text='我的怀表怎么不见了！',Speaker=893213,}",
  [167484] = "{id=167484,Text='可是那群魔物太可怕了，我再也不想去那个地方了…我们明天再想怎么办吧！',Speaker=893213,}",
  [167485] = "{id=167485,Text='冒险者…我昨天想了想，觉得自己做了不对的事情…',Speaker=893213,Option='什么事？',}",
  [167486] = "{id=167486,Text='我应该尊重每一个人的想法，不该质疑他们的品味的…虽然深渊骑士们是亡灵生物，但他们也有自己的习惯、自己的思想啊！我做了不对的事情，应该道歉！',Speaker=893213,Option='{我支持你,2},{不太理解,3}',}",
  [167487] = "{id=167487,Text='哎呀！他们也应当得到尊重嘛！冒险者怎么这样的！',Speaker=893213,}",
  [167488] = "{id=167488,Text='是吧！冒险者也这么想真是太好了！',Speaker=893213,}",
  [167489] = "{id=167489,Text='所以可以拜托冒险者陪我一起去向他们道歉嘛…我虽然坚定了想法，但害怕他们还在生气呢！',Speaker=893213,Option='{当然可以,5},{我还有事,6}',}",
  [167490] = "{id=167490,Text='那好吧…那我在这里等你，有时间了一定要来帮我哦！',Speaker=893213,}",
  [167491] = "{id=167491,Text='太好了！那我们现在就出发吧！',Speaker=893213,}",
  [167492] = "{id=167492,Text='对不起！我昨天的话———',Speaker=893180,}",
  [167493] = "{id=167493,Text='先等等！这[c][ffff00]怀表[-][/c]是你们的东西吧！',Speaker=893181,}",
  [167494] = "{id=167494,Text='没错，这确实是我的东西，是我家族代代相传的！',Speaker=893180,}",
  [167495] = "{id=167495,Text='小姑娘，你叫什么名字？',Speaker=893181,}",
  [167496] = "{id=167496,Text='这位…先生，我叫[c][ffff00]卡洛·史蒂芬[-][/c]，请问发生什么事情了吗？',Speaker=893180,}",
  [167497] = "{id=167497,Text='哈哈哈哈哈！我的名字叫[c][ffff00]埃隆加·史蒂芬[-][/c]，这块怀表里夹着的照片，上面就是我！',Speaker=893181,Option='所以你们…',}",
  [167498] = "{id=167498,Text='哈哈哈哈哈！我先带你们回我的营地，我们慢慢聊！',Speaker=893181,}",
  [167499] = "{id=167499,Text='原来你就是大名鼎鼎的冒险者啊！你的名字在亡灵中间也很有名气哦！',Speaker=893185,Option='听着怪怪的',}",
  [167500] = "{id=167500,Text='别看我现在这样，以前我和这群兄弟们还是白骑士团的一员呢！',Speaker=893185,}",
  [167501] = "{id=167501,Text='白骑士团战无不胜！',Speaker=893182,}",
  [167502] = "{id=167502,Text='白骑士团战无不胜！',Speaker=893183,}",
  [167503] = "{id=167503,Text='白骑士团战无不胜！',Speaker=893184,}",
  [167504] = "{id=167504,Text='既然如此，那为什么……',Emoji=8,}",
  [167505] = "{id=167505,Text='我大概知道一点点其中的原因——',Speaker=893180,}",
  [167506] = "{id=167506,Text='当年我们驻扎在古城，守护着普隆德拉的和平…但后来骑士团被人算计，大家都堕落成了深渊骑士…',Speaker=893185,Option='所以你们才…',}",
  [167507] = "{id=167507,Text='没什么，现在的普隆德拉一定很幸福祥和了！那我们这群战士就满足了！',Speaker=893185,}",
  [167508] = "{id=167508,Text='太惭愧了…他们可是普隆德拉的英雄呀！结果我却…',Speaker=893180,Option='送些花致歉？',}",
  [167509] = "{id=167509,Text='好主意！那我们采一些花送给他们吧~',Speaker=893180,}",
  [167510] = "{id=167510,Text='啊！谢谢你们俩！已经很久没有人送给我们花了！',Speaker=893185,}",
  [167511] = "{id=167511,Text='时间不早了，我们也得向您道别了~我们还有工作，以后会来看望您的！',Speaker=893180,}",
  [167512] = "{id=167512,Text='真是个乖巧的孩子啊！快回去吧！',Speaker=893185,}",
  [167513] = "{id=167513,Text='我真的真的想不到还有哪里很适合踏春了…',Speaker=893213,Option='问问埃隆加',Emoji=42,}",
  [167514] = "{id=167514,Text='哎！你说的有道理诶~他们一定有更加丰富的经验才对！',Speaker=893213,}",
  [167515] = "{id=167515,Text='那我们现在就去找深渊骑士们问问看吧~',Speaker=893213,Option='{这就过去,2},{我再想想,3}',}",
  [167516] = "{id=167516,Text='哎呀~时间不等人哦！',Speaker=893213,}",
  [167517] = "{id=167517,Text='好哦！那我们这就出发~传送魔法，启动！',Speaker=893213,}",
  [167518] = "{id=167518,Text='嗯？你们两个小家伙怎么这么快就回来看我们了啊！哈哈哈哈哈哈~',Speaker=893185,}",
  [167519] = "{id=167519,Text='其实是这样的……',Speaker=893180,}",
  [167520] = "{id=167520,Text='卡洛·史蒂芬向深渊骑士长讲述了情况…',}",
  [167521] = "{id=167521,Text='[c][ffff00]普隆德拉西门[-][/c]不就很好嘛，你们两个小家伙怎么舍近求远的？',Speaker=893185,}",
  [167522] = "{id=167522,Text='啊…啊？普隆德拉西门？',Speaker=893180,}",
  [167523] = "{id=167523,Text='我记得我那时候西门有着郁郁葱葱的森林、婉转的鸟鸣、醉人的花香……还有温暖的，幸福的阳光。',Speaker=893185,Option='听起来不错',}",
  [167524] = "{id=167524,Text='对啊！我怎么没想到呢~明明最适合踏春的地方就在眼前呢！',Speaker=893180,}",
  [167525] = "{id=167525,Text='尊敬的埃隆加骑士，虽然非常唐突，但我想邀请您与白骑士团的战友们与我们一同去普隆德拉西门考察踏春地点！',Speaker=893180,}",
  [167526] = "{id=167526,Text='您作为城市的英雄，一定想再看看普隆德拉这座自己保护过的城市吧！',Speaker=893180,}",
  [167527] = "{id=167527,Text='这……',Speaker=893185,}",
  [167528] = "{id=167528,Text='怎么了么？',Speaker=893180,}",
  [167529] = "{id=167529,Text='冒险者，可以让我们先讨论一下吗？这种事情我不能一个人决定。',Speaker=893185,Option='好的',}",
  [167530] = "{id=167530,Text='那就拜托你了。',Speaker=893185,}",
  [167531] = "{id=167531,Text='（为什么总感觉哪里不对？）',}",
  [167532] = "{id=167532,Text='我们先离开这里吧，给几位骑士先生一些聊天的空间~',Speaker=893180,}",
  [167533] = "{id=167533,Text='等等！有没有听到什么奇怪的声音…',Speaker=893180,Option='小心！',}",
  [167534] = "{id=167534,Text='在晚上两个人单独出来，可是很危险的哦~',Speaker=893188,Option='你们！',Emoji=5,}",
  [167535] = "{id=167535,Text='不会吧~看着很有气势，其实也不过如此嘛~',Speaker=893188,}",
  [167536] = "{id=167536,Text='全军列阵！',Speaker=893185,}",
  [167537] = "{id=167537,Text='是！',Speaker=893182,}",
  [167538] = "{id=167538,Text='是！',Speaker=893183,}",
  [167539] = "{id=167539,Text='是！',Speaker=893184,}",
  [167540] = "{id=167540,Text='怎么是你们！一群不伦不类的家伙，亡灵生物当不下去就来伸张正义了？',Speaker=893188,}",
  [167541] = "{id=167541,Text='现在离开还来得及，不然我不保证会发生什么！',Speaker=893185,}",
  [167542] = "{id=167542,Text='哼！我们走！',Speaker=893188,}",
  [167543] = "{id=167543,Text='太感谢你们了，骑士先生们！',Speaker=893180,Option='你们认识？',}",
  [167544] = "{id=167544,Text='那群怪物也是古城的亡灵生物，估计是和我们走一样的路来到这里的吧。',Speaker=893185,}",
  [167545] = "{id=167545,Text='骑士先生们讨论的怎么样了？',Speaker=893180,Option='询问结果',}",
  [167546] = "{id=167546,Text='唉…拗不过你这孩子，那我们就陪你们去一趟吧！就当是回一次家了。',Speaker=893185,}",
  [167547] = "{id=167547,Text='太好了！那我们明天在西门不见不散哦！',Speaker=893180,}",
  [167548] = "{id=167548,Text='你来啦，冒险者！我们等了你好久了！',Speaker=893180,}",
  [167549] = "{id=167549,Text='这里的环境真的很棒！空气也很清新，作为郊游地点再好不过了！',Speaker=893180,}",
  [167550] = "{id=167550,Text='哈哈哈哈哈哈哈~既然如此，你们是不是要先布置一下场地呢！我和我的老伙计们可以帮忙哦！',Speaker=893185,Option='同意',Emoji=17,}",
  [167551] = "{id=167551,Text='既然如此，那就麻烦冒险者去采一些果子，再把这几天收集的物资布置好吧！',Speaker=893180,Option='好的',}",
  [167552] = "{id=167552,Text='感谢大家帮忙布置会场！大家辛苦了~',Speaker=893180,}",
  [167553] = "{id=167553,Text='桀桀桀！你们果然在这里！',Speaker=893208,Option='什么人！？',}",
  [167554] = "{id=167554,Text='怎么又是你们这群坏蛋！',Speaker=893180,}",
  [167555] = "{id=167555,Text='我想了想，我们数量比你们多，为什么要跑呢？哈哈哈哈哈~',Speaker=893188,}",
  [167556] = "{id=167556,Text='嘿嘿~没力气了吧！让我给你最后一击吧——',Speaker=893188,}",
  [167557] = "{id=167557,Text='不，不不——啊！……',Speaker=893188,}",
  [167558] = "{id=167558,Text='这是怎么回事？它怎么自己消失了…',Speaker=893180,Option='不清楚',}",
  [167559] = "{id=167559,Text='也得谢谢骑士先生们帮我们…！骑士先生！',Speaker=893180,Option='发生什么了',}",
  [167560] = "{id=167560,Text='就算比那些风魔巫师的亡灵位阶更高，也抵挡不住阳光的伤害吗…',Speaker=893185,}",
  [167561] = "{id=167561,Text='怎么会这样！',}",
  [167562] = "{id=167562,Text='骑士先生们正在消失……',Speaker=893180,}",
  [167563] = "{id=167563,Text='魔物天生就是被阳光克制的，被阳光照射等于宣判死刑…',Speaker=893185,Option='我早该想到…',}",
  [167564] = "{id=167564,Text='都是我不好，我不应该求着骑士先生们回来的！怎么办啊！',Speaker=893180,}",
  [167565] = "{id=167565,Text='孩子，不是你的错，我们早就知道来到这里会付出生命了…',Speaker=893185,}",
  [167566] = "{id=167566,Text='但不管怎样，这座祥和的城市，普隆德拉…我们也始终割舍不下啊…',Speaker=893185,}",
  [167567] = "{id=167567,Text='谢谢你们陪着我实现了最后的心愿…请代替我们守护好这座城市吧…',Speaker=893185,Option='不…',}",
  [167568] = "{id=167568,Text='骑士先生们离开的时候应该很幸福吧……',Speaker=893180,}",
  [167569] = "{id=167569,Text='他们虽然成为了亡灵生物，却以人类的心灵生活，没有忘记初心。',Speaker=893180,Option='卡洛…',}",
  [167570] = "{id=167570,Text='（擦干眼泪）我们不应该哭的！骑士先生们实现了愿望不是嘛！',Speaker=893180,}",
  [167571] = "{id=167571,Text='就把地点定在这里吧，我想把骑士先生们的故事分享给普隆德拉的大家！',Speaker=893180,Option='好！',}",
  [167572] = "{id=167572,Text='求救！冒险者请帮帮我吧！',Speaker=893997,}",
  [167573] = "{id=167573,Text='发生什么事了？',}",
  [167574] = "{id=167574,Text='不知从哪来了2个捣蛋又不听话的哥布灵，说是在过愚人节搞恶作剧，它们手里拿着变傻光线枪在星泪森林里疯狂攻击欢乐跳菇！',Speaker=893997,}",
  [167575] = "{id=167575,Text='星泪森林里好多曼陀罗被它们一会变大一会又变小，神经都要错乱了！',Speaker=893997,}",
  [167576] = "{id=167576,Text='你可以帮我赶走这些哥布灵吗？',Speaker=893997,Option='{没问题,3},{现在没空……,2}',}",
  [167577] = "{id=167577,Text='看来它们没有搞清楚恶作剧和做坏事的区别……',}",
  [167578] = "{id=167578,Text='那我就亲自出马制止它们吧',}",
  [167579] = "{id=167579,Text='好吧，让我再等个有缘人……',Speaker=893997,}",
  [167580] = "{id=167580,Text='呜呜呜，你真是个好人，就让我把你传送过去吧，加油！',Speaker=893997,}",
  [167581] = "{id=167581,Text='快住手，你们这些干坏事的哥布灵！',}",
  [167582] = "{id=167582,Text='是爱管闲事的冒险者！什么叫干坏事，我们只是在小小的恶作剧，愚人节万岁！',Speaker=893209,}",
  [167583] = "{id=167583,Text='不用怕他，我来把他变小！看他还能怎么办！',Speaker=893210,}",
  [167584] = "{id=167584,Text='雕虫小技，你们以为这样我就没办法了吗？',}",
  [167585] = "{id=167585,Text='呜哇，冒险者好强……',Speaker=893209,}",
  [167586] = "{id=167586,Text='呜啊，我们认输……',Speaker=893210,}",
  [167587] = "{id=167587,Text='虽然你们只是想搞个恶作剧娱乐一下，但是只要超过了红线那就是做坏事了哦！',}",
  [167588] = "{id=167588,Text='以后不准再欺负别的小魔物，把恶作剧光线枪也要还回去！',}",
  [167589] = "{id=167589,Text='呜哇，我们知道错了……',Speaker=893210,}",
  [167590] = "{id=167590,Text='呜呜呜，冒险者你回来了，怎么样，它们还在恶作剧吗？',Speaker=893997,}",
  [167591] = "{id=167591,Text='放心吧，它们回去了',}",
  [167592] = "{id=167592,Text='真是太感谢了，请收下我的一点心意。',Speaker=893997,}",
  [167596] = "{id=167596,Text='你好冒险者，今天我有个关于清明节的小贴士想要分享给你。',Speaker=1582,Option='清明节？',}",
  [167597] = "{id=167597,Text='没错，清明节是斐扬的一个重要的传统节日，时间在每年的春分过后，谷雨之前。',Speaker=1582,}",
  [167598] = "{id=167598,Text='每当到了这个节日，人们便会相约外出踏青，祭奠故人。',Speaker=1582,}",
  [167599] = "{id=167599,Text='可惜我离乡多年，始终没有机会去给我逝去的亲人扫墓。',Speaker=1582,}",
  [167600] = "{id=167600,Text='请问你可以帮我去墓园摆放一些鲜花表达我对他们的思念吗？',Speaker=1582,Option='{当然可以,3},{现在没空……,2}',}",
  [167601] = "{id=167601,Text='太感谢了，我准备了一份礼物，等你做完后回来拿吧。',Speaker=1582,}",
  [167602] = "{id=167602,Text='好吧，希望我能抽出点时间去给他们扫墓……',Speaker=1582,}",
  [167603] = "{id=167603,Text='已经做好了吗？请收下我的谢礼吧。',Speaker=1582,}",
  [167604] = "{id=167604,Text='不过值得一提的是，清明节不只是一个严肃的节日，也是让我们亲近自然，感受春日的美好时节，希望你可以从中感受到生活的美好~',Speaker=1582,}",
  [167605] = "{id=167605,Text='不管怎么说，我们成功保护了踏春地点，赶跑了魔物！多亏了你啊，冒险者！',Speaker=893180,}",
  [167608] = "{id=167608,Text='哈喽冒险者，好久不见~',Speaker=892853,}",
  [167609] = "{id=167609,Text='首先来看看我为你准备的一份见面礼吧~',Speaker=892853,}",
  [167610] = "{id=167610,Text='伴随着一年一度的复活节的到来，这次我们来做一个新游戏怎么样？',Speaker=892853,}",
  [167611] = "{id=167611,Text='只要你赢了，就可以拿走下一份奖励哦~',Speaker=892853,Option='{当然可以,3},{现在没空……,2}',}",
  [167612] = "{id=167612,Text='好吧，希望我还有机会送出这份礼物……',Speaker=892853,}",
  [167613] = "{id=167613,Text='嗯嗯，真不错，爱挑战的冒险者总是讨人喜欢，那么就让我们开始吧！',Speaker=892853,}",
  [167614] = "{id=167614,Text='怎么样？看花眼了吗？',Speaker=892853,Option='？？？',}",
  [167615] = "{id=167615,Text='等等，你先解释一下刚才的烟雾是什么情况',}",
  [167616] = "{id=167616,Text='嘻嘻，不要在意这些细节，现在，请你猜猜金苹果在哪个宝箱里面吧~',Speaker=892853,Option='{左边,6},{中间,7},{右边,8}',}",
  [167617] = "{id=167617,Text='喔喔！答对了~赶快收下礼物吧，期待我们明年再见！',Speaker=892853,}",
  [167618] = "{id=167618,Text='……很可惜，答错了，再猜一次吧~',Speaker=892853,}",
  [167623] = "{id=167623,Text='郊游地点已经定为普隆德拉南门附近啦！但是现在我们紧缺大量郊游需要的物资！冒险家你有物资可以捐给大家吗？',Speaker=893224,}",
  [167624] = "{id=167624,Text='记得每天都来哦~',Speaker=893224,}",
  [167625] = "{id=167625,Text='现在，请你猜猜金苹果在哪个宝箱里面吧~',Speaker=892853,Option='{左边,6},{中间,7},{右边,8}',}",
  [167626] = "{id=167626,Text='（卡洛突然一言不发的跑开了，她怎么了？）',}",
  [167627] = "{id=167627,Text='对不起骑士先生，我那天太过分了…我要向您道歉！',Speaker=893180,}",
  [167628] = "{id=167628,Text='哈哈哈哈~哪有我这么大年纪的人还和你这么年轻的小女孩计较的，我早就原谅你啦！',Speaker=893185,}",
  [167629] = "{id=167629,Text='你们可真是阴魂不散！冒险者，我们一起消灭他们！',Speaker=893185,Option='好！',}",
  [167630] = "{id=167630,Text='一年一度的女王节又来啦！！',Speaker=893225,Option='妇女节吗？',}",
  [167631] = "{id=167631,Text='什么！？谁说是妇女节！？',Speaker=893225,}",
  [167632] = "{id=167632,Text='不对不对！在我的故乡，没有叫妇女节的节日！只有“女王节”。',Speaker=893225,}",
  [167633] = "{id=167633,Text='为了这神圣的节日，我们给大家准备了礼物哦~~',Speaker=893225,Option='谢谢',}",
  [167634] = "{id=167634,Text='记得明年再来找我哦~我还会来的！',Speaker=893225,}",
  [167635] = "{id=167635,Text='你好，冒险家。知道今天是什么日子吗？',Speaker=893226,Option='植树节',}",
  [167636] = "{id=167636,Text='你说对了，今天是植树节，是爱护树木的日子。',Speaker=893226,}",
  [167637] = "{id=167637,Text='普隆德拉的冒险家们，都有良好的爱护树木的意识，因此为了感谢大家，我带来了小小的礼物，你收下吧~',Speaker=893226,}",
  [167638] = "{id=167638,Text='听说马上就要去郊游啦！好期待喵~~我准备了郊游补给品给各位冒险家~',Speaker=893227,}",
  [167639] = "{id=167639,Text='记得每日来领取喵！！',Speaker=893227,}",
  [167640] = "{id=167640,Text='哈喽！！冒险者，我们要去的郊游目的地，最近忽然出现了大量的魔物！',Speaker=893228,}",
  [167641] = "{id=167641,Text='可以帮忙去消灭他们吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893228,Option='{我来帮你,2},{我还有事,3}',}",
  [167642] = "{id=167642,Text='太可惜了……如果有时间一定要帮帮大家呀！',Speaker=893228,}",
  [167643] = "{id=167643,Text='太好了！那就拜托你啦，打败它们获得的材料「春的气息」可以给我，我会给你谢礼哦！第五天我会给你特殊的头饰「趴趴灵鹿」！！',Speaker=893228,Option='知道啦！',}",
  [167644] = "{id=167644,Text='哈喽！！冒险者，你来啦！你今天收集好50个「春的气息」了吗？',Speaker=893228,Option='收集好了',}",
  [167645] = "{id=167645,Text='你貌似还没有50个哦~~快去击败树精获取吧！',Speaker=893228,}",
  [167646] = "{id=167646,Text='今日份的「春的气息」已收到，这是给你的奖励~~还有4天就可以获得头饰「趴趴灵鹿」啦！',Speaker=893228,}",
  [167647] = "{id=167647,Text='今日份的「春的气息」已收到，这是给你的奖励~~还有3天就可以获得头饰「趴趴灵鹿」啦！',Speaker=893228,}",
  [167648] = "{id=167648,Text='今日份的「春的气息」已收到，这是给你的奖励~~还有2天就可以获得头饰「趴趴灵鹿」啦！',Speaker=893228,}",
  [167649] = "{id=167649,Text='今日份的「春的气息」已收到，这是给你的奖励~~还有1天就可以获得头饰「趴趴灵鹿」啦！',Speaker=893228,}",
  [167650] = "{id=167650,Text='今日份的「春的气息」已收到，这是给你的奖励~~今天有特殊的奖励哦！「趴趴灵鹿」送给你！',Speaker=893228,}",
  [167651] = "{id=167651,Text='今日份的「春的气息」已收到，这是给你的奖励~~',Speaker=893228,}",
  [167664] = "{id=167664,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893231,}",
  [167665] = "{id=167665,Text='看到如此博学的我，是不是充满了羡慕？',Speaker=893231,Action={5},}",
  [167666] = "{id=167666,Text='哈哈~不用羡慕，只要你帮我把知识分享出去，所有人都可以变得跟我一样博学~！',Speaker=893231,Option='我要怎么做？',}",
  [167667] = "{id=167667,Text='我总结了许多实用的[c][ffff00]「冒险经验」[-][/c]可以帮助到更多的冒险者。只要你愿意将[c][ffff00]「冒险经验」[-][/c]传递出去，想必可以帮助到更多的冒险者！',Speaker=893231,}",
  [167670] = "{id=167670,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「哥布灵的冒险礼盒」x1）[-][/c]\n那么，今天需要分享出去的[c][ffff00]「冒险经验」[-][/c]是：',Speaker=893231,}",
  [167682] = "{id=167682,Text='呜呜呜呜呜~庆典就要开始了，但是物资都还没有筹集好',Speaker=893232,}",
  [167683] = "{id=167683,Text='冒险者，如果你能帮我度过这次难关，我哥布灵族一定忘不了你！',Speaker=893232,}",
  [167685] = "{id=167685,Text='总有一些哥布灵想要破坏这场庆典',Speaker=893231,Option='哪些哥布灵？',}",
  [167686] = "{id=167686,Text='捣蛋的[c][ffff00]喷射哥布灵[-][/c]出现在了[c][ffff00]克克魔海滩[-][/c]上，据说需要清理了他们的同伙[c][ffff00]刺刀哥布灵[-][/c]，他们才会出现',Speaker=893231,}",
  [167687] = "{id=167687,Text='如果冒险者能给我带回来5个[c][ffff00]「哥布灵的求饶信」[-][/c]，我一定会给你珍藏已久的礼物~',Speaker=893231,}",
  [167688] = "{id=167688,Text='确定要提交[c][ffff00]「哥布灵的求饶信」[-][/c]x5吗？',Speaker=893231,Option='{确定,4},{再想想,3}',}",
  [167689] = "{id=167689,Text='冒险者，你身上暂时没有足够的材料哦！请先去获取后再来捐献吧！前往[c][ffff00]克克魔海滩[-][/c]看看吧~',Speaker=893231,}",
  [167690] = "{id=167690,Text='感谢你~祝你冒险快乐~！',Speaker=893231,}",
  [167692] = "{id=167692,Text='波利电音节吗……驻唱乐队怎么还没来呀！这可如何是好？你可以帮助我吗？',Speaker=893234,}",
  [167693] = "{id=167693,Text='啊！年轻的冒险者，我有一个请求。',Speaker=893234,}",
  [167694] = "{id=167694,Text='驻唱乐队——波利粉。虽然早在几个月前，就接受了我们的邀请，但却迟迟未见踪影……',Speaker=893234,}",
  [167695] = "{id=167695,Text='我不禁怀疑……在这过往的几个月中，他们是不是发生了什么变故？',Speaker=893234,Option='不会吧……',}",
  [167696] = "{id=167696,Text='请你帮我找寻一下他们吧。对了，我这里有些地址，你明天可以前往拜访一下，或许能获得一些线索。',Speaker=893234,Option='没问题',}",
  [167698] = "{id=167698,Text='没错，我就是波利粉乐队的头号粉丝。每张单曲，我都会！',Speaker=893238,Option='他们在哪儿？',}",
  [167699] = "{id=167699,Text='唉，别说了，想起来就伤心。上次看到他们的现场，还是在去年的波利庆典上客串了一个开场。',Speaker=893238,}",
  [167700] = "{id=167700,Text='听说那次演出后，伴奏吉米就默默退出了乐队……',Speaker=893238,Option='因为演出不成功吗？',}",
  [167701] = "{id=167701,Text='哦不！这倒没有！虽然只是一个开场，但仍然点燃了全场！',Speaker=893238,Option='那是为什么呢？',}",
  [167702] = "{id=167702,Text='嗯……因为直到演出结束，都没有人发现吉米因为紧张，乐器都拿反了……',Speaker=893238,}",
  [167703] = "{id=167703,Text='对了对了！你去梦罗克拜访吉米吧……他或许还有其他人的联系方式！',Speaker=893238,Option='好，谢谢你',}",
  [167705] = "{id=167705,Text='在梦罗克安度晚年才是正确的选择…',Speaker=893236,Option='波利粉乐队？',}",
  [167706] = "{id=167706,Text='没想到过去了这么久，还有人能记得我的失误……唉，我也很后悔，竟然因为一次失误，就退出乐队了……',Speaker=893236,}",
  [167707] = "{id=167707,Text='啊……不好意思，听到这个“波利粉”这个词，我就开始自言自语了。你找我有什么事吗？',Speaker=893236,Option='电音节的邀约',}",
  [167708] = "{id=167708,Text='原来是这样。错过邀约可不是第一次的事情了……哪怕是上次的波利庆典，也是主办方提前1个月就开始跟随我们了，才保证准时到场。',Speaker=893236,}",
  [167709] = "{id=167709,Text='让我想想……按照他们的习惯，这个时候多半在依斯鲁得的码头那儿，喝着酒，聊着天吧！嘿，那是我们每年这个时候，最喜欢做的事！',Speaker=893236,Option='好，我去看看！',}",
  [167710] = "{id=167710,Text='我还有一个请求……如果可以的话……',Speaker=893236,}",
  [167711] = "{id=167711,Text='如果……你遇到了他们，能不能……帮我问一下，我……我还能回到乐队吗？我是说如果……他们能原谅我的失误。',Speaker=893236,Option='没问题',}",
  [167712] = "{id=167712,Text='自从上次演出之后，我真的没脸面对他们了……',Speaker=893236,Option='交给我吧',}",
  [167714] = "{id=167714,Text='你好呀，冒险者。虽然还早，但是好想喝一杯呀！',Speaker=893239,Option='波利粉乐队？',}",
  [167715] = "{id=167715,Text='虽然我不知道你说的是谁……但每年的这几天码头总会出现2个奇怪的家伙。',Speaker=893239,}",
  [167716] = "{id=167716,Text='他们呀！边喝着酒，边敲打着瓶子发出“咚咚……砰砰……”的声音，可以闹腾一整天！他们是你要找的乐队吗？',Speaker=893239,Option='有可能！',}",
  [167717] = "{id=167717,Text='我不懂音乐啦~但是听到他们发出的声音，竟然可以抖腿一整天！',Speaker=893239,}",
  [167718] = "{id=167718,Text='不过他们总是隔两天才出现一次。诶，明天！明天刚好是出现的时间！不妨明日来偶遇他们吧！',Speaker=893239,Option='好，谢谢你',}",
  [167720] = "{id=167720,Text='诶？你说是费雷德委托你来的？没想到我们又要迟到了，哈哈哈哈哈哈！',Speaker=893235,}",
  [167721] = "{id=167721,Text='（果然是一个神经大条的蝗虫，每次音乐节都少不了的蝗虫……）',}",
  [167722] = "{id=167722,Text='普隆德拉的电音节啊……知道了知道了，距离开始不是还有几天吗！诶？是几天来着……',Speaker=893235,Option='……',}",
  [167723] = "{id=167723,Text='随着年岁渐长，身边的人注定会逐渐离去，还有那些永远也记不到的事情。唉。',Speaker=893235,Option='怎么了？',}",
  [167724] = "{id=167724,Text='你问我为什么有点忧伤吗？或许，你知道我们之前是个三人组……但因为某些事情，渐走渐远了……',Speaker=893235,}",
  [167725] = "{id=167725,Text='现在想来都是年少时的冲动……明明风格迥异，各有所好，却能在一起创造很多美妙的音乐！',Speaker=893235,Option='吉米？',}",
  [167726] = "{id=167726,Text='对，他是一个幼稚鬼，又爱较劲！嗯？你见过他啦！他……他还好吗？',Speaker=893235,Option='转达吉米的问候',}",
  [167727] = "{id=167727,Text='（查理沉默了……难道我说错话了？）',}",
  [167728] = "{id=167728,Text='哈哈哈哈哈哈哈！这小子也太傻了吧！下次见到他，我一定要再次笑话他！哈哈哈哈！',Speaker=893235,Option='听起来和好了！',}",
  [167729] = "{id=167729,Text='嗯呐，早就不在意了！他肯定也是这么想的。你还没见过他吧？我们的主唱，依斯鲁得每个卖酒的地方，他都光顾过！你去碰碰运气，没准呢？',Speaker=893235,Option='好，我明天去找找！',}",
  [167731] = "{id=167731,Text='[c][ffff00][PlayerName][-][/c]，你来啦~查理已经转达了你的来意。谢谢你远道而来，至于电音节，我们会如期到场的。',Speaker=893237,}",
  [167732] = "{id=167732,Text='（那个神经大条的蝗虫，难道忘记了什么重要的事情吗？）',Option='转达吉米的问候',}",
  [167733] = "{id=167733,Text='人生如梦。如果可以的话，有些话我想当面告诉他。不如……？',Speaker=893237,}",
  [167734] = "{id=167734,Text='你也想看到完整的乐队演出吧？请去顺便拜访一下吉米吧！',Speaker=893237,}",
  [167735] = "{id=167735,Text='“老地方见！”对，就这样告诉他。明天的这个时候，我们在码头见！拜托了，冒险者。',Speaker=893237,Option='好',}",
  [167736] = "{id=167736,Text='[c][ffff00][PlayerName][-][/c]，你回来了！怎样？',Speaker=893236,Option='分享路上的见闻',}",
  [167737] = "{id=167737,Text='呜呜呜呜呜~大家……原来，我才是那个大傻瓜！',Speaker=893236,Option='老地方',}",
  [167738] = "{id=167738,Text='好！我明天一定去，你也要来哟~',Speaker=893236,Option='好',}",
  [167739] = "{id=167739,Text='[c][ffff00][PlayerName][-][/c]，你来啦！我们已经在这里等了好一会儿了。',Speaker=893236,}",
  [167740] = "{id=167740,Text='其实并不是每个人都会有机会，有生之年又再次重逢吧……大家一般都只看到了我们光彩的一面，在舞台上获得掌声，在台下受到追捧……',Speaker=893236,}",
  [167741] = "{id=167741,Text='但经历了这些，最让我刻骨铭心的还是朋友之间的包容吧……',Speaker=893236,}",
  [167742] = "{id=167742,Text='没想到，许久了未见，吉米还是没有长高呀！当初那件小事，是时候让它过去了！来，让我们重组波利粉乐队吧！',Speaker=893235,}",
  [167743] = "{id=167743,Text='嗯。但是下次除了专心投入创造之中，我们也要保证高质量的演出。',Speaker=893237,}",
  [167744] = "{id=167744,Text='其实，我们比大多数人都幸运。很多人都穷其一生，贡献出自己全部的时间去创造，将自己的人生写进歌中，只想被人们记住。',Speaker=893237,}",
  [167745] = "{id=167745,Text='终有些落寞，有些陨落，有些郁郁而终……唉……',Speaker=893237,Option='能做点什么吗？',}",
  [167746] = "{id=167746,Text='或许吧，不要吝啬赞美的话语，继续享受你爱的作品。这大概对于我们而言，就是最好的结局了……',Speaker=893237,}",
  [167747] = "{id=167747,Text='没错！比起个人的小情绪，我更想为大家带去我们的新作品。以后，也请继续支持我们！',Speaker=893236,Option='好',}",
  [167748] = "{id=167748,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=893234,}",
  [167749] = "{id=167749,Text='呐，今天可是母亲节哦~记得要多陪陪妈妈，妈妈可是世界上最伟大的人呢~',Speaker=893234,Option='好',}",
  [167750] = "{id=167750,Text='如果你乖乖听话的话，这个「暖暖的心意」就当做我送给你的小礼物吧~',Speaker=893234,Option='谢谢',}",
  [167751] = "{id=167751,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=893234,}",
  [167752] = "{id=167752,Text='今天属于我，属于你，属于你们，每个打工人都值得被赞扬！',Speaker=893234,}",
  [167753] = "{id=167753,Text='这份「打工人必需品」就送给你了！',Speaker=893234,Option='谢谢',}",
  [167754] = "{id=167754,Text='又见面了，冒险者！我们准备开启新的巡游啦！',Speaker=893242,}",
  [167755] = "{id=167755,Text='但是！现在我们紧缺远行需要的物资！冒险家你有物资可以捐给大家吗？',Speaker=893242,Option='当然',}",
  [167757] = "{id=167757,Text='完成信仰试炼，我会有特别的奖励赠予你！甚至还有我珍藏已久，普隆德拉大陆最酷炫的头饰——「波利镭射灯」！',Speaker=893240,}",
  [167758] = "{id=167758,Text='准备好了的话，就出发吧！',Speaker=893240,Option='好',}",
  [167759] = "{id=167759,Text='噢！天哪，据说这些「狂热粉丝」身上携带着早已绝版的「首次演出的门票」！',Speaker=893238,}",
  [167760] = "{id=167760,Text='如果收集了「首次演出的门票」x3，就能获得波利粉乐队主唱用过的「电音贝斯」！太想要了！！',Speaker=893238,}",
  [167761] = "{id=167761,Text='怎么样？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=893234,Option='{是,2},{否,3} ',BlackMask=0,ActType=0}",
  [167763] = "{id=167763,Text='你好呀，冒险者。你知道吗？整个米德加尔特大陆都在讨论——这棵一夜之间就出现的神秘大树！',Speaker=893244,}",
  [167764] = "{id=167764,Text='听说把自己的愿望写在小卡片上，然后挂上去就有可能实现哦~',Speaker=893244,}",
  [167765] = "{id=167765,Text='愿望越详细成功率越高！嘿嘿，你也来试试吧！',Speaker=893244,Option='好',Emoji=5,}",
  [167767] = "{id=167767,Text='你好，勇士！感谢你响应王国的号召！',Speaker=891647,}",
  [167768] = "{id=167768,Text='如今仍有许多危机威胁王国的安危，为了培养更多守护王国的勇士，国王陛下特别开放了[c][ffff00]普隆德拉新的城区[-][/c]作为公会战的争夺地。',Speaker=891647,}",
  [167769] = "{id=167769,Text='陛下号召冒险者们参与到公会领地的争夺，在战斗中提升自己的实力！',Speaker=891647,}",
  [167770] = "{id=167770,Text='勇士，准备好跟同伴们一同加入到新工会领地的争夺了吗？听说还有机会获得[c][ffff00]全新的神器[-][/c]哦！',Speaker=891647,Option='{准备好了！,0},{真棒！,0}',}",
  [167771] = "{id=167771,Text='很好，很有干劲！加油吧，冒险者！',Speaker=891647,Emoji=41,}",
  [167778] = "{id=167778,Text='米德加尔特王国为了庆祝这特殊的日子，给每一位冒险者都准备了一份特殊的礼物哦~',Speaker=4301,}",
  [167779] = "{id=167779,Text='希望你能喜欢这顶象征着热血的红色棒球帽，也希望你能在这里收获一段愉快的冒险旅途~',Speaker=4301,}",
  [167780] = "{id=167780,Text='最近接到一个新的采访任务《佣兵猫的日常》。不过我还要写稿子，而且很赶！冒险家，你能否帮我去拍几张照片呀？我不会亏待你的！[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=893253,Option='{好的,2},{我也很忙,3} ',BlackMask=0,ActType=0}",
  [167781] = "{id=167781,Text='太棒了！那就出发吧！',Speaker=893253,BlackMask=0,ActType=0}",
  [167782] = "{id=167782,Text='今天的佣兵猫站岗的地点已经提前被我打听到了！那么就马上出发吧！',Speaker=893253,Option='{好,2},{自己去,3}',BlackMask=0,ActType=0}",
  [167783] = "{id=167783,Text='喵喵喵~我们是佣兵喵预备军！我们在搜刮物资！冒险家~要不要被搜刮一下喵~~会给你奖励哦~',Speaker=893249,BlackMask=0,ActType=0}",
  [167789] = "{id=167789,Text='哎~~我儿子已经两年没有回家了，不知道去冒险中的他过得怎么样，什么时候回来看我呢。',Speaker=893251,BlackMask=0,ActType=0}",
  [167790] = "{id=167790,Text='呀！今天是属于我们儿童的节日！大家一定要快快乐乐的！！',Speaker=893252,BlackMask=0,ActType=0}",
  [167791] = "{id=167791,Text='最近南门被“小坏蛋”风精灵占领了，佣兵猫都在奋力抵抗。冒险可以协助我们吗？',Speaker=893250,}",
  [167793] = "{id=167793,Text='听说今天是端午节，我就来看看大家有没有吃粽子~ 没吃的！都给我吃起来~~',Speaker=893258,}",
  [167805] = "{id=167805,Text='你……你好，我……我叫艾……艾……莉莎',Speaker=893262,}",
  [167806] = "{id=167806,Text='（可恶，紧张到开始结巴了）',Speaker=893262,}",
  [167807] = "{id=167807,Text='唔，不要这么看着我，我是来宣传克魔岛的海洋剧场的！',Speaker=893262,}",
  [167808] = "{id=167808,Text='因为从来没有出过门，到了这里有点紧张……',Speaker=893262,}",
  [167809] = "{id=167809,Text='三！',Speaker=893262,}",
  [167810] = "{id=167810,Text='二！',Speaker=893262,}",
  [167811] = "{id=167811,Text='一！',Speaker=893262,}",
  [167812] = "{id=167812,Text='剪刀石头布——',Speaker=893262,Option='{石头,40},{剪刀,50},{布,60}',}",
  [167813] = "{id=167813,Text='唉~紧张的情绪并没有得到缓解呢~',Speaker=893262,}",
  [167814] = "{id=167814,Text='嘿嘿，我赢了！',Speaker=893262,}",
  [167815] = "{id=167815,Text='要不我们再来一次？',Speaker=893262,Option='{再来一次,103},{不玩啦,102}',}",
  [167816] = "{id=167816,Text='哦吼？不简单嘛！',Speaker=893262,}",
  [167817] = "{id=167817,Text='唔 … … 没有分出胜负呢。',Speaker=893262,}",
  [167818] = "{id=167818,Text='怎么样，要不要再来一次？',Speaker=893262,Option='{再试一次,104},{不玩啦,101}',}",
  [167819] = "{id=167819,Text='哟～你赢了呢！',Speaker=893262,}",
  [167820] = "{id=167820,Text='谢谢你缓解了我的情绪，这份礼物送给你~',Speaker=893262,}",
  [167821] = "{id=167821,Text='不管如此，还是谢谢你呢~',Speaker=893262,}",
  [167822] = "{id=167822,Text='那就让我们开始吧~',Speaker=893262,}",
  [167823] = "{id=167823,Text='就这么结束了吗 … …',Speaker=893262,}",
  [167824] = "{id=167824,Text='没错，我们再来，再来！',Speaker=893262,}",
  [167825] = "{id=167825,Text='[c][ffff00][PlayerName][-][/c]，你可以陪我玩个小游戏，帮我缓解一下紧张的情绪吗？',Speaker=893262,}",
  [167826] = "{id=167826,Text='如果你能缓解我的情绪，我可以给你一份克魔岛的礼物哦~',Speaker=893262,Option='{没问题,2},{还是算了,3}',}",
  [167828] = "{id=167828,Text='克魔岛上有个洞窟，里面藏着很多宝物呢~',Speaker=893262,}",
  [167829] = "{id=167829,Text='谢谢你帮助了我~也谢谢你能听我唠叨，这份礼物请你收下~',Speaker=893262,}",
  [167830] = "{id=167830,Text='你知道佳佳熊吗？她现在可是克魔岛的吉祥物~',Speaker=893262,}",
  [167831] = "{id=167831,Text='如果克魔岛能变得跟普隆德拉一样繁华就好了~',Speaker=893262,}",
  [167832] = "{id=167832,Text='克魔岛上没有冬天，一年四季大家都穿得很清凉呢~',Speaker=893262,}",
  [167833] = "{id=167833,Text='你知道我们岛上的第一美男吗？他可是岛上万千少女每晚的梦呢~！（害羞）',Speaker=893262,}",
  [167834] = "{id=167834,Text='如果你见到卡梅阿婆婆，一定要找她算一卦哦，她占卜可准了~',Speaker=893262,}",
  [167835] = "{id=167835,Text='虽然外面的世界很大，但是克魔岛才是我的家呀~',Speaker=893262,}",
  [167837] = "{id=167837,Text='有缘人，老太婆我帮你算算今天的运势如何？',Speaker=893263,}",
  [167838] = "{id=167838,Text='要想富，先占卜，记得每天都来找我算算~',Speaker=893263,}",
  [167840] = "{id=167840,Text='克魔岛没有普隆德拉那么繁华，很多孩子甚至都上不起学',Speaker=893264,}",
  [167841] = "{id=167841,Text='你是否愿意奉献一点爱心，给岛上的孩子们一些帮助呢？',Speaker=893264,}",
  [167842] = "{id=167842,Text='如果你愿意的话，记得每天都来这里找我，我代表孩子们，先谢谢你了~',Speaker=893264,}",
  [167859] = "{id=167859,Text='[c][ffff00][PlayerName][-][/c]，来参加「甜心糖果乐园」吧！限时开放的哦~',Speaker=893271,}",
  [167860] = "{id=167860,Text='如果你能成功通关，就能拿到稀有的「心形甜甜圈」，快来试试吧！',Speaker=893271,}",
  [167862] = "{id=167862,Text='[c][ffff00][PlayerName][-][/c]，请帮帮我！我只是想去普隆德拉出售鲜红玫瑰……没想到路过这里，就遇到了一大群流氓……',Speaker=893272,}",
  [167863] = "{id=167863,Text='他们不仅抢走了我的玫瑰，还把我困在这里！',Speaker=893272,Emoji=24,}",
  [167864] = "{id=167864,Text='拜托了！请收拾这些游手好闲的流氓吧！',Speaker=893272,Option='交给我吧！',}",
  [167866] = "{id=167866,Text='[c][ffff00][PlayerName][-][/c]，又见面啦！因为迷路，我错过很多事情……',Speaker=893273,Emoji=15,}",
  [167867] = "{id=167867,Text='不过没关系，我准备一些小礼物！能跟你交换一些其他东西嘛？就当做这些旅途的特别纪念吧！',Speaker=893273,}",
  [167869] = "{id=167869,Text='[c][ffff00][PlayerName][-][/c]，虽然我也不知道你喜欢什么，但还是为你也准备了一份特别的礼物！',Speaker=893269,Option='谢谢！',Emoji=17,}",
  [167870] = "{id=167870,Text='如果你也有心上人的话，不妨今天也准备一份礼物送给TA吧~',Speaker=893269,}",
  [167871] = "{id=167871,Text='送什么好呢……我想要送一份礼物给我的心上人——艾德妮。',Speaker=893269,}",
  [167872] = "{id=167872,Text='可我却怎么想都想不到送什么好……能拜托你帮我去打听一下，好吗？\n[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=893269,Option='{没问题,2},{还是算了,3} ',}",
  [167873] = "{id=167873,Text='让我想想先问谁呢……如果直接问艾德妮的话，就没有惊喜了……不行不行……',Speaker=893269,Option='诺恩·薇？',}",
  [167874] = "{id=167874,Text='你说的是那只悬浮在空中的猫猫吗？嗯，虽然不知道它是什么来头，不过看起来能预知未来的样子。或许值得一试。',Speaker=893269,Emoji=11,}",
  [167875] = "{id=167875,Text='那拜托你了，冒险者~我如果到处去打听的话，一定会被艾德妮发现的！',Speaker=893269,Option='交给我吧！',}",
  [167876] = "{id=167876,Text='又见面了，冒险者。真是稀客，这次前来又为何事呢？',Speaker=807603,Option='说明来意',}",
  [167877] = "{id=167877,Text='呵呵……占卜揭示未来，可命运的轨迹需要自己抉择。',Speaker=807603,}",
  [167878] = "{id=167878,Text='（诺恩·薇凝视着前方，不再发言）\n（看来这种事情寄希望于占卜不太可行……）',}",
  [167879] = "{id=167879,Text='没办法，只能明天去找巴德尔，再商量吧……',}",
  [167880] = "{id=167880,Text='[c][ffff00][PlayerName][-][/c]，怎么这么快就回来了……有头绪了吗？',Speaker=893269,Option='摇摇头',Emoji=7,}",
  [167881] = "{id=167881,Text='果然行不通吗……唉，看来这种事还得找朋友问问才好。',Speaker=893269,Option='呃，那找谁呢？',}",
  [167882] = "{id=167882,Text='造型师西莉雅吧！前段时间，艾德妮才去她那儿换了发型，想必她俩之前会有不少共同话题呢！',Speaker=893269,Option='好主意',}",
  [167883] = "{id=167883,Text='嘿呀！冒险者，今天想换个发型换个心情吗？找我就对了！',Speaker=1017,Option='说明来意',}",
  [167884] = "{id=167884,Text='唔…这种事找我也行！我可是“给人送礼物”跟“给人换发型”一样擅长！',Speaker=1017,}",
  [167885] = "{id=167885,Text='艾德妮吗……那我的建议就是——1份「暖心料理」',Speaker=1017,Option='啊？',}",
  [167886] = "{id=167886,Text='相信我！我的眼光是不会错的……应该。好啦，别多想了~',Speaker=1017,Emoji=20,}",
  [167887] = "{id=167887,Text='因为艾德妮老是说食用一口暖心料理，就能感到全身暖意！没错！这一定是最佳选项！',Speaker=1017,Emoji=5,}",
  [167888] = "{id=167888,Text='这样真的没问题吗……说实话，他俩之间的事情只有他们本人最清楚了。只有明天再问问巴德尔了……',}",
  [167889] = "{id=167889,Text='你从西莉雅那拿到礼物了吗？',Speaker=893269,Option='拿到了',}",
  [167890] = "{id=167890,Text='暖心料理？什么，是西莉雅告诉你的吗？这种随处可买的料理，真的能作为礼物吗？（叹气）',Speaker=893269,}",
  [167891] = "{id=167891,Text='但我们要是再三追问，也不太好……可为什么会是暖心料理呢……',Speaker=893269,Option='特别的意义？',Emoji=19,}",
  [167892] = "{id=167892,Text='让我想想…暖心料理…料理……第一次……嗯？我知道了！',Speaker=893269,}",
  [167893] = "{id=167893,Text='我，第一次给艾德妮做的就是“暖心料理”——虽然看起来像一锅乱炖，但味道还是很接近了！',Speaker=893269,}",
  [167894] = "{id=167894,Text='只是没想到，答案原来这么简单！不过，这次我还是打算自己做一份“暖心料理”！',Speaker=893269,}",
  [167895] = "{id=167895,Text='对了，这些给你，谢谢你帮忙。希望这件特殊的礼物，她会喜欢。',Speaker=893269,Option='一定的！',}",
  [167897] = "{id=167897,Text='你好，请问你是？',Speaker=893270,Option='巴德尔的朋友',}",
  [167898] = "{id=167898,Text='（说起来，从来没有见过艾德妮，她……到底喜不喜欢那份简单的礼物呢）',}",
  [167899] = "{id=167899,Text='（巴德尔也是，一直没有带我见过女主角，今天正好了解一下好了）',}",
  [167900] = "{id=167900,Text='[c][ffff00][PlayerName][-][/c]，嘿呀！你总算来了，我还想着……还要过几天去找你呢！',Speaker=893270,}",
  [167901] = "{id=167901,Text='感谢你这段时间对巴德尔的照顾。他呀，虽然做事很笨拙又不会说话，还总是犹犹豫豫的，但再小的事情也会很上心！总之是一个很靠谱的人呐……',Speaker=893270,}",
  [167902] = "{id=167902,Text='（看来，艾德妮对他很认可呢！我的担心是多余的……\n等等，难道她已经知道发生了什么事情了嘛）',}",
  [167903] = "{id=167903,Text='虽然我不知道你们这几天在干什么……但他每天似乎都过得很开心，有时候还在偷着笑呢。',Speaker=893270,Emoji=17,}",
  [167904] = "{id=167904,Text='对了，听说过几天，他要给我准备一个惊喜！太期待了！',Speaker=893270,Option='真棒',}",
  [167905] = "{id=167905,Text='（真好啊，我也想有人送我礼物……）',}",
  [167906] = "{id=167906,Text='[c][ffff00][PlayerName][-][/c]，告诉你一个好消息和一个坏消息！',Speaker=893269,Option='好消息？',}",
  [167907] = "{id=167907,Text='嘿嘿，虽然只是一份简单的料理，但是却得艾德妮赞扬！没想到这个简单的小礼物，竟然获得了全胜！',Speaker=893269,Option='坏消息？',}",
  [167908] = "{id=167908,Text='坏消息是为了做出一份完美的“暖心料理”，我大概尝试了999次，所以接下来很长一段时间，我都要消化这部分暖心料理了……',Speaker=893269,Emoji=15,}",
  [167909] = "{id=167909,Text='太感谢你为了这件小事情跑前跑后的，我也准备了礼物送给你！快收下吧！~',Speaker=893269,Option='谢谢',}",
  [167910] = "{id=167910,Text='嘿嘿，送礼这种事情，看来还是要有心意才行！如果你也有特别在意的人，不要顾虑太多，用心去做就好啦！',Speaker=893269,Emoji=20,}",
  [167911] = "{id=167911,Text='[c][ffff00][PlayerName][-][/c]，你来啦~虽然有点难以启齿，但我还是想告诉你一个秘密……',Speaker=893270,Option='什么？',}",
  [167912] = "{id=167912,Text='不得不说巴德尔的厨艺还是没有长进！“暖心料理”还是一言难尽……',Speaker=893270,}",
  [167913] = "{id=167913,Text='我喜欢的可是料理商人出售的「暖心料理」哎！但为了不伤他的心，我又再次“夸奖”了他……',Speaker=893270,Option='啊？',Emoji=15,}",
  [167914] = "{id=167914,Text='唉，不会……不会下次还收到这个礼物吧……',Speaker=893270,Option='说实话吧',}",
  [167915] = "{id=167915,Text='真的要这样吗？……但是不这样做的话，这件事还会再次发生的吧……果然，第一次就应该坦诚地告诉他了……',Speaker=893270,Option='应该相信他',}",
  [167916] = "{id=167916,Text='对呀！他应该能面对这个小小的建议！好，我找个合适的时机就把真相告诉他吧！谢谢你的建议！',Speaker=893270,}",
  [167917] = "{id=167917,Text='不用安慰我！我知道你要说什么！',Speaker=893269,Option='生气了？',}",
  [167918] = "{id=167918,Text='这点小事才不会击垮我！可我的“暖心料理”真的不及格嘛……你要不要试试？',Speaker=893269,Option='啊？不了不了',Emoji=24,}",
  [167919] = "{id=167919,Text='我知道了……以后我就去买999份料理商人的「暖心料理」，1份送给艾德妮，998份用来做研究……但我还缺一大笔Zeny……',Speaker=893269,Emoji=24,}",
  [167920] = "{id=167920,Text='多亏了艾德妮告诉我真相，我才能努力成为料理之神！',Speaker=893269,}",
  [167921] = "{id=167921,Text='（看来……真相反倒激励了他的斗志）',}",
  [167922] = "{id=167922,Text='哼哼，我要努力去变强了！这份谢礼就送给你吧~',Speaker=893269,}",
  [167923] = "{id=167923,Text='你能感受到吗？空气中散发着香甜的气息……',Speaker=893274,}",
  [167924] = "{id=167924,Text='少男少女们期盼着的蓝色爱神日就要来临了！[c][ffff00]【此任务每个账号仅限一个角色完成，请谨慎接取】[-][/c]',Speaker=893274,Option='{默默离开,2},{很感兴趣,3}',}",
  [167925] = "{id=167925,Text='在我爱神菲尔的努力下，米德加尔特的少男少女们都学会了如何去追求自己的心上人。',Speaker=893274,}",
  [167926] = "{id=167926,Text='本来想在这个日子里！我本想带着毁卡到处游玩一下……',Speaker=893274,}",
  [167927] = "{id=167927,Text='但是，这个世界上，一些无知少男的内心纠结让我放心不下。[c][ffff00][PlayerName][-][/c]，身经百战的你！去帮助他们吧！',Speaker=893274,Option='{你是玩忽职守！,0},{没有身经百战！,0}',}",
  [167928] = "{id=167928,Text='这都不重要，我听得见，第一个少男，他现在在克魔岛。他那颗悸动的心，焦灼着……',Speaker=893274,}",
  [167929] = "{id=167929,Text='！！！！',Speaker=893276,Option='尼希尔',}",
  [167930] = "{id=167930,Text='[c][ffff00][PlayerName][-][/c]，你怎么会突然出现在这里。',Speaker=893276,Option='{你需要帮助,0},{你很焦虑,0}',}",
  [167931] = "{id=167931,Text='才……才没有！你不要胡说。',Speaker=893276,Option='{恋爱问题？,0},{小白鸽？,0}',}",
  [167932] = "{id=167932,Text='……你从那家伙那学来的洞察力能不能别用在我身上……',Speaker=893276,Option='展开说说！',}",
  [167933] = "{id=167933,Text='……好吧。小白鸽她约我今天出来透透气……',Speaker=893276,}",
  [167934] = "{id=167934,Text='第一次……咳咳……约会，还是在这种日子……有点不适应。',Speaker=893276,}",
  [167935] = "{id=167935,Text='尼希尔～！久等啦！呀～[c][ffff00][PlayerName][-][/c]也在这里呀！嗯～那就一起走吧！',Speaker=893275,Option='{嗯？？？,0},{还有我？,0}',}",
  [167936] = "{id=167936,Text='嗯？你有别的事情吗？我听说克魔岛这新开了一个水族馆～建在海底！可有趣了！哥哥帮我抢到了几张门票～',Speaker=893275,Option='{这不好吧……,0},{挺想去的,0}',}",
  [167937] = "{id=167937,Text='嗯嗯～这么巧碰到了，就一起去吧！尼希尔也不会介意的！我们这就出发！',Speaker=893275,}",
  [167938] = "{id=167938,Text='……',Speaker=893276,}",
  [167939] = "{id=167939,Text='想在蓝色爱神日与心爱的人一起在深海与鱼儿们近距离接触吗？那就出示你的门票吧！',Speaker=893277,Option='出示门票',}",
  [167940] = "{id=167940,Text='三位客人……嗯？三位？……额……三位客人请进！',Speaker=893277,}",
  [167958] = "{id=167958,Text='喵喵喵喵！！！快让开～～～我停不下来了！',Speaker=893278,}",
  [167959] = "{id=167959,Text='喵～安全着陆！',Speaker=893278,Option='{好久不见！,4},{少男……？,3}',}",
  [167960] = "{id=167960,Text='[c][ffff00][PlayerName][-][/c]，好久不见喵！竟然能在这里碰见你！',Speaker=893278,}",
  [167961] = "{id=167961,Text='喵喵喵！我可是少猫！少猫懂吗？',Speaker=893278,}",
  [167962] = "{id=167962,Text='虽然成功抵达了！但是……接下去呢……',Speaker=893278,Option='{恋爱烦恼？,0},{我可以帮你,0}',}",
  [167963] = "{id=167963,Text='喵喵喵……我听说！这两天是普隆德拉的蓝色爱神日！在这一天，是要回送爱神日的礼物的！',Speaker=893278,}",
  [167964] = "{id=167964,Text='爱神日的那一天……嘉丽可是送了我一个烤焦的蘑菇香草甜甜圈！虽然……她可能并不知道那天是爱神日！但是我还是要回礼给她！',Speaker=893278,}",
  [167965] = "{id=167965,Text='呜呜呜……可是送什么给她好呢……人类和猫猫的东西她已经见多了……',Speaker=893278,Option='{精灵的东西,7},{海拉的东西,6}',}",
  [167966] = "{id=167966,Text='喵！死亡女神海拉！不行的喵！那么可怕的女人的东西怎么可以送给嘉丽！',Speaker=893278,}",
  [167967] = "{id=167967,Text='喵！这个主意不错喵！',Speaker=893278,}",
  [167968] = "{id=167968,Text='精灵的东西嘉丽一定没见过喵！可是……可是……怎么才能去精灵的国度呀！',Speaker=893278,Option='交给我吧！',}",
  [167969] = "{id=167969,Text='喵！你真好！我们赶紧出发喵！',Speaker=893278,}",
  [167970] = "{id=167970,Text='喵呜！这里就是精灵的国度吗？这里也太漂亮了！喵！要不是嘉丽没法离开，我都想带她来这里了喵！',Speaker=893278,}",
  [167971] = "{id=167971,Text='喵喵喵！我听杰克船长说过！是精灵们培育出了整个世界的第一朵花花！然后花粉随着风散落到了世界各处！',Speaker=893278,}",
  [167972] = "{id=167972,Text='这里的花花都是没见过的品种！嘉丽一定也没见过！我要收集这里的花花给嘉丽做个花环喵！',Speaker=893278,}",
  [167973] = "{id=167973,Text='喵呜！真是太好了！这下嘉丽总该愿意和我多说几句话了吧～',Speaker=893278,}",
  [167974] = "{id=167974,Text='艾比沉浸在对未来的幻想中自顾自的离开了……',Option='{重色轻友！,0},{舔猫一只！,0}',}",
  [167975] = "{id=167975,Text='天黑了。',}",
  [167976] = "{id=167976,Text='哎……塔米……',Speaker=893279,Option='呦！无知的少男！',}",
  [167977] = "{id=167977,Text='无知少男？冒险者，你在叫我吗？',Speaker=893279,Option='说出你的苦恼',}",
  [167978] = "{id=167978,Text='哎……你是知道的，我和塔米是无法相见的。',Speaker=893279,}",
  [167979] = "{id=167979,Text='她只能在白天出现，我只能在夜晚出现。',Speaker=893279,}",
  [167980] = "{id=167980,Text='只是，今年的蓝色爱神日……我又不能陪在塔米的身边……',Speaker=893279,Option='{塔米不会介意,4},{送东西给她,3}',}",
  [167981] = "{id=167981,Text='哎，我知道她不会介意，可我总是很自责。',Speaker=893279,}",
  [167982] = "{id=167982,Text='这倒是个好主意。',Speaker=893279,}",
  [167983] = "{id=167983,Text='如果能送点什么给塔米，代替我陪着她也是好的。',Speaker=893279,}",
  [167984] = "{id=167984,Text='可是送什么东西好呢？',Speaker=893279,Option='我帮你问问',}",
  [167985] = "{id=167985,Text='那就拜托你了，冒险者。',Speaker=893279,}",
  [167986] = "{id=167986,Text='天亮了。',}",
  [167987] = "{id=167987,Text='冒险者，你找我有什么事吗？',Speaker=893280,Option='你喜欢什么',}",
  [167988] = "{id=167988,Text='嗯？怎么突然问这个？我呀……最喜欢月亮了……因为月亮出现了，安布罗斯就会出现。',Speaker=893280,}",
  [167989] = "{id=167989,Text='可惜……我见不到月亮……也见不到他……',Speaker=893280,}",
  [167990] = "{id=167990,Text='天黑了。',}",
  [167991] = "{id=167991,Text='冒险者，结果怎么样？',Speaker=893279,Option='告知情况',}",
  [167992] = "{id=167992,Text='或许，她最想要的我给不了吧……',Speaker=893279,Option='{未必,0},{我有办法,0}',}",
  [167993] = "{id=167993,Text='你不用安慰我，从我们下决定的守护普隆德拉那一刻起，我们就准备好面对这些了。',Speaker=893279,Option='{月之湖上,0},{有月之石,0}',}",
  [167994] = "{id=167994,Text='啊！没错！月之石吸收着月亮的光辉！塔米一定会喜欢的！冒险者，拜托你了，找到月之石，帮我交给塔米。',Speaker=893279,}",
  [167995] = "{id=167995,Text='希望月之石能代替我，守护在塔米的身边。',Speaker=893279,Option='出发！',}",
  [167996] = "{id=167996,Text='将月之石带回去吧。',}",
  [167997] = "{id=167997,Text='天已经亮了。',}",
  [167998] = "{id=167998,Text='冒险者，又见面了。',Speaker=893280,Option='安布罗斯的礼物',}",
  [167999] = "{id=167999,Text='这，这是月亮的气息……是安布罗斯的气息……',Speaker=893280,}",
  [168000] = "{id=168000,Text='就仿佛，他在我的身边……',Speaker=893280,}",
  [168001] = "{id=168001,Text='塔米将月之石贴在自己的脸颊边，露出了幸福的微笑。',}",
  [168022] = "{id=168022,Text='听说冒险者学徒已经在各地已经有了不小的成就，冒险家们，能否为我拍下他们的照片作为他们资质申请的参考呢？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=893295,Option='{好的,2},{我也很忙,3} ',BlackMask=0,ActType=0}",
  [168023] = "{id=168023,Text='太棒了！那就出发吧！',Speaker=893295,BlackMask=0,ActType=0}",
  [168024] = "{id=168024,Text='今天的冒险者学徒探索的地点已经提前被我打听到了！那么就马上出发吧！',Speaker=893295,Option='{好,2},{自己去,3}',BlackMask=0,ActType=0}",
  [168031] = "{id=168031,Text='祝今天的所有教师！教师节快乐呀！',Speaker=893298,BlackMask=0,ActType=0}",
  [168032] = "{id=168032,Text='团圆时节，记得吃月饼哦~',Speaker=893297,BlackMask=0,ActType=0}",
  [168033] = "{id=168033,Text='最近[c][ffff00]波利岛[-][/c]出现了很多火精灵，对新生有危险，冒险家能否帮忙打败他们呢？',Speaker=893299,}",
  [168035] = "{id=168035,Text='冒险者，每天都来捐一捐物资吧！会有丰厚奖励哦！',Speaker=893296,}",
  [168044] = "{id=168044,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为【星帝】了吗？',Speaker=893315,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [168045] = "{id=168045,Text='转职过程中遇到困难了吗？看来你需要继续修炼。',Speaker=893315,}",
  [168046] = "{id=168046,Text='继续加油，成功就在眼前啦！',Speaker=893315,}",
  [168047] = "{id=168047,Text='[c][ffff00][PlayerName][-][/c]，你已经成功转职成为【天启】了吗？',Speaker=893315,Option='{是的，成功转职了,1},{继续努力,2}',}",
  [168048] = "{id=168048,Text='哇，你成功了！现在是出色的【星帝】了。',Speaker=893315,}",
  [168049] = "{id=168049,Text='但是还不能松懈，你可以更上一层楼！',Speaker=893315,}",
  [168050] = "{id=168050,Text='这是我小小的心意，请你收下。',Speaker=893315,}",
  [168052] = "{id=168052,Text='你已经成为了这么强大的【天启】了！',Speaker=893315,}",
  [168053] = "{id=168053,Text='成长的如此快，你一定经历了不少难忘的冒险吧！',Speaker=893315,}",
  [168054] = "{id=168054,Text='不要忘了继续修炼自己，成为更强大的冒险家！',Speaker=893315,}",
  [168055] = "{id=168055,Text='这是我的心意，请你收下。',Speaker=893315,}",
  [168056] = "{id=168056,Text='嗯？你闻到浓郁的咖啡味了吗？',Speaker=821194,Option='说明来意',}",
  [168057] = "{id=168057,Text='噢？想让我帮忙出售「奥西里斯」的料理吗……',Speaker=821194,}",
  [168058] = "{id=168058,Text='如果想要在黄金美食节上，获得更多的人认可，这的确是一个不错的办法！',Speaker=821194,}",
  [168059] = "{id=168059,Text='不过，我有一个特别的请求……',Speaker=821194,Emoji=5,}",
  [168060] = "{id=168060,Text='[c][ffff00][PlayerName][-][/c]，来一局“石头剪刀布”对决吧！',Speaker=821194,}",
  [168061] = "{id=168061,Text='如果你能赢得对决，我就帮你宣传，同时还能赢得额外的奖励！',Speaker=821194,Option='{没问题,2},{还是算了,3}',}",
  [168062] = "{id=168062,Text='三！',Speaker=821194,}",
  [168063] = "{id=168063,Text='二！',Speaker=821194,}",
  [168064] = "{id=168064,Text='一！',Speaker=821194,}",
  [168065] = "{id=168065,Text='剪刀石头布——',Speaker=821194,Option='{石头,40},{剪刀,50},{布,60}',}",
  [168066] = "{id=168066,Text='唉~不争取一下吗？',Speaker=821194,}",
  [168067] = "{id=168067,Text='嘿嘿，我赢了！',Speaker=821194,}",
  [168068] = "{id=168068,Text='要不我们再来一次？',Speaker=821194,Option='{再来一次,103},{不玩啦,102}',}",
  [168069] = "{id=168069,Text='哦吼？不简单嘛！',Speaker=821194,}",
  [168070] = "{id=168070,Text='唔 … … 没有分出胜负呢。',Speaker=821194,}",
  [168071] = "{id=168071,Text='怎么样，要不要再来一次？',Speaker=821194,Option='{再试一次,104},{不玩啦,101}',}",
  [168072] = "{id=168072,Text='哟～你赢了呢！',Speaker=821194,}",
  [168073] = "{id=168073,Text='哇哦！不错嘛！你赢得了我的认可！',Speaker=821194,}",
  [168074] = "{id=168074,Text='不管如此，还是谢谢你呢~',Speaker=821194,}",
  [168075] = "{id=168075,Text='那就让我们开始吧~',Speaker=821194,}",
  [168076] = "{id=168076,Text='就这么结束了吗 … …',Speaker=821194,}",
  [168077] = "{id=168077,Text='没错，我们再来，再来！',Speaker=821194,}",
  [168079] = "{id=168079,Text='料理协会中，每位大师的认可都至关重要！请让他们也感受我这充满创意的料理吧！',Speaker=893320,}",
  [168080] = "{id=168080,Text='那就拜托你了，冒险者。从明天开始，拜访料理协会的大师们！',Speaker=893320,Option='交给我吧！',}",
  [168081] = "{id=168081,Text='噢？一份特别的料理？单单从外形来看，真是令人垂涎欲滴！无论是颜色的搭配还是整体的摆盘，都算得上一流！',Speaker=6446,}",
  [168082] = "{id=168082,Text='嘿，你已经得到了我的认可！',Speaker=6446,}",
  [168083] = "{id=168083,Text='不过，这可仅仅不够，一份好的料理需要从各个维度去评价！去寻求更多大师的认可吧~',Speaker=6446,Option='我知道啦！',}",
  [168084] = "{id=168084,Text='出自「奥西里斯」的料理？天哪，这家伙的刀功已经如此精湛了吗！这一定是多年勤学苦练的结果！',Speaker=6444,}",
  [168085] = "{id=168085,Text='嘿，你已经得到了我的认可！',Speaker=6444,}",
  [168086] = "{id=168086,Text='不过，这可仅仅不够，一份好的料理需要从各个维度去评价！去寻求更多大师的认可吧~',Speaker=6444,Option='我知道啦！',}",
  [168087] = "{id=168087,Text='虽然我很苛刻没错啦！但是不得不承认，光是闻到这个味道，我就觉得它一定很美味！',Speaker=6445,}",
  [168088] = "{id=168088,Text='嘿，你已经得到了我的认可！',Speaker=6445,}",
  [168089] = "{id=168089,Text='不过，这可仅仅不够，一份好的料理需要从各个维度去评价！去寻求更多大师的认可吧~',Speaker=6445,Option='我知道啦！',}",
  [168090] = "{id=168090,Text='味蕾间的每一次绽放都像是在为这份料理欢呼！天哪，我又要有强劲的对手了吗……',Speaker=6442,}",
  [168091] = "{id=168091,Text='嘿，你已经得到了我的认可！',Speaker=6442,}",
  [168092] = "{id=168092,Text='不过，这可仅仅不够，一份好的料理需要从各个维度去评价！去寻求更多大师的认可吧~',Speaker=6442,Option='我知道啦！',}",
  [168093] = "{id=168093,Text='许久未见到如此优秀的料理作品了。无论是食材的选取，还是味道的搭配上，都是令人意想不到的组合。太难得了，大胆的尝试，完美的呈现！',Speaker=6448,}",
  [168094] = "{id=168094,Text='嘿，你已经得到了我的认可！',Speaker=6448,}",
  [168095] = "{id=168095,Text='不过，这可仅仅不够，一份好的料理需要从各个维度去评价！去寻求更多大师的认可吧~',Speaker=6448,Option='我知道啦！',}",
  [168096] = "{id=168096,Text='噢？一名新人的料理……这是菜谱上没有的配方。但高超的技艺将食材烹饪得恰到好处，看得出厨师对美食有着自己的想法……',Speaker=6439,}",
  [168097] = "{id=168097,Text='果然，大家对她好评如潮……如果可以，请将这份美味分享给更多的人们吧~',Speaker=6439,}",
  [168098] = "{id=168098,Text='那么，恭喜你，「奥西里斯」的料理已经通过了料理协会的认可！也谢谢你能为我们带来如此美妙的料理！',Speaker=6439,Option='小事一桩！',}",
  [168100] = "{id=168100,Text='不知道选什么喵？来看看我的推荐喵~一定不会错的喵~',Speaker=893321,}",
  [168101] = "{id=168101,Text='记得每天都要来哦~美味等着你喵~',Speaker=893321,}",
  [168103] = "{id=168103,Text='[c][ffff00][PlayerName][-][/c]，太感谢你了，我的料理现在大受欢迎！',Speaker=893322,}",
  [168104] = "{id=168104,Text='嘿嘿，你是否有多余的材料跟我交换呢？\n我可是有特别的技艺能处理各种奇奇怪怪的材料！当然，奖励也不会少了你的！',Speaker=893322,}",
  [168117] = "{id=168117,Text='[c][ffff00][PlayerName][-][/c]，无论你在哪里在做什么，都希望你今天有美食和快乐相伴！',Speaker=893320,Option='谢谢！',Emoji=17,}",
  [168118] = "{id=168118,Text='是要出去玩了吗？好耶，约上三五好友，一起去品尝美食吧！',Speaker=893320,}",
  [168120] = "{id=168120,Text='喂，这位冒险者，你带糖果了吗？',Speaker=893347,Option='没有',}",
  [168121] = "{id=168121,Text='哼~出门在外，竟然不带糖果~',Speaker=893347,}",
  [168123] = "{id=168123,Text='除非……你来陪我玩耍吧~！',Speaker=893347,Emoji=5,}",
  [168124] = "{id=168124,Text='[c][ffff00][PlayerName][-][/c]，来一局“石头剪刀布”对决吧！',Speaker=893347,}",
  [168125] = "{id=168125,Text='如果你能赢得对决，我还能送你一份大礼哦~',Speaker=893347,Option='{没问题,2},{还是算了,3}',}",
  [168126] = "{id=168126,Text='三！',Speaker=893347,}",
  [168127] = "{id=168127,Text='二！',Speaker=893347,}",
  [168128] = "{id=168128,Text='一！',Speaker=893347,}",
  [168129] = "{id=168129,Text='剪刀石头布——',Speaker=893347,Option='{石头,40},{剪刀,50},{布,60}',}",
  [168130] = "{id=168130,Text='那么快就要放弃了吗？',Speaker=893347,}",
  [168131] = "{id=168131,Text='唉~无敌真是寂寞……',Speaker=893347,}",
  [168132] = "{id=168132,Text='要不我们再来一次？',Speaker=893347,Option='{再来一次,103},{不玩啦,102}',}",
  [168133] = "{id=168133,Text='哦吼？不简单嘛！',Speaker=893347,}",
  [168134] = "{id=168134,Text='唔 … … 没有分出胜负呢。',Speaker=893347,}",
  [168135] = "{id=168135,Text='怎么样，要不要再来一次？',Speaker=893347,Option='{再试一次,104},{不玩啦,101}',}",
  [168136] = "{id=168136,Text='哟～你赢了呢！',Speaker=893347,}",
  [168137] = "{id=168137,Text='哇哦！不错嘛！那就祝你万圣节快乐吧~！',Speaker=893347,}",
  [168138] = "{id=168138,Text='下次记得带糖果再出门哦~',Speaker=893347,}",
  [168139] = "{id=168139,Text='来吧~！',Speaker=893347,}",
  [168140] = "{id=168140,Text='就这么结束了吗 … …',Speaker=893347,}",
  [168141] = "{id=168141,Text='再来，再来！',Speaker=893347,}",
  [168142] = "{id=168142,Text='[c][ffff00][PlayerName][-][/c]，你终于来啦~',Speaker=893349,}",
  [168144] = "{id=168144,Text='如果你能帮我分担一些的话，我可以偷偷给你一些特殊的购物积分哦~',Speaker=893349,Option='我要怎么做？',}",
  [168145] = "{id=168145,Text='很简单，你只需要把[c][ffff00]「双十一特惠信息」[-][/c]传递出去，让更多冒险者知道，就可以啦~',Speaker=893349,}",
  [168148] = "{id=168148,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「普隆德拉购物积分」x2）[-][/c]\n那么，今天需要分享出去的[c][ffff00]「双十一特惠信息」[-][/c]是：',Speaker=893349,}",
  [168156] = "{id=168156,Text='盛大的庆典总是会吸引一些小坏蛋来捣乱！',Speaker=893351,Option='怎么了？',}",
  [168157] = "{id=168157,Text='我们给冒险者准备的[c][ffff00]礼物[-][/c]被[c][ffff00]捣乱的小坏蛋[-][/c]抢走了，他们现在应该已经到了[c][ffff00]波利岛[-][/c]上',Speaker=893351,}",
  [168158] = "{id=168158,Text='如果冒险者能给我带回来1个[c][ffff00]「被抢走的礼物」[-][/c]，我一定会给你珍藏已久的礼物~',Speaker=893351,}",
  [168159] = "{id=168159,Text='确定要提交[c][ffff00]「被抢走的礼物」[-][/c]x1吗？',Speaker=893351,Option='{确定,4},{再想想,3}',}",
  [168160] = "{id=168160,Text='冒险者，你身上暂时没有足够的材料哦！请先去获取后再来捐献吧！前往[c][ffff00]波利岛[-][/c]看看吧~',Speaker=893351,}",
  [168161] = "{id=168161,Text='感谢你~祝你冒险快乐~！',Speaker=893351,}",
  [168162] = "{id=168162,Text='有人牵挂的漂泊不叫流浪；有人陪伴的哭泣不叫悲伤；',Speaker=893348,}",
  [168163] = "{id=168163,Text='有人分担的忧愁不叫痛苦；有人分享的快乐叫做幸福！',Speaker=893348,}",
  [168165] = "{id=168165,Text='购物节就要接近尾声了，今年的购物节可以说是圆满落幕！',Speaker=893351,}",
  [168166] = "{id=168166,Text='为了表彰你在购物节中的英勇行为，我这里还有一些库存，可以低价给你哦~',Speaker=893351,}",
  [168167] = "{id=168167,Text='你甚至可以拿一些材料来跟我交换~',Speaker=893351,}",
  [168172] = "{id=168172,Text='从出生在普隆德拉到现在，不知不觉已经过去了20年呢~',Speaker=893361,}",
  [168173] = "{id=168173,Text='感谢冒险者你还爱着波利，爱着RO',Speaker=893361,}",
  [168174] = "{id=168174,Text='这份礼物，请你收好哦~',Speaker=893361,}",
  [168175] = "{id=168175,Text='冒险者，听说过[c][ffff00]机动能量[-][/c]吗？',Speaker=892430,Option='那是什么？',}",
  [168176] = "{id=168176,Text='那是来自异世界的能量，相传可以提升占星的能力！',Speaker=892430,}",
  [168177] = "{id=168177,Text='如果冒险者能给我带回来5个[c][ffff00]「机动能量」[-][/c]，我一定会给你珍藏已久的礼物~',Speaker=892430,}",
  [168178] = "{id=168178,Text='确定要提交[c][ffff00]「机动能量」[-][/c]x5吗？',Speaker=892430,Option='{确定,4},{再想想,3}',}",
  [168179] = "{id=168179,Text='冒险者，你身上暂时没有足够的材料哦！请先去获取后再来捐献吧！',Speaker=892430,}",
  [168180] = "{id=168180,Text='感谢你~祝你冒险快乐~！',Speaker=892430,}",
  [168186] = "{id=168186,Text='只要冒险者能通关「甜心糖果乐园」，就可以获得丰厚的奖励哦~',Speaker=893350,}",
  [168187] = "{id=168187,Text='活动期间，每天都可以挑战，各位冒险者不要错过哦~',Speaker=893350,}",
  [168191] = "{id=168191,Text='啊！了不起的冒险者——[c][ffff00][PlayerName][-][/c]！',Speaker=893373,}",
  [168192] = "{id=168192,Text='我……我有个请求想要拜托您！拜托了！',Speaker=893373,}",
  [168193] = "{id=168193,Text='[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Option='{答应驯鹿,2},{假装没听见,3}',}",
  [168194] = "{id=168194,Text='大事不妙！圣诞老人旷工了……不过别慌，姜饼城扛把子总有好办法！这也是我不远万里来到普隆德拉的原因……',Speaker=893373,}",
  [168195] = "{id=168195,Text='我的请求就是找到一个代班圣诞老人——\nTa既要和蔼可亲，还要耐心负责，更要幽默欢乐！',Speaker=893373,}",
  [168196] = "{id=168196,Text='想必四处游历的冒险者有不少合适人选！请问，帮忙拍一些和[c][ffff00]候选人有关的照片[-][/c]好吗？',Speaker=893373,Option='好！',}",
  [168211] = "{id=168211,Text='我的建议？去找一些城里知名人士——比如手滑的忽克连？',Speaker=893373,}",
  [168212] = "{id=168212,Text='嗯……没有什么特别的理由。大概就是他的印象太深入人心了！',Speaker=893373,}",
  [168213] = "{id=168213,Text='（这张照片还不错。希望能派上用场！）',}",
  [168214] = "{id=168214,Text='（不过，比起做圣诞老人，看来忽克连更适合敲装备呐！）',}",
  [168215] = "{id=168215,Text='今天还是在姜饼城寻找一下合适的人选吧！但愿有收获……',}",
  [168216] = "{id=168216,Text='小朋友你好啊，你知道附近谁最和蔼可亲又风趣幽默吗？',}",
  [168217] = "{id=168217,Text='咦！你说得是不是圣诞老人！其实，对面的毛茸茸老爷爷也很好噢！每次路过都会给我们糖果！',Speaker=6965,}",
  [168218] = "{id=168218,Text='悄悄告诉你，我们都猜测他是圣诞老人！毕竟白花花的胡子、眉毛、头发，都是圣诞老人的特征！',Speaker=6965,}",
  [168219] = "{id=168219,Text='谢谢你，可爱的孩子！',}",
  [168220] = "{id=168220,Text='（姜饼城的老爷爷果然都很像圣诞老人）',}",
  [168221] = "{id=168221,Text='（或许热情好客的克魔岛居民也能带来一个别样的圣诞节？）',}",
  [168222] = "{id=168222,Text='吼吼吼，圣诞节？听起来不错！可是，我也一把年纪了，不方便到处折腾……',Speaker=821517,}",
  [168223] = "{id=168223,Text='不过拍照什么的，热情好客的克魔岛人总是来着不拒……',Speaker=821517,}",
  [168224] = "{id=168224,Text='来都来了，那就留个影做纪念吧！快来！',Speaker=821517,}",
  [168225] = "{id=168225,Text='（看来是白跑了一趟，不过能让老爷爷开心也是值得了）',}",
  [168226] = "{id=168226,Text='嘿，冒险者！对，就是这里！你有一封神秘邮件等待查收！',Speaker=1025,}",
  [168227] = "{id=168227,Text='别问了！我也不知道是谁寄！我可是被千叮万嘱过了！',Speaker=1025,}",
  [168228] = "{id=168228,Text='（自言自语）那个小家伙，虽然个子小小，但是戴着大大的帽子太滑稽了！哈哈哈哈！好了好了，你快打开看看吧！',Speaker=1025,Option='打开邮件',Emoji=10,}",
  [168229] = "{id=168229,Text='亲爱的冒险者，听说你正在寻找圣诞老人的代班？我……一直梦想着有这么一天！能为更多的孩子带去快乐！',Emoji=10,}",
  [168230] = "{id=168230,Text='可身形矮小的我，不知道能否胜任这个形象……我想了整整一天，觉得至少还要争取一下！这几天，我会一直在普隆德拉的音乐盒附近等你！',Emoji=10,}",
  [168231] = "{id=168231,Text='嘿，别怪我多嘴！那个小家伙可热心了，每天都来询问有没有送出邮件！看来，今天能给他一个好消息了！',Speaker=1025,}",
  [168232] = "{id=168232,Text='（唔，看起来是一个内向的小伙伴，明天去拜访一下好了）',}",
  [168233] = "{id=168233,Text='这里这里！在这里！冒险者！没想到你真的来了，太好了！',Speaker=893374,}",
  [168234] = "{id=168234,Text='唔！我叫乔克！虽然我没有白花花的胡子，也没有胖胖的肚子，看起来一点都不像一个圣诞老人……',Speaker=893374,Option='说明驯鹿的要求',Emoji=24,}",
  [168235] = "{id=168235,Text='可我超会！超会陪小孩子玩耍了！他们可喜欢我的红鼻子和手上的气球了……',Speaker=893374,}",
  [168236] = "{id=168236,Text='如果……如果可以的话，请给我一个机会！能不能让我当一次圣诞老人，哪怕就1天也行！',Speaker=893374,Option='那先拍照吧！',}",
  [168237] = "{id=168237,Text='（成为圣诞老人可不轻松，唉，希望他小小的身躯能承担住这份重担吧）',}",
  [168238] = "{id=168238,Text='这样就好了吗？那我就静待结果吧！希望还能再见。',Speaker=893374,Emoji=6,}",
  [168239] = "{id=168239,Text='回来啦，冒险者！怎么样，有遇到不错的候选人吗？',Speaker=893373,Emoji=6,}",
  [168240] = "{id=168240,Text='哇哦！看起来很不错呢！有人气选手忽克连，也有神似圣诞老人的毛茸茸老爷爷，哪怕是误入的克魔岛村长，也拍得不错嘛！',Speaker=893373,Emoji=6,}",
  [168241] = "{id=168241,Text='咦，最后这张小家伙的照片也是彩蛋吗？',Speaker=893373,Option='说明缘由',Emoji=6,}",
  [168242] = "{id=168242,Text='（驯鹿思索了一会儿，然后突然两眼发光，似乎想到了什么美妙的点子）',Speaker=893373,}",
  [168243] = "{id=168243,Text='听着，虽然难以置信，但这一定是一次冒险却富有创造力的尝试！哪怕真正的圣诞老人听了，也会赞成这个决定！',Speaker=893373,Option='真的吗？',}",
  [168244] = "{id=168244,Text='没错！明天你就去找他，把这个好消息告诉他！谁能想到今年的圣诞老人居然是他呢？他也一定会全身心完成这件事的！这就是最好的选择！',Speaker=893373,}",
  [168245] = "{id=168245,Text='哎，别走！我这里有一本《从入门到精通：成为圣诞老人》，记得带给他！',Speaker=893373,}",
  [168246] = "{id=168246,Text='哦！天哪！没想到还能见面……是一个好消息吗？',Speaker=893374,Option='转达驯鹿的结论',}",
  [168247] = "{id=168247,Text='哇~这将是我最好的圣诞礼物！我……我一定不会辜负你们的期望！',Speaker=893374,Option='转交驯鹿的书',}",
  [168248] = "{id=168248,Text='这是——《从入门到精通：成为圣诞老人》！还是驯鹿签名珍藏版……嗯，我会努力的！',Speaker=893374,Emoji=11,}",
  [168251] = "{id=168251,Text='圣诞节需要怎样过呢？每个人心中会有不同的想法~我去拜访了一些老朋友，收集到了有趣的故事，一起来看看吧！',Speaker=893375,}",
  [168252] = "{id=168252,Text='谢谢你能愿意抽出时间听我唠叨，这份礼物请你收下~',Speaker=893375,}",
  [168253] = "{id=168253,Text='“唔，圣诞节就是那天所有人都找我传送至姜饼城，可我自己也好想去呀！”\n——来自从来没有离开过普隆德拉的卡普拉服务人员',}",
  [168254] = "{id=168254,Text='“交易所的生意可是全年无休！那几天最畅销的就是圣诞帽、圣诞角、圣诞波利卡！”\n——B格猫星人',}",
  [168255] = "{id=168255,Text='“年轻的勇者啊，你想再一次听迷路的圣诞老人的故事吗？”\n——吟游诗人',}",
  [168256] = "{id=168256,Text='“我已经写了《从入门到精通：成为圣诞老人》、《21天精通每个烟囱》……每一年都有一本关于圣诞节的著作！”\n——驯鹿',}",
  [168257] = "{id=168257,Text='“不知道圣诞节有什么特殊魔力！把把都能出+15！手气从来没有这么好过！”\n——忽克连',}",
  [168258] = "{id=168258,Text='“天哪！音乐盒那天会一直放《白色圣诞》，为什么不直接去姜饼城坐着呀？”\n——音乐盒旁的雪南南',}",
  [168259] = "{id=168259,Text='“唉，这一天大家都不会来打木桩了，大概都去打雪仗，或者交换礼物了吧”\n——木桩看守者 斯吉莉',}",
  [168260] = "{id=168260,Text='可恶……没想到连邪恶老公公都暴走了……',Speaker=893379,Emoji=22,}",
  [168261] = "{id=168261,Text='请帮忙收拾一下这些占领波利岛的邪恶老公公吧！当然了，会有丰厚的报酬！',Speaker=893379,}",
  [168263] = "{id=168263,Text='哈哈~我喜欢各种稀奇古怪的东西，什么都可以！快来跟我交换吧！',Speaker=893376,}",
  [168264] = "{id=168264,Text='[c][ffff00][PlayerName][-][/c]，虽然我也不知道你喜欢什么，但还是为你也准备了一份特别的礼物！',Speaker=893375,Option='谢谢！',Emoji=17,}",
  [168265] = "{id=168265,Text='如果你也有在意的人，不妨今天也准备一份小礼物送给TA吧~',Speaker=893375,}",
  [168271] = "{id=168271,Text='[c][ffff00][PlayerName][-][/c]，首先，恭喜你通过了普隆德拉英雄协会的考核。',Speaker=891801,}",
  [168272] = "{id=168272,Text='其次，你先不要开心得太早，因为通过考核只是第一步，接下来你需要面对更加困难的挑战！',Speaker=891801,}",
  [168273] = "{id=168273,Text='你也知道，最近有一些强大的魔物通过时空裂隙来到了普隆德拉，给普隆德拉的治安带来了极大的隐患……',Speaker=891801,}",
  [168274] = "{id=168274,Text='这一次，你的任务就是击败他们！',Speaker=891801,}",
  [168275] = "{id=168275,Text='当然，如果你自己处理起来有些困难，我们提供了[c][ffff00]场外求救电话[-][/c]，只要拨通电话，就会有超级英雄前来助战。',Speaker=891801,}",
  [168276] = "{id=168276,Text='现在，你准备好进入战斗了吗？',Speaker=891801,Option='{开始挑战,2},{晚点再来,3}',}",
  [168277] = "{id=168277,Text='你准备好了之后，请随时来找我。',Speaker=891801,}",
  [168281] = "{id=168281,Text='果然我没有看错人，这份奖励，你值得拥有。',Speaker=891801,}",
  [168290] = "{id=168290,Text='嘿，冒险者，你也是听说了普隆德拉英雄协会限时开启了英雄等级的评定而特意前来的吗？',Speaker=891801,}",
  [168291] = "{id=168291,Text='只要你能完成我给你的委托，你就可以增加英雄积分，提升英雄等级，最高可以提升到S级英雄哦！',Speaker=891801,}",
  [168292] = "{id=168292,Text='鉴于我当前还不了解你的实力，我就先给你一个简单的委托吧，去[c][ffff00]西门[-][/c]教训一下「异界波利」吧！',Speaker=891801,}",
  [168293] = "{id=168293,Text='你完成得很出色，如果你明天还能完成我的委托，我就会给你「C级英雄」的评定，记得明天来找我哦~',Speaker=891801,}",
  [168294] = "{id=168294,Text='[c][ffff00][PlayerName][-][/c]，你真是一个守时的人，让我猜一猜，是不是迫不及待想成为「C级英雄」了？',Speaker=891801,}",
  [168295] = "{id=168295,Text='今天的挑战可比昨天要困难哦，如果觉得有难度的话，可以与小伙伴一起组队前往。',Speaker=891801,}",
  [168296] = "{id=168296,Text='那么，今天就去[c][ffff00]西门[-][/c]教训一下「异界天使波利」吧！',Speaker=891801,}",
  [168297] = "{id=168297,Text='恭喜你成为普隆德拉的「C级英雄」，现在才是真正的开始，明天，我还在这里等你。',Speaker=891801,}",
  [168298] = "{id=168298,Text='我一眼就看到我们的「C级英雄」迈着欢快的脚步一路小跑了过来，果然，你还沉浸在晋级的喜悦中。',Speaker=891801,}",
  [168299] = "{id=168299,Text='今天是你成为「C级英雄」后的第一个委托，我们就从简单的开始吧',Speaker=891801,}",
  [168300] = "{id=168300,Text='[c][ffff00]哥布灵森林[-][/c]出现了捣乱的「异界摩卡」，今天的委托，就是去教训教训他们',Speaker=891801,}",
  [168301] = "{id=168301,Text='我就知道，这点难度的委托，对你来说根本不算什么，那么，明天，我会把更有难度的委托留给你哦~',Speaker=891801,}",
  [168302] = "{id=168302,Text='[c][ffff00][PlayerName][-][/c]，怎么今天有些紧张？是因为我昨天说要给你更有难度的委托的关系吗？',Speaker=891801,}",
  [168303] = "{id=168303,Text='哈哈哈哈，不用担心，毕竟你现在还只是「C级英雄」，再难的委托也不会有什么危险',Speaker=891801,}",
  [168304] = "{id=168304,Text='所以你就放心的去吧，今天还是在[c][ffff00]哥布灵森林[-][/c]，去教训一下「异界邪骸士兵」吧',Speaker=891801,}",
  [168305] = "{id=168305,Text='看来是我小瞧你了，那么，明天，我会给你「B级英雄」的进阶委托哦，不要迟到了！',Speaker=891801,}",
  [168306] = "{id=168306,Text='今天可是「B级英雄」的考核日，虽然C级和B级只差了1级，但是在实力的要求上，可是天差地别存在！',Speaker=891801,}",
  [168307] = "{id=168307,Text='今天的委托可能有些难度，如果觉得困难，请带上你的小伙伴一起组队前往。',Speaker=891801,}",
  [168308] = "{id=168308,Text='那么，[c][ffff00]发乐斯灯塔[-][/c]的「异界巴风特」，就是你今天委托的目标！祝你顺利！',Speaker=891801,}",
  [168309] = "{id=168309,Text='恭喜你成为普隆德拉的「B级英雄」，今天肯定累坏了吧，明天记得再来找我哦~',Speaker=891801,}",
  [168310] = "{id=168310,Text='[c][ffff00][PlayerName][-][/c]，「B级英雄」可不是终点，接下来可要更加努力了',Speaker=891801,}",
  [168311] = "{id=168311,Text='B级委托就算是普通的魔物，也充满了危险哦',Speaker=891801,}",
  [168312] = "{id=168312,Text='今天的委托目标就是[c][ffff00]古城郊外[-][/c]的「异界恶魔侍者」',Speaker=891801,}",
  [168313] = "{id=168313,Text='是不是有些吃力了？如果觉得难度还能接受，那么，明天请继续来找我',Speaker=891801,}",
  [168314] = "{id=168314,Text='强啊！我已经很久没遇到过像你这样又有实力又有毅力的年轻人了',Speaker=891801,}",
  [168315] = "{id=168315,Text='那么再接再厉，虽然今天的委托有些难度，但是我相信这点难度根本难不倒你',Speaker=891801,}",
  [168316] = "{id=168316,Text='今天的委托目标就是[c][ffff00]古城郊外[-][/c]的「异界深渊骑士」',Speaker=891801,}",
  [168317] = "{id=168317,Text='果然，我就知道，这点难度根本就难不倒你，明天，我还在这里等你',Speaker=891801,}",
  [168318] = "{id=168318,Text='你知道比实力更重要的是什么吗？那就是一个人的毅力！',Speaker=891801,}",
  [168319] = "{id=168319,Text='我相信，只要坚持，你必定能通过所有的考核，成为万人瞩目的「S级英雄」',Speaker=891801,}",
  [168320] = "{id=168320,Text='脚踏实地，先从今天的委托开始吧，今天需要解决的是[c][ffff00]古城郊外[-][/c]的「异界爱丽丝女仆」',Speaker=891801,}",
  [168321] = "{id=168321,Text='明天又到了进阶日了，你准备好成为「A级英雄」了吗？',Speaker=891801,}",
  [168322] = "{id=168322,Text='米德加尔特大陆有几千万冒险者，但是能够成为「A级英雄」的却是少之又少',Speaker=891801,}",
  [168323] = "{id=168323,Text='今天，你即将成为「A级英雄」，只要你能够击败[c][ffff00]「古城大厅」[-][/c]中的「异界梦罗克」，你就可以晋级！',Speaker=891801,}",
  [168324] = "{id=168324,Text='我在这里等你的好消息！',Speaker=891801,}",
  [168325] = "{id=168325,Text='恭喜你成为普隆德拉的「A级英雄」，这个身份意为着实力，同时也意为着责任。明天，请不要迟到。',Speaker=891801,}",
  [168326] = "{id=168326,Text='你好，「A级英雄」，不知道昨天你有没有与朋友们一起庆祝？',Speaker=891801,}",
  [168327] = "{id=168327,Text='不要开心得太早，「A级英雄」上面还有「S级英雄」，还是要不断努力哦~',Speaker=891801,}",
  [168328] = "{id=168328,Text='今天就来一份开胃菜吧，[c][ffff00]国境检查站[-][/c]的「异界黑魔女」，去教训教训她们吧~',Speaker=891801,}",
  [168329] = "{id=168329,Text='今天的委托对于你来说真的是太过简单了，我们明天再见吧~',Speaker=891801,}",
  [168330] = "{id=168330,Text='很高兴见到你，[c][ffff00][PlayerName][-][/c]',Speaker=891801,}",
  [168331] = "{id=168331,Text='我知道你在努力成为「S级英雄」，我也就不耽误你的时间了',Speaker=891801,}",
  [168332] = "{id=168332,Text='今天的委托依旧在[c][ffff00]国境检查站[-][/c]，去击败「异界烈鬃马」吧',Speaker=891801,}",
  [168333] = "{id=168333,Text='只要坚持下去，你一定可以成为「S级英雄」',Speaker=891801,}",
  [168334] = "{id=168334,Text='是不是觉得有些累了？日复一日的修行、日复一日的委托',Speaker=891801,}",
  [168335] = "{id=168335,Text='只要能在这样的环境下坚持下去的，必定是不一般的人',Speaker=891801,}",
  [168336] = "{id=168336,Text='那么今天的委托地点在[c][ffff00]里希塔乐原野[-][/c]，去消灭「异界吹笛人」吧',Speaker=891801,}",
  [168337] = "{id=168337,Text='快了，距离你成为「S级英雄」的时刻已经不远了',Speaker=891801,}",
  [168338] = "{id=168338,Text='[c][ffff00][PlayerName][-][/c]，你来得正是时候',Speaker=891801,}",
  [168339] = "{id=168339,Text='[c][ffff00]圣域冰洞[-][/c]出现了「异界银月魔女」',Speaker=891801,}",
  [168340] = "{id=168340,Text='这就是你今天的委托',Speaker=891801,}",
  [168341] = "{id=168341,Text='是时候了，明天，就是你的进阶日！',Speaker=891801,}",
  [168342] = "{id=168342,Text='[c][ffff00][PlayerName][-][/c]，历经两周，今天你终于可以成为「S级英雄」了',Speaker=891801,}",
  [168343] = "{id=168343,Text='只要你前往[c][ffff00]圣域神殿地下[-][/c]消灭「异界幽暗梦魇」，你就可以进阶！',Speaker=891801,}",
  [168344] = "{id=168344,Text='不要勉强，如果觉得这个委托太难，请记得叫上你的小伙伴一起！',Speaker=891801,}",
  [168345] = "{id=168345,Text='恭喜你成为「S级英雄」，但是这只是开始！',Speaker=891801,}",
  [168366] = "{id=168366,Text='新的一年来咯~这次，由我来给大家送礼物了~！',Speaker=893398,}",
  [168367] = "{id=168367,Text='过去的一年，[c][ffff00][PlayerName][-][/c]有什么不一样的收获吗？是不是也面临了很多不一样的挑战？',Speaker=893398,}",
  [168368] = "{id=168368,Text='新的一年，要继续加油哦~希望这份礼物可以带给你好运，陪伴你往更美好的方向，大步前进！',Speaker=893398,Option='{谢谢,2},{过会再领,3}',}",
  [168371] = "{id=168371,Text='嘿，冒险者，想必你早已参与过各种庆典了，见识了不少有趣的小玩意~',Speaker=893399,}",
  [168372] = "{id=168372,Text='来来，帮我选选哪件小玩具能在庆典期间畅销~当然了，报酬自然是少不了你的！',Speaker=893399,}",
  [168374] = "{id=168374,Text='击败庆典飞龙，开启6个[c][ffff00]「庆典魔力宝箱」[-][/c]，100%额外获赠[c][ffff00]「精炼头饰+15券」[-][/c]!',Speaker=893409,}",
  [168375] = "{id=168375,Text='如果有人能组队带带我就好了，唉……',Speaker=893409,Emoji=15,}",
  [168377] = "{id=168377,Text='你好呀，冒险者。你知道吗？整个米德加尔特大陆都在讨论——这棵一夜之间就出现的神秘大树！',Speaker=893244,}",
  [168378] = "{id=168378,Text='听说把自己的愿望写在小卡片上，然后挂上去就有可能实现噢~',Speaker=893244,}",
  [168379] = "{id=168379,Text='愿望越详细成功率越高！嘿嘿，你也来试试吧！',Speaker=893244,Option='好',Emoji=5,}",
  [168380] = "{id=168380,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893398,}",
  [168381] = "{id=168381,Text='在这个特殊的节日里，我想把最诚挚的祝福想要送给你！',Speaker=893398,Emoji=5,}",
  [168382] = "{id=168382,Text='一个人送祝福实在太没意思了，不如你来跟我一起吧？',Speaker=893398,Option='我要怎么做？',}",
  [168383] = "{id=168383,Text='只要你将[c][ffff00]「祝愿」[-][/c]传递出去，米德加尔特的冒险者们就会收到我们的祝福了！',Speaker=893398,}",
  [168384] = "{id=168384,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得丰厚奖励）[-][/c]\n那么，这句[c][ffff00]「祝愿」[-][/c]是：',Speaker=893398,}",
  [168385] = "{id=168385,Text='带着祝福，朝着更美好的方向，大步前行吧！一起守护永恒的爱！',Option='{谢谢,2},{过会再领,3}',}",
  [168386] = "{id=168386,Text='庆典虽然会结束，但活动商人的筹备可不会停！\n除了Zeny，我也收各式各样的制作材料噢！',Speaker=893400,}",
  [168387] = "{id=168387,Text='这可是变废为宝的好机会，快来交换吧！',Speaker=893400,}",
  [168391] = "{id=168391,Text='新的一年来咯~这次，本兔兔给冒险者准备一份大大的压岁钱~',Speaker=893412,}",
  [168392] = "{id=168392,Text='除了祝冒险者在新的一年里身体健康工作（学业）顺利，这个满满当当的压岁钱，你可一定要收好了哦~',Speaker=893412,}",
  [168393] = "{id=168393,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893145,}",
  [168394] = "{id=168394,Text='在这个特殊的节日里，我有个最诚挚的祝福想要送给大家~',Speaker=893145,Emoji=5,}",
  [168395] = "{id=168395,Text='但是米德加尔特有那么多冒险者，我一个人实在是忙不过来',Speaker=893145,Option='我要怎么做？',}",
  [168396] = "{id=168396,Text='如果你能帮我把[c][ffff00]「新春祝福」[-][/c]传递出去，那就有更多的冒险者能获得祝福了！',Speaker=893145,}",
  [168399] = "{id=168399,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「拜年红包2023」x5）[-][/c]\n那么，B格猫星人的[c][ffff00]「新春祝福」[-][/c]是：',Speaker=893145,}",
  [168401] = "{id=168401,Text='如果你见到了我的兄弟姐妹们，下手请轻一些',Speaker=893443,}",
  [168402] = "{id=168402,Text='虽然说他们给你准备了很多礼物，总要走个过场，挨几下打',Speaker=893443,}",
  [168403] = "{id=168403,Text='但是这大过年的，他们还在加班，还是这种苦力活，多不容易啊！',Speaker=893443,}",
  [168405] = "{id=168405,Text='[c][ffff00][PlayerName][-][/c]，你来啦~准备好迎接这次的皇家庆典了吗？',Speaker=893414,Emoji=5,}",
  [168406] = "{id=168406,Text='这次，我们准备了“与疯兔赛跑”、“忽克连十连锤”、“南门数波利”以及更多意想不到的庆典活动！',Speaker=893414,}",
  [168407] = "{id=168407,Text='另外，只要参与了庆典活动，就能获得特制的[c][ffff00]「彩虹波利币」[-][/c] —— 庆典限定的时装、头饰都能免费！免费换取哦~',Speaker=893414,Option='知道啦~',}",
  [168408] = "{id=168408,Text='怎么样？有没有兴趣参加呀？[c][ffff00]【该任务同账号仅限一个角色接取，请谨慎考虑是否接取】[-][/c]',Speaker=893414,Option='{是,2},{否,3} ',}",
  [168409] = "{id=168409,Text='等等~冒险者~在进行庆典游玩之前，有一位特别的冒险者申请与你组队，俗话说两人办事事半功倍，记得去拜访一下哦~',Speaker=893414,}",
  [168410] = "{id=168410,Text='啊~怎么还没来~MVP都要刷新第20轮了~',Speaker=893398,Option='啧……',}",
  [168411] = "{id=168411,Text='[c][ffff00][PlayerName][-][/c]，快来！嘿嘿嘿，我已经等不及了。',Speaker=893398,}",
  [168412] = "{id=168412,Text='（这不就是不会射箭的猎人，俗称不会干活的懒人……天哪，这真的是靠谱的队友吗？）',}",
  [168413] = "{id=168413,Text='还记得过去我们的冒险吗？我可真是怀念。所以，我希望……我们可以再组一次！',Speaker=893398,Option='我拒绝',}",
  [168414] = "{id=168414,Text='诶~别…别走！「彩虹波利币」都给你，赢得小礼物都给我就好了！这笔交易，如何？',Speaker=893398,Option='{这个嘛…,0},{真的嘛？,0}',}",
  [168415] = "{id=168415,Text='呜呜呜……那我身上这些「彩虹波利币」就当做约定先送给你~\n如果你有意向，那明天来这里找我吧！',Speaker=893398,}",
  [168416] = "{id=168416,Text='哼，我再等1分钟就走掉了！可我已经等了300分钟了……也不差这个1分钟了……',Speaker=893398,Option='听说你要走？',}",
  [168417] = "{id=168417,Text='哈哈哈，[c][ffff00][PlayerName][-][/c]，来得真早啊，看来你很有干劲。',Speaker=893398,}",
  [168418] = "{id=168418,Text='好，我们马上开始吧！今天要参与的活动是——“决战吧！乐园团”\n首先，你要先去……',Speaker=893398,Option='你不去吗',}",
  [168419] = "{id=168419,Text='哼哼……如果我亲自下场的话，赢得比赛会被认为是作弊！没办法，大人物总有大人物的烦恼！',Speaker=893398,}",
  [168420] = "{id=168420,Text='总之，我提供情报，你获得奖励，何乐而不为？好了好了，快去找【赛厄娜】~你去了就知道规则了！\n我先下班了，明天再见！',Speaker=893398,}",
  [168421] = "{id=168421,Text='（这……这不就是跑腿吗……）',}",
  [168422] = "{id=168422,Text='下个挑战者是谁？无论你出什么，我都能精准预测！',Speaker=6744,}",
  [168423] = "{id=168423,Text='（什么嘛……原来是猜拳！我可刚使用了备战精英，准备大打出手了……）',}",
  [168424] = "{id=168424,Text='哦？你要来试试？如果你能赢得对决，我就把「彩虹波利币」送给你！',Speaker=6744,Option='{没问题,2},{还是算了,3}',}",
  [168425] = "{id=168425,Text='那就让我们开始吧~',Speaker=6744,}",
  [168426] = "{id=168426,Text='唉~不争取一下吗？',Speaker=6744,}",
  [168427] = "{id=168427,Text='三！',Speaker=6744,}",
  [168428] = "{id=168428,Text='二！',Speaker=6744,}",
  [168429] = "{id=168429,Text='一！',Speaker=6744,}",
  [168430] = "{id=168430,Text='剪刀石头布——',Speaker=6744,Option='{石头,40},{剪刀,50},{布,60}',}",
  [168431] = "{id=168431,Text='嘿嘿，我赢了！',Speaker=6744,}",
  [168432] = "{id=168432,Text='要不我们再来一次？',Speaker=6744,Option='{再来一次,103},{不玩啦,102}',}",
  [168433] = "{id=168433,Text='哟～你赢了呢！',Speaker=6744,}",
  [168434] = "{id=168434,Text='哇哦！不错嘛！这不过是个幸运一击！',Speaker=6744,}",
  [168435] = "{id=168435,Text='哦吼？不简单嘛！',Speaker=6744,}",
  [168436] = "{id=168436,Text='唔 … … 没有分出胜负呢。',Speaker=6744,}",
  [168437] = "{id=168437,Text='怎么样，要不要再来一次？',Speaker=6744,Option='{再试一次,104},{不玩啦,101}',}",
  [168438] = "{id=168438,Text='就这么结束了吗 … …',Speaker=6744,}",
  [168439] = "{id=168439,Text='不管如此，还是谢谢你呢~',Speaker=6744,}",
  [168440] = "{id=168440,Text='没错，我们再来，再来！',Speaker=6744,}",
  [168441] = "{id=168441,Text='哈哈哈，[c][ffff00][PlayerName][-][/c]，今天很准时嘛！',Speaker=893398,}",
  [168442] = "{id=168442,Text='昨天，你大获全胜的消息传遍了整个普隆德拉~不亏是我骄傲的助手！',Speaker=893398,}",
  [168443] = "{id=168443,Text='不过……赛厄娜有些许不服气，毕竟全胜战绩的她在一局之间，竟然落败了！',Speaker=893398,Option='你的意思是……',}",
  [168444] = "{id=168444,Text='噢噢，再去来一局？毕竟，「彩虹波利币」什么的，谁都不会嫌多~',Speaker=893398,}",
  [168445] = "{id=168445,Text='哼，怎么又是你！来，再来！',Speaker=6744,Option='{没问题,2},{还是算了,3}',}",
  [168446] = "{id=168446,Text='好吧，我承认了，你是真的厉害！',Speaker=6744,}",
  [168447] = "{id=168447,Text='[c][ffff00][PlayerName][-][/c]，你可太棒了！',Speaker=893398,}",
  [168448] = "{id=168448,Text='想必今天的挑战也是轻轻松松，不过可能有点费时，具体的规则，你去找南门的【啵啵】了解吧~',Speaker=893398,}",
  [168449] = "{id=168449,Text='嘤！啵啵发起的挑战是——\n南门一共有多少只波利！',Speaker=5675,Option='什…什么',}",
  [168450] = "{id=168450,Text='冒险者，仔细数一数！小心狸猫变成的小波利~嘤~',Speaker=5675,}",
  [168451] = "{id=168451,Text='（这……这怎么做得到！波利无处不在，波利随时都在……）',}",
  [168452] = "{id=168452,Text='（看来！这次只能智取了，打开背包，拿出……）',}",
  [168456] = "{id=168456,Text='噢~这个味道，酸酸甜甜，是波利最喜欢的——青苹果！',Speaker=5675,}",
  [168457] = "{id=168457,Text='啵啵想要青苹果，可啵啵只有波利币！好吧，交换！',Speaker=5675,}",
  [168458] = "{id=168458,Text='（果然，没有哪个波利会拒绝青苹果的诱惑……）',}",
  [168459] = "{id=168459,Text='（那么，今天就直接去拜访啵啵吧）',}",
  [168460] = "{id=168460,Text='噢！是冒险者，又来交换波利币了！1个青苹果1个币~',Speaker=5675,}",
  [168461] = "{id=168461,Text='青苹果的味道！波利最喜欢了！彩虹波利币给你吧，希望有苹果再见~',Speaker=5675,}",
  [168462] = "{id=168462,Text='看来，你已经获得不少「彩虹波利币」了！那么，我来揭晓下一个任务地点——水果商人！',Speaker=893398,}",
  [168463] = "{id=168463,Text='那些忙碌于工作的人，才不会关注身边的小趣味！怎么样，冒险者，愿意接受我的挑战吗？',Speaker=1197,Option='展开说说',}",
  [168464] = "{id=168464,Text='去普隆德拉到处逛逛，只要能找到我身后这种花车，就能获得「彩虹波利币」！',Speaker=1197,Option='好',}",
  [168465] = "{id=168465,Text='噢！没错！就是这个！那么，这是你的报酬！',Speaker=1197,Option='好',}",
  [168466] = "{id=168466,Text='[c][ffff00][PlayerName][-][/c]，你回来啦~看起来，收获颇丰呢~',Speaker=893398,}",
  [168467] = "{id=168467,Text='怎么样？陪陪老朋友玩玩，是不是也挺有趣的~',Speaker=893398,}",
  [168468] = "{id=168468,Text='好啦，我知道的「彩虹波利币」获取途径都全部告诉你了~',Speaker=893398,}",
  [168469] = "{id=168469,Text='想要更多？唔，那只有到处去逛逛了~毕竟庆典才刚刚开始~',Speaker=893398,}",
  [168470] = "{id=168470,Text='活动期间，我会一直在这里~想我的话，就过来看看吧~\n喏，这是你应得的报酬~',Speaker=893398,}",
  [168472] = "{id=168472,Text='走过路过不要错过！庆典特供——「辣味汽水」，正在限时出售！只要一小口，火热的感觉将燃烧全身！\n活动期间，仅售1Zeny！',Speaker=893415,Option='负面效果？',}",
  [168473] = "{id=168473,Text='唔……已知的负面效果有口干舌燥、浑身着火，还有……勇敢的冒险者，不要畏惧，只要你愿意尝试，我们将提供丰厚的奖励！',Speaker=893415,}",
  [168474] = "{id=168474,Text='[c][ffff00]使用「辣味汽水」后，只要与同样使用了「辣味汽水」冒险者进行任意双人动作，即可获得「庆典达人礼盒」x1！[-][/c]\n（双人动作包括：举高、拥吻、亲吻、击掌）',Speaker=893415,}",
  [168516] = "{id=168516,Text='集齐[限时联动]扭蛋机中的所有宝物后，可以获得限定坐骑「金龙」。',Speaker=891126,}",
  [168517] = "{id=168517,Text='[限时联动]扭蛋机中的宝物包括：魔神英雄传系列时装、魔神英雄传系列头饰、古拿马魔力头套、飘飘空神丸、趴趴火美子、趴趴虎王、趴趴战部渡、希里冰花、炽热之剑、邪虎丸',Speaker=891127,}",
  [168518] = "{id=168518,Text='冒险者可以组队挑战「封印之地」副本，击败多恶达，有概率获得机动能量、神龙勾玉礼盒等活动道具',Speaker=892430,}",
  [168519] = "{id=168519,Text='机动能量每个帐号每天只能获得5个，神龙勾玉礼盒每个帐号每天只能获得1个',Speaker=892430,}",
  [168520] = "{id=168520,Text='祝你好运吧！',Speaker=892430,}",
  [168521] = "{id=168521,Text='联动职业返场啦~',Speaker=892968,}",
  [168522] = "{id=168522,Text='活动期间，只需要使用B格猫金币参与10次[限时联动]扭蛋机，即可额外获赠联动职业转职道具「勾玉」',Speaker=892968,}",
  [168523] = "{id=168523,Text='冒险家千万不要错过哦~',Speaker=892968,}",
  [168526] = "{id=168526,Text='选择感兴趣的活动看一看吧。',Speaker=891804,Option='{活动拼图,2},{联动扭蛋,3},{极速成长礼包,4},{异界馈赠,5}',}",
  [168527] = "{id=168527,Text='活动期间冒险者们可通过“热点”按钮进入“活动拼图”，收集拼图达成一定进度时可获得特别奖励哟~',Speaker=891804,}",
  [168528] = "{id=168528,Text='活动期间普隆德拉广场将登陆联动限定扭蛋机[c][ffff00]异次元扭蛋机[-][/c]，冒险者可通过[c][ffff00]异次元扭蛋机[-][/c]获得稀有奖励！',Speaker=891804,}",
  [168529] = "{id=168529,Text='活动期间首次转职成为[c][ffff00]小救星[-][/c]职业，可触发[c][ffff00]极速成长礼包·小救星[-][/c]购买权限，售价648B格猫金币，礼包内含有丰富内容，[c][ffff00]每个账户限购1次，礼包存在时间为7天，7天后没有购买则消失[-][/c]。',Speaker=891804,}",
  [168530] = "{id=168530,Text='活动期间打赏金额达到指定档位，即可领取对应超值奖励。',Speaker=891804,}",
  [168531] = "{id=168531,Text='活动期间Base等级≥25级的冒险者可在普隆德拉广场通过[c][ffff00]金龙[-][/c]接取[c][ffff00]魔神英雄传[-][/c]系列任务。',Speaker=891804,}",
  [168532] = "{id=168532,Text='[c][ffff00]魔神英雄传[-][/c]共五个连续任务，每完成1个任务即可获得[c][ffff00]神龙的馈赠[-][/c]*1、[c][ffff00]神龙勾玉[-][/c]*20、[c][ffff00]神龙扭蛋币[-][/c]*10，完成首日任务可额外获得限定家具[c][ffff00]火美火美浣熊分身[-][/c]*1，完成第5日任务额外获得限定图纸[c][ffff00]飘飘幻神丸[-][/c]图纸*1。',Speaker=891804,}",
  [168533] = "{id=168533,Text='冒险者使用[c][ffff00]神龙勾玉[-][/c]后可打开创界山商店，并可使用勾玉兑换奖励，活动结束后[c][ffff00]神龙勾玉[-][/c]将消失。',Speaker=891804,}",
  [168534] = "{id=168534,Text='冒险者可以前往[c][ffff00]西门[-][/c]找到古老的占卜师[c][ffff00]莱欧娜[-][/c]，她在日常占星时捕捉到一股邪恶的力量，经过缜密地推算，原来黑暗魔神[c][ffff00]多恶达[-][/c]也来到了米德加尔特大陆，在多恶达力量还未恢复之前，请冒险者们与小渡联手击败他！',Speaker=891804,}",
  [168535] = "{id=168535,Text='击败守护多恶达的魔物，解除多恶达设下的魔法阵，用龙神丸努力消灭多恶达吧！[c][ffff00]请注意，多恶达只能由龙神丸造成伤害[-][/c]。',Speaker=891804,}",
  [168536] = "{id=168536,Text='[c][ffff00]组队[-][/c]挑战副本，击败[c][ffff00]多恶达[-][/c]有概率获得[c][ffff00]神龙勾玉礼盒[-][/c]，该礼盒为限定类道具，每个账号每天仅可获得1个，副本内小怪有概率掉落[c][ffff00]机动能量[-][/c]，提交[c][ffff00]机动能量[-][/c]给莱欧娜可获得[c][ffff00]神龙精华[-][/c]*1，[c][ffff00]该活动人物每个账号每天仅可完成一次[-][/c]。',Speaker=891804,}",
  [168537] = "{id=168537,Text='[c][ffff00]挑战副本不消耗疲劳，副本内掉落亦和疲劳无关[-][/c]，击败30只[c][ffff00]魔动机兵[-][/c]可解锁限定称号[c][ffff00]小救星[-][/c]。',Speaker=891804,}",
  [168538] = "{id=168538,Text='在冒险者们的努力下，多恶达势力终于被消灭了，剑部武一郎也要返回创界山了，临走前剑部武一郎还有一个请求……',Speaker=891804,}",
  [168539] = "{id=168539,Text='他想知道经过这段时间的并肩作战，冒险者们对他和创界山是否有了一定了解呢？他迫切地想知道米德加尔特大陆上的冒险者是否欢迎自己。',Speaker=891804,}",
  [168540] = "{id=168540,Text='活动期间Base等级大于12级的冒险者可前往普隆德拉找到[c][ffff00]剑部武一郎[-][/c]，回答他的问题，每日回答后可获得[c][ffff00]剑部武一郎的谢礼[-][/c]*1、[c][ffff00]神龙勾玉[-][/c]*10、[c][ffff00]神龙扭蛋币[-][/c]*20。',Speaker=891804,}",
  [168541] = "{id=168541,Text='滴滴，没时间多解释了，我是多世界的时空守护者，检测到有异时空的危机出现，冒险者，又该你拯救大陆了！',Speaker=891810,}",
  [168542] = "{id=168542,Text='帮助剑部武一郎消灭黑暗魔神多恶达，让米德加尔特大陆重归静宁。',Speaker=891810,}",
  [168543] = "{id=168543,Text='当然啦，英雄总是需要鲜花和掌声，我们也为你准备了丰厚的奖励！',Speaker=891810,}",
  [168544] = "{id=168544,Text='活动期间，Base等级≥25级的冒险者可在普隆德拉广场通过[c][ffff00][卡普拉服务人员][-][/c]接取[卡普拉的邀约]系列任务。',Speaker=893414,}",
  [168545] = "{id=168545,Text='完成系列任务，可获得「庆典特供」x1、「彩虹波利币」x1。\n「彩虹波利币」可兑换庆典时装/头饰哦~\n「庆典特供」打开后，可获得「波利心愿」所需的礼物哦！',Speaker=893414,}",
  [168546] = "{id=168546,Text='活动期间，Base等级≥25级的冒险者可在普隆德拉广场找到[c][ffff00][霍克拉·银][-][/c]，帮助他挑选合适的庆典玩具。',Speaker=893399,}",
  [168547] = "{id=168547,Text='完成每日选择后，可获得「庆典特供」x1、「彩虹波利币」x1。\n「彩虹波利币」可兑换庆典时装/头饰哦~\n「庆典特供」打开后，可获得「波利心愿」所需的礼物哦！',Speaker=893399,}",
  [168548] = "{id=168548,Text='活动期间，每周五~每周日波利岛将刷新[c][ffff00][庆典飞龙][-][/c]！',Speaker=893409,}",
  [168549] = "{id=168549,Text='击败庆典飞龙，可获得[c][ffff00]「庆典魔力宝箱」x1[-][/c]，该宝箱为限定类道具，每个账号每天仅可获得1个。\n「庆典魔力宝箱」打开后，可获得「波利心愿」所需的礼物哦！',Speaker=893409,}",
  [168550] = "{id=168550,Text='在第6次开启[c][ffff00]「庆典魔力宝箱」[-][/c]时，100%额外获赠[c][ffff00]「精炼头饰+15券」[-][/c]!',Speaker=893409,}",
  [168551] = "{id=168551,Text='活动期间，每位冒险者仅能许愿一次，可不要太贪心哦~',Speaker=893410,}",
  [168552] = "{id=168552,Text='完成许愿后，可获得[c][ffff00]「好运币」x10、「彩虹波利币」x3[-][/c]',Speaker=893410,}",
  [168553] = "{id=168553,Text='活动期间，在[c][ffff00][亨特][-][/c]处购买并使用「辣味汽水」后，只要与同样使用了「辣味汽水」的冒险者进行任意双人动作，即可获得[c][ffff00]「庆典达人礼盒」x1[-][/c]！\n（双人动作包括：举高、拥吻、亲吻、击掌）',Speaker=893415,}",
  [168554] = "{id=168554,Text='[c][ffff00]「庆典达人礼盒」x1[-][/c]为限定类道具，每个账号每天仅可获得1个。\n[c][ffff00]「庆典达人礼盒」x1[-][/c]打开后，可获得「波利心愿」所需的礼物哦！',Speaker=893415,}",
  [168555] = "{id=168555,Text='活动期间，冒险者可在普隆德拉广场找到[c][ffff00][霍克拉·银][-][/c]，向他捐赠材料，可获得「庆典特供」x1。\n「庆典特供」打开后，可获得「波利心愿」所需的礼物哦！',Speaker=893400,}",
  [168557] = "{id=168557,Text='被骗了吧？其实我不是战部渡，但无论从哪个角度，看起来都一模一样吧？你想不想变得和我一样无可挑剔？',Speaker=891811,}",
  [168558] = "{id=168558,Text='只要去边上的[c][ffff00]异次元扭蛋机[-][/c]抽满10次扭蛋就可以额外获得“[c][ffff00]勾玉[-][/c]”哦，使用了“勾玉”很快你也会变得和我一样帅气啦。',Speaker=891811,}",
  [168559] = "{id=168559,Text='选择感兴趣的活动看一看吧。',Speaker=891812,Option='{活动拼图,2},{喵喵乐园签到,3}',}",
  [168560] = "{id=168560,Text='[c][ffff00]11月1日5：00--12月1日5：00[-][/c]，冒险者们可通过“[c][ffff00]热点[-][/c]”按钮进入[c][ffff00]活动拼图[-][/c]，收集拼图达成一定进度时可获得特别奖励哟~',Speaker=891812,}",
  [168561] = "{id=168561,Text='[c][ffff00]11月7日5：00--11月21日5：00[-][/c]，Base等级≥25级的冒险者，通过“[c][ffff00]热点[-][/c]”可进入[c][ffff00]喵喵乐园[-][/c]。\n累计登录达到指定天数，即可领取1份奖励~',Speaker=891812,}",
  [168563] = "{id=168563,Text='[c][ffff00]11月1日5：00--11月10日5：00[-][/c]，Base等级≥25级的冒险家，前往[c][ffff00]普隆德拉[-][/c]，找到【购物节宣传大使】[c][ffff00]琪琪[-][/c]即可参与活动，帮助【购物节宣传大使】宣传本届购物节，即可获得奖励[c][ffff00]普隆德拉购物积分[-][/c]x2。 \n【[c][ffff00]该活动为帐号限制任务，每个帐号每日仅可完成1次[-][/c]。】',Speaker=891812,}",
  [168564] = "{id=168564,Text='购物的季节又要来临了~这次小爱神【购物节宣传大使】[c][ffff00]琪琪[-][/c]为冒险者准备了一个「甜心糖果乐园」！',Speaker=891812,}",
  [168565] = "{id=168565,Text='[c][ffff00]11月10日5：00--11月17日5：00[-][/c]，冒险者与【购物节宣传大使】[c][ffff00]琪琪[-][/c]对话，可进入副本「甜心糖果乐园」，副本不限次挑战，不消耗战斗时长，活动为账号限定，每个指定波数奖励，每日可获得[c][ffff00]1次[-][/c]。',Speaker=891812,}",
  [168566] = "{id=168566,Text='完成第5波，可获得「普隆德拉购物积分」x1，\n完成第10波，可获得「普隆德拉购物积分」x1，\n完成第15波，可获得「普隆德拉购物积分」x1、「购物节惊喜礼盒」x1。',Speaker=891812,}",
  [168567] = "{id=168567,Text='火热的购物节引来了大量【[c][ffff00]捣乱的小坏蛋[-][/c]】想要浑水摸鱼，冒险者们，现在是该你们出手的时间啦~',Speaker=891812,}",
  [168568] = "{id=168568,Text='[c][ffff00]11月17日5：00--11月24日5：00[-][/c]，[c][ffff00]波利岛[-][/c]将出现大量【[c][ffff00]捣乱的小坏蛋[-][/c]】，击败【[c][ffff00]捣乱的小坏蛋[-][/c]】，概率获得[c][ffff00]被抢走的礼物[-][/c]。提交[c][ffff00]被抢走的礼物[-][/c]给组委会主席[c][ffff00]亨特[-][/c]，可以获得[c][ffff00]购物节神秘礼盒[-][/c]x1。',Speaker=891812,}",
  [168569] = "{id=168569,Text='[c][ffff00]被抢走的礼物[-][/c]每日账号可获得[c][ffff00]上限1个[-][/c]，吃锁链多倍掉落/消耗战斗时长，疲劳后也会掉落！',Speaker=891812,}",
  [168570] = "{id=168570,Text='购物节临近尾声，但是还有一些库存，为了报答冒险者在此次购物节中见义勇为的的行为，组委会决定将这些库存骨折价出售，冒险者们还不快去看看！',Speaker=891812,}",
  [168571] = "{id=168571,Text='[c][ffff00]11月24日5：00--12月1日5：00[-][/c]，前往[c][ffff00]普隆德拉广场[-][/c]找到购物节组委会主席[c][ffff00]亨特[-][/c]即可参与活动，每次捐献材料可以获得「[c][ffff00]购物节回馈礼盒[-][/c]」x1。\n[c][ffff00]该活动帐号限定，每个帐号每天可捐献的最高次数为2次[-][/c]。',Speaker=891812,}",
  [168572] = "{id=168572,Text='[c][ffff00]11月24日5：00--12月1日5：00[-][/c]，Base等级≥25级的冒险者，前往普隆德拉，找到【[c][ffff00]温暖的小天使[-][/c]】，与他对话即可获得[c][ffff00]感恩节礼盒[-][/c]x1，【[c][ffff00]该活动帐号限定，每个帐号仅可参与1次[-][/c]。】',Speaker=891812,}",
  [168575] = "{id=168575,Text='Emmm，说起来有些难以启齿，但是我又不得不说',Speaker=893426,Option='怎么了？',}",
  [168576] = "{id=168576,Text='我邀请了一些朋友来我家庆祝万圣节，结果他们竟然趁我不注意偷了我的[c][ffff00]德古拉的珍藏[-][/c]，这可是我的宝贝呀！快在[c][ffff00]雾之森林[-][/c]使用[c][ffff00]幽灵相机[-][/c]找到他们吧！',Speaker=893426,}",
  [168577] = "{id=168577,Text='如果冒险者能给我带回来1个[c][ffff00]「德古拉的珍藏」[-][/c]，我一定会报答你的！需要注意的是，我的这些朋友都很狡猾，你需要用[c][ffff00]幽灵相机[-][/c]才能找到他们哦~',Speaker=893426,}",
  [168578] = "{id=168578,Text='确定要提交[c][ffff00]「德古拉的珍藏」[-][/c]x1吗？',Speaker=893426,Option='{确定,4},{再想想,3}',}",
  [168579] = "{id=168579,Text='你骗人，你身上暂时没有足够的材料哦！前往[c][ffff00]雾之森林[-][/c]，使用[c][ffff00]幽灵相机[-][/c]看看吧~',Speaker=893426,}",
  [168580] = "{id=168580,Text='感谢你~祝你冒险快乐~！',Speaker=893426,}",
  [168582] = "{id=168582,Text='想要了解哪个活动？',Speaker=893427,Option='{活动拼图,2},{联动扭蛋,3},{急速成长礼包,4},{异界馈赠,5}',}",
  [168583] = "{id=168583,Text='活动期间冒险者们可通过“热点”按钮进入“活动拼图”，收集拼图达成一定进度时可获得特别奖励哟~',Speaker=893427,}",
  [168584] = "{id=168584,Text='活动期间，普隆德拉广场将登陆联动限定扭蛋机[异次元扭蛋机]，冒险者可通过[异次元扭蛋机]获得稀有奖励！使用金币抽取10次，即可免费解锁联动限定冒险职业——影·埼玉！',Speaker=893427,}",
  [168585] = "{id=168585,Text='活动期间，金币商城出售[极速成长礼包·影·埼玉]及[极速成长礼包·影·杰诺斯]，售价均为198B格猫金币，礼包内含有丰富内容，欢迎冒险者选购。',Speaker=893427,}",
  [168586] = "{id=168586,Text='活动期间，打赏金额达到指定档位，即可领取对应超值奖励。累计充值金额达到328，即可免费解锁联动限定冒险职业——影·杰诺斯!',Speaker=893427,}",
  [168588] = "{id=168588,Text='完成任务即可获得【英雄赠礼】*1、【异界能量】*20、【英雄扭蛋币】*5，首次完成任务可以额外获得限定头饰【英雄·地狱的吹雪】*1。',Speaker=893427,}",
  [168589] = "{id=168589,Text='当冒险者进阶为【C级英雄】、【B级英雄】、【A级英雄】、【S级英雄】时，可以分别额外获得对应的英雄礼盒。',Speaker=893427,}",
  [168590] = "{id=168590,Text='活动期间，完成【英雄进阶】系列任务，且达到【S级英雄】后即可解锁【英雄试炼】副本挑战资格。',Speaker=893427,}",
  [168591] = "{id=168591,Text='通关【英雄试炼】副本即可获得【超级英雄赠礼】x1。',Speaker=893427,}",
  [168593] = "{id=168593,Text='冒险者，大陆护卫队勘测到时空裂隙再次开启！',Speaker=893434,}",
  [168594] = "{id=168594,Text='从这次的裂隙连接到的异世界，能感受到异常强大的力量。',Speaker=893434,}",
  [168595] = "{id=168595,Text='裂隙出现的非常古怪并且不同寻常，护卫队也正在慎重调查。',Speaker=893434,}",
  [168596] = "{id=168596,Text='从目前的调查情况来说，似乎有人在后面规划着这一切……',Speaker=893434,}",
  [168597] = "{id=168597,Text='另外，那个世界中的一些强者也因为裂隙的出现被带到了米德加尔特大陆。',Speaker=893434,}",
  [168598] = "{id=168598,Text='听说他们正在寻找回去的方法，如果在冒险的途中遇到，麻烦你能提供力所能及的帮助。',Speaker=893434,}",
  [168605] = "{id=168605,Text='冒险者，想要了解哪一个活动?',Speaker=893447,Option='{圣诞敲波利,2},{波利大富翁,3}',}",
  [168606] = "{id=168606,Text='冒险者们可在“[c][ffff00]热点[-][/c]”界面点击按钮[c][ffff00]圣诞敲波利[-][/c]进入活动~',Speaker=893447,}",
  [168607] = "{id=168607,Text='[c][ffff00]圣诞敲波利[-][/c]每轮将有8个随机奖励小波利+1个额外奖励大波利组成，消耗小木锤点击波利获得奖励！',Speaker=893447,}",
  [168608] = "{id=168608,Text='获得本轮所有小波利奖励后，将自动获得该轮大波利奖励~',Speaker=893447,}",
  [168609] = "{id=168609,Text='[c][ffff00]圣诞敲波利[-][/c]活动总计4轮，轮次之间存在前后置解锁关系，领取完上一轮次所有奖励后，将自动解锁下一轮次进度。',Speaker=893447,}",
  [168610] = "{id=168610,Text='当界面上仍有未被打碎的小波利，在特定时间可以使用Zeny购买小木锤，锤子购买次数每日重置。',Speaker=893447,}",
  [168611] = "{id=168611,Text='【[c][ffff00]活动为账号限定，账号下所有角色共享完成进度[-][/c]。】',Speaker=893447,}",
  [168612] = "{id=168612,Text='冒险者们可在“[c][ffff00]热点[-][/c]”界面点击按钮[c][ffff00]波利大富翁[-][/c]进入活动~',Speaker=893447,}",
  [168613] = "{id=168613,Text='首次打开界面，获得1个普通骰子，每日首次登录，获得1个普通骰子。每间隔6小时，自然恢复1个普通骰子，最多存储5个。',Speaker=893447,}",
  [168614] = "{id=168614,Text='首次停留在[c][ffff00]天使波利格子[-][/c]，获得1份奖励，每次停留在普通波利格子 ，都会获得1份奖励。',Speaker=893447,}",
  [168615] = "{id=168615,Text='完成指定圈数后，可以获得对应奖励哦~~【[c][ffff00]活动为账号限定，账号下所有角色共享完成进度[-][/c]。】',Speaker=893447,}",
  [168616] = "{id=168616,Text='Base等级≥25级的冒险家，前往[c][ffff00]普隆德拉[-][/c]，找到[c][ffff00]驯鹿[-][/c]接取系列任务，帮助[c][ffff00]驯鹿[-][/c]安排好一年一度的圣诞节。 \n【[c][ffff00]活动为账号限定，每个账号每天仅可参与1次[-][/c]。】',Speaker=893447,}",
  [168617] = "{id=168617,Text='Base等级≥25级的冒险家，前往[c][ffff00]普隆德拉[-][/c]，找到[c][ffff00]乔克[-][/c]接取系列任务。 \n【[c][ffff00]活动为账号限定，每个账号每天仅可参与1次[-][/c]。】',Speaker=893447,}",
  [168618] = "{id=168618,Text='波利岛出现了大量的【[c][ffff00]邪恶老公公[-][/c]】，冒险者们，现在是该你们出手的时间啦~',Speaker=893447,}",
  [168619] = "{id=168619,Text='[c][ffff00]波利岛[-][/c]将出现大量【[c][ffff00]邪恶老公公[-][/c]】，击败【[c][ffff00]邪恶老公公[-][/c]】，概率获得[c][ffff00]遗失的礼物盒[-][/c]或[c][ffff00]圣诞老人的长袜[-][/c]。收集[c][ffff00]圣诞老人的长袜[-][/c]x3，即兑换限定[c][ffff00]圣诞福袋[-][/c]x1！',Speaker=893447,}",
  [168620] = "{id=168620,Text='[c][ffff00]遗失的礼物盒[-][/c]每日账号可获得[c][ffff00]上限3个[-][/c]，[c][ffff00]圣诞老人的长袜[-][/c]每日账号可获得[c][ffff00]上限1个[-][/c]！',Speaker=893447,}",
  [168621] = "{id=168621,Text='甜饼人带着数不清的礼物箱来到了普隆德拉，想要礼物的话，就去与它交换吧！',Speaker=893447,}",
  [168622] = "{id=168622,Text='前往[c][ffff00]普隆德拉广场[-][/c]找到[c][ffff00]甜饼人[-][/c]即可参与活动，每次捐献可以获得「[c][ffff00]神秘礼物盒[-][/c]」x1。\n[c][ffff00]该活动帐号限定，每个帐号每天可捐献的最高次数为2次[-][/c]。',Speaker=893447,}",
  [168623] = "{id=168623,Text='Base等级≥25级的冒险者，前往普隆德拉，找到【[c][ffff00]乔克[-][/c]】，与他对话即可领取[c][ffff00]圣诞礼物[-][/c]1份，【[c][ffff00]该活动帐号限定，每个帐号仅可参与1次[-][/c]。】',Speaker=893447,}",
  [168688] = "{id=168688,Text='冒险者你来啦，要不要猜一猜我手里的灯谜啊？',Speaker=893448,}",
  [168689] = "{id=168689,Text='我这里的灯谜难度很高，但同样奖品也很丰厚哦！',Speaker=893448,}",
  [168690] = "{id=168690,Text='那我们开始答题获赠神秘礼盒吧！要立刻开始嘛？',Speaker=893448,Option='{先等一等,2},{开始猜灯谜,3}',}",
  [168691] = "{id=168691,Text='黑眼睛，蓝皮肤，尾巴短，耳朵长。',Speaker=893448,Option='{蓝疯兔,4},{疯兔,5},{枫叶疯兔,5}',}",
  [168692] = "{id=168692,Text='我不觉得这是正确答案呢。',Speaker=893448,Option='我再想想',}",
  [168693] = "{id=168693,Text='恭喜你答对啦，这是你的奖励。',Speaker=893448,}",
  [168694] = "{id=168694,Text='性情躁烈爆，常披黄皮袄，斐扬称大王，我说那是猫。',Speaker=893448,Option='{虎王,4},{月夜猫,5},{犬妖首领,5}',}",
  [168695] = "{id=168695,Text='似鸟又非鸟，有翅身无毛，一脸丑模样，专爱夜遨游',Speaker=893448,Option='{红蝙蝠,4},{克瑞米,5},{狮鹫兽,5}',}",
  [168696] = "{id=168696,Text='尖尖小嘴，细细小腿，拖条大尾，疑神疑鬼',Speaker=893448,Option='{蛇妖戈耳工,5},{希尔队长,5},{狐仙莱卡翁,4}',}",
  [168697] = "{id=168697,Text='小小家伙眼睛亮，花中采宝回家酿！',Speaker=893448,Option='{蜂后,5},{彩蝶精灵,5},{蜂兵,4}',}",
  [168699] = "{id=168699,Text='在这个特殊的日子里，也不知道那个拎巨锤的家伙在干嘛',Speaker=893467,}",
  [168700] = "{id=168700,Text='喂，要不你去帮我看看他有没有在好好得过节？如果能带回来一张照片，那就更好啦！',Speaker=893467,Option='没问题',}",
  [168701] = "{id=168701,Text='哎呀，有人偷拍，我们快换个地方吧！',Speaker=893442,}",
  [168702] = "{id=168702,Text='哈哈哈哈，很好，今年他终于是开窍了，我以后也不必为他操心了！',Speaker=893467,}",
  [168703] = "{id=168703,Text='谢谢你了。',Speaker=893467,}",
  [168705] = "{id=168705,Text='一年一度的情人节就要来了，帮助爱情天使完成任务，可以获得丰厚的奖励哦~',Speaker=893443,}",
  [168706] = "{id=168706,Text='活动期间，Base≥25级的冒险者通过[c][ffff00]爱情天使[-][/c]接取任务，拍下照片，即可获得奖励~',Speaker=893443,}",
  [168707] = "{id=168707,Text='共有5个连续的任务，每天只可以完成1个哦~完成所有5天任务，可以获得限定头饰[c][ffff00]一箭倾心[-][/c]',Speaker=893443,}",
  [168708] = "{id=168708,Text='元宵节当然不能没有猜灯谜啦~在元宵节当天(2/5)，[c][ffff00]月之代理人[-][/c]将会降临普隆德拉哦~',Speaker=893443,}",
  [168709] = "{id=168709,Text='Base≥25级的冒险者只要找到[c][ffff00]月之代理人[-][/c]，猜中所有5题灯谜，就可以获得限定尾饰[c][ffff00]团团圆圆[-][/c]',Speaker=893443,}",
  [168710] = "{id=168710,Text='情人节当然要甜甜蜜蜜！为了让冒险者们感受到更多的爱~爱情天使特意打开了[c][ffff00]甜蜜乐园[-][/c]',Speaker=893443,}",
  [168711] = "{id=168711,Text='2/9-2/23期间，冒险者通过热点中的[c][ffff00]甜蜜乐园[-][/c]参与活动，只要登录7日，就可以拿齐所有奖励~！',Speaker=893443,}",
  [168712] = "{id=168712,Text='怎样才能让情人节过得更有意义呢？那当然是在情人节当天结婚啦~不但有意义，还有大优惠！',Speaker=893443,}",
  [168713] = "{id=168713,Text='2/13-2/20期间，婚礼、婚纱半价特惠哦~赶紧带上你最爱的那个TA，一起步入婚姻的殿堂吧~！',Speaker=893443,}",
  [168714] = "{id=168714,Text='爱情天使很是好奇忽克连的情人节到底是怎么过的呢？这不，她正在找人帮她去看看~',Speaker=893443,}",
  [168715] = "{id=168715,Text='2/14-2/21期间，Base≥25级的冒险者帮助[c][ffff00]爱情天使[-][/c]完成任务，可以获得[c][ffff00]弗雷硬币[-][/c]等稀有奖励哦~',Speaker=893443,}",
  [168716] = "{id=168716,Text='卡普拉竟然要变卖忽克连送的礼物！虽然听起来有点别扭，但是谁都知道“男友赠送”这些东西，转让价真的很便宜！',Speaker=893443,}",
  [168717] = "{id=168717,Text='2/20-2/27期间，冒险者捐献物资给普隆德拉广场的[c][ffff00]卡普拉[-][/c]，可以获得一份[c][ffff00]忽克连的心意[-][/c]哦~',Speaker=893443,}",
  [168718] = "{id=168718,Text='忽克连可真是的，以为给我买东西，我就会开心~谁又在意这些物质上的东西呢~',Speaker=893449,}",
  [168719] = "{id=168719,Text='虽然他给的实在太多了，但是我还是想把这些东西处理掉，然后凑个首付的钱~跟他有一个爱的小屋',Speaker=893449,}",
  [168720] = "{id=168720,Text='冒险者，如果你有我需要的东西的话，我愿意拿忽克连的心意跟你交换哦~',Speaker=893449,}",
  [168846] = "{id=168846,Text='我的那些朋友只有透过[c][ffff00]幽灵相机[-][/c]的镜头才能看到它们！',Speaker=893426,}",
  [168847] = "{id=168847,Text='幽灵相机你知道吧？这是冒险者的必备技能！',Speaker=893426,}",
  [168848] = "{id=168848,Text='我听说，普隆德拉的冒险公会冒险技能导师罗兰，可以教会你！',Speaker=893426,}",
  [168849] = "{id=168849,Text='咳咳，说回我的朋友们。他们应该分开隐藏在雾之森林的各处！',Speaker=893426,}",
  [168850] = "{id=168850,Text='透过相机镜头找到它们后，[c][ffff00]按下快门[-][/c]，用幽灵相机的闪光灯逼它们显形！给他们一些教训！',Speaker=893426,}",
  [168851] = "{id=168851,Text='不过，[c][ffff00]他们并不是每次都会掉落我的珍藏[-][/c]。',Speaker=893426,}",
  [168852] = "{id=168852,Text='冒险者！找到它们！帮我抢回我的珍藏吧！',Speaker=893426,}",
  [168853] = "{id=168853,Text='哦~冒险者！你也要来参与吗？',Speaker=893465,Option='要做什么？',}",
  [168854] = "{id=168854,Text='自我介绍一下，我是本次周年庆活动，负责烟花相关的工程师。',Speaker=893465,}",
  [168855] = "{id=168855,Text='我们正在测试烟花的相关性能，有没有兴趣一起测试一下。',Speaker=893465,Option='{有兴趣,3},{转身离开,2}',}",
  [168856] = "{id=168856,Text='可惜！测试又多了一份危险……',Speaker=893465,}",
  [168857] = "{id=168857,Text='强大的冒险者，正好能够保护我们的安全，太好了！',Speaker=893466,}",
  [168858] = "{id=168858,Text='嘘~不要乱说话……（咳咳）戴好护目镜和安全头盔，我们要开始了！',Speaker=893465,}",
  [168859] = "{id=168859,Text='嗯……底座安装牢固，最大分贝值低于验收标准，合格！',Speaker=893466,Action={actionid=31},}",
  [168860] = "{id=168860,Text='呜~效果还真是不错呢！等到晚上，一定会更加出彩~',Speaker=893465,Option='晚上？',Action={actionid=31},}",
  [168861] = "{id=168861,Text='是的，冒险者。1/19 05:00 ~ 1/26 05:00期间，晚上19:00~21:00，在普隆德拉上空可以看到烟花秀哦~',Speaker=893466,Option='具体位置？',}",
  [168862] = "{id=168862,Text='就在这附近，冒险者要一起来热闹一下吗？',Speaker=893465,Option='当然',}",
  [168863] = "{id=168863,Text='欢迎欢迎！希望你能在周年庆玩得愉快！',Speaker=893465,Option='谢谢',Action={actionid=35},}",
  [168864] = "{id=168864,Text='这可不是个好兆头，把这一批烟花的型号记一下！',Speaker=893465,Option='咳咳！',Action={actionid=35},}",
  [168865] = "{id=168865,Text='还没有测试完，倒也不用这么紧张，还好有冒险者的保护！',Speaker=893466,Option='你拉住我的！',}",
  [168866] = "{id=168866,Text='不要在意这些细节，冒险者。经历过挫折才能发现最美丽的烟花~',Speaker=893465,Option='哪里还有烟花',}",
  [168867] = "{id=168867,Text='1/19 05:00 ~ 1/26 05:00期间，晚上19:00~21:00，在普隆德拉上空可以看到烟花秀哦！',Speaker=893465,Option='……',}",
  [168868] = "{id=168868,Text='希望晚上美丽的烟花，能弥补你这次的遗憾~',Speaker=893466,}",
  [168874] = "{id=168874,Text='1/1-2/1期间，冒险者通过热点中的[c][ffff00]波利心愿[-][/c]参与活动，完成波利的任务，即可获得大量[c][ffff00]「彩虹波利币」[-][/c]！\n除此之外，还可获得6周年庆头饰「告别钟声头饰自选券」和限定卡片「庆典赞歌卡片」！',Speaker=893463,}",
  [168875] = "{id=168875,Text='周年庆期间，每周五~每周日波利岛将刷新[c][ffff00][庆典飞龙][-][/c]！\n击败它，可获得[c][ffff00]「庆典魔力宝箱」x1[-][/c]，该宝箱为限定类道具，每个账号每天仅可获得1个。\n在第6次开启[c][ffff00]「庆典魔力宝箱」[-][/c]时，100%额外获赠[c][ffff00]「精炼头饰+15券」[-][/c]!',Speaker=893463,}",
  [168876] = "{id=168876,Text='1/1-1/8期间：\n①冒险者找到[c][ffff00]不会射箭的猎人[-][/c]，即可领取[c][ffff00]「彩虹波利币」x2[-][/c]\n②冒险者前往南门找到[c][ffff00]许愿树[-][/c]，完成许愿，可获得[c][ffff00]「彩虹波利币」x3[-][/c]！认真写下愿望，听说有机会实现噢~',Speaker=893463,}",
  [168877] = "{id=168877,Text='1/1-1/16期间，Base≥25级的冒险者帮助[c][ffff00]卡普拉服务人员[-][/c]完成7个系列任务，每个任务都可获得[c][ffff00]「彩虹波利币」x1[-][/c]！ \n【[c][ffff00]活动为账号限定，每个账号每天仅可参与1次[-][/c]。】',Speaker=893463,}",
  [168878] = "{id=168878,Text='1/5-1/12期间，冒险者找到[c][ffff00]霍克拉·银[-][/c]完成活动，每日可获得[c][ffff00]「彩虹波利币」x1[-][/c]！ \n【[c][ffff00]活动为账号限定，每个账号每天仅可参与1次[-][/c]。】',Speaker=893463,}",
  [168879] = "{id=168879,Text='1/19-1/26期间，冒险者找到[c][ffff00]不会射箭的猎人[-][/c]完成对话，可获得[c][ffff00]「彩虹波利币」x2[-][/c]！ \n【[c][ffff00]活动为账号限定，每个账号仅可参与1次[-][/c]。】',Speaker=893463,}",
  [168880] = "{id=168880,Text='1/19-1/26期间，冒险者找到[c][ffff00]亨特[-][/c]购买并使用「辣味汽水」，只要与同样使用了「辣味汽水」的冒险者进行任意双人动作，即可获得[c][ffff00]「彩虹波利币」x1[-][/c]！\n【[c][ffff00]活动为账号限定，每个账号每天仅可参与1次[-][/c]。】',Speaker=893463,}",
  [168881] = "{id=168881,Text='以上就是[c][ffff00]「彩虹波利币」[-][/c]所有获取途径了~\n错过活动也不要着急，可以在我这里购买少量的[c][ffff00]「彩虹波利币」[-][/c]噢~',Speaker=893463,}",
  [168888] = "{id=168888,Text='又到了我最爱的日子，你闻到了吗？',Speaker=893467,Option='{什么？,11},{闻到了,12}',}",
  [168889] = "{id=168889,Text='这种全世界都泛着甜蜜的味道~',Speaker=893467,}",
  [168890] = "{id=168890,Text='爱情所散发的荷尔蒙，比任何香水都醉人啊。',Speaker=893467,}",
  [168891] = "{id=168891,Text='没错，甜甜的恋爱的味道，每个人都能闻到吧。',Speaker=893467,}",
  [168892] = "{id=168892,Text='对了，还要拜托你帮我去刺探那些有情人的进展！',Speaker=893467,}",
  [168893] = "{id=168893,Text='我记得[c][ffff00]梦克罗[-][/c]，有一对至死不渝的甜蜜情侣呢。',Speaker=893467,}",
  [168894] = "{id=168894,Text='记得把他们的甜蜜模样[c][ffff00]拍下来[-][/c]，然后带给我看哟。',Speaker=893467,}",
  [168895] = "{id=168895,Text='人家没听清~',Speaker=893468,}",
  [168896] = "{id=168896,Text='再过来点嘛！',Speaker=893469,}",
  [168897] = "{id=168897,Text='亲爱的，不要再给我写情书了！',Speaker=893468,}",
  [168898] = "{id=168898,Text='为什么！你不喜欢吗？',Speaker=893469,}",
  [168899] = "{id=168899,Text='你每次都撕布条，我怕你没衣服穿了。',Speaker=893468,}",
  [168900] = "{id=168900,Text='原来是这样，你真体贴！',Speaker=893469,}",
  [168901] = "{id=168901,Text='我真想紧紧地裹着你，把你带回我的棺材里。',Speaker=893469,}",
  [168902] = "{id=168902,Text='我才想用尾巴卷着你，让我们永远也不分开呢。',Speaker=893468,}",
  [168903] = "{id=168903,Text='真是可怕的一段对话啊……',Emoji=1,}",
  [168904] = "{id=168904,Text='什么声音？',Speaker=893469,}",
  [168905] = "{id=168905,Text='亲爱的，又有讨厌的人来打扰我们了。',Speaker=893468,}",
  [168906] = "{id=168906,Text='要是换个时间，非得给他点厉害瞧瞧，今天我们就离他远点吧。',Speaker=893469,}",
  [168907] = "{id=168907,Text='宝贝，我又想到一句情诗了，我可以用楔形文字写给你哟。',Speaker=893469,}",
  [168908] = "{id=168908,Text='多么感人的爱情啊！竟然保持了几百年的热恋。',Speaker=893467,}",
  [168909] = "{id=168909,Text='你也很羡慕吧！',Speaker=893467,Option='{是的,21},{还是算了...,22}',}",
  [168910] = "{id=168910,Text='嘤嘤嘤，又是为美好爱情落泪的一天呢。',Speaker=893467,}",
  [168911] = "{id=168911,Text='什么嘛，你的要求可真高啊！',Speaker=893467,}",
  [168912] = "{id=168912,Text='你来啦！今天你恋爱了吗？',Speaker=893467,Option='{恋爱了,11},{没有,12}',}",
  [168913] = "{id=168913,Text='果然没错，你刚才走过来的时候，我已经闻到了。',Speaker=893467,}",
  [168914] = "{id=168914,Text='是比花蜜还要甜滋滋的味道呢，祝福你们！',Speaker=893467,}",
  [168915] = "{id=168915,Text='我能感应到，你的那一位很快就要出现咯。',Speaker=893467,}",
  [168916] = "{id=168916,Text='或许只需要一点点的勇气呢。',Speaker=893467,}",
  [168917] = "{id=168917,Text='对了，还要拜托你帮我去刺探那些有情人的进展！',Speaker=893467,}",
  [168918] = "{id=168918,Text='我记得[c][ffff00]天津町[-][/c]的樱花树下，有一对心心相印的情侣呢。',Speaker=893467,}",
  [168919] = "{id=168919,Text='记得把他们现在的模样[c][ffff00]拍下来[-][/c]，然后带给我看哟。',Speaker=893467,}",
  [168920] = "{id=168920,Text='小樱……',Speaker=893470,}",
  [168921] = "{id=168921,Text='真美啊……',Speaker=893471,}",
  [168922] = "{id=168922,Text='樱花真美啊。',Speaker=893471,}",
  [168923] = "{id=168923,Text='可惜很快就凋零了。',Speaker=893471,}",
  [168924] = "{id=168924,Text='不，我们也可以把樱花珍藏起来。',Speaker=893470,}",
  [168925] = "{id=168925,Text='不……别这样，秀明。',Speaker=893471,}",
  [168926] = "{id=168926,Text='被强行留住的樱花，已经不是原来的它了。',Speaker=893471,}",
  [168927] = "{id=168927,Text='可我又怎么能眼睁睁看着它凋零呢？',Speaker=893470,}",
  [168928] = "{id=168928,Text='别想那些了，至少这一刻我们在一起，这就够了。',Speaker=893471,}",
  [168929] = "{id=168929,Text='对，至少此刻我还能看着你。',Speaker=893470,}",
  [168930] = "{id=168930,Text='我们继续赏樱吧。',Speaker=893471,}",
  [168931] = "{id=168931,Text='原来是你，节日快乐啊。',Speaker=893470,}",
  [168932] = "{id=168932,Text='既然小樱都这么说，那就节日快乐吧。',Speaker=893471,}",
  [168933] = "{id=168933,Text='很高兴能再次见到你呢，祝你幸福。',Speaker=893471,}",
  [168934] = "{id=168934,Text='真是让人感慨的爱情。',Speaker=893467,}",
  [168935] = "{id=168935,Text='曾经拥有和天长地久，你认为哪个更重要？',Speaker=893467,Option='{曾经拥有,21},{天长地久,22}',}",
  [168936] = "{id=168936,Text='是啊，那一刹那的璀璨，如同烟花一般令人着迷，即便消失也会永远留在心间。',Speaker=893467,}",
  [168937] = "{id=168937,Text='没错，就像两座依偎的山，在时光的见证下永远伫立着，彼此守望。',Speaker=893467,}",
  [168938] = "{id=168938,Text='你来啦！今天有没有对爱的人表白呢？',Speaker=893467,Option='{有,11},{没有,12}',}",
  [168939] = "{id=168939,Text='很好！爱就要大声说出来嘛。',Speaker=893467,}",
  [168940] = "{id=168940,Text='你和听到的人都会开心呢。',Speaker=893467,}",
  [168941] = "{id=168941,Text='哇，外表看不出来，没想到你这么羞涩呢。',Speaker=893467,}",
  [168942] = "{id=168942,Text='大概在心中藏了太久，反倒很难说出口吧。',Speaker=893467,}",
  [168943] = "{id=168943,Text='对了，还要拜托你帮我去刺探那些有情人的进展！',Speaker=893467,}",
  [168944] = "{id=168944,Text='我记得[c][ffff00]依斯鲁得岛[-][/c]，有一位爱在心中口难开的男士呢。',Speaker=893467,}",
  [168945] = "{id=168945,Text='记得把他和暗恋之人现在的模样[c][ffff00]拍下来[-][/c]，然后带给我看哟。',Speaker=893467,}",
  [168946] = "{id=168946,Text='请问……',Speaker=893473,}",
  [168947] = "{id=168947,Text='这个人好奇怪喔。',Speaker=893472,}",
  [168948] = "{id=168948,Text='从刚才开始，这个人就一直站在旁边。',Speaker=893472,}",
  [168949] = "{id=168949,Text='你看他是不是很奇怪呀？',Speaker=893472,Option='他好像有话要说？',}",
  [168950] = "{id=168950,Text='抱歉，打扰一下，其实我想问……',Speaker=893473,}",
  [168951] = "{id=168951,Text='您好，请问有什么需要吗？今天买花有折扣哟。',Speaker=893472,}",
  [168952] = "{id=168952,Text='啊……那、那我买一只玫瑰吧。',Speaker=893473,}",
  [168953] = "{id=168953,Text='好的，希望你和你的她能过得开心。',Speaker=893472,}",
  [168954] = "{id=168954,Text='我、我没有，那个……花送、送给你吧。',Speaker=893473,}",
  [168955] = "{id=168955,Text='啊？你不想要了吗？',Speaker=893472,}",
  [168956] = "{id=168956,Text='不是，我是想说……',Speaker=893473,}",
  [168957] = "{id=168957,Text='什么？',Speaker=893472,Emoji=19,}",
  [168958] = "{id=168958,Text='这里的景色很好，我能站在你身旁画画吗？',Speaker=893473,Emoji=18,}",
  [168959] = "{id=168959,Text='当然可以，您请便吧。',Speaker=893472,}",
  [168960] = "{id=168960,Text='请问您在做什么呢？',Speaker=893472,Option='给有情人拍照',}",
  [168961] = "{id=168961,Text='啊啊啊……你……你怎么可以。',Speaker=893473,}",
  [168962] = "{id=168962,Text='原来是这个意思啊，我好像明白了。',Speaker=893472,}",
  [168963] = "{id=168963,Text='您可以再拍一张呢，这位先生似乎能画出很漂亮的画。',Speaker=893472,}",
  [168964] = "{id=168964,Text='真的吗？我真的不是在做梦吗？',Speaker=893473,Emoji=7,}",
  [168965] = "{id=168965,Text='冒险者，谢谢你！！！',Speaker=893473,}",
  [168966] = "{id=168966,Text='那份埋在心中的爱恋，颤抖的双唇却无法说出。',Speaker=893467,}",
  [168967] = "{id=168967,Text='真是让人动容的情感啊，如果是你的话，会勇敢说出自己的爱吗？',Speaker=893467,Option='{会!,21},{不会...,22}',}",
  [168968] = "{id=168968,Text='没错！只要一点点勇气，朝前多走一步，就能收获美好的恋情呢。',Speaker=893467,}",
  [168969] = "{id=168969,Text='唔，就像青涩的果酒，需要经过时间去发酵吗？',Speaker=893467,}",
  [168970] = "{id=168970,Text='你来啦，今天也很有精神呢！',Speaker=893467,}",
  [168971] = "{id=168971,Text='说起来很多有情人都经历过分分合合，你也有过类似的经历吗？',Speaker=893467,Option='{有,11},{没有,12}',}",
  [168972] = "{id=168972,Text='虽然短暂地分开了，但爱意却没有消失。',Speaker=893467,}",
  [168973] = "{id=168973,Text='兜兜转转，最后还是会相遇呢，真是让人感动啊。',Speaker=893467,}",
  [168974] = "{id=168974,Text='人生漫漫，旅途上总有人会与你同路而行，可惜也有人中途退场。',Speaker=893467,}",
  [168975] = "{id=168975,Text='但无论如何，两人一起欣赏过途中的风景，那就已经足够了。',Speaker=893467,}",
  [168976] = "{id=168976,Text='对了，还要拜托你帮我去刺探那些有情人的进展！',Speaker=893467,}",
  [168977] = "{id=168977,Text='我记得在[c][ffff00]生命体研究所[-][/c]，有两位经历坎坷的情侣呢。',Speaker=893467,}",
  [168978] = "{id=168978,Text='记得把他们现在的模样[c][ffff00]拍下来[-][/c]，然后带给我看哟。',Speaker=893467,}",
  [168979] = "{id=168979,Text='应该过去吗？',Speaker=893474,}",
  [168980] = "{id=168980,Text='好想靠近点……',Speaker=893475,}",
  [168981] = "{id=168981,Text='我早已忘了自己的姓名和生平，我的心似乎也被冰封。',Speaker=893474,}",
  [168982] = "{id=168982,Text='但不知为何，看到那边少女的第一眼，心脏竟然重新开始跳动了般。',Speaker=893474,}",
  [168983] = "{id=168983,Text='我终于鼓起了勇气，那个……请问你的名字是？',Speaker=893475,}",
  [168984] = "{id=168984,Text='抱歉！',Speaker=893474,Emoji=18,}",
  [168985] = "{id=168985,Text='对、对不起，打扰了。',Speaker=893475,Emoji=18,}",
  [168986] = "{id=168986,Text='不，我的意思是这件事应该由我主动来做，美丽的小姐，我是否有幸知道你的芳名？',Speaker=893474,}",
  [168987] = "{id=168987,Text='我叫……对不起，我似乎忘了自己的名字。',Speaker=893475,Action={9},}",
  [168988] = "{id=168988,Text='这并不重要，因为我也忘却了名字，可不知为何，你对我而言却分外熟悉。',Speaker=893474,}",
  [168989] = "{id=168989,Text='请您不要认为我是个油嘴滑舌的人，这都是我的真心话。',Speaker=893474,}",
  [168990] = "{id=168990,Text='我相信……',Speaker=893475,}",
  [168991] = "{id=168991,Text='因为这也是我想说的话。',Speaker=893475,}",
  [168992] = "{id=168992,Text='这位冒险者，请留步，你似乎对我们很熟悉？',Speaker=893474,}",
  [168993] = "{id=168993,Text='我和这位美丽的小姐以前认识吗？',Speaker=893474,Option='{认识,21},{不认识,22}',}",
  [168994] = "{id=168994,Text='原来我们以前真的是旧相识。',Speaker=893475,}",
  [168995] = "{id=168995,Text='或许我曾经也深爱着你，即便我此刻忘却了自己的名字，心脏却依旧因你而炙热。',Speaker=893474,}",
  [168996] = "{id=168996,Text='难道是我的错觉吗？',Speaker=893475,}",
  [168997] = "{id=168997,Text='没关系，至少从此刻开始，我们可以认识了。',Speaker=893474,}",
  [168998] = "{id=168998,Text='你说得对……',Speaker=893475,Emoji=2,}",
  [168999] = "{id=168999,Text='真感动啊，真正相爱的人，即便再次遇见，依旧还会因对方而变得脸蛋通红。',Speaker=893467,}",
  [169000] = "{id=169000,Text='你认为爱情有保鲜期吗？',Speaker=893467,Option='{有,31},{没有,32}',}",
  [169001] = "{id=169001,Text='唔，对于许多人而言，或许爱情的确会变质。',Speaker=893467,}",
  [169002] = "{id=169002,Text='但作为爱情天使，我更希望这个保质期的期限能贯彻整个生命的周期，',Speaker=893467,}",
  [169003] = "{id=169003,Text='我喜欢你的回答~',Speaker=893467,}",
  [169004] = "{id=169004,Text='祝你爱的热烈而持久，如奔腾大海，也如涓涓细流。',Speaker=893467,}",
  [169005] = "{id=169005,Text='见识了各种各样甜蜜的爱情，真是让人欣慰啊。',Speaker=893467,}",
  [169006] = "{id=169006,Text='爱情让人喜让人优，但人类的情绪总是多种多样，也经常会觉得孤单呢，[PlayerName]你也会吗？',Speaker=893467,Option='{会,11},{不会,12}',}",
  [169007] = "{id=169007,Text='大陆这么小，而我们却如此渺小，时光这么久，我们所经历的只是短短一瞬。',Speaker=893467,}",
  [169008] = "{id=169008,Text='好在孤独并不总是坏事，它能让我们看清自我，也能让我们更珍惜每一次相遇。',Speaker=893467,}",
  [169009] = "{id=169009,Text='天呐，你是如此的幸运。',Speaker=893467,}",
  [169010] = "{id=169010,Text='或许你已足够有趣，所以才能发现藏在生活各处的惊喜。',Speaker=893467,}",
  [169011] = "{id=169011,Text='对了，还要拜托你帮我去寻找一只暖心熊，它总是带给别人温暖，却忽略了自己。',Speaker=893467,}",
  [169012] = "{id=169012,Text='我记得它在[c][ffff00]姜饼城[-][/c]，希望你能尽快找到它呢。',Speaker=893467,}",
  [169013] = "{id=169013,Text='记得和它[c][ffff00]合照[-][/c]一张，然后带给我看哟。',Speaker=893467,}",
  [169014] = "{id=169014,Text='抱歉，刚才发了会呆，你是来找我的吗？',Speaker=893476,Option='当然了。',}",
  [169015] = "{id=169015,Text='我知道啦，你也想要抱抱吗？',Speaker=893476,}",
  [169016] = "{id=169016,Text='得到了我的抱抱，你就有勇气去告白啦，去追求幸福吧。',Speaker=893476,Option='{那你呢？,0},{你也会孤单,0}',}",
  [169017] = "{id=169017,Text='我……我没关系的，我的职责就是让大家感觉到温暖啊。',Speaker=893476,}",
  [169018] = "{id=169018,Text='就算你们都离开了，但只要你们幸福，我就开心，这就是我存在的意义。',Speaker=893476,Option='安慰暖心熊',}",
  [169019] = "{id=169019,Text='好温暖……原来主动被关心是这种感觉。',Speaker=893476,}",
  [169020] = "{id=169020,Text='谢谢你，冒险者，你让我感受到了温暖，祝你节日快乐！',Speaker=893476,Emoji=17,}",
  [169021] = "{id=169021,Text='现在你可以去寻找你的幸福啦。',Speaker=893476,Option='我们一起过节吧。',}",
  [169022] = "{id=169022,Text='真的吗？我不是在做梦吧！',Speaker=893476,}",
  [169023] = "{id=169023,Text='这么多年还是第一次有人陪熊熊一起过节。',Speaker=893476,}",
  [169024] = "{id=169024,Text='这是我最快乐的一天啦！啦啦啦，我不再孤单啦。',Speaker=893476,Emoji=2,}",
  [169025] = "{id=169025,Text='我刚才是不是应该说茄子？呃，还是别的什么？',Speaker=893476,Emoji=19,}",
  [169026] = "{id=169026,Text='对于这种事，我不是太有经验。',Speaker=893476,Option='别紧张~',}",
  [169027] = "{id=169027,Text='嘿嘿，你的姿势摆的真不错，我应该多向你学习。',Speaker=893476,}",
  [169028] = "{id=169028,Text='情人节，真是让人幸福的节日啊！',Speaker=893476,}",
  [169029] = "{id=169029,Text='真好啊，情人节并不一定只有爱情，还有其他许许多多的感情。',Speaker=893467,}",
  [169030] = "{id=169030,Text='今天你开心吗？',Speaker=893467,Option='{开心,21},{不开心,22}',}",
  [169031] = "{id=169031,Text='太棒了，我喜欢你的笑容！',Speaker=893467,}",
  [169032] = "{id=169032,Text='这个送给你，祝你永远开心。',Speaker=893467,}",
  [169033] = "{id=169033,Text='那就抱抱自己吧，你这么棒，应该得到夸奖。',Speaker=893467,}",
  [169034] = "{id=169034,Text='你猜怎么着，我要送最优秀的冒险者一份礼物~',Speaker=893467,}",
  [169035] = "{id=169035,Text='喏，希望你能拥有美好的一天！',Speaker=893467,}",
  [169046] = "{id=169046,Text='散发迷人的芬芳，吐露埋在心间的声音。',Speaker=893467,}",
  [169047] = "{id=169047,Text='又到了我最爱的日子，我的目标是，让大陆充满爱~',Speaker=893467,}",
  [169048] = "{id=169048,Text='人家可不是什么偷窥狂，人家只是想为别人的爱情感动落泪罢了。',Speaker=893467,}",
  [169049] = "{id=169049,Text='[c][ffff00]【该任务在同账号下只能完成一次，请确认账号角色后谨慎接取！】[-][/c]',Speaker=893467,Option='{好的,11},{再想想,12}',}",
  [169051] = "{id=169051,Text='[c][ffff00][PlayerName][-][/c]，你来啦~一年一度的“女王节”会眷顾每一位特别的冒险者噢~',Speaker=893482,}",
  [169052] = "{id=169052,Text='这个送给你，希望这份小小的礼物可以带给你温暖~',Speaker=893482,Option='{谢谢,2},{过会再领,3}',}",
  [169053] = "{id=169053,Text='[c][ffff00][PlayerName][-][/c]，你来啦~你知道吗？今天是“树精保护日”！',Speaker=893482,}",
  [169054] = "{id=169054,Text='这个送给你，希望这份小小的礼物可以带给你快乐~',Speaker=893482,Option='{谢谢,2},{过会再领,3}',}",
  [169055] = "{id=169055,Text='[c][ffff00][PlayerName][-][/c]，你来啦~你知道吗？今天是“白色情人节”！',Speaker=893482,}",
  [169056] = "{id=169056,Text='这个送给你，希望这份小小的礼物可以带给你幸福~',Speaker=893482,Option='{谢谢,2},{过会再领,3}',}",
  [169057] = "{id=169057,Text='呀~看来你是想要制作一份[c][ffff00]情谊巧克力[-][/c]呢？需要的材料是：[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂蜜×5[-][/c]以及[c][ffff00]奶油榛果x2[-][/c]哦~只需这些我们就会帮你做好呦~',Speaker=893492,Option='{B格猫巧克力?,5},{委托制作,2},{默默走开,1}',}",
  [169058] = "{id=169058,Text='哎呀呀~制作[c][ffff00]情谊巧克力[-][/c]需要[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂蜜×5[-][/c]以及[c][ffff00]奶油榛果x2[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我们呦~',Speaker=893492,}",
  [169059] = "{id=169059,Text='吼哦？~看你洋溢着幸福的表情，想[c][ffff00]亲手[-][/c]为心中最喜欢的那个人制作一份[c][ffff00]本命巧克力[-][/c]吧！好好好！我先告诉你配方，需要：[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂胶×5[-][/c]以及[c][ffff00]暖心料理x2[-][/c]哦~以及制作过程中需要播放一张[c][ffff00]我想念你[-][/c]唱片，脑海中边想象着那个你最喜欢最喜欢的人并将那份爱意一起放入，就能做出全世界独一无二的[c][ffff00]本命巧克力[-][/c]了！',Speaker=893492,Option='{B格猫巧克力?,5},{立即动手,2},{默默走开,1}',}",
  [169060] = "{id=169060,Text='哎呀呀，先不要心急哦。制作[c][ffff00]本命巧克力[-][/c]需要唱片[c][ffff00]我想念你x1[-][/c]、[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂胶×5[-][/c]以及[c][ffff00]暖心料理x2[-][/c]哦，耐心集齐材料后再来找我吧~',Speaker=893492,}",
  [169061] = "{id=169061,Text='呀~看来你是想要制作一张[c][ffff00]恋之双子星卡片[-][/c]呢？需要的材料是：[c][ffff00]暖暖的爱意×1[-][/c]、[c][ffff00]幸福的爱意×1[-][/c]哦~只需这些我们就会帮你做好呦~',Speaker=893492,Option='{如何获得,5},{立即动手,2},{默默走开,1}',}",
  [169062] = "{id=169062,Text='哎呀呀~制作[c][ffff00]恋之双子星卡片[-][/c]需要[c][ffff00]暖暖的爱意×1[-][/c]、[c][ffff00]幸福的爱意×1[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我们呦~',Speaker=893492,}",
  [169063] = "{id=169063,Text='呀~看来你是想要一块[c][ffff00]B格猫巧克力[-][/c]呢？只需：[c][ffff00]Zeny×520[-][/c]哦~只需这些我们就会帮你做好呦~',Speaker=893492,Option='{立即动手,2},{默默走开,1}',}",
  [169064] = "{id=169064,Text='哎呀呀~制作[c][ffff00]B格猫巧克力[-][/c]需要[c][ffff00]Zeny×520[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我们呦~',Speaker=893492,}",
  [169066] = "{id=169066,Text='太难了……年复一年，日复一日，我在这里讲着各种各样的故事……可关于春天，关于远方，我却没有什么故事，也找不到灵感……',Speaker=893483,}",
  [169067] = "{id=169067,Text='噢！伟大的冒险者，你走遍了米德加尔特大陆每一处，想必你知道不少适合取材的好地方！请帮帮我，冒险者，去发现春天吧！',Speaker=893483,Emoji=24,}",
  [169068] = "{id=169068,Text='我这里有几个来自卡普拉的推荐地点，不妨去看看吧',Speaker=893483,Option='好',Emoji=23,}",
  [169069] = "{id=169069,Text='(第1个推荐地点：四季如春的[c][ffff00]普隆德拉南门[-][/c])',}",
  [169070] = "{id=169070,Text='(那里除了数不清的波利，还有什么呢？)',}",
  [169071] = "{id=169071,Text='这是……懒散的野猪骑士和勤奋的护卫队？想必这种反差，会有一定戏剧冲突可以作为灵感！',Speaker=893483,}",
  [169072] = "{id=169072,Text='嗯，今天的灵感已经找到了！明天再来吧！',Speaker=893483,}",
  [169073] = "{id=169073,Text='你来啦~让我看看第2个推荐地点……噢，是[c][ffff00]普隆德拉西门[-][/c]！',Speaker=893483,}",
  [169074] = "{id=169074,Text='听说，西门会出现极高音乐天赋的摇滚蝗虫~真想跟它较量一下呢~不过，要小心调皮捣蛋的溜溜猴……不少冒险者都曾在西门滑到……',Speaker=893483,Option='那我出发了！',Emoji=23,}",
  [169075] = "{id=169075,Text='哇~这真是难得一见的风景！每一个故事都离不了背景的烘托，环境的渲染，这或许能作为一个不错的参考！',Speaker=893483,}",
  [169076] = "{id=169076,Text='你来啦~让我看看下一个推荐地点……噢，是[c][ffff00]天津町[-][/c]！',Speaker=893483,}",
  [169077] = "{id=169077,Text='让我看看推荐理由：“樱花盛开浪漫之都，其最大的樱花树见证了许多情侣互相许诺的瞬间……”听起来，很有感觉嘛！',Speaker=893483,Option='那我出发了！',Emoji=23,}",
  [169078] = "{id=169078,Text='真好啊，这棵大树见证了过去、现在，甚至可能还有未来的故事，值得记录下来！希望我也能创造更多有趣的故事呢……',Speaker=893483,}",
  [169079] = "{id=169079,Text='你来啦~下一个推荐地点……噢，是[c][ffff00]绵绵云[-][/c]！',Speaker=893483,}",
  [169080] = "{id=169080,Text='“遥望异世界的岛屿，前往罗扎纳的必经之路。从这里开始，你将被多兰族以及数不尽的猫毛所围绕！”',Speaker=893483,}",
  [169081] = "{id=169081,Text='啊~不行了，鼻子已经开始发痒了！只有麻烦你了，冒险者。我实在是……对猫毛过敏！',Speaker=893483,Option='那我出发了！',Emoji=23,}",
  [169082] = "{id=169082,Text='这也太治愈了吧！彩虹、云朵、猫！比春天更温暖更治愈啊',Speaker=893483,}",
  [169083] = "{id=169083,Text='你来啦~今天去[c][ffff00]王室狩猎场[-][/c]看看吧！',Speaker=893483,}",
  [169084] = "{id=169084,Text='听说，那里有一把“巨剑”！惊天奇观什么的，最能吸引观众无限的遐想！',Speaker=893483,}",
  [169085] = "{id=169085,Text='嘿嘿，一定要拍下“巨剑”的全景！拜托你了，冒险者！',Speaker=893483,Option='那我出发了！',Emoji=23,}",
  [169086] = "{id=169086,Text='虽然只是看着照片，却能隐约感受到它的威严……等等，这真的适合“春天”这个主题嘛？',Speaker=893483,Emoji=8,}",
  [169087] = "{id=169087,Text='你来啦~素材已经收集差不多！现在就缺一位主人公的形象了！',Speaker=893483,}",
  [169088] = "{id=169088,Text='让我想想……威武高大的骑士，难以琢磨的大法师，或者是名不见经传的落魄武者……好像都与这个主题不太契合……',Speaker=893483,Option='最普通的人？',}",
  [169089] = "{id=169089,Text='最普通的人，最平凡的故事，听起来很容易产出共鸣！你有什么推荐人选吗？',Speaker=893483,Option='卡普拉！',}",
  [169090] = "{id=169090,Text='不错的主意！甚至连卡普拉自己也不会想到她提供的素材，会以她为中心展开一场冒险！',Speaker=893483,Emoji=11,}",
  [169091] = "{id=169091,Text='不过，我有一个特别的请求……请拍一张卡普拉辛勤工作的照片吧！有画面才有更多的想象嘛！',Speaker=893483,Option='那我出发了！',Emoji=23,}",
  [169092] = "{id=169092,Text='不亏是最专业服务人员，哪怕被这么近拍摄，也丝毫不会影响她的工作！',Speaker=893483,}",
  [169093] = "{id=169093,Text='所有准备工作都已完成！明天来见证最终的结果吧！',Speaker=893483,}",
  [169094] = "{id=169094,Text='你来啦~多亏了你这几天的帮助！现在，万事俱备！',Speaker=893483,}",
  [169095] = "{id=169095,Text='那么，今天我们就来讲讲普隆德拉最伟大的人——卡普拉！',Speaker=893483,}",
  [169096] = "{id=169096,Text='故事讲到这里，冒险者，我先去睡一觉！',Speaker=893483,Option='什么！',}",
  [169097] = "{id=169097,Text='啊~不行不行~这春天也太温暖了……',Speaker=893483,}",
  [169098] = "{id=169098,Text='不要心急，冒险者！一个好故事娓娓道来，冒险也是，先要学会享受！',Speaker=893483,}",
  [169099] = "{id=169099,Text='好啦~丰厚的报酬给你！我要去享受春天了~',Speaker=893483,}",
  [169100] = "{id=169100,Text='好心的冒险者，你收集到了[c][ffff00]奶油芭娜娜×1[-][/c]了吗？\n可以跟我换取「来自女武神的馈赠」x5、「金质勋章」x5、「莫拉硬币」x5、「B格猫福利券·Ⅰ」x5呢！',Speaker=893484,Option='{给,2},{默默走开,1}',}",
  [169101] = "{id=169101,Text='哎呀呀~是[c][ffff00]奶油芭娜娜x1[-][/c]哦!好可惜…你身上没有呢！\n在[c][ffff00]普隆德拉西门[-][/c]击败[c][ffff00]贪吃仓鼠[-][/c]会有概率掉落哦~',Speaker=893484,}",
  [169104] = "{id=169104,Text='古城真的很大……可没想到普隆德拉也太豪华了吧！',Speaker=893485,}",
  [169105] = "{id=169105,Text='不止是漂浮的气球，还是特有的花朵，或是波利咖啡厅的波利，我都想带回古城！',Speaker=893485,}",
  [169106] = "{id=169106,Text='我需要你的帮助，冒险者！如果你有什么特色的小道具，就来跟我交换礼物吧！',Speaker=893485,Option='好',Emoji=21,}",
  [169107] = "{id=169107,Text='救命呀！仓鼠要冲过来抢夺我的香蕉了！自从普隆德拉西门成为了踏春圣地，我的香蕉库存就越来越少了……',Speaker=893484,Emoji=1,}",
  [169108] = "{id=169108,Text='请帮帮我，善良的冒险者！击败可恶的[c][ffff00]贪吃仓鼠[-][/c]，帮我抢回珍贵的[c][ffff00]奶油芭娜娜[-][/c]吧！',Speaker=893484,}",
  [169109] = "{id=169109,Text='虽然我没有什么宝藏，但是在西门日积月累捡拾道具，也有一大堆道具可以作为报酬！',Speaker=893484,}",
  [169110] = "{id=169110,Text='又到了踏春时节，记得出门走走，去感受春天哦~',Speaker=893482,}",
  [169111] = "{id=169111,Text='活动期间，Base≥25级的冒险者通过[c][ffff00]卡洛·史蒂芬[-][/c]接取任务，完成任务，即可获得奖励~',Speaker=893482,}",
  [169112] = "{id=169112,Text='共有5个连续的任务，每天只可以完成1个哦~完成所有5天任务，可以获得限定头饰[c][ffff00]春日低语[-][/c]',Speaker=893482,}",
  [169113] = "{id=169113,Text='白色情人节当然也要甜甜蜜蜜！为了让冒险者们感受到更多的爱~[c][ffff00]恋之双子星[-][/c]特意来到了普隆德拉~',Speaker=893482,}",
  [169114] = "{id=169114,Text='3/14-3/21期间，冒险者可委托[c][ffff00]恋之双子星[-][/c]制作充满爱意特殊巧克力！对其他冒险者使用后，能收获到「暖暖的爱意」或「幸福的爱意」！',Speaker=893482,}",
  [169115] = "{id=169115,Text='收集「暖暖的爱意」和「幸福的爱意」，可委托[c][ffff00]恋之双子星[-][/c]制作限定卡片「恋之双子星卡片」哦！',Speaker=893482,}",
  [169116] = "{id=169116,Text='没想到大名鼎鼎的[c][ffff00]吟游诗人[-][/c]也会有找不到灵感的一天~帮助他，去寻找关于春天的灵感吧~',Speaker=893482,}",
  [169117] = "{id=169117,Text='3/9-4/1期间，Base≥25级的冒险者通过[c][ffff00]吟游诗人[-][/c]接取任务，完成任务，即可获得奖励~',Speaker=893482,}",
  [169118] = "{id=169118,Text='共有7个连续的任务，每天只可以完成1个哦~',Speaker=893482,}",
  [169119] = "{id=169119,Text='自从西门成为了踏春圣地，大量的[c][ffff00]贪吃仓鼠[-][/c]疯狂涌入西门！这可急坏了溜溜猴！',Speaker=893482,}",
  [169120] = "{id=169120,Text='3/16-3/23期间，冒险者前往[c][ffff00]普隆德拉西门[-][/c]击败[c][ffff00]贪吃仓鼠[-][/c]，即可获得溜溜猴的最爱[c][ffff00]奶油芭娜娜[-][/c]！',Speaker=893482,}",
  [169121] = "{id=169121,Text='把[c][ffff00]奶油芭娜娜[-][/c]交给哭泣的溜溜猴，可获得丰厚的奖励哦！',Speaker=893482,}",
  [169122] = "{id=169122,Text='勤劳的爱丽丝可不会放过任何一处的灰尘！哪怕是远在他方的普隆德拉也不例外！',Speaker=893482,}",
  [169123] = "{id=169123,Text='3/23-3/30期间，冒险者只要上交道具给[c][ffff00]爱丽丝女仆[-][/c]，即可获得[c][ffff00]「爱丽丝的谢礼」[-][/c]哦~',Speaker=893482,}",
  [169124] = "{id=169124,Text='[c][ffff00]暖暖的爱意[-][/c]：对其他冒险者使用[c][ffff00]「情谊巧克力」[-][/c]后，可以获得哦~\n[c][ffff00]幸福的爱意[-][/c]：对其他冒险者使用[c][ffff00]「本命巧克力」[-][/c]后，可以获得哦~',Speaker=893492,}",
  [169125] = "{id=169125,Text='[c][ffff00]B格猫巧克力[-][/c]只需花费[c][ffff00]Zeny×520[-][/c]委托我制作哦~如果你需要的话，跟我对话就好啦~',Speaker=893492,}",
  [169128] = "{id=169128,Text='搭档，你收集到了[c][ffff00]精粹之水x1[-][/c]了吗？\n可以跟我换取「圣水祝福」x3、「幸运水滴」x1哦~',Speaker=893510,}",
  [169129] = "{id=169129,Text='哎呀呀~是[c][ffff00]精粹之水x1[-][/c]哦!好可惜…你身上没有呢！\n击败[c][ffff00]水属性魔物[-][/c]会有概率掉落哦~',Speaker=893510,}",
  [169130] = "{id=169130,Text='呀~看来你是想要交换一张[c][ffff00]圣水祝福卡片[-][/c]呢？需要的材料是：[c][ffff00]庆典花环×3[-][/c]、[c][ffff00]庆典圣水×3[-][/c]哦~只需要这些，我就能跟你交换了！',Speaker=893510,Option='{如何获得,5},{立即动手,2},{默默走开,1}',}",
  [169131] = "{id=169131,Text='哎呀呀~交换[c][ffff00]圣水祝福卡片[-][/c]需要[c][ffff00]庆典花环×3[-][/c]、[c][ffff00]庆典圣水×3[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我呦~',Speaker=893510,}",
  [169132] = "{id=169132,Text='[c][ffff00]庆典花环、庆典圣水[-][/c]：对其他冒险者使用[c][ffff00]「圣水祝福」[-][/c]后，概率获得其中之一哦~\n[c][ffff00]圣水祝福[-][/c]：击败[c][ffff00]水属性魔物[-][/c]后，收集[c][ffff00]精粹之水[-][/c]交给我；我将送给你特制的圣水祝福！',Speaker=893510,}",
  [169139] = "{id=169139,Text='嘿，你是来参加“幸运之蛋”活动吗？只要寻找到幸运之蛋，就能获得好运！这可是一年一度的限定活动哦~',Speaker=893486,Option='准备好了！',}",
  [169140] = "{id=169140,Text='那么，开始今天的冒险：\n今天的“幸运之蛋”藏在[c][ffff00]西边的海滩上[-][/c]！',Speaker=893486,Option='出发！',}",
  [169141] = "{id=169141,Text='吼吼~太棒了！“幸运之蛋”被你找到了！这是你的奖励！',}",
  [169142] = "{id=169142,Text='那么，开始今天的冒险：\n今天的“幸运之蛋”藏在[c][ffff00]海洋剧场附近[-][/c]！',Speaker=893486,Option='出发！',}",
  [169143] = "{id=169143,Text='那么，开始今天的冒险：\n今天的“幸运之蛋”藏在[c][ffff00]偷懒的辛格拉附近[-][/c]！',Speaker=893486,Option='出发！',}",
  [169144] = "{id=169144,Text='我的占卜，独一无二~',Speaker=893511,}",
  [169145] = "{id=169145,Text='哦？是一位落单的客人。要来占卜吗？',Speaker=893511,Option='准吗？',}",
  [169146] = "{id=169146,Text='当然。也许你只见过会占卜的猫。可会魔法的湖主，占卜也一样靠谱。来试试吧~或许，会有意想不到的结果呢。',Speaker=893511,}",
  [169147] = "{id=169147,Text='冒险者，请帮帮我！',Speaker=893512,Option='怎么了？',}",
  [169148] = "{id=169148,Text='因为庆典中消耗了大量的材料，让本就不富裕的我们更加雪上加霜！哪怕是我们引以为傲的哥布灵面具都没有足够的材料可以制作！',Speaker=893512,Emoji=24,}",
  [169149] = "{id=169149,Text='如果你身上有多余的材料，请跟我交换吧！',Speaker=893512,}",
  [169150] = "{id=169150,Text='一年一度的春日庆典开幕！今年的哥布灵们要举办一场盛大春日庆典！',Speaker=893514,}",
  [169151] = "{id=169151,Text='活动期间，Base≥25级的冒险者通过[c][ffff00]托拉基[-][/c]接取任务，完成任务，即可获得奖励~',Speaker=893514,}",
  [169152] = "{id=169152,Text='共有5个连续的任务~',Speaker=893514,}",
  [169153] = "{id=169153,Text='清明节是斐扬村的一个古老节日，吟游诗人伦纳德将向冒险者讲述关于清明节的传统习俗……',Speaker=893514,}",
  [169154] = "{id=169154,Text='4/5~4/12期间，Base≥25级的冒险者通过[c][ffff00]吟游诗人伦纳德[-][/c]接取任务，完成任务，即可获得奖励~',Speaker=893514,}",
  [169155] = "{id=169155,Text='发明小天才库拉拉为了在春日庆典上争取更多出场机会，它需要各位冒险者的帮助。当然，它也准备了特别的礼物给热心的冒险者们。',Speaker=893514,}",
  [169156] = "{id=169156,Text='4/10~4/17期间，冒险者击败[c][ffff00]水属性魔物[-][/c]概率获得[c][ffff00]「精粹之水」[-][/c]，把它交给库拉拉可以获得「圣水祝福」！',Speaker=893514,}",
  [169157] = "{id=169157,Text='「圣水祝福」对其他冒险者使用后，能收获到「庆典花环」或「庆典圣水」\n收集「庆典花环」x3、「庆典圣水」x3，可以在库拉拉处交换活动专属卡片「圣水祝福卡片」x1！',Speaker=893514,}",
  [169158] = "{id=169158,Text='4/13~4/27期间，Base等级≥25级的冒险者，通过“[c][ffff00]热点[-][/c]”可进入[c][ffff00]净水祈福[-][/c]。\n累计登录达到指定天数，即可领取1份奖励~',Speaker=893514,}",
  [169159] = "{id=169159,Text='本次庆典邀请了银鱼湖主来参加庆典，银鱼湖主可是出了名的占卜大师，冒险者可要抓紧机会啦~！',Speaker=893514,}",
  [169160] = "{id=169160,Text='4/17~4/24期间，冒险者与[c][ffff00]银鱼湖主[-][/c]对话，完成占卜，即可获得奖励~',Speaker=893514,}",
  [169161] = "{id=169161,Text='庆典中消耗了大量的材料，以至于本不富裕的哥布灵们雪上加霜。好心的冒险者，快来帮助哥布灵吧。',Speaker=893514,}",
  [169162] = "{id=169162,Text='4/24~5/1期间，冒险者将材料捐赠给[c][ffff00]卡布[-][/c]，即可获得奖励~',Speaker=893514,}",
  [169163] = "{id=169163,Text='怎么会少了一年一度的幸运之蛋活动呢？想参与的话，就去克魔岛拜访远道而来的好运兔吧~',Speaker=893514,}",
  [169164] = "{id=169164,Text='4/7~4/10期间，Base≥25级的冒险者通过[c][ffff00]克魔岛的好运兔[-][/c]接取任务，找到藏在克魔岛的幸运之蛋，即可获得奖励哦~',Speaker=893514,}",
  [169170] = "{id=169170,Text='日安，米德加尔特最伟大的冒险者！国王陛下让我传达他最诚挚的问候。',Speaker=893546,}",
  [169171] = "{id=169171,Text='是否能耽误阁下一些时间，陛下有些事情想请求你的帮助。[c][ffff00]【该任务在同账号下只能完成一次，请确认账号角色后谨慎接取！】[-][/c]',Speaker=893546,Option='{乐意帮忙,4} ,{稍后再说,3}',}",
  [169172] = "{id=169172,Text='希望阁下能再考虑一下，我们需要您的帮助。',Speaker=893546,}",
  [169173] = "{id=169173,Text='不知阁下是否去过海港之都——艾尔贝塔？',Speaker=893546,Option='{没去过,0} ,{听说过,0}',}",
  [169174] = "{id=169174,Text='那是个沿海的富裕之地，航运与商业都极为发达，是巴尔特家族的管辖地。',Speaker=893546,}",
  [169175] = "{id=169175,Text='原本那里是个没有冬天的地方，但是近日来却变得如同姜饼城一般寒冷。',Speaker=893546,}",
  [169176] = "{id=169176,Text='巴尔特家家主紧急派出了商队来采购御寒用品，目前已经抵达普隆德拉。',Speaker=893546,}",
  [169177] = "{id=169177,Text='您这样的冒险者对周边地区一定十分熟悉，请帮助他们寻找合适的物资吧！',Speaker=893546,}",
  [169178] = "{id=169178,Text='派特陛下说会派人来协助我们……没想到是你和小白鸽！',Speaker=893547,Option='{除了我还能是谁,0}, {我也没想到是你,0}',}",
  [169179] = "{id=169179,Text='嗯哼~我和[c][ffff00][PlayerName][-][/c]的组合，没有什么问题解决不了啦！',Speaker=893548,}",
  [169180] = "{id=169180,Text='情况你们听说了吧？跟着我来的商人们已经在普隆德拉各处采购物资，我这边也在调度更多船只来运送货物。',Speaker=893547,}",
  [169181] = "{id=169181,Text='这是我第一次带商队出行，之前都是哥哥负责的……',Speaker=893547,Option='{请节哀,0} ,{……,0}',}",
  [169182] = "{id=169182,Text='……我一直不如哥哥，能保证调度一切顺利都已经用尽了全部的脑子了！',Speaker=893547,}",
  [169183] = "{id=169183,Text='阿诺……别这么说，我们会帮助你的！碰到什么困难，尽管告诉我们！',Speaker=893548,}",
  [169184] = "{id=169184,Text='那就拜托你们去看看服装商人伊丽丝的情况，我看她似乎满脸焦急，不是很顺利。',Speaker=893547,Option='交给我们吧',}",
  [169185] = "{id=169185,Text='哎……真的没办法了嘛……',Speaker=893549,}",
  [169186] = "{id=169186,Text='有什么需要帮助的吗？我们受阿诺之托来看看你们的情况。',Speaker=893548,}",
  [169187] = "{id=169187,Text='是这样啊！请听我说！我计算了一下现在御寒衣服的缺口数量，艾诺斯先生却表示无法接下我的订单！',Speaker=893549,}",
  [169188] = "{id=169188,Text='不止如此，我跑遍了所有普隆德拉的服装商店，发现他们所有能支持的数量加起来都无法满足我的订单！',Speaker=893549,}",
  [169189] = "{id=169189,Text='哎，无论如何也没有办法赶制出这批衣物了吗……这可怎么办？！艾尔贝塔的温度普通人没有棉服是扛不住的！',Speaker=893549,}",
  [169190] = "{id=169190,Text='别着急，我们先去了解下情况。',Speaker=893548,}",
  [169191] = "{id=169191,Text='宝贝们~想要什么样的搭配？~诶~伊丽丝宝贝，都和你说了，订单我们接不了呀~',Speaker=1723,Option='{帮帮忙吧！,0},{ 这很重要！,0}',}",
  [169192] = "{id=169192,Text='哦~宝贝们~我也听说艾尔贝塔的情况了~我们也想帮忙~但实在~做不了呀~',Speaker=1723,}",
  [169193] = "{id=169193,Text='哎~制作棉服最重要的保暖层原材料——绒毛，我们只有极少的库存~在这个季节可没有原料商还在继续供应呢~',Speaker=1723,}",
  [169194] = "{id=169194,Text='绒毛……如果我们能提供足够的绒毛呢？',Speaker=893548,}",
  [169195] = "{id=169195,Text='那我们当然愿意加班加点赶工这批货物哦~宝贝~',Speaker=1723,}",
  [169196] = "{id=169196,Text='绒毛我也到处问过了，原料商都已经停工了……',Speaker=893549,}",
  [169197] = "{id=169197,Text='伊丽丝，别担心，我有个主意。[c][ffff00][PlayerName][-][/c]，和我一起去南门吧！',Speaker=893548,}",
  [169198] = "{id=169198,Text='看！这些疯兔！它们身上的绒毛是不是看上去很蓬松~很温暖~',Speaker=893548,Option='{好残忍！,6}{好主意！,7}',}",
  [169199] = "{id=169199,Text='别这么说嘛~我看过《米德加尔特魔物志》，疯兔的毛长起来可是很快的！',Speaker=893548,}",
  [169200] = "{id=169200,Text='嘿嘿~对吧~我看过《米德加尔特魔物志》，疯兔的毛长起来可是很快的！',Speaker=893548,}",
  [169201] = "{id=169201,Text='好了！我们先拿一些疯兔的绒毛回去给艾诺斯先生看看，是不是可以使用。',Speaker=893548,}",
  [169202] = "{id=169202,Text='快拿回去给艾诺斯先生看看。',Speaker=893548,}",
  [169203] = "{id=169203,Text='天哪！这蓬松的绒毛……！非常棒啊！宝贝们，你们太厉害了！但就这点数量的话……',Speaker=1723,}",
  [169204] = "{id=169204,Text='放心！以疯兔在南门的数量和繁殖速度，绒毛肯定没问题了！',Speaker=893548,}",
  [169205] = "{id=169205,Text='啊！那我得赶紧找阿诺先生，雇佣一些冒险者去南门帮忙收集疯兔！',Speaker=893549,}",
  [169206] = "{id=169206,Text='别着急，我会让尼希尔去安排的，你只管等着收货就行！',Speaker=893548,}",
  [169207] = "{id=169207,Text='实在太感谢了！艾诺斯先生！我们赶紧把订单合同签下吧！',Speaker=893549,}",
  [169208] = "{id=169208,Text='情况怎么样？一切都顺利吗？',Speaker=893547,Option='{当然,0},{多亏了小白鸽,0}',}",
  [169209] = "{id=169209,Text='嗯哼~~我和[c][ffff00][PlayerName][-][/c]的配合，当然一切都很顺利啦~',Speaker=893548,}",
  [169210] = "{id=169210,Text='呼……我这边运货的船只也已经安排好了，后日会抵达依斯鲁得的码头。',Speaker=893547,}",
  [169211] = "{id=169211,Text='对了，明天我请客！谢谢你们今天的帮助！',Speaker=893547,}",
  [169212] = "{id=169212,Text='好啊~！那明天见啦！',Speaker=893548,Option='明天见',}",
  [169213] = "{id=169213,Text='你可算来了！小白鸽都快把三人份的餐食吃完了！',Speaker=893547,}",
  [169214] = "{id=169214,Text='阿诺！你别胡说！我哪有吃那么多！明明你吃了一大半！',Speaker=893548,}",
  [169215] = "{id=169215,Text='[c][ffff00][PlayerName][-][/c]，快来尝尝！这是我特意给你留的~不然就被阿诺吃掉啦！',Speaker=893548,}",
  [169216] = "{id=169216,Text='坐地起价！趁火打劫！没有商德！',Speaker=893551,}",
  [169217] = "{id=169217,Text='怎么回事？那边是在吵架吗？',Speaker=893548,}",
  [169218] = "{id=169218,Text='啊……是卡夫卡，和我一起来的日用品商人。发生什么事了？',Speaker=893547,}",
  [169219] = "{id=169219,Text='怎么可以这样！昨天我们说好的，我以7000zeny的价格把你的暖炉全包了！',Speaker=893551,}",
  [169220] = "{id=169220,Text='你们普隆德拉的人！真是奸诈！听说我来自艾尔贝塔，居然抬高了暖炉的价格！',Speaker=893551,}",
  [169221] = "{id=169221,Text='诶诶诶！你可注意你的言辞，市场需求决定商品价值，你自己也是个商人！你不明白这个道理吗？',Speaker=893552,}",
  [169222] = "{id=169222,Text='这批货根本不是为了赚钱！艾尔贝塔的民众都快要冻死了！更何况你那是涨一点吗？你从7000涨到了50万！',Speaker=893551,}",
  [169223] = "{id=169223,Text='谁和你谈好了？订单签字了吗？口说无凭！',Speaker=893552,}",
  [169224] = "{id=169224,Text='你……你……你……',Speaker=893551,}",
  [169225] = "{id=169225,Text='可恶！——这个奸商！',Speaker=893547,}",
  [169226] = "{id=169226,Text='等等！阿诺！冷静，别冲动。',Speaker=893548,}",
  [169227] = "{id=169227,Text='[c][ffff00][PlayerName][-][/c]，让人哑口无言可是我们的拿手好戏。',Speaker=893548,}",
  [169228] = "{id=169228,Text='喏，这是我昨天在路边捡到的[c][ffff00]《店铺传单》[-][/c]，原本想给阿诺做个价格参考的。就是这家店的，上面可是清楚的写着暖炉的价格！',Speaker=893548,}",
  [169229] = "{id=169229,Text='还有还有，这份[c][ffff00]《紧急文件》[-][/c]，是前几天派特陛下专门为艾尔贝塔遭遇极端气温而发布的。',Speaker=893548,}",
  [169230] = "{id=169230,Text='嗯哼哼，我最近可在研读[c][ffff00]《卢恩律法》[-][/c]，我很肯定他的行为是违法的！',Speaker=893548,}",
  [169231] = "{id=169231,Text='好了！资料都给你了，现在让这个奸商乖乖认错吧。',Speaker=893548,}",
  [169232] = "{id=169232,Text='干什么干什么！',Speaker=893552,}",
  [169233] = "{id=169233,Text='这位先生，打扰一下，我们是卡夫卡先生聘请的律师。',Speaker=893548,}",
  [169234] = "{id=169234,Text='我请的律师？我没有啊……',Speaker=893551,}",
  [169235] = "{id=169235,Text='咳咳，我们是您——请——的——律——师——，卡夫卡先生。',Speaker=893548,}",
  [169236] = "{id=169236,Text='……',Speaker=893551,Option='……',}",
  [169237] = "{id=169237,Text='呵！这是市场经济！就算请了律师也没用！',Speaker=893586,}",
  [169239] = "{id=169239,Text='国王陛下早在几日前就下达了[c][ffff00]《紧急文件》[-][/c]，上面明确写着“艾尔贝塔遭遇极端低温，严控市场经济，启用物资紧急预案。”',Speaker=893587,}",
  [169240] = "{id=169240,Text='切，严控又不是禁止，我就是要抬高物价怎么了？你又能拿我怎么办？',Speaker=893586,}",
  [169242] = "{id=169242,Text='根据[c][ffff00]《卢恩律法》[-][/c]第七百五十八条所说，物资紧急预案启动后，禁止哄抬物价，违令者吊销营业执照。',Speaker=893587,}",
  [169243] = "{id=169243,Text='……你……你……你……我之前卖暖炉就是5W一个！谁哄抬物价了！',Speaker=893586,}",
  [169245] = "{id=169245,Text='这是你的[c][ffff00]《店铺传单》[-][/c]哦！事情已经很清楚了，那么现在我就联系商业部门的大臣来吊销你的营业执照吧。',Speaker=893587,}",
  [169246] = "{id=169246,Text='啊啊啊！可恶！……算了算了！原价卖给你们吧！',Speaker=893586,}",
  [169247] = "{id=169247,Text='二位实在是太厉害了！律师费多少？我结算给你们！',Speaker=893551,}",
  [169248] = "{id=169248,Text='不用啦~我们其实不是律师，骗他的啦~还好最近跟着陛下看了不少的书，没想到真的能用上~',Speaker=893548,}",
  [169249] = "{id=169249,Text='卡夫卡，没关系的，他们是我的朋友，我会好好感谢他们的。',Speaker=893547,}",
  [169250] = "{id=169250,Text='太感谢你们！我得赶紧和他签下订单，以免又出什么意外。',Speaker=893551,}",
  [169251] = "{id=169251,Text='今天又麻烦你们……',Speaker=893547,}",
  [169252] = "{id=169252,Text='阿诺，以后可不能像今天这样冲动啦，你可是巴尔特家族下一任的家主！',Speaker=893548,}",
  [169253] = "{id=169253,Text='唔……知道啦……',Speaker=893547,}",
  [169254] = "{id=169254,Text='阿诺他们的运输船只马上就要到达预计启航时间了，但有一队运输物资的马车到现在还没有抵达！',Speaker=893548,}",
  [169255] = "{id=169255,Text='我们得立刻前往南门，沿着运输路线去寻找车队！',Speaker=893548,}",
  [169256] = "{id=169256,Text='地上有车轮的痕迹 ，往那条路去了。',Speaker=893548,}",
  [169257] = "{id=169257,Text='他们在那里！好像被魔物包围了！',Speaker=893548,Option='交给我吧',}",
  [169258] = "{id=169258,Text='哇！冒险者！你真厉害！',Speaker=893548,}",
  [169259] = "{id=169259,Text='这群狸猫实在太残暴！太不讲道理了！和它们说了不是吃的！还是不肯走！',Speaker=893553,Option='{它们也许……,0} {听不懂……,0}',}",
  [169260] = "{id=169260,Text='幸好你们出现了！这里面可是对现在艾尔贝塔来说最重要的物资——燃料！',Speaker=893553,}",
  [169261] = "{id=169261,Text='好了，启航的时间快到了，不用管我们，你们快点将货物送去码头吧！',Speaker=893548,}",
  [169262] = "{id=169262,Text='他们入城就安全了，我们也过去向阿诺告别吧。',Speaker=893548,}",
  [169300] = "{id=169300,Text='这个证据可不能让他乖乖认错。',Speaker=893548,}",
  [169322] = "{id=169322,Text='放心将多余的材料交给我们吧！你会得到好东西的！不会让你吃亏！',Speaker=893589,}",
  [169324] = "{id=169324,Text='[c][ffff00][PlayerName][-][/c]，感受到春天的气息了吗？',Speaker=893590,}",
  [169325] = "{id=169325,Text='如果想去寻找春天，[c][ffff00]「春日波利号」[-][/c]将是你最好的选择哦~',Speaker=893590,}",
  [169326] = "{id=169326,Text='收集一定数量的[c][ffff00]「春日波利币」[-][/c]，可以兑换[c][ffff00]「春日波利号」[-][/c]哦',Speaker=893590,}",
  [169327] = "{id=169327,Text='同时，如果你有多余的[c][ffff00]「春日系列头饰」[-][/c]，也可以在我这里回收成[c][ffff00]「春日波利币」[-][/c]呢~',Speaker=893590,}",
  [169328] = "{id=169328,Text='想要更多的「春日波利币」，也可以在金币商城购买礼包获得哦~',Speaker=893590,}",
  [169333] = "{id=169333,Text='精灵们为感谢所有辛勤劳动的人，并祈求晨曦永恒祝福而举办的迎接新光耀年的祭典。',Speaker=828278,}",
  [169334] = "{id=169334,Text='5.1~5.29期间，Base等级≥25级的冒险者可以前往普隆德拉广场，找到「娜拉·海拉尔」即可参与活动',Speaker=828278,}",
  [169335] = "{id=169335,Text='共4环任务，每周一早晨5点会下发一环任务，若玩家冒险者未在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=828278,}",
  [169336] = "{id=169336,Text='晨曦祭典就要来了！快来帮助精灵们收集更多的花种，并挑选属于自己的“永恒之花”吧！',Speaker=828278,}",
  [169337] = "{id=169337,Text='5.1~5.29期间，Base等级≥25级的冒险者可以前往艾卡拉奇找到「赫蒂」即可参与活动',Speaker=828278,}",
  [169338] = "{id=169338,Text='共5环任务，任务会按服务器时间连续5天下发，若玩家冒险者未在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=828278,}",
  [169339] = "{id=169339,Text='是时候前往花开伊始之地了！在精灵的指导下，培育自己的“永恒之花”吧！',Speaker=828278,}",
  [169340] = "{id=169340,Text='5.8~5.29期间，Base等级≥25级的冒险者前往艾卡拉奇找到「赫蒂」即可参与活动',Speaker=828278,}",
  [169341] = "{id=169341,Text='共7环任务，任务会按服务器时间连续7天下发，若玩家冒险者未在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=828278,}",
  [169342] = "{id=169342,Text='密米尔指使玩偶魔妖们在夜晚偷走了精灵们的“永恒之花”！帮助密米尔收集更多的材料酿制花蜜酒，让他停止盗花行为。',Speaker=828278,}",
  [169343] = "{id=169343,Text='5.15~5.29期间，Base等级≥25级的冒险者可以前往时间花园找到「密米尔」参与活动',Speaker=828278,}",
  [169344] = "{id=169344,Text='共5环任务，任务会按服务器时间连续5天下发，若玩家冒险者未在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=828278,}",
  [169345] = "{id=169345,Text='库里斯诺·诺比拉尔给各位冒险者准备了礼物，当然这礼物可不是白拿的，需要用材料进行交换哦~',Speaker=828278,}",
  [169346] = "{id=169346,Text='5.22~5.29期间，冒险者可以前往艾卡拉奇找到「库里斯诺·诺比拉尔」参与活动',Speaker=828278,}",
  [169347] = "{id=169347,Text='该活动为账号限定类活动，每个帐号每日仅可参与2次',Speaker=828278,}",
  [169348] = "{id=169348,Text='浮空精灵们在艾卡拉奇的神像前演出和祷告，让花瓣与花粉洒满整个艾卡拉奇！',Speaker=828278,}",
  [169349] = "{id=169349,Text='每周六、周日晚19点、20点、21点，将在艾卡拉奇进行花祭表演，每场表演持续10分钟',Speaker=828278,}",
  [169350] = "{id=169350,Text='每周六、周日晚19点10分、20点10分、21点10，「宝石灵兽」将带领魔物破坏祭典，每场活动持续60分钟',Speaker=828278,}",
  [169351] = "{id=169351,Text='击败「宝石灵兽」有概率获得精美时装哦~',Speaker=828278,}",
  [169391] = "{id=169391,Text='亲亲亲爱的冒险者呀~拉拉贝尔很听话的在等你呀！',Speaker=893598,Option='{在等我？,0},{为什么？,0}',}",
  [169392] = "{id=169392,Text='六月呀~是幻想的季节呀~是快乐的季节呀~是最棒的儿童节呀！',Speaker=893598,}",
  [169393] = "{id=169393,Text='拉拉贝尔我呀~代表梦幻城呀~邀请你呀~来做客呀！',Speaker=893598,}",
  [169394] = "{id=169394,Text='拜托了呀~来做客呀~~拉拉贝尔在真诚的邀请你呀~[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893598,Option='{必须参加,4},{还有些事,3}',}",
  [169395] = "{id=169395,Text='拉拉……贝尔……没有不开心……冒险者一定是因为还有更重要的事情……',Speaker=893598,}",
  [169396] = "{id=169396,Text='拉拉贝尔好开心呀~那就进入梦乡吧！咻的一下~就能到梦幻城了呀！',Speaker=893598,}",
  [169397] = "{id=169397,Text='嘿嘿~这可是超超超超级厉害的赛纳斯大人特意为你准备的梦幻床哦！',Speaker=893598,}",
  [169399] = "{id=169399,Text='你还是第一次这样进入幻想乡吧~？',Speaker=893600,Option='这是梦吗？',}",
  [169400] = "{id=169400,Text='是梦，也不是梦~我为拥有着纯真与幻想的人在梦境中打开了来到幻想乡的通道。',Speaker=893600,}",
  [169401] = "{id=169401,Text='邀请他们来参加我们专门为他们举办的节日——儿童节！',Speaker=893600,Option='只有孩子吗？',}",
  [169402] = "{id=169402,Text='当然不是~只不过，大多数人长大后，会遗失这份力量。',Speaker=893600,}",
  [169403] = "{id=169403,Text='你看~你不就是来参加儿童节的“大孩子”嘛~',Speaker=893600,}",
  [169404] = "{id=169404,Text='不用担心，不是只有你一个大孩子~我觉得你们应该认识一下！',Speaker=893600,}",
  [169405] = "{id=169405,Text='他现在应该在小剧场那给孩子们讲故事呢~',Speaker=893600,}",
  [169406] = "{id=169406,Text='公主的宝剑刺进了巨龙的右眼之中，巨龙痛苦的哀嚎着，它的嘶吼响彻天空。',Speaker=893601,}",
  [169407] = "{id=169407,Text='乌云散去，阳光照射在沐浴龙血的公主身上，这一刻她与整个王国一起，获得了新生。',Speaker=893601,}",
  [169408] = "{id=169408,Text='好了~小家伙们，故事说完了，自己去玩吧！',Speaker=893601,}",
  [169409] = "{id=169409,Text='他应该就是赛纳斯所说的大孩子吧。',}",
  [169410] = "{id=169410,Text='嗯？你也是来听故事的吗？今天的故事讲完了哦！',Speaker=893601,Option='自我介绍',}",
  [169411] = "{id=169411,Text='原来你就是赛纳斯先生说的那位，拯救了幻想乡的冒险者！幸会，叫我桑特奇就好。',Speaker=893601,}",
  [169412] = "{id=169412,Text='冒险者~冒险者~你是不是很厉害呀！',Speaker=893602,Option='{很厉害哦！,0},{你是谁呀？,0}',}",
  [169413] = "{id=169413,Text='这是我的女儿，芭芭拉。',Speaker=893601,}",
  [169414] = "{id=169414,Text='芭芭拉知道你很厉害哦！就像我爸爸一样厉害~！他可是很有名的作家哦！',Speaker=893602,}",
  [169415] = "{id=169415,Text='别听这孩子的，我只是个以写童话谋生的无名之辈罢了。',Speaker=893601,}",
  [169416] = "{id=169416,Text='芭芭拉没有瞎说！冒险者~冒险者~芭芭拉可以请你帮个忙吗？',Speaker=893602,Option='{当然可以,0},{说说看~,0}',}",
  [169417] = "{id=169417,Text='爸爸想要写一个最厉害的童话故事~他想要收集……嗯……收集……树材！',Speaker=893602,Option='树材？',}",
  [169418] = "{id=169418,Text='芭芭拉，是素材~和我念~素材~',Speaker=893601,}",
  [169419] = "{id=169419,Text='还是我来说吧！听说赛纳斯先生他们要帮助孩子们实现愿望，我也想帮忙。',Speaker=893601,}",
  [169420] = "{id=169420,Text='这样我可以更了解孩子们的内心，才能写出能打动他们的故事。',Speaker=893601,}",
  [169421] = "{id=169421,Text='嘿嘿，爸爸写的故事芭芭拉都会喜欢哦！不过，芭芭拉也想实现大家的愿望！',Speaker=893602,}",
  [169422] = "{id=169422,Text='最最厉害的冒险者！有你的帮忙，大家的愿望一定会实现的！拜托啦~拜托啦~',Speaker=893602,Option='{我答应你,0},{这就是命吧,0}',}",
  [169423] = "{id=169423,Text='好耶！那芭芭拉带你们去找思丽卡~！她的愿望是要成为最厉害的骑士！',Speaker=893602,}",
  [169424] = "{id=169424,Text='嚯！哈！',Speaker=893603,}",
  [169425] = "{id=169425,Text='思丽卡~思丽卡~',Speaker=893602,}",
  [169426] = "{id=169426,Text='立正！桑特奇叔叔骑士！芭芭拉骑士！你们来了！',Speaker=893603,}",
  [169427] = "{id=169427,Text='思丽卡！我和爸爸找来了最最最厉害的冒险者！',Speaker=893602,}",
  [169428] = "{id=169428,Text='让他加入我们的骑士团，你一定可以打败魔物的！',Speaker=893602,}",
  [169429] = "{id=169429,Text='想加入骑士团？那就让我看看你的本事吧！',Speaker=893603,Option='{好的长官！,0},{要怎么做？,0}',}",
  [169430] = "{id=169430,Text='咳咳，看到这些训练木桩了没有？试着打它们，我能分辨你的实力！',Speaker=893603,}",
  [169431] = "{id=169431,Text='哇，果然好厉害！！',Speaker=893602,}",
  [169432] = "{id=169432,Text='这……这……木桩——全坏了？！',Speaker=893603,}",
  [169433] = "{id=169433,Text='我……我练了大半年……好不容易易才能让它转动起来……',Speaker=893603,Option='{我的实力如何？,0},{能加入骑士团吗？,0}',}",
  [169434] = "{id=169434,Text='咳咳，这位勇士的实力相当不错，我批准你加入骑士团！任命你为骑士团副团长！',Speaker=893603,}",
  [169435] = "{id=169435,Text='太好了！思丽卡骑士团全员到齐，拍照留念一下吧！',Speaker=893602,}",
  [169436] = "{id=169436,Text='骑士团医师长，芭芭拉听令！',Speaker=893603,}",
  [169437] = "{id=169437,Text='到！',Speaker=893602,}",
  [169438] = "{id=169438,Text='骑士团监察长，桑特奇叔叔听令！',Speaker=893603,}",
  [169439] = "{id=169439,Text='到！',Speaker=893601,}",
  [169440] = "{id=169440,Text='骑士团副团长，[PlayerName]听令！',Speaker=893603,Option='到！',}",
  [169441] = "{id=169441,Text='随时准备出发，讨伐魔物！',Speaker=893603,Option='好的长官！',}",
  [169442] = "{id=169442,Text='好的长官！',Speaker=893601,}",
  [169443] = "{id=169443,Text='好的长官！',Speaker=893602,}",
  [169444] = "{id=169444,Text='嘻嘻~思丽卡团长有模有样的~',Speaker=893602,}",
  [169445] = "{id=169445,Text='嘿嘿，你别说，感觉真不错！',Speaker=893603,}",
  [169446] = "{id=169446,Text='这也是我答应芭芭拉的，要给她写一个最棒的童话故事！',Speaker=893601,}",
  [169447] = "{id=169447,Text='在一个宁静的夜晚，芭芭拉一如往常的进入了梦乡，可是再睁开眼时，她却来到了陌生的森林。',Speaker=893601,}",
  [169448] = "{id=169448,Text='嗯，好像是个不错的开头……',Speaker=893601,}",
  [169449] = "{id=169449,Text='冒险者！~有没有帮思丽卡完成她的心愿呀？',Speaker=893602,Option='{完成了！,3},{还没有...,4}',}",
  [169450] = "{id=169450,Text='真好呀~~思丽卡一定很开心吧！',Speaker=893602,}",
  [169451] = "{id=169451,Text='可不要让思丽卡久等了~空闲的话就去帮帮她吧！',Speaker=893602,}",
  [169452] = "{id=169452,Text='芭芭拉发现了新目标：我们快给阿利斯塔做一些糖果吧！他快饿晕了！',Speaker=893602,}",
  [169453] = "{id=169453,Text='哎，那孩子没有糖果就不肯好好吃饭，一直闹脾气呢。',Speaker=893601,}",
  [169454] = "{id=169454,Text='芭芭拉觉得，想要好多好多糖果就是阿利斯塔的愿望！芭芭拉想要帮他实现！',Speaker=893602,}",
  [169455] = "{id=169455,Text='麦琪太太特意请来了她的弟弟，麦克唐叔叔，帮助我们做糖果！',Speaker=893602,}",
  [169456] = "{id=169456,Text='不过，麦克唐叔叔需要我们先把食材找齐了！',Speaker=893602,}",
  [169457] = "{id=169457,Text='最最最厉害的冒险者，你一定会帮芭芭拉的忙的吧？',Speaker=893602,Option='{额……好吧~,0},{当然会！,0}',}",
  [169458] = "{id=169458,Text='食材在甜梦原野都可以找到，我们也和你一起，我也想去看看神奇的甜梦原野。',Speaker=893601,}",
  [169459] = "{id=169459,Text='哇！爸爸，你看好大的冰激凌！那是山吗？爸爸——',Speaker=893602,}",
  [169460] = "{id=169460,Text='这……如果童话世界真实存在，应该就是这里吧……',Speaker=893601,}",
  [169461] = "{id=169461,Text='我得赶紧把眼前的景象记下来！',Speaker=893601,}",
  [169462] = "{id=169462,Text='……（小声嘟囔）芭芭拉的鼻子里都是甜甜的味道……',Speaker=893602,}",
  [169463] = "{id=169463,Text='……',Speaker=893602,Option='{怎么了？,0},{摸摸头,0}',}",
  [169464] = "{id=169464,Text='芭芭拉没事哦！我们要快点做出糖果给阿利斯塔！',Speaker=893602,}",
  [169465] = "{id=169465,Text='麦琪太太说了，流莱可以在溪水中汲取——咦？为什么溪水中会有流莱？',Speaker=893602,Option='是牛奶',}",
  [169466] = "{id=169466,Text='流~莱~！流莱~！然后是蜂蜜~嗯……可以从狂野蜂兵身上获得！',Speaker=893602,}",
  [169467] = "{id=169467,Text='最后最后是甘蔗！可以在甜梦农场那边找到！麦克唐叔叔说这是最关键的！',Speaker=893602,}",
  [169468] = "{id=169468,Text='芭芭拉是不是很厉害！都记住了！',Speaker=893602,Option='真厉害！',}",
  [169469] = "{id=169469,Text='那出发吧！爸爸！快跟上！~不然芭芭拉和冒险者就把你扔掉啦！',Speaker=893602,}",
  [169470] = "{id=169470,Text='……啊，来了……嗯，甜点森林……不错的点子。',Speaker=893601,}",
  [169471] = "{id=169471,Text='有了！骑士带着芭芭拉来到牛奶流成的小溪旁……',Speaker=893601,}",
  [169472] = "{id=169472,Text='这吸溜~~是牛奶！这小溪里流的是牛奶！爸爸！你也——',Speaker=893602,}",
  [169473] = "{id=169473,Text='芭芭拉弯下腰，捧起了白色的溪水，扑面而来的牛奶甜香治愈了她旅途的劳顿……',Speaker=893601,}",
  [169474] = "{id=169474,Text='……（小声嘟囔）爸爸也真是的，只顾着写故事……',Speaker=893602,}",
  [169475] = "{id=169475,Text='糖果们跳跃着组成了小小的桥梁……',Speaker=893601,}",
  [169476] = "{id=169476,Text='芭芭拉小心翼翼地踏着糖果渡过了凶险的河流……',Speaker=893601,}",
  [169477] = "{id=169477,Text='爸爸——爸爸——！我们拿到蜂蜜啦！',Speaker=893602,}",
  [169478] = "{id=169478,Text='巨大的蜂兵挡住了去路……风之精灵在此刻出现，吹跑了蜂兵，保护了芭芭拉！',Speaker=893601,}",
  [169479] = "{id=169479,Text='——嗯？芭芭拉？怎么了？',Speaker=893601,}",
  [169480] = "{id=169480,Text='……没什么~爸爸的故事写的怎么样了？',Speaker=893602,}",
  [169481] = "{id=169481,Text='多亏了芭芭拉！很顺利！爸爸这次一定会写出满意的故事的！',Speaker=893601,}",
  [169482] = "{id=169482,Text='太好了……嗯……太好了……',Speaker=893602,}",
  [169483] = "{id=169483,Text='芭芭拉拜托风之精灵赋予了稻草人生命……',Speaker=893601,}",
  [169484] = "{id=169484,Text='它终于可以离开这个困住它的地方了~',Speaker=893601,}",
  [169485] = "{id=169485,Text='麦克唐叔叔要的东西都准备好啦！我们快去找他吧！',Speaker=893602,}",
  [169486] = "{id=169486,Text='嗯……然后呢……故事要怎么继续呢？',Speaker=893601,}",
  [169487] = "{id=169487,Text='嗯——？都收集齐了，不错。',Speaker=893611,}",
  [169488] = "{id=169488,Text='嘿嘿~都是冒险者的功劳~',Speaker=893602,Option='都是芭芭拉的功劳',}",
  [169489] = "{id=169489,Text='那稍等一下，糖果很快就好。',Speaker=893611,}",
  [169490] = "{id=169490,Text='爸爸！爸爸！快来看麦克唐叔叔做糖果！',Speaker=893602,}",
  [169491] = "{id=169491,Text='嗯……嗯……马上……马上……接下去芭芭拉还会遇到什么呢？……',Speaker=893601,}",
  [169492] = "{id=169492,Text='……哎。',Speaker=893602,}",
  [169493] = "{id=169493,Text='糖果，做好了哦。',Speaker=893611,}",
  [169494] = "{id=169494,Text='哇~好香啊~~麦克唐叔叔你好厉害！',Speaker=893602,}",
  [169495] = "{id=169495,Text='（嗅）是牛奶糖的香味~牛奶糖~牛奶糖~在哪里~',Speaker=893605,}",
  [169496] = "{id=169496,Text='牛奶糖~牛奶糖~啊！是牛奶糖~！',Speaker=893605,}",
  [169497] = "{id=169497,Text='真厉害，寻着香味找过来了。',Speaker=893611,}",
  [169498] = "{id=169498,Text='我还想要！还想要！我想要吃也吃不完的糖果！给我！给我！！快给我！',Speaker=893605,}",
  [169499] = "{id=169499,Text='我要糖果！永远吃不完的糖果！',Speaker=893605,}",
  [169500] = "{id=169500,Text='糖果？嗯，芭芭拉用糖果让孤独的狮子成为了她的伙伴。',Speaker=893601,}",
  [169501] = "{id=169501,Text='原来，阿利斯塔的愿望是要吃不完的糖果呀！',Speaker=893602,}",
  [169502] = "{id=169502,Text='可他吃的也太快了，麦克唐叔叔做的速度都跟不上他。',Speaker=893602,}",
  [169503] = "{id=169503,Text='冒险者，冒险者，我们要想办法实现阿利斯塔的愿望呀！',Speaker=893602,Option='{要怎么做呢,0},{这个很困难,0}',}",
  [169504] = "{id=169504,Text='爸爸又不理我了……他老是这样……我们只能靠自己了！',Speaker=893602,}",
  [169505] = "{id=169505,Text='冒险者，你和芭芭拉一起想想吧~有什么办法可以做出吃不完的糖果呢？',Speaker=893602,}",
  [169506] = "{id=169506,Text='报告团长~冒险者骑士已经就位！人已到齐！',Speaker=893602,Option='你爸爸呢？',}",
  [169507] = "{id=169507,Text='爸爸呀……爸爸还在写东西……没空过来……',Speaker=893602,}",
  [169508] = "{id=169508,Text='明明说好……一起帮大家实现愿望的……',Speaker=893602,}",
  [169509] = "{id=169509,Text='咳咳！我说两句！虽然今天我们的骑士团监察长，桑特奇叔叔缺勤了。',Speaker=893603,}",
  [169510] = "{id=169510,Text='但是！我们迎来了我们的新成员，圆圆骑士——阿利斯塔！',Speaker=893603,}",
  [169511] = "{id=169511,Text='（嚼——嚼——），大家（嚼——），好……',Speaker=893605,}",
  [169512] = "{id=169512,Text='还有我们擅长寻宝的的侦查骑士——琉利斯！',Speaker=893603,}",
  [169513] = "{id=169513,Text='接下去就由琉利斯给我们带来前线消息！',Speaker=893603,}",
  [169514] = "{id=169514,Text='大家好！很高兴加入思丽卡骑士团！我来说一下近期的侦查成果。',Speaker=893604,}",
  [169515] = "{id=169515,Text='赛纳斯哥哥他们正在做一个好大好大的皮纳塔！',Speaker=893604,Option='皮纳塔？',}",
  [169516] = "{id=169516,Text='芭芭拉在书上看到过！皮纳塔是好多好多彩色卡纸做出来的玩具哦！',Speaker=893602,}",
  [169517] = "{id=169517,Text='没错！我探查到，他们没有多余的人手做小小的皮纳塔啦！',Speaker=893604,}",
  [169518] = "{id=169518,Text='赛纳斯哥哥拿出珍藏已久的藏宝图，交换做好的皮纳塔！',Speaker=893604,}",
  [169519] = "{id=169519,Text='这是我们骑士团的机会！思丽卡骑士团听令！',Speaker=893603,}",
  [169520] = "{id=169520,Text='制作——皮纳塔——！换取——藏宝图——！',Speaker=893603,Option='怎么做？',}",
  [169521] = "{id=169521,Text='嘻嘻~原来冒险者也有不会的事情呀！芭芭拉教你呀！',Speaker=893602,}",
  [169522] = "{id=169522,Text='走~我们一起去奇遇广场~',Speaker=893602,}",
  [169523] = "{id=169523,Text='那边有许多赛纳斯哥哥他们剩下的卡纸！',Speaker=893602,}",
  [169524] = "{id=169524,Text='看！地上有好多卡纸~挑你喜欢的颜色就可以哦！',Speaker=893602,}",
  [169525] = "{id=169525,Text='然后！然后！然后从苹果果树上摇一些苹果下来呀！',Speaker=893602,Option='苹果？ ',}",
  [169526] = "{id=169526,Text='因为赛纳斯哥哥说皮纳塔的肚子里要放进最喜欢的东西呀！',Speaker=893602,}",
  [169527] = "{id=169527,Text='芭芭拉最喜欢苹果啦~',Speaker=893602,Option='{非常健康！,0},{我也喜欢！,0}',}",
  [169528] = "{id=169528,Text='嘿嘿~！！不喜欢苹果的孩子不是好孩子！',Speaker=893602,}",
  [169529] = "{id=169529,Text='苹果？！不行，我要放牛奶糖！',Speaker=893605,}",
  [169530] = "{id=169530,Text='我要把我的骑士勋章放进去！',Speaker=893603,}",
  [169531] = "{id=169531,Text='我要把昨天找到的戒指放进去！',Speaker=893604,}",
  [169533] = "{id=169533,Text='接下来嘛……用胶水把卡纸粘起来，包裹住苹果就好啦！',Speaker=893602,}",
  [169534] = "{id=169534,Text='完成啦！~我们去找赛纳斯哥哥换取藏宝图吧！',Speaker=893602,}",
  [169535] = "{id=169535,Text='嗯？大朋友小朋友们，找我有什么事？',Speaker=893600,Option='{皮纳塔,0},{藏宝图,0}',}",
  [169536] = "{id=169536,Text='呀，消息还没有公布，你们居然就知道了呀！',Speaker=893600,}",
  [169537] = "{id=169537,Text='那是当然！我可是思丽卡骑士团的侦查骑士！',Speaker=893604,}",
  [169538] = "{id=169538,Text='真是厉害~好哦！那我就收下这些皮纳塔啦！',Speaker=893600,}",
  [169539] = "{id=169539,Text='哇！',Speaker=893602,}",
  [169540] = "{id=169540,Text='会动诶！',Speaker=893603,}",
  [169541] = "{id=169541,Text='好好玩！',Speaker=893604,}",
  [169542] = "{id=169542,Text='谢谢你们赞助的皮纳塔！',Speaker=893600,}",
  [169543] = "{id=169543,Text='喏，五个皮纳塔，五张藏宝图，给你们。',Speaker=893600,}",
  [169544] = "{id=169544,Text='侦查骑士，琉利斯！寻宝就交给你了！',Speaker=893603,}",
  [169545] = "{id=169545,Text='另外，考虑到寻宝可能会遇到的危险，我将副团长指派给你！',Speaker=893603,}",
  [169546] = "{id=169546,Text='由你们二人进行寻宝任务！',Speaker=893603,Option='{……好吧。,0},{是，团长！,0}',}",
  [169547] = "{id=169547,Text='是！团长！保证完成任务！',Speaker=893604,}",
  [169548] = "{id=169548,Text='冒险者~冒险者~芭芭拉……想要大家一起拍张照……',Speaker=893602,Option='{没问题,0},{交给我！,0}',}",
  [169549] = "{id=169549,Text='拍照好啊！和我们的皮纳塔一起拍张照！',Speaker=893603,}",
  [169550] = "{id=169550,Text='哇，这就是照片吗？嘿嘿，这是我第一次拍照。',Speaker=893604,}",
  [169551] = "{id=169551,Text='在我成为一个厉害的宝藏猎手之后，我要买个相机！',Speaker=893604,}",
  [169552] = "{id=169552,Text='琉利斯骑士，你怎么这么喜欢寻宝？',Speaker=893603,}",
  [169553] = "{id=169553,Text='嘿嘿~我的愿望啊！就是找到大宝藏！赚好多好多的钱！',Speaker=893604,}",
  [169554] = "{id=169554,Text='放心吧！这次一定能找到大宝藏的！',Speaker=893603,}",
  [169555] = "{id=169555,Text='……',Speaker=893602,Option='{怎么了？,0},{芭芭拉？,0}',}",
  [169556] = "{id=169556,Text='芭芭拉只是在想……',Speaker=893602,}",
  [169557] = "{id=169557,Text='照片真好，芭芭拉会一直珍藏着的。只是……',Speaker=893602,}",
  [169558] = "{id=169558,Text='这张照片上，爸爸也在就好了……',Speaker=893602,}",
  [169559] = "{id=169559,Text='芭芭拉从眼前消失了……这是怎么回事？……',}",
  [169560] = "{id=169560,Text='骑士团副团长！我们的骑士团医师长去哪里了？',Speaker=893603,Option='{医师长？,0},{芭芭拉？,0}',}",
  [169561] = "{id=169561,Text='对啊！医师长芭芭拉骑士！',Speaker=893603,}",
  [169562] = "{id=169562,Text='上次做完皮纳塔，拍完合照，我们就没见过她了。',Speaker=893603,Option='我也是 ',}",
  [169563] = "{id=169563,Text='这是怎么回事？我们去找桑特奇叔叔骑士问问吧！',Speaker=893603,}",
  [169564] = "{id=169564,Text='桑特奇叔叔骑士一直把自己关在这屋子里……我们敲门试试！',Speaker=893603,}",
  [169565] = "{id=169565,Text='谁都不能打扰我……不能……',Speaker=893601,}",
  [169566] = "{id=169566,Text='咳咳！哇——这这这——这是怎么了？',Speaker=893603,}",
  [169567] = "{id=169567,Text='这…这是什么怪物呀！思……思丽卡，不怕！你是骑士团团长！',Speaker=893603,}",
  [169568] = "{id=169568,Text='快了，芭芭拉再等爸爸一下……',Speaker=893601,}",
  [169569] = "{id=169569,Text='就差最后一步了——最后一步！',Speaker=893601,}",
  [169570] = "{id=169570,Text='副副副副副团长——我们……我们……思丽卡骑士团！出击！',Speaker=893603,}",
  [169571] = "{id=169571,Text='芭芭拉，我的芭芭拉……',Speaker=893601,}",
  [169572] = "{id=169572,Text='思丽卡骑士团的各位，没受伤吧？',Speaker=893600,Option='{我们没事……,0},{桑特奇他……,0}',}",
  [169573] = "{id=169573,Text='他很痛苦，这让他的梦境与噩梦相连。',Speaker=893600,}",
  [169574] = "{id=169574,Text='我暂时隔断了他的梦境，但这样下去他的身体会承受不住噩梦之力。',Speaker=893600,}",
  [169575] = "{id=169575,Text='能否告诉我，究竟发生了什么事吗？',Speaker=893600,Option='说明情况',}",
  [169576] = "{id=169576,Text='你是说芭芭拉从你的面前消失了？嗯……这样啊……',Speaker=893600,}",
  [169577] = "{id=169577,Text='思丽卡骑士长！拜托你守在这里，如果有什么情况及时通知我们。',Speaker=893600,}",
  [169578] = "{id=169578,Text='交给我吧！保证完成任务！',Speaker=893603,}",
  [169579] = "{id=169579,Text='冒险者，你随我一同前往幻光森林。',Speaker=893600,}",
  [169580] = "{id=169580,Text='准备好前往幻光森林了吗？',Speaker=893600,Option='{准备好了,4},{稍等片刻,3}',}",
  [169591] = "{id=169591,Text='米德加尔特王国为了庆祝这特殊的日子，给每一位冒险者都准备了一份特殊的礼物哦~',Speaker=893644,}",
  [169592] = "{id=169592,Text='希望你能喜欢这顶象征着勇气的水手宣言，也希望你能在这里收获一段愉快的冒险旅途~',Speaker=893644,Option='{谢谢,2},{过会再领,3}',}",
  [169594] = "{id=169594,Text='[c][ffff00][PlayerName][-][/c]，想要成为老练的冒险者，了解各个魔物的弱点是必不可少的一步哦~',Speaker=893644,}",
  [169595] = "{id=169595,Text='去击败更多魔物，累积冒险经验吧！每日只要击败[c][ffff00]300个任意魔物[-][/c]，我将有强力的装备自选礼盒送给你！',Speaker=893644,Option='知道了！',}",
  [169596] = "{id=169596,Text='怎么样~和魔物战斗的感觉还习惯嘛？又掌握了更多的冒险技巧了嘛~',Speaker=893644,}",
  [169597] = "{id=169597,Text='好啦~这是勇者的奖励！今后也要继续努力哦~',Speaker=893644,}",
  [169615] = "{id=169615,Text='……即使如此，芭芭拉仍在想念自己的父亲呢。',Speaker=893600,}",
  [169616] = "{id=169616,Text='……我……我怎么……我以为我……',Speaker=893602,}",
  [169617] = "{id=169617,Text='芭芭拉，你的爸爸还在里面哦！去带他回家吧。',Speaker=893600,}",
  [169618] = "{id=169618,Text='嗯，芭芭拉，要和爸爸一起，回家。',Speaker=893602,}",
  [169619] = "{id=169619,Text='风之精灵呼唤出四季的风，芭芭拉双手将巨大的树叶高高举起，风托起树叶带着芭芭拉飞向了空中。',Speaker=893601,}",
  [169620] = "{id=169620,Text='她与勇敢的骑士告别，与可爱的狮子告别，与智慧的飞龙告别。',Speaker=893601,}",
  [169621] = "{id=169621,Text='她飞过牛奶流成的小溪，越过糖果组成的小桥。',Speaker=893601,}",
  [169622] = "{id=169622,Text='终于，在风的帮助下，芭芭拉找到了回家的路。',Speaker=893601,}",
  [169623] = "{id=169623,Text=' 太好了！',Speaker=893604,}",
  [169624] = "{id=169624,Text=' 太好了！',Speaker=893603,}",
  [169625] = "{id=169625,Text='孩子们！~准备来打皮纳塔了哦！',Speaker=893600,}",
  [169626] = "{id=169626,Text='好久不见，冒险者，多亏了我才能从那种状态中醒过来。',Speaker=893601,Option='芭芭拉怎么样了？',}",
  [169627] = "{id=169627,Text='……芭芭拉她……',Speaker=893601,Option='{不会吧！,0},{芭芭拉……,0}',}",
  [169628] = "{id=169628,Text='哎……',Speaker=893601,}",
  [169629] = "{id=169629,Text='真是的！爸爸你在磨蹭什么！赛纳斯哥哥都准备好了！诶~是冒险者！',Speaker=893602,Option='{芭芭拉！,0},{这……,0}',}",
  [169630] = "{id=169630,Text='芭芭拉她……被赛纳斯先生叫走去准备皮纳塔盛典了。',Speaker=893601,Option='说话不要大喘气！',}",
  [169631] = "{id=169631,Text='那时候芭芭拉的身体状况突然变差，陷入了昏迷，让我失去了理智。',Speaker=893601,}",
  [169632] = "{id=169632,Text='当时的我居然扔下在医院的芭芭拉，再次进入梦境中创作，我真是该死！',Speaker=893601,}",
  [169633] = "{id=169633,Text='是我太执着于写出最棒的童话献给我的芭芭拉，却忘记了自己的初心……',Speaker=893601,}",
  [169634] = "{id=169634,Text='我一开始只是想让芭芭拉高兴啊……',Speaker=893601,}",
  [169635] = "{id=169635,Text='芭芭拉虽然很喜欢爸爸的故事，但是对芭芭拉来说，最重要的是爸爸能陪芭芭拉玩嘛~',Speaker=893602,}",
  [169636] = "{id=169636,Text='嗯……孩子们的愿望虽然五花八门，但是其实再简单不过了……',Speaker=893601,}",
  [169637] = "{id=169637,Text='他们要的不是一个完美的童话故事，而是爱与陪伴啊……',Speaker=893601,}",
  [169638] = "{id=169638,Text='那边三个孩子，大家都在等你们呢！',Speaker=893600,}",
  [169643] = "{id=169643,Text='希望借助齿轮的力量，棒棒糖可以重新变大！',Speaker=893627,}",
  [169646] = "{id=169646,Text='米德加尔特王国为各位新晋的冒险者，准备了一系列[c][ffff00]收集挑战[-][/c]！',Speaker=893644,}",
  [169647] = "{id=169647,Text='「收集挑战」活动共分「精炼挑战」、「头饰挑战」、「卡片挑战」三期，每期都需要在限定时间段内完成！达成挑战条件后，即可在[c][ffff00]收集挑战商店[-][/c]以1 Zeny的超低价购买各类超值大礼！',Speaker=893644,}",
  [169648] = "{id=169648,Text='「精炼挑战」，活动时间：4/6 05:00~5/6 05:00\n「头饰挑战」，活动时间：5/6 05:00~6/6 05:00\n「卡片挑战」，活动时间：6/6 05:00~7/6 05:00',Speaker=893644,}",
  [169649] = "{id=169649,Text='[c][ffff00]收集挑战商店[-][/c]内的商品，将在各个挑战时间段内，限时出售！赶快去完成挑战吧！',Speaker=893644,}",
  [169655] = "{id=169655,Text='七月是属于海滩的日子！每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！',Speaker=893669,}",
  [169656] = "{id=169656,Text='[c][ffff00][PlayerName][-][/c]，别愣着哦~快收下啦~',Speaker=893669,Option='打开',}",
  [169658] = "{id=169658,Text='年轻人，婆婆我占卜了一辈子，你说我占卜得准不准？',Speaker=893671,}",
  [169659] = "{id=169659,Text='无论是猎物随机出没的地点，还是可能掉落的宝物，婆婆我啊，我都能略知一二！',Speaker=893671,}",
  [169660] = "{id=169660,Text='来找婆婆进行占卜吧~说不定啊，有好运等着你呢！',Speaker=893671,}",
  [169662] = "{id=169662,Text='唔唔唔……最近在乌龟岛狩猎，收集了大量的白宝石，看来能送给更多人了！',Speaker=893672,Option='为什么要这样做？',}",
  [169663] = "{id=169663,Text='如果非要说原因的话，将收获的成果分享给大家，是我成为猎人的骄傲噢！',Speaker=893672,}",
  [169664] = "{id=169664,Text='嘿嘿，如果你也有多余的材料，不妨交给我！可能你无法体验我的心境，但你会收到我的回馈噢！',Speaker=893672,}",
  [169665] = "{id=169665,Text='迷藏森林想要走出去的话，应该是上下左右下上左上上左。？还是上下下上左上上左左右？但又好像是……',Speaker=891471,Option='打断她',}",
  [169666] = "{id=169666,Text='噢！！[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=891471,}",
  [169667] = "{id=169667,Text='不好意思，让你见笑了！我过于专注了，竟然没察觉到你已经来了~',Speaker=891471,}",
  [169668] = "{id=169668,Text='优秀的猎人往往都会具备对宝物的敏锐感知，而老练的冒险者想必也熟知米德加尔特每一处！',Speaker=891471,Option='当然！',}",
  [169669] = "{id=169669,Text='那么！参加我精心准备的「宝物狩猎」吧，去寻找布置在各地的宝藏，去那些熟知的地方再探索一次！',Speaker=891471,Option='第一个宝藏？',}",
  [169670] = "{id=169670,Text='那么……\n第一个宝藏藏在[c][ffff00]「普隆德拉的南门」[-][/c]！',Speaker=891471,Option='这范围也太大了！',}",
  [169671] = "{id=169671,Text='诶诶，别着急，我已经在你的地图上，圈出大致范围！',Speaker=891471,Option='我马上出发！',}",
  [169673] = "{id=169673,Text='吼吼~太棒了！宝箱被你找到了！这是你的奖励！',}",
  [169674] = "{id=169674,Text='[c][ffff00][PlayerName][-][/c]，准备接受新的挑战了吗？',Speaker=891471,}",
  [169675] = "{id=169675,Text='那么……\n第二个宝藏，藏在[c][ffff00]「依斯鲁得岛」[-][/c]！',Speaker=891471,Option='出发！',}",
  [169676] = "{id=169676,Text='那么……\n第三个宝藏，藏在[c][ffff00]「哥布灵森林」[-][/c]！',Speaker=891471,Option='出发！',}",
  [169677] = "{id=169677,Text='那么……\n第四个宝藏，藏在[c][ffff00]「克魔岛」[-][/c]！',Speaker=891471,Option='出发！',}",
  [169678] = "{id=169678,Text='那么……\n第五个宝藏，藏在[c][ffff00]「艾尔贝塔」[-][/c]！',Speaker=891471,Option='出发！',}",
  [169680] = "{id=169680,Text='冒险者！「魔物狩猎」将在今晚举行！你准备好了吗？',Speaker=822145,Option='{什么是魔物狩猎？ ,11},{几点开始？,12},{需要做些什么？,13},{我会参加的,14}',}",
  [169681] = "{id=169681,Text='「魔物狩猎」是一年一度狩猎季必不可少的大型活动！',Speaker=822145,}",
  [169682] = "{id=169682,Text='届时，大量魔物将入侵主城！老练的猎人们，往往会组成小队，一同前往狩猎魔物，赢得丰厚的奖励！',Speaker=822145,}",
  [169683] = "{id=169683,Text='本次魔物入侵的主城就是「克魔岛」！',Speaker=822145,}",
  [169684] = "{id=169684,Text='那么~今晚为了「克魔岛」而战！',Speaker=822145,}",
  [169685] = "{id=169685,Text='嗯……会在每周六周日晚上16点~24点进行「魔物狩猎」。',Speaker=822145,}",
  [169686] = "{id=169686,Text='一整个月的周末都会进行哦！千万不要错过了~~',Speaker=822145,}",
  [169687] = "{id=169687,Text='提前约好朋友，组好队伍，按时前来击杀魔物！',Speaker=822145,}",
  [169688] = "{id=169688,Text='总之，就是享受「魔物狩猎」的快感！击败最终的BOSS，将有概率获得稀有的时装！',Speaker=822145,}",
  [169689] = "{id=169689,Text='芭芭拉说她有办法帮我做出吃不完的糖果！',Speaker=893626,}",
  [169690] = "{id=169690,Text='没错哦~芭芭拉听说呀，姜饼城有一种让糖果吃不完的魔法！',Speaker=893627,}",
  [169691] = "{id=169691,Text='哇，那里一定就是妈妈说的天堂吧！',Speaker=893626,}",
  [169692] = "{id=169692,Text='姜饼城的小伙伴告诉我，可以找雪橇哥哥帮忙！',Speaker=893627,}",
  [169693] = "{id=169693,Text='小朋友们哟，欢迎来到姜饼城哟！',Speaker=5201,Option='吃不完的糖果',}",
  [169694] = "{id=169694,Text='那是~在姜饼城教堂那被施过魔咒的糖果~每天都会变出好多好多新的糖果哦！',Speaker=5201,}",
  [169695] = "{id=169695,Text='哇！拜托拜托~把魔咒教给我们吧！',Speaker=893626,}",
  [169696] = "{id=169696,Text='没问题，接下去我念一边，你们可要记住了！',Speaker=5201,}",
  [169697] = "{id=169697,Text='“甜饼人，绿饼人，糖果糖果姜饼人~”',Speaker=5201,}",
  [169698] = "{id=169698,Text='“甜饼人，绿饼人，糖果糖果姜饼人~”',Speaker=893627,}",
  [169699] = "{id=169699,Text='芭芭拉记住了！谢谢雪橇叔叔！',Speaker=893627,}",
  [169700] = "{id=169700,Text='记住哦，要去教堂那边哦！',Speaker=5201,}",
  [169701] = "{id=169701,Text='就是这里吧！阿利斯塔，把糖果放在这里吧',Speaker=893627,}",
  [169702] = "{id=169702,Text='来吧！我们一起念魔咒吧！',Speaker=893627,}",
  [169703] = "{id=169703,Text='甜饼人！',Speaker=893627,}",
  [169704] = "{id=169704,Text='绿饼人！',Speaker=893626,}",
  [169705] = "{id=169705,Text='糖果糖果——',Speaker=893627,Option='{姜饼人 ,4},{甜饼人 ,3},{绿饼人 ,3}',}",
  [169706] = "{id=169706,Text='不对不对！是姜饼人！重来一遍！',Speaker=893627,}",
  [169707] = "{id=169707,Text='好啦，我们把糖果带回幻想乡吧~',Speaker=893627,}",
  [169708] = "{id=169708,Text='如果可以成功的话，阿利斯塔每天都能拥有好多好多糖果了！',Speaker=893627,}",
  [169709] = "{id=169709,Text='我就在这里守着！这样我就能第一时间看到它们了！',Speaker=893626,}",
  [169710] = "{id=169710,Text='你们都骗人！！糖果明明没有变多！！！',Speaker=893626,}",
  [169711] = "{id=169711,Text='怎么会这样？！雪橇哥哥居然骗了我们！！！',Speaker=893627,}",
  [169712] = "{id=169712,Text='我的好多好多糖果呢！它们在哪里！在哪里！',Speaker=893626,}",
  [169713] = "{id=169713,Text='阿利斯塔你别着急！我们这就去找圣诞老人告状！',Speaker=893627,}",
  [169714] = "{id=169714,Text='小朋友们，怎么一脸不高兴呀？',Speaker=6944,Option='说明情况',}",
  [169715] = "{id=169715,Text='哎呀呀呀~原来是这样！雪橇哥哥没有骗你们哦~',Speaker=6944,}",
  [169716] = "{id=169716,Text='因为你们把糖果带离姜饼城了，所以“魔法”失效了哦！',Speaker=6944,}",
  [169717] = "{id=169717,Text='啊！只能在姜饼城生效吗？',Speaker=893627,}",
  [169718] = "{id=169718,Text='你们现在把糖果放回昨天的地方，然后躲起来~糖果一定会变多哦！',Speaker=6944,}",
  [169719] = "{id=169719,Text='好耶好耶！我们快去试试！',Speaker=893626,}",
  [169720] = "{id=169720,Text='接下来……我们躲在圣诞树的后面吧！',Speaker=893627,}",
  [169723] = "{id=169723,Text='哇！糖果！',Speaker=893626,}",
  [169724] = "{id=169724,Text='甜饼人和绿饼人带来好多一模一样的糖果。',}",
  [169725] = "{id=169725,Text='孩子们，好好珍惜这些糖果哦，这都是绿饼人和甜饼人们熬夜为你们制作的哦！',Speaker=893621,}",
  [169726] = "{id=169726,Text='这就是姜饼城糖果魔法的真相~',Speaker=893621,}",
  [169727] = "{id=169727,Text='原来如此！圣诞老人，拜托你帮我们谢谢它们！',Speaker=893627,}",
  [169728] = "{id=169728,Text='我还是想要吃不完的糖果！！！我要！我要！',Speaker=893626,}",
  [169729] = "{id=169729,Text='嚯嚯嚯~或许你们可以去精灵国度艾卡拉奇试试哦。',Speaker=893621,}",
  [169730] = "{id=169730,Text='精灵们很擅长种植，听说他们任何食物都可以种出来~',Speaker=893621,}",
  [169731] = "{id=169731,Text='冒险者冒险者~可以带我们去精灵的国度吗？',Speaker=893627,Option='{当然！,0},{跟我走！ ,0}',}",
  [169732] = "{id=169732,Text='哇！好厉害！这就是精灵的国度嘛~好漂亮~芭芭拉好喜欢！',Speaker=893627,}",
  [169733] = "{id=169733,Text='糖果！糖果！糖果！',Speaker=893626,}",
  [169734] = "{id=169734,Text='冒险者~好久不见！~',Speaker=805305,}",
  [169735] = "{id=169735,Text='冒险者~今天怎么有空过来呀~还带了两个小客人~',Speaker=805305,Option='种糖果！',}",
  [169736] = "{id=169736,Text='呀！原来如此~我可以帮忙哦！',Speaker=805305,}",
  [169737] = "{id=169737,Text='可以啦~我已经把糖果种下并赋予了它晨曦的魔力~',Speaker=805305,}",
  [169738] = "{id=169738,Text='明天它就会破土而出啦~你们就可以拥有很多糖果啦！',Speaker=805305,}",
  [169739] = "{id=169739,Text='谢谢精灵姐姐~',Speaker=893627,}",
  [169740] = "{id=169740,Text='好期待呀~明天我们是不是可以收获一颗糖果树了呀！',Speaker=893627,}",
  [169741] = "{id=169741,Text='糖果树！糖果树！糖果树！好吃的糖果树！',Speaker=893626,}",
  [169742] = "{id=169742,Text='你们看！！！！根本没有糖果树！这长出的是什么呀？！',Speaker=893626,}",
  [169743] = "{id=169743,Text='……冒险者，芭芭拉怎么觉得这个像是甘蔗！？',Speaker=893627,Option='{……,0},{是甘蔗,0}',}",
  [169744] = "{id=169744,Text='糖果树不见了！糖果树不见了！我要糖果树！',Speaker=893626,}",
  [169745] = "{id=169745,Text='我们去找精灵姐姐问问这是怎么回事吧！',Speaker=893627,}",
  [169746] = "{id=169746,Text='怎么样~还顺利吗？糖果有没有好好长大呀？',Speaker=805305,Option='展示花盆',}",
  [169747] = "{id=169747,Text='真不错~长势很棒哦！好好照看它你们可以拥有很多很多甘蔗哦！',Speaker=805305,}",
  [169748] = "{id=169748,Text='精灵姐姐，我们不要甘蔗，我们想要的是糖果树。',Speaker=893627,}",
  [169749] = "{id=169749,Text='糖果树？！那不行的，没有这种植物呀！',Speaker=805305,}",
  [169750] = "{id=169750,Text='精灵们能培育出的，只有食物的原材料哦~',Speaker=805305,}",
  [169751] = "{id=169751,Text='啊……原来是这样……',Speaker=893627,}",
  [169752] = "{id=169752,Text='那我永远吃不完的糖果怎么办？',Speaker=893626,}",
  [169753] = "{id=169753,Text='永远吃不完的糖果……啊！有了！',Speaker=805305,}",
  [169754] = "{id=169754,Text='我听圣女大人说过哦~人类有个叫做吉芬的魔法之城~',Speaker=805305,}",
  [169755] = "{id=169755,Text='那里的法师可以把东西变得好大好大~',Speaker=805305,}",
  [169756] = "{id=169756,Text='如果不把糖果吃完，用魔法再把它变大的话~是不是就可以永远吃不完了！',Speaker=805305,}",
  [169757] = "{id=169757,Text='精灵姐姐！你好聪明！谢谢你～',Speaker=893627,}",
  [169758] = "{id=169758,Text='冒险者，一切可好？',Speaker=1321,Option='变大法术',}",
  [169759] = "{id=169759,Text='这个魔法很简单，任何魔法师都可以做到。',Speaker=1321,}",
  [169760] = "{id=169760,Text='我将变形法术放进了魔法球中，将它放在想要变大的物品上就可以触发它。',Speaker=1321,}",
  [169761] = "{id=169761,Text='不过，为了安全起见，这个法术彻底被释放需要一些时间，你们要耐心哦！',Speaker=1321,}",
  [169762] = "{id=169762,Text='谢谢法师爷爷～！',Speaker=893627,}",
  [169763] = "{id=169763,Text='不客气。……嗯？爷爷……？爷爷……？',Speaker=1321,}",
  [169764] = "{id=169764,Text='我们只要等待魔法起效就好啦！',Speaker=893627,}",
  [169765] = "{id=169765,Text='这次一定要成功啊！我可是已经告诉大家了，我有吃不完的糖果！',Speaker=893626,}",
  [169766] = "{id=169766,Text='快看！糖果变大了！～好大好大～',Speaker=893626,}",
  [169767] = "{id=169767,Text='真的诶！变大了！太好了！阿利斯塔的愿望实现了！',Speaker=893627,}",
  [169768] = "{id=169768,Text='有了吃不完的糖果，大家一定都会羡慕我的吧！',Speaker=893626,}",
  [169769] = "{id=169769,Text='你们两个！我的好朋友们，一起吃糖吧！',Speaker=893626,}",
  [169770] = "{id=169770,Text='奇怪……吃在嘴里没有任何的甜味。',}",
  [169771] = "{id=169771,Text='这……这……这不是糖！不是糖！好难吃啊！',Speaker=893626,}",
  [169772] = "{id=169772,Text='完全没有甜味……怎么会这样？阿利斯塔，别着急，我们去问问法师爷爷。',Speaker=893627,}",
  [169773] = "{id=169773,Text='魔法球用的还顺利吗？',Speaker=1321,Option='说明情况',}",
  [169774] = "{id=169774,Text='哈哈哈哈，原来如此，原来是想要获得吃也吃不完的糖果啊。',Speaker=1321,}",
  [169775] = "{id=169775,Text='很抱歉，变形魔法并不能帮你们达到这个目的。',Speaker=1321,}",
  [169776] = "{id=169776,Text='它只能改变物品的外观，无法改变或是增加物品内部的结构。',Speaker=1321,}",
  [169777] = "{id=169777,Text='魔法也不行吗？……那要怎么办呀？',Speaker=893627,}",
  [169778] = "{id=169778,Text='嗯……如果糖果自己拥有生命的话，或许就可以自己长大了。',Speaker=1321,}",
  [169779] = "{id=169779,Text='糖果怎么可能会拥有生命嘛……',Speaker=893626,}",
  [169780] = "{id=169780,Text='冒险者，你可以带他们去远星大陆试一试，那里的召唤师或许可以做到。',Speaker=1321,}",
  [169781] = "{id=169781,Text='找我什么事？',Speaker=893622,Option='说明情况',}",
  [169782] = "{id=169782,Text='想要我赋予糖果生命？',Speaker=893622,}",
  [169783] = "{id=169783,Text='没错没错！',Speaker=893627,}",
  [169784] = "{id=169784,Text='让它可以自己长大？',Speaker=893622,}",
  [169785] = "{id=169785,Text='是的是的！',Speaker=893627,}",
  [169786] = "{id=169786,Text='让这个小胖子能吃到无尽的糖果？',Speaker=893622,}",
  [169787] = "{id=169787,Text='你才是小胖子！',Speaker=893626,}",
  [169788] = "{id=169788,Text='美丽漂亮的猫猫姐姐～芭芭拉拜托你帮帮忙啦！',Speaker=893627,}",
  [169789] = "{id=169789,Text='……让糖果拥有生命没有问题，但是……算了，就帮你们这一回。',Speaker=893622,}",
  [169790] = "{id=169790,Text='等到明天，你们就会看到一个拥有生命的糖果。',Speaker=893622,}",
  [169791] = "{id=169791,Text='芭芭拉……芭芭拉有种不好的预感。',Speaker=893627,}",
  [169792] = "{id=169792,Text='糖果啊糖果，你是吃不完的糖果，你可要好好记住了！',Speaker=893626,}",
  [169793] = "{id=169793,Text='它它它它它它……它真的会说话了！',Speaker=893626,}",
  [169794] = "{id=169794,Text='你们好～我是吃不完的糖果～请吃我吧！吃我吧～',Speaker=893623,}",
  [169795] = "{id=169795,Text='它它它它它它…………它还会跳舞！',Speaker=893627,}",
  [169796] = "{id=169796,Text='当然～我可喜欢跳舞了～请吃我吧！吃我吧～',Speaker=893623,}",
  [169797] = "{id=169797,Text='芭芭拉觉得……要是吃了它我们会得到惩罚的吧……',Speaker=893627,Option='{下不了口,0},{太奇怪了,0}',}",
  [169798] = "{id=169798,Text='没关系～请吃我吧！吃我吧～我是吃不完的糖果～',Speaker=893623,}",
  [169799] = "{id=169799,Text='……这是我第一次不想吃糖了。',Speaker=893626,}",
  [169800] = "{id=169800,Text='这不是糖果！不是糖果！',Speaker=893626,}",
  [169801] = "{id=169801,Text='我是糖果～我是糖果～请吃我吧！吃我吧～',Speaker=893623,}",
  [169802] = "{id=169802,Text='……要不然……我们还是把它送回猫猫姐姐的身边吧。',Speaker=893627,Option='我同意……',}",
  [169803] = "{id=169803,Text='怎么样，有生命的糖果好吃吗？',Speaker=893622,Option='你是故意的！',}",
  [169804] = "{id=169804,Text='没错啊，谁让你们这么轻视生命。',Speaker=893622,}",
  [169805] = "{id=169805,Text='猫猫姐姐，芭芭拉知道错了……糖果还是交给你照看吧。',Speaker=893627,}",
  [169806] = "{id=169806,Text='别走啊～请吃我吧！吃我吧～我是吃不完的糖果～',Speaker=893623,}",
  [169807] = "{id=169807,Text='看在你们还能认识到错误的份上，给你们一个建议吧。',Speaker=893622,}",
  [169808] = "{id=169808,Text='你们想要的吃不完的糖果，可以借助时间的力量哦。',Speaker=893622,}",
  [169809] = "{id=169809,Text='好了，吃不完的糖果，你跟我走吧。',Speaker=893622,}",
  [169810] = "{id=169810,Text='时间？这是什么意思啊？',Speaker=893627,}",
  [169811] = "{id=169811,Text='算了——我放弃了——不就是大家都嫌我胖不和我玩嘛……',Speaker=893626,}",
  [169812] = "{id=169812,Text='我不要请他们吃糖了，哼！我不要和他们玩了，哼！',Speaker=893626,}",
  [169813] = "{id=169813,Text='我不要吃不完的糖果了，哼！',Speaker=893626,}",
  [169814] = "{id=169814,Text='阿利斯塔跑掉了……原来他想用吃不完的糖果和大家交朋友呀……',Speaker=893627,}",
  [169815] = "{id=169815,Text='不行！我们不能让阿利斯塔伤心！',Speaker=893627,}",
  [169816] = "{id=169816,Text='冒险者，你有什么办法吗？',Speaker=893627,Option='{时间倒流,0},{恢复糖果,0}',}",
  [169817] = "{id=169817,Text='时间居然能倒流！冒险者，芭芭拉知道你肯定有办法！',Speaker=893627,Option='{钟楼,0},{时间管理者,0}',}",
  [169818] = "{id=169818,Text='可是这次，芭芭拉不能陪你去了！',Speaker=893627,}",
  [169819] = "{id=169819,Text='芭芭拉要回去找麦克唐叔叔做一个好大好大的棒棒糖！',Speaker=893627,}",
  [169820] = "{id=169820,Text='好大好大的棒棒糖加上时间魔法，这样才够阿利斯塔交到好多好多的朋友～',Speaker=893627,}",
  [169821] = "{id=169821,Text='那么！我们就分头行动吧！',Speaker=893627,}",
  [169822] = "{id=169822,Text='时间，神圣不可侵犯。',Speaker=893624,Option='说明情况',}",
  [169823] = "{id=169823,Text='原本我应该拒绝的。但是如果是在幻想乡，我倒是可以宽容你们一次。',Speaker=893624,}",
  [169824] = "{id=169824,Text='将这个时间齿轮放进糖果里，它会在每个午夜时分，将糖果的时间回溯。',Speaker=893624,Option='谢谢',}",
  [169825] = "{id=169825,Text='不必言谢，孩子们的愿望啊……是比时间还永恒的存在啊。',Speaker=893624,}",
  [169826] = "{id=169826,Text='冒险者！快来看～麦克唐叔叔正在熬制糖浆呢～',Speaker=893627,}",
  [169827] = "{id=169827,Text='芭芭拉这帮阿利斯塔邀请了好多小伙伴哦！',Speaker=893627,}",
  [169828] = "{id=169828,Text='你那边怎么样了？找到让时间倒流的方法了吗？',Speaker=893627,Option='说明情况',}",
  [169829] = "{id=169829,Text='太好了！只要让麦克唐叔叔把这个时间齿轮放在棒棒糖的中间就行了！',Speaker=893627,}",
  [169830] = "{id=169830,Text='给我。',Speaker=893628,}",
  [169832] = "{id=169832,Text='大家～这是阿利斯塔为大家准备的棒棒糖哦！一起开动吧！',Speaker=893627,}",
  [169833] = "{id=169833,Text='太好了！',Speaker=893607,}",
  [169835] = "{id=169835,Text='好吃！',Speaker=893609,}",
  [169836] = "{id=169836,Text='明天～大家也要再来哦！',Speaker=893627,}",
  [169837] = "{id=169837,Text='接下来～我们就看看到了午夜时分～愿望能不能被实现啦！',Speaker=893627,}",
  [169838] = "{id=169838,Text='成功了！',Speaker=893627,}",
  [169839] = "{id=169839,Text='成功了！',Speaker=893626,}",
  [169840] = "{id=169840,Text='好啊～阿利斯塔，居然躲起来偷偷看我们！',Speaker=893627,}",
  [169841] = "{id=169841,Text='呜呜呜呜——我的，吃不完的，糖果！',Speaker=893626,}",
  [169842] = "{id=169842,Text='今天大家都吃的都很开心哦！大家还约定了明天再来哦！',Speaker=893627,}",
  [169843] = "{id=169843,Text='明天，就会有好多好多小伙伴和你一起吃糖果，一起玩耍啦！',Speaker=893627,}",
  [169844] = "{id=169844,Text='呜呜呜呜呜——芭芭拉，谢谢你，只有你没有糖果也愿意理我。',Speaker=893626,}",
  [169845] = "{id=169845,Text='不是的哦，阿利斯塔。没有糖果，大家也一定愿意和你交朋友的！',Speaker=893627,}",
  [169846] = "{id=169846,Text='这个爱的棒棒糖只是一个开始呀～',Speaker=893627,}",
  [169847] = "{id=169847,Text='是否要品尝代表着友谊与陪伴的棒棒糖？',Speaker=893683,Option='{品尝,3},{稍后,2}',}",
  [169848] = "{id=169848,Text='副团长！侦查骑士琉利斯就位！',Speaker=893629,}",
  [169849] = "{id=169849,Text='请看，这是今天的藏宝图。',Speaker=893629,}",
  [169850] = "{id=169850,Text='嗯……根据图上的建筑和环境……宝藏应该在……',Speaker=893629,Picture='new_npc_pic_31',Option='{梦幻城,3},{甜梦原野,2},{幻光森林,2},{灾厄领域,2}',}",
  [169851] = "{id=169851,Text='不对，根据我的侦查经验来看，应该是梦幻城。',Speaker=893629,}",
  [169852] = "{id=169852,Text='没错，我也是这么认为的！',Speaker=893629,}",
  [169853] = "{id=169853,Text='目标，找到梦幻城里的宝藏！',Speaker=893629,}",
  [169854] = "{id=169854,Text='不愧是骑士团副团长！',Speaker=893629,}",
  [169855] = "{id=169855,Text='啊……时间过得好快，差不多到我起床干活的时间了！',Speaker=893629,}",
  [169856] = "{id=169856,Text='明天我们再继续寻宝吧！',Speaker=893629,}",
  [169857] = "{id=169857,Text='副团长！侦查骑士琉利斯就位！',Speaker=893629,}",
