  [169883] = "{id=169883,Text='目标，找到甜梦原野里的宝藏！',Speaker=893629,}",
  [169908] = "{id=169908,Text='一年一度的狩猎季即将到来，在这场盛大的节日里，冒险家都将化身为老练的猎人，去寻找自己的猎物……但是这一次，猎人竟然成为猎物……',Speaker=893669,}",
  [169909] = "{id=169909,Text='7/1~7/31期间，Base≥25级的冒险者通过[c][ffff00]阿维娃[-][/c]接取系列任务~',Speaker=893669,}",
  [169910] = "{id=169910,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「狩猎勋章」x5\n完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=893669,}",
  [169913] = "{id=169913,Text='优秀的猎人往往都会具备对宝物的敏锐感知。寻找「阿维娃」藏着各地的「宝箱」，证明自己的实力吧！',Speaker=893669,}",
  [169914] = "{id=169914,Text='7/6~7/31期间，Base≥25级的冒险者通过[c][ffff00]阿维娃[-][/c]接取系列任务~',Speaker=893669,}",
  [169915] = "{id=169915,Text='共5个连续的任务，冒险者需要根据提示，前往不同地图，在指定区域内寻找宝箱：\n完成第1~4个任务，可获得Zeny x 150,000，「狩猎勋章」x1\n完成第5个任务，可以获得奖励Zeny x 300,000，「狩猎勋章」x3',Speaker=893669,}",
  [169916] = "{id=169916,Text='除了基本的狩猎技术外，狩猎的成功与否也取决于运气的好坏~冒险者如果想要加大狩猎成功率的话，不妨去找「卡梅阿婆婆」进行占卜哦~',Speaker=893669,}",
  [169917] = "{id=169917,Text='7/13~7/20期间，冒险者与[c][ffff00]卡梅阿婆婆[-][/c]进行对话，完成占卜~\n每日完成占卜，即可获得Zeny x 100,000，「狩猎勋章」x1',Speaker=893669,}",
  [169918] = "{id=169918,Text='成为老练的猎人，能够狩猎更强大的魔物，寻找到更多的宝物，而将收获的成果分享给大家，也是成为猎人的荣誉。',Speaker=893669,}",
  [169919] = "{id=169919,Text='7/20~7/27期间，冒险者将材料捐赠给[c][ffff00]阿尔特[-][/c]，即可获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=893669,}",
  [169920] = "{id=169920,Text='「魔物狩猎」是一年一度狩猎季必不可少的大型活动！本次魔物入侵的主城就是「克魔岛」~',Speaker=893669,}",
  [169921] = "{id=169921,Text='7/1~7/31期间，每周六周日晚上16~24点将在克魔岛进行「魔物狩猎」！',Speaker=893669,}",
  [169922] = "{id=169922,Text='提前约好朋友，组好队伍，前来击杀魔物，将有概率获得稀有的时装！',Speaker=893669,}",
  [169924] = "{id=169924,Text='根据举报，有一只变异的摇滚蝗虫天天在南门乐园团举办演奏会。',Speaker=893688,}",
  [169925] = "{id=169925,Text='由于演奏过于难听，严重影响了乐园团的日常事务！',Speaker=893688,}",
  [169926] = "{id=169926,Text='我们思丽卡骑士团有义务赶走蝗虫，还乐园团一片宁静！',Speaker=893688,}",
  [169927] = "{id=169927,Text='骑士团副团长，[c][ffff00][PlayerName][-][/c]听令！',Speaker=893688,Option='到！',}",
  [169928] = "{id=169928,Text='出发！',Speaker=893688,}",
  [169929] = "{id=169929,Text='哇，真的好难听。',Speaker=893688,}",
  [169930] = "{id=169930,Text='做的很好！大家一定会记住我们思丽卡骑士团的！',Speaker=893688,}",
  [169931] = "{id=169931,Text='不要骄傲，成为优秀骑士的路上，我们还需要努力！',Speaker=893688,}",
  [169932] = "{id=169932,Text='我打听到，海盗之王出现在了普隆德拉西门！',Speaker=893688,}",
  [169933] = "{id=169933,Text='不止如此！他还到处打劫路人，好多人都向普隆德拉护卫队投诉！',Speaker=893688,Option='你怎么知道的？',}",
  [169934] = "{id=169934,Text='嘿嘿~我偷听的~我爸爸可是护卫队中优秀的骑士！',Speaker=893688,}",
  [169935] = "{id=169935,Text='好啦！骑士团副团长，我们出发吧！',Speaker=893688,}",
  [169936] = "{id=169936,Text='呼——总算是解决了！',Speaker=893688,}",
  [169937] = "{id=169937,Text='哼哼~好期待看到爸爸他们来这里发现海盗之王已经被赶走了的样子~',Speaker=893688,}",
  [169938] = "{id=169938,Text='哼哼哼~我离成为一个优秀的骑士越来越近啦！',Speaker=893688,}",
  [169939] = "{id=169939,Text='气气气死我了！那群家伙！说女孩子是没法成为骑士的！',Speaker=893688,Option='别听他们的！',}",
  [169940] = "{id=169940,Text='我打架比他们强多了！可恶！我要证明给他们看！',Speaker=893688,}",
  [169941] = "{id=169941,Text='流浪之狼在梦罗克城的外围到处伤人！',Speaker=893688,}",
  [169942] = "{id=169942,Text='副团长！我们走！让他们瞧瞧我们的厉害！',Speaker=893688,}",
  [169943] = "{id=169943,Text='哼！真是轻轻松松的！',Speaker=893688,}",
  [169944] = "{id=169944,Text='我一定要成为一个优秀的骑士！',Speaker=893688,}",
  [169945] = "{id=169945,Text='然后……然后……然后我要消灭所有讨厌的魔物！',Speaker=893688,}",
  [169946] = "{id=169946,Text='我从小伙伴那边打听到一件事！',Speaker=893688,}",
  [169947] = "{id=169947,Text='蜂后带着它的小弟们在大肆破坏普隆德拉北部的花田！',Speaker=893688,}",
  [169948] = "{id=169948,Text='我们可得赶在花农们投诉到护卫队之前解决了它们！',Speaker=893688,}",
  [169949] = "{id=169949,Text='快快快！！思丽卡骑士团快快出发！',Speaker=893688,}",
  [169950] = "{id=169950,Text='这下花农就不会麻烦护卫队了吧~',Speaker=893688,}",
  [169951] = "{id=169951,Text='哼哼！少了这一桩事情，爸爸今晚可以早点下班了吧~',Speaker=893688,}",
  [169952] = "{id=169952,Text='嘿嘿~我先回家帮妈妈一起准备一下啦！',Speaker=893688,}",
  [169953] = "{id=169953,Text='爸爸昨天……还是没能有时间回家吃饭……',Speaker=893688,}",
  [169954] = "{id=169954,Text='我要是能变得再厉害点就好了……爸爸是不是就能回家陪我玩了？',Speaker=893688,}",
  [169955] = "{id=169955,Text='不，我不能消沉！我是思丽卡骑士团的团长！',Speaker=893688,}",
  [169956] = "{id=169956,Text='副团长！我们出发去斐扬树林赶走捣乱的枯树精吧！',Speaker=893688,}",
  [169957] = "{id=169957,Text='太棒了！不愧是我的副团长！',Speaker=893688,}",
  [169958] = "{id=169958,Text='副团长，我长大了，会和你一样厉害吗？能成为优秀的骑士吗？',Speaker=893688,Option='一定可以！',}",
  [169959] = "{id=169959,Text='我会努力的！我要消灭所有的魔物，让所有的骑士爸爸都有时间回家！',Speaker=893688,}",
  [169961] = "{id=169961,Text='虽然熊熊最大的心愿，就是可以给你带来快乐~',Speaker=893689,}",
  [169962] = "{id=169962,Text='但是熊熊好像也拿不出什么东西送给你呢~',Speaker=893689,}",
  [169963] = "{id=169963,Text='对了，这张明信片是熊熊收藏了很久的，现在送给你啦~！',Speaker=893689,Option='谢谢！',}",
  [169964] = "{id=169964,Text='冒险者，想要成为老练的猎人，可不能错过一年一度的「魔物狩猎」活动！',Speaker=822145,}",
  [169965] = "{id=169965,Text='每周六、周日，将在克魔岛刷新大量暴走的魔物！',Speaker=822145,}",
  [169966] = "{id=169966,Text='只要击杀3个暴走的啤酒企鹅，我将赠予你稀有的「狩猎勋章」！',Speaker=822145,Option='我知道了！',}",
  [169967] = "{id=169967,Text='让我看看你的击杀记录……唔，不亏是你，远远超过了要求的数量！这是你的报酬！',Speaker=822145,}",
  [169969] = "{id=169969,Text='[c][ffff00][PlayerName][-][/c]，你知道皮纳塔吗？',Speaker=893692,}",
  [169970] = "{id=169970,Text='每周六、周日晚16~24点，将在梦幻城刷新大量皮纳塔魔物哦波~！',Speaker=893692,}",
  [169971] = "{id=169971,Text='击败皮纳塔魔物，他们会在周围留下「皮纳塔彩带」，这是我们皮纳塔给冒险者们的礼物哦波~千万不要忘记拾取哦波~',Speaker=893692,}",
  [169972] = "{id=169972,Text='波波波~[c][ffff00][PlayerName][-][/c]，不要错过任何一个皮纳塔哦波~',Speaker=893692,}",
  [169973] = "{id=169973,Text='波波波，不要再打了啦~这个给你，放过本波波吧~！',Speaker=893692,}",
  [169975] = "{id=169975,Text='嗨咻嗨咻！',Speaker=893675,}",
  [169976] = "{id=169976,Text='嗨咻嗨咻！',Speaker=893676,}",
  [169977] = "{id=169977,Text='被发现了！',Speaker=893675,}",
  [169978] = "{id=169978,Text='快跑！',Speaker=893676,}",
  [169979] = "{id=169979,Text='为了感谢孩子们为幻想乡的付出，幻想乡的大贤者赛纳斯决定举办盛大的活动，邀请孩子们来到幻想乡，为他们实现天真而善良的愿望。',Speaker=893689,}",
  [169980] = "{id=169980,Text='6/1~6/29期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]拉拉贝尔[-][/c]接取系列任务~',Speaker=893689,}",
  [169981] = "{id=169981,Text='共4个连续的任务，每周一早晨5点会下发一环任务：\n完成第1~4个任务，可获得Zeny x 300,000、「纯真心愿」x5\n完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=893689,}",
  [169982] = "{id=169982,Text='女孩思丽卡想要成为英勇的骑士，率领士兵们斩杀魔物！',Speaker=893689,}",
  [169983] = "{id=169983,Text='6/1~6/29期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]思丽卡[-][/c]接取系列任务~',Speaker=893689,}",
  [169984] = "{id=169984,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「纯真心愿」x1\n完成第5个任务，可以获得Zeny x 300,000、「纯真心愿」x3',Speaker=893689,}",
  [169985] = "{id=169985,Text='小胖猫阿利斯塔想要拥有吃也吃不完的美味糖果，这样的糖果哪里能找到呢？',Speaker=893689,}",
  [169986] = "{id=169986,Text='6/8~6/29期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]阿利斯塔[-][/c]接取系列任务~',Speaker=893689,}",
  [169987] = "{id=169987,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「纯真心愿」x1\n完成第5个任务，可以获得Zeny x 300,000、「纯真心愿」x3',Speaker=893689,}",
  [169988] = "{id=169988,Text='完成所有「吃不完的糖果」后，可以解锁「爱的棒棒糖」任务，每天品尝1次棒棒糖，可以获得「纯真心愿」x1',Speaker=893689,}",
  [169989] = "{id=169989,Text='男孩琉利斯想要找到各式各样的宝藏，成为出色的宝藏猎人！',Speaker=893689,}",
  [169990] = "{id=169990,Text='6/15~6/29期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]琉利斯[-][/c]接取系列任务~',Speaker=893689,}",
  [169991] = "{id=169991,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「纯真心愿」x1\n完成第5个任务，可以获得Zeny x 300,000、「纯真心愿」x3',Speaker=893689,}",
  [169992] = "{id=169992,Text='「皮纳塔」是幻想乡送给充满童心的人最好的礼物',Speaker=893689,}",
  [169993] = "{id=169993,Text='活动期间，每周六、日晚16点~24点，梦幻城将会出现大量「皮纳塔魔物」，击败「皮纳塔魔物」可以获得「皮纳塔附魔礼盒」、「皮纳塔符文礼盒」等奖励',Speaker=893689,}",
  [169994] = "{id=169994,Text='击败「拉链熊皮纳塔」还有概率获得「皮纳塔时装礼盒」，不要错过哟~',Speaker=893689,}",
  [169995] = "{id=169995,Text='听说那些活蹦乱跳的「波利皮纳塔」，是幻想乡带给【孩子王】的礼物哦~',Speaker=893689,}",
  [170032] = "{id=170032,Text='听说七夕是找老婆的节日，俺也想要老婆！',Speaker=893703,}",
  [170033] = "{id=170033,Text='那啥……冒险者，你能不能帮俺找老婆？',Speaker=893703,Option='{怎么找？,30},{说来听听,30}',}",
  [170034] = "{id=170034,Text='俺打不过族里的女战士，嘿嘿，你能不能趁着她们洗澡，帮我把衣服偷来？',Speaker=893703,}",
  [170035] = "{id=170035,Text='到时候俺再还给她，她肯定觉得俺可厉害了，说不定就愿意跟了俺。',Speaker=893703,Option='{不太好吧……,40},{我想想……,40}',}",
  [170036] = "{id=170036,Text='哎呀，你就帮帮俺嘛，俺在族里总是被人嘲笑没老婆，可丢人了，俺保证以后对老婆好。',Speaker=893703,Option='{我帮你！,60},{不行！,50}',}",
  [170037] = "{id=170037,Text='嘿嘿，谢谢你咧，衣服就在岸边不远处！不过记得跑快点！别被追到啦。',Speaker=893703,}",
  [170038] = "{id=170038,Text='完咧，俺这辈子是找不到老婆了，呜呜呜。',Speaker=893703,}",
  [170041] = "{id=170041,Text='还真拿来了，谢谢你！',Speaker=893703,}",
  [170042] = "{id=170042,Text='以后等俺们成家了，请你来喝酒。',Speaker=893703,Option='{还是算了,70},{我怕挨打,70}',}",
  [170043] = "{id=170043,Text='嘿嘿，打两下就打两下嘛，她一个小姑娘，打人能有多疼？',Speaker=893703,Option='……',}",
  [170044] = "{id=170044,Text='哎呀，好多手呀~',Speaker=893706,}",
  [170045] = "{id=170045,Text='嗨嗨嗨~七夕可真是一个好日子啊，人类都忙着出来交——交朋友。',Speaker=893706,}",
  [170046] = "{id=170046,Text='我们和麻烦的人类可不一样，天生就有另一半，只要看形状就能确定谁是我的那个它。',Speaker=893706,Option='{这么厉害？,30},{真羡慕啊……,30}',}",
  [170047] = "{id=170047,Text='可今天的手实在太多了，看得我眼花缭乱，冒险者，你去帮我找一找吧。',Speaker=893706,Option='{好吧,50},{不！我也是单身！,40}',}",
  [170048] = "{id=170048,Text='记住，要形状能和我配对的，我们在一起可是天生一对，充满浪漫的爱心。',Speaker=893706,}",
  [170049] = "{id=170049,Text='哼，看出来了，你的怨念可真重啊。',Speaker=893706,}",
  [170050] = "{id=170050,Text='呀，你说找到了我的另一半？',Speaker=893707,}",
  [170051] = "{id=170051,Text='那好吧，我就跟你去看看。',Speaker=893707,}",
  [170052] = "{id=170052,Text='这不是我的另一半吗？啊，我总算找到了天生适合的另一只手！',Speaker=893706,}",
  [170053] = "{id=170053,Text='呀~你干嘛一直朝人家挥手呀，真害羞。',Speaker=893707,}",
  [170054] = "{id=170054,Text='谢谢你，冒险者，后面的事情你还是别看了，留给我们一点空间嘛。',Speaker=893706,Option='{无情！,60},{过河拆桥！,60}',}",
  [170055] = "{id=170055,Text='讨厌啦~~等等，在这之前，先帮我们拍一张甜蜜合照吧。',Speaker=893707,}",
  [170056] = "{id=170056,Text='多谢帮助~接下来就是蜜月时光！',Speaker=893706,Option='{这么快？,70},{有点羡慕……,70}',}",
  [170057] = "{id=170057,Text='哎呀呀，不要在大庭广众下直接握手啦，太害羞了。',Speaker=893707,}",
  [170076] = "{id=170076,Text='今天是一个特殊的日子，每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！如果今天你也有想感谢的恩师，记得送去问候哦~',Speaker=893715,}",
  [170077] = "{id=170077,Text='[c][ffff00][PlayerName][-][/c]，别愣着哦~快收下啦~',Speaker=893715,Option='打开',}",
  [170078] = "{id=170078,Text='通过这一系列事件，学院感受到了“白银之风”旺盛的生命力与深藏的潜力，所以决定把这次的“学院交换祭”交由我们来负责。',Speaker=893716,}",
  [170079] = "{id=170079,Text='正巧，经验丰富的冒险者，想必有不少奇奇怪怪的材料能来交换！太好了，我们也不会无偿让你帮忙，来交换吧！你可以获得一些小小的报酬~',Speaker=893716,Option='听起来不错',}",
  [170115] = "{id=170115,Text='又到了[c][ffff00]天津町[-][/c]一年一度的[c][ffff00]花嫁节[-][/c]，真让人激动。',Speaker=891813,Option='花嫁节？',}",
  [170116] = "{id=170116,Text='没错，[c][ffff00]花嫁节[-][/c]是爱人们立下诚挚誓言，期许相爱一生的甜蜜节日，冒险者，你有心爱的恋人吗？',Speaker=891813,Option='{有,2},{没有,3}',}",
  [170117] = "{id=170117,Text='太棒了！那你们一定不能错过花嫁节，它会让你们的爱情更显甜蜜。',Speaker=891813,}",
  [170118] = "{id=170118,Text='花嫁节每年都会吸引无数游客前来，说不定你的缘分就在这里呢~',Speaker=891813,}",
  [170119] = "{id=170119,Text='怎么样？要不要参加花嫁节，欣赏天津町盛大的[c][ffff00]流星雨[-][/c]和美丽的[c][ffff00]樱花树[-][/c]？【[c][ffff00]该任务同账号仅可完成一次。[-][/c]】',Speaker=891813,Option='{当然参加,5},{还有点事,6}',}",
  [170120] = "{id=170120,Text='这样呀~没关系，天津町的樱花会一直为你开放呢，期待着你和你的爱人到来，对了，花嫁节会持续一个月哟。',Speaker=891813,}",
  [170121] = "{id=170121,Text='太棒啦，愿你能在花嫁节找到属于你的浪漫。',Speaker=891813,}",
  [170122] = "{id=170122,Text='相信我，巫女的祝福可是很有效的哟~这边是专门开辟的花嫁之门，可以直接抵达天津町。',Speaker=891813,}",
  [170123] = "{id=170123,Text='冒险者，准备好后请触摸花嫁之门，前往天津町参加这场美丽的节日吧。',Speaker=891813,}",
  [170124] = "{id=170124,Text='现在就要去天津町参加花嫁节吗？',Speaker=891840,Option='{立刻出发,8},{稍等片刻,9}',}",
  [170125] = "{id=170125,Text='这里是飘荡着爱与樱花的天津町，冒险者，你是来参加花嫁盛典的吗？哇，超~级开心！',Speaker=891824,}",
  [170126] = "{id=170126,Text='啊抱歉，我好像忘了做自我介绍，我是[c][ffff00]见习巫女奈奈[-][/c]，才成为巫女不久，正在努力通过考核期。',Speaker=891824,Option='{加油！,0},{很棒哟~,0}',}",
  [170127] = "{id=170127,Text='花嫁节期间我的职责是[c][ffff00]守护樱花树[-][/c]，你应该能猜到，樱花树在花嫁节中可是很重要的！',Speaker=891824,Option='{是吗？,0},{为什么？,0}',}",
  [170128] = "{id=170128,Text='传说中在流星的照耀下，樱花树旁手持捧花的恋人们相互拥抱——',Speaker=891824,}",
  [170129] = "{id=170129,Text='他们将长久陪伴在彼此身旁，永远保持这一刻的甜蜜与浪漫！',Speaker=891824,Option='{听起来不错,0},{很浪漫,0}',}",
  [170130] = "{id=170130,Text='天津町的樱花和别处不同，走吧，我带你看看它的特别之处。',Speaker=891824,}",
  [170131] = "{id=170131,Text='每年八月，天津町将迎来最大的一场流星雨，超~级~美！',Speaker=891824,}",
  [170132] = "{id=170132,Text='这里的樱花树也被称为爱情树，你瞧，好多飘落的樱花呢。',Speaker=891824,}",
  [170133] = "{id=170133,Text='这是花嫁节前神社新栽种的樱花树，别看现在只有一棵樱花苗，但很快它就会长得超~级高大！',Speaker=891824,Option='这么厉害？',}",
  [170134] = "{id=170134,Text='冒险者，你能想办法让它快快长大吗？',Speaker=891824,Option='我来试试……',}",
  [170135] = "{id=170135,Text='应该怎么做呢？',Option='{浇水,11},{注入能量,12}',}",
  [170136] = "{id=170136,Text='樱花树苗毫无反应……',}",
  [170137] = "{id=170137,Text='樱花树苗毫无反应……',}",
  [170138] = "{id=170138,Text='不管用的~其实这就是我要告诉你的秘密，这里的樱花树所吸收的不是水或养分，而是恋人间的甜言蜜语！',Speaker=891824,Option='{什么？！,0},{怎会如此……,0}',}",
  [170139] = "{id=170139,Text='这也被我们称为[c][ffff00]“甜蜜誓约”[-][/c]，吸收了足够甜蜜誓约的樱花树会长得格外漂亮呢！对了，冒险者，你也试试看吧！',Speaker=891824,}",
  [170140] = "{id=170140,Text='啊，糟糕，是人家没说清楚吗？果然人家有点笨，不会通不过考核吧？',Speaker=891824,Option='{别紧张,0},{说仔细点,0}',}",
  [170141] = "{id=170141,Text='只要你对这棵樱花树苗，说一些甜蜜的话，它就会很开心呢！一定会超~级迅速地长大。',Speaker=891824,Option='我试试',}",
  [170142] = "{id=170142,Text='就算没有恋人也没关系哟，可以当做对未来的爱人说的嘛，我会替你保密的！',Speaker=891824,}",
  [170143] = "{id=170143,Text='必须要诚心啦！好吧，我来帮助你~',Speaker=891824,}",
  [170144] = "{id=170144,Text='冒险者，你认为什么才是真正的爱情。',Speaker=891824,Option='{持久的陪伴,13},{怦然心动的悸动,14},{温柔的包容,15}',}",
  [170145] = "{id=170145,Text='无论时光再快，只要有你相伴，每一刻都是永恒。',Speaker=891824,}",
  [170146] = "{id=170146,Text='茫茫人海中，你就像我的阳光，相遇那一刻便让我彻底着迷。',Speaker=891824,}",
  [170147] = "{id=170147,Text='你的温柔像湖水，我就像一条鱼，只有在你怀中，我才能找到归属。',Speaker=891824,}",
  [170148] = "{id=170148,Text='快看啊冒险者，它似乎很高兴，好像又长大了一点呢！',Speaker=891824,Option='好神奇……',}",
  [170149] = "{id=170149,Text='冒险者，你能帮我拍照吗？这可是我照顾的第一棵樱花树呢。',Speaker=891824,Option='{没问题,0},{交给我吧,0}',}",
  [170150] = "{id=170150,Text='真开心呀，我能感受到这棵樱花树的雀跃呢。',Speaker=891824,}",
  [170151] = "{id=170151,Text='人家超~级高兴！',Speaker=891824,Option='{看出来了,0},{好好照顾它,0}',}",
  [170152] = "{id=170152,Text='所以冒险者，我可以拜托你一件事吗？',Speaker=891824,Option='{什么事？,0},{你先说说看,0}',}",
  [170153] = "{id=170153,Text='如果你有心爱的人，麻烦带他来樱花树下，说一些甜蜜的悄悄话~',Speaker=891824,}",
  [170154] = "{id=170154,Text='说不定樱花树长得快一点，人家就能早点通过考核！',Speaker=891824,}",
  [170155] = "{id=170155,Text='相信[c][ffff00][PlayerName][-][/c]这么好的人，你们之间的甜蜜誓约，肯定可以让樱花树快快长大呢！',Speaker=891824,Option='{我尽力……,16},{好的！,17}',}",
  [170156] = "{id=170156,Text='那就拜托你了，人家一定会超~级感谢你的！',Speaker=891824,}",
  [170157] = "{id=170157,Text='太好了！你真是个热心肠的冒险者，相信能被你喜欢的人，一定很幸福呀。',Speaker=891824,}",
  [170158] = "{id=170158,Text='冒险者~怎么样，这段时间有感受到天津町热情如火的恋爱氛围吗？',Speaker=891824,Option='{感受到了,2},{还没有,3}',}",
  [170159] = "{id=170159,Text='是呀，这段时间经常能看到许多如胶似漆的情侣呢，他们的脸上都洋溢着幸福。',Speaker=891824,}",
  [170160] = "{id=170160,Text='真让奈奈羡慕啊~',Speaker=891824,}",
  [170161] = "{id=170161,Text='啊？还没有吗？是不是最近很少来天津町呀？',Speaker=891824,}",
  [170162] = "{id=170162,Text='这里到处都是来参加花嫁节的恋人们呢。',Speaker=891824,}",
  [170163] = "{id=170163,Text='想到我的樱花树苗或许已经长大了，我就超~级兴奋呢！',Speaker=891824,Option='{看出来了,0},{超~级兴奋,0}',}",
  [170164] = "{id=170164,Text='冒险者，快陪我一起去看看樱花树苗吧。',Speaker=891824,Option='好的',}",
  [170165] = "{id=170165,Text='我已经迫不及待啦~',Speaker=891824,}",
  [170166] = "{id=170166,Text='我的樱花树苗……怎么会……',Speaker=891824,Option='完全没长大',}",
  [170167] = "{id=170167,Text='不可能！每天这么多的恋人，这么多[c][ffff00]甜蜜誓约[-][/c]的浇灌，它怎么还是这样。',Speaker=891824,}",
  [170168] = "{id=170168,Text='不对，它好像还在枯萎，这到底是怎么回事？',Speaker=891824,}",
  [170169] = "{id=170169,Text='冒险者，快拦住它！它的身上藏着甜蜜誓约。',Speaker=891824,}",
  [170170] = "{id=170170,Text='啊，糟糕……被发现了噜。',Speaker=891825,}",
  [170171] = "{id=170171,Text='呜呜呜，太过分了……实在太过分了噜！',Speaker=891825,Option='{……,0},{怎么还哭了？,0}',}",
  [170172] = "{id=170172,Text='你、你怎么好意思哭，明明是你偷走了浇灌樱花树的甜蜜誓约。',Speaker=891824,}",
  [170173] = "{id=170173,Text='原本可以很快长大的樱花树，现在反倒更小了，你这个小偷！',Speaker=891824,}",
  [170174] = "{id=170174,Text='我只是希望找个女朋友噜，不要打我噜，我不是故意的噜。',Speaker=891825,}",
  [170175] = "{id=170175,Text='像你这么漂亮的人，肯定有很多人喜欢，可是我一直没有人爱啊噜。',Speaker=891825,}",
  [170176] = "{id=170176,Text='他们只会欺负我，再看着我笨拙的样子发出哈哈的笑声，根本不会喜欢我噜。',Speaker=891825,}",
  [170177] = "{id=170177,Text='不！！不要哭了！！樱花树苗不喜欢负面情绪，你的眼泪和抱怨会让它枯萎的！',Speaker=891824,}",
  [170178] = "{id=170178,Text='天啦撸，可怜的我现在就连哭都不被允许了噜？',Speaker=891825,}",
  [170179] = "{id=170179,Text='救命啊，冒险者你快想办法安慰它，我可怜的樱花树啊……',Speaker=891824,Option='{我试试看……,0},{谁更可怜？,0}',}",
  [170180] = "{id=170180,Text='世界上没有人会喜欢我，谁会喜欢一只可爱、懂礼貌、又会撒娇的小猪噜？',Speaker=891825,Option='……',}",
  [170181] = "{id=170181,Text='呜呜呜，我太可怜了，我只是想如果能偷走一点甜蜜誓约，说不定也会有人因此喜欢我噜。',Speaker=891825,Option='{摸奇噜噜的头,4},{握住奇噜噜的手,5}',}",
  [170182] = "{id=170182,Text='哇，我感觉到一阵温暖，太棒噜！',Speaker=891825,}",
  [170183] = "{id=170183,Text='总算不哭了，奇噜噜，你偷走甜蜜誓约是没有用的，它只是樱花树的养分。',Speaker=891824,}",
  [170184] = "{id=170184,Text='呜……好吧，那我应该怎么做，才会有人喜欢我噜？',Speaker=891825,}",
  [170185] = "{id=170185,Text='呃，其实我也不太清楚，毕竟人家只是一个小小的见习巫女嘛。',Speaker=891824,}",
  [170186] = "{id=170186,Text='不过伟大的冒险者一定知道该怎么做！',Speaker=891824,Option='……',}",
  [170187] = "{id=170187,Text='有道理！他看起来就很厉害，一定被很多人喜欢，那么到底要怎么做，才能得到爱情噜？',Speaker=891825,Option='{努力提升自己,6},{积极找寻真爱,7},{学会关心别人,8}',}",
  [170188] = "{id=170188,Text='原来是这样噜，一定是因为我还不够强大！如果我变成厉害的魔物，一定会获得爱情噜！',Speaker=891825,}",
  [170189] = "{id=170189,Text='我明白了！看来是我还是不够努力，以后我要多去发现生活中的美好噜~',Speaker=891825,}",
  [170190] = "{id=170190,Text='噜，有道理！我只关注自己，却忽略了他人感受，或许这才是我找不到真爱的原因噜。',Speaker=891825,}",
  [170191] = "{id=170191,Text='太好了冒险者！它似乎终于醒悟啦，那快把你手中的甜蜜誓约还给樱花树吧！',Speaker=891824,}",
  [170192] = "{id=170192,Text='还给你们噜。',Speaker=891825,}",
  [170193] = "{id=170193,Text='太好……呃……可是樱花树只长大了一点点……怎么会这样呢？',Speaker=891824,}",
  [170194] = "{id=170194,Text='这么多的恋人，它应该长得很大了才对，奇噜噜，你是不是还偷偷藏了一些？',Speaker=891824,}",
  [170195] = "{id=170195,Text='才不是噜！哼，现在的我已经不想要甜蜜誓约噜，还不如这里的樱花花瓣漂亮噜。',Speaker=891825,}",
  [170196] = "{id=170196,Text='如果你们能帮我收集一些樱花花瓣，我就告诉你们樱花树苗长不大的原因噜。',Speaker=891825,}",
  [170197] = "{id=170197,Text='（小声）竟然还提出要求，真是超~级过分，冒险者，又得麻烦你了。',Speaker=891824,Option='{没关系,0},{已经习惯了,0}',}",
  [170198] = "{id=170198,Text='其实我只是想体验被送礼物的感觉噜！',Speaker=891825,Option='{好吧,0},{送给你,0}',}",
  [170199] = "{id=170199,Text='谢谢你噜！那我就告诉你们，其实这段时间有[c][ffff00]不少人来偷甜蜜誓约[-][/c]噜。',Speaker=891825,}",
  [170200] = "{id=170200,Text='什么？！！这简直超~级可怕！',Speaker=891824,}",
  [170201] = "{id=170201,Text='呜呜呜，怎么会这样！',Speaker=891824,}",
  [170202] = "{id=170202,Text='等等……你不是让我别哭，自己怎么哭起来噜……',Speaker=891825,Option='{冷静点！,0},{想想办法！,0}',}",
  [170203] = "{id=170203,Text='对对，我不能哭，可现在怎么办呀？樱花树苗长不大，我肯定没办法通过见习了。',Speaker=891824,}",
  [170204] = "{id=170204,Text='都怪我管理疏忽，难道、难道我天生不适合当巫女？人家怎么会这么笨！',Speaker=891824,}",
  [170205] = "{id=170205,Text='不要难过噜，刚才我试过噜，甜蜜誓约不能吃更不能消化，只要再抢回来就好噜，我可以偷偷告诉你们是谁抢走了它们。',Speaker=891825,Option='{真的吗？,0},{你怎么会知道？,0}',}",
  [170206] = "{id=170206,Text='因为我一开始只是想参加花嫁节噜，后来发现他们来偷东西噜，我蹲了好几天，才终于敢一起偷噜。',Speaker=891825,}",
  [170207] = "{id=170207,Text='可惜被你们发现噜。',Speaker=891825,Option='{……,0},{这么久,0}',}",
  [170208] = "{id=170208,Text='啊？我竟然一直没发现！都怪我不好，冒险者，你能不能帮我[c][ffff00]拿回这些甜蜜誓约[-][/c]？我真的很希望能通过考核。',Speaker=891824,Option='没问题！',}",
  [170209] = "{id=170209,Text='真的超~级感谢你，我后面一定会时时刻刻紧盯着，再也不会让人偷走甜蜜誓约了！',Speaker=891824,}",
  [170210] = "{id=170210,Text='看来不但是我，你也要加油噜。',Speaker=891825,}",
  [170211] = "{id=170211,Text='亲爱的冒险者，我真的超~级感谢你！现在甜蜜誓约都被追回了，樱花树也终于长大啦。',Speaker=891824,Option='{不客气,0},{我也很高兴,0}',}",
  [170212] = "{id=170212,Text='这段时间我几乎是寸步不离地看着樱花树，我敢保证，绝对没有魔物敢再来偷甜蜜誓约！',Speaker=891824,}",
  [170213] = "{id=170213,Text='走吧，我们一起去看看现在的樱花树，真的长得非常茂密哟~',Speaker=891824,}",
  [170214] = "{id=170214,Text='哼，你根本不懂我要什么！',Speaker=891826,}",
  [170215] = "{id=170215,Text='那你告诉我啊，你到底想要什么？',Speaker=891827,}",
  [170216] = "{id=170216,Text='如果什么事情都让我告诉你，那怎么体现出我们的爱呢？',Speaker=891826,}",
  [170217] = "{id=170217,Text='我知道了，一定是因为我拍的照片让你不满意，可我已经很努力了！',Speaker=891827,}",
  [170218] = "{id=170218,Text='根本不是这样的，我只是觉得，我没有找到一直想要的！',Speaker=891826,}",
  [170219] = "{id=170219,Text='哼！',Speaker=891826,}",
  [170220] = "{id=170220,Text='唉……',Speaker=891827,}",
  [170221] = "{id=170221,Text='不要在樱花树下吵架，好不容易才茂盛起来的……冒险者，快帮我去劝劝他们！',Speaker=891824,Option='{我试试……,0},{这也要我管？,0}',}",
  [170222] = "{id=170222,Text='我们两个在旅途中相识，恋爱后就开始环游大陆的计划。',Speaker=891826,}",
  [170223] = "{id=170223,Text='我喜欢拍照，他说喜欢给我拍照……',Speaker=891826,Option='这不是很好吗？',}",
  [170224] = "{id=170224,Text='可这次花嫁节，我们一起来到天津町，这么美丽的樱花下，我却发现拍出来的照片缺少了灵魂！',Speaker=891826,Option='{缺了什么？,0},{不太理解……,0}',}",
  [170225] = "{id=170225,Text='我也不知道，但我能感觉到，一定是哪里不对劲，所以我才会生气，可他——',Speaker=891826,}",
  [170226] = "{id=170226,Text='完全不能理解我！！！',Speaker=891826,Option='……',}",
  [170227] = "{id=170227,Text='唉唉唉，真不知道该从何说起，如你所见，我们原本是一对深爱彼此的恋人。',Speaker=891827,}",
  [170228] = "{id=170228,Text='从第一次见面起，我便被她深深吸引，不由自主地拿起相机为她拍了一张照片。',Speaker=891827,}",
  [170229] = "{id=170229,Text='那声音引起了她的注意，我们就这样认识了……',Speaker=891827,Option='{这不是重点……,0},{我不想听恋爱故事,0}',}",
  [170230] = "{id=170230,Text='好吧……可后来她的要求越来越多，每到一个地方，我都要为她拍摄许多照片，当然，我心甘情愿。',Speaker=891827,}",
  [170231] = "{id=170231,Text='这次我们来到天津町参加花嫁节，有许多情侣都将照片上传到[c][ffff00]时空相框[-][/c]里，可安吉拉却说我拍的照片毫无灵魂！',Speaker=891827,}",
  [170232] = "{id=170232,Text='这可是我拍了好几个小时的照片啊！我的腿都要断了！',Speaker=891827,Option='{……,0},{的确让人生气,0}',}",
  [170233] = "{id=170233,Text='怎么样，你了解到两个人为什么吵架了吗？',Speaker=891824,Option='因为拍照……',}",
  [170234] = "{id=170234,Text='[c][ffff00]旅行和拍照的确也是花嫁节的老传统了[-][/c]，可因为这个吵架，实在是超~级荒谬啦。',Speaker=891824,}",
  [170235] = "{id=170235,Text='怎么办，花嫁节这么浪漫的节日，千万不要吵架呀，我的樱花树好像也无精打采了些……',Speaker=891824,Option='{我也不知道,0},{让我想想,0}',}",
  [170236] = "{id=170236,Text='不然——由你来帮忙拍照吧！',Speaker=891824,Option='{我？,0},{这个……,0}',}",
  [170237] = "{id=170237,Text='是呀，你这么厉害，说不定能拍出安吉拉想要的灵魂呢！',Speaker=891824,Option='我试试吧。',}",
  [170238] = "{id=170238,Text='呃……你要给我拍照？',Speaker=891826,Option='是的',}",
  [170239] = "{id=170239,Text='那就让冒险者给你拍吧，说不定他能让你满意呢。',Speaker=891827,}",
  [170240] = "{id=170240,Text='哼，你这是什么态度！讨厌……反正我不满意你拍的照片，',Speaker=891826,}",
  [170241] = "{id=170241,Text='我还不满意呢，谁知道你到底想要什么。',Speaker=891827,Option='{……,0},[又来了,0}',}",
  [170242] = "{id=170242,Text='求求你们别吵了，唉……我可怜的樱花树啊，冒险者，你快给安吉拉拍照吧。',Speaker=891824,}",
  [170243] = "{id=170243,Text='我来看看！',Speaker=891826,}",
  [170244] = "{id=170244,Text='这也不是我想要的呀……',Speaker=891826,}",
  [170245] = "{id=170245,Text='你总是这么说，就算拍一万张也是如此，我看世界上根本没有人能拍出让你满意的照片！',Speaker=891827,}",
  [170246] = "{id=170246,Text='可我就是觉得缺了点什么嘛！',Speaker=891826,}",
  [170247] = "{id=170247,Text='又有小情侣在秀恩爱？吼吼吼，看来又能偷到一些甜蜜誓约了。',Speaker=891844,}",
  [170248] = "{id=170248,Text='救命！！！有魔物啊……',Speaker=891826,}",
  [170249] = "{id=170249,Text='安吉拉，快，来我身后，我会保护你的。',Speaker=891827,}",
  [170250] = "{id=170250,Text='超~级可恶的魔物，还想偷甜蜜誓约，冒险者，快教训它！',Speaker=891824,Option='交给我吧！',}",
  [170252] = "{id=170252,Text='救命……',Speaker=891826,}",
  [170253] = "{id=170253,Text='咔嚓！',Speaker=891824,}",
  [170254] = "{id=170254,Text='……怎么连一点甜蜜誓约都没有？明明这里有情侣。',Speaker=891844,}",
  [170255] = "{id=170255,Text='笨蛋，他们两个正在吵架呢！',Speaker=891824,}",
  [170256] = "{id=170256,Text='我已经记住你的样子了，如果你再来，我就让掌管神社的千代子小姐收拾你！',Speaker=891824,}",
  [170257] = "{id=170257,Text='好吧，算我倒霉。',Speaker=891844,}",
  [170258] = "{id=170258,Text='约翰，谢谢你刚才保护我，我不该和你生气的。',Speaker=891826,}",
  [170259] = "{id=170259,Text='不，保护你是我的职责，是我不应该惹你生气……我愿意继续给你拍照。',Speaker=891827,}",
  [170260] = "{id=170260,Text='咳咳，说到这个嘛，其实我刚才拍了一张照片喔。',Speaker=891824,}",
  [170261] = "{id=170261,Text='什么？',Speaker=891826,}",
  [170262] = "{id=170262,Text='正是他保护你的样子呢。',Speaker=891824,}",
  [170263] = "{id=170263,Text='（接过照片）天呐，这正是我需要的照片！',Speaker=891826,}",
  [170264] = "{id=170264,Text='原来我想要的不是自己在照片中多么美丽，而是我和约翰一起入镜，我竟然现在才知道。',Speaker=891826,}",
  [170265] = "{id=170265,Text='亲爱的，原来是这样……',Speaker=891827,}",
  [170266] = "{id=170266,Text='没有你和我一起，照片注定是不完整的，也是失去灵魂的，这说明我们早已成为一体。',Speaker=891826,}",
  [170267] = "{id=170267,Text='我好感动啊，第一次给你拍照时，我曾感慨怎么会有这么漂亮的女孩，而现在她却能时时刻刻陪伴着我。',Speaker=891827,}",
  [170268] = "{id=170268,Text='太好了！樱花树变得更漂亮了！这就是甜蜜誓约的力量，是爱情的力量！',Speaker=891824,}",
  [170269] = "{id=170269,Text='我真的超~级感动，果然恋人的甜蜜会让樱花树更漂亮啊。',Speaker=891824,Option='{是啊,0},{真好,0}',}",
  [170270] = "{id=170270,Text='也要感谢你们呀，是你们让我和约翰明白了，最美的照片不在于风景，而是一起合照的那个人。',Speaker=891826,}",
  [170271] = "{id=170271,Text='太棒了，祝福你们！对了，花嫁节会评选出[c][ffff00]最美CP[-][/c]的照片，[c][ffff00]记得将拍好的照片放到时空相框里[-][/c]。',Speaker=891824,}",
  [170272] = "{id=170272,Text='还可以[c][ffff00]去神社将你们的照片放在御守里[-][/c]，保佑你们的恋爱始终保持超~级甜蜜的状态。',Speaker=891824,Option='{好厉害……,0},{听起来很神奇,0}',}",
  [170273] = "{id=170273,Text='那最近我们一定要多拍几张！挑选出安吉拉最好看的一张。',Speaker=891827,}",
  [170274] = "{id=170274,Text='就现在吧，冒险者，拜托你帮我们拍一张照片吧！',Speaker=891826,Option='没问题',}",
  [170275] = "{id=170275,Text='嘻嘻，谢谢你啦，我们还要去很多很多地方拍照，到时候记得给我们投票呀！',Speaker=891826,}",
  [170276] = "{id=170276,Text='希望我们还能在大陆的其他地方相遇~',Speaker=891827,}",
  [170277] = "{id=170277,Text='真好啊，今天我们不但帮助了一对恋人，还又一次拯救了樱花树呢。',Speaker=891824,Option='{是啊,0},{总觉得说反了……,0}',}",
  [170278] = "{id=170278,Text='我还会继续守护者天津町的樱花树的！',Speaker=891824,}",
  [170279] = "{id=170279,Text='你又来！',Speaker=891824,}",
  [170280] = "{id=170280,Text='救命噜……',Speaker=891825,}",
  [170281] = "{id=170281,Text='冒险者你看，奇噜噜又来了，幸亏我现在每天都守着樱花树，这才被我抓到啦。',Speaker=891824,}",
  [170282] = "{id=170282,Text='我真的超~级生气！',Speaker=891824,}",
  [170283] = "{id=170283,Text='快放开我噜，你弄错了，我不是来偷甜蜜誓约的噜。',Speaker=891825,Option='{那是为什么？,0},{你说清楚,0}',}",
  [170284] = "{id=170284,Text='其实我是为了[c][ffff00]流星碎片[-][/c]噜。',Speaker=891825,}",
  [170285] = "{id=170285,Text='啊，原来是这样……是我弄错了。',Speaker=891824,Option='{流星碎片？,0},{这是什么？,0}',}",
  [170286] = "{id=170286,Text='没关系噜，我原谅你噜，冒险者，我来给你解释噜。',Speaker=891825,}",
  [170287] = "{id=170287,Text='或许你知道，每年花嫁节时，天津町都会迎来[c][ffff00]一年一度最盛大的流星雨[-][/c]噜。',Speaker=891825,Option='{知道,2},{不知道,3}',}",
  [170288] = "{id=170288,Text='没错噜，和爱的人一起看流星超浪漫噜，只可惜我还没找到爱人噜。',Speaker=891825,}",
  [170289] = "{id=170289,Text='天啦噜，你竟然不知道？传说在流星雨和樱花树下，和爱的人告白，就可以相守一生噜。',Speaker=891825,}",
  [170290] = "{id=170290,Text='而且流星雨后，天津町的各处会有[c][ffff00]掉落的流星碎片[-][/c]噜，听说可以[c][ffff00]用流星碎片去换取漂亮的发卡[-][/c]噜。',Speaker=891825,Option='{所以……,0},{然后呢？,0}',}",
  [170291] = "{id=170291,Text='就是那个噜……我想如果我能用流星发卡和喜欢的人告白，她一定会同意噜。',Speaker=891825,Option='你有喜欢的人了？',}",
  [170292] = "{id=170292,Text='这不是重点噜！重点是我的流星碎片被坏蛋抢走噜，所以我来樱花树下等你，希望你能帮我噜。',Speaker=891825,}",
  [170293] = "{id=170293,Text='然后你就被我撞见了，果然是我误会啦，真是超~级不好意思呢。',Speaker=891824,}",
  [170294] = "{id=170294,Text='冒险者，奇噜噜好可怜啊，你就帮帮他吧。',Speaker=891824,Option='为什么？',}",
  [170295] = "{id=170295,Text='毕竟它也是为了给喜欢的人告白嘛，在浪漫的花嫁节，说不定我们又能促成一对情侣呢。',Speaker=891824,Option='{有道理,0},[那好吧,0}',}",
  [170296] = "{id=170296,Text='太好噜！我们快去找那个大坏蛋噜！',Speaker=891825,}",
  [170297] = "{id=170297,Text='奈奈小姐真是认真负责噜。',Speaker=891825,}",
  [170298] = "{id=170298,Text='哎呦，笨蛋小猪，这次你是带来帮手了？',Speaker=891845,}",
  [170299] = "{id=170299,Text='你快把东西还给我噜，冒险者很厉害的噜！',Speaker=891825,}",
  [170300] = "{id=170300,Text='你这个胆小鬼，尽想着让别人帮忙，难怪现在还是单身呢，就算拿着流星碎片又有什么用？',Speaker=891845,}",
  [170301] = "{id=170301,Text='还不如让我送给我的八个老婆，她们都会很高兴的。',Speaker=891845,Option='{你确定？, 0},{我可不这么认为,0}',}",
  [170302] = "{id=170302,Text='但这是我的！你太坏了噜，怎么可以抢走我的东西噜。',Speaker=891825,}",
  [170303] = "{id=170303,Text='你可真烦人，少废话，被我抢到就是我的了，冒险者，我劝你可别不自量力。',Speaker=891845,Option='{哼！,0},{动手吧！,0}',}",
  [170304] = "{id=170304,Text='真是敬酒不吃吃罚酒。',Speaker=891845,}",
  [170305] = "{id=170305,Text='冒险者，加油噜！',Speaker=891825,}",
  [170306] = "{id=170306,Text='哼，算你有本事，还给你！',Speaker=891845,}",
  [170307] = "{id=170307,Text='太好噜，冒险者万岁！',Speaker=891825,}",
  [170308] = "{id=170308,Text='我终于拿回了流星碎片噜，这样应该可以顺利告白噜。',Speaker=891825,Option='{你喜欢谁？,0},{对谁告白？,0}',}",
  [170309] = "{id=170309,Text='其实我想保密的噜，但既然你帮了我，我就偷偷告诉你噜。',Speaker=891825,}",
  [170310] = "{id=170310,Text='我喜欢的，是奈奈小姐噜。',Speaker=891825,}",
  [170311] = "{id=170311,Text='怎么啦，你很惊讶噜？',Speaker=891825,Option='{很惊讶！,0},{其实也能猜到……,0}',}",
  [170312] = "{id=170312,Text='我知道噜，你肯定心里在想，奈奈小姐笨笨的，还冒失，又总爱说奇怪的超~级，我怎么会喜欢她噜？',Speaker=891825,Option='{……,0},{你也很奇怪噜,0}',}",
  [170313] = "{id=170313,Text='但是爱情这东西噜，是不能用常理来衡量的噜。',Speaker=891825,}",
  [170314] = "{id=170314,Text='我好喜欢奈奈小姐噜，如果兑换了流星发卡，不知道她能不能接受我噜？',Speaker=891825,Option='{不知道噜,0},{这很难说,0}',}",
  [170315] = "{id=170315,Text='有噜！冒险者，你和奈奈小姐的关系那么好，去帮我偷偷打听一下噜。',Speaker=891825,}",
  [170316] = "{id=170316,Text='求求你噜，难道你忍心拒绝这么可爱、懂礼貌、又会撒娇的小猪？',Speaker=891825,Option='{……,0},{好吧,0}',}",
  [170317] = "{id=170317,Text='谢谢你噜！你真好！我都要爱上你噜。',Speaker=891825,}",
  [170318] = "{id=170318,Text='你快去帮我探听噜，我偷偷躲起来噜。',Speaker=891825,}",
  [170319] = "{id=170319,Text='怎么样，奇噜噜的流星碎片找回来了吗？',Speaker=891824,Option='找回来了',}",
  [170320] = "{id=170320,Text='那就好，花嫁节果然有奇妙的魔力，一转眼奇噜噜都找到了喜欢的人。',Speaker=891824,Option='{其实……,0},{那个……,0}',}",
  [170321] = "{id=170321,Text='啊，这里堆积的花瓣太多了，不会影响到樱花树吸收甜蜜誓约吧？',Speaker=891824,}",
  [170322] = "{id=170322,Text='冒险者，可以麻烦你帮我清理一下吗？',Speaker=891824,Option='好吧',}",
  [170323] = "{id=170323,Text='太棒啦，我真的超~级兴奋，我的樱花树越长越漂亮啦。',Speaker=891824,}",
  [170324] = "{id=170324,Text='对了冒险者，你刚才是不是有话要问我？',Speaker=891824,Option='没错！',}",
  [170325] = "{id=170325,Text='怎么啦？我对你当然是知无不言啦。',Speaker=891824,Option='你有没有喜欢的对象？',}",
  [170326] = "{id=170326,Text='啊，这个问题真的超~级让人害羞呢，不过……有喔。',Speaker=891824,}",
  [170327] = "{id=170327,Text='咳咳，如果是别人的话，我恐怕不敢说，但冒险者一定可以接受的吧！',Speaker=891824,Option='{继续说……,0},{什么意思？,0}',}",
  [170328] = "{id=170328,Text='其实我喜欢的对象，并不是一个人喔……',Speaker=891824,Option='难道是？',}",
  [170329] = "{id=170329,Text='其实就是你想的那样，人家真是超~级羞于启齿呢。',Speaker=891824,Option='{哇哦,0},{其实它也……,0}',}",
  [170330] = "{id=170330,Text='没错，就是这棵樱花树啦！',Speaker=891824,Option='{什么？,0},{你在说什么？,0}',}",
  [170331] = "{id=170331,Text='啊？为什么你看起来那么惊讶，明明刚才不是猜到了？',Speaker=891824,Option='{这个真没有,0},{出乎意料的答案,0}',}",
  [170332] = "{id=170332,Text='好吧，人家知道这样超~级奇怪啦，可是这段时间我一直在看护它，照顾它。',Speaker=891824,}",
  [170333] = "{id=170333,Text='我要担心有没有魔物伤害它，还要担心它长不大，每天早晚我都要来看它，时时刻刻都想着它。',Speaker=891824,}",
  [170334] = "{id=170334,Text='而且它也一直陪伴着我，从来不会像其他人那样，嘲笑我的口音，我想这大概就是爱情吧！',Speaker=891824,Option='你确定吗？',}",
  [170335] = "{id=170335,Text='你怎么可以这样噜，人家竟然比不上一棵树？呜呜呜……',Speaker=891825,}",
  [170336] = "{id=170336,Text='啊，奇噜噜你快别哭了，求求你了！',Speaker=891824,}",
  [170337] = "{id=170337,Text='呜呜，你在心疼我噜？',Speaker=891825,}",
  [170338] = "{id=170338,Text='虽然不知道你为什么伤心，但我担心你的眼泪会污染这棵树的。',Speaker=891824,Option='啊这……',}",
  [170339] = "{id=170339,Text='哇哇哇，我最讨厌奈奈小姐噜！我再也不要理你噜！',Speaker=891825,}",
  [170340] = "{id=170340,Text='啊，真是超~级奇怪呢，奇噜噜到底怎么回事啦？',Speaker=891824,Option='{我也不知道,0},{你还是别知道的好,0}',}",
  [170341] = "{id=170341,Text='说回我们刚才的话题吧，虽然一开始只是为了考核，但现在我真的很喜欢这棵树呢。',Speaker=891824,Option='你喜欢就好……',}",
  [170342] = "{id=170342,Text='我听说秀明先生好像喜欢上了樱之灵呢，说不定这颗樱花树也会变成灵喔……',Speaker=891824,Option='{……,0},{或许吧,0}',}",
  [170343] = "{id=170343,Text='在花嫁节，爱情是不分种族的，虽然它只是一棵树，但我感觉也超~级爱它呢。',Speaker=891824,}",
  [170344] = "{id=170344,Text='对啦，为了感谢你一直帮我照顾樱花树，我要给你一样东西！',Speaker=891824,Option='什么礼物？',}",
  [170345] = "{id=170345,Text='这是[c][ffff00]樱誓琥珀[-][/c]，希望你能[c][ffff00]对心爱的人使用[-][/c]，这也是花嫁节的传统之一。',Speaker=891824,Option='樱誓琥珀？',}",
  [170346] = "{id=170346,Text='没错，当那个人感受到你的爱意后，你就能得到[c][ffff00]誓约之戒[-][/c]哟，拿着[c][ffff00]誓约之戒[-][/c]来找我吧，我将送你漂亮的礼物！',Speaker=891824,}",
  [170347] = "{id=170347,Text='太棒啦，看来你也在花嫁节找到了属于你的浪漫。',Speaker=891824,}",
  [170348] = "{id=170348,Text='我真的超~级为你开心哟~虽然流星和樱花都很短暂，但我相信你的爱情一定会甜蜜而持久！',Speaker=891824,}",
  [170349] = "{id=170349,Text='冒险者，你是超~级棒的人呢！你和你的爱人，一定会获得幸福~',Speaker=891824,}",
  [170350] = "{id=170350,Text='我——见习巫女奈奈——会在神社一直为你祈祷！',Speaker=891824,}",
  [170351] = "{id=170351,Text='你知道吗？旅行可是检验恋情的好办法！',Speaker=891838,Option='{为什么？,0},{是这样吗？,0}',}",
  [170352] = "{id=170352,Text='爱情的火花或许起源于一时的新鲜，但长久的陪伴才是爱情的真谛。',Speaker=891838,}",
  [170353] = "{id=170353,Text='旅行能展示恋人们面对压力、沟通和解决问题时的表现，可以说是婚姻的一种折射！',Speaker=891838,Option='原来如此',}",
  [170354] = "{id=170354,Text='所以花嫁之旅也是花嫁节的重要组成部分呢，只是选择旅行的地点可是很重要的~',Speaker=891838,}",
  [170355] = "{id=170355,Text='要风景好，不要太辛苦，要符合彼此的爱好，还要考虑浪漫。',Speaker=891838,Option='{这么多要点……,0},{你好懂啊,0}',}",
  [170356] = "{id=170356,Text='嘻嘻，我可是一只经验丰富的恋爱大师呢，这样吧，看在你一直耐心求教的份上，我来告诉你一些好的恋爱地点！',Speaker=891838,Option='{真的吗？,0},{那太好了！,0}',}",
  [170357] = "{id=170357,Text='要认真听哦，虽说爱情中走弯路并不算什么，但旅行中还是要少走弯路呢！',Speaker=891838,}",
  [170358] = "{id=170358,Text='我会指导你一些好看的风景，你可以拍照拍下来，以后带着爱人一起去，怎么样？',Speaker=891838,Option='{好主意！,0},{果然是大师,0}',}",
  [170359] = "{id=170359,Text='嘻嘻，以后有什么不懂的还可以来问我哟，松鼠我呀，最乐于助人啦。',Speaker=891838,}",
  [170360] = "{id=170360,Text='很好，你来的很准时，准时也是旅行中很重要的部分。',Speaker=891838,}",
  [170361] = "{id=170361,Text='今天我要告诉你的约会地点，是[c][ffff00]花海[-][/c]！',Speaker=891838,Option='{花海？,0},{我知道了,0}',}",
  [170362] = "{id=170362,Text='鲜花的氛围，鲜艳的色彩，你可以悄悄握住他的手，这可是天然的恋爱圣地啊！',Speaker=891838,}",
  [170363] = "{id=170363,Text='不过要小心，可别被蜜蜂或者什么虫子叮咬，所以要记得带防虫喷雾哟。',Speaker=891838,}",
  [170364] = "{id=170364,Text='怎么样？要不要现在去花海拍照留存？以后可以带着心爱的那个人一起前往~',Speaker=891838,Option='{现在就去,2},{我再想想,3}',}",
  [170365] = "{id=170365,Text='优柔寡断可不适合恋爱，不过按你的节奏来吧~',Speaker=891838,}",
  [170366] = "{id=170366,Text='不错嘛，这里果然很适合约会~',Speaker=891838,}",
  [170367] = "{id=170367,Text='你们徜徉在花海中，仿佛被甜蜜包围，想想都觉得浪漫啊！',Speaker=891838,}",
  [170368] = "{id=170368,Text='你做的很好，这是给你的奖励。',Speaker=891838,}",
  [170369] = "{id=170369,Text='今天要教你另外一课，那就是空间感！',Speaker=891838,}",
  [170370] = "{id=170370,Text='如果你想让你们的感情升温，约会中一定要想办法多创造独处空间。',Speaker=891838,}",
  [170371] = "{id=170371,Text='所以今天我要说的约会地点是[c][ffff00]摩天轮[-][/c]！',Speaker=891838,Option='{摩天轮？,0},{独处啊……,0}',}",
  [170372] = "{id=170372,Text='狭窄的空间，几乎能听见彼此加快的心跳，这可是天然的恋爱圣地啊！',Speaker=891838,}",
  [170373] = "{id=170373,Text='更有在最高处许愿能相守终身的传说，多么让人感动啊。',Speaker=891838,}",
  [170374] = "{id=170374,Text='怎么样？要不要现在去摩天轮拍照留存？以后可以带着心爱的那个人一起前往~',Speaker=891838,Option='{现在就去,2},{我再想想,3}',}",
  [170375] = "{id=170375,Text='优柔寡断可不适合恋爱，不过按你的节奏来吧~',Speaker=891838,}",
  [170376] = "{id=170376,Text='想当年我的前前前前男友，可就是在这里对我表白的呢~',Speaker=891838,}",
  [170377] = "{id=170377,Text='那一刻加速的心跳，真是——咳咳咳，算了，别提那些了，祝你们约会愉快。',Speaker=891838,}",
  [170378] = "{id=170378,Text='你做的很好，这是给你的奖励。',Speaker=891838,}",
  [170379] = "{id=170379,Text='你知道吗？你们人类似乎总会对毛茸茸的小动物产生怜爱，希望将它们抱在怀里的感觉。',Speaker=891838,Option='比如你？',}",
  [170380] = "{id=170380,Text='喂喂，你可不要靠过来，弄乱我新打理的毛发！不过没办法，萌萌的我就是这么招人喜爱。',Speaker=891838,}",
  [170381] = "{id=170381,Text='所以今天我要说的约会地点是[c][ffff00]猫猫之家[-][/c]！',Speaker=891838,Option='{猫猫之家？,0},{一群小猫咪？,0}',}",
  [170382] = "{id=170382,Text='很多人说小猫就像小朋友，当然在我心中小猫要容易照顾的多，当它用那双无辜大眼睛看着你的时候——',Speaker=891838,}",
  [170383] = "{id=170383,Text='你简直无法拒绝它的要求！',Speaker=891838,}",
  [170384] = "{id=170384,Text='怎么样？要不要现在去猫猫之家拍照留存？去那里约会可以提前体验到当父母的感觉，当然，是快乐版本的。',Speaker=891838,Option='{现在就去,2},{我再想想,3}',}",
  [170385] = "{id=170385,Text='优柔寡断可不适合恋爱，不过按你的节奏来吧~',Speaker=891838,}",
  [170386] = "{id=170386,Text='哎呀呀，光是看到照片，心都像要融化了呢，真是可爱啊。',Speaker=891838,}",
  [170387] = "{id=170387,Text='你知道吗？人类面对猫猫时，会不由自主发出完全不像自己的声音，还会学它们的叫声~',Speaker=891838,Option='{嘿嘿……,0},{好像是的,0}',}",
  [170388] = "{id=170388,Text='到时候带着你喜欢的人来约会，说不定你们彼此都能发现对方的另一面呢。',Speaker=891838,}",
  [170389] = "{id=170389,Text='你做的很好，这是给你的奖励。',Speaker=891838,}",
  [170390] = "{id=170390,Text='其实很多时候，所谓的浪漫通常是用光线营造的，人类的眼睛所能感知到的色彩，会让他们的心情产生变化。',Speaker=891838,Option='{是吗？, 0},{学到了！,0}',}",
  [170391] = "{id=170391,Text='想象一个场景，鱼儿从你们身边摇曳而过，水波荡漾，你们相互对视，从对方的眼中看见汪洋大海。',Speaker=891838,Option='哇哦……',}",
  [170392] = "{id=170392,Text='所以今天我要推荐的约会场景是[c][ffff00]水族馆[-][/c]！',Speaker=891838,}",
  [170393] = "{id=170393,Text='这里可是普隆德拉情侣约会调查中，排名居高不下总是位列前三的地点哟。',Speaker=891838,}",
  [170394] = "{id=170394,Text='怎么样？要不要现在去水族馆拍照留存？以后可以带着心爱的那个人一起前往~',Speaker=891838,Option='{现在就去,2},{我再想想,3}',}",
  [170395] = "{id=170395,Text='优柔寡断可不适合恋爱，不过按你的节奏来吧~',Speaker=891838,}",
  [170396] = "{id=170396,Text='真漂亮呀，根据我的实战经验，在这里告白，能将几率提高28%！',Speaker=891838,}",
  [170397] = "{id=170397,Text='当然了，你可以提前偷偷查资料，到时候不经意地展现出渊博的知识。',Speaker=891838,}",
  [170398] = "{id=170398,Text='我的这个建议怎么样？',Speaker=891838,Option='{还不错,0},{我考虑一下……,0}',}",
  [170399] = "{id=170399,Text='你做的很好，这是给你的奖励。',Speaker=891838,}",
  [170400] = "{id=170400,Text='怎么样，这段时间有没有感觉学到了很多？',Speaker=891838,Option='{有！,2},{没有！,3}',}",
  [170401] = "{id=170401,Text='我可是我们松鼠界最权威的恋爱大师！',Speaker=891838,}",
  [170402] = "{id=170402,Text='你听说过一句话吗？师傅领进门，修行在个人，看来你还需要更用心地学习。',Speaker=891838,}",
  [170403] = "{id=170403,Text='我们来说说今天的约会地点吧，其实最美的风景往往都发生在我们身边，只是我们往往会忽略它。',Speaker=891838,}",
  [170404] = "{id=170404,Text='所以你知道我今天要推荐的约会地点是哪里了吗？',Speaker=891838,Option='{家里！,4},{天津町,5}',}",
  [170405] = "{id=170405,Text='笨蛋！是天津町！！！咳，虽然家里约会也不是不可以，但一般比较熟悉才会这样啦。',Speaker=891838,}",
  [170406] = "{id=170406,Text='没错，你可真是一个有悟性的学生。',Speaker=891838,}",
  [170407] = "{id=170407,Text='难道你没发现吗？我站的位置其实就是一个很好的约会地点！',Speaker=891838,}",
  [170408] = "{id=170408,Text='这里有浪漫的樱花树，边上有水帘悬挂的瀑布，还可以站在桥上欣赏风景。',Speaker=891838,Option='原来如此！',}",
  [170409] = "{id=170409,Text='来吧，给我拍摄一张照片作为留念，',Speaker=891838,Option='{没问题！,0},{好的~,0}',}",
  [170410] = "{id=170410,Text='你的拍照水平嘛——还是不错的。',Speaker=891838,}",
  [170411] = "{id=170411,Text='要知道拍照也是旅行中的重要环节哟~好啦，当了你这么久的老师，总要送你点纪念品。',Speaker=891838,}",
  [170412] = "{id=170412,Text='这是神社的御守，到时候可以将你们的约会照片放在御守里，保佑恋情的顺利美满。',Speaker=891838,Option='{太感谢了,0},{老师真好！,0}',}",
  [170413] = "{id=170413,Text='再接再厉哟，其实约会最重要的不是找一个合适的地点，而是合适的人。',Speaker=891838,}",
  [170414] = "{id=170414,Text='又到了难得可见的月圆之时了，真让人激动。',Speaker=807603,}",
  [170415] = "{id=170415,Text='你知道吗？米德加尔特大陆离月亮最近的地方就在[c][ffff00]发乐斯灯塔[-][/c]。',Speaker=807603,}",
  [170416] = "{id=170416,Text='渴望月光照耀的人啊，请帮我拍摄一张照片作为此时的留念吧。',Speaker=807603,Option='{没问题！,0},{好的~,0}',}",
  [170417] = "{id=170417,Text='你回来啦~怎么样？今夜的月神之力是不是格外的充盈？',Speaker=807603,Option='{有！,0},{太美了,0}',}",
  [170418] = "{id=170418,Text='真漂亮呀，感谢你的分享~好啦，送你一些小礼物~希望明年的这个时候，我们还能一起见证月圆之时。',Speaker=807603,}",
  [170422] = "{id=170422,Text='嘿呀~冒险者，你可算来了！每当周末的时候，一群放学的捣蛋鬼就在依斯鲁得岛大肆破坏！',Speaker=1110,}",
  [170423] = "{id=170423,Text='仅凭我一人之力，可只有挨打的份……拜托拜托，帮帮我，好心的冒险者~',Speaker=1110,Option='{报酬呢？,0},{又是我？,0}',Emoji=15,}",
  [170424] = "{id=170424,Text='3个捣蛋鬼换1个「学院奖章」，行吧？这波可不亏，「学院奖章」可是能交换到时装呢！',Speaker=1110,Option='勉强答应',}",
  [170425] = "{id=170425,Text='切记！捣蛋鬼的出没时间：[c][ffff00]每周六、周日16点~24点[-][/c]，就在[c][ffff00]依斯鲁得岛[-][/c]！',Speaker=1110,}",
  [170426] = "{id=170426,Text='让我看看你的击杀记录……唔，不亏是你，远远超过了要求的数量！不亏是优秀的冒险者，这是你的报酬！',Speaker=1110,Emoji=20,}",
  [170428] = "{id=170428,Text='选择您想要前往的约会场所！',Speaker=891846,Option='{花海入口,2},{摩天轮入口,3},{猫猫之家入口,4},{梦幻水族馆入口,5},{我再想想,6}',}",
  [170436] = "{id=170436,Text='唉……猫老师总是在关键时刻消失……这次呀，又不知道躲在哪里晒太阳睡大觉了~',Speaker=891494,Option='猫老师？',Emoji=14,}",
  [170437] = "{id=170437,Text='没错，考虑到如何与猫猫相处是现在的热门话题！学院可是花大价钱聘请了猫老师来作为特约导师！',Speaker=891494,Option='喵喵？',}",
  [170438] = "{id=170438,Text='嘿，猫老师虽然看着只是一只[c][ffff00]普通的黑猫[-][/c]，但它可以与我们正常沟通！而且，它也学会了上班摸鱼……',Speaker=891494,}",
  [170439] = "{id=170439,Text='热心肠的冒险者，只要你找到了猫老师，我就赠予你「学院奖章」！',Speaker=891494,Option='{听着还不错,0},{已经习惯了,0}',}",
  [170440] = "{id=170440,Text='猫老师总是喜欢在[c][ffff00]角落里[-][/c]藏着~',Speaker=891494,Option='这范围也太大了！',}",
  [170441] = "{id=170441,Text='诶诶，别着急，我已经在你的地图上，圈出大致范围！',Speaker=891494,Option='出发！出发！',}",
  [170442] = "{id=170442,Text='吼吼~太棒了！猫老师被你找到了！这是你的奖励！',}",
  [170443] = "{id=170443,Text='[c][ffff00][PlayerName][-][/c]，你来啦！猫老师又躲起来了！',Speaker=891494,}",
  [170444] = "{id=170444,Text='拜托拜托，再去找找猫老师吧！我已经在你的地图上，圈出大致范围！',Speaker=891494,Option='出发！出发！',}",
  [170446] = "{id=170446,Text='我见到过甜蜜誓约噜，让我想想，那天我看到的魔物戴着个环——还是粉色的噜。',Speaker=893721,}",
  [170447] = "{id=170447,Text='我知道噜，一定是[c][ffff00]普隆德拉南门[-][/c]的[c][ffff00]甜蜜的天使波利[-][/c]噜。',Speaker=893721,}",
  [170448] = "{id=170448,Text='千万不要出卖我噜，我只是一只无辜但视力好的小猪猪~找回甜蜜誓约后，就来交给我噜。',Speaker=893721,}",
  [170449] = "{id=170449,Text='因为那颗樱花树，我好着急噜。',Speaker=893721,}",
  [170450] = "{id=170450,Text='所以你现在就要出发噜？',Speaker=893721,Option='出发',}",
  [170451] = "{id=170451,Text='甜蜜誓约到手，去交给奇噜噜吧。',}",
  [170452] = "{id=170452,Text='太好啦，冒险者你真的超~级棒啊！',Speaker=893721,}",
  [170453] = "{id=170453,Text='我一直在等你噜，让我再想想，我还看见了一只奇怪的蚂蚁噜，啊，一定是蚂蚁密穴的蚁后！',Speaker=893721,}",
  [170454] = "{id=170454,Text='对噜对噜，我是不会看错的噜~找回甜蜜誓约后，还是交给我噜。',Speaker=893721,}",
  [170455] = "{id=170455,Text='所以你现在就要出发噜？',Speaker=893721,Option='出发',}",
  [170456] = "{id=170456,Text='甜蜜誓约到手，去交给奇噜噜吧。',}",
  [170457] = "{id=170457,Text='太好啦，冒险者你真的超~级棒啊！',Speaker=893721,}",
  [170458] = "{id=170458,Text='来噜！今天我也在拼命回忆噜，有一个全身缠满绷带的可怕家伙……',Speaker=893721,}",
  [170459] = "{id=170459,Text='我想到噜，他好像坐着去梦罗克的船，一定是那对有名的情侣——蛇女和俄赛里斯。',Speaker=893721,}",
  [170460] = "{id=170460,Text='对噜对噜，我是不会看错的噜~找回甜蜜誓约后，还是交给我噜。',Speaker=893721,}",
  [170461] = "{id=170461,Text='所以你现在就要出发噜？',Speaker=893721,Option='出发',}",
  [170462] = "{id=170462,Text='甜蜜誓约到手，去交给奇噜噜吧。',}",
  [170463] = "{id=170463,Text='奇怪，这次拿回来的甜蜜誓约，好像变的更甜蜜了~',Speaker=893721,}",
  [170464] = "{id=170464,Text='让我想想噜，对噜，有一个绿色皮肤的家伙，偷走了好多甜蜜誓约！',Speaker=893721,}",
  [170465] = "{id=170465,Text='根据我这只见多识广小猪猪的建议，应该是兽人酋长噜，他朝着斐扬跑去噜。',Speaker=893721,}",
  [170466] = "{id=170466,Text='所以你现在就要出发噜？',Speaker=893721,Option='出发',}",
  [170467] = "{id=170467,Text='甜蜜誓约到手，去交给奇噜噜吧。',}",
  [170468] = "{id=170468,Text='甜蜜誓约灌溉出的樱花树真是令人着迷呢~！',Speaker=893721,}",
  [170469] = "{id=170469,Text='有一个口气超级大的家伙，偷走了甜蜜誓约，看起来是一只巨大的乌龟',Speaker=893721,}",
  [170470] = "{id=170470,Text='知道噜知道噜，一定是乌龟将军噜，他现在应该在克克魔海滩散步！',Speaker=893721,}",
  [170471] = "{id=170471,Text='所以你现在就要出发噜？',Speaker=893721,Option='出发',}",
  [170472] = "{id=170472,Text='甜蜜誓约到手，去交给奇噜噜吧。',}",
  [170473] = "{id=170473,Text='哇，好漂亮的樱花树，这都是你的功劳呢~',Speaker=893721,}",
  [170474] = "{id=170474,Text='冒险者，你知道花嫁节的传统吗？',Speaker=891839,}",
  [170475] = "{id=170475,Text='传说在流星雨的照耀下，樱花树旁带着御守的恋人们相互拥抱——',Speaker=891839,}",
  [170476] = "{id=170476,Text='他们将长久地陪伴在彼此身旁，永远保持这一刻的浪漫与甜蜜。',Speaker=891839,Option='{樱花树旁？,2},{流星雨？,3},{拥抱？,4},{我会参加的,5}',}",
  [170477] = "{id=170477,Text='天津町的樱花树被称为爱情树，近日[c][ffff00]见习巫女奈奈[-][/c]在照顾新栽种的樱花树，你有看见她吗？',Speaker=891839,}",
  [170478] = "{id=170478,Text='如果你帮助了她，应该能看到非常漂亮的樱花树呢。',Speaker=891839,}",
  [170479] = "{id=170479,Text='活动期间，你会在每晚18: 00-次日5: 00，看见一整夜的流星雨~',Speaker=891839,}",
  [170480] = "{id=170480,Text='天津町将同步降落大量流星碎片，收集[c][ffff00]五个流星碎片[-][/c]，就能获得漂亮的[c][ffff00]逐星闪烁[-][/c]哟。',Speaker=891839,}",
  [170481] = "{id=170481,Text='在流星雨持续期间，站在樱花树旁，你可以拥抱身旁喜欢的人哟，樱花树将送给你们祝福呢。',Speaker=891839,}",
  [170482] = "{id=170482,Text='不要害羞，主动去拥抱他人吧！',Speaker=891839,}",
  [170483] = "{id=170483,Text='太棒啦，希望你在花嫁节过得愉快呢~',Speaker=891839,}",
  [170484] = "{id=170484,Text='在这个甜蜜的季节里，连风都是甜的呢~',Speaker=891813,}",
  [170485] = "{id=170485,Text='这张明信片，请你收好哦~',Speaker=891813,}",
  [170496] = "{id=170496,Text='终于集齐了甜蜜誓约噜，[c][ffff00][PlayerName][-][/c]快去种下属于自己的樱花树吧~',Speaker=893721,}",
  [170497] = "{id=170497,Text='[c][ffff00][PlayerName][-][/c]，甜蜜誓约是樱花树最好的养料噜，我早就记下了是谁偷的噜~！',Speaker=893721,}",
  [170498] = "{id=170498,Text='散发着甜蜜气息的传送门，似乎可以直接通往天津町的花嫁节。',Speaker=891846,Option='{前往天津町,2},{我再想想,3}',}",
  [170500] = "{id=170500,Text='在天津町有一个传说，据说两个相爱的人，在流星雨下拥吻就可以幸福一辈子~',Speaker=891839,}",
  [170501] = "{id=170501,Text='快去和你相爱的那个人，在流星雨下拥吻吧~',Speaker=891839,}",
  [170502] = "{id=170502,Text='天津町的花嫁节是爱人们互相拥抱，许下一生誓言的盛大节日，快带上你心爱的他/她，一起来参加花嫁盛典吧~',Speaker=893729,}",
  [170503] = "{id=170503,Text='8/1~8/28期间，Base≥25级的冒险者通过普隆德拉的樱花女巫接取系列任务',Speaker=893729,}",
  [170504] = "{id=170504,Text='共4个连续的任务，每周一早晨5点会下发一环任务：\n完成第1~4个任务，可获得Zeny x 150,000、「真爱之心」x1、「B格猫福利券·宴」x5\n完成第5个任务，可以获得Zeny x 300,000、「真爱之心」x3、「B格猫福利券·宴」x10',Speaker=893729,}",
  [170505] = "{id=170505,Text='花嫁节即将开始，然而樱花树却日渐枯萎，原来是维持樱花绽放的甜蜜誓约被偷走啦，快想办法在大陆各处收集甜蜜誓约，让樱花重现活力，保证花嫁节的如期举行吧！',Speaker=893729,}",
  [170506] = "{id=170506,Text='8/7~8/28期间，Base≥25级的冒险者通过天津町的「奇噜噜」接取任务',Speaker=893729,}",
  [170507] = "{id=170507,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「真爱之心」x1\n完成第5个任务，可以获得Zeny x 300,000、「真爱之心」x3',Speaker=893729,}",
  [170508] = "{id=170508,Text='展示甜蜜恋爱也是花嫁节的传统，希望照相机能记录下你们相伴的身影~',Speaker=893729,}",
  [170509] = "{id=170509,Text='8/14~8/28期间，Base≥25级的冒险者通过天津町的「粉唧唧」接取任务',Speaker=893729,}",
  [170510] = "{id=170510,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「真爱之心」x1\n完成第5个任务，可以获得Zeny x 300,000、「真爱之心」x3',Speaker=893729,}",
  [170511] = "{id=170511,Text='一年一度的花嫁节正好赶上一年一度最大的流星雨，在这浪漫的氛围下，去拥抱你最重要的那个人吧~',Speaker=893729,}",
  [170512] = "{id=170512,Text='8/21~8/28期间，每晚18点至次日5点，天津町将会出现整晚的流星雨',Speaker=893729,}",
  [170513] = "{id=170513,Text='在流星雨持续期间，冒险者与其余冒险者完成「拥吻」双人动作，即可以获得「真爱礼盒」x1，该奖励每天可以获得1次',Speaker=893729,}",
  [170514] = "{id=170514,Text='在流星雨持续期间，天津町将出现大量的「流星碎片」，冒险者可以前往拾取，冒险者使用5个「流星碎片」即可合成活动限定头饰「逐星闪烁」。「流星碎片」通过拾取，每天仅可获得1个。',Speaker=893729,}",
  [170515] = "{id=170515,Text='花嫁节恰逢洛阳的七夕，许仙陷入莫名的惆怅中，就在这时他在桥上见到了一个熟悉的背影，正是梦中那人，但一群可恶的魔物却阻挡着他靠近的脚步。',Speaker=893729,}",
  [170516] = "{id=170516,Text='活动期间，每周六、周日晚16点至24点，天津町将有大量魔物入侵，击败入侵的魔物、完成活动任务均可以获得「萤火之约」',Speaker=893729,}",
  [170518] = "{id=170518,Text='8/21~8/28期间，婚礼仪式半价，「圣洁系列时装」、「樱之系列时装」、「至臻的爱恋系列时装」5折优惠，还没结婚的冒险者要抓住机会哟~',Speaker=893729,}",
  [170528] = "{id=170528,Text='新的一年开学季到了，克里图拉学院迎来了新一届的学员。冒险者被委托作为学员们的接引人~在引导新人们的过程中，又会有怎样的奇遇呢？',Speaker=893715,}",
  [170529] = "{id=170529,Text='9/1~10/1期间，Base≥25级的冒险者通过普隆德拉广场的「格里斯」接取系列任务',Speaker=893715,}",
  [170530] = "{id=170530,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「学院奖章」x5\n完成第5个任务，可获得Zeny x 300,000、「学院奖章」x5、「B格猫福利券·宴」x30',Speaker=893715,}",
  [170531] = "{id=170531,Text='在进行引导新生的过程中，学院周边发生了许多奇怪的事情，白银之风担任起学院守护者的身份，前往学院及普隆德拉周边进行探索吧！',Speaker=893715,}",
  [170532] = "{id=170532,Text='9/4~10/1期间，Base≥25级的冒险者通过普隆德拉广场的「凯奇」接取系列任务',Speaker=893715,}",
  [170533] = "{id=170533,Text='共7个连续的任务：\n完成第1~6个任务，可获得Zeny x 150,000、「学院奖章」x1\n完成第7个任务，可获得Zeny x 300,000、「学院奖章」x3',Speaker=893715,}",
  [170534] = "{id=170534,Text='猫老师们给各位新来的学员布置了特殊的课后作业。完成猫老师的试炼，成为优秀的学员吧！',Speaker=893715,}",
  [170535] = "{id=170535,Text='9/11~10/1期间，Base≥25级的冒险者通过普隆德拉广场的「格里斯」接取系列任务',Speaker=893715,}",
  [170536] = "{id=170536,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「学院奖章」x1\n完成第5个任务，可获得Zeny x 300,000、「学院奖章」x3',Speaker=893715,}",
  [170537] = "{id=170537,Text='捣蛋鬼总喜欢在放学后到处搞破坏，这次它们将在依斯鲁得岛集结……',Speaker=893715,}",
  [170538] = "{id=170538,Text='活动期间，每周六、周日晚16点至24点，依斯鲁得岛将有大量魔物入侵~',Speaker=893715,}",
  [170539] = "{id=170539,Text='击败「生气的捣蛋鬼」可以获得「学院守护礼盒」\n击败「暴怒的捣蛋鬼」概率获得「学院符文礼盒」和「学院典藏礼盒」，不要错过哟~',Speaker=893715,}",
  [170540] = "{id=170540,Text='由于白银之风近期的出色表现，得到了学院长的大力认可~所以，本学期的学院交换祭就由热血武僧布鲁斯来组织了~用奇奇怪怪的材料与布鲁斯交换各种实用的道具吧~',Speaker=893715,}",
  [170541] = "{id=170541,Text='9/18~9/25期间，冒险者通过普隆德拉广场的「布鲁斯」参与交换活动~',Speaker=893715,}",
  [170542] = "{id=170542,Text='与他交换材料，可获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=893715,}",
  [170543] = "{id=170543,Text='恰逢一年一度的满月时节，希望照相机能记录下此刻美好的夜晚~',Speaker=893715,}",
  [170544] = "{id=170544,Text='9/29~10/1期间，Base≥25级的冒险者通过普隆德拉音乐盒附近的「诺恩·薇」接取任务',Speaker=893715,}",
  [170545] = "{id=170545,Text='完成任务，可获得限定嘴饰「兔兔慕斯」x1、「弗雷硬币」x1、「闪耀的符文石」x1、「B格猫福利券·宴」x30',Speaker=893715,}",
  [170546] = "{id=170546,Text='阿罗格被战胜后，[c][ff0000]时空裂隙[-][/c]逐渐稳定了……',Speaker=891003,}",
  [170547] = "{id=170547,Text='但此次的冲击过于强大，在米德加尔特大陆突然出现了多处异常的裂隙！',Speaker=891003,}",
  [170548] = "{id=170548,Text='在魔物撕开裂隙来袭之前，请前往各地清除裂隙，再次拯救这个世界吧！勇敢的冒险者！',Speaker=891003,Option='没问题',}",
  [170549] = "{id=170549,Text='[c][ffff00]普隆德拉西门[-][/c]出现了多处异常的裂隙，你只需找到并消灭一处即可。',Speaker=891003,}",
  [170550] = "{id=170550,Text='那么，现在出发吧？',Speaker=891003,Option='好',}",
  [170551] = "{id=170551,Text='异常的裂隙已经被消除！快将结果告诉哈尔特吧！',}",
  [170552] = "{id=170552,Text='让我看看……嗯……异常裂隙有所减少……',Speaker=891003,}",
  [170553] = "{id=170553,Text='这是你的报酬！探测其他异常的地方需要一点时间，请明天再来找我吧，勇敢的冒险者。',Speaker=891003,Option='好',}",
  [170554] = "{id=170554,Text='你来啦~勇敢的冒险者，让我看看探测的结果……',Speaker=891003,}",
  [170555] = "{id=170555,Text='[c][ffff00]哥布灵森林[-][/c]出现了多处异常的裂隙，你只需找到并消灭一处即可。',Speaker=891003,}",
  [170556] = "{id=170556,Text='[c][ffff00]苏克拉特沙漠[-][/c]出现了多处异常的裂隙，你只需找到并消灭一处即可。',Speaker=891003,}",
  [170557] = "{id=170557,Text='[c][ffff00]古城郊外[-][/c]出现了多处异常的裂隙，你只需找到并消灭一处即可。',Speaker=891003,}",
  [170558] = "{id=170558,Text='[c][ffff01]妙勒尼山脉[-][/c]出现了多处异常的裂隙，你只需找到并消灭一处即可。',Speaker=891003,}",
  [170559] = "{id=170559,Text='[c][ffff00]斐扬树林[-][/c]出现了多处异常的裂隙，你只需找到并消灭一处即可。',Speaker=891003,}",
  [170560] = "{id=170560,Text='这是你的报酬！异常的裂隙大幅减少，感谢你的帮助，勇敢的冒险者。',Speaker=891003,}",
  [170561] = "{id=170561,Text='通过时空裂隙到来的不止是五位铠甲武士，还有妖邪帝王的爪牙！这究竟是世界线的变动，还是新世界的开端……',Speaker=893738,}",
  [170562] = "{id=170562,Text='……啊，我有点激动过头了。听着，冒险者，妖邪帝王的爪牙利用铠甲的残余力量召唤了大量魔物！',Speaker=893738,}",
  [170563] = "{id=170563,Text='魔物被暂时困在了「封印之间」。还用说吗？当然是要消灭魔物，再次拯救世界！',Speaker=893738,}",
  [170564] = "{id=170564,Text='仁、义、礼、智、信通过时空裂隙穿越到了米德加尔特大陆，代表着正义的异乡武士他们在这片大陆将展开一段新的冒险旅程。汇集魔神坛斗士，去击败妖邪帝王吧！',Speaker=891003,}",
  [170565] = "{id=170565,Text='8/1~9/1期间，Base≥25级的冒险者通过普隆德拉广场的「哈尔特」接取魔神坛斗士系列任务。',Speaker=891003,}",
  [170566] = "{id=170566,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「烈火之力」x5\n完成第5个任务，可获得Zeny x 300,000、「烈火之力」x5、「魔神坛斗士扭蛋币」x30',Speaker=891003,}",
  [170567] = "{id=170567,Text='「火焰神真田辽」一行人想在米德加尔特大陆多驻留一段时间，继续进行各自的修行。完成火焰神的委托，帮忙寻找合适的修行之地吧~',Speaker=891003,}",
  [170568] = "{id=170568,Text='8/7~9/1期间，Base≥25级的冒险者通过普隆德拉西门的「真田辽」接取寻找修行之地系列任务。',Speaker=891003,}",
  [170569] = "{id=170569,Text='共5个连续的任务：\n完成每个任务，获得Zeny x 150,000，「烈火之力」x5。',Speaker=891003,}",
  [170570] = "{id=170570,Text='通过时空裂隙到来的不止是五位铠甲武士，还有妖邪帝王的爪牙！爪牙利用铠甲的残余力量召唤了大量魔物！',Speaker=891003,}",
  [170571] = "{id=170571,Text='8/14~8/21期间，冒险者通过普隆德拉广场的「神秘人」进入副本「封印之间」，消灭不断涌来的魔物，可获得丰厚的奖励！',Speaker=891003,}",
  [170572] = "{id=170572,Text='完成第5波，可获得「烈火之力」x1\n完成第10波，可获得「烈火之力」x3\n完成第15波，可获得「烈火之力」x5、「妖邪散落的宝物」x1',Speaker=891003,}",
  [170573] = "{id=170573,Text='阿罗格被战胜后，时空裂隙逐渐稳定了……但此次的冲击过于强大，在米德加尔特大陆突然出现了多处异常的裂隙！在魔物撕开裂隙来袭之前，请前往各地清除裂隙，再次拯救这个世界吧！勇敢的冒险者！',Speaker=891003,}",
  [170574] = "{id=170574,Text='8/21~9/1期间，Base≥25级的冒险者通过普隆德拉广场的「哈尔特」接取善后工作系列任务。',Speaker=891003,}",
  [170575] = "{id=170575,Text='共5个连续的任务：\n完成每个任务，获得Zeny x 150,000，「烈火之力」x5。',Speaker=891003,}",
  [170576] = "{id=170576,Text='联动职业返场啦~8/1~9/1期间，普隆德拉广场将登场联动限定扭蛋机「限时联动」！',Speaker=891003,}",
  [170577] = "{id=170577,Text='只需要使用B格猫金币参与10次「限时联动」扭蛋机，即可额外获赠联动职业转职道具「迦雄须神的来信」！',Speaker=891003,}",
  [170578] = "{id=170578,Text='本期扭蛋机新增了限定双人坐骑「金耀红炎王」、全新卡片「金耀红炎王卡片」！一定不要错过哦~',Speaker=891003,}",
  [170580] = "{id=170580,Text='魔神坛斗士复刻联动扭蛋上新啦！只需要使用B格猫金币参与10次「限时联动」扭蛋机，即可额外获赠联动职业转职道具「迦雄须神的来信」！',Speaker=893750,}",
  [170581] = "{id=170581,Text='另外，本期扭蛋除了给冒险者带来复刻外观之外，还新增了[c][ffff00]限定双人坐骑「金耀红炎王」[-][/c]，冒险者千万不要错过哟~',Speaker=893750,}",
  [170582] = "{id=170582,Text='冒险者如果抽到了重复的外观也不要气馁，可以在我这分解成[c][ffff00]「辉煌精华」[-][/c]，「辉煌精华」可以兑换全新的[c][ffff00]限定卡片「金耀红炎王卡片」[-][/c]以及所有联动扭蛋中的外观道具哦~',Speaker=893750,}",
  [170583] = "{id=170583,Text='那么，这些「辉煌精华」就当作是我送你的见面礼啦~祝你好运~',Speaker=893750,}",
  [170585] = "{id=170585,Text='[c][ffff00]「辉煌精华」[-][/c]价值连城，冒险者可以通过分解联动扭蛋中的外观类道具获得',Speaker=893750,}",
  [170586] = "{id=170586,Text='使用[c][ffff00]「辉煌精华」[-][/c]可以兑换全新的[c][ffff00]限定卡片「金耀红炎王卡片」[-][/c]以及所有联动扭蛋中的外观道具哦~',Speaker=893750,}",
  [170588] = "{id=170588,Text='一年一度的美食节开始啦~这个时候可不要在乎体重了，尽情地去品味各种美味吧！当然，每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！',Speaker=893752,}",
  [170589] = "{id=170589,Text='[c][ffff00][PlayerName][-][/c]，别愣着哦~快收下啦~',Speaker=893752,Option='打开',}",
  [170590] = "{id=170590,Text='[c][ffff00][PlayerName][-][/c]，您终于来了！我在整理今日的委托时，发现了一个特殊的求助……',Speaker=1373,}",
  [170591] = "{id=170591,Text='你有兴趣接取这个委托吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=1373,Option='{我很感兴趣,4},{不是很感兴趣,3}',}",
  [170592] = "{id=170592,Text='明白了。如果您改变主意，可以再来找我。',Speaker=1373,}",
  [170593] = "{id=170593,Text='感谢您的热情。那么，我将给您介绍一下这个特殊的委托。',Speaker=1373,}",
  [170594] = "{id=170594,Text='这是一个来自流星林的求助。流星林的小当家[c][ffff00]云伊[-][/c]想要制作出一份永远也吃不完的料理，想要让更多人填饱肚子……',Speaker=1373,Option='{能做到吗,0},{怎么做呢,0}',}",
  [170595] = "{id=170595,Text='唔……美食节举办了这么多次，这样的料理还未存在过……这样好了，我送您去一趟流星林，当面拜访一下委托人吧。',Speaker=1373,Option='好',}",
  [170596] = "{id=170596,Text='真是神奇，竟然有人接取了我的委托！嘿嘿，丑话说在前面，我可没有什么丰厚的报酬给你~',Speaker=826529,Option='{放弃还来得及吗,0},{这种事可见多了,0}',}",
  [170597] = "{id=170597,Text='诶诶，别跑呀！我们现在要做的可是一件前无古人后无来者的壮举呢！壮举！',Speaker=826529,Option='展开说说',}",
  [170598] = "{id=170598,Text='你听说过“传说中的厨具”吗？比起普通的锅碗瓢盆，使用它们的话，能做出更美味甚至还会被附魔的神奇料理！',Speaker=826529,Option='如何获得',}",
  [170599] = "{id=170599,Text='我可是花费了大价钱买到的线索——[c][ffff00]姜饼城的雪人[-][/c]知道传说厨具的存在！',Speaker=826529,}",
  [170600] = "{id=170600,Text='可现在天色已晚，请善良的冒险者，明日前往一趟[c][ffff00]姜饼城[-][/c]吧！嘻嘻，今天的报酬，请你先收下吧~',Speaker=826529,}",
  [170601] = "{id=170601,Text='你想陪我玩吗？',Speaker=6983,Option='说明来意',}",
  [170602] = "{id=170602,Text='唔……我确实知道传说中的厨具……可我在这里站太久，太无聊了……',Speaker=6983,}",
  [170603] = "{id=170603,Text='这样好了~你陪我玩一局猜拳，我就告诉你~',Speaker=6983,Option='好',}",
  [170604] = "{id=170604,Text='三！',Speaker=6983,}",
  [170605] = "{id=170605,Text='二！',Speaker=6983,}",
  [170606] = "{id=170606,Text='一！',Speaker=6983,}",
  [170607] = "{id=170607,Text='剪刀石头布——',Speaker=6983,Option='{石头,40},{剪刀,50},{布,60}',}",
  [170608] = "{id=170608,Text='太遗憾了，再接再厉！',Speaker=6983,}",
  [170609] = "{id=170609,Text='嘿嘿，我赢了！',Speaker=6983,Emoji=17,}",
  [170610] = "{id=170610,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=6983,Option='{再玩一次,21},{不玩啦,102}',}",
  [170611] = "{id=170611,Text='太厉害了！我玩得很开心！喏，你拿走吧，我的木棒~',Speaker=6983,Option='木棒？',}",
  [170612] = "{id=170612,Text='嗯嗯，就是我的木棒啦！使用它来搅拌食物的话，会加速熟成哦~不管是多少份的料理，也能在一瞬间完成呢！',Speaker=6983,Option='太棒了！',}",
  [170613] = "{id=170613,Text='另外，这是一起玩耍的报酬，请你收下吧~',Speaker=6983,}",
  [170614] = "{id=170614,Text='咦，你身上有股特别的味道，难道你寻找到了传说中的厨具？',Speaker=826529,Option='当然',}",
  [170615] = "{id=170615,Text='你说是这个雪人一直携带的木棒？还是通过猜拳赢得的！',Speaker=826529,Emoji=1,}",
  [170616] = "{id=170616,Text='事不宜迟，我们马上去寻找下一件传说厨具吧！',Speaker=826529,Option='你有线索',Emoji=5,}",
  [170617] = "{id=170617,Text='狼外婆的平底锅——专门用于处理肉类，可使肉类的腥味荡然无存。就在雾之森林，马上出发吧！',Speaker=826529,Option='好',}",
  [170618] = "{id=170618,Text='冒险者，你回来了！',Speaker=826529,}",
  [170619] = "{id=170619,Text='你拿到了吗？太谢谢你了！！',Speaker=826529,Option='说明情况',}",
  [170620] = "{id=170620,Text='果然你是最可靠的！',Speaker=826529,}",
  [170621] = "{id=170621,Text='唉……？这样一来，我们就差最后一件传说厨具了——魔力铁炉的保鲜库！据说是可以将食材长久保鲜！',Speaker=826529,}",
  [170622] = "{id=170622,Text='不过天色不早了，今天你快回去休息吧。',Speaker=826529,}",
  [170623] = "{id=170623,Text='明天我在这里等你！这是我的最最最最最后一个请求，真的是最最最最最后一个。',Speaker=826529,Option='好',}",
  [170624] = "{id=170624,Text='冒险者，你来了！今天我们要去找到最特别的魔力铁炉！',Speaker=826529,Option='{魔力铁炉？,0},{哪一个？,0}',}",
  [170625] = "{id=170625,Text='没错，米德加尔特大陆上有数不清的魔力铁炉！如果要说哪一只最特别的话……那应该就是普隆德拉的了！',Speaker=826529,Option='为什么？',}",
  [170626] = "{id=170626,Text='笨笨，它存在的时间最长，见识肯定最多！',Speaker=826529,}",
  [170627] = "{id=170627,Text='不要犹豫了！出发吧，去拜访一下普隆德拉的魔力铁炉！',Speaker=826529,}",
  [170628] = "{id=170628,Text='喀嚓~喀……喀……！消化不良，需要……需要[c][ffff00]3个粘稠液体[-][/c]……才能恢复……恢复！',Speaker=1550,}",
  [170629] = "{id=170629,Text='（魔力铁炉好像卡住了……先帮它找到[c][ffff00]3个粘稠液体[-][/c]吧！）',}",
  [170630] = "{id=170630,Text='喀……喀……好心……的冒险者，你……你有[c][ffff00]3个粘稠液体[-][/c]吗？',Speaker=1550,Option='{立即交出,2},{默默走开,10}',}",
  [170631] = "{id=170631,Text='不……还不够……请你帮我找到[c][ffff00]3个粘稠液体[-][/c]吧！',Speaker=1550,}",
  [170632] = "{id=170632,Text='喀嚓~喀嚓~我又可以了！是你帮助了我吗？太谢谢你了！',Speaker=1550,}",
  [170633] = "{id=170633,Text='这是你的报酬！我还想再感谢你一次，这个保鲜库也一并送给你吧！',Speaker=1550,Option='谢谢',}",
  [170634] = "{id=170634,Text='没想到这么快，就集齐了全部的传说厨具！拿上它们后，感觉全身都充满了力量！',Speaker=826529,}",
  [170635] = "{id=170635,Text='太感谢你了[c][ffff00][PlayerName][-][/c]，多亏你的帮助，我完成了心愿！',Speaker=826529,}",
  [170636] = "{id=170636,Text='我要带着你这份热情，留在流星林继续帮助更多的人！',Speaker=826529,}",
  [170637] = "{id=170637,Text='路过的冒险者，请问你有空吗？',Speaker=893753,Option='什么事',}",
  [170638] = "{id=170638,Text='没错，如你所见，我是一位料理师，我想要知道更多人的口味~',Speaker=893753,}",
  [170639] = "{id=170639,Text='我在这里准备了大量的美食，都可以免费品尝！',Speaker=893753,}",
  [170640] = "{id=170640,Text='另外，每天可以在我这里领取1份[c][ffff00]「神秘料理盒」[-][/c]！对其他冒险者使用这份神秘料理，你们将感受到美食的魔力！',Speaker=893753,Option='让我试试',}",
  [170645] = "{id=170645,Text='冒险者，你知道吗？美食节有一个惯例，就是要为每位老人送去可口的美食！',Speaker=892122,}",
  [170646] = "{id=170646,Text='让我看看……还有谁没有收到……唔，普隆德拉的卡米尔奶奶！',Speaker=892122,}",
  [170647] = "{id=170647,Text='请冒险者将这份可口的香浓鱼汤给卡米尔奶奶送去吧~',Speaker=892122,Option='好',}",
  [170648] = "{id=170648,Text='哦？年轻人找奶奶我有什么事情？',Speaker=821185,Option='说明来意',}",
  [170649] = "{id=170649,Text='没想到一把年纪了还有人惦记着，那真是太麻烦你了~',Speaker=821185,}",
  [170650] = "{id=170650,Text='奶奶我啊，没有什么好东西可以给你了，就这些小玩意送给你吧~',Speaker=821185,}",
  [170651] = "{id=170651,Text='嘿呀~冒险者，你可算来了！每当周末的时候，贪吃的魔物将簇拥在洛阳的花街欺负着各个小吃摊主！',Speaker=809812,}",
  [170652] = "{id=170652,Text='仅凭我一人之力，可只有挨打的份……拜托拜托，帮帮我，好心的冒险者~',Speaker=809812,Option='有报酬？',Emoji=15,}",
  [170653] = "{id=170653,Text='消灭3个偷吃鼬换1个「美味的见证」，行吧？这波可不亏，「美味的见证」可是能交换到时装呢！',Speaker=809812,Option='勉强答应',}",
  [170654] = "{id=170654,Text='切记！魔物的出没时间：[c][ffff00]每周六、周日16点~24点[-][/c]，就在[c][ffff00]花街[-][/c]！',Speaker=809812,}",
  [170655] = "{id=170655,Text='让我看看你的击杀记录……唔，不亏是你，远远超过了要求的数量！不亏是优秀的冒险者，这是你的报酬！',Speaker=809812,Emoji=20,}",
  [170659] = "{id=170659,Text='一年一度的黄金美食节再次降临普隆德拉！传说中的料理配方「黄金蛋炒饭」再现人间，看似平平无奇的一道料理背后，却隐藏着一个沉寂了千年的秘密……',Speaker=893752,}",
  [170660] = "{id=170660,Text='10/1~10/31期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「胡雷特」[-][/c]，接取系列任务~',Speaker=893752,}",
  [170661] = "{id=170661,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「美味的见证」x5\n完成第5个任务，可获得Zeny x 300,000、「美味的见证」x5、「B格猫福利券·宴」x30',Speaker=893752,}",
  [170664] = "{id=170664,Text='流星林的小当家云伊有一个梦想，就是能做出更美味的食物填饱更多人的肚子。出发吧，冒险者！帮助云伊实现梦想！',Speaker=893752,}",
  [170665] = "{id=170665,Text='10/9~10/31期间，Base等级≥25级的冒险者前往普隆德拉找到[c][ffff00]「雪莉」[-][/c]，接取系列任务~',Speaker=893752,}",
  [170666] = "{id=170666,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000，「美味的见证」x1\n完成第5个任务，可以获得奖励Zeny x 300,000，「美味的见证」x3',Speaker=893752,}",
  [170667] = "{id=170667,Text='料理师查理想在普隆德拉开一家美食商店，但究竟怎样的味道才会被大家接受呢？与其他冒险者分享查理特制的「神秘料理盒」，与更多的冒险者一起分享美食的力量吧！',Speaker=893752,}",
  [170668] = "{id=170668,Text='10/16~10/23期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「查理」[-][/c]，每日可领取「神秘料理盒」x1：\n对其他冒险家使用「神秘料理盒」后，你和TA都可获得1次“美食的魔力”',Speaker=893752,}",
  [170669] = "{id=170669,Text='同时获得「美味的见证」x1、Zeny x 150,000。这个魔力会将头饰随机变成任一种美食哦~\n[c][ffff00]美食的魔力：每天账号获取上限为2次[-][/c]',Speaker=893752,}",
  [170670] = "{id=170670,Text='10/23~10/26期间，Base等级≥25级的冒险者前往普隆德拉找到[c][ffff00]「胡雷特」[-][/c]接取任务~',Speaker=893752,}",
  [170671] = "{id=170671,Text='完成任务后，可获得节日礼物：「弗雷硬币」x1、「闪耀的符文石」x1、「B格猫福利券·宴」x30',Speaker=893752,}",
  [170672] = "{id=170672,Text='偷吃的魔物将簇拥在洛阳的花街欺负着各个小吃摊主，消灭游行的魔物，保卫美食街吧！',Speaker=893715,}",
  [170673] = "{id=170673,Text='活动期间，每周六、周日晚16点至24点，[c][ffff00]花街[-][/c]将有大量魔物入侵~',Speaker=893715,}",
  [170674] = "{id=170674,Text='击败「赊账鳄鱼人」可以获得「美食街守护礼盒」\n击败「大嘴皮里恩」概率获得「美食街符文礼盒」和「美食街的谢礼·MVP」，不要错过哟~',Speaker=893715,}",
  [170677] = "{id=170677,Text='[c][ffff00][PlayerName][-][/c]，你看到月亮上的那只大兔子了吗？',Speaker=893759,}",
  [170678] = "{id=170678,Text='听说那只大兔子叫[c][ffff00]「明祥玉兔」[-][/c]，只有在每年月亮最圆的时候才会出现哦~',Speaker=893759,}",
  [170679] = "{id=170679,Text='收集一定数量的[c][ffff00]「追月礼券」[-][/c]，就可以兑换[c][ffff00]「明祥玉兔」[-][/c]哦',Speaker=893759,}",
  [170680] = "{id=170680,Text='同时，如果你有多余的[c][ffff00]「中秋系列外观」[-][/c]，也可以在我这里回收成[c][ffff00]「追月礼券」[-][/c]呢~',Speaker=893759,}",
  [170681] = "{id=170681,Text='想要更多的[c][ffff00]「追月礼券」[-][/c]，也可以在金币商城购买礼包获得哦~',Speaker=893759,}",
  [170682] = "{id=170682,Text='[c][ffff00][PlayerName][-][/c]，有一个问题困惑我很久了~',Speaker=893760,Option='怎么了？',}",
  [170683] = "{id=170683,Text='你觉得我的发型比较好看，还是晴虹的发型比较好看呢？',Speaker=893760,Option='这很难评',}",
  [170684] = "{id=170684,Text='那么难选吗？那我就送你一个礼物，在我和晴虹的发型里面，你只能二选一，你选了，我就知道答案了~',Speaker=893760,}",
  [170685] = "{id=170685,Text='什么嘛？你还没有想好要买些什么？卡普拉购物节已经开始了！这可是一年当中，折扣力度最大的时候！别忘了，每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！',Speaker=893761,}",
  [170686] = "{id=170686,Text='[c][ffff00][PlayerName][-][/c]，别愣着哦~快收下啦~',Speaker=893761,Option='打开',}",
  [170687] = "{id=170687,Text='[c][ffff00][PlayerName][-][/c]，你终于来啦~',Speaker=893762,Option='怎么了？',}",
  [170688] = "{id=170688,Text='一年一度的卡普拉购物节虽然开始了，但你看看，怎么附魔小店、符文商店等等，还没有入驻呀！',Speaker=893762,Emoji=24,}",
  [170689] = "{id=170689,Text='这次购物节的重头戏[c][ffff00]卡普拉珍宝店[-][/c]少了这些超值的商品！我的营业额……流水……KPI……我的工作，该怎么办！',Speaker=893762,Option='我来帮你！',}",
  [170690] = "{id=170690,Text='不愧是[c][ffff00][PlayerName][-][/c]，热心的冒险者，总能在危机时刻出现！',Speaker=893762,Option='从何做起',}",
  [170691] = "{id=170691,Text='请去拜访这些商人！他们总是有着最刁钻的问题来考验你的诚心。完成他们的考验，势必会赢得他们的认可，这样才能成功邀约！',Speaker=893762,Option='我去试试',}",
  [170692] = "{id=170692,Text='嗯，你想得到我的认可吗？那么接下来就来进行考验吧！',Speaker=806040,Option='{开始,0}{稍等,2}',}",
  [170693] = "{id=170693,Text='回答的太糟糕了！你需要加深对符文商店的了解！',Speaker=806040,Emoji=12,}",
  [170694] = "{id=170694,Text='回答的很好！我这关算你通过了！',Speaker=806040,}",
  [170695] = "{id=170695,Text='经验丰富的冒险者果然是无所不知~',Speaker=806040,}",
  [170696] = "{id=170696,Text='悄悄告诉你，[c][ffff00]猫小友[-][/c]的考验会更加刁钻，请明天去拜访他吧！',Speaker=806040,}",
  [170697] = "{id=170697,Text='嗯，你想得到我的认可吗？那么接下来就来进行考验吧！',Speaker=2157,Option='{开始,0}{稍等,2}',}",
