  [170712] = "{id=170712,Text='唔，你回来啦~那么接下来，我们核对一下达成邀约的名单吧~',Speaker=893762,Option='{开始,0}{稍等,2}',}",
  [170713] = "{id=170713,Text='回答的太糟糕了！你需要再想想~',Speaker=893762,Emoji=12,}",
  [170714] = "{id=170714,Text='不错嘛，果然很靠谱呢！',Speaker=893762,}",
  [170715] = "{id=170715,Text='[c][ffff00]卡普拉珍宝店[-][/c]正在营业中哦！',Speaker=893762,}",
  [170716] = "{id=170716,Text='你可以通过[c][ffff00]热点[-][/c]按钮进入[c][ffff00]购物节[-][/c]选购到各个商店提供的超值商品哦~',Speaker=893762,}",
  [170717] = "{id=170717,Text='这是给你的报酬，请收下吧！',Speaker=893762,}",
  [170718] = "{id=170718,Text='走过路过，不要错过！卡普拉公司准备了优惠力度最大的抽奖活动——「呱呱乐」！',Speaker=893762,Option='了解一下',}",
  [170719] = "{id=170719,Text='嘻嘻，使用「呱呱乐」，有机会获得大量「卡普拉购物券」哟~快来试试运气吧！',Speaker=893762,Option='如何获得',}",
  [170720] = "{id=170720,Text='那么！猜拳对决，冒险者！只要赢得猜拳，你就能获得一张「呱呱乐」！',Speaker=893762,Option='来吧！',}",
  [170721] = "{id=170721,Text='三！',Speaker=893762,}",
  [170722] = "{id=170722,Text='二！',Speaker=893762,}",
  [170723] = "{id=170723,Text='一！',Speaker=893762,}",
  [170724] = "{id=170724,Text='剪刀石头布——',Speaker=893762,Option='{石头,40},{剪刀,50},{布,60}',}",
  [170725] = "{id=170725,Text='太遗憾了，再接再厉！',Speaker=893762,}",
  [170726] = "{id=170726,Text='哇哦！你赢啦~',Speaker=893762,}",
  [170727] = "{id=170727,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=893762,Option='{再玩一次,21},{不玩啦,102}',}",
  [170728] = "{id=170728,Text='太厉害了！我玩得很开心！喏，这张「呱呱乐」归你啦~',Speaker=893762,}",
  [170729] = "{id=170729,Text='嗯哼~感恩节要到了哦~那你想不想吃美味的火鸡料理~！',Speaker=893764,Option='想！',}",
  [170730] = "{id=170730,Text='不过，要先有鸡，才有火鸡料理哦……',Speaker=893764,}",
  [170731] = "{id=170731,Text='找到散养在米德加尔特大陆上各处的[c][ffff00]白火鸡[-][/c]吧！用作火鸡料理的话，想必是美味至极！',Speaker=893764,Option='去哪儿找',}",
  [170732] = "{id=170732,Text='让我想想……有了！\n今天就去[c][ffff00]「普隆德拉的北部」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170733] = "{id=170733,Text='诶诶，别着急，我已经在你的地图上，圈出大致范围！',Speaker=893764,}",
  [170734] = "{id=170734,Text='（健硕的体型，光泽的毛发，洋气的神态，一看就很美味呢！快把它带给莱厄斯吧！）',}",
  [170735] = "{id=170735,Text='[c][ffff00][PlayerName][-][/c]，你回来啦~怎么样，找到了吗？',Speaker=893764,Option='是这个吗？',}",
  [170736] = "{id=170736,Text='喔喔喔……！一定不会错，这就是最棒的白火鸡！',Speaker=893764,Emoji=1,}",
  [170737] = "{id=170737,Text='这是给你的报酬，请收下吧！明天也记得来帮忙噢~',Speaker=893764,}",
  [170738] = "{id=170738,Text='让我想想……有了！\n今天就去[c][ffff00]「普隆德拉」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170739] = "{id=170739,Text='让我想想……有了！\n今天就去[c][ffff00]「兽人村落」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170740] = "{id=170740,Text='让我想想……有了！\n今天就去[c][ffff00]「姜饼城」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170741] = "{id=170741,Text='让我想想……有了！\n今天就去[c][ffff00]「洛阳」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170742] = "{id=170742,Text='让我想想……有了！\n今天就去[c][ffff00]「梦幻城」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170743] = "{id=170743,Text='让我想想……有了！\n今天就去[c][ffff00]「甜梦原野」[-][/c]！',Speaker=893764,Option='这范围也太大了！',}",
  [170744] = "{id=170744,Text='这些就足够了，火鸡盛宴马上完成！另外，这是给你的报酬，请收下吧！',Speaker=893764,}",
  [170745] = "{id=170745,Text='是否要品尝美味的火鸡盛宴？',Speaker=9758,Option='{品尝,3},{稍后,2}',}",
  [170746] = "{id=170746,Text='感恩节，你最想感恩谁呢？冒险者！',Speaker=893764,Option='你呢？',}",
  [170747] = "{id=170747,Text='让我想想……我最想感谢一直以来乐于助人的你！所以，我为你准备了一份特别礼物！请收下吧！',Speaker=893764,}",
  [170748] = "{id=170748,Text='卡普拉珍宝店正在营业呢！坐骑、翅膀、时装，现在只要超低的价格就能在珍宝店买到哦~',Speaker=893761,}",
  [170749] = "{id=170749,Text='这是特别为你准备的[c][ffff00]「珍宝提货券」[-][/c]！',Speaker=893761,}",
  [170750] = "{id=170750,Text='想要更多的[c][ffff00]「珍宝提货券」[-][/c]，也可以在金币商城购买礼包获得哦~',Speaker=893761,}",
  [170751] = "{id=170751,Text='如果你在购物节没有买到想要的东西，不妨来跟我交换哦~',Speaker=893765,}",
  [170752] = "{id=170752,Text='只需少量的材料就能换到实用的道具哦~快来试试吧！',Speaker=893765,}",
  [170753] = "{id=170753,Text='嘿呀~冒险者，你可算来了！每当周末的时候，可恶的魔物将簇拥在艾尔帕兰！',Speaker=6465,}",
  [170754] = "{id=170754,Text='请去消灭这些可恶的魔物，保卫艾尔帕兰吧！',Speaker=6465,Option='勉强答应',}",
  [170755] = "{id=170755,Text='切记！魔物的出没时间：[c][ffff00]每周六、周日16点~24点[-][/c]，就在[c][ffff00]艾尔帕兰[-][/c]！',Speaker=6465,}",
  [170756] = "{id=170756,Text='让我看看你的击杀记录……唔，不愧是你，远远超过了要求的数量！这是你的报酬！',Speaker=6465,}",
  [170757] = "{id=170757,Text='一年一度的卡普拉购物节开始了！琳琅满目的商品统统都买下吧！',Speaker=893761,}",
  [170758] = "{id=170758,Text='11/1~12/1期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「瑞奇」[-][/c]，接取系列任务~',Speaker=893761,}",
  [170759] = "{id=170759,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「卡普拉购物券」x5\n完成第5个任务，可获得Zeny x 300,000、「卡普拉购物券」x5、「B格猫福利券·宴」x30',Speaker=893761,}",
  [170760] = "{id=170760,Text='卡普拉公司准备了优惠力度最大的抽奖活动！与瑞奇进行对决，赢得猜拳，获得呱呱乐吧！',Speaker=893761,}",
  [170761] = "{id=170761,Text='11/6~11/13期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「瑞奇」[-][/c]，接取系列任务~',Speaker=893761,}",
  [170762] = "{id=170762,Text='共7个连续的任务：\n完成每个任务，可获得「呱呱乐」x1',Speaker=893761,}",
  [170763] = "{id=170763,Text='11/13~12/1期间，Base等级≥25级的冒险者，通过“[c][ffff00]热点[-][/c]”可进入[c][ffff00]感恩回馈[-][/c]。\n累计登录达到指定天数，即可领取1份奖励~',Speaker=893761,}",
  [170764] = "{id=170764,Text='每年的购物节，料理协会都会准备火鸡盛宴用来招待远道而来的商人和顾客~找到美味的白火鸡，帮助「莱厄斯」完成火鸡盛宴吧！',Speaker=893761,}",
  [170765] = "{id=170765,Text='11/20~12/1期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「莱厄斯」[-][/c]，接取系列任务~',Speaker=893761,}",
  [170766] = "{id=170766,Text='共7个连续的任务：\n完成第1~6个任务，可获得Zeny x 150,000、「卡普拉购物券」x1、「波利印章」x1\n完成第7个任务，可获得Zeny x 300,000、「卡普拉购物券」x3、「波利印章」x1、限定嘴饰「手枪火鸡腿」x1',Speaker=893761,}",
  [170767] = "{id=170767,Text='11/27~12/1期间，冒险者前往普隆德拉广场找到[c][ffff00]「卡普拉营业员」[-][/c]进行材料交换~',Speaker=893761,}",
  [170768] = "{id=170768,Text='完成交换，可获得「卡普拉购物券」x1、「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1',Speaker=893761,}",
  [170769] = "{id=170769,Text='消灭聚集的魔物，保卫艾尔帕兰吧！',Speaker=893761,}",
  [170770] = "{id=170770,Text='活动期间，每周六、周日16点至24点，[c][ffff00]艾尔帕兰[-][/c]将有大量魔物入侵~',Speaker=893761,}",
  [170771] = "{id=170771,Text='击败「盗宝狸猫」可以获得「卡普拉守护礼盒」\n击败「盗宝黄金虫」概率获得「卡普拉符文礼盒」和「卡普拉的谢礼·MVP」，不要错过哟~',Speaker=893761,}",
  [170777] = "{id=170777,Text='11/1~12/1期间，卡普拉珍宝店限时开启！坐骑、翅膀、时装，将以超低的价格在珍宝店内限时出售！',Speaker=893761,}",
  [170778] = "{id=170778,Text='通过[c][ffff00]热点[-][/c]按钮进入[c][ffff00]购物节[-][/c]开始选购吧！',Speaker=893761,}",
  [170779] = "{id=170779,Text='一年一度的万圣节，那可是属于我的节日！',Speaker=893770,Option='南瓜杰克？',}",
  [170780] = "{id=170780,Text='怎么样？我的新装扮，还不错吧！',Speaker=893770,}",
  [170781] = "{id=170781,Text='嘿，你只要完成5次我的委托，我可以送你一张[c][ffff00]诡夜的狂想头饰兑换券[-][/c]作为报酬！如果你完成7次的话，可以额外获得1个[c][ffff00]狂想万圣夜礼盒 [-][/c]哦~',Speaker=893770,Option='什么委托？',}",
  [170782] = "{id=170782,Text='我准备在[c][ffff00]尼夫海姆[-][/c]举办派对，可总有一些捣蛋幽灵偷偷溜了进来……',Speaker=893770,}",
  [170783] = "{id=170783,Text='这些捣蛋幽灵都很狡猾，需要使用[c][ffff00]幽灵相机[-][/c]才能找到他们！',Speaker=893770,}",
  [170784] = "{id=170784,Text='所以，我想委托你，前往尼夫海姆击败1个捣蛋幽灵！',Speaker=893770,Option='交给我吧！',}",
  [170785] = "{id=170785,Text='我可以把你传送过去，准备好了吗？',Speaker=893770,Option='嗯',}",
  [170786] = "{id=170786,Text='咦！这……也太快了！让我看看你的记录……唔，不愧是你，远远超过了要求的数量！',Speaker=893770,}",
  [170787] = "{id=170787,Text='这是你的报酬！再完成[c][ffff00]4[-][/c]次委托，就能获得[c][ffff00]诡夜的狂想头饰兑换券[-][/c]!明天记得来喔~',Speaker=893770,}",
  [170788] = "{id=170788,Text='这是你的报酬！再完成[c][ffff00]3[-][/c]次委托，就能获得[c][ffff00]诡夜的狂想头饰兑换券[-][/c]!明天记得来喔~',Speaker=893770,}",
  [170789] = "{id=170789,Text='这是你的报酬！再完成[c][ffff00]2[-][/c]次委托，就能获得[c][ffff00]诡夜的狂想头饰兑换券[-][/c]!明天记得来喔~',Speaker=893770,}",
  [170790] = "{id=170790,Text='这是你的报酬！再完成[c][ffff00]1[-][/c]次委托，就能获得[c][ffff00]诡夜的狂想头饰兑换券[-][/c]!明天记得来喔~',Speaker=893770,}",
  [170791] = "{id=170791,Text='这是你的报酬！你已经完成[c][ffff00]5[-][/c]次委托，这是送给你的[c][ffff00]诡夜的狂想头饰兑换券[-][/c]!',Speaker=893770,}",
  [170792] = "{id=170792,Text='这是你的报酬！明天还有最后一次委托，记得来完成喔！完成了就可以获得1个[c][ffff00]狂想万圣夜礼盒 [-][/c]哦~',Speaker=893770,}",
  [170793] = "{id=170793,Text='这是你的报酬！你已经完成了所有的委托！',Speaker=893770,}",
  [170796] = "{id=170796,Text='很高兴遇到你，冒险者。又是一年雪季，我们在姜饼城举办了圣诞盛典，欢迎你们一同参加。',Speaker=891849,Option='圣诞盛典！！',}",
  [170797] = "{id=170797,Text='是的！我们也很期待，所以请和我们一起感受这次的盛典仪式吧！',Speaker=891849,}",
  [170798] = "{id=170798,Text='而且还会有惊喜的礼物等着你噢。一起参加吧！【[c][ffff00]该任务同账号仅可完成一次。[-][/c]】',Speaker=891849,Option='{期待已久,2},{稍等片刻,3}',}",
  [170799] = "{id=170799,Text='很高兴你做出了选择！可以轻触[c][ffff00]雪之门[-][/c]，前往姜饼城参加这次的盛典。',Speaker=891849,}",
  [170800] = "{id=170800,Text='快去试试吧，这是专属盛典的通道噢。',Speaker=891849,}",
  [170801] = "{id=170801,Text='如果你做好准备了，就去吧！',Speaker=891849,}",
  [170802] = "{id=170802,Text='嗯嗯，好哒好哒，我想我们还会再见面哒！',Speaker=891849,}",
  [170803] = "{id=170803,Text='是否前往姜饼城参与圣诞盛典？',Speaker=891850,Option='{出发吧,4},{等一下,5}',}",
  [170804] = "{id=170804,Text='冒险者！你好呀~欢迎你来到姜饼城。',Speaker=891851,}",
  [170805] = "{id=170805,Text='这里的圣诞盛典才刚刚开始，我们正在商量如何装扮姜饼城。',Speaker=891851,}",
  [170806] = "{id=170806,Text='不过我已经有了好主意，希望你可以帮助我实现。',Speaker=891851,Option='需要我做什么',}",
  [170807] = "{id=170807,Text='可以帮我去莱恩那拿回一些铃铛吗？',Speaker=891851,Option='当然可以',}",
  [170808] = "{id=170808,Text='嘿冒险者，竟然在雪季的日子看到你出现，很高兴见到你。',Speaker=891852,Option='来拿取铃铛',}",
  [170809] = "{id=170809,Text='喔？看来布莱迪已经做出他的决定。',Speaker=891852,}",
  [170810] = "{id=170810,Text='这些铃铛请交给布莱迪吧！希望能让姜饼城变得有趣起来。',Speaker=891852,}",
  [170811] = "{id=170811,Text='太棒了！和我一起用铃铛装饰姜饼城吧！',Speaker=891851,}",
  [170812] = "{id=170812,Text='说起来你一定不知道我为什么要用铃铛布置姜饼城，对吗？',Speaker=891851,Option='{是的,0},{想要知道,0}',}",
  [170813] = "{id=170813,Text='你抬头看看天空。',Speaker=891851,}",
  [170814] = "{id=170814,Text='看到了吗，雪花原本是六瓣的晶体，可是每年的最后一个月雪花会变成五瓣的样子。',Speaker=891851,}",
  [170815] = "{id=170815,Text='因为失去的那一瓣被化作了泪水。',Speaker=891851,Option='泪水？',}",
  [170816] = "{id=170816,Text='嗯嗯，是的，是我们最喜欢的圣诞夫人的在哭泣呐……',Speaker=891851,}",
  [170817] = "{id=170817,Text='她在思念她的丈夫，圣诞老人……',Speaker=891851,}",
  [170818] = "{id=170818,Text='所以我才会想到用铃铛的声音传递这份思念，希望圣诞老人早点回家！！',Speaker=891851,Option='{喔！,0},{好主意！ ,0}',}",
  [170819] = "{id=170819,Text='那就布置起来吧！走！',Speaker=891851,}",
  [170820] = "{id=170820,Text='叮铃铃……叮铃铃……',Speaker=891853,}",
  [170821] = "{id=170821,Text='走吧，去下一个地方！',Speaker=891851,}",
  [170822] = "{id=170822,Text='叮铃铃……叮铃铃……',Speaker=891853,}",
  [170823] = "{id=170823,Text='开门关门的时候，铃铛声就会响起来！',Speaker=891851,}",
  [170824] = "{id=170824,Text='叮铃铃……叮铃铃……',Speaker=891853,}",
  [170825] = "{id=170825,Text='好啦，我们已经装饰好了！',Speaker=891851,}",
  [170826] = "{id=170826,Text='风会以铃铛的声音将思念传出去的，太棒了！',Speaker=891851,}",
  [170827] = "{id=170827,Text='叮叮当当……',Speaker=891853,}",
  [170828] = "{id=170828,Text='很棒耶，整个姜饼城都传来铃铛的声音……',Speaker=891851,}",
  [170829] = "{id=170829,Text='不过你看那……在铃铛的周围被唤醒了一些沉睡的魔物。',Speaker=891851,}",
  [170830] = "{id=170830,Text='糟了，我竟然忘了铃铛的声音也有唤醒沉睡魔物的作用……请去击退它们吧！',Speaker=891851,}",
  [170831] = "{id=170831,Text='谢谢你帮我们击退了那些魔物，否则那些魔物很有可能会影响这次盛典的正常举行。',Speaker=891851,}",
  [170832] = "{id=170832,Text='总之，谢谢你来参加这次举行的盛典活动，如果还有需要你的地方，请不要拒绝我哦。',Speaker=891851,}",
  [170833] = "{id=170833,Text='哎哟哎哟……请把我扶起来。',Speaker=891855,}",
  [170834] = "{id=170834,Text='原来是你呀，你怎么在这里？',Speaker=891851,}",
  [170835] = "{id=170835,Text='我只是想来找你商量一下冰霜苹果的种植缺少人手的事情。',Speaker=891855,}",
  [170836] = "{id=170836,Text='这样啊……这位冒险者十分优秀，解决了我很多麻烦，不如就让冒险者帮忙吧！',Speaker=891851,}",
  [170837] = "{id=170837,Text='真的吗？那就拜托你了！',Speaker=891855,Option='{我很乐意,0},{没问题,0}',}",
  [170838] = "{id=170838,Text='呼~听说你为了种植冰霜苹果花了做了很多很多的努力~多亏了你的帮忙！',Speaker=891851,}",
  [170839] = "{id=170839,Text='小饼饼人特别感谢你，在我面前说到你很多次。称赞你是他见过最优秀的冒险者！',Speaker=891851,}",
  [170840] = "{id=170840,Text='那些因为你努力而得到的冰霜苹果，已经送往冰霜酒馆制作成美酒。',Speaker=891851,}",
  [170841] = "{id=170841,Text='走吧！我带你去看看冰霜苹果变成美酒的样子！用冰霜苹果酿造的美酒真的真的很清香扑鼻呢！',Speaker=891851,}",
  [170842] = "{id=170842,Text='当然我还想把铃铛带到酒吧，就让铃铛的声音也在酒吧响起吧！',Speaker=891851,}",
  [170843] = "{id=170843,Text='准备好的话，就触碰雪人的手吧~它会送我们去往目的地~',Speaker=891851,}",
  [170844] = "{id=170844,Text='是否前往冰霜酒馆',Speaker=891850,Option='{立刻出发,2},{稍等片刻,3}',}",
  [170845] = "{id=170845,Text='这里就是酿造诗之美酒的地方！',Speaker=891851,}",
  [170846] = "{id=170846,Text='就连空气也散发着美酒的香气呢！',Speaker=891851,}",
  [170847] = "{id=170847,Text='听说用冰霜苹果酿成的诗之美酒，喝下的人不会昏昏沉沉，反而会说出很多人浪漫的情话。',Speaker=891851,}",
  [170848] = "{id=170848,Text='所以才会有人在婚礼上选用诗之美酒。',Speaker=891851,}",
  [170849] = "{id=170849,Text='喔……很难想象圣诞老人对圣诞夫人说情话的样子。哈哈……',Speaker=891851,}",
  [170850] = "{id=170850,Text='去吧，一边参观酿酒厂一边用铃铛布置这里吧！',Speaker=891851,}",
  [170851] = "{id=170851,Text='叮叮当……',Speaker=891853,}",
  [170852] = "{id=170852,Text='叮叮当……',Speaker=891853,}",
  [170853] = "{id=170853,Text='叮叮当……',Speaker=891853,}",
  [170854] = "{id=170854,Text='喔，很美妙的声音！谢谢你装饰了我们的小酒馆，为了感谢你，这杯诗之美酒属于你了。',Speaker=891856,}",
  [170855] = "{id=170855,Text='当然，我们可以酿造出如此美妙的诗之美酒，多亏了你。',Speaker=891856,}",
  [170856] = "{id=170856,Text='不要怀疑，这是属于你的功勋。',Speaker=891856,}",
  [170857] = "{id=170857,Text='味道怎么样？',Speaker=891856,Option='{太棒了,0},{很美妙,0}',}",
  [170858] = "{id=170858,Text='哈哈……喝过的人都会这样说，这一点我们很自信。',Speaker=891856,}",
  [170859] = "{id=170859,Text='因为这是用冰霜苹果和千年的积雪酿制而成，所以会有独特的香气。',Speaker=891856,Option='原来如此',}",
  [170860] = "{id=170860,Text='不过如此美妙的酒香，除了会吸引冒险者……',Speaker=891856,}",
  [170861] = "{id=170861,Text='也会引来嗜酒的小妖精！',Speaker=891851,}",
  [170862] = "{id=170862,Text='我闻到了酒的味道……',Speaker=891857,}",
  [170863] = "{id=170863,Text='只是想尝尝美酒而已……姜饼城的小气鬼们！',Speaker=891857,}",
  [170864] = "{id=170864,Text='其实我们也不是那么小气的人啦……只是一点点酒而已。',Speaker=891851,}",
  [170865] = "{id=170865,Text='至于我们为什么不给它们喝……也是有原因的。',Speaker=891851,Option='说来听听',}",
  [170866] = "{id=170866,Text='人类喝下诗之美酒，会吟唱出美妙的诗歌……啦啦啦……',Speaker=891851,}",
  [170867] = "{id=170867,Text='但是魔物喝下诗之美酒，则会出现完全相反的状态！它们会骂骂咧咧，喋喋不休……',Speaker=891851,}",
  [170868] = "{id=170868,Text='喔……这是什么鬼地方！哐当哐……',Speaker=891858,}",
  [170869] = "{id=170869,Text='糟了，原来雪兽已经偷偷喝了我们的诗之美酒。',Speaker=891856,}",
  [170870] = "{id=170870,Text='什么破地方……我要再喝一点！嘿，你这个小丑人。',Speaker=891858,Option='{是在说我吗,0},{我？？？,0}',}",
  [170871] = "{id=170871,Text='布莱迪说的没错吧，它们喝完酒后就会变成这样……所以请击退它们吧！',Speaker=891856,}",
  [170872] = "{id=170872,Text='是的是的！它们喝酒后的样子变得更讨厌了。',Speaker=891851,}",
  [170873] = "{id=170873,Text='可恶的丑东西啊！',Speaker=891858,}",
  [170874] = "{id=170874,Text='哇太棒了，幸亏有你在，不然我们对喝醉的它们一点办法都没有……',Speaker=891851,}",
  [170875] = "{id=170875,Text='希望下次再见到你的时候，会降临好运！再见吧～～',Speaker=891851,}",
  [170876] = "{id=170876,Text='你好冒险者，我们又见面了！你听这铃铛的声音……',Speaker=891851,}",
  [170877] = "{id=170877,Text='喔……会让人不自觉地愉快起来，',Speaker=891851,}",
  [170878] = "{id=170878,Text='我想也正是因为这些铃铛的声音，也把那些魔物吸引了过来。',Speaker=891851,Option='发生了什么',}",
  [170879] = "{id=170879,Text='姜饼城北部出现了很多奇怪的宝箱，人们都以为那是什么神秘人所赠之礼……',Speaker=891851,}",
  [170880] = "{id=170880,Text='没想到打开后却埋伏了魔物，魔物竟然用这样的方式和我们恶作剧。',Speaker=891851,}",
  [170881] = "{id=170881,Text='长在冰霜之地的，散发着如月光一般的光芒。',}",
  [170882] = "{id=170882,Text='长在冰霜之地的，散发着如月光一般的光芒。',}",
  [170883] = "{id=170883,Text='一切都顺利吧！',Speaker=891851,Option='顺利',}",
  [170884] = "{id=170884,Text='很好，诱饵的香气一定会让魔物乖乖走出宝箱！',Speaker=891851,}",
  [170886] = "{id=170886,Text='我只是感受到姜饼城快乐的气氛，也想参一起加这次盛典而已啦……',Speaker=891862,}",
  [170887] = "{id=170887,Text='但是你躲在宝箱中会吓坏小朋友。',}",
  [170888] = "{id=170888,Text='我以后不会躲起来恶作剧了……',Speaker=891862,}",
  [170889] = "{id=170889,Text='我以为我躲在宝箱中只是想给路过的小朋友们一个惊喜而已……',Speaker=891857,}",
  [170890] = "{id=170890,Text='看起来他们并不喜欢。',}",
  [170891] = "{id=170891,Text='好吧……我会换一个形式参与这样的盛典。',Speaker=891857,}",
  [170892] = "{id=170892,Text='吓死我了……我只是想躲在宝箱中安静的听着铃铛的声音。',Speaker=891859,}",
  [170893] = "{id=170893,Text='原来是这样……',}",
  [170894] = "{id=170894,Text='对啊……只是总有人以为宝箱里装着的是宝物……也没错了，我就是宝物。只是有些恐怖……',Speaker=891859,}",
  [170895] = "{id=170895,Text='看来这个盛典并不欢迎我……而我只是被铃铛声所吸引。',Speaker=891865,}",
  [170896] = "{id=170896,Text='因为你躲在箱子里会吓坏路人。',}",
  [170897] = "{id=170897,Text='好吧，其实只要不打开就好了……好了，我会离开的。希望有朝一日可以一起与大家共度这个盛典。',Speaker=891865,}",
  [170898] = "{id=170898,Text='怎么样，一切都顺利吗？',Speaker=891851,Option='很顺利',}",
  [170899] = "{id=170899,Text='太好了，你又一次帮了姜饼城的大忙！希望你在这次盛典中玩的开心哦！',Speaker=891851,}",
  [170900] = "{id=170900,Text='呼~又见到你啦，关于你的故事已经传遍了姜饼城的每一个角落。',Speaker=891851,}",
  [170901] = "{id=170901,Text='感谢你的到来，帮助姜饼城的每一个人。',Speaker=891851,}",
  [170902] = "{id=170902,Text='整个姜饼城正在渐渐进入一种令人愉悦的气氛中……我相信圣诞夫人也会因此感受到快乐。',Speaker=891851,}",
  [170903] = "{id=170903,Text='当然……我们也在期待圣诞老人的到来，意味着圣诞夫人会停下对丈夫的思念。',Speaker=891851,}",
  [170904] = "{id=170904,Text='他的到来也意味着我们的圣诞袜中就会在平安夜的晚上出现礼物……',Speaker=891851,}",
  [170905] = "{id=170905,Text='就是这样的铃铛声声……也感染了魔物，它们也渴望在这样的雪季获得礼物。',Speaker=891851,}",
  [170906] = "{id=170906,Text='可是……它们却总是想要不劳而获……',Speaker=891851,Option='发生了什么',}",
  [170907] = "{id=170907,Text='它们抢走了姜饼城小朋友们准备的圣诞帽。',Speaker=891851,}",
  [170908] = "{id=170908,Text='那些帽子……本来是为圣诞老人而准备的……',Speaker=891851,}",
  [170909] = "{id=170909,Text='你愿意帮助我们找回失去的圣诞帽吗？',Speaker=891851,Option='当然愿意',}",
  [170911] = "{id=170911,Text='请去调查它们吧！',Speaker=891851,}",
  [170912] = "{id=170912,Text='就在不久前，我听到了一些奇怪的声音，可是它的速度太快了，等我出来的时候……',Speaker=891860,}",
  [170913] = "{id=170913,Text='只看到一个影子……我本来并不在意……',Speaker=891860,}",
  [170914] = "{id=170914,Text='结果在清点的时候发现我的圣诞帽居然被偷走了……可以帮我找回吗？',Speaker=891860,Option='我会的',}",
  [170915] = "{id=170915,Text='这里就是伊尔丢失圣诞帽的地方，你看我发现了什么！',Speaker=891851,}",
  [170916] = "{id=170916,Text='似乎是一个脚印耶……一定是魔物留下的！',Speaker=891851,}",
  [170917] = "{id=170917,Text='是雪兽的足印。',}",
  [170918] = "{id=170918,Text='不止一个，跟随雪兽的脚追上它们！',Speaker=891851,}",
  [170919] = "{id=170919,Text='快去吧！追回伊尔的圣诞帽。',Speaker=891851,}",
  [170920] = "{id=170920,Text='可恶，还是被你追上了。',Speaker=891858,}",
  [170921] = "{id=170921,Text='我只是想要礼物…',Speaker=891858,}",
  [170922] = "{id=170922,Text='谢谢你帮我找回圣诞帽！不过听说还有其他人的圣诞帽也丢了。',Speaker=891860,}",
  [170923] = "{id=170923,Text='是的，听说它们成立了一个圣诞帽盗贼团。',Speaker=891851,Option='盗贼团？',}",
  [170924] = "{id=170924,Text='是的！跟我走吧，我会在路上告诉你。',Speaker=891851,}",
  [170925] = "{id=170925,Text='姜饼城中还有三个人丢失了帽子。',Speaker=891851,}",
  [170926] = "{id=170926,Text='丢失的时间一样。',Speaker=891851,}",
  [170927] = "{id=170927,Text='在我们来之前，已经有人在这里发现了三个不同脚印。',Speaker=891851,}",
  [170928] = "{id=170928,Text='它们分别是冰波利、小雪兽以及蛋黄波利的脚印。',}",
  [170929] = "{id=170929,Text='原来是它们呀！快去追上它们并拿回小朋友们的圣诞帽吧！',Speaker=891851,}",
  [170930] = "{id=170930,Text='这是冰波利留下的印子……',}",
  [170931] = "{id=170931,Text='这是小雪兽的脚印……',}",
  [170932] = "{id=170932,Text='这是蛋黄波利留下的印子……',}",
  [170933] = "{id=170933,Text='太好了，小朋友们的帽子都找到了，谢谢你再一次帮助了姜饼城的小朋友们！',Speaker=891851,}",
  [170934] = "{id=170934,Text='今夜他们一定会有一个好梦。',Speaker=891851,}",
  [170935] = "{id=170935,Text='再次感谢你守护每一个小朋友的梦。',Speaker=891851,}",
  [170954] = "{id=170954,Text='想尝尝吗？',Speaker=891856,Option='{当然,4},{真的可以吗？,5}',}",
  [170955] = "{id=170955,Text='而且它们总是躲在里面一动不动，不过我们想到了办法吸引它们出来。',Speaker=891851,}",
  [170956] = "{id=170956,Text='可以麻烦你去采集一些冰月草和星雪花吗？这两个可以制作成诱饵。',Speaker=891851,Option='好的',}",
  [170957] = "{id=170957,Text='把冰月草和星雪花制作成诱饵吧！',Speaker=891851,}",
  [170958] = "{id=170958,Text='去吧！请帮我解决那些恶作剧的魔物吧！',Speaker=891851,}",
  [170967] = "{id=170967,Text='嘿，冒险者，很高兴在这里遇到你。我总是在想如果能在雪花纷飞的日子里做些有趣的事情就好了！',Speaker=891875,}",
  [170968] = "{id=170968,Text='比如说……我总是在雪季来临之前在树下埋下一些宝贝。',Speaker=891875,Option='为什么呢？',}",
  [170969] = "{id=170969,Text='这样在大雪之后，我再凭借记忆找到的话……',Speaker=891875,}",
  [170970] = "{id=170970,Text='我就可以体验到失而复得的快乐。',Speaker=891875,}",
  [170971] = "{id=170971,Text='你一定觉得我很奇怪吧？如此寂寥的日子总是要给自己找点事做才好呢！',Speaker=891875,}",
  [170972] = "{id=170972,Text='你想要试试吗？如果想要试试的话，我会告诉你它在什么地方。',Speaker=891875,Option='{很愿意试试,2},{我再想想,3}',}",
  [170973] = "{id=170973,Text='就在我不远处的树下哦……是一个布娃娃！',Speaker=891875,}",
  [170974] = "{id=170974,Text='好吧，那就再想想吧。',Speaker=891875,}",
  [170975] = "{id=170975,Text='被小伊森埋在树下的玩具，其实是他最亲的兄长所赠。',Speaker=891876,}",
  [170976] = "{id=170976,Text='这么快就找到了我的宝贝！这是我最喜欢的布娃娃。',Speaker=891875,}",
  [170977] = "{id=170977,Text='是我的兄长所赠。他曾说过等我大后就会来看我！不过……',Speaker=891875,}",
  [170978] = "{id=170978,Text='听说他和你一样成为了一位冒险者！他一定去了很远远的地方，他真的会来看我吗？',Speaker=891875,Option='当然会',}",
  [170979] = "{id=170979,Text='嗯嗯……那我等着他回来，希望他履行他的誓言。',Speaker=891875,}",
  [170980] = "{id=170980,Text='你好冒险者，想要听听圣诞夫人和圣诞老人的故事吗？',Speaker=891877,Option='{当然,0},{我很乐意,0}',}",
  [170981] = "{id=170981,Text='圣诞老人在追求圣诞夫人的时候，曾许下一百个誓言……',Speaker=891877,}",
  [170982] = "{id=170982,Text='圣诞老人也一直为了实现这一百个誓言而努力。',Speaker=891877,}",
  [170983] = "{id=170983,Text='而每当实现一个誓言，被实现的誓言便会化作宝藏，深埋在大雪之下。',Speaker=891877,}",
  [170984] = "{id=170984,Text='听起来很浪漫吧！如今很多人都在寻找那些誓言化成的宝藏。',Speaker=891877,}",
  [170985] = "{id=170985,Text='你也不例外，对吧！而我也如此！',Speaker=891877,}",
  [170986] = "{id=170986,Text='我也收到了宝藏图哦！！可以帮我找到属于我的宝藏吗！',Speaker=891877,Option='{我很乐意,2},{我还很忙,3}',}",
  [170987] = "{id=170987,Text='真的吗，那就快去吧！就在一棵红色玫瑰花之下！',Speaker=891877,}",
  [170988] = "{id=170988,Text='好吧，等你有兴趣的时候再来找我吧！',Speaker=891877,}",
  [170989] = "{id=170989,Text='誓言将玫瑰花的种子变成红色宝石。',Speaker=891878,}",
  [170990] = "{id=170990,Text='天啊，竟然是罕见的红色宝石！谢谢你把它交给我。',Speaker=891877,}",
  [170991] = "{id=170991,Text='希望有一天我也能拥有圣诞老人和圣诞夫人那样真挚的感情。',Speaker=891877,}",
  [170992] = "{id=170992,Text='我的好朋友莉莉丝很快就要生日了，我曾许诺过她在她的生日为她送上世界上最美的东西。',Speaker=891879,}",
  [170993] = "{id=170993,Text='为了实现我的誓言，我每天都在思考什么是最美的东西……',Speaker=891879,}",
  [170994] = "{id=170994,Text='我终于想到了！',Speaker=891879,Option='会是什么呢',}",
  [170995] = "{id=170995,Text='莉莉丝最喜欢的是花儿～而最美的花……',Speaker=891879,}",
  [170996] = "{id=170996,Text='当然是被冰霜点缀的克拉尤里花！我想世界上不会有比这个更美的事物了。',Speaker=891879,}",
  [170997] = "{id=170997,Text='可以帮我采一些回来吗！',Speaker=891879,Option='{愿意为你效劳,2},{我再想想,3}',}",
  [170998] = "{id=170998,Text='那就快去吧！小心路滑哦。',Speaker=891879,}",
  [170999] = "{id=170999,Text='好吧好吧，那只能再让你考虑一小会儿了。',Speaker=891879,}",
  [171000] = "{id=171000,Text='和我想象中的样子一样美丽……莉莉丝一定会喜欢的。而且这花有着奇特的香气，令人难以忘记。',Speaker=891879,}",
  [171001] = "{id=171001,Text='谢谢你帮我实现了对莉莉丝的誓言！',Speaker=891879,}",
  [171002] = "{id=171002,Text='希望这个世界因为更多的誓言被实现而化作无价宝藏喔～～',Speaker=891879,}",
  [171003] = "{id=171003,Text='我收到了翠西送给我的生日礼物，我……我……',Speaker=891881,}",
  [171004] = "{id=171004,Text='我实在是太喜欢了！我真的没想到世界上竟然会有那么美的花！',Speaker=891881,}",
  [171005] = "{id=171005,Text='翠西实现了对我的誓言！她总能轻易知道我想要什么！我们太有默契了！',Speaker=891881,}",
  [171006] = "{id=171006,Text='在雪季，那些被实现的誓言都会不经意之间化作宝藏回馈给真挚的人们！',Speaker=891881,}",
  [171007] = "{id=171007,Text='翠西值得获得这样的回馈，因此我将我的回礼和那些宝藏一起埋着大雪之下。',Speaker=891881,}",
  [171008] = "{id=171008,Text='并且画成藏宝图的样子，希望你能将藏宝图转送给翠西。你愿意吗？',Speaker=891881,Option='{太棒了，我愿意,2},{我再想一想,3}',}",
  [171009] = "{id=171009,Text='我就知道，你一定会愿意的，因为这是一件很有趣的事情。',Speaker=891881,}",
  [171010] = "{id=171010,Text='好吧，我会等你再做决定哒！',Speaker=891881,}",
  [171011] = "{id=171011,Text='这是誓言宝藏的藏宝图……谢谢你送给我。',Speaker=891879,}",
  [171012] = "{id=171012,Text='不过为什么看起来有些不一样。嗯……这是克拉尤里花的味道……',Speaker=891879,}",
  [171013] = "{id=171013,Text='难道是莉莉丝画的藏宝图……对吧？',Speaker=891879,Option='你们很有默契',}",
  [171014] = "{id=171014,Text='哈哈……那就假装不知道吧！谢谢你，请不要告诉她我已经知道了。',Speaker=891879,}",
  [171015] = "{id=171015,Text='翠西收到藏宝图了吧！',Speaker=891881,Option='是的',}",
  [171016] = "{id=171016,Text='哈哈，开始期待她收获礼物后大喊大叫的样子！',Speaker=891881,}",
  [171017] = "{id=171017,Text='总之，谢谢你帮我送藏宝图啦！谢谢啦……',Speaker=891881,}",
  [171018] = "{id=171018,Text='这个寂寞的雪季，我竟然对一个雪人许下誓言……',Speaker=891882,Option='说来听听',}",
  [171019] = "{id=171019,Text='额……我居然对雪人说，我会对它做一些温暖的事情。',Speaker=891882,}",
  [171020] = "{id=171020,Text='可是对雪人而言温暖并不是什么好事……',Speaker=891882,}",
  [171021] = "{id=171021,Text='但……我却又慎重的许下了誓言……',Speaker=891882,}",
  [171022] = "{id=171022,Text='我感觉我十分荒唐，直到我想明白了一个道理，温暖并不仅仅只是在温度上有所体现……',Speaker=891882,}",
  [171023] = "{id=171023,Text='也可以是抚慰心灵的事情！所以我决定实现我的誓言！',Speaker=891882,}",
  [171024] = "{id=171024,Text='你愿意帮我实现吗？',Speaker=891882,Option='{我很愿意,2},{我再想想,3}',}",
  [171025] = "{id=171025,Text='它一整个雪季都在外面站着，帮我清理雪人身上的污渍吧。你一定能做好吧！',Speaker=891882,}",
  [171026] = "{id=171026,Text='好吧，再给你一点思考的时间……',Speaker=891882,}",
  [171027] = "{id=171027,Text='谢谢你帮我清理，已经很久没有人为我做如此温暖的事情了。',Speaker=891883,}",
  [171028] = "{id=171028,Text='都清理好了吧！雪人会满意吗？',Speaker=891882,Option='{它很满意,0},{它感受到了温暖,0}',}",
  [171029] = "{id=171029,Text='太棒了，我实现了我的誓言，而你也实现了对我的誓言！',Speaker=891882,}",
  [171030] = "{id=171030,Text='谢谢你的到来！你是一个温暖的人～',Speaker=891882,}",
  [171032] = "{id=171032,Text='还好有你来帮助我，要知道冰霜苹果的种子是很难得到的。',Speaker=893776,Option='真的吗？',}",
  [171033] = "{id=171033,Text='因为噢，冰霜苹果的种子长埋在深雪之下，而守护那里的魔物十分强大。',Speaker=893776,}",
  [171034] = "{id=171034,Text='你看我们（虽然不愿承认但是又是一个不争的事实）又矮又小的，所以……获得这些种子有些困难。',Speaker=893776,}",
  [171035] = "{id=171035,Text='如果你准备好了，就请帮我们前往海底洞穴找寻那些冰霜种子吧。',Speaker=893776,Option='准备好了！',}",
  [171036] = "{id=171036,Text='希望一切顺利！',Speaker=893776,}",
  [171037] = "{id=171037,Text='冰霜种子好像不在卡仑身上……',}",
  [171038] = "{id=171038,Text='没有关系，起码我们排除了一个错误答案~！明天~我们接着寻找吧~',Speaker=893776,}",
  [171039] = "{id=171039,Text='或许我还没有告诉过你，我们需要冰霜种子的原因。',Speaker=893776,}",
  [171040] = "{id=171040,Text='因为我们打算用它来种植冰霜苹果，这样才能酿造出诗之美酒。',Speaker=893776,Option='诗之美酒？',}",
  [171041] = "{id=171041,Text='听说圣诞老人和圣诞夫人大婚的日子，喝的就是诗之美酒。',Speaker=893776,}",
  [171042] = "{id=171042,Text='为了让圣诞夫人不再因为思念丈夫而哭泣，所以我们决定酿出诗之美酒，讨夫人的开心。',Speaker=893776,Option='好主意。',}",
  [171043] = "{id=171043,Text='因此我们才需要冰霜种子。或者你可以去找寒冰雕像看看。',Speaker=893776,}",
  [171044] = "{id=171044,Text='如果你准备好了，就请帮我们前往圣域冰洞找寻那些冰霜种子吧。',Speaker=893776,Option='准备好了！',}",
  [171045] = "{id=171045,Text='那就出发吧！',Speaker=893776,}",
  [171046] = "{id=171046,Text='冰霜种子好像不在寒冰雕像身上……',}",
  [171047] = "{id=171047,Text='没有关系，起码我们又排除了一个错误答案~！明天~我们接着寻找吧~',Speaker=893776,}",
  [171048] = "{id=171048,Text='你好冒险者，很高兴再次见到你。',Speaker=893776,}",
  [171049] = "{id=171049,Text='今天我们去看看冰暴骑士那有没有冰霜种子吧。',Speaker=893776,}",
  [171050] = "{id=171050,Text='希望今天可以有所收获吧~你准备好了吗？',Speaker=893776,Option='准备好了！',}",
  [171051] = "{id=171051,Text='那就出发吧！要小心哦～～',Speaker=893776,}",
  [171052] = "{id=171052,Text='冰霜种子好像不在冰暴骑士身上……',}",
  [171053] = "{id=171053,Text='呜呜呜呜呜呜~怎么那么难！',Speaker=893776,Option='别放弃！',}",
  [171054] = "{id=171054,Text='谢谢你的鼓励，明天你还会来帮我吗？',Speaker=893776,Option='那当然！',}",
  [171055] = "{id=171055,Text='那我还在这里等你~',Speaker=893776,}",
  [171056] = "{id=171056,Text='又又又见面啦，这次麻烦你去找欧德姆布拉看看吧~',Speaker=893776,}",
  [171057] = "{id=171057,Text='这一次呢……有一些些麻烦，欧德姆布拉可是出了名的凶悍。',Speaker=893776,}",
  [171058] = "{id=171058,Text='请一定要小心呀~！',Speaker=893776,Option='冲',}",
  [171059] = "{id=171059,Text='加油~',Speaker=893776,}",
  [171060] = "{id=171060,Text='冰霜种子好像不在欧德姆布拉身上……',}",
  [171061] = "{id=171061,Text='又是空手而归吗？对了，我记得我家里有本古书记录了冰霜种子相关的事情，今晚我要好好研究研究~',Speaker=893776,Option='那明天？',}",
  [171062] = "{id=171062,Text='明天肯定能成功！',Speaker=893776,}",
  [171063] = "{id=171063,Text='书上说“在那极寒的冰霜领域，有那么一群守护者，世世代代守护着这冰霜之种，他们……”',Speaker=893776,Option='说重点',}",
  [171064] = "{id=171064,Text='寒冰龙！',Speaker=893776,Option='交给我吧',}",
  [171065] = "{id=171065,Text='以你的实力，肯定能带回冰霜之种的！',Speaker=893776,Option='必须的',}",
  [171066] = "{id=171066,Text='那就出发吧！请一定要小心哦！',Speaker=893776,Option='冲',}",
  [171067] = "{id=171067,Text='我等你回来哟~',Speaker=893776,}",
  [171068] = "{id=171068,Text='是冰霜种子！把它带回去给小饼饼人吧~',}",
  [171069] = "{id=171069,Text='太棒了，这就是冰霜种子！你快挑个合适的地方，把它种下吧~',Speaker=893776,}",
  [171070] = "{id=171070,Text='很快，就能长出冰霜苹果了~谢谢你，冒险者~！',Speaker=893776,}",
  [171072] = "{id=171072,Text='呜呜呜呜，那些哥布灵真的太可恶了~',Speaker=893776,Option='怎么了？',}",
  [171073] = "{id=171073,Text='在这个欢乐的日子里，他们竟然来捣乱，不但偷走了大家准备的礼物，还仗着自己比我们高大，不断欺负我们饼饼人！',Speaker=893776,}",
  [171074] = "{id=171074,Text='冒险者，你能帮我去教训教训他们吗？',Speaker=893776,Option='没问题',}",
  [171075] = "{id=171075,Text='哈哈哈哈，看到他们落荒而逃的样子，真是让人开心呢~',Speaker=893776,}",
  [171077] = "{id=171077,Text='[c][ffff00][PlayerName][-][/c]，想要成为老练的冒险者，了解各个魔物的弱点是必不可少的一步哦~',Speaker=893780,}",
  [171078] = "{id=171078,Text='去击败更多魔物，累积冒险经验吧！每日只要击败[c][ffff00]300个任意魔物[-][/c]，我将有强力的装备自选礼盒送给你！',Speaker=893780,Option='知道了！',}",
  [171079] = "{id=171079,Text='怎么样~和魔物战斗的感觉还习惯嘛？又掌握了更多的冒险技巧了嘛~',Speaker=893780,}",
  [171080] = "{id=171080,Text='好啦~这是勇者的奖励！今后也要继续努力哦~',Speaker=893780,}",
  [171169] = "{id=171169,Text='米德加尔特王国为了庆祝这特殊的日子，给每一位冒险者都准备了一份特殊的礼物哦~',Speaker=893780,}",
  [171170] = "{id=171170,Text='希望你能喜欢这顶象征着勇气的水手宣言，也希望你能在这里收获一段愉快的冒险旅途~',Speaker=893780,Option='{谢谢,2},{过会再领,3}',}",
  [171171] = "{id=171171,Text='好久不见了，[c][ffff00][PlayerName][-][/c]！',Speaker=6944,Option='确实，好久不见',}",
  [171172] = "{id=171172,Text='虽然我一直都在这里，但是平时你压根就想不起要来看看我这个老头！',Speaker=6944,Option='毕竟冒险太忙了',}",
  [171173] = "{id=171173,Text='是是是，你们年轻人忙着打拼忙着冒险，不像我们这些老头子，每年就等着那几个可以相聚的日子~',Speaker=6944,}",
  [171174] = "{id=171174,Text='说起来，姜饼城最热闹的日子又快到了，你知道的，在这个日子里，只要你许下心愿，我可都会去尽力满足的！',Speaker=6944,}",
  [171175] = "{id=171175,Text='所以，你今年的愿望是什么呢？说出来听听~',Speaker=6944,Option='{更加好看,0},{更加强大,0},{更加富有,0},{世界和平,0}',}",
  [171176] = "{id=171176,Text='咳咳咳，真是朴实无华的愿望，那就让我去看看有什么礼物是适合你的，等我采购完了，记得来领哦~',Speaker=6944,}",
  [171177] = "{id=171177,Text='[c][ffff00][PlayerName][-][/c]，你是来领礼物的吗？',Speaker=6944,Option='是啊是啊',}",
  [171178] = "{id=171178,Text='在领礼物之前，我有一个坏消息要告诉你~',Speaker=6944,Option='坏消息？',}",
  [171179] = "{id=171179,Text='你知道的，我年纪大了，所以你之前的心愿，我不小心忘记了，所以你的礼物……',Speaker=6944,Option='没了？',}",
  [171180] = "{id=171180,Text='不不不，所以我决定给你一份大礼~这些就是我能采购到的所有礼物啦~希望你可以喜欢~',Speaker=6944,Option='谢谢！',}",
  [171181] = "{id=171181,Text='对了，我还给你准备了一份神秘的礼物，现在大概已经配送到你[c][ffff00]家园[-][/c]里了，不知道你收到了没~',Speaker=6944,}",
  [171182] = "{id=171182,Text='配送的礼物是有时限的，没收到的话，赶紧回家去看看吧~！',Speaker=6944,}",
  [171186] = "{id=171186,Text='装饰姜饼城吧！为姜饼城挂起铃铛和丝带，叮叮当……叮叮当……铃铛声化作思念的话语在空中奏响。',Speaker=893868,}",
  [171187] = "{id=171187,Text='12/1~1/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「小姜饼人」即可参与活动~',Speaker=893868,}",
  [171188] = "{id=171188,Text='完成活动可以获得Zeny、[c][ffff00]圣诞雪花[-][/c]、[c][ffff00]B格猫福利券·宴[-][/c]等奖励哦~',Speaker=893868,}",
  [171189] = "{id=171189,Text='就让我们采摘一些新鲜的冰霜葡萄再交给姜饼城的孩子们酿成美酒吧！这可是圣诞夫人和圣诞老人婚礼上共饮的诗之美酒的原料噢。',Speaker=893868,}",
  [171190] = "{id=171190,Text='12/1~1/1期间，Base等级大于等于25级的冒险者前往姜饼城，找到「小饼饼人」即可参与活动~',Speaker=893868,}",
  [171191] = "{id=171191,Text='完成活动可以获得Zeny、[c][ffff00]圣诞雪花[-][/c]等奖励哦~',Speaker=893868,}",
  [171192] = "{id=171192,Text='冰霜种子发芽啦~赶紧去看看能收获什么好东西吧~',Speaker=893868,}",
  [171193] = "{id=171193,Text='完成「冰霜种子」任务后，冒险者每天可以采集「冰霜之芽」，一共可以采集5次~',Speaker=893868,}",
  [171194] = "{id=171194,Text='完成活动可以获得[c][ffff00]活动家具[-][/c]的奖励哦~',Speaker=893868,}",
  [171195] = "{id=171195,Text='圣诞老人为了见到圣诞夫人，在雪花纷飞的夜晚赶回姜饼城，那些誓言终以爱的形式实现，化作宝藏埋在雪花之下，快去寻找吧！',Speaker=893868,}",
  [171196] = "{id=171196,Text='12/18~1/1期间，Base等级大于等于25级的冒险者前往姜饼城，找到「小伊森」即可参与活动~',Speaker=893868,}",
  [171197] = "{id=171197,Text='完成活动可以获得Zeny、[c][ffff00]圣诞雪花[-][/c]等奖励哦~',Speaker=893868,}",
  [171198] = "{id=171198,Text='圣诞节到了，圣诞礼物当然少不了~今年，圣诞老人给冒险者准备了多重大礼哦~',Speaker=893868,}",
  [171199] = "{id=171199,Text='12/18~12/23期间，Base等级大于等于25级的冒险者可以前往姜饼城，找到「圣诞老人」许下愿望~',Speaker=893868,}",
  [171200] = "{id=171200,Text='12/24~1/1期间，许好愿望的冒险者可以找「圣诞老人」领取圣诞礼物~同时，圣诞老人还给冒险者准备了一份神秘礼物，冒险者可以前往[c][ffff00]家园[-][/c]查看',Speaker=893868,}",
  [171201] = "{id=171201,Text='12/22~12/29期间，姜饼城场景将出现大量[c][ffff00]「圣诞礼盒」[-][/c]，拾取之后就可以获得一份圣诞礼物~',Speaker=893868,}",
  [171202] = "{id=171202,Text='圣诞节期间，不仅孩子们渴望礼物，就连魔物也渴望得到礼物，所以他们悄悄潜入姜饼城想偷走孩子的礼物噢，请击退他们吧！守护姜饼城每一个孩子的小小梦想。',Speaker=893868,}",
  [171203] = "{id=171203,Text='活动期间，每周六、周日16~24点，盗宝的魔物将刷新在姜饼城中，冒险者拿起手中的武器，去击败他们吧~',Speaker=893868,}",
  [171204] = "{id=171204,Text='击败入侵的魔物将有机会获得「哥布灵圣诞礼盒」、「哥布灵符文礼盒」、[c][ffff00]「绒雪颂歌系列外观」」[-][/c]等稀有奖励哦~',Speaker=893868,}",
  [171205] = "{id=171205,Text='今天怎么会想到来找嗒啦啦呢？',Speaker=893872,Option='祝你生日快乐！',}",
  [171206] = "{id=171206,Text='哎呀，[c][ffff00][PlayerName][-][/c]竟然记得嗒啦啦的生日呢！',Speaker=893872,Option='那当然~',}",
  [171207] = "{id=171207,Text='那么，作为感谢，嗒啦啦也给冒险者你准备了礼物呢~',Speaker=893872,}",
  [171208] = "{id=171208,Text='这是嗒啦啦的第5个生日，所以嗒啦啦给冒险者准备了5份礼物哦~但是礼物一天只能拿一份~记得接下去的几天都要来我哦~',Speaker=893872,Option='好',}",
  [171209] = "{id=171209,Text='今天的礼物是嗒啦啦亲手调制的[c][ffff00]「TapTap能量」」[-][/c]，希望你可以喜欢~',Speaker=893872,}",
  [171210] = "{id=171210,Text='[c][ffff00][PlayerName][-][/c]，已经迫不及待想领取第2份礼物了吗？',Speaker=893872,Option='主要还是想见见你',}",
  [171211] = "{id=171211,Text='能够听到这样的话真开心，嗒啦啦已经迫不及待想把第2份礼物送给你了呢~',Speaker=893872,}",
  [171212] = "{id=171212,Text='今天的礼物是[c][ffff00]「B格猫福利券·宴」」[-][/c]，希望你在嗒啦啦的祝福下可以一发入魂哦~',Speaker=893872,}",
  [171213] = "{id=171213,Text='嗒啦啦等得有些着急了呢，下次记得要早点来哦~',Speaker=893872,}",
  [171214] = "{id=171214,Text='今天的礼物是嗒啦啦亲手调制的[c][ffff00]「TapTap拿铁」[-][/c]，希望可以带给你力量~',Speaker=893872,}",
  [171215] = "{id=171215,Text='还有2份礼物，记得来领哦~',Speaker=893872,}",
  [171216] = "{id=171216,Text='每次见到[c][ffff00][PlayerName][-][/c]都有一种说不上来的亲切感呢，所以就会让嗒啦啦忍不住想亲手做些什么给你~',Speaker=893872,Option='今天是什么？',}",
  [171217] = "{id=171217,Text='今天的礼物是嗒啦啦亲手调制的[c][ffff00]「TapTap 特调」[-][/c]，希望你会喜欢这个味道~',Speaker=893872,}",
  [171218] = "{id=171218,Text='这最后一份礼物，嗒啦啦也犹豫了好久，不知道要送你什么~',Speaker=893872,}",
  [171219] = "{id=171219,Text='思来想去，嗒啦啦决定要送给你[c][ffff00]好运[-][/c]，好运的人做什么事都会成功的~',Speaker=893872,Option='谢谢',}",
  [171220] = "{id=171220,Text='在接下来的冒险旅程中，[c][ffff00][PlayerName][-][/c]可一定要欧气满满哦~！',Speaker=893872,}",
  [171222] = "{id=171222,Text='又见面了，命运之风！奇怪，皇家庆典看起来还挺有意思的！',Speaker=893874,Option='可别错过！',}",
  [171223] = "{id=171223,Text='是吗……？那这段时间我可能也会偶尔来看看。喂，我可不是在偷懒！',Speaker=893874,}",
  [171224] = "{id=171224,Text='这是我在庆典上偶然买到的明信片，还没想好送给谁……哎呀，就给你吧！',Speaker=893874,}",
  [171225] = "{id=171225,Text='快啊，打开看看！[c][ffff00][PlayerName][-][/c]，别发呆了！',Speaker=893874,Option='打开',}",
  [171238] = "{id=171238,Text='（小声嘀咕）不管是过去还是现在，丢三落四的习惯总是改不了……',Speaker=893874,Option='怎么了',Emoji=25,}",
  [171239] = "{id=171239,Text='好吧，如你所见，我正在为弄丢的[c][ffff00]宝藏[-][/c]而发愁。                                        ',Speaker=893874,Option='{宝藏？,0},{星星眼！,0}',}",
  [171240] = "{id=171240,Text='没错，我准备了几只[c][ffff00]黄金波利[-][/c]，想在庆典上派上用场……',Speaker=893874,}",
  [171241] = "{id=171241,Text='而蹦蹦跳跳的波利，却在我发呆的时候偷偷溜走！可恶！',Speaker=893874,}",
  [171242] = "{id=171242,Text='听说，热心的冒险者不会错过任何一个委托！',Speaker=893874,Option='确实，展开说说',Emoji=5,}",
  [171243] = "{id=171243,Text='今天就麻烦你去[c][ffff00]「波利咖啡厅」[-][/c]看看吧！或许能发现什么……',Speaker=893874,}",
  [171244] = "{id=171244,Text='什么？你不知道在哪儿！打开地图看看，我已经给你画出了大致范围。',Speaker=893874,}",
  [171245] = "{id=171245,Text='（从没有见过如此特别的波利……这一定是要找黄金波利）',}",
  [171246] = "{id=171246,Text='[c][ffff00][PlayerName][-][/c]，你回来啦~果然是熟练工！',Speaker=893874,Option='是这个吗？',}",
  [171247] = "{id=171247,Text='喔喔喔……！一定不会错，这就是最稀有的黄金波利！这是给你的报酬，请收下吧！明天也记得来帮忙噢~',Speaker=893874,}",
  [171248] = "{id=171248,Text='今天就麻烦你去[c][ffff00]「坐骑商人」[-][/c]那儿看看吧！或许能发现什么……',Speaker=893874,}",
  [171249] = "{id=171249,Text='打开地图看看，我已经给你画出了大致范围。',Speaker=893874,}",
  [171250] = "{id=171250,Text='咳咳，给你一个提示，黄金波利可不止一个形态！',Speaker=893874,}",
  [171251] = "{id=171251,Text='今天就麻烦你去[c][ffff00]「普隆德拉南门」[-][/c]那儿看看吧！或许能发现什么……',Speaker=893874,}",
  [171252] = "{id=171252,Text='今天就麻烦你去[c][ffff00]「普隆德拉西门」[-][/c]那儿看看吧！或许能发现什么……',Speaker=893874,}",
  [171253] = "{id=171253,Text='今天就麻烦你去[c][ffff00]「普隆德拉北门」[-][/c]那儿看看吧！或许能发现什么……',Speaker=893874,}",
  [171254] = "{id=171254,Text='喔喔喔……！你居然找齐了所有黄金波利！这是给你的报酬，请收下吧！',Speaker=893874,}",
  [171255] = "{id=171255,Text='哦~冒险者！你也要来参与吗？',Speaker=893875,Option='要做什么？',}",
  [171256] = "{id=171256,Text='自我介绍一下，我是本次皇家庆典活动，负责烟花相关的工程师。',Speaker=893875,}",
  [171257] = "{id=171257,Text='我正在筹备这次烟花表演，有没有兴趣一起参与一下？',Speaker=893875,Option='有兴趣！',}",
  [171258] = "{id=171258,Text='强大的冒险者，正好能够收集稀有的「黑火药」，太好了！',Speaker=893875,Option='咳咳！',}",
  [171259] = "{id=171259,Text='我保证，等完成了筹备，一定会让你看到最美丽的烟花~',Speaker=893875,}",
  [171260] = "{id=171260,Text='如果你准备好了，就请前往「梦罗克」寻找可能携带「黑火药」的魔物吧！',Speaker=893875,Option='出发',}",
  [171261] = "{id=171261,Text='是「黑火药」~把它带回去给盖尔吧~',}",
  [171262] = "{id=171262,Text='你回来啦~有收获吗？',Speaker=893875,}",
  [171263] = "{id=171263,Text='太棒了，这就是易燃易爆的「黑火药」！制作出一款绚烂的烟花，需要大量的「黑火药」来试验，请明天也来帮忙吧！',Speaker=893875,}",
  [171264] = "{id=171264,Text='冒险者，你来啦~距离烟花的完成，还需要4颗「黑火药」噢！',Speaker=893875,}",
  [171265] = "{id=171265,Text='如果你准备好了，就请前往「哥布灵森林」寻找可能携带「黑火药」的魔物~',Speaker=893875,Option='出发',}",
  [171266] = "{id=171266,Text='又见面了，冒险者！不知道鬼火是怎么做到持续燃烧的呢？真令人费解！',Speaker=893875,Option='今天的目标是？',}",
  [171267] = "{id=171267,Text='喂喂，今天要讨伐的对象可不是鬼火！',Speaker=893875,}",
  [171268] = "{id=171268,Text='如果你准备好了，就请前往「斐扬树林」寻找可能携带「黑火药」的魔物~',Speaker=893875,Option='出发',}",
  [171269] = "{id=171269,Text='你好冒险者，很高兴再次见到你！距离成功越来越近了，我能感受到更多的火药味了！',Speaker=893875,}",
  [171270] = "{id=171270,Text='如果你准备好了，就请前往「金字塔1F」寻找可能携带「黑火药」的魔物~可不要迷路呀！',Speaker=893875,Option='出发',}",
  [171271] = "{id=171271,Text='冒险者，距离大功告成还差最后1颗「黑火药」了！不想加班的话，就快出发吧！',Speaker=893875,}",
  [171272] = "{id=171272,Text='如果你准备好了，就请前往「妙勒尼山脉」寻找可能携带「黑火药」的魔物~',Speaker=893875,Option='出发',}",
  [171273] = "{id=171273,Text='哦哦哦哦哦哦！已经收集了足够多的「黑火药」，太感谢你了！',Speaker=893875,}",
  [171274] = "{id=171274,Text='哎哎，你先别走！一起来欣赏一下共同完成的烟花吧！',Speaker=893875,Action={actionid=31},}",
  [171275] = "{id=171275,Text='呜~效果还真是不错呢！唉，希望以后有机会能量产！',Speaker=893875,}",
  [171276] = "{id=171276,Text='到时候，一定免费送给你！敬请期待！',Speaker=893875,Option='谢谢',}",
  [171277] = "{id=171277,Text='最后，希望你能在皇家庆典玩得愉快！',Speaker=893875,Action={actionid=35},}",
  [171278] = "{id=171278,Text='呜呜呜呜……西门涌入了大量的异形波利，这可如何是好！',Speaker=1630,Option='怎么了？',}",
  [171279] = "{id=171279,Text='在这个特殊的日子里，可不能让它们入侵普隆德拉破坏皇家庆典！',Speaker=1630,}",
  [171280] = "{id=171280,Text='冒险者，你能帮我去教训教训它们吗？',Speaker=1630,Option='没问题',}",
  [171281] = "{id=171281,Text='哈哈哈哈，看到他们落荒而逃的样子，真是让人开心呢~',Speaker=1630,}",
  [171282] = "{id=171282,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893873,}",
  [171283] = "{id=171283,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=893873,}",
  [171284] = "{id=171284,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【-Naoyuki-】[-][/c]绘制的共创头像！',Speaker=893873,Option='哇！',}",
  [171285] = "{id=171285,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=893873,Option='谢谢',}",
  [171310] = "{id=171310,Text='还有，关于……',Speaker=893882,}",
  [171311] = "{id=171311,Text='你的问题太多了！',Speaker=893874,}",
  [171312] = "{id=171312,Text='谢天谢地，你总算来了，这个家伙快把我烦死了。',Speaker=893874,Option='{他是谁？,0},{你的朋友？,0}',}",
  [171313] = "{id=171313,Text='他嘛……一个被梦罗克驱赶的初心者，被扔到了亚空间，还好被我及时发现。',Speaker=893874,Option='{因为什么？,0},{具体点,0}',}",
  [171314] = "{id=171314,Text='没时间解释了，我知道现在是[c][ffff00]皇家庆典[-][/c]，你很忙，但时间又成了一团乱麻，我们总得做点什么。',Speaker=893874,}",
  [171315] = "{id=171315,Text='哇，他就是你说的那个强大又厉害的冒险者吗？',Speaker=893882,Option='{是我！,0},{过奖了,0}',}",
  [171316] = "{id=171316,Text='最近梦罗克正在追杀这个小家伙，除了你，我想不到还有谁能保护他。',Speaker=893874,}",
  [171317] = "{id=171317,Text='你也当过初心者，怎么样，教给这个小家伙一点冒险常识吧？【[c][ffff00]该任务同账号仅可完成一次。[-][/c]】',Speaker=893874,Option='{我正好有空 ,3},{我最近很忙,99}',}",
  [171318] = "{id=171318,Text='不愧是伟大的冒险者，你先去忙吧，等有空了再来找我，我会等你的。',Speaker=893882,}",
  [171319] = "{id=171319,Text='前提是他还没被梦罗克干掉，算了，看你的安排吧冒险者，毕竟你的时间是有限的。',Speaker=893874,}",
  [171320] = "{id=171320,Text='太好了，他归你了。',Speaker=893874,}",
  [171321] = "{id=171321,Text='我？真的吗？我真的有资格和他学习吗？这真的太让人兴奋了！',Speaker=893882,Option='{别太激动,0},{（有种带孩子的错觉）,0}',}",
  [171322] = "{id=171322,Text='让我来算算，按照米德加尔特大陆的时间，大概[c][ffff00]七天[-][/c]左右，我就可以让他回到原本的时空。',Speaker=893874,}",
  [171323] = "{id=171323,Text='只有七天吗？如果时间能再长一点……',Speaker=893882,}",
  [171324] = "{id=171324,Text='你以为在度假吗？冒险者，最近这段时间你可得看好他，我敢保证梦罗克不会安静等待。',Speaker=893874,Option='{知道了,0},{我会注意的！,0}',}",
  [171325] = "{id=171325,Text='好了，我还得处理梦罗克撕裂时空留下的烂摊子，剩下的交给你了！',Speaker=893874,}",
  [171326] = "{id=171326,Text='是你吗？就是你吗？',Speaker=893882,Option='{什么意思？,0}',}",
  [171327] = "{id=171327,Text='我刚才听到好多人谈论你，你拯救了许多地方，你在各处留下了名字，你简直是吟游诗人口中的传奇，活着的传奇！',Speaker=893882,Option='{……,0},{哪里怪怪的,0}',}",
  [171328] = "{id=171328,Text='我已经迫不及待了，我们第一站去哪里？死者之都尼夫海姆，还是传说中的兽人村落？',Speaker=893882,}",
  [171329] = "{id=171329,Text='对了，你的第一站是从哪里开始的？哇哦，传奇踏出的第一步，太让我好奇了。',Speaker=893882,Option='{普隆德拉南门,0}',}",
  [171330] = "{id=171330,Text='那我们还在等什么？为什么不现在就出发！',Speaker=893882,Option='{现在出发,5},{再等等,98}',}",
  [171331] = "{id=171331,Text='虽然不知道为什么，但你一定有你的道理，好吧，那我们再等等。',Speaker=893882,}",
  [171332] = "{id=171332,Text='真是激动人心的时刻！我们会开启怎样的冒险呢？吟游诗人也会提到我吗？以冒险者朋友的身份？',Speaker=893882,}",
  [171333] = "{id=171333,Text='呃……乐园？和我想象中不太一样，我以为我们会看到可怕的魔物呢。',Speaker=893882,Option='{你想多了……,0},{安静一点,0}',}",
  [171334] = "{id=171334,Text='我们要找谁？',Speaker=893882,}",
  [171335] = "{id=171335,Text='船会动！！',Speaker=893882,}",
  [171336] = "{id=171336,Text='冒险者？很高兴再次见到你，我还记得和你第一次见面的场景，那时候你和赛尼亚一起来的。',Speaker=1054,}",
  [171340] = "{id=171340,Text='抱歉，我忘了赛尼亚已经……勾起了你不好的回忆。',Speaker=1054,Option='{没关系……,0}',}",
  [171341] = "{id=171341,Text='但请不要悲伤，我始终相信有些人不曾离开，就像宝雅姐姐！',Speaker=1054,}",
  [171342] = "{id=171342,Text='让我们打起精神来，今天你身边的这位是……',Speaker=1054,Option='{初心者,0},{一个小朋友,0}',}",
  [171343] = "{id=171343,Text='看出来了，他和你当初一样天真又可爱，我的意思是——潜力无限！',Speaker=1054,}",
  [171344] = "{id=171344,Text='很高兴被你夸奖，那么我现在要做什么？拯救大陆吗？还是什么可怕的危机？我已经完全准备好了！',Speaker=893882,}",
  [171345] = "{id=171345,Text='咳……有这个决心很好，但至少你得先学会一些战斗技巧，从最简单的波利开始吧。',Speaker=1054,}",
  [171346] = "{id=171346,Text='冒险者，带他去战胜波利吧。',Speaker=1054,}",
  [171347] = "{id=171347,Text='（小声嘀咕）波利？会不会太简单了点，似乎没什么危险性的样子，用它来作为传奇的开端吗？',Speaker=893882,Option='{快走吧！,0},{别发呆了,0}',}",
  [171348] = "{id=171348,Text='那棵樱花树真漂亮啊，一定有被好好照顾。',Speaker=893882,}",
  [171349] = "{id=171349,Text='这里的风景还不错。',Speaker=893882,}",
  [171350] = "{id=171350,Text='波利~波利~你在哪里~~',Speaker=893882,}",
  [171351] = "{id=171351,Text='哇……比我想象中……要大、大一点。',Speaker=893882,Option='{快点！,0},{动手吧！,0}',}",
  [171352] = "{id=171352,Text='等、等下，我没武器啊。',Speaker=893882,Option='{用树枝！,0}',}",
  [171353] = "{id=171353,Text='树枝？没开玩笑吧，至少应该给我个锋利点的，我承认虽然波利看起来很可爱，但它也是魔物啊。',Speaker=893882,}",
  [171354] = "{id=171354,Text='啊，好痛啊……冒险者，救命！',Speaker=893882,}",
  [171355] = "{id=171355,Text='太厉害了，不愧是传奇的冒险者，你只用了一下，它简直是不堪一击。',Speaker=893882,Option='{别夸了,0},{轮到你了,0}',}",
  [171356] = "{id=171356,Text='我刚才看到了你的动作，干脆利落，我想……我可以试试！',Speaker=893882,}",
  [171357] = "{id=171357,Text='我做到了，冒险者，你看，我做到了！',Speaker=893882,Option='{恭喜你,0},{你做的很好！,0}',}",
  [171358] = "{id=171358,Text='嘿嘿，虽然和想象中有点差距，好在结果差不多，呼……我承认我有点累了。',Speaker=893882,Option='{早点休息？,0}',}",
  [171359] = "{id=171359,Text='我也是这么想的，过会我再去和维琪聊聊更多的战斗机巧，然后咱们明天在普隆德拉见面？',Speaker=893882,Option='{我看行,0},{那就这么定了！,0}',}",
  [171360] = "{id=171360,Text='再见~让我目送着你的背影吧。（小声嘀咕）这样就不会被发现我腿软这件事了吧？',Speaker=893882,}",
  [171361] = "{id=171361,Text='庆典真热闹啊，我逛了一圈，还交了一些朋友。',Speaker=893882,Option='{你没遇到……,0},{某个铁匠吧？,0}',}",
  [171362] = "{id=171362,Text='啊？铁匠？是你的熟人吗？',Speaker=893882,Option='{大陆第一铁匠,2},{一个骗子,3}',}",
  [171363] = "{id=171363,Text='听起来很厉害的样子！那我一定要去见识一下。',Speaker=893882,}",
  [171364] = "{id=171364,Text='骗子？你被骗了钱吗？普隆德拉的卫兵不管吗？',Speaker=893882,}",
  [171365] = "{id=171365,Text='不过能骗到传奇冒险者的人可不多呢。',Speaker=893882,}",
  [171366] = "{id=171366,Text='带我去看看你口中的那个人吧？',Speaker=893882,Option='{好吧,0},{带你认认路,0}',}",
  [171367] = "{id=171367,Text='嘿，老兄，你怎么知道我从艾尔贝塔的铁匠铺来这里了？',Speaker=1014,}",
  [171368] = "{id=171368,Text='没错，我可不会错过皇家庆典，会有许多人来，我会趁机帮他们打造武器。',Speaker=1014,Option='{不愧是你……,0}',}",
  [171369] = "{id=171369,Text='这就是你要带我见的那个铁匠吗？',Speaker=893882,}",
  [171370] = "{id=171370,Text='哇哦，新朋友！你给我的感觉很亲切呢，冒险者和你说过我吗？',Speaker=1014,}",
  [171371] = "{id=171371,Text='怎么样，他一定提到我是大陆第一铁匠这件事了吧？',Speaker=1014,}",
  [171372] = "{id=171372,Text='呃，关于这个嘛，其实……',Speaker=893882,Option='{这不重要！,0},{换个话题,0}',}",
  [171373] = "{id=171373,Text='没错！其实我只是想看看武器打造的过程，为以后做准备，毕竟我现在的武器只是……呃……一根树枝。',Speaker=893882,}",
  [171374] = "{id=171374,Text='这听起来有点可怜，这样吧，让我来为你打造一把匕首，作为给新朋友的礼物。',Speaker=1014,Option='{（警惕地看着他）,0},{多少钱？,0}',}",
  [171375] = "{id=171375,Text='喂喂，你这是什么意思？他是你的朋友，当然免费咯，来吧，只是需要你在旁边打打下手。',Speaker=1014,Option='{好吧,0}',}",
  [171376] = "{id=171376,Text='由传奇冒险者一起帮我打造的武器吗？我已经迫不及待了！',Speaker=893882,}",
  [171379] = "{id=171379,Text='成功了！给你，这可是饱含我与老兄祝愿和爱意的匕首啊。',Speaker=1014,Option='{不至于…… ,0},{有点过了……,0}',}",
  [171380] = "{id=171380,Text='哇……太棒了，我一定会好好珍惜的！绝对~一定~保证爱惜它。',Speaker=893882,Option='{你喜欢就好,0}',}",
  [171381] = "{id=171381,Text='帮我拍个照吧，我一定要留下这一刻，或许未来在史书上能配上这张照片呢。',Speaker=893884,Option='{……,0}',}",
  [171382] = "{id=171382,Text='嘿嘿，我还挺上照的嘛，当然啦，当然这把武器也是。',Speaker=893884,}",
  [171383] = "{id=171383,Text='哈哈哈，还是很配你的！主要是我的手艺好，毕竟我可是……',Speaker=1014,}",
  [171384] = "{id=171384,Text='这、这些是什么……',Speaker=1014,}",
  [171385] = "{id=171385,Text='时空裂隙！那个叫梦罗克的家伙就是把我扔到这里面，我才被关进了亚空间！',Speaker=893884,Option='{别怕,0},{我在！,0}',}",
  [171386] = "{id=171386,Text='没错！老兄还在这呢，我们应该是安全的……吧？',Speaker=1014,}",
  [171387] = "{id=171387,Text='好可怕，它们都是梦罗克派来的吗？',Speaker=893884,Option='{一起上！,0}',}",
  [171388] = "{id=171388,Text='啊……啊？我也一起吗？没错，既然我已经拿到了真正武器，那我就失去了当胆小鬼的资格！',Speaker=893884,}",
  [171389] = "{id=171389,Text='这一招，好帅！',Speaker=893884,}",
  [171390] = "{id=171390,Text='学到了！',Speaker=893884,}",
  [171391] = "{id=171391,Text='终于结束了，这和昨天的战斗可不一样啊……',Speaker=893884,}",
  [171392] = "{id=171392,Text='哈哈，我就说吧，有老兄在不会有事的！',Speaker=1014,}",
  [171393] = "{id=171393,Text='真不愧是传奇冒险者啊，今天的事我会铭记在心，等恰当的时机告诉吟游诗人。',Speaker=893884,}",
  [171394] = "{id=171394,Text='当然，我会小小润色一下，比如我刚才在发抖一类的小事，就没必要说的那么详细啦。',Speaker=893884,Option='{刚开始战斗总是这样,0},{你表现的很好,0}',}",
  [171395] = "{id=171395,Text='嘿嘿，谢谢你的鼓励或者安慰？但我也得有自知之明，刚才完全是你在保护我！',Speaker=893884,}",
  [171396] = "{id=171396,Text='梦罗克的追杀还会继续的，我必须尽快成长起来，下次或许就不是这么简单了。',Speaker=893884,Option='{你说得对,0}',}",
  [171397] = "{id=171397,Text='我们明天继续在普隆德拉见面吧，下次请教我更多冒险的知识，拜托了~',Speaker=893884,Option='{当然~ ,0},{希望你做好准备,0}',}",
  [171398] = "{id=171398,Text='当然，我会竭尽所能的！很期待我接下来的冒险之旅！',Speaker=893884,}",
  [171399] = "{id=171399,Text='冒险者，请帮帮我！',Speaker=893889,Option='怎么了？',}",
  [171400] = "{id=171400,Text='因为庆典中消耗了大量的材料，让本就不富裕的庆典委员会更加雪上加霜！',Speaker=893889,Emoji=24,}",
  [171401] = "{id=171401,Text='如果你身上有多余的材料，请捐赠给我吧！拜托了！',Speaker=893889,}",
  [171405] = "{id=171405,Text='筹备庆典烟花吧！一起用绚烂的烟花，点亮庆典之夜的天空吧~',Speaker=893873,}",
  [171406] = "{id=171406,Text='1/8~2/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到工程师「盖尔」即可参与活动~',Speaker=893873,}",
  [171407] = "{id=171407,Text='完成活动可以获得Zeny、[c][ffff00]庆典纪念币[-][/c]哦~',Speaker=893873,}",
  [171408] = "{id=171408,Text='奥斯卡愿意拿出珍藏的宝物与你一同分享，快去寻找吧！那些无论经过多少时间都在熠熠发光的宝藏……',Speaker=893873,}",
  [171409] = "{id=171409,Text='1/15~2/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「奥斯卡」即可参与活动~',Speaker=893873,}",
  [171410] = "{id=171410,Text='完成活动可以获得Zeny、[c][ffff00]庆典纪念币[-][/c]哦~',Speaker=893873,}",
  [171411] = "{id=171411,Text='由于庆典前期规划不足，在庆典过程中出现了大量材料缺口，因此「布布」希望冒险者可以提供材料帮助他们哦~',Speaker=893873,}",
  [171412] = "{id=171412,Text='1/22~1/29期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「布布」即可参与活动~',Speaker=893873,}",
  [171413] = "{id=171413,Text='完成活动可以获得[c][ffff00]B格猫福利券·宴[-][/c]等奖励哦~',Speaker=893873,}",
  [171414] = "{id=171414,Text='异常的时空持续着波动，穿越而来的还有另一个时空的波利……它们大量涌入西门，觊觎着普隆德拉的庆典…',Speaker=893873,}",
  [171415] = "{id=171415,Text='活动期间，每周六、周日16~24点，捣蛋的魔物将刷新在普隆德拉西门中，冒险者拿起手中的武器，去击败他们吧~',Speaker=893873,}",
  [171416] = "{id=171416,Text='击败入侵的魔物将有机会获得「时空附魔礼盒」、「时空符文礼盒」、[c][ffff00]「皇家庆典系列外观」」[-][/c]等稀有奖励哦~',Speaker=893873,}",
  [171421] = "{id=171421,Text='嘿哟！嘿哟！',Speaker=1014,}",
  [171422] = "{id=171422,Text='真热闹啊，不愧是普隆德拉！',Speaker=893882,}",
  [171423] = "{id=171423,Text='啊，传奇冒险者，你来了？',Speaker=893882,Option='{你在看什么？,0},{怎么东张西望？,0}',}",
  [171424] = "{id=171424,Text='你看庆典上大家穿得多漂亮啊，五颜六色的服装，我简直眼花缭乱了！',Speaker=893882,Option='{不错吧?,0}',}",
  [171425] = "{id=171425,Text='听起来传奇冒险者在这一点上有话要说？说起来我早就注意到你的打扮了。',Speaker=893882,}",
  [171426] = "{id=171426,Text='我想已经有很多人称赞过，非常具有个人风格，在我看来简直是最亮眼的一个！',Speaker=893882,Option='{多谢夸奖,0},{我也这么认为,0}',}",
  [171427] = "{id=171427,Text='这些衣服要问谁购买呢？咳咳，当然，我就问问，毕竟我初来乍到，可没有足够的Zeny。',Speaker=893882,Option='{走吧,0},{带你去看看,0}',}",
  [171428] = "{id=171428,Text='要见到传奇冒险者的服装师了吗？真让人激动。',Speaker=893882,}",
  [171429] = "{id=171429,Text='各位请注意，普隆德拉新一季潮流款式上市啦！走过路过不要错过。',Speaker=1723,}",
  [171430] = "{id=171430,Text='哎呀呀，这不是我们的潮流达人[c][ffff00][PlayerName][-][/c]嘛，你身边这位朋友，走的难道是简约风？',Speaker=1723,}",
  [171431] = "{id=171431,Text='（挠头）我刚来这里不久，还跟不上潮流，（小声）冒险者，我不会让你丢人了吧？',Speaker=893882,Option='{当然不会！,0},{还好啦,0}',}",
  [171432] = "{id=171432,Text='哈哈哈，不要担心，冒险者可是我们的老主顾，既然你是他的朋友，自然也是我的朋友！',Speaker=1723,}",
  [171433] = "{id=171433,Text='我决定送你一件本季度最新潮的服饰！',Speaker=1723,}",
  [171434] = "{id=171434,Text='啊？这有点不好意思……可、可我没钱啊，你不会想让冒险者出钱吧？',Speaker=893882,}",
  [171435] = "{id=171435,Text='如果这样，那就算了吧，我觉得我现在这样也还不错。',Speaker=893882,}",
  [171436] = "{id=171436,Text='不不不，是送，免费送你，看在冒险者的面子上！',Speaker=1723,}",
  [171437] = "{id=171437,Text='我、我真的可以收下吗？',Speaker=893882,Option='{收下吧！,0},{换个造型,0}',}",
  [171438] = "{id=171438,Text='嘿嘿嘿，那我就不客气了！',Speaker=893882,}",
  [171439] = "{id=171439,Text='我、我真不知道说什么好，我感觉自己已经变成了另一个人！',Speaker=893891,}",
  [171440] = "{id=171440,Text='我会上杂志吗？会被邀请去皇家庆典走红毯吗？会引来无数尖叫吗？',Speaker=893891,Option='{别太离谱！,0},{别说梦话！,0}',}",
  [171441] = "{id=171441,Text='所以这真的是冒险的一部分吗？',Speaker=893891,Option='{当然！,0}',}",
  [171442] = "{id=171442,Text='冒险者说的没错，出门在外人靠衣装，服装可是很重要的，那些饰品还能增强你的战斗力。',Speaker=1723,}",
  [171443] = "{id=171443,Text='又是一个知识点，我已经记下来了！',Speaker=893891,}",
  [171444] = "{id=171444,Text='去吧，让冒险者带你见见可爱的微笑小姐，她和冒险者也是老熟人，说不定同样会有赠礼给你。',Speaker=1723,}",
  [171445] = "{id=171445,Text='（崇拜地看着你）不愧是传奇冒险者，朋友简直遍布大陆，现在我也是你的朋友了吗？',Speaker=893891,Option='{是是是,2},{你还得努力呢,3}',}",
  [171446] = "{id=171446,Text='嘿嘿，这比我得到一身新衣服还高兴！',Speaker=893891,}",
  [171447] = "{id=171447,Text='放心，你打击不到我的，我会朝着这个目标，努力进发！',Speaker=893891,}",
  [171448] = "{id=171448,Text='好啦，快去吧，微笑小姐还在等着你们呢。',Speaker=1723,}",
  [171449] = "{id=171449,Text='怎么样，今天看上了哪款头饰？',Speaker=1077,Option='{不是我,0},{是我的朋友,0}',}",
  [171450] = "{id=171450,Text='一张生面孔，又有点熟悉~可爱的初心者在我这里通常都有优待。',Speaker=1077,}",
  [171451] = "{id=171451,Text='嘿嘿，真的吗？',Speaker=893891,}",
  [171452] = "{id=171452,Text='当然，尤其你还是冒险者的朋友，更应该得到一些礼物，比如这个怎么样？',Speaker=1077,}",
  [171453] = "{id=171453,Text='我真的有这个荣幸可以得到它吗？',Speaker=893891,Option='{快收下吧！,0},{别客气了,0}',}",
  [171454] = "{id=171454,Text='怎么样？好看吗？我真希望我的面前能有一面镜子。',Speaker=893892,Option='{还不错,0},{很适合你,0}',}",
  [171455] = "{id=171455,Text='这下我也成了普隆德拉时尚圈的一员啦，冒险者，再帮我拍个照吧？我想看看我现在的模样！',Speaker=893892,}",
  [171456] = "{id=171456,Text='天哪，我几乎都认不出来照片里的这个人了，这还是我吗？',Speaker=893892,Option='{当然,0},{不然是谁？,0}',}",
  [171457] = "{id=171457,Text='但是总感觉还差点什么……让我想想……',Speaker=1077,}",
  [171458] = "{id=171458,Text='想到了，是坐骑！想去冒险，去环游大陆，怎么可以没有坐骑呢？',Speaker=1077,Option='{有道理,0},{差点忘了,0}',}",
  [171459] = "{id=171459,Text='坐骑？那我应该去哪里获得，野外捕捉吗？那我可以捉一条地龙吗？',Speaker=893892,Option='{……,0},{异想天开,0}',}",
  [171460] = "{id=171460,Text='这可是可爱的新人容易犯的错，事实上训练坐骑可是一件很不容易的事。',Speaker=1077,}",
  [171461] = "{id=171461,Text='算算时间，今天坐骑商店管理员已经下班了，不如明天再去？',Speaker=1077,}",
  [171462] = "{id=171462,Text='同意！！！今天我得到的已经太多了，更多的喜悦还是留给明天吧。',Speaker=893892,}",
  [171463] = "{id=171463,Text='（小声）顺带一说，梦罗克毫无动静，它不会已经放弃追杀我了吧？',Speaker=893892,Option='{嘘！,0},{别说这种话,0}',}",
  [171464] = "{id=171464,Text='嘿嘿，好像的确没发生什么，看来今天是我的幸运日！',Speaker=893892,}",
  [171465] = "{id=171465,Text='传奇冒险者，我们明天见吧！',Speaker=893892,Option='{明天见！,0},{希望明天也很幸运,0}',}",
  [171467] = "{id=171467,Text='啦啦啦~下一个一定可以抽到！',Speaker=893892,Option='{你在干什么？,0},{嘿~,0}',}",
  [171468] = "{id=171468,Text='（被吓了一跳）啊，传奇冒险者，是你！',Speaker=893892,}",
  [171469] = "{id=171469,Text='嘿嘿，这个扭蛋机可真有意思，简直就像魔法~根本停不下来。',Speaker=893892,Option='{还是先停停,0},{我们有别的事,0}',}",
  [171470] = "{id=171470,Text='让我想想，对了，坐骑！',Speaker=893892,Option='{没错,0},{现在就带你去,0}',}",
  [171471] = "{id=171471,Text='我的坐骑会是什么呢？一头狼？老虎？还是带翅膀的鸟？',Speaker=893892,}",
  [171472] = "{id=171472,Text='嗨，两位，今早微笑小姐已经和我说过了，你们是来挑选坐骑的，对吗？',Speaker=5092,Option='{是的,0},{给身边这位小朋友,0}',}",
  [171473] = "{id=171473,Text='没错！坐骑！我已经迫不及待了。',Speaker=893892,}",
  [171474] = "{id=171474,Text='哈哈，当然，这是每一个初心者应该拥有的，毕竟大陆很大，光靠我们的双脚可无法环游。',Speaker=5092,}",
  [171475] = "{id=171475,Text='坐骑是冒险者最好的伙伴，你想要什么呢？',Speaker=5092,Option='{有什么选项？,0}',}",
  [171476] = "{id=171476,Text='我们有漂亮的亚特兰地龙，威猛的苍翼狮鹫，以及可爱的羊驼，只要你能想到的……我都可以给你打折。',Speaker=5092,}",
  [171477] = "{id=171477,Text='呃，打折……可是打完折的价格，似乎也……',Speaker=893892,}",
  [171478] = "{id=171478,Text='好的坐骑可不容易捕捉到，我们还要经过精心的饲养和培育，相信我，这一切物超所值！',Speaker=5092,}",
  [171479] = "{id=171479,Text='（犹豫）那、那店里最划算的坐骑是哪一个？好吧，我再说的明白点，我要最便宜的。',Speaker=893892,}",
  [171480] = "{id=171480,Text='呃，那恐怕只有初心波利了，那个背着行李的巨大波利，可它没有地龙的气势，也没有狮鹫的威武，你确定……',Speaker=5092,}",
  [171481] = "{id=171481,Text='就是它，没错！波利也很可爱嘛，（小声嘀咕）实在不行还是算了吧，其实我也没那么需要坐骑。',Speaker=893892,Option='{还是买一个吧,0},{既然答应要照顾你,0}',}",
  [171482] = "{id=171482,Text='……我都不知道该说什么好了，好吧好吧，我愿意用史无前例的低价出售，[c][ffff00]100Zeny[-][/c]怎么样？',Speaker=5092,Option='{同意！,0},{成交！,0}',}",
  [171483] = "{id=171483,Text='漂亮的衣服、头饰和虽然便宜但非常可爱的坐骑，我现在就像在做梦。',Speaker=893892,Option='{快醒醒,0},{这不是梦,0}',}",
  [171484] = "{id=171484,Text='你知道吗？我现在甚至开始感谢梦罗克了，如果不是他把我驱逐去了亚空间，或许……',Speaker=893892,}",
  [171485] = "{id=171485,Text='天呐……不不不，我刚才是开玩笑的，我当然不会感谢梦罗克和他手下那些该死的家伙。',Speaker=893892,Option='{似乎说晚了……,0},{它们已经来了,0}',}",
  [171486] = "{id=171486,Text='我、我、我该怎么办，它们看起来好可怕。',Speaker=893892,Option='{打起精神来！,0},{不要怕,0}',}",
  [171487] = "{id=171487,Text='（深呼吸）是的，传奇冒险者亲自为我挑选了服装、饰品和坐骑，而我则需要拿出战斗的勇气！',Speaker=893892,}",
  [171488] = "{id=171488,Text='我准备好了，我们上吧！',Speaker=893892,}",
  [171493] = "{id=171493,Text='可怜的波利，他似乎受伤很严重……天呐，它帮我挡了一下，我宁可受伤的是我。',Speaker=893892,Option='{想想办法,0},{先治疗它,0}',}",
  [171494] = "{id=171494,Text='我这里有坐骑专用的治疗药水，或许你们需要？',Speaker=5092,}",
  [171495] = "{id=171495,Text='当然！！！太谢谢您了。',Speaker=893892,}",
  [171496] = "{id=171496,Text='这个小家伙似乎还没有习惯，事实上无论是宠物、坐骑甚至冒险者，都会经常受伤。',Speaker=5092,}",
  [171497] = "{id=171497,Text='伤痕可是冒险者的勋章呢！',Speaker=5092,Option='{希望他尽快习惯,0}',}",
  [171498] = "{id=171498,Text='太好了，波利，我亲爱的波利……谢谢你！',Speaker=893892,}",
  [171499] = "{id=171499,Text='还有传奇冒险者，谢谢你一直陪着我，谢谢你将初心波利送给我。',Speaker=893892,Option='{不用客气,0},{这只是一点小事,0}',}",
  [171500] = "{id=171500,Text='为了我的波利，也为了少给你增加麻烦，我想快点变得强大！',Speaker=893892,}",
  [171501] = "{id=171501,Text='冒险者，拜托……明天见面的时候，请你教我更多冒险技巧，好吗？',Speaker=893892,Option='{我答应你,0},{没问题,0}',}",
  [171502] = "{id=171502,Text='在许多冒险故事里，沙漠是非常适合练习和变强的地方，我已经打听好了，明天我们在苏克拉特沙漠汇合好吗？',Speaker=893892,Option='{好~,0},{明天见！,0}',}",
  [171503] = "{id=171503,Text='虽然发生了一些波折，但我还是很高兴，现在你在我的心中不仅仅是那个传奇冒险者，更是我的朋友！',Speaker=893892,}",
  [171504] = "{id=171504,Text='你是我在米德加尔特大陆的第一个朋友！我会永远记住这份心情的~或许战斗中特别容易滋生真挚的情谊呢！',Speaker=893892,}",
  [171517] = "{id=171517,Text='救命——这里的大嘴鸟太凶残了！它们在啄我的屁股！',Speaker=893893,Option='{大嘴鸟战斗力惊人 ,0},{比得上两个剑士,0}',}",
  [171518] = "{id=171518,Text='唉，看来冒险真不是一件容易的事，你一个人在大陆冒险，一定遇到过许多可怕的事吧？',Speaker=893893,Option='{不是一个人 ,0},{我有伙伴！,0}',}",
  [171519] = "{id=171519,Text='我想起来了，吟游诗人口中的传奇故事，听说你甚至认识神族甚至精灵？',Speaker=893893,Option='{当然！,0}',}",
  [171520] = "{id=171520,Text='咳咳，我有个请求……你也知道我只能在这里七天，很快就要走了，在离开前，能让我见见你的朋友吗？',Speaker=893893,}",
  [171521] = "{id=171521,Text='或许我也能从他们身上学到一些战斗机巧和冒险经验呢？',Speaker=893893,Option='{有道理……,0}',}",
  [171522] = "{id=171522,Text='嘿嘿，那就拜托啦，让我见见你的朋友吧！',Speaker=893893,}",
  [171523] = "{id=171523,Text='命运之风，我们又见面了。',Speaker=893898,}",
  [171524] = "{id=171524,Text='是你……冒险者。',Speaker=893899,}",
  [171525] = "{id=171525,Text='嗨，[c][ffff00][PlayerName][-][/c]好久不见啦。',Speaker=893900,}",
  [171526] = "{id=171526,Text='哇……神族、长着龙角的男人，和……有翅膀的精灵？',Speaker=893893,Option='{是的 ,0},{这些是我的朋友,0}',}",
  [171527] = "{id=171527,Text='天呐，我真的不知道如何表达我的崇拜，但、但这也太了不起了，让人难以置信。',Speaker=893893,}",
  [171528] = "{id=171528,Text='他是你的新朋友吗？',Speaker=893898,}",
  [171529] = "{id=171529,Text='看起来很可爱呢！',Speaker=893900,Option='{将一切告知,0}',}",
  [171530] = "{id=171530,Text='所以你是希望我们教导他？',Speaker=893899,Option='{是的 ,0},{给一些建议,0}',}",
  [171531] = "{id=171531,Text='拜托拜托，还有两天我就要离开这里了，你们的建议对我非常有用。',Speaker=893893,}",
  [171532] = "{id=171532,Text='初心者……你让我想起冒险者刚来到洛阳的模样。',Speaker=893899,}",
  [171533] = "{id=171533,Text='聪明、勇敢，能从抽丝剥茧中找到真相。',Speaker=893899,Option='{嘿嘿，是吗？ ,0},{（被夸的不好意思）,0}',}",
  [171534] = "{id=171534,Text='哇，不愧是传奇冒险者，不愧是我的新朋友！',Speaker=893893,}",
  [171535] = "{id=171535,Text='但你知道最重要的是什么吗？或许用语言很难说明……让我看看你战斗的姿态。',Speaker=893899,}",
  [171536] = "{id=171536,Text='啊？那我应该怎么做呢？',Speaker=893893,}",
  [171537] = "{id=171537,Text='让冒险者带着你，去战胜8只凶狠的大嘴鸟。',Speaker=893899,}",
  [171538] = "{id=171538,Text='那些大嘴鸟，好像……有点可怕。',Speaker=893893,Option='{别害怕 ,0},{勇敢起来,0}',}",
  [171539] = "{id=171539,Text='恐惧并不是错误，它能让我们走得更远，去吧，战胜它！',Speaker=893899,}",
  [171540] = "{id=171540,Text='我、我试试！',Speaker=893893,}",
  [171541] = "{id=171541,Text='我的战斗姿势标准吗？',Speaker=893893,}",
  [171542] = "{id=171542,Text='好紧张啊，哎呀，差点被大嘴鸟咬',Speaker=893893,}",
  [171543] = "{id=171543,Text='数量够了，回去找法海吧。',}",
  [171544] = "{id=171544,Text='冒险者就不必说了，这对你而言已经是家常便饭，初心者——你感觉如何？',Speaker=893899,}",
  [171545] = "{id=171545,Text='有点激动，原来大嘴鸟也不像想象中那样可怕嘛。',Speaker=893893,}",
  [171546] = "{id=171546,Text='虽然我的屁股被他们狠狠啄了几下，但没关系，我给了它们更多教训！',Speaker=893893,Option='{很厉害啊 ,0},{干得漂亮,0}',}",
  [171547] = "{id=171547,Text='没错，就是这样，在战斗中我们时常会遇到可怕的敌人，但最重要的是我们必须相信——',Speaker=893899,}",
  [171548] = "{id=171548,Text='胜利者会是我们！',Speaker=893899,}",
  [171549] = "{id=171549,Text='这就是我要教你的事——信念，一往无前的信念！',Speaker=893899,Option='{原来如此,0}',}",
  [171550] = "{id=171550,Text='原来如此，我明白了！谢谢您的教导！',Speaker=893893,}",
  [171551] = "{id=171551,Text='再见！',Speaker=893899,}",
  [171552] = "{id=171552,Text='我的建议……让我想想，或许你可以帮我采点附近的仙人掌果吗？',Speaker=893898,}",
  [171553] = "{id=171553,Text='当然没问题！',Speaker=893893,}",
  [171554] = "{id=171554,Text='啊，被仙人掌扎了下，不过问题不大。',Speaker=893893,}",
  [171555] = "{id=171555,Text='差不多了，把仙人掌果交给芬里尔吧。',}",
  [171556] = "{id=171556,Text='和仙人掌的外表不同，它的果实很甜，汁水充沛。',Speaker=893898,}",
  [171557] = "{id=171557,Text='呃……所以这是你想教导我的吗？在沙漠中可以找到能吃的果实，不至于把自己饿死？',Speaker=893893,Option='{倒是…… ,0},{也有点用,0}',}",
  [171558] = "{id=171558,Text='当然不是，只是你看那些仙人掌，浑身是刺，可它的果实却那样甜美。',Speaker=893898,}",
  [171559] = "{id=171559,Text='这说明我们需要让自己有足够的能力，也就是尖刺，去守护我们真正想要守护的东西。',Speaker=893898,Option='{原来如此……,0}',}",
  [171560] = "{id=171560,Text='守护……',Speaker=893893,}",
  [171561] = "{id=171561,Text='是的，这就是我要教给你的，我们的能力是为了守护亲人、爱人，最终才能结出甜美的果实，希望你能记住这一点。',Speaker=893898,}",
  [171562] = "{id=171562,Text='（重重呼了一口气）我懂了！谢谢您！',Speaker=893893,}",
  [171563] = "{id=171563,Text='命运之风，期待我们的下次相见。',Speaker=893898,}",
  [171564] = "{id=171564,Text='冒险者，很高兴再见到你。',Speaker=893900,Option='{我也很高兴 ,0},{好久不见,0}',}",
  [171565] = "{id=171565,Text='你身边地的这个初心者，很可爱啊！',Speaker=893900,}",
  [171566] = "{id=171566,Text='嘿嘿……谢谢夸奖……嘿嘿。',Speaker=893893,}",
  [171567] = "{id=171567,Text='那我要告诉初心者只有一个词——伙伴！',Speaker=893900,Option='{伙伴……,0}',}",
  [171568] = "{id=171568,Text='你的意思是？',Speaker=893893,}",
  [171569] = "{id=171569,Text='如果没有冒险者的帮助，我们现在恐怕还都被巨大的谎言所蒙蔽。',Speaker=893900,}",
  [171570] = "{id=171570,Text='或许冒险需要强大的实力，绝佳的运气。',Speaker=893900,}",
  [171571] = "{id=171571,Text='但最最重要的是拥有志同道合的伙伴，伙伴才能让你破除迷障，走得更远！',Speaker=893900,Option='{是啊…… ,0},{伙伴很重要！,0}',}",
  [171572] = "{id=171572,Text='（看向冒险者）你说的很对，这几天我已经完全感受到了。',Speaker=893893,}",
  [171573] = "{id=171573,Text='那我就没有更多需要教你的啦~毕竟你已经掌握了冒险者最棒的一点。',Speaker=893900,}",
  [171574] = "{id=171574,Text='信念、守护和伙伴！我想我掌握了冒险最重要的三元素！',Speaker=893893,Option='{恭喜你！,0}',}",
  [171575] = "{id=171575,Text='它们就像是三原色，可以混合出无数的色彩，让枯燥的旅途变成一道彩虹~多美啊~',Speaker=893893,Option='{是啊 ,0},{漂亮的色彩,0}',}",
  [171576] = "{id=171576,Text='我多么想和你，传奇冒险者，继续这段旅程，可今天是第五天了……',Speaker=893893,}",
  [171577] = "{id=171577,Text='如果我的数学没有出错，那么我只剩下两天时间了。',Speaker=893893,Option='{是啊 ,0},{有点不舍……,0}',}",
  [171578] = "{id=171578,Text='不过~那就让我们好好享受接下来的时光吧，幸好现在正是皇家庆典，能让我留下很多有值得纪念的回忆。',Speaker=893893,}",
  [171579] = "{id=171579,Text='听说明天是热闹[c][ffff00]庆典集市[-][/c]，美味的料理摊位，贩卖小玩意的跳蚤市场以及舞台剧表演，都会让普隆德拉热闹非凡。',Speaker=893893,Option='{我明白了 ,0},{我会陪着你的,0}',}",
  [171580] = "{id=171580,Text='是的，你一直在陪着我，不是吗？',Speaker=893893,}",
  [171582] = "{id=171582,Text='嗨，你来啦！庆典可真热闹啊~',Speaker=893893,Option='{是啊 ,0},{好多人,0}',}",
  [171583] = "{id=171583,Text='你闻到香味了吗？这些料理简直在勾引我，庆典和美食是分不开的，我多么希望我像牛一样有四个胃。',Speaker=893893,}",
  [171584] = "{id=171584,Text='不过在我们一起品尝料理之前，我想……',Speaker=893893,Option='{说出来 ,0},{不要害羞,0}',}",
  [171585] = "{id=171585,Text='咳咳，我们可以一起去报名那边的表演吗？听说只要表演的足够精彩，会得到一份超棒的料理。',Speaker=893893,Option='{好主意！ ,0},{走吧~,0}',}",
  [171586] = "{id=171586,Text='你们是来参加庆典表演的吗？',Speaker=893935,Option='{是！,0}',}",
  [171587] = "{id=171587,Text='有什么要求吗？',Speaker=893893,}",
  [171588] = "{id=171588,Text='只要你们能拿出看家本领！两个人的话，报名双人舞怎么样？',Speaker=893935,}",
  [171589] = "{id=171589,Text='好！！！',Speaker=893893,Option='{呃…… ,0},{不再考虑一下？,0}',}",
  [171590] = "{id=171590,Text='没问题的，快来！',Speaker=893893,}",
  [171591] = "{id=171591,Text='配合的不错。',Speaker=893932,}",
  [171592] = "{id=171592,Text='跳的很棒啊。',Speaker=893931,}",
  [171593] = "{id=171593,Text='这两人有点配啊……',Speaker=893931,}",
  [171594] = "{id=171594,Text='什么都磕只会害了你！',Speaker=893932,}",
  [171595] = "{id=171595,Text='我们的配合真默契啊，你有没有一种感觉……',Speaker=893893,Option='{什么感觉？ ,0},{你的意思是……,0}',}",
  [171596] = "{id=171596,Text='心意相通，简直就像……',Speaker=893893,}",
  [171599] = "{id=171599,Text='我已经修复了大部分的时空裂隙，但因为初心者在这里，它似乎并不打算放弃。',Speaker=893957,}",
  [171600] = "{id=171600,Text='它为什么要一直追杀我……我这么弱小无助又可怜……只是因为能吃了点吗？',Speaker=893893,}",
  [171601] = "{id=171601,Text='（深深看一眼初心者）梦罗克毕竟拥有时空之力，或许它看到了你未来的可能性。',Speaker=893957,Option='{我们该怎么做？ ,0},{有解决的办法吗？,0}',}",
  [171602] = "{id=171602,Text='我打算在你们准备好后，故意留出一个时空缺口，让梦罗克出现。',Speaker=893957,}",
  [171603] = "{id=171603,Text='你们从正面，彻彻底底地击败它吧！',Speaker=893957,Option='{可初心者…… ,0},{会受伤吧？,0}',}",
  [171604] = "{id=171604,Text='不用担心我！这一切本来就是因为我引起的。',Speaker=893893,}",
  [171605] = "{id=171605,Text='我必须要肩负起责任，解决这件事！',Speaker=893893,Option='{很好！ ,0},{很有气势,0}',}",
  [171606] = "{id=171606,Text='嘿嘿，再说了，不是还有传奇冒险者陪着我嘛。',Speaker=893893,}",
  [171607] = "{id=171607,Text='既然如此，那就这么办吧，不过现在普隆德拉正在举办庆典，不能把战斗地点放在这里……',Speaker=893957,}",
  [171608] = "{id=171608,Text='有了！不如放到亚空间，海因里希还能一起帮助你呢。',Speaker=893957,Option='{有道理~,0}',}",
  [171609] = "{id=171609,Text='这次战斗，我一定不会退缩！',Speaker=893893,}",
  [171610] = "{id=171610,Text='那就明天见吧~拜拜，我去加！班！了！',Speaker=893957,}",
  [171611] = "{id=171611,Text='啊，对了……刚才我们的双人舞大受好评，获得了这份料理，我们一起吃吧！',Speaker=893893,}",
  [171612] = "{id=171612,Text='守时可是很重要的，你终于来了。',Speaker=893957,}",
  [171613] = "{id=171613,Text='准备好了吗？',Speaker=893957,Option='{准备好了！ ,3},{再等等,4}',}",
  [171614] = "{id=171614,Text='或许我和冒险者还需要一点时间。',Speaker=893893,}",
  [171615] = "{id=171615,Text='好吧，尽快，明白吗？你们知道加班会让我有多大的怨气吗？',Speaker=893957,}",
  [171616] = "{id=171616,Text='我也准备好的，完完全全的！',Speaker=893893,}",
  [171617] = "{id=171617,Text='那我们现在就出发吧~站稳了，可别迷失在时空的洪流。',Speaker=893957,}",
  [171646] = "{id=171646,Text='我猜……到了分别的时候，对吗？',Speaker=893893,Option='{或许…… ,0},{我想是的,0}',}",
  [171647] = "{id=171647,Text='这就像吟游诗人总是会提到的，欢乐的时光总是短暂……',Speaker=893893,}",
  [171648] = "{id=171648,Text='或许我们没有再次相见的机会了，但我会永远记住你的！',Speaker=893893,Option='{我也是 ,0},{祝你平安,0}',}",
  [171649] = "{id=171649,Text='咳咳，初心者，你现在还没有把自己的名字告诉冒险者吧？',Speaker=893957,}",
  [171650] = "{id=171650,Text='啊……是我的疏忽！对不起，请让我重新介绍一下！我的名字是[c][ffff00][PlayerName][-][/c]。',Speaker=893893,Option='{！！！ ,0},{什么？,0}',}",
  [171714] = "{id=171714,Text='要试听我们为庆典排演的歌舞剧台词吗？',Speaker=893905,Option='{听！,2},{算了~,3}',}",
  [171715] = "{id=171715,Text='不听吗？有点失望喵~',Speaker=893905,}",
  [171716] = "{id=171716,Text='咳咳（清清嗓子），那就开始了喵！',Speaker=893905,}",
  [171717] = "{id=171717,Text='是谁~在梦罗克入侵时拿起武器，从不退却？',Speaker=893904,}",
  [171718] = "{id=171718,Text='为米德加尔特迎来新的和平？',Speaker=893906,}",
  [171719] = "{id=171719,Text='是你！英勇无畏的冒险者！',Speaker=893905,}",
  [171720] = "{id=171720,Text='是谁~抽丝剥茧寻找线索？',Speaker=893904,}",
  [171721] = "{id=171721,Text='让黄金时钟再次转动？',Speaker=893906,}",
  [171722] = "{id=171722,Text='是你！谱写时间序曲的冒险者！',Speaker=893905,}",
  [171723] = "{id=171723,Text='是谁~帮圣诞老人解救了马戏团里孩子的灵魂？',Speaker=893904,}",
  [171724] = "{id=171724,Text='姜饼城内再次响起欢乐的笑声？',Speaker=893906,}",
  [171725] = "{id=171725,Text='是你！孩子们的好朋友冒险者！',Speaker=893905,}",
  [171726] = "{id=171726,Text='拯救了尼夫海姆的灵魂、封印了童话书里的恶魔……',Speaker=893904,}",
  [171727] = "{id=171727,Text='让艾尔贝塔的冰川彻底融化！',Speaker=893906,}",
  [171728] = "{id=171728,Text='而这一切都是我们的英雄——冒险者！',Speaker=893905,}",
  [171729] = "{id=171729,Text='破除黑暗与诅咒。',Speaker=893904,}",
  [171730] = "{id=171730,Text='迎来希望与光明。',Speaker=893906,}",
  [171731] = "{id=171731,Text='让我们大声喊出他的名字——',Speaker=893905,}",
  [171732] = "{id=171732,Text='冒险者！冒险者！冒险者！！！',Speaker=893904,}",
  [171733] = "{id=171733,Text='冒险者！冒险者！冒险者！！！',Speaker=893906,Option='{哇……,0}',}",
  [171734] = "{id=171734,Text='嘿嘿，而这就是关于我们的彩排内容喵，请期待正式演出喵~~',Speaker=893905,}",
  [171736] = "{id=171736,Text='老忽老忽，你怎么愁眉苦脸的？',}",
  [171737] = "{id=171737,Text='[c][ffff00][PlayerName][-][/c]，唉，别说了，最近这不是一直在降本增效嘛~',Speaker=1014,}",
  [171738] = "{id=171738,Text='以前精炼的时候，我可是只要铆足了劲随便敲就好，降本增效一来，每次精炼需要消耗的材料都被限制了数量，对于我这样一个经（经）验（常）丰（失）富（败）的铁匠来说，可真是一个挑战！',Speaker=1014,}",
  [171739] = "{id=171739,Text='说白了，就是没材料了！巧妇难为无米之炊，巧匠也不能少了精炼的材料，这样吧，如果你能帮我找一些精炼材料，那么我就给你一些特殊的报酬~',Speaker=1014,}",
  [171740] = "{id=171740,Text='就先从简单的开始吧，今天你只要帮我收集10个神之金属就好~确定要提交吗？',Speaker=1014,Option='{确定,4},{再想想,3}',}",
  [171741] = "{id=171741,Text='不愧是你，那么，明天，请记得来这里找我哦~',Speaker=1014,}",
  [171742] = "{id=171742,Text='HI~[c][ffff00][PlayerName][-][/c]，想不到你这么早就来了，是不是迫不及待想要换取报酬了？',Speaker=1014,Option='还行吧',}",
  [171743] = "{id=171743,Text='昨天已经收集了10个神之金属了，那么今天就收集10个铝吧，要知道神之金属和铝，可是精炼界的好搭档呢~',Speaker=1014,}",
  [171744] = "{id=171744,Text='确定要提交10个铝吗？',Speaker=1014,Option='{确定,4},{再想想,3}',}",
  [171745] = "{id=171745,Text='想不到今天也是那么快的速度，明天的难度可是要增加了哦，你自己考虑要不要来哦~',Speaker=1014,}",
  [171746] = "{id=171746,Text='我就知道你会来，毕竟你可是米德加尔特大陆最擅长挑战困难的人~！',Speaker=1014,}",
  [171747] = "{id=171747,Text='今天我想要10个炉心余烬，这可是高级材料，远古装备才能用的到！',Speaker=1014,Option='就这？',}",
  [171748] = "{id=171748,Text='那么，确定要提交10个炉心余烬吗？',Speaker=1014,Option='{确定,4},{再想想,3}',}",
  [171749] = "{id=171749,Text='哇~10个炉心余烬对你来说竟然也是不费吹灰之力！',Speaker=1014,}",
  [171750] = "{id=171750,Text='那看起来明天的挑战，对于你来说也算不上是挑战了~我们明天见吧~',Speaker=1014,}",
  [171751] = "{id=171751,Text='今天我就开门见山了，我要10个海灵余烬！确定要提交吗？',Speaker=1014,Option='{确定,4},{再想想,3}',}",
  [171752] = "{id=171752,Text='早知道就要20个了！',Speaker=1014,Option='别得寸进尺哦~',}",
  [171753] = "{id=171753,Text='我就说说嘛~我要去敲装备了，明天见~',Speaker=1014,}",
  [171754] = "{id=171754,Text='鉴于你这几天无往不利的优秀表现，从今天开始，提交的材料难度又升级了哦~今天我要浓缩神之金属哦~',Speaker=1014,}",
  [171755] = "{id=171755,Text='虽然浓缩神之金属和神之金属就2个字的区别，可是你要知道，5个神之金属才能合成1个浓缩神之金属哦，可见，浓缩这两个字的含义是有多么地深刻！5个浓缩神之金属是不是有点太难了？',Speaker=1014,}",
  [171756] = "{id=171756,Text='所以，确定要提交5个浓缩神之金属吗？',Speaker=1014,Option='{确定,4},{再想想,3}',}",
  [171757] = "{id=171757,Text='谢谢你，冒险者，明天也请记得来找我~',Speaker=1014,}",
  [171758] = "{id=171758,Text='你来了~有了浓缩神之金属之后，当然还需要浓缩铝来配套',Speaker=1014,}",
  [171759] = "{id=171759,Text='确定要提交5个浓缩铝吗？',Speaker=1014,Option='{确定,4},{再想想,3}',}",
  [171760] = "{id=171760,Text='太好了，有了这些材料，我觉得我又可以疯狂敲装备了~！',Speaker=1014,}",
  [171761] = "{id=171761,Text='呜呜呜呜呜呜~呜呜呜呜呜~',Speaker=1014,Option='？？？',}",
  [171762] = "{id=171762,Text='我昨天在敲装备的时候，被一只不知道哪里来的魔物扰乱了思路，导致我最后+15的是时候手一滑，失败了！',Speaker=1014,}",
  [171763] = "{id=171763,Text='如果可以，HI~[c][ffff00][PlayerName][-][/c]你能去帮我教训一下那些四处乱窜的魔物吗？',Speaker=1014,Option='哪些魔物？',}",
  [171764] = "{id=171764,Text='我当时也没看清是什么魔物，不管了，只要是魔物，教训了总不会错，让他们有则改之无则加勉！',Speaker=1014,Option='……',}",
  [171765] = "{id=171765,Text='我会给你报酬的，比前几次的还要丰厚的报酬！',Speaker=1014,Option='那行吧',}",
  [171766] = "{id=171766,Text='好了，谢谢你，勇敢的冒险者~这几天你真的帮了我很多~',Speaker=1014,}",
  [171767] = "{id=171767,Text='以后有需要我（帮忙敲装备）的时候，请一定要来找我！',Speaker=1014,}",
  [171768] = "{id=171768,Text='看起来你还没有准备好哦~',Speaker=1014,}",
  [171769] = "{id=171769,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=893873,}",
  [171770] = "{id=171770,Text='[c][ffff00]「七周年直播庆典」[-][/c]将在[c][ffff00]1月19日19点[-][/c]开始！\n本次直播将会在[c][ffff00]抖音/微信官方直播间[-][/c]同步举行！届时，将有大量福利掉落，记得前去观看喔~',Speaker=893873,}",
  [171771] = "{id=171771,Text='周年庆期间，每天登录游戏，跟我对话，还能领取[c][ffff00]30张「B格猫福利券·宴（限时）」[-][/c]哦！一定不要错过！',Speaker=893873,}",
  [171772] = "{id=171772,Text='对了，这是今天的周年庆礼物，明天也要记得来哦~',Speaker=893873,}",
  [171773] = "{id=171773,Text='真是热闹的庆典，不妨多走走多看看，用照相机记录下这美好的时光。希望这次也能带给你快乐~',Speaker=893873,}",
  [171774] = "{id=171774,Text='对了，这是最后一份的周年庆礼物！但是，庆典可没有结束，冒险也要继续哦~',Speaker=893873,}",
  [171775] = "{id=171775,Text='[c][ffff00][PlayerName][-][/c]，快抬头看看天空！不得不承认，此时绚烂的天空令人陶醉…',Speaker=893874,}",
  [171776] = "{id=171776,Text='不过，不知道用什么魔法，能将这一刻保留……',Speaker=893874,Option='{我有办法！,0},{你在这等着,0}',}",
  [171777] = "{id=171777,Text='这是……？',Speaker=893874,Option='照片呀！',}",
  [171778] = "{id=171778,Text='噢！原来这样的话，无论什么时候都能看到这番美景了！',Speaker=893874,}",
  [171779] = "{id=171779,Text='喂喂，我可不是什么没见过市面，这只是偶尔，极少数时候对你的赞赏！',Speaker=893874,}",
  [171780] = "{id=171780,Text='好了好了，接下来我要静静欣赏了，这份礼物就额外送给你吧~',Speaker=893874,}",
  [171781] = "{id=171781,Text='噢？你回来了，怎样？',Speaker=893874,}",
  [171789] = "{id=171789,Text='嘿，冒险者，我们下载了一款游戏，不小心穿越到了这里',Speaker=893970,}",
  [171790] = "{id=171790,Text='只要按照任务面板完成任务，通关游戏，我们就可以回到原来的世界！',Speaker=893970,}",
  [171791] = "{id=171791,Text='但是目前我抽不开身，你能帮我去完成一下这特殊的任务吗？',Speaker=893970,Option='没问题',}",
  [171792] = "{id=171792,Text='今天的任务面板上写着“找到并击败异界塔妮小姐”，我会用我的魔法传送你过去，你准备好了吗？',Speaker=893970,Option='冲',}",
  [171793] = "{id=171793,Text='完成任务之后记得回来找我领取奖励！',Speaker=893970,}",
  [171794] = "{id=171794,Text='好样的，但是似乎明天还会有新的任务，明天我也在这里等你，',Speaker=893970,}",
  [171795] = "{id=171795,Text='任务面板刷新了，果然，这是一个连续的任务，只有做完，我们才能回到原来的世界！',Speaker=893970,}",
  [171796] = "{id=171796,Text='今天的任务面板上写着“找到并击败异界蜂后”，老规矩，我会用我的魔法传送你过去，你准备好了吗？',Speaker=893970,Option='准备好了！',}",
  [171797] = "{id=171797,Text='任务面板又刷新了，这次看起来是个难缠的家伙！',Speaker=893970,}",
  [171798] = "{id=171798,Text='今天的任务面板上写着“找到并击败异界死灵”，冒险者，你要小心啊！',Speaker=893970,Option='好的',}",
  [171799] = "{id=171799,Text='怎么感觉有做不完的任务？今天的任务又刷新了！',Speaker=893970,}",
  [171800] = "{id=171800,Text='今天的任务面板上写着“找到并击败异界巴风特”，准备好了我就要用传送魔法了！',Speaker=893970,Option='冲',}",
  [171801] = "{id=171801,Text='任务面板上说今天已经是最后一个任务了！这几天真的是辛苦你了！',Speaker=893970,}",
  [171802] = "{id=171802,Text='今天的任务面板上写着“找到并击败异界灵魂奏者”，这名字，听起来就是最后的大BOSS级别了！',Speaker=893970,}",
  [171803] = "{id=171803,Text='今天你完成任务之后，我们大概已经回到原来的世界了，这一次的奖励就是我送你最真挚的祝福吧！',Speaker=893970,}",
  [171804] = "{id=171804,Text='虽然寒碜了一些，但是绝对是真心的，那么，让我最后一次使用魔法将你传送过去吧！谢谢了，冒险者！',Speaker=893970,}",
  [171805] = "{id=171805,Text='？？？怎么会这样？为什么我还没有回去？怎么任务面板又刷新了那么多新的任务？！',Speaker=893970,}",
  [171806] = "{id=171806,Text='啊啊啊啊啊啊啊，有没有人来救救我们！',Speaker=893970,}",
  [171810] = "{id=171810,Text='冒险者你见过异世界的魔法吗？',Speaker=893977,}",
  [171811] = "{id=171811,Text='这种异世界的魔法不但可以揭晓今日的运势，还可以根据揭晓的运势获得不同的能力加成哦~',Speaker=893977,}",
  [171812] = "{id=171812,Text='但是因为魔力的限制，这种魔法我每天只能施展一次哦~',Speaker=893977,}",
  [171813] = "{id=171813,Text='由于时空裂隙的关系，强大的魔物从裂隙中源源不断地涌出，我需要你的帮助！',Speaker=893976,}",
  [171814] = "{id=171814,Text='击败不同波次的魔物，你将会获得不同的奖励哦~',Speaker=893976,}",
  [171815] = "{id=171815,Text='12/12~1/12期间，Base等级≥25级的冒险者，前往普隆德拉找到「瑞库」即可参与活动',Speaker=893971,}",
  [171816] = "{id=171816,Text='完成活动可以获得「异世界馈赠」、「异世界归来的舅舅系列头像」、「异世界碎片」等奖励',Speaker=893971,}",
  [171817] = "{id=171817,Text='活动共5个连续的任务，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=893971,}",
  [171818] = "{id=171818,Text='12/19~1/12期间，Base等级≥25级的冒险者，前往普隆德拉找到「岛㟢阳介」即可参与活动',Speaker=893971,}",
  [171819] = "{id=171819,Text='完成活动可以获得「异世界馈赠」、「异世界碎片」等奖励',Speaker=893971,}",
  [171820] = "{id=171820,Text='活动共5个连续的任务，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=893971,}",
  [171821] = "{id=171821,Text='12/26~1/2期间，Base等级≥25级的冒险者，前往普隆德拉找到「艾丽西亚」即可参与活动',Speaker=893971,}",
  [171822] = "{id=171822,Text='完成活动可以获得「限时增益效果」、「异世界馈赠」等奖励',Speaker=893971,}",
  [171823] = "{id=171823,Text='1/2~1/9期间，Base等级≥25级的冒险者，前往普隆德拉找到「艾尔加」即可参与挑战副本',Speaker=893971,}",
  [171824] = "{id=171824,Text='副本奖励跟根据最终通关的波次发放，最高可以获得「舅舅扭蛋币」x10、「岛㟢阳介的信物」x1',Speaker=893971,}",
  [171825] = "{id=171825,Text='收集5个「岛㟢阳介的信物」可以合成联动限定头饰「趴趴岛㟢阳介」',Speaker=893971,}",
  [171826] = "{id=171826,Text='异世界归来的舅舅联动扭蛋开启！只需要使用B格猫金币参与10次「限时联动」扭蛋机，即可额外获赠联动职业转职道具[c][ffff00]「英雄职业·古代魔导具使解锁券」[-][/c]！',Speaker=893975,}",
  [171827] = "{id=171827,Text='冒险者如果抽到了重复的外观也不要气馁，可以在我这分解成[c][ffff00]「异世界碎片」[-][/c]，「异世界碎片」可以兑换全新的[c][ffff00]限定卡片「古代魔导具使卡片」[-][/c]以及所有联动扭蛋中的外观道具哦~',Speaker=893975,}",
  [171828] = "{id=171828,Text='那么，这些「异世界碎片」就当作是我送你的见面礼啦~祝你好运~',Speaker=893975,}",
  [171830] = "{id=171830,Text='为了避免他被梦罗克认出来，我已经改变了他的外貌，（坏笑）说不定还有性别哟。',Speaker=893874,Option='{……,0},{安全起见？,0}',}",
  [171835] = "{id=171835,Text='冒险者，如果你也是来购买巧克力，恐怕要空手而归了……唉……',Speaker=893981,Option='发生了什么',}",
  [171836] = "{id=171836,Text='众所周知，情人节自然是与巧克力最为相配！这浓郁而甜蜜的味道，飘过彩虹桥，总是会被贪吃的魔物所觊觎……',Speaker=893981,}",
  [171837] = "{id=171837,Text='可万万没想到，这次失窃的竟然是「白巧克力」……好心的冒险者，请帮帮我吧！',Speaker=893981,Option='可我只是路过……',}",
  [171838] = "{id=171838,Text='诶，先别急着走，你听我说完：只要找回5块「白巧克力」，我愿意把这款极富创造力的巧克力帽赠送你！',Speaker=893981,Option='{……,0},{也有点用,0}',}",
  [171839] = "{id=171839,Text='嗯嗯，如果你准备好了，就请帮前往「古城郊外」寻找贪吃的魔物吧！',Speaker=893981,Option='出发吧！',}",
  [171840] = "{id=171840,Text='是白巧克力！把它带回给慕斯·可可吧！',}",
  [171841] = "{id=171841,Text='你回来了，冒险者！怎么样，找回「白巧克力」了吗？',Speaker=893981,Option='给你~',}",
  [171842] = "{id=171842,Text='让我闻闻……嗯，这个味道不会有错，就是我的「白巧克力」！',Speaker=893981,}",
  [171843] = "{id=171843,Text='太感谢你了，冒险者！这份奖励送给你，明天也要记得来帮忙哦~',Speaker=893981,}",
  [171844] = "{id=171844,Text='又见面了，[c][ffff00][PlayerName][-][/c]！希望今天的冒险也会很轻松！',Speaker=893981,}",
  [171845] = "{id=171845,Text='嗯嗯，如果你准备好了，就请帮前往「幽灵船」寻找贪吃的魔物吧！',Speaker=893981,Option='出发吧！',}",
  [171846] = "{id=171846,Text='嗯嗯，如果你准备好了，就请帮前往「克特森林」寻找贪吃的魔物吧！',Speaker=893981,Option='出发吧！',}",
  [171847] = "{id=171847,Text='而且，我可是有最敏锐的鼻子，让我嗅嗅……嗯，唔……这样的话……我知道在哪里了!',Speaker=893981,}",
  [171848] = "{id=171848,Text='稍等一下，让我嗅嗅……嗯，唔……这样的话……我知道在哪里了!',Speaker=893981,}",
  [171849] = "{id=171849,Text='嗯嗯，如果你准备好了，就请帮前往「罗比奥森林」寻找贪吃的魔物吧！',Speaker=893981,Option='出发吧！',}",
  [171850] = "{id=171850,Text='太感谢你了，冒险者！所有「白巧克力」都找回来啦！',Speaker=893981,}",
  [171851] = "{id=171851,Text='按照约定，这款「纯情巧克力」头饰赠送给你，希望它能带给你表白的勇气！',Speaker=893981,}",
  [171853] = "{id=171853,Text='哟嚯，罗扎纳最厉害的巧克力大师在这里！路过的冒险者，想来一份巧克力吗？',Speaker=893982,}",
  [171854] = "{id=171854,Text='请让我给你推荐当季最畅销的「勇气巧克力」—— “只在特定日子到来时，才在人间进行出售的拥有独特口味的巧克力，在不同的心境下品尝会有不同的体验哦~”',Speaker=893982,}",
  [171855] = "{id=171855,Text='怎么样，心动了吗？可以免费给你试吃一小块哦~',Speaker=893982,Option='{来都来了,0},{让我试试,0}',}",
  [171858] = "{id=171858,Text='（恰到好处的甜度，入口即化的绵密……感觉自己被满满的爱关注着，但似乎少了一点什么……）',}",
  [171859] = "{id=171859,Text='看你的表情，似乎还不错？明天再来试试吧，或许有不一样的体验。',Speaker=893982,}",
  [171860] = "{id=171860,Text='（过于厚重的甜度，令人感到舌尖发酸……感觉自己被浓烈的爱关注着，但似乎太多了……）',}",
  [171861] = "{id=171861,Text='（一切都刚刚好，感觉自己被特别的爱关注着，每一分每一秒都觉得幸福~）',}",
  [171862] = "{id=171862,Text='真是勇气可嘉！记得哦，不论结果如何，我们在对待爱时都应该鼓起勇气去尝试！',Speaker=893982,}",
  [171863] = "{id=171863,Text='吼哦？~看你洋溢着幸福的表情，想[c][ffff00]亲手[-][/c]为心中最喜欢的那个人制作一份[c][ffff00]本命巧克力[-][/c]吧！好好好！我先告诉你配方，需要：[c][ffff00]魔女星沙x5[-][/c]、[c][ffff00]奶油榛果x5[-][/c]以及[c][ffff00]Zeny x100[-][/c]哦~在制作的过程中，脑海中边想象着那个你最喜欢最喜欢的人并将那份爱意一起放入，就能做出全世界独一无二的[c][ffff00]本命巧克力[-][/c]了！',Speaker=893982,Option='{立即动手,2},{默默走开,1}',}",
  [171864] = "{id=171864,Text='哎呀呀，先不要心急哦。制作[c][ffff00]本命巧克力[-][/c]需要[c][ffff00]魔女星沙x5[-][/c]、[c][ffff00]奶油榛果x5[-][/c]以及[c][ffff00]Zeny x100[-][/c]哦，耐心集齐材料后再来找我吧~',Speaker=893982,}",
  [171865] = "{id=171865,Text='呀~看来你是想要兑换一个[c][ffff00]浓情巧克力[-][/c]头饰呢？需要的材料是：[c][ffff00]黑巧克力x1[-][/c]哦~',Speaker=893492,Option='{如何获得,5},{立即兑换,2},{默默走开,1}',}",
  [171866] = "{id=171866,Text='哎呀呀~兑换[c][ffff00]浓情巧克力[-][/c]头饰需要[c][ffff00]黑巧克力x1[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我呦~',Speaker=893492,}",
  [171867] = "{id=171867,Text='对其他冒险者使用[c][ffff00]「本命巧克力」[-][/c]后，可以获得[c][ffff00]黑巧克力[-][/c]哦~',Speaker=893492,}",
  [171868] = "{id=171868,Text='嗯嗯，如果你准备好了，就请帮前往「兽人村落」寻找贪吃的魔物吧！',Speaker=893981,Option='出发吧！',}",
  [171870] = "{id=171870,Text='嗨！亲爱的冒险者~我又又又双叒叕来了呢~',Speaker=893983,}",
  [171871] = "{id=171871,Text='在过去的几天里，我见证了无数的巧克力互赠，心意互诉的情景~',Speaker=893983,}",
  [171872] = "{id=171872,Text='有见证过可歌可泣、荡气回肠的告白，也见过平淡如水却又真真切切的告白~',Speaker=893983,}",
  [171873] = "{id=171873,Text='我在想啊，那么还有谁没有收到这份心意，这份普通却在此刻意义非凡的巧克力呢？',Speaker=893983,}",
  [171874] = "{id=171874,Text='让我想想……对了，那些勤勤恳恳，一直为我们服务的NPC！',Speaker=893983,}",
  [171875] = "{id=171875,Text='善良的冒险者，想必你最熟知他们喜欢什么口味的巧克力，那么这个任务就交给你了！',Speaker=893983,Option='{等等…,0},{又是我,0}',}",
  [171876] = "{id=171876,Text='伟大的爱神，可不会轻易出场！当然，完成任务的奖励，我早就为你准备好了！',Speaker=893983,Option='好吧！',}",
  [171877] = "{id=171877,Text='嘻嘻~言归正传，冒险者！这次我准备了多种不同口味的巧克力，请依照你的心意，根据你的了解，送上他们喜欢的口味吧！',Speaker=893983,}",
  [171878] = "{id=171878,Text='好的，时间不等人，巧克力可要融化了，快动身吧，冒险者！',Speaker=893983,}",
  [171879] = "{id=171879,Text='说吧，今天是敲武器还是副手？',Speaker=1014,Option='有东西要给你',}",
  [171880] = "{id=171880,Text='让我猜猜，这个时间点，难道是……？',Speaker=1014,Option='{榛果巧克力,2},{奶油巧克力,3}',}",
  [171881] = "{id=171881,Text='哦？这是……免费送我的吗？嘿嘿嘿，多谢了，老伙计！',Speaker=1014,Emoji=26,}",
  [171882] = "{id=171882,Text='奶油啊，吃了这个会不会更加手滑……不，我可什么都没说！',Speaker=1014,Emoji=17,}",
  [171883] = "{id=171883,Text='等等，先别走！让我看看有啥可以送给你的……嗯，只有这些了，虽然微不足道，但希望你也能喜欢！',Speaker=1014,}",
  [171884] = "{id=171884,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=893983,}",
  [171885] = "{id=171885,Text='不知道老朋友「吟游诗人」有没有写过少年少女爱恋的故事呢~',Speaker=893983,}",
  [171886] = "{id=171886,Text='好了，话不多说！拿上巧克力，出发吧！',Speaker=893983,}",
  [171887] = "{id=171887,Text='又到了这个时候呢，连空气中都散发着甜蜜的味道~',Speaker=1582,Option='有东西要给你',}",
  [171888] = "{id=171888,Text='我的朋友，你又准备了什么惊喜呢？',Speaker=1582,Option='{猫币巧克力,2},{奶油巧克力,3}',}",
  [171889] = "{id=171889,Text='把巧克力制作猫币的样式？有趣，我似乎有了新的灵感……',Speaker=1582,Emoji=17,}",
  [171890] = "{id=171890,Text='谢谢。等你下次来访，我愿和你分享我新写的故事~',Speaker=1582,Emoji=26,}",
  [171891] = "{id=171891,Text='等等，先别走！让我看看有啥可以送给你的……嗯，只有这些了，虽然微不足道，但希望你也能喜欢！',Speaker=1582,}",
  [171892] = "{id=171892,Text='今天要不出个远门呗？就去吉芬吧！别抱怨了，有一句话不是说任重而道远嘛~',Speaker=893983,}",
  [171893] = "{id=171893,Text='冒险者，来魔法公会有什么事么？',Speaker=1321,Option='有东西要给你',}",
  [171894] = "{id=171894,Text='原来如此，真是辛苦你一路送巧克力过来啊。很简单的推理，能在这个时候来魔法公会还能有什么事吗？',Speaker=1321,Option='{酒心巧克力,2},{纯黑巧克力,3}',}",
  [171895] = "{id=171895,Text='谢谢你的心意，不过身为一个优秀的魔法师，可要离酒远一些呢~酒心巧克力要是换成别的，我会更高兴的！',Speaker=1321,Emoji=26,}",
  [171896] = "{id=171896,Text='有品位的魔法师应当好好对待收到的每一份礼物，尽管这看着会很苦……说笑了。你送的，我自然会认真品尝。',Speaker=1321,Emoji=17,}",
  [171897] = "{id=171897,Text='好了，我要继续钻研魔法了。这些谢礼，希望你能笑纳。',Speaker=1321,}",
  [171898] = "{id=171898,Text='话不多说，你也知道要做什么了，对吧？那就出发吧！',Speaker=893983,}",
  [171899] = "{id=171899,Text='冒险者，你需要帮助吗？',Speaker=801222,Option='有东西要给你',}",
  [171900] = "{id=171900,Text='哇~那我要闭上眼睛吗？我准备好了，快告诉我是什么吧！',Speaker=801222,Option='{奶油巧克力,2},{CD形状的巧克力,3}',}",
  [171901] = "{id=171901,Text='啊！你提醒我了，我也该给大家准备上一份情人节礼物……',Speaker=801222,Emoji=7,}",
  [171902] = "{id=171902,Text='我还以为是新发布的CD呢~看来你和我一样有喜欢音乐呀，谢谢你！巧克力我也会分一点给大家的~',Speaker=801222,Emoji=17,}",
  [171903] = "{id=171903,Text='嗯嗯，这份礼物是我悄悄为你准备的！快收下吧！',Speaker=801222,}",
  [171904] = "{id=171904,Text='让我看看……唔，不知不觉只剩下最后一块巧克力了！快快行动吧~',Speaker=893983,}",
  [171905] = "{id=171905,Text='诶？诶诶诶！是要给我的意思吗？',Speaker=893983,}",
  [171906] = "{id=171906,Text='没想到啊，我当爱神这么久！第一次收到的情人节礼物，竟然是来自冒险者！',Speaker=893983,Emoji=24,}",
  [171907] = "{id=171907,Text='很甜，我很喜欢，谢谢你。',Speaker=893983,}",
  [171910] = "{id=171910,Text='新的一年来咯~这次，B格猫星人给冒险者准备一份大大的压岁钱~',Speaker=893985,}",
  [171911] = "{id=171911,Text='除了祝冒险者在新的一年里身体健康工作（学业）顺利，这个满满当当的压岁钱，你可一定要收好了哦~',Speaker=893985,}",
  [171912] = "{id=171912,Text='恭喜恭喜，恭喜发财！',Speaker=893985,}",
  [171913] = "{id=171913,Text='真热闹呀！每年过年的时候，就是我最忙的时候……',Speaker=893985,}",
  [171914] = "{id=171914,Text='为了筹备各种庆祝活动，都抽不出功夫去给平时关照我的朋友们拜年……',Speaker=893985,}",
  [171915] = "{id=171915,Text='你能帮我去吗？会有红包作为感谢哟~[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=893985,Option='{交给我吧,2},{还有别的事,3}',}",
  [171916] = "{id=171916,Text='那可真是帮大忙了！',Speaker=893985,}",
  [171917] = "{id=171917,Text='请帮我把这份礼物交给《国家地理》特派员娜莎吧！她应该就在普隆德拉广场喷泉附近。',Speaker=893985,Option='好的',}",
  [171918] = "{id=171918,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171919] = "{id=171919,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893985,}",
  [171920] = "{id=171920,Text='请帮我把这份礼物交给时空旅行者埃尔维斯吧！他应该就在艾尔帕兰的钟楼附近。',Speaker=893985,}",
  [171921] = "{id=171921,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171922] = "{id=171922,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893985,}",
  [171923] = "{id=171923,Text='请帮我把这份礼物交给魔法公会的雷藏吧！他应该就在吉芬的法师塔附近。',Speaker=893985,}",
  [171924] = "{id=171924,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171925] = "{id=171925,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893985,}",
  [171926] = "{id=171926,Text='请帮我把这份礼物交给辛格拉吧！他应该就在里希塔乐镇工作。',Speaker=893985,}",
  [171927] = "{id=171927,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171928] = "{id=171928,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893985,}",
  [171929] = "{id=171929,Text='城里变得更热闹了！',Speaker=803273,Option='递交礼物',}",
  [171930] = "{id=171930,Text='啊！是新年礼物！',Speaker=803273,}",
  [171931] = "{id=171931,Text='真高兴~这个送给你！',Speaker=803273,}",
  [171932] = "{id=171932,Text='新年快乐~',Speaker=803273,}",
  [171933] = "{id=171933,Text='请帮我把这份礼物交给吟游诗人博宁·马格努斯吧！他应该就在斐扬城的城南。',Speaker=893985,}",
  [171934] = "{id=171934,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171935] = "{id=171935,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893985,}",
  [171936] = "{id=171936,Text='请帮我把这份礼物交给梦罗克商旅会财政官阿布吧！你能在梦罗克城中雕像西边找到他。',Speaker=893985,}",
  [171937] = "{id=171937,Text='今天只送这家就行了。谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171938] = "{id=171938,Text='明天如果有空，就再回来帮我的忙吧！新年快乐！',Speaker=893985,}",
  [171939] = "{id=171939,Text='请帮我把这份礼物交给总统秘书昆特吧！他肯定还在朱诺城西的总统府加班呢。',Speaker=893985,}",
  [171940] = "{id=171940,Text='谢谢你，真是帮大忙了！',Speaker=893985,}",
  [171941] = "{id=171941,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893985,}",
  [171942] = "{id=171942,Text='在这个特殊的节日里，当然是要把祝福送给更多的冒险者',Speaker=893985,Emoji=5,}",
  [171943] = "{id=171943,Text='当然，这靠我一个人的力量肯定是做不到的，所以我需要你来帮我一起送祝福',Speaker=893985,Option='我要怎么做？',}",
  [171944] = "{id=171944,Text='只要你将[c][ffff00]「新春祝福」[-][/c]传递出去，米德加尔特的冒险者们就会收到我的祝福了！',Speaker=893985,}",
  [171945] = "{id=171945,Text='[c][ffff00]（分享至世界频道传递给更多冒险者即可获得「拜年红包·2024」x5）[-][/c]\n那么，幸运虎王的[c][ffff00]「新春祝福」[-][/c]是：',Speaker=893985,}",
  [171949] = "{id=171949,Text='我代表所有B格猫星人，祝你在新的一年里，事业顺风，步步高升！',Speaker=893985,}",
  [171950] = "{id=171950,Text='当然，最重要的，祝你在新的一年里，不用再看忽克连的脸色，祝你小手一抖，+15就有~',Speaker=893985,}",
  [171951] = "{id=171951,Text='新年新年，当然一切都要新新的，新年新气象，新年必须穿新衣~！',Speaker=893985,}",
  [171952] = "{id=171952,Text='所以，我向领导申请了一发衣装扭蛋的十连送给你，希望你可以一发入魂哦~',Speaker=893985,}",
  [171953] = "{id=171953,Text='有一点需要说明的是，这次的券具有时效性，所以，拿到手就请尽快去使用哦~',Speaker=893985,}",
  [171954] = "{id=171954,Text='那么，祝你好运~！',Speaker=893985,}",
  [171955] = "{id=171955,Text='波利岛又出现了一些魔物，跟以往的不同的是，这次出现的都是来送祝福的~',Speaker=893985,}",
  [171956] = "{id=171956,Text='如果你能帮我收集到「飞龙贺岁」、「虎王送福」、「年兽迎春」这三个道具，我就有一份大大的奖励给你哦~',Speaker=893985,}",
  [171957] = "{id=171957,Text='确定要提交「飞龙贺岁」x1、「虎王送福」x1、「年兽迎春」x1吗？',Speaker=893985,Option='{确定,4},{再想想,3}',}",
  [171958] = "{id=171958,Text='看起来你还没有准备好哦~击败波利岛的魔物有概率掉落哦~',Speaker=893985,}",
  [171959] = "{id=171959,Text='谢谢你，冒险者~！',Speaker=893985,}",
  [171963] = "{id=171963,Text='情人节就要来了，没有谈过恋爱的「雅尼克」对初次见面的「蕾欧娜」一见钟情。毫无经验的他，想先拜托冒险者收集一些可行的恋爱攻略……恋爱本就是个人的事，真的有什么捷径可言吗？',Speaker=893980,}",
  [171964] = "{id=171964,Text='2/1~3/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「诺尔」接取系列任务',Speaker=893980,}",
  [171965] = "{id=171965,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「爱恋的感觉」x5\n完成第5个任务，可获得Zeny x 300,000、「爱恋的感觉」x5、「B格猫福利券·宴」x30、「告白助力礼盒」x1',Speaker=893980,}",
  [171966] = "{id=171966,Text='为了迎接一年一度的情人节，多兰族最优秀的白巧克力大师「慕斯·可可」制作了可食用的巧克力头饰……但意外总是接踵而至，这次又会发生怎样的故事呢',Speaker=893980,}",
  [171967] = "{id=171967,Text='2/5~3/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「慕斯·可可」即可参与活动~',Speaker=893980,}",
  [171968] = "{id=171968,Text='完成活动可以获得Zeny、[c][ffff00]爱恋的感觉[-][/c]、限定活动头饰[c][ffff00]纯情巧克力[-][/c]哦~',Speaker=893980,}",
  [171969] = "{id=171969,Text='多兰族黑巧克力大师「焦糖·可可」也来到了普隆德拉，听说这里的人们喜欢在这个时候互送各种巧克力，然后大胆告白~她也为你准备了特殊的巧克力哦！',Speaker=893980,}",
  [171970] = "{id=171970,Text='2/12~3/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「焦糖·可可」即可参与活动~',Speaker=893980,}",
  [171971] = "{id=171971,Text='完成活动可以获得Zeny、[c][ffff00]爱恋的感觉[-][/c]、限定活动头饰[c][ffff00]浓情巧克力[-][/c]哦~',Speaker=893980,}",
  [171972] = "{id=171972,Text='吃太多甜的对牙可不好，可这个时候谁不想来一块呢~把各式各样的巧克力赠送给NPC吧，谁又能拒绝甜蜜的礼物呢？',Speaker=893980,}",
  [171973] = "{id=171973,Text='2/19~3/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到爱神「菲尔」即可参与活动~',Speaker=893980,}",
  [171974] = "{id=171974,Text='完成活动可以获得Zeny、[c][ffff00]爱恋的感觉[-][/c]哦~',Speaker=893980,}",
  [171975] = "{id=171975,Text='2/26~3/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到小爱神「艾玛」即可参与活动~',Speaker=893980,}",
  [171976] = "{id=171976,Text='完成活动可以获得料理兑换券、[c][ffff00]B格猫福利券·宴[-][/c]等奖励哦~',Speaker=893980,}",
  [171977] = "{id=171977,Text='传闻，价值不菲的「永恒玫瑰」象征着永不凋落的爱情！而每年这个时候，洛阳会出现携带着「永恒玫瑰」的魔物，击败它们，收集稀有的玫瑰吧！',Speaker=893980,}",
  [171978] = "{id=171978,Text='2/1~3/1期间，每周六、周日晚16点至24点，洛阳将有大量魔物入侵，冒险者拿起手中的武器，去击败他们吧~',Speaker=893980,}",
  [171979] = "{id=171979,Text='击败入侵的魔物将有机会获得「花灵附魔礼盒」、「花灵符文礼盒」、[c][ffff00]「戌愿衣系列外观」」[-][/c]等稀有奖励哦~',Speaker=893980,}",
  [171980] = "{id=171980,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893984,}",
  [171981] = "{id=171981,Text='你知道吗？没有什么是不付出就有回报的，恋爱也是如此哦~',Speaker=893984,}",
  [171982] = "{id=171982,Text='嗯嗯，不如我们来试试~如果你愿意付出一点的，我会给你意想不到的收获哦~',Speaker=893984,}",
  [171983] = "{id=171983,Text='每年这个时候，洛阳会出现携带着「永恒玫瑰」的魔物……',Speaker=809785,Option='永恒玫瑰？',}",
  [171984] = "{id=171984,Text='对，价值不菲的「永恒玫瑰」象征着永不凋落的爱情~但这些魔物也是令人心烦。',Speaker=809785,}",
  [171985] = "{id=171985,Text='冒险者，你能帮我去教训教训它们吗？',Speaker=809785,Option='没问题',}",
  [171986] = "{id=171986,Text='哈哈哈哈，看到他们落荒而逃的样子，真是让人开心呢~',Speaker=809785,}",
  [171989] = "{id=171989,Text='春节登录就送十连哦~冒险者千万不要错过~',Speaker=893986,}",
  [171990] = "{id=171990,Text='2月9日-2月12日期间，Base等级≥25级的冒险者，只要登录游戏，就可以前往普隆德拉广场，找到「B格猫星人」领取衣装扭蛋十连奖励',Speaker=893986,}",
  [171991] = "{id=171991,Text='春节期间每日登录都有奖励哦~记得上来动动手指，领取礼物~',Speaker=893986,}",
  [171992] = "{id=171992,Text='2月9日-3月31日期间，「春节签到」活动开启，完成签到即可获得新春外观、符文石、福利券、弗雷硬币等超值奖励',Speaker=893986,}",
  [171993] = "{id=171993,Text='过年当然少不了压岁钱~今年给冒险者准备的压岁钱，可以很有分量哦~',Speaker=893986,}",
  [171994] = "{id=171994,Text='2月9日-2月19日期间，Base等级≥25级的冒险者，前往普隆德拉广场，找到「B格猫星人」即可领取一份压岁钱~',Speaker=893986,}",
  [171995] = "{id=171995,Text='「新春年兽」入侵普隆德拉，「吉祥飞龙」、「幸运虎王」肆虐波利岛，击败他们，可以获得特殊奖励哦~',Speaker=893986,}",
  [171996] = "{id=171996,Text='2月9日-2月13日 4点，普隆德拉将刷新大量「年兽」，击败后有概率获得「年兽珍藏礼盒」，有较高概率获得「年兽迎春」',Speaker=893986,}",
  [171997] = "{id=171997,Text='2月9日-2月19日 4点，波利岛将刷新大量「吉祥飞龙」、「幸运虎王」击败后有概率获得「新春福袋2024」、「飞龙贺岁」、「虎王送福」，有较低概率获得「年兽迎春」',Speaker=893986,}",
  [171998] = "{id=171998,Text='收集「年兽迎春」、「飞龙贺岁」、「虎王送福」可以前往普隆德拉广场，找到「B格猫星人」兑换春节限定头饰「辰头巾」',Speaker=893986,}",
  [171999] = "{id=171999,Text='春节可是B格猫星人最忙碌的时候，一边要给冒险者带来各种福利，另一边还不能忘记给各种重要的NPC去拜年~',Speaker=893986,}",
  [172000] = "{id=172000,Text='2月10日-2月19日，Base等级≥25级的冒险者，前往普隆德拉广场，找到「B格猫星人」即可参与「新春大拜年」活动',Speaker=893986,}",
  [172001] = "{id=172001,Text='共7个连续的任务，若冒险者未能在下发当日完成，可在活动期间任意时间内按顺序完成已下发任务，完成每个任务可以获得「拜年红包2024」x1',Speaker=893986,}",
  [172002] = "{id=172002,Text='B格猫星人想要把新春的祝福传递给每一个冒险者，但是这没有你的帮助可不行哦~',Speaker=893986,}",
  [172003] = "{id=172003,Text='2月10日-2月19日，Base等级≥25级的冒险者，前往普隆德拉广场，找到「B格猫星人」即可参与「恭贺新禧」活动',Speaker=893986,}",
  [172004] = "{id=172004,Text='帮助「B格猫星人」传递新春祝福即可获得「拜年红包2024」x5',Speaker=893986,}",
  [172005] = "{id=172005,Text='正月初五迎财神，新的一年财运旺~！',Speaker=893986,}",
  [172006] = "{id=172006,Text='2月14日-2月15日，「财之神」将现身「普隆德拉」、「吉芬」、「梦罗克」、「斐扬」中心广场，Base等级≥25级的冒险者可以在每个城市的「财之神」处领取「财神福袋」x1',Speaker=893986,}",
  [172007] = "{id=172007,Text='当冒险者第4次开启「财神福袋」，必定可以获得「本职业星符文」x1',Speaker=893986,}",
  [172008] = "{id=172008,Text='过完了春节，又到了开工的日子啦~B格猫星人为各位冒险者准备了「开工利是」!',Speaker=893986,}",
  [172009] = "{id=172009,Text='2月18日-2月19日，Base等级≥25级的冒险者，前往普隆德拉广场，找到「B格猫星人」即可领取「开工利是2024」！',Speaker=893986,}",
  [172010] = "{id=172010,Text='哈哈哈哈，你仔细看看他的样子，我是说他原本的模样。',Speaker=893957,}",
  [172011] = "{id=172011,Text='护送来自普隆德拉北部的圣诞马车，为普隆德拉的市民们带去圣诞礼物吧！',Speaker=850004,}",
  [172012] = "{id=172012,Text='护送过程中会遇到多个突发事件，只有解决掉这些突发事件，马车才会继续前进哦~',Speaker=850004,}",
  [172013] = "{id=172013,Text='活动开放时间为12/11~12/18 每天的8：00~23：00，请各位冒险家注意参与时间~',Speaker=850004,}",
  [172014] = "{id=172014,Text='参与活动可以获得圣诞老人的心意，每天获取上限为200个~达到上限后，可以明天再来参与活动哦~',Speaker=850004,}",
  [172017] = "{id=172017,Text='呜呜呜……善良的冒险者，请帮帮我！',Speaker=893987,Option='怎么了？',}",
  [172018] = "{id=172018,Text='我很开心地来参加庆典，准备了超大的蛋糕准备分享给大家……可是，我的气球……一不留神就……飘走了！',Speaker=893987,Option='我来帮你！',}",
  [172019] = "{id=172019,Text='呜呜呜……我就知道，你能帮我的！',Speaker=893987,Option='怎么做？',}",
  [172020] = "{id=172020,Text='[c][ffff00]气球怪盗[-][/c]将携带着各式各样的气球，游荡在米德加尔特大陆各处，击败它们就能获得庆典气球了！',Speaker=893987,Option='我知道了',}",
  [172021] = "{id=172021,Text='唔~~~没有受伤吧！庆典气球收集好了么？',Speaker=893987,Option='嗯嗯',}",
  [172022] = "{id=172022,Text='你看看，这个是你的气球吗？',}",
  [172023] = "{id=172023,Text='是庆典气球！把它带回给庆典巨人吧！',}",
  [172024] = "{id=172024,Text='哇~你找到了庆典气球！！太感谢了，这个礼物送给你！',Speaker=893987,}",
  [172025] = "{id=172025,Text='请等等哦，让我戴上它~',Speaker=893987,}",
  [172026] = "{id=172026,Text='怎么样，还不错吧？不过，正如你所见，我还差一些气球，明天也请来帮助我吧！',Speaker=893987,}",
  [172027] = "{id=172027,Text='据我所知，今天它应该在[c][ffff00]南门[-][/c]出没。准备好了的话，我就直接传送你过去吧~',Speaker=893987,Option='准备好了',}",
  [172028] = "{id=172028,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893987,}",
  [172029] = "{id=172029,Text='话不多说，你也知道要做什么了，对吧？那么……',Speaker=893987,}",
  [172030] = "{id=172030,Text='据我所知，今天它应该在[c][ffff00]尼夫海姆[-][/c]出没。准备好了的话，我就直接传送你过去吧~',Speaker=893987,Option='准备好了',}",
  [172031] = "{id=172031,Text='据我所知，今天它应该在[c][ffff00]发乐斯灯塔[-][/c]出没。准备好了的话，我就直接传送你过去吧~',Speaker=893987,Option='准备好了',}",
  [172032] = "{id=172032,Text='据我所知，今天它应该在[c][ffff00]姜饼城[-][/c]出没。准备好了的话，我就直接传送你过去吧~',Speaker=893987,Option='准备好了',}",
  [172033] = "{id=172033,Text='据我所知，今天它应该在[c][ffff00]艾尔贝塔[-][/c]出没。准备好了的话，我就直接传送你过去吧~',Speaker=893987,Option='准备好了',}",
  [172034] = "{id=172034,Text='哇，集齐了！谢谢你，有了HAPPY，我就可以开心地去参加庆典啦~',Speaker=893987,}",
  [172035] = "{id=172035,Text='嘿嘿，我悄悄为你准备了一份特别的礼物——[c][ffff00]波利蛋糕[-][/c]！希望这次庆典也会成为你美好的回忆~',Speaker=893987,Option='谢谢',}",
  [172036] = "{id=172036,Text='喵老师布置了作业，要我们画下春天，虽然说绿油油的就能代表春天了，但是这也太俗了~',Speaker=801663,}",
  [172037] = "{id=172037,Text='[c][ffff00][PlayerName][-][/c]，你知道还有什么是可以代表春天的吗？',Speaker=801663,Option='当然！',}",
  [172038] = "{id=172038,Text='太好了，能麻烦你去帮我拍一张独特的照片，然后带回来给我，让我一些灵感吗？',Speaker=801663,Option='没问题！',}",
  [172039] = "{id=172039,Text='那今天就先去多拉贡岛看看吧~出发咯~',Speaker=801663,}",
  [172040] = "{id=172040,Text='呀~原来春天就是和朋友们的踏青~这真是个好灵感~',Speaker=801663,}",
  [172041] = "{id=172041,Text='那今天就先去多罗比奥森林看看吧~出发咯~',Speaker=801663,}",
  [172042] = "{id=172042,Text='呀~原来春天就是一江春水哗哗流，如果有小鸭子在里面，肯定能感受到水温渐渐暖了起来呢~',Speaker=801663,}",
  [172043] = "{id=172043,Text='呀~原来春天就是无限的生机~这真是个好灵感~',Speaker=801663,}",
  [172044] = "{id=172044,Text='那今天就去流星林看看吧~出发咯~',Speaker=801663,}",
  [172045] = "{id=172045,Text='呀，原来春天就是茁壮地成长~这真是个好灵感~',Speaker=801663,}",
  [172046] = "{id=172046,Text='那今天就去普隆德拉南门看看吧~出发咯~',Speaker=801663,}",
  [172047] = "{id=172047,Text='呀，原来春天就是肆意妄为地盛开，不管别人的看法，绽放自我！这真是个好灵感~',Speaker=801663,}",
  [172048] = "{id=172048,Text='又到了这个特殊的节日喵~',Speaker=801620,}",
  [172049] = "{id=172049,Text='本喵已经把要给你的祝福全都写进了这张明信片里面，快打开看看吧喵~',Speaker=801620,}",
  [172050] = "{id=172050,Text='回去告诉赫卡蒂·曲奇今天的树苗已经种下了吧!',}",
  [172051] = "{id=172051,Text='辛苦了喵~罗扎纳因为有你而更美丽~！',Speaker=807798,}",
  [172053] = "{id=172053,Text='哎呀呀~制作[c][ffff00]情谊巧克力[-][/c]需要[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂蜜×5[-][/c]以及[c][ffff00]奶油榛果x2[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我呦~',Speaker=801586,}",
  [172054] = "{id=172054,Text='[c][ffff00]B格猫巧克力[-][/c]只需花费[c][ffff00]Zeny×520[-][/c]委托我制作哦~如果你需要的话，跟我对话就好啦~',Speaker=801586,}",
  [172055] = "{id=172055,Text='吼哦？~看你洋溢着幸福的表情，想[c][ffff00]亲手[-][/c]为心中最喜欢的那个人制作一份[c][ffff00]本命巧克力[-][/c]吧！好好好！我先告诉你配方，需要：[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂胶×5[-][/c]以及[c][ffff00]暖心料理x2[-][/c]哦~制作的时候，脑海中边想象着那个你最喜欢最喜欢的人并将那份爱意一起放入，就能做出全世界独一无二的[c][ffff00]本命巧克力[-][/c]了！',Speaker=801586,Option='{B格猫巧克力?,5},{立即动手,2},{默默走开,1}',}",
  [172056] = "{id=172056,Text='哎呀呀，先不要心急哦。制作[c][ffff00]本命巧克力[-][/c]需要唱片[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂胶×5[-][/c]以及[c][ffff00]暖心料理x2[-][/c]哦，耐心集齐材料后再来找我吧~',Speaker=801586,}",
  [172057] = "{id=172057,Text='呀~看来你是想要兑换一个[c][ffff00]CP卡片自选礼盒[-][/c]呢？需要的材料是：[c][ffff00]暖暖的爱意×1[-][/c]、[c][ffff00]幸福的爱意×1[-][/c]哦~只需这些我就会帮你做好呦~',Speaker=801586,Option='{如何获得,5},{立即动手,2},{默默走开,1}',}",
  [172058] = "{id=172058,Text='哎呀呀~兑换[c][ffff00]CP卡片自选礼盒[-][/c]需要[c][ffff00]暖暖的爱意×1[-][/c]、[c][ffff00]幸福的爱意×1[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我呦~',Speaker=801586,}",
  [172059] = "{id=172059,Text='呀~看来你是想要一块[c][ffff00]B格猫巧克力[-][/c]呢？只需：[c][ffff00]Zeny×520[-][/c]哦~只需这些我们就会帮你做好呦~',Speaker=801586,Option='{立即动手,2},{默默走开,1}',}",
  [172060] = "{id=172060,Text='哎呀呀~制作[c][ffff00]B格猫巧克力[-][/c]需要[c][ffff00]Zeny×520[-][/c]哦，好可惜...你的材料不够呢！收集齐了再来找我们呦~',Speaker=801586,}",
  [172061] = "{id=172061,Text='呜呜呜呜呜呜呜呜~o(╥﹏╥)o',Speaker=801805,Option='怎么了？',}",
  [172062] = "{id=172062,Text='约翰特意为我们做的甜品被一群魔物抢了，我们一口都没吃到！',Speaker=801805,}",
  [172063] = "{id=172063,Text='你能帮我去把甜品抢回来吗？我们真的好想吃啊呜呜呜呜呜~',Speaker=801805,Option='没问题',}",
  [172064] = "{id=172064,Text='先去吉芬看看吧，我看到一个紫色的身影往那边去了~',Speaker=801805,}",
  [172065] = "{id=172065,Text='那我就用魔法传送你过去吧~记得甜品要轻拿轻放呀~',Speaker=801805,}",
  [172066] = "{id=172066,Text='果然还是晚了一步吗，我都不敢相信他在吃甜品的时候内心会有多么得幸福！',Speaker=801805,}",
  [172067] = "{id=172067,Text='我的甜品我的甜品我的甜品~喵喵喵~',Speaker=801805,}",
  [172068] = "{id=172068,Text='今天去斐扬看看吧~那天来抢甜品的魔物里面，我看到了那头大老虎的身影~！',Speaker=801805,}",
  [172069] = "{id=172069,Text='那我就用魔法传送你过去吧~记得甜品要轻拿轻放呀~',Speaker=801805,}",
  [172070] = "{id=172070,Text='果然还是晚了一步吗，我都不敢相信他在吃甜品的时候内心会有多么得幸福！',Speaker=801805,}",
  [172071] = "{id=172071,Text='再不吃甜品，我觉得我的肚子要抗议了！',Speaker=801805,}",
  [172072] = "{id=172072,Text='今天去金字塔看看吧，那个浑身缠满绷带的家伙，也不知道抢了甜品去干嘛！难道是要送给蛇女吃吗？',Speaker=801805,}",
  [172073] = "{id=172073,Text='那我就用魔法传送你过去吧~记得甜品要轻拿轻放呀~',Speaker=801805,}",
  [172074] = "{id=172074,Text='啊啊啊啊啊啊啊，难道这些甜品我真的一个都吃不上了吗？',Speaker=801805,}",
  [172075] = "{id=172075,Text='好了，我已经对甜品不抱期望了~但是我要让那些抢甜品的坏蛋知道，抢我甜品者，虽远必诛！',Speaker=801805,}",
  [172076] = "{id=172076,Text='今天去生命体研究所看看吧，那只大鸟应该是往那个方向飞去了~',Speaker=801805,}",
  [172077] = "{id=172077,Text='那我就用魔法传送你过去吧~',Speaker=801805,}",
  [172078] = "{id=172078,Text='哼，吃我的甜品是要付出代价的！',Speaker=801805,}",
  [172079] = "{id=172079,Text='终于到最后一个了！教训完他们，以后应该就不会再有谁来抢甜品了~',Speaker=801805,}",
  [172080] = "{id=172080,Text='今天去乌龟岛密穴看看吧，这是那只贪吃的大乌龟的老巢，他抢了甜品之后，肯定躲起来了',Speaker=801805,}",
  [172081] = "{id=172081,Text='那我就用魔法传送你过去吧~',Speaker=801805,}",
  [172082] = "{id=172082,Text='这看起来是一份甜品~赶紧给那只贪吃的小猫咪带回去吧~',}",
  [172083] = "{id=172083,Text='哇！真是想不到，竟然还能吃到这美味的甜品！',Speaker=801805,}",
  [172084] = "{id=172084,Text='谢谢你，冒险者~！',Speaker=801805,}",
  [172086] = "{id=172086,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893994,}",
  [172087] = "{id=172087,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=893994,}",
  [172088] = "{id=172088,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【-Naoyuki-】[-][/c]绘制的共创头像！',Speaker=893994,Option='哇！',}",
  [172089] = "{id=172089,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=893994,Option='谢谢',}",
  [172090] = "{id=172090,Text='都怪我的甜品做得实在是太好吃了，以至于一些贪吃的魔物都忍不住来罗扎纳抢夺甜品！',Speaker=801648,Option='怎么了？',}",
  [172091] = "{id=172091,Text='但是这些甜品都是为辛辛苦苦为罗扎纳的可爱的小猫咪们准备的，被贪吃的魔物抢了，就意味着小猫咪们要饿肚子了！',Speaker=801648,}",
  [172092] = "{id=172092,Text='冒险者，你能帮我去教训教训他们吗？',Speaker=801648,Option='没问题',}",
  [172093] = "{id=172093,Text='希望能给其余的魔物一点威慑！',Speaker=801648,}",
  [172094] = "{id=172094,Text='三月春暖花开，普隆德拉的人民决定放假去郊游，享受春天的气息。不过郊游的目的地还没有决定，‘卡洛·史蒂芬’忙的不可开交，没时间实地考察了....',Speaker=893994,}",
  [172095] = "{id=172095,Text='3月1日-4月1日期间，Base≥25级的冒险者前往普隆德拉找到「卡洛·史蒂芬」即可参与活动',Speaker=893994,}",
  [172096] = "{id=172096,Text='共5个连续的任务，每完成每个任务，可以获得Zeny x 300,000，「春日嫩芽」x5；完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=893994,}",
  [172097] = "{id=172097,Text='在这个万物初生的季节，「芬克斯·雪耳」缺在烦恼要怎么画出春天，冒险者赶紧带上「照相机」去拍下春天的美景帮助「芬克斯·雪耳」寻找画画的灵感吧~',Speaker=893994,}",
  [172098] = "{id=172098,Text='3月7日-4月1日期间，Base≥25级的冒险者前往罗扎纳找到「芬克斯·雪耳」即可参与活动',Speaker=893994,}",
  [172099] = "{id=172099,Text='共5个连续的任务，完成第1--4个任务，可以获得Zeny x 150,000，「春日嫩芽」x1；完成第5个任务，可以获得Zeny x 300,000，「春日嫩芽」x3',Speaker=893994,}",
  [172100] = "{id=172100,Text='这是属于万千女王的节日，「伊蕾塔·绒绒」也给冒险者带来了喵喵的祝福~',Speaker=893994,}",
  [172101] = "{id=172101,Text='3月8日-3月15日期间，Base≥25级的冒险者前往罗扎纳找到「伊蕾塔·绒绒」即可领取「女王节明信片」',Speaker=893994,}",
  [172102] = "{id=172102,Text='打开明信片将可以收到「弗雷硬币」x1、「闪耀的符文石」x1、「好运币」x10的礼物哦',Speaker=893994,}",
  [172103] = "{id=172103,Text='春天是适合种树的季节，冒险者动动手，来让罗扎纳变得更加美丽吧~',Speaker=893994,}",
  [172104] = "{id=172104,Text='3月12日-3月15日期间，Base≥25级的冒险者前往罗扎纳找到「赫卡蒂·曲奇」，帮助他在罗扎纳种下小树，即可获得Zeny x 200,000，「春日嫩芽」x2、「B格猫福利券·宴」x10',Speaker=893994,}",
  [172105] = "{id=172105,Text='白色情人节来啦~去把亲手做的巧克力送给最爱的TA吧~',Speaker=893994,}",
  [172106] = "{id=172106,Text='3月14日-4月1日期间，Base≥25级的冒险者前往罗扎纳找到「阿钱·银牙」即可制作「情谊巧克力」、「情谊巧克力」',Speaker=893994,}",
  [172107] = "{id=172107,Text='完成任务还可以获得「CP卡片自选礼盒」',Speaker=893994,}",
  [172108] = "{id=172108,Text='「阿锋·起司」的甜品吸引了一些贪吃的魔物，击败它们，帮助小猫咪夺回甜品！',Speaker=893994,}",
  [172109] = "{id=172109,Text='3月21日-4月1日期间，Base≥25级的冒险者前往罗扎纳找到「阿锋·起司」即可参与活动',Speaker=893994,}",
  [172110] = "{id=172110,Text='共5个连续的任务，完成第1--4个任务，均可获得Zeny x 150,000，「春日嫩芽」x1；完成第5个任务，可以获得Zeny x 300,000，「春日嫩芽」x3',Speaker=893994,}",
  [172111] = "{id=172111,Text='罗扎纳特产的美味甜品吸引了一大波贪吃的魔物，击败它们，守护美味的甜品和可爱的喵喵吧~',Speaker=893994,}",
  [172112] = "{id=172112,Text='每周六、周日，携带美味甜品的魔物将刷新在罗扎纳，击败他们可以获得「美味附魔礼盒」、「美味符文礼盒」、「美味珍藏礼盒·MVP」等奖励',Speaker=893994,}",
  [172113] = "{id=172113,Text='冒险者将第三次打开「美味珍藏礼盒·MVP」必定获得「星夜蔷薇头饰兑换券」哦~',Speaker=893994,}",
  [172114] = "{id=172114,Text='哎哎，你先别走！这是特别为你定制的[c][ffff00]「烟花筒」[-][/c]，一定要试试！',Speaker=893875,}",
  [172115] = "{id=172115,Text='纷乱的时间线总是被拨动……为了修复异常的时空，为了举办盛大的庆典，冒险者将再次登场，跟随初心者一起，重启在米德加尔特大陆的旅程……',Speaker=893873,}",
  [172116] = "{id=172116,Text='1/1~2/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「奥斯卡」即可参与活动~',Speaker=893873,}",
  [172117] = "{id=172117,Text='跟随任务的指引，重温过往，收集属于自己的回忆，解锁周年回忆中的专属记忆~',Speaker=893873,}",
  [172118] = "{id=172118,Text='完成活动可以获得Zeny、[c][ffff00]庆典纪念币[-][/c]、[c][ffff00]B格猫福利券·宴[-][/c]等奖励哦~',Speaker=893873,}",
  [172119] = "{id=172119,Text='对未来抱有期待吧，这次的期许或许会得到特别的回应…',Speaker=893873,}",
  [172120] = "{id=172120,Text='1/1~2/1期间，Base等级大于等于25级的冒险者前往南门，找到「许愿树」即可参与活动~',Speaker=893873,}",
  [172121] = "{id=172121,Text='[c][ffff00]1月19日周年庆直播现场[-][/c]，将随机抽取多个冒险者实现愿望哦~',Speaker=893873,}",
  [172122] = "{id=172122,Text='1/20~2/1期间，Base等级大于等于25级的冒险者前往普隆德拉，找到「庆典巨人」即可参与活动~',Speaker=893873,}",
  [172123] = "{id=172123,Text='完成活动可以获得Zeny、庆典纪念币、限定头饰[c][ffff00]波利蛋糕[-][/c]等奖励哦~',Speaker=893873,}",
  [172124] = "{id=172124,Text='对啦，[c][ffff00]1月19日周年庆直播现场[-][/c]，将随机抽取多个冒险者实现愿望哦！一定不要错过哦！',Speaker=893244,}",
  [172125] = "{id=172125,Text='善良的冒险者，请帮帮我！',Speaker=2700,Option='怎么了？',}",
  [172126] = "{id=172126,Text='如你所见，我经营着金币商店，数年如一日的坚持与努力，才有了今天的结果……',Speaker=2700,}",
  [172127] = "{id=172127,Text='总有冒险者跟我抱怨——人生最大的遗憾，莫过于在错误的时间不期而遇！想要的珍宝，总是在错过……',Speaker=2700,}",
  [172128] = "{id=172128,Text='为此，我决定举办一场“年终钜惠”，让诸位冒险者有机会重逢那些美好的珍宝！',Speaker=2700,Option='哇！',}",
  [172129] = "{id=172129,Text='当然了，每次活动的举办，总少不了一些必要的素材！所以，善良的冒险者，请帮帮我！',Speaker=2700,Option='怎么做？',}",
  [172130] = "{id=172130,Text='就先从简单的开始吧，今天你只要帮我收集10个蓝色魔力矿石就好！那么，确定要提交吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172131] = "{id=172131,Text='不愧是你，这次活动的赞助人将写上你的大名！那么，明天，请记得来这里找我哦~',Speaker=2700,}",
  [172132] = "{id=172132,Text='看起来你还没有准备好哦~',Speaker=2700,}",
  [172134] = "{id=172134,Text='HI~[c][ffff00][PlayerName][-][/c]，你果然是一如既往的准时！',Speaker=2700,Option='还行吧',}",
  [172135] = "{id=172135,Text='那么，今天就收集10个魔女星沙吧！要知道这可是最好用的魔力催化剂！',Speaker=2700,}",
  [172136] = "{id=172136,Text='确定要提交10个魔女星沙吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172137] = "{id=172137,Text='想不到今天也是那么快的速度，明天也要记得来哦！',Speaker=2700,}",
  [172138] = "{id=172138,Text='那么，今天就收集10个恶魔角吧！快去快回吧！',Speaker=2700,}",
  [172139] = "{id=172139,Text='确定要提交10个恶魔角吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172140] = "{id=172140,Text='那么，今天就收集10个燃烧的心脏吧！快去快回吧！',Speaker=2700,}",
  [172141] = "{id=172141,Text='确定要提交10个燃烧的心脏吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172142] = "{id=172142,Text='那么，今天就收集10个土元素结晶吧！快去快回吧！',Speaker=2700,}",
  [172143] = "{id=172143,Text='确定要提交10个土元素结晶吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172144] = "{id=172144,Text='那么，今天就收集10个粘稠液体吧！快去快回吧！',Speaker=2700,}",
  [172145] = "{id=172145,Text='确定要提交10个粘稠液体吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172146] = "{id=172146,Text='那么，今天就收集10个沙金吧！快去快回吧！',Speaker=2700,}",
  [172147] = "{id=172147,Text='确定要提交10个沙金吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [172148] = "{id=172148,Text='让我看看……唔，这下所需的材料都集齐了！怎么样，这几天的报酬没有亏待你吧！',Speaker=2700,Option='{还不错,0},{下次继续,0}',}",
  [172149] = "{id=172149,Text='好了，谢谢你，善良的冒险者~这几天你真的帮了我很多~最后一份谢礼，请收下吧！',Speaker=2700,}",
  [172150] = "{id=172150,Text='1/15  ~ 1/21 期间，晚上19:00~21:00，在普隆德拉上空可以看到烟花秀哦！一定不要错过！',Speaker=893873,}",
  [172151] = "{id=172151,Text='据传说只有聪明的人才能听懂吵吵闹闹的哥布灵在说些什么。嗯？你说也你听懂了？！',Speaker=893996,}",
  [172152] = "{id=172152,Text='嘿嘿，那这张明信片，请你收好哦~这是我们友谊的见证！',Speaker=893996,}",
  [172153] = "{id=172153,Text='冒险者，吉芬出现异样，我需要你的协助！',Speaker=1321,Option='怎么了？',}",
  [172154] = "{id=172154,Text='不知道你发现了什么，但我有个推测——或许是因为哥布灵国王的外出，大量变异的巴风特入侵吉芬，难道他们是想占领哥布灵森林吗……',Speaker=1321,}",
  [172155] = "{id=172155,Text='放任不管可不行！冒险者，你能去教训教训他们吗？',Speaker=1321,Option='没问题',}",
  [172156] = "{id=172156,Text='希望能给其余的魔物一点威慑！',Speaker=1321,}",
  [172157] = "{id=172157,Text='嘿~哥布灵庆典的盛大活动！路过的冒险者，你也想来参与一下吗？',Speaker=893997,Option='{是什么,0},{继续说,0}',}",
  [172158] = "{id=172158,Text='没错！就是寻找哥布灵最喜爱的「黄金蜂蜜」！当然了，可不能从哥布灵身上抢夺！！',Speaker=893997,}",
  [172159] = "{id=172159,Text='我会将特殊的「黄金蜂蜜」藏匿在米德加尔特的各处，只要你能找到它，就能领取丰厚的奖励！',Speaker=893997,Option='有线索？',}",
  [172160] = "{id=172160,Text='让我想想，我可能把它[c][ffff00]藏在角落里[-][/c]了，也可能[c][ffff00]放在高处[-][/c]了，还有可能……',Speaker=893997,}",
  [172161] = "{id=172161,Text='怎么样？听起来是不是很简单？那么，话不多说，我们开始挑战吧！',Speaker=893997,}",
  [172162] = "{id=172162,Text='让我看看……这次的挑战场地是在[c][ffff00]「依斯鲁得岛」[-][/c]！',Speaker=893997,}",
  [172163] = "{id=172163,Text='准备好了的话，就前往目的地附近吧！',Speaker=893997,Option='那出发吧！',}",
  [172164] = "{id=172164,Text='（找到了黄金蜂蜜！把它带回去交给哥布灵国王吧！）',}",
  [172165] = "{id=172165,Text='[c][ffff00][PlayerName][-][/c]，你回来啦~怎么样，找到了吗？',Speaker=893997,Option='是这个吗？',}",
  [172166] = "{id=172166,Text='让我尝一尝……喔喔！一定不会错，这就是「黄金蜂蜜」！这是给你的奖励，请收下吧！明天也记得来挑战噢~',Speaker=893997,}",
  [172167] = "{id=172167,Text='冒险者，你来得很准时呢。',Speaker=893997,Option='当然',}",
  [172168] = "{id=172168,Text='那么，让我看看……这次的挑战场地是在[c][ffff00]「朱诺」[-][/c]！',Speaker=893997,}",
  [172169] = "{id=172169,Text='那么，让我看看……这次的挑战场地是在[c][ffff00]「艾卡拉奇」[-][/c]！',Speaker=893997,}",
  [172170] = "{id=172170,Text='那么，让我看看……这次的挑战场地是在[c][ffff00]「尼夫海姆」[-][/c]！',Speaker=893997,}",
  [172171] = "{id=172171,Text='那么，让我看看……这次的挑战场地是在[c][ffff00]「克魔岛」[-][/c]！',Speaker=893997,}",
  [172172] = "{id=172172,Text='让我尝一尝……喔喔！一定不会错，这就是「黄金蜂蜜」！',Speaker=893997,}",
  [172173] = "{id=172173,Text='好……好厉害，我已经没有更难的挑战给你了！奖励要给予你这种真正的智者！',Speaker=893997,}",
  [172174] = "{id=172174,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893996,}",
  [172175] = "{id=172175,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=893996,}",
  [172176] = "{id=172176,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【em史吉】[-][/c]绘制的共创头像！',Speaker=893996,Option='哇！',}",
  [172177] = "{id=172177,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=893996,Option='谢谢',}",
  [172178] = "{id=172178,Text='这两天都在忙着消灭魔物了，我都没有时间去参加哥布灵庆典了！',Speaker=893999,Option='需要帮助吗？',Emoji=24,}",
  [172179] = "{id=172179,Text='当然！哥布灵的水源之地受到了暗黑魔物的侵染，需要各位勇士前来协助！',Speaker=893999,}",
  [172180] = "{id=172180,Text='完成之后也会有珍贵的「水之祝福」送给勇敢的你！你愿意嘛？',Speaker=893999,Option='我去试试！',}",
  [172186] = "{id=172186,Text='一年一度的春日庆典开幕！今年的哥布灵们要举办一场盛大春日庆典！这次，你也会来参加吧！',Speaker=893996,}",
  [172187] = "{id=172187,Text='4月1日-5月1日期间，Base≥25级的冒险者前往普隆德拉找到「托拉基」即可参与活动',Speaker=893996,}",
  [172188] = "{id=172188,Text='共5个连续的任务，每完成一个任务，可以获得Zeny x 300,000，「祝福花环」x5；完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=893996,}",
  [172189] = "{id=172189,Text=' 节日大使「鲁特」为了各位冒险者准备了「祝福水碗」，用纯净的清水相互泼洒，互相祝愿好运吧！',Speaker=893996,}",
  [172190] = "{id=172190,Text='4月8日-5月1日期间，Base≥25级的冒险者前往普隆德拉找到「鲁特」即可参与活动',Speaker=893996,}",
  [172191] = "{id=172191,Text='共5个连续的任务：完成第1~4个任务可以获得Zeny x 150,000，「祝福花环」x1、「水之祝福」x1；完成第5个任务，可以获得Zeny x 300,000，「祝福花环」x3、「水之祝福」x1',Speaker=893996,}",
  [172192] = "{id=172192,Text='哥布灵的水源之地受到了暗黑魔物的侵染，保护水源之地，收集水之祝福吧！',Speaker=893996,}",
  [172193] = "{id=172193,Text='4月15日-4月20日期间，Base≥25级的冒险者前往普隆德拉找到「拉姆」即可参与活动副本',Speaker=893996,}",
  [172194] = "{id=172194,Text='副本奖励跟根据最终通关的波次发放，最高可以获得「祝福花环」x9、「水之祝福」x1\n收集「水之祝福」可兑换玩具「圣水祝福」、卡片「圣水祝福」、头饰「盛夏头纱」；如果以上都已拥有，也可以选择「B格猫福利券·宴」噢！',Speaker=893996,}",
  [172195] = "{id=172195,Text='哥布灵一族自然有不少宝物，但想要收入囊中，可需要付出一点努力噢！',Speaker=893996,}",
  [172196] = "{id=172196,Text='4月22日-5月1日期间，Base≥25级的冒险者前往普隆德拉找到「哥布灵国王」即可参与活动',Speaker=893996,}",
  [172197] = "{id=172197,Text='共5个连续的任务：完成第1~4个任务可以获得Zeny x 150,000，「祝福花环」x1；完成第5个任务，可以获得Zeny x 300,000，「祝福花环」x3',Speaker=893996,}",
  [172198] = "{id=172198,Text='或许是因为哥布灵国王的外出，大量变异的巴风特入侵吉芬，难道他们是想占领哥布灵森林吗……',Speaker=893996,}",
  [172199] = "{id=172199,Text='每周六、周日，巴风特一族将刷新在吉芬，击败他们可以获得「附魔礼盒」、「符文礼盒」、「珍藏礼盒·MVP」等奖励',Speaker=893996,}",
  [172200] = "{id=172200,Text='冒险者将第三次打开「珍藏礼盒·MVP」必定获得「富丽堂皇头饰兑换券」哦~',Speaker=893996,}",
  [172201] = "{id=172201,Text='嘿！朋友！你准备好接收我最真诚的祝福了吧！',Speaker=893998,Option='什么？',}",
  [172202] = "{id=172202,Text='？？？你你你，你不会不知道吧！最近可是泼水节呢——将祝福之水洒向偶遇的冒险者，双方都能获得好运哦！',Speaker=893998,}",
  [172203] = "{id=172203,Text='我知道了，看来，你没有准备好道具呢！没关系，专业人士总有最齐全的准备！',Speaker=893998,}",
  [172204] = "{id=172204,Text='喏，祝福水碗，给你！挑选一位幸运冒险者，给予Ta最真诚的祝福吧！',Speaker=893998,Option='我去试试！',}",
  [172206] = "{id=172206,Text='呀~看来你是想要制作一份[c][ffff00]情谊巧克力[-][/c]呢？需要的材料是：[c][ffff00]B格猫巧克力×1[-][/c]、[c][ffff00]蜂蜜×5[-][/c]以及[c][ffff00]奶油榛果x2[-][/c]哦~只需这些我们就会帮你做好呦~',Speaker=801586,Option='{B格猫巧克力?,5},{委托制作,2},{默默走开,1}',}",
  [172207] = "{id=172207,Text='嘿！朋友！你又来啦！那我们准备开始吧~',Speaker=893998,}",
  [172208] = "{id=172208,Text='喏，祝福水碗，给你！挑选一位幸运冒险者，给予Ta最真诚的祝福吧！',Speaker=893998,}",
  [172210] = "{id=172210,Text='亲爱的[c][ffff00][PlayerName][-][/c]，准备好迎接新年了吗？',Speaker=894001,}",
  [172211] = "{id=172211,Text='不知不觉就要到年末了呢，明年又会有怎样的故事展开呢？',Speaker=894001,}",
  [172212] = "{id=172212,Text='但是现在，你把视野往短了看，比如接下里的一会儿，接下来的一段时间里，我可是为你准备了很多礼物喔，这种感觉应该还是挺值的期待的吧~',Speaker=894001,Option='当然！',Emoji=30,}",
  [172213] = "{id=172213,Text='那么，收下这份礼物，开始享受岁末的福利吧！',Speaker=894001,}",
  [172214] = "{id=172214,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=894001,}",
  [172215] = "{id=172215,Text='还记得过去一年的冒险吗？可真是怀念。所以，我希望……我们来一次故地重游，去看看那些老地方，去见见那些老朋友~',Speaker=894001,Option='好吖',}",
  [172216] = "{id=172216,Text='怎么样！有想到什么嘛，带上你的相机，去记录一下吧~',Speaker=894001,Option='好，走吧',}",
  [172217] = "{id=172217,Text='你回来啦！怎么样，有什么收获吗？',Speaker=894001,}",
  [172218] = "{id=172218,Text='让我看看……噢，是艾尔贝塔呢！真怀念海风拂面的感觉，还有那空气中咸咸的味道……',Speaker=894001,}",
  [172219] = "{id=172219,Text='请收下这个，谢谢能与我分享这份感动。',Speaker=894001,}",
  [172220] = "{id=172220,Text='[c][ffff00][PlayerName][-][/c]，你来啦！今天的拍摄也拜托你了！',Speaker=894001,}",
  [172221] = "{id=172221,Text='让我看看……噢，这次是艾尔贝塔的老忽。看起来，他还是一如既往地在认真打铁呢！',Speaker=894001,Emoji=17,}",
  [172222] = "{id=172222,Text='让我看看……噢，这次是乌龟岛的留影。真好奇，果树龟身上的野果，不知道是什么味道呢~',Speaker=894001,Emoji=17,}",
  [172223] = "{id=172223,Text='让我看看……噢，这次是旧良宫的留影。不知道什么时候，我也能去一趟这样的远方呢。',Speaker=894001,Emoji=17,}",
  [172224] = "{id=172224,Text='让我看看……噢，这次是时空龙奥斯卡。他是你今年新认识的朋友？哇，真厉害呐！',Speaker=894001,}",
  [172225] = "{id=172225,Text='让我看看……噢，这次是普隆德拉的留影。每年的1月可是普隆德拉最热闹的时候呢。',Speaker=894001,}",
  [172226] = "{id=172226,Text='让我看看……噢，这次是南门的留影。或许，你已经不记得第一次来这里的情形，但这个世界里每一位相遇的NPC都会记住你哦~',Speaker=894001,}",
  [172227] = "{id=172227,Text='你来啦，冒险者！',Speaker=894002,}",
  [172228] = "{id=172228,Text='每到岁末将至，我就会来到此处，为大家献上祝福，送上礼物~',Speaker=894002,}",
  [172229] = "{id=172229,Text='不过久了没见人，竟然有点落寞……不如，来猜拳吧！',Speaker=894002,}",
  [172230] = "{id=172230,Text='从猜拳游戏里得到快乐，真是再容易不过的事情了——嘿， 只要你能赢，礼物就归你！',Speaker=894002,Option='{来吧,2},{考虑一下,3}',}",
  [172231] = "{id=172231,Text='嘿！一场精彩刺激的对决要开始了！',Speaker=894002,}",
  [172232] = "{id=172232,Text='三！',Speaker=894002,}",
  [172233] = "{id=172233,Text='二！',Speaker=894002,}",
  [172234] = "{id=172234,Text='一！',Speaker=894002,}",
  [172235] = "{id=172235,Text='剪刀石头布——',Speaker=894002,Option='{石头,40},{剪刀,50},{布,60}',}",
  [172236] = "{id=172236,Text='太遗憾了，我准备的礼物可是很诱人的呢！',Speaker=894002,}",
  [172237] = "{id=172237,Text='嘿嘿，我赢了！',Speaker=894002,}",
  [172238] = "{id=172238,Text='怎么样，见识到我的厉害了吧。还想再挑战我吗？ ',Speaker=894002,Option='{再玩一次,103},{不玩啦,102}',}",
  [172239] = "{id=172239,Text='哟～你赢了呢！',Speaker=894002,}",
  [172240] = "{id=172240,Text='好吧，好运属于你！礼物是你的了！',Speaker=894002,}",
  [172241] = "{id=172241,Text='哦吼？不简单嘛！',Speaker=894002,}",
  [172242] = "{id=172242,Text='唔 … … 没有分出胜负呢。',Speaker=894002,}",
  [172243] = "{id=172243,Text='怎么样，要不要再来一局？',Speaker=894002,Option='{再玩一次,104},{不玩啦,101}',}",
  [172244] = "{id=172244,Text='就这么结束了吗 … …',Speaker=894002,}",
  [172245] = "{id=172245,Text='呵呵。倒也不失为明智的选择呢，毕竟我是这么厉害！',Speaker=894002,}",
  [172246] = "{id=172246,Text='哎哟，看来你倒是挺有勇气的嘛，那我们来吧！',Speaker=894002,}",
  [172247] = "{id=172247,Text='没错，我们再来再来！',Speaker=894002,}",
  [172248] = "{id=172248,Text='你来啦，冒险者！怎么样，昨天的礼物有开出幸运大奖吗？',Speaker=894002,Option='{没有…,0},{嗯嗯！,0}',}",
  [172249] = "{id=172249,Text='那么，我们再接再厉！——嘿， 只要你能赢，礼物就归你！',Speaker=894002,Option='{来吧,2},{考虑一下,3}',}",
  [172250] = "{id=172250,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=894001,}",
  [172251] = "{id=172251,Text='你知道吗，岁末大扫除可是每年必备的环节哦——将过往的霉运和晦气都扫走，迎接来年新气象！',Speaker=894001,}",
  [172252] = "{id=172252,Text='那么，拜托你一件事！请去清理附近的朽魔吧，让普隆德拉焕然一新！',Speaker=894001,Option='好',}",
  [172253] = "{id=172253,Text='你回来得好快呀！哇，朽魔退散了，我甚至能闻到新年的味道了！',Speaker=894001,}",
  [172254] = "{id=172254,Text='这是给你的礼物，请收下吧！',Speaker=894001,}",
  [172255] = "{id=172255,Text='可能还有别处需要清理，明天也请多多帮忙！',Speaker=894001,}",
  [172256] = "{id=172256,Text='今天要为依斯鲁得岛进行大扫除哦！那么，事不宜迟，我传送你过去吧！',Speaker=894001,Option='好',}",
  [172257] = "{id=172257,Text='今天要为吉芬进行大扫除哦！那么，事不宜迟，我传送你过去吧！',Speaker=894001,Option='好',}",
  [172258] = "{id=172258,Text='今天要为斐扬进行大扫除哦！那么，事不宜迟，我传送你过去吧！',Speaker=894001,Option='好',}",
  [172259] = "{id=172259,Text='今天要为姜饼城进行大扫除哦！那么，事不宜迟，我传送你过去吧！',Speaker=894001,Option='好',}",
  [172260] = "{id=172260,Text='今天要为朱诺进行大扫除哦！那么，事不宜迟，我传送你过去吧！',Speaker=894001,Option='好',}",
  [172261] = "{id=172261,Text='今天是最后一站，要为远方的洛阳进行大扫除哦！那么，事不宜迟，我传送你过去吧！',Speaker=894001,Option='好',}",
  [172262] = "{id=172262,Text='辛苦你了，冒险者！大扫除已经完成了噢，开始期待新年的到来吧~想想都很激动呢！',Speaker=894001,}",
  [172263] = "{id=172263,Text='吼吼~只要你的冒险还在持续，我们总有一天还会相遇。这是给你的礼物，请收下吧！',Speaker=894001,Emoji=17,}",
  [172273] = "{id=172273,Text='在这个特殊的日子里，希望天下有情人终成眷属~',Speaker=894003,}",
  [172274] = "{id=172274,Text='还没遇到有情之人也不要心急，我这里有一些[c][ffff00]爱恋之心[-][/c]，据说可以给人带来桃花哦~',Speaker=894003,}",
  [172275] = "{id=172275,Text='收集更多的[c][ffff00]爱恋之心[-][/c]可以在我这里兑换各类情人节专属外观哦~',Speaker=894003,}",
  [172276] = "{id=172276,Text='你刚从相依丫头那边过来吧~',Speaker=894004,Option='对',}",
  [172277] = "{id=172277,Text='让我看看，她竟然还送了你爱恋之心！这可是她的宝贝呢！',Speaker=894004,}",
  [172278] = "{id=172278,Text='那，我也不能小气了！这个特殊的头像框，请你一定要收下哦~',Speaker=894004,Option='谢谢',}",
  [172279] = "{id=172279,Text='最后，祝你节日快乐~',Speaker=894004,}",
  [172280] = "{id=172280,Text='你好，冒险者，我们又见面了！你看这星月同辉的时刻，就是我们重逢之时！',Speaker=894015,}",
  [172281] = "{id=172281,Text='嘿，你感受到月亮的魔力了吗？虽然平时也有些微弱的魔力，但是这几天魔力越来越强了！',Speaker=894015,Option='{有一点,0},{还不够强烈,0}',}",
  [172282] = "{id=172282,Text='那么，我建议你去寻找抱月兽吧——只有当月光笼罩整个米德加尔特大陆之时，珍贵的抱月兽才会出没！',Speaker=894015,}",
  [172283] = "{id=172283,Text='每个与抱月兽相逢的冒险者，都将收到一份特别的馈赠哦！',Speaker=894015,Option='要去哪里呢',}",
  [172284] = "{id=172284,Text='让我观测一下天象……今天应该在「发乐斯灯塔」噢！',Speaker=894015,}",
  [172285] = "{id=172285,Text='如果你准备好了，就让我帮你传送过去吧。',Speaker=894015,Option='出发吧',}",
  [172286] = "{id=172286,Text='（看来，你找到了抱月兽。但它害羞地跑掉了……）',}",
  [172287] = "{id=172287,Text='（但它留了一份特别的礼物给你……）',}",
  [172288] = "{id=172288,Text='怎么样？找到抱月兽，还挺有收获吧？事不宜迟，我们开始今天的冒险吧！',Speaker=894015,}",
  [172289] = "{id=172289,Text='让我观测一下天象……今天应该在「克魔岛」噢！',Speaker=894015,}",
  [172290] = "{id=172290,Text='让我观测一下天象……今天应该在「克雷斯特汉姆」噢！',Speaker=894015,}",
  [172291] = "{id=172291,Text='让我观测一下天象……今天应该在「哥布灵森林」噢！',Speaker=894015,}",
  [172292] = "{id=172292,Text='让我观测一下天象……今天应该在「雾之森林」噢！',Speaker=894015,}",
  [172293] = "{id=172293,Text='[c][ffff00][PlayerName][-][/c]，[c][ffff00][PlayerName][-][/c]，我在这里！快过来！',Speaker=894015,Option='{不要急,0},{怎么了？,0}',}",
  [172294] = "{id=172294,Text='没错，我又需要你的帮助了！当月光洒向大地的时候，那些暗藏的魔物也蠢蠢欲动……',Speaker=894015,}",
  [172295] = "{id=172295,Text='它们霸占了月光照耀的每一处海滩，每一处丛林，不允许任何人靠近……',Speaker=894015,Emoji=15,}",
  [172296] = "{id=172296,Text='所以，我想委托你，前往米德加尔特各地，清理这些自私的魔物！',Speaker=894015,Option='{它们在哪里,0},{它们长什么样,0}',}",
  [172297] = "{id=172297,Text='让我想想…它们手持一把大砍刀，猩红的眼睛想必一眼就能认出……每个天黑的地方，它都有可能出没……',Speaker=894015,}",
  [172298] = "{id=172298,Text='我知道了！那我们去「赫瓦格密尔」看看，越是人烟稀少的地方，越是存在危险！',Speaker=894015,}",
  [172299] = "{id=172299,Text='如果你准备好了，就让我帮你传送过去吧。',Speaker=894015,Option='出发吧',}",
  [172300] = "{id=172300,Text='可恶！果然有魔物出没……那么，明天也要记得来帮忙噢！',Speaker=894015,}",
  [172301] = "{id=172301,Text='[c][ffff00][PlayerName][-][/c]，不好了不好了，出大事了！',Speaker=894015,Option='怎么了？',}",
  [172302] = "{id=172302,Text='我感受到了远方的家乡出现了危机……难道它们动身前往了死者国度！',Speaker=894015,}",
  [172303] = "{id=172303,Text='事不宜迟！我们去雾之森林看看吧！',Speaker=894015,}",
  [172304] = "{id=172304,Text='太感谢了！如果不是你，不敢想象会发生什么……那么，明天也要记得来帮忙噢！',Speaker=894015,}",
  [172305] = "{id=172305,Text='你来啦，今天我们就去遥远的西方调查一下吧！',Speaker=894015,Option='西门吗？',}",
  [172306] = "{id=172306,Text='不不，更远一点，这次是要去位于罗扎纳的宝藏洞窟！',Speaker=894015,}",
  [172307] = "{id=172307,Text='你真是个可靠的伙伴，每天都是这么准时呢！',Speaker=894015,}",
  [172308] = "{id=172308,Text='昨天我收到了克魔岛村长的拜托——海滩上出现了一只凶恶的魔物，严重影响了克魔岛的旅游业……',Speaker=894015,}",
  [172309] = "{id=172309,Text='对！你没有猜错！一定是它！那么，今天的目标就是清理海滩上的魔物！',Speaker=894015,}",
  [172310] = "{id=172310,Text='看来，你越来越熟练了呢，魔物猎人！那么，我们明天再见吧！',Speaker=894015,}",
  [172311] = "{id=172311,Text='今天去哪里驱魔呢？让我想想……',Speaker=894015,}",
  [172312] = "{id=172312,Text='在此之前，我想先感谢你，因为伴随着这几天的努力，我总感觉星月之力更加充盈了~',Speaker=894015,}",
  [172313] = "{id=172313,Text='嗯嗯，不要磨蹭了，我们出发前往最后一站吧——海底洞穴！',Speaker=894015,}",
  [172314] = "{id=172314,Text='好啦，你的委托就到这里告一段落了……愿这份礼物，你会喜欢。',Speaker=894015,}",
  [172316] = "{id=172316,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894015,}",
  [172317] = "{id=172317,Text='星月交辉期间，我将把最诚挚的祝福想要送给你，记得每天前来领取噢！',Speaker=894015,Emoji=2,}",
  [172318] = "{id=172318,Text='月夜下伟岸的守护者啊，米德加尔特因为有你而星光熠熠！\n愿星之力为你驱散迷雾，愿月之力为你指引方向。',Speaker=894015,Option='谢谢',}",
  [172319] = "{id=172319,Text='今天是一个特殊的日子，我将代表月亮赠予你一份特别的礼物哦！快收下吧！',Speaker=894015,Option='哇！',}",
  [172324] = "{id=172324,Text='冒险者，你见过满天星河吗？',Speaker=894016,}",
  [172325] = "{id=172325,Text='我能凭借占星之术揭晓今日的运势，还可以根据揭晓的运势赐予你不同的能力加成哦~',Speaker=894016,}",
  [172326] = "{id=172326,Text='但是因为魔力的限制，我每天只能施展一次哦~',Speaker=894016,}",
  [172327] = "{id=172327,Text='还差一点点，月之桥就能完成修缮了！要是材料再多一些就好了……',Speaker=894017,}",
  [172328] = "{id=172328,Text='所有路过的冒险者都会在月之桥上驻足，为这美景所折服！',Speaker=894017,}",
  [172329] = "{id=172329,Text='嘿，你！善良的冒险者，能再给我一点多余的材料吗？一起来完成月之桥的修缮吧！',Speaker=894017,Option='好',}",
  [172330] = "{id=172330,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=805180,}",
  [172331] = "{id=172331,Text='祭典期间，艾卡拉奇可是有不少音乐精灵在默默演出哦！不妨给他们合影留念吧，毕竟晨曦祭典可是一年一次呢！',Speaker=805180,}",
  [172332] = "{id=172332,Text='怎么样！有想到什么嘛，带上你的相机，去记录一下吧~',Speaker=805180,Option='好，我去找找',}",
  [172333] = "{id=172333,Text='你回来啦！怎么样，有什么收获吗？',Speaker=805180,}",
  [172334] = "{id=172334,Text='连我都想不到会有这番景象呢，不亏是心思缜密的冒险者呢。',Speaker=805180,}",
  [172335] = "{id=172335,Text='请收下这个，谢谢能与我分享这份美好。',Speaker=805180,}",
  [172336] = "{id=172336,Text='[c][ffff00][PlayerName][-][/c]，你来啦！今天的拍摄也拜托你了！',Speaker=805180,}",
  [172337] = "{id=172337,Text='又到了一年一度的晨曦祭典，真让人激动。',Speaker=828255,}",
  [172338] = "{id=172338,Text='浮空精灵已经把要给你的祝福全都写进了这张明信片里面，快打开看看吧~',Speaker=828255,}",
  [172339] = "{id=172339,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=828278,}",
  [172340] = "{id=172340,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=828278,}",
  [172341] = "{id=172341,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【-Naoyuki-】[-][/c]绘制的共创头像！',Speaker=828278,Option='哇！',}",
  [172342] = "{id=172342,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=828278,Option='谢谢',}",
  [172343] = "{id=172343,Text='每年的晨曦祭典，艾卡拉奇总会有捣乱的魔物入侵，觊觎着祭典的永恒之花……',Speaker=805195,}",
  [172344] = "{id=172344,Text='但凭借我们浮空精灵的人力远远不够抗衡……',Speaker=805195,}",
  [172345] = "{id=172345,Text='冒险者，你愿意与我一起消灭魔物，保卫晨曦祭典吗？',Speaker=805195,Option='没问题',}",
  [172346] = "{id=172346,Text='哈哈哈哈，看到他们落荒而逃的样子，真是让人开心呢~',Speaker=805195,}",
  [172347] = "{id=172347,Text='5.1~6.1期间，Base等级≥25级的冒险者可以前往普隆德拉广场，找到「娜拉·海拉尔」即可参与活动',Speaker=828278,}",
  [172348] = "{id=172348,Text='共4个连续的任务，每完成一个任务，可以获得Zeny x 300,000，「永恒花瓣」x5；完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=828278,}",
  [172349] = "{id=172349,Text='5.6~6.1期间，Base等级≥25级的冒险者可以前往艾卡拉奇，找到「赫蒂」即可参与活动',Speaker=828278,}",
  [172350] = "{id=172350,Text='共5个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [172351] = "{id=172351,Text='5.13~6.1期间，Base等级≥25级的冒险者可以前往艾卡拉奇，找到「赫蒂」即可参与活动',Speaker=828278,}",
  [172352] = "{id=172352,Text='共7个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [172353] = "{id=172353,Text='5.20~6.1期间，Base等级≥25级的冒险者可以前往时间花园，找到「密米尔」即可参与活动',Speaker=828278,}",
  [172354] = "{id=172354,Text='共5个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [172355] = "{id=172355,Text='聆听精灵们的旋律，那是这个世界最美的律动！拿出照相机记录下这美好的一刻吧！',Speaker=828278,}",
  [172356] = "{id=172356,Text='5.27~6.1期间，Base等级≥25级的冒险者前往艾卡拉奇，找到「赛尔妲·海拉尔」即可参与活动',Speaker=828278,}",
  [172357] = "{id=172357,Text='共5个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [172358] = "{id=172358,Text='每周六、周日16点 ~ 24点，「愤怒的蜂后」将带领魔物破坏祭典！',Speaker=828278,}",
  [172361] = "{id=172361,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893598,}",
  [172362] = "{id=172362,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=893598,}",
  [172363] = "{id=172363,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【Yoratetetsu】[-][/c]绘制的共创头像！',Speaker=893598,Option='哇！',}",
  [172364] = "{id=172364,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=893598,Option='谢谢',}",
  [172365] = "{id=172365,Text='六月是属于梦幻的日子！每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！',Speaker=893598,}",
  [172366] = "{id=172366,Text='[c][ffff00][PlayerName][-][/c]，别愣着哦~快收下啦~',Speaker=893598,Option='打开',}",
  [172367] = "{id=172367,Text='[c][ffff00][PlayerName][-][/c]，你知道皮纳塔吗？',Speaker=822045,}",
  [172368] = "{id=172368,Text='每周六、周日晚16~24点，将在梦幻城刷新大量皮纳塔魔物哦！',Speaker=822045,}",
  [172369] = "{id=172369,Text='击败皮纳塔魔物，他们会掉落礼物哦~这是梦幻城送给各位冒险者们的礼物哦~千万不要错啦~',Speaker=822045,Option='知道了',}",
  [172370] = "{id=172370,Text='哈哈哈哈，看来你已经收到了梦幻城的礼物！这是额外的奖励哦~',Speaker=822045,}",
  [172371] = "{id=172371,Text='6/1~7/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]拉拉贝尔[-][/c]接取系列任务~',Speaker=893689,}",
  [172372] = "{id=172372,Text='共4个连续的任务：\n完成第1~4个任务，可获得Zeny x 300,000、「梦幻甜心」x5\n完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=893689,}",
  [172373] = "{id=172373,Text='6/10~7/1期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]思丽卡[-][/c]接取系列任务~',Speaker=893689,}",
  [172374] = "{id=172374,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「梦幻甜心」x1\n完成第5个任务，可以获得Zeny x 300,000、「梦幻甜心」x3',Speaker=893689,}",
  [172375] = "{id=172375,Text='6/17~7/1期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]阿利斯塔[-][/c]接取系列任务~',Speaker=893689,}",
  [172376] = "{id=172376,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「梦幻甜心」x1\n完成第5个任务，可以获得Zeny x 300,000、「梦幻甜心」x3',Speaker=893689,}",
  [172377] = "{id=172377,Text='完成所有「吃不完的糖果」后，可以解锁「爱的棒棒糖」任务，每天品尝1次棒棒糖，可以获得「梦幻甜心」x1',Speaker=893689,}",
  [172378] = "{id=172378,Text='6/24~7/1期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]琉利斯[-][/c]接取系列任务~',Speaker=893689,}",
  [172379] = "{id=172379,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「梦幻甜心」x1\n完成第5个任务，可以获得Zeny x 300,000、「梦幻甜心」x3',Speaker=893689,}",
  [172380] = "{id=172380,Text='活动期间，每周六、日晚16点~24点，梦幻城将会出现大量「皮纳塔魔物」，击败「皮纳塔魔物」可以获得「附魔礼盒」、「符文礼盒」等奖励',Speaker=893689,}",
  [172381] = "{id=172381,Text='击败「拉链熊皮纳塔」还有概率获得「梦幻礼盒·MVP」，不要错过哟~',Speaker=893689,}",
  [172384] = "{id=172384,Text='水是万物之源，冒险者，你能帮我去收集几种不同的水吗？',Speaker=894019,Option='没问题',}",
  [172385] = "{id=172385,Text='第1种需要收集的水，叫「初心之水」，在南门就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172386] = "{id=172386,Text='收集到了之后，记得回来找我领取奖励哦~',Speaker=894019,}",
  [172387] = "{id=172387,Text='想不到那么快就收集到了，谢谢你冒险者，这是你的礼物，请收下~',Speaker=894019,}",
  [172388] = "{id=172388,Text='第3种需要收集的水，叫「生命之水」，在苏克特沙漠就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172389] = "{id=172389,Text='第4种需要收集的水，叫「纯净之水」，在姜饼城就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172390] = "{id=172390,Text='第5种需要收集的水，叫「忠贞之水」，在天津町就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172391] = "{id=172391,Text='第6种需要收集的水，叫「狂欢之水」，在克魔岛就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172392] = "{id=172392,Text='第7种需要收集的水，叫「天然之水」，在乌龟岛就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172393] = "{id=172393,Text='第2种需要收集的水，叫「希望之水」，在落日浅谈就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894019,}",
  [172394] = "{id=172394,Text='感谢你这几天来帮我把所有的水都收集齐了，这是你的礼物，请收下~',Speaker=894019,}",
  [172397] = "{id=172397,Text='[c][ffff00][PlayerName][-][/c]，你知道[c][ffff00]丛林之力[-][/c]吗？',Speaker=894023,Option='那是什么？',}",
  [172398] = "{id=172398,Text='[c][ffff00]丛林之力[-][/c]是远古森林中沉淀下来的力量，据说收集到一定的数量，就可以召唤[c][ffff00]丛林之息[-][/c]！',Speaker=894023,Option='丛林之息？',}",
  [172399] = "{id=172399,Text='没错！[c][ffff00]丛林之息[-][/c]是森林中的王者，拥有矫健的体格以及闪电般的速度，我愿称它为“米德加尔特最快”！',Speaker=894023,Option='那么厉害？',}",
  [172400] = "{id=172400,Text='那是必须的！这样吧，我这刚好有一些[c][ffff00]丛林之力[-][/c]，我把它送给你，等你拥有更多了，记得去召唤[c][ffff00]丛林之息[-][/c]！',Speaker=894023,}",
  [172401] = "{id=172401,Text='嘿，冒险者，你也是被[c][ffff00]丛林之息[-][/c]吸引过来的吗？',Speaker=894022,}",
  [172402] = "{id=172402,Text='虽然我的力量不足以帮你召唤一只完整的[c][ffff00]丛林之息[-][/c]，但是我可以把我这只借给你感受一下哦~',Speaker=894022,}",
  [172403] = "{id=172403,Text='骑着它的时候可千万要抓紧啦~不然摔下来可是很疼的哟~',Speaker=894022,}",
  [172404] = "{id=172404,Text='对了，5/1 5:00~5/3 5:00期间，商城的「丛林之息坐骑礼盒」限时折扣哦~这是获取「丛林之力」的[c][ffff00]最佳时机[-][/c]！',Speaker=894022,}",
  [172407] = "{id=172407,Text='我真粗心！',Speaker=894025,Option='怎么了？',}",
  [172408] = "{id=172408,Text='我原本是一名音乐家，与我的音符们一起游历四方创作歌曲，但是就在最近，当我游历到这儿的时候，我竟然把我的音符们给弄丢了！这就意味着我再也无法创作歌曲了！',Speaker=894025,}",
  [172409] = "{id=172409,Text='这些音符可是我最宝贵的东西了~！如果你可以帮我把它们找到，我一定为你量身打造一首金曲！',Speaker=894025,Option='就这么定了！',}",
  [172410] = "{id=172410,Text='那么让我想想，我最近都去了哪里，唔~好像是一个叫做「普隆德拉北部」的地方，要不你去看看吧？',Speaker=894025,}",
  [172411] = "{id=172411,Text='这看起来就是布鲁斯遗失的音符，赶快带回去给他吧~！',}",
  [172412] = "{id=172412,Text='我就知道你肯定能找到的！这个音符叫“Do”，你看，属于它的音乐精灵已经迫不及待出现了~去听听它的声音吧~',Speaker=894025,}",
  [172414] = "{id=172414,Text='怎么样？是不是很动听？还剩6个音符没有找到，明天我还在这里等你哦~',Speaker=894025,}",
  [172415] = "{id=172415,Text='离开北门之后，我又去了「银鱼湖底」，那是一个美丽的地方，如果我没有遗失我的音符，我觉得那里会更美~！',Speaker=894025,}",
  [172416] = "{id=172416,Text='今天找到的是“Re”呢，你看，属于它的音乐精灵已经迫不及待出现了~去听听看它的声音吧~',Speaker=894025,}",
  [172418] = "{id=172418,Text='那就还剩5个音符没有找到，明天我还在这里等你哦~',Speaker=894025,}",
  [172419] = "{id=172419,Text='接着，我在「克特森林深处」看到一只性感的猫，当时我在演奏，她在跳舞，她的尾巴扭动得比她的腰还灵活！',Speaker=894025,}",
  [172420] = "{id=172420,Text='哦，天呐，我是在回忆些什么！我竟然记住了一只大猫跳舞，缺弄丢了我的音符！',Speaker=894025,}",
  [172421] = "{id=172421,Text='原来是“Mi”，难怪那只猫咪那么激动~你也去听听“Mi”的声音吧~',Speaker=894025,}",
  [172423] = "{id=172423,Text='谢谢你，只剩4个音符了，我感觉我们已经胜利了一半了！',Speaker=894025,}",
  [172424] = "{id=172424,Text='接着，我去了「古城远郊」，那儿住着一个兽人，虽然长得很丑，但是唱歌还挺好听，我弹着琴，他唱着歌，我们就这样度过了美好的一天~',Speaker=894205,}",
  [172425] = "{id=172425,Text='今天回来的是“Fa”，据说听了这个声音的人都可以发财哦~你快去听听看吧~',Speaker=894205,}",
  [172427] = "{id=172427,Text='一个好消息！我刚才在整理音符的时候发现，原来“Sol”和“La”这两个音符一直就在我身上，那就只剩最后一个“Si”了，我明天还在这里等你哦~',Speaker=894025,}",
  [172428] = "{id=172428,Text='对了，“Sol”和“La”也已经在那边了，你有空的时候可以去听一下哦~',Speaker=894025,}",
  [172429] = "{id=172429,Text='最后，我去了「钟楼地下」，我看到一个巨大的球，他长着数不清的眼睛，我想……',Speaker=894025,Option='想什么？',}",
  [172430] = "{id=172430,Text='我的想法有点可怕，你真的要听吗？',Speaker=894025,Option='说吧',}",
  [172431] = "{id=172431,Text='我想要是把他的眼睛都换成五颜六色的灯，那把这个球挂在家里，一定很动感！',Speaker=894025,}",
  [172432] = "{id=172432,Text='我的音符终于都齐了！谢谢你，冒险者~之前答应的为你量身打造的金曲也已经写好了！',Speaker=894025,Option='这么快？',}",
  [172433] = "{id=172433,Text='Do Do Sol Sol La La Sol~就是这个旋律，你可以自己去找音乐精灵，把这段只属于你的旋律演奏出来哦~',Speaker=894025,}",
  [172434] = "{id=172434,Text='为了感谢你帮我找回了音符，我为你准备了一些奖励哦~但是这些奖励也不是白拿的，只有回答正确我的问题才可以获得哦~',Speaker=894025,}",
  [172435] = "{id=172435,Text='那么接下来就来进行考验吧！',Speaker=894025,Option='{开始,0}{稍等,2}',}",
  [172436] = "{id=172436,Text='回答的太糟糕了！',Speaker=894025,Emoji=12,}",
  [172437] = "{id=172437,Text='回答的很好！我这关算你通过了！',Speaker=894025,}",
  [172438] = "{id=172438,Text='经验丰富的冒险者果然是无所不知~',Speaker=894025,}",
  [172439] = "{id=172439,Text='这是对你的认可，请收下吧！悄悄告诉你，这次只是开胃菜，接下来还有更难的考验等着你哦~',Speaker=894025,}",
  [172440] = "{id=172440,Text='今天的题目有些难哦，你准备好了吗？',Speaker=894025,Option='{开始,0}{稍等,2}',}",
  [172441] = "{id=172441,Text='不得不承认，你知识储备很渊博，我得去认真准备些题目了~',Speaker=894025,}",
  [172442] = "{id=172442,Text='终于到了这最后一题了~！你准备好了吗？',Speaker=894025,Option='{开始,0}{稍等,2}',}",
  [172443] = "{id=172443,Text='这奖励是你应得的！',Speaker=894025,}",
  [172445] = "{id=172445,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894025,}",
  [172446] = "{id=172446,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894025,}",
  [172447] = "{id=172447,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【Kikansha】[-][/c]绘制的共创头像！',Speaker=894025,Option='哇！',}",
  [172448] = "{id=172448,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894025,Option='谢谢',}",
  [172449] = "{id=172449,Text='波利粉乐队即将开始全米德加尔特大陆巡演了哦~为了感谢你的付出，特意准备了一些小礼物给你哦~',Speaker=894033,}",
  [172450] = "{id=172450,Text='当然，吉米也想在巡演的时候想起你，所以，你也可以给我一些专属于你的东西吗？',Speaker=894033,}",
  [172451] = "{id=172451,Text='7/1~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]布鲁斯[-][/c]即可领取到本月玩家共创的头像哦~',Speaker=894024,}",
  [172452] = "{id=172452,Text='7/1~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]费雷德[-][/c]即可接取任务',Speaker=894024,}",
  [172453] = "{id=172453,Text='共7个连续的任务，完成每个任务可以获得Zeny x 300,000、「电音节奏」x5；完成第5个任务，可以额外获得「B格猫福利券·宴」x30；完成第7个任务，可以额外获得「奋进礼盒」x1',Speaker=894024,}",
  [172454] = "{id=172454,Text='7/8~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]布鲁斯[-][/c]即可接取任务',Speaker=894024,}",
  [172455] = "{id=172455,Text='共5个连续的任务，完成每个任务可以获得Zeny x 150,000、「电音节奏」x1；完成第5个任务，可以额外获得「奋进礼盒」x1',Speaker=894024,}",
  [172456] = "{id=172456,Text='7/15~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]布鲁斯[-][/c]即可接取任务',Speaker=894024,}",
  [172457] = "{id=172457,Text='共7个连续的任务，完成每个任务可以获得Zeny x 150,000、「电音节奏」x1；完成第7个任务，可以额外获得「奋进礼盒」x1',Speaker=894024,}",
  [172458] = "{id=172458,Text='7/22~7/29期间，冒险者前往普隆德拉找到[c][ffff00]吉米[-][/c]即可与它交换礼盒~',Speaker=894024,}",
  [172459] = "{id=172459,Text='每次交换礼盒可以获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=894024,}",
  [172460] = "{id=172460,Text='每个帐号每天只能交换2次哦~',Speaker=894024,}",
  [172461] = "{id=172461,Text='「暴走的魔鬼大乌贼」带领魔物入侵克魔岛，冒险者快拿起武器保卫克魔岛吧！',Speaker=894024,}",
  [172462] = "{id=172462,Text='7/1~7/31期间，每周六、周日将刷新大量暴走的魔物，击败即有概率获得精美时装、符文等奖励哦~',Speaker=894024,}",
  [172463] = "{id=172463,Text='克魔岛出现了大量入侵的魔物，我们的家园需要你来守护！',Speaker=822145,}",
  [172464] = "{id=172464,Text='每周六、周日，将在克魔岛刷新大量暴走的魔物！',Speaker=822145,}",
  [172465] = "{id=172465,Text='只要击杀1个暴走的啤酒企鹅，我将赠予你稀有的「电音节奏」！',Speaker=822145,Option='我知道了！',}",
  [172466] = "{id=172466,Text='让我看看你的击杀记录……唔，不亏是你，远远超过了要求的数量！这是你的报酬！',Speaker=822145,}",
  [172477] = "{id=172477,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894034,}",
  [172478] = "{id=172478,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894034,}",
  [172479] = "{id=172479,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【Kikansha】[-][/c]绘制的共创头像！',Speaker=894034,Option='哇！',}",
  [172480] = "{id=172480,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894034,Option='谢谢',}",
  [172481] = "{id=172481,Text='HI~[c][ffff00][PlayerName][-][/c]，你也看到这场盛大的流星雨了吗！',Speaker=891839,Option='当然',}",
  [172482] = "{id=172482,Text='每天晚上18点至次日5点，随着流星雨的降临，天津町将会刷新大量的流星碎片，如果你能帮我收集到5个流星碎片的话，我有特殊的奖励给你哦~',Speaker=891839,}",
  [172483] = "{id=172483,Text='确定要提交5个流星碎片吗？',Speaker=891839,Option='{确定,4},{再想想,3}',}",
  [172484] = "{id=172484,Text='看起来你还没有准备好哦~每天晚上18点至次日5点，随着流星雨的降临，天津町将会刷新大量的流星碎片哦~',Speaker=891839,}",
  [172485] = "{id=172485,Text='这是我特意为你准备的礼物~！',Speaker=891839,}",
  [172486] = "{id=172486,Text='天津町的花嫁节是爱人们互相拥抱，许下一生誓言的盛大节日，快带上你心爱的他/她，一起来参加花嫁盛典吧~',Speaker=894034,}",
  [172487] = "{id=172487,Text='8/1~9/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「樱花女巫」即可参与活动',Speaker=894034,}",
  [172488] = "{id=172488,Text='共4个连续的任务，完成1-3个任务，均可获得Zeny x 150,000，「真爱之心」x1、「B格猫福利券·宴」x10；完成第4个任务，可以获得奖励Zeny x 300,000，「真爱之心」x3、「奋进礼盒」x1',Speaker=894034,}",
  [172489] = "{id=172489,Text='活动期间，任务会按服务器时间连续4天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894034,}",
  [172490] = "{id=172490,Text='该活动为帐号限定类任务哦~',Speaker=894034,}",
  [172491] = "{id=172491,Text='一年一度的花嫁节正好赶上一年一度最大的流星雨，在这浪漫的氛围下，去拥抱你最重要的那个人吧~',Speaker=894034,}",
  [172492] = "{id=172492,Text='8/8~8/14的每晚18点至次日早晨5点，天津町将出现整晚的流星雨',Speaker=894034,}",
  [172493] = "{id=172493,Text='在流星雨持续期间，天津町将出现大量的「流星碎片」，冒险者可以前往拾取，每次拾取可以获得「流星碎片」x1、Zeny x 150,000、「福利券自选礼包」x1',Speaker=894034,}",
  [172494] = "{id=172494,Text='8/15日之前，Base等级≥25级的冒险者，收集5个「流星碎片」提交给「流星指引大使」可以获得「流星自选礼盒」x1，打开之后可以自选「逐星闪烁」x1或者「莫拉金币」x10',Speaker=894034,}",
  [172495] = "{id=172495,Text='花嫁节即将开始，然而樱花树却日渐枯萎，原来是维持樱花绽放的甜蜜誓约被偷走啦，快想办法在大陆各处收集甜蜜誓约，让樱花重现活力，保证花嫁节的如期举行吧！',Speaker=894034,}",
  [172496] = "{id=172496,Text='8/15~9/1期间，Base等级大于等于25级的冒险者前往天津町，找到「奇噜噜」即可参与活动',Speaker=894034,}",
  [172497] = "{id=172497,Text='共5个连续的任务，冒险者根据提示前往指定地点击杀指定魔物。完成第1--4个任务，均可获得Zeny x 150,000，「真爱之心」x1；完成第5个任务，可以获得Zeny x 300,000，「真爱之心」x3、「奋进礼盒」x1',Speaker=894034,}",
  [172498] = "{id=172498,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894034,}",
  [172499] = "{id=172499,Text='该活动为帐号限定类任务哦~',Speaker=894034,}",
  [172500] = "{id=172500,Text='展示甜蜜恋爱也是花嫁节的传统，希望照相机能记录下你们相伴的身影',Speaker=894034,}",
  [172501] = "{id=172501,Text='8/22~9/1期间，Base等级大于等于25级的冒险者前往天津町，找到「粉唧唧」即可参与活动',Speaker=894034,}",
  [172502] = "{id=172502,Text='共5个连续的任务，冒险者根据提示前往指定约会圣地进行拍照。完成第1--4个任务，均可获得Zeny x 150,000，「真爱之心」x1；完成第5个任务，可以获得Zeny x 300,000，「真爱之心」x3、「奋进礼盒」x1',Speaker=894034,}",
  [172503] = "{id=172503,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894034,}",
  [172504] = "{id=172504,Text='该活动为帐号限定类任务哦~',Speaker=894034,}",
  [172505] = "{id=172505,Text='花嫁节恰逢洛阳的七夕，许仙陷入莫名的惆怅中，就在这时他在桥上见到了一个熟悉的背影，正是梦中那人，但一群可恶的魔物却阻挡着他靠近的脚步。',Speaker=894034,}",
  [172506] = "{id=172506,Text='每周六、周日16：00-24：00，大批魔物将入侵天津町，冒险者需要拿起手中的武器，保护许仙靠近他的梦中人，让有情人终成眷属！',Speaker=894034,}",
  [172507] = "{id=172507,Text='击败入侵的魔物，可以获得大量「萤火之约」，「萤火之约」为货币类道具，可以兑换「许仙的谢礼」，打开「许仙的谢礼」有概率获得往期外观「偷心魅魔头饰兑换券」、「偷心魅魔时装自选券」',Speaker=894034,}",
  [172508] = "{id=172508,Text='通过击败魔物获得的「萤火之约」，每日帐号上限为1000',Speaker=894034,}",
  [172513] = "{id=172513,Text='呼~听说睡太久会让人神志不清，看来这是真的，我这一睡就是几百年，猛地醒过来，都不记得我为什么要来这里了……',Speaker=894037,}",
  [172514] = "{id=172514,Text='依稀记得有人指责我、有人感激我，可是那些事都过去太久太久了，[c][ffff00][PlayerName][-][/c]，你能帮我回忆回忆吗？',Speaker=894037,Option='没问题',}",
  [172515] = "{id=172515,Text='先去那个年代久远的村子里看看吧，印象中那里的人都对我不大友善，你也要小心哦~',Speaker=894037,}",
  [172516] = "{id=172516,Text='什么广寒仙子，我可没听过，要不你可以去问问星痕，她可是我们这最有名的占卜师，就算她不知道，肯定也能给你占卜出来',Speaker=2542,}",
  [172517] = "{id=172517,Text='广寒仙子？只是一个自私又薄情的人罢了，她配叫什么仙子！',Speaker=2541,}",
  [172518] = "{id=172518,Text='为什么这么说？',}",
  [172519] = "{id=172519,Text='她是从隔壁村子下嫁到斐扬的，虽然男方家境不怎么样，但是可没让她受过苦，脏活累活都是男人干，她就只要在家里享福就行。',Speaker=2541,}",
  [172520] = "{id=172520,Text='可是就算这样，她仍然不知足，有一天，她丈夫不知道从哪里求来了一粒仙丹，据说吃了这粒仙丹，就可以成仙，从此不老不死。',Speaker=2541,}",
  [172521] = "{id=172521,Text='原本他们商量好是一人一半分了吃，这样虽然成不了仙，但是两人都可以延年益寿，可是这个女人，却趁丈夫出门干活的时候把这粒仙丹整粒吞了！',Speaker=2541,}",
  [172522] = "{id=172522,Text='后来，她就飞升去了月亮上，她的丈夫思念成疾，没过多久就去世了。你说这样的女人她配叫仙子吗，只不过是一个薄情寡义的小偷！',Speaker=2541,}",
  [172523] = "{id=172523,Text='原来是这样吗？',Speaker=894037,}",
  [172524] = "{id=172524,Text='那我以前真不是个人，竟然做出了这样的事情，他们骂我、指责我也都是我咎由自取。',Speaker=894037,}",
  [172525] = "{id=172525,Text='只是，我记得完整的故事并不是这样的，算了，今天我也累了，明天我还在这里等你，剩下的事情，我们明天再说吧。',Speaker=894037,}",
  [172526] = "{id=172526,Text='[c][ffff00][PlayerName][-][/c]你来得真是时候，我刚睡醒，但凡你来早一步，你还得在边上等我睡醒呢~',Speaker=894037,}",
  [172527] = "{id=172527,Text='我有一只小兔子，我刚到月亮上就收留了它，可能它能知道些什么吧。',Speaker=894037,}",
  [172528] = "{id=172528,Text='小……小兔子？',}",
  [172529] = "{id=172529,Text='咕咕咕，你身上有仙子的味道，兔兔很喜欢~',Speaker=894040,}",
  [172530] = "{id=172530,Text='（你将星痕说的话转告给了小兔子）',}",
  [172531] = "{id=172531,Text='咕！她喵喵喵喵喵，仙子不是这样的人！',Speaker=894040,}",
  [172532] = "{id=172532,Text='等等，虽然这件事很重要，但是你为什么会发出喵喵喵的猫叫声？',}",
  [172533] = "{id=172533,Text='我喵喵喵的！气死了，为什么在这块大陆，只要一说脏话就会变成“喵”呢！',Speaker=894040,}",
  [172534] = "{id=172534,Text='原来如此，我们继续来说说仙子的事情吧。',}",
  [172535] = "{id=172535,Text='仙子是全天下最善良的人，她怎么可能做那样的事情！',Speaker=894040,Option='展开说说',}",
  [172536] = "{id=172536,Text='兔兔本来在月亮上风餐露宿的，仙子来了之后，兔兔再也不用担心吃不饱了！',Speaker=894040,Option='……',}",
  [172537] = "{id=172537,Text='你看，仙子对我这样的一只兔子都那么好，怎么可能会去辜负一个相爱的人呢？',Speaker=894040,}",
  [172538] = "{id=172538,Text='（感觉这个兔子也说不出什么东西，还是回去找仙子吧）',}",
  [172539] = "{id=172539,Text='是我考虑不周了，兔子是我到了月亮之后才收养的，又怎么会清楚我在人间做了什么。',Speaker=894037,}",
  [172540] = "{id=172540,Text='让我想想还能去问谁吧，你明天再来找我吧，我需要好好想想',Speaker=894037,}",
  [172541] = "{id=172541,Text='或许你可以去找梦罗克的考古学家问问，虽然不能保证他能知道，但是总得去尝试一下。',Speaker=894037,}",
  [172542] = "{id=172542,Text='你说广寒仙子？那可是一个了不起的人啊！',Speaker=3013,}",
  [172543] = "{id=172543,Text='咦？我听说的版本是……',}",
  [172544] = "{id=172544,Text='这些都是不明真相的人瞎传罢了，一开始我也觉得他们说的是对的，直到某一天，我发现了一座雕像，雕像上正是飞升的仙子，雕像下立了块石碑，记载了她传奇的一生……',Speaker=3013,}",
  [172545] = "{id=172545,Text='石碑上说：恐怖的大王从天而降，致使梦罗克大王为之复活，统治四方。为了阻止恐怖大王降临，必须要有人前往恐怖大王的封印之地——也就是月球，日复一日地加强封印。',Speaker=3013,}",
  [172546] = "{id=172546,Text='广寒仙子为了守护这片大陆，狠心放下她的小家，义无反顾地去了月亮之上！',Speaker=3013,}",
  [172547] = "{id=172547,Text='好像当时的情况，确实是这样的，但是都过去那么久了，又有谁在乎呢~',Speaker=894037,}",
  [172548] = "{id=172548,Text='好了，我觉得有些累了，剩下的事情，我们明天再聊吧~',Speaker=894037,}",
  [172549] = "{id=172549,Text='所以，关于我的故事，你已经都知道了，你觉得我是一个什么样的人？',Speaker=894037,Option='了不起的人',}",
  [172550] = "{id=172550,Text='伟大吗？可能也谈不上吧，我只是有想要保护的人罢了，我的出发点其实也是狭隘的，我只是想保护他，没想到在保护他的同时，保护了那么多人。',Speaker=894037,}",
  [172551] = "{id=172551,Text='可是还有那么多误会你的人啊！',}",
  [172552] = "{id=172552,Text='那就让他们继续误会下去吧，对于我来说，他们都是些无关紧要的人，他们是怎么想的，要怎么说，都不重要。',Speaker=894037,}",
  [172553] = "{id=172553,Text='好了，谢谢你陪我回忆起了这些事情，希望你也能和我一样，坚持初心，不要被外界的声音影响。',Speaker=894037,}",
  [172554] = "{id=172554,Text='如果有缘，我们还会再见。',Speaker=894037,}",
  [172555] = "{id=172555,Text='[c][ffff00][PlayerName][-][/c]，那么久没见，我以前教你的知识你还记得吗？',Speaker=894038,Option='当然',}",
  [172556] = "{id=172556,Text='那么接下来就来进行考验吧！',Speaker=894038,Option='{开始,0}{稍等,2}',}",
  [172557] = "{id=172557,Text='回答的太糟糕了！',Speaker=894038,Emoji=12,}",
  [172558] = "{id=172558,Text='回答的很好！我这关算你通过了！',Speaker=894038,}",
  [172559] = "{id=172559,Text='经验丰富的冒险者果然是无所不知~',Speaker=894038,}",
  [172560] = "{id=172560,Text='这是对你的认可，请收下吧！悄悄告诉你，这次只是开胃菜，接下来还有更难的考验等着你哦~',Speaker=894038,}",
  [172561] = "{id=172561,Text='你昨天表现得非常优秀，那么就开始今天的考验吧~！',Speaker=894038,Option='{开始,0}{稍等,2}',}",
  [172562] = "{id=172562,Text='今天是最后一场校验了哦~准备好了我们就开始吧~',Speaker=894038,Option='{开始,0}{稍等,2}',}",
  [172563] = "{id=172563,Text='你也知道了，月亮上封印着一个恶魔，虽然他的身体已经被封印了，但是却封印不住他强大的魔气。',Speaker=894037,}",
  [172564] = "{id=172564,Text='这些充盈着残暴之力的魔气会使原本的魔物更加凶残，而每当月圆，就是魔气最盛的时候。',Speaker=894037,}",
  [172565] = "{id=172565,Text='接下来就不用我多说了吧，先去首都下水道2F看看吧，你在那里应该会有所收获~',Speaker=894037,}",
  [172566] = "{id=172566,Text='真不错~明天还会有更具挑战的任务等着你~',Speaker=894037,}",
  [172567] = "{id=172567,Text='今天去蚂蚁地狱看看吧，我在那感受到了一丝威胁',Speaker=894037,}",
  [172568] = "{id=172568,Text='这次的目标出现在吉芬地下2F，他是一名了不起的对手！',Speaker=894037,}",
  [172569] = "{id=172569,Text='去古城地下墓地看看吧，希望你能平安回来~',Speaker=894037,}",
  [172570] = "{id=172570,Text='差不多了，今天就是最后一击了，去玩具工厂2F看看吧，那么美好的地方，可不能被破坏了~',Speaker=894037,}",
  [172571] = "{id=172571,Text='谢谢你，冒险者~！',Speaker=894037,}",
  [172573] = "{id=172573,Text='仙子为了感谢你，可是把压箱底的礼物都拿出来了哦~',Speaker=894041,}",
  [172574] = "{id=172574,Text='记得每天来找我交换礼物哦~每天都可以交换2次哦~',Speaker=894041,}",
  [172575] = "{id=172575,Text='那些原先无害的魔物，因为大魔王的影响，现在也变得开始暴动',Speaker=894042,}",
  [172576] = "{id=172576,Text='如果你可以击败一只暴动的小月兽，我可以给你一些奖励',Speaker=894042,}",
  [172577] = "{id=172577,Text='这是你应得的，请收好~',Speaker=894042,}",
  [172579] = "{id=172579,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894039,}",
  [172580] = "{id=172580,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894039,}",
  [172581] = "{id=172581,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【ᐯ 卂 し】[-][/c]绘制的共创头像！',Speaker=894039,Option='哇！',}",
  [172582] = "{id=172582,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894039,Option='谢谢',}",
  [172583] = "{id=172583,Text='传说在月圆之夜，在普隆德拉就会出现一个神秘的女人，她年复一年的在等待这一个有缘人，来聆听关于她的故事……',Speaker=894039,}",
  [172584] = "{id=172584,Text='9/1~10/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「广寒仙子」即可参与活动',Speaker=894039,}",
  [172585] = "{id=172585,Text='共4个连续的任务，完成1-3个任务，均可获得Zeny x 150,000，「月相之力」x1、「B格猫福利券·宴」x10；完成第4个任务，可以获得奖励Zeny x 300,000，「月相之力」x3、「奋进礼盒」x1',Speaker=894039,}",
  [172586] = "{id=172586,Text='活动期间，任务会按服务器时间连续4天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894039,}",
  [172587] = "{id=172587,Text='该活动为帐号限定类任务哦~',Speaker=894039,}",
  [172588] = "{id=172588,Text='随着一年一度教师节的来临，猫老师又准备了一些题目来考验冒险者们的基本功，就让猫老师见识一下冒险者的实力吧~',Speaker=894039,}",
  [172589] = "{id=172589,Text='9/9~10/1期间，Base等级大于等于25级的冒险者前往普隆德拉，找到「猫老师」即可参与活动',Speaker=894039,}",
  [172590] = "{id=172590,Text='共5个连续的任务，冒险者需要回答猫老师提出的问题。完成第1--4个任务，均可获得Zeny x 150,000，「月相之力」x1；完成第5个任务，可以获得Zeny x 300,000，「月相之力」x3、「奋进礼盒」x1',Speaker=894039,}",
  [172591] = "{id=172591,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894039,}",
  [172592] = "{id=172592,Text='该活动为帐号限定类任务哦~',Speaker=894039,}",
  [172593] = "{id=172593,Text='受到大魔王之力的影响，每当月圆之夜时，就会有大量怪物发生暴动，「广寒仙子」需要冒险者前往各个地图净化暴动的魔物',Speaker=894039,}",
  [172594] = "{id=172594,Text='9/16~10/1期间，Base等级大于等于25级的冒险者前往普隆德拉，找到「广寒仙子」即可参与活动',Speaker=894039,}",
  [172595] = "{id=172595,Text='共5个连续的任务，冒险者需要前往不同的地图找到并击败指定魔物。完成第1--4个任务，均可获得Zeny x 150,000，「月相之力」x1；完成第5个任务，可以获得Zeny x 300,000，「月相之力」x3、「奋进礼盒」x1',Speaker=894039,}",
  [172596] = "{id=172596,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894039,}",
  [172597] = "{id=172597,Text='该活动为帐号限定类任务哦~',Speaker=894039,}",
  [172598] = "{id=172598,Text='「广寒仙子」为了感谢冒险者聆听她的故事，并且帮助她净化了暴动的魔物，她决定拿出一些珍藏已久的礼物，想和冒险者进行交换，她派出的使者已经在普隆德拉啦，冒险者快去看看吧~',Speaker=894039,}",
  [172599] = "{id=172599,Text='9/23~9/30期间，冒险者前往普隆德拉，找到「巨大的兔子」，即可使用多余材料交换礼物',Speaker=894039,}",
  [172600] = "{id=172600,Text='每次交换礼物可以获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=894039,}",
  [172601] = "{id=172601,Text='活动为帐号限定，每个帐号每天可捐献的最高次数为2次',Speaker=894039,}",
  [172602] = "{id=172602,Text='「暴动的银月魔女」带领魔物入侵哈默林，冒险者快拿起武器保卫哈默林吧！',Speaker=894039,}",
  [172603] = "{id=172603,Text='9/1~10/1期间，每周六、周日晚上16~24点，哈默林将刷新大量暴走的魔物，击败即有概率获得精美时装、符文等奖励哦~',Speaker=894039,}",
  [172604] = "{id=172604,Text='嗯，我很满意你今年的表现~',Speaker=894038,}",
  [172640] = "{id=172640,Text='一眨眼就到了……这里是哪？看上去不像是岛屿。',Speaker=894136,Option='梦幻城',}",
  [172641] = "{id=172641,Text='冒险者，你直接把我们带到了梦幻城？不愧是魔法的能力！',Speaker=894137,}",
  [172642] = "{id=172642,Text='好漂亮的地方啊，真是让人移不开眼睛。',Speaker=894137,}",
  [172643] = "{id=172643,Text='我们到的还算早，距离比赛开始应该还有一段时间。',Speaker=894136,}",
  [172644] = "{id=172644,Text='那正好，我们可以逛逛梦幻城了！',Speaker=894137,}",
  [172645] = "{id=172645,Text='嗯，那我们一边逛逛，一边前往比赛场地吧！',Speaker=894136,}",
  [172646] = "{id=172646,Text='我们在路上遇到了……呃……古拉。',Speaker=894136,}",
  [172647] = "{id=172647,Text='他是梦幻城的居民，似乎在料理上遇见了难题。',Speaker=894136,}",
  [172648] = "{id=172648,Text='或许我们可以帮帮他。',Speaker=894136,}",
  [172649] = "{id=172649,Text='前段时间，糖姐姐帮了我个大忙，我想做道漂亮的南瓜料理感谢她。',Speaker=894140,}",
  [172650] = "{id=172650,Text='我尝试着用刀雕刻南瓜，可是一次都没有成功……',Speaker=894140,}",
  [172651] = "{id=172651,Text='雕刻南瓜确实需要熟练的刀工，或许我可以教教你。',Speaker=894136,}",
  [172652] = "{id=172652,Text='真是太棒了！但是我的南瓜都用完了……这可怎么办才好……',Speaker=894140,Option='我来帮你',}",
  [172653] = "{id=172653,Text='真的吗？冒险者，你愿意帮我采集南瓜？',Speaker=894140,Option='没错',}",
  [172654] = "{id=172654,Text='太好了，有了你们的帮助，我一定可以顺利做出南瓜料理的！',Speaker=894140,}",
  [172655] = "{id=172655,Text='不许动这些南瓜！',Speaker=894142,Option='怎么了',}",
  [172656] = "{id=172656,Text='呃……这些南瓜是我们先看到的，谁都不许碰！',Speaker=894142,}",
  [172657] = "{id=172657,Text='没错！我们先看到的！',Speaker=894143,}",
  [172658] = "{id=172658,Text='就是就是！我们先看到的！',Speaker=894144,Option='……',}",
  [172659] = "{id=172659,Text='除非你能打败我们，不然这些南瓜，你想都别想！',Speaker=894142,}",
  [172660] = "{id=172660,Text='没错！打败我们！',Speaker=894143,}",
  [172661] = "{id=172661,Text='就是就是！打败我们！',Speaker=894144,Option='还有这种要求？',}",
  [172662] = "{id=172662,Text='哼！兄弟们，揍他！',Speaker=894142,}",
  [172663] = "{id=172663,Text='呜哇！你怎么这么厉害！',Speaker=894142,}",
  [172664] = "{id=172664,Text='兄弟们！撤！',Speaker=894142,}",
  [172665] = "{id=172665,Text='可是我们的南瓜……',Speaker=894143,}",
  [172666] = "{id=172666,Text='笨蛋！什么我们的南瓜！现在是冒险者大人的南瓜了！',Speaker=894142,}",
  [172667] = "{id=172667,Text='哇！好新鲜的南瓜！真是太感谢了！',Speaker=894140,}",
  [172668] = "{id=172668,Text='欸！你刚刚和我说的雕刻南瓜的技巧，我来试试……',Speaker=894140,}",
  [172669] = "{id=172669,Text='时间差不多了，我们该前往比赛场地了。',Speaker=894136,}",
  [172670] = "{id=172670,Text='嗯，我们赶紧出发吧。',Speaker=894139,}",
  [172719] = "{id=172719,Text='呸！没有抢到南瓜，真晦气！',Speaker=894142,}",
  [172720] = "{id=172720,Text='这回我们来抢占这片醒醒草吧！嘿嘿……',Speaker=894142,}",
  [172721] = "{id=172721,Text='呃……老大……',Speaker=894143,}",
  [172722] = "{id=172722,Text='呃……他又来了……',Speaker=894144,}",
  [172723] = "{id=172723,Text='什什什什什么……？可恶！兄弟们，揍他！',Speaker=894142,}",
  [172724] = "{id=172724,Text='呜哇！不要了不要了！醒醒草都给你！',Speaker=894142,}",
  [172725] = "{id=172725,Text='兄弟们！撤！',Speaker=894142,}",
  [172726] = "{id=172726,Text='那我们的醒醒草……',Speaker=894143,}",
  [172727] = "{id=172727,Text='现在是冒险者大人的醒醒草了！',Speaker=894142,}",
  [172784] = "{id=172784,Text='这里……和我的家乡真像。',Speaker=894180,}",
  [172785] = "{id=172785,Text='是啊，就仿佛回到了原来的世界……',Speaker=894181,}",
  [172786] = "{id=172786,Text='感觉十分亲切呢。',Speaker=894180,Option='介绍洛阳',}",
  [172787] = "{id=172787,Text='哦？洛阳还有花灯节？听上去十分有趣！',Speaker=894180,}",
  [172788] = "{id=172788,Text='啊！放出去的花灯会飞往天空，一定很美吧……',Speaker=894181,Option='没错',}",
  [172789] = "{id=172789,Text='有机会一定要体验一下！',Speaker=894181,}",
  [172790] = "{id=172790,Text='嗯！不过现在，我们要抓紧去比赛场地了！',Speaker=894180,}",
  [172791] = "{id=172791,Text='出发！',Speaker=894181,}",
  [172810] = "{id=172810,Text='酒的种类可多了，你想要哪种？',Speaker=809715,Option='{米酒,5},{果酒,5},{最好的酒,6}',}",
  [172811] = "{id=172811,Text='嗯？鼎鼎有名的冒险者，普通的酒就能满足你了吗？',Speaker=809715,}",
  [172812] = "{id=172812,Text='不愧是你啊，冒险者，真有眼光！要选就选最好的！',Speaker=809715,}",
  [172813] = "{id=172813,Text='偷偷告诉你……我可是知道整个洛阳最好的酒在哪！',Speaker=809715,Option='展开说说',}",
  [172814] = "{id=172814,Text='嘿嘿，这我收你点报酬不过分吧！',Speaker=809715,Option='{？,0},{多少zeny,0}',}",
  [172815] = "{id=172815,Text='别急呀，我不要zeny。',Speaker=809715,}",
  [172816] = "{id=172816,Text='你去帮我收集10份黑蛇的牙齿吧，最近毒牙酒卖的太好了，毒牙已经不够了……',Speaker=809715,}",
  [172817] = "{id=172817,Text='快去快回！',Speaker=809715,}",
  [172818] = "{id=172818,Text='哇哦，这么快就回来了，干得不错！',Speaker=809715,}",
  [172819] = "{id=172819,Text='哼哼，那我就把酒的线索告诉你吧！',Speaker=809715,}",
  [172820] = "{id=172820,Text='这酒名叫百里醉，是我爷爷酿造的，据说相隔百里都能闻到醉人的芬芳……',Speaker=809715,}",
  [172821] = "{id=172821,Text='爷爷将酒封存在一处平台，需要使用玉石来激活平台上的机关柱。',Speaker=809715,}",
  [172822] = "{id=172822,Text='你往南走，看到小河后往西……然后再往南……绕过去之后就是藏酒的平台了！',Speaker=809715,}",
  [172823] = "{id=172823,Text='（小声）反正我试过了，根本激活不成功嘛……',Speaker=809715,}",
  [172824] = "{id=172824,Text='呐，玉石给你，能不能激活机关柱，拿到百里醉，就看你的运气啦！',Speaker=809715,}",
  [172825] = "{id=172825,Text='刚刚似乎看到了依丝蒂和……向恩。',}",
  [172826] = "{id=172826,Text='机关柱似乎没有反应。',}",
  [172827] = "{id=172827,Text='要镶嵌哪块玉石？',Speaker=894212,Option='{火属性玉石,2},{水属性玉石,2},{风属性玉石,3},{土属性玉石,2}',}",
  [172828] = "{id=172828,Text='机关柱似乎没有反应。',}",
  [172829] = "{id=172829,Text='要镶嵌哪块玉石？',Speaker=894214,Option='{火属性玉石,3},{水属性玉石,2},{风属性玉石,2},{土属性玉石,2}',}",
  [172830] = "{id=172830,Text='机关柱似乎没有反应。',}",
  [172831] = "{id=172831,Text='要镶嵌哪块玉石？',Speaker=894215,Option='{火属性玉石,2},{水属性玉石,3},{风属性玉石,2},{土属性玉石,2}',}",
  [172832] = "{id=172832,Text='机关柱似乎没有反应。',}",
  [172833] = "{id=172833,Text='要镶嵌哪块玉石？',Speaker=894213,Option='{火属性玉石,2},{水属性玉石,2},{风属性玉石,2},{土属性玉石,3}',}",
  [172915] = "{id=172915,Text='冒险者，事关重大，我需要你的帮助！',Speaker=894224,Option='怎么了',}",
  [172916] = "{id=172916,Text='南门再次发现了未知的时空裂隙，我们需要你帮忙调查。',Speaker=894224,Option='{……,0},{又是时空裂隙？,0}',}",
  [172917] = "{id=172917,Text='呃……没错……也许又有一些神秘的人物被卷入了这个世界……',Speaker=894224,}",
  [172918] = "{id=172918,Text='拜托了，冒险者，刻不容缓，赶紧出发吧！',Speaker=894224,Option='{立刻出发,3},{等等再说,4}',}",
  [172919] = "{id=172919,Text='我果然没有看错你！我就知道你一定会答应的！',Speaker=894224,}",
  [172920] = "{id=172920,Text='如果你改变主意了，就再来找我吧！',Speaker=894224,}",
  [172926] = "{id=172926,Text='这里是……',Speaker=894234,Option='介绍情况',}",
  [172927] = "{id=172927,Text='你是说……这里是另一个世界？',Speaker=894234,Option='没错',}",
  [172928] = "{id=172928,Text='不稳定的时空裂隙将我们带到了这里……？',Speaker=894234,}",
  [172929] = "{id=172929,Text='什么嘛……真是闻所未闻……',Speaker=894235,}",
  [172930] = "{id=172930,Text='我才不相信你的鬼话，一定是黑暗料理界的阴谋！',Speaker=894235,Option='{……,0},{该不会以为我听得懂吧,0}',}",
  [172931] = "{id=172931,Text='没错……说不定就是黑暗料理界绑架了我们！',Speaker=894235,}",
  [172932] = "{id=172932,Text='或者……你能证明你说的是真的吗？',Speaker=894044,Option='{我可以,0},{今天就要让你开开眼,0}',}",
  [172933] = "{id=172933,Text='那就让我们看看吧。',Speaker=894044,}",
  [172934] = "{id=172934,Text='要使用哪种魔法？',Option='{火焰魔法,2},{冰冻魔法,3},{狂风魔法,4},{召唤魔物,5}',}",
  [172935] = "{id=172935,Text='嗯？你把生火的工具藏在哪儿了？',Speaker=894235,}",
  [172936] = "{id=172936,Text='啊！下雪了吗？天气突然变得好冷！',Speaker=894235,}",
  [172937] = "{id=172937,Text='唔……哪来的风，我要睁不开眼了！',Speaker=894235,}",
  [172938] = "{id=172938,Text='哇！凭空出现了！毛茸茸的兔子，好可爱啊！',Speaker=894235,}",
  [172939] = "{id=172939,Text='这样看来，我们的确来到了异世界……',Speaker=894043,}",
  [172940] = "{id=172940,Text='不知道怎样才能离开。',Speaker=894043,Option='等待裂隙修复',}",
  [172941] = "{id=172941,Text='只要等待时空裂隙修复就好了？这么简单？',Speaker=894234,}",
  [172942] = "{id=172942,Text='这样真是再好不过了……啊！请容许我做个自我介绍。',Speaker=894234,}",
  [172943] = "{id=172943,Text='我叫刘昴星，是来自四川的特级厨师，他们都是我的伙伴……',Speaker=894234,}",
  [172944] = "{id=172944,Text='梅丽，雷恩，兰飞鸿。',Speaker=894234,}",
  [172945] = "{id=172945,Text='虽然来到这个世界有些突然……但很高兴能认识你！',Speaker=894234,Option='我也是',}",
  [172946] = "{id=172946,Text='等待时空裂隙修复的时间里，我们可以在附近转转……等等！',Speaker=894234,}",
  [172947] = "{id=172947,Text='转龙壶！转龙壶不见了！',Speaker=894234,Option='那是什么？',}",
  [172948] = "{id=172948,Text='是传说中的厨具，对我非常重要，我必须要找到它。',Speaker=894234,Option='我来帮你',}",
  [172949] = "{id=172949,Text='多谢，梅丽、雷恩、阿飞！我们在附近找找看吧。',Speaker=894234,}",
  [172950] = "{id=172950,Text='嗨，冒险者，找我什么事？',Speaker=894047,Option='传说中的厨具',}",
  [172951] = "{id=172951,Text='传说中的厨具……？没有见过呢……',Speaker=894047,}",
  [172952] = "{id=172952,Text='不过你可以问问学徒格雷，他是大陆顶级厨师的徒弟，或许会知道……',Speaker=894047,}",
  [172953] = "{id=172953,Text='嗯？你找我吗？',Speaker=894046,Option='传说中的厨具',}",
  [172954] = "{id=172954,Text='传说中的厨具……不知道哎……',Speaker=894046,}",
  [172955] = "{id=172955,Text='我的老师特米尔也许知道，他马上就要出发去参加厨王争霸赛了。',Speaker=894046,}",
  [172956] = "{id=172956,Text='你快点过去，或许可以找到他。',Speaker=894046,}",
  [172957] = "{id=172957,Text='嗨！鼎鼎有名的冒险者！',Speaker=894051,Option='传说中的厨具',}",
  [172958] = "{id=172958,Text='诶？你也听说那个厨艺比赛了？',Speaker=894051,Option='{？？？,0},{什么？,0}',}",
  [172959] = "{id=172959,Text='咳，我这儿得到的消息是，料理协会的会长最近意外捡到了一个神奇的厨具。',Speaker=894051,}",
  [172960] = "{id=172960,Text='是一个……瞬间就能完成料理的壶！',Speaker=894051,}",
  [172961] = "{id=172961,Text='为了激励大陆的厨师们，会长特地举办了厨王争霸赛，把这个神奇的壶作为最终奖品。',Speaker=894051,}",
  [172962] = "{id=172962,Text='喏，那边的公告栏有传单，你感兴趣的话可以去看看。',Speaker=894051,}",
  [172963] = "{id=172963,Text='事不宜迟，我要赶紧出发去参加比赛了！',Speaker=894051,}",
  [172964] = "{id=172964,Text='你打听到到转龙壶的消息了？',Speaker=894234,Option='公告栏',}",
  [172965] = "{id=172965,Text='我看看……厨王争霸赛……最终奖品是……',Speaker=894234,}",
  [172966] = "{id=172966,Text='没错！这一定是转龙壶！',Speaker=894234,}",
  [172967] = "{id=172967,Text='冒险者，我必须要参加比赛，取得胜利，拿回转龙壶！',Speaker=894234,Option='好',}",
  [172968] = "{id=172968,Text='感谢你的帮助，作为回报，我可以传授你我的独家厨艺！',Speaker=894234,Option='太棒了',}",
  [172969] = "{id=172969,Text='那我们快准备一下出发！',Speaker=894234,}",
  [172970] = "{id=172970,Text='初试地点是艾尔贝塔，距离远吗？我们应该怎么过去？',Speaker=894044,Option='交给我吧',}",
  [172971] = "{id=172971,Text='多谢了，冒险者！',Speaker=894234,}",
  [172972] = "{id=172972,Text='没想到这么快就到达了目的地！',Speaker=894055,}",
  [172973] = "{id=172973,Text='还等什么！我们快去报名参加比赛吧！',Speaker=894056,}",
  [172974] = "{id=172974,Text='梅丽说的对，我们出发！',Speaker=894055,Option='出发！',}",
  [172987] = "{id=172987,Text='你把海鲜还给我！',Speaker=894066,}",
  [172988] = "{id=172988,Text='呜……我好不容易收集到的海鲜，全被抢走了！',Speaker=894066,}",
  [172989] = "{id=172989,Text='真是太过分了！怎么可以抢别人的东西！',Speaker=894056,Option='就是',}",
  [172990] = "{id=172990,Text='……刚刚那个人……看背影似乎有点眼熟……',Speaker=894055,}",
  [172991] = "{id=172991,Text='什么嘛，就是个讨厌的强盗而已！',Speaker=894056,}",
  [172992] = "{id=172992,Text='真奇怪……一定在哪里见过她……',Speaker=894055,}",
  [172993] = "{id=172993,Text='呜……没有了食材，我一定没办法通过比赛了……',Speaker=894066,}",
  [172994] = "{id=172994,Text='你先别哭嘛，我们这么多人，说不定可以帮到你。',Speaker=894056,Option='没错',}",
  [172995] = "{id=172995,Text='没错，只收集我一个人需要的食材，也用不到这么多人。',Speaker=894055,}",
  [172996] = "{id=172996,Text='不如……梅丽、冒险者和我一起，雷恩、阿飞你们陪她一起好了！',Speaker=894055,}",
  [172997] = "{id=172997,Text='那真是……太谢谢你们了！',Speaker=894066,}",
  [172998] = "{id=172998,Text='这和我家乡的鲷鱼十分相像！',Speaker=894055,}",
  [172999] = "{id=172999,Text='或许……可以把他当做食材！',Speaker=894055,Option='就是他了！',}",
  [173000] = "{id=173000,Text='太棒了！你这么快就收集到了新鲜鲷鱼！',Speaker=894055,}",
  [173001] = "{id=173001,Text='让我想想……嗯……或许可以做那道菜！',Speaker=894055,}",
  [173002] = "{id=173002,Text='不过，我还需要一些新鲜蔬菜。',Speaker=894055,}",
  [173003] = "{id=173003,Text='哦！欢迎光临！新鲜蔬菜任意挑选哦！',Speaker=894072,}",
  [173004] = "{id=173004,Text='只要100zeny，面前的蔬菜你可以拿走哦！',Speaker=894072,Option='{上交100zeny,11},{稍后购买,10}',}",
  [173005] = "{id=173005,Text='期待下次光临~',Speaker=894072,}",
  [173006] = "{id=173006,Text='快去挑选吧，离店后概不负责！',Speaker=894072,}",
  [173007] = "{id=173007,Text='你钱不够啊！',Speaker=894072,}",
  [173008] = "{id=173008,Text='哇！全部都是新鲜的蔬菜，冒险者你的眼光真不错！',Speaker=894055,}",
  [173009] = "{id=173009,Text='我们回来了。',Speaker=894058,}",
  [173010] = "{id=173010,Text='找到了一些龙虾，看上去还不错。',Speaker=894058,}",
  [173011] = "{id=173011,Text='我们也收集到了足够的食材，可以回到比赛场地了！',Speaker=894055,}",
  [173012] = "{id=173012,Text='谢谢你们，真是帮了大忙了！',Speaker=894066,}",
  [173013] = "{id=173013,Text='我们走吧~',Speaker=894066,}",
  [173065] = "{id=173065,Text='冒险者你来啦~四年一度的奥运会正在进行中，我这里有个关于奥运会的问题想要考你，你有兴趣吗？',Speaker=894262,}",
  [173066] = "{id=173066,Text='虽然难度很高，但同样奖品也很丰厚哦！',Speaker=894262,}",
  [173067] = "{id=173067,Text='那我们开始答题获赠神秘礼盒吧！要立刻开始嘛？',Speaker=894262,Option='{先等一等,2},{开始答题,3}',}",
  [173068] = "{id=173068,Text='2024年的奥运会是在哪里举行？',Speaker=894262,Option='{巴黎,4},{北京,5},{东京,5}',}",
  [173069] = "{id=173069,Text='我不觉得这是正确答案呢。',Speaker=894262,Option='我再想想',}",
  [173070] = "{id=173070,Text='恭喜你答对啦，这是你的奖励。',Speaker=894262,}",
  [173071] = "{id=173071,Text='你好呀冒险者~在奥运会举行期间，你每天都可以来找我领取「初心五环」x1哦~',Speaker=894263,}",
  [173072] = "{id=173072,Text='让我们一起关注这场四年一遇的体育盛事吧~！',Speaker=894263,Option='好哒',}",
  [173091] = "{id=173091,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894269,}",
  [173092] = "{id=173092,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894269,}",
  [173093] = "{id=173093,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【LuNaRiY】[-][/c]绘制的共创头像！',Speaker=894269,Option='哇！',}",
  [173094] = "{id=173094,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894269,Option='谢谢',}",
  [173117] = "{id=173117,Text='[c][ffff00][PlayerName][-][/c]，本来想做一道拿手好菜招待朋友们，但是我准备好的食材却被一些贪吃的魔物抢走了。',Speaker=894271,}",
  [173118] = "{id=173118,Text='如果你愿意帮我夺回那些食材的话，我愿意拿奖励跟你交换~！',Speaker=894271,}",
  [173119] = "{id=173119,Text='今天就去[c][ffff00]首都地下水道1F[-][/c]看看吧，早去早回，我在这里等你~！',Speaker=894271,Option='冲',}",
  [173120] = "{id=173120,Text='看起来这就是阿昴的食材，赶快带回去给他吧~',}",
  [173121] = "{id=173121,Text='这确实是我丢失的食材，但是只有这一份还远远不够，明天我们再去别处看看吧~！',Speaker=894271,}",
  [173122] = "{id=173122,Text='[c][ffff00][PlayerName][-][/c]，我正在等你呢~',Speaker=894271,}",
  [173123] = "{id=173123,Text='今天我们去[c][ffff00]海底洞穴[-][/c]看看吧，我看到有个贪吃的身影往那边去了~',Speaker=894271,Option='冲',}",
  [173124] = "{id=173124,Text='今天要去的地方有些危险，[c][ffff00][PlayerName][-][/c]你要小心哦~',Speaker=894271,}",
  [173125] = "{id=173125,Text='如果你准备好了，我们就出发吧~！',Speaker=894271,Option='冲',}",
  [173126] = "{id=173126,Text='食材已经收集得差不多了~那么，今天去[c][ffff00]苏克拉特沙漠[-][/c]看看吧~',Speaker=894271,Option='冲',}",
  [173127] = "{id=173127,Text='现在就只差最后一份食材啦~！',Speaker=894271,}",
  [173128] = "{id=173128,Text='今天是最后一份食材了，只要找回这份食材，我就可以开始做料理啦~！',Speaker=894271,}",
  [173129] = "{id=173129,Text='去[c][ffff00]钟楼地下[-][/c]看看吧，今天的家伙可不好对付呢~！',Speaker=894271,Option='冲',}",
  [173130] = "{id=173130,Text='齐了齐了，这几天真是辛苦你了，谢谢你~！',Speaker=894271,}",
  [173131] = "{id=173131,Text='[c][ffff00][PlayerName][-][/c]，在下黑暗料理界向恩，虽然身处黑暗料理界，但是对于料理的知识，在下也称得上是博学！',Speaker=894275,Option='当然',}",
  [173132] = "{id=173132,Text='如果你能通过在下的考验，那么在下必定会对你有所奖励！',Speaker=894275,Option='{开始,0}{稍等,2}',}",
  [173133] = "{id=173133,Text='回答的太糟糕了！',Speaker=894275,Emoji=12,}",
  [173134] = "{id=173134,Text='回答的很好！我这关算你通过了！',Speaker=894275,}",
  [173135] = "{id=173135,Text='经验丰富的冒险者果然是无所不知~',Speaker=894275,}",
  [173136] = "{id=173136,Text='这是对你的认可，请收下吧！悄悄告诉你，这次只是开胃菜，接下来还有更难的考验等着你哦~',Speaker=894275,}",
  [173137] = "{id=173137,Text='你昨天表现得非常优秀，那么就开始今天的考验吧~！',Speaker=894275,Option='{开始,0}{稍等,2}',}",
  [173138] = "{id=173138,Text='今天是最后一场校验了哦~准备好了我们就开始吧~',Speaker=894275,Option='{开始,0}{稍等,2}',}",
  [173139] = "{id=173139,Text='由于时空裂隙的关系，强大的魔物从裂隙中源源不断地涌出，我需要你的帮助！',Speaker=894276,}",
  [173140] = "{id=173140,Text='击败不同波次的魔物，你将会获得不同的奖励哦~',Speaker=894276,}",
  [173185] = "{id=173185,Text='[c][ffff00][PlayerName][-][/c]，欢迎参加黄金美食节。本次美食料理大赛的主题为“重现传说秘方：黄金蛋炒饭”。',Speaker=894315,Option='{传说秘方？,0},{黄金蛋炒饭？,0}',}",
  [173186] = "{id=173186,Text='千年前抗击魔王梦罗克时，存在着一种神奇的料理，食用后能让人“充满力量”。',Speaker=894315,}",
  [173187] = "{id=173187,Text='然而美食家协会只找到秘方残卷，始终没能做出具有特殊功效的蛋炒饭。',Speaker=894315,}",
  [173188] = "{id=173188,Text='因此我们将它设为大赛主题，重现黄金蛋炒饭的人将成为冠军，获得丰厚奖励。',Speaker=894315,}",
  [173189] = "{id=173189,Text='[c][ffff00][PlayerName][-][/c]，你有兴趣参加吗？[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=894315,Option='{我很感兴趣,4},{不是很感兴趣,3}',}",
  [173190] = "{id=173190,Text='明白了。如果您改变主意，可以再来找我。',Speaker=894315,}",
  [173191] = "{id=173191,Text='感谢您的参与。顺带一提，比赛是可以组队参加的。',Speaker=894315,}",
  [173192] = "{id=173192,Text='目前有大量参赛者聚集在普隆德拉，您不妨找找有无心仪的伙伴。',Speaker=894315,Option='好的',}",
  [173197] = "{id=173197,Text='厨神小当家联动扭蛋开启！只需要使用B格猫金币参与10次[限时联动]扭蛋机，即可额外获赠联动职业转职道具[c][ffff00][英雄职业·刘昴星解锁券][-][/c]！',Speaker=894303,}",
  [173198] = "{id=173198,Text='冒险者如果抽到了重复的外观也不要气馁，可以在我这分解成[c][ffff00][厨神碎片][-][/c]，「厨神碎片」可以兑换全新的[c][ffff00]限定卡片[刘昴星卡片][-][/c]以及所有联动扭蛋中的外观道具哦~',Speaker=894303,}",
  [173199] = "{id=173199,Text='那么，这些「厨神碎片」就当作是我送你的见面礼啦~祝你好运~',Speaker=894303,}",
  [173200] = "{id=173200,Text='刘昴星等人被时空裂隙卷入，来到了米德加尔特大陆，他们听闻这里将举办厨艺大赛，第一名的奖品疑似在时空裂隙中丢失的转龙壶。为了重获转龙壶，刘昴星正在寻找能够帮得上忙的冒险者，你会是那个天选之人吗？',Speaker=894274,}",
  [173201] = "{id=173201,Text='9/4~10/4期间，Base等级大于等于25级的冒险者，前往普隆德拉广场找到[菲迪尔]即可参与活动',Speaker=894274,}",
  [173202] = "{id=173202,Text='共5个连续的任务，完成每个任务均可获得[厨神馈赠]x1，[厨神扭蛋币]x5，[厨神小当家系列头像]x1，完成第5个任务可以额外获得限定尾饰[特级厨师]x1，[厨神碎片]x2',Speaker=894274,}",
  [173203] = "{id=173203,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894274,}",
  [173204] = "{id=173204,Text='该活动为帐号限定类任务',Speaker=894274,}",
  [173205] = "{id=173205,Text='阿昴本打算宴请好友，却不想准备的食材被贪吃的魔物给夺走了，冒险者，是时候去帮阿昴夺回食材了~！',Speaker=894274,}",
  [173206] = "{id=173206,Text='9/12~10/4期间，Base等级大于等于25级的冒险者，前往普隆德拉广场找到[刘昴星]即可参与活动',Speaker=894274,}",
  [173207] = "{id=173207,Text='共5个连续的任务，完成1~4个任务可以获得Zeny x 150,000，[厨神馈赠]x1；完成第5个任务可以获得Zeny x 300,000，[厨神馈赠]x1，[厨神碎片]x2，限定头饰「真鲷江山图」x1',Speaker=894274,}",
  [173208] = "{id=173208,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894274,}",
  [173209] = "{id=173209,Text='该活动为帐号限定类任务',Speaker=894274,}",
  [173210] = "{id=173210,Text='黑暗料理界的向恩准备了一些关于料理的题目想要考考冒险者，身为米德加尔特的精英可不能丢脸~',Speaker=894274,}",
  [173211] = "{id=173211,Text='活动期间，Base等级≥25级的冒险者前往普隆德拉广场找到[向恩]即可参与活动',Speaker=894274,}",
  [173212] = "{id=173212,Text='共5个连续的任务，完成1~4个任务可以获得Zeny x 150,000，[厨神馈赠]x1；完成第5个任务可以获得Zeny x 300,000，[厨神馈赠]x1，[厨神碎片]x2',Speaker=894274,}",
  [173213] = "{id=173213,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894274,}",
  [173214] = "{id=173214,Text='该活动为帐号限定类任务',Speaker=894274,}",
  [173215] = "{id=173215,Text='米德加尔特大陆出现了一道来自异时空的裂隙，强大的魔物从裂隙中源源不断地涌出，勇敢的冒险者，拿起武器，用勇气来保卫爱的家园吧~！',Speaker=894274,}",
  [173216] = "{id=173216,Text='活动期间，Base等级≥25级的冒险者前往普隆德拉广场找到[周梅丽]即可参与活动',Speaker=894274,}",
  [173217] = "{id=173217,Text='通关第5波怪物，可以获得[厨神扭蛋币]x2；通关第10波怪物，可以获得[厨神扭蛋币]x3；通关第15波怪物，可以获得[厨神扭蛋币]x5，[真龙碎片]x1，收集5个[真龙碎片]可以召唤活动限定宠物[焰纹御龙宠物蛋]',Speaker=894274,}",
  [173218] = "{id=173218,Text='活动副本不限次数挑战，不消耗战斗时长，每个指定波次的奖励每个帐号每天仅可获得1次',Speaker=894274,}",
  [173221] = "{id=173221,Text='黄金美食节迎来了世界各地的美食家们，大家带来了许多特色美食进行分享！',Speaker=894243,}",
  [173222] = "{id=173222,Text='这里有其他美食家准备的小礼物给你，作为交换，也分享你带来的美味给他们吧~',Speaker=894243,}",
  [173223] = "{id=173223,Text='怎么办！到底去哪里了……',Speaker=894249,}",
  [173224] = "{id=173224,Text='太好了！[c][ffff00][PlayerName][-][/c]你来了！',Speaker=894249,Option='怎么了？',}",
  [173225] = "{id=173225,Text='美食节快要结束了，许多美食家们也陆陆续续准备离开，我奉命在这里站岗。',Speaker=894249,}",
  [173226] = "{id=173226,Text='刚才来了一位美食家，我正打算给他指路，突然起了一场大雾！一群魔物冒了出来！',Speaker=894249,}",
  [173227] = "{id=173227,Text='再一转眼，他就消失了！我想去找他，可我方向感也不太好……',Speaker=894249,}",
  [173228] = "{id=173228,Text='勇敢的冒险家！请帮帮我！',Speaker=894249,Option='{好吧好吧,0},{现在就去！,0}',}",
  [173229] = "{id=173229,Text='啊！发生了什么？',Speaker=894305,}",
  [173230] = "{id=173230,Text='我正打算回家，走到一片森林，之后就什么都不记得了……',Speaker=894305,Option='说明缘由',}",
  [173231] = "{id=173231,Text='原来如此！我这是走到哪里来了呀……',Speaker=894305,}",
  [173232] = "{id=173232,Text='不过还好你来了，谢谢你呀！',Speaker=894305,}",
  [173233] = "{id=173233,Text='太好了！不愧是[c][ffff00][PlayerName][-][/c]，我就知道你一定能找到他的！',Speaker=894249,}",
  [173234] = "{id=173234,Text='又失踪了一位美食家……',Speaker=894249,Option='{别担心,0},{我们去找他,0}',}",
  [173235] = "{id=173235,Text='……可恶！可恶啊！',Speaker=894249,}",
  [173236] = "{id=173236,Text='发生什么了，又有人失踪了吗？',}",
  [173237] = "{id=173237,Text='是啊！急死我了！！',Speaker=894249,Option='{别急别急,0},{我们去找他,0}',}",
  [173238] = "{id=173238,Text='今天是我最后一天在这里站岗，我要轮到别的地方去了！',Speaker=894249,}",
  [173239] = "{id=173239,Text='这些天太感谢你了，这个给你！以后有空来普隆德拉看我训练啊！',Speaker=894249,Option='一定一定',}",
  [173240] = "{id=173240,Text='啊！发生了什么？',Speaker=894306,}",
  [173241] = "{id=173241,Text='我路过一片森林，好像正在看地图，之后就什么都不记得了……',Speaker=894306,Option='说明缘由',}",
  [173242] = "{id=173242,Text='这样啊，果然还是好人多！',Speaker=894306,}",
  [173243] = "{id=173243,Text='我会记住你的！下次美食节见！',Speaker=894306,}",
  [173244] = "{id=173244,Text='啊！发生了什么？',Speaker=894307,}",
  [173245] = "{id=173245,Text='我听到了一声虎啸，之后就什么都不记得了……',Speaker=894307,Option='说明缘由',}",
  [173246] = "{id=173246,Text='好可怕！不过你真的能打败虎王吗！',Speaker=894307,}",
  [173247] = "{id=173247,Text='哇哦！我要把你写进我的美食节游记中，勇敢的大冒险家只身一人勇闯虎穴只为救出……',Speaker=894307,Option='……？',}",
  [173248] = "{id=173248,Text='啊！发生了什么？',Speaker=894308,}",
  [173249] = "{id=173249,Text='好像起了一场雾，之后我就什么都不记得了……',Speaker=894308,Option='说明缘由',}",
  [173250] = "{id=173250,Text='呜呜呜……好感动！原来还有人这么努力的找我！',Speaker=894308,}",
  [173251] = "{id=173251,Text='下次来普隆德拉我一定会给你带超超超大的蛋糕！',Speaker=894308,}",
  [173252] = "{id=173252,Text='啊！发生了什么？',Speaker=894309,}",
  [173253] = "{id=173253,Text='我只记得走到森林看到一大片巧克力，忍不住朝着它走去，之后就什么都不知道了……',Speaker=894309,Option='说明缘由',}",
  [173254] = "{id=173254,Text='原来如此！还好你来救我了！',Speaker=894309,}",
  [173255] = "{id=173255,Text='糟糕！来不及了，我该走啦！好心的冒险者，有缘再见啊~',Speaker=894309,}",
  [173280] = "{id=173280,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=894314,}",
  [173281] = "{id=173281,Text='每周六、周日晚16~24点，永夜海域许多少见的魔物将跟随着永夜海王巡游过柏伊亚岚海域！',Speaker=894314,}",
  [173282] = "{id=173282,Text='而这些魔物恰好是萨依的神秘配方所需要的，击败他们，帮萨依收集一些原材料吧~',Speaker=894314,}",
  [173283] = "{id=173283,Text='太感谢你了，有了这些材料，萨依一定能研制出更多美味！欢迎你来品尝哦~',Speaker=894314,}",
  [173285] = "{id=173285,Text='呼！这一盘巧克力也发完了！[c][ffff00][PlayerName][-][/c]想来一块百味巧克力吗！',Speaker=894245,}",
  [173286] = "{id=173286,Text='我发现了一种神奇的魔法食材，加入后每个人尝到的巧克力都是不同味道！',Speaker=894245,}",
  [173287] = "{id=173287,Text='但是因为魔力限制，每天只可以吃一块哦~',Speaker=894245,}",
  [173289] = "{id=173289,Text='一年一度的黄金美食节再次降临普隆德拉！传说中的料理配方「黄金蛋炒饭」再现人间，看似平平无奇的一道料理背后，却隐藏着一个沉寂了千年的秘密……',Speaker=894269,}",
  [173290] = "{id=173290,Text='10/1~10/31期间，Base等级≥25级的冒险者前往普隆德拉广场找到找到「亚契」即可参与活动',Speaker=894269,}",
  [173291] = "{id=173291,Text='共5个连续的任务，完成1~4任务，均可获得Zeny x 150,000，「美味的见证」x1、「B格猫福利券·宴」x6；完成第5个任务，可获得Zeny x 300,000，「美味的见证」x3、「B格猫福利券·宴」x6、「奋进礼盒」x1',Speaker=894269,}",
  [173292] = "{id=173292,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894269,}",
  [173294] = "{id=173294,Text='擅长甜点的可可家族中有一位神秘的小猫「萨依·可可」，她最近研究出了新的巧克力配方——「百味巧克力」，快去尝尝看它到底怎么样吧~',Speaker=894269,}",
  [173295] = "{id=173295,Text='10/7 ~ 10/14期间，Base等级≥25级的冒险家，前往普隆德拉，找到「萨依·可可」即可获得「百味巧克力」x1',Speaker=894269,}",
  [173296] = "{id=173296,Text='食用「百味巧克力」可以获得Zeny x 150,000，「美味的见证」x1，并且随机获得增益效果，增益效果持续12小时',Speaker=894269,}",
  [173297] = "{id=173297,Text='该活动为帐号限定类任务，每天都可以去品尝一次巧克力哦~',Speaker=894269,}",
  [173298] = "{id=173298,Text='美食节即将落下帷幕，来自世界各地的美食家们也要离开了。可是在路过迷藏森林的时候，有些美食家被坏心眼的魔物扰乱了方向，快去找到他们，击败魔物，为美食家们指引正确的方向吧！',Speaker=894269,}",
  [173299] = "{id=173299,Text='10/21 ~ 11/1期间，Base等级≥25级的冒险者前往迷藏森林找到「罗克加」即可参与活动',Speaker=894269,}",
  [173300] = "{id=173300,Text='共5个连续的任务，冒险者需要前往不同的地图找到并击败指定魔物，找到「迷路的美食家」。完成第1--4个任务，均可获得Zeny x 150,000，「美味的见证」x1；完成第5个任务，可以获得Zeny x 300,000，「美味的见证」x3、「奋进礼盒」x1',Speaker=894269,}",
  [173301] = "{id=173301,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894269,}",
  [173303] = "{id=173303,Text='起司家族的「巴萨」在这次美食节中品尝到了出乎意料的美食，心情非常好，他大手一挥，在普隆德拉留下了一些礼物给大家哦~',Speaker=894269,}",
  [173304] = "{id=173304,Text='10/21~11/1期间，活动期间，Base等级大于等于25级的冒险者前往普隆德拉，找到「巴萨的管家」即可领取「起司家的礼物」x1',Speaker=894269,}",
  [173306] = "{id=173306,Text='打开「起司家的礼物」冒险者可以获得「乐园币」x500、「闪耀的符文石」x1、「拾光邀请函」x30',Speaker=894269,}",
  [173308] = "{id=173308,Text='黄金美食节迎来了世界各地的美食家们，大家都带来了各种各样的美食进行分享，把你准备的美味捐献给组委会吧，他们会帮你分享出去，你也会收到其他美食家的小礼物哦！',Speaker=894269,}",
  [173309] = "{id=173309,Text='10/14~10/21期间，活动期间，冒险者前往普隆德拉广场找到「塞尼」，即可使用多余材料交换礼物',Speaker=894269,}",
  [173310] = "{id=173310,Text='每次交换礼物可以获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=894269,}",
  [173312] = "{id=173312,Text='永夜海域许多少见的魔物将跟随着「永夜海王」巡游过柏伊亚岚海域，这些魔物恰好是萨依神秘配方中的一味重要材料——「幽蓝尾鳍」的唯一来源，捕捉魔物，帮助萨依收集材料吧！',Speaker=894269,}",
  [173313] = "{id=173313,Text='10/1~11/1期间，每周六、周日晚上16~24点，海底洞穴将刷新大量来自永夜海域的魔物，击败即有概率获得精美时装、符文等奖励哦~',Speaker=894269,}",
  [173314] = "{id=173314,Text='茱萸又名“辟邪翁”，代表着吉祥和怀念，组委会为大家准备了很多茱萸，在重阳节制作一个茱萸香囊吧！不论是随身携带还是送给朋友，都有着非常美好的寓意~',Speaker=894269,}",
  [173315] = "{id=173315,Text='10/11 ~ 10/14期间，Base等级≥25级的冒险者前往普隆德拉，找到「尼森特」即可参与活动',Speaker=894269,}",
  [173316] = "{id=173316,Text='与「尼森特」对话，提交茱萸即可制作「茱萸香囊」，制作成功后获得节日礼物：「弗雷硬币」x1、「好运币」x10、「莫拉金币」x10',Speaker=894269,}",
  [173317] = "{id=173317,Text='又是一年九月九~啦啦啦啦~',Speaker=894250,}",
  [173318] = "{id=173318,Text='[c][ffff00][PlayerName][-][/c]，你知道今天是什么节日吗？',Speaker=894250,Option='重阳节？',}",
  [173319] = "{id=173319,Text='bingo！重阳节到了，组委会派我来给你们送点茱萸~',Speaker=894250,}",
  [173320] = "{id=173320,Text='你知道茱萸有什么用吗？',Speaker=894250,Option='{不知道,0},{你来讲讲,0}',}",
  [173321] = "{id=173321,Text='茱萸可是驱邪避祸的好宝贝哦~还象征着重阳节的美好祝愿！',Speaker=894250,}",
  [173322] = "{id=173322,Text='要不要我教你做个香囊，随身带着？',Speaker=894250,Option='好啊！',}",
  [173323] = "{id=173323,Text='首先拿出一块布料、绳子和这些茱萸。把茱萸放在布料中央，对折起来，包好~',Speaker=894250,}",
  [173324] = "{id=173324,Text='用绳子把布料的开口扎紧，确保茱萸不会掉出来！一个香囊就完成啦~',Speaker=894250,}",
  [173325] = "{id=173325,Text='简单吧！当然，如果你想做一个给朋友，我这里还有很多材料！',Speaker=894250,}",
  [173326] = "{id=173326,Text='记得重阳节佩戴茱萸香囊，我这里还有一份小礼物，祝你好运不断，冒险顺利哦！🎉',Speaker=894250,Option='谢谢你呀！',}",
  [173327] = "{id=173327,Text='尊敬的冒险者，你来啦！',Speaker=894242,}",
  [173328] = "{id=173328,Text='我是起司家族的管家，很高兴见到你~',Speaker=894242,}",
  [173329] = "{id=173329,Text='在这次美食节中，我们尊贵的巴萨大人品尝到了许多出人意料的美味佳肴！',Speaker=894242,}",
  [173330] = "{id=173330,Text='我从来没见过巴萨大人胃口这么好过！（抹眼泪）',Speaker=894242,}",
  [173331] = "{id=173331,Text='大人他心情非常好，决定准备一些小小的惊喜~',Speaker=894242,Option='哇哦……',}",
  [173332] = "{id=173332,Text='所有来参加美食节的人，都会获得一份巴萨大人的礼物！',Speaker=894242,}",
  [173333] = "{id=173333,Text='巴萨大人希望这些礼物能给大家带来更多的快乐和幸福！',Speaker=894242,Option='感谢巴萨！',}",
  [173334] = "{id=173334,Text='不用客气，这是他的一份心意~请继续享受美食节的乐趣吧！',Speaker=894242,}",
  [173335] = "{id=173335,Text='10/1~11/1期间，Base等级≥25级的冒险者前往普隆德拉广场，找到「亚西」可领取到本月玩家共创的头像哦~',Speaker=894269,}",
  [173359] = "{id=173359,Text='云追月，月思云，但愿人长久，千里共婵娟~',Speaker=894322,}",
  [173360] = "{id=173360,Text='在这个特殊的日子里，我也给你准备了特殊的礼物~这个象征着满月的背景框，希望你可以喜欢~',Speaker=894322,Option='谢谢',}",
  [173361] = "{id=173361,Text='冒险者，你听说过[c][ffff00]相思明月[-][/c]吗？据说可以给人带来团圆哦~',Speaker=894321,}",
  [173362] = "{id=173362,Text='在这个特殊的日子里，我也为你准备了一些[c][ffff00]相思明月[-][/c]哦~希望你生活美满，团团圆圆~！',Speaker=894321,}",
  [173363] = "{id=173363,Text='当然，如果你收集到了更多的[c][ffff00]相思明月[-][/c]，可以在我这里兑换各类中秋节专属外观哦~',Speaker=894321,}",
  [173378] = "{id=173378,Text='有想好买什么吗？普隆德拉购物节已经开始了哦！这可是一年当中，折扣力度最大的时候！别忘了，每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！',Speaker=894325,}",
  [173379] = "{id=173379,Text='[c][ffff00][PlayerName][-][/c]，别愣着了~快收下啦~',Speaker=894325,Option='打开',}",
  [173380] = "{id=173380,Text='[c][ffff00][PlayerName][-][/c]，你终于来啦~',Speaker=894326,Option='怎么了？',}",
  [173381] = "{id=173381,Text='一年一度的普隆德拉购物节已经拉开帷幕，但我还在头痛几大商铺的选品问题……',Speaker=894326,Emoji=24,}",
  [173382] = "{id=173382,Text='这次购物节的重头戏[c][ffff00]普隆德拉奇珍店[-][/c]如果掺杂了不合适的商品！那我的营业额……流水……KPI……我的工作，该怎么办！',Speaker=894326,Option='我来帮你！',}",
  [173383] = "{id=173383,Text='不愧是[c][ffff00][PlayerName][-][/c]，热心的冒险者，总能在危机时刻出现！',Speaker=894326,Option='从何做起',}",
  [173384] = "{id=173384,Text='请去拜访这些商人！他们会提供不同的商品种类供你选择，一定要选出最合适的商品呀！',Speaker=894326,Option='我去试试',}",
  [173385] = "{id=173385,Text='嗯？冒险者你来的可正好，我进了一批商品，正愁着不知道上什么好呢，快帮我看看吧',Speaker=1046,Option='{开始,0}{稍等,2}',}",
  [173386] = "{id=173386,Text='选的太糟糕了！不符合道具商店的定位！',Speaker=1046,Emoji=12,}",
  [173387] = "{id=173387,Text='真是不错的选择，就这么定了！',Speaker=1046,}",
  [173388] = "{id=173388,Text='经验丰富的冒险者果然是无所不知~',Speaker=1046,}",
  [173389] = "{id=173389,Text='悄悄告诉你，[c][ffff00]莱妮[-][/c]的商品明天也会到货，请明天去帮助她吧！',Speaker=1046,}",
  [173390] = "{id=173390,Text='哦！感谢你的到来，冒险者，快帮我看看这批商品上什么合适呢？',Speaker=806040,Option='{开始,0}{稍等,2}',}",
  [173391] = "{id=173391,Text='选的太糟糕了！不符合符文商店的定位！',Speaker=806040,Emoji=12,}",
  [173392] = "{id=173392,Text='真是不错的选择，就这么定了！',Speaker=806040,}",
  [173393] = "{id=173393,Text='经验丰富的冒险者果然是无所不知~',Speaker=806040,}",
  [173394] = "{id=173394,Text='这是对你的感谢，请收下吧！悄悄告诉你，[c][ffff00]坐骑商店的卡迪尔[-][/c]的商品明天也会到货，请明天去帮助他吧！',Speaker=806040,}",
  [173395] = "{id=173395,Text='冒险者，是你！快帮我看看这次购物上什么商品合适呢？',Speaker=5092,Option='{开始,0}{稍等,2}',}",
  [173396] = "{id=173396,Text='选的太糟糕了！不符合坐骑商店的定位！',Speaker=5092,Emoji=12,}",
  [173397] = "{id=173397,Text='真是不错的选择，就这么定了！',Speaker=5092,}",
  [173398] = "{id=173398,Text='经验丰富的冒险者果然是无所不知~',Speaker=5092,}",
  [173399] = "{id=173399,Text='这是对你的感谢，请收下吧！悄悄告诉你，[c][ffff00]金币商店的狄丝·瓦尔[-][/c]的商品明天也会到货，请明天去帮助她吧！',Speaker=5092,}",
  [173400] = "{id=173400,Text='你终于来啦！购物节马上开始了，快帮我看看上什么商品合适吧！',Speaker=2700,Option='{开始,0}{稍等,2}',}",
  [173401] = "{id=173401,Text='选的太糟糕了！不符合金币商店的定位！',Speaker=2700,Emoji=12,}",
  [173402] = "{id=173402,Text='真是不错的选择，就这么定了！',Speaker=2700,}",
  [173403] = "{id=173403,Text='经验丰富的冒险者果然是无所不知~',Speaker=2700,}",
  [173404] = "{id=173404,Text='这是对你的感谢，请收下吧！',Speaker=2700,}",
  [173405] = "{id=173405,Text='唔，你回来啦~那么接下来，我们核对下已经确定好选品的商铺吧',Speaker=894326,Option='{开始,0}{稍等,2}',}",
  [173406] = "{id=173406,Text='回答的太糟糕了！你需要再想想~',Speaker=894326,Emoji=12,}",
  [173407] = "{id=173407,Text='不错嘛，果然很靠谱呢！',Speaker=894326,}",
  [173408] = "{id=173408,Text='[c][ffff00]普隆德拉奇珍店[-][/c]正在营业中哦！',Speaker=894326,}",
  [173409] = "{id=173409,Text='你可以通过[c][ffff00]热点[-][/c]按钮进入[c][ffff00]购物节[-][/c]选购到各个商店提供的超值商品哦~',Speaker=894326,}",
  [173410] = "{id=173410,Text='这是给你的报酬，请收下吧！',Speaker=894326,}",
  [173411] = "{id=173411,Text='嘿，朋友！普隆德拉公司带来了超级给力的活动——‘幸运3选1’！别错过这个赢大奖的好机会，快来参加吧',Speaker=894326,Option='了解一下',}",
  [173412] = "{id=173412,Text='活动中有机会获得大量「普隆德拉购物券」哟，嘻嘻，准备好了吗？',Speaker=894326,Option='来吧！',}",
  [173413] = "{id=173413,Text='那么你要选择哪个呢~？',Speaker=894326,Option='{摇头摆尾的选项一,0}{喜笑颜开的选项二,0},{沉默不语的选项三,0}',}",
  [173414] = "{id=173414,Text='伴随着购物节，又一年的感恩节到来了~普隆德拉准备了一场盛大的烟花庆典哦！想必一定热闹非凡',Speaker=894327,}",
  [173415] = "{id=173415,Text='不过，不少调皮捣蛋魔物也被氛围所吸引，还把我准备的烟花偷走了！',Speaker=894327,}",
  [173416] = "{id=173416,Text='如果有人能帮我一起找回四处躲藏的魔物，夺回[c][ffff00]烟花[-][/c]就好了……',Speaker=894327,Option='去哪儿找',}",
  [173417] = "{id=173417,Text='今天得到的传闻是在\n[c][ffff00]「普隆德拉南门」[-][/c]，快去看看吧！',Speaker=894327,Option='好',}",
  [173418] = "{id=173418,Text='诶诶，别着急，在地图上要仔细寻找哦~',Speaker=894327,}",
  [173419] = "{id=173419,Text='[c][ffff00][PlayerName][-][/c]，你回来啦~怎么样，夺回来了吗？',Speaker=894327,Option='是这个吗？',}",
  [173420] = "{id=173420,Text='喔喔！是的！太好了，这是我们精心准备的烟花呀',Speaker=894327,Emoji=1,}",
  [173421] = "{id=173421,Text='这是给你的报酬，请收下吧！明天也记得来帮忙噢~',Speaker=894327,}",
  [173422] = "{id=173422,Text='今天得到的传闻是在\n[c][ffff00]「普隆德拉西门」[-][/c]，快去看看吧！',Speaker=894327,Option='好',}",
  [173423] = "{id=173423,Text='今天得到的传闻是在\n[c][ffff00]「金字塔3F」[-][/c]，快去看看吧！',Speaker=894327,Option='好',}",
  [173424] = "{id=173424,Text='今天得到的传闻是在\n[c][ffff00]「生命体研究所 地下3F」[-][/c]，快去看看吧！',Speaker=894327,Option='好',}",
  [173426] = "{id=173426,Text='今天得到的传闻是在\n[c][ffff00]「兽人村落」[-][/c]，快去看看吧！',Speaker=894327,Option='好',}",
  [173428] = "{id=173428,Text='好了，已经全部找回来了，烟花庆典的道具也都准备齐全，庆典马上就要开始了！',Speaker=894327,}",
  [173429] = "{id=173429,Text='和我一起倒计时吧，冒险者！',Speaker=894327,Option='好',}",
  [173430] = "{id=173430,Text='三！',Speaker=894327,}",
  [173431] = "{id=173431,Text='二！',Speaker=894327,}",
  [173432] = "{id=173432,Text='一！',Speaker=894327,}",
  [173433] = "{id=173433,Text='真是美丽的烟花啊……',Speaker=894327,Option='是啊是啊',}",
  [173434] = "{id=173434,Text='感恩节，你最想感恩谁呢？冒险者！',Speaker=894327,Option='你呢？',}",
  [173435] = "{id=173435,Text='让我想想……我最想感谢一直以来乐于助人的你！所以，我为你准备了一份特别礼物！请收下吧！',Speaker=894327,}",
  [173436] = "{id=173436,Text='普隆德拉奇珍店正在营业呢！坐骑、翅膀、时装，现在只要超低的价格就能在珍宝店买到哦~',Speaker=894325,}",
  [173437] = "{id=173437,Text='这是特别为你准备的[c][ffff00]「奇珍购物券」[-][/c]！',Speaker=894325,}",
  [173438] = "{id=173438,Text='想要更多的[c][ffff00]「奇珍购物券」[-][/c]，也可以在金币商城购买礼包获得哦~',Speaker=894325,}",
  [173439] = "{id=173439,Text='如果你在购物节没有买到想要的东西，不妨来跟我交换哦~',Speaker=894328,}",
  [173440] = "{id=173440,Text='只需少量的材料就能换到实用的道具哦~快来试试吧！',Speaker=894328,}",
  [173445] = "{id=173445,Text='一年一度的购物节拉开帷幕！快开始购物之旅吧！',Speaker=894325,}",
  [173446] = "{id=173446,Text='11/1~12/1期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「瑞秋」[-][/c]，接取系列任务~',Speaker=894325,}",
  [173447] = "{id=173447,Text='共5个连续的任务：完成第1~4个任务，均可获得Zeny x 150,000，「普隆德拉购物券」x1、「B格猫福利券·宴」x6;完成第5个任务，获得Zeny x 300,000，「普隆德拉购物券」x3、「B格猫福利券·宴」x6、「奋进礼盒」x1',Speaker=894325,}",
  [173448] = "{id=173448,Text='为感谢大家的支持，普隆德拉广场更是开放了「幸运3选1」活动，有机会获得大量「普隆德拉购物券」哦！',Speaker=894325,}",
  [173449] = "{id=173449,Text='11/7~12/1期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「瑞秋」[-][/c]，可以参与「幸运3选1」活动~',Speaker=894325,}",
  [173450] = "{id=173450,Text='每次选择可以随机获得「普隆德拉购物券」x1、「普隆德拉购物券」x5或「普隆德拉购物券」x30',Speaker=894325,}",
  [173451] = "{id=173451,Text='11/14~12/1期间，Base等级≥25级的冒险者，通过“[c][ffff00]热点[-][/c]”可进入[c][ffff00]感恩回馈[-][/c]。\n累计登录达到指定天数，即可领取1份奖励~',Speaker=894325,}",
  [173452] = "{id=173452,Text='这次购物节，普隆德拉广场正筹备一场盛大的烟花庆典，为庆祝节日和各路客人的到来~快去帮助「雷克斯莉」完成庆典筹备吧！',Speaker=894325,}",
  [173453] = "{id=173453,Text='11/21~12/1期间，Base等级≥25级的冒险者前往普隆德拉广场找到[c][ffff00]「雷克斯莉」[-][/c]，接取系列任务~',Speaker=894325,}",
  [173454] = "{id=173454,Text='共5个连续的任务：\n完成第1~4个任务，可获得Zeny x 150,000、「普隆德拉购物券」x1\n完成第5个任务，可获得Zeny x 300,000、「普隆德拉购物券」x3、「奋进礼盒」x1',Speaker=894325,}",
  [173455] = "{id=173455,Text='11/25~12/1期间，冒险者前往普隆德拉广场找到[c][ffff00]「普隆德拉营业员」[-][/c]进行材料交换~',Speaker=894325,}",
  [173456] = "{id=173456,Text='完成交换，可获得「普隆德拉购物券」x1、「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1',Speaker=894325,}",
  [173457] = "{id=173457,Text='活动期间，每周六、周日[c][ffff00]普隆德拉[-][/c]周边都会出现大量的宝箱！',Speaker=894325,}",
  [173458] = "{id=173458,Text='参与宝箱大作战活动，可以获得「普隆德拉守护礼盒」和「普隆德拉的谢礼·MVP」，不要错过哟~',Speaker=894325,}",
  [173459] = "{id=173459,Text='届时记得找到[c][ffff00]「狄丝·瓦尔」[-][/c]体验一下，获得丰厚奖励吧！',Speaker=894325,}",
  [173465] = "{id=173465,Text='11/1~12/1期间，普隆德拉奇珍店限时开启！坐骑、翅膀、时装，将以超低的价格在珍宝店内限时出售！',Speaker=894325,}",
  [173466] = "{id=173466,Text='通过[c][ffff00]热点[-][/c]按钮进入[c][ffff00]购物节[-][/c]开始选购吧！',Speaker=894325,}",
  [173472] = "{id=173472,Text='宝箱里藏着一张纸条，上面写着“前往南门击败1只波利”',}",
  [173473] = "{id=173473,Text='宝箱里藏着一张纸条，上面写着“前往南门击败1只疯兔”',}",
  [173474] = "{id=173474,Text='宝箱里藏着一张纸条，上面写着“前往西门击败1只溜溜猴”',}",
  [173475] = "{id=173475,Text='宝箱里藏着一张纸条，上面写着“前往西门击败1只蝗虫”',}",
  [173476] = "{id=173476,Text='宝箱里藏着一张纸条，上面写着“前往北门击败1只森灵”',}",
  [173477] = "{id=173477,Text='呀~被变身成了喵星巡逻队员~',}",
  [173478] = "{id=173478,Text='呀~被变身成了喵星强袭队员~',}",
  [173479] = "{id=173479,Text='呀~被变身成了喵星厨师长~',}",
  [173480] = "{id=173480,Text='呀~被变身成了喵星财务大臣~',}",
  [173481] = "{id=173481,Text='活动期间，每个周末，普隆德拉都会出现大量的宝箱，这些宝箱分为普通宝箱、惊奇宝箱和惊趣宝箱~',Speaker=2700,}",
  [173482] = "{id=173482,Text='不管是什么宝箱，打开之后必定可以获得[c][ffff00]宝箱碎片x1[-][/c]，如果你能给我3个宝箱碎片的话，我可以给你额外的奖励哦~',Speaker=2700,}",
  [173483] = "{id=173483,Text='那么，你已经收集到3个宝箱碎片了吗？',Speaker=2700,Option='{确定,4},{再想想,3}',}",
  [173484] = "{id=173484,Text='看起来你还没有准备好哦~',Speaker=2700,}",
  [173485] = "{id=173485,Text='不愧是你，那么，请收下我的奖励吧~！',Speaker=2700,}",
  [173513] = "{id=173513,Text='一年一度的万圣节，那可是属于我的节日！',Speaker=894335,}",
  [173514] = "{id=173514,Text='怎么样？我的新坐骑，还不错吧~',Speaker=894335,}",
  [173515] = "{id=173515,Text='嘿，你只要完成1次我的委托，我可以送你一个[c][ffff00]无头骑士·体验版[-][/c]作为报酬！如果你完成7次的话，可以额外获得1个[c][ffff00]无头骑士礼盒 [-][/c]哦~',Speaker=894335,Option='什么委托？',}",
  [173516] = "{id=173516,Text='我准备在[c][ffff00]尼夫海姆[-][/c]举办派对，可总有一些捣蛋幽灵偷偷溜了进来……',Speaker=894335,}",
  [173517] = "{id=173517,Text='这些捣蛋幽灵都很狡猾，需要使用[c][ffff00]幽灵相机[-][/c]才能找到他们！',Speaker=894335,}",
  [173518] = "{id=173518,Text='所以，我想委托你，前往尼夫海姆击败1个捣蛋幽灵！',Speaker=894335,Option='交给我吧！',}",
  [173519] = "{id=173519,Text='我可以把你传送过去，准备好了吗？',Speaker=894335,Option='嗯',}",
  [173520] = "{id=173520,Text='咦！这……也太快了！让我看看你的记录……唔，不愧是你，远远超过了要求的数量！',Speaker=894335,}",
  [173521] = "{id=173521,Text='这是你的报酬！',Speaker=894335,}",
  [173522] = "{id=173522,Text='你……是来帮忙的吗？',Speaker=894336,Option='帮忙？发生了什么事？',}",
  [173523] = "{id=173523,Text='我意外进入了一个梦境，醒来就来到了一个废弃的玩具工厂。在那里，我听到了许多玩具的故事……',Speaker=894336,}",
  [173524] = "{id=173524,Text='我走得太远了……在探索工厂的深处时，那些故事似乎触动了某种力量，我现在不能继续前进了。',Speaker=894336,}",
  [173525] = "{id=173525,Text='你能帮我去聆听那些未完的故事，了解他们的过去吗？',Speaker=894336,Option='{没问题,3},{有空再来,2}',}",
  [173526] = "{id=173526,Text='那好吧，我在这里等你哦。',Speaker=894336,}",
  [173527] = "{id=173527,Text='谢谢你……[c][ffff00][PlayerName][-][/c]。',Speaker=894336,Option='那天发生了什么？',}",
  [173528] = "{id=173528,Text='那天啊，其实我只是想出去找找灵感，新年快到了，我却陷入了瓶颈期……',Speaker=894336,}",
  [173529] = "{id=173529,Text='（自言自语）最近一直没灵感，或许该出去走走，透透气……',Speaker=894336,}",
  [173530] = "{id=173530,Text='森林那边的景色，说不定能带来些启发。',Speaker=894336,}",
  [173531] = "{id=173531,Text='或许雪景能带给我一些灵感？又或者是森林里的某个不经意的瞬间……',Speaker=894336,}",
  [173532] = "{id=173532,Text='如果能画出这片森林的神秘感就好了。',Speaker=894336,}",
  [173533] = "{id=173533,Text='就在这时，当卡不小心踩到一块冰，脚下一滑，整个人摔倒在雪地里。',Speaker=894345,}",
  [173534] = "{id=173534,Text='哎哟……！这是什么？',Speaker=894336,}",
  [173536] = "{id=173536,Text='身边有一个玩具小熊。它看起来破旧不堪，身上的绒布已经褪色，只有一颗亮晶晶的纽扣眼睛还闪着微弱的光。',Speaker=894345,}",
  [173537] = "{id=173537,Text='你怎么会在这里呢？这么冷的地方，也不知道是谁丢了你……',Speaker=894336,}",
  [173538] = "{id=173538,Text='当卡轻轻地拍掉小熊身上的雪，抱在怀里，觉得它虽然破旧，却带着一种让人安心的感觉。她靠在一棵松树下休息，没多久便在午后的阳光中睡着了。',Speaker=894345,}",
  [173540] = "{id=173540,Text='我看到房子高耸入云，屋顶上点缀着五彩斑斓的灯光，四周飘荡着欢快的音乐和笑声。无数玩具在这里愉快地工作和玩耍，到处充满了生机与欢乐。',Speaker=894336,}",
  [173541] = "{id=173541,Text='啊！刚刚那是什么……！',Speaker=894336,}",
  [173542] = "{id=173542,Text='我从没见过这么美妙的地方……如果我能把这一切画下来……',Speaker=894336,}",
  [173545] = "{id=173545,Text='唉，这一觉睡了好久啊……',Speaker=894348,}",
  [173546] = "{id=173546,Text='你……会说话？怎么会……',Speaker=894336,}",
  [173547] = "{id=173547,Text='是啊，但不是所有人都能听到的。',Speaker=894348,}",
  [173548] = "{id=173548,Text='只有心中怀有童话梦的孩子才能听到我说话。',Speaker=894348,}",
  [173549] = "{id=173549,Text='那刚刚的梦是……我从没见过这么美妙的地方……',Speaker=894336,}",
  [173550] = "{id=173550,Text='那是玩具工厂，我也来自那里，那里的曾经充满欢笑和幸福。',Speaker=894348,}",
  [173551] = "{id=173551,Text='但邪恶的力量侵袭了工厂，把它变成了一片废墟。曾经可爱的玩具们被黑暗腐蚀，变得疯狂而危险。',Speaker=894348,}",
  [173552] = "{id=173552,Text='邪恶的力量……到底发生了什么？',Speaker=894336,}",
  [173553] = "{id=173553,Text='（叹了一口气）这是很久以前的事了。那时工厂还很繁荣，我们每天都在欢笑中度过。可是突然间，一股不知从何而来的邪恶力量席卷了工厂，它腐蚀了我们的心灵。',Speaker=894348,}",
  [173554] = "{id=173554,Text='许多玩具因此变得充满攻击性，我们失去了原本的快乐。最终，姜饼城的守卫官为了保护人们，将工厂关闭，我们也从此被困在其中。',Speaker=894348,}",
  [173555] = "{id=173555,Text='太可怕了……我能帮上什么忙吗？',Speaker=894336,}",
  [173556] = "{id=173556,Text='或许你是不同的呢，我已经很久没有见过能听到我说话的孩子了。跟我来吧，我会带你去看看现在的工厂。',Speaker=894348,}",
  [173557] = "{id=173557,Text='之后我就跟着小熊，听到了许多故事，那些玩具……当你看到就会明白的。',Speaker=894336,}",
  [173558] = "{id=173558,Text='谢谢你愿意帮我…小熊会带你走完剩下的路。',Speaker=894336,}",
  [173559] = "{id=173559,Text='冒险者你来了！当卡好点了吗？',Speaker=894348,}",
  [173560] = "{id=173560,Text='那个小丑好像不太对劲，一会哭泣，一会又发出笑声，我们过去看看吧！',}",
  [173561] = "{id=173561,Text='一颗，两颗……嘿嘿……小熊！',Speaker=894342,}",
  [173562] = "{id=173562,Text='嘿嘿！我是玩具小丑啊，呜呜……哈哈！',Speaker=894342,Option='你怎么了？',}",
  [173563] = "{id=173563,Text='你知道吗……我曾经是最快乐的玩具。我总是带给露西笑声和快乐。那时候，我的帽子还很新，衣服上没有一丝皱褶。每当露西难过时，我会表演各种滑稽的动作，逗她开心。',Speaker=894342,Option='露西？',}",
  [173564] = "{id=173564,Text='（目光变得黯淡）露西……她是个善良的小女孩，我们是彼此最好的朋友。每当她感到难过或孤单时，我都会使出小丑的绝技，逗得她哈哈大笑。露西还会为我编织小帽子、小围巾，把我打扮得像一个小王子……',Speaker=894342,}",
  [173565] = "{id=173565,Text='可是，露西慢慢长大了……需要我的时间越来越少，她开始花更多时间在其他事情上。',Speaker=894342,}",
  [173566] = "{id=173566,Text='直到有一天，她告诉我她要离开了。她要去很远很远的地方，去做大人该做的事情。我很希望她能带上我，可是她只是对我笑了笑，挥了挥手，就那样转身离开了。',Speaker=894342,}",
  [173567] = "{id=173567,Text='日子一天天过去，我开始感到自己在消失。没有了露西的笑声，仿佛连我自己的存在也变得不再重要。',Speaker=894342,}",
  [173568] = "{id=173568,Text='后来呢，邪恶的力量入侵了工厂。你知道吗？它们带来了无尽的黑暗和痛苦。那些曾经欢乐的日子瞬间崩塌，工厂里的玩具们都开始变得奇怪和危险。连我，也没能幸免。',Speaker=894342,Option='邪恶的力量？',}",
  [173569] = "{id=173569,Text='终于消失了……当初，它们带着邪恶力量侵蚀了我，吞噬了我的快乐和笑声。它们放大了我心中的孤独与恐惧，让我变成了一个可怕的影子。',Speaker=894342,}",
  [173570] = "{id=173570,Text='我再也无法逗人发笑，甚至连自己的笑容都无法维持，我忍不住去伤害周围的人……不再是那个快乐的小丑。',Speaker=894342,}",
  [173571] = "{id=173571,Text='那么，现在的你……有什么愿望吗？',}",
  [173572] = "{id=173572,Text='（眼神中透露出一丝渴望）我希望……露西还能记得我。记得我们曾经的时光。我不奢望她回来，只希望她心中还有我这个朋友的影子。或许，那样我就能重新找回一些快乐。',Speaker=894342,}",
  [173573] = "{id=173573,Text='我会记住你的故事，让更多的人知道它。露西她也一定还记得你！',}",
  [173574] = "{id=173574,Text='谢谢你。能够被记住……对我来说，已经足够了。',Speaker=894342,}",
  [173575] = "{id=173575,Text='话音刚落，小丑的身影开始变得透明，小丑的眼中闪过一丝光芒，他微微笑了一下。',Speaker=894345,}",
  [173577] = "{id=173577,Text='（欢快地扑过来，不停打转，尾巴摇得飞快）你回来啦！你终于回来啦！我好想你！',Speaker=894343,}",
  [173578] = "{id=173578,Text='你是不是认错人了？我不是你的主人。',}",
  [173579] = "{id=173579,Text='你……不是我的主人吗？可是你看起来和他好像……。',Speaker=894343,}",
  [173580] = "{id=173580,Text='（声音中带着疑惑，但眼神中依然有一丝希望）你真的不是他？',Speaker=894343,}",
  [173581] = "{id=173581,Text='你一直在等你的主人吗？',}",
  [173582] = "{id=173582,Text='是的！我的主人是阿诺。他是个勇敢的探险家，梦想环游世界！我们总是在花园里探险，他说我是最棒的伙伴。每次他准备出发前，总会让我和他一起训练，我们奔跑、攀爬，像真正的探险家那样。',Speaker=894343,}",
  [173583] = "{id=173583,Text='阿诺总是对我说，‘贝贝，等我学会所有的探险技能，我们就去最远的地方，找到失落的宝藏！’他从不畏惧任何困难，而我也永远跟在他身边。无论他去哪里，我都愿意陪着他。',Speaker=894343,}",
  [173584] = "{id=173584,Text='你们一定经历了许多精彩的冒险。那后来呢？发生了什么？',}",
  [173585] = "{id=173585,Text='有一天，阿诺收到了一个神秘的邀请函。那是他梦寐以求的机会——一次真正的探险旅程。他告诉我，‘贝贝，这一次我必须独自去，这是我成为伟大探险家的机会。等我找到宝藏，一定会回来找你！',Speaker=894343,}",
  [173586] = "{id=173586,Text='（贝贝的目光开始黯淡，尾巴停止了摇动）阿诺带着背包和那张邀请函出发了，他答应我一定会回来。但是……好久好久啊。我一直在这里等他，盯着工厂的门口，等他推开门，带着宝藏和笑容回来找我。',Speaker=894343,}",
  [173587] = "{id=173587,Text='你一直在等他，是吗？',}",
  [173588] = "{id=173588,Text='是的，我知道阿诺不会忘记我。他一定还在外面冒险，只是还没有找到回来的路。',Speaker=894343,}",
  [173589] = "{id=173589,Text='贝贝，你等了多久了？',Speaker=894348,}",
  [173590] = "{id=173590,Text='我不知道……时间对我来说变得模糊了。那股黑暗的力量已经在我心里扎了根，它一直在低语，告诉我阿诺不会回来了……',Speaker=894343,}",
  [173591] = "{id=173591,Text='但每当我快要放弃时，总有个微弱的声音在说——阿诺会回来的。他答应过我，不会让我孤单……',Speaker=894343,}",
  [173592] = "{id=173592,Text='阿诺如果看到你还在等他，他一定会感动的。',}",
  [173593] = "{id=173593,Text='不，我不想让他难过……我只希望他开心。只要他回来，不管多久，我都会等着他。我们有过那么多美好的时光，我知道他一定会回来……',Speaker=894343,}",
  [173594] = "{id=173594,Text='贝贝，阿诺一定感受得到。',}",
  [173595] = "{id=173595,Text='谢谢你……只要能再看到阿诺的笑容，我就满足了。',Speaker=894343,}",
  [173596] = "{id=173596,Text='随着贝贝的话音逐渐消失，它的身影也慢慢变得透明，最终化作点点光芒',Speaker=894345,}",
  [173598] = "{id=173598,Text='滴滴答~你们是谁呀，是来看我跳舞的吗~',Speaker=894344,}",
  [173599] = "{id=173599,Text='故事？唔……我曾是艾米的芭蕾娃娃，她是一位梦想成为舞蹈家的小女孩。',Speaker=894344,}",
  [173600] = "{id=173600,Text='我们曾经一起跳舞。她牵着我的手，教我舞蹈的每一个动作，我也和她一起旋转，跳跃，仿佛我们就是舞台上的明星。',Speaker=894344,}",
  [173601] = "{id=173601,Text='你和艾米一定有过很美好的时光。那时候的你，一定很快乐吧？',}",
  [173602] = "{id=173602,Text='是的，我们每天都在一起跳舞。她总是会拿着我，教我转圈，她说我跳得比她还好。',Speaker=894344,}",
  [173603] = "{id=173603,Text='每当音乐响起，我就会自动旋转，仿佛与音乐融为一体。艾米总是微笑着看着我，眼中充满了欣喜与爱。',Speaker=894344,Option='那后来呢？',}",
  [173604] = "{id=173604,Text='（声音低沉下来，充满了悲伤）有一天，艾米病了。她的双腿再也无法跳舞，但她仍然会抱着我，听着那首熟悉的旋律。她说她会很快好起来，然后我们再一起跳舞。',Speaker=894344,}",
  [173605] = "{id=173605,Text='可……可是她没有好起来。我一遍遍地跳着她教给我的舞蹈，希望能让她开心，但她只是静静地看着我，越来越沉默。',Speaker=894344,}",
  [173606] = "{id=173606,Text='她的家人决定带她离开去治疗，她告诉我，‘等我回来，我们还会一起跳舞。’但她没有带走我，把我留在了她的房间里。',Speaker=894344,}",
  [173607] = "{id=173607,Text='……她一定很舍不得你。',}",
  [173608] = "{id=173608,Text='是的，但她离开后，我再也没有见过她。我站在她的房间里，日复一日地等她回来，练习她教给我的舞蹈。然后，邪恶的力量侵入了这座工厂。',Speaker=894344,}",
  [173609] = "{id=173609,Text='侵蚀了我，让我无法再旋转。我再也跳不出艾米教给我的舞蹈了。每次尝试跳舞，身体会发出刺耳的声响，关节卡住，动作变得僵硬。我变成了一个故障的芭蕾娃娃，失去了我的意义。',Speaker=894344,}",
  [173610] = "{id=173610,Text='艾米不会再想要我了。我已经失去了让她快乐的能力。',Speaker=894344,}",
  [173611] = "{id=173611,Text='你没有失去你的意义，你曾带给艾米无尽的快乐，那份回忆永远存在……你还记得她看着你旋转时的笑容，对吗？',}",
  [173612] = "{id=173612,Text='我记得……她的笑容，她眼中的光芒，那是我最美好的时光。',Speaker=894344,}",
  [173613] = "{id=173613,Text='那就把那份记忆留在心里，艾米对你的爱永远不会被抹去。',}",
  [173614] = "{id=173614,Text='或许你是对的，虽然我不能再跳舞，但我曾是她最珍爱的伙伴，这一点永远不会改变。',Speaker=894344,}",
  [173615] = "{id=173615,Text='芭蕾娃娃的身体开始发出柔和的光芒。她的关节不再咯吱作响，裙摆仿佛恢复了往日的光泽。她轻轻旋转了一圈，脸上浮现出一丝久违的微笑，',Speaker=894345,}",
  [173616] = "{id=173616,Text='谢谢你，[c][ffff00][PlayerName][-][/c]，你让我想起了我真正的意义。',Speaker=894344,}",
  [173618] = "{id=173618,Text='唉……玩具们的故事总是是这样，不知知道艾米现在有没有好起来……',Speaker=894348,}",
  [173619] = "{id=173619,Text='再往深处，是我也不能抵抗的邪恶力量。',Speaker=894348,}",
  [173620] = "{id=173620,Text='你回来了。工厂的情况如何？',Speaker=894336,}",
  [173621] = "{id=173621,Text='（叹气）玩具工厂……曾经是那么美好的地方。但现在，那里已经被邪恶的力量摧毁了。那些玩具……他们的故事让我感到悲伤。',}",
  [173622] = "{id=173622,Text='他们都告诉了你些什么？',Speaker=894336,}",
  [173623] = "{id=173623,Text='有一个孤独的小丑，曾经是充满欢笑的玩具。他一直在等待他的主人——露西，可她再也没有回来。邪恶的力量把他从内到外毁坏了，现在他只是一片孤独的影子。',}",
  [173624] = "{id=173624,Text='这座城市……也曾像那个小丑一样，被遗忘在时间的长河里。所幸，它还有重生的希望。',Speaker=894336,}",
  [173625] = "{id=173625,Text='还有贝贝，阿诺的玩具小狗。他们本来是形影不离的好朋友，可阿诺不得不离开他。贝贝因为失去了主人的陪伴，被邪恶力量侵蚀，永远在工厂里徘徊，失去了曾经的欢快和温暖。',}",
  [173626] = "{id=173626,Text='那些玩具们，本不该承受这样的痛苦。',Speaker=894336,}",
  [173627] = "{id=173627,Text='还有芭蕾娃娃，她是艾米最珍爱的玩具。艾米生病了，再也不能跳舞，最后不得不离开。而芭蕾娃娃的机械装置被邪恶力量破坏了，她再也不能优雅地旋转了……她的心碎了。',}",
  [173628] = "{id=173628,Text='这些玩具曾是工厂的灵魂，它们承载了无数孩子的梦想。而当邪恶力量侵蚀了工厂，不仅破坏了它们的身体，也摧毁了它们的心灵。',Speaker=894336,}",
  [173629] = "{id=173629,Text='我不能让这些故事被遗忘。我会画下他们的故事，给他们画出一个圆满的可能性。我希望通过这幅画，让人们记住他们，这样他们的故事就不会白白消失。',Speaker=894336,}",
  [173630] = "{id=173630,Text='来吧，[c][ffff00][PlayerName][-][/c]，让我们一起创造这幅画吧！',Speaker=894336,}",
  [173631] = "{id=173631,Text='姜饼城是个充满奇迹的地方……也许在这里，一切真的都有可能。',Speaker=894336,}",
  [173632] = "{id=173632,Text='这幅画送给你，冒险者，很高兴认识你，愿你也像这幅画一样快乐~',Speaker=894336,}",
  [173633] = "{id=173633,Text='嘿！你来了！你知道吗？每年的深冬，村庄中央的雪人都会获得短暂的生命。',Speaker=894339,}",
  [173634] = "{id=173634,Text='雪人今年有个特殊的愿望，它……想要飞翔！听上去是不是很疯狂？但我想试试看！',Speaker=894339,}",
  [173635] = "{id=173635,Text='我决定研制一款小飞行器，但问题是，时间不多了，还差一些关键的材料。你愿意帮我一把吗？只要找齐材料，我就能继续制作，帮雪人实现它的梦想！',Speaker=894339,}",
  [173636] = "{id=173636,Text='雪人会感激你的——我也是！来吧，圣诞一切都有可能实现！',Speaker=894339,}",
  [173637] = "{id=173637,Text='又是一个消失的周末……',Speaker=894341,}",
  [173638] = "{id=173638,Text='哦，你来得正是时候！出现了一些小麻烦……',Speaker=894341,}",
  [173639] = "{id=173639,Text='每年这个时候，大家都会互相赠送‘平安果’，象征着平安和幸福。但今年，一到周末，庆典准备的平安果就不见了一大批！',Speaker=894341,}",
  [173640] = "{id=173640,Text='我们调查了一下，原来是那些没有收到平安果的魔物干的。它们看到大家庆祝，觉得自己被落下了，就偷走了苹果！你能帮我个忙，去击败这些捣蛋的魔物，找回平安果吗？',Speaker=894341,Option='没问题！',}",
  [173641] = "{id=173641,Text='太好了！据我所知，他们应该会在周六周日16点 ~ 24点出现在玩具工厂附近！期待你的好消息~',Speaker=894341,}",
  [173642] = "{id=173642,Text='真是太感谢了，你真的找回了这些平安果！',Speaker=894341,}",
  [173643] = "{id=173643,Text='这是我的小小谢意，希望你喜欢。谢谢你让姜饼城的圣诞节再次充满欢乐和平安！',Speaker=894341,}",
  [173644] = "{id=173644,Text='姜饼城有一位小画家「当卡」，外出寻找灵感时，意外捡回了一个破损的玩具小熊。当晚，她做了一个梦：在姜饼城的深处，有一座繁华的玩具工厂，那里有许多玩具小熊和各式童话角色……和「当卡」一起探寻玩具工厂过去的故事，共同绘出梦中的旧日乐园吧！',Speaker=894337,}",
  [173645] = "{id=173645,Text='12/1~1/1期间，Base等级≥25级的冒险者前往姜饼城找到找到「当卡」即可参与活动',Speaker=894337,}",
  [173646] = "{id=173646,Text='共5个连续的任务，完成1~4任务，均可获得Zeny x 150,000，「平安果的祝福」x1、「B格猫福利券·宴」x6；完成第5个任务，可获得Zeny x 300,000，「平安果的祝福」x3、「B格猫福利券·宴」x6、「奋进礼盒」x1',Speaker=894337,}",
  [173647] = "{id=173647,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894337,}",
  [173648] = "{id=173648,Text='该活动为帐号限定类任务哦~',Speaker=894337,}",
  [173649] = "{id=173649,Text='就让我们采摘一些新鲜的冰霜葡萄交给姜饼城的孩子们酿成美酒吧！这可是圣诞夫人和圣诞老人婚礼上共饮的诗之美酒的原料噢。~',Speaker=894337,}",
  [173650] = "{id=173650,Text='12/9~1.1期间，Base等级≥25级的冒险家，冒险者前往姜饼城，找到「小饼饼人」即可参与活动，冒险者需要根据提示前往指定地图击败指定魔物获得「冰霜种子」',Speaker=894337,}",
  [173651] = "{id=173651,Text='共5个连续的任务，完成第1--4个任务，均可获得Zeny x 150,000，「平安果的祝福」x1',Speaker=894337,}",
  [173652] = "{id=173652,Text='完成第5个任务，可以获得Zeny x 300,000，「平安果的祝福」x3，「奋进礼盒」x1',Speaker=894337,}",
  [173653] = "{id=173653,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894337,}",
  [173655] = "{id=173655,Text='每年的深冬，村庄中央的雪人都会获得短暂的生命。这一年的圣诞夜，雪人有一个心愿：它想要飞翔。机械小工「泉尔」决定研制一款飞行器，来帮助雪人实现愿望，但距离圣诞已经没有几天了，去给泉尔提供一些材料吧，雪人也会感激你的~',Speaker=894337,}",
  [173656] = "{id=173656,Text='12/16~ ~ 12/23期间，Base等级≥25级的冒险者前往姜饼城找到「泉尔」即可参与活动',Speaker=894337,}",
  [173657] = "{id=173657,Text='活动期间，冒险者前往姜饼城找到「泉尔」，即可使用多余材料进行捐献',Speaker=894337,}",
  [173658] = "{id=173658,Text='每次捐献可以获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=894337,}",
  [173660] = "{id=173660,Text='在姜饼城的传统中，每年圣诞，居民们都会将一种名为“平安果”的苹果送给彼此，象征着平安和幸福。然而，今年却出现了意外——每到周末，镇上用于庆典的平安果就会消失一大批。原来是没有收到平安果的魔物们在捣乱，快去击败他们，追回平安果吧！',Speaker=894337,}",
  [173661] = "{id=173661,Text='12/1~1/1期间，每周六、周日晚上16~24点，偷走了平安果的魔物会出现在玩具工厂，击败即有概率获得精美时装、符文等奖励哦~',Speaker=894337,}",
  [173662] = "{id=173662,Text='圣诞节到啦~为了感谢大家所做的一切，圣诞老人给大家准备了三重大礼哦~各位冒险者不要错过~',Speaker=894337,}",
  [173663] = "{id=173663,Text='12/24 ~ 12/31期间，Base等级≥25级的冒险者前往姜饼城，找到「圣诞老人」即可参与活动',Speaker=894337,}",
  [173664] = "{id=173664,Text='圣诞礼物第一重：12.24--1.1期间，圣诞老人讲在姜饼城撒下大量礼盒，冒险者可以前往拾取，每次拾取可以获得以下奖励中的一种：「B格猫福利券·宴」x5、「莫拉金币」x5、「祈祷晶片包」x5、「B格猫福利券·Ⅰ」x5、「拾光邀请函」x5',Speaker=894337,}",
  [173665] = "{id=173665,Text='圣诞礼物第二重：12.24--1.1期间，冒险者可以在姜饼城找到【圣诞老人】领取圣诞礼物哦~',Speaker=894337,}",
  [173666] = "{id=173666,Text='圣诞礼物第三重：12.24--1.1期间，圣诞老人将会快递一份特殊的礼物给冒险者，冒险者记得回【家园】接收哦~',Speaker=894337,}",
  [173671] = "{id=173671,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894337,}",
  [173672] = "{id=173672,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894337,}",
  [173673] = "{id=173673,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【Cybee】[-][/c]绘制的共创头像！',Speaker=894337,Option='哇！',}",
  [173674] = "{id=173674,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894337,Option='谢谢',}",
  [173677] = "{id=173677,Text='唉……邪恶的力量太可恶了，冒险者你也要小心点哦！',Speaker=894348,}",
  [173678] = "{id=173678,Text='咳咳……我……我感觉到那些黑影又来了。',Speaker=894342,}",
  [173679] = "{id=173679,Text='它们是邪恶力量的一部分，每当我想起露西，它们就会出现，扰乱我的记忆，让我无法继续讲述。我害怕它们会吞噬我那些珍贵的回忆。',Speaker=894342,Option='别担心',}",
  [173680] = "{id=173680,Text='[c][ffff00][PlayerName][-][/c]，你来啦！今天我们继续往前走走吧！',Speaker=894348,}",
  [173681] = "{id=173681,Text='后来呢……嗯？我好像想不起来了，阿诺每次出门时，我都会把一块最喜欢的骨头藏起来，等阿诺回来我们再一起挖出来……',Speaker=894343,}",
  [173682] = "{id=173682,Text='骨头……对了，骨头！这次阿诺离开的太久了，我已经快要忘记那块骨头藏在哪里了……',Speaker=894343,}",
  [173683] = "{id=173683,Text='我记得好像在一棵圣诞树下，但是这里长得像的树太多了……你可以帮我找找吗？',Speaker=894343,Option='没问题！',}",
  [173684] = "{id=173684,Text='谢谢你！！居然真的找到了！这块骨头对我来说，就像是阿诺又回来了，又一次帮我挖出了藏起来的骨头。',Speaker=894343,}",
  [173685] = "{id=173685,Text='这一定就是那块骨头了！快交给贝贝吧~',}",
  [173686] = "{id=173686,Text='冒险者，你可以帮我拍张照吗？',Speaker=894344,}",
  [173687] = "{id=173687,Text='就当留个纪念，如果……我是说如果……你见到艾米的话，帮我交给她吧。',Speaker=894344,}",
  [173688] = "{id=173688,Text='就走到这里吧，谢谢你，愿意听听我们的故事~',Speaker=894348,}",
  [173693] = "{id=173693,Text='最近出现了一些令人头疼的魔物，他们趁着庆典，偷走了原本为庆典准备的宝藏',Speaker=893874,}",
  [173694] = "{id=173694,Text='由于我实在抽不开身，[c][ffff00][PlayerName][-][/c]你能帮我走一趟吗？',Speaker=893874,}",
  [173695] = "{id=173695,Text='先去南门看看吧，击败变异的摇滚蝗虫，把宝藏拿回来给我~',Speaker=893874,}",
  [173696] = "{id=173696,Text='那么，[c][ffff00][PlayerName][-][/c]，准备好了吗？',Speaker=893874,Option='准备好了！',}",
  [173697] = "{id=173697,Text='出发！',Speaker=893874,}",
  [173698] = "{id=173698,Text='这就是宝藏，快拿回去给时空龙吧~',}",
  [173699] = "{id=173699,Text='做的很好！大家一定会记住你的！',Speaker=893874,}",
  [173700] = "{id=173700,Text='明天我也在这等你，记得来找我！',Speaker=893874,}",
  [173701] = "{id=173701,Text='今天去普隆德拉西门看看吧，变异的海盗之王可是个难缠的家伙！',Speaker=893874,}",
  [173702] = "{id=173702,Text='这个家伙可是个惯犯了！',Speaker=893874,}",
  [173703] = "{id=173703,Text='希望经过这次的教训，他可以改邪归正。',Speaker=893874,}",
  [173704] = "{id=173704,Text='好啦！我们出发吧！',Speaker=893874,}",
  [173705] = "{id=173705,Text='这就是宝藏，快拿回去给时空龙吧~',}",
  [173706] = "{id=173706,Text='好样的，这是你的酬劳！',Speaker=893874,}",
  [173707] = "{id=173707,Text='明天我也在这等你，记得来找我！',Speaker=893874,}",
  [173708] = "{id=173708,Text='变异的流浪之狼出现在了梦罗克城外！',Speaker=893874,}",
  [173709] = "{id=173709,Text='抓住这个机会，去把宝藏夺回来吧！',Speaker=893874,}",
  [173710] = "{id=173710,Text='要小心它锋利的爪牙，被它咬到可不是开玩笑的！',Speaker=893874,}",
  [173711] = "{id=173711,Text='那么，祝你好运~！',Speaker=893874,}",
  [173712] = "{id=173712,Text='这就是宝藏，快拿回去给时空龙吧~',}",
  [173713] = "{id=173713,Text='真不错呀，竟然毫发无伤地就把宝藏带回来了！',Speaker=893874,}",
  [173714] = "{id=173714,Text='明天我也在这等你，记得来找我！',Speaker=893874,}",
  [173715] = "{id=173715,Text='变异的蜂后正带着宝藏四处逃窜！',Speaker=893874,}",
  [173716] = "{id=173716,Text='听说她刚才出现在了普隆德拉北部。',Speaker=893874,}",
  [173717] = "{id=173717,Text='你赶紧去那边看看吧，万一她又逃走了，可不容易跟上。',Speaker=893874,}",
  [173718] = "{id=173718,Text='那就赶紧出发吧~！',Speaker=893874,}",
  [173719] = "{id=173719,Text='这就是宝藏，快拿回去给时空龙吧~',}",
  [173720] = "{id=173720,Text='想不到你的速度也是一流的！',Speaker=893874,}",
  [173721] = "{id=173721,Text='明天我也在这等你，记得来找我！',Speaker=893874,}",
  [173722] = "{id=173722,Text='只剩最后一个宝藏了！',Speaker=893874,}",
  [173723] = "{id=173723,Text='去斐扬树林看看吧，变异的枯树精也是个惯犯，可能在他身上可以发现一些什么~',Speaker=893874,Option='出发！',}",
  [173724] = "{id=173724,Text='这就是宝藏，快拿回去给时空龙吧~',}",
  [173725] = "{id=173725,Text='多亏有你，才能让这庆典继续举行！',Speaker=893874,}",
  [173726] = "{id=173726,Text='为了庆祝这盛大的庆典，B格猫星人托我给你带了礼物~',Speaker=893874,}",
  [173727] = "{id=173727,Text='从今天开始，你每天都可以来找我领取[c][ffff00]B格猫福利券·宴x30[-][/c]哦，一共可以领取10次，也就是300张~如果错过当天，就会损失30张哦~',Speaker=893874,}",
  [173728] = "{id=173728,Text='希望你拿着这些券券，可以抽到你喜欢的外观~',Speaker=893874,}",
  [173729] = "{id=173729,Text='这是今天的奖励，请收好~',Speaker=893874,}",
  [173733] = "{id=173733,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894351,}",
  [173734] = "{id=173734,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894351,}",
  [173735] = "{id=173735,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【颛颛】[-][/c]创作的共创头饰！',Speaker=894351,Option='哇！',}",
  [173736] = "{id=173736,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的内容与你相逢哦~敬请期待吧~',Speaker=894351,Option='谢谢',}",
  [173737] = "{id=173737,Text='一月是万象更新的日子！每个可爱的冒险者都将收到一封特别的[c][ffff00]明信片[-][/c]！',Speaker=894351,}",
  [173738] = "{id=173738,Text='[c][ffff00][PlayerName][-][/c]，别愣着哦~快收下啦~',Speaker=894351,Option='打开',}",
  [173739] = "{id=173739,Text='庆典的成功举办，离不开各位冒险者的帮助哦~',Speaker=894350,}",
  [173740] = "{id=173740,Text='如果你有多余的材料，欢迎来找我兑换礼物哦~',Speaker=894350,}",
  [173741] = "{id=173741,Text='通过完成各类活跃活动，可以获得大量的「庆典纪念券」，在庆典商店中，冒险者可以消耗「庆典纪念券」兑换各类庆典外观~',Speaker=894349,}",
  [173742] = "{id=173742,Text='在今年的皇家庆典中，冒险者可以通过「头饰挑战」活动获得「精炼+15头饰券」；通过「庆典冒险录」获得「装备精炼+15自选券」~',Speaker=894349,}",
  [173743] = "{id=173743,Text='同时，活动期间登录，还可以获得「B格猫福利券·宴」x300的福利~',Speaker=894349,}",
  [173744] = "{id=173744,Text='更多福利，等待冒险者自己去发现哦~',Speaker=894349,}",
  [173745] = "{id=173745,Text='庆典活动期间，野外怪物都将掉落「HAPPY礼盒」，打开之后可以随机获得一个字母',Speaker=894349,}",
  [173746] = "{id=173746,Text='集齐HAPPY五个字母，可以在活动中免费兑换周年庆限定翅膀外观哦~',Speaker=894349,}",
  [173747] = "{id=173747,Text='同时，「庆典冒险录」中还投放了「HAPPY自选礼盒」，帮助各位冒险者更快获得奖励~',Speaker=894349,}",
  [173748] = "{id=173748,Text='1/1~1/15期间，冒险者可以前往南门找到许愿树许下心愿',Speaker=894349,}",
  [173749] = "{id=173749,Text='1月19日的周年庆直播中，将进行抽奖活动，只要被抽中，就可以满足你的愿望哦~',Speaker=894349,}",
  [173754] = "{id=173754,Text='又到了每年给冒险者派发压岁钱的日子了~',Speaker=893145,}",
  [173755] = "{id=173755,Text='今年我给冒险者准备了不一样的压岁钱哦~',Speaker=893145,}",
  [173756] = "{id=173756,Text='今年的压岁钱一共可以打开十次，每次都可以获得上一次双倍的奖励，希望冒险者在新的一年里，十全十美，福禄双至~！',Speaker=893145,}",
  [173757] = "{id=173757,Text='新一年的起点，我想拜托冒险者去看看那些伟大的先辈们~',Speaker=893145,}",
  [173758] = "{id=173758,Text='如果没有他们，也不会有现在的米德加尔特大陆~',Speaker=893145,}",
  [173759] = "{id=173759,Text='米依花代表着尊敬和思念，让他们知道，我们每个人都还想着他们~！',Speaker=893145,}",
  [173760] = "{id=173760,Text='准备好了，我就送你过去了哦~',Speaker=893145,}",
  [173765] = "{id=173765,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894355,}",
  [173766] = "{id=173766,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894355,}",
  [173767] = "{id=173767,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【KAYIN】[-][/c]绘制的共创头像！',Speaker=894355,Option='哇！',}",
  [173768] = "{id=173768,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894355,Option='谢谢',}",
  [173769] = "{id=173769,Text='欢迎，年轻的冒险者！在这浪漫的季节里，我的枝叶再次苏醒，感受到了你们心中的愿望。',Speaker=894359,}",
  [173770] = "{id=173770,Text='愿望？难道……一千只纸鹤的愿望是真的吗？',}",
  [173771] = "{id=173771,Text='一千只纸鹤？哈哈哈……那是个美丽的传说，传说一千只纸鹤能换一个愿望，怎么说呢……也不算错，但那是老头子我神力最盛的时候',Speaker=894359,}",
  [173772] = "{id=173772,Text='事实上，一千只纸鹤象征的，是折纸时那份发自内心的爱意。正是这样的心念，让纸鹤拥有真正的力量。',Speaker=894359,Option='……现在呢',}",
  [173773] = "{id=173773,Text='哎呀！来都来了，[c][ffff00][PlayerName][-][/c]，能不能帮我找些材料回来？说不定修复好，我的力量还能恢复一些呢~',Speaker=894359,}",
  [173774] = "{id=173774,Text='当然了，报酬不会少了你的~',Speaker=894359,}",
  [173784] = "{id=173784,Text='你……你知道这是哪里吗……',Speaker=894356,}",
  [173785] = "{id=173785,Text='眼前神秘的人晃了晃头，看起来不太清醒的样子。',Speaker=894345,}",
  [173786] = "{id=173786,Text='我的头……好痛……我怎么会在这里呢？',Speaker=894356,}",
  [173787] = "{id=173787,Text='你记不清了？那你还记得怎么来到这里的吗？',}",
  [173788] = "{id=173788,Text='（微微摇头）不太记得了，我感觉自己在寻找什么，或者是在等什么人，但除此之外，脑海里一片模糊。',Speaker=894356,}",
  [173789] = "{id=173789,Text='你能帮帮我么！我知道这要求有些突然，但……我真的感觉到失去了什么重要的东西。[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=894356,Option='{没问题,3},{现在还有事,2}',}",
  [173790] = "{id=173790,Text='好叭……那我再想想……',Speaker=894356,}",
  [173791] = "{id=173791,Text='你人真好！你是这里的居民吗……',Speaker=894356,}",
  [173792] = "{id=173792,Text='……嗯？',Speaker=894356,}",
  [173793] = "{id=173793,Text='莫娜瓦忽然转过身，面向大海，仿佛在倾听着什么',Speaker=894345,}",
  [173794] = "{id=173794,Text='你有没有听到什么声音？',Speaker=894356,}",
  [173795] = "{id=173795,Text='我好像听到有人在喊我的名字……是一个很熟悉的声音……',Speaker=894356,}",
  [173796] = "{id=173796,Text='我什么都没有听到，你还好吗？',}",
  [173797] = "{id=173797,Text='为什么……我感觉到很悲伤，好像心里缺失了什么重要的东西。可我却怎么也想不起来……',Speaker=894356,}",
  [173798] = "{id=173798,Text='也许我们可以找人问问，先四处打听一下吧，说不定会找到什么线索。',}",
  [173799] = "{id=173799,Text='那太好了，有你帮忙的话，也许我很快就能找回记忆了！',Speaker=894356,}",
  [173800] = "{id=173800,Text='失忆了？抱歉，我从没见过她……',Speaker=894365,}",
  [173801] = "{id=173801,Text='不过这里常有远方的旅客停留，或许是个外乡人？',Speaker=894365,}",
  [173802] = "{id=173802,Text='唔，她的样子确实特别，是人鱼族吧！',Speaker=894366,}",
  [173803] = "{id=173803,Text='可我不记得见过她啊。',Speaker=894366,}",
  [173804] = "{id=173804,Text='看来还得继续打听打听。',}",
  [173805] = "{id=173805,Text='失忆？这倒让我想起了一个人……或许他能帮上你们的忙。',Speaker=894367,Option='是谁？',}",
  [173807] = "{id=173807,Text='他叫伊塞尔，是个有些年纪的隐士，住在普隆德拉城郊的树林里。',Speaker=894367,}",
  [173808] = "{id=173808,Text='据说他精通许多古老的法术，帮过不少迷失自我的人。如果有谁能帮你唤醒记忆，那就非他莫属了。',Speaker=894367,}",
  [173809] = "{id=173809,Text='不过他性格有点古怪……你们去试试吧，说不定能有办法呢！',Speaker=894367,}",
  [173810] = "{id=173810,Text='真的吗？太好了，谢谢你！',Speaker=894356,}",
  [173811] = "{id=173811,Text='[c][ffff00][PlayerName][-][/c]，那我们去找这位伊塞尔先生吧，说不定他能有些办法呢！',Speaker=894356,Option='好呀！',}",
  [173812] = "{id=173812,Text='嗯？是谁……你们找我有事？',Speaker=894363,Option='说明缘由',}",
  [173813] = "{id=173813,Text='（观察着莫娜瓦，略微点头） 你身上的气息，确实不同寻常……',Speaker=894363,}",
  [173814] = "{id=173814,Text='你是人鱼族吧，但不像我以前见过的那些……你还记得家在哪里吗？',Speaker=894363,}",
  [173815] = "{id=173815,Text='不记得了……只有一些片段，我看到边界之外总是被风暴笼罩着……',Speaker=894356,}",
  [173816] = "{id=173816,Text='人鱼族、总是被风暴笼罩着？嗯……我想想……',Speaker=894363,}",
  [173817] = "{id=173817,Text='原来那本书里记载的是真的！',Speaker=894363,}",
  [173818] = "{id=173818,Text='“风暴无尽，生死难求，诅咒和痛苦相伴，珍珠终致毁灭……”',Speaker=894363,}",
  [173819] = "{id=173819,Text='这是传说里的地方……有没有听说过，人鱼的眼泪能够化成珍珠，但是我从没有真的见到过。',Speaker=894363,}",
  [173820] = "{id=173820,Text='嗯……好像是？我记不清了……',Speaker=894356,}",
  [173821] = "{id=173821,Text='你身上的气息带着不祥的味道，这更像是一种诅咒，或者说，宝藏本身没错，但身怀宝藏总会招来灾难，这更像是……黑魔法的印记。',Speaker=894363,}",
  [173823] = "{id=173823,Text='黑魔法？那有办法解开它吗？',}",
  [173824] = "{id=173824,Text='办法是有的，有一种古老的仪式，能够解开黑魔法的印记，仪式并不复杂，只是需要的材料有些困难……',Speaker=894363,}",
  [173825] = "{id=173825,Text='海底神殿外有一座不起眼的神碑，在神碑旁的融合三样特定的材料，就能链接深渊的力量，帮助你唤起灵魂中尘封的记忆',Speaker=894363,}",
  [173826] = "{id=173826,Text='那三样材料……是什么？',Speaker=894356,}",
  [173827] = "{id=173827,Text='需要你的一滴眼泪，一滴尾尖血，以及一种稀有的物质——“梦境回响”',Speaker=894363,Option='梦境回响？',}",
  [173828] = "{id=173828,Text='梦境回响来自一种名为噬梦魔的生物，只有击败它，才能从它体内提取出这种稀有的物质。梦境回响蕴含无数梦境的回声，是打破灵魂封锁的关键。',Speaker=894363,}",
  [173829] = "{id=173829,Text='眼泪、尾尖血……和梦境回响……好，我们会努力找到它们。',}",
  [173830] = "{id=173830,Text='（微微点头）记住，一旦回溯记忆，不仅会唤回美好的片段，也可能触及深埋的痛苦……你要做好心理准备。',Speaker=894363,}",
  [173831] = "{id=173831,Text='我想想……应该怎么办……',Speaker=894363,}",
  [173832] = "{id=173832,Text='谢谢您，我感受到遗忘了重要的东西，是一些即便痛苦也不能忘记的……我一定会找回它们的！',Speaker=894356,}",
  [173833] = "{id=173833,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=894356,}",
  [173834] = "{id=173834,Text='我们去寻找噬梦魔吧。',Speaker=894356,Option='出发！',}",
  [173835] = "{id=173835,Text='前面看起来就是噬梦魔的洞穴了，小心！',Speaker=894356,}",
  [173836] = "{id=173836,Text='我来自一个被风暴环绕的海底家园，那里是人鱼族世代守护的地方。我们宁静地生活在其中，世代守护着这个与世隔绝的秘境。',Speaker=894356,}",
  [173837] = "{id=173837,Text='原来，那个呼唤我的人是他啊……',Speaker=894356,Option='他？',}",
  [173838] = "{id=173838,Text='他啊……他叫布瑞夫，是我心中最温柔，也最勇敢的人，小时候他就常说，要成为我的依靠，永远守护我。',Speaker=894356,}",
  [173841] = "{id=173841,Text='贪婪与邪恶破坏了一切。是一个外来者，他闯入了我们风暴守护的海域，带来了毁灭的力量。',Speaker=894356,}",
  [173842] = "{id=173842,Text='莫娜瓦的表情逐渐变得痛苦，语气中带着深深的伤感。',Speaker=894345,}",
  [173843] = "{id=173843,Text='那夜雷声滚滚，风暴在海域肆虐。我们隐秘的海底世界似乎被撕裂了，一个外来的海盗团闯了进来，他们的头领……叫戴沃。',Speaker=894356,}",
  [173845] = "{id=173845,Text='莫娜瓦！小心，这些是什么！',}",
  [173846] = "{id=173846,Text='他带着他的同伙，带着武器和黑魔法，闯入了我们的家园。大火吞噬了宫殿，族人四散逃亡，根本无法抵挡那些入侵者。',Speaker=894356,Option='太可恶了！',}",
  [173847] = "{id=173847,Text='就在莫娜瓦深陷记忆的痛苦时，周围忽然出现了扭曲的幻影。玩家和莫娜瓦的四周被黑暗笼罩，幻境中的戴沃带领着幽灵军团向他们逼近，眼中透出贪婪与恶意。',Speaker=894345,}",
  [173848] = "{id=173848,Text='（她的声音渐渐哽咽）我永远不会忘记，他微笑着对我说‘莫娜瓦，不用害怕，有我在’，然后毫不犹豫地挡住了戴沃的利刃。',Speaker=894356,}",
  [173849] = "{id=173849,Text='他用尽最后的力量救了我，而我却只能眼睁睁看着他倒下……',Speaker=894356,Option='唉……',}",
  [173850] = "{id=173850,Text='我的泪水洒在他身上，化作一颗颗珍珠。那是我们人鱼的诅咒——悲伤的泪水会凝成珍珠。而戴沃，那个贪婪的海盗，他见到珍珠后，命令手下将我带走，试图从我的悲伤中获取更多珍珠。',Speaker=894356,}",
  [173852] = "{id=173852,Text='莫娜瓦！别想逃出我的掌心！',Speaker=894358,}",
  [173854] = "{id=173854,Text='让我们把这三样材料融合起来吧。',Speaker=894356,}",
  [173855] = "{id=173855,Text='莫娜瓦……你还好吗？',}",
  [173856] = "{id=173856,Text='我、我没事……我已经想起了很多事情了……',Speaker=894356,Option='太好了！',}",
  [173862] = "{id=173862,Text='情人节就要来了，「爱情天使」降临普隆德拉，她想要看看米德加尔特大陆上的人是怎么庆祝情人节的，冒险者去拍几张照片帮她了解一下风土人情吧~！',Speaker=894355,}",
  [173863] = "{id=173863,Text='2.1-3.1期间，Base等级≥25级的冒险者前往普隆德拉，找到「爱情天使」即可参与活动',Speaker=894355,}",
  [173864] = "{id=173864,Text='冒险者每天将前往各大约会圣地进行拍照，该任务一共可以完成5次，完成前4次均可获得Zeny x 150,000，「滴水珍珠」x1；完成第5次任务，可以获得Zeny x 300,000，「滴水珍珠」x3、「奋进礼盒」x1',Speaker=894355,}",
  [173865] = "{id=173865,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894355,}",
  [173866] = "{id=173866,Text='该活动为帐号限定类任务哦~',Speaker=894355,}",
  [173867] = "{id=173867,Text='在古老的传说中，人鱼的眼泪会结成珍珠……在艾尔贝塔的港口，最近出现了一个神秘的女人，她远远眺望大海，仿佛在等待着什么，去看看到底发生了什么吧~',Speaker=894355,}",
  [173868] = "{id=173868,Text='2.10-3.1期间，Base等级≥25级的冒险家，前往艾尔贝塔找到「莫娜瓦」，接取系列任务',Speaker=894355,}",
  [173869] = "{id=173869,Text='共5个连续的任务，完成第1--4个任务，均可获得Zeny x 150,000，「滴水珍珠」x1、「B格猫福利券·宴」x6',Speaker=894355,}",
  [173870] = "{id=173870,Text='完成第5个任务，可以获得Zeny x 300,000，「滴水珍珠」x3、「B格猫福利券·宴」x6、「奋进礼盒」x1',Speaker=894355,}",
  [173871] = "{id=173871,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894355,}",
  [173872] = "{id=173872,Text='该活动为帐号限定类任务哦~',Speaker=894355,}",
  [173873] = "{id=173873,Text='一千只纸鹤交换一个心愿，飞向手中的千纸鹤藏着谁的期许，打开它吧，看看那份隐在折痕中的爱意，是否正是你渴望听到的答案~',Speaker=894355,}",
  [173874] = "{id=173874,Text='2.17--2.24期间，冒险者前往普隆德拉南门找到「心愿之树」，即可使用多余材料进行捐献',Speaker=894355,}",
  [173875] = "{id=173875,Text='每次捐献可以获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=894355,}",
  [173876] = "{id=173876,Text='在这甜蜜的季节，守护愿望的小天使「露米艾尔」，跟随爱情天使一同来到普隆德拉。她持有神秘的塔罗牌，等待那些渴望知道未来的人前来占卜。快去试试吧，塔罗牌的预言将为你指引爱的方向~',Speaker=894355,}",
  [173877] = "{id=173877,Text='2.24--3.1期间，Base等级≥25级的冒险家，前往普隆德拉找到小天使「露米艾尔」，完成「恋爱占卜」即可获得「小天使的祝福」x1',Speaker=894355,}",
  [173878] = "{id=173878,Text='完成「恋爱占卜」可以获得Zeny x 150,000，「人鱼的眼泪」x1，并且随机获得增益效果，增益效果持续12小时',Speaker=894355,}",
  [173879] = "{id=173879,Text='曾经追捕过人鱼的海盗似乎搜寻到了线索，开始蠢蠢欲动，想要试图跨越海域，抓回莫娜瓦。为了保护自己，也为了不让无辜的人受到牵连，莫娜瓦请求勇敢的冒险者们前往岸边，阻止亡灵海盗靠近！',Speaker=894355,}",
  [173880] = "{id=173880,Text='2.1-3.1期间，每周六、周日晚上16~24点，海盗亡灵的力量会得到短暂的增强，他们的幽灵船会从深海中浮现，来到岸上，击败即有概率获得精美时装、符文等奖励哦~',Speaker=894355,}",
  [173881] = "{id=173881,Text='这里的雾气……有种奇异的感觉，太古怪了……',Speaker=894356,}",
  [173882] = "{id=173882,Text='小心点，伊塞尔说噬梦魔十分强大，我们必须谨慎行事。',Speaker=894356,}",
  [173883] = "{id=173883,Text='快看！那是什么！',Speaker=894356,}",
  [173884] = "{id=173884,Text='渺小的生命……来到我的梦境之地，想要做什么？',Speaker=894368,}",
  [173885] = "{id=173885,Text='我们无意冒犯，只想从你那里取一些“梦境回响”，它对我们有非常重要的意义。',}",
  [173886] = "{id=173886,Text='梦境回响……那可是我最珍贵的财富。你们以为自己配得上它？',Speaker=894368,}",
  [173887] = "{id=173887,Text='配不配得上，不如你试试看！',}",
  [173889] = "{id=173889,Text='太好了，有了它，我们就离找回记忆更近了一步。',Speaker=894356,}",
  [173890] = "{id=173890,Text='这里就是伊塞尔说的地方……这块神碑……让我感觉心头有些不安。',Speaker=894356,}",
  [173891] = "{id=173891,Text='好的……那么，就从泪水开始吧。',Speaker=894356,}",
  [173892] = "{id=173892,Text='莫娜瓦闭上眼睛，回忆起她模糊的过往，记忆模糊不清，也很难唤起情感的波动。',Speaker=894345,}",
  [173893] = "{id=173893,Text='我还是什么都想不起来，怎么办……',Speaker=894356,}",
  [173894] = "{id=173894,Text='忽然，莫娜瓦又听到了那个呼唤她名字的声音',Speaker=894345,}",
  [173895] = "{id=173895,Text='这声音究竟是谁呢？',Speaker=894356,}",
  [173896] = "{id=173896,Text='不知不觉中，一滴眼泪滑落下来。那滴泪水在她掌心中凝结成一颗珍珠，透出柔和的光芒。',Speaker=894345,}",
  [173897] = "{id=173897,Text='我刚刚又听到了那个呼唤我的声音……为什么，我会这么痛苦呢……',Speaker=894356,}",
  [173898] = "{id=173898,Text='我们很快就会知道了，别担心，你一定会想起来的。',}",
  [173899] = "{id=173899,Text='接下来，只剩下尾尖血了。[c][ffff00][PlayerName][-][/c]，你能帮我采集吗？',Speaker=894356,Option='没问题',}",
  [173900] = "{id=173900,Text='痛感比我想象中更深……不过，我已经做好了准备。',Speaker=894356,}",
  [173901] = "{id=173901,Text='梦境回响吸收了莫娜瓦的泪水和尾尖血，散发出耀眼的光芒，忽然，梦境回响骤然炸开，化作无数闪亮的光点，缓缓融入莫娜瓦的身体。',Speaker=894345,}",
  [173902] = "{id=173902,Text='呃——！！！！头好痛！',Speaker=894356,}",
  [173903] = "{id=173903,Text='（闭上眼睛，神情微微颤抖） 我……我看到了……那些回忆正在涌上来……',Speaker=894356,}",
  [173904] = "{id=173904,Text='（痛苦地捂住头） 这是……那一夜的幻象……戴沃和他的军队摧毁了一切……布瑞夫也……',Speaker=894356,}",
  [173905] = "{id=173905,Text='莫娜瓦！不要被幻境控制住，这只是过去的回忆，不是真实的！',}",
  [173906] = "{id=173906,Text='怎么办……他们太强大了……',Speaker=894356,}",
  [173907] = "{id=173907,Text='忽然，布瑞夫也出现在了幻境中，他站在了莫娜瓦身前，抵抗着海盗的攻击，正如那夜一样。',Speaker=894345,}",
  [173908] = "{id=173908,Text='布瑞夫！！不要……我不要你再次倒在我面前……',Speaker=894356,}",
  [173909] = "{id=173909,Text='冒险者你说得对，这只是幻影而已！',Speaker=894356,}",
  [173910] = "{id=173910,Text='莫娜瓦渐渐平静下来，眼中闪烁出坚定的光芒，她挥出一道魔法，击碎了戴沃和幽灵军团的护盾。',Speaker=894345,}",
  [173912] = "{id=173912,Text='幻象消散了……太好了，莫娜瓦，你做到了！',}",
  [173913] = "{id=173913,Text='是啊，这一次，换我来守护他……',Speaker=894356,}",
  [173914] = "{id=173914,Text='原来这就是你的过去……你承受了太多',}",
  [173915] = "{id=173915,Text='就在这时，神碑上缓缓飘出一丝幽光，逐渐凝聚成一个模糊的身影。那是布瑞夫的残魂，他的目光温柔地注视着莫娜瓦。',Speaker=894345,}",
  [173916] = "{id=173916,Text='莫娜瓦，你终于想起来了。',Speaker=894357,}",
  [173917] = "{id=173917,Text='布瑞夫……我以为你已经……',Speaker=894356,}",
  [173918] = "{id=173918,Text='这是我最后的一部分灵魂，你是我在这世上仅剩的的牵挂，能看到你好好的，让我无悔所做的一切。',Speaker=894357,}",
  [173919] = "{id=173919,Text='对不起……我没能保护好家园，也没能……',Speaker=894356,}",
  [173920] = "{id=173920,Text='不，那不是你的错，莫娜瓦。守护你是我的承诺，只是可惜，以后没办法再看到你的笑，继续陪着你了……我唯一希望的，那就是希望你能放下仇恨，去寻找属于你自己的幸福。',Speaker=894357,}",
  [173922] = "{id=173922,Text='不要……布瑞夫，我怎么能放下你！',Speaker=894356,}",
  [173923] = "{id=173923,Text='莫娜瓦，你是我最珍贵的宝贝，不要让我的牺牲成为你无法挣脱的枷锁……这世间依然有太多美好，值得你去感受，带着我们的记忆，就当是替我看一看吧。',Speaker=894357,}",
  [173926] = "{id=173926,Text='莫娜瓦，我爱你……再见了。',Speaker=894357,}",
  [173928] = "{id=173928,Text='布瑞夫的残魂在微光中彻底消散，化作一缕光点拂过莫娜瓦的脸庞。她站在悬崖边，凝视着他消失的地方，止不住地痛哭。',Speaker=894345,}",
  [173929] = "{id=173929,Text='莫娜瓦，别太难过了，他也一定希望你能走向新的生活，振作起来。',}",
  [173930] = "{id=173930,Text='你说得对……是你让我找回了这一切，帮我重新面对过去的记忆。',Speaker=894356,}",
  [173931] = "{id=173931,Text='谢谢你，冒险者……谢谢你陪我走完了这一段最艰难的旅程。',Speaker=894356,}",
  [173932] = "{id=173932,Text='很高兴能帮到你，接下来有什么打算？',}",
  [173933] = "{id=173933,Text='我想要走遍这个世界，去感受它的每一处风景……带着布瑞夫的期许，勇敢地去面对未来。',Speaker=894356,}",
  [173934] = "{id=173934,Text='希望我们能在未来的某一天再次相见，或许那时的我，已完全走出阴霾……[c][ffff00][PlayerName][-][/c]，再见了。',Speaker=894356,}",
  [173935] = "{id=173935,Text='欢迎，冒险者！你来的正是时候！在这甜蜜的季节，我来为那些心中怀抱爱情或对未来充满疑惑的人带来一些指引。',Speaker=894360,Option='指引？',}",
  [173936] = "{id=173936,Text='没错！我带来了这些神秘的塔罗牌，每张牌都蕴藏着特殊的力量，等待着为你揭示爱情和勇气的方向。',Speaker=894360,}",
  [173937] = "{id=173937,Text='只要接受‘恋爱占卜’，你就会获得一张独特的塔罗牌祝福。谁知道呢，也许它会带来你所渴望的答案哦！',Speaker=894360,}",
  [173938] = "{id=173938,Text='当然啦，每日完成‘恋爱占卜’还能得到‘小天使的祝福’作为奖励！希望这些塔罗牌的祝福能让你的每一天都充满希望与甜蜜。',Speaker=894360,Option='谢谢你！',}",
  [173939] = "{id=173939,Text='那么，祝你好运！',Speaker=894360,}",
  [173940] = "{id=173940,Text='你来了，冒险者！最近我们在依斯鲁得岛周围发现了不少可疑的动向，那些该死的海盗亡灵似乎在酝酿什么阴谋！',Speaker=894338,}",
  [173941] = "{id=173941,Text='据说，他们在傍晚时分从深海中浮现，带着幽灵船袭击岸边……目的是抓走一位逃离他们魔掌的“人鱼”',Speaker=894338,}",
  [173942] = "{id=173942,Text='人鱼？难道是……莫娜瓦？',}",
  [173943] = "{id=173943,Text='没错。我们已经收到了她的求助，为了防止这些亡灵海盗祸害无辜，我们必须挡住他们的进攻！这些海盗亡灵在每周六和周日的傍晚到深夜间会出现在，邪恶的力量在那段时间最为强盛。',Speaker=894338,}",
  [173944] = "{id=173944,Text='准备好了吗，勇敢的冒险者！',Speaker=894338,Option='出发！',}",
  [173946] = "{id=173946,Text='我们去找伊塞尔吧。',Speaker=894356,Option='出发！',}",
  [173947] = "{id=173947,Text='那一定就是伊塞尔了，我们过去看看吧~',Speaker=894356,}",
  [173948] = "{id=173948,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=894356,}",
  [173949] = "{id=173949,Text='材料收集的差不多了，我们快去海底神殿看看吧！',Speaker=894356,}",
  [173950] = "{id=173950,Text='一拳超人联动扭蛋开启！只需要使用B格猫金币参与10次[限时联动]扭蛋机，即可额外获赠联动职业转职道具[c][ffff00][英雄职业·影·埼玉解锁券][-][/c]！',Speaker=894372,}",
  [173951] = "{id=173951,Text='冒险者如果抽到了重复的外观也不要气馁，可以在我这分解成[c][ffff00][英雄勋章][-][/c]，「英雄勋章」可以兑换全新的[c][ffff00]限定卡片[影·埼玉卡片]、[影·杰诺斯卡片][-][/c]以及所有联动扭蛋中的外观道具哦~',Speaker=894372,}",
  [173952] = "{id=173952,Text='那么，这些「英雄勋章」就当作是我送你的见面礼啦~祝你好运~',Speaker=894372,}",
  [173966] = "{id=173966,Text='嘿！[c][ffff00][PlayerName][-][/c]，你知道吗，这个春天，普隆德拉将要举办一场盛大的郊游！整个城市都在为这场活动忙得热火朝天！',Speaker=894376,}",
  [173967] = "{id=173967,Text='但要让大家都玩得尽兴，少不了充足的物资支持！',Speaker=894376,}",
  [173968] = "{id=173968,Text='我们现在正在开展一项‘春日奉献’行动，号召大家捐献一些物资~你愿意加入吗？',Speaker=894376,}",
  [173969] = "{id=173969,Text='有了你的支持，我相信这次郊游一定会更加完美！',Speaker=894376,}",
  [173970] = "{id=173970,Text='哎呀！冒险者！你听说了吗？我们精心准备的郊游场地居然被一群调皮的魔物占领了，那些家伙简直无法无天！',Speaker=894377,}",
  [173971] = "{id=173971,Text='在草地上打滚，把气球踩扁，还要举办什么‘怪蛋派对’！',Speaker=894377,Option='真是太过分了!',}",
  [173972] = "{id=173972,Text='[c][ffff00][PlayerName][-][/c]，能不能帮个忙？如果不赶紧把它们赶出去，整个郊游活动就要泡汤了……',Speaker=894377,}",
  [173973] = "{id=173973,Text='我就知道冒险者一定能搞定它们，这是大家的心意，送给你~欢迎一起来郊游哦！',Speaker=894377,}",
  [173974] = "{id=173974,Text='冒险者，你来得正好！今天是一个特别的日子——妇女节！',Speaker=894375,}",
  [173975] = "{id=173975,Text='这是属于所有女性的节日，用来赞美她们的坚韧、智慧和无私奉献。',Speaker=894375,}",
  [173976] = "{id=173976,Text='你知道吗？这个节日不仅是庆祝女性的成就，也提醒我们要更加关注平等和尊重，不论是母亲、姐妹、朋友，还是那些在工作和生活中默默努力的女性，都值得被感谢和尊敬！',Speaker=894375,}",
  [173977] = "{id=173977,Text='每个人都有属于自己的光芒，祝你度过一个美好的妇女节，也希望你能把这份祝福带给更多人~',Speaker=894375,Option='{谢谢,2},{过会再领,3}',}",
  [173978] = "{id=173978,Text='[c][ffff00][PlayerName][-][/c]，你来啦，知道今天是什么日子吗？',Speaker=894375,Option='植树节',}",
  [173979] = "{id=173979,Text='答对咯！今天是植树节——一个属于自然和新生的节日。在这美丽的春天，冒险者有没有去看看大自然呢~',Speaker=894375,}",
  [173980] = "{id=173980,Text='为了庆祝这个节日，燕妮特别准备了一些‘自然的礼物’给大家哦！',Speaker=894375,}",
  [173981] = "{id=173981,Text='希望每个人都能记住自然的馈赠——就像这微风、绿叶、春天的每一缕阳光，都是不可替代的珍宝~',Speaker=894375,Option='{谢谢,2},{过会再领,3}',}",
  [173982] = "{id=173982,Text='春天的脚步悄然而至，郊游的计划也被提上日程，但究竟哪个地方才是最适合感受春日气息的目的地呢……',Speaker=894378,}",
  [173983] = "{id=173983,Text='3.1-4.1期间，Base等级≥25级的冒险者前往普隆德拉，找到「卡洛·史蒂芬」即可参与活动',Speaker=894355,}",
  [173984] = "{id=173984,Text='共5个连续的任务：完成1~4任务，均可获得Zeny x 150,000，「春灵之露」x1、「B格猫福利券·宴」x6。完成第5个任务，可以获得Zeny x 300,000，「春灵之露」x3、「B格猫福利券·宴」x6、「奋进礼盒」x1',Speaker=894355,}",
  [173985] = "{id=173985,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894355,}",
  [173986] = "{id=173986,Text='该活动为帐号限定类任务哦~',Speaker=894355,}",
  [173987] = "{id=173987,Text='郊游在即，吟游诗人“艾尔文”来到了普隆德拉，打算为庆典献上一首春日赞歌，快快拿起相机，用镜头定格那些让人心动的瞬间，为春日赞歌注入新的灵感吧！',Speaker=894355,}",
  [173988] = "{id=173988,Text='3.10--4.1期间，Base等级≥25级的冒险家，前往普隆德拉找到「吟游诗人」，接取系列任务',Speaker=894355,}",
  [173989] = "{id=173989,Text='冒险者每天将前往美丽的地点进行拍照，该任务一共可以完成7次，完成前6次均可获得Zeny x 150,000，「春灵之露」x1',Speaker=894355,}",
  [173990] = "{id=173990,Text='完成第7个任务，可以获得Zeny x 300,000，「春灵之露」x3、「奋进礼盒」x1',Speaker=894355,}",
  [173991] = "{id=173991,Text='活动期间，任务会按服务器时间连续7天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894355,}",
  [173993] = "{id=173993,Text='白色情人节快到了，甜蜜的计划正在启动！亲手做一份巧克力吧，无论是送给你的小伙伴，还是心中最特别的那个人，都会让这次郊游更浪漫哟~！',Speaker=894355,}",
  [173994] = "{id=173994,Text='3.14--4.1期间，Base等级≥25的冒险者前往罗扎纳，与「阿钱·银牙」对话，即可参与活动。',Speaker=894355,}",
  [173995] = "{id=173995,Text='收集「暖暖的爱意」、「幸福的爱意」，在【阿钱·银牙】处，可兑换「CP卡片自选礼盒」哦~',Speaker=894355,}",
  [173996] = "{id=173996,Text='三月真是节日满满呢！普隆德拉的居民为大家准备了特别的礼物，每一份都充满了春天的仪式感，快去领取属于你的节日惊喜吧~',Speaker=894355,}",
  [173997] = "{id=173997,Text='妇女节（3.8--3.15）/植树节（3.12--3.19）期间，Base等级≥25级的冒险者前往普隆德拉，与「燕妮」对话即可领取奖励~',Speaker=894355,}",
  [173998] = "{id=173998,Text='郊游的场地突然被一群调皮的魔物占领了！它们在草地上打滚，把气球踩扁，甚至还要在场地中央举办“怪蛋派对”！冒险者们，是时候拿起武器，把这些“春日捣蛋鬼”赶出去啦！',Speaker=894355,}",
  [173999] = "{id=173999,Text='3.1--4.1期间，每周六、周日，在普隆德拉西门会出现一大群捣蛋鬼，击败即有概率获得精美时装、符文等奖励哦~',Speaker=894355,}",
  [174000] = "{id=174000,Text='郊游的热潮席卷了整个普隆德拉，但举办一场盛大的活动少不了物资的支持！一场关于春日奉献的行动悄然开始，每一份捐赠都在为这次郊游增添色彩。献上一点心意，将春天的喜悦传递给每一个人吧~',Speaker=894355,}",
  [174001] = "{id=174001,Text='3.24--4.1期间，冒险者前往普隆德拉找到「纳穆」，即可使用多余材料进行捐献',Speaker=894355,}",
  [174002] = "{id=174002,Text='每次捐献可以获得「料理兑换券」x1、「坠星陨铁」x1、「波利成长秘药」x1、「B格猫福利券·宴」x3',Speaker=894355,}",
  [174003] = "{id=174003,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894355,}",
  [174004] = "{id=174004,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894355,}",
  [174005] = "{id=174005,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【背背绝世大聪明】[-][/c]绘制的共创头像！',Speaker=894355,Option='哇！',}",
  [174006] = "{id=174006,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894355,Option='谢谢',}",
  [174009] = "{id=174009,Text='在这个特殊的日子里，希望天下有情人终成眷属~',Speaker=894380,}",
  [174010] = "{id=174010,Text='还没遇到有情之人也不要心急，我这里有一些[c][ffff00]魔法甜心[-][/c]，据说可以给人带来桃花哦~',Speaker=894380,}",
  [174011] = "{id=174011,Text='收集更多的[c][ffff00]魔法甜心[-][/c]可以在我这里兑换各类情人节专属外观哦~',Speaker=894380,}",
  [174012] = "{id=174012,Text='你也感受到今天的浪漫了吗？',Speaker=894381,Option='对',}",
  [174013] = "{id=174013,Text='果然，今天连空气中吹着得风都是甜的~',Speaker=894381,}",
  [174014] = "{id=174014,Text='那么，在这个特殊的节日里，我这份礼物也希望你可以喜欢~',Speaker=894381,Option='谢谢',}",
  [174015] = "{id=174015,Text='最后，祝你节日快乐~',Speaker=894381,}",
  [174016] = "{id=174016,Text='嘿！[c][ffff00][PlayerName][-][/c]，你知道吗，哥布灵庆典正在举办中！整个城市都在为这场活动忙得热火朝天！',Speaker=894383,}",
  [174017] = "{id=174017,Text='但要让大家都玩得尽兴，少不了充足的物资支持！',Speaker=894383,}",
  [174018] = "{id=174018,Text='我们现在正在开展一项‘庆典捐献’行动，号召大家捐献一些物资~你愿意加入吗？',Speaker=894383,}",
  [174019] = "{id=174019,Text='有了你的支持，我相信这次庆典一定会更加完美！',Speaker=894383,}",
  [174021] = "{id=174021,Text='今年哥布灵们把狂欢的地点选择在了吉芬，但是中间却出现了捣乱的哥布灵，冒险者快去阻止吧',Speaker=894386,}",
  [174022] = "{id=174022,Text='感谢你制止了捣乱的哥布灵！',Speaker=894386,}",
  [174024] = "{id=174024,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893996,}",
  [174025] = "{id=174025,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=893996,}",
  [174026] = "{id=174026,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【Rededie】[-][/c]绘制的共创头像！',Speaker=893996,Option='哇！',}",
  [174027] = "{id=174027,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=893996,Option='谢谢',}",
  [174028] = "{id=174028,Text='一年一度的春日庆典开幕！今年的哥布灵们要举办一场盛大春日庆典！这次，你也会来参加吧！',Speaker=893996,}",
  [174029] = "{id=174029,Text='4月1日-5月1日期间，Base≥25级的冒险者前往普隆德拉找到「托拉基」即可参与活动',Speaker=893996,}",
  [174030] = "{id=174030,Text='共5个连续的任务，每完成一个任务，可以获得Zeny x 150,000，「祝福花环」x1，「B格猫福利券·宴」x6；完成第5个任务，可以获得Zeny x 300,000，「祝福花环」x3，「B格猫福利券·宴」x6，「奋进礼盒」x1',Speaker=893996,}",
  [174031] = "{id=174031,Text='哥布灵一族自然有不少宝物，但想要收入囊中，可需要付出一点努力噢！',Speaker=893996,}",
  [174032] = "{id=174032,Text='4月21日-5月1日期间，Base≥25级的冒险者前往普隆德拉找到「哥布灵国王」即可参与活动',Speaker=893996,}",
  [174033] = "{id=174033,Text='共5个连续的任务：完成第1~4个任务可以获得Zeny x 150,000，「祝福花环」x1；完成第5个任务，可以获得Zeny x 300,000，「祝福花环」x3，「奋进礼盒」x1',Speaker=893996,}",
  [174034] = "{id=174034,Text='或许是因为哥布灵国王的外出，大量变异的巴风特入侵吉芬，难道他们是想占领哥布灵森林吗……',Speaker=893996,}",
  [174035] = "{id=174035,Text='每周六、周日，巴风特一族将刷新在吉芬，击败他们可以获得「附魔礼盒」、「符文礼盒」、「珍藏礼盒·MVP」等奖励',Speaker=893996,}",
  [174036] = "{id=174036,Text='冒险者将第三次打开「珍藏礼盒·MVP」必定获得「银河守望者头饰兑换券」哦~',Speaker=893996,}",
  [174037] = "{id=174037,Text='[c][ffff00][PlayerName][-][/c]，你来啦！',Speaker=805180,}",
  [174038] = "{id=174038,Text='祭典期间，艾卡拉奇可是有不少音乐精灵在默默演出哦！不妨给他们合影留念吧，毕竟晨曦祭典可是一年一次呢！',Speaker=805180,}",
  [174039] = "{id=174039,Text='怎么样！有想到什么嘛，带上你的相机，去记录一下吧~',Speaker=805180,Option='好，我去找找',}",
  [174040] = "{id=174040,Text='你回来啦！怎么样，有什么收获吗？',Speaker=805180,}",
  [174041] = "{id=174041,Text='连我都想不到会有这番景象呢，不亏是心思缜密的冒险者呢。',Speaker=805180,}",
  [174042] = "{id=174042,Text='请收下这个，谢谢能与我分享这份美好。',Speaker=805180,}",
  [174043] = "{id=174043,Text='[c][ffff00][PlayerName][-][/c]，你来啦！今天的拍摄也拜托你了！',Speaker=805180,}",
  [174046] = "{id=174046,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=828278,}",
  [174047] = "{id=174047,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=828278,}",
  [174048] = "{id=174048,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【KX1314】[-][/c]绘制的共创头像！',Speaker=828278,Option='哇！',}",
  [174049] = "{id=174049,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=828278,Option='谢谢',}",
  [174050] = "{id=174050,Text='每年的晨曦祭典，艾卡拉奇总会有捣乱的魔物入侵，觊觎着祭典的永恒之花……',Speaker=805195,}",
  [174051] = "{id=174051,Text='但凭借我们浮空精灵的人力远远不够抗衡……',Speaker=805195,}",
  [174052] = "{id=174052,Text='冒险者，你愿意与我一起消灭魔物，保卫晨曦祭典吗？',Speaker=805195,Option='没问题',}",
  [174053] = "{id=174053,Text='哈哈哈哈，看到他们落荒而逃的样子，真是让人开心呢~',Speaker=805195,}",
  [174054] = "{id=174054,Text='5.1~6.1期间，Base等级≥25级的冒险者可以前往普隆德拉广场，找到「娜拉·海拉尔」即可参与活动',Speaker=828278,}",
  [174055] = "{id=174055,Text='共4个连续的任务，每完成一个任务，可以获得Zeny x 300,000，「永恒花瓣」x5；完成第5个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=828278,}",
  [174056] = "{id=174056,Text='5.5~6.1期间，Base等级≥25级的冒险者可以前往艾卡拉奇，找到「赫蒂」即可参与活动',Speaker=828278,}",
  [174057] = "{id=174057,Text='共5个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [174058] = "{id=174058,Text='5.12~6.1期间，Base等级≥25级的冒险者可以前往艾卡拉奇，找到「赫蒂」即可参与活动',Speaker=828278,}",
  [174059] = "{id=174059,Text='共7个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [174060] = "{id=174060,Text='5.19~6.1期间，Base等级≥25级的冒险者可以前往时间花园，找到「密米尔」即可参与活动',Speaker=828278,}",
  [174061] = "{id=174061,Text='共5个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [174062] = "{id=174062,Text='聆听精灵们的旋律，那是这个世界最美的律动！拿出照相机记录下这美好的一刻吧！',Speaker=828278,}",
  [174063] = "{id=174063,Text='5.26~6.1期间，Base等级≥25级的冒险者前往艾卡拉奇，找到「赛尔妲·海拉尔」即可参与活动',Speaker=828278,}",
  [174064] = "{id=174064,Text='共5个连续的任务，完成任务可获得「永恒花瓣」、Zeny等奖励',Speaker=828278,}",
  [174065] = "{id=174065,Text='每周六、周日16点 ~ 24点，「愤怒的蜂后」将带领魔物破坏祭典！',Speaker=828278,}",
  [174067] = "{id=174067,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893573,}",
  [174068] = "{id=174068,Text='星月交辉期间（3月26日~4月21日），每周六/日，我将把最诚挚的祝福想要送给你！',Speaker=893573,Emoji=2,}",
  [174069] = "{id=174069,Text='月夜下伟岸的守护者啊，米德加尔特因为有你而星光熠熠！\n愿星之力为你驱散迷雾，愿月之力为你指引方向。',Speaker=893573,Option='{谢谢,2},{过会再领,3}',}",
  [174073] = "{id=174073,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893573,}",
  [174074] = "{id=174074,Text='多亏了你的帮助，西门游荡的魔物已经被全部赶走了！',Speaker=893573,Emoji=20,}",
  [174075] = "{id=174075,Text='月光又重新洒满了大地，邪恶的力量终将得到驱散~这份感谢之礼，赠予你！',Speaker=893573,Option='{谢谢,2},{过会再领,3}',}",
  [174077] = "{id=174077,Text='嘿！[c][ffff00][PlayerName][-][/c]，你知道吗，晨曦祭典正在举办中！整个城市都在为这场活动忙得热火朝天！',Speaker=894393,}",
  [174078] = "{id=174078,Text='但要让大家都玩得尽兴，少不了充足的物资支持！',Speaker=894393,}",
  [174079] = "{id=174079,Text='我们现在正在开展一项‘祭典捐献’行动，号召大家捐献一些物资~你愿意加入吗？',Speaker=894393,}",
  [174080] = "{id=174080,Text='有了你的支持，我相信这次祭典一定会更加完美！',Speaker=894393,}",
  [174082] = "{id=174082,Text='水是万物之源，冒险者，你能帮我去收集几种不同的水吗？',Speaker=894398,Option='没问题',}",
  [174083] = "{id=174083,Text='第1种需要收集的水，叫「初心之水」，在南门就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174084] = "{id=174084,Text='收集到了之后，记得回来找我领取奖励哦~',Speaker=894398,}",
  [174085] = "{id=174085,Text='想不到那么快就收集到了，谢谢你冒险者，这是你的礼物，请收下~',Speaker=894398,}",
  [174086] = "{id=174086,Text='第2种需要收集的水，叫「生命之水」，在苏克特沙漠就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174087] = "{id=174087,Text='第3种需要收集的水，叫「纯净之水」，在姜饼城就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174088] = "{id=174088,Text='第4种需要收集的水，叫「忠贞之水」，在天津町就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174089] = "{id=174089,Text='第5种需要收集的水，叫「狂欢之水」，在克魔岛就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174090] = "{id=174090,Text='第6种需要收集的水，叫「天然之水」，在乌龟岛就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174091] = "{id=174091,Text='第7种需要收集的水，叫「希望之水」，在落日浅滩就可以采集到，拿着这个瓶子，帮我去装一些来吧~',Speaker=894398,}",
  [174092] = "{id=174092,Text='感谢你这几天来帮我把所有的水都收集齐了，这是你的礼物，请收下~',Speaker=894398,}",
  [174093] = "{id=174093,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=893598,}",
  [174094] = "{id=174094,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=893598,}",
  [174095] = "{id=174095,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【彩虹巧克力】[-][/c]绘制的共创头像！',Speaker=893598,Option='哇！',}",
  [174096] = "{id=174096,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=893598,Option='谢谢',}",
  [174097] = "{id=174097,Text='6/1~7/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]拉拉贝尔[-][/c]接取系列任务~',Speaker=893689,}",
  [174098] = "{id=174098,Text='共4个连续的任务：\n完成第1~3个任务，可获得Zeny x 300,000、「梦幻甜心」x5\n完成第4个任务，可以额外获得「B格猫福利券·宴」x30',Speaker=893689,}",
  [174099] = "{id=174099,Text='6/9~7/1期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]思丽卡[-][/c]接取系列任务~',Speaker=893689,}",
  [174101] = "{id=174101,Text='6/16~7/1期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]阿利斯塔[-][/c]接取系列任务~',Speaker=893689,}",
  [174104] = "{id=174104,Text='6/23~7/1期间，Base≥25级的冒险者通过梦幻城的[c][ffff00]琉利斯[-][/c]接取系列任务~',Speaker=893689,}",
  [174112] = "{id=174112,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894025,}",
  [174113] = "{id=174113,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【致幻蘑菇菌】[-][/c]绘制的共创头像框！',Speaker=894025,Option='哇！',}",
  [174114] = "{id=174114,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像框与你相逢哦~敬请期待吧~',Speaker=894025,Option='谢谢',}",
  [174115] = "{id=174115,Text='又见面了，冒险者！我们准备开启新的巡游啦！',Speaker=894033,}",
  [174116] = "{id=174116,Text='但是！现在我们紧缺远行需要的物资！冒险家你有物资可以捐给大家吗？',Speaker=894033,}",
  [174117] = "{id=174117,Text='7/7~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]布鲁斯[-][/c]即可接取任务',Speaker=894024,}",
  [174118] = "{id=174118,Text='7/14~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]布鲁斯[-][/c]即可接取任务',Speaker=894024,}",
  [174119] = "{id=174119,Text='7/21~7/28期间，冒险者前往普隆德拉找到[c][ffff00]吉米[-][/c]即可与它交换礼盒~',Speaker=894024,}",
  [174129] = "{id=174129,Text='7/1~8/1期间，Base≥25级的冒险者通过普隆德拉的[c][ffff00]布鲁斯[-][/c]即可领取到本月玩家共创的头像框哦~',Speaker=894024,}",
  [174142] = "{id=174142,Text='[c][ffff00][PlayerName][-][/c]，你来啦~',Speaker=894034,}",
  [174143] = "{id=174143,Text='你相信有平行世界存在么？看似纯粹的米德加尔特，实际上有来自世界各地的冒险者在一起冒险哦~',Speaker=894034,}",
  [174144] = "{id=174144,Text='现在，有一份来自其他冒险者的礼物要送给你——来自[c][ffff00]【颛颛】[-][/c]绘制的共创头像框！',Speaker=894034,Option='哇！',}",
  [174145] = "{id=174145,Text='在接下来的冒险旅程中，也许会有更多冒险者创造的头像与你相逢哦~敬请期待吧~',Speaker=894034,Option='谢谢',}",
  [174146] = "{id=174146,Text='天津町的花嫁节是爱人们互相拥抱，许下一生誓言的盛大节日，快带上你心爱的他/她，一起来参加花嫁盛典吧~',Speaker=894034,}",
  [174147] = "{id=174147,Text='8/1~9/1期间，Base等级大于等于25级的冒险者前往普隆德拉广场，找到「樱花女巫」即可参与活动',Speaker=894034,}",
  [174148] = "{id=174148,Text='共4个连续的任务，完成1-3个任务，均可获得Zeny x 150,000，「真爱之心」x1、「B格猫福利券·宴」x10；完成第4个任务，可以获得奖励Zeny x 300,000，「真爱之心」x3、「奋进礼盒」x1',Speaker=894034,}",
  [174149] = "{id=174149,Text='活动期间，任务会按服务器时间连续4天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894034,}",
  [174150] = "{id=174150,Text='该活动为帐号限定类任务哦~',Speaker=894034,}",
  [174151] = "{id=174151,Text='一年一度的花嫁节正好赶上一年一度最大的流星雨，在这浪漫的氛围下，去拥抱你最重要的那个人吧~',Speaker=894034,}",
  [174152] = "{id=174152,Text='8/7~8/13的每晚18点至次日早晨5点，天津町将出现整晚的流星雨',Speaker=894034,}",
  [174153] = "{id=174153,Text='在流星雨持续期间，天津町将出现大量的「流星碎片」，冒险者可以前往拾取，每次拾取可以获得「流星碎片」x1、Zeny x 150,000、「福利券自选礼包」x1',Speaker=894034,}",
  [174154] = "{id=174154,Text='8/14日之前，Base等级≥25级的冒险者，收集5个「流星碎片」提交给「流星指引大使」可以获得「流星自选礼盒」x1，打开之后可以自选「逐星闪烁」x1或者「莫拉金币」x10',Speaker=894034,}",
  [174155] = "{id=174155,Text='花嫁节即将开始，然而樱花树却日渐枯萎，原来是维持樱花绽放的甜蜜誓约被偷走啦，快想办法在大陆各处收集甜蜜誓约，让樱花重现活力，保证花嫁节的如期举行吧！',Speaker=894034,}",
  [174156] = "{id=174156,Text='8/14~9/1期间，Base等级大于等于25级的冒险者前往天津町，找到「奇噜噜」即可参与活动',Speaker=894034,}",
  [174157] = "{id=174157,Text='共5个连续的任务，冒险者根据提示前往指定地点击杀指定魔物。完成第1--4个任务，均可获得Zeny x 150,000，「真爱之心」x1；完成第5个任务，可以获得Zeny x 300,000，「真爱之心」x3、「奋进礼盒」x1',Speaker=894034,}",
  [174158] = "{id=174158,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894034,}",
  [174159] = "{id=174159,Text='该活动为帐号限定类任务哦~',Speaker=894034,}",
  [174160] = "{id=174160,Text='展示甜蜜恋爱也是花嫁节的传统，希望照相机能记录下你们相伴的身影',Speaker=894034,}",
  [174161] = "{id=174161,Text='8/21~9/1期间，Base等级大于等于25级的冒险者前往天津町，找到「粉唧唧」即可参与活动',Speaker=894034,}",
  [174162] = "{id=174162,Text='共5个连续的任务，冒险者根据提示前往指定约会圣地进行拍照。完成第1--4个任务，均可获得Zeny x 150,000，「真爱之心」x1；完成第5个任务，可以获得Zeny x 300,000，「真爱之心」x3、「奋进礼盒」x1',Speaker=894034,}",
  [174163] = "{id=174163,Text='活动期间，任务会按服务器时间连续5天下发，若玩家未能在下发当日完成，可在活动期间任意时间按顺序完成已下发任务',Speaker=894034,}",
  [174164] = "{id=174164,Text='该活动为帐号限定类任务哦~',Speaker=894034,}",
  [174165] = "{id=174165,Text='花嫁节恰逢洛阳的七夕，许仙陷入莫名的惆怅中，就在这时他在桥上见到了一个熟悉的背影，正是梦中那人，但一群可恶的魔物却阻挡着他靠近的脚步。',Speaker=894034,}",
  [174166] = "{id=174166,Text='每周六、周日16：00-24：00，大批魔物将入侵天津町，冒险者需要拿起手中的武器，保护许仙靠近他的梦中人，让有情人终成眷属！',Speaker=894034,}",
  [174167] = "{id=174167,Text='击败入侵的魔物，可以获得大量「萤火之约」，「萤火之约」为货币类道具，可以兑换「许仙的谢礼」，打开「许仙的谢礼」有概率获得往期外观「曙光舰队头饰兑换券」、「曙光舰队时装自选券」',Speaker=894034,}",
  [174168] = "{id=174168,Text='通过击败魔物获得的「萤火之约」，每日帐号上限为1000',Speaker=894034,}",
  [174169] = "{id=174169,Text='为此，我决定举办一场“年中钜惠”，让诸位冒险者有机会重逢那些美好的珍宝！',Speaker=2700,Option='哇！',}",
  [310992] = "{id=310992,Text='想要雇佣异世界的朋友，做你的执事吗？',Speaker=803228,Option='{确认,3},{我再看看,4}',}",
  [310993] = "{id=310993,Text='哦。这样啊……',Speaker=803228,}",
  [310994] = "{id=310994,Text='我尊重你的决定哦，你真的想好了嘛？我的朋友！',Speaker=803228,Option='{确认,6},{我再看看,7}',}",
  [310995] = "{id=310995,Text='哦。这样啊……',Speaker=803228,}",
  [310998] = "{id=310998,Text='当然，如果你以后的实力成长到已经不需要她的陪伴，也可以选择其他执事和你一起冒险哦！',Speaker=803228,}",
  [311000] = "{id=311000,Text='那今后的日子还请多多指教啦，一起向着未知的冒险前进！！',Speaker=803228,Emoji=2,}",
  [311001] = "{id=311001,Text='音乐之声既是爱之声，有什么需要帮忙的吗？我的朋友。',Speaker=803228,Option='更换执事',}",
  [311002] = "{id=311002,Text='你是想要更换执事么？已有执事的人可以免费更换哦！',Speaker=803228,}",
  [311003] = "{id=311003,Text='她已经是你的执事了哦，如果你希望在冒险道路上有别的执事陪伴你，可以找其他执事进行更换哦！',Speaker=803228,}",
  [311004] = "{id=311004,Text='你希望这位异世界的朋友来做你的执事吗？',Speaker=803228,Option='{希望,6},{我再看看,7}',Emoji=17,}",
  [311005] = "{id=311005,Text='哦。这样啊……',Speaker=803228,}",
  [311011] = "{id=311011,Text='当然，如果你以后的实力成长到已经不需要她的陪伴，也可以选择其他执事和你一起冒险哦！',Speaker=803228,}",
  [311013] = "{id=311013,Text='那今后的日子还请多多指教啦，一起向着未知的冒险前进！！',Speaker=803228,Emoji=2,}",
  [311014] = "{id=311014,Text='我的雇佣券只有特殊时期才能获得哟！但我可以跟你交易，也可以和你聊点音乐！',Speaker=803228,}",
  [430814] = "{id=430814,Text='还剩下三条裙子了，冒险者。',Speaker=823623,}",
  [430815] = "{id=430815,Text='[c][ffff00]我的照片，怎么不见了？[-][/c]',Speaker=823623,}",
  [430816] = "{id=430816,Text='什么？！会是谁偷了我们的照片啊？',Speaker=823624,Option='怎么会这样',Emoji=37,}",
  [430817] = "{id=430817,Text='我也不知道是什么时候消失的，恐怕要先找到照片才能继续寻找宝物。',Speaker=823623,}",
  [430818] = "{id=430818,Text='那我们赶快开启新的旅行吧！',Speaker=823624,Option='没问题',}",
  [430819] = "{id=430819,Text='冒险者，时间紧急，我想我们还是分头行动，这样会快一些。',Speaker=823623,}",
  [430820] = "{id=430820,Text='那就天黑之前，我们在这里会合。希望那时候我们已经把照片找到了！',Speaker=823624,Option='好',}",
  [430821] = "{id=430821,Text='虽然应承了下来但是没有什么头绪，先在附近找找看吧。',}",
  [430822] = "{id=430822,Text='喂，冒险者。',Speaker=823682,Option='谁？',}",
  [430823] = "{id=430823,Text='还能是谁，我啊，我在这里!',Speaker=823682,}",
  [430824] = "{id=430824,Text='哎呀你反应好慢啊，居然才看到我。',Speaker=823626,Option='你是？',}",
  [430825] = "{id=430825,Text='你是失忆了嘛，我是丝碧卡啊！',Speaker=823626,}",
  [430826] = "{id=430826,Text='我们不是一起去过好多地方，大家都在悄悄谈论：[c][ffff00]最近有个“厉害的寻宝三人组”这样[-][/c]？',Speaker=823626,Option='{厉害的三人组？,0},{哪里来的称号？,0}',}",
  [430827] = "{id=430827,Text='嗨，我就这么随口一说，你还较真。',Speaker=823626,Option='……',}",
  [430828] = "{id=430828,Text='嗯？怎么了？',Speaker=823626,Option='{你的样子,5},{你的语气,6},{你的头发,7}',}",
  [430829] = "{id=430829,Text='嗯？我觉得我样子没什么问题。',Speaker=823626,}",
  [430830] = "{id=430830,Text='哦？哎，女孩子有时候语气啊情绪啊发生点变化很正常。',Speaker=823626,}",
  [430831] = "{id=430831,Text='换一个克雷斯特汉姆最近很流行的发型有什么问题吗？',Speaker=823626,Option='……',}",
  [430832] = "{id=430832,Text='这些不重要好嘛？重要的是，[c][ffff00]我找到照片啦！[-][/c]',Speaker=823626,Option='真的？！',}",
  [430833] = "{id=430833,Text='当然，我们赶紧去找吧！找到了正好还能给那个[c][ffff00]什么什么娜[-][/c]一个惊喜。',Speaker=823626,}",
  [430834] = "{id=430834,Text='丝碧卡这个样子很奇怪，要不要再询问下？',Option='{好,9},{没关系,10}',}",
  [430835] = "{id=430835,Text='哎呀冒险者你问题真多！快收下照片去寻找裙子吧！',Speaker=823626,}",
  [430836] = "{id=430836,Text='好吧，虽然觉得很奇怪，但姑且先找裙子吧。',}",
  [430837] = "{id=430837,Text='哦哦哦哦太神奇了！',Speaker=823626,Emoji=8,}",
  [430838] = "{id=430838,Text='丝碧卡的反应不太对……',}",
  [431022] = "{id=431022,Text='是否要进入秘境？',Option='{是的,2},{再见,3}',}",
  [485533] = "{id=485533,Text='要问我最爱哪个英雄，我只能说，小孩子才做选择，我，全，都，爱！',Speaker=827111,Option='英雄？',}",
  [485534] = "{id=485534,Text='没错，名扬大陆的[c][ffff00][PlayerName][-][/c]，我们做了个很有趣让你的[c][ffff00]活动[-][/c]，你一定很想参加对不对？！（求求了）',Speaker=827111,Option='{说来听听,3},{不感兴趣,2}',}",
  [485535] = "{id=485535,Text='吓！拒绝得好直接！看来，俺只能更加继续努力了！',Speaker=827111,}",
  [485536] = "{id=485536,Text='事情是这样，我们在配置表里配了……不是，我们GM指令招出来……不是！！我重说……',Speaker=827111,Option='啊这……',}",
  [485537] = "{id=485537,Text='我们“邀请”了几位冒险旅途中，令大家[c][ffff00]印象深刻的伙伴们[-][/c]回到了普隆德拉（Pos点就在我旁边）！',Speaker=827111,Option='被绑架了就眨眨眼',}",
  [485538] = "{id=485538,Text='希望能由玩家投票选出一位[c][ffff00]最喜欢[-][/c]（票数最高）的英雄，借由无限书库英雄职业板块，把这名英雄更加完美得展现在冒险者的面前。',Speaker=827111,}",
  [485539] = "{id=485539,Text='做这个活动呢，主要是为了帮助大家更多了解这些勇者…………毕竟再不做点什么你们怕是都要忘记他们了………现实的残酷，我好懂！（嘤嘤抹泪）',Speaker=827111,}",
  [485540] = "{id=485540,Text='当然也是为了帮助我们自己想起来……啊不是，重温这些英雄的光辉事迹。',Speaker=827111,}",
  [485541] = "{id=485541,Text='至于没被选上的英雄嘛……呵呵呵，你们懂得！毕竟我们策划组特别擅长写悲剧（不是）。',Speaker=827111,}",
  [485542] = "{id=485542,Text='所以最近请一定要抽时间和我们的英雄们聊聊天，重温一下与他们建立羁绊的那片天地，拍下美好的瞬间。',Speaker=827111,}",
  [485543] = "{id=485543,Text='再给你心中最喜欢的英雄，投上神圣又宝贵的一票就没问题了！再说了，活动策划组是给了奖励的，不拿白不拿（对吧）。',Speaker=827111,Option='明白了！',}",
  [485544] = "{id=485544,Text='好的！那我继续严肃宣传了！更多关于活动和投票的细则，都被我仔仔细细写在了[c][ffff00]公告牌[-][/c]上，随时都可以点击查阅！',Speaker=827111,}",
  [485545] = "{id=485545,Text='我时常想起伊米尔之心的力量凝结成的星之花，从冷光中飞过的瞬间。[c][ffff00][PlayerName][-][/c]，想再跟我一起去发乐斯灯塔看看么？',Speaker=827112,Option='{当然可以,3},{暂时不了,2}',}",
  [485546] = "{id=485546,Text='月亮还是和之前一样，孤单地徘徊在天地树之外。关于奥丁的一切也依旧在迷雾中见不到踪迹。',Speaker=827117,}",
  [485547] = "{id=485547,Text='但有一点是确定的，就是我会愿意向你讲述面具下的故事，亦或让你拥有我的力量。',Speaker=827117,}",
  [485548] = "{id=485548,Text='不过现在这些都不重要，重要的是在此时此地，拍下你觉得美好的瞬间……如果你想要这么做的话。',Speaker=827117,}",
  [485549] = "{id=485549,Text='用鲜血起誓，要用一生来维护世间力量的平衡。[c][ffff00][PlayerName][-][/c]，旅行中的你，初心是否依旧未变。',Speaker=827113,}",
  [485550] = "{id=485550,Text='与你相遇确实勾起了些许过往的记忆，要跟我回梦罗克看看么？',Speaker=827113,Option='{当然可以,3},{暂时不了,2}',}",
  [485551] = "{id=485551,Text='我一直在隐藏在黑暗中，带着刺客不朽传承的决意，将一切破坏平衡的事物刺穿。',Speaker=827118,}",
  [485552] = "{id=485552,Text='这样的日子过得太久，都快忘记了我其实对自己一无所知，即便是突然产生的强烈思念，都无法确定自己思念的是什么……',Speaker=827118,}",
  [485553] = "{id=485553,Text='如果是[c][ffff00][PlayerName][-][/c]，或许我们可以在更深一层的羁绊中，找到和我有关的记忆。',Speaker=827118,}",
  [485554] = "{id=485554,Text='不过现在这些都不重要，重要的是在此时此地，拍下你觉得美好的瞬间……如果你想要这么做的话。',Speaker=827118,}",
  [485555] = "{id=485555,Text='听说你们在挑选自己喜欢的勇者？那不是我，还能是谁？',Speaker=827114,}",
  [485556] = "{id=485556,Text='我怎么突然想起了斐扬……啧，要不你陪我回去看看？',Speaker=827114,Option='{当然可以,3},{暂时不了,2}',}",
  [485557] = "{id=485557,Text='不管过去多久，这里都还是那么平静，平静到令人恶心。',Speaker=827119,}",
  [485558] = "{id=485558,Text='对我来说，这里有着难以忘却的记忆，回想起来倒也不是那么令人愉悦。',Speaker=827119,}",
  [485559] = "{id=485559,Text='嗯……如果你想知道这些故事，大大方方选我就好！不过这些暂时都不重要……',Speaker=827119,}",
  [485560] = "{id=485560,Text='不管怎么说，斐扬这块地方，还是可以让人尘虑尽涤，俗念消退，可以多拍几张照片留念。',Speaker=827119,}",
  [485561] = "{id=485561,Text='它始终在那里，或是照亮着旅人前行的道路，又或是寄托着无尽的思念……',Speaker=827115,}",
  [485562] = "{id=485562,Text='[c][ffff00][PlayerName][-][/c]，我还是想回到月之湖，我们去那里聊天如何？',Speaker=827115,Option='{当然可以,3},{暂时不了,2}',}",
  [485563] = "{id=485563,Text='我还记得答应过你和耶梦加得，会把奥丁的秘密告诉你们。',Speaker=827120,}",
  [485564] = "{id=485564,Text='如果命运的齿轮已经开始转动，那或许你的选择，会成为你我全新的羁绊也说不定。',Speaker=827120,}",
  [485565] = "{id=485565,Text='不过这些暂时都不重要，这里有着难得一见的美景，如果你愿意，可以把他们拍下来留作纪念。至于我，会在这里陪着你。',Speaker=827120,}",
  [485566] = "{id=485566,Text='怎么样？有没有兴趣跟我学真正的锻造技术？',Speaker=827116,}",
  [485567] = "{id=485567,Text='一直以来都是在我的精炼铺子见到我，想不想跟我去看看我提升锻造技艺的地方？',Speaker=827116,Option='{当然可以,3},{暂时不了,2}',}",
  [485568] = "{id=485568,Text='怎么样，我就是在这里和师父哈沃德一起精炼技艺，成为了普隆德拉最棒的铁匠。',Speaker=827121,}",
  [485569] = "{id=485569,Text='当然还有关于卡伦的故事，这些如果你想知道，我都可以告诉你，包括我的力量也可以拿去使用。',Speaker=827121,}",
  [485570] = "{id=485570,Text='不过这些暂时都不重要，这里有着难得一见的美景，如果你愿意，可以把他们拍下来留作纪念。',Speaker=827121,}",
  [485571] = "{id=485571,Text='英雄职业投票活动火热开启中！请问有什么想要了解的呢？',Speaker=827122,Option='{英雄投票活动,2},{如何投票,3},{英雄回归时间,4},{没有想了解的,5}',}",
  [485572] = "{id=485572,Text='亲爱的冒险者，有没有哪一位英雄，在你心中留下了[c][ffff00]深刻的印象[-][/c]。',Speaker=827122,}",
  [485573] = "{id=485573,Text='TA或许是陪你战斗过的勇者，',Speaker=827122,}",
  [485574] = "{id=485574,Text='或许是给予过你方向的导师……',Speaker=827122,}",
  [485575] = "{id=485575,Text='因为种种原因，你们可能许久未见。',Speaker=827122,}",
  [485576] = "{id=485576,Text='现在有一个机会，可以让TA重新以英雄职业的身份再次回归...',Speaker=827122,}",
  [485577] = "{id=485577,Text='而这名英雄是谁，将由你的[c][ffff00]投票[-][/c]决定！',Speaker=827122,}",
  [485578] = "{id=485578,Text='还有什么想要了解的吗？',Speaker=827122,Option='{英雄投票活动,2},{如何投票,3},{英雄回归时间,4},{没有想了解的,5}',}",
  [485579] = "{id=485579,Text='与我身边你想要选择回归的英雄对话后，在英雄带你前往的地图[c][ffff00]与TA合照/截图[-][/c]。',Speaker=827122,}",
  [485580] = "{id=485580,Text='然后移步RO的TapTap论坛，找到对应的投票活动帖子，',Speaker=827122,}",
  [485581] = "{id=485581,Text='按照帖子中的活动规则参与即可～',Speaker=827122,}",
  [485582] = "{id=485582,Text='还有丰富的参与奖在等待你们哦！',Speaker=827122,}",
  [485583] = "{id=485583,Text='也可以在【主界面-热点-动态】中找到对应的帖子，参与活动嗷~',Speaker=827122,}",
  [485584] = "{id=485584,Text='还有什么想要了解的吗？',Speaker=827122,Option='{英雄投票活动,2},{如何投票,3},{英雄回归时间,4},{没有想了解的,5}',}",
  [485585] = "{id=485585,Text='在投票结束、统计完毕后会公示结果。',Speaker=827122,}",
  [485586] = "{id=485586,Text='最终得票最高的TA，将会开始进入研发制作期。',Speaker=827122,}",
  [485587] = "{id=485587,Text='接下来可能会需要半年的时间，让我们共同来一点点把TA以英雄职业的身份带回RO。',Speaker=827122,}",
  [485588] = "{id=485588,Text='期间我们会不定期向大家公布[c][ffff00]研发进度[-][/c]，',Speaker=827122,}",
  [485589] = "{id=485589,Text='让我们一起期待和见证TA的新生吧！',Speaker=827122,}",
  [485590] = "{id=485590,Text='还有什么想要了解的吗？',Speaker=827122,Option='{英雄投票活动,2},{如何投票,3},{英雄回归时间,4},{没有想了解的,5}',}",
  [485762] = "{id=485762,Text='哥从来不忽悠！不要着急嘛，我知道我的粉丝很多，一个一个来，我会在你们的武器上签名的。',Speaker=827274,}",
  [485763] = "{id=485763,Text='等等……你把武器举得那么高做什么？离……离我远点。',Speaker=827274,}",
  [659595] = "{id=659595,Text='[c][ffff00][PlayerName][-][/c],幸亏有你的帮助，不然我都不知道该怎么办才好！',Speaker=828235,}",
  [659596] = "{id=659596,Text='让我看一下清单，唔——今天就去收集高原蔷薇的种子吧！',Speaker=828235,Option='{高原蔷薇？,0},{哪里收集？,0}',}",
  [659597] = "{id=659597,Text='高原蔷薇产于艾因布洛克周边原野~是那里独特的环境的产物。',Speaker=828235,}",
  [659598] = "{id=659598,Text='这可是高原佩瑞斯培育的，是她们的得意之作。',Speaker=828235,}",
  [659599] = "{id=659599,Text='晨曦将为你开启前往艾因布洛克原野的空间通道~',Speaker=828235,}",
  [659600] = "{id=659600,Text='怎么样？准备好了就出发吧！',Speaker=828235,Option='{准备好了,11},{稍等片刻,12}',}",
  [659601] = "{id=659601,Text='好哦~我会在这里等你~',Speaker=828235,}",
  [659602] = "{id=659602,Text='愿晨曦祝福你的旅程。',Speaker=828235,}",
  [659603] = "{id=659603,Text='获得许多高原蔷薇的种子，回去交给赫蒂吧。',}",
  [659604] = "{id=659604,Text='[c][ffff00][PlayerName][-][/c]，欢迎回来~',Speaker=828235,Option='上交花种',}",
  [659605] = "{id=659605,Text='没错，这些就是高原蔷薇的种子。',Speaker=828235,}",
  [659606] = "{id=659606,Text='怎么样？这浓郁的紫色，是不是很美丽？',Speaker=828235,}",
  [659607] = "{id=659607,Text='在氧气稀缺的环境中生长出来的花朵，代表着坚韧与孤傲。',Speaker=828235,}",
  [659608] = "{id=659608,Text='相信它也会成为永恒之花的一个热门选择。',Speaker=828235,}",
  [659609] = "{id=659609,Text='不出所料，高原蔷薇果然大受欢迎呢~！',Speaker=828235,Option='{今天要去哪里？,0},{采集什么种子？,0}',}",
  [659610] = "{id=659610,Text='让我看看……啊，找到了！是向日葵种子~',Speaker=828235,}",
  [659611] = "{id=659611,Text='据我所知，向日葵由于受到了魔王气息的影响，有一部分异化成了魔物。',Speaker=828235,}",
  [659612] = "{id=659612,Text='从它们身上应该获得大量的向日葵种子~',Speaker=828235,}",
  [659613] = "{id=659613,Text='怎么样？准备好了就出发吧！',Speaker=828235,Option='{准备好了,11},{稍等片刻,12}',}",
  [659614] = "{id=659614,Text='好哦~我会在这里等你~',Speaker=828235,}",
  [659615] = "{id=659615,Text='愿晨曦祝福你的旅程。',Speaker=828235,}",
  [659616] = "{id=659616,Text='获得许多向日葵的种子，回去交给赫蒂吧。',}",
  [659617] = "{id=659617,Text='[c][ffff00][PlayerName][-][/c]，收获如何？',Speaker=828235,Option='上交花种',}",
  [659618] = "{id=659618,Text='没错，这些就是向日葵的种子，',Speaker=828235,}",
  [659619] = "{id=659619,Text='永远抬着头，迎着太阳，光辉而又高傲这就是向日葵呀！',Speaker=828235,}",
  [659620] = "{id=659620,Text='听说，在人类世界中，向日葵还代表着沉默而又不弃的爱。',Speaker=828235,}",
  [659621] = "{id=659621,Text='相信它会是很多人心中的永恒之花。',Speaker=828235,}",
  [659622] = "{id=659622,Text='[c][ffff00][PlayerName][-][/c]，今天又要麻烦你啦~',Speaker=828235,}",
  [659623] = "{id=659623,Text='今天的花种在辉煌领域就能找到哦！不用再长途奔波啦~',Speaker=828235,Option='什么种子？',}",
  [659624] = "{id=659624,Text='是荧光堇~！这个时节的话，捕虫堇们会在浮光星岩里播撒种子。',Speaker=828235,}",
  [659625] = "{id=659625,Text='可要小心哦！这个时候的她们会有些暴躁。',Speaker=828235,}",
  [659626] = "{id=659626,Text='晨曦将为你开启前往浮光星岩的空间通道~',Speaker=828235,}",
  [659627] = "{id=659627,Text='怎么样？准备好了就出发吧！',Speaker=828235,Option='{准备好了,11},{稍等片刻,12}',}",
  [659628] = "{id=659628,Text='好哦~我会在这里等你~',Speaker=828235,}",
  [659629] = "{id=659629,Text='愿晨曦祝福你的旅程。',Speaker=828235,}",
  [659630] = "{id=659630,Text='获得许多荧光堇的种子，回去交给赫蒂吧。',}",
  [659631] = "{id=659631,Text='你看上去收获颇丰啊~',Speaker=828235,Option='上交花种',}",
  [659632] = "{id=659632,Text='没错，这些就是荧光堇的种子。',Speaker=828235,}",
  [659633] = "{id=659633,Text='看，这小小的花朵，如同蝴蝶一般，像是要起飞。',Speaker=828235,}",
  [659634] = "{id=659634,Text='它在精灵们的心中寄托着思念，承载着无条件的爱。',Speaker=828235,}",
  [659635] = "{id=659635,Text='荧光堇就是这么浪漫，很多精灵都会选择它作为自己的永恒之花。',Speaker=828235,}",
  [659636] = "{id=659636,Text='荧光堇的种子果然被一抢而空！不过你放心，我给你留了一颗~',Speaker=828235,}",
  [659637] = "{id=659637,Text='今天，就要麻烦你跑一趟苏克拉特沙漠啦！',Speaker=828235,Option='{沙漠里？,0},{有花吗？,0}',}",
  [659638] = "{id=659638,Text='嘿嘿~你可能不知道，仙人掌是会开花的~',Speaker=828235,}",
  [659639] = "{id=659639,Text='摩卡身上的种子可以开出摩卡花~不过可要当心它们身上的刺哦！',Speaker=828235,}",
  [659640] = "{id=659640,Text='怎么样？准备好了就出发吧！',Speaker=828235,Option='{准备好了,11},{稍等片刻,12}',}",
  [659641] = "{id=659641,Text='好哦~我会在这里等你~',Speaker=828235,}",
  [659642] = "{id=659642,Text='愿晨曦祝福你的旅程。',Speaker=828235,}",
  [659643] = "{id=659643,Text='获得许多摩卡花的种子，回去交给赫蒂吧。',}",
  [659644] = "{id=659644,Text='[c][ffff00][PlayerName][-][/c]，没受伤吧？',Speaker=828235,Option='上交花种',}",
  [659645] = "{id=659645,Text='这……这就是摩卡花的种子吗？我也是第一次见~',Speaker=828235,}",
  [659646] = "{id=659646,Text='和摩卡一样，在沙漠中亮眼的颜色，能让人感到清凉。',Speaker=828235,}",
  [659647] = "{id=659647,Text='能忍受孤独寂寞的那份坚毅勇敢，是迷途之人眼中的希望。',Speaker=828235,}",
  [659648] = "{id=659648,Text='这样的它，会成为什么样的人心中的永恒之花呢？',Speaker=828235,}",
  [659649] = "{id=659649,Text='[c][ffff00][PlayerName][-][/c]！只剩下今天最后的花种了！',Speaker=828235,Option='{太好了！,0},{速战速决！,0}',}",
  [659650] = "{id=659650,Text='在伊米尔大人创造的洛阳中，有一朵名为落日牡丹的名贵之花。',Speaker=828235,}",
  [659651] = "{id=659651,Text='我曾有幸见到过一次，那橙红的花朵，如落日般闪耀。',Speaker=828235,}",
  [659652] = "{id=659652,Text='听闻，枫树精灵伊斯玛特别喜欢它，从她那里应该可以获得落日牡丹的花种。',Speaker=828235,}",
  [659653] = "{id=659653,Text='晨曦将为你开启前往浮光星岩的空间通道~',Speaker=828235,}",
  [659654] = "{id=659654,Text='怎么样？准备好了就出发吧！',Speaker=828235,Option='{准备好了,11},{稍等片刻,12}',}",
  [659655] = "{id=659655,Text='好哦~我会在这里等你~',Speaker=828235,}",
  [659656] = "{id=659656,Text='愿晨曦祝福你的旅程。',Speaker=828235,}",
  [659657] = "{id=659657,Text='获得许多落日牡丹的种子，回去交给赫蒂吧。',}",
  [659658] = "{id=659658,Text='看你的表情，应该一切都很顺利吧~',Speaker=828235,Option='上交花种',}",
  [659659] = "{id=659659,Text='你看，种子的外表上也有着落日余晖般的光泽。',Speaker=828235,}",
  [659660] = "{id=659660,Text='这花瓣的颜色与光泽，不愧是洛阳的百花之王。',Speaker=828235,}",
  [659661] = "{id=659661,Text='优雅而又从容，美丽而又富有生机。',Speaker=828235,}",
  [659662] = "{id=659662,Text='在懂它的人心中，是当之无愧的永恒之花吧。',Speaker=828235,}",
  [659663] = "{id=659663,Text='花种都已经收集齐了~等忙完这两天，我带你好好体验一下晨曦祭典~',Speaker=828235,}",
  [659664] = "{id=659664,Text='这些都是你亲自收集的花种，来吧~挑选出你心目中的永恒之花吧！',Speaker=828235,}",
  [659665] = "{id=659665,Text='在高原环境中生长出来的蔷薇，坚韧而又孤傲。',Speaker=828236,Option='{选择它,11},{再看看,12}',}",
  [659666] = "{id=659666,Text='花朵永远迎着太阳，代表着沉默与不弃的爱。',Speaker=828237,Option='{选择它,11},{再看看,12}',}",
  [659667] = "{id=659667,Text='如同蝴蝶般轻盈的花瓣，仿佛可以带着思念飞向天空。',Speaker=828238,Option='{选择它,11},{再看看,12}',}",
  [659668] = "{id=659668,Text='沙漠中的一抹绿，坚毅而又勇敢，是迷途之人眼中的希望。',Speaker=828239,Option='{选择它,11},{再看看,12}',}",
  [659669] = "{id=659669,Text='拥有着落日余晖般的颜色与光泽，从容而又优雅，美丽而又富有生机。',Speaker=828240,Option='{选择它,11},{再看看,12}',}",
  [659670] = "{id=659670,Text='呀~你最后选了这个呀！眼光很不错哦~很适合你！',Speaker=828235,}",
  [659671] = "{id=659671,Text='来吧~去花开伊始之地，培育属于你的永恒之花。',Speaker=828235,}",
  [659672] = "{id=659672,Text='就是这里~这一小块可是我特意挑选为你留下的好位置！',Speaker=828235,}",
  [659673] = "{id=659673,Text='现在，就将你的花种种下吧！记得要许愿哦！',Speaker=828235,}",
  [659674] = "{id=659674,Text='看！种子发芽了~~只要接下来每天好好照料它，很快就能开花啦！',Speaker=828235,}",
  [659675] = "{id=659675,Text='其实照料花朵是个很复杂的事情，但是这次开辟出的这块区域都经过特殊处理，大大降低了难度。',Speaker=828235,}",
  [659676] = "{id=659676,Text='而你只需要每天来查看一下花朵的情况，并根据不同的状况做出简单的应对就行。',Speaker=828235,}",
  [659677] = "{id=659677,Text='好了，现在你去观察一下它的情况吧~',Speaker=828235,}",
  [659678] = "{id=659678,Text='哎呀，根部的泥土似乎有些干燥，知道现在应该怎么做吗？',Speaker=828235,Option='{浇水,11},{除草,12},{灭虫,12}',}",
  [659679] = "{id=659679,Text='没错~要保持泥土的湿润，花朵才能获得好的生长环境。',Speaker=828235,}",
  [659680] = "{id=659680,Text='不对不对~现在更需要浇水，保持泥土的湿润。',Speaker=828235,}",
  [659681] = "{id=659681,Text='那现在，就给花朵浇水吧~',Speaker=828235,}",
  [659682] = "{id=659682,Text='根部的泥土微微湿润，花枝随风微微摇摆，似乎很惬意。',Speaker=828320,}",
  [659687] = "{id=659687,Text='嗯嗯~它有在好好长大！~按这个速度的话，五天就可以开花了~',Speaker=828235,}",
  [659688] = "{id=659688,Text='不过，如果周围有野草，就会抢夺花朵的养分；如果有虫子，就会啃食花朵。',Speaker=828235,}",
  [659689] = "{id=659689,Text='所以，要记得每天来看看它，选择合适的方式照料它哦！',Speaker=828235,Option='{知道了,21},{好麻烦,22}',}",
  [659690] = "{id=659690,Text='嘿嘿~好期待你的永恒之花呀！',Speaker=828235,}",
  [659691] = "{id=659691,Text='哎呀呀~想要收获就得付出努力哦！',Speaker=828235,}",
  [659692] = "{id=659692,Text='那么我们祭典再见啦！',Speaker=828235,}",
  [659693] = "{id=659693,Text='花朵看上去奄奄的，周围的泥土中似乎长出了杂草。',Speaker=828320,Option='{浇水,11},{除草,12},{灭虫,13}',}",
  [659694] = "{id=659694,Text='泥土的湿润度已经足够了。',Speaker=828320,}",
  [659695] = "{id=659695,Text='没有看到需要清除的害虫。',Speaker=828320,}",
  [659696] = "{id=659696,Text='将杂草拔除，让花朵更好的汲取养分吧。',Speaker=828320,}",
  [659697] = "{id=659697,Text='周围的杂草已经清理干净，花枝随风微微摇摆，似乎很惬意。',Speaker=828320,}",
  [659698] = "{id=659698,Text='花朵看上去奄奄的，周围的泥土中似乎长出了杂草。',Speaker=828320,Option='{浇水,11},{除草,12},{灭虫,13}',}",
  [659699] = "{id=659699,Text='泥土的湿润度已经足够了。',Speaker=828320,}",
  [659700] = "{id=659700,Text='没有看到需要清除的害虫。',Speaker=828320,}",
  [659701] = "{id=659701,Text='将杂草拔除，让花朵更好的汲取养分吧。',Speaker=828320,}",
  [659718] = "{id=659718,Text='花朵看上去奄奄的，好像有虫子正聚集在花朵周围。',Speaker=828320,Option='{浇水,11},{除草,12},{灭虫,13}',}",
  [659719] = "{id=659719,Text='泥土的湿润度已经足够了。',Speaker=828320,}",
  [659720] = "{id=659720,Text='没有看到需要清理的杂草。',Speaker=828320,}",
  [659721] = "{id=659721,Text='清除害虫，保护花朵不受伤害。',Speaker=828320,}",
  [659722] = "{id=659722,Text='害虫已经清除完毕，花枝随风微微摇摆，似乎很惬意。',Speaker=828320,}",
  [659723] = "{id=659723,Text='花朵看上去奄奄的，好像有虫子正聚集在花朵周围。',Speaker=828320,Option='{浇水,21},{除草,22},{灭虫,23}',}",
  [659724] = "{id=659724,Text='泥土的湿润度已经足够了。',Speaker=828320,}",
  [659725] = "{id=659725,Text='没有看到需要清理的杂草。',Speaker=828320,}",
  [659726] = "{id=659726,Text='清除害虫，保护花朵不受伤害。',Speaker=828320,}",
  [659743] = "{id=659743,Text='花朵看上去奄奄的，根部的泥土似乎有些干燥。',Speaker=828320,Option='{浇水,31},{除草,32},{灭虫,33}',}",
  [659744] = "{id=659744,Text='要保持泥土的湿润，花朵才能获得好的生长环境。',Speaker=828320,}",
  [659745] = "{id=659745,Text='没有看到需要清除的害虫。',Speaker=828320,}",
  [659746] = "{id=659746,Text='将杂草拔除，让花朵更好的汲取养分吧。',Speaker=828320,}",
  [659747] = "{id=659747,Text='根部的泥土微微湿润，花枝随风微微摇摆，似乎很惬意。',Speaker=828320,}",
  [659768] = "{id=659768,Text='就是你这个臭小子，坏了我的好事吧！',Speaker=828241,Option='{诶嘿~,11},{什么？,12}',}",
  [659769] = "{id=659769,Text='装可爱是没有用的！臭小子！打伤玩偶魔妖就算了，还破坏了我的计划！',Speaker=828241,}",
  [659770] = "{id=659770,Text='别装傻！玩偶魔妖是你打伤的吧？它们这么可爱你怎么下得了手！',Speaker=828241,}",
  [659771] = "{id=659771,Text='可怜我一个老头子，被小布这个小毛孩骂了一顿——颜面何在！颜面何在啊！——',Speaker=828241,}",
  [659772] = "{id=659772,Text='老头我啊，被芙蕾雅那臭丫头埋在这里，不能动弹。',Speaker=828241,}",
  [659773] = "{id=659773,Text='现在即不能参加晨曦祭典，又喝不到美味的花酿酒！残忍，太残忍了！——',Speaker=828241,}",
  [659774] = "{id=659774,Text='我不就是让玩偶魔妖们帮我去采点花来酿酒嘛！你们打它们干什么！？',Speaker=828241,Option='{它们采的,0},{是永恒之花,0}',}",
  [659775] = "{id=659775,Text='你可以和它们好好说嘛！它们都还只是孩子呢！',Speaker=828241,}",
  [659776] = "{id=659776,Text='好啦……我会管好它们的。不过……你这个臭小子可得继续帮我完成我的计划！',Speaker=828241,Option='{为什么是我！,0},{什么计划？,0}',}",
  [659777] = "{id=659777,Text='嗯哼，老头我活了这么多年，酿的酒可比那群乳臭未干的精灵美味。',Speaker=828241,}",
  [659778] = "{id=659778,Text='臭小子，你去九界各地帮我采集不同的花来吧，让你们尝尝什么叫真正的花酿酒！',Speaker=828241,}",
  [659779] = "{id=659779,Text='哼哼，今天你就跑一趟天津町吧，那里的樱花可是非常合适的酿酒材料。',Speaker=828241,}",
  [659780] = "{id=659780,Text='快去快回吧，多收集一些樱花花瓣来！对了！要找那里最大的樱花树啊！',Speaker=828241,}",
  [659781] = "{id=659781,Text='整个天津町最大的樱花树应该就是眼前的这棵了吧。',}",
  [659782] = "{id=659782,Text='呼呼——这气息，是爱——情——的味道啊！',Speaker=828241,Option='{……,0},{那是什么味道……,0}',}",
  [659783] = "{id=659783,Text='小家伙，你还年轻呢！爱——情——啊，是复杂而又醇厚的味道。',Speaker=828241,}",
  [659784] = "{id=659784,Text='啊……真怀念我年轻的时候，那帅气的模样，女巨人们哪个不爱慕我？',Speaker=828241,}",
  [659785] = "{id=659785,Text='哎……爱——情——啊——',Speaker=828241,}",
  [659786] = "{id=659786,Text='我思考了一夜，花酿酒需要甜味。这种味道只有那里有了吧！就是那里——',Speaker=828241,Option='{哪里？,0},{那里啊！,0}',}",
  [659787] = "{id=659787,Text='罗扎纳！没错！就是罗扎纳！那些猫咪小玩意们，看着就很可口。',Speaker=828241,}",
  [659789] = "{id=659789,Text='这就是密米尔所说的甜甜花吧。',}",
  [659790] = "{id=659790,Text='你人还没出现，我就闻到了这甜蜜的味道了。',Speaker=828241,}",
  [659791] = "{id=659791,Text='这真是令人幸福的滋味啊~',Speaker=828241,}",
  [659792] = "{id=659792,Text='花酿酒里，怎么可以缺少清凉的气息呢！',Speaker=828241,}",
  [659793] = "{id=659793,Text='去吧！今天的目标——姜饼城！雪绒花知道吧？给老头我采点回来！',Speaker=828241,}",
  [659794] = "{id=659794,Text='眼前的这些散发着冰凉气息的花朵就是雪绒花吧。',}",
  [659795] = "{id=659795,Text='清凉的花酿顺着喉壁流淌进身体中，酒精的作用让人微醺。',Speaker=828241,}",
  [659796] = "{id=659796,Text='凉爽又不失温暖。怎么样？是不是更加期待老头我的花酿酒了?',Speaker=828241,}",
  [659797] = "{id=659797,Text='嗯……还差点什么呢？嗯，世界上最神奇的植物，只能是它了。',Speaker=828241,Option='{那是什么？,0},{有话直说！,0}',}",
  [659798] = "{id=659798,Text='哎，年轻人，别着急，听我说嘛……是天地树啊！',Speaker=828241,}",
  [659799] = "{id=659799,Text='我美味的花酿酒里怎么能少了天地树上开的尤克特拉希尔花？！',Speaker=828241,}",
  [659800] = "{id=659800,Text='这些散发着生命气息的花就是尤克特拉希尔花吧。',}",
  [659801] = "{id=659801,Text='世界上最好的良药，就是天地树的一切。',Speaker=828241,}",
  [659802] = "{id=659802,Text='用少许的尤克特拉希尔花就能赶走饮用者的疲倦，治愈他们的心灵。',Speaker=828241,}",
  [659803] = "{id=659803,Text='花酿酒现在已经完成的差不多了，但是，还缺最后的灵魂。',Speaker=828241,}",
  [659804] = "{id=659804,Text='那是生长于雾之森林的幽灵花，悔恨与执念是它的养料。',Speaker=828241,Option='{啊……？,0},{加它不合适吧？,0}',}",
  [659805] = "{id=659805,Text='你懂什么，臭小鬼，你只要负责把花带过来！',Speaker=828241,}",
  [659806] = "{id=659806,Text='这些生长在黑雾中的就是幽灵花吧。',}",
  [659807] = "{id=659807,Text='唔，不错。你有没有发现，它生长的环境却是十分的阴暗。',Speaker=828241,}",
  [659808] = "{id=659808,Text='但是你看它现在，从悔恨与执念中生长出来的，是最纯粹的思念。',Speaker=828241,}",
  [659809] = "{id=659809,Text='好了，花都收集齐了，等着尝尝老头我酿制出的美味花酿酒吧！',Speaker=828241,}",
  [659811] = "{id=659811,Text='之前提到过，你帮忙收集的那些花种，是为了给大家挑选后培育属于自己的永恒之花的。',Speaker=828235,}",
  [659812] = "{id=659812,Text='永恒之花在每个人心中都有不同的定义和选择，是内心对期待和珍惜的真实写照。',Speaker=828235,}",
  [659813] = "{id=659813,Text='来~带你去看看这时候的花开伊始之地~会很特别哦！',Speaker=828235,}",
  [659814] = "{id=659814,Text='准备好的话，就来接触晨曦之门吧~它会送我们去往目的地~',Speaker=828235,}",
  [659815] = "{id=659815,Text='是否前往花开伊始之地？',Speaker=828258,Option='{立刻出发,11},{稍等片刻,12}',}",
  [659816] = "{id=659816,Text='花开伊始之地可以说是晨曦祭典重要的后方了~',Speaker=828235,}",
  [659817] = "{id=659817,Text='这里的土壤是浮空精灵们的骄傲，没有这里的土壤种不出的花~',Speaker=828235,}",
  [659818] = "{id=659818,Text='所以，大家都会选择在这里培育自己的永恒之花。',Speaker=828235,}",
  [659819] = "{id=659819,Text='在料理花朵之余，精灵们也会在此结伴踏青野餐，品尝花蜜酒。',Speaker=828235,}",
  [659820] = "{id=659820,Text='来，跟紧我，可不要走丢了哦！',Speaker=828235,}",
  [659821] = "{id=659821,Text='小精灵们正在派发花蜜酒！',Speaker=828235,Option='{花蜜酒？ ,0},{好喝吗？,0}',}",
  [659822] = "{id=659822,Text='唔……怎么说呢~这是小妖精们在晨曦祭典的传统，它们会收集被风吹落的花瓣来酿酒。',Speaker=828235,}",
  [659823] = "{id=659823,Text='但在晨曦祭典期间，各式各样的花齐聚花开伊始之地，不乏古怪的品种。',Speaker=828235,}",
  [659824] = "{id=659824,Text='所以，这酒的味道就比较看运气了。',Speaker=828235,}",
  [659825] = "{id=659825,Text='不管怎么说，这也是晨曦祭典的传统，我们过去讨要一杯吧~',Speaker=828235,}",
  [659826] = "{id=659826,Text='已经给过你们好几大桶了！怎么没完没了了！',Speaker=828248,}",
  [659827] = "{id=659827,Text='没完没了了！',Speaker=828249,}",
  [659828] = "{id=659828,Text='哦~小圣女！请帮帮我们！这群玩偶魔妖太贪婪了！',Speaker=828248,}",
  [659829] = "{id=659829,Text='太贪婪了！',Speaker=828249,}",
  [659830] = "{id=659830,Text='感谢二位的帮助~不然可就麻烦了！',Speaker=828248,}",
  [659831] = "{id=659831,Text='可就麻烦了！',Speaker=828249,}",
  [659832] = "{id=659832,Text='来一杯被晨曦祝福的花蜜酒吧~',Speaker=828248,}",
  [659833] = "{id=659833,Text='花蜜酒吧~',Speaker=828249,}",
  [659834] = "{id=659834,Text='喝下花蜜酒就能将永恒带回家~',Speaker=828248,}",
  [659835] = "{id=659835,Text='带回家~',Speaker=828249,Option='来一杯',}",
  [659836] = "{id=659836,Text='有品位的异乡人，这是你的酒~',Speaker=828248,}",
  [659837] = "{id=659837,Text='你的酒~',Speaker=828249,}",
  [659838] = "{id=659838,Text='甜甜的，清凉的液体从口中流入，但似乎夹杂着一丝苦涩。',}",
  [659839] = "{id=659839,Text='怎么样？味道如何？',Speaker=828235,Option='{甜甜的 ,0},{但有点苦,0}',}",
  [659840] = "{id=659840,Text='哎呀呀！哎呀呀！是哪个孩子在伤心！？',Speaker=828248,}",
  [659841] = "{id=659841,Text='哪个孩子在伤心！？',Speaker=828249,}",
  [659842] = "{id=659842,Text='愿时间能治愈他心中的苦涩。',Speaker=828248,}",
  [659843] = "{id=659843,Text='愿时间能治愈他心中的苦涩。',Speaker=828249,Option='{我没有啊…… ,0},{什么意思……,0}',}",
  [659844] = "{id=659844,Text='嘻嘻~他们并不是在说你啦！',Speaker=828235,}",
  [659845] = "{id=659845,Text='有一种说法是，酒的味道与酿酒使用的花瓣主人的心情有关。',Speaker=828235,}",
  [659846] = "{id=659846,Text='我曾经还喝到过甜过头的酒，那或许说明，大家的心情都是快乐和甜蜜吧！',Speaker=828235,}",
  [659847] = "{id=659847,Text='我们继续走走吧，前面还有风之轻语乐团的演出。',Speaker=828235,}",
  [659848] = "{id=659848,Text='看，那就是风之轻语乐团，他们正在这里传颂晨曦祭典的歌谣。',Speaker=828235,}",
  [659849] = "{id=659849,Text='永恒之花、纯洁的永恒之花，晨曦的气息滋养着你，辉煌地绽放。',Speaker=828250,}",
  [659850] = "{id=659850,Text='你从古老的土壤而来，去到崭新纪年，在伊始之地开花。',Speaker=828250,}",
  [659852] = "{id=659852,Text='这是第一次举办晨曦祭典时，苍夜公主向晨曦献上永恒之花时的景象。',Speaker=828235,}",
  [659853] = "{id=659853,Text='辉煌领域可以说从那个时候开始，开启了新的纪元。',Speaker=828235,}",
  [659854] = "{id=659854,Text='那么，我们就去看看，永恒之花是如何诞生的吧！',Speaker=828235,}",
  [659855] = "{id=659855,Text='我怎么总觉得有人在盯着我们……',Speaker=828235,}",
  [659856] = "{id=659856,Text='就是这里！你看，已经有很多花种被种植在了这里了。',Speaker=828235,Option='{乱七八糟…… ,0},{真是壮观……,0}',}",
  [659857] = "{id=659857,Text='毕竟~大家心目中的关于“永恒”的愿望各不相同~',Speaker=828235,}",
  [659858] = "{id=659858,Text='更何况，在我们两个的共同努力下，更是丰富了花的种类~',Speaker=828235,Option='居然还都盛开了',}",
  [659859] = "{id=659859,Text='嘿嘿~这块土壤我们提前松土和施肥，另外还加入了一些精灵独特的魔法。',Speaker=828235,}",
  [659860] = "{id=659860,Text='不过，魔法可不能过多干预自然的生长~所以~也只有在晨曦祭典上才能看到这样的景象啦！',Speaker=828235,}",
  [659861] = "{id=659861,Text='好香好香~这里~好香~啊~~姐妹们！有了它们~我们会变得更迷人~',Speaker=828252,}",
  [659862] = "{id=659862,Text='等等！——这里的花不行——！',Speaker=828235,}",
  [659863] = "{id=659863,Text='可是~别的地方的花呀！都没有这里的漂亮呀~姐妹们~还不赶紧挑选！',Speaker=828252,}",
  [659864] = "{id=659864,Text='哎呀！不好！不能让她们采摘永恒之花！这可是大家的心血呀！',Speaker=828235,}",
  [659865] = "{id=659865,Text='哼，不给就不给嘛……怎么还打我们~要是美丽的羽毛受损了，我绝不绕你们！',Speaker=828252,}",
  [659866] = "{id=659866,Text='呀！是玩偶魔妖！它们躲在蘑菇后面干什么？',Speaker=828235,Option='{我也不知道 ,0},{别管它们了,0}',}",
  [659867] = "{id=659867,Text='呼~幸好我们在这，花朵们都无碍。我得赶紧回去让布兰迪斯在这里加强警卫。',Speaker=828235,}",
  [659868] = "{id=659868,Text='[c][ffff00][PlayerName][-][/c]，记得要种下属于自己的永恒之花~花种可以去苗圃挑选哦！',Speaker=828235,}",
  [659873] = "{id=659873,Text='跟上他们！',}",
  [659874] = "{id=659874,Text='来了来了！',}",
  [659875] = "{id=659875,Text='被发现了！',}",
  [659877] = "{id=659877,Text='冒险者！花祭晚会会在今晚举行哦！这是整个祭典中我最推荐的环节~',Speaker=828352,Option='{什么是花祭晚会？ ,11},{几点开始？,12},{需要做些什么？,13},{我会参加的,14}',}",
  [659878] = "{id=659878,Text='花祭晚会是晨曦祭典的传统表演~唔——听布兰迪斯大人说，像人类的烟花一样。',Speaker=828352,}",
  [659879] = "{id=659879,Text='不同的是，精灵们会用产自艾卡拉奇的鲜花编排出美轮美奂的表演，可漂亮了！！',Speaker=828352,}",
  [659880] = "{id=659880,Text='届时，艾卡拉奇会如同下了一场花瓣雨，花香将充斥着整座城市~',Speaker=828352,}",
  [659881] = "{id=659881,Text='用你们人类的计时方式来说——嗯……会在每周六周日晚上19点、20点、21点举办花祭晚会。',Speaker=828352,}",
  [659882] = "{id=659882,Text='一整个月的周末都会进行演出哦！千万不要错过了~~',Speaker=828352,}",
  [659883] = "{id=659883,Text='当然需要啦！需要你拿起你那神奇的——嗯？是叫照相机吧……记录下美丽的演出吧！',Speaker=828352,}",
  [659884] = "{id=659884,Text='你也可以叫上你伙伴或是心爱的人，一起享受这份浪漫~',Speaker=828352,}",
  [659885] = "{id=659885,Text='总之，就是享受这场祭典盛宴吧！',Speaker=828352,}",
  [659886] = "{id=659886,Text='那么~今晚见哦！',Speaker=828352,}",
  [659887] = "{id=659887,Text='人类冒险者~你好~我们正在举办晨曦祭典，诚邀大家前往艾卡拉奇做客~',Speaker=828255,Option='晨曦祭典？',}",
  [659888] = "{id=659888,Text='没错~浮空精灵们为了感谢所有人的付出和辛劳而举办的祭典~',Speaker=828255,}",
  [659889] = "{id=659889,Text='冒险者，忙碌了这么久，要不要参加祭典放松一下~这可是我们第一次对外举办晨曦祭典呢~[c][ffff00]【该任务同账号仅限一个角色接取，请慎重考虑是否接取。】[-][/c]',Speaker=828255,Option='{必须参加 ,11},{还有些事,12}',}",
  [659890] = "{id=659890,Text='呀~太可惜了！晨曦祭典将持续一整个月~要是有空，欢迎随时加入呀！',Speaker=828255,}",
  [659891] = "{id=659891,Text='你不会后悔这个决定的！看到我旁边的晨曦之门了吗？',Speaker=828255,}",
  [659892] = "{id=659892,Text='这是特地为这次祭典开辟的空间通道，直达祭典现场~',Speaker=828255,}",
  [659893] = "{id=659893,Text='如果你做好准备了，就碰触晨曦之门，前往艾卡拉奇参与这场盛大的祭典吧~',Speaker=828255,}",
  [659894] = "{id=659894,Text='是否前往艾卡拉奇参与晨曦祭典？',Speaker=828258,Option='{立刻出发 ,21},{稍等片刻,22}',}",
  [659895] = "{id=659895,Text='冒险者！欢迎来到艾卡拉奇的晨曦祭典~',Speaker=828259,}",
  [659896] = "{id=659896,Text='这可是精灵们为感谢所有辛勤劳动的人，并祈求晨曦永恒祝福而举办的迎接新光耀年的祭典。',Speaker=828259,}",
  [659897] = "{id=659897,Text='嗯……总而言之，就是类似于你们人类的新年吧！~',Speaker=828259,}",
  [659898] = "{id=659898,Text='好啦~就让我来带你四处逛逛吧~',Speaker=828259,}",
  [659899] = "{id=659899,Text='抱歉，这里有些乱糟糟的……',Speaker=828259,}",
  [659900] = "{id=659900,Text='啊，看！是布兰迪斯大人！',Speaker=828259,}",
  [659901] = "{id=659901,Text='布——兰——迪——斯——大——人——！',Speaker=828259,}",
  [659902] = "{id=659902,Text='过去和布兰迪斯大人打个招呼吧~他应该会很高兴的！',Speaker=828259,}",
  [659903] = "{id=659903,Text='[c][ffff00][PlayerName][-][/c]！没想到你这个大忙人居然有空来参加我们的祭典。',Speaker=828260,Option='{你在做什么？ ,0},{这里是在……？,0}',}",
  [659904] = "{id=659904,Text='哦，这里正在为晨曦祭典的花祭晚会做准备~我嘛~当然是现场指挥，这也是大祭司的工作之一。',Speaker=828260,Option='花祭晚会？',}",
  [659905] = "{id=659905,Text='嗯，会持续整个祭典的花祭晚会。感兴趣的话，欢迎在祭典期间的星月日来参加！',Speaker=828260,}",
  [659906] = "{id=659906,Text='星月日就是——嗯，你们人类计时中所说的周六和周日。',Speaker=828260,}",
  [659907] = "{id=659907,Text='一定要来！花祭晚会真的！非常！非常！非常！好看！真的！',Speaker=828259,}",
  [659908] = "{id=659908,Text='哈哈哈哈，那是当然，这可是晨曦祭典上，最有人气的活动了。',Speaker=828260,}",
  [659909] = "{id=659909,Text='更何况，今年还是由我来设计的，绝对是史上最精彩的鲜花演出。',Speaker=828260,}",
  [659910] = "{id=659910,Text='诶！——怎么回事？！！',Speaker=828260,}",
  [659911] = "{id=659911,Text='呀！好像布置出现了意外！我们过去看一下有什么能帮忙的吧！',Speaker=828259,}",
  [659912] = "{id=659912,Text='布兰迪斯大人！就是这群三角兽突然闯进来，让准备好的鲜花盒提前炸开了！',Speaker=828261,}",
  [659913] = "{id=659913,Text='还愣着干什么？赶紧把它们都赶出去！别让它们继续搞破坏了！',Speaker=828260,}",
  [659914] = "{id=659914,Text='[c][ffff00][PlayerName][-][/c]！你怎么来了？',Speaker=828260,Option='{帮助你们,0},{找点事做 ,0}',}",
  [659915] = "{id=659915,Text='谢谢你的帮助，但客人就要有客人的样子，这里的事你就别管了！',Speaker=828260,}",
  [659916] = "{id=659916,Text='莱拉，你照顾好[c][ffff00][PlayerName][-][/c]，我就先失陪了。',Speaker=828260,}",
  [659917] = "{id=659917,Text='都打起精神来！抓紧时间！',Speaker=828260,}",
  [659918] = "{id=659918,Text='来吧，冒险者，我们继续四处逛逛吧~',Speaker=828259,}",
  [659919] = "{id=659919,Text='冒险者，你看！这里已经都点缀上了鲜花装饰~是不是很漂亮？',Speaker=828259,Option='{好漂亮啊,0},{花费不少 ,0}',}",
  [659920] = "{id=659920,Text='毕竟每一个浮空精灵都为这场祭典努力呢~',Speaker=828259,}",
  [659921] = "{id=659921,Text='“自浮空精灵来到这天地树的枝灌起，晨曦女神奥斯塔拉便将晨曦之光重新赐予了这片黑暗的领域。”',Speaker=828259,}",
  [659922] = "{id=659922,Text='“微风从古老的花园中吹来了种子，巨人让花朵绽放点缀了这片荒芜的土地。”',Speaker=828259,}",
  [659923] = "{id=659923,Text='艾卡拉奇自绽放第一朵花后，在我们浮空精灵的努力下，荒芜的艾卡拉奇开满了鲜花。',Speaker=828259,}",
  [659924] = "{id=659924,Text='不过，花朵的灿烂可是很短暂的~一定要珍惜呀！',Speaker=828259,}",
  [659925] = "{id=659925,Text='让让——当心——快让让——！',Speaker=828235,}",
  [659926] = "{id=659926,Text='圣——圣女大人！你！你怎么在这里？！',Speaker=828259,}",
  [659927] = "{id=659927,Text='唔——头晕——诶！这不是[c][ffff00][PlayerName][-][/c]和莱拉吗？',Speaker=828235,Option='{没事吧？,0},{好久不见！ ,0}',}",
  [659928] = "{id=659928,Text='圣女大人！你没事吧？你什么时候回来的？',Speaker=828259,}",
  [659929] = "{id=659929,Text='唔，我也就刚刚才到。晨曦祭典我怎么能错过？',Speaker=828235,}",
  [659930] = "{id=659930,Text='看！这是我从各地搜集到的花种！是布兰迪斯委托我带回来的~',Speaker=828235,Option='{花种？,0},{有什么用？ ,0}',}",
  [659931] = "{id=659931,Text='哎呀！都怪我，我还没来得及和冒险者介绍晨曦祭典的传统！',Speaker=828259,}",
  [659932] = "{id=659932,Text='没关系，我来介绍吧~晨曦祭典上，最重要的就是花了！',Speaker=828235,}",
  [659933] = "{id=659933,Text='很久以前，苍夜公主会将这片土地上精灵们耕耘出的花朵献给晨曦，以求晨曦永恒的眷顾。',Speaker=828235,}",
  [659934] = "{id=659934,Text='这朵花被称之为“永恒之花”，它象征着这片土地与精灵们的耕耘。',Speaker=828235,}",
  [659935] = "{id=659935,Text='而现在，精灵们都会选择一朵自己心目中的“永恒之花”，或是直接采摘，或是自己亲手培育。',Speaker=828235,}",
  [659936] = "{id=659936,Text='我这次负责的就是花种筹备，为精灵们和外乡人们提供各式各样的花种，让他们挑选属于自己的永恒之花。',Speaker=828235,}",
  [659937] = "{id=659937,Text='来吧~我带你们去祭典花圃瞧瞧~',Speaker=828235,}",
  [659938] = "{id=659938,Text='就是这里啦！怎么样？我精心打理的花圃，很漂亮吧？',Speaker=828235,}",
  [659939] = "{id=659939,Text='真好看！圣女大人！你真厉害！',Speaker=828259,}",
  [659940] = "{id=659940,Text='[c][ffff00][PlayerName][-][/c]，我记得你有个神奇的，可以将眼前所见绘制成影像的神奇道具，叫什么来着？',Speaker=828235,Option='照相机',}",
  [659941] = "{id=659941,Text='没错没错！就是它！能帮我记录一下我的成果吗？',Speaker=828235,Option='没问题',}",
  [659942] = "{id=659942,Text='哇！真好看！你们人类的道具真的很神奇！',Speaker=828235,}",
  [659943] = "{id=659943,Text='圣女！圣女！',Speaker=828264,}",
  [659944] = "{id=659944,Text='可算找到你了！——',Speaker=828264,}",
  [659945] = "{id=659945,Text='你之前拜托我们找的花种，我们那有的都在这里了。',Speaker=828264,}",
  [659946] = "{id=659946,Text='剩下的花种需要去外面寻找了，那太危险了，我们可能不敢！',Speaker=828264,}",
  [659947] = "{id=659947,Text='诶！莉莉丝！等等——这，这可怎么办呀……',Speaker=828235,}",
  [659948] = "{id=659948,Text='这段时间我需要在这里看管花圃，分发花种……也没法亲自去找……',Speaker=828235,}",
  [659949] = "{id=659949,Text='唔……外面的话，冒险者可以帮忙呀！',Speaker=828259,}",
  [659950] = "{id=659950,Text='这……这不好吧？[c][ffff00][PlayerName][-][/c]是我们的客人……',Speaker=828235,Option='{我可以帮忙,0},{已经习惯了 ,0}',}",
  [659951] = "{id=659951,Text='真的吗？！感谢晨曦的眷顾！[c][ffff00][PlayerName][-][/c]，浮空精灵感谢你的善举。',Speaker=828235,Option='具体需要做什么',}",
  [659952] = "{id=659952,Text='天地树世界中，有一些珍稀的花种诞生于植物系的魔物的身上~',Speaker=828235,}",
  [659953] = "{id=659953,Text='简单来说你要做的事情就是——打倒魔物，取得花种！',Speaker=828235,}",
  [659954] = "{id=659954,Text='所幸离祭典正式开启还有一些时间，不用着急，我们可以慢慢来！',Speaker=828235,}",
  [659955] = "{id=659955,Text='你可以先四处看看，等空闲了再来找我。',Speaker=828235,}",
  [659956] = "{id=659956,Text='周围的精灵守卫增多，但是……花朵的数量似乎比之前少了。',}",
  [659957] = "{id=659957,Text='不能放任失窃案继续发生！',Speaker=828260,}",
  [659958] = "{id=659958,Text='[c][ffff00][PlayerName][-][/c]！你快看看这里！',Speaker=828260,Option='{守卫变多了,0},{花朵变少了 ,0}',}",
  [659959] = "{id=659959,Text='哎，没想到增派守卫，也没有防止永恒之花失窃。',Speaker=828260,Option='失窃？！',}",
  [659960] = "{id=659960,Text='没错，我们赶走了鸟人哈比后，布兰迪斯就增派这里的守卫。',Speaker=828235,}",
  [659961] = "{id=659961,Text='只是没想到，永恒之花还是失窃了！',Speaker=828235,Option='会不会还是鸟人哈比？',}",
  [659962] = "{id=659962,Text='不是她们，我们已经确认了她们当天就离开了艾卡拉奇去往别处了。',Speaker=828260,}",
  [659963] = "{id=659963,Text='已经好几天了，都抓不到小偷——这下可怎么办，祭典马上就要到了！',Speaker=828235,}",
  [659964] = "{id=659964,Text='我会安排人调查小偷的踪迹，并继续加强守卫。',Speaker=828260,}",
  [659965] = "{id=659965,Text='哼，别让我抓到这个偷窃小贼！否则，哼！',Speaker=828260,}",
  [659966] = "{id=659966,Text='呀——气呼呼地走掉了……他最近的压力一定很大吧……',Speaker=828235,Option='{脾气真差,0},{也是可怜 ,0}',}",
  [659967] = "{id=659967,Text='[c][ffff00][PlayerName][-][/c]！我们帮忙一起找找小偷的踪迹吧！不然或许你的花也会……',Speaker=828235,Option='必须找出小偷！',}",
  [659968] = "{id=659968,Text='我们先在附近向守卫们打听一下线索吧。',Speaker=828235,}",
  [659969] = "{id=659969,Text='希望小偷能够收手吧，被窃花朵的主人该多伤心？',Speaker=828265,Option='有什么异常？',}",
  [659970] = "{id=659970,Text='说到异常……这两天蜂兵的数量明显变多了，虽然它们只是在采蜜……',Speaker=828265,}",
  [659971] = "{id=659971,Text='可我觉得是小偷鸟干的！',Speaker=828266,Option='小偷鸟？',}",
  [659972] = "{id=659972,Text='不然如何解释？我们日夜守在这里！只有小偷鸟才能神不知鬼不觉的偷走！',Speaker=828266,}",
  [659973] = "{id=659973,Text='这么一说……之前在月色朦胧的时候，有一只虎王踏入此处……',Speaker=828265,Option='然后呢？',}",
  [659974] = "{id=659974,Text='然后……然后我就睡过去了……哎，可能最近太累了……',Speaker=828265,}",
  [659975] = "{id=659975,Text='怎么样？有找到小偷的线索吗？',Speaker=828235,Option='说明情况',}",
  [659976] = "{id=659976,Text='唔……居然有三个嫌疑犯！看来得把它们带过来调查一下。',Speaker=828235,}",
  [659977] = "{id=659977,Text='这里似乎就是蜂兵聚集的地方，等等——它们好像把我当敌人了！',}",
  [659978] = "{id=659978,Text='住手！——',Speaker=828268,}",
  [659979] = "{id=659979,Text='为何要伤害我的子民！',Speaker=828268,Option='说明情况',}",
  [659980] = "{id=659980,Text='怀疑我的子民有偷窃行为，这种污蔑之事，我必须亲自澄清。',Speaker=828268,}",
  [659981] = "{id=659981,Text='蜂族世代采蜜为生，而恰好这里的花长势极好，我们经常光顾有什么问题？',Speaker=828268,}",
  [659982] = "{id=659982,Text='小偷鸟似乎正在清点金币的数量，十分专注。',}",
  [659983] = "{id=659983,Text='叽叽！你想干什么！金币都是我的！我的！',Speaker=828269,}",
  [659984] = "{id=659984,Text='叽叽！叽叽！叽叽！我的金币……',Speaker=828269,Option='不要你的金币',}",
  [659985] = "{id=659985,Text='不要金币！叽叽！你说吧！来找我做什么！只要不要金币我都答应！叽叽！',Speaker=828269,Option='说明情况',}",
  [659986] = "{id=659986,Text='叽叽！开什么玩笑！叽叽！这花是长成金币的模样嘛！我稀罕它们？！',Speaker=828269,}",
  [659987] = "{id=659987,Text='走！我必须去证明我的眼光！叽叽叽叽！！！',Speaker=828269,}",
  [659988] = "{id=659988,Text='叽叽！就这些破花！它们是有金闪闪的光芒吗？叽叽！送我我都不要！',Speaker=828269,}",
  [659989] = "{id=659989,Text='虎王似乎睡得正香甜，呼噜声震耳欲聋。',}",
  [659990] = "{id=659990,Text='是谁在摸老子的屁股！吼——！',Speaker=828270,}",
  [659991] = "{id=659991,Text='叫醒老子想干嘛！吼——！要是没什么事就再揍你一顿！',Speaker=828270,Option='说明情况',}",
  [659992] = "{id=659992,Text='什么！永恒之花被偷了！——不好！',Speaker=828270,}",
  [659993] = "{id=659993,Text='哦~幸好没事~呜呜~我的宝贝永恒之花真香！',Speaker=828270,}",
  [659994] = "{id=659994,Text='唔……有点难办呢……你觉得小偷会在它们当中吗？',Speaker=828235,Option='{蜂后,11},{小偷鸟 ,12},{虎王 ,13},{不是它们 ,14}',}",
  [659995] = "{id=659995,Text='唔……它们有经常来的理由，却没有偷走花的理由。',Speaker=828235,}",
  [659996] = "{id=659996,Text='我仔细看过它的赃物，全都是金币，没找到花朵。',Speaker=828235,}",
  [659997] = "{id=659997,Text='它似乎也种下了永恒之花，看上去也只在乎这一朵。',Speaker=828235,}",
  [659998] = "{id=659998,Text='难道小偷并不在它们之中？那……还能是谁呢？',Speaker=828235,}",
  [659999] = "{id=659999,Text='小圣女，小圣女，其实我们看到了哦！',Speaker=828248,}",
  [660000] = "{id=660000,Text='看到了哦！',Speaker=828249,}",
  [660001] = "{id=660001,Text='真的吗？是谁？是谁偷走了永恒之花？',Speaker=828235,}",
  [660002] = "{id=660002,Text='是他们哦！再等一会，他们就会出现啦！',Speaker=828248,}",
  [660003] = "{id=660003,Text='就会出现啦！',Speaker=828249,}",
  [660004] = "{id=660004,Text='嘘——他们来喽。屏住呼吸~',Speaker=828248,}",
  [660005] = "{id=660005,Text='屏住呼吸~',Speaker=828249,}",
  [660006] = "{id=660006,Text='这是——晕晕粉！难怪要我们屏住呼吸。',Speaker=828235,}",
  [660007] = "{id=660007,Text='原来是它们！[c][ffff00][PlayerName][-][/c]，快！快阻止它们！',Speaker=828235,}",
  [660009] = "{id=660009,Text='哼，还想逃？说！你们为什么要盗窃永恒之花！',Speaker=828260,}",
  [660010] = "{id=660010,Text='密米尔大人！密米尔大人！救命！救命！',Speaker=828247,}",
  [660011] = "{id=660011,Text='啊？这究竟是怎么回事？',Speaker=828235,}",
  [660012] = "{id=660012,Text='密米尔大人！密米尔大人！密米尔大人！密米尔大人！',Speaker=828247,}",
  [660013] = "{id=660013,Text='这魔力——那个糟老头子！',Speaker=828260,}",
  [660014] = "{id=660014,Text='啊——他又气呼呼地走掉了——',Speaker=828235,}",
  [660015] = "{id=660015,Text='看起来，是密米尔大人联合玩偶魔妖们偷窃了永恒之花呢……',Speaker=828235,}",
  [660016] = "{id=660016,Text='可他们为什么要这样做呢？',Speaker=828235,Option='为什么呢？',}",
  [660017] = "{id=660017,Text='之后，只能去找密米尔大人听他亲口说说了。',Speaker=828235,}",
  [660018] = "{id=660018,Text='不过小偷的身份和偷窃方式都已经很清楚了，我会将这些告知守卫们的。',Speaker=828235,}",
  [660019] = "{id=660019,Text='谢谢你们，安琪琪和卢卡卡。',Speaker=828235,}",
  [660020] = "{id=660020,Text='[c][ffff00][PlayerName][-][/c]，请继续享受晨曦祭典吧！',Speaker=828235,}",
  [660021] = "{id=660021,Text='[c][ffff00][PlayerName][-][/c]！那臭老头的酒是不是酿完了？',Speaker=828260,Option='{差不多了,0},{不太清楚 ,0}',}",
  [660022] = "{id=660022,Text='我算算时间也该差不多了。咳咳，别的我不敢保证，但臭老头的酒还是相当美味的。',Speaker=828260,}",
  [660023] = "{id=660023,Text='前段时间看你帮他收集花瓣那么忙碌，想必这次他酿酒的数量不会少吧。',Speaker=828260,Option='你该不会…… ',}",
  [660024] = "{id=660024,Text='嘿嘿，作为辉煌领域曾经的主人，他也要为晨曦祭典出一份力，对不对？',Speaker=828260,}",
  [660025] = "{id=660025,Text='哼，那臭老头一定会想办法把酒藏起来自己独享。',Speaker=828260,}",
  [660026] = "{id=660026,Text='不用担心，你只要正常前去赴约即可，剩下的交给我。',Speaker=828260,}",
  [660027] = "{id=660027,Text='嘘——（小声）不要声张。闻到了吗？这幽幽的花香和清冽的酒香。',Speaker=828241,Option='可以品尝了吗?',}",
  [660028] = "{id=660028,Text='不要心急哟！现在还差最后一步，我需要一点点拥有“时间魔法“的水。',Speaker=828241,Option='哪里有？',}",
  [660029] = "{id=660029,Text='在时间花园，最不用担心的就是”时间“了。',Speaker=828241,}",
  [660030] = "{id=660030,Text='现在时间刚好，那些由时间凝固而成的花朵上，正凝结着拥有了时间魔力的露珠。',Speaker=828241,}",
  [660031] = "{id=660031,Text='不错不错。那么，来完成最后一步吧！',Speaker=828241,}",
  [660032] = "{id=660032,Text='要不是你帮我收集齐了花瓣，我才不会分你几口呢。',Speaker=828241,}",
  [660033] = "{id=660033,Text='来吧，臭小子，给你涨涨见识！',Speaker=828241,}",
  [660034] = "{id=660034,Text='一种从所未有的滋味从舌尖蔓延，整个身体进入了一种轻飘飘的状态，感觉到了幸福。',}",
  [660035] = "{id=660035,Text='嚯嚯嚯~看你小子的表情就知道，老头我的手艺啊！可没退步哦！',Speaker=828241,}",
  [660036] = "{id=660036,Text='臭臭老头！吃独食！吃独食！',Speaker=828247,}",
  [660037] = "{id=660037,Text='！！！！你们！你们怎么来了！？',Speaker=828241,}",
  [660038] = "{id=660038,Text='我们也要喝酒！我们也要喝酒！',Speaker=828247,}",
  [660039] = "{id=660039,Text='小子！！快！！帮我拦住它们！！！我的酒啊——！',Speaker=828241,}",
  [660040] = "{id=660040,Text='呜哇哇哇——呜哇哇哇——臭臭老头和臭臭冒险者联合欺负我们！！',Speaker=828247,}",
  [660041] = "{id=660041,Text='哦？他们怎么欺负你了？',Speaker=828260,}",
  [660042] = "{id=660042,Text='！！！小布布？？？你怎么来了？！',Speaker=828241,}",
  [660043] = "{id=660043,Text='我这是听到了有小东西在控诉，就赶过来主持一下正义。',Speaker=828260,}",
  [660044] = "{id=660044,Text='呜呜，臭臭老头让我们去偷花，说要做好喝的酒给我们喝。',Speaker=828247,}",
  [660045] = "{id=660045,Text='我们好几夜啊好几夜啊没睡觉！帮他偷花，结果被臭臭冒险者打了一顿，呜呜……',Speaker=828247,Option='{……,0},{？？？ ,0}',}",
  [660046] = "{id=660046,Text='呜呜呜呜，结果啊，臭臭老头和臭臭冒险者居然偷偷自己喝酒……然后又打了我们……',Speaker=828247,}",
  [660047] = "{id=660047,Text='呜哇哇哇哇哇——呜哇哇哇哇哇哇——',Speaker=828247,Option='{别哭了！,0},{对不起…… ,0}',}",
  [660048] = "{id=660048,Text='确实是可恶！',Speaker=828260,}",
  [660049] = "{id=660049,Text='我……啊……这……别哭了……这里的酒给你们喝行了吧！',Speaker=828241,}",
  [660050] = "{id=660050,Text='老师，智慧巨人使用了时间魔法酿出来的酒，可不止这些吧？',Speaker=828260,}",
  [660051] = "{id=660051,Text='你说什么呢？什么时间魔法？嗯——当然就只有这点啦——',Speaker=828241,}",
  [660052] = "{id=660052,Text='嗯，刚刚玩偶魔妖出现的时候，你的眼神一直看向那个方向吧？',Speaker=828260,}",
  [660053] = "{id=660053,Text='！我的花酿酒！！！！你居然拿走了我的酒之泉！啊啊啊啊啊——',Speaker=828241,}",
  [660054] = "{id=660054,Text='嗯，香味醇厚，这么好的酒可不得给大家分享。',Speaker=828260,}",
  [660055] = "{id=660055,Text='魔妖们，来参加晨曦祭典吧，这酒随便喝！',Speaker=828260,}",
  [660056] = "{id=660056,Text='布兰迪斯！你个臭小子！你是故意的吧！玩偶魔妖也是你引来的吧！？',Speaker=828241,}",
  [660057] = "{id=660057,Text='老师，你说什么呢？我这怎么会做这种事呢？好了，快到晨曦之时了。',Speaker=828260,}",
  [660058] = "{id=660058,Text='[c][ffff00][PlayerName][-][/c]，我先行一步，请你稍后来艾卡拉奇找我，还有事要拜托你。',Speaker=828260,}",
  [660059] = "{id=660059,Text='我的酒！我的酒！可恶啊！！布布——你变了——布布——你怎么变成了这样！？',Speaker=828241,}",
  [660060] = "{id=660060,Text='那可是我精心制作，加入了时间魔法，取之不竭，喝之不尽的酒之泉啊！',Speaker=828241,}",
  [660061] = "{id=660061,Text='没有了它……我活着还有什么意义！',Speaker=828241,}",
  [660062] = "{id=660062,Text='年轻人！冒险者！我只能依靠你了！祭典结束，帮我从布布那里抢回我的酒之泉啊！',Speaker=828241,Option='{考虑一下,0},{我答应你 ,0}',}",
  [660063] = "{id=660063,Text='（嘟囔）美味的花酿酒，甜甜的梦里花——',Speaker=828241,}",
  [660064] = "{id=660064,Text='智慧巨人此时像个失意的小老头，精神有些恍惚了。',}",
  [660065] = "{id=660065,Text='[c][ffff00][PlayerName][-][/c]，这里这里~！',Speaker=828235,}",
  [660066] = "{id=660066,Text='[c][ffff00][PlayerName][-][/c]，我想邀请你向晨曦献上“永恒之花”。',Speaker=828260,Option='{我可以吗？,0},{怎么是我？ ,0}',}",
  [660067] = "{id=660067,Text='当然，必须是你呀！你可是拯救艾卡拉奇的大英雄呢！',Speaker=828235,}",
  [660068] = "{id=660068,Text='嗯，况且这并不是我们两个决定的，是浮空精灵们共同的意愿。',Speaker=828260,}",
  [660069] = "{id=660069,Text='我们也希望通过这样的形式，向你表达我们的感谢和祝愿。',Speaker=828235,}",
  [660071] = "{id=660071,Text='准备好了吗？可不要错过新光耀年的第一缕晨曦之光。',Speaker=828260,Option='{开始吧,11},{再等一下 ,12}',}",
  [660072] = "{id=660072,Text='不用紧张，你值得这一切。',Speaker=828260,}",
  [660073] = "{id=660073,Text='那么，请，我们最尊贵的客人——',Speaker=828260,}",
  [660081] = "{id=660081,Text='呼，严肃的环节总算结束了。那么，接下来就是放松的时候了。',Speaker=828260,}",
  [660082] = "{id=660082,Text='嗯？你还要做什么？',Speaker=828235,}",
  [660083] = "{id=660083,Text='哈哈~走吧~给你们看看我好不容易从老师那”抢“来的好东西。',Speaker=828260,}",
  [660084] = "{id=660084,Text='就是这里了。你们等着看好吧。',Speaker=828260,}",
  [660085] = "{id=660085,Text='哦，我的奥斯塔拉，这是什么？看上去……神圣而又温暖，唔~还有花香和酒香！',Speaker=828235,}",
  [660086] = "{id=660086,Text='这是酒之泉，是老师用智慧与时间凝结出的，能够源源不断涌出美味花酿酒的魔法。',Speaker=828260,}",
  [660087] = "{id=660087,Text='这种好东西，怎么能让被他一个人私藏，当然是要拿出来给大家一起享用。',Speaker=828260,}",
  [660088] = "{id=660088,Text='好啦~大家！尽情享用花酿酒吧，享受艾卡拉奇的晨曦祭典吧！',Speaker=828260,}",
  [660091] = "{id=660091,Text='给我们酒！',}",
  [660092] = "{id=660092,Text='不够不够！',}",
  [660093] = "{id=660093,Text='不给就抢！',}",
  [660094] = "{id=660094,Text='小气鬼！',Speaker=828251,}",
  [660102] = "{id=660102,Text='1,2,3,4……',}",
  [660103] = "{id=660103,Text='zzzz',}",
  [660104] = "{id=660104,Text='我的宝贝~',}",
  [660105] = "{id=660105,Text='开工喽！',}",
  [660106] = "{id=660106,Text='不客气！',}",
  [660107] = "{id=660107,Text='好耶！',}",
  [660190] = "{id=660190,Text='呼~总算忙完花种准备的事情了~多亏了你的帮忙！',Speaker=828359,}",
  [660191] = "{id=660191,Text='和你约好的，要带你好好逛逛晨曦祭典。',Speaker=828359,}",
  [660192] = "{id=660192,Text='恭喜！属于你的永恒之花培育完成了~是一朵很漂亮的花哦！',Speaker=828359,Option='那是当然！',}",
  [660193] = "{id=660193,Text='哎，只不过，有些永恒之花就没那么幸运了……',Speaker=828359,Option='发生什么了吗？',}",
  [660194] = "{id=660194,Text='它们失窃了……我带你去花开伊始之地看看吧。',Speaker=828359,}",
  [660195] = "{id=660195,Text='布兰迪斯都已经知道了！',Speaker=828359,Option='知道什么了？',}",
  [660196] = "{id=660196,Text='那天我们不是发现是密米尔大人指使了玩偶魔妖偷窃了永恒之花嘛~',Speaker=828359,}",
  [660197] = "{id=660197,Text='布兰迪斯就气冲冲地去把密米尔大人训斥了一番。',Speaker=828359,}",
  [660198] = "{id=660198,Text='他刚刚又神神秘秘地和我说，你帮密米尔大人到处采集花瓣制作花酿酒！',Speaker=828359,Option='他怎么知道的？',}",
  [660199] = "{id=660199,Text='这我就不知道了……然后他和我说，他会和你一起联手在花祭演出后准备一个惊喜。',Speaker=828359,}",
  [660200] = "{id=660200,Text='这不，他正在会场中心等你呢~',Speaker=828359,}",
  [662167] = "{id=662167,Text='冒险者，快过来，大陆卫队需要你的帮助！',Speaker=829135,Option='怎么了',}",
  [662168] = "{id=662168,Text='听说普隆德拉南门出现了两个鬼鬼祟祟的人，但是我们的人正在执行任务，暂时无法前往调查。',Speaker=829135,Option='鬼鬼祟祟的人？',}",
  [662169] = "{id=662169,Text='没错，根据上报的信息来看，其中一位长相酷似兽人战士，另一位相对来说朴素许多。',Speaker=829135,Option='我明白了',}",
  [662170] = "{id=662170,Text='请接下这份委托，帮助我们调查一下这两个奇怪的人吧！\n【[c][ffff00]活动为账号限定，账号下所有角色共享完成进度[-][/c]。】',Speaker=829135,Option='{没问题,11},{等等再说,12}',}",
  [662171] = "{id=662171,Text='感谢你的帮助，大陆卫队给予你最崇高的敬意！',Speaker=829135,}",
  [662172] = "{id=662172,Text='近期我会一直在这里执行任务，如果你改变主意了，可以再来找我。',Speaker=829135,}",
  [662173] = "{id=662173,Text='嗯？有什么事吗？',Speaker=829136,Option='说明来意',}",
  [662174] = "{id=662174,Text='啊……！我们是被当成鬼鬼祟祟的人了吗？',Speaker=829137,Option='{是的……,0},{难道不像吗？,0}',}",
  [662175] = "{id=662175,Text='才不是这样……我叫高丘敬文，这位是我的舅舅，岛㟢阳介。',Speaker=829137,Option='舅舅？',}",
  [662176] = "{id=662176,Text='呃……你也可以这样叫我。',Speaker=829136,}",
  [662177] = "{id=662177,Text='所以这里是哪里？难不成……我们穿越到了异世界！！？',Speaker=829137,Option='没错',}",
  [662178] = "{id=662178,Text='嗯……我好像知道我们穿越到异世界的原因了……',Speaker=829136,}",
  [662179] = "{id=662179,Text='一定是因为下载了那个游戏，我们才会来到这里。',Speaker=829136,Option='游戏？',}",
  [662180] = "{id=662180,Text='没错，我的面前正浮现着一个任务面板……怎么，你们没有吗？',Speaker=829136,Option='{呃……,0},{这很难说,0}',}",
  [662181] = "{id=662181,Text='当然没有了！不过这种事情发生在舅舅你的身上也不奇怪。',Speaker=829137,}",
  [662182] = "{id=662182,Text='嗯……让我看看任务面板上写了什么……',Speaker=829136,}",
  [662183] = "{id=662183,Text='……集结伙伴……打败危害大陆的魔物赤炎恶灵……拯救米德加尔特大陆……',Speaker=829136,}",
  [662184] = "{id=662184,Text='原来如此！我明白了。',Speaker=829136,Option='{什么？,0},{我也明白了,0}',}",
  [662185] = "{id=662185,Text='只要按照任务面板完成任务，通关游戏，我们就可以回到原来的世界！',Speaker=829136,}",
  [662186] = "{id=662186,Text='哦？？！！说的很有道理！快看看第一个任务是什么？',Speaker=829137,}",
  [662187] = "{id=662187,Text='让我看看……嗯……啊……找一个叫作维琪的女人领取新手武器——乐园团短剑，然后击杀5个魔物。',Speaker=829136,Option='{……,0},{这个我熟,0}',}",
  [662188] = "{id=662188,Text='看起来像是游戏的新手任务，想必也不会难到哪儿去。',Speaker=829136,Option='我来帮你',}",
  [662189] = "{id=662189,Text='真是位好心的冒险者啊！走吧，我们去找维琪。',Speaker=829136,}",
  [662190] = "{id=662190,Text='出发！',Speaker=829137,}",
  [662191] = "{id=662191,Text='是岛㟢阳介吗？看起来激情满满啊~来，让我确认下！',Speaker=1054,}",
  [662192] = "{id=662192,Text='嗯……没有替考，也没发现作弊工具。好的，检查完毕! 接下来请接受乐园团的考试吧！',Speaker=1054,}",
  [662193] = "{id=662193,Text='请给我乐园团短剑！',Speaker=829136,}",
  [662194] = "{id=662194,Text='乐园团的考试内容是……诶？！你是怎么知道的！这是我接下来的台词嘛……',Speaker=1054,}",
  [662195] = "{id=662195,Text='真是奇怪……不过既然你已经知道了，那就请接受我们乐园团为新手定制的武器——乐园团短剑吧！',Speaker=1054,}",
  [662196] = "{id=662196,Text='拿到乐园团短剑后，请击杀5个淘气的波利，我想，这应该难不倒你！',Speaker=1054,}",
  [662197] = "{id=662197,Text='没问题，让我来解决这些淘气的波利！',Speaker=829136,}",
  [662198] = "{id=662198,Text='哦！！这些就是波利吗？看上去真是人畜无害啊！',Speaker=829137,}",
  [662199] = "{id=662199,Text='唔……这可不好说，长相可爱的生物偶尔也会给人致命一击呢。',Speaker=829136,Option='{哦？,0},{展开说说,0}',}",
  [662200] = "{id=662200,Text='这是我根据曾经的异世界经历得出来的结论。',Speaker=829136,}",
  [662201] = "{id=662201,Text='啊……没错，舅舅曾经解救过被关在牢里的小型魔物……但是……',Speaker=829137,}",
  [662202] = "{id=662202,Text='那些可爱的魔物却狠狠地攻击了舅舅。',Speaker=829137,}",
  [662203] = "{id=662203,Text='现在想想，我的颈动脉还隐隐作痛……',Speaker=829136,}",
  [662204] = "{id=662204,Text='~~>.<~~',Speaker=829138,}",
  [662205] = "{id=662205,Text='哦哦！但是这些波利看上去真的很温驯可爱呢！',Speaker=829137,}",
  [662206] = "{id=662206,Text='啊啊啊啊啊！',Speaker=829137,}",
  [662207] = "{id=662207,Text='咻……',Speaker=829138,}",
  [662208] = "{id=662208,Text='所以说啊，千万不要被外表迷惑！',Speaker=829136,}",
  [662209] = "{id=662209,Text='这是你们的……新手魔物？',Speaker=829136,Option='{不是这样的……,0},{你听我解释……,0}',}",
  [662210] = "{id=662210,Text='这样吗……难不成我接受的任务是困难模式……',Speaker=829136,}",
  [662211] = "{id=662211,Text='走吧，接下来该找维琪去交任务了。',Speaker=829136,}",
  [662212] = "{id=662212,Text='居然这么快……！明明是困难模式的波利嘛……',Speaker=1054,}",
  [662213] = "{id=662213,Text='咳咳、太棒了！冒险者们，你们果然没让我失望！',Speaker=1054,Option='{转变得真快,0},{……,0}',}",
  [662214] = "{id=662214,Text='接下来请前往依斯鲁得的香槟蓝集市，请参与香槟蓝美食节，购买食材制作料理吧~',Speaker=1054,}",
  [662215] = "{id=662215,Text='这些zeny是刚刚击杀波利的奖励，请收好，记得要合理使用哦~',Speaker=1054,}",
  [662216] = "{id=662216,Text='1……2……总共有500zeny，货真价实的银币啊，看起来不少嘛！',Speaker=829136,}",
  [662217] = "{id=662217,Text='那我们岂不是可以购买很多食材，制作丰盛无比的料理！',Speaker=829137,Option='{呃……,0},{你想多了,0}',}",
  [662218] = "{id=662218,Text='不管怎么说，先去依斯鲁得看看就知道了！',Speaker=829136,}",
  [662219] = "{id=662219,Text='我们出发！',Speaker=829137,}",
  [662226] = "{id=662226,Text='参加制作肉排的挑战吗……让我想想。',Speaker=829136,}",
  [662227] = "{id=662227,Text='反正舅舅的任务也是制作料理吧，不如参加看看！说不定会有什么了不得的奖励！',Speaker=829137,}",
  [662228] = "{id=662228,Text='说的也是，冒险者，我们去看看吧。',Speaker=829136,}",
  [662230] = "{id=662230,Text='水果味的肉排吗……听起来怪怪的。',Speaker=829136,}",
  [662231] = "{id=662231,Text='这么贵！！！！！我们的500zeny什么也买不起啊……',Speaker=829137,Option='我就知道',}",
  [662232] = "{id=662232,Text='冒险者，购买食材这个艰巨的任务，就交给你了。',Speaker=829136,Option='没问题',}",
  [662236] = "{id=662236,Text='没有购买肉排……无法制作豪华秘制肉排。',}",
  [662237] = "{id=662237,Text='嗯……果然是豪华秘制肉排的味道！',Speaker=829136,}",
  [662238] = "{id=662238,Text='不愧是冒险者！看来我们可以得到奖励了！',Speaker=829137,}",
  [662239] = "{id=662239,Text='好香的味道……你们竟然真的制作出了豪华秘制肉排！',Speaker=829139,}",
  [662240] = "{id=662240,Text='说话算话，宝箱里的奖励就归你们了！',Speaker=829139,}",
  [662241] = "{id=662241,Text='噔噔~奖励就是豪华料理台！',Speaker=829217,}",
  [662242] = "{id=662242,Text='她是怎么把料理台装进这个宝箱里的……',Speaker=829137,}",
  [662243] = "{id=662243,Text='我劝你不要在魔法世界里问这些……',Speaker=829136,}",
  [662244] = "{id=662244,Text='收好你们的奖励，当然，别忘了品尝肉排哦~',Speaker=829139,}",
  [662245] = "{id=662245,Text='唔，味道真不错，等等……',Speaker=829137,}",
  [662246] = "{id=662246,Text='嗯……完全不是豪华秘制肉排嘛。',Speaker=829136,}",
  [662247] = "{id=662247,Text='相差地不是一点……',Speaker=829137,}",
  [662248] = "{id=662248,Text='问题不大，虽然领不到奖励了，但也是可以品尝一下的……',Speaker=829136,}",
  [662249] = "{id=662249,Text='亲爱的冒险者们，看在你们这么辛苦的份上，这个奖励就送给你们吧~',Speaker=829139,}",
  [662250] = "{id=662250,Text='噔噔~奖励就是普通料理台！',Speaker=829217,}",
  [662251] = "{id=662251,Text='收好你们的奖励，当然，别忘了品尝美食哦~',Speaker=829139,}",
  [662252] = "{id=662252,Text='嗯……勉勉强强吧，等等……',Speaker=829137,}",
  [662253] = "{id=662253,Text='我感觉身体出现了一些变化！',Speaker=829137,}",
  [662254] = "{id=662254,Text='原来如此，食用料理使我们的身体素质得到了提升。',Speaker=829136,}",
  [662255] = "{id=662255,Text='先是获取武器，击杀魔物，再是提升身体素质，看来今天的任务是让我们了解这个世界的生存法则嘛！',Speaker=829136,Option='{没错,0},{才没那么简单！,0}',}",
  [662256] = "{id=662256,Text='不管怎么说，今天的任务已经完成了，接下来的任务看起来明天才可以继续。',Speaker=829136,}",
  [662257] = "{id=662257,Text='这样吗？那接下来我们去哪？',Speaker=829137,}",
  [662258] = "{id=662258,Text='按照我以往的异世界经历，找个驿馆是个不错的选择！',Speaker=829136,Option='我来帮忙',}",
  [662259] = "{id=662259,Text='啊！有个异世界的朋友，感觉真是太棒了，多亏了你啊冒险者！',Speaker=829137,Option='不客气',}",
  [662263] = "{id=662263,Text='啊，真是睡了安稳的一觉呢。',Speaker=829136,}",
  [662264] = "{id=662264,Text='说起来，我竟然成功地在异世界度过了一天呢。',Speaker=829137,Option='今天的任务',}",
  [662265] = "{id=662265,Text='让我看看……寻找三个神秘的伙伴……',Speaker=829136,}",
  [662266] = "{id=662266,Text='嗯……这些伙伴应该是拯救米德加尔特大陆必不可少的助力吧。',Speaker=829136,}",
  [662267] = "{id=662267,Text='但在这之前，需要完成三个商人的挑战，只有完成了挑战，才能收到伙伴的线索。',Speaker=829136,}",
  [662268] = "{id=662268,Text='完成挑战才能收到伙伴的线索？听上去怪怪的。',Speaker=829137,}",
  [662269] = "{id=662269,Text='……是游戏中惯有的设计呢……',Speaker=829136,}",
  [662270] = "{id=662270,Text='第一个挑战在香槟蓝集市的调味商人摩迪那里。',Speaker=829136,}",
  [662271] = "{id=662271,Text='那还等什么！我们快点出发吧！',Speaker=829137,}",
  [662272] = "{id=662272,Text='欢迎光临香槟蓝调料店！',Speaker=829144,Option='挑战',}",
  [662273] = "{id=662273,Text='哦？你们是为挑战而来吗？只要完成了我的挑战，我就会告知你们一位神秘伙伴的线索。',Speaker=829144,}",
  [662274] = "{id=662274,Text='那么我的挑战就是……调味瓶大作战！',Speaker=829144,}",
  [662275] = "{id=662275,Text='我在香槟蓝集市附近藏了10瓶美味蜜露，只要找到它们，就算你们挑战成功！',Speaker=829144,}",
  [662276] = "{id=662276,Text='记住哦，美味蜜露是长这样的！',Speaker=829144,Option='记住了',}",
  [662277] = "{id=662277,Text='在这里！这个调味商人可真会藏东西啊！',Speaker=829137,}",
  [662278] = "{id=662278,Text='全部都找齐了，我们去找摩迪交付任务完成挑战吧。',Speaker=829136,}",
  [662279] = "{id=662279,Text='这么快就完成了我的挑战，看来你们有点本事嘛！',Speaker=829144,}",
  [662280] = "{id=662280,Text='听好了，第一位伙伴就在兽人村落，相信你们很快就会找到她了！',Speaker=829144,}",
  [662281] = "{id=662281,Text='当然，我的朋友材料商人摩卡也在那里，拿着我的通过证书，展示给摩卡看，他会为你们安排第二个挑战。',Speaker=829144,}",
  [662282] = "{id=662282,Text='兽人村落吗……很容易让人想起不美好的回忆啊……',Speaker=829136,Option='？',}",
  [662283] = "{id=662283,Text='真是悲痛的回忆啊……听上去可不是什么好地方……',Speaker=829137,Option='？？',}",
  [662284] = "{id=662284,Text='咳……不提那些了，我们出发吧。',Speaker=829136,}",
  [662288] = "{id=662288,Text='所以……这里是异世界？我们都是因为特殊的原因穿越了过来……？',Speaker=829151,Option='是的',}",
  [662289] = "{id=662289,Text='只有帮助兽人脸完成任务才能离开这里吗……真是匪夷所思……',Speaker=829151,}",
  [662290] = "{id=662290,Text='没错，你是我拯救米德加尔特大陆必不可少的助力，我十分需要你。',Speaker=829136,}",
  [662291] = "{id=662291,Text='诶诶诶？！我吗？？？',Speaker=829151,}",
  [662292] = "{id=662292,Text='既然你都这么说了……那我勉为其难地加入你们好了。',Speaker=829151,}",
  [662293] = "{id=662293,Text='我这样做是为了顺利地回到原来的世界，才不是为了帮助你……！',Speaker=829151,}",
  [662294] = "{id=662294,Text='啊，她同意了，敬文，我们去找材料商人摩卡吧。',Speaker=829136,}",
  [662295] = "{id=662295,Text='嗯？哦！好的舅舅。',Speaker=829137,}",
  [662296] = "{id=662296,Text='……你真是……',Speaker=829151,}",
  [662297] = "{id=662297,Text='嗯？这是摩迪的挑战通过证书，不错，你们有资格接下我的挑战。',Speaker=829160,}",
  [662298] = "{id=662298,Text='我的挑战可没那么容易……你们听说过纯白之羽吗？',Speaker=829160,Option='{听说过,11},{没听说过,12}',}",
  [662299] = "{id=662299,Text='这你都听说过？这可是我自己取的名字啊！',Speaker=829160,}",
  [662300] = "{id=662300,Text='你当然没听说了，这可是我自己取的名字！',Speaker=829160,}",
  [662301] = "{id=662301,Text='算了算了，这不重要，总之那是我的爱人送给我的礼物，前段时间被窃贼偷走了！',Speaker=829160,}",
  [662302] = "{id=662302,Text='真是可恶的小偷，他不仅仅偷走了我的纯白之羽，还将它高价卖了出去！',Speaker=829160,}",
  [662303] = "{id=662303,Text='看见那边的贝莉了吗，就是她买走了纯白之羽，我想买回来，但她却一直不同意。',Speaker=829160,}",
  [662304] = "{id=662304,Text='只要你们能想办法将我的纯白之羽拿回来，我就算你们通过挑战。',Speaker=829160,}",
  [662305] = "{id=662305,Text='这有什么难的，以我的战斗力，区区一个纯白之羽……',Speaker=829151,}",
  [662306] = "{id=662306,Text='等等！暴力不可取啊！你怎么能对游戏npc下手！',Speaker=829136,}",
  [662307] = "{id=662307,Text='我们还是先去和贝莉聊聊，看看怎么才能拿到纯白之羽吧。',Speaker=829136,}",
  [662308] = "{id=662308,Text='这是……星月皇冠！天呐！我愿意用纯白之羽和你交换！',Speaker=829153,}",
  [662309] = "{id=662309,Text='太漂亮了！我会好好珍惜的！',Speaker=829154,}",
  [662310] = "{id=662310,Text='你们找我做什么？',Speaker=829153,Option='纯白之羽',}",
  [662311] = "{id=662311,Text='嗯？你们也想要纯白之羽吗？',Speaker=829153,Option='是的',}",
  [662312] = "{id=662312,Text='不换，多少钱都不换，你们想都别想！',Speaker=829153,Option='开个条件',}",
  [662313] = "{id=662313,Text='唔……我听说里特有一个星月皇冠，在夜晚中散发着绚丽的光芒，有了它，或许我会考虑下。',Speaker=829153,}",
  [662314] = "{id=662314,Text='你把夜樱圣刀带过来了？瑞泽拉那个脾气古怪的人竟然会把它给你！',Speaker=829155,}",
  [662315] = "{id=662315,Text='那我就勉为其难地用星月皇冠和你交换吧。',Speaker=829155,}",
  [662316] = "{id=662316,Text='嗯，和我的气质很搭嘛！',Speaker=829156,}",
  [662317] = "{id=662317,Text='看着我干嘛，有什么事吗？',Speaker=829155,Option='星月皇冠',}",
  [662318] = "{id=662318,Text='想要我的星月皇冠？真是异想天开！',Speaker=829155,Option='开个条件',}",
  [662319] = "{id=662319,Text='除了瑞泽拉的夜樱圣刀，还没有什么能够打动我的！',Speaker=829155,}",
  [662320] = "{id=662320,Text='兽人战士真是太帅了！嗯？你们是谁？',Speaker=829157,Option='夜樱圣刀',}",
  [662321] = "{id=662321,Text='什么嘛，你们是想要我的夜樱圣刀啊，我还以为是来帮助我的人呢。',Speaker=829157,Option='怎么了',}",
  [662322] = "{id=662322,Text='还不是为了我的梦想！我想做一个巨大的兽人战士玩偶！',Speaker=829157,Option='然后呢',}",
  [662323] = "{id=662323,Text='为此我偷袭了990只兽人战士！最近他们发现我了……每次我一过去，他们就警戒起来！',Speaker=829157,}",
  [662324] = "{id=662324,Text='真是可恶啊……！还差10只！只要再取来10只兽人战士的毛发，我的巨大玩偶就可以完成了！',Speaker=829157,Option='我来帮你',}",
  [662325] = "{id=662325,Text='真的吗！？你要是愿意帮助我，我就把夜樱圣刀送给你！',Speaker=829157,}",
  [662327] = "{id=662327,Text='太棒了！（嗅）新鲜的兽人战士毛发！',Speaker=829157,}",
  [662328] = "{id=662328,Text='巨大的兽人战士玩偶……我要立刻完成这个杰作！',Speaker=829157,}",
  [662329] = "{id=662329,Text='看他美妙的光泽，还有微弱的呼吸，真是完美的作品啊……',Speaker=829157,}",
  [662330] = "{id=662330,Text='答应你们的，夜樱圣刀就交给你们了！',Speaker=829157,}",
  [662331] = "{id=662331,Text='你们把纯白之羽带回来了吗？',Speaker=829160,Option='是的',}",
  [662332] = "{id=662332,Text='我看看……是纯白之羽！是我的纯白之羽没错了！',Speaker=829160,}",
  [662333] = "{id=662333,Text='这回我一定会好好保护它的，能够重新找回纯白之羽，真是多亏了你们啊！',Speaker=829152,}",
  [662334] = "{id=662334,Text='我的挑战就算你们通过了，拿好通过证书，你们的伙伴在艾尔贝塔等着你们。',Speaker=829152,}",
  [662335] = "{id=662335,Text='我的朋友玩具商人摩拉也在那里，找到伙伴后，就去接受他的挑战吧！',Speaker=829152,}",
  [662336] = "{id=662336,Text='噢！成功完成挑战了！真是值得庆祝呢！',Speaker=829151,}",
  [662337] = "{id=662337,Text='关于第二位伙伴……舅舅有什么想法吗？',Speaker=829137,}",
  [662338] = "{id=662338,Text='总感觉也是你认识的人呢……',Speaker=829137,}",
  [662339] = "{id=662339,Text='这样吗？到了艾尔贝塔就知道了，应该也是强大的战力吧！',Speaker=829136,}",
  [662343] = "{id=662343,Text='原来是这样……必须要完成任务才能离开吗！',Speaker=829161,}",
  [662344] = "{id=662344,Text='我不想完成任务，我可以在原地等你们……',Speaker=829161,}",
  [662345] = "{id=662345,Text='不行，只有成为有用的助力，一起打败危害世界的魔物，才可以离开哦。',Speaker=829136,}",
  [662346] = "{id=662346,Text='怎么可以这样嘛……',Speaker=829161,}",
  [662347] = "{id=662347,Text='接下来我们该做什么？我记得摩卡那家伙说要找到玩具商人摩拉对吧。',Speaker=829151,}",
  [662348] = "{id=662348,Text='是这样没错，我们该出发了，梅贝露你也别丧着一张脸了……',Speaker=829136,}",
  [662349] = "{id=662349,Text='知道了……',Speaker=829161,}",
  [662350] = "{id=662350,Text='嗯？任务地点显示是这里没错啊，npc去哪里了？',Speaker=829136,}",
  [662351] = "{id=662351,Text='看，门上有个纸条！',Speaker=829137,}",
  [662352] = "{id=662352,Text='今日外出采购玩具。——摩拉',Speaker=829162,}",
  [662353] = "{id=662353,Text='好不容易找到这里了，人竟然不在！',Speaker=829151,}",
  [662354] = "{id=662354,Text='我好累……我想休息。',Speaker=829161,}",
  [662358] = "{id=662358,Text='快来，冒险者，我们找到玩具商人摩拉了。',Speaker=829136,}",
  [662359] = "{id=662359,Text='说说看你的挑战是什么吧。',Speaker=829151,}",
  [662360] = "{id=662360,Text='想要接受我的挑战嘛……先把材料商人摩卡的挑战通过证书给我看看！',Speaker=829163,}",
  [662361] = "{id=662361,Text='嗯……货真价实的通过证书，你们有点本事嘛！',Speaker=829163,}",
  [662362] = "{id=662362,Text='别啰嗦了，快告诉我们你的挑战是什么！',Speaker=829151,}",
  [662363] = "{id=662363,Text='真是没有耐心的冒险者啊……咳咳，你们也知道，作为玩具商人，家里的玩具必不可少……',Speaker=829163,}",
  [662364] = "{id=662364,Text='这些玩具堆在家中乱七八糟，你们只要帮我收拾好就可以了。',Speaker=829163,}",
  [662365] = "{id=662365,Text='当然了……家里的垃圾也要帮我清理掉！',Speaker=829163,}",
  [662366] = "{id=662366,Text='要是能整理成……这样，就最好不过了！',Speaker=829163,Picture='new_npc_pic_40',}",
  [662367] = "{id=662367,Text='整理房间……我不想劳动嘛！你这是让我们做苦力！',Speaker=829161,}",
  [662368] = "{id=662368,Text='哎？别这么说嘛……整理完毕，我的挑战就算你们通过了。',Speaker=829163,}",
  [662369] = "{id=662369,Text='怎么样？准备好了我们就出发吧！',Speaker=829163,Option='{出发,11},{看看照片,12}',}",
  [662370] = "{id=662370,Text='呐……看仔细了，要打扫的一模一样哦！',Speaker=829163,Picture='new_npc_pic_40',}",
  [662379] = "{id=662379,Text='还不死心吗？既然你们坚持要再挑战一下，那就出发吧。',Speaker=829163,Option='{出发,11},{看看照片,22}',}",
  [662380] = "{id=662380,Text='看在这么整洁的房间份上，我的挑战你们通过了。',Speaker=829163,}",
  [662381] = "{id=662381,Text='你们的伙伴在斐扬等着你们，快去那里找她吧。',Speaker=829163,}",
  [662382] = "{id=662382,Text='还有这份挑战通过证书也给你们了，（小声）虽然接下来也没什么用了……',Speaker=829163,}",
  [662383] = "{id=662383,Text='我似乎知道第三个伙伴是谁了……',Speaker=829137,Option='{哦？？,0},{是谁？,0}',}",
  [662384] = "{id=662384,Text='是舅舅回忆里出现过的一个人呢……',Speaker=829137,}",
  [662385] = "{id=662385,Text='嗯？你们在说什么我不知道的事吗？',Speaker=829151,}",
  [662386] = "{id=662386,Text='啊啊，没什么，我们快点出发去斐扬吧。',Speaker=829137,}",
  [662390] = "{id=662390,Text='听上去好复杂的样子……',Speaker=829183,}",
  [662391] = "{id=662391,Text='总之跟着你们一起行动就可以了对吧！',Speaker=829183,}",
  [662392] = "{id=662392,Text='是这样没错……',Speaker=829136,}",
  [662393] = "{id=662393,Text='但是舅舅，三个伙伴已经都到了，那么我们是不是该去击杀魔物了。',Speaker=829137,}",
  [662394] = "{id=662394,Text='还不急，在击杀魔物前，我们还有许多工作要做。',Speaker=829136,}",
  [662395] = "{id=662395,Text='怎么这么麻烦……还要做些什么？',Speaker=829151,}",
  [662396] = "{id=662396,Text='嗯……我看看……任务写的是……啊，需要提升你们的身体素质。',Speaker=829136,}",
  [662397] = "{id=662397,Text='……原来之前奖励的料理台是要用在这里啊……',Speaker=829137,}",
  [662398] = "{id=662398,Text='什么料理台？',Speaker=829151,}",
  [662399] = "{id=662399,Text='我找找……在这里。',Speaker=829136,}",
  [662400] = "{id=662400,Text='那么接下来我们可以在这附近寻找一些食材，冒险者有什么建议吗？',Speaker=829136,Option='{大脚熊肉,31},{树精的皮,32}',}",
  [662401] = "{id=662401,Text='听上去很不错啊！那就麻烦你帮忙获取一些大脚熊肉吧！',Speaker=829136,Option='没问题',}",
  [662402] = "{id=662402,Text='感觉不是很好吃啊……还有别的选择吗？',Speaker=829136,Option='大脚熊肉',}",
  [662404] = "{id=662404,Text='我们也会在附近搜寻一些食材，等会儿我们就在郊外制作料理吧。',Speaker=829136,}",
  [662405] = "{id=662405,Text='冒险者，你回来啦，我们也准备的差不多了。',Speaker=829136,}",
  [662406] = "{id=662406,Text='我采摘了好多蔬菜……现在疲惫地只想尽快吃到食物！',Speaker=829161,}",
  [662407] = "{id=662407,Text='我也付出了劳动哦~',Speaker=829183,}",
  [662408] = "{id=662408,Text='还等什么，我们快点开始制作料理吧！',Speaker=829151,}",
  [662409] = "{id=662409,Text='好香……这是兽人脸亲手制作的料理……',Speaker=829151,}",
  [662410] = "{id=662410,Text='我我我……我要开动了！',Speaker=829151,}",
  [662411] = "{id=662411,Text='（小声）你好歹稍微克制一些嘛……',Speaker=829137,}",
  [662412] = "{id=662412,Text='我亲手采摘的蔬菜味道很不一般嘛！',Speaker=829161,}",
  [662413] = "{id=662413,Text='我感觉身体暖暖的……真是一份让人愉悦的暖身料理啊……',Speaker=829183,Option='{暖身料理……,0},{确实如此,0}',}",
  [662414] = "{id=662414,Text='兽人脸的手艺竟然这么好，真是让人感到意外！',Speaker=829151,}",
  [662415] = "{id=662415,Text='嗯？等等……',Speaker=829136,}",
  [662416] = "{id=662416,Text='发生什么了？',Speaker=829151,}",
  [662417] = "{id=662417,Text='我今天的任务似乎结束了。',Speaker=829136,}",
  [662418] = "{id=662418,Text='这么快吗？好像也没做什么嘛……',Speaker=829151,}",
  [662419] = "{id=662419,Text='嗯……确实，我有预感，今天过后，或许就会迎来残酷的战斗了……',Speaker=829136,}",
  [662420] = "{id=662420,Text='这是根据我多年的游戏经验得出的结论，往往平静又愉快的时光过后，人们将会面对未知的危险……',Speaker=829136,}",
  [662421] = "{id=662421,Text='舅舅说的好吓人……',Speaker=829137,}",
  [662422] = "{id=662422,Text='当然了，今天短暂的快乐还是要及时享受的！',Speaker=829136,}",
  [662423] = "{id=662423,Text='诶？你想为我们留影合照？我当然可以了！难得有来到异世界的机会，舅舅你觉得呢？',Speaker=829137,}",
  [662424] = "{id=662424,Text='啊，我没什么意见。',Speaker=829136,}",
  [662430] = "{id=662430,Text='冒险者，你来了，我们正在讨论舅舅今天的任务。',Speaker=829137,}",
  [662431] = "{id=662431,Text='今天的任务是让我们调查赤炎恶灵。',Speaker=829136,}",
  [662432] = "{id=662432,Text='有点无从下手呢……或许可以找附近的村民询问一下？',Speaker=829137,Option='图书馆',}",
  [662433] = "{id=662433,Text='图书馆吗？是个不错的地方，应该可以获取到详细的资料。',Speaker=829136,}",
  [662434] = "{id=662434,Text='冒险者，我和你走一趟吧，敬文你们就在附近询问一下村民好了，说不定会有不一样的线索。',Speaker=829136,}",
  [662435] = "{id=662435,Text='等等！我也要去图书馆！',Speaker=829151,}",
  [662436] = "{id=662436,Text='我我我……是担心，兽人脸你长得这么恐怖，万一被管理员阻拦在外面可怎么办！',Speaker=829151,}",
  [662437] = "{id=662437,Text='好歹我也是能帮上忙的！',Speaker=829151,}",
  [662438] = "{id=662438,Text='……那我们准备一下，朝着图书馆出发吧！',Speaker=829136,}",
  [662439] = "{id=662439,Text='冒险者，我们现在出发吗？',Speaker=829136,Option='{出发,11},{再等等,12}',}",
  [662453] = "{id=662453,Text='你们回来了，有得到什么有用的信息吗？',Speaker=829137,Option='交代信息',}",
  [662454] = "{id=662454,Text='太好了！我们询问了附近的村民，也有所收获……',Speaker=829137,}",
  [662455] = "{id=662455,Text='是呢！赤炎恶灵最近正出没在炎之洞穴，不过听说那里温度非常高，普通人没法过去呢……',Speaker=829183,}",
  [662456] = "{id=662456,Text='但是我打听到，只要喝下霜冻花制成的药剂，就可以不惧高温，前往炎之洞穴了！',Speaker=829183,}",
  [662457] = "{id=662457,Text='没错，听说斐扬树林就有霜冻花，附近还有些冰冷的植物，冰极草大概也在那边。',Speaker=829137,}",
  [662458] = "{id=662458,Text='这样就只差猩红番茄了……有了猩红番茄，或许可以吸引赤炎恶灵的注意……',Speaker=829136,}",
  [662459] = "{id=662459,Text='嗯？你是说这个吗……？',Speaker=829161,}",
  [662460] = "{id=662460,Text='梅贝露从村民的农田里捡到的。',Speaker=829211,}",
  [662461] = "{id=662461,Text='你什么时候偷偷溜到农田里去的……',Speaker=829137,}",
  [662462] = "{id=662462,Text='不管怎么说，找到了猩红番茄，也是帮上大忙了！',Speaker=829136,}",
  [662463] = "{id=662463,Text='冒险者，那我们现在就去斐扬树林吧，敬文你们可以准备一些制作药剂的工具。',Speaker=829136,}",
  [662464] = "{id=662464,Text='没问题，准备好了我们就去斐扬树林找你们。',Speaker=829137,}",
  [662465] = "{id=662465,Text='这就是霜冻花吧，很好找呢。',Speaker=829136,}",
  [662466] = "{id=662466,Text='感觉这里的气温比旁边低了不少……',Speaker=829151,}",
  [662467] = "{id=662467,Text='应该没错了，我们收集一些吧。',Speaker=829136,}",
  [662468] = "{id=662468,Text='滚开！这些冰极草是我们的了！',Speaker=829193,Option='？？？',}",
  [662469] = "{id=662469,Text='怎么？你这是什么表情，对我们亮闪闪盗贼团有意见吗？',Speaker=829193,Option='……',}",
  [662470] = "{id=662470,Text='还有旁边那个，摆着个兽人脸对着我们，找死是吧！',Speaker=829193,}",
  [662471] = "{id=662471,Text='……',Speaker=829136,}",
  [662472] = "{id=662472,Text='哼，我看你们才是找死！',Speaker=829151,}",
  [662473] = "{id=662473,Text='呀？很有勇气嘛！兄弟们，给我揍！',Speaker=829193,}",
  [662474] = "{id=662474,Text='别打了别打了！冰极草给你们就是了！',Speaker=829193,}",
  [662475] = "{id=662475,Text='哼！算我们输了！兄弟们，我们走！',Speaker=829193,}",
  [662476] = "{id=662476,Text='可算找到你们了，一切都还顺利吗？',Speaker=829137,}",
  [662477] = "{id=662477,Text='啊，霜冻花和冰极草都拿到了，你们呢，制作药剂的工具准备好了吗？',Speaker=829136,}",
  [662478] = "{id=662478,Text='都准备好了，梅贝露和艾丽西亚就在附近守着。',Speaker=829137,}",
  [662479] = "{id=662479,Text='药剂台已经准备好了！冒险者，接下来就拜托你啦~！',Speaker=829183,}",
  [662480] = "{id=662480,Text='有了霜冻花制成的药剂，我们就无惧炎之洞穴的高温，',Speaker=829136,}",
  [662481] = "{id=662481,Text='使用猩红番茄吸引赤炎恶灵的注意，然后对他进行偷袭，',Speaker=829136,}",
  [662482] = "{id=662482,Text='最终在他虚弱的时候，对他洒下冰极草研磨的药粉……',Speaker=829136,}",
  [662483] = "{id=662483,Text='没错！像舅舅说的这样，我们就可以彻底打败它了！',Speaker=829137,}",
  [662484] = "{id=662484,Text='诶！快看那边！我发现了了不得的东西！',Speaker=829183,}",
  [662485] = "{id=662485,Text='是温泉！这里竟然有温泉！',Speaker=829183,}",
  [662486] = "{id=662486,Text='噢！看上去很不错呢！',Speaker=829151,}",
  [662487] = "{id=662487,Text='这么好的温泉……不如我们在这里休整一番？',Speaker=829183,}",
  [662488] = "{id=662488,Text='说的是呢，喂，兽人脸，和我们一起吧。',Speaker=829151,}",
  [662489] = "{id=662489,Text='……',Speaker=829136,}",
  [662502] = "{id=662502,Text='大家都到齐了，今天我们要去炎之洞穴，击杀赤炎恶灵了！',Speaker=829136,}",
  [662503] = "{id=662503,Text='只要击杀了赤炎恶灵，我们就可以回到原来的世界了。',Speaker=829137,}",
  [662504] = "{id=662504,Text='我搜罗了好多米德加尔特的纪念品，到时候要一起带回去！',Speaker=829137,}",
  [662505] = "{id=662505,Text='敬文的异世界之旅也算是小有收获嘛……好了，我们也该准备准备出发了。',Speaker=829136,}",
  [662506] = "{id=662506,Text='冒险者，将霜冻花制成的药剂拿出来，我们现在就喝下吧。',Speaker=829136,Option='没问题',}",
  [662507] = "{id=662507,Text='唔……好清凉！',Speaker=829151,}",
  [662508] = "{id=662508,Text='嗯，这下可以放心大胆地前往炎之洞穴了！我们出发！',Speaker=829136,}",
  [662549] = "{id=662549,Text='事情我都已经大致了解了，这几天真是辛苦你们了。',Speaker=829135,}",
  [662550] = "{id=662550,Text='感谢你们击杀赤炎恶灵，为米德加尔特做出了巨大的贡献。',Speaker=829135,}",
  [662551] = "{id=662551,Text='作为回报，我把卡普拉服务人员带过来了，她可以帮助你们回到原来的世界。',Speaker=829135,}",
  [662552] = "{id=662552,Text='你们好~卡普拉为您服务~',Speaker=829207,}",
  [662553] = "{id=662553,Text='冒险者，也感谢你的协助，快和他们道个别吧。',Speaker=829135,}",
  [662554] = "{id=662554,Text='这几天多亏了冒险者你的陪伴，不然我们也不会这么快就能离开这里。',Speaker=829136,}",
  [662555] = "{id=662555,Text='如果你哪天来到了我们的世界，我会让敬文好好招待你的。',Speaker=829136,Option='好的',}",
  [662556] = "{id=662556,Text='到时候一定要打上三天三夜的游戏啊！',Speaker=829136,}",
  [662557] = "{id=662557,Text='……舅舅真是……这算什么招待嘛……',Speaker=829137,}",
  [662558] = "{id=662558,Text='兽人脸你……可不要再消失不见了啊……',Speaker=829151,}",
  [662559] = "{id=662559,Text='啊哈……',Speaker=829136,}",
  [662560] = "{id=662560,Text='回家！回家！我要回家！',Speaker=829161,}",
  [662561] = "{id=662561,Text='哎呀，异世界的冒险也是很有趣的嘛！',Speaker=829183,}",
  [662562] = "{id=662562,Text='咳咳、时候不早了，我们该回去了。',Speaker=829136,}",
  [662563] = "{id=662563,Text='冒险者，再见了。',Speaker=829136,Option='再见',}",
  [662573] = "{id=662573,Text='有时空裂隙的气息……他们看上去不属于这个世界。',}",
  [662576] = "{id=662576,Text='仔细看下房间照片吧……',Picture='new_npc_pic_40',}",
  [662577] = "{id=662577,Text='仔细看下房间照片吧……',Picture='new_npc_pic_41',}",
  [662578] = "{id=662578,Text='……',Picture='new_npc_pic_41',}",
  [711376] = "{id=711376,Text='丝碧卡小姐非常喜欢海边，可以去海滩附近找一找。',Speaker=810522,}",
  [711377] = "{id=711377,Text='（这里就是克鲁克斯提到的克魔岛……）',}",
  [711378] = "{id=711378,Text='不好意思，我没空和陌生人闲聊。',Speaker=810524,}",
  [711379] = "{id=711379,Text='有什么事情嘛？',Speaker=810524,Option='你是丝碧卡？',}",
  [711380] = "{id=711380,Text='你怎么知道我的名字？',Speaker=810524,Option='{我猜的,2},{克鲁克斯,3}',}",
  [711381] = "{id=711381,Text='如果你只是想搭讪或者闲聊，那你找错人了，失陪。',Speaker=810524,}",
  [711382] = "{id=711382,Text='哦？他找我有什么事？',Speaker=810511,}",
  [711383] = "{id=711383,Text='……',Speaker=810511,Option='不喜欢嘛？',}",
  [711384] = "{id=711384,Text='不，我很喜欢，克鲁克斯是非常体贴的朋友……',Speaker=810511,Option='只是朋友？',}",
  [711385] = "{id=711385,Text='嗯？你误会了，不过不重要。谢谢你冒险者，麻烦你转告克鲁克斯，我暂时无法回去。',Speaker=810511,}",
  [711386] = "{id=711386,Text='我需要一件[c][ffff00]米德加尔特大陆独一无二的礼物[-][/c]，找到后自会返程，告辞。',Speaker=810511,Option='我可以帮你',}",
  [711387] = "{id=711387,Text='你确定嘛？',Speaker=810511,Option='{还是算了,5},{非常确定,4}',}",
  [711388] = "{id=711388,Text='果然如此，再见冒险者。',Speaker=810511,}",
  [711389] = "{id=711389,Text='哼，大话谁都会说。',Speaker=810511,}",
  [711390] = "{id=711390,Text='你能帮上什么忙？难道说你站在这许个愿，天上就能掉下礼物？真是好笑……',Speaker=810511,}",
  [711391] = "{id=711391,Text='这……也太凑巧了。',Speaker=810511,Option='确……确实',}",
  [711392] = "{id=711392,Text='那我倒要看看，这个跟随你出现的异世界古书，到底是什么名堂。',Speaker=810511,}",
  [711405] = "{id=711405,Text='啊啊啊啊啊啊啊啊啊啊啊！',Speaker=810512,Emoji=42,}",
  [711406] = "{id=711406,Text='我的裙子！我的裙子不见了！我怎么还变成了这个样子！',Speaker=810512,}",
  [711407] = "{id=711407,Text='是不是你们！我要惩罚你们！',Speaker=810512,}",
  [711408] = "{id=711408,Text='我的裙子不见了可怎么办啊？',Speaker=810512,Emoji=42,}",
  [711409] = "{id=711409,Text='该怎么让她安静下来？',Option='哄一哄',}",
  [711410] = "{id=711410,Text='哄也没有用！！！',Speaker=810512,Emoji=42,}",
  [711412] = "{id=711412,Text='好好听。',Speaker=810512,Option='刚才是……',}",
  [711413] = "{id=711413,Text='我叫波莉安娜，是[c][ffff00]《天才裁缝勒梅珍藏品》[-][/c]一书的守护者。',Speaker=810512,}",
  [711416] = "{id=711416,Text='为了防止坏蛋把古书中的宝物偷走，勒梅姐姐用厚厚的魔法丝线给书制作了封印。',Speaker=810512,}",
  [711418] = "{id=711418,Text='我没有守护住勒梅姐姐珍藏的东西，我……啊啊啊啊',Speaker=810512,Option='她又哭了',Emoji=42,}",
  [711419] = "{id=711419,Text='冒险者，帮我一起把裙子找回来。',Speaker=810511,Option='这命令的口吻',}",
  [711420] = "{id=711420,Text='……我知道是我不对，所以我想快点弥补自己的过错。',Speaker=810511,}",
  [711422] = "{id=711422,Text='干嘛？（吸鼻子）',Speaker=810512,Option='讲述情况',}",
  [711423] = "{id=711423,Text='原来，这位叫丝碧卡的小姐姐也有想完成的愿望啊！',Speaker=810512,}",
  [711424] = "{id=711424,Text='好吧。',Speaker=810512,}",
  [711425] = "{id=711425,Text='勒梅姐姐一直教育我要做个体谅别人的好孩子。',Speaker=810512,}",
  [711426] = "{id=711426,Text='所以，只要你们帮我找到裙子，我就原谅你们。',Speaker=810512,}",
  [711427] = "{id=711427,Text='别看我小小一只其实我还蛮厉害的~',Speaker=810512,}",
  [711428] = "{id=711428,Text='九条裙子现在散落在你们生活的[c][ffff00]整个大陆[-][/c]。',Speaker=810512,}",
  [711429] = "{id=711429,Text='寻找的线索在弄丢它们的人身上。',Speaker=810512,Option='丝碧卡？',}",
  [711430] = "{id=711430,Text='[c][ffff00]凝视记忆，凝视深爱之人。群星闪耀着光芒，在开启者最想去而去不得的地方。[-][/c]',Speaker=810512,Option='这是什么？',}",
  [711431] = "{id=711431,Text='这是勒梅姐姐写给波莉安娜的，说如果找不到裙子，就念出这句话。',Speaker=810512,Option='然后？',}",
  [711432] = "{id=711432,Text='然后，就不知道了！',Speaker=810512,Option='你……',Emoji=17,}",
  [711433] = "{id=711433,Text='破坏古书的，是我。',Speaker=810511,}",
  [711434] = "{id=711434,Text='所以，想要找到裙子，[c][ffff00]就要去我最想去而去不得的地方[-][/c]……',Speaker=810511,Option='有道理',}",
  [711435] = "{id=711435,Text='凝视，深爱的人……我确实和很重要的人小时候去过的一些地方……',Speaker=810511,}",
  [711436] = "{id=711436,Text='但太过久远，已经完全不记得那是哪里。不过，我把这些[c][ffff00]照片[-][/c]带在了身上。',Speaker=810511,}",
  [711437] = "{id=711437,Text='丝碧卡视为珍宝的照片。',Speaker=810523,Picture='new_npc_pic_9',BlackMask=1,}",
  [711438] = "{id=711438,Text='这张照片就[c][ffff00]放在你背包里[-][/c]吧，这样你[c][ffff00]随时都可以打开看[-][/c]。',Speaker=810511,}",
  [711439] = "{id=711439,Text='没错冒险者，收好照片就赶紧出发吧！',Speaker=810512,Option='为什么是我？',}",
  [711440] = "{id=711440,Text='因为波莉安娜喜欢你啊！',Speaker=810512,}",
  [711441] = "{id=711441,Text='嗯，既然那么喜欢你，就再告诉你个秘密~',Speaker=810512,Emoji=17,}",
  [711443] = "{id=711443,Text='如果宝物都能回来，我也可以帮你准备一份难得的礼物给到你的。',Speaker=810512,}",
  [711444] = "{id=711444,Text='虽然，我还不知道那会是什么……',Speaker=810512,}",
  [711446] = "{id=711446,Text='地方应该找对了，接下来该做什么呢？',Speaker=810511,}",
  [711447] = "{id=711447,Text='如果不是要做守护神，我也想在舞台上翩翩起舞，做个大舞蹈家！',Speaker=810512,}",
  [711461] = "{id=711461,Text='我来试试看哦！',Speaker=810512,}",
  [711462] = "{id=711462,Text='这是？',Speaker=810511,}",
  [711463] = "{id=711463,Text='真不愧是我！我们走吧！宝物应该就藏在这里。',Speaker=810512,}",
  [711465] = "{id=711465,Text='确定要进入吗？',Speaker=810535,Option='{算了,2},{确定,3}',}",
  [711480] = "{id=711480,Text='首战告捷！',Speaker=810512,}",
  [711481] = "{id=711481,Text='可以冲去下一个地方了，耶！',Speaker=810512,Option='去哪里',}",
  [711482] = "{id=711482,Text='我不知道啊！',Speaker=810512,Option='什么？！',Emoji=17,}",
  [711483] = "{id=711483,Text='你可真得很容易惹冒险者生气呢。',Speaker=810511,Option='……',Emoji=30,}",
  [711484] = "{id=711484,Text='冒险者，我这里还有张照片，不知道能不能帮到你！',Speaker=810511,}",
  [711485] = "{id=711485,Text='只要是丝碧卡的照片，就一定有用！',Speaker=810512,Option='马屁拍过头了吧',}",
  [711486] = "{id=711486,Text='波莉安娜是喜欢！喜欢，才不是拍马屁，哼！',Speaker=810512,}",
  [711487] = "{id=711487,Text='冒险者，请收下。',Speaker=810511,}",
  [711488] = "{id=711488,Text='接下来的事情，就继续拜托了。',Speaker=810511,}",
  [711489] = "{id=711489,Text='开心！让我们齐心协力，找到裙子的下落吧！',Speaker=810512,}",
  [711490] = "{id=711490,Text='我在一直给你加油诶！大概，也算齐心协力吧，哈哈哈！',Speaker=810512,}",
  [711498] = "{id=711498,Text='应该就是这里，我们去找那个漂亮姐姐问问看呢？',Speaker=810512,}",
  [711499] = "{id=711499,Text='哇，这不就是我们剧院曾经的样子！',Speaker=4997,Option='真的吗？',}",
  [711500] = "{id=711500,Text='没错，对艺术抱有梦想的人都会在这里拍照留念。',Speaker=4997,}",
  [711501] = "{id=711501,Text='毕竟无数的艺术家都是从这家剧院开始，被世人认识，从此星光熠熠。',Speaker=4997,}",
  [711503] = "{id=711503,Text='嗯，那接下来就交给我！',Speaker=810512,}",
  [711504] = "{id=711504,Text='嘿嘿，感觉已经可以运用的很熟练了！走吧！',Speaker=810512,}",
  [711507] = "{id=711507,Text='我们就这么出来了？没有宝箱，也没有我的宝贝？！！',Speaker=810512,Option='没有',}",
  [711508] = "{id=711508,Text='呜…………对，对不起。',Speaker=810512,}",
  [711509] = "{id=711509,Text='都怪我能力不够，没办法准确知道裙子在哪里，害大家白忙活了。',Speaker=810512,Option='不是你的错',Emoji=42,}",
  [711510] = "{id=711510,Text='冒险者说的对，我们去下一个地方找找看便是，不要怪自己。',Speaker=810511,Emoji=30,}",
  [711511] = "{id=711511,Text='呜呜，丝碧卡姐姐和冒险者，都是大好人！',Speaker=810512,}",
  [711512] = "{id=711512,Text='冒险者，我看到波莉安娜很伤心，出了什么事情嘛？',Speaker=4997,Option='讲述经过',}",
  [711513] = "{id=711513,Text='居然会这样？',Speaker=4997,}",
  [711514] = "{id=711514,Text='虽说你们肯定不是在寻找普通的裙子，但我曾在剧院后台发现过一条很好看的裙装。',Speaker=4997,}",
  [711515] = "{id=711515,Text='如果你们需要的话，就送给你们如何？',Speaker=4997,}",
  [711516] = "{id=711516,Text='啊啊啊啊啊啊啊！我的裙子！它居然在这里！',Speaker=810512,Emoji=2,}",
  [711517] = "{id=711517,Text='真的嘛？那可真是太好了，波莉安娜不用再哭了。',Speaker=4997,Emoji=30,}",
  [711518] = "{id=711518,Text='我们真得可以拿走这件宝贝吗？',Speaker=810511,}",
  [711519] = "{id=711519,Text='当然！它本来就是你们的不是嘛？',Speaker=4997,}",
  [711520] = "{id=711520,Text='在这个世界遇到的人，真得都太好了！',Speaker=810512,Emoji=42,}",
  [711521] = "{id=711521,Text='我很高兴你能这么说，但[c][ffff00]这个世界还是会有坏人的[-][/c]。',Speaker=4997,}",
  [711522] = "{id=711522,Text='就像这个剧场，光鲜亮丽背后免不了也会有些黑暗，所以小家伙：[c][ffff00]旅行注意安全[-][/c]。',Speaker=4997,Emoji=17,}",
  [711523] = "{id=711523,Text='嗯？嗯……好。',Speaker=810512,}",
  [711524] = "{id=711524,Text='冒险者，请收下。',Speaker=810511,}",
  [711525] = "{id=711525,Text='接下来的事情，就继续拜托冒险者了。',Speaker=810511,Option='没问题',}",
  [711526] = "{id=711526,Text='那我和丝碧卡姐姐就先去看剧啦！',Speaker=810512,Option='什么？',}",
  [711527] = "{id=711527,Text='嘿嘿机会难得嘛，等你找到目的地，我和姐姐就会“嘭”得一声出现哒！',Speaker=810512,}",
  [711528] = "{id=711528,Text='那么回见啦，冒险者！',Speaker=810512,}",
  [711534] = "{id=711534,Text='应该就是这里了，试着召唤丝碧卡和波莉安娜吧……',}",
  [711535] = "{id=711535,Text='就是这里了？',Speaker=810511,Option='嗯',}",
  [711536] = "{id=711536,Text='诶，旁边那只浣熊在跟我们说话？',Speaker=810512,}",
  [711537] = "{id=711537,Text='哟， 看你们可不像是本地人啊。',Speaker=810516,}",
  [711538] = "{id=711538,Text='没错，我们是从异世界来到这里的旅行者。',Speaker=810512,Option='只有你是',}",
  [711539] = "{id=711539,Text='旅行者……有点意思，诶，你们手里拿的是什么？',Speaker=810516,Option='说明情况',}",
  [711540] = "{id=711540,Text='嗯……你们找这里是要做什么？',Speaker=810516,}",
  [711541] = "{id=711541,Text='我们要进到一个奇怪神秘的地方，找回来自异世界的宝物！',Speaker=810512,}",
  [711542] = "{id=711542,Text='哦！你说的那个啊，那里我知道！',Speaker=810516,Option='嗯？！！',}",
  [711543] = "{id=711543,Text='就是放着精致宝箱……箱子里有宝贝的地方嘛！',Speaker=810516,Emoji=14,}",
  [711544] = "{id=711544,Text='没错没错！冒险者，我们居然找到了知道裙子下落的人。',Speaker=810512,}",
  [711545] = "{id=711545,Text='是吗？我很怀疑……',Speaker=810511,}",
  [711546] = "{id=711546,Text='这种语气可真是有点不礼貌呢我漂亮的小姐……',Speaker=810516,Emoji=25,}",
  [711547] = "{id=711547,Text='我可是这里的常驻商人，出了名的乐于助人万事通，不信你看……',Speaker=810516,Emoji=25,}",
  [711548] = "{id=711548,Text='还真有入口出现。',Speaker=810512,}",
  [711549] = "{id=711549,Text='我没骗你们吧，加油亲爱的小姐们，祝你们成功。',Speaker=810516,}",
  [711550] = "{id=711550,Text='……冒险者，虽然没有什么证据，但我很怀疑这个商人的话。',Speaker=810511,Option='{我也怀疑,0},{可以相信,0}',}",
  [711551] = "{id=711551,Text='我选择相信商人先生！毕竟一路走来我们遇到的都是很好的人！',Speaker=810512,}",
  [711552] = "{id=711552,Text='好吧，虽然心里有些不安，但我相信冒险者的能力。',Speaker=810511,}",
  [711553] = "{id=711553,Text='这次，还请千万注意安全。',Speaker=810511,Option='好',}",
  [711584] = "{id=711584,Text='波莉安娜不明白，以后，我该相信谁，又不该相信谁呢？',Speaker=810512,Emoji=42,}",
  [711585] = "{id=711585,Text='波莉安娜不要难过，[c][ffff00]人心本就是最难懂的[-][/c]。',Speaker=810511,}",
  [711586] = "{id=711586,Text='不需要奢望所有人都是好人。[c][ffff00]有被很好的人爱着和保护着，就足够了[-][/c]！',Speaker=810511,Option='丝碧卡说的是',}",
  [711587] = "{id=711587,Text='我明白了，就像冒险者，丝碧卡姐姐，勒梅姐姐。',Speaker=810512,}",
  [711588] = "{id=711588,Text='还有我的骑士！',Speaker=810511,Option='骑士？',}",
  [711589] = "{id=711589,Text='没错，我的骑士是世界上最好的人。我也是为了我的骑士在寻找礼物！',Speaker=810511,}",
  [711590] = "{id=711590,Text='那我们赶紧去找齐裙子吧，好不好冒险者！',Speaker=810512,Option='乐意效劳！',}",
  [711591] = "{id=711591,Text='刚才精灵说给了我们新的线索，会是什么呢？',Speaker=810511,}",
  [711592] = "{id=711592,Text='照片正面没什么问题，看看背面吧！',Picture='new_npc_pic_11',}",
  [711593] = "{id=711593,Text='好像确实有些不太一样，试着带着新线索指明的信息。全力前进吧！',}",
  [711594] = "{id=711594,Text='您好，请问有什么事嘛？',Speaker=810518,Option='说明情况',}",
  [711595] = "{id=711595,Text='啊，这是我多年前为奈利乌斯家族拍下的照片。',Speaker=810518,}",
  [711596] = "{id=711596,Text='居然是七王室曾经的御用摄影师凯希尔先生！',Speaker=810511,}",
  [711597] = "{id=711597,Text='天哪，没想到当年还年幼的大小姐如今已经长成亭亭玉立的淑女了。',Speaker=810518,}",
  [711598] = "{id=711598,Text='你们来找我，是有什么事情吗？',Speaker=810518,Option='讲述经过',}",
  [711599] = "{id=711599,Text='异世界的裙子我倒是没有听说。不过最近天津町里确实出现了怪事。',Speaker=810518,}",
  [711600] = "{id=711600,Text='你们如果不介意可以去看看，或许能发现点线索。',Speaker=810518,Option='好的',}",
  [711601] = "{id=711601,Text='好的，那我就带各位过去，不过要小心注意安全。',Speaker=810518,}",
  [711603] = "{id=711603,Text='救命啊！突然有天森林里就出现了这只猫头鹰。',Speaker=810519,}",
  [711604] = "{id=711604,Text='巨大一只不说，还会喷火！我们快要生活不下去了，能不能帮忙消灭掉这只猫头鹰啊！',Speaker=810519,}",
  [711605] = "{id=711605,Text='怎么会有这么可恶的猫头鹰！虽然它长得还蛮可爱！',Speaker=810512,}",
  [711606] = "{id=711606,Text='试试看能不能和它沟通吧。',Speaker=810511,Option='好',}",
  [711607] = "{id=711607,Text='啊谁都不要理我，我气死了！什么伙伴友情，都是骗人的。',Speaker=810521,Emoji=7,}",
  [711608] = "{id=711608,Text='感觉它脾气好暴躁，谁的话都听不进去诶！',Speaker=810512,Option='跟你一样',}",
  [711609] = "{id=711609,Text='哈？我那么吓人嘛？讨厌……',Speaker=810512,}",
  [711610] = "{id=711610,Text='看来是没办法和他沟通了，我们想想别的办法呢？',Speaker=810511,Option='好！',}",
  [711611] = "{id=711611,Text='我们去看看那边的[c][ffff00]竖琴[-][/c]呢？万一能奏响曲子安抚猫头鹰，问题不就解决了！',Speaker=810512,}",
  [711612] = "{id=711612,Text='将我唤醒？所为何事？',Speaker=810520,Option='讲述过程',}",
  [711613] = "{id=711613,Text='大胆！为这种小事，就可以把我守护的土地烧为焦土，惊扰天津町民众至此嘛？不可原谅！',Speaker=810520,}",
  [711614] = "{id=711614,Text='啊大块头不要打我们，不是我们干的，都怪那个[c][ffff00]猫头鹰[-][/c]。',Speaker=810512,}",
  [711615] = "{id=711615,Text='猫头鹰？',Speaker=810520,Option='讲述经过',}",
  [711616] = "{id=711616,Text='有趣，冒险者，带我去看看。',Speaker=810520,}",
  [711617] = "{id=711617,Text='这就是你们所说的魔物？',Speaker=810520,Option='没错',}",
  [711618] = "{id=711618,Text='啊！！！气死我了！',Speaker=810521,Emoji=12,}",
  [711619] = "{id=711619,Text='倒是可爱……',Speaker=810520,Option='什么？',}",
  [711620] = "{id=711620,Text='咳咳。我说，我倒要看看它为什么这么猖狂！',Speaker=810520,Emoji=25,}",
  [711621] = "{id=711621,Text='冒险者，趁现在，击败这只萌物，啊不是，魔物吧！',Speaker=810520,}",
  [711622] = "{id=711622,Text='这么看起来是挺可爱。',Speaker=810512,}",
  [711623] = "{id=711623,Text='身体半透明……它应该也是灵魂了。',Speaker=810511,}",
  [711624] = "{id=711624,Text='一个期待玩伴回来陪伴它的……灵魂嘛？',}",
  [711625] = "{id=711625,Text='呜呜，我之前的小伙伴都不要我了，我好寂寞呜呜呜。',Speaker=62954,Emoji=24,}",
  [711626] = "{id=711626,Text='但其实应该是小伙伴没办法和已经死去的它再见面吧……',Speaker=810511,Option='是',}",
  [711627] = "{id=711627,Text='哎呀你别哭，以后我照顾你好不好？',Speaker=810520,}",
  [711628] = "{id=711628,Text='诶？',Speaker=810512,Option='诶！',}",
  [711629] = "{id=711629,Text='怎么了冒险者，你们有意见吗？',Speaker=810520,Option='没有没有',}",
  [711630] = "{id=711630,Text='真的可以嘛？你以后不会离开我？',Speaker=62954,}",
  [711631] = "{id=711631,Text='你要是喜欢这儿，就和我一起守护这座城，我绝对不会抛弃你，如何？',Speaker=810520,}",
  [711632] = "{id=711632,Text='我愿意！',Speaker=62954,Emoji=26,}",
  [711633] = "{id=711633,Text='真太可爱了……',Speaker=810520,Option='金将军冷静',Emoji=27,}",
  [711634] = "{id=711634,Text='咳咳，冒险者，刚才错怪你们了。',Speaker=810520,}",
  [711635] = "{id=711635,Text='我和咔叽也该走了。',Speaker=810520,Option='名字都取上了？',}",
  [711636] = "{id=711636,Text='嗯？你们有意见吗啊？',Speaker=810520,Option='没有没有',}",
  [711637] = "{id=711637,Text='咔叽喜欢这个名字！',Speaker=62954,Option='诶！',}",
  [711638] = "{id=711638,Text='那么冒险者，再会。',Speaker=810520,}",
  [711639] = "{id=711639,Text='太好了，咔叽有了新的同伴！',Speaker=810512,}",
  [711640] = "{id=711640,Text='真是件高兴事。',Speaker=810511,Option='但裙子又没找到',}",
  [711641] = "{id=711641,Text='嘛，我们也别泄气，好事多磨！有冒险者在，我一点都不担心。',Speaker=810512,Option='话虽这么说',}",
  [711642] = "{id=711642,Text='啊是裙子！是波莉安娜的裙子！',Speaker=810512,}",
  [711643] = "{id=711643,Text='太好了，没想到居然真的有裙子，灯神果然没有骗我。',Speaker=810512,}",
  [711644] = "{id=711644,Text='我又有了继续的动力！',Speaker=810512,Option='我也一样',}",
  [711646] = "{id=711646,Text='冒险者，我和波莉安娜先在天津町逛逛。',Speaker=810511,}",
  [711647] = "{id=711647,Text='晚些时候我们在天津町神社汇合吧。',Speaker=810511,Option='好的',}",
  [711648] = "{id=711648,Text='冒险者你来了。',Speaker=810511,Option='波莉安娜？',}",
  [711649] = "{id=711649,Text='她刚才玩太疯，睡着了而已……我有在照顾她，不必担心。',Speaker=810511,}",
  [711650] = "{id=711650,Text='不瞒你说，看着你和波莉安娜那么努力在找回裙子，想要恢复古书。',Speaker=810511,}",
  [711651] = "{id=711651,Text='我就想起[c][ffff00]自己的不辞而别，对等待我继位家主身份的家族而言，是不是太过分了[-][/c]？',Speaker=810511,Option='{我觉得是,2},{一定有原因,3}',}",
  [711652] = "{id=711652,Text='你也这么觉得是不是？但我有我自己的理由。',Speaker=810511,}",
  [711653] = "{id=711653,Text='你说得对，我确实有自己的理由。',Speaker=810511,}",
  [711654] = "{id=711654,Text='我最刚开始去往克魔岛，不是为了逃避。',Speaker=810511,}",
  [711655] = "{id=711655,Text='只是，只有完成这个梦想，我才能更好的面对未来。',Speaker=810511,}",
  [711656] = "{id=711656,Text='[c][ffff00]面对作为奈利乌斯家主的未来[-][/c]。',Speaker=810511,}",
  [711657] = "{id=711657,Text='（怎么回事？！）',}",
  [711658] = "{id=711658,Text='呜呜……小姐姐不要害怕，我来保护你！',Speaker=810512,Emoji=14,}",
  [711659] = "{id=711659,Text='（睡梦中的波莉安娜好像释放了什么不得了的力量……）',}",
  [711660] = "{id=711660,Text='（迷雾太浓了……什么都看不清……）',}",
  [711661] = "{id=711661,Text='（好像得到的某种神奇的力量，使用一下试试看吧。）',}",
  [711662] = "{id=711662,Text='（丝碧卡和波莉安娜应该是困住这里了。）',}",
  [711663] = "{id=711663,Text='（牢记那首歌的旋律，一鼓作气冲进去救出她们吧！）',}",
  [711664] = "{id=711664,Text='谢谢你冒险者，我没事。',Speaker=810511,}",
  [711665] = "{id=711665,Text='倒是你，我真的很担心你会受到伤害！',Speaker=810511,Option='你的歌声在帮我',}",
  [711666] = "{id=711666,Text='能帮到你真得太好了，说真的，有时候我会觉得自己是个没用的队友。',Speaker=810511,Option='丝碧卡很重要',}",
  [711667] = "{id=711667,Text='谢谢你，对了，能拜托你和我一起去安慰下波莉安娜嘛？我们都知道她没有恶意，希望她别太自责。',Speaker=810511,Option='好',}",
  [711668] = "{id=711668,Text='啊啊啊对不起！！！！冒险者！！我也不知道发生了什么！',Speaker=810512,Emoji=42,}",
  [711669] = "{id=711669,Text='我只记得梦里听到丝碧卡姐姐在哭，以为有坏人要伤害她。所以，所以……',Speaker=810512,Option='没事的',}",
  [711670] = "{id=711670,Text='波莉安娜，我很好，你不用自责。',Speaker=810511,}",
  [711671] = "{id=711671,Text='可是我居然召唤出了魔物，还把你们困了起来。',Speaker=810512,}",
  [711672] = "{id=711672,Text='也许是因为裙子消失你的力量失衡导致的，说到底我也要负责任。',Speaker=810511,}",
  [711673] = "{id=711673,Text='波莉安娜这么在意我，接下来的旅行我会很安心！',Speaker=810511,Option='我也一样',Emoji=30,}",
  [711674] = "{id=711674,Text='丝碧卡姐姐！冒险者！！！',Speaker=810512,Emoji=42,}",
  [711675] = "{id=711675,Text='（感觉，和波莉安娜以及丝碧卡都成为了好朋友了呢……）',}",
  [711676] = "{id=711676,Text='冒险者，最近的旅途辛苦了！我觉得我们现在需要适当的休息！我也会再想想有什么寻找裙子的线索。',Speaker=810511,}",
  [711677] = "{id=711677,Text='相信不会过很久我就会再来麻烦你！那么冒险者，我们回见。',Speaker=810511,Option='再见',}",
  [711688] = "{id=711688,Text='这是我……和我很重要的人一起学过的曲子。',Speaker=810511,}",
  [711689] = "{id=711689,Text='波莉安娜好像安静下来了……',}",
  [711690] = "{id=711690,Text='强行破坏封印，裙子就会逃去安全的地方。',Speaker=810512,}",
  [711691] = "{id=711691,Text='嗨漂亮的旅行者们，你们在做什么啊？',Speaker=810516,}",
  [711692] = "{id=711692,Text='我是丝碧卡·奈利乌斯，许久不见，不知道您还记不记得我。',Speaker=810511,}",
  [711693] = "{id=711693,Text='这个猫头鹰是疯子吧！',Speaker=810519,}",
  [711694] = "{id=711694,Text='谁要跟它做朋友！',Speaker=810519,}",
  [711695] = "{id=711695,Text='我要搬家！',Speaker=810519,}",
  [711696] = "{id=711696,Text='救命啊！',Speaker=810519,}",
  [711701] = "{id=711701,Text='给骑士寻找一份礼物，也不只是简单的一厢情愿。',Speaker=810511,}",
  [711702] = "{id=711702,Text='我从来没有想要逃避自己的责任。',Speaker=810511,}",
  [711705] = "{id=711705,Text='（克鲁克斯说过，丝碧卡很喜欢蓝色的裙子，找找看穿蓝色裙子的金发女孩吧……）',}",
  [711706] = "{id=711706,Text='[c][ff0000]♪[-][/c] [c][00ff00]♬[-][/c] [c][ffff00]♩[-][/c] [c][ba55d3]♫[-][/c] [c][1e90ff]♪[-][/c] ……',}",
  [711707] = "{id=711707,Text='（一段美妙的旋律……）',}",
  [711708] = "{id=711708,Text='[c][ffff00]旧时的回忆都化作今日的星芒[-][/c]，是不是很棒的指示？',Speaker=810512,Option='星星光芒？',}",
  [711709] = "{id=711709,Text='毕竟“群星闪耀着光芒”嘛~',Speaker=810512,}",
  [711710] = "{id=711710,Text='哎呀冒险者问题真多，我们快出发啦！',Speaker=810512,}",
  [711711] = "{id=711711,Text='除了照片，闪闪星光也会是线索哦！',Speaker=810512,}",
  [711715] = "{id=711715,Text='照片依旧可以随时从背包里翻出来看哦！',Speaker=810512,}",
  [711737] = "{id=711737,Text='哎呀这里太美了，我如果是摄影师，一定会给树上的花拍无数照片！',Speaker=810512,}",
  [786336] = "{id=786336,Text='看看我呀看看我~',Speaker=840144,}",
  [786337] = "{id=786337,Text='是你！拥有一双慧眼的冒险者，你还记得我吗？',Speaker=840144,Option='{记得！,20},{不记得……,30}',}",
  [786338] = "{id=786338,Text='看到你真是太亲切啦，上次你帮助我们找回了水晶灯，让我们的舞台剧大获成功！',Speaker=840144,}",
  [786339] = "{id=786339,Text='闹闹现在可是我们的台柱子，这些都要感谢你呢~',Speaker=840144,}",
  [786340] = "{id=786340,Text='没关系，你接触的世界太宽广，难免会忘掉一些东西。',Speaker=840144,}",
  [786341] = "{id=786341,Text='那就让我们重新认识一下吧，我是情绪幽灵乐乐，我们会收集人类的故事，改编成舞台剧。',Speaker=840144,}",
  [786342] = "{id=786342,Text='最近我们要排演和[c][ffff00]古城[-][/c]有关的剧，可是……',Speaker=840144,Option='{怎么了？,0},{发生了什么？,0}',}",
  [786343] = "{id=786343,Text='虽然幽灵的寿命很长，我们也有很多同伴，可通过口口相传的历史，总会有一些偏差。',Speaker=840144,}",
  [786344] = "{id=786344,Text='能不能拜托你帮忙，找到一些关于[c][ffff00]古城历史的资料[-][/c]，这样我才能写好舞台剧的剧本。',Speaker=840144,Option='{没问题！,0},{我要怎么做？,0}',}",
  [786345] = "{id=786345,Text='我想想，历史应当从多方面去了解，我们去找[c][ffff00]一些熟知历史的人[-][/c]去问问吧！',Speaker=840144,Option='好的~',}",
  [786346] = "{id=786346,Text='今天就先从历史学家开始！冒险者，你准备好了吗？[c][ffff00]【该任务同账号仅可完成一次。】[-][/c]',Speaker=840144,Option='{好了！,60},{再等等,50}',}",
  [786347] = "{id=786347,Text='没关系，你先去忙吧，毕竟我们幽灵能活很久呢~',Speaker=840144,}",
  [786348] = "{id=786348,Text='找到啦，在那边！',Speaker=840144,}",
  [786349] = "{id=786349,Text='哼，现在的年轻人，真是越来越不尊重老师，更不尊重历史！',Speaker=840145,Option='{请问……,0},{老师好！,0}',}",
  [786350] = "{id=786350,Text='你是哪个班的？刚才一窝蜂往外跑的人里有没有你？把老师的眼镜都挤掉了。',Speaker=840145,Option='{我不是,0},{我没有,0}',}",
  [786351] = "{id=786351,Text='算了，你找我有什么事？',Speaker=840145,Option='关于古城历史……',}",
  [786352] = "{id=786352,Text='什么？！古城的历史你竟然还不清楚？这不是我上周讲课的内容吗？你上课有没有认真听啊！',Speaker=840145,}",
  [786353] = "{id=786353,Text='哎呀呀，这个老师好像有点凶啊，这下可怎么办，冒险者……快想想办法。',Speaker=840144,Option='{让我想想……,0},{这下糟糕了。,0}',}",
  [786354] = "{id=786354,Text='你在和谁说话？旁边有人吗？难道我的近视又加深了？',Speaker=840145,Option='去找回眼镜',}",
  [786355] = "{id=786355,Text='嗯？你要帮我找眼镜，唔……如果能找到的话，我就原谅你上课不认真听讲。',Speaker=840145,}",
  [786356] = "{id=786356,Text='只可惜像你这样拥有清澈无邪的双眼，能看到我们的人太少啦~',Speaker=840144,}",
  [786357] = "{id=786357,Text='所以我只能跟着你一起行动，不会给你添麻烦吧？',Speaker=840144,}",
  [786358] = "{id=786358,Text='咦？是谁掉的钱包！',}",
  [786359] = "{id=786359,Text='太过分了，在这里乱丢垃圾！',}",
  [786360] = "{id=786360,Text='总算找到了……',}",
  [786361] = "{id=786361,Text='太好啦~我们快把眼镜拿给历史学家吧。',Speaker=840144,}",
  [786362] = "{id=786362,Text='哎呀，我才发现，你不是学院的学生？',Speaker=840146,Option='{嗯……,0},{这下看清了？,0}',}",
  [786363] = "{id=786363,Text='真让我诧异，我还以为只有为了应付考试的学生，才会对古城的历史感兴趣。',Speaker=840146,}",
  [786364] = "{id=786364,Text='毕竟现在更热门的是乌龟岛旧史和作为度假胜地的克魔岛传说。',Speaker=840146,Option='我只想知道古城的',}",
  [786365] = "{id=786365,Text='学习知识可不是毫无代价啊，这样吧，刚才我被学生撞到时，除了眼镜丢了，连写教案的记事簿也被撞飞了。',Speaker=840146,}",
  [786366] = "{id=786366,Text='你去帮我把记事簿找回来吧。',Speaker=840146,Option='{呃……,0},{好吧！,0}',}",
  [786367] = "{id=786367,Text='哎呀，真是太麻烦你啦！加油加油喔~',Speaker=840144,}",
  [786368] = "{id=786368,Text='我看过了，附近应该没有啦，拿去给他吧。',Speaker=840144,}",
  [786369] = "{id=786369,Text='我看看，嗯……还好今天的风不算大，所有的都找回来了。',Speaker=840146,Option='可以说了吗？',}",
  [786370] = "{id=786370,Text='那你得先回答我几个问题，让我相信你是一个真正的历史爱好者！',Speaker=840146,Option='{啊？,0},{这么麻烦……,0}',}",
  [786371] = "{id=786371,Text='我得确认你不是什么无良小报的记者，我最讨厌那些用历史当猎奇，胡乱撰写文章的人。',Speaker=840146,}",
  [786372] = "{id=786372,Text='历史需要无数的人付出心血去复原，这中间可能有谬误，或许也会很片面，但决不能被恶意曲解。',Speaker=840146,Option='{好吧……,0},{也有道理,0}',}",
  [786373] = "{id=786373,Text='等你准备好回答问题后告诉我！',Speaker=840146,}",
  [786374] = "{id=786374,Text='不错，看起来你很有信心嘛，那我要开始第一个问题了，我相信你很难答错。',Speaker=840146,}",
  [786375] = "{id=786375,Text='卢恩王国的首都是哪个？',Speaker=840146,Option='{艾卡拉奇,120},{普隆德拉,210},{拉赫,120}',}",
  [786376] = "{id=786376,Text='一派胡言！你到底对历史有没有基本了解？',Speaker=840146,}",
  [786377] = "{id=786377,Text='普隆德拉……',Speaker=840144,}",
  [786378] = "{id=786378,Text='是的，没错，这个问题很简单吧？那下个问题我要加一点难度了。',Speaker=840146,}",
  [786379] = "{id=786379,Text='你知道寿命极长的巴特蒙德的称号是什么吗？',Speaker=840146,Option='{大魔法家,220},{时间滞留者,220},{大贤者,310}',}",
  [786380] = "{id=786380,Text='你不会连巴特蒙德是谁都不知道吧？真可怕……',Speaker=840146,}",
  [786381] = "{id=786381,Text='大贤者啦！',Speaker=840144,}",
  [786382] = "{id=786382,Text='是的，没错，看来你对历史还是有些了解的，那么最后一题……',Speaker=840146,}",
  [786383] = "{id=786383,Text='推翻了施密特暴政的骑士是？',Speaker=840146,Option='{亚瑟王,410},{特里斯坦一世,320},{达纳托斯,320}',}",
  [786384] = "{id=786384,Text='你没有上过学吗？你的历史不会是魔法课老师教的吧！',Speaker=840146,}",
  [786385] = "{id=786385,Text='亚瑟王啦~',Speaker=840144,}",
  [786386] = "{id=786386,Text='很好，看来你并不是那种无知的人，我愿意将你当成我的学生，告诉你一些史料。',Speaker=840146,}",
  [786387] = "{id=786387,Text='提到克雷斯特汉姆这座古城，就要从卢恩王国的历史说起。',Speaker=840146,}",
  [786388] = "{id=786388,Text='随着亚当国王的故去，伊甸王朝内乱爆发，他的儿子该隐野心膨胀，恐惧弥漫在整个人类世界。',Speaker=840146,}",
  [786389] = "{id=786389,Text='英雄总诞生于黑暗，英勇善战的特里斯坦打败了该隐，推翻了伊甸王朝，创立了卢恩王国。',Speaker=840146,}",
  [786390] = "{id=786390,Text='还记得吗？当初我们排演的舞台剧就是关于这段历史呢~他说的分毫不差呢，好厉害呀！',Speaker=840144,Option='{嘘……,0},{先听他说,0}',}",
  [786391] = "{id=786391,Text='喂，别走神，我还没说完！卢恩王国始于特里斯坦一世，却差点在施密特的时代被终结。',Speaker=840146,}",
  [786392] = "{id=786392,Text='古城现在已沦为死城，残留的资料很少，但根据现有的记录和当年民众留下的口述，施密特是一个不折不扣的暴君！',Speaker=840146,}",
  [786393] = "{id=786393,Text='他横征暴敛，为了追求绝对的权利和统治，一意孤行将都城从普隆德拉迁都到克雷斯特汉姆。',Speaker=840146,}",
  [786394] = "{id=786394,Text='这就是古城的由来！而今古城的结局你们也看到了，施密特的所作所为差点让卢恩亡国，他应该被钉在耻辱柱上！',Speaker=840146,Option='{真可怕啊……,0},{果然是暴君。,0}',}",
  [786395] = "{id=786395,Text='好了，我还要去上下一节课，今天关于古城的始末就讲到这里吧。',Speaker=840146,Option='谢谢老师！',}",
  [786396] = "{id=786396,Text='如果以后还有什么问题，欢迎你随时来找我，我喜欢好学的孩子~',Speaker=840146,}",
  [786397] = "{id=786397,Text='今天收集到了很有用的材料呢，我要想想看，怎么把这些放到剧本里。',Speaker=840144,}",
  [786398] = "{id=786398,Text='原来古城是因为暴君的命令，从普隆德拉迁都而来，但还有很多细节都是空白的，真希望能收集到更多资料啊。',Speaker=840144,Option='我来帮你！',}",
  [786399] = "{id=786399,Text='冒险者，你真好，难怪你能看到我们呢~我先回去整理资料，好了以后继续在[c][ffff00]普隆德拉[-][/c]碰面！',Speaker=840144,Option='{好的！,0},{没问题！,0}',}",
  [786400] = "{id=786400,Text='冒险者呀~在哪里呀~',Speaker=840144,}",
  [786401] = "{id=786401,Text='冒险者，你来啦~我现在已经有了一些灵感，就从被称为暴君的施密特开始切入吧！',Speaker=840144,Option='{很不错,20},{很期待,20}',}",
  [786402] = "{id=786402,Text='可是我们对他了解的还是太少，所以今天一起去找[c][ffff00]吉芬[-][/c]的[c][ffff00]图书馆职员[-][/c]吧！',Speaker=840144,}",
  [786403] = "{id=786403,Text='听说他看了好多好多书，一定能给我们提供帮助！',Speaker=840144,}",
  [786404] = "{id=786404,Text='怎么样，准备好和我一起去吉芬了吗？',Speaker=840144,Option='{好了！,80},{再等等,30}',}",
  [786405] = "{id=786405,Text='没关系，你先去忙吧，毕竟我们幽灵能活很久呢~',Speaker=840144,}",
  [786406] = "{id=786406,Text='哎呀，看来他遇到了一些麻烦，我们快去帮助他吧！',Speaker=840144,}",
  [786407] = "{id=786407,Text='讨厌的家伙！',Speaker=840147,}",
  [786408] = "{id=786408,Text='小心，别打到书……',Speaker=840147,}",
  [786409] = "{id=786409,Text='这些坏蛋总算跑啦。',Speaker=840144,}",
  [786410] = "{id=786410,Text='这群可恶的家伙实在太烦人了，图书馆是老威登的家，老威登决不允许它们作乱，多谢你伸出援手！',Speaker=840147,Option='{不客气,0},{顺手的事,0}',}",
  [786411] = "{id=786411,Text='那么请问你要借那本书呢？不是老威登吹牛，馆里的书放在什么位置，老威登全都知道。',Speaker=840147,Option='关于暴君施密特……',}",
  [786412] = "{id=786412,Text='你是想让我和你聊聊古城和施密特的事儿？你刚才帮了我，我应当回报你……',Speaker=840147,}",
  [786413] = "{id=786413,Text='但现在图书馆里太忙，你瞧，那边还有两个人在吵架，难道他们不知道这里不能大声喧哗吗？',Speaker=840147,}",
  [786414] = "{id=786414,Text='老威登得把这些乱七八糟的事情处理好，才能有空和你聊聊。',Speaker=840147,Option='{交给我吧！,0},{我来处理~,0}',}",
  [786415] = "{id=786415,Text='你可真是一个热心肠啊，老威登今天可真幸运。',Speaker=840147,}",
  [786416] = "{id=786416,Text='这道题你写错啦！',Speaker=840148,}",
  [786417] = "{id=786417,Text='明明是你错了，你的数学成绩还不如我呢。',Speaker=840149,}",
  [786418] = "{id=786418,Text='可明明是你做错了！',Speaker=840148,}",
  [786419] = "{id=786419,Text='那你说应该是多少？',Speaker=840149,}",
  [786420] = "{id=786420,Text='我……我也不知道，但我还是觉得这个答案是错的。',Speaker=840148,}",
  [786421] = "{id=786421,Text='哼！我看你就是胡说八道。',Speaker=840149,}",
  [786422] = "{id=786422,Text='是图书馆职员让你来的？对不起，我们应该小声一点的。',Speaker=840148,}",
  [786423] = "{id=786423,Text='哼，还不都是因为你！',Speaker=840149,Option='发生了什么事？',}",
  [786424] = "{id=786424,Text='我们来图书馆写作业，老师布置了这道题——',Speaker=840149,}",
  [786425] = "{id=786425,Text='三个魔法师三天画三个魔法护符，那么九个魔法师用九天能画多少个呢？明明应该是九个嘛！',Speaker=840149,}",
  [786426] = "{id=786426,Text='啊？可我觉得不对……',Speaker=840148,}",
  [786427] = "{id=786427,Text='厉害的冒险者，那你来说，答案应该是什么？',Speaker=840149,Option='{就是九个,115},{二十七个,120},{我也不知道,115}',}",
  [786428] = "{id=786428,Text='看吧，我写的肯定是正确答案！',Speaker=840149,}",
  [786429] = "{id=786429,Text='不对不对，还是不对。',Speaker=840148,}",
  [786430] = "{id=786430,Text='好像的确不对……',Speaker=840144,}",
  [786431] = "{id=786431,Text='没错，应该是二十七个！九个魔法师三天画九个，九个魔法师九天就画二十七个，老师教过的！',Speaker=840148,}",
  [786432] = "{id=786432,Text='哦……好像是我错了，好吧，我现在就改答案。',Speaker=840149,Option='{讨论问题……,0},{也要小声喔。,0}',}",
  [786433] = "{id=786433,Text='好吧，我们会安静点的。',Speaker=840149,}",
  [786434] = "{id=786434,Text='太好啦，这下我们可以去给老威登汇报了。',Speaker=840144,}",
  [786435] = "{id=786435,Text='你已经搞定了？速度很快嘛，不过能不能再帮老威登一个忙？',Speaker=840147,Option='{说吧,0},{怎么了？,0}',}",
  [786436] = "{id=786436,Text='我需要整理散乱的书籍，可因为刚才那些可恶的小恶魔，我的腰闪了……',Speaker=840147,}",
  [786437] = "{id=786437,Text='人类年龄大了以后好可怜啊，幸好我们幽灵不用经历这些，冒险者，我们帮帮他吧！',Speaker=840144,Option='知道了……',}",
  [786438] = "{id=786438,Text='真棒，都整理好啦！去告诉老威登吧~',Speaker=840144,}",
  [786439] = "{id=786439,Text='呼……总算能清闲片刻了，管理一个图书馆可真不容易啊，好了，我现在可以和你好好讲讲施密特了。',Speaker=840147,Option='{终于啊……,0},{真不容易！,0}',}",
  [786440] = "{id=786440,Text='施密特是历史上著名的暴君，也是克雷斯特汉姆沦为死城的元凶，而他自取灭亡最大的原因是他实行的政策！',Speaker=840147,}",
  [786441] = "{id=786441,Text='施密特虽然是贵族出生，被老国王领养后成为下一任王国继承人，但他却背叛了自己的阶层，对贵族极力打压。',Speaker=840147,}",
  [786442] = "{id=786442,Text='或许这也是施密特对权利具有极强贪欲的表现之一，引发了新老贵族的不满和王国的动荡。',Speaker=840147,}",
  [786443] = "{id=786443,Text='原来是这样，我得好好记住！',Speaker=840144,}",
  [786444] = "{id=786444,Text='为了和贵族抗争，施密特组建了白骑士团，并选拔出六名骑士团团长，而他们都是施密特的忠实爪牙。',Speaker=840147,Option='{听起来很厉害,0}',}",
  [786445] = "{id=786445,Text='当然，他们都是拥有卓绝剑术的人，但在这些人之上，还有一个被称为暴君鹰犬的神秘男人。',Speaker=840147,}",
  [786446] = "{id=786446,Text='据说他拥有聪明到令人恐惧的头脑，没有人能直面他的审视，可这样一个人却依附着施密特实行暴政，真是可惜啊。',Speaker=840147,}",
  [786447] = "{id=786447,Text='哇，听起来就让人害怕的可怕人物喔。',Speaker=840144,}",
  [786448] = "{id=786448,Text='好啦，我回答完你们的问题了，我打算好好休息一会。',Speaker=840147,Option='{啊？,0},{我们？,0}',}",
  [786449] = "{id=786449,Text='哈哈哈，虽然看不到，但我总感觉你身边应该跟着什么有趣的东西~',Speaker=840147,}",
  [786450] = "{id=786450,Text='哎呀呀，被发现啦！',Speaker=840144,}",
  [786451] = "{id=786451,Text='救命……克雷斯特汉姆要被黑暗……吞噬……',Speaker=840156,}",
  [786452] = "{id=786452,Text='怎么了？你的脸色不好看。',Speaker=840147,Option='{你们有没有……,0},{听到奇怪的声音？,0}',}",
  [786453] = "{id=786453,Text='什么声音？图书馆里很安静啊。',Speaker=840147,}",
  [786454] = "{id=786454,Text='是啊，作为幽灵，我也没有听到喔，冒险者，是不是你最近太累了呀？',Speaker=840144,Option='{或许吧,0},{可能是幻觉,0}',}",
  [786455] = "{id=786455,Text='怎么样，你的身体还好吗？',Speaker=840144,Option='{应该……,0},{没问题吧……,0}',}",
  [786456] = "{id=786456,Text='那就好喔，如果不舒服，一定要及时休息呢，健康是人类最重要的财富！',Speaker=840144,}",
  [786457] = "{id=786457,Text='今天也很感谢你呀，我继续回去整理剧本啦，等好了以后我在[c][ffff00]普隆德拉[-][/c]等你哟。',Speaker=840144,}",
  [786458] = "{id=786458,Text='剧本呀~差一点呀~',Speaker=840144,}",
  [786459] = "{id=786459,Text='冒险者你来啦，现在舞台剧的剧本只差一点小尾巴了！',Speaker=840144,Option='太好了！',}",
  [786460] = "{id=786460,Text='在故事里总是英雄击败了恶龙，所以今天我们需要打探是暴君怎么被消灭的！',Speaker=840144,}",
  [786461] = "{id=786461,Text='你知道吟游诗人和我们一样，往往也是历史的承载者，我们去找找[c][ffff00]古城附近的[c][ffff00]吟游诗人[-][/c]吧！',Speaker=840144,}",
  [786462] = "{id=786462,Text='怎么样，准备好和我一起去古城了吗？',Speaker=840144,Option='{好了！,80},{再等等,20}',}",
  [786463] = "{id=786463,Text='没关系，你先去忙吧，毕竟我们幽灵能活很久呢~',Speaker=840144,}",
  [786464] = "{id=786464,Text='嗓子……我的嗓子。',Speaker=840154,}",
  [786465] = "{id=786465,Text='咳咳咳，我很……咳咳……乐意，但是我的嗓子……不舒服。',Speaker=840154,Option='{怎么了？,0},{需要帮助吗？,0}',}",
  [786466] = "{id=786466,Text='你们帮我……采一点……草药，咳咳咳，就在旁边。',Speaker=840154,Option='知道了……',}",
  [786467] = "{id=786467,Text='应该足够啦，希望他的嗓子能恢复如初~',Speaker=840144,}",
  [786468] = "{id=786468,Text='谢天谢地，我的嗓子终于恢复了！',Speaker=840154,Option='{那你可以……,0},{讲故事了吗？,0}',}",
  [786469] = "{id=786469,Text='当然可以，只是需要付出一点点的金钱。',Speaker=840154,Option='多少钱？',}",
  [786470] = "{id=786470,Text='100万Zeny！',Speaker=840154,}",
  [786471] = "{id=786471,Text='对于富有的冒险者来说，应该不算什么吧？',Speaker=840154,Option='{太贵了！,0},{不可能！,0}',}",
  [786472] = "{id=786472,Text='真小气啊……好吧，没钱就出点力，古城里的恶魔女仆和恶魔侍者简直太讨厌了，差点杀了我。',Speaker=840154,}",
  [786473] = "{id=786473,Text='我要给他们一些教训，就由你来帮我吧。',Speaker=840154,Option='{……,0},{好吧,0}',}",
  [786474] = "{id=786474,Text='总觉得这个吟游诗人和我见过的那些不一样，好像太贪财了。',Speaker=840144,}",
  [786475] = "{id=786475,Text='我们不会找错人了吧……应该不会，唉，麻烦你啦冒险者。',Speaker=840144,}",
  [786476] = "{id=786476,Text='等舞台剧排好，邀请你来当我们的第一个观众！',Speaker=840144,}",
  [786477] = "{id=786477,Text='咦？你已经好好教训了他们？我果然没看错你，你很厉害嘛。',Speaker=840154,}",
  [786478] = "{id=786478,Text='让我想想，还有什么事能让你去做……',Speaker=840154,Option='{要打一架？,0},{（凶狠地盯着他）,0}',}",
  [786479] = "{id=786479,Text='别……别激动，我只是开开玩笑嘛，好啦，你帮了我这么多，我现在就告诉你。',Speaker=840154,}",
  [786480] = "{id=786480,Text='人类真是多样啊，看来有些人是没办法好好沟通的，算了，听他说吧。',Speaker=840144,}",
  [786481] = "{id=786481,Text='施密特登上王位时，曾被寄予厚望，许多人以为他会成为一个圣明的君主，但施密特让所有人失望了。',Speaker=840154,}",
  [786482] = "{id=786482,Text='他苛待贵族，肆意妄为，还用手中的剑斩杀了忠心耿耿的骑士。',Speaker=840154,}",
  [786483] = "{id=786483,Text='竟然斩杀效忠的骑士，好可怕……',Speaker=840144,}",
  [786484] = "{id=786484,Text='更让人恐惧的是，为获得禁忌之力，施密特竟将民众和骑士进行献祭，从黑暗中召唤出可怕的怪物。',Speaker=840154,}",
  [786485] = "{id=786485,Text='腐化如瘟疫般蔓延，克雷斯特汉姆几乎沦为死城。',Speaker=840154,}",
  [786486] = "{id=786486,Text='直到骑士亚瑟王联合其他六名骑士发动起义，暴君的时代才被彻底终结，卢恩王国方能延续至今。',Speaker=840154,}",
  [786487] = "{id=786487,Text='所以施密特是一个毫无疑问的暴君！应该被所有人唾弃，他是卢恩王国的耻辱！',Speaker=840154,}",
  [786488] = "{id=786488,Text='啊，救命啊，冒险者！快救我！',Speaker=840154,}",
  [786489] = "{id=786489,Text='好可怕啊，这些腐化的家伙……不会传染吧？',Speaker=840154,}",
  [786490] = "{id=786490,Text='冒险者，小心！',Speaker=840144,}",
  [786491] = "{id=786491,Text='太吓人了，不过他们的出现也说说明了一件事！',Speaker=840154,Option='什么事？',}",
  [786492] = "{id=786492,Text='施密特是个彻头彻尾的暴君，不然他们听到他的名字，怎么会这么激动？',Speaker=840154,}",
  [786493] = "{id=786493,Text='这些腐化的骑士都没有神智了，却还记得这个仇人的名字，可见施密特有多可恶。',Speaker=840154,}",
  [786494] = "{id=786494,Text='好了，今天我的故事讲完了，嘿嘿，是不是应该给我一些打赏呀？',Speaker=840154,Option='{你想得美！,0},{再见！,0}',}",
  [786495] = "{id=786495,Text='（小声嘀咕）不是说当吟游诗人能赚很多钱吗？早知道就换个职业了。',Speaker=840154,}",
  [786496] = "{id=786496,Text='冒险者，真是麻烦你啦……你帮了我很多很多，还遇到那种人。',Speaker=840144,Option='{不用客气,0},{等你们的剧,0}',}",
  [786497] = "{id=786497,Text='这几天我们已经知道了，卢恩王国差点因为施密特灭亡，他做了很多坏事，贵族对他很不满！',Speaker=840144,}",
  [786498] = "{id=786498,Text='施密特还献祭了民众和骑士的生命，召唤了禁忌之力，所以克雷斯特汉姆才会变成现在这样~',Speaker=840144,Option='{没错,0},{是这样,0}',}",
  [786499] = "{id=786499,Text='虽然那个吟游诗人奇奇怪怪的，不过之前的历史学家和图书馆职员也都这么说，应该不会错啦。',Speaker=840144,}",
  [786500] = "{id=786500,Text='谢谢你，我要回去完善剧本啦，等我们再见面时，希望能为你呈现精彩的舞台剧。',Speaker=840144,Option='{加油！,0},{我很期待~,0}',}",
  [852888] = "{id=852888,Text='冒险者，好久不见！',Speaker=817919,Option='好久不见',}",
  [852889] = "{id=852889,Text='波莉安娜也好久不见冒险者！',Speaker=817920,Option='你又粘着丝碧卡',Action={actionid=34},}",
  [852890] = "{id=852890,Text='没错！因为波莉安娜喜欢丝碧卡，就一直陪着丝碧卡玩了好多地方，买了好多好东西！[c][ffff00]还知道了丝碧卡姐姐的小秘密。[-][/c]',Speaker=817920,Option='什么秘密？',}",
  [852891] = "{id=852891,Text='嘿嘿，帮我找到裙子我就告诉你！',Speaker=817920,Option='又来了……',}",
  [852892] = "{id=852892,Text='冒险者，不知道你现在有没有时间，继续和我们一起寻找裙子？',Speaker=817919,Option='{好,3},{暂时没有时间,2}',}",
  [852893] = "{id=852893,Text='这是新的照片。',Speaker=817919,}",
  [852894] = "{id=852894,Text='冒险者快收下照片，一起出发吧！',Speaker=817920,Option='好！',}",
  [852895] = "{id=852895,Text='还是老样子，[c][ffff00]这张照片随时都可以打开看哦！[-][/c]那我们赶快开启新的旅行吧！',Speaker=817920,Option='没问题',}",
  [852896] = "{id=852896,Text='“让旧时的回忆都化作今日的星芒，便能找到丢失的宝藏”希望你还记得。',Speaker=817919,Picture='new_npc_pic_13',BlackMask=1,}",
  [852897] = "{id=852897,Text='应该就是这里了。',Speaker=817920,}",
  [852898] = "{id=852898,Text='冒险者，我知道你在纠结什么，我也很奇怪怎么会什么事情都没有发生。',Speaker=817919,}",
  [852899] = "{id=852899,Text='不如去问问蛋糕店老板，或许她会知道些什么。',Speaker=817919,Option='{好,3},{再想想,2}',}",
  [852900] = "{id=852900,Text='呜……好饿，波莉安娜应该是想不出什么法子了。',Speaker=817920,}",
  [852901] = "{id=852901,Text='欢迎来到瓦莱丽蛋糕店！请问找我有什么事情嘛？',Speaker=817921,Option='{没事,4},{给她看照片,5}',}",
  [852902] = "{id=852902,Text='哎呀，[c][ffff00]这不是我们蛋糕店开业第一年过圣诞节的时候[-][/c]，直到现在我还会在这个圣诞树下放上礼物。',Speaker=817921,Option='找到了！',}",
  [852903] = "{id=852903,Text='这里真不错，波莉安娜喜欢这里！',Speaker=817920,Option='因为蛋糕嘛？',}",
  [852904] = "{id=852904,Text='嘿嘿，被你发现了。',Speaker=817920,}",
  [852905] = "{id=852905,Text='怎么了？',Speaker=817921,Option='讲述情况',}",
  [852906] = "{id=852906,Text='这样啊……虽然我有努力在想，但好像也没有见到什么异世界的裙子。',Speaker=817921,}",
  [852907] = "{id=852907,Text='咦咦咦咦咦？就从来没见过？',Speaker=817920,}",
  [852908] = "{id=852908,Text='[c][ffff00]一条都没有吗？也没有什么神奇光芒？奇怪事件？什么的都没有？？？？[-][/c]',Speaker=817920,Emoji=1,}",
  [852909] = "{id=852909,Text='诶，你要是这么说的话，最近我的蛋糕店倒是真的遇到了些奇怪事。',Speaker=817921,}",
  [852910] = "{id=852910,Text='我不确定会不会和你们想要的裙子有关，但……或许可以来看看？',Speaker=817921,Option='{不想,6},{想,7}',}",
  [852911] = "{id=852911,Text='波莉安娜觉得，这就是冒险者的试炼！我们为什么不去看看呢？',Speaker=817920,}",
  [852912] = "{id=852912,Text='万一老板娘家的蛋糕也很好吃！那不就赚大了？',Speaker=817920,Option='你就是想吃蛋糕',Emoji=26,}",
  [852913] = "{id=852913,Text='嘿嘿……',Speaker=817920,}",
  [852914] = "{id=852914,Text='那各位，欢迎来瓦莱丽蛋糕店。',Speaker=817921,}",
  [852915] = "{id=852915,Text='请允许我介绍下，这位就是我的妹妹[c][ffff00]艾妮娜[-][/c]。',Speaker=817921,Option='妹妹？',Emoji=39,}",
  [852916] = "{id=852916,Text='大家好，希望你们能喜欢瓦莱丽的蛋糕！',Speaker=817922,}",
  [852917] = "{id=852917,Text='姐妹一起开蛋糕店……听起来就很幸福。',Speaker=817919,}",
  [852918] = "{id=852918,Text='话虽这么说，实际上我烘培很差劲，店里蛋糕都是姐姐的手艺。',Speaker=817922,Emoji=26,}",
  [852920] = "{id=852920,Text='这么棒！不过说到这，我的丝碧卡小姐姐也是非常厉害的人！是不是？',Speaker=817920,Emoji=38,}",
  [852921] = "{id=852921,Text='……姐姐？丝碧卡姐姐！！',Speaker=817920,Emoji=42,}",
  [852922] = "{id=852922,Text='嗯？嗯……什么？',Speaker=817919,}",
  [852923] = "{id=852923,Text='你刚才走神了，没事情吧？波莉安娜很担心。',Speaker=817920,}",
  [852924] = "{id=852924,Text='我没事。',Speaker=817919,}",
  [852925] = "{id=852925,Text='哦，就是我旁边的这幅画，有一天突然就缺了一块。',Speaker=817921,Option='被人撕掉了？',}",
  [852926] = "{id=852926,Text='不是，就是突然间就不见了。虽然妹妹一直说没关系，但我还是觉得有些纠结。',Speaker=817921,}",
  [852927] = "{id=852927,Text='所以冒险者，可以拜托你们找到这块缺失的画布嘛？',Speaker=817921,Option='我们还要找裙子',}",
  [852928] = "{id=852928,Text='我会给各位准备好吃的小蛋糕作为谢礼。',Speaker=817921,}",
  [852929] = "{id=852929,Text='没问题！这对冒险者来说，都是小事情。',Speaker=817920,Option='你果然想吃蛋糕',Emoji=28,}",
  [852930] = "{id=852930,Text='可以嘛？',Speaker=817921,Option='{不要,8},{好吧,9}',}",
  [852931] = "{id=852931,Text='为什么不试试呢？',Speaker=817919,}",
  [852932] = "{id=852932,Text='那就拜托你们了，如果有任何需要我帮助回忆的信息，也请不必客气，来问我就好。',Speaker=817921,}",
  [852933] = "{id=852933,Text='冒险者，有什么想知道的嘛？',Speaker=817921,Option='{有关妹妹,10},{有关画作,11},{画布缺失当天,12}',}",
  [852934] = "{id=852934,Text='艾妮娜话是不多，但她是个很善良温柔的好孩子，处处为我考虑。这样就反倒让我心疼，害怕她委屈自己。',Speaker=817921,}",
  [852935] = "{id=852935,Text='画作是艾妮娜绘画学校的毕业作品，她跟我说，画中的主角文森特也是一位热爱生活的画家，哎，不知道是不是妹妹梦想中未来的样子呢。',Speaker=817921,}",
  [852936] = "{id=852936,Text='哦对了画布缺失当天，我们带着它去简单修补了下画框。[c][ffff00]我还记得我们坐了镇上的观光车，也许画布会遗失在那附近？[-][/c]',Speaker=817921,}",
  [852937] = "{id=852937,Text='还有想问的吗？',Speaker=817921,Option='{继续询问,13},{不再询问,14}',}",
  [852939] = "{id=852939,Text='地上好像有看起来是画布的东西。',Option='捡起来看看',}",
  [852940] = "{id=852940,Text='应该就是之前画作的残页，带回去给尤娜看看。',}",
  [852941] = "{id=852941,Text='冒险者，你找到画布了？',Speaker=817921,Option='交出画布',}",
  [852942] = "{id=852942,Text='没错，这就是这幅画缺失的那块。',Speaker=817921,}",
  [852943] = "{id=852943,Text='我说什么来着，我们冒险者最棒了！嗝……',Speaker=817920,Option='感觉你吃了不少',}",
  [852944] = "{id=852944,Text='哼，我才没有。',Speaker=817920,Emoji=40,}",
  [852945] = "{id=852945,Text='我们来试试看把这幅画修复好吧？',Speaker=817919,}",
  [852946] = "{id=852946,Text='但是修补画作需要些材料，冒险者，我能拜托你帮我收集些东西嘛？',Speaker=817921,Option='好',}",
  [852947] = "{id=852947,Text='我需要[c][ffff00]10份颜料、15份金丝线、12份空白画布[-][/c]。他们会出现在[c][ffff00]金属波利、飓风魔、土拨鼠拾荒者[-][/c]身上，辛苦你了。',Speaker=817921,}",
  [852948] = "{id=852948,Text='已经收集到[c][ffff00]10份颜料、15份金丝线、12份空白画布[-][/c]了嘛？',Speaker=817921,}",
  [852949] = "{id=852949,Text='这个数量的[c][ffff00]颜料、金丝线、空白画布[-][/c]......好像不合适呢！',Speaker=817921,}",
  [852950] = "{id=852950,Text='真不愧是冒险者，那我们也开始工作吧。',Speaker=817921,}",
  [852951] = "{id=852951,Text='我……我也可以帮忙。',Speaker=817919,Option='诶？',}",
  [852952] = "{id=852952,Text='怎么，不可以嘛？',Speaker=817919,Option='就觉得很突然',}",
  [852953] = "{id=852953,Text='……就当我在体验生活。',Speaker=817919,Option='也行吧',}",
  [852954] = "{id=852954,Text='画作修好了！我要仔细看看画作！',Speaker=817920,}",
  [852955] = "{id=852955,Text='只要你们需要，随时都可以看画作哦。',Speaker=817921,}",
  [852956] = "{id=852956,Text='一副描绘艺术家文森特光鲜生活的画作，仔细查看！',Speaker=817924,Picture='new_npc_pic_17',Option='{看好了,21},{稍后再看,20}',BlackMask=1,}",
  [852957] = "{id=852957,Text='我就说过我们会有好运气！赶快去把裙子拿回来吧！',Speaker=817920,}",
  [852958] = "{id=852958,Text='可能是在修复画作时，这幅画受到了波莉安娜魔力的影响。',Speaker=817919,}",
  [852959] = "{id=852959,Text='有可能，毕竟我们找回了三条裙子，我觉得我的力量有回来。',Speaker=817920,}",
  [852960] = "{id=852960,Text='她可真是个活力四射的孩子呢。',Speaker=817921,}",
  [852961] = "{id=852961,Text='[c][ffff00]因为有冒险者的保护，她总是放心大胆冲在最前面[-][/c]，尤娜姐姐，先告辞。',Speaker=817919,}",
  [852962] = "{id=852962,Text='好的，祝你们好运。',Speaker=817921,}",
  [852992] = "{id=852992,Text='怎么样？',Speaker=817921,Option='讲述情况',}",
  [852993] = "{id=852993,Text='他是我见过的，最有脾气的艺术家。',Speaker=817920,Option='我还要再回去',}",
  [852994] = "{id=852994,Text='没错，我要去拯救我的宝物！',Speaker=817920,}",
  [852995] = "{id=852995,Text='但，各位刚才冲出来把画弄破了！可能要麻烦各位去收集[c][ffff00]10份颜料、15份金丝线、12份空白画布[-][/c]来吧。',Speaker=817921,Emoji=20,}",
  [852996] = "{id=852996,Text='冒险者已经收集完了？',Speaker=817921,}",
  [852997] = "{id=852997,Text='真不愧是冒险者，那我们也开始工作吧。',Speaker=817921,}",
  [852998] = "{id=852998,Text='画作修好了，我觉得这次要[c][ffff00]认认真真看一下照片[-][/c]，记下上面都有些什么。',Speaker=817920,}",
  [853007] = "{id=853007,Text='冒险者，这次怎么样？',Speaker=817921,Option='成功了',}",
  [853008] = "{id=853008,Text='那可真是太好了!',Speaker=817921,}",
  [853009] = "{id=853009,Text='尤娜女士，您这边会不会还有些关于裙子的线索呢？',Speaker=817919,}",
  [853010] = "{id=853010,Text='我还想进到画里面，觉得好有趣！',Speaker=817920,Emoji=28,}",
  [853011] = "{id=853011,Text='嗯，或许艾妮娜的另一幅画里还会有些蛛丝马迹，冒险者要不要再找找看呢？',Speaker=817921,Option='好',}",
  [853012] = "{id=853012,Text='那就拜托了。',Speaker=817921,}",
  [853013] = "{id=853013,Text='嗯，有任何需要都可以叫我，我一直在。',Speaker=817921,Option='麻烦了',}",
  [853015] = "{id=853015,Text='一副展现学生时代参观图书馆的画作，仔细查看！',Speaker=817932,Picture='new_npc_pic_16',Option='{环境有问题,3},{学生有问题,4},{没有问题,3}',BlackMask=1,}",
  [853017] = "{id=853017,Text='冒险者，发现什么蛛丝马迹了吗？',Speaker=817921,}",
  [853018] = "{id=853018,Text='诶？好奇怪啊？',Speaker=817921,Option='怎么了？',}",
  [853019] = "{id=853019,Text='如果我没有记错，之前妹妹画的孩子们[c][ffff00]穿的都是校服……怎么这个女孩子如今穿的是条裙子？[-][/c]',Speaker=817921,}",
  [853020] = "{id=853020,Text='这就是我的裙子！走，我们去把裙子拿回来！',Speaker=817920,}",
  [853021] = "{id=853021,Text='那冒险者，我们一会儿见。',Speaker=817921,}",
  [853083] = "{id=853083,Text='冒险者们，不好了！我妹妹不见了！',Speaker=817921,Option='什么？',}",
  [853084] = "{id=853084,Text='什么叫不见了？',Speaker=817919,}",
  [853085] = "{id=853085,Text='不想吃蛋糕了？',Speaker=817920,Option='波莉安娜！',}",
  [853086] = "{id=853086,Text='其实，我确实担心妹妹是因为照顾蛋糕店不快乐离家出走。',Speaker=817921,Option='什么意思？',}",
  [853087] = "{id=853087,Text='[c][ffff00]艾妮娜一直想成为一名画家，但为了帮我照顾家中的蛋糕店，她主动放弃了成为画家的梦想。[-][/c]',Speaker=817921,}",
  [853088] = "{id=853088,Text='放弃梦想……',Speaker=817919,}",
  [853089] = "{id=853089,Text='我很想告诉妹妹她不用做这样的牺牲。',Speaker=817921,}",
  [853090] = "{id=853090,Text='冒险者，能不能拜托你帮我把妹妹找回来？',Speaker=817921,Option='{应该不用找吧,2},{没问题,3}',}",
  [853091] = "{id=853091,Text='那我只能想想别的办法了。',Speaker=817921,}",
  [853092] = "{id=853092,Text='那就拜托冒险者了，有任何我能帮上忙的请务必告诉我。',Speaker=817921,Option='更多线索',}",
  [853093] = "{id=853093,Text='冒险者，还有什么是我可以做的？',Speaker=817921,}",
  [853094] = "{id=853094,Text='我不知道还有什么线索，但如果不介意，各位可以去我和妹妹的住所找找看？',Speaker=817921,Option='没问题',}",
  [853123] = "{id=853123,Text='冒险者，你知道丝碧卡为什么会这么在意尤娜妹妹的事情嘛？',Speaker=817920,Option='不知道',}",
  [853124] = "{id=853124,Text='我这里有张照片，想偷偷拿给你看下。',Speaker=817920,Emoji=28,}",
  [853125] = "{id=853125,Text='照片里是[c][ffff00]不久前的丝碧卡和一个女孩子[-][/c]。',Picture='new_npc_pic_18',BlackMask=1,}",
  [853126] = "{id=853126,Text='你还记得丝碧卡不辞而别离开家族的原因嘛？',Speaker=817920,Option='{不想做家主,2},{给骑士准备礼物,3},{想游历河山,2}',}",
  [853127] = "{id=853127,Text='当然不是，冒险者你再好好想想。',Speaker=817920,}",
  [853128] = "{id=853128,Text='没错，我们一直以为这个骑士是像克鲁克斯那样的人。',Speaker=817920,Option='不是嘛？',}",
  [853129] = "{id=853129,Text='具体是谁我们路上再说，现在我们去往[c][ffff00]丝碧卡的内心[-][/c]，帮丝碧卡取回一件能让她心安的东西。',Speaker=817920,}",
  [853130] = "{id=853130,Text='冒险者，之前找到的宝藏公主裙，让我的力量也强大了！',Speaker=817920,}",
  [853131] = "{id=853131,Text='哈！我觉得，自己可以为你们做点什么了。',Speaker=817920,Option='看你的！',}",
  [853134] = "{id=853134,Text='走吧。',Speaker=817920,}",
  [853140] = "{id=853140,Text='我们再回到照片中吧，这次也请务必当心。',Speaker=817920,}",
  [853145] = "{id=853145,Text='冒险者，我一直在找你们，我们出发去找尤娜的妹妹吧！',Speaker=817919,Option='我们有东西给你',}",
  [853146] = "{id=853146,Text='什么？',Speaker=817919,Emoji=8,}",
  [853147] = "{id=853147,Text='一直守护丝碧卡姐姐的骑士，其实就[c][ffff00]丝碧卡的妹妹[-][/c]吧！',Speaker=817920,Option='诶？',}",
  [853148] = "{id=853148,Text='……没错，我的骑士，生命中最重要的人。就是我的妹妹，[c][ffff00]“影子公主”丝琪亚·奈利乌斯[-][/c]。',Speaker=817919,}",
  [853149] = "{id=853149,Text='这并不是一个安全的身份，毕竟“影子公主”[c][ffff00]在关键时刻，要为家主奉献生命[-][/c]。',Speaker=817919,Option='奉献生命？！',}",
  [853150] = "{id=853150,Text='说来话长，不过谢谢你们，见到这枚镜子我的心情好了许多。我们先出发，找到尤娜的妹妹再来聊我的事情吧。',Speaker=817919,}",
  [853152] = "{id=853152,Text='冒险者？！没想到我们在姜饼城还能碰到？旅行一切顺利吗？',Speaker=818013,}",
  [853153] = "{id=853153,Text='我们没有继续旅行，我们是专门来找你。',Speaker=817919,}",
  [853154] = "{id=853154,Text='找我？有什么事情嘛？',Speaker=818013,Option='讲述情况',}",
  [853155] = "{id=853155,Text='天哪！我完全没意识到。我只是想给姐姐一个惊喜！',Speaker=818013,Option='惊喜？',Emoji=1,}",
  [853156] = "{id=853156,Text='我想帮姐姐[c][ffff00]找到最合适装饰蛋糕的礼物[-][/c]才离开的。',Speaker=818013,}",
  [853157] = "{id=853157,Text='但姐姐不清楚状况以为你离家出走了！',Speaker=817919,}",
  [853158] = "{id=853158,Text='……没想到，我以为自己看似体贴的选择居然给姐姐带来了这么大的压力。',Speaker=818013,}",
  [853159] = "{id=853159,Text='那冒险者，我能冒昧麻烦你们帮我找到合适的礼物吗？这样我就能快一点赶回去！我不想让姐姐担心。',Speaker=818013,Option='没问题',}",
  [853161] = "{id=853161,Text='姜饼城里开了一家很有名气，[c][ffff00]也很有脾气[-][/c]的礼品店。',Speaker=818013,}",
  [853185] = "{id=853185,Text='就是我身后这家店。',Speaker=818013,}",
  [853186] = "{id=853186,Text='门虽然开着，但是老板却不在，我已经等了很久了……',Speaker=818013,}",
  [853187] = "{id=853187,Text='没关系！我们先进去挑礼物，等老板回来再付钱就好啦！',Speaker=817920,}",
  [853188] = "{id=853188,Text='可是听说老板非常有脾气呢……',Speaker=818013,}",
  [853189] = "{id=853189,Text='诶？',Speaker=818013,}",
  [853190] = "{id=853190,Text='波莉安娜！',Speaker=817919,}",
  [853191] = "{id=853191,Text='真是没办法。冒险者，艾妮娜，我们先进去吧。',Speaker=817919,}",
  [853192] = "{id=853192,Text='只要小心点，应该不会惹怒老板吧。',Speaker=817919,}",
  [853193] = "{id=853193,Text='谢天谢地冒险者你们回来了，找到我妹妹了嘛？',Speaker=817921,}",
  [853194] = "{id=853194,Text='姐姐！',Speaker=817922,}",
  [853195] = "{id=853195,Text='艾妮娜！你这几天跑哪儿去了？怎么还瘦了？我快急死了！你听姐姐说，我其实一直想告诉你，我……',Speaker=817921,}",
  [853196] = "{id=853196,Text='姐姐，姐姐我不是离家出走，我只是想要给你准备个惊喜。',Speaker=817922,}",
  [853197] = "{id=853197,Text='惊喜？',Speaker=817921,}",
  [853198] = "{id=853198,Text='嗯，还要多亏冒险者，不然我都没办法得到这件礼物。',Speaker=817922,}",
  [853199] = "{id=853199,Text='好漂亮！',Speaker=817921,}",
  [853200] = "{id=853200,Text='这样你就不用发愁自己蛋糕上放什么装饰了！',Speaker=817922,}",
  [853201] = "{id=853201,Text='是！',Speaker=817921,}",
  [853202] = "{id=853202,Text='这样漂亮的大蛋糕我可以全部吃掉！',Speaker=817920,Option='净说大话',}",
  [853203] = "{id=853203,Text='姐姐，除了这个之外，我还想告诉你：[c][ffff00]世界上没有什么比一边照顾蛋糕店一边画画更幸福的事情，这就是我最大的梦想！[-][/c]',Speaker=817922,}",
  [853204] = "{id=853204,Text='艾妮娜！',Speaker=817921,Emoji=42,}",
  [853205] = "{id=853205,Text='丝碧卡姐姐，说不定，你的妹妹梦想也就是守护你呢？',Speaker=817920,}",
  [853206] = "{id=853206,Text='话虽这么说……',Speaker=817919,}",
  [853256] = "{id=853256,Text='进展还顺利吗？',Speaker=817921,Option='讲述情况',}",
  [853257] = "{id=853257,Text='居然有冒险者答不上的题目？',Speaker=817920,}",
  [853258] = "{id=853258,Text='啊这，确实很难判断，总是害怕错怪了学生们。',Speaker=817919,Option='再尝试一次吧',}",
  [853259] = "{id=853259,Text='这画应该需要跟上次一样的材料就可以修复了。',Speaker=817921,Option='什么材料来着',}",
  [853260] = "{id=853260,Text='[c][ffff00]10份【金丝线】、10份【颜料】、10份【空白画布】[-][/c]。我们再试一次！',Speaker=817921,}",
  [853261] = "{id=853261,Text='这下没问题，我们很快就搞定。',Speaker=817921,}",
  [853262] = "{id=853262,Text='一副描绘艺术家文森特光鲜生活的画作，仔细查看！',Speaker=817924,Picture='new_npc_pic_17',Option='{看好了,28},{再看一次,29}',BlackMask=1,}",
  [853803] = "{id=853803,Text='[c][ffff00]你们看，这个大蛋糕，就是姐姐为了镇子上即将到来的周年庆准备的！是不是很棒？！[-][/c]',Speaker=817922,}",
  [853804] = "{id=853804,Text='但总觉得还缺些什么，一直不是很满意。',Speaker=817921,}",
  [853805] = "{id=853805,Text='艾妮娜确实不像我狂热喜欢烘培，[c][ffff00]她是非常厉害的画家[-][/c]！',Speaker=817921,Emoji=27,}",
  [853806] = "{id=853806,Text='我们蛋糕店里所有的画与装饰都是艾妮娜创作布置的。',Speaker=817921,Emoji=38,}",
  [853928] = "{id=853928,Text='哎呀冒险者快快准备好，然后我们就一个冲！',Speaker=817920,}",
  [853936] = "{id=853936,Text='好像没有什么奇怪的。',}",
  [853937] = "{id=853937,Text='去找尤娜确认下吧。',}",
  [853950] = "{id=853950,Text='[c][ffff00]最温暖又最寒冷的地方[-][/c]，还不知道找不找的到呢！',Speaker=817920,}",
  [853951] = "{id=853951,Text='波莉安娜！',Speaker=817919,}",
  [853955] = "{id=853955,Text='这是！',Speaker=817919,}",
  [853956] = "{id=853956,Text='好漂亮的裙子！',Speaker=817921,}",
  [853957] = "{id=853957,Text='哦吼吼我的裙子，我最喜欢甜品的一条裙子！它出现了！冒险者快收下！',Speaker=817920,}",
  [853958] = "{id=853958,Text='太好了冒险者，你们也完成了自己的寻宝之旅。',Speaker=817922,}",
  [853959] = "{id=853959,Text='嗯嗯，算是阶段性的完成啦，接下来还要继续麻烦冒险者咯！',Speaker=817920,}",
  [853960] = "{id=853960,Text='冒险者，这一路上多亏有你。我看到了很多事，学到了很多我的家族没有教会我的事。',Speaker=817919,}",
  [853961] = "{id=853961,Text='我会努力去找寻最后的照片线索。',Speaker=817919,}",
  [853962] = "{id=853962,Text='等找到照片后，我会再来和你相见。',Speaker=817919,Option='下次见',}",
  [856710] = "{id=856710,Text='冒险者！我们感知到一个陌生而又强大的[c][ffff00]时空裂隙[-][/c]！',Speaker=891648,}",
  [856711] = "{id=856711,Text='大陆卫队已经大范围展开排查，不过我们人手有限，需要你的帮助！\n【[c][ffff00]活动为账号限定，每个账号每天仅可参与1次[-][/c]。】',Speaker=891648,Option='{这就去,3},{暂时没空,2}',}",
  [856712] = "{id=856712,Text='很好，时空裂隙就在[c][ffff00]西门[-][/c]，快去吧。',Speaker=891648,}",
  [856713] = "{id=856713,Text='大陆卫队给予你最崇高的敬意！',Speaker=891648,}",
  [856714] = "{id=856714,Text='这件事非常重要，等你有时间后，请务必协助我们。',Speaker=891648,}",
  [856715] = "{id=856715,Text='我从没见过这么强大的人！',Speaker=891650,}",
  [856716] = "{id=856716,Text='你们是……？',Speaker=891650,Option='调查时空裂隙',}",
  [856717] = "{id=856717,Text='我叫[c][ffff00]雷欧[-][/c]，你们说的是这里的时空裂隙吗……',Speaker=891650,Option='没错',}",
  [856722] = "{id=856722,Text='没错！就是个光头！真是前所未有的强大啊！',Speaker=891651,}",
  [856723] = "{id=856723,Text='他们从裂隙走出来后，看到了被魔物攻击的我，',Speaker=891651,}",
  [856724] = "{id=856724,Text='光头大人只用了一拳就击败了那些魔物！',Speaker=891651,}",
  [856725] = "{id=856725,Text='他的实力，绝对比最近很有名的那个……叫什么[c][ffff00][PlayerName][-][/c]的家伙厉害多了！',Speaker=891651,Option='{？？？,6},{我就是,7}',}",
  [856726] = "{id=856726,Text='你这什么表情……难不成你认识那个[c][ffff00][PlayerName][-][/c]？',Speaker=891651,Option='我就是',}",
  [856727] = "{id=856727,Text='……',Speaker=891651,}",
  [856728] = "{id=856728,Text='什么！！！！？？？！！！！！',Speaker=891651,}",
  [856729] = "{id=856729,Text='给我签个名吧！！！！！我想成为像你一样的冒险者！！',Speaker=891651,Option='{……,0},{你变得真快,0}',}",
  [856730] = "{id=856730,Text='嘿嘿，那位光头英雄是真的很厉害呢！',Speaker=891651,}",
  [856731] = "{id=856731,Text='我一定要找到他！如果能跟着他学习如何变得强大……那该多威风啊！',Speaker=891651,}",
  [856732] = "{id=856732,Text='你要调查裂隙的话，不如和我一起？',Speaker=891651,Option='也好',}",
  [856733] = "{id=856733,Text='他往那边去了，我们赶紧跟上！',Speaker=891651,}",
  [856734] = "{id=856734,Text='看！他们在那！',Speaker=891651,}",
  [856735] = "{id=856735,Text='你们是……',Speaker=891652,Option='说明来意',}",
  [856736] = "{id=856736,Text='原来如此，我是[c][ffff00]杰诺斯[-][/c]，这位是我的老师[c][ffff00]埼玉[-][/c]。',Speaker=891653,}",
  [856737] = "{id=856737,Text='果然是时空裂隙……异世界人的投影吗……？',}",
  [856738] = "{id=856738,Text='啊……[c][ffff00]时空裂隙[-][/c]？那是什么?',Speaker=891652,}",
  [856739] = "{id=856739,Text='你们大概是通过时空裂隙从异世界召唤过来的。不同于这个世界，从别的世界召唤过来的存在我们称之为影子。',}",
  [856740] = "{id=856740,Text='一阵强光过后，我们就已经来到这里了，这么说来，这里是另一个世界么……',Speaker=891653,Option='是的',}",
  [856741] = "{id=856741,Text='……好饿，杰诺斯，我们先去找些食物填饱肚子，然后再找找回去的办法吧。',Speaker=891652,}",
  [856742] = "{id=856742,Text='没问题，老师。',Speaker=891653,}",
  [856743] = "{id=856743,Text='为了感谢你们刚刚的解救，我愿意为你们制作料理！',Speaker=891651,}",
  [856744] = "{id=856744,Text='在那之后，我和[c][ffff00][PlayerName][-][/c]会一同帮忙寻找回去的办法！',Speaker=891651,}",
  [856745] = "{id=856745,Text='既然这样……那麻烦你们了。',Speaker=891653,}",
  [856754] = "{id=856754,Text='唔……我这里有龙虾和海鱼，那就制作一份[c][ffff00]刺刺木海鲜料理[-][/c]吧！',Speaker=891651,}",
  [856755] = "{id=856755,Text='[c][ffff00][PlayerName][-][/c]，请帮我去采集一些[c][ffff00]刺刺木[-][/c]，我会在这期间布置料理台。',Speaker=891651,}",
  [856756] = "{id=856756,Text='对了，再从溜溜猴身上收集一些[c][ffff00]跳跳蕉[-][/c]，我制作的[c][ffff00]跳跳蕉奶昔[-][/c]大家都赞不绝口！',Speaker=891651,Option='没问题',}",
  [856757] = "{id=856757,Text='总算回来了！快把食材放在[c][ffff00]木桶[-][/c]上吧！',Speaker=891651,}",
  [856758] = "{id=856758,Text='冒险者你来打下手。',Speaker=891651,}",
  [856759] = "{id=856759,Text='龙虾要火焰炙烤，海鱼要捶打鱼身，刺刺木只取木尖，跳跳蕉则要压泥，明白了吗？',Speaker=891651,Option='明白了',}",
  [856760] = "{id=856760,Text='好，那就开始了！',Speaker=891651,}",
  [856761] = "{id=856761,Text='处理龙虾应该……',Speaker=891675,Option='{火焰炙烤,2},{泥土包裹,3},{冰镇保鲜,4}',}",
  [856762] = "{id=856762,Text='看上去非常完美。',}",
  [856763] = "{id=856763,Text='感觉哪里不对劲……',}",
  [856764] = "{id=856764,Text='感觉哪里不对劲……',}",
  [856765] = "{id=856765,Text='处理海鱼应该……',Speaker=891676,Option='{快刀剔骨,2},{拔掉鳞片,3},{捶打鱼身,4}',}",
  [856766] = "{id=856766,Text='感觉哪里不对劲……',}",
  [856767] = "{id=856767,Text='感觉哪里不对劲……',}",
  [856768] = "{id=856768,Text='看上去非常完美。',}",
  [856769] = "{id=856769,Text='刺刺木应该使用……',Speaker=891677,Option='{木尖,2},{木身,3},{木根,4}',}",
  [856770] = "{id=856770,Text='看上去非常完美。',}",
  [856771] = "{id=856771,Text='感觉哪里不对劲……',}",
  [856772] = "{id=856772,Text='感觉哪里不对劲……',}",
  [856773] = "{id=856773,Text='处理跳跳蕉应该……',Speaker=891678,Option='{切片,2},{压泥,3},{榨汁,4}',}",
  [856774] = "{id=856774,Text='感觉哪里不对劲……',}",
  [856775] = "{id=856775,Text='看上去非常完美。',}",
  [856776] = "{id=856776,Text='感觉哪里不对劲……',}",
  [856777] = "{id=856777,Text='这么快就处理好了，让我看看……',Speaker=891651,}",
  [856778] = "{id=856778,Text='没错！就是这样，你简直太厉害了！',Speaker=891651,}",
  [856779] = "{id=856779,Text='这……等等！你竟然一个都没处理对！你简直是黑暗料理之王！',Speaker=891651,}",
  [856780] = "{id=856780,Text='嗯……虽然有些处理方式并不恰当，不过这不算什么大问题。',Speaker=891651,}",
  [856781] = "{id=856781,Text='接下来交给我，让我来给你露一手！',Speaker=891651,}",
  [856782] = "{id=856782,Text='完成了，嗯……真香！',Speaker=891651,}",
  [856783] = "{id=856783,Text='埼玉老师，杰诺斯，快来品尝一下吧！',Speaker=891651,}",
  [856784] = "{id=856784,Text='怎么样，我的手艺还行吗？',Speaker=891651,Option='{非常棒,0},{还不错,0}',}",
  [856785] = "{id=856785,Text='料理可是我最擅长的！',Speaker=891651,}",
  [856786] = "{id=856786,Text='啊，果然非常美味呢！谢谢你们！',Speaker=891652,}",
  [856787] = "{id=856787,Text='诶，发生了什么？',Speaker=891651,Option='好像是……',}",
  [856788] = "{id=856788,Text='这是……巴风特？',Speaker=891651,}",
  [856789] = "{id=856789,Text='好像有被改造的痕迹……',Speaker=891651,}",
  [856790] = "{id=856790,Text='是怪人吗？',Speaker=891652,}",
  [856791] = "{id=856791,Text='与其说是怪人，不如说是魔物更为恰当……啊，他要向我们攻击了，大家当心！',Speaker=891651,}",
  [856792] = "{id=856792,Text='在这个世界也是一拳就结束了吗？',Speaker=891652,}",
  [856793] = "{id=856793,Text='埼玉老师……你也……太厉害了！！！！',Speaker=891651,}",
  [856794] = "{id=856794,Text='好像有什么[c][ffff00]碎屑状的东西[-][/c]掉下来了。',Speaker=891651,}",
  [856795] = "{id=856795,Text='这是……[c][ffff00]格拉石[-][/c]？',Speaker=891651,}",
  [856796] = "{id=856796,Text='似乎是只有[c][ffff00]梦罗克[-][/c]才有的矿石，格外的稀少珍贵呢。',Speaker=891651,}",
  [856797] = "{id=856797,Text='[c][ffff00]梦罗克[-][/c]…那是个地名吗？…或许我们需要走一趟了。',Speaker=891653,}",
  [856798] = "{id=856798,Text='确实。',Speaker=891652,}",
  [856799] = "{id=856799,Text='明天的这个时候，我们就在[c][ffff00]梦罗克[-][/c]集合吧，也许调查[c][ffff00]格拉石[-][/c]，能解开时空裂隙的谜团。',Speaker=891653,Option='没问题',}",
  [856846] = "{id=856846,Text='啊……这里就是梦罗克吗，很荒凉的样子。',Speaker=891706,}",
  [856847] = "{id=856847,Text='哎，这里曾经也是辉煌过的……',Speaker=891707,}",
  [856848] = "{id=856848,Text='按照目前的线索来看，格拉石和时空裂隙或许有关联……',Speaker=891708,}",
  [856849] = "{id=856849,Text='等等！有人来了。',Speaker=891708,}",
  [856850] = "{id=856850,Text='他们好像在说些什么。',Speaker=891708,}",
  [856864] = "{id=856864,Text='光芒扩散之地……[c][ffff00][PlayerName][-][/c]，还能联想到什么？',Speaker=891709,Option='有阳光的地方',}",
  [856865] = "{id=856865,Text='光芒扩散之地……[c][ffff00][PlayerName][-][/c]，还能联想到什么？',Speaker=891709,Option='{有灯光的地方,0},{有月光的地方,0}',}",
  [856870] = "{id=856870,Text='哎，格拉石越来越少了，再这么挖下去，梦罗克都要被挖没了。',Speaker=891719,}",
  [856871] = "{id=856871,Text='哪那么多废话，[c][ffff00]老大[-][/c]要的东西，咱们去做就是了。',Speaker=891721,}",
  [856872] = "{id=856872,Text='等完成了[c][ffff00]大事[-][/c]，少不了咱们的好处！明白吗？',Speaker=891721,}",
  [856873] = "{id=856873,Text='明白！',Speaker=891719,}",
  [856874] = "{id=856874,Text='那还愣着干嘛，快去挖格拉石！',Speaker=891721,}",
  [856875] = "{id=856875,Text='……（不小心弄出动静）',Speaker=891707,}",
  [856876] = "{id=856876,Text='嗯？是谁！？被你们听到了？去死吧！',Speaker=891721,}",
  [856877] = "{id=856877,Text='别打了别打了，我说！',Speaker=891721,}",
  [856878] = "{id=856878,Text='我们是[c][ffff00]生命体研究所[-][/c]的研究人员……',Speaker=891721,}",
  [856879] = "{id=856879,Text='骗人！研究所明明都已经废弃了！',Speaker=891707,}",
  [856880] = "{id=856880,Text='咳……是真的！我们是[c][ffff00]梅菲[-][/c]的手下，是他命令我们来这里[c][ffff00]偷取格拉石[-][/c]的。',Speaker=891721,Option='梅菲是谁？',}",
  [856881] = "{id=856881,Text='他……是一个[c][ffff00]科学家[-][/c]。',Speaker=891721,Option='为什么偷取格拉石',}",
  [856882] = "{id=856882,Text='这……据说格拉石有[c][ffff00]撕裂时空[-][/c]的能力……',Speaker=891721,Option='目的是什么？',}",
  [856883] = "{id=856883,Text='哼哼，老大要[c][ffff00]做一件大事[-][/c]，完成后，整个世界都是我们的！',Speaker=891721,Option='什么大事',}",
  [856884] = "{id=856884,Text='这我就不知道了……我隐隐约约听到他说，要去什么……[c][ffff00]光芒扩散之地[-][/c]……',Speaker=891721,}",
  [856885] = "{id=856885,Text='光芒扩散之地……[c][ffff00][PlayerName][-][/c]，你能联想到什么吗？',Speaker=891707,Option='{有灯光的地方,2},{有阳光的地方,3},{有月光的地方,2}',}",
  [856886] = "{id=856886,Text='唔……既有灯光又有月光！[c][ffff00]发乐斯灯塔[-][/c]！',Speaker=891707,}",
  [856887] = "{id=856887,Text='阳光吗……传说中[c][ffff00]艾卡拉奇[-][/c]的晨曦之光极其神圣。但是……',Speaker=891707,}",
  [856888] = "{id=856888,Text='我们无法确认光芒到底指的是什么，既然如此我们分头行动。',Speaker=891708,}",
  [856889] = "{id=856889,Text='我们三个前往[c][ffff00]发乐斯灯塔[-][/c]，老师去[c][ffff00]艾卡拉奇[-][/c]。',Speaker=891708,}",
  [856890] = "{id=856890,Text='交给我吧！',Speaker=891706,}",
  [856891] = "{id=856891,Text='是变异的魔物！它们攻击过来了！',Speaker=891707,}",
  [856924] = "{id=856924,Text='你们还好吗！',Speaker=891706,Option='杰诺斯受了伤……',}",
  [856925] = "{id=856925,Text='……老……师……',Speaker=891708,}",
  [856926] = "{id=856926,Text='损坏了啊，看来要找个地方修复杰诺斯的身体了。',Speaker=891706,}",
  [856931] = "{id=856931,Text='唔……杰诺斯的身体构造比较复杂，不知道能否在这个世界进行修复。',Speaker=891706,Option='去里希塔乐',}",
  [856932] = "{id=856932,Text='里希塔乐……？那里可以修复杰诺斯的身体吗？',Speaker=891706,Option='应该可以',}",
  [856933] = "{id=856933,Text='哦！好诶。',Speaker=891706,}",
  [856939] = "{id=856939,Text='我真是没用……',Speaker=891707,}",
  [856940] = "{id=856940,Text='一点忙都帮不上……杰诺斯也为了救我受伤……',Speaker=891707,Option='别这么说',}",
  [856941] = "{id=856941,Text='别太在意，说不定明天会有解决的办法呢。',Speaker=891706,}",
  [856942] = "{id=856942,Text='嗯……',Speaker=891707,}",
  [856943] = "{id=856943,Text='我们就在里希塔乐见吧，我会带着杰诺斯过来的。',Speaker=891706,Option='没问题',}",
  [856944] = "{id=856944,Text='……再见。',Speaker=891707,Option='雷欧……',}",
  [856946] = "{id=856946,Text='我……我对不起，大家！',Speaker=891737,Option='怎么了',}",
  [856947] = "{id=856947,Text='是我害了杰诺斯，他是为了救我才……',Speaker=891737,}",
  [856948] = "{id=856948,Text='咳……别……这么说……',Speaker=891738,}",
  [856949] = "{id=856949,Text='杰诺斯，你放心，我不会让你白白受伤的！',Speaker=891737,}",
  [856950] = "{id=856950,Text='其实……',Speaker=891736,}",
  [856951] = "{id=856951,Text='无论付出什么代价，我都会把你治好！',Speaker=891737,}",
  [856952] = "{id=856952,Text='呃……',Speaker=891736,}",
  [856953] = "{id=856953,Text='其实……只要找工匠修复身体就行了，我是[c][ffff00]机器人[-][/c]呢。',Speaker=891738,}",
  [856954] = "{id=856954,Text='里希塔乐最出色的工匠是……[c][ffff00]丝莱[-][/c]！我们去找他！',Speaker=891737,}",
  [856955] = "{id=856955,Text='修复机器人？这有什么难的，送上门的生意，哪有不做的道理。',Speaker=891739,}",
  [856956] = "{id=856956,Text='啧啧，下手可真是狠啊……',Speaker=891739,}",
  [856981] = "{id=856981,Text='给我一天时间就能把这个机器人修的漂漂亮亮的。',Speaker=891739,}",
  [856982] = "{id=856982,Text='啊，解决了一件大事。',Speaker=891736,}",
  [856983] = "{id=856983,Text='[c][ffff00]生命体研究所[-][/c]就在这里，接下来我们可以收集些关于[c][ffff00]梅菲[-][/c]的线索。',Speaker=891737,Option='雷欧说的对',}",
  [856984] = "{id=856984,Text='对哦。',Speaker=891736,}",
  [856985] = "{id=856985,Text='好像是梅菲手下的研究员……',}",
  [856986] = "{id=856986,Text='梅……梅菲在[c][ffff00]研究所地下1F[-][/c]的[c][ffff00]隐藏房间里[-][/c]！',Speaker=891743,Option='隐藏房间？',}",
  [856987] = "{id=856987,Text='他从不让我们去那个房间……我们也不知道具体在哪……',Speaker=891743,Option='还有别的信息吗',}",
  [856988] = "{id=856988,Text='呃……还有……想要进入研究所的话！必须穿着研究院的[c][ffff00]统一着装[-][/c]……',Speaker=891743,Option='如果是这样的话……',}",
  [856989] = "{id=856989,Text='等等……你要做什么！！！！',Speaker=891743,}",
  [856990] = "{id=856990,Text='我的衣服！',Speaker=891745,}",
  [856991] = "{id=856991,Text='快跑！',Speaker=891745,}",
  [857001] = "{id=857001,Text='咳咳……你回来啦。',Speaker=891737,Option='发生什么了',}",
  [857002] = "{id=857002,Text='没……没什么，我就是和埼玉老师随便聊聊。',Speaker=891737,}",
  [857003] = "{id=857003,Text='怎么样，有打听到什么线索吗？',Speaker=891737,Option='详细告知',}",
  [857004] = "{id=857004,Text='太棒了！等明天杰诺斯的身体修复完成，我们就立刻向着[c][ffff00]梅菲的老巢[-][/c]出发！',Speaker=891737,}",
  [857005] = "{id=857005,Text='他的邪恶计划休想得逞！',Speaker=891737,}",
  [857006] = "{id=857006,Text='我们在这儿！',Speaker=891747,}",
  [857007] = "{id=857007,Text='我的身体已经修复好了，别担心。',Speaker=891748,}",
  [857008] = "{id=857008,Text='今天我们必须要去[c][ffff00]生命体研究所[-][/c]了。',Speaker=891748,}",
  [857009] = "{id=857009,Text='没错，我们快换上研究员的衣服。',Speaker=891747,}",
  [857010] = "{id=857010,Text='这……只有两件吗？',Speaker=891747,}",
  [857011] = "{id=857011,Text='你们穿上吧，我和老师从高处潜入，我们直接在研究所地下会合。',Speaker=891748,}",
  [857013] = "{id=857013,Text='闲杂人等，不许进入雷根贝勒公司！',Speaker=800132,}",
  [857014] = "{id=857014,Text='你怎么把衣服脱下来了，快穿上！',Speaker=891747,}",
  [857015] = "{id=857015,Text='咳……老大要的材料我们带回来了，这就给他送过去。',Speaker=891747,}",
  [857016] = "{id=857016,Text='好的，研究员先生。',Speaker=800132,}"
}
